import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_39" tabindex="-1"><a class="header-anchor" href="#_39" aria-hidden="true">#</a> 39</h1><p>\u300C\u6674\u5929\u7684\u6674\uFF0C\u5927\u96EA\u7684\u96EA\u2026\u2026\u300D\u5973\u5B69\u5AE9\u5AE9\u7684\u58F0\u97F3\u4F3C\u4E4E\u8FD8\u5728\u8033\u8FB9\u56DE\u54CD\u3002</p><p>\u9759\u989C\u4ECE\u8239\u8231\u51FA\u6765\uFF0C\u7EB7\u98DE\u7684\u9633\u5149\u8C61\u96EA\u7247\u4E00\u6837\u6251\u5728\u8EAB\u4E0A\uFF0C\u5F77\u4F5B\u6E7F\u900F\u4E86\u8863\u895F\u3002\u8239\u592B\u5728\u8231\u540E\u300C\u54D1\u54D1\u300D\u6447\u7740\u6A79\uFF0C\u6D69\u8361\u7684\u6C49\u6C34\u5F77\u4F5B\u6D41\u5230\u4E86\u5929\u5730\u4E4B\u5916\uFF0C\u5BBD\u9614\u7684\u6C5F\u9762\u4E0A\u770B\u4E0D\u5230\u4E00\u821F\u4E00\u4EBA\u3002</p><p>\u9759\u989C\u6000\u91CC\u53D6\u51FA\u4E00\u6761\u67D4\u8F6F\u7684\u7EC7\u7269\uFF0C\u90A3\u662F\u4E00\u6761\u9ED1\u8272\u7684\u4E1D\u5DFE\uFF0C\u7531\u4E1C\u6D77\u7684\u9C9B\u4E1D\u7EC7\u6210\uFF0C\u53C8\u8F7B\u53C8\u6ED1\uFF0C\u51C9\u51C9\u7684\uFF0C\u5B9B\u5982\u591C\u7684\u989C\u8272\u3002\u4E1D\u5DFE\u4E00\u89D2\uFF0C\u7EE3\u7740\u4E00\u6735\u5C0F\u5C0F\u7684\u73AB\u7470\u82B1\u82DE\u3002</p><p>\u90A3\u662F\u5C0F\u516C\u4E3B\u6254\u5728\u8273\u5C38\u8138\u4E0A\u7684\u4E1D\u5DFE\uFF0C\u9759\u989C\u4E00\u76F4\u653E\u5728\u8EAB\u8FB9\u3002\u7B2C\u4E00\u6B21\u62FF\u8D77\u8FD9\u5757\u4E1D\u5DFE\uFF0C\u770B\u5230\u90A3\u6735\u5C0F\u5C0F\u7684\u73AB\u7470\u82B1\u82DE\uFF0C\u5979\u5C31\u6709\u79CD\u4F3C\u66FE\u76F8\u8BC6\u7684\u611F\u89C9\u3002\u4F46\u5979\u4E0D\u76F8\u4FE1\u547D\u8FD0\u4F1A\u662F\u8FD9\u4E48\u5DE7\u5999\u800C\u6B8B\u5FCD\u3002</p><p>\u5E08\u7236\u3001\u5E08\u5A18\u3001\u9759\u83BA\u59B9\u59B9\uFF0C\u8FD8\u6709\u7476\u963F\u59E8\u2026\u2026\u5979\u628A\u8FD9\u4E9B\u751F\u547D\u4E2D\u6700\u4EB2\u8FD1\u7684\u4EBA\u4E00\u4E2A\u4E2A\u8E29\u5728\u811A\u4E0B\uFF0C\u52AA\u529B\u53BB\u63A5\u8FD1\u5FC3\u76EE\u4E2D\u4EC7\u4EBA\u7684\u5973\u513F\uFF0C\u671F\u5F85\u7740\u6709\u4E00\u5929\u80FD\u8086\u610F\u8DF5\u8E0F\u5979\u7684\u8089\u4F53\u548C\u5C0A\u4E25\u3002\u6700\u540E\u624D\u53D1\u73B0\u5979\u7ADF\u662F\u81EA\u5DF1\u85CF\u5728\u5FC3\u5E95\u7684\u90A3\u4E2A\u5973\u5B69\u3002</p><p>\u6674\u96EA\uFF0C\u6674\u7A7A\u4E00\u6837\u660E\u51C0\uFF0C\u96EA\u82B1\u4E00\u6837\u7EAF\u6D01\u3002\u90A3\u4E2A\u6676\u83B9\u5F97\u5982\u540C\u900F\u660E\u7684\u5C0F\u5973\u5B69\uFF0C\u5728\u5979\u5FC3\u76EE\u4E2D\u4E00\u76F4\u662F\u7F8E\u7684\u5316\u8EAB\u3002</p><p>\u300C\u2026\u2026\u6DEB\u8D31\u6B7B\u4E86\uFF0C\u90A3\u4E48\u5C0F\u5C31\u8DDF\u7537\u4EBA\u4E0A\u5E8A\u4E86\u3002\u300D</p><p>\u300C\u5979\u559C\u6B22\u7ED9\u5973\u4EBA\u5F00\u82DE\u2026\u2026\u300D</p><p>\u300C\u5979\u7528\u90A3\u4E48\u7C97\u7684\u4E1C\u897F\uFF0C\u5E72\u4EBA\u5BB6\u7684\u5C41\u773C\u513F\u2026\u2026\u300D</p><p>\u8FD9\u662F\u592D\u592D\u53E3\u91CC\u7684\u6674\u96EA\u3002\u4E00\u4E2A\u516D\u5C81\u5C31\u8DDF\u7537\u4EBA\u6DEB\u4E71\uFF0C\u559C\u6B22\u7ED9\u5973\u4EBA\u5F00\u82DE\uFF0C\u5E72\u522B\u4EBA\u5C41\u773C\uFF0C\u628A\u6DF3\u4E8E\u5BB6\u4E09\u6735\u540D\u82B1\u505A\u6210\u706F\u7B3C\u8D4F\u73A9\u7684\u5C11\u5973\u3002\u5C31\u50CF\u4E00\u53EA\u53D1\u60C5\u7684\u880D\u5B50\uFF0C\u53C8\u6DEB\u53C8\u6BD2\u3002</p><p>\u300C\u9F99\u54E5\u54E5\u2026\u2026\u300D\u5979\u4F3C\u4E4E\u8FD8\u80FD\u542C\u5230\u90A3\u4E2A\u5E26\u7740\u5976\u8154\u7684\u58F0\u97F3\uFF0C\u6CA1\u6709\u4E1D\u6BEB\u6C61\u6D4A\uFF0C\u7EAF\u51C0\u6781\u4E86\u3002</p><p>\u300C\u65A9\u4E0B\u5468\u5B50\u6C5F\u7684\u5934\u9885\uFF0C\u628A\u51CC\u96C5\u7434\u5E9F\u53BB\u6B66\u529F\uFF0C\u4EA4\u7531\u5999\u82B1\u5E08\u592A\u5904\u7F6E\u3002\u7B49\u4F60\u56DE\u6765\uFF0C\u672C\u5BAB\u4EB2\u81EA\u7ED9\u4F60\u5F00\u82DE\u3002\u300D\u8FD9\u4E2A\u51B7\u51B0\u51B0\u7684\u58F0\u97F3\u66F4\u4E3A\u6E05\u6670\u3002</p><p>\u5979\u65E0\u6CD5\u60F3\u50CF\uFF0C\u4E94\u5C81\u7684\u6674\u96EA\u4E0E\u5341\u4E94\u5C81\u7684\u5C0F\u516C\u4E3B\u5982\u4F55\u80FD\u8054\u7CFB\u8D77\u6765\u3002\u8FD9\u5341\u5E74\uFF0C\u5979\u662F\u5982\u4F55\u5EA6\u8FC7\u7684\uFF1F\u5979\u662F\u5426\u8FD8\u8BB0\u5F97\u90A3\u4E2A\u96EA\u591C\uFF0C\u8BB0\u5F97\u90A3\u4E2A\u7518\u613F\u4E3A\u5979\u51FA\u5356\u8272\u76F8\u7684\u9F99\u54E5\u54E5\uFF1F</p><p>\u300C\u542C\u8BF4\u4F60\u4E0A\u4E5D\u534E\u4E4B\u524D\u8FD8\u5728\u5E7F\u5B8F\u5E2E\u4F4F\u8FC7\u4E00\u6BB5\u65E5\u5B50\uFF0C\u90A3\u4E48\u5C0F\u5C31\u5F00\u59CB\u5973\u626E\u7537\u88C5\uFF0C\u771F\u662F\u82E6\u4E86\u4F60\u4E86\u3002\u300D</p><p>\u9759\u989C\u628A\u4E1D\u5DFE\u653E\u5728\u988A\u4E0A\uFF0C\u90A3\u80A1\u6DE1\u6DE1\u7684\u5E7D\u9999\u4F7F\u5979\u4E0D\u5B89\u7684\u5FC3\u6E10\u6E10\u5E73\u8861\u4E0B\u6765\u3002\u5979\u7A76\u7ADF\u8FD8\u77E5\u9053\u591A\u5C11\u4E8B\u60C5\u5462\uFF1F\u6050\u6015\u5979\u65E9\u5FD8\u4E86\u4E94\u5C81\u65F6\u7684\u90A3\u6B21\u9082\u9005\u4E86\u5427\u3002\u8FD9\u6837\u6700\u597D\uFF0C\u6211\u4E5F\u53EF\u4EE5\u5FD8\u6389\u90A3\u4E9B\u4E8B\uFF0C\u50CF\u964C\u751F\u4EBA\u4E00\u6837\uFF0C\u7B49\u5F85\u4F60\u7ED9\u6211\u5F00\u82DE\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5E7D\u5E7F\u65E0\u9645\u7684\u7EC8\u5357\uFF0C\u5B9B\u5982\u5E9E\u7136\u5DE8\u7269\u6A2A\u4E98\u5728\u5929\u5730\u4E4B\u95F4\uFF0C\u5411\u7740\u6DF1\u9083\u7684\u591C\u8272\u7EF5\u5EF6\u5F00\u53BB\u3002\u5C71\u98CE\u5076\u8FC7\uFF0C\u6574\u5EA7\u671B\u4E0D\u89C1\u8FB9\u9645\u7684\u5927\u5C71\u987F\u65F6\u98D8\u6447\u800C\u8D77\uFF0C\u5F77\u4F5B\u4E09\u5343\u91CC\u7684\u5927\u9E4F\u5F20\u5F00\u7FBD\u7FFC\uFF0C\u817E\u4E0A\u865A\u7A7A\u3002\u884C\u8D70\u5176\u95F4\u7684\u65C5\u4EBA\uFF0C\u5C31\u5982\u540C\u4E00\u7C92\u5FAE\u4E0D\u8DB3\u9053\u7684\u5C18\u57C3\uFF0C\u8F7B\u5F97\u6CA1\u6709\u4EFB\u4F55\u4EFD\u91CF\u3002</p><p>\u4E00\u4E2A\u7FE0\u886B\u5C11\u5973\u98CE\u4E00\u6837\u63A0\u8FC7\u5C71\u6797\uFF0C\u5979\u7684\u8EAB\u4F53\u6BD4\u6708\u5149\u4E0B\u7684\u98DE\u9E1F\u66F4\u8F7B\u76C8\uFF0C\u6CA1\u6709\u60CA\u9192\u811A\u4E0B\u7684\u679D\u53F6\u3002\u5979\u5728\u4E00\u68F5\u5DE8\u677E\u4E4B\u5DC5\u505C\u4F4F\u811A\u6B65\uFF0C\u9759\u9759\u671B\u5411\u5929\u9645\u3002\u4E00\u94A9\u6B8B\u6708\u8FDC\u8FDC\u60AC\u5728\u591C\u7A7A\u7684\u8FB9\u7F18\uFF0C\u5728\u5C11\u5973\u9ED1\u4EAE\u7684\u77B3\u5B54\u4E2D\u5370\u51FA\u4E24\u5F2F\u5C0F\u5C0F\u7684\u83B9\u767D\u3002\u518D\u8FC7\u51E0\u5929\uFF0C\u5C31\u662F\u6714\u65E5\u4E86\u3002</p><p>\u90A3\u662F\u4E2A\u6CA1\u6709\u6708\u4EAE\u7684\u591C\u665A\uFF0C\u6708\u5149\u5C06\u88AB\u5F7B\u5E95\u63A9\u76D6\uFF0C\u5929\u5730\u95F4\u552F\u6709\u9ED1\u6697\u3002</p><p>\u8FD9\u91CC\u8DDD\u661F\u6708\u6E56\u5DF2\u7136\u4E0D\u8FDC\uFF0C\u6781\u76EE\u671B\u53BB\uFF0C\u4F9D\u7A00\u80FD\u770B\u5230\u63A9\u5728\u5C71\u5CE6\u95F4\u7684\u6000\u6708\u5CF0\u3002\u4E0D\u77E5\u5C31\u91CC\u7684\u4EBA\u7EB5\u7136\u5230\u4E86\u6B64\u5904\uFF0C\u4E5F\u4F1A\u628A\u5B83\u5F53\u6210\u7FA4\u5CF0\u4E00\u8109\uFF0C\u8C01\u80FD\u60F3\u5230\uFF0C\u90A3\u5EA7\u5149\u79C3\u79C3\u7684\u77F3\u5CF0\uFF0C\u4F1A\u662F\u5EA7\u843D\u5728\u4E00\u6C60\u78A7\u6E56\u4E4B\u4E2D\uFF0C\u9690\u85CF\u7740\u6B66\u6797\u4E2D\u5343\u767E\u5E74\u6765\u7684\u79D8\u5BC6\u3002</p><p>\u4E00\u4E2A\u7EA4\u5DE7\u7684\u8EAB\u5F71\u4ECE\u6797\u4E0B\u63A0\u8FC7\uFF0C\u8EAB\u6CD5\u53C8\u8F7B\u53C8\u5FEB\uFF0C\u8F6C\u773C\u5C31\u63A0\u51FA\u5341\u4F59\u4E08\u7684\u8DDD\u79BB\u3002\u90A3\u5C11\u5973\u5728\u8EAB\u540E\u9690\u9690\u6D12\u4E0B\u4E00\u4E32\u7EC6\u5FAE\u7684\u62BD\u6CE3\u58F0\uFF0C\u5F77\u4F5B\u4E00\u4E32\u6676\u83B9\u7684\u6CEA\u73E0\u5728\u679D\u53F6\u98D8\u821E\u3002</p><p>\u9759\u989C\u5FC3\u4E0B\u4E00\u52A8\uFF0C\u98DE\u8EAB\u63A0\u4E0B\u5DE8\u677E\uFF0C\u671D\u5C11\u5973\u8FFD\u53BB\u3002\u6B64\u65F6\u5979\u5DF2\u7ECF\u5C06\u5E08\u7236\u3001\u5E08\u5A18\u7684\u529F\u529B\u5B8C\u5168\u5438\u7EB3\u3001\u5316\u89E3\uFF0C\u878D\u5165\u81EA\u5DF1\u4E39\u7530\u4E4B\u4E2D\u3002\u7434\u5251\u53CC\u4FA0\u7684\u529F\u529B\u679C\u7136\u4E0D\u51E1\uFF0C\u867D\u7136\u5316\u4E3A\u5DF1\u6709\u7684\u53EA\u6709\u4E09\u5206\u4E4B\u4E00\uFF0C\u4F46\u5BF9\u9759\u989C\u6765\u8BF4\uFF0C\u5DF2\u4E0D\u557B\u4E8E\u8131\u80CE\u6362\u9AA8\u3002\u90A3\u5C11\u5973\u8F7B\u529F\u8FD8\u548C\u4EE5\u524D\u4E00\u6837\u597D\uFF0C\u4F46\u9759\u989C\u5DF2\u7ECF\u8FDC\u80DC\u4E8E\u524D\u3002</p><p>\u5979\u98DE\u8EAB\u8FFD\u4E0A\u5C11\u5973\uFF0C\u4ECE\u540E\u8F7B\u8F7B\u633D\u4F4F\u5979\u7EC6\u8F6F\u7684\u8170\u80A2\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5C0F\u6BCD\u72D7\uFF0C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u5C11\u5973\u56DE\u8FC7\u5934\u6765\uFF0C\u8138\u4E0A\u5C3D\u662F\u60CA\u559C\u4EA4\u52A0\u7684\u795E\u8272\uFF0C\u6708\u5149\u4E0B\u7684\u6CEA\u73E0\u6F84\u6F88\u5254\u900F\uFF0C\u886C\u7740\u5979\u59E3\u8273\u7684\u4FCF\u8138\uFF0C\u8BF4\u4E0D\u51FA\u662F\u7EAF\u6D01\u8FD8\u662F\u5996\u5A9A\u3002\u5979\u8F6C\u8EAB\u7D27\u7D27\u6402\u4F4F\u9759\u989C\uFF0C\u5E26\u7740\u54ED\u8154\u9053\uFF1A\u300C\u9F99\u59D0\u59D0\uFF0C\u4F60\u7EC8\u4E8E\u56DE\u6765\u4E86\u3002\u300D</p><p>\u9759\u989C\u5728\u5979\u8033\u6839\u5475\u4E86\u53E3\u6C14\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u4E3A\u4EC0\u4E48\u4E0D\u5F00\u5FC3\u5462\uFF1F\u662F\u5C0F\u516C\u4E3B\u8D23\u602A\u4F60\u4E86\u5417\uFF1F\u300D</p><p>\u592D\u592D\u5728\u5979\u6000\u91CC\u6447\u4E86\u6447\u5934\uFF0C\u300C\u4E0D\u662F\u7684\u3002\u5979\u53BB\u4E86\u6D1B\u9633\u3002\u300D</p><p>\u300C\u54E6\uFF1F\u300D\u60F3\u5230\u4E0D\u5FC5\u9762\u5BF9\u516C\u4E3B\uFF0C\u9759\u989C\u5FC3\u91CC\u65E0\u7531\u5730\u8F7B\u677E\u8D77\u6765\u3002</p><p>\u9759\u989C\u628A\u592D\u592D\u6A2A\u62B1\u5728\u6000\u91CC\uFF0C\u6765\u4E00\u5904\u4EBA\u8FF9\u7F55\u81F3\u7684\u60AC\u5D16\u4E0A\uFF0C\u501A\u7740\u53C2\u5929\u5DE8\u677E\u5750\u4E0B\u3002\u5979\u4E00\u624B\u6402\u7740\u592D\u592D\u7684\u80A9\u5934\uFF0C\u4E00\u624B\u638F\u51FA\u4E1D\u5DFE\uFF0C\u8F7B\u67D4\u5730\u62B9\u53BB\u5C11\u5973\u8138\u4E0A\u7684\u6CEA\u75D5\uFF0C\u300C\u544A\u8BC9\u59D0\u59D0\uFF0C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u592D\u592D\u7684\u6CEA\u6C34\u8D8A\u6765\u8D8A\u591A\uFF0C\u5FFD\u7136\u545C\u7684\u4E00\u58F0\u94BB\u8FDB\u9759\u989C\u6000\u91CC\uFF0C\u75DB\u54ED\u9053\uFF1A\u300C\u9F99\u59D0\u59D0\uFF0C\u6211\u5A18\u4E3A\u4EC0\u4E48\u90A3\u4E48\u8D31\u2026\u2026\u300D</p><p>\u300C\u5979\u7684\u624B\u7B4B\u811A\u7B4B\u90FD\u88AB\u4EBA\u62BD\u4E86\uFF0C\u5C41\u773C\u513F\u53C8\u5F04\u6210\u90A3\u4E2A\u6837\u5B50\uFF0C\u6574\u5929\u8DB4\u5728\u90A3\u91CC\u8BA9\u4EBA\u7ED9\u5979\u6324\u5976\u2026\u2026\u4EBA\u5BB6\u662F\u628A\u5979\u5F53\u6210\u6BCD\u725B\u6765\u517B\uFF0C\u53EF\u5979\u8FD8\u90A3\u4E48\u8D31\uFF0C\u4E00\u8FB9\u8BA9\u592D\u592D\u6345\u5979\u7684\u5C41\u773C\u513F\uFF0C\u4E00\u8FB9\u8FD8\u53EB\u7740\u4ED6\u7684\u540D\u5B57\u2026\u2026\u5979\u662F\u6211\u5A18\u554A\uFF0C\u5979\u600E\u4E48\u4F1A\u8FD9\u4E48\u8D31\u2026\u2026\u300D\u592D\u592D\u54ED\u5F97\u8BF4\u4E0D\u4E0B\u53BB\u3002</p><p>\u300C\u5176\u5B9E\u2026\u2026\u4F60\u3001\u6211\u3001\u5979\uFF0C\u6211\u4EEC\u90FD\u662F\u4E00\u6837\u7684\u554A\u3002\u300D\u9759\u989C\u5728\u5FC3\u91CC\u8BF4\u7740\uFF0C\u62E5\u7D27\u592D\u592D\u62BD\u52A8\u7684\u8EAB\u5B50\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u54ED\u5427\uFF0C\u59D0\u59D0\u4F1A\u5728\u8FD9\u91CC\u7167\u987E\u592D\u592D\u7684\u3002\u300D</p><p>\u592D\u592D\u5C3D\u60C5\u54ED\u6CE3\u8D77\u6765\uFF0C\u624B\u6307\u7D27\u7D27\u634F\u7740\u9759\u989C\u7684\u8863\u89D2\uFF0C\u5F77\u4F5B\u6EBA\u6C34\u7684\u4EBA\u6293\u4F4F\u4E86\u4E00\u6839\u7A3B\u8349\u3002\u826F\u4E45\uFF0C\u5979\u626C\u8D77\u68A8\u82B1\u5E26\u96E8\u7684\u5C0F\u8138\uFF0C\u54FD\u54BD\u9053\uFF1A\u300C\u597D\u59D0\u59D0\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u4E0D\u662F\u6211\u5A18\u2026\u2026\u300D</p><p>\u9759\u989C\u8F7B\u7B11\u9053\uFF1A\u300C\u4F60\u53EB\u554A\u3002\u300D</p><p>\u300C\u5A18\uFF01\u300D\u592D\u592D\u8131\u53E3\u800C\u51FA\uFF0C\u63A5\u7740\u5C0F\u8138\u5237\u7684\u7EA2\u4E86\uFF0C\u7F9E\u7B54\u7B54\u4F4E\u4E0B\u5934\u3002\u5979\u6BD5\u7ADF\u53EA\u6709\u5341\u4E94\u5C81\uFF0C\u5373\u4F7F\u662F\u4E00\u6735\u8BE1\u5F02\u7684\u7F42\u7C9F\uFF0C\u4E5F\u6709\u7740\u67D4\u5F31\u7684\u82B1\u74E3\u3002</p><p>\u60AC\u5D16\u7684\u5DE8\u677E\u4E0B\uFF0C\u4E24\u4E2A\u5B64\u72EC\u7684\u5C11\u5973\u6402\u62B1\u7740\u504E\u4F9D\u5728\u4E00\u8D77\u3002\u592D\u592D\u8737\u8EAB\u6A2A\u5367\u5728\u9759\u989C\u6000\u4E2D\uFF0C\u4E24\u624B\u62B1\u7740\u5979\u7684\u819D\u5934\uFF0C\u4E4C\u4EAE\u7684\u79C0\u53D1\u5782\u5728\u5979\u817F\u4FA7\uFF0C\u4E56\u5F97\u50CF\u732B\u54AA\u4E00\u6837\u3002</p><p>\u9759\u989C\u626F\u4F4F\u592D\u592D\u7684\u8863\u5E26\uFF0C\u8F7B\u8F7B\u62C9\u5F00\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u4E56\u5973\u513F\uFF0C\u5A18\u8981\u5E72\u4F60\u7684\u5C41\u773C\u513F\u4E86\u3002\u300D</p><p>\u592D\u592D\u51FA\u5947\u7684\u7F9E\u6DA9\u8D77\u6765\uFF0C\u5979\u4E56\u4E56\u89E3\u5F00\u7F57\u8863\uFF0C\u892A\u4E0B\u4EB5\u88E4\uFF0C\u4F46\u7C89\u5AE9\u7684\u5C0F\u5C41\u80A1\u5374\u7D27\u7D27\u5408\u5728\u4E00\u8D77\uFF0C\u4E0D\u613F\u5206\u5F00\u3002\u5979\u7EA2\u7740\u8138\u5C0F\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u8FD8\u662F\u53EB\u592D\u592D\u5C0F\u6BCD\u72D7\u5427\u3002\u6211\u2026\u2026\u300D</p><p>\u9759\u989C\u773C\u4E2D\u900F\u51FA\u4E00\u4E1D\u601C\u60DC\uFF0C\u5979\u4E0D\u6562\u60F3\u50CF\u6709\u79CD\u7231\u4F1A\u8BA9\u4EBA\u5982\u6B64\u2026\u2026\u5C31\u50CF\u5979\u5F62\u5BB9\u751F\u6BCD\u4E00\u6837\u3002\u4F46\u5979\u80FD\u611F\u89C9\u5230\u90A3\u79CD\u7EF5\u7EF5\u5BC6\u5BC6\uFF0C\u6D53\u5F97\u5316\u4E0D\u5F00\u7684\u60C5\u612B\uFF0C\u5982\u6B64\u771F\u5B9E\uFF0C\u771F\u5B9E\u5F97\u89E6\u624B\u53EF\u53CA\uFF0C\u5979\u5F20\u5F00\u624B\u81C2\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5C0F\u6BCD\u72D7\uFF0C\u8FC7\u6765\u5427\u2026\u2026\u300D</p><p>\u592D\u592D\u8DEA\u5728\u5730\u4E0A\uFF0C\u4E00\u624B\u6276\u7740\u5DE8\u677E\uFF0C\u4E00\u624B\u6276\u7740\u9759\u989C\u7684\u9633\u5177\uFF0C\u7F13\u7F13\u6C89\u4E0B\u7C89\u81C0\u3002\u4E00\u7F15\u79C0\u53D1\u4ECE\u8033\u8FB9\u5782\u4E0B\uFF0C\u5728\u7389\u988A\u4E0A\u6447\u6765\u6447\u53BB\u3002\u5728\u5979\u9762\u524D\uFF0C\u662F\u671B\u4E0D\u5230\u5E95\u7684\u4E07\u4E08\u60AC\u5D16\u3002</p><p>\u9F9F\u5934\u6324\u5165\u809B\u4E2D\uFF0C\u592D\u592D\u53D1\u51C9\u7684\u7389\u4F53\u987F\u65F6\u70ED\u4E86\u8D77\u6765\uFF0C\u5979\u53D1\u51FA\u4E00\u58F0\u65E0\u6CD5\u538B\u6291\u7684\u5A9A\u53EB\uFF0C\u4E24\u7C92\u8FD8\u672A\u957F\u6210\u7684\u5C0F\u4E73\u5934\u786C\u786C\u7FD8\u8D77\u3002\u5979\u677E\u5F00\u5DE8\u6811\uFF0C\u4E24\u624B\u62B1\u4F4F\u7C89\u81C0\uFF0C\u65CB\u8F6C\u7740\u5411\u4E0B\u5750\u53BB\u3002\u988A\u4E0A\u4E00\u6EF4\u6CEA\u73E0\u6620\u7740\u5929\u9645\u7684\u6B8B\u6708\u7F13\u7F13\u6ED1\u5230\u9F3B\u5C16\uFF0C\u6700\u540E\u6389\u5411\u6DF1\u6E0A\u3002</p><p>\u9633\u5177\u7F13\u7F13\u8FDB\u5165\u4F53\u5185\uFF0C\u7C89\u5AE9\u7684\u5C0F\u5C41\u80A1\u8D34\u5728\u8179\u4E0A\uFF0C\u7D27\u5BC6\u5F97\u6CA1\u6709\u4E00\u4E1D\u7F1D\u9699\u3002\u9759\u989C\u63FD\u4F4F\u592D\u592D\u7684\u80A9\u5934\uFF0C\u62E5\u7740\u5979\u9999\u5AE9\u7684\u8EAB\u5B50\uFF0C\u7528\u820C\u5C16\u6311\u5F04\u7740\u5979\u60AC\u7740\u660E\u73E0\u7684\u8033\u5782\u3002\u592D\u592D\u661F\u7738\u534A\u95ED\uFF0C\u547B\u541F\u7740\u626C\u8D77\u8138\uFF0C\u4E0E\u9759\u989C\u4EA4\u9888\u7F20\u7EF5\u3002\u9759\u989C\u82AC\u82B3\u7684\u5507\u74E3\u5728\u5979\u9888\u4E0A\u3001\u816E\u4E0A\u8F7B\u67D4\u64E6\u8FC7\uFF0C\u543B\u53BB\u5979\u7684\u6CEA\u75D5\uFF0C\u6700\u540E\u4F38\u51FA\u820C\u5C16\uFF0C\u5728\u5979\u5507\u89D2\u8214\u8210\u7740\u4F38\u4E86\u8FDB\u53BB\u3002</p><p>\u592D\u592D\u95ED\u4E0A\u773C\uFF0C\u67D4\u987A\u5730\u62AC\u8D77\u7389\u988C\uFF0C\u8273\u7EA2\u7684\u5C0F\u5634\u5438\u542E\u7740\u9759\u989C\u7684\u5507\u74E3\u9999\u820C\u3002\u9759\u989C\u4E00\u8FB9\u6E29\u5B58\u5730\u633A\u52A8\u4E0B\u8179\uFF0C\u4E00\u8FB9\u6469\u6332\u7740\u592D\u592D\u5149\u6D01\u7684\u808C\u80A4\uFF0C\u6307\u5C16\u5728\u5979\u7C89\u7EA2\u7684\u4E73\u6655\u4E0A\u6765\u56DE\u6311\u9017\u3002</p><p>\u592D\u592D\u54BD\u4E0B\u9999\u553E\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u592D\u592D\u7684\u5976\u5B50\u592A\u5C0F\u4E86\u2026\u2026\u300D</p><p>\u9759\u989C\u5B89\u6170\u9053\uFF1A\u300C\u8FD8\u597D\u5566\uFF0C\u4EE5\u540E\u4F1A\u6162\u6162\u957F\u5927\u7684\u3002\u300D\u8BF4\u7740\u6258\u4F4F\u592D\u592D\u7684\u819D\u5F2F\uFF0C\u62E5\u7740\u5979\u7684\u8EAB\u5B50\u8F7B\u8F7B\u5957\u5F04\u3002</p><p>\u300C\u592D\u592D\u4F1A\u8BA9\u59D0\u59D0\u6EE1\u610F\u7684\u2026\u2026\u300D\u8BF4\u7740\u592D\u592D\u6781\u529B\u633A\u8D77\u80F8\u4E73\uFF0C\u597D\u8BA9\u5979\u73A9\u5F97\u5F00\u5FC3\u4E00\u4E9B\u3002</p><p>\u82CD\u7FE0\u7684\u5DE8\u677E\u4E0B\uFF0C\u4E24\u5177\u767D\u767D\u7684\u8EAB\u5B50\u6E29\u67D4\u5730\u7F20\u7EF5\u5728\u4E00\u8D77\u3002\u540E\u9762\u7684\u5C11\u5973\u5750\u5728\u866F\u5C48\u7684\u6811\u6839\u4E0A\uFF0C\u8EAB\u524D\u4E00\u5177\u7C89\u5AE9\u7684\u7389\u4F53\u4E0A\u4E0B\u8D77\u843D\uFF0C\u5957\u5F04\u7740\u96EA\u81C0\u4E0B\u90A3\u6839\u8840\u7EA2\u7684\u8089\u68D2\u3002\u5979\u5927\u5F20\u7684\u53CC\u817F\u95F4\uFF0C\u4E00\u6761\u8F6F\u8F6F\u7684\u5C0F\u8089\u68D2\u5E26\u7740\u51FA\u5947\u7684\u767D\u5AE9\uFF0C\u5728\u8179\u4E0B\u6643\u6765\u6643\u53BB\u3002</p><p>\u9759\u989C\u63E1\u4F4F\u592D\u592D\u7684\u5C0F\u8089\u68D2\u7B11\u9053\uFF1A\u300C\u597D\u8F6F\u5462\u3002\u300D</p><p>\u592D\u592D\u8EAB\u5B50\u4E00\u50F5\uFF0C\u300C\u597D\u59D0\u59D0\uFF0C\u4F60\u8BA8\u538C\u5C0F\u6BCD\u72D7\u90A3\u4E2A\u4E1C\u897F\u5417\uFF1F\u300D</p><p>\u300C\u600E\u4E48\u4F1A\u5462\uFF1F\u300D\u9759\u989C\u634B\u52A8\u7740\u8089\u68D2\u8F7B\u58F0\u9053\u3002</p><p>\u9759\u989C\u624B\u4E0A\u4E00\u70ED\uFF0C\u5374\u662F\u592D\u592D\u6EF4\u4E0B\u7684\u6CEA\u73E0\u3002\u300C\u90FD\u662F\u6211\u5A18\u2026\u2026\u592D\u592D\u4E00\u751F\u4E0B\u6765\u5C31\u88AB\u5272\u4E86\u777E\u4E38\u3002\u4ED6\u4EEC\u90FD\u770B\u4E0D\u8D77\u592D\u592D\uFF0C\u8BF4\u6211\u4E0D\u662F\u7537\u4EBA\uFF0C\u4E5F\u4E0D\u662F\u5973\u4EBA\uFF0C\u662F\u4E2A\u8BA8\u538C\u7684\u5C0F\u5996\u602A\u2026\u2026\u597D\u59D0\u59D0\uFF0C\u53EA\u6709\u4F60\u4E0D\u5ACC\u5F03\u4EBA\u5BB6\u2026\u2026\u300D</p><p>\u300C\u59D0\u59D0\u600E\u4E48\u4F1A\u5ACC\u5F03\u4F60\u5462\uFF1F\u6211\u662F\u4F60\u5A18\uFF0C\u592D\u592D\u662F\u5A18\u7684\u5C0F\u6BCD\u72D7\u554A\u2026\u2026\u300D\u9759\u989C\u4E00\u8FB9\u5728\u5979\u8033\u65C1\u4F4E\u8BED\uFF0C\u4E00\u8FB9\u719F\u7EC3\u5730\u634B\u52A8\u7740\u5C0F\u8089\u68D2\u3002</p><p>\u592D\u592D\u9F3B\u4E2D\u53D1\u9178\uFF0C\u767D\u5AE9\u7684\u5C0F\u8089\u68D2\u5728\u9759\u989C\u624B\u4E2D\u8FC5\u901F\u575A\u633A\u8D77\u6765\u3002</p><p>\u9759\u989C\u7EA4\u8F6F\u7684\u7389\u6307\u5939\u7740\u8089\u68D2\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u60F3\u63D2\u5230\u5A18\u7684\u91CC\u8FB9\u5417\uFF1F\u300D</p><p>\u300C\u4E0D\u4E0D\u4E0D\u2026\u2026\u300D\u592D\u592D\u8FDE\u5FD9\u6447\u5934\u3002</p><p>\u300C\u4E3A\u4EC0\u4E48\u5462\uFF1F\u300D\u9759\u989C\u5927\u611F\u610F\u5916\uFF0C\u9664\u4E86\u7B2C\u4E00\u6B21\u592D\u592D\u78B0\u5230\u5979\u7684\u5C41\u773C\u513F\u4EE5\u5916\uFF0C\u5F80\u540E\u90FD\u662F\u5979\u53BB\u63D2\u592D\u592D\u7684\u5C41\u773C\u513F\uFF0C\u800C\u592D\u592D\u4E5F\u662F\u8001\u8001\u5B9E\u5B9E\u4EFB\u5979\u6765\u5E72\u3002\u5979\u80FD\u770B\u51FA\u6765\u592D\u592D\u4E00\u76F4\u60F3\u7740\u81EA\u5DF1\u7684\u5C41\u773C\u513F\uFF0C\u53EA\u662F\u4E0D\u6562\u5F00\u53E3\u3002\u4ECA\u665A\u89C1\u592D\u592D\u4F24\u5FC3\uFF0C\u9759\u989C\u624D\u60F3\u7528\u5B83\u6765\u5B89\u6170\u592D\u592D\uFF0C\u6CA1\u60F3\u5230\u592D\u592D\u5374\u4E0D\u613F\u610F\u3002</p><p>\u592D\u592D\u5C0F\u58F0\u9053\uFF1A\u300C\u4EBA\u5BB6\u662F\u5A18\u7684\u5C0F\u6BCD\u72D7\uFF0C\u53EA\u80FD\u8BA9\u5A18\u6765\u63D2\u4EBA\u5BB6\uFF0C\u5C0F\u6BCD\u72D7\u600E\u4E48\u80FD\u63D2\u5A18\u5462\uFF1F\u54EA\u6709\u90A3\u6837\u7684\u5C0F\u6BCD\u72D7\u2026\u2026\u300D</p><p>\u9759\u989C\u6014\u4E86\u4E00\u4E0B\uFF0C\u6CA1\u6709\u4E0D\u518D\u8BF4\u8BDD\uFF0C\u5979\u62CD\u4E86\u62CD\u592D\u592D\u7684\u5C0F\u5C41\u80A1\uFF0C\u592D\u592D\u7ACB\u523B\u4E56\u5DE7\u5730\u722C\u8D77\u6765\uFF0C\u8DB4\u5728\u5730\u4E0A\uFF0C\u6485\u8D77\u7C89\u5AE9\u7684\u5C0F\u5C41\u80A1\u3002\u8FD9\u662F\u6BCD\u72D7\u7684\u6807\u51C6\u59FF\u52BF\uFF0C\u8868\u793A\u7740\u5B8C\u5168\u7684\u81E3\u670D\u548C\u670D\u4ECE\uFF0C\u628A\u8EAB\u4F53\u4E0D\u8BBE\u9632\u5730\u732E\u7ED9\u4E3B\u4EBA\uFF0C\u7531\u4E3B\u4EBA\u4EFB\u610F\u4F7F\u7528\u3002</p><p>\u9759\u989C\u63B0\u4F4F\u5979\u7684\u81C0\u8089\uFF0C\u8170\u8EAB\u4E00\u633A\uFF0C\u517D\u6839\u7B14\u76F4\u6345\u5165\u5C0F\u6BCD\u72D7\u7EA2\u5AE9\u7684\u809B\u6D1E\u91CC\u3002\u592D\u592D\u300C\u5440\u300D\u7684\u53EB\u4E86\u4E00\u58F0\uFF0C\u80EF\u4E0B\u7684\u5C0F\u8089\u68D2\u786C\u786C\u7FD8\u8D77\u3002\u5979\u4E0D\u77E5\u9053\u4E3B\u4EBA\u7684\u8089\u68D2\u4E3A\u4EC0\u4E48\u4F1A\u6709\u7528\u8FD9\u79CD\u9B54\u529B\uFF0C\u5355\u5355\u53EA\u662F\u63D2\u5165\uFF0C\u4FBF\u8BA9\u5979\u5174\u594B\u5F97\u96BE\u4EE5\u81EA\u5DF2\u3002</p><p>\u9759\u989C\u6162\u6162\u633A\u52A8\u7740\u8170\u8EAB\uFF0C\u7531\u4E8E\u4E0D\u9700\u8981\u63A2\u5165\u82B1\u5FC3\u91C7\u8865\u9634\u7CBE\uFF0C\u5979\u7684\u517D\u6839\u7B2C\u4E00\u6B21\u5B8C\u5168\u81A8\u80C0\u8D77\u6765\uFF0C\u8D85\u8FC7\u4E24\u5BF8\u7684\u76F4\u5F84\uFF0C\u5DF2\u7ECF\u8FBE\u5230\u4E86\u592D\u592D\u6240\u80FD\u627F\u53D7\u7684\u6781\u9650\uFF0C\u5979\u6781\u529B\u4E3E\u8D77\u96EA\u81C0\uFF0C\u7EC6\u5BC6\u7684\u83CA\u7EB9\u5B8C\u5168\u5C55\u5F00\uFF0C\u53D8\u6210\u4E00\u4E2A\u6D51\u5706\u7684\u7EA2\u5708\uFF0C\u968F\u7740\u8089\u68D2\u7684\u8FDB\u51FA\u4E0D\u4F4F\u9F13\u80C0\u6536\u7F29\u3002</p><p>\u9759\u989C\u62B1\u7740\u592D\u592D\u7684\u5C41\u80A1\u5E72\u4E86\u7247\u523B\uFF0C\u53C8\u6362\u4E86\u59FF\u52BF\uFF0C\u62C9\u8D77\u592D\u592D\u4E00\u6761\u7C89\u817F\uFF0C\u4ECE\u5979\u53CC\u817F\u95F4\u659C\u8EAB\u523A\u5165\u3002\u4E24\u4EBA\u7389\u817F\u4EA4\u53C9\uFF0C\u5F77\u4F5B\u4E24\u67C4\u7389\u526A\u54AC\u5728\u4E00\u8D77\uFF0C\u8D34\u7740\u5F7C\u6B64\u6ED1\u817B\u7684\u817F\u6839\u7528\u529B\u78E8\u64E6\u3002</p><p>\u592D\u592D\u53EB\u58F0\u8D8A\u6765\u8D8A\u54CD\uFF0C\u5979\u7389\u817F\u7EF7\u7D27\uFF0C\u7FD8\u5728\u9759\u989C\u7684\u80EF\u95F4\uFF0C\u8138\u7EA2\u5F97\u5F77\u4F5B\u8981\u6EF4\u4E0B\u80ED\u8102\u3002\u5FFD\u7136\u95F4\uFF0C\u4E00\u53EA\u624B\u63E1\u4F4F\u5979\u7684\u5C0F\u8089\u68D2\uFF0C\u671D\u7D27\u8D34\u7684\u7C89\u81C0\u95F4\u9001\u53BB\u3002\u592D\u592D\u53EA\u89C9\u8089\u68D2\u9876\u7AEF\u4E00\u6ED1\uFF0C\u94BB\u8FDB\u4E00\u4E2A\u6E7F\u70ED\u7D27\u5BC6\u7684\u7F8E\u7A74\u4E2D\uFF0C\u63A5\u7740\uFF0C\u871C\u8089\u7F20\u7D27\u5979\u7684\u8089\u68D2\uFF0C\u50CF\u5C0F\u5634\u4E00\u6837\u542E\u5438\u8D77\u6765\u3002</p><p>\u9759\u989C\u9633\u5177\u6DF1\u6DF1\u63D2\u5728\u592D\u592D\u809B\u4E2D\uFF0C\u4E00\u624B\u6276\u7740\u5979\u7684\u5C0F\u8089\u68D2\u63D2\u5728\u81EA\u5DF1\u81C0\u95F4\u3002\u4E24\u53EA\u767D\u817B\u7684\u7C89\u81C0\u4EA4\u9519\u7740\u7D27\u7D27\u538B\u5728\u4E00\u8D77\uFF0C\u541E\u6CA1\u4E86\u5F7C\u6B64\u7684\u9633\u5177\u3002\u96EA\u81C0\u5FAE\u5206\uFF0C\u9732\u51FA\u4E24\u6839\u5E73\u884C\u7684\u8089\u68D2\uFF0C\u63A5\u7740\u53C8\u84E6\u7136\u5408\u7D27\u3002\u592D\u592D\u773C\u4E2D\u8499\u4E0A\u4E00\u5C42\u6C34\u96FE\uFF0C\u7EA2\u5507\u50F5\u4F4F\u53D1\u4E0D\u51FA\u4E00\u4E1D\u58F0\u97F3\uFF0C\u8089\u4F53\u65E0\u4E0E\u4F26\u6BD4\u7684\u5FEB\u611F\u6F6E\u6C34\u822C\u6D8C\u6765\uFF0C\u51B2\u8D70\u4E86\u4E00\u5207\u3002</p><p>\u300C\u5A18\u2014\u2014\u300D\u592D\u592D\u557C\u54ED\u7740\u53EB\u4E86\u4E00\u58F0\uFF0C\u5C41\u773C\u513F\u98A4\u6296\u7740\u5939\u7D27\u8089\u68D2\uFF0C\u9633\u5177\u5728\u9759\u989C\u7F8E\u5999\u7684\u83CA\u809B\u91CC\u5267\u70C8\u5730\u55B7\u5C04\u8D77\u6765\u3002</p><p>\u826F\u4E45\uFF0C\u592D\u592D\u7684\u55B7\u5C04\u624D\u6E10\u6E10\u505C\u6B62\u3002\u9759\u989C\u7B11\u7740\u62CD\u4E86\u62CD\u5979\u7684\u8138\u988A\uFF0C\u62AC\u8D77\u8EAB\u5B50\u3002\u96EA\u81C0\u5206\u5F00\uFF0C\u8D64\u7EA2\u7684\u9633\u5177\u4F9D\u7136\u8840\u7EA2\u575A\u633A\uFF0C\u767D\u5AE9\u7684\u5C0F\u8089\u68D2\u5374\u8F6F\u8F6F\u7684\uFF0C\u5E26\u51FA\u4E00\u6EE9\u86CB\u6E05\u4F3C\u7684\u9ECF\u6DB2\u3002</p><p>\u9759\u989C\u53D6\u51FA\u4E1D\u5DFE\uFF0C\u6B63\u5F85\u62B9\u62ED\uFF0C\u592D\u592D\u5DF2\u7ECF\u4FEF\u4E0B\u81FB\u9996\uFF0C\u7528\u4E00\u79CD\u8FD1\u4E4E\u5D07\u656C\u7684\u8654\u8BDA\uFF0C\u671D\u5979\u81C0\u95F4\u543B\u53BB\u3002\u5979\u5148\u8214\u51C0\u4E86\u809B\u6D1E\u5468\u56F4\u7684\u7CBE\u6DB2\uFF0C\u7136\u540E\u628A\u7EA2\u5507\u8D34\u5728\u809B\u857E\u4E0A\uFF0C\u4F38\u51FA\u9999\u820C\uFF0C\u5438\u542E\u7740\u76F4\u80A0\u5185\u7684\u6B8B\u7CBE\u3002</p><p>\u9759\u989C\u4E00\u624B\u652F\u7740\u67D4\u9888\uFF0C\u644A\u5F00\u7389\u4F53\uFF0C\u601C\u60DC\u800C\u53C8\u54C0\u4F24\u5730\u671B\u7740\u9762\u524D\u7684\u5C11\u5973\u3002\u7B49\u8214\u51C0\u6700\u540E\u4E00\u70B9\u7CBE\u6DB2\uFF0C\u592D\u592D\u626C\u8D77\u8138\uFF0C\u7F9E\u6DA9\u5730\u8BF4\uFF1A\u300C\u5C0F\u6BCD\u72D7\u4E0D\u662F\u6709\u610F\u5F04\u810F\u59D0\u59D0\u7684\u2026\u2026\u300D</p><p>\u591C\u98CE\u62C2\u8FC7\uFF0C\u968F\u98CE\u98D8\u6765\u4E00\u58F0\u5E7D\u5E7D\u7684\u8F7B\u53F9\u3002</p><p>\u300C\u8C01\uFF01\uFF1F\u300D\u9759\u989C\u5A07\u8EAF\u5E94\u58F0\u5F39\u8D77\uFF0C\u6D88\u5931\u5728\u679D\u53F6\u6DF1\u5904\u3002\u592D\u592D\u4F0F\u5728\u5730\u4E0A\uFF0C\u671B\u7740\u5979\u6D88\u5931\u7684\u5730\u65B9\u53D1\u6123\uFF0C\u592D\u592D\u6CA1\u60F3\u5230\u5979\u7684\u8F7B\u529F\u5C45\u7136\u8FD9\u4E48\u597D\uFF0C\u597D\u50CF\u6BD4\u4E00\u4E2A\u6708\u524D\u53C8\u5F3A\u4E86\u8BB8\u591A\u3002</p><p>\u7247\u523B\u540E\uFF0C\u9759\u989C\u6D01\u767D\u7684\u7389\u4F53\u4ECE\u679D\u4E0A\u6ED1\u843D\uFF0C\u9762\u8272\u51DD\u91CD\u5730\u8D70\u5230\u677E\u6811\u4E0B\u3002\u592D\u592D\u95EE\u9053\uFF1A\u300C\u6709\u4EBA\u5417\uFF1F\u300D</p><p>\u9759\u989C\u6447\u4E86\u6447\u5934\u3002</p><p>\u592D\u592D\u5C0F\u5FC3\u5730\u95EE\u9053\uFF1A\u300C\u59D0\u59D0\u662F\u4E0D\u662F\u542C\u9519\u4E86\uFF1F\u300D</p><p>\u9759\u989C\u6CA1\u6709\u56DE\u7B54\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E24\u4EBA\u5728\u5C71\u6797\u4E2D\u8FC7\u4E86\u4E00\u5BBF\uFF0C\u76F4\u5230\u5929\u8272\u5927\u4EAE\uFF0C\u624D\u633D\u7740\u624B\u56DE\u5230\u661F\u6708\u6E56\u3002\u4F17\u5973\u8FD8\u8BB0\u5F97\u592D\u62A4\u6CD5\u5F53\u65F6\u90A3\u4E00\u8FDE\u4E32\u547D\u4EE4\uFF0C\u50CF\u662F\u8981\u628A\u8FD9\u4E2A\u7F8E\u8C8C\u5973\u5B50\u751F\u5403\u4E86\u4E00\u822C\uFF0C\u53EF\u73B0\u5728\u4E24\u4EBA\u4E0D\u4EC5\u4EB2\u5BC6\u5F97\u5F77\u4F5B\u59D0\u59B9\uFF0C\u592D\u62A4\u6CD5\u4E0D\u65F6\u671B\u7740\u5979\u7684\u773C\u795E\uFF0C\u7ADF\u4F3C\u4E4E\u6709\u51E0\u5206\u8BA8\u597D\u3002</p><p>\u592D\u592D\u4E0D\u7BA1\u65C1\u4EBA\u600E\u4E48\u770B\uFF0C\u53EA\u4E00\u5473\u817B\u7740\u9759\u989C\u3002\u5C0F\u516C\u4E3B\u4E0D\u5728\u5BAB\u4E2D\uFF0C\u767D\u6C0F\u59D0\u59B9\u53C8\u523B\u610F\u907F\u5728\u5916\u9762\uFF0C\u53F6\u62A4\u6CD5\u4E07\u4E8B\u4E0D\u95EE\uFF0C\u661F\u6708\u6E56\u5730\u4F4D\u6700\u9AD8\u7684\u5C31\u662F\u5979\u4E86\u3002</p><p>\u5E2E\u9759\u989C\u6D17\u5B8C\u8EAB\u5B50\uFF0C\u592D\u592D\u8DEA\u5728\u5979\u817F\u95F4\uFF0C\u7528\u5507\u820C\u6E05\u7406\u5979\u7684\u9634\u6237\u3002\u9759\u989C\u672C\u4E0D\u60F3\u5979\u8FD9\u6837\u670D\u4F8D\uFF0C\u4F46\u592D\u592D\u575A\u6301\u8BF4\u5C31\u662F\u6700\u67D4\u8F6F\u7684\u6BDB\u5DFE\uFF0C\u4E5F\u4F1A\u78E8\u7834\u8FD9\u4E48\u7EC6\u5AE9\u7684\u808C\u80A4\u3002\u300C\u59D0\u59D0\u7684\u9634\u6237\u597D\u7F8E\u2026\u2026\u59D0\u59D0\u7684\u8EAB\u5B50\u5DF2\u7ECF\u591F\u5A07\u5AE9\u4E86\uFF0C\u9634\u6237\u6BD4\u8EAB\u5B50\u8FD8\u5AE9\u5462\u2026\u2026\u300D</p><p>\u592D\u592D\u65E0\u610F\u4E2D\u7684\u8BDD\u8BED\uFF0C\u5374\u4F7F\u9759\u989C\u5FC3\u5934\u6697\u9707\uFF0C\u9759\u83BA\u59B9\u59B9\u6BD4\u81EA\u5DF1\u5C0F\u4E86\u4E94\u5C81\uFF0C\u4E34\u6B7B\u65F6\u8FD8\u662F\u4E2A\u51B0\u6E05\u7389\u6D01\u7684\u597D\u5973\u5B69\uFF0C\u5979\u7EAF\u6D01\u7684\u9634\u6237\u653E\u5728\u81EA\u5DF1\u6DEB\u8D31\u7684\u8EAB\u4F53\u4E0A\uFF0C\u81EA\u7136\u663E\u5F97\u5206\u5916\u5A07\u5AE9\u3002</p><p>\u8FD9\u672C\u6765\u662F\u5979\u51C6\u5907\u732E\u7ED9\u81EA\u5DF1\uFF0C\u5C11\u5973\u6700\u4E3A\u73CD\u8D35\u7684\u793C\u7269\uFF0C\u7136\u800C\u73B0\u5728\uFF0C\u5979\u53C8\u8981\u628A\u8FD9\u4EFD\u793C\u7269\u539F\u5C01\u4E0D\u52A8\u5730\u9001\u7ED9\u4EC7\u4EBA\u7684\u5973\u513F\u3002</p><p>\u9759\u989C\u7AD9\u8D77\u8EAB\u6765\uFF0C\u7389\u8DB3\u8E0F\u788E\u4E86\u6C60\u4E2D\u7684\u73E0\u5F71\u3002\u5979\u63E1\u7740\u6E7F\u6DCB\u6DCB\u7684\u957F\u53D1\uFF0C\u7528\u624B\u6307\u8F7B\u8F7B\u68B3\u7406\u3002\u7389\u6307\u62B9\u8FC7\uFF0C\u6C34\u8FF9\u6084\u7136\u6D88\u5931\uFF0C\u79C0\u53D1\u987F\u65F6\u53D8\u5F97\u4E1D\u7EF8\u4E00\u6837\u67D4\u987A\u3002</p><p>\u300C\u59D0\u59D0\u7684\u529F\u592B\u771F\u597D\u5462\u3002\u300D\u592D\u592D\u7531\u8877\u5730\u8D5E\u9053\u3002</p><p>\u9759\u989C\u6D45\u7B11\u9053\uFF1A\u300C\u6BD4\u8D77\u592D\u62A4\u6CD5\u7684\u9ED1\u715E\u638C\u8FD8\u5DEE\u5F97\u8FDC\u5462\u3002\u300D</p><p>\u592D\u592D\u4E0D\u597D\u610F\u601D\u5730\u7B11\u4E86\u8D77\u6765\uFF0C\u300C\u4EBA\u5BB6\u53EA\u662F\u5728\u516C\u4E3B\u5B66\u827A\u7684\u65F6\u5019\u8DDF\u7740\u7EC3\u4E86\u51E0\u65E5\uFF0C\u600E\u4E48\u80FD\u8DDF\u59D0\u59D0\u6BD4\u5462\u3002\u300D</p><p>\u300C\u516C\u4E3B\u7684\u6B66\u529F\u5F88\u597D\u5417\uFF1F\u662F\u8DDF\u8C01\u5B66\u7684\uFF1F\u300D</p><p>\u300C\u6709\u7239\u7239\u4EB2\u81EA\u6559\u5979\uFF0C\u80FD\u4E0D\u597D\u5417\uFF1F\u300D</p><p>\u300C\u5979\u7239\u7239\uFF1F\u7687\u4E0A\u5417\uFF1F\u300D</p><p>\u592D\u592D\u6014\u4E86\u4E00\u4E0B\uFF0C\u5979\u4E5F\u4E0D\u8BB0\u5F97\u544A\u8BC9\u8FC7\u9759\u989C\uFF0C\u6155\u5BB9\u9F99\u7684\u8EAB\u4EFD\uFF0C\u4F46\u8FD9\u5728\u6559\u5185\u4E5F\u7B97\u4E0D\u5F97\u592A\u5927\u7684\u79D8\u5BC6\uFF0C\u4E8E\u662F\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u9759\u989C\u9759\u9759\u51DD\u89C6\u592D\u592D\u534A\u664C\uFF0C\u300C\u4F60\u7239\u7239\u5462\uFF1F\u300D</p><p>\u592D\u592D\u677F\u7740\u8138\u9053\uFF1A\u300C\u6211\u6CA1\u6709\u7239\u7239\u3002\u300D</p><p>\u9759\u989C\u7B11\u4E86\u7B11\uFF0C\u6CA1\u6709\u518D\u95EE\uFF0C\u53EA\u9053\uFF1A\u300C\u6211\u60F3\u89C1\u89C1\u4F60\u5A18\u3002\u300D</p><p>\u592D\u592D\u773C\u775B\u5FAE\u5FAE\u95EA\u4EAE\uFF0C\u300C\u5A18\uFF0C\u4F60\u8981\u5E72\u4EBA\u5BB6\u4EB2\u5A18\u5417\uFF1F\u300D</p><p>\u592D\u592D\u7ED9\u9759\u989C\u6253\u4E86\u4E2A\u624B\u52BF\uFF0C\u8BA9\u5979\u7B49\u5728\u5916\u9762\uFF0C\u81EA\u5DF1\u63A8\u95E8\u5165\u5185\u3002\u521A\u8FDB\u95E8\uFF0C\u5979\u7684\u58F0\u97F3\u5C31\u8F6F\u4E86\u4E0B\u6765\uFF0C\u300C\u53F6\u62A4\u6CD5\uFF0C\u60A8\u4E5F\u5728\u8FD9\u91CC\u554A\u3002\u300D</p><p>\u9759\u989C\u6084\u6084\u671B\u53BB\uFF0C\u53EA\u89C1\u53F6\u884C\u5357\u95ED\u7740\u773C\u5750\u5728\u6905\u4E0A\uFF0C\u7528\u4E00\u6839\u624B\u6307\u642D\u7740\u8427\u4F5B\u5974\u7684\u7693\u8155\uFF0C\u96EA\u767D\u7684\u957F\u987B\u4E00\u52A8\u4E0D\u52A8\u3002\u8427\u4F5B\u5974\u67D4\u987A\u5730\u547C\u5438\u7740\uFF0C\u7F8E\u76EE\u6CE2\u5149\u6D41\u8F6C\uFF0C\u542B\u7B11\u671B\u7740\u513F\u5B50\u3002</p><p>\u5728\u5979\u69BB\u65C1\uFF0C\u98CE\u665A\u534E\u4F0F\u5728\u4E00\u5757\u957F\u7ED2\u6BE1\u6BEF\u4E0A\uFF0C\u6B63\u4F38\u7740\u820C\u5934\uFF0C\u53BB\u8214\u53F6\u884C\u5357\u811A\u4E0A\u7684\u978B\u5B50\u3002</p><p>\u534A\u664C\uFF0C\u53F6\u884C\u5357\u8D77\u8EAB\u8896\u4E86\u836F\u5323\uFF0C\u4E00\u8A00\u4E0D\u53D1\u5730\u626C\u957F\u800C\u53BB\u3002\u9759\u989C\u8FDE\u5FD9\u8E72\u8EAB\u884C\u793C\uFF0C\u773C\u89D2\u779F\u7740\u53F6\u884C\u5357\u67AF\u7626\u7684\u624B\u6307\u3002\u4ED6\u4E0E\u4E49\u6BCD\u662F\u5B8C\u5168\u4E0D\u540C\u7684\u4E24\u7C7B\u4EBA\uFF0C\u4F46\u4ED6\u4EEC\u6709\u7740\u76F8\u540C\u7684\u773C\u775B\uFF0C\u4F3C\u4E4E\u80FD\u770B\u900F\u4E00\u5207\u3002\u88AB\u4ED6\u7684\u76EE\u5149\u626B\u5230\uFF0C\u9759\u989C\u603B\u4F1A\u611F\u89C9\u5230\u4E00\u79CD\u65E0\u5F62\u7684\u538B\u529B\uFF0C\u8BA9\u5979\u7684\u5FC3\u8DF3\u4E0D\u7531\u81EA\u4E3B\u5730\u7D0A\u4E71\u8D77\u6765\u3002</p><p>\u300C\u592D\u513F\uFF0C\u4F60\u6765\u4E86\u3002\u300D\u8427\u4F5B\u5974\u7B11\u7740\u67D4\u58F0\u8BF4\u9053\u3002</p><p>\u300C\u6211\u8981\u5E72\u4F60\u3002\u300D\u592D\u592D\u76F4\u63A5\u8BF4\u9053\u3002</p><p>\u8427\u4F5B\u5974\u7389\u8138\u4E00\u7EA2\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u5A18\u8FD8\u6CA1\u6709\u5403\u65E9\u996D\u5462\u2026\u2026\u300D</p><p>\u300C\u6EDA\u5F00\u3002\u300D\u592D\u592D\u628A\u98CE\u665A\u534E\u64B5\u5230\u4E00\u65C1\uFF0C\u4F38\u624B\u6380\u5F00\u88AB\u8925\uFF0C\u5C06\u6BCD\u4EB2\u7684\u8863\u94AE\u4E00\u4E00\u89E3\u5F00\u3002\u8F6C\u773C\u95F4\u8427\u4F5B\u5974\u8863\u886B\u90FD\u88AB\u5265\u5C3D\uFF0C\u53EA\u5269\u4E0B\u80A1\u95F4\u7684\u5C3F\u5E03\u3002\u7B49\u592D\u592D\u89E3\u5F00\u5C3F\u5E03\uFF0C\u65E0\u529B\u963B\u6B62\u7684\u8427\u4F5B\u5974\u53EA\u597D\u8BF4\u9053\uFF1A\u300C\u4E0D\u8981\u78B0\u5A18\u524D\u9762\uFF0C\u4F60\u7239\u7239\u4F1A\u4E0D\u9AD8\u5174\u7684\u2026\u2026\u300D</p><p>\u592D\u592D\u51B7\u51B0\u51B0\u7684\u5C0F\u8138\u9732\u51FA\u4E00\u4E1D\u72E1\u9EE0\u7684\u7B11\u610F\uFF0C\u300C\u8D31\u8D27\uFF0C\u4ECA\u5929\u4F60\u7684\u5C41\u773C\u513F\u4F1A\u4E50\u75AF\u7684\u3002\u300D</p><p>\u98CE\u665A\u534E\u5367\u5728\u5899\u89D2\uFF0C\u7528\u6B8B\u7F3A\u7684\u8098\u81C2\u63C9\u5F04\u7740\u81EA\u5DF1\u7684\u4E73\u5C16\u3002\u9759\u989C\u8FD9\u624D\u6CE8\u610F\u5230\uFF0C\u5979\u7684\u53F3\u4E73\u7279\u522B\u677E\u8F6F\uFF0C\u7FFB\u5F00\u7684\u4E73\u6655\u4E0B\u9732\u51FA\u4E00\u4E2A\u53EF\u5BB9\u9633\u5177\u8FDB\u51FA\u7684\u5706\u5B54\u3002\u9759\u989C\u4E0D\u7531\u6253\u4E86\u4E2A\u5BD2\u5664\uFF0C\u4EE5\u524D\u4E0E\u7537\u4EBA\u4EEC\u5468\u65CB\u65F6\uFF0C\u5C11\u4E0D\u4E86\u88AB\u4EBA\u6293\u7740\u4E73\u623F\u634F\u5F04\uFF0C\u6BCF\u6B21\u5979\u662F\u90FD\u5FCD\u75DB\u5F3A\u989C\u6B22\u7B11\u3002\u50CF\u8FD9\u6837\u6363\u7A7F\u4E73\u623F\u4F1A\u662F\u4EC0\u4E48\u6837\u7684\u75DB\u82E6\uFF0C\u5979\u8FDE\u60F3\u90FD\u4E0D\u6562\u60F3\u3002</p><p>\u592D\u592D\u628A\u6BCD\u4EB2\u62B1\u5230\u4E00\u5F20\u5947\u602A\u7684\u6728\u67B6\u4E0A\uFF0C\u90A3\u6728\u67B6\u4E00\u770B\u4FBF\u662F\u4E3A\u8427\u4F5B\u5974\u7279\u5236\u7684\uFF0C\u65E0\u8BBA\u5BBD\u7A84\u5927\u5C0F\u9AD8\u4F4E\u90FD\u6070\u5230\u597D\u5904\u3002\u7528\u76AE\u5E26\u56FA\u5B9A\u8098\u819D\u4E4B\u540E\uFF0C\u7F8E\u5987\u5C31\u50CF\u51CC\u7A7A\u8DEA\u5728\u7A7A\u65F7\u7684\u77F3\u5BA4\u4E2D\uFF0C\u9AD8\u7FD8\u7684\u96EA\u81C0\u767D\u751F\u751F\u4E3E\u5728\u534A\u7A7A\uFF0C\u81C0\u8089\u5FAE\u5206\uFF0C\u9732\u51FA\u4E2D\u95F4\u4E00\u6735\u7EA2\u8273\u8273\u7684\u809B\u83CA\u3002</p>',102);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
