import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_40" tabindex="-1"><a class="header-anchor" href="#_40" aria-hidden="true">#</a> 40</h1><p>\u592D\u592D\u5C06\u4E00\u6761\u4E1D\u5DFE\u7ED1\u5728\u6BCD\u4EB2\u773C\u4E0A\uFF0C\u7136\u540E\u5728\u5979\u5927\u5C41\u80A1\u4E0A\u6E05\u8106\u5730\u62CD\u4E86\u4E00\u8BB0\uFF0C\u300C\u5A18\uFF0C\u5B69\u513F\u8981\u8FDB\u53BB\u4E86\u3002\u300D</p><p>\u8427\u4F5B\u5974\u83CA\u809B\u7ACB\u523B\u8815\u52A8\u7740\u6536\u7D27\uFF0C\u7FFB\u51FA\u65F6\uFF0C\u7EA2\u5AE9\u7684\u809B\u857E\u5DF2\u7ECF\u53D8\u5F97\u6E7F\u6DA6\u3002\u4E00\u6839\u706B\u70ED\u7684\u8089\u68D2\u6491\u5F00\u809B\u6D1E\uFF0C\u53FD\u549B\u4E00\u58F0\u4FBF\u6345\u5165\u534A\u5C3A\uFF0C\u9F9F\u5934\u786C\u786C\u9876\u5728\u80A0\u58C1\u4E0A\uFF0C\u6BD4\u5979\u60F3\u50CF\u4E2D\u8981\u5927\u4E86\u8BB8\u591A\u3002\u8427\u4F5B\u5974\u60CA\u53EB\u4E00\u58F0\uFF0C\u300C\u592D\u513F\uFF01\u300D</p><p>\u592D\u592D\u5728\u8EAB\u540E\u7B11\u9053\uFF1A\u300C\u8212\u670D\u5417\uFF1F\u300D</p><p>\u8089\u68D2\u62D4\u51FA\u5C11\u8BB8\uFF0C\u7136\u540E\u518D\u6B21\u7528\u529B\u633A\u5165\uFF0C\u8FD9\u56DE\u4E0D\u4F46\u8FDB\u5F97\u66F4\u6DF1\uFF0C\u800C\u4E14\u8FD8\u6709\u4E2A\u7C97\u5927\u7684\u786C\u7269\u9876\u5728\u809B\u857E\u4E0A\uFF0C\u4ECE\u90A3\u79CD\u538B\u8FEB\u611F\u4E2D\uFF0C\u8427\u4F5B\u5974\u610F\u8BC6\u5230\u786C\u7269\u7684\u76F4\u5F84\u8D85\u8FC7\u4E86\u4E09\u5BF8\uFF0C\u6BD4\u4E00\u822C\u5973\u5B50\u7684\u7C89\u62F3\u8FD8\u8981\u5927\u4E9B\u3002\u5979\u538B\u6291\u4F4F\u5FC3\u5934\u7684\u6050\u614C\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u592D\u513F\uFF0C\u4F60\u4E0D\u8981\u4E71\u6765\uFF0C\u5A18\u7684\u8EAB\u5B50\u2026\u2026\u5A18\u7684\u8EAB\u5B50\u2026\u2026\u300D</p><p>\u4ECE\u8FDB\u5165\u4F53\u5185\u90A3\u90E8\u5206\u7684\u89E6\u611F\u548C\u70ED\u5EA6\u4E0A\uFF0C\u8427\u4F5B\u5974\u80FD\u89C9\u67E5\u51FA\u90A3\u662F\u4E00\u6839\u7537\u4EBA\u7684\u9633\u5177\uFF0C\u867D\u7136\u4E0D\u53CA\u9F99\u54E5\u54E5\u7684\u7C97\u957F\u72DE\u5389\uFF0C\u4F46\u7EDD\u4E0D\u662F\u592D\u513F\u6240\u80FD\u5177\u6709\u7684\u5C3A\u5BF8\uFF0C\u53EF\u8427\u4F5B\u5974\u600E\u4E48\u4E5F\u4E0D\u4F1A\u76F8\u4FE1\uFF0C\u513F\u5B50\u7ADF\u7136\u4F1A\u5E26\u4EBA\u5978\u6DEB\u81EA\u5DF1\u7684\u4EB2\u751F\u6BCD\u4EB2\u3002</p><p>\u300C\u592D\u592D\u77E5\u9053\uFF0C\u5A18\u662F\u4E0D\u80FD\u968F\u4FBF\u8BA9\u7537\u4EBA\u78B0\u7684\u3002\u8D31\u6BCD\u72D7\uFF0C\u4F60\u8BF4\u662F\u5417\uFF1F\u300D</p><p>\u98CE\u665A\u534E\u6C6A\u4E86\u4E00\u58F0\u3002\u8427\u4F5B\u5974\u5FC3\u4E0B\u7565\u89C9\u5BBD\u6170\uFF0C\u5012\u4E0D\u662F\u56E0\u4E3A\u513F\u5B50\u7684\u4FDD\u8BC1\uFF0C\u800C\u662F\u98CE\u665A\u534E\u662F\u88AB\u8BAD\u7EC3\u6210\u6DEB\u517D\u7684\u6BCD\u72D7\uFF0C\u4E00\u65E6\u95FB\u5230\u7537\u4EBA\u6027\u5668\u7684\u6C14\u5473\u5C31\u4F1A\u53D1\u60C5\uFF0C\u8FD9\u6837\u770B\u6765\uFF0C\u623F\u91CC\u5E76\u6CA1\u6709\u7537\u4EBA\u3002\u5979\u5598\u4E86\u53E3\u6C14\uFF0C\u58F0\u97F3\u7F9E\u6DA9\u8D77\u6765\uFF0C\u300C\u592D\u513F\uFF0C\u4F60\u5728\u62FF\u4EC0\u4E48\u6363\u5A18\u7684\u5C41\u773C\u513F\uFF1F\u300D</p><p>\u592D\u592D\u51B7\u51B7\u9053\uFF1A\u300C\u8D31\u8D27\uFF0C\u5C41\u773C\u513F\u8212\u670D\u5417\uFF1F\u300D</p><p>\u300C\u8212\u670D\u2026\u2026\u300D</p><p>\u300C\u53EA\u8981\u8212\u670D\u5C31\u884C\u4E86\u3002\u7BA1\u90A3\u4E48\u591A\u5E72\u561B\uFF1F\u300D\u592D\u592D\u6276\u7740\u9759\u989C\u7684\u8170\u80A2\u6765\u56DE\u63A8\u9001\uFF0C\u5634\u91CC\u9053\uFF1A\u300C\u6709\u4E2A\u7C97\u7684\u8981\u63D2\u8FDB\u53BB\u4E86\u5462\u3002\u300D</p><p>\u90A3\u8089\u8282\u5728\u809B\u857E\u4E0A\u4E00\u9876\uFF0C\u5C06\u67D4\u8F6F\u7684\u5C41\u773C\u513F\u6324\u5F97\u6012\u7EFD\u5F00\u6765\u3002\u8427\u4F5B\u5974\u300C\u554A\u300D\u7684\u5A9A\u53EB\u4E00\u58F0\uFF0C\u80A5\u767D\u7684\u5706\u81C0\u98A4\u6296\u7740\u80C0\u5F00\u3002\u62F3\u5934\u5927\u7684\u8089\u8282\u6491\u5F00\u809B\u857E\uFF0C\u6CBE\u7740\u6E17\u51FA\u7684\u871C\u6C41\u94BB\u5165\u809B\u6D1E\uFF0C\u786C\u6886\u6886\u5361\u5728\u76F4\u80A0\u91CC\u3002</p><p>\u8427\u4F5B\u5974\u5FC3\u91CC\u53CD\u800C\u5B89\u5B9A\u4E0B\u6765\uFF0C\u9664\u4E86\u9F99\u54E5\u54E5\uFF0C\u5176\u4ED6\u7537\u4EBA\u7EDD\u4E0D\u4F1A\u6709\u8FD9\u6837\u5947\u5F02\u7684\u6027\u5177\uFF0C\u591A\u534A\u662F\u513F\u5B50\u505A\u4E86\u6765\u8BA9\u81EA\u5DF1\u5F00\u5FC3\u7684\u3002\u5979\u914D\u5408\u5730\u8038\u8D77\u96EA\u81C0\uFF0C\u817B\u58F0\u9053\uFF1A\u300C\u592D\u513F\uFF0C\u597D\u597D\u73A9\u5A18\u7684\u5C41\u773C\u513F\u5427\u2026\u2026\u300D</p><p>\u90A3\u67D4\u5A9A\u7684\u58F0\u97F3\u8C61\u878D\u5316\u7684\u871C\u6C41\u822C\u751C\u817B\uFF0C\u8FDE\u9759\u989C\u4E5F\u4E0D\u7981\u82B3\u5FC3\u6697\u98A4\uFF0C\u82E5\u4E0D\u662F\u5979\u624B\u811A\u762B\u8F6F\uFF0C\u4E0D\u77E5\u9053\u8BE5\u662F\u600E\u6837\u98A0\u5012\u4F17\u751F\u7684\u5C24\u7269\u5462\u3002\u602A\u4E0D\u5F97\u6155\u5BB9\u9F99\u4F1A\u5BF9\u5979\u4E07\u822C\u5BA0\u7231\uFF0C\u4ECA\u5929\u5C31\u8BA9\u6211\u597D\u751F\u5BA0\u7231\u5BA0\u7231\u7687\u4E0A\u7684\u7231\u5983\u5427\u3002</p><p>\u8089\u8282\u5728\u76F4\u80A0\u5185\u6ED1\u6765\u6ED1\u53BB\uFF0C\u809B\u6D1E\u88AB\u6491\u5F97\u65E0\u6CD5\u5408\u62E2\uFF0C\u968F\u7740\u8089\u8282\u5730\u6ED1\u52A8\uFF0C\u50CF\u5149\u6DA6\u7684\u8102\u7EA2\u5C0F\u5634\u822C\u5706\u5706\u7684\u4E00\u6536\u4E00\u5F20\uFF0C\u5410\u51FA\u6E7F\u6ED1\u7684\u9ECF\u6DB2\u3002\u9F9F\u5934\u5728\u80A0\u9053\u5185\u56DB\u5904\u4E71\u649E\uFF0C\u9876\u5F97\u7F8E\u5987\u6D6A\u53EB\u8FDE\u58F0\u3002</p><p>\u8427\u4F5B\u5974\u76EE\u4E0D\u89C1\u7269\uFF0C\u53EA\u4EE5\u4E3A\u662F\u5728\u88AB\u513F\u5B50\u6DEB\u73A9\uFF0C\u5979\u81EA\u89C9\u5BF9\u592D\u592D\u4E8F\u6B20\u592A\u591A\uFF0C\u96BE\u5F97\u5979\u6709\u5174\u8DA3\u62FF\u81EA\u5DF1\u7684\u5C41\u773C\u513F\u53D6\u4E50\uFF0C\u4E8E\u662F\u52A0\u500D\u5C55\u9732\u5A9A\u6001\uFF0C\u597D\u8BA9\u513F\u5B50\u5F00\u5FC3\u3002\u867D\u7136\u56DB\u80A2\u7B4B\u8171\u88AB\u62BD\uFF0C\u624B\u811A\u65E0\u6CD5\u52A8\u4F5C\uFF0C\u8427\u4F5B\u5974\u8FD8\u662F\u6781\u529B\u633A\u52A8\u8170\u80A2\uFF0C\u80A5\u7F8E\u7684\u5927\u767D\u5C41\u80A1\u5728\u7A7A\u4E2D\u4E0D\u4F4F\u65CB\u8F6C\u7814\u78E8\uFF0C\u6DEB\u8273\u65E0\u6BD4\u3002</p><p>\u50CF\u5979\u8FD9\u6837\u96CD\u5BB9\u534E\u7F8E\u7684\u8D35\u5987\uFF0C\u653E\u8361\u4E2D\u522B\u6709\u4E00\u756A\u8BF1\u4EBA\u7684\u598D\u6001\uFF0C\u5979\u4F0F\u5728\u67B6\u4E0A\uFF0C\u4E00\u8FB9\u300C\u554A\u2026\u554A\u2026\u2026\u300D\u7684\u4F4E\u53EB\uFF0C\u4E00\u8FB9\u5A07\u58F0\u9053\uFF1A\u300C\u592D\u513F\uFF0C\u518D\u6DF1\u4E00\u4E9B\u2026\u2026\u7528\u529B\u6345\u5A18\u7684\u5C41\u773C\u513F\u2026\u2026\u300D</p><p>\u8089\u68D2\u5DF2\u7ECF\u6574\u6839\u8FDB\u5165\u809B\u5185\uFF0C\u4E24\u4E2A\u7855\u5927\u7684\u8089\u8282\u8F6E\u756A\u5728\u809B\u6D1E\u8FDB\u51FA\uFF0C\u5C41\u773C\u513F\u65F6\u5F00\u65F6\u5408\uFF0C\u6CA1\u6709\u534A\u523B\u5B89\u5B81\u3002\u8427\u4F5B\u5974\u7684\u5A9A\u53EB\u8D8A\u6765\u8D8A\u54CD\uFF0C\u9759\u989C\u5FC3\u91CC\u4E5F\u8D8A\u6765\u8D8A\u7D27\uFF0C\u5979\u5DF2\u7ECF\u4F7F\u5C3D\u624B\u6BB5\uFF0C\u5047\u5982\u6B64\u523B\u9762\u524D\u6485\u7740\u5C41\u80A1\u7684\u662F\u592D\u592D\uFF0C\u6B64\u65F6\u5373\u4F7F\u4E0D\u88AB\u5E72\u788E\u5C41\u773C\u513F\uFF0C\u4E5F\u65E9\u5DF2\u662F\u5C04\u7CBE\u65E0\u5EA6\uFF0C\u4F53\u8F6F\u5982\u7EF5\u3002\u53EF\u8427\u4F5B\u5974\u7684\u5C41\u773C\u513F\u5374\u50CF\u4E00\u4E2A\u65E0\u6CD5\u586B\u6EE1\u7684\u8089\u6B32\u9677\u9631\uFF0C\u518D\u51F6\u731B\u7684\u62BD\u9001\uFF0C\u4E5F\u53EA\u80FD\u4F7F\u5979\u6108\u53D1\u5174\u594B\u3002</p><p>\u4F0F\u5728\u6728\u67B6\u4E0A\u7684\u7F8E\u5987\u50CF\u4E00\u53EA\u7F8E\u8273\u7EDD\u4F26\u7684\u6BCD\u517D\uFF0C\u6D82\u8FC7\u8309\u8389\u82B1\u6CB9\u7684\u808C\u80A4\u767D\u817B\u5982\u8102\uFF0C\u8840\u7EA2\u7684\u517D\u6839\u5728\u767D\u5149\u5149\u7684\u96EA\u81C0\u4E2D\u4E0D\u4F4F\u633A\u5F04\uFF0C\u8D2F\u7A7F\u4E86\u7F8E\u5987\u7684\u80A0\u9053\u3002\u9759\u989C\u5C0F\u8179\u649E\u5728\u81C0\u4E0A\uFF0C\u53D1\u51FA\u6E05\u8106\u7684\u8089\u54CD\u3002\u8427\u4F5B\u5974\u7389\u9888\u6602\u8D77\uFF0C\u7F1A\u5728\u773C\u4E0A\u7684\u4E1D\u5DFE\u66F4\u589E\u6DFB\u4E86\u4E00\u4EFD\u67D4\u5F31\u548C\u59A9\u5A9A\u3002</p><p>\u968F\u7740\u81C0\u540E\u7684\u633A\u5F04\uFF0C\u4F18\u7F8E\u7684\u7389\u4F53\u524D\u540E\u6447\u6643\uFF0C\u96EA\u767D\u7684\u4E73\u7403\u5728\u80F8\u524D\u6447\u6765\u6447\u53BB\uFF0C\u8361\u51FA\u8000\u76EE\u7684\u80A4\u5149\u3002\u6BD4\u8D77\u5F53\u65E5\u8273\u51E4\u7684\u8C6A\u4E73\uFF0C\u8427\u4F5B\u5974\u7684\u4E73\u623F\u867D\u7136\u7565\u5C0F\u4E00\u4E9B\uFF0C\u4F46\u9971\u6EE1\u5F02\u5E38\uFF0C\u4E73\u5934\u6E7F\u6E7F\u7684\uFF0C\u7EA2\u6DA6\u65E0\u6BD4\u3002</p><p>\u5F53\u9759\u989C\u53C8\u4E00\u6B21\u7528\u529B\u63D2\u5165\uFF0C\u5C06\u957F\u8FD1\u5C3A\u8BB8\u7684\u517D\u9633\u6574\u6839\u6345\u8FDB\u90A3\u53EA\u5706\u6DA6\u7684\u7F8E\u81C0\uFF0C\u8427\u4F5B\u5974\u6D51\u5706\u7684\u96EA\u4E73\u7A81\u7136\u4E00\u98A4\uFF0C\u540C\u65F6\u55B7\u51FA\u6D53\u767D\u7684\u4E73\u6C41\u3002\u8273\u7EA2\u7684\u4E73\u5934\u786C\u5F97\u53D1\u7D27\uFF0C\u4E24\u80A1\u7EC6\u7EC6\u7684\u4E73\u6C41\u5F77\u4F5B\u767D\u8272\u7684\u55B7\u6CC9\uFF0C\u5E26\u7740\u8BF1\u4EBA\u7684\u5976\u9999\uFF0C\u55B7\u6E85\u5728\u6C34\u9752\u8272\u7684\u77F3\u9762\u4E0A\u3002</p><p>\u9759\u989C\u6014\u6014\u505C\u4E86\u4E0B\u6765\uFF0C\u53EF\u8427\u4F5B\u5974\u8FD8\u5728\u6643\u7740\u5706\u81C0\u53BB\u5957\u5F04\u5979\u7684\u9633\u5177\uFF0C\u817B\u58F0\u9053\uFF1A\u300C\u592D\u513F\uFF0C\u5FEB\u6765\u5E72\u5A18\u7684\u5C41\u773C\u513F\u554A\u2026\u2026\u300D</p><p>\u9759\u989C\u4ECE\u6765\u6CA1\u6709\u89C1\u8FC7\u8FD9\u6837\u7684\u5973\u5B50\uFF0C\u5979\u5C31\u50CF\u4E00\u7247\u671B\u4E0D\u8FB9\u7684\u80A5\u7F8E\u8349\u539F\uFF0C\u6B32\u706B\u4E00\u65E6\u70B9\u71C3\uFF0C\u5C31\u4F1A\u65E0\u6B62\u5883\u5730\u71C3\u70E7\u4E0B\u53BB\u3002\u65E0\u8BBA\u662F\u81EA\u5DF1\u7684\u6DEB\u6280\u8FD8\u662F\u7C97\u5927\u7684\u517D\u9633\uFF0C\u90FD\u65E0\u6CD5\u6291\u5236\u5979\u7684\u6B32\u671B\u4E4B\u706B\u3002\u8FD9\u662F\u4E00\u4E2A\u5979\u65E0\u80FD\u4E3A\u529B\u7684\u5973\u4EBA\uFF0C\u4E00\u4E2A\u65E0\u80FD\u4E3A\u529B\u7684\u5C41\u773C\u513F\u3002\u518D\u591A\u7684\u4EC7\u6068\uFF0C\u4E5F\u65E0\u6CD5\u5F81\u670D\u8FD9\u5177\u53EA\u4E3A\u6027\u6B32\u800C\u6D3B\u7684\u8EAF\u4F53\u3002</p><p>\u9759\u989C\u62D4\u51FA\u9633\u5177\uFF0C\u6361\u8D77\u8863\u7269\uFF0C\u5782\u5934\u8D70\u51FA\u77F3\u5BA4\u3002\u7559\u4E0B\u76EE\u77AA\u53E3\u5446\u7684\u592D\u592D\uFF0C\u548C\u6DEB\u53EB\u4E0D\u7EDD\u7684\u8427\u4F5B\u5974\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u59D0\u59D0\uFF0C\u6211\u5A18\u60F9\u4F60\u4E0D\u9AD8\u5174\u4E86\u5417\uFF1F\u300D</p><p>\u9759\u989C\u5F20\u5F00\u624B\u81C2\uFF0C\u592D\u592D\u8FDF\u7591\u4E86\u4E00\u4E0B\uFF0C\u4F0F\u5728\u5979\u6000\u4E2D\uFF0C\u5FE7\u5FC3\u5FE1\u5FE1\u5730\u671B\u7740\u9759\u989C\u3002</p><p>\u300C\u6CA1\u5173\u7CFB\u7684\u3002\u300D\u9759\u989C\u6DE1\u6DE1\u7B11\u7740\uFF0C\u4E00\u4E1D\u4E1D\u63A0\u597D\u592D\u592D\u7684\u79C0\u53D1\u3002\u8FDB\u5165\u661F\u6708\u6E56\u4E4B\u524D\uFF0C\u5979\u7684\u76EE\u6807\u5F88\u660E\u786E\uFF0C\u6740\u6389\u6155\u5BB9\u9F99\uFF0C\u6740\u6389\u5F53\u65F6\u5728\u573A\u7684\u6240\u6709\u7537\u4EBA\uFF0C\u5C06\u6155\u5BB9\u9F99\u7684\u6240\u6709\u4EB2\u4EBA\uFF0C\u4ED6\u7684\u59BB\u5B50\u3001\u5973\u513F\u4E00\u4E00\u6298\u78E8\u81F3\u6B7B\uFF0C\u4E3A\u7239\u5A18\u62A5\u4EC7\u96EA\u6068\u3002</p><p>\u4F46\u73B0\u5728\u5979\u5374\u8FF7\u60D8\u4E86\u3002\u7B2C\u4E00\u4E2A\u53EF\u4EE5\u590D\u4EC7\u7684\u76EE\u6807\uFF0C\u662F\u6155\u5BB9\u9F99\u7684\u5BA0\u5983\uFF0C\u4E00\u4E2A\u624B\u811A\u762B\u8F6F\u7684\u534E\u7F8E\u5973\u4EBA\u3002\u5BF9\u4E8E\u9759\u989C\u6765\u8BF4\uFF0C\u5411\u4E00\u4E2A\u65E0\u529B\u53CD\u6297\u7684\u67D4\u5F31\u5973\u5B50\u4E0B\u624B\uFF0C\u5E76\u6CA1\u6709\u7F6A\u6076\u611F\u2014\u2014\u5373\u4F7F\u65E0\u8F9C\u8005\u5979\u4E5F\u6740\u8FC7\u8BB8\u591A\u3002\u4F46\u662F\u5979\u5374\u4E0D\u77E5\u9053\u8BE5\u62FF\u8427\u4F5B\u5974\u600E\u4E48\u529E\u624D\u597D\u3002\u5979\u5148\u540E\u6DEB\u73A9\u4E86\u90A3\u4E2A\u5973\u4EBA\u4E24\u6B21\uFF0C\u4F46\u90A3\u79CD\u611F\u89C9\uFF0C\u5012\u50CF\u662F\u88AB\u5979\u6DEB\u73A9\u2026\u2026</p><p>\u4ECE\u592D\u592D\u6628\u665A\u7684\u54ED\u8BC9\u4E2D\uFF0C\u9759\u989C\u624D\u77E5\u9053\u5979\u7684\u624B\u7B4B\u811A\u7B4B\u6B63\u662F\u88AB\u6155\u5BB9\u9F99\u62BD\u53BB\u7684\uFF0C\u8FD9\u4F4D\u6BCD\u8D35\u5983\u5176\u5B9E\u4E5F\u662F\u6155\u5BB9\u9F99\u7684\u53D7\u5BB3\u8005\u3002\u800C\u4E14\uFF0C\u5979\u8FD8\u662F\u592D\u592D\u7684\u6BCD\u4EB2\u3002\u65E0\u8BBA\u592D\u592D\u518D\u600E\u4E48\u6068\u6BCD\u4EB2\uFF0C\u5728\u5979\u5FC3\u91CC\uFF0C\u4E00\u76F4\u90FD\u662F\u6DF1\u7231\u7740\u6BCD\u4EB2\uFF0C\u4E0D\u7136\u4E5F\u4E0D\u4F1A\u5728\u6DF1\u591C\u4E00\u4E2A\u4EBA\u54ED\u7740\u5728\u5C71\u6797\u4E2D\u5954\u8DD1\u3002</p><p>\u592D\u592D\u662F\u5979\u7684\u5C0F\u6BCD\u72D7\uFF0C\u4E5F\u662F\u5979\u81F3\u4ECA\u552F\u4E00\u517B\u8FC7\u7684\u5BA0\u7269\u3002\u4F5C\u4E3A\u90FD\u662F\u88AB\u6155\u5BB9\u9F99\u6BC1\u53BB\u7537\u6027\u7279\u5F81\u7684\u7578\u4EBA\uFF0C\u9759\u989C\u5BF9\u592D\u592D\u6000\u6709\u7684\u4E0D\u4EC5\u662F\u601C\u7231\uFF0C\u8FD8\u6709\u4EB2\u5207\u3002\u90A3\u662F\u5979\u9762\u5BF9\u540C\u6837\u4EB2\u5207\u7684\u5E08\u5A18\u3001\u4E49\u6BCD\u6240\u4E0D\u4E00\u6837\u7684\u611F\u89C9\u2026\u2026\u5C31\u50CF\u662F\u4E00\u6735\u5996\u83B2\u4E0E\u4E00\u6735\u9B3C\u7F42\u7C9F\u7684\u76F8\u9022\u3002</p><p>\u6216\u8005\u627E\u4E2A\u673A\u4F1A\u628A\u5979\u7A83\u5230\u5BAB\u5916\uFF0C\u9001\u5230\u4E00\u4E2A\u4E0B\u8D31\u7684\u7A91\u5B50\u91CC\u3002\u9759\u989C\u592A\u4E86\u89E3\u90A3\u4E9B\u5F00\u5728\u6697\u8857\u7684\u5A3C\u9986\u4E86\uFF0C\u6CA1\u6709\u4EBA\u7167\u6599\uFF0C\u4E0D\u51FA\u4E24\u4E2A\u6708\uFF0C\u8FD9\u4E2A\u5929\u751F\u4E3D\u8D28\u7684\u8D35\u5987\u5C31\u4F1A\u88AB\u90A3\u4E9B\u7C97\u9119\u7684\u5BA2\u4EBA\u5E72\u6210\u4E00\u5806\u81ED\u4E0D\u53EF\u95FB\u7684\u810F\u8089\u3002\u53EF\u8FD9\u6837\u505A\u6155\u5BB9\u9F99\u4F1A\u5FC3\u75BC\u5417\uFF1F\u6700\u5FC3\u75BC\u7684\uFF0C\u53EF\u80FD\u8FD8\u662F\u8FD9\u4F1A\u513F\u5367\u5728\u819D\u4E0A\u7684\u592D\u592D\u4E86\u2026\u2026\u90A3\u5C31\u4E0D\u8981\u8BA9\u5979\u77E5\u9053\u597D\u4E86\u3002</p><p>\u592D\u592D\u4F0F\u5728\u5979\u6000\u4E2D\uFF0C\u5E7D\u5E7D\u9053\uFF1A\u300C\u6211\u5A18\u5176\u5B9E\u597D\u53EF\u601C\u7684\u2026\u2026\u300D</p><p>\u300C\u662F\u554A\u3002\u300D\u957F\u5F97\u592A\u7F8E\u7684\u5973\u4EBA\uFF0C\u603B\u662F\u65E0\u6CD5\u628A\u63E1\u81EA\u5DF1\u7684\u547D\u8FD0\u3002\u9759\u989C\u671B\u7740\u5979\uFF0C\u5728\u5FC3\u91CC\u8F7B\u8F7B\u8BF4\u9053\uFF1A\u300C\u5C0F\u6BCD\u72D7\uFF0C\u77E5\u9053\u59D0\u59D0\u51C6\u5907\u600E\u4E48\u5904\u7406\u4F60\u5A18\u5417\uFF1F\u300D</p><p>\u300C\u592D\u62A4\u6CD5\uFF0C\u516C\u4E3B\u56DE\u6765\u4E86\u3002\u300D</p><p>\u661F\u6708\u6E56\u7684\u5E2E\u4F17\u4F3C\u4E4E\u5C11\u4E86\u8BB8\u591A\uFF0C\u5728\u5CB8\u65C1\u8FCE\u63A5\u516C\u4E3B\u7684\uFF0C\u4E0D\u8DB3\u4E0A\u6B21\u534A\u6570\u3002</p><p>\u5927\u8239\u9760\u5CB8\uFF0C\u653E\u4E0B\u4E00\u6761\u5F88\u5BBD\u7684\u8237\u68AF\uFF0C\u8DB3\u4EE5\u4F9B\u8239\u4E0A\u7684\u9A6C\u8F66\u76F4\u63A5\u9A70\u5165\u6708\u5C9B\u3002\u8F66\u5E18\u6380\u5F00\uFF0C\u8DF3\u51FA\u6765\u4E00\u4E2A\u5C0F\u7537\u5B69\u3002\u4ED6\u770B\u4E0A\u53BB\u56DB\u4E94\u5C81\u7684\u6837\u5B50\uFF0C\u7528\u4E00\u9876\u5C0F\u5C0F\u7684\u91D1\u51A0\u675F\u7740\u53D1\uFF0C\u9762\u76EE\u79C0\u7F8E\u65E0\u6BD4\u3002</p><p>\u300C\u592A\u5B50\uFF1F\u300D\u9759\u989C\u8F7B\u8F7B\u95EE\u9053\u3002</p><p>\u592D\u592D\u5FAE\u5FAE\u70B9\u4E86\u70B9\u5934\uFF0C\u773C\u4E2D\u4F3C\u4E4E\u6709\u4E9B\u60B2\u54C0\u3002</p><p>\u300C\u51B2\u513F\uFF0C\u4E0D\u8981\u8DD1\u3002\u300D\u968F\u7740\u4E00\u58F0\u8F6F\u8F6F\u7684\u5A07\u547C\uFF0C\u4E00\u5F20\u9C9C\u82B1\u822C\u660E\u8273\u7684\u7389\u8138\u51FA\u73B0\u5728\u9759\u989C\u9762\u524D\u3002</p><p>\u516C\u4E3B\u4E00\u624B\u6380\u5F00\u5E18\u5B50\uFF0C\u7F13\u6B65\u4E0B\u4E86\u9A6C\u8F66\u3002\u5979\u81C2\u95F4\u62B1\u7740\u4E00\u4E2A\u4E00\u5C81\u591A\u5927\u7684\u5973\u5A74\uFF0C\u751F\u5F97\u7C89\u96D5\u7389\u7422\uFF0C\u51B0\u96EA\u53EF\u7231\u3002\u4F46\u9759\u989C\u7684\u76EE\u5149\u59CB\u7EC8\u505C\u5728\u516C\u4E3B\u8EAB\u4E0A\u3002\u5979\u771F\u7684\u5C31\u662F\u90A3\u4E2A\u6674\u96EA\u5417\uFF1F\u8FD8\u662F\u540D\u5B57\u7684\u5DE7\u5408\uFF1F\u5979\u552F\u4E00\u80FD\u80AF\u5B9A\u7684\u662F\uFF0C\u5979\u4EEC\u4E24\u4E2A\u90FD\u5F88\u7F8E\u3002\u4F46\u5979\u65E0\u6CD5\u60F3\u50CF\uFF0C\u6674\u96EA\u957F\u5927\u540E\u4F1A\u662F\u5982\u4F55\u7F8E\u4E3D\u3002\u56E0\u4E3A\u5728\u5979\u5FC3\u4E2D\uFF0C\u6674\u96EA\u6C38\u8FDC\u90FD\u662F\u4E94\u5C81\u3002</p><p>\u516C\u4E3B\u8FD8\u662F\u4E00\u88AD\u9ED1\u8863\uFF0C\u8863\u7F1D\u95F4\u96EA\u767D\u7684\u5927\u817F\u82E5\u9690\u82E5\u73B0\u3002\u5979\u6015\u4E71\u8DD1\u7684\u5B69\u5B50\u6389\u8FDB\u6C34\u4E2D\uFF0C\u5E72\u8106\u628A\u4ED6\u4E5F\u62B1\u5728\u6000\u4E2D\uFF0C\u7136\u540E\u4E00\u624B\u6258\u7740\u5973\u5A74\uFF0C\u89E3\u5F00\u8941\u8913\u3002\u5973\u5A74\u4E0D\u60C5\u613F\u5730\u54ED\u4E86\u8D77\u6765\uFF0C\u6674\u96EA\u4E00\u8FB9\u67D4\u58F0\u5475\u54C4\uFF0C\u4E00\u8FB9\u5FEB\u624B\u5FEB\u811A\u5730\u8131\u4E0B\u5979\u7684\u5C0F\u88E4\u5B50\u3002</p><p>\u7537\u5B69\u773C\u775B\u4E00\u4EAE\uFF0C\u5728\u6674\u96EA\u81C2\u95F4\u4F38\u51FA\u624B\u53BB\uFF0C\u6478\u5F04\u7740\u5973\u5A74\u80A1\u95F4\u5AE9\u5AE9\u7684\u8089\u7F1D\uFF0C\u300C\u7075\u513F\u59B9\u59B9\u771F\u5F97\u4E0D\u4F1A\u957F\u9E21\u9E21\u5417\uFF1F\u300D</p><p>\u6674\u96EA\u51FA\u5947\u5730\u6CA1\u6709\u963B\u6B62\u4ED6\u7684\u4E3E\u52A8\uFF0C\u53EA\u662F\u5F53\u4ED6\u624B\u6307\u60F3\u4F38\u8FDB\u8089\u7F1D\u65F6\u624D\u628A\u5973\u5A74\u62B1\u5230\u4E00\u8FB9\uFF0C\u8138\u4E0A\u51B7\u51B7\u7684\u3002</p><p>\u9759\u989C\u5FC3\u91CC\u54C8\u4E86\u534A\u58F0\uFF0C\u8FD9\u5B69\u5B50\u679C\u7136\u662F\u6155\u5BB9\u9F99\u7684\u8840\u7EDF\uFF0C\u8FD9\u4E48\u5C0F\u5C31\u77E5\u9053\u53BB\u73A9\u5973\u4EBA\u7684\u5C44\u4E86\uFF0C\u8DDF\u6CAE\u6E20\u5144\u59B9\u7684\u5B9D\u513F\u5012\u6709\u4E00\u6BD4\uFF0C\u53EA\u662F\u76F8\u8C8C\u548C\u7075\u6C14\uFF0C\u53EF\u662F\u5929\u5DEE\u5730\u522B\u4E86\u3002\u4E0D\u77E5\u9053\u4ED6\u6BCD\u4EB2\u662F\u8C01\uFF0C\u80FD\u548C\u6155\u5BB9\u9F99\u751F\u51FA\u8FD9\u6837\u7684\u513F\u5B50\u2026\u2026</p><p>\u51B2\u513F\u5728\u6674\u96EA\u6000\u91CC\u626D\u6765\u626D\u53BB\uFF0C\u8FD8\u60F3\u53BB\u6380\u59B9\u59B9\u7684\u8941\u8913\uFF0C\u6674\u96EA\u76B1\u8D77\u7709\u5934\uFF0C\u559D\u9053\uFF1A\u300C\u5B89\u5206\u4E9B\uFF01\u300D</p><p>\u65C1\u8FB9\u7684\u5973\u5974\u4E0A\u6765\u63A5\u8FC7\u592A\u5B50\uFF0C\u51B2\u513F\u5374\u62B1\u7740\u6674\u96EA\u7684\u624B\u81C2\u4E0D\u613F\u6492\u624B\uFF0C\u4ED6\u53EA\u5B89\u5206\u4E86\u5598\u53E3\u6C14\u7684\u5DE5\u592B\uFF0C\u7B49\u6674\u96EA\u628A\u5C0F\u88E4\u5B50\u4EA4\u7ED9\u5973\u5974\uFF0C\u53C8\u4F38\u51FA\u624B\uFF0C\u7528\u624B\u6307\u6363\u4E86\u6363\u59B9\u59B9\u6D41\u53E3\u6C34\u7684\u5C0F\u5634\u3002</p><p>\u6674\u96EA\u51B7\u51B7\u6311\u8D77\u7709\u5934\uFF0C\u679C\u7136\u51B2\u513F\u53C8\u8BF4\u9053\uFF1A\u300C\u59B9\u59B9\u7684\u5634\u5DF4\u771F\u597D\u73A9\uFF0C\u9E21\u9E21\u63D2\u91CC\u9762\u597D\u8212\u670D\u2026\u2026\u300D</p><p>\u9759\u989C\u77A0\u76EE\u7ED3\u820C\uFF0C\u4EE5\u4E3A\u81EA\u5DF1\u662F\u4E0D\u662F\u542C\u9519\u4E86\u3002</p><p>\u516C\u4E3B\u5BD2\u58F0\u95EE\u9053\uFF1A\u300C\u8C01\u8BA9\u4F60\u505A\u7684\uFF1F\u300D</p><p>\u300C\u662F\u7236\u7687\u554A\uFF0C\u4ED6\u8BF4\u59B9\u59B9\u7684\u5634\u5DF4\u8FD8\u6CA1\u6709\u957F\u7259\uFF0C\u4E0D\u6015\u54AC\u7684\u3002\u300D</p><p>\u9759\u989C\u96BE\u4EE5\u7F6E\u4FE1\u5730\u671B\u7740\u5468\u56F4\uFF0C\u53EA\u89C1\u4F17\u4EBA\u90FD\u662F\u9762\u8272\u5982\u5E38\uFF0C\u50CF\u662F\u4EC0\u4E48\u90FD\u6CA1\u542C\u5230\u3002\u53EA\u6709\u592D\u592D\u7EF7\u7740\u8138\uFF0C\u9762\u65E0\u8868\u60C5\u3002\u770B\u6765\u662F\u771F\u7684\u4E86\uFF0C\u8FD9\u4F4D\u6155\u5BB9\u7687\u5E1D\u2026\u2026\u786E\u5B9E\u5F88\u7279\u522B\u2026\u2026</p><p>\u9759\u989C\u56DE\u8FC7\u5934\u53BB\u770B\u5C0F\u516C\u4E3B\u7684\u53CD\u5E94\uFF0C\u5374\u542C\u51B2\u513F\u53C8\u8BF4\u9053\uFF1A\u300C\u51B2\u513F\u4EC0\u4E48\u65F6\u5019\u80FD\u5E72\u59B9\u59B9\uFF1F\u300D</p><p>\u9759\u989C\u5934\u76AE\u53D1\u9EBB\u5730\u671B\u7740\u516C\u4E3B\uFF0C\u53EA\u89C1\u5979\u6DE1\u6DE1\u9053\uFF1A\u300C\u5979\u8FD8\u5C0F\u3002\u300D\u8FDE\u773C\u775B\u4E5F\u6CA1\u6709\u7728\u4E00\u4E0B\u3002</p><p>\u5C0F\u516C\u4E3B\u4E0B\u4E86\u8239\uFF0C\u660E\u7738\u5411\u8FD9\u8FB9\u779F\u6765\u3002\u9759\u989C\u8DEA\u8EAB\u9053\uFF1A\u300C\u5974\u5A62\u9759\u989C\uFF0C\u62DC\u89C1\u516C\u4E3B\u3002\u300D</p><p>\u516C\u4E3B\u505C\u4E0B\u811A\u6B65\uFF0C\u300C\u8FD9\u4E48\u5FEB\u5C31\u56DE\u6765\u4E86\u3002\u300D</p><p>\u9759\u989C\u626C\u8138\u4E00\u7B11\uFF0C\u300C\u5A62\u5B50\u5E78\u4E0D\u8FB1\u547D\u3002\u300D</p><p>\u516C\u4E3B\u70B9\u4E86\u70B9\u5934\uFF0C\u6B63\u5F85\u4E3E\u6B65\uFF0C\u6000\u91CC\u7684\u7537\u5B69\u53EB\u4E86\u8D77\u6765\uFF0C\u300C\u5979\u957F\u5F97\u597D\u6F02\u4EAE\uFF0C\u5A18\uFF0C\u7ED9\u51B2\u513F\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u9759\u989C\u8111\u4E2D\u8F70\u7136\u4E00\u54CD\uFF0C\u5446\u5446\u671B\u7740\u516C\u4E3B\u7EA4\u7F8E\u7684\u8170\u8EAB\uFF0C\u8FDE\u5979\u8FD1\u5728\u54AB\u5C3A\u7684\u58F0\u97F3\u90FD\u6CA1\u6709\u542C\u5230\u3002\u81EA\u5DF1\u80AF\u5B9A\u662F\u542C\u9519\u4E86\uFF0C\u5979\u600E\u4E48\u4F1A\u662F\u4E24\u4E2A\u5B69\u5B50\u7684\u6BCD\u4EB2\uFF1F\u5979\u8FD8\u8FD9\u4E48\u5C0F\u2026\u2026\u5979\u5AC1\u7ED9\u8C01\u4E86\uFF1F\u592A\u5B50\u7684\u7239\u7239\u662F\u6155\u5BB9\u9F99\uFF0C\u96BE\u9053\u5979\u4E0D\u662F\u6155\u5BB9\u9F99\u7684\u4EB2\u751F\u5973\u513F\uFF1F</p><p>\u300C\u662F\u554A\u3002\u300D\u592D\u592D\u501A\u5728\u69BB\u4E0A\uFF0C\u6709\u4E9B\u80F8\u75BC\u4F3C\u7684\u63C9\u7740\u80F8\u4E73\uFF0C\u300C\u5979\u5F53\u7136\u662F\u7687\u4E0A\u4EB2\u751F\u7684\u4E56\u5973\u513F\u554A\u3002\u300D</p><p>\u300C\u5979\u5AC1\u4EBA\u4E86\uFF1F\u300D\u9759\u989C\u56DE\u5230\u5723\u5BAB\uFF0C\u5C31\u8FEB\u4E0D\u53CA\u5F85\u5730\u8BAF\u95EE\u592D\u592D\u3002</p><p>\u592D\u592D\u7B11\u4E86\u8D77\u6765\uFF0C\u300C\u6CA1\u6709\u554A\u3002\u600E\u4E48\u4F1A\u5462\uFF1F\u300D</p><p>\u300C\u90A3\u5979\u2026\u2026\u600E\u4E48\u4F1A\u6709\u4E24\u4E2A\u5B69\u5B50\uFF1F\u300D</p><p>\u300C\u4E0D\u5AC1\u4EBA\u4E5F\u53EF\u4EE5\u6709\u5B69\u5B50\u554A\uFF0C\u8981\u4E0D\u662F\u7678\u5316\u6C64\uFF0C\u6559\u91CC\u7684\u6DEB\u5974\u65E9\u5C31\u513F\u5973\u6210\u7FA4\uFF0C\u50CF\u6BCD\u732A\u4E00\u6837\u751F\u5B69\u5B50\u751F\u5F97\u8EAB\u5B50\u90FD\u53D8\u5F62\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4F1A\u5BB9\u5FCD\u5973\u513F\u6709\u79C1\u751F\u5B50\uFF1F\u9759\u989C\u8D8A\u542C\u8D8A\u8FF7\u60D8\uFF0C\u300C\u90A3\u4E24\u4E2A\u5B69\u5B50\u771F\u662F\u5979\u751F\u7684\uFF1F</p><p>\u4E0D\u662F\u62B1\u517B\u6765\u7684\uFF1F\u300D</p><p>\u592D\u592D\u6B63\u8981\u5F00\u53E3\uFF0C\u5973\u5974\u7684\u58F0\u97F3\u5148\u54CD\u4E86\u8D77\u6765\uFF0C\u300C\u592D\u62A4\u6CD5\uFF0C\u516C\u4E3B\u4F20\u989C\u5974\u7ACB\u523B\u53BB\u89C1\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6309\u53F6\u62A4\u6CD5\u7684\u610F\u89C1\uFF0C\u5B69\u5B50\u4EEC\u5E76\u6CA1\u6709\u4F4F\u5728\u8FD9\u91CC\u3002\u521A\u624D\u7537\u5B69\u7684\u5435\u95F9\u548C\u5973\u5A74\u7684\u54ED\u58F0\u4F3C\u4E4E\u662F\u4E00\u573A\u68A6\uFF0C\u5C0F\u516C\u4E3B\u4ECD\u662F\u4E00\u4E2A\u4EBA\u9759\u9759\u5750\u5728\u5BA4\u5185\uFF0C\u5C31\u50CF\u4E00\u5377\u4F18\u96C5\u7684\u56FE\u753B\u3002</p><p>\u5979\u4FEF\u8EAB\u6380\u5F00\u7389\u5236\u85B0\u7089\uFF0C\u4EB2\u624B\u653E\u5165\u9999\u6599\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u7434\u5251\u53CC\u4FA0\u5462\uFF1F\u300D</p><p>\u300C\u5949\u516C\u4E3B\u8C15\u65E8\uFF0C\u5974\u5A62\u5DF2\u7ECF\u53D6\u6765\u5468\u5B50\u6C5F\u7684\u5934\u9885\uFF0C\u5E9F\u53BB\u51CC\u96C5\u7434\u7684\u6B66\u529F\uFF0C\u7531\u4E24\u4F4D\u767D\u62A4\u6CD5\u9001\u5F80\u5EFA\u5EB7\u3002\u300D\u9759\u989C\u8BF4\u7740\u9012\u4E0A\u6728\u5323\u3002</p><p>\u5C0F\u516C\u4E3B\u538C\u6076\u5730\u76B1\u8D77\u7709\u5934\uFF0C\u300C\u4E0D\u7528\u6253\u5F00\u4E86\u3002\u653E\u5728\u5916\u9762\u5427\u3002\u300D</p><p>\u5979\u6362\u4E86\u4E00\u4EF6\u4E1D\u888D\uFF0C\u867D\u7136\u8FD8\u662F\u9ED1\u8272\uFF0C\u4F46\u4E0D\u662F\u90A3\u79CD\u521A\u786C\u7684\u8272\u8C03\uFF0C\u800C\u662F\u591C\u7A7A\u4E00\u6837\u8F7B\u76C8\u800C\u53C8\u7EAF\u51C0\u7684\u9ED1\u8272\u3002\u4E1D\u888D\u5F88\u5BBD\u677E\uFF0C\u4F7F\u5979\u7684\u5A07\u8EAF\u6108\u53D1\u7EA4\u7F8E\uFF0C\u8170\u95F4\u968F\u610F\u675F\u7740\u4E00\u6761\u7F0E\u5E26\uFF0C\u957F\u53D1\u7528\u4E00\u53EA\u91D1\u73AF\u675F\u7740\uFF0C\u62AB\u5728\u80A9\u540E\uFF0C\u7EA4\u5DE7\u7684\u79C0\u8DB3\u4ECE\u8863\u4E0B\u9732\u51FA\u5C11\u8BB8\uFF0C\u5B9B\u5982\u4E00\u622A\u6253\u78E8\u6676\u83B9\u7684\u660E\u7389\u3002\u5979\u9759\u9759\u5750\u5728\u90A3\u91CC\uFF0C\u5B9D\u77F3\u822C\u7684\u661F\u7738\u9759\u82E5\u6B62\u6C34\uFF0C\u811A\u65C1\u6DE1\u6DE1\u7684\u9999\u96FE\u7F2D\u7ED5\u800C\u8D77\uFF0C\u5F77\u4F5B\u68A6\u5883\u4E00\u6837\u8FF7\u79BB\u3002</p><p>\u9759\u989C\u4F3C\u4E4E\u80FD\u542C\u5230\u81EA\u5DF1\u8840\u8109\u8FD0\u884C\u7684\u58F0\u97F3\uFF0C\u5979\u5DF2\u7ECF\u76EE\u7779\u8FC7\u516C\u4E3B\u8D64\u88F8\u7684\u8EAB\u4F53\uFF0C\u5374\u6CA1\u60F3\u5230\u5979\u4F1A\u6709\u8FD9\u6837\u7684\u9B45\u529B\uFF0C\u53EA\u662F\u534A\u53EA\u7EA4\u8DB3\uFF0C\u5C31\u50CF\u78C1\u77F3\u4E00\u6837\u5438\u5F15\u4E86\u81EA\u5DF1\u5168\u90E8\u5FC3\u795E\u3002\u82E5\u4E0D\u662F\u8FDB\u95E8\u524D\u5148\u5C01\u4E86\u7A74\u9053\uFF0C\u6B64\u523B\u517D\u9633\u65E9\u5C31\u4F38\u4E86\u51FA\u6765\u3002</p><p>\u623F\u5185\u9759\u4E86\u7247\u523B\uFF0C\u5C0F\u516C\u4E3B\u9053\uFF1A\u300C\u65E2\u7136\u4F60\u90FD\u529E\u5230\u4E86\u3002\u90A3\u5C31\u4E0A\u69BB\u53BB\u5427\u3002\u300D</p><p>\u300C\u662F\u3002\u300D\u9759\u989C\u8D77\u8EAB\uFF0C\u5979\u7EA2\u7740\u8138\u89E3\u5F00\u7FE0\u7EEB\u8863\u5E26\uFF0C\u8131\u4E0B\u5916\u886B\uFF0C\u7136\u540E\u9664\u53BB\u4E2D\u8863\uFF0C\u53EA\u5269\u4E0B\u4E00\u6761\u7EE3\u7740\u6843\u82B1\u7684\u5927\u7EA2\u62B9\u80F8\u3002\u5979\u7F9E\u4E0D\u53EF\u652F\u5730\u5782\u4E0B\u5934\uFF0C\u6162\u6162\u626F\u5F00\u4E1D\u7EE6\uFF0C\u892A\u4E0B\u957F\u88D9\uFF0C\u9732\u51FA\u4FEE\u957F\u7684\u7389\u817F\u548C\u8179\u4E0B\u4E00\u4E1B\u7EA4\u8F6F\u7684\u6BDB\u53D1\u3002\u5C0F\u516C\u4E3B\u5982\u6C34\u7684\u76EE\u5149\u4ECE\u5979\u8EAB\u4E0A\u6DCC\u8FC7\uFF0C\u6CA1\u6709\u9057\u6F0F\u4EFB\u4F55\u4E00\u4E2A\u7EC6\u8282\u3002</p><p>\u69BB\u4E0A\u7684\u9526\u88AB\u53C8\u6ED1\u53C8\u8F6F\uFF0C\u8EBA\u5728\u4E0A\u9762\uFF0C\u5C31\u50CF\u5367\u5728\u4E91\u7AEF\u4E00\u6837\u3002\u4F46\u8FD9\u662F\u9759\u989C\u540E\u6765\u624D\u53D1\u73B0\u7684\u3002\u5F53\u65F6\u5979\u5750\u5728\u69BB\u4E0A\uFF0C\u89E3\u4E0B\u6700\u540E\u853D\u4F53\u7684\u90A3\u6761\u62B9\u80F8\uFF0C\u7136\u540E\u50CF\u4E00\u4E2A\u7F9E\u6DA9\u7684\u5904\u5B50\u90A3\u6837\uFF0C\u7528\u624B\u63A9\u7740\u80F8\u4E73\u548C\u4E0B\u8179\uFF0C\u7F13\u7F13\u8EBA\u5012\uFF0C\u5FC3\u91CC\u6026\u6026\u76F4\u8DF3\u3002\u5979\u7684\u5FC3\u8DF3\u58F0\u5982\u6B64\u5267\u70C8\uFF0C\u5979\u751A\u81F3\u6000\u7591\u8FDE\u5C0F\u516C\u4E3B\u90FD\u80FD\u542C\u5230\u3002</p><p>\u300C\u4F60\u6709\u4E9B\u7D27\u5F20\u5462\u3002\u300D\u5C0F\u516C\u4E3B\u6CA1\u6709\u8D77\u8EAB\uFF0C\u53EA\u8F7B\u8F7B\u63A8\u6765\u4E00\u53EA\u7389\u76CF\u3002</p><p>\u300C\u591A\u8C22\u516C\u4E3B\u3002\u300D\u9759\u989C\u611F\u6FC0\u5730\u8BF4\u9053\uFF0C\u62FF\u8D77\u7389\u76CF\u6D45\u6D45\u5477\u4E86\u4E00\u53E3\uFF0C\u53C8\u653E\u4E0B\u4E86\u3002</p><p>\u5C0F\u516C\u4E3B\u6B3E\u6B3E\u8D77\u8EAB\uFF0C\u62C9\u5F00\u8863\u5E26\u3002\u7EAF\u9ED1\u7684\u4E1D\u888D\u6C34\u4E00\u6837\u4ECE\u80A9\u5934\u6ED1\u4E0B\uFF0C\u9732\u51FA\u4E00\u5177\u5B8C\u7F8E\u65E0\u7455\u7684\u7389\u4F53\u3002\u9759\u989C\u76F8\u4FE1\u90A3\u4E24\u4E2A\u5B69\u5B50\u90FD\u662F\u5979\u62B1\u517B\u7684\uFF0C\u56E0\u4E3A\u5979\u8EAB\u4E0A\u770B\u4E0D\u5230\u4EFB\u4F55\u598A\u5A20\u7684\u75D5\u8FF9\uFF0C\u5B8C\u5168\u8FD8\u662F\u5C11\u5973\u7684\u6A21\u6837\u3002\u7EA4\u67D4\u7684\u8170\u80A2\u548C\u5E73\u5766\u7684\u5C0F\u8179\uFF0C\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u53EF\u80FD\u751F\u517B\u8FC7\u80CE\u513F\u2014\u2014\u5047\u5982\u4E0D\u77E5\u9053\u5979\u662F\u6155\u5BB9\u9F99\u7684\u5973\u513F\uFF0C\u9759\u989C\u4F1A\u4EE5\u4E3A\u5979\u8FD8\u662F\u4E2A\u7EAF\u6D01\u7684\u5904\u5B50\u3002\u4F46\u9759\u989C\u77E5\u9053\u8FD9\u4E9B\u90FD\u662F\u5047\u8C61\u3002\u8FD9\u6837\u7EAF\u7F8E\u7684\u5916\u8868\u80CC\u540E\uFF0C\u662F\u4E00\u4E2A\u6DEB\u8361\u800C\u53C8\u6076\u6BD2\u7684\u5973\u4EBA\uFF0C\u4E00\u4E2A\u516D\u5C81\u5C31\u8DDF\u7537\u4EBA\u4E0A\u5E8A\u7684\u5A4A\u5B50\uFF01\u5979\u600E\u4E48\u4F1A\u662F\u6674\u96EA\uFF1F</p><p>\u5C0F\u516C\u4E3B\u9759\u9759\u51DD\u89C6\u4E86\u5979\u4E00\u773C\uFF0C\u8D70\u5230\u5C4F\u98CE\u540E\u3002\u518D\u51FA\u6765\u65F6\uFF0C\u5979\u8179\u4E0B\u5DF2\u7ECF\u591A\u4E86\u6761\u7389\u830E\u3002\u7389\u830E\u662F\u7528\u4E0A\u7B49\u7684\u7F8A\u8102\u7389\u96D5\u523B\u800C\u6210\uFF0C\u4E0E\u5C0F\u516C\u4E3B\u7684\u808C\u80A4\u4E00\u6837\uFF0C\u5149\u6DA6\u4E4B\u6781\u3002\u830E\u8EAB\u7C97\u4EC5\u5BF8\u8BB8\uFF0C\u957F\u4E0D\u8DB3\u4E09\u5BF8\uFF0C\u659C\u659C\u7FD8\u5728\u8179\u4E0B\uFF0C\u770B\u4E0A\u53BB\u5C31\u50CF\u592D\u592D\u52C3\u8D77\u7684\u5C0F\u8089\u68D2\u3002</p><p>\u5979\u5C31\u662F\u62FF\u8FD9\u4E2A\u7ED9\u5904\u5973\u7834\u4F53\u5417\uFF1F\u9759\u989C\u5FC3\u4E0B\u4E00\u9635\u597D\u7B11\u3002\u5979\u85CF\u5728\u8179\u5185\u7684\u517D\u6839\u867D\u7136\u53EA\u6709\u6307\u4F59\u957F\u77ED\uFF0C\u53EF\u4E00\u65E6\u52C3\u8D77\uFF0C\u65E0\u8BBA\u7C97\u957F\u90FD\u6709\u8FD9\u4E2A\u5927\u4E0A\u4E09\u500D\uFF0C\u5982\u679C\u4EAE\u51FA\u6765\uFF0C\u4FDD\u8BC1\u8BA9\u8FD9\u4E2A\u6DEB\u5A03\u60ED\u6127\u5F97\u8981\u6B7B\u3002</p><p>\u5C0F\u516C\u4E3B\u7F13\u7F13\u8D70\u5230\u69BB\u65C1\uFF0C\u9759\u989C\u7F9E\u6DA9\u5730\u5782\u4E0B\u773C\uFF0C\u773C\u89D2\u5374\u779F\u7740\u5979\u7684\u79D8\u5904\u3002\u7389\u830E\u6839\u90E8\u6B63\u6321\u5728\u7389\u6237\u4E0A\u65B9\uFF0C\u53EA\u662F\u5979\u62AC\u817F\u4E0A\u69BB\u65F6\uFF0C\u624D\u80FD\u770B\u5230\u4E0B\u7F18\u4E00\u70B9\u5FAE\u7EA2\u4E00\u95EA\u800C\u8FC7\u3002</p><p>\u9759\u989C\u4FA7\u8FC7\u8138\uFF0C\u67D4\u987A\u5730\u5F20\u5F00\u817F\uFF0C\u677E\u5F00\u624B\u638C\uFF0C\u5C06\u9759\u83BA\u59B9\u59B9\u7684\u9634\u6237\u66B4\u9732\u5728\u7389\u830E\u4E4B\u4E0B\u3002\u9759\u83BA\u59B9\u59B9\u628A\u9634\u6237\u732E\u7ED9\u81EA\u5DF1\u7684\u65F6\u5019\uFF0C\u4E0D\u4F1A\u60F3\u5230\u6709\u4E00\u5929\u81EA\u5DF1\u4F1A\u628A\u5B83\u732E\u7ED9\u4EC7\u4EBA\u5973\u513F\u7684\u5047\u9633\u5177\u5427\u3002</p><p>\u5C0F\u516C\u4E3B\u4F4E\u4F4E\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u4F60\u7684\u8EAB\u4F53\u4FDD\u517B\u5F97\u5F88\u597D\u3002\u300D\u4E00\u6839\u67D4\u8F6F\u7684\u624B\u6307\u63A2\u5165\u8179\u4E0B\uFF0C\u5C06\u4E00\u4E9B\u6E05\u51C9\u7684\u6DB2\u4F53\u6D45\u6D45\u6D82\u5728\u9634\u6237\u4E0A\u3002</p><p>\u9759\u989C\u5FC3\u91CC\u4E00\u7D27\uFF0C\u5979\u662F\u5728\u7ED9\u81EA\u5DF1\u6D82\u6DEB\u836F\u5417\uFF1F\u8FD9\u4E2A\u6DEB\u8D31\u7684\u5973\u4EBA\uFF0C\u603B\u6709\u4E00\u5929\uFF0C\u6211\u8981\u5E72\u904D\u5979\u6D51\u8EAB\u4E0A\u4E0B\u7684\u6DEB\u6D1E\u3002</p><p>\u4E00\u80A1\u70ED\u70ED\u7684\u6C14\u606F\u4ECE\u4E24\u817F\u95F4\u6DCC\u8FC7\uFF0C\u4E0D\u7528\u770B\uFF0C\u9759\u989C\u4FBF\u77E5\u9053\u5C0F\u516C\u4E3B\u5DF2\u7ECF\u4FEF\u5728\u8EAB\u4E0A\uFF0C\u9A6C\u4E0A\u5C31\u8981\u4E0E\u81EA\u5DF1\u808C\u80A4\u76F8\u63A5\u3002\u5979\u95ED\u4E0A\u773C\uFF0C\u5A07\u7F9E\u65E0\u9650\u5730\u7EC6\u58F0\u9053\uFF1A\u300C\u6C42\u516C\u4E3B\u5782\u601C\u2026\u2026\u300D</p><p>\u4E24\u56E2\u6ED1\u817B\u7684\u8F6F\u8089\u5728\u4E73\u4E0A\u4E00\u89E6\uFF0C\u9707\u98A4\u7740\u6ED1\u5F00\u3002\u9759\u989C\u5FC3\u8DF3\u84E6\u7136\u52A0\u5FEB\uFF0C\u9AD8\u7FD8\u7684\u4E73\u5934\u4E5F\u968F\u4E4B\u9707\u98A4\u8D77\u6765\u3002\u90A3\u662F\u5979\u7684\u4E73\u623F\uFF0C\u90A3\u4E48\u6ED1\u5AE9\u2026\u2026\u4E00\u80A1\u5E7D\u9999\u5E26\u7740\u96BE\u4EE5\u8A00\u8BF4\u7684\u8BF1\u60D1\u8FDB\u5165\u9F3B\u606F\uFF0C\u9759\u989C\u7981\u4E0D\u4F4F\u624B\u6307\u4E00\u52A8\uFF0C\u5DEE\u70B9\u513F\u60F3\u4F38\u624B\u63E1\u4F4F\u90A3\u5BF9\u9165\u4E73\u3002</p><p>\u5C0F\u516C\u4E3B\u7F8E\u76EE\u4E00\u77AC\u4E0D\u77AC\u5730\u51DD\u89C6\u7740\u9759\u989C\uFF0C\u826F\u4E45\uFF0C\u5979\u89E3\u4E0B\u91D1\u73AF\uFF0C\u957F\u53D1\u98D8\u6D12\u800C\u4E0B\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u4F1A\u6709\u4E00\u70B9\u75DB\u3002\u300D</p><p>\u7389\u830E\u63A2\u5165\u9634\u6237\uFF0C\u6CBF\u7740\u5C42\u6B21\u5206\u660E\u7684\u82B1\u74E3\u5411\u4E0B\u6ED1\u53BB\u3002\u9759\u989C\u95ED\u7740\u773C\uFF0C\u51B7\u5389\u7684\u5FC3\u5934\u4E0D\u671F\u7136\u63A0\u8FC7\u9759\u83BA\u59B9\u59B9\u7684\u9762\u5BB9\u3002\u5979\u7F9E\u6DA9\u5730\u644A\u5F00\u8EAB\u4F53\uFF0C\u300C\u9F99\u54E5\u54E5\uFF0C\u9759\u83BA\u628A\u4E00\u5207\u90FD\u7ED9\u4F60\u2026\u2026\u300D</p><p>\u5979\u7684\u68A6\u60F3\u5B9E\u73B0\u4E86\u3002\u5979\u6240\u73CD\u60DC\u7684\u5904\u5B50\uFF0C\u4F1A\u5728\u9F99\u54E5\u54E5\u4F53\u5185\u4E4D\u88C2\uFF0C\u73CD\u8D35\u7684\u5143\u7EA2\u5C06\u5728\u5979\u7684\u8089\u7A74\u98DE\u6E85\u3002\u9F99\u54E5\u54E5\u4F1A\u548C\u5979\u4E00\u8D77\uFF0C\u5206\u4EAB\u5C5E\u4E8E\u5979\u4EEC\u7684\u6BCF\u4E00\u4E1D\u75BC\u75DB\uFF0C\u6BCF\u4E00\u6EF4\u9C9C\u8840\u3002</p><p>\u7389\u830E\u8F7B\u67D4\u5730\u6ED1\u8FC7\u79D8\u5904\uFF0C\u6CBE\u7740\u6D82\u4E0A\u7684\u6DB2\u4F53\u6ED1\u5165\u5904\u5B50\u7684\u5AE9\u7A74\u3002\u9759\u989C\u6697\u6697\u5438\u4E86\u53E3\u6C14\uFF0C\u611F\u89C9\u7740\u90A3\u6839\u5149\u6ED1\u7684\u7EC6\u7269\u6D45\u6D45\u63A2\u5165\u72ED\u7D27\u7684\u5AE9\u7A74\uFF0C\u7136\u540E\u5411\u5916\u9000\u53BB\u3002\u8FD9\u5C31\u662F\u6328\u808F\u5417\uFF1F</p><p>\u9759\u989C\u7A81\u7136\u89C9\u5F97\u4E00\u9635\u597D\u7B11\uFF0C\u5979\u5E72\u8FC7\u65E0\u6570\u5973\u4EBA\uFF0C\u8FD9\u4F1A\u513F\u8EBA\u5728\u8FD9\u91CC\u88AB\u5973\u4EBA\u5E72\uFF0C\u771F\u662F\u5929\u9053\u597D\u8FD8\uFF0C\u62A5\u5E94\u4E0D\u723D\u3002</p><p>\u7EA4\u8F6F\u7684\u53D1\u4E1D\u4ECE\u80A9\u5934\u63A0\u8FC7\uFF0C\u50CF\u98CE\u4E00\u6837\u6E29\u67D4\u3002\u5C0F\u516C\u4E3B\u82AC\u82B3\u7684\u6C14\u606F\u51B2\u6DE1\u4E86\u9759\u989C\u5FC3\u5934\u7684\u6218\u6817\uFF0C\u5979\u7565\u5FAE\u62AC\u8D77\u8170\u81C0\uFF0C\u50CF\u4E00\u4E2A\u6DEB\u5974\u7B49\u5F85\u4E3B\u4EBA\u63D2\u5165\u90A3\u6837\uFF0C\u7B49\u5F85\u7740\u5C0F\u516C\u4E3B\u7ED9\u81EA\u5DF1\u5F00\u82DE\u3002</p><p>\u76F4\u5230\u6B64\u65F6\uFF0C\u9759\u989C\u8FD8\u72B9\u8C6B\u672A\u51B3\uFF0C\u4E0D\u77E5\u9053\u662F\u8BE5\u8001\u8001\u5B9E\u5B9E\u8BA9\u5C0F\u516C\u4E3B\u7834\u4E86\u81EA\u5DF1\u7684\u8EAB\u5B50\uFF0C\u56DE\u53BB\u7EE7\u7EED\u505A\u5979\u7684\u6DEB\u5974\uFF1B\u8FD8\u662F\u7B49\u5979\u7ED9\u81EA\u5DF1\u5F00\u82DE\u4E4B\u540E\uFF0C\u7A81\u7136\u9732\u51FA\u9633\u5177\uFF0C\u628A\u5979\u5E72\u5F97\u670D\u670D\u8D34\u8D34\uFF0C\u5C31\u50CF\u592D\u592D\u90A3\u6837\uFF0C\u5FC3\u7518\u60C5\u613F\u505A\u81EA\u5DF1\u7684\u53C8\u4E00\u53EA\u6BCD\u72D7\u3002</p><p>\u7B2C\u4E8C\u79CD\u9009\u62E9\u592A\u5371\u9669\u4E86\u3002\u5979\u59CB\u7EC8\u770B\u4E0D\u900F\u5C0F\u516C\u4E3B\u7684\u6DF1\u6D45\uFF0C\u4E07\u4E00\u5931\u624B\uFF0C\u90A3\u5C31\u4E00\u8D25\u6D82\u5730\u4E86\u3002\u53EF\u7B2C\u4E00\u79CD\u9009\u62E9\u672A\u514D\u592A\u4E0D\u7518\u5FC3\uFF0C\u597D\u4E0D\u5BB9\u6613\u5F97\u5230\u4E0E\u5C0F\u516C\u4E3B\u808C\u80A4\u76F8\u4EB2\u7684\u673A\u4F1A\uFF0C\u88AB\u5979\u767D\u767D\u5E72\u4E86\u81EA\u5DF1\u7684\u5904\u5B50\uFF0C\u5374\u6CA1\u6709\u4E00\u70B9\u56DE\u62A5\u2026\u2026\u53EA\u6709\u89C1\u673A\u884C\u4E8B\u4E86\u3002\u5979\u4E0D\u662F\u559C\u6B22\u7ED9\u5904\u5B50\u5F00\u82DE\u5417\uFF1F\u5C31\u8BA9\u5979\u73A9\u4E2A\u9AD8\u5174\u597D\u4E86\u3002</p><p>\u300C\u516C\u4E3B\uFF0C\u300D\u9759\u989C\u5A07\u5598\u7EC6\u7EC6\u5730\u633A\u8D77\u4E0B\u8179\uFF0C\u300C\u5974\u5A62\u7684\u5904\u5B50\u8FD8\u7B49\u7740\u60A8\u6765\u5F00\u82DE\u5462\u2026\u2026\u300D</p><p>\u7389\u830E\u518D\u6B21\u8FDB\u5165\u4F53\u5185\u3002\u9759\u989C\u7528\u5C41\u773C\u513F\u63A5\u7EB3\u8FC7\u65E0\u6570\u8089\u68D2\uFF0C\u4F46\u8089\u7A74\u88AB\u4FB5\u5165\u8FD8\u662F\u7B2C\u4E00\u6B21\u3002\u5BC6\u95ED\u7684\u8089\u8154\u751A\u81F3\u6BD4\u809B\u6D1E\u8FD8\u7D27\uFF0C\u5728\u575A\u786C\u7684\u7389\u830E\u4E0B\u7F13\u7F13\u5206\u5F00\uFF0C\u4E00\u76F4\u4F38\u5411\u4F53\u5185\u6DF1\u5904\u3002\u7389\u830E\u9876\u7AEF\u8FD8\u5E26\u7740\u81EA\u5DF1\u7684\u4F53\u6E29\uFF0C\u540E\u9762\u4E00\u7247\u6E29\u51C9\uFF0C\u5149\u6DA6\u7684\u7F8A\u8102\u7389\u78E8\u64E6\u5728\u7EC6\u5AE9\u5374\u7565\u663E\u5E72\u6DA9\u7684\u8089\u58C1\u4E0A\uFF0C\u4F20\u6765\u4E00\u79CD\u5F02\u6837\u7684\u611F\u89C9\u3002</p><p>\u7389\u830E\u63D2\u8FDB\u4E00\u4E2A\u6307\u8282\u957F\u77ED\uFF0C\u4F53\u5185\u5FFD\u7136\u4E00\u7D27\uFF0C\u4E00\u5C42\u97E7\u97E7\u7684\u8584\u819C\u6321\u4F4F\u4E86\u7389\u830E\u524D\u8FDB\u7684\u65B9\u5411\u3002\u9759\u989C\u6545\u610F\u633A\u52A8\u4E0B\u8179\uFF0C\u7528\u90A3\u5C42\u97E7\u819C\u9876\u5F04\u7740\u7389\u830E\uFF0C\u8BA9\u5979\u611F\u89C9\u5230\u8584\u819C\u7684\u5B58\u5728\uFF0C\u5A9A\u773C\u5982\u4E1D\u5730\u817B\u58F0\u9053\uFF1A\u300C\u516C\u4E3B\uFF0C\u8FD9\u5C31\u662F\u5974\u5A62\u7684\u5904\u5973\u819C\u4E86\u2026\u2026\u300D</p><p>\u5C0F\u516C\u4E3B\u5947\u602A\u5730\u671B\u7740\u5979\uFF0C\u6CA1\u6709\u8BF4\u8BDD\uFF0C\u7F8E\u76EE\u5F02\u5F69\u8FDE\u95EA\u3002\u826F\u4E45\uFF0C\u5979\u7F13\u7F13\u6C89\u4E0B\u8170\u80A2\u3002</p><p>\u9759\u989C\u6CA1\u60F3\u5230\u9759\u83BA\u59B9\u59B9\u7684\u5904\u5973\u819C\u4F1A\u6709\u8FD9\u4E48\u575A\u97E7\uFF0C\u90A3\u5C42\u8584\u819C\u5DF2\u7ECF\u51F9\u4E0B\u534A\u5BF8\uFF0C\u5374\u59CB\u7EC8\u6CA1\u6709\u7834\u88C2\u3002</p><p>\u7389\u830E\u7684\u7C97\u7EC6\u5E76\u4E0D\u8DB3\u4EE5\u5F04\u75BC\u8089\u7A74\uFF0C\u751A\u81F3\u53EF\u4EE5\u8BF4\u5F88\u6E29\u67D4\uFF0C\u4F46\u968F\u7740\u7389\u830E\u7684\u8FDB\u5165\uFF0C\u75BC\u75DB\u4ECE\u8584\u819C\u5468\u56F4\u8513\u5EF6\u5F00\u6765\uFF0C\u8089\u58C1\u7D27\u5F20\u5730\u5FAE\u5FAE\u6536\u7D27\u3002\u9759\u989C\u5F3A\u88C5\u7B11\u5BB9\uFF0C\u5F2F\u66F2\u7684\u7389\u817F\u5C3D\u529B\u5F20\u5F00\uFF0C\u597D\u8BA9\u5C0F\u516C\u4E3B\u80FD\u4E0D\u8D39\u529B\u6C14\u5730\u8D2F\u7A7F\u81EA\u5DF1\uFF0C\u624B\u6307\u5374\u4E0D\u7531\u81EA\u4E3B\u5730\u634F\u7D27\u4E86\u88AB\u8925\u3002</p>',102);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
