import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_46" tabindex="-1"><a class="header-anchor" href="#_46" aria-hidden="true">#</a> 46</h1><p>\u661F\u6708\u6E56\u63A5\u5230\u4E66\u4FE1\u5DF2\u5728\u56DB\u4E2A\u65F6\u8FB0\u4E4B\u540E\uFF0C\u9759\u989C\u770B\u7F62\u6CA1\u6709\u8A00\u8BED\uFF0C\u9012\u7ED9\u6674\u96EA\u4FBF\u81EA\u884C\u79BB\u53BB\u3002\u661F\u6708\u6E56\u4E0E\u5EFA\u5EB7\u76F8\u8DDD\u867D\u8FDC\uFF0C\u4F46\u4EE5\u90A3\u5973\u5B50\u7684\u6B66\u529F\uFF0C\u6700\u591A\u4E94\u65E5\u4FBF\u53EF\u62B5\u8FBE\uFF0C\u7B97\u6765\u6B64\u65F6\u5E94\u8BE5\u5DF2\u7ECF\u8FDB\u5165\u7EC8\u5357\u5C71\u3002</p><p>\u6674\u96EA\u5927\u662F\u5947\u602A\uFF0C\u5979\u63A5\u4EFB\u661F\u6708\u6E56\u5BAB\u4E3B\uFF0C\u53EA\u662F\u4E3A\u4E86\u79BB\u5F00\u7239\u7239\uFF0C\u6781\u5C11\u7406\u4F1A\u6559\u4E2D\u4E8B\u52A1\uFF0C\u9664\u4E86\u547D\u592D\u592D\u8BDB\u706D\u6DF3\u4E8E\u68E0\u548C\u6DF3\u4E8E\u7476\u4E24\u5BB6\u5916\uFF0C\u518D\u672A\u6811\u654C\uFF0C\u90A3\u5973\u5B50\u7A76\u7ADF\u662F\u4F55\u65B9\u4EBA\u6C0F\uFF1F</p><p>\u96BE\u9053\u662F\u7239\u7239\u6614\u65E5\u7684\u4EC7\u4EBA\uFF1F</p><p>\u53F6\u884C\u5357\u4E5F\u8BF4\u4E0D\u4E0A\u6765\u54EA\u91CC\u8FD8\u6709\u8FD9\u7B49\u4EBA\u7269\uFF0C\u661F\u6708\u6E56\u5C5E\u4E0B\u63A7\u5236\u7740\u5927\u5C0F\u6570\u767E\u4E2A\u95E8\u6D3E\uFF0C\u4FE1\u606F\u4E4B\u4E30\u5929\u4E0B\u65E0\u53CC\u3002\u6C5F\u6E56\u4E2D\u80FD\u4EBA\u5F02\u58EB\u867D\u591A\uFF0C\u4F46\u50CF\u8FD9\u79CD\u7EDD\u9876\u9AD8\u624B\u4E5F\u662F\u5C48\u6307\u53EF\u6570\uFF0C\u66F4\u4E0D\u4F1A\u662F\u5BC2\u5BC2\u65E0\u540D\u3002</p><p>\u4F46\u53F6\u884C\u5357\u66F4\u5173\u5FC3\u7684\u8FD8\u662F\u6674\u96EA\uFF0C\u773C\u89C1\u5979\u8FD9\u51E0\u65E5\u6CA1\u6709\u5F02\u72B6\uFF0C\u4ED6\u624D\u7565\u7565\u653E\u4E86\u5FC3\u3002\u4F46\u90A3\u8D31\u5A62\u59CB\u7EC8\u662F\u4E2A\u5FC3\u8179\u5927\u60A3\uFF0C\u8981\u65E9\u65E5\u60F3\u529E\u6CD5\u89E3\u51B3\u4E86\u8FD9\u4E2A\u9EBB\u70E6\u3002</p><p>\u5927\u96E8\u65B9\u6674\uFF0C\u5929\u84DD\u5982\u975B\uFF0C\u4E00\u6761\u5F69\u8679\u4ECE\u7FA4\u5CF0\u4E4B\u95F4\u8DE8\u8FC7\uFF0C\u4E03\u5F69\u7EB7\u5448\u3002\u4E2D\u5348\u65F6\u5206\uFF0C\u6E56\u5916\u4F20\u6765\u9E23\u955D\u7684\u9510\u54CD\uFF0C\u901A\u62A5\u6C90\u62A4\u6CD5\u548C\u4E24\u4F4D\u5A18\u5A18\u62B5\u8FBE\u661F\u6708\u6E56\u3002\u7AD9\u5728\u5CB8\u5934\uFF0C\u9759\u989C\u60CA\u8BB6\u5730\u53D1\u73B0\uFF0C\u4E0D\u4EC5\u661F\u6708\u6E56\u4E0A\u4E0B\u9616\u5BAB\u5C3D\u51FA\uFF0C\u8FDE\u4E07\u4E8B\u4E0D\u95EE\u7684\u53F6\u884C\u5357\u4E5F\u4EB2\u81EA\u51FA\u9762\uFF0C\u8FCE\u63A5\u6674\u96EA\u7684\u5A18\u4EB2\u3002</p><p>\u821F\u822B\u9760\u5CB8\uFF0C\u90A3\u4E2A\u66FE\u5728\u7518\u9732\u5BFA\u4E0E\u9759\u989C\u4EA4\u8FC7\u624B\u7684\u9752\u8863\u8001\u8005\u5140\u7136\u7ACB\u5728\u8239\u5934\u3002\u9759\u989C\u7B11\u76C8\u76C8\u7ACB\u5728\u6674\u96EA\u8EAB\u540E\uFF0C\u5766\u7136\u8EAC\u4E0B\u8EAB\u5B50\uFF0C\u5A07\u58F0\u9053\uFF1A\u300C\u6C90\u62A4\u6CD5\u3002\u300D\u6C90\u58F0\u4F20\u867D\u7136\u5DF2\u8131\u79BB\u661F\u6708\u6E56\uFF0C\u4F46\u4F59\u5A01\u72B9\u5728\uFF0C\u6559\u5185\u5E2E\u4F17\u8FD8\u4EE5\u62A4\u6CD5\u76F8\u79F0\u3002</p><p>\u6C90\u58F0\u4F20\u4F3C\u4E4E\u6CA1\u6709\u8BA4\u51FA\u5979\u6765\uFF0C\u53EA\u6DE1\u6DE1\u77A5\u4E86\u9759\u989C\u4E00\u773C\uFF0C\u4FBF\u63FD\u8863\u4E0B\u8239\u3002\u4ED6\u4E0E\u53F6\u884C\u5357\u6781\u4E3A\u719F\u7A14\uFF0C\u4E24\u4EBA\u62F1\u4E86\u62F1\u624B\uFF0C\u8138\u4E0A\u540C\u65F6\u9732\u51FA\u7B11\u5BB9\u3002\u4ED6\u4EEC\u4FE9\u5171\u4E8B\u5DF2\u6709\u4E94\u5341\u5E74\uFF0C\u662F\u661F\u6708\u6E56\u7855\u679C\u4EC5\u5B58\u7684\u4E24\u4F4D\u5143\u8001\u3002</p><p>\u63A5\u7740\u4E00\u4E2A\u8D35\u5987\u6B3E\u6B3E\u8D70\u4E0B\u8237\u68AF\uFF0C\u5979\u7A7F\u7740\u4E00\u8EAB\u9E45\u9EC4\u7684\u5BAB\u88C5\uFF0C\u4E91\u9AFB\u5CE8\u5CE8\uFF0C\u9EDB\u7709\u5165\u9B13\uFF0C\u5507\u89D2\u6302\u7740\u4E00\u7F15\u67D4\u67D4\u7684\u7B11\u610F\uFF0C\u6E29\u5A49\u52A8\u4EBA\uFF0C\u6B63\u662F\u601D\u5983\u7EAA\u7709\u59A9\u3002\u5979\u4EE5\u7687\u5983\u4E4B\u5C0A\uFF0C\u5BF9\u6674\u96EA\u5374\u5341\u5206\u606D\u8C28\uFF0C\u4E0B\u4E86\u8239\u5148\u5BF9\u516C\u4E3B\u65BD\u793C\u81F4\u610F\u3002\u6674\u96EA\u53EA\u70B9\u4E86\u70B9\u5934\uFF0C\u773C\u775B\u5374\u671B\u7740\u8239\u8231\u3002</p><p>\u5341\u4E94\u5E74\u524D\uFF0C\u9759\u989C\u66FE\u89C1\u8FC7\u6674\u96EA\u7684\u6BCD\u4EB2\u3002\u90A3\u662F\u9759\u989C\u89C1\u8FC7\u6700\u52A8\u4EBA\u7684\u5973\u5B50\uFF0C\u90A3\u65F6\u5979\u4E00\u88AD\u7EA2\u8863\uFF0C\u5750\u5728\u6155\u5BB9\u9F99\u8EAB\u4FA7\uFF0C\u660E\u7738\u7693\u9F7F\uFF0C\u96EA\u80A4\u82B1\u8C8C\uFF0C\u5C31\u50CF\u4E09\u73E0\u6811\u4E0A\u4E03\u5B9D\u6512\u5C31\u7684\u540D\u82B1\uFF0C\u987E\u76FC\u95F4\u660E\u8273\u4E0D\u53EF\u65B9\u7269\u3002\u8FD9\u4E9B\u5E74\u517B\u5C0A\u5904\u4F18\uFF0C\u60F3\u6765\u98CE\u97F5\u72B9\u80DC\u4ECE\u524D\u3002</p><p>\u4E24\u540D\u5A62\u5973\u62AC\u7740\u90A3\u53EA\u85E4\u7389\u5236\u6210\u7684\u6447\u7BEE\u4E0B\u4E86\u8239\u3002\u6447\u7BEE\u4E0A\u4E25\u4E25\u5B9E\u5B9E\u76D6\u7740\u660E\u9EC4\u8272\u7684\u9526\u7F0E\uFF0C\u78A7\u7A7A\u5982\u6D17\uFF0C\u6E56\u84DD\u5982\u955C\uFF0C\u9526\u7F0E\u4E0A\u7684\u7EE3\u51E4\u5149\u534E\u6D41\u52A8\uFF0C\u5F77\u4F5B\u8981\u98DE\u5411\u5929\u9645\u7684\u5F69\u8679\u3002</p><p>\u6674\u96EA\u4E0A\u524D\u6276\u4F4F\u6447\u7BEE\uFF0C\u7EAA\u7709\u59A9\u655B\u8863\u8DDF\u5728\u540E\u9762\uFF0C\u6B63\u6B32\u4E3E\u6B65\uFF0C\u6674\u96EA\u671D\u5979\u5FAE\u5FAE\u77A5\u4E86\u4E00\u773C\u3002\u592D\u592D\u5411\u524D\u4E00\u6B65\uFF0C\u4F3C\u7B11\u975E\u7B11\u5730\u8BF4\u9053\uFF1A\u300C\u7EAA\u5A18\u5A18\u591A\u5E74\u672A\u6765\uFF0C\u53EF\u662F\u5FD8\u4E86\u661F\u6708\u6E56\u7684\u89C4\u77E9\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u8138\u987F\u65F6\u7EA2\u4E86\uFF0C\u5979\u7A98\u8FEB\u5730\u505C\u4E0B\u811A\u6B65\uFF0C\u98DE\u5FEB\u5730\u626B\u4E86\u4F17\u4EBA\u4E00\u773C\uFF0C\u5C0F\u58F0\u5E94\u9053\uFF1A\u300C\u662F\u3002\u300D</p><p>\u4E24\u540D\u5A62\u5973\u4E0D\u5F85\u5429\u5490\u4FBF\u8131\u53BB\u4EB5\u88E4\uFF0C\u7EAA\u7709\u59A9\u5F2F\u4E0B\u8170\u80A2\uFF0C\u53F3\u8155\u4E24\u53EA\u7389\u956F\u53D1\u51FA\u51E0\u58F0\u6E05\u60A6\u7684\u8F7B\u54CD\uFF0C\u5979\u62C9\u8D77\u9E45\u9EC4\u7684\u5916\u88D9\uFF0C\u9732\u51FA\u4E00\u89D2\u6D45\u7EEF\u8272\u7684\u4EB5\u88E4\uFF0C\u5148\u677E\u5F00\u8E1D\u4E0A\u675F\u7740\u88E4\u7BA1\u7684\u4E1D\u7EE6\uFF0C\u7136\u540E\u4E24\u624B\u4F38\u5165\u88D9\u5185\uFF0C\u89E3\u5F00\u8170\u95F4\u7684\u8863\u5E26\u3002\u867D\u7136\u7EAA\u7709\u59A9\u7AED\u529B\u63A9\u9970\uFF0C\u4F46\u968F\u7740\u7EA4\u624B\u7EC6\u5C0F\u7684\u52A8\u4F5C\uFF0C\u4F9D\u7136\u80FD\u770B\u5230\u5979\u8170\u95F4\u4E00\u62B9\u5982\u96EA\u7684\u80A4\u5149\u4E00\u6643\u800C\u8FC7\u3002\u7EAA\u7709\u59A9\u6B66\u529F\u65E9\u5DF2\u88AB\u5E9F\uFF0C\u52A8\u4F5C\u518D\u5FEB\u4E5F\u4E0D\u8FC7\u4E0E\u5E38\u4EBA\u65E0\u5F02\uFF0C\u843D\u5728\u9759\u989C\u8FD9\u4E9B\u6B66\u529F\u9AD8\u660E\u4E4B\u4EBA\u773C\u4E2D\uFF0C\u5979\u8131\u4E0B\u4EB5\u88E4\u7684\u6BCF\u4E2A\u7EC6\u8282\u90FD\u5386\u5386\u5728\u76EE\u3002</p><p>\u7531\u4E8E\u6709\u5916\u88D9\u906E\u63A9\uFF0C\u7EAA\u7709\u59A9\u7684\u4EB5\u88E4\u8584\u5982\u8749\u7FFC\uFF0C\u5305\u88F9\u7740\u6D51\u5706\u7FD8\u7F8E\u7684\u7389\u81C0\u3002\u7EC6\u7EB1\u892A\u4E0B\uFF0C\u9732\u51FA\u8102\u7389\u822C\u767D\u817B\u7684\u81C0\u8089\u3002\u5F85\u4EB5\u88E4\u892A\u5230\u81C0\u7F18\uFF0C\u9759\u989C\u60CA\u5947\u5730\u53D1\u73B0\uFF0C\u5728\u8FD9\u4E2A\u6E29\u96C5\u5982\u8BD7\u7684\u8D35\u5987\u8179\u4E0B\uFF0C\u8D6B\u7136\u7A81\u8D77\u4E00\u56E2\u7EA2\u8273\u7684\u8089\u82B1\u3002</p><p>\u9759\u989C\u4ECE\u672A\u89C1\u8FC7\u5982\u6B64\u80A5\u7855\u7684\u5916\u9634\uFF0C\u7EAA\u7709\u59A9\u79D8\u5904\u6BCF\u7247\u5AE9\u8089\u90FD\u6BD4\u5E73\u5E38\u5973\u5B50\u80A5\u539A\u6570\u500D\uFF0C\u4E0D\u77E5\u662F\u5929\u751F\u5F02\u76F8\uFF0C\u8FD8\u662F\u56E0\u4E3A\u5176\u5B83\u7F18\u6545\u3002\u7EAA\u7709\u59A9\u8138\u4E0A\u7EA2\u6F6E\u5DF2\u9000\uFF0C\u5979\u63A9\u4F4F\u5916\u88D9\uFF0C\u987A\u7740\u96EA\u767D\u7684\u7C89\u817F\u5C06\u4EB5\u88E4\u892A\u5230\u8E1D\u95F4\uFF0C\u7136\u540E\u62AC\u811A\u53D6\u4E0B\uFF0C\u82E5\u65E0\u5176\u4E8B\u5730\u62FF\u5728\u624B\u4E2D\u3002</p><p>\u9759\u989C\u6697\u6697\u79F0\u5947\uFF0C\u6674\u96EA\u4E0E\u5979\u5728\u4E00\u8D77\u65F6\u67D4\u987A\u53EF\u4EBA\uFF0C\u5BF9\u8FD9\u4EF6\u4E8B\u5374\u6BEB\u4E0D\u901A\u878D\uFF0C\u7EAA\u7709\u59A9\u8EAB\u4E3A\u6155\u5BB9\u9F99\u7684\u5983\u5B50\uFF0C\u8BF4\u8D77\u6765\u8FD8\u662F\u5979\u7684\u5EB6\u6BCD\uFF0C\u4E5F\u8981\u53D7\u6B64\u89E3\u88E4\u4E4B\u8FB1\u3002\u4E0D\u77E5\u9053\u5979\u5BF9\u81EA\u5DF1\u7684\u4EB2\u5A18\u662F\u5426\u4E00\u89C6\u540C\u4EC1\uFF0C\u540C\u6837\u8981\u8131\u53BB\u4EB5\u88E4\u624D\u80FD\u4E0A\u5C9B\u3002\u4F5C\u4E3A\u6155\u5BB9\u9F99\u6700\u5BA0\u7231\u7684\u5973\u5B50\uFF0C\u5979\u6BCD\u4EB2\u90A3\u53CC\u7389\u817F\u8BE5\u662F\u5982\u4F55\u8FF7\u4EBA\u5462\uFF1F</p><p>\u6674\u96EA\u6276\u7740\u6447\u7BEE\u4E3E\u6B65\u5148\u884C\uFF0C\u592D\u592D\u8DDF\u5728\u540E\u9762\uFF0C\u7D27\u63A5\u7740\u662F\u7EAA\u7709\u59A9\u548C\u51E0\u540D\u6367\u7740\u5DFE\u7BB1\u7684\u5974\u5A62\u3002\u9759\u989C\u6014\u4E86\u7247\u523B\uFF0C\u770B\u5230\u592D\u592D\u5BF9\u5979\u62DB\u624B\uFF0C\u624D\u660E\u767D\u8239\u4E0A\u5DF2\u7ECF\u7A7A\u65E0\u4E00\u4EBA\u3002\u6674\u96EA\u7684\u5A18\u4EB2\u5462\uFF1F\u96BE\u9053\u6CA1\u6765\uFF1F\u9759\u989C\u5FC3\u91CC\u4E0D\u7531\u4E00\u9635\u5931\u843D\uFF0C\u53C8\u9690\u9690\u6709\u79CD\u89E3\u8131\u7684\u8F7B\u677E\u3002\u4F46\u5979\u77E5\u9053\uFF0C\u8F7B\u677E\u53EA\u662F\u6682\u65F6\u7684\uFF0C\u8FDF\u65E9\u6709\u4E00\u5929\uFF0C\u5979\u8981\u786C\u4E0B\u5FC3\u80A0\uFF0C\u5C06\u4EC7\u6068\u53D1\u6CC4\u5728\u6674\u96EA\u7684\u6BCD\u4EB2\u8EAB\u4E0A\u3002</p><p>\u53F6\u884C\u5357\u4E0E\u6C90\u58F0\u4F20\u79BB\u5F00\u4F17\u4EBA\uFF0C\u5E76\u80A9\u671D\u6708\u5C9B\u897F\u7AEF\u7684\u671B\u6708\u4EAD\u8D70\u53BB\u3002\u671B\u6708\u4EAD\u79EF\u77F3\u4E3A\u5E95\uFF0C\u9AD8\u8FD1\u4E08\u8BB8\uFF0C\u4EAD\u57FA\u63A9\u6620\u4E8E\u82B1\u6811\u4E4B\u95F4\uFF0C\u98DE\u7FD8\u7684\u4EAD\u6A90\u72B9\u5982\u78A7\u7FC5\uFF0C\u7AD9\u5728\u4EAD\u95F4\uFF0C\u7FE0\u53F6\u7E41\u82B1\u6D8C\u52A8\u5982\u6F6E\u3002\u6C90\u58F0\u4F20\u8D1F\u624B\u800C\u7ACB\uFF0C\u4E45\u4E45\u672A\u66FE\u4F5C\u58F0\u3002</p><p>\u53F6\u884C\u5357\u53F9\u9053\uFF1A\u300C\u4F60\u6211\u90FD\u5782\u5782\u8001\u77E3\uFF0C\u661F\u6708\u6E56\u5374\u8FD8\u662F\u8FD9\u822C\u3002\u516D\u5341\u5E74\uFF0C\u72B9\u5982\u4E00\u573A\u5927\u68A6\u2026\u2026\u300D</p><p>\u6C90\u58F0\u4F20\u772F\u7740\u773C\uFF0C\u5BA1\u89C6\u7740\u6A90\u89D2\u7684\u94DC\u94C3\uFF0C\u826F\u4E45\u624D\u9053\uFF1A\u300C\u5927\u4E1A\u672A\u5C31\uFF0C\u53F6\u7FC1\u4F55\u4EE5\u5982\u6B64\u611F\u6168\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u5475\u5475\u7B11\u9053\uFF1A\u300C\u884C\u4E86\uFF0C\u8001\u6C90\uFF0C\u4F60\u8FD9\u6B21\u4F1A\u4EB2\u81EA\u51FA\u6765\uFF0C\u6211\u770B\u4E5F\u662F\u6709\u4E9B\u5FC3\u7070\u610F\u51B7\u4E86\u5427\uFF1F\u300D</p><p>\u6C90\u58F0\u4F20\u6728\u7136\u7684\u9762\u988A\u4E00\u677E\uFF0C\u82E6\u7B11\u9053\uFF1A\u300C\u795E\u9F9F\u867D\u5BFF\uFF0C\u72B9\u6709\u7ADF\u65F6\uFF0C\u4F55\u51B5\u6211\u7B49\u3002\u6614\u65E5\u6211\u5E38\u9053\uFF1A\u5386\u4EE3\u5BAB\u4E3B\u591A\u4E0D\u95EE\u4E16\u4E8B\uFF0C\u4EE5\u81F4\u795E\u6559\u65E5\u8870\uFF0C\u82E5\u80FD\u4E00\u6539\u7956\u8BAD\uFF0C\u663E\u626C\u4E16\u95F4\uFF0C\u4F55\u6101\u4E0D\u5929\u4E0B\u666F\u4ECE\uFF1F\u6B64\u756A\u4E00\u51FA\uFF0C\u65B9\u77E5\u4E8B\u4E4B\u96BE\u4E3A\u3002\u300D</p><p>\u300C\u53EF\u662F\u965B\u4E0B\u2026\u2026\u300D</p><p>\u6C90\u58F0\u4F20\u5FAE\u5FAE\u70B9\u5934\uFF0C\u300C\u965B\u4E0B\u65E9\u5DF2\u65E0\u5FC3\u653F\u4E8B\uFF0C\u5E73\u79E6\u4E4B\u540E\u65E5\u89C1\u8427\u6563\uFF0C\u53EA\u6015\u4E0D\u5F85\u4F10\u5B8B\u4FBF\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u6C89\u541F\u534A\u664C\uFF0C\u7B11\u9053\uFF1A\u300C\u4F60\u5982\u4ECA\u8D35\u4E3A\u592A\u5E08\uFF0C\u4E00\u4EBA\u4E4B\u4E0B\uFF0C\u4E07\u4EBA\u4E4B\u4E0A\uFF0C\u4F55\u4E8B\u4E0D\u53EF\u4E3A\uFF1F\u300D</p><p>\u300C\u5C82\u6709\u8FD9\u822C\u5BB9\u6613\u3002\u300D\u6C90\u58F0\u4F20\u626C\u624B\u9053\uFF1A\u300C\u5355\u8FD9\u661F\u6708\u6E56\uFF0C\u6211\u5165\u6559\u65F6\u66FE\u9053\uFF1A\u4F46\u6709\u6211\u6C90\u58F0\u4F20\u5728\uFF0C\u5FC5\u62A4\u6559\u4EE5\u59CB\u7EC8\u3002\u795E\u6559\u4E4B\u89C4\uFF0C\u81EA\u5BAB\u4E3B\u4EE5\u4E0B\u7686\u4E0D\u84C4\u59BB\u5B50\uFF0C\u5973\u5B50\u4E0D\u8FC7\u662F\u70BC\u529F\u4E4B\u9F0E\u7089\uFF0C\u8D31\u5982\u6CE5\u571F\u3002\u7ED3\u679C\u5148\u662F\u6709\u9634\u59EC\u4E4B\u7978\uFF0C\u533A\u533A\u4E00\u4E2A\u8D31\u5974\uFF0C\u4EE5\u5973\u5B50\u4E4B\u8EAB\u800C\u4E3A\u5BAB\u4E3B\uFF0C\u6211\u6C90\u58F0\u4F20\u5374\u65E0\u4E00\u7B56\u76F8\u6D4E\u3002\u300D</p><p>\u9634\u59EC\u4E4B\u4E8B\u53F6\u884C\u5357\u4E0E\u6C90\u58F0\u4F20\u7686\u66FE\u4EB2\u5386\uFF0C\u5982\u4ECA\u60F3\u6765\u8FD8\u662F\u532A\u5937\u6240\u601D\uFF0C\u90A3\u65F6\u600E\u4E48\u4F1A\u8BA9\u4E00\u4E2A\u5973\u5B50\u5F53\u4E0A\u5BAB\u4E3B\uFF1F</p><p>\u300C\u9634\u59EC\u4EA1\u540E\uFF0C\u6211\u4EE5\u4E3A\u795E\u6559\u4E4B\u5384\u5DF2\u7EC8\uFF0C\u5B70\u77E5\u2026\u2026\u300D\u6C90\u58F0\u4F20\u62C2\u7740\u680F\u5E72\u6447\u5934\u7B11\u9053\uFF1A\u300C\u5982\u4ECA\u7684\u5BAB\u4E3B\u53C8\u662F\u5973\u5B50\u3002\u300D</p><p>\u53F6\u884C\u5357\u8FA9\u9053\uFF1A\u300C\u516C\u4E3B\u662F\u965B\u4E0B\u4EB2\u5973\uFF0C\u6267\u638C\u795E\u6559\u65E0\u53EF\u539A\u975E\u3002\u300D</p><p>\u300C\u5BAB\u4E3B\u4EE5\u4E0B\uFF0C\u4E09\u4F4D\u62A4\u6CD5\u767D\u7389\u83BA\u3001\u767D\u7389\u9E42\u5360\u4E86\u4E00\u4F4D\uFF0C\u53E6\u4E00\u4F4D\u4E0D\u7537\u4E0D\u5973\uFF0C\u7B97\u6765\u5DF2\u7ECF\u5360\u4E86\u534A\u6570\uFF1B\u795E\u5C06\u4E2D\u6709\u8273\u51E4\uFF1B\u957F\u8001\u4E2D\u53C8\u6709\u5999\u82B1\u2014\u2014\u6559\u4E2D\u8EAB\u5C45\u9AD8\u4F4D\u7684\u5973\u5B50\u72B9\u80DC\u9634\u59EC\u4E4B\u65F6\u3002\u300D</p><p>\u53F6\u884C\u5357\u6B63\u5F85\u5F00\u53E3\uFF0C\u8FDC\u5904\u7A81\u7136\u4F20\u6765\u4E00\u58F0\u5A07\u5412\uFF1A\u300C\u9F99\u6714\uFF01\u4F60\u7ED9\u6211\u51FA\u6765\uFF01\u300D\u58F0\u97F3\u6E05\u4EAE\uFF0C\u5374\u53C8\u662F\u4E00\u4E2A\u5973\u5B50\u3002</p><p>\u6C90\u58F0\u4F20\u4E0E\u53F6\u884C\u5357\u8FDE\u8882\u8D76\u5230\u6000\u6708\u5CF0\u4E0B\uFF0C\u90A3\u5973\u5B50\u5DF2\u7ECF\u95EF\u8FC7\u516D\u9053\u5173\u5361\uFF0C\u8E0F\u4E0A\u4E86\u795E\u6BBF\u524D\u7684\u77F3\u5880\u3002\u5979\u4E00\u88AD\u767D\u8863\uFF0C\u53CC\u7709\u4FEE\u957F\u5982\u753B\uFF0C\u9752\u4E1D\u7528\u4E00\u65B9\u7D20\u5E15\u675F\u5728\u80A9\u540E\uFF0C\u5468\u8EAB\u672A\u89C1\u4EFB\u4F55\u9970\u7269\uFF0C\u867D\u7136\u770B\u4E0A\u53BB\u5E74\u8FD1\u4E09\u5341\uFF0C\u4F46\u7389\u5BB9\u6E05\u4E3D\u5982\u65B0\uFF0C\u6E29\u5A49\u7D20\u6DE1\uFF0C\u522B\u6709\u4E00\u756A\u97F5\u81F4\u3002\u53EA\u662F\u6B64\u523B\u6C14\u6068\u4EA4\u52A0\uFF0C\u7389\u988A\u5FAE\u5FAE\u53D1\u7EA2\u2014\u2014\u5979\u4F3C\u4E4E\u4ECE\u672A\u53D1\u8FC7\u6012\uFF0C\u7709\u5B87\u95F4\u751A\u81F3\u8FD8\u9690\u7EA6\u6709\u4E00\u4E1D\u7F9E\u610F\u3002</p><p>\u6BBF\u524D\u662F\u65B0\u664B\u7684\u571F\u5802\u957F\u8001\u6F58\u5929\u8000\uFF0C\u4ED6\u4F7F\u4E00\u5BF9\u77ED\u53C9\uFF0C\u53C9\u5C3E\u7531\u94A2\u94FE\u8054\u7ED3\uFF0C\u53EF\u4F5C\u957F\u5175\u5668\u4F7F\u7528\uFF0C\u8FDC\u653B\u8FD1\u51FB\u65E0\u4E0D\u5F97\u5FC3\u5E94\u624B\uFF0C\u800C\u90A3\u5973\u5B50\u5374\u7EA4\u624B\u7A7A\u7A7A\uFF0C\u5355\u4EE5\u4E00\u53CC\u7389\u638C\u5728\u53C9\u5F71\u4E2D\u98D8\u98DE\u3002\u6570\u62DB\u4E00\u8FC7\uFF0C\u6F58\u5929\u8000\u817E\u632A\u4E4B\u9645\u6E10\u6E10\u6EDE\u91CD\u3002\u5FFD\u7136\u95F4\uFF0C\u90A3\u5973\u5B50\u5355\u638C\u4ECE\u53C9\u95F4\u5288\u5165\uFF0C\u5C06\u94A2\u94FE\u7EDE\u5728\u8155\u4E0A\uFF0C\u63A5\u7740\u7D20\u624B\u4E00\u626C\uFF0C\u300C\u5D29\u300D\u7684\u4E00\u58F0\u8106\u54CD\uFF0C\u7ADF\u7136\u7528\u90A3\u53EA\u767D\u5982\u971C\u96EA\u7684\u7693\u8155\uFF0C\u5C06\u7EAF\u94A2\u6253\u5236\u7684\u94C1\u94FE\u751F\u751F\u5D29\u65AD\u3002</p><p>\u571F\u5802\u957F\u8001\u4E24\u624B\u864E\u53E3\u540C\u65F6\u8FF8\u88C2\uFF0C\u94A2\u53C9\u8131\u624B\u800C\u51FA\uFF0C\u4ED6\u5927\u9A87\u9000\u5F00\uFF0C\u751F\u6015\u90A3\u5973\u5B50\u8D81\u52BF\u62A2\u653B\u3002\u90A3\u5973\u5B50\u628A\u94A2\u53C9\u5F80\u5730\u4E0A\u4E00\u4E22\uFF0C\u95EA\u8EAB\u671D\u6BBF\u5185\u63A0\u53BB\uFF0C\u77A7\u4E5F\u4E0D\u77A7\u4ED6\u4E00\u773C\u3002\u6F58\u5929\u8000\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u8116\u5B50\u731B\u7136\u4E00\u7C97\uFF0C\u63A5\u7740\u300C\u54C7\u300D\u7684\u5F20\u5F00\u5927\u5634\uFF0C\u55B7\u51FA\u4E00\u84EC\u7EC6\u6C99\u3002</p><p>\u8FD9\u662F\u571F\u5802\u7EDD\u6280\u300C\u542B\u6C99\u5C04\u5F71\u300D\uFF0C\u51CC\u5389\u9634\u72E0\uFF0C\u4EE4\u4EBA\u9632\u4E0D\u80DC\u9632\uFF0C\u4E0D\u5C11\u6B66\u6797\u4E2D\u7684\u6210\u540D\u8C6A\u6770\u90FD\u60E8\u8D25\u5728\u8FD9\u4E00\u51FB\u4E4B\u4E0B\u3002\u4F46\u90A3\u767D\u8863\u5973\u5B50\u5934\u4E5F\u4E0D\u56DE\uFF0C\u53EA\u4FE1\u624B\u5411\u540E\u4E00\u6325\uFF0C\u75BE\u5C04\u7684\u7EC6\u6C99\u7ACB\u523B\u4E56\u4E56\u98DE\u5165\u888D\u8896\uFF0C\u672A\u66FE\u6389\u843D\u4E00\u7C92\u3002\u63A5\u7740\u90A3\u5973\u5B50\u4E00\u5377\u8863\u8896\uFF0C\u7EC6\u6C99\u6247\u72B6\u98DE\u5F00\uFF0C\u626B\u5728\u8EAB\u540E\u4E00\u4F17\u6559\u5F92\u819D\u4E0A\uFF0C\u5305\u62EC\u6F58\u5929\u8000\u5728\u5185\uFF0C\u5341\u4F59\u540D\u5E2E\u4F17\u5E94\u624B\u6EDA\u5012\uFF0C\u6454\u5F97\u72FC\u72C8\u4E0D\u582A\u3002</p><p>\u300C\u9F99\u6714\uFF01\u4F60\u7ED9\u6211\u51FA\u6765\uFF01\u300D\u90A3\u5973\u5B50\u51E4\u76EE\u542B\u6012\uFF0C\u626C\u624B\u671D\u6BBF\u95E8\u5370\u53BB\u3002\u4EE5\u5979\u638C\u4E0A\u7684\u52B2\u529B\uFF0C\u5C31\u7B97\u6BBF\u95E8\u662F\u5B9E\u94C1\u94F8\u6210\uFF0C\u4E5F\u4F1A\u88AB\u9707\u5F97\u8131\u6846\u98DE\u51FA\uFF0C\u6728\u95E8\u5B9A\u662F\u7C89\u4E3A\u788E\u5C51\u3002</p><p>\u5C31\u5728\u90A3\u5973\u5B50\u4E24\u638C\u5C06\u8981\u5370\u4E0A\u4E4B\u9645\uFF0C\u6BBF\u95E8\u5FAE\u5FAE\u5F00\u4E86\u4E00\u7EBF\uFF0C\u4E00\u53EA\u5E26\u7740\u7FE1\u7FE0\u7389\u956F\u7684\u5C0F\u624B\u6B3E\u6B3E\u4F38\u51FA\uFF0C\u6309\u5728\u5979\u7684\u638C\u4E0A\uFF0C\u6E29\u67D4\u5F97\u5F77\u4F5B\u629A\u6478\u4E00\u822C\u3002\u90A3\u5973\u5B50\u773C\u795E\u4E00\u5229\uFF0C\u5411\u540E\u9000\u4E86\u4E00\u6B65\u3002\u90A3\u53EA\u5C0F\u624B\u7D27\u63A5\u7740\u5411\u524D\u9012\u6765\uFF0C\u4E0E\u5979\u7684\u624B\u638C\u7D27\u7D27\u8D34\u5728\u4E00\u8D77\u3002</p><p>\u767D\u8863\u5973\u5B50\u8D34\u7740\u90A3\u53EA\u5C0F\u624B\u5411\u53F3\u4E0B\u5212\u4E86\u4E2A\u534A\u5F27\uFF0C\u7136\u540E\u5411\u524D\u5FAE\u5FAE\u4E00\u63A8\uFF0C\u63A5\u7740\u5A07\u8EAF\u540E\u4EF0\uFF0C\u624B\u638C\u75BE\u6325\u3002\u300C\u54CE\u5440\u300D\u4E00\u58F0\uFF0C\u95E8\u5185\u7684\u5A07\u5A1C\u5C11\u5973\u88AB\u5979\u4E00\u63A8\u4E00\u62C9\uFF0C\u62FD\u5F97\u6454\u5230\u95E8\u5916\u3002</p><p>\u90A3\u5973\u5B50\u626C\u624B\u6309\u4F4F\u592D\u592D\u7684\u7C89\u9888\uFF0C\u559D\u9053\uFF1A\u300C\u9F99\u6714\u5728\u54EA\u91CC\uFF1F\u300D</p><p>\u592D\u592D\u7389\u8138\u53D1\u767D\uFF0C\u9759\u989C\u6613\u540D\u5165\u6559\u4E4B\u4E8B\u7275\u6D89\u6781\u591A\uFF0C\u4E00\u65E6\u9F99\u6714\u4E0E\u9759\u989C\u4E4B\u95F4\u5173\u7CFB\u66B4\u9732\uFF0C\u540E\u679C\u96BE\u4EE5\u9884\u6599\u3002\u56E0\u6B64\u5979\u542C\u5230\u300C\u9F99\u6714\u300D\u8FD9\u4E2A\u540D\u5B57\uFF0C\u4FBF\u52A8\u4E86\u6740\u673A\uFF0C\u7B2C\u4E00\u62DB\u4FBF\u4F7F\u4E0A\u9ED1\u715E\u638C\u7684\u5DE5\u592B\uFF0C\u60F3\u628A\u8FD9\u5973\u5B50\u6BD9\u4E8E\u638C\u4E0B\u3002\u4F46\u6CA1\u60F3\u5230\u8FD9\u5973\u5B50\u6B66\u529F\u5982\u6B64\u4E4B\u5F3A\uFF0C\u4E00\u9000\u4E00\u6447\u4FBF\u5316\u53BB\u4E86\u9ED1\u715E\u638C\u7684\u5267\u6BD2\uFF0C\u8FD8\u987A\u52BF\u9ECF\u4E0A\u5979\u7684\u624B\u638C\uFF0C\u4F7F\u5979\u6B32\u9000\u4E0D\u5F97\u3002</p><p>\u767D\u8863\u5973\u5B50\u624B\u6307\u5FAE\u5FAE\u4E00\u7D27\uFF0C\u592D\u592D\u9888\u4E2D\u8840\u7BA1\u987F\u65F6\u4E00\u9635\u66B4\u8DF3\uFF0C\u90A3\u5973\u5B50\u8138\u4E0A\u9732\u51FA\u4E00\u4E1D\u5947\u602A\u7684\u795E\u60C5\uFF0C\u8BA4\u771F\u770B\u4E86\u5979\u51E0\u773C\uFF0C\u624B\u6307\u4E00\u6839\u6839\u6162\u6162\u677E\u5F00\uFF0C\u5937\u7136\u9053\uFF1A\u300C\u4F60\u662F\u7537\u5B50\uFF1F\u300D</p><p>\u300C\u975E\u4E5F\u3002\u300D\u82CD\u8001\u7684\u58F0\u97F3\u4ECE\u540E\u54CD\u8D77\uFF0C\u6C90\u58F0\u4F20\u62AC\u638C\u671D\u90A3\u5973\u5B50\u8170\u95F4\u62CD\u6765\uFF0C\u624B\u6CD5\u529B\u9053\u4E0E\u5979\u521A\u624D\u4E00\u822C\u65E0\u4E8C\u3002</p><p>\u767D\u8863\u5973\u5B50\u9762\u5BB9\u6C89\u9759\u5730\u5C01\u4E86\u592D\u592D\u7684\u7A74\u9053\uFF0C\u7136\u540E\u65CB\u8FC7\u8EAB\u5B50\uFF0C\u53F3\u624B\u4E94\u6307\u4F18\u96C5\u5730\u671D\u4E0A\u6563\u5F00\uFF0C\u5411\u6C90\u58F0\u4F20\u8155\u4E0A\u6258\u6765\u3002\u5979\u7684\u6307\u6CD5\u8F7B\u626C\u5A49\u4E3E\uFF0C\u76F4\u5982\u7EA2\u7C89\u4F73\u4EBA\u6311\u62B9\u7434\u5F26\u822C\u67D4\u6DE1\uFF0C\u4F46\u6C90\u58F0\u4F20\u770B\u51FA\u5979\u4E94\u6307\u53C2\u5DEE\uFF0C\u6307\u5C16\u5404\u5BF9\u7740\u8155\u4E0A\u4E00\u5904\u7A74\u9053\uFF0C\u5206\u660E\u662F\u4E00\u79CD\u975E\u540C\u5BFB\u5E38\u7684\u622A\u8109\u624B\u6CD5\u3002\u4E24\u624B\u4E00\u89E6\u5373\u5206\uFF0C\u90A3\u5973\u5B50\u6307\u4E0A\u7684\u771F\u6C14\u6DF3\u548C\u5E73\u6B63\uFF0C\u51B2\u6DE1\u82E5\u865A\uFF0C\u662F\u7EAF\u6B63\u7684\u4F5B\u95E8\u7384\u529F\uFF0C\u4F46\u4E0E\u4E2D\u571F\u5374\u5927\u76F8\u8FF3\u5EAD\u3002</p><p>\u53F6\u884C\u5357\u533B\u672F\u901A\u795E\uFF0C\u6B66\u529F\u5374\u975E\u5176\u957F\uFF0C\u53EA\u5728\u65C1\u63A0\u9635\u3002\u4E24\u4EBA\u4EA4\u624B\u95F4\uFF0C\u7A7A\u6C14\u4E2D\u6DE1\u6DE1\u6765\u4E00\u80A1\u5947\u5F02\u7684\u9999\u6C14\uFF0C\u53F6\u884C\u5357\u4ED4\u7EC6\u55C5\u4E86\u55C5\uFF0C\u7709\u5934\u4E0D\u7531\u6E10\u6E10\u6311\u8D77\uFF0C\u6EE1\u773C\u7591\u60D1\u5730\u671B\u7740\u90A3\u4E2A\u5973\u5B50\u3002</p><p>\u6C90\u58F0\u4F20\u9000\u5F00\u4E00\u6B65\uFF0C\u8138\u8272\u51DD\u91CD\u5730\u4ECE\u8896\u4E2D\u6478\u51FA\u4E00\u622A\u77ED\u68CD\uFF0C\u50CF\u8981\u9012\u5230\u5979\u624B\u4E2D\u4E00\u6837\u671D\u524D\u7F13\u7F13\u9001\u53BB\u3002\u767D\u8863\u5973\u5B50\u5E76\u8D77\u4E24\u6839\u4FEE\u957F\u7EA4\u67D4\u7684\u7389\u6307\uFF0C\u5728\u77ED\u68CD\u9876\u7AEF\u8F7B\u8F7B\u4E00\u89E6\uFF0C\u5316\u53BB\u950B\u8292\uFF0C\u63A5\u7740\u7389\u624B\u72B9\u5982\u5947\u82B1\u6012\u653E\uFF0C\u5E7B\u5316\u51FA\u65E0\u6570\u7389\u767D\u8272\u7684\u6307\u5F71\uFF0C\u5728\u77ED\u68CD\u5468\u56F4\u76D8\u65CB\u98DE\u821E\u3002</p><p>\u4E24\u4EBA\u5728\u6BBF\u524D\u7684\u4EA4\u624B\u4F3C\u4E4E\u6781\u6162\uFF0C\u53C8\u4F3C\u4E4E\u6781\u5FEB\u3002\u4ED6\u4EEC\u811A\u6B65\u90FD\u672A\u66FE\u79FB\u52A8\u8FC7\uFF0C\u751A\u81F3\u8FDE\u624B\u81C2\u4E5F\u4E0D\u66FE\u6709\u4E00\u70B9\u591A\u4F59\u7684\u52A8\u4F5C\uFF0C\u53EA\u6709\u624B\u8155\u4E0E\u4E94\u6307\u5728\u72ED\u5C0F\u7684\u7A7A\u95F4\u5185\uFF0C\u5FEB\u6377\u65E0\u4F26\u5730\u7FFB\u98DE\u4E0D\u5B9A\u3002\u6C90\u58F0\u4F20\u624B\u4E2D\u7684\u77ED\u68CD\u5FFD\u52FE\u5FFD\u6311\u5FFD\u957F\u5FFD\u77ED\uFF0C\u53D8\u5E7B\u65E0\u7A77\u3002\u800C\u90A3\u5973\u5B50\u5219\u5BF9\u4ED6\u7684\u624B\u8155\u624B\u6307\u6BEB\u4E0D\u7406\u776C\uFF0C\u7389\u624B\u65F6\u6307\u65F6\u638C\uFF0C\u53EA\u4E0E\u77ED\u68CD\u4EA4\u51FB\u3002</p><p>\u4E00\u67F1\u9999\u5DE5\u592B\u540E\uFF0C\u6C90\u58F0\u4F20\u9996\u5148\u53D8\u62DB\u53D1\u96BE\uFF0C\u4ED6\u5C55\u81C2\u7FFB\u8EAB\u8DC3\u8D77\uFF0C\u5DE6\u624B\u4E94\u6307\u5982\u94A9\uFF0C\u75BE\u5982\u95EA\u7535\u5730\u6293\u5411\u90A3\u5973\u5B50\u540E\u5FC3\u3002\u767D\u8863\u5973\u5B50\u811A\u4E0B\u4E00\u6ED1\uFF0C\u5F77\u4F5B\u5728\u6C34\u4E0A\u6F02\u8FC7\u822C\uFF0C\u8F7B\u98D8\u98D8\u5212\u4E86\u5C0F\u5708\u5B50\uFF0C\u907F\u5F00\u6C90\u58F0\u4F20\u7684\u4E94\u6307\uFF0C\u63A5\u7740\u79C0\u8DB3\u5FAE\u626C\uFF0C\u8DB3\u5C16\u70B9\u5411\u6C90\u58F0\u4F20\u7684\u819D\u5F2F\u3002</p><p>\u6C90\u58F0\u4F20\u8138\u4E0A\u9752\u6C14\u4E00\u95EA\u800C\u8FC7\uFF0C\u5BF9\u5979\u7684\u7EA4\u8DB3\u4E0D\u95EA\u4E0D\u907F\uFF0C\u77ED\u68CD\u5374\u4ECE\u8170\u540E\u7FFB\u51FA\uFF0C\u6084\u65E0\u58F0\u606F\u5730\u523A\u5411\u5973\u5B50\u8170\u8179\u3002\u5C31\u5728\u6C90\u58F0\u4F20\u819D\u5F2F\u4E2D\u811A\u7684\u540C\u65F6\uFF0C\u767D\u8863\u5973\u5B50\u7684\u624B\u638C\u4E5F\u633D\u4F4F\u4E86\u77ED\u68CD\u3002\u771F\u6C14\u76F8\u4EA4\uFF0C\u4E24\u4EBA\u5404\u9000\u4E00\u6B65\uFF0C\u4F9D\u7136\u662F\u4E0D\u5206\u9AD8\u4E0B\u3002</p><p>\u592D\u592D\u8EBA\u5728\u5730\u4E0A\uFF0C\u773C\u73E0\u6EF4\u6E9C\u6E9C\u8F6C\u4E2A\u4E0D\u505C\u3002\u90A3\u5973\u5B50\u771F\u6C14\u522B\u8D70\u8E4A\u5F84\uFF0C\u65E0\u8BBA\u5979\u600E\u4E48\u8FD0\u529F\uFF0C\u90FD\u65E0\u6CD5\u51B2\u5F00\u7A74\u9053\u3002\u4E0D\u53EA\u662F\u5979\uFF0C\u521A\u624D\u88AB\u7EC6\u6C99\u51FB\u4E2D\u73AF\u8DF3\u7A74\u7684\u5341\u4F59\u540D\u5E2E\u4F17\u4E5F\u662F\u4E00\u822C\u3002</p><p>\u6C90\u58F0\u4F20\u6A2A\u68CD\u800C\u7ACB\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u9601\u4E0B\u597D\u529F\u592B\u3002\u4E0D\u77E5\u53EF\u662F\u5929\u7AFA\u4E03\u5B9D\u6CD5\u76F8\u4E4B\u9996\u7684\u8FE6\u7F57\u771F\u6C14\uFF1F\u300D</p><p>\u767D\u8863\u5973\u5B50\u626C\u8D77\u7389\u6307\uFF0C\u5C06\u9B13\u4FA7\u7684\u4E00\u7F15\u79C0\u53D1\u63A0\u5230\u8033\u540E\uFF0C\u63A5\u7740\u53F3\u624B\u4E2D\u6307\u6390\u5728\u62C7\u6307\u4E2D\u592E\uFF0C\u5176\u4F59\u4E09\u6307\u5E73\u5E73\u4F38\u51FA\uFF0C\u4EAE\u4E86\u4E00\u4E2A\u6CD5\u8BC0\uFF0C\u51DD\u671B\u7740\u84C4\u52B2\u5F85\u53D1\u7684\u6C90\u58F0\u4F20\uFF0C\u5BF9\u8001\u4EBA\u7684\u773C\u5149\u4E5F\u9887\u4E3A\u94A6\u4F69\u3002</p><p>\u6C90\u58F0\u4F20\u9664\u5728\u8273\u51E4\u624B\u4E0B\u5C0F\u632B\u4EE5\u5916\uFF0C\u5E73\u751F\u672A\u9047\u654C\u624B\uFF0C\u5982\u4ECA\u8001\u800C\u5F25\u8FA3\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u4E03\u5B9D\u6CD5\u76F8\u7384\u5965\u65E0\u7A77\uFF0C\u8001\u592B\u95FB\u540D\u5DF2\u4E45\uFF0C\u4ECA\u65E5\u5C31\u6765\u9886\u6559\u9601\u4E0B\u7684\u5929\u7AFA\u7EDD\u5B66\u3002\u300D</p><p>\u6BBF\u95E8\u6253\u5F00\u4E00\u6247\uFF0C\u4E00\u4E2A\u5A62\u5973\u95EA\u8EAB\u51FA\u6765\uFF0C\u5BF9\u767D\u8863\u5973\u5B50\u606D\u606D\u656C\u656C\u5730\u65BD\u4E86\u4E00\u793C\uFF0C\u8BF4\u9053\uFF1A\u300C\u516C\u4E3B\u8BF7\u5C0A\u9A7E\u5165\u6BBF\u3002\u300D</p><p>\u542C\u5230\u516C\u4E3B\u8C15\u65E8\uFF0C\u6C90\u58F0\u4F20\u7F13\u7F13\u6536\u56DE\u77ED\u68CD\u3002\u767D\u8863\u5973\u5B50\u8F6C\u8EAB\u65F6\uFF0C\u76EE\u5149\u5728\u53F6\u884C\u5357\u6B8B\u7F3A\u7684\u53F3\u624B\u4E0A\u505C\u4E86\u4E00\u4E0B\uFF0C\u624D\u63D0\u88D9\u8DE8\u8FC7\u95E8\u69DB\u3002</p><p>\u6C90\u58F0\u4F20\u5E72\u54B3\u4E00\u58F0\uFF0C\u5524\u9053\uFF1A\u300C\u8001\u53F6\u3002\u300D</p><p>\u53F6\u884C\u5357\u4F3C\u4E4E\u6709\u4E9B\u9B42\u4E0D\u5B88\u820D\uFF0C\u534A\u664C\u5012\u62BD\u4E00\u53E3\u51C9\u6C14\uFF0C\u6014\u6014\u8BF4\u9053\uFF1A\u300C\u4E16\u95F4\u679C\u7136\u6709\u5982\u6B64\u4E4B\u4EBA\u2026\u2026\u300D</p><p>\u300C\u5514\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u7684\u773C\u795E\u50CF\u770B\u5230\u5947\u4E39\u5999\u836F\u4E00\u822C\u95EA\u4EAE\uFF0C\u5583\u5583\u9053\uFF1A\u300C\u8FD9\u5973\u5B50\u8EAB\u5177\u81F3\u9634\u4E4B\u4F53\uFF0C\u4EE5\u9EC4\u7CBE\u77F3\u4E73\u4E3A\u98DF\uFF0C\u53C8\u4FEE\u4E60\u4F5B\u95E8\u7384\u529F\uFF0C\u5E38\u5E74\u6D78\u6DEB\u4E8E\u767E\u836F\u4E4B\u95F4\uFF0C\u8840\u8109\u5F02\u4E8E\u5E38\u4EBA\uFF0C\u624D\u4F1A\u6709\u5982\u6B64\u6C14\u606F\u3002\u96BE\u9053\u662F\u2026\u2026\u300D</p><p>\u2026.\u6587.\u2026;</p><p>\u2026.\u4EBA.\u2026;</p><p>\u2026.\u4E66.\u2026;</p><p>\u2026.\u5C4B.\u2026;</p><p>\u2026.\u5C0F.\u2026;</p><p>\u2026.\u8BF4.\u2026;</p><p>\u2026.\u4E0B.\u2026;</p><p>\u2026.\u8F7D.\u2026;</p><p>\u2026.\u7F51.\u2026;</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7EB5\u7136\u662F\u6B63\u5348\u65F6\u5206\uFF0C\u795E\u6BBF\u4E5F\u5E7D\u6697\u5982\u591C\u3002\u7CBE\u81F4\u7684\u96D5\u67F1\u6491\u8D77\u5DE8\u5927\u7684\u7A79\u9876\uFF0C\u867D\u7136\u521A\u521A\u5EFA\u6210\uFF0C\u5374\u5F77\u4F5B\u5DF2\u878D\u5165\u661F\u6708\u6E56\u5343\u5E74\u5386\u53F2\u4E4B\u4E2D\u3002\u67F1\u8EAB\u5E03\u6EE1\u7E41\u590D\u800C\u8BE1\u5F02\u7684\u96D5\u9970\uFF0C\u9AD8\u4E0D\u89C1\u9876\u3002\u8BBE\u5728\u67F1\u65C1\u7684\u957F\u660E\u706F\u72B9\u5982\u6EE1\u5BA4\u661F\u8FB0\uFF0C\u62F1\u536B\u7740\u6BBF\u4E0A\u660E\u73E0\u822C\u5C11\u5973\u3002</p><p>\u89C1\u5230\u661F\u6708\u6E56\u5982\u4ECA\u7684\u4E3B\u4EBA\u7ADF\u7136\u662F\u5982\u6B64\u7F8E\u8C8C\u7684\u5C11\u5973\uFF0C\u767D\u8863\u5973\u5B50\u4E0D\u7981\u5FAE\u5FAE\u6709\u4E9B\u9519\u6115\uFF0C\u5979\u6311\u8D77\u5A25\u7709\uFF0C\u51DD\u7136\u95EE\u9053\uFF1A\u300C\u9F99\u6714\u5728\u54EA\u91CC\uFF1F\u4E3A\u4F55\u4E0D\u51FA\u6765\u89C1\u6211\u3002\u300D</p><p>\u5E7D\u6697\u4E2D\uFF0C\u6674\u96EA\u7684\u4E00\u88AD\u9ED1\u8863\u975E\u4F46\u6CA1\u6709\u4E0E\u6697\u8272\u878D\u4E3A\u4E00\u4F53\uFF0C\u53CD\u800C\u6108\u53D1\u9192\u76EE\u3002\u5411\u7740\u706F\u706B\u7684\u8863\u895F\u4E0A\uFF0C\u6CDB\u8D77\u6781\u7EC6\u7684\u91D1\u8272\u5149\u6CFD\uFF0C\u9690\u9690\u52FE\u52D2\u51FA\u4E00\u53EA\u626C\u8D77\u7684\u51E4\u7FFC\u3002</p><p>\u6674\u96EA\u4E4C\u4EAE\u7684\u773C\u7738\u51DD\u89C6\u7740\u8FD9\u4E2A\u672A\u66FE\u8C0B\u9762\u7684\u5973\u5B50\uFF0C\u826F\u4E45\u624D\u9053\uFF1A\u300C\u4F60\u8BF4\u7684\u90A3\u4E2A\u4EBA\u2026\u2026\u5DF2\u7ECF\u4E0D\u5728\u4EBA\u4E16\u4E86\u3002\u300D</p><p>\u767D\u8863\u5973\u5B50\u7389\u5BB9\u60E8\u53D8\uFF0C\u9999\u8EAF\u5F77\u4F5B\u51DD\u56FA\u4E00\u822C\uFF0C\u50F5\u5728\u5F53\u573A\u3002</p><p>\u592D\u592D\u62D6\u7740\u6B65\u5B50\u8D70\u8FDB\u795E\u6BBF\uFF0C\u5979\u7531\u6C90\u58F0\u4F20\u5F3A\u884C\u89E3\u5F00\u7A74\u9053\uFF0C\u8170\u817F\u8840\u8109\u672A\u7545\uFF0C\u6B65\u4F10\u9887\u4E3A\u602A\u5F02\u3002\u5979\u8D70\u5230\u516C\u4E3B\u6848\u524D\uFF0C\u63ED\u5F00\u9999\u7089\uFF0C\u5C06\u4E00\u679A\u9F99\u773C\u5927\u5C0F\u7684\u6C89\u9999\u653E\u5165\u7089\u5185\u3002</p><p>\u6674\u96EA\u6D45\u7B11\u9053\uFF1A\u300C\u5FEB\u6536\u8D77\u6765\u5427\uFF0C\u83AB\u8BA9\u4EBA\u5BB6\u7B11\u8BDD\u3002\u4E16\u95F4\u6709\u54EA\u79CD\u6BD2\u7269\u80FD\u7792\u5F97\u8FC7\u68B5\u4ED9\u5B50\u7684\u6CD5\u773C\u5462\uFF1F\u300D</p><p>\u592D\u592D\u779F\u4E86\u68B5\u96EA\u828D\u4E00\u773C\uFF0C\u5C34\u5C2C\u5730\u53D6\u56DE\u8FF7\u9999\uFF0C\u7AD9\u5230\u5C4F\u98CE\u65C1\u8FB9\u3002\u5F53\u65E5\u6BBF\u4E0A\u7684\u7389\u5236\u5C4F\u98CE\u5DF2\u88AB\u51FB\u788E\uFF0C\u5982\u4ECA\u6446\u7684\u662F\u4E00\u67B6\u56DB\u6298\u9526\u5C4F\uFF0C\u5D4C\u7740\u7CBE\u7F8E\u7684\u523A\u7EE3\u3002\u5979\u5C4F\u606F\u655B\u5BB9\uFF0C\u4E24\u624B\u4EA4\u63E1\u8EAB\u524D\uFF0C\u50CF\u4E2A\u4E56\u5DE7\u7684\u5C0F\u5A62\u4F8D\u7ACB\u5728\u6674\u96EA\u8EAB\u540E\u3002</p><p>\u68B5\u96EA\u828D\u518D\u5EA6\u5F00\u53E3\uFF0C\u5374\u95EE\u9053\uFF1A\u300C\u96EA\u5CF0\u795E\u5C3C\u5462\uFF1F\u300D\u96EA\u5CF0\u795E\u5C3C\u662F\u5979\u7684\u597D\u53CB\uFF0C\u5341\u4E94\u524D\u5931\u9677\u4E8E\u661F\u6708\u6E56\uFF0C\u4ECE\u6B64\u97F3\u8BAF\u7686\u65E0\u3002\u5979\u6B63\u662F\u56E0\u6B64\u624D\u4ECE\u5357\u6D77\u6765\u5230\u4E2D\u539F\uFF0C\u9047\u4E0A\u4E86\u6539\u53D8\u5979\u540E\u534A\u751F\u7684\u9F99\u6714\u3002</p><p>\u6674\u96EA\u6DE1\u6DE1\u9053\uFF1A\u300C\u90A3\u4E2A\u4EBA\uFF0C\u4E5F\u5DF2\u7ECF\u4E0D\u5728\u4EBA\u4E16\u3002\u300D</p><p>\u300C\u5979\u4EEC\u7684\u9057\u9AA8\u5462\uFF1F\u300D</p><p>\u6674\u96EA\u6447\u4E86\u6447\u5934\u3002</p><p>\u68B5\u96EA\u828D\u6C89\u9ED8\u79FB\u65F6\uFF0C\u5FFD\u7136\u6CE3\u9053\uFF1A\u300C\u75F4\u513F\uFF0C\u75F4\u513F\u2026\u2026\u300D</p><p>\u6DF3\u4E8E\u7476\u5931\u8E2A\uFF0C\u5468\u5B50\u6C5F\u66B4\u6B7B\uFF0C\u51CC\u96C5\u7434\u4E0B\u843D\u4E0D\u660E\uFF0C\u4E00\u8FDE\u4E32\u602A\u4E8B\u5F15\u8D77\u4E86\u68B5\u96EA\u828D\u7684\u4E0D\u5B89\u3002\u5979\u56DB\u5904\u5BFB\u627E\u9F99\u6714\u4E0D\u679C\uFF0C\u6700\u540E\u51ED\u7740\u4ECE\u4E5D\u534E\u5251\u6D3E\u67E5\u5230\u7684\u86DB\u4E1D\u9A6C\u8FF9\uFF0C\u5F97\u77E5\u51CC\u96C5\u7434\u66FE\u8D74\u5EFA\u5EB7\uFF0C\u4E00\u8DEF\u8FFD\u81F3\u9690\u5982\u5EB5\u3002\u5F53\u4ECE\u9773\u5982\u70DF\u53E3\u4E2D\u542C\u5230\u6709\u4E2A\u4E5D\u534E\u5F1F\u5B50\u4E3A\u5165\u661F\u6708\u6E56\uFF0C\u4E0D\u60DC\u51FA\u5356\u5E08\u5A18\uFF0C\u5979\u624D\u660E\u767D\u53D1\u751F\u4E86\u4EC0\u4E48\u4E8B\u3002</p><p>\u68B5\u96EA\u828D\u77E5\u9053\u6714\u513F\u4E3A\u6C42\u62A5\u4EC7\u4E0D\u62E9\u624B\u6BB5\uFF0C\u5374\u6CA1\u60F3\u5230\u4ED6\u4F1A\u5982\u6B64\u5351\u9119\u3002\u5979\u4E0D\u7720\u4E0D\u4F11\u4ECE\u5EFA\u5EB7\u4E00\u8DEF\u8D76\u81F3\u7EC8\u5357\uFF0C\u5C31\u662F\u8981\u4EB2\u773C\u770B\u770B\u81EA\u5DF1\u7684\u4E49\u5B50\u7A76\u7ADF\u662F\u4EBA\u8FD8\u662F\u5996\u9B54\uFF0C\u4E0D\u6599\u542C\u5230\u7684\uFF0C\u5374\u662F\u5669\u8017\u3002</p><p>\u6674\u96EA\u6E29\u8A00\u9053\uFF1A\u300C\u5F80\u8005\u5DF2\u77E3\uFF0C\u68B5\u4ED9\u5B50\u83AB\u8981\u96BE\u8FC7\u2026\u2026\u9F99\u54E5\u54E5\u8BF4\uFF0C\u4ED6\u6C38\u8FDC\u90FD\u8C22\u8C22\u4F60\u7684\u3002\u300D</p><p>\u68B5\u96EA\u828D\u62AC\u8D77\u773C\uFF0C\u65E0\u9650\u54C0\u4F24\u5730\u671B\u7740\u6BBF\u4E0A\u3002\u5FFD\u7136\u95F4\uFF0C\u4E00\u9053\u5BD2\u5149\u6BEB\u65E0\u5F81\u5146\u5730\u4ECE\u5979\u8896\u4E2D\u98DE\u51FA\uFF0C\u4ECE\u6674\u96EA\u8138\u4FA7\u75BE\u5C04\u800C\u8FC7\u3002</p><p>\u6674\u96EA\u4EE5\u4E3A\u5979\u662F\u8981\u4E3A\u4E49\u5B50\u548C\u597D\u53CB\u62A5\u4EC7\uFF0C\u5F53\u4E0B\u51DD\u795E\u6212\u5907\uFF0C\u4F46\u90A3\u9053\u5BD2\u5149\u4E0E\u5979\u9694\u4E86\u6570\u5BF8\uFF0C\u8FF3\u76F4\u98DE\u5F80\u8EAB\u540E\u3002\u8BE7\u5F02\u95F4\uFF0C\u5BD2\u5149\u5DF2\u7ECF\u7A7F\u900F\u5C4F\u98CE\uFF0C\u63A5\u7740\u5411\u4E0A\u4E00\u8DF3\uFF0C\u5212\u7834\u4E86\u5C4F\u98CE\u4E0A\u7684\u9526\u7EE3\u3002</p><p>\u5BD2\u5149\u300C\u5F53\u5577\u300D\u4E00\u58F0\uFF0C\u6389\u5728\u4E00\u4E2A\u5A07\u8273\u5982\u82B1\u7684\u7FE0\u8863\u5973\u5B50\u811A\u8FB9\u3002</p>',89);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
