import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_47" tabindex="-1"><a class="header-anchor" href="#_47" aria-hidden="true">#</a> 47</h1><p>\u504C\u5927\u7684\u795E\u6BBF\u98D8\u8361\u7740\u706F\u706B\u660E\u706D\u7684\u58F0\u97F3\uFF0C\u68B5\u96EA\u828D\u5FC3\u788E\u5730\u671B\u7740\u81EA\u5DF1\u4E00\u624B\u4F5C\u51FA\u7684\u5C11\u5973\uFF0C\u6CEA\u73E0\u4E00\u6EF4\u6EF4\u6389\u5728\u8EAB\u524D\u3002</p><p>\u9759\u989C\u5AE3\u7136\u4E00\u7B11\uFF0C\u4FEF\u8EAB\u62FE\u8D77\u534E\u4F57\u5200\uFF0C\u7A7F\u8FC7\u7834\u788E\u7684\u5C4F\u98CE\uFF0C\u4E00\u8FB9\u7528\u4E1D\u5DFE\u7EC6\u7EC6\u62B9\u62ED\uFF0C\u4E00\u8FB9\u8D70\u5230\u68B5\u96EA\u828D\u9762\u524D\uFF0C\u9012\u8FC7\u5C0F\u5200\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u7684\u5200\u6389\u4E86\u3002\u300D</p><p>\u68B5\u96EA\u828D\u54FD\u54BD\u9053\uFF1A\u300C\u4F60\u4E3A\u4EC0\u4E48\u8981\u9A97\u6211\uFF1F\u300D</p><p>\u9759\u989C\u59D4\u5C48\u5730\u8BF4\uFF1A\u300C\u5B69\u513F\u6CA1\u6709\u9A97\u4F60\u554A\u3002\u300D</p><p>\u68B5\u96EA\u828D\u75DB\u5FC3\u5730\u98A4\u58F0\u9053\uFF1A\u300C\u6211\u53EA\u7ED9\u4F60\u79FB\u5165\u4E00\u6837\u91CE\u517D\u7684\u80A2\u4F53\uFF0C\u4F60\u5C31\u53D8\u6210\u79BD\u517D\u4E86\u5417\uFF1F\u6714\u513F\u2026\u2026\u300D</p><p>\u5C11\u5973\u5FAE\u7B11\u9053\uFF1A\u300C\u6714\u513F\u5DF2\u7ECF\u6B7B\u4E86\u3002\u73B0\u5728\u53EA\u5269\u4E0B\u4E86\u4E00\u4E2A\u9759\u989C\uFF0C\u9F99\u9759\u989C\u3002\u300D</p><p>\u5C11\u5973\u660E\u8273\u7EDD\u4F26\u7684\u5BB9\u8C8C\uFF0C\u4F7F\u68B5\u96EA\u828D\u5F77\u4F5B\u9762\u5BF9\u4E00\u4E2A\u719F\u6089\u7684\u964C\u751F\u4EBA\uFF0C\u5979\u65E0\u58F0\u6DCC\u7740\u6CEA\uFF0C\u8F7B\u95EE\u9053\uFF1A\u300C\u4F60\u60F3\u8981\u7684\u662F\u4EC0\u4E48\u5462\uFF1F\u6714\u513F\uFF1F\u300D</p><p>\u9759\u989C\u7738\u4E2D\u95EA\u8FC7\u4E00\u62B9\u5F02\u6837\u7684\u5149\u5F69\uFF0C\u826F\u4E45\u626C\u8D77\u7693\u8155\uFF0C\u671D\u8EAB\u540E\u8F7B\u8F7B\u70B9\u53BB\uFF0C\u300C\u5979\uFF0C\u548C\u5979\u3002\u300D</p><p>\u6674\u96EA\u7684\u660E\u5A9A\u548C\u592D\u592D\u7684\u5996\u8273\uFF0C\u5B9B\u5982\u4E00\u5BF9\u59FF\u6001\u5404\u5F02\u7684\u540D\u82B1\uFF0C\u5728\u6CA1\u6709\u9633\u5149\u7684\u6697\u5904\u5404\u81EA\u5410\u9732\u82B3\u534E\u3002\u5979\u4EEC\u7684\u773C\u775B\u53C8\u9759\u53C8\u6DF1\uFF0C\u987E\u76FC\u95F4\u59CB\u7EC8\u56F4\u7ED5\u7740\u9759\u989C\u3002\u68B5\u96EA\u828D\u6CEA\u773C\u5A46\u5A11\u5730\u529D\u9053\uFF1A\u300C\u56DE\u6765\u597D\u5417\uFF1F\u6714\u513F\uFF0C\u4E0D\u8981\u518D\u8D70\u4E86\u2026\u2026\u300D</p><p>\u9759\u989C\u633A\u8D77\u9AD8\u8038\u7684\u4E73\u623F\uFF0C\u7B11\u9053\uFF1A\u300C\u56DE\u54EA\u91CC\u5462\uFF1F\u8FD9\u91CC\u662F\u6211\u7684\u5BB6\u554A\u3002\u300D</p><p>\u795E\u6BBF\u5BD2\u610F\u4FB5\u4EBA\uFF0C\u68B5\u96EA\u828D\u7528\u51B0\u51C9\u7684\u6307\u5C16\u62ED\u53BB\u6CEA\u75D5\uFF0C\u7AEF\u5E84\u79C0\u7F8E\u7684\u7389\u5BB9\u6108\u53D1\u59E3\u4E3D\uFF0C\u6D41\u9732\u51FA\u6151\u4EBA\u7684\u5149\u534E\u3002\u5979\u7528\u6E05\u6670\u7684\u58F0\u97F3\u5E73\u9759\u5730\u8BF4\u9053\uFF1A\u300C\u5979\u53EB\u9F99\u6714\uFF0C\u662F\u9F99\u6218\u91CE\u7684\u72EC\u5B50\u3002\u5341\u4E94\u5E74\u524D\uFF0C\u5979\u7236\u6BCD\u90FD\u6B7B\u5728\u6155\u5BB9\u9F99\u624B\u4E0A\u3002\u4E3A\u4E86\u62A5\u4EC7\uFF0C\u5979\u4FEE\u4E60\u300A\u623F\u5FC3\u661F\u76D1\u300B\uFF0C\u50CF\u5973\u4EBA\u4E00\u6837\u91C7\u8865\u7537\u4EBA\u771F\u5143\uFF0C\u8FD8\u5256\u5F00\u522B\u4EBA\u7684\u8EAB\u4F53\uFF0C\u53D8\u6210\u4E00\u4E2A\u5973\u5B50\u2026\u2026\u300D</p><p>\u9759\u989C\u77E5\u9053\u4E49\u6BCD\u662F\u60F3\u63ED\u7A7F\u81EA\u5DF1\uFF0C\u597D\u7EDD\u4E86\u5979\u62A5\u4EC7\u7684\u4F01\u56FE\uFF0C\u7B11\u76C8\u76C8\u9053\uFF1A\u300C\u8FD9\u4E9B\u5979\u4EEC\u90FD\u77E5\u9053\u7684\u3002\u5979\u4EEC\u8FD8\u77E5\u9053\u5B69\u513F\u7684\u4E1C\u897F\u5F88\u5389\u5BB3\uFF0C\u80FD\u628A\u5979\u4EEC\u5E72\u5F97\u6B7B\u53BB\u6D3B\u6765\u2026\u2026\u662F\u4E0D\u662F\u554A\uFF1F\u5C0F\u6BCD\u72D7\u4EEC\uFF1F\u300D</p><p>\u6674\u96EA\u6655\u751F\u53CC\u988A\uFF0C\u7F9E\u6DA9\u5730\u5782\u4E0B\u8138\uFF0C\u592D\u592D\u5374\u5A9A\u58F0\u9053\uFF1A\u300C\u771F\u7684\u5462\uFF0C\u9F99\u59D0\u59D0\u90A3\u91CC\u53C8\u7C97\u53C8\u957F\uFF0C\u786C\u6886\u6886\u80FD\u6363\u5230\u4EBA\u5FC3\u53E3\u4E0A\u53BB\u2014\u2014\u4ED9\u5B50\u8FD9\u4E48\u6F02\u4EAE\uFF0C\u9F99\u59D0\u59D0\u80AF\u5B9A\u559C\u6B22\uFF0C\u4F60\u8131\u5149\u4E86\uFF0C\u5F20\u5F00\u817F\uFF0C\u8BA9\u9F99\u59D0\u59D0\u63D2\u51E0\u4E0B\uFF0C\u5C31\u77E5\u9053\u6709\u591A\u8212\u670D\u4E86\u3002\u300D</p><p>\u300C\u5979\u662F\u6211\u7684\u4E49\u5B50\u3002\u300D\u68B5\u96EA\u828D\u8BF4\u9053\uFF1A\u300C\u6211\u89C1\u8FC7\u5979\u88AB\u4EBA\u9501\u5728\u7B3C\u4E2D\u3002\u88AB\u4EBA\u6C61\u8FB1\u3002\u6211\u4E86\u89E3\u5979\uFF0C\u5C31\u50CF\u4E86\u89E3\u5979\u7684\u4E09\u8F6E\u4E03\u8109\u3002\u4E5F\u8BB8\u4F60\u4EEC\u4F1A\u4EE5\u4E3A\u5979\u4F1A\u6C89\u8FF7\u4E8E\u4F60\u4EEC\u7684\u8089\u4F53\uFF0C\u4F46\u6211\u77E5\u9053\u2026\u2026\u300D</p><p>\u300C\u53EE\u300D\u7684\u4E00\u58F0\u8106\u54CD\uFF0C\u534E\u4F57\u5200\u88AB\u68B5\u96EA\u828D\u7528\u7389\u6307\u5F39\u5F00\uFF0C\u9489\u5728\u67F1\u4E0A\u7684\u96D5\u9F99\u773C\u4E0A\uFF0C\u300C\u5979\u6C38\u8FDC\u4E0D\u4F1A\u653E\u5F03\u4EC7\u6068\uFF0C\u90A3\u662F\u5979\u7684\u751F\u547D\uFF0C\u5979\u4F1A\u50CF\u86F0\u4F0F\u7684\u6BD2\u86C7\u4E00\u6837\uFF0C\u7B49\u5F85\u6BCF\u4E00\u4E2A\u673A\u4F1A\uFF01\u300D</p><p>\u9759\u989C\u4E00\u51FB\u4E0D\u679C\uFF0C\u7ACB\u5373\u7FFB\u8155\u62D4\u51FA\u4F69\u5251\uFF0C\u75BE\u6311\u68B5\u96EA\u828D\u5589\u5934\u3002\u68B5\u96EA\u828D\u53CD\u638C\u7528\u624B\u80CC\u6253\u5728\u5251\u810A\u4E0A\uFF0C\u7136\u540E\u7EA4\u6307\u626C\u8D77\uFF0C\u50CF\u633D\u4F4F\u4E00\u6761\u4E1D\u5E26\u822C\u5C06\u9752\u950B\u5251\u7F20\u5728\u624B\u4E0A\u3002</p><p>\u5F53\u5E74\u4E3A\u6CBB\u7652\u4E39\u7530\u7684\u4F24\u52BF\uFF0C\u9759\u989C\u65E0\u6570\u6B21\u63A5\u53D7\u8FC7\u4E49\u6BCD\u8F93\u6765\u7684\u771F\u6C14\uFF0C\u4F46\u5979\u6CA1\u60F3\u5230\u90A3\u80A1\u6E29\u548C\u4ECE\u5BB9\u7684\u771F\u6C14\uFF0C\u4E00\u65E6\u5BF9\u9635\u7ADF\u4F1A\u5982\u6B64\u51CC\u5229\uFF0C\u300A\u623F\u5FC3\u661F\u76D1\u300B\u7684\u771F\u6C14\u521A\u521A\u9012\u51FA\u4FBF\u88AB\u514B\u5236\uFF0C\u4EA4\u624B\u4E0D\u8FC7\u4E00\u62DB\uFF0C\u957F\u5251\u5DF2\u88AB\u62E7\u6210\u5706\u73AF\uFF0C\u4EC0\u4E48\u5251\u6CD5\u3001\u62DB\u672F\u7EDF\u7EDF\u5931\u53BB\u51ED\u85C9\u3002</p><p>\u5979\u5FD8\u4E86\uFF0C\u5C31\u5728\u81EA\u5DF1\u63A5\u53D7\u771F\u6C14\u7684\u540C\u65F6\uFF0C\u68B5\u96EA\u828D\u4E5F\u5BF9\u5979\u6C14\u8109\u8FD0\u884C\u4E86\u5982\u6307\u638C\uFF0C\u300A\u623F\u5FC3\u661F\u76D1\u300B\u8BF8\u822C\u8BE1\u5F02\u4E4B\u5904\uFF0C\u68B5\u96EA\u828D\u751A\u81F3\u6BD4\u5979\u81EA\u5DF1\u8FD8\u8981\u6E05\u695A\u3002\u68B5\u96EA\u828D\u5148\u7528\u8FE6\u7F57\u771F\u6C14\u622A\u65AD\u4E86\u5979\u7684\u771F\u6C14\u8FD0\u884C\uFF0C\u62E7\u5F2F\u4E86\u957F\u5251\uFF0C\u63A5\u7740\u4FBF\u4FA7\u638C\u671D\u9759\u989C\u808B\u4E0B\u6309\u53BB\u3002</p><p>\u5BF8\u8BB8\u7684\u7A7A\u865A\u4E2D\uFF0C\u5E7B\u5316\u51FA\u4E00\u53EA\u5170\u82B1\u822C\u7684\u7389\u624B\uFF0C\u9999\u8F6F\u800C\u53C8\u8FF7\u79BB\u3002\u8DDD\u79BB\u5F77\u4F5B\u77AC\u95F4\u62C9\u5F00\uFF0C\u7ED9\u90A3\u53EA\u7389\u624B\u8BA9\u51FA\u8DB3\u591F\u65BD\u5C55\u7684\u7A7A\u95F4\u3002\u660E\u7389\u822C\u7684\u7EA4\u6307\u751F\u51FA\u7F8E\u5999\u7684\u53D8\u5316\uFF0C\u5C31\u5728\u4E0E\u68B5\u96EA\u828D\u7389\u638C\u76F8\u4EA4\u7684\u4E00\u523B\uFF0C\u90A3\u53EA\u624B\u98DF\u4E2D\u4E24\u6307\u7AD6\u8D77\uFF0C\u5FAE\u5FAE\u5206\u5F00\uFF0C\u62B5\u4F4F\u68B5\u96EA\u828D\u7684\u638C\u5FC3\uFF0C\u63A5\u7740\u5C0F\u6307\u7528\u4E00\u4E2A\u4E0D\u53EF\u601D\u8BAE\u7684\u89D2\u5EA6\u4ECE\u4E0B\u6311\u8D77\uFF0C\u70B9\u5728\u68B5\u96EA\u828D\u8155\u95F4\uFF0C\u62C7\u6307\u4E0E\u65E0\u540D\u6307\u4E00\u6263\u5373\u5206\uFF0C\u5F39\u51FA\u4E00\u7F15\u6307\u98CE\uFF0C\u88AD\u5411\u68B5\u96EA\u828D\u8098\u5F2F\u3002</p><p>\u68B5\u96EA\u828D\u5C48\u8D77\u4E09\u6307\uFF0C\u62C7\u6307\u659C\u51FA\uFF0C\u5C0F\u6307\u5FAE\u7FD8\uFF0C\u4E0E\u90A3\u53EA\u624B\u8F7B\u8F7B\u4E00\u89E6\uFF0C\u6536\u4E86\u56DE\u6765\uFF0C\u60CA\u7591\u4E0D\u5B9A\u5730\u671B\u7740\u90A3\u4E2A\u660E\u8273\u5C11\u5973\u3002</p><p>\u6674\u96EA\u6321\u5728\u9759\u989C\u8EAB\u524D\uFF0C\u5DE6\u624B\u7AD6\u8D77\uFF0C\u4F9D\u7136\u662F\u62C7\u6307\u4E0E\u65E0\u540D\u6307\u76F8\u6263\uFF0C\u4E2D\u6307\u85CF\u5728\u638C\uFF0C\u98DF\u6307\u4E0E\u5C0F\u6307\u524D\u540E\u659C\u51FA\uFF0C\u72B6\u5982\u51E4\u773C\u3002\u592D\u592D\u6084\u6084\u632A\u52A8\u811A\u6B65\uFF0C\u8BD5\u56FE\u7ED5\u5230\u68B5\u96EA\u828D\u80CC\u540E\u3002\u6674\u96EA\u9053\uFF1A\u300C\u4F60\u53BB\u53D6\u7384\u5929\u5251\u6765\u3002\u300D\u5979\u5999\u76EE\u751F\u8F89\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u672C\u5BAB\u65E0\u610F\u7559\u96BE\u68B5\u4ED9\u5B50\uFF0C\u5982\u613F\u79BB\u5F00\uFF0C\u672C\u5BAB\u7EDD\u4E0D\u963B\u6321\u3002\u300D</p><p>\u68B5\u96EA\u828D\u6CA1\u60F3\u5230\u5979\u5C0F\u5C0F\u5E74\u7EAA\u6B66\u529F\u7ADF\u5982\u6B64\u7CBE\u6E5B\uFF0C\u771F\u6C14\u9634\u9633\u4EA4\u9519\uFF0C\u5373\u4F7F\u624D\u8D28\u7EDD\u4F73\u4E5F\u4E0D\u53EF\u80FD\u62E5\u6709\u8FD9\u822C\u529F\u529B\uFF0C\u5C24\u5176\u662F\u90A3\u81F3\u9634\u81F3\u9633\u4E24\u80A1\u771F\u6C14\u6CFE\u6E2D\u5206\u660E\uFF0C\u72B9\u5982\u4E24\u4E2A\u624D\u534E\u6A2A\u7EDD\u7684\u9AD8\u624B\u540C\u65F6\u4FEE\u6210\uFF0C\u518D\u5408\u4E3A\u4E00\u4F53\uFF0C\u5979\u7A76\u7ADF\u662F\u5982\u4F55\u505A\u5230\u7684\uFF1F</p><p>\u300C\u6714\u513F\uFF1F\u300D</p><p>\u300C\u6211\u662F\u9759\u989C\u3002\u4E0D\u8BA4\u8BC6\u4F60\u8BF4\u7684\u6714\u513F\u3002\u300D\u5C11\u5973\u629B\u5F00\u5F2F\u6298\u7684\u957F\u5251\uFF0C\u4E00\u638C\u63A9\u4F4F\u4E39\u7530\uFF0C\u4E00\u638C\u659C\u4F38\uFF0C\u4E94\u6307\u7D27\u5E76\u5982\u5200\u3002</p><p>\u9762\u5BF9\u51B3\u88C2\u7684\u4E49\u5B50\uFF0C\u68B5\u96EA\u828D\u5947\u602A\u6CA1\u6709\u611F\u89C9\u5230\u4F24\u75DB\uFF0C\u53EA\u662F\u5FC3\u4E27\u6B32\u6B7B\u5730\u5462\u5583\u9053\uFF1A\u300C\u6211\u9020\u4E0B\u7684\u5B7D\u679C\uFF0C\u81EA\u7136\u8FD8\u5F97\u6211\u6765\u89E3\u8131\u3002\u300D\u5979\u51C4\u7136\u4E00\u7B11\uFF0C\u300C\u6714\u513F\uFF0C\u8DDF\u5A18\u8D70\u5427\u2026\u2026\u300D</p><p>\u68B5\u96EA\u828D\u4E24\u624B\u5408\u4EC0\uFF0C\u5341\u6307\u4EA4\u53C9\uFF0C\u7136\u540E\u4E00\u6839\u4E00\u6839\u7AD6\u8D77\uFF0C\u5979\u7684\u624B\u6307\u4FEE\u957F\u767D\u7699\uFF0C\u5149\u6D01\u800C\u53C8\u7EC6\u817B\uFF0C\u78E8\u64E6\u95F4\u9690\u9690\u4F20\u51FA\u9E23\u7389\u822C\u7684\u8F7B\u54CD\u3002\u968F\u7740\u7389\u6307\u677E\u5F00\uFF0C\u4E00\u80A1\u758F\u6DE1\u60A0\u8FDC\u7684\u9999\u6C14\u7F13\u7F13\u6563\u5F00\uFF0C\u68B5\u96EA\u828D\u7684\u773C\u775B\u4E5F\u968F\u4E4B\u4EAE\u8D77\u3002\u6700\u540E\u53CC\u638C\u4E00\u9519\uFF0C\u671D\u4E24\u5973\u5206\u522B\u98D8\u53BB\u3002</p><p>\u6674\u96EA\u5A07\u5412\u4E00\u58F0\uFF0C\u7FFB\u5F00\u53CC\u624B\u3002\u5979\u5DE6\u624B\u83B9\u767D\u5982\u7389\uFF0C\u53F3\u624B\u5374\u6BB7\u7EA2\u80DC\u706B\uFF0C\u51B7\u82E5\u51B0\u971C\u548C\u70BD\u70ED\u5982\u706B\u7684\u4E24\u80A1\u771F\u6C14\uFF0C\u968F\u7740\u7EA4\u6307\u7684\u8F7B\u6447\u75BE\u5C04\u800C\u51FA\uFF0C\u5728\u7A7A\u4E2D\u56DB\u6563\u6FC0\u8361\u3002</p><p>\u68B5\u96EA\u828D\u7389\u5BB9\u65E0\u6CE2\uFF0C\u9AD8\u624B\u5BF9\u9635\u76F8\u5DEE\u53EA\u5728\u4E00\u7EBF\uFF0C\u5982\u6B64\u534E\u4E3D\u7684\u62DB\u672F\u867D\u7136\u7729\u76EE\uFF0C\u4F46\u4E0D\u514D\u5206\u6563\u3002\u5979\u53F3\u638C\u75BE\u7FFB\uFF0C\u62CD\u5F00\u6FC0\u8361\u7684\u6307\u98CE\uFF0C\u5370\u5728\u6674\u96EA\u7EEF\u7EA2\u7684\u53F3\u638C\u4E0A\u3002\u5DE6\u638C\u518D\u6B21\u622A\u4F4F\u9759\u989C\u7684\u7ECF\u8109\uFF0C\u987A\u52BF\u63A8\u51FA\u3002\u8FD9\u6B21\u5979\u4F7F\u4E0A\u4E86\u5168\u529B\uFF0C\u9759\u989C\u53EA\u89C9\u4E00\u80A1\u6C9B\u83AB\u96BE\u5F53\u7684\u771F\u6C14\u6D8C\u6765\uFF0C\u81EA\u5DF1\u771F\u6C14\u88AB\u622A\uFF0C\u65E0\u80FD\u4E3A\u5FA1\uFF0C\u53EA\u597D\u4EF0\u8EAB\u7FFB\u51FA\uFF0C\u907F\u5F00\u950B\u8292\u3002</p><p>\u6674\u96EA\u4E3E\u624B\u6295\u8DB3\u95F4\u59FF\u6001\u5A49\u5999\u4E4B\u6781\uFF0C\u5979\u503E\u5C3D\u5168\u529B\uFF0C\u592A\u4E00\u7ECF\u4E0E\u51E4\u51F0\u5B9D\u5178\u8F6E\u756A\u65BD\u5C55\uFF0C\u4E0E\u68B5\u96EA\u828D\u6597\u5F97\u96BE\u5206\u96BE\u89E3\u3002\u592D\u592D\u6367\u7740\u7384\u5929\u5251\u5954\u56DE\u795E\u6BBF\uFF0C\u6B63\u89C1\u6674\u96EA\u5468\u8EAB\u7EA2\u5149\u5927\u76DB\uFF0C\u7389\u8776\u822C\u7684\u7EA4\u638C\u6325\u51FA\uFF0C\u68B5\u96EA\u828D\u7684\u8896\u53E3\u7ACB\u65F6\u8C61\u88AB\u70C8\u706B\u70E4\u7099\u822C\u5377\u8D77\u3002</p><p>\u68B5\u96EA\u828D\u7EB5\u8EAB\u9000\u5F00\uFF0C\u5389\u58F0\u559D\u9053\uFF1A\u300C\u4F60\u4ECE\u54EA\u91CC\u5B66\u6765\u7684\u51E4\u51F0\u5B9D\u5178\uFF1F\u53EF\u662F\u5F97\u81EA\u96EA\u5CF0\u795E\u5C3C\uFF1F\u300D\u51E4\u51F0\u5B9D\u5178\u662F\u98D8\u6885\u5CF0\u5386\u4EE3\u76F8\u4F20\u7684\u7EDD\u6280\uFF0C\u597D\u53CB\u96EA\u5CF0\u795E\u5C3C\u6B63\u662F\u4EE5\u6B64\u6280\u60CA\u6C5F\u6E56\uFF0C\u88AB\u79F0\u4E3A\u5929\u4E0B\u7B2C\u4E00\u9AD8\u624B\u3002</p><p>\u6674\u96EA\u51DB\u7136\u9053\uFF1A\u300C\u51E4\u51F0\u5B9D\u5178\u662F\u6211\u661F\u6708\u6E56\u9547\u6559\u795E\u529F\uFF0C\u98D8\u6885\u5CF0\u4E0D\u8FC7\u662F\u7A83\u5176\u76AE\u6BDB\uFF0C\u600E\u53EF\u4E0E\u6211\u661F\u6708\u6E56\u4E89\u8F89\uFF1F\u300D</p><p>\u5F53\u5E74\u96EA\u5CF0\u795E\u5C3C\u8FDC\u8D74\u5357\u6D77\uFF0C\u6B63\u662F\u4E0E\u68B5\u96EA\u828D\u63A2\u5BFB\u51E4\u51F0\u5B9D\u5178\u7684\u7591\u96BE\u4E4B\u5904\u3002\u96EA\u5CF0\u795E\u5C3C\u4FEE\u70BC\u51E4\u51F0\u5B9D\u5178\u6570\u5341\u5E74\uFF0C\u59CB\u7EC8\u672A\u80FD\u7A81\u7834\u7B2C\u4E03\u5C42\uFF0C\u4F46\u8FD9\u5C11\u5973\u771F\u6C14\u5706\u8F6C\u5982\u610F\uFF0C\u7ADF\u4F3C\u5DF2\u529F\u6210\u5706\u6EE1\uFF0C\u7EC3\u6210\u4E86\u7B2C\u4E5D\u5C42\u51E4\u6E05\u7D2B\u9E3E\u3002</p><p>\u592D\u592D\u89C1\u5C0F\u516C\u4E3B\u5360\u4E86\u4E0A\u98CE\uFF0C\u4E0D\u7531\u5927\u559C\uFF0C\u5979\u628A\u7384\u5929\u5251\u6367\u7ED9\u9759\u989C\uFF0C\u5A07\u5412\u4E00\u58F0\uFF0C\u62A2\u8EAB\u6251\u51FA\u3002\u68B5\u96EA\u828D\u8840\u8109\u672A\u901A\uFF0C\u811A\u6B65\u7565\u663E\u6EDE\u91CD\uFF0C\u5F53\u4E0B\u5341\u6307\u8F7B\u5F39\uFF0C\u5316\u53BB\u9ED1\u715E\u638C\u7684\u6BD2\u6027\uFF0C\u7136\u540E\u7FFB\u8155\u62FF\u4F4F\u5979\u7684\u8109\u95E8\uFF0C\u624B\u4E00\u6296\uFF0C\u592D\u592D\u5C16\u53EB\u4E00\u58F0\uFF0C\u53F3\u81C2\u5DF2\u88AB\u62C9\u8131\u3002\u68B5\u96EA\u828D\u607C\u592D\u592D\u638C\u529B\u6B79\u6BD2\uFF0C\u626C\u624B\u5C06\u5979\u671D\u6BBF\u4FA7\u7684\u5DE8\u67F1\u63B7\u53BB\u3002</p><p>\u773C\u89C1\u67F1\u4E0A\u7684\u96D5\u523B\u98DE\u901F\u903C\u8FD1\uFF0C\u592D\u592D\u5413\u5F97\u95ED\u4E0A\u773C\u775B\uFF0C\u4E00\u9897\u5FC3\u51E0\u4E4E\u8981\u8DF3\u51FA\u80F8\u8154\u3002\u60F6\u60E7\u4E2D\uFF0C\u8EAB\u5B50\u843D\u5728\u4E00\u56E2\u67D4\u8F6F\u4E2D\uFF0C\u63A5\u7740\u4E00\u4E2A\u67D4\u7F8E\u7684\u58F0\u97F3\u54CD\u8D77\uFF0C\u300C\u5C0F\u6BCD\u72D7\uFF0C\u4E56\u4E56\u5728\u8FD9\u91CC\u7B49\u7740\u2026\u2026\u300D\u592D\u592D\u7741\u5F00\u773C\uFF0C\u6B63\u770B\u5230\u9759\u989C\u7738\u4E2D\u51B0\u51B7\u7684\u6740\u610F\uFF0C\u4E0D\u7981\u6253\u4E86\u4E2A\u5BD2\u5664\u3002</p><p>\u6674\u96EA\u8F7B\u53F9\u9053\uFF1A\u300C\u68B5\u4ED9\u5B50\u8EAB\u5728\u4F5B\u95E8\uFF0C\u600E\u4E0D\u77E5\u8D2A\u3001\u55D4\u3001\u75F4\u662F\u4E3A\u4E09\u6BD2\uFF1F\u5982\u6B64\u6267\u5984\uFF1F\u300D</p><p>\u68B5\u96EA\u828D\u767D\u9E64\u822C\u659C\u63A0\u800C\u8D77\uFF0C\u4E24\u638C\u5288\u5F00\u6674\u96EA\u70BD\u70ED\u7684\u62A4\u4F53\u771F\u6C14\uFF0C\u53F3\u624B\u5C0F\u6307\u659C\u51FA\uFF0C\u4E0E\u6674\u96EA\u6307\u950B\u4E00\u62B5\uFF0C\u968F\u5373\u98D8\u5F00\u3002\u9759\u989C\u6458\u4E0B\u9CA8\u9C7C\u76AE\u6240\u5236\u7684\u5251\u9798\uFF0C\u4EAE\u51FA\u9798\u5185\u4E00\u67C4\u5F62\u5F0F\u53E4\u62D9\u7684\u957F\u5251\uFF0C\u82CD\u7070\u8272\u7684\u5251\u8EAB\u4E0D\u77E5\u4F55\u7269\u94F8\u6210\uFF0C\u72B9\u5982\u4E00\u6BB5\u67AF\u6728\u3002\u5979\u7F13\u7F13\u63E1\u7D27\u5251\u67C4\uFF0C\u4E4C\u6C89\u6C89\u7684\u5251\u8EAB\u968F\u4E4B\u4EAE\u8D77\uFF0C\u76F4\u81F3\u901A\u4F53\u5149\u660E\uFF0C\u6563\u53D1\u51FA\u6EE1\u6708\u822C\u7684\u94F6\u8F89\u3002\u5979\u6115\u7136\u4E00\u6325\uFF0C\u53EA\u542C\u5251\u950B\u632F\u8D77\u4E00\u58F0\u6E05\u8D8A\u4E4B\u6781\u7684\u9510\u54CD\uFF0C\u94F6\u5149\u5B9B\u5982\u6296\u843D\u7684\u6D41\u8424\uFF0C\u660E\u660E\u706D\u706D\u6251\u5230\u5251\u810A\u4E0A\u3002</p><p>\u6674\u96EA\u771F\u6C14\u4E00\u541E\u4E00\u5410\uFF0C\u5DF2\u7531\u81F3\u9633\u53D8\u4E3A\u81F3\u9634\uFF0C\u592A\u4E00\u7ECF\u7684\u68EE\u51B7\u5BD2\u610F\u5B9B\u5982\u65E0\u58F0\u7684\u6697\u6D41\u6F5C\u6D8C\u800C\u51FA\u3002\u9759\u989C\u811A\u4E0B\u4E00\u6ED1\uFF0C\u6E9C\u81F3\u68B5\u96EA\u828D\u8EAB\u540E\uFF0C\u4E00\u62DB\u82CD\u5C71\u66AE\u8FDC\u6B63\u5927\u5802\u7687\uFF0C\u6CD5\u5EA6\u68EE\u4E25\uFF0C\u6B63\u662F\u4E5D\u534E\u5251\u6D3E\u6B63\u5B97\u5251\u6CD5\u3002\u7384\u5929\u5251\u88AB\u5979\u7684\u771F\u6C14\u6FC0\u53D1\uFF0C\u66F4\u52A0\u7480\u74A8\u593A\u76EE\uFF0C\u6EE1\u5802\u5149\u534E\u6D41\u6EA2\uFF0C\u6620\u5F97\u68B5\u96EA\u828D\u7389\u988A\u7C89\u767D\u3002</p><p>\u9759\u989C\u4E0D\u6562\u8F7B\u4E3E\u5984\u52A8\uFF0C\u4E00\u62DB\u4E00\u5F0F\u90FD\u606A\u5B88\u6CD5\u5EA6\uFF0C\u7262\u7262\u6321\u4F4F\u68B5\u96EA\u828D\u7684\u540E\u8DEF\u3002\u6B63\u9762\u5BF9\u654C\u7684\u6674\u96EA\u5C3D\u663E\u5176\u8D85\u5353\u975E\u51E1\u7684\u529F\u529B\uFF0C\u8D77\u521D\u53EA\u4F7F\u51FA\u638C\u6CD5\u3001\u6307\u6CD5\uFF0C\u6E10\u6E10\u629B\u5F00\u77DC\u6301\uFF0C\u5149\u6E9C\u6E9C\u7684\u7389\u817F\u8D77\u821E\u822C\u626C\u8D77\uFF0C\u5728\u9ED1\u8272\u7684\u8863\u88D9\u4E0B\u7EFD\u9732\u51FA\u8FF7\u4EBA\u7684\u98CE\u60C5\u3002</p><p>\u68B5\u96EA\u828D\u9762\u8272\u51DD\u91CD\uFF0C\u9762\u524D\u7684\u5C0F\u516C\u4E3B\u4F3C\u4E4E\u6709\u65E0\u5C3D\u7684\u6F5C\u529B\uFF0C\u968F\u7740\u4EA4\u624B\u65F6\u95F4\u7684\u6D41\u901D\uFF0C\u6674\u96EA\u7684\u771F\u6C14\u4E5F\u6108\u53D1\u5145\u6C9B\u7EAF\u719F\uFF0C\u5F77\u4F5B\u6C89\u7761\u7684\u771F\u5143\u88AB\u6E10\u6B21\u5524\u9192\u3002\u82E5\u5728\u5E73\u65F6\uFF0C\u5979\u5C3D\u53EF\u4EE5\u4E0E\u4E4B\u5468\u65CB\uFF0C\u4F3A\u673A\u800C\u52A8\uFF0C\u4F46\u6B64\u65F6\u8EAB\u540E\u8FD8\u4E00\u540D\u624B\u6267\u661F\u6708\u6E56\u9547\u6559\u795E\u5175\u7684\u5927\u654C\u3002\u9759\u989C\u7684\u6B66\u529F\u6BD4\u6674\u96EA\u867D\u7565\u6709\u4E0D\u5982\uFF0C\u4F46\u5DF2\u7ECF\u662F\u6C5F\u6E56\u4E2D\u51FA\u7C7B\u62D4\u8403\u7684\u9AD8\u624B\uFF0C\u5979\u62DB\u672F\u8C28\u4E25\uFF0C\u771F\u6C14\u5374\u8BE1\u5F02\u65E0\u6BD4\uFF0C\u82E5\u975E\u81EA\u5DF1\u5BF9\u5176\u77E5\u4E4B\u6781\u6DF1\uFF0C\u4E5F\u96BE\u7A33\u64CD\u80DC\u5238\u3002\u6B64\u523B\u88AB\u4E24\u4EBA\u5939\u653B\uFF0C\u4E0D\u591A\u65F6\u4FBF\u9669\u76F8\u73AF\u751F\u3002</p><p>\u6674\u96EA\u89C1\u68B5\u96EA\u828D\u6E10\u6E10\u540E\u79FB\uFF0C\u4EE5\u4E3A\u5979\u5DF2\u840C\u751F\u9000\u610F\uFF0C\u661F\u6708\u6E56\u6709\u8BB8\u591A\u7684\u4E00\u51FB\u5FC5\u6740\u7684\u9634\u6BD2\u62DB\u672F\uFF0C\u4F46\u6674\u96EA\u4E00\u76F4\u85CF\u800C\u672A\u7528\uFF0C\u751A\u81F3\u8FDE\u68B5\u96EA\u828D\u8BD5\u56FE\u8131\u8EAB\u4E5F\u672A\u52A0\u963B\u6321\u3002</p><p>\u4E09\u4EBA\u5728\u6BBF\u5185\u4EA4\u624B\u5DF2\u6709\u534A\u4E2A\u65F6\u8FB0\uFF0C\u4E24\u5927\u795E\u529F\u5728\u8EAB\u7684\u6674\u96EA\u8D8A\u6765\u8D8A\u6325\u6D12\u81EA\u5982\uFF0C\u9759\u989C\u7684\u7384\u5929\u5251\u7EF5\u7EF5\u5BC6\u5BC6\u4E0D\u9732\u7834\u7EFD\uFF0C\u800C\u68B5\u96EA\u828D\u5219\u8FED\u9022\u9669\u62DB\uFF0C\u8FDE\u675F\u53D1\u7684\u4E1D\u5E15\u4E5F\u88AB\u9759\u989C\u6311\u843D\uFF0C\u6EE1\u5934\u9752\u4E1D\u62AB\u5728\u80A9\u5934\u3002\u5979\u54AC\u7740\u7EA2\u5507\uFF0C\u6F84\u6F88\u7684\u773C\u795E\u4E1D\u6BEB\u4E0D\u4E71\u3002</p><p>\u6674\u96EA\u624B\u6325\u76EE\u9001\uFF0C\u4E00\u53CC\u7389\u638C\u72B9\u5982\u82B1\u95F4\u7684\u7389\u8776\u7FE9\u7FE9\u8D77\u821E\u3002\u5FFD\u7136\u5979\u8170\u80A2\u4E00\u6298\uFF0C\u4E24\u624B\u624B\u80CC\u76F8\u6620\uFF0C\u4E00\u9634\u4E00\u9633\u671D\u68B5\u96EA\u828D\u8170\u95F4\u62E2\u53BB\u3002\u68B5\u96EA\u828D\u95EA\u8170\u659C\u9000\uFF0C\u767E\u5FD9\u4E2D\u5C48\u6307\u5F39\u5F00\u9759\u989C\u7684\u7384\u5929\u5251\uFF0C\u63A5\u7740\u811A\u5C16\u4E00\u70B9\uFF0C\u4F5C\u52BF\u671D\u6BBF\u95E8\u63A0\u53BB\u3002</p><p>\u6674\u96EA\u72B9\u8C6B\u4E86\u4E00\u4E0B\uFF0C\u6CA1\u6709\u8FDB\u51FB\u3002\u9759\u989C\u4E00\u62DB\u6307\u5929\u5212\u65E5\uFF0C\u75BE\u5288\u68B5\u96EA\u828D\u8170\u8EAB\u3002\u7384\u5929\u5251\u5F77\u4F5B\u5438\u5C3D\u4E86\u7A7A\u4E2D\u6FC0\u8361\u7684\u771F\u6C14\uFF0C\u4E00\u77AC\u95F4\u5149\u534E\u5927\u76DB\uFF0C\u68B5\u96EA\u828D\u88AB\u5251\u52BF\u903C\u81F3\u67F1\u4FA7\uFF0C\u9000\u65E0\u53EF\u9000\uFF0C\u5979\u7693\u8155\u4E00\u626C\uFF0C\u8896\u5185\u7684\u94F6\u9488\u5C3D\u6570\u98DE\u51FA\uFF0C\u5C04\u5411\u9759\u989C\u80F8\u8179\u8981\u5BB3\u3002\u9759\u989C\u51B7\u7B11\u4E00\u58F0\uFF0C\u5A07\u8EAF\u5728\u7A7A\u4E2D\u4E00\u6A2A\uFF0C\u907F\u5F00\u94F6\u9488\uFF0C\u624B\u4E2D\u62DB\u5F0F\u4E0D\u53D8\uFF0C\u771F\u6C14\u6240\u805A\uFF0C\u52BF\u5C06\u68B5\u96EA\u828D\u7684\u7EA4\u8170\u8FDE\u540C\u5DE8\u67F1\u4E00\u5E76\u65A9\u65AD\u3002</p><p>\u68B5\u96EA\u828D\u5FFD\u7136\u8F7B\u53F9\u4E00\u58F0\uFF0C\u5782\u624B\u9760\u5728\u67F1\u4E0A\u3002\u9759\u989C\u6CA1\u60F3\u5230\u75BE\u6597\u6B63\u9163\uFF0C\u4E49\u6BCD\u5374\u653E\u5F03\u51FA\u624B\uFF0C\u4F46\u5979\u53EA\u5FAE\u4E00\u9519\u6115\uFF0C\u624B\u4E2D\u7684\u7384\u5929\u5251\u4FBF\u52A0\u901F\u5288\u843D\u3002\u68B5\u96EA\u828D\u5BF9\u7384\u5929\u5251\u4E0D\u7406\u4E0D\u776C\uFF0C\u67D4\u7F8E\u7684\u5507\u89D2\u6CDB\u8D77\u4E00\u4E1D\u82E6\u6DA9\u7684\u7B11\u610F\uFF0C\u90A3\u53CC\u5999\u76EE\u6DF1\u6DF1\u671B\u7740\u9759\u989C\uFF0C\u5145\u6EE1\u4E86\u96BE\u8A00\u7684\u75DB\u82E6\u548C\u60B2\u60AF\u3002</p><p>\u5C31\u5728\u9759\u989C\u5251\u52BF\u4F7F\u5C3D\u4E4B\u9645\uFF0C\u9489\u5728\u96D5\u9F99\u773C\u4E0A\u7684\u534E\u4F57\u5200\u731B\u7136\u8DF3\u51FA\uFF0C\u7B14\u76F4\u5C04\u5411\u9759\u989C\u5589\u5934\u3002\u9759\u989C\u9B42\u98DE\u9B44\u6563\uFF0C\u4F46\u5DF2\u65E0\u6CD5\u53D8\u62DB\uFF0C\u53EA\u80FD\u773C\u7741\u7741\u770B\u7740\u90A3\u67C4\u91CD\u5851\u4E86\u5979\u8EAB\u4F53\u7684\u534E\u4F57\u5200\uFF0C\u95EA\u7535\u822C\u5C04\u5411\u54BD\u5589\u3002\u9759\u989C\u8FD9\u624D\u660E\u767D\uFF0C\u4E49\u6BCD\u662F\u8981\u4E0E\u81EA\u5DF1\u540C\u5F52\u4E8E\u5C3D\u2026\u2026</p><p>\u5589\u5934\u5DF2\u7ECF\u80FD\u611F\u53D7\u5230\u5200\u950B\u7684\u5BD2\u610F\uFF0C\u7EC6\u767D\u7684\u808C\u80A4\u5BF8\u5BF8\u6536\u7D27\u3002\u5343\u94A7\u4E00\u53D1\u4E4B\u9645\uFF0C\u4E00\u80A1\u5145\u6C9B\u4E4B\u6781\u7684\u771F\u6C14\u4ECE\u65C1\u63A0\u8FC7\uFF0C\u534E\u4F57\u5200\u5FAE\u5FAE\u4E00\u504F\uFF0C\u4ECE\u9759\u989C\u9888\u4FA7\u5212\u8FC7\uFF0C\u7559\u4E0B\u4E00\u9053\u7EC6\u957F\u7684\u8840\u75D5\u3002</p><p>\u9759\u989C\u9A87\u51FA\u4E00\u8EAB\u51B7\u6C57\uFF0C\u624B\u6307\u677E\u5F00\uFF0C\u7384\u5929\u5251\u545B\u5577\u6389\u5728\u5730\u4E0A\u3002\u4F46\u5979\u60CA\u9B42\u672A\u5B9A\uFF0C\u5DF2\u88AB\u4E00\u53EA\u67D4\u8F6F\u7684\u624B\u638C\u6309\u4F4F\u81BB\u4E2D\u8981\u7A74\u3002</p><p>\u6674\u96EA\u62FC\u5C3D\u5168\u529B\u7684\u9694\u7A7A\u4E00\u51FB\uFF0C\u51E0\u4E4E\u7D2F\u81F3\u865A\u8131\uFF0C\u5979\u60F6\u7136\u53EB\u9053\uFF1A\u300C\u68B5\u4ED9\u5B50\uFF01\u300D</p><p>\u68B5\u96EA\u828D\u6DE1\u6DE1\u77A5\u4E86\u5979\u4E00\u773C\uFF0C\u8F6C\u76EE\u51DD\u89C6\u7740\u9759\u989C\u3002\u6674\u96EA\u6B66\u529F\u867D\u5F3A\uFF0C\u6B64\u65F6\u4E5F\u675F\u624B\u65E0\u7B56\uFF0C\u53EA\u80FD\u4E0E\u592D\u592D\u60CA\u614C\u5730\u671B\u7740\u5979\u4EEC\u6BCD\u5B50\u3002</p><p>\u300C\u75BC\u5417\uFF1F\u300D\u68B5\u96EA\u828D\u6E29\u51C9\u7684\u7389\u6307\u62C2\u8FC7\u4F24\u53E3\uFF0C\u6B62\u4F4F\u4E86\u6DCB\u6F13\u7684\u9C9C\u8840\u3002\u5979\u7684\u58F0\u97F3\u8FD8\u548C\u4ECE\u524D\u4E00\u6837\u8F7B\u67D4\uFF0C\u4F46\u6309\u5728\u4E49\u5B50\u80F8\u53E3\u7684\u624B\u638C\u5374\u8574\u85CF\u7740\u81F4\u547D\u7684\u771F\u6C14\u3002</p><p>\u300C\u8FD8\u597D\u2026\u2026\u300D\u9759\u989C\u52C9\u5F3A\u6324\u51FA\u4E00\u4E1D\u7B11\u5BB9\u3002</p><p>\u68B5\u96EA\u828D\u957F\u957F\u7684\u4E1D\u53D1\u4ECE\u8138\u4FA7\u62AB\u6563\u4E0B\u6765\uFF0C\u7389\u5BB9\u5B9D\u76F8\u5E84\u4E25\uFF0C\u7F8E\u76EE\u6E5B\u7136\u751F\u8F89\u3002\u604D\u60DA\u95F4\uFF0C\u9759\u989C\u60F3\u8D77\u66FE\u6709\u4E00\u5C0A\u767D\u7389\u89C2\u97F3\uFF0C\u4E5F\u662F\u8FD9\u6837\u6709\u7740\u60B2\u60AF\u7684\u7F8E\u6001\u2026\u2026</p><p>\u300C\u516D\u9053\u8F6E\u56DE\uFF0C\u7109\u80FD\u4E0D\u82E6\u3002\u5B69\u513F\uFF0C\u4E0D\u8981\u602A\u5A18\u3002\u300D\u68B5\u96EA\u828D\u8F7B\u58F0\u9053\uFF1A\u300C\u5047\u5982\u6765\u751F\u8FD8\u662F\u4EBA\u8EAB\uFF0C\u5A18\u4F1A\u4E5E\u6C42\u5341\u6BBF\u960E\u7F57\uFF0C\u8BA9\u4F60\u8F6C\u4E16\u751F\u4E3A\u5973\u8EAB\uFF0C\u4E0E\u5A18\u6BCD\u5973\u76F8\u4F9D\u2026\u2026\u300D\u8BF4\u7740\u5979\u51C4\u7136\u4E00\u7B11\uFF0C\u624B\u638C\u7F13\u7F13\u4F7F\u529B\u3002</p><p>\u9759\u989C\u683C\u683C\u4F4E\u7B11\u8D77\u6765\u3002\u300C\u5A18\u8BF4\u9519\u4E86\u5462\uFF0C\u6765\u751F\u6211\u4EEC\u53EF\u662F\u4E00\u6837\u5927\uFF0C\u8BF4\u4E0D\u5B9A\u662F\u59D0\u59B9\uFF0C\u8BF4\u4E0D\u5B9A\u662F\u5144\u5F1F\uFF0C\u8FD8\u53EF\u80FD\u662F\u592B\u59BB\u2026\u2026\u53EA\u662F\u4E0D\u77E5\u9053\u4F60\u662F\u7537\u4EBA\uFF0C\u8FD8\u662F\u6211\u662F\u7537\u4EBA\uFF0C\u662F\u4F60\u5E72\u6211\u5462\uFF0C\u8FD8\u662F\u6211\u5E72\u4F60\u3002\u300D</p><p>\u68B5\u96EA\u828D\u7389\u8138\u53D1\u767D\uFF0C\u7F8E\u76EE\u4E00\u77AC\u4E0D\u77AC\u5730\u76EF\u7740\u81EA\u5DF1\u7684\u7231\u5B50\u3002</p><p>\u9759\u989C\u7B11\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u597D\u50CF\u8FD8\u662F\u5904\u5B50\u54CE\uFF0C\u4E00\u8F88\u5B50\u6CA1\u88AB\u7537\u4EBA\u5E72\u8FC7\uFF0C\u771F\u662F\u767D\u505A\u4E00\u56DE\u4EBA\u8EAB\u4E86\u5462\u3002\u300D</p><p>\u68B5\u96EA\u828D\u773C\u5708\u53D1\u7EA2\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u8FD9\u4E48\u65E0\u803B\u2026\u2026\u300D</p><p>\u9759\u989C\u6B22\u7B11\u9053\uFF1A\u300C\u7537\u6B22\u5973\u7231\uFF0C\u6709\u4EC0\u4E48\u65E0\u803B\u4E0D\u65E0\u803B\u7684\uFF1F\u5A18\uFF0C\u4F60\u7684\u7F8E\u5C44\u80AF\u5B9A\u53C8\u5AE9\u53C8\u7D27\uFF0C\u4E0D\u5982\u8BA9\u5B69\u513F\u4E00\u5C3D\u5B5D\u5FC3\uFF0C\u4EB2\u81EA\u7ED9\u5A18\u5F00\u82DE\u2014\u2014\u300D\u5979\u6311\u4E86\u6311\u7709\u5CF0\uFF0C\u300C\u5B69\u513F\u7684\u5E8A\u4E0A\u529F\u592B\u5F88\u597D\uFF0C\u4E00\u5B9A\u4F1A\u8BA9\u5A18\u6B32\u4ED9\u6B32\u6B7B\u5462\uFF0C\u521A\u624D\u5979\u4EEC\u4E5F\u8BF4\u2026\u2026\u300D</p><p>\u68B5\u96EA\u828D\u626C\u624B\u7ED9\u4E86\u5979\u4E00\u4E2A\u8033\u5149\uFF0C\u7EA2\u5507\u5267\u98A4\uFF0C\u73E0\u6CEA\u4E00\u6EF4\u6EF4\u6EDA\u5728\u80F8\u895F\u4E0A\u3002\u9759\u989C\u7C89\u988A\u6162\u6162\u6CDB\u8D77\u638C\u75D5\uFF0C\u5979\u50CF\u662F\u88AB\u4E49\u6BCD\u4E00\u638C\u63B4\u9192\uFF0C\u6C89\u9ED8\u7247\u523B\u540E\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u7684\u8863\u670D\u6253\u6E7F\u4E86\u5462\u3002\u300D\u8BF4\u7740\u8C61\u62B9\u53BB\u6CEA\u73E0\u822C\u626C\u624B\u671D\u68B5\u96EA\u828D\u80F8\u524D\u8F7B\u8F7B\u62B9\u53BB\u3002</p><p>\u9759\u989C\u7684\u624B\u638C\u7EA4\u7F8E\u800C\u53C8\u6D01\u767D\uFF0C\u8F7B\u67D4\u5F97\u5F77\u4F5B\u591C\u8272\u4E2D\u7684\u5FAE\u98CE\uFF0C\u6309\u5411\u6BCD\u4EB2\u9999\u878D\u878D\u7684\u80F8\u819B\uFF0C\u6307\u7F1D\u95F4\u5374\u9732\u51FA\u4E00\u62B9\u5BD2\u5149\u2026\u2026\u68B5\u96EA\u828D\u8138\u8272\u4E00\u53D8\uFF0C\u4EF0\u8EAB\u671D\u540E\u9000\u53BB\u3002</p><p>\u300C\u5472\u300D\u7684\u4E00\u58F0\u8106\u54CD\uFF0C\u58F0\u5982\u88C2\u5E1B\uFF0C\u68B5\u96EA\u828D\u96EA\u767D\u7684\u8863\u886B\u88AB\u9510\u5229\u7684\u5200\u950B\u5F53\u80F8\u5212\u5F00\u3002</p><p>\u68B5\u96EA\u828D\u8170\u8EAB\u67D4\u8F6F\u4E4B\u6781\uFF0C\u5371\u6025\u4E2D\u7684\u4E00\u4EF0\uFF0C\u8793\u9996\u51E0\u4E4E\u89E6\u5230\u5730\u9762\u3002\u80F8\u8863\u4E2D\u5206\uFF0C\u9732\u51FA\u4E00\u62B9\u5982\u96EA\u7684\u80A4\u5149\uFF0C\u4E0D\u7B49\u68B5\u96EA\u828D\u76F4\u8D77\u7EA4\u8170\uFF0C\u8863\u895F\u4E2D\u7A81\u7136\u5F39\u8D77\u4E24\u56E2\u80A5\u7855\u5706\u6DA6\u7684\u96EA\u8089\uFF0C\u63A5\u7740\u6389\u51FA\u51E0\u6761\u88AB\u65A9\u65AD\u7684\u4E1D\u5E26\u3002\u90A3\u662F\u5979\u7528\u6765\u675F\u80F8\u7684\u767D\u7EEB\u3002</p><p>\u6674\u96EA\u548C\u592D\u592D\u773C\u775B\u4E00\u4E0B\u77AA\u5F97\u8001\u5927\uFF0C\u8C01\u4E5F\u4E0D\u4F1A\u60F3\u5230\u7AEF\u5E84\u5723\u6D01\u7684\u9999\u836F\u5929\u5973\u7ADF\u7136\u4F1A\u6709\u5982\u6B64\u4E30\u7855\u7684\u4E00\u5BF9\u5927\u4E73\u3002\u98A4\u5FAE\u5FAE\u9AD8\u9AD8\u60AC\u5728\u8863\u5916\uFF0C\u9F13\u80C0\u5F97\u5F77\u4F5B\u8981\u6389\u4E0B\u6765\u3002</p><p>\u68B5\u96EA\u828D\u7389\u8138\u65F6\u7EA2\u65F6\u767D\uFF0C\u5979\u95EA\u8EAB\u9000\u5230\u4E00\u8FB9\uFF0C\u626F\u8863\u8BD5\u56FE\u63A9\u4F4F\u80F8\u4E73\u3002\u4F46\u5979\u7684\u4E73\u623F\u592A\u8FC7\u80A5\u7855\uFF0C\u4EE5\u5F80\u90FD\u662F\u5148\u7528\u4E1D\u5E26\u675F\u597D\u518D\u62AB\u4E0A\u5916\u8863\uFF0C\u6B64\u65F6\u65E0\u8BBA\u5979\u600E\u4E48\u63A9\u9970\uFF0C\u90A3\u4E24\u56E2\u96EA\u8089\u59CB\u7EC8\u65E0\u6CD5\u85CF\u5728\u8863\u5185\uFF0C\u53CD\u800C\u56E0\u4E3A\u5979\u7684\u4E3E\u52A8\u6296\u4E2A\u4E0D\u505C\u3002\u5149\u6D01\u4E73\u623F\u53C8\u767D\u53C8\u6ED1\uFF0C\u56E0\u4E3A\u8FD8\u662F\u672A\u66FE\u7834\u8EAB\u5904\u5B50\u800C\u5206\u5916\u575A\u633A\uFF0C\u4E45\u7F1A\u8131\u56F0\u66F4\u662F\u8C61\u704C\u6EE1\u871C\u6C41\u7684\u76AE\u7403\u4E00\u6837\uFF0C\u5728\u80F8\u524D\u6EDA\u6765\u6EDA\u53BB\uFF0C\u8361\u51FA\u8000\u773C\u7684\u8089\u5149\u3002</p><p>\u9759\u989C\u6536\u8D77\u591A\u6B21\u633D\u6551\u81EA\u5DF1\u6027\u547D\u7684\u5315\u9996\uFF0C\u8D81\u673A\u63D0\u5251\u800C\u8D77\uFF0C\u4E00\u8F6E\u6025\u653B\uFF0C\u903C\u5F97\u68B5\u96EA\u828D\u624B\u5FD9\u811A\u4E71\uFF0C\u518D\u65E0\u6687\u987E\u53CA\u80F8\u4E73\u3002\u53EA\u89C1\u4E24\u53EA\u767D\u5149\u5149\u7684\u7389\u4E73\u6491\u5F00\u7834\u788E\u7684\u8863\u895F\uFF0C\u5728\u80F8\u524D\u4E1C\u6447\u897F\u6446\uFF0C\u72B9\u5982\u719F\u900F\u7684\u6D46\u679C\u822C\u9971\u6EE1\u5F97\u51E0\u6B32\u88C2\u5F00\uFF0C\u8BA9\u4EBA\u62C5\u5FC3\u5B83\u4EEC\u4F1A\u5728\u78B0\u649E\u4E2D\u4E4D\u88C2\u3002</p><p>\u300C\u597D\u5927\u7684\u5976\u5B50\u2026\u2026\u300D\u592D\u592D\u62D6\u957F\u58F0\u97F3\uFF0C\u9178\u6E9C\u6E9C\u8BF4\u9053\uFF1A\u300C\u53EA\u6709\u5728\u8857\u5934\u6447\u7740\u5976\u5B50\u63A5\u5BA2\u7684\u4E0B\u8D31\u5A4A\u5B50\uFF0C\u624D\u4F1A\u957F\u5F97\u8FD9\u4E48\u5927\u5462\u3002\u96BE\u9053\u68B5\u4ED9\u5B50\u4E5F\u505A\u8FC7\u8DEF\u8FB9\u7684\u6697\u5A3C\uFF1F\u300D</p><p>\u68B5\u96EA\u828D\u9762\u7EA2\u8FC7\u8033\uFF0C\u5DE6\u624B\u6A2A\u5728\u80F8\u524D\uFF0C\u53F3\u624B\u52C9\u5F3A\u62B5\u6321\u7740\u7384\u5929\u5251\u7684\u950B\u8292\u3002\u7B49\u6674\u96EA\u52A0\u5165\u6218\u56E2\uFF0C\u68B5\u96EA\u828D\u95EA\u907F\u6108\u53D1\u8270\u96BE\u3002\u9759\u989C\u6293\u4F4F\u673A\u4F1A\uFF0C\u4E00\u5251\u6A2A\u5288\uFF0C\u5F85\u68B5\u96EA\u828D\u51FA\u624B\u5C01\u683C\u65F6\u7A81\u7136\u4E00\u8F6C\uFF0C\u6697\u5730\u62AC\u819D\u671D\u68B5\u96EA\u828D\u8179\u4FA7\u51FB\u53BB\u3002\u68B5\u96EA\u828D\u7D20\u624B\u5E73\u6325\uFF0C\u6321\u4F4F\u819D\u76D6\uFF0C\u771F\u6C14\u4E00\u89E6\uFF0C\u5374\u89C1\u9759\u989C\u771F\u6C14\u9A73\u6742\uFF0C\u4F3C\u4E4E\u662F\u5F3A\u5F13\u4E4B\u672B\uFF0C\u7389\u6307\u987A\u52BF\u70B9\u51FA\uFF0C\u5C01\u4E86\u5979\u7684\u73AF\u8DF3\u7A74\u3002</p><p>\u9759\u989C\u8EAB\u5B50\u4E00\u4FA7\uFF0C\u8DEA\u5728\u5730\u4E0A\uFF0C\u626C\u8138\u9732\u51FA\u4E00\u4E2A\u751C\u751C\u7684\u7B11\u5BB9\u3002</p><p>\u68B5\u96EA\u828D\u53F3\u624B\u505C\u5728\u7A7A\u4E2D\uFF0C\u6A2A\u5728\u80F8\u524D\u7684\u5DE6\u81C2\u65E0\u529B\u5730\u6ED1\u5230\u8EAB\u65C1\uFF0C\u9732\u51FA\u7C89\u817B\u7684\u53CC\u4E73\u3002</p><p>\u4E24\u53EA\u7EA2\u7EA2\u7684\u4E73\u5934\u8DF3\u52A8\u7740\uFF0C\u6E10\u6E10\u5E73\u9759\u4E0B\u6765\u3002</p><p>\u300C\u9F99\u54E5\u54E5\uFF01\u300D\u6674\u96EA\u677E\u5F00\u70B9\u5728\u68B5\u96EA\u828D\u80CC\u5FC3\u7684\u7EA4\u6307\uFF0C\u8FDE\u5FD9\u6276\u8D77\u9759\u989C\uFF0C\u89E3\u5F00\u5979\u819D\u4E0A\u7684\u7A74\u9053\u3002</p><p>\u9759\u989C\u6545\u610F\u9732\u51FA\u7834\u7EFD\uFF0C\u8BA9\u6674\u96EA\u4E00\u51FB\u5F97\u624B\uFF0C\u7EC8\u4E8E\u5236\u4F4F\u4E86\u6B66\u529F\u7CBE\u5F3A\u7684\u68B5\u96EA\u828D\uFF0C\u5979\u6276\u7740\u6674\u96EA\u7684\u9999\u80A9\u7AD9\u8D77\u8EAB\u5B50\uFF0C\u7B11\u541F\u541F\u4E3E\u8D77\u7384\u5929\u5251\uFF0C\u5E73\u5E73\u67B6\u5728\u68B5\u96EA\u828D\u80A5\u5AE9\u7684\u7855\u4E73\u4E0B\uFF0C\u7B11\u9053\uFF1A\u300C\u5A18\u7684\u5976\u5B50\u597D\u50CF\u53C8\u5927\u4E86\u5462\u3002\u770B\u8D77\u6765\u80A5\u80A5\u5AE9\u5AE9\uFF0C\u771F\u60F3\u54AC\u4E00\u53E3\u2026\u2026\u300D</p><p>\u88AB\u51B0\u51B7\u7684\u5251\u950B\u4E00\u6FC0\uFF0C\u68B5\u96EA\u828D\u7684\u53CC\u4E73\u7ACB\u523B\u6536\u7D27\uFF0C\u4E73\u5934\u786C\u786C\u7FD8\u8D77\u3002\u592D\u592D\u54C2\u7B11\u9053\uFF1A\u300C\u54C8\uFF0C\u786C\u4E86\u5462\uFF0C\u8FD9\u5A4A\u5B50\u7684\u4E73\u5934\u786C\u8D77\u6765\u4E86\u3002\u300D</p><p>\u68B5\u96EA\u828D\u7F9E\u4E0D\u6B32\u751F\uFF0C\u8EAB\u5B50\u5411\u524D\u4E00\u6251\uFF0C\u671D\u950B\u9510\u65E0\u6BD4\u7684\u7384\u5929\u5251\u4E0A\u731B\u6251\u8FC7\u53BB\u3002</p><p>\u957F\u5251\u8361\u8D77\uFF0C\u300C\u556A\u300D\u7684\u4E00\u58F0\uFF0C\u5251\u810A\u5728\u80A5\u4E73\u4E0A\u91CD\u91CD\u6253\u4E86\u4E00\u8BB0\u3002\u63A5\u7740\u9759\u989C\u633A\u8D77\u8EAB\u5B50\uFF0C\u8FD8\u5251\u5165\u9798\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u9999\u836F\u5929\u5973\u68B5\u96EA\u828D\u5DF2\u4E3A\u795E\u6559\u6240\u64D2\uFF0C\u8BF7\u516C\u4E3B\u793A\u4E0B\u3002\u300D</p><p>\u6674\u96EA\u80A9\u5934\u4F3C\u4E4E\u8FD8\u7559\u6709\u9759\u989C\u7684\u98A4\u6296\uFF0C\u5979\u5185\u5FC3\u8FDC\u4E0D\u50CF\u8868\u9732\u7684\u90A3\u6837\u9547\u5B9A\u5462\uFF0C\u5979\u5728\u60F3\u4EC0\u4E48\uFF1F\u4E3A\u4EC0\u4E48\u4E0D\u544A\u8BC9\u6211\u5462\uFF1F</p><p>\u300C\u7531\u4F60\u5904\u7F6E\u5427\u3002\u6211\u53BB\u53D6\u4E9B\u5316\u771F\u6563\u6765\u3002\u300D\u6674\u96EA\u62C9\u8D77\u592D\u592D\uFF0C\u53C8\u5631\u5490\u9053\uFF1A\u300C\u5C0F\u5FC3\u4E9B\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u65E0\u8BBA\u5916\u754C\u9634\u6674\u96E8\u96EA\uFF0C\u4F4D\u4E8E\u6000\u6708\u5CF0\u4E0B\u7684\u5730\u5BAB\u4E00\u5982\u6697\u591C\u3002\u9759\u989C\u5C06\u68B5\u96EA\u828D\u6A2A\u62B1\u5728\u624B\u4E2D\uFF0C\u671D\u9ED1\u4E0D\u89C1\u5E95\u7684\u6DF1\u5904\u8D70\u53BB\u3002</p><p>\u300C\u5A18\uFF0C\u300D\u9759\u989C\u4FA7\u8138\u8D34\u7740\u68B5\u96EA\u828D\u7684\u7389\u988A\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u5B69\u513F\u7EC8\u4E8E\u627E\u5230\u661F\u6708\u6E56\u4E86\u3002\u300D</p><p>\u67D4\u8F6F\u7684\u53D1\u4E1D\u5728\u8138\u65C1\u62C2\u8FC7\uFF0C\u53E3\u8102\u7684\u82AC\u82B3\u9999\u7EAF\u5982\u5170\u3002\u8FD9\u4E2A\u6BD4\u5973\u4EBA\u66F4\u8FF7\u4EBA\u7684\u5C11\u5973\u662F\u5979\u7684\u6770\u4F5C\uFF0C\u53EF\u68B5\u96EA\u828D\u5374\u4E1D\u6BEB\u4E5F\u9AD8\u5174\u4E0D\u8D77\u6765\u3002</p><p>\u9759\u989C\u6B49\u610F\u5730\u7B11\u4E86\u7B11\uFF0C\u300C\u5FD8\u4E86\u544A\u8BC9\u5A18\uFF0C\u5B69\u513F\u5DF2\u7ECF\u627E\u5230\u4E00\u4E2A\u613F\u610F\u4E3A\u6211\u751F\u5B69\u5B50\u7684\u5973\u4EBA\uFF0C\u5979\u53EB\u6674\u96EA\uFF0C\u5C31\u662F\u521A\u624D\u70B9\u5012\u5A18\u7684\u5973\u5B50\u3002\u662F\u4E0D\u662F\u5F88\u6F02\u4EAE\uFF1F\u5979\u6B66\u529F\u53C8\u9AD8\uFF0C\u957F\u5F97\u53C8\u7F8E\uFF0C\u5BF9\u5B69\u513F\u6B7B\u5FC3\u584C\u5730\u2014\u2014\u53EF\u5B69\u513F\u4E0D\u4F1A\u5A36\u5979\u5F53\u59BB\u5B50\uFF0C\u53EA\u628A\u5979\u5F53\u6210\u6211\u517B\u7684\u5C0F\u6BCD\u72D7\u2026\u2026\u56E0\u4E3A\u5979\u662F\u6155\u5BB9\u9F99\u7684\u5973\u513F\u3002\u300D</p><p>\u300C\u53E6\u4E00\u4E2A\u53EB\u592D\u592D\u3002\u5A18\u662F\u4E0D\u662F\u770B\u51FA\u6765\u4E86\uFF0C\u5979\u8DDF\u6211\u4E00\u6837\u5462\u3002\u4E0D\u8FC7\u5979\u53C8\u55F2\u53C8\u5A9A\uFF0C\u6BD4\u5973\u4EBA\u8FD8\u9A9A\uFF0C\u6BCF\u6B21\u5E72\u5979\u5C41\u773C\u513F\uFF0C\u5979\u90FD\u6447\u7740\u5C41\u80A1\u8BA9\u6211\u518D\u63D2\u6DF1\u4E00\u70B9\u513F\u3002\u300D\u9759\u989C\u4F4E\u7B11\u9053\uFF1A\u300C\u517B\u4E86\u8FD9\u6837\u4E24\u53EA\u5C0F\u6BCD\u72D7\uFF0C\u597D\u73A9\u5F97\u5F88\u5462\u3002\u5A18\uFF0C\u4F60\u4E0D\u4E3A\u5B69\u513F\u9AD8\u5174\u5417\uFF1F\u300D</p><p>\u68B5\u96EA\u828D\u773C\u89D2\u7684\u6CEA\u6C34\u6E10\u6E10\u51B7\u53BB\uFF0C\u5979\u8EBA\u5728\u4E49\u5B50\u6000\u4E2D\uFF0C\u80F8\u524D\u655E\u9732\u7684\u8863\u895F\u95F4\uFF0C\u5706\u6EDA\u6EDA\u7684\u7389\u4E73\u4E00\u6447\u4E00\u6447\uFF0C\u5B9B\u5982\u9999\u8F6F\u7684\u817B\u8102\u3002</p><p>\u300C\u53EF\u5B69\u513F\u8FD8\u6CA1\u6709\u89C1\u5230\u4ED6\u3002\u542C\u8BF4\u4ED6\u5F53\u4E86\u71D5\u56FD\u7684\u7687\u5E1D\u3002\u8981\u6740\u4ED6\u5F88\u4E0D\u5BB9\u6613\u2026\u2026\u300D\u9759\u989C\u8F7B\u53F9\u9053\uFF1A\u300C\u4ED6\u867D\u7136\u662F\u6674\u96EA\u7684\u7239\u7239\uFF0C\u6211\u8FD8\u662F\u8981\u6740\u4ED6\u7684\u3002\u300D</p><p>\u6DD9\u6DD9\u7684\u6D41\u6C34\u58F0\u4ECE\u8FDC\u5904\u4F20\u6765\uFF0C\u9759\u989C\u5FFD\u7136\u9053\uFF1A\u300C\u6211\u5F88\u5BB3\u6015\u3002\u4ED6\u597D\u50CF\u8D8A\u6765\u8D8A\u8FD1\uFF0C\u6709\u65F6\u80FD\u542C\u5230\u4ED6\u7684\u58F0\u97F3\uFF0C\u770B\u5230\u4ED6\u7684\u5F71\u5B50\u2026\u2026\u6211\u4E0D\u6015\u6B7B\uFF0C\u4F46\u6015\u6B7B\u4E86\u6CA1\u529E\u6CD5\u518D\u62A5\u4EC7\uFF0C\u300D\u9759\u989C\u7684\u58F0\u97F3\u98A4\u6296\u8D77\u6765\uFF0C\u300C\u5A18\uFF0C\u5B69\u513F\u4ED8\u51FA\u4E86\u90A3\u4E48\u591A\u2026\u2026\u300D</p><p>\u9ED1\u6697\u4E2D\u4EAE\u8D77\u4E00\u7247\u96EA\u767D\u7684\u5149\u8292\uFF0C\u4E00\u5177\u4F18\u7F8E\u7684\u5973\u4F53\u8DEA\u5728\u6CB3\u7554\uFF0C\u9759\u9759\u671B\u7740\u8FD9\u5BF9\u76F8\u4F9D\u76F8\u504E\u7684\u6BCD\u5973\u3002</p>',88);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
