import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_52" tabindex="-1"><a class="header-anchor" href="#_52" aria-hidden="true">#</a> 52</h1><p>\u9759\u989C\u6CA1\u6709\u518D\u89C1\u5230\u5E08\u5A18\uFF0C\u4ECE\u4E39\u697C\u51FA\u6765\uFF0C\u9762\u8272\u94C1\u9752\u7684\u6CAE\u6E20\u5C55\u626C\u4FBF\u7ACB\u5373\u5E26\u4EBA\u8FD4\u56DE\u5EFA\u5EB7\uFF0C\u751A\u81F3\u6CA1\u6709\u5411\u516C\u4E3B\u8F9E\u884C\u3002\u53EA\u542C\u5C9B\u4E0A\u7684\u5E2E\u4F17\u7B11\u7740\u8BF4\uFF0C\u4E0D\u77E5\u9053\u5317\u795E\u5C06\u4E2D\u4E86\u4EC0\u4E48\u90AA\uFF0C\u628A\u65B0\u8FC7\u95E8\u7684\u513F\u5AB3\u5987\u5265\u5F97\u5149\u5149\u7684\u5E26\u4E0A\u8239\uFF0C\u300C\u771F\u770B\u4E0D\u51FA\u6765\uFF0C\u90A3\u4E48\u6807\u81F4\u4E2A\u5987\u4EBA\uFF0C\u4E0B\u8FB9\u7ED9\u73A9\u6210\u90A3\u6A21\u6837\uFF0C\u677E\u5F97\u8FDE\u811A\u90FD\u80FD\u585E\u8FDB\u53BB\u2026\u2026\u300D</p><p>\u9759\u989C\u9ED8\u9ED8\u7ECF\u8FC7\u8C08\u7B11\u7684\u4EBA\u7FA4\uFF0C\u8D70\u8FDB\u6B66\u51E4\u522B\u9662\u3002\u56DB\u9547\u795E\u5C06\u5206\u9A7B\u5404\u5904\uFF0C\u5728\u5C9B\u4E0A\u5404\u6709\u522B\u9662\uFF0C\u8FD9\u6B66\u51E4\u522B\u9662\u4FBF\u662F\u8273\u51E4\u7684\u5C45\u5904\uFF0C\u767D\u6C0F\u59D0\u59B9\u4E0D\u613F\u5165\u5BAB\uFF0C\u4E5F\u4F4F\u5728\u8FD9\u91CC\u3002</p><p>\u300C\u542C\u8BF4\u4F60\u5F88\u5F97\u516C\u4E3B\u7684\u6B22\u5FC3\u5462\uFF1F\u300D\u767D\u7389\u83BA\u7B11\u9053\u3002</p><p>\u9759\u989C\u7B11\u4E86\u7B11\uFF0C\u6CA1\u6709\u8BF4\u8BDD\u3002\u767D\u7389\u9E42\u62C9\u4F4F\u5979\u7684\u624B\uFF0C\u5C0F\u58F0\u95EE\u9053\uFF1A\u300C\u5979\u77E5\u9053\u4F60\u7684\u8EAB\u4F53\u4E86\u5417\uFF1F\u300D</p><p>\u9759\u989C\u5766\u7136\u6447\u5934\uFF0C\u300C\u54EA\u91CC\u4F1A\u8BA9\u5979\u77E5\u9053\u3002\u300D</p><p>\u300C\u90A3\u5C31\u597D\u3002\u300D\u767D\u7389\u83BA\u629A\u4F4F\u5979\u7684\u80A9\u5934\uFF0C\u300C\u5728\u5BAB\u91CC\u4E0B\u624B\u592A\u8FC7\u5371\u9669\uFF0C\u4F60\u60F3\u529E\u6CD5\u628A\u5979\u5F15\u5230\u5916\u9762\uFF0C\u8D81\u5979\u4E0D\u5907\u51FA\u624B\u5236\u4F4F\uFF0C\u5230\u65F6\u6311\u4E86\u5979\u7684\u624B\u7B4B\u811A\u7B4B\uFF0C\u5E9F\u53BB\u5979\u7684\u6B66\u529F\uFF0C\u4F60\u60F3\u600E\u4E48\u73A9\u5C31\u600E\u4E48\u73A9\u3002\u300D\u5979\u7B11\u4E86\u7B11\uFF0C\u300C\u7B49\u4F60\u73A9\u591F\u4E86\uFF0C\u59D0\u59D0\u4EEC\u6765\u5E2E\u4F60\u5904\u7406\u90A3\u4E2A\u5C0F\u5A4A\u5B50\u3002\u300D</p><p>\u9759\u989C\u77E5\u9053\u5979\u4EEC\u4E0E\u6674\u96EA\u5FC3\u6709\u82A5\u8482\uFF0C\u4F46\u6CA1\u60F3\u5230\u4F1A\u6709\u5982\u6B64\u6DF1\u4EC7\u5927\u6068\u3002\u5047\u5982\u6674\u96EA\u843D\u5230\u5979\u4EEC\u624B\u91CC\u2026\u2026\u9759\u989C\u7B11\u9053\uFF1A\u300C\u6211\u89C1\u8BC6\u8FC7\u516C\u4E3B\u7684\u6B66\u529F\uFF0C\u6211\u4E00\u4E2A\u4EBA\u53EF\u5236\u4E0D\u4F4F\u5979\u3002\u300D</p><p>\u767D\u7389\u9E42\u770B\u4E86\u59D0\u59D0\u4E00\u773C\uFF0C\u6B32\u8A00\u53C8\u6B62\u3002\u767D\u7389\u83BA\u6C89\u541F\u7247\u523B\uFF0C\u8BF4\u9053\uFF1A\u300C\u4E0D\u7528\u6025\uFF0C\u59D0\u59D0\u4EEC\u660E\u5929\u8981\u53BB\u9F99\u57CE\u4E00\u8D9F\uFF0C\u5FEB\u5219\u4E00\u6708\uFF0C\u8FDF\u5219\u79CB\u5929\uFF0C\u7B49\u56DE\u6765\u518D\u505A\u8BA1\u8F83\u4E0D\u8FDF\u3002\u300D</p><p>\u767D\u7389\u9E42\u8E2E\u8D77\u811A\u5C16\uFF0C\u4E0B\u8179\u9876\u5728\u9759\u989C\u80EF\u95F4\u7814\u78E8\u7740\u8F7B\u7B11\u9053\uFF1A\u300C\u85CF\u4E86\u8FD9\u4E48\u4E45\uFF0C\u5C0F\u6714\u8FD9\u4E9B\u5929\u662F\u4E0D\u662F\u618B\u574F\u4E86\uFF1F\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u68B5\u96EA\u828D\u88AB\u56DA\u5DF2\u7ECF\u534A\u6708\u6709\u4F59\uFF0C\u9759\u989C\u6BCF\u65E5\u6765\u4E0E\u5979\u4EA4\u5408\uFF0C\u53EA\u5B57\u4E0D\u63D0\u8981\u5982\u4F55\u5904\u7F6E\u5979\uFF0C\u7ADF\u50CF\u662F\u628A\u5979\u5F53\u6210\u8C62\u517B\u7684\u79C1\u7269\uFF0C\u6C38\u8FDC\u56DA\u7981\u5728\u4E0D\u89C1\u5929\u65E5\u7684\u5730\u4E0B\u3002</p><p>\u9ED1\u6697\u4E2D\uFF0C\u4E00\u53EA\u624B\u4F38\u6765\u6309\u5728\u80A9\u5934\uFF0C\u63A5\u7740\u90A3\u5177\u719F\u6089\u7684\u8EAB\u4F53\u538B\u5728\u8EAB\u4E0A\uFF0C\u53EA\u8F7B\u8F7B\u4E00\u62E8\uFF0C\u6D25\u53E3\u4FBF\u6E7F\u4E86\u3002\u575A\u786C\u7684\u517D\u6839\u9876\u4F4F\u7A74\u53E3\uFF0C\u4E0D\u987E\u5979\u7684\u6323\u624E\u548C\u53CD\u6297\uFF0C\u6DF1\u6DF1\u8FDB\u5165\u4F53\u5185\u3002\u6BCF\u6B21\u88AB\u5979\u5F3A\u884C\u8FDB\u5165\uFF0C\u68B5\u96EA\u828D\u90FD\u75DB\u4E0D\u6B32\u751F\u3002\u4F46\u9759\u989C\u6BCF\u6B21\u603B\u80FD\u64A9\u62E8\u8D77\u5979\u7684\u5FEB\u611F\uFF0C\u4F7F\u5979\u5728\u6218\u6817\u4E2D\u4E00\u6B21\u6B21\u8FBE\u5230\u9AD8\u6F6E\u3002\u68B5\u96EA\u828D\u4ECE\u672A\u8C61\u73B0\u5728\u8FD9\u6837\u618E\u6068\u81EA\u5DF1\u7684\u8EAB\u4F53\uFF0C\u5B83\u5351\u6C61\u800C\u53C8\u4E0B\u8D31\uFF0C\u4F7F\u81EA\u5DF1\u4E00\u6B21\u53C8\u4E00\u6B21\u8499\u7F9E\u3002</p><p>\u6E29\u51C9\u7684\u624B\u6307\u56DB\u5904\u6E38\u79FB\uFF0C\u7231\u629A\u7740\u8EAB\u4F53\u6BCF\u4E00\u5BF8\u808C\u80A4\uFF0C\u5F88\u5FEB\u8089\u4F53\u4FBF\u5C48\u670D\u5728\u5979\u7684\u6311\u9017\u4E0B\u3002\u9759\u529F\u88AB\u7834\uFF0C\u68B5\u96EA\u828D\u518D\u65E0\u6CD5\u4FDD\u6301\u5FC3\u5982\u6B62\u6C34\u7684\u5883\u754C\uFF0C\u5979\u50CF\u5973\u5B69\u4E00\u6837\u62BD\u6CE3\u7740\u644A\u5F00\u8EAB\u4F53\uFF0C\u8FCE\u5408\u7740\u9759\u989C\u7684\u62BD\u9001\u3002</p><p>\u9759\u989C\u6C89\u6D78\u5728\u4E49\u6BCD\u72EC\u6709\u7684\u4F53\u9999\u4E2D\uFF0C\u7D0A\u4E71\u7684\u5FC3\u6E56\u6E10\u6E10\u5E73\u606F\u3002\u767D\u6C0F\u59D0\u59B9\u5DF2\u7ECF\u79BB\u5F00\u6570\u65E5\uFF0C\u53EA\u5269\u8273\u51E4\u8FD8\u7559\u5728\u6B64\u95F4\uFF0C\u5374\u4E00\u76F4\u4E0D\u89C1\u8E2A\u5F71\u3002\u8273\u51E4\u6B66\u529F\u4E4B\u9AD8\u5728\u661F\u6708\u6E56\u4E0D\u4F5C\u7B2C\u4E8C\u4EBA\u60F3\uFF0C\u5373\u4F7F\u653E\u773C\u5929\u4E0B\uFF0C\u80FD\u4E0E\u4E4B\u5339\u654C\u7684\u4E5F\u5BE5\u5BE5\u65E0\u51E0\u3002\u5979\u53C8\u77E5\u6653\u81EA\u5DF1\u7684\u8EAB\u4EFD\uFF0C\u4E07\u4E00\u900F\u51FA\u98CE\u58F0\uFF0C\u5373\u4F7F\u6674\u96EA\u4E0D\u52A0\u7406\u4F1A\uFF0C\u81EA\u5DF1\u4E5F\u96BE\u4EE5\u5728\u661F\u6708\u6E56\u5B58\u8EAB\u3002</p><p>\u5F53\u65E5\u5979\u4E00\u65F6\u51B2\u52A8\uFF0C\u5F3A\u66B4\u4E86\u6E05\u9192\u4E2D\u7684\u8427\u4F5B\u5974\uFF0C\u679C\u7136\u4E0E\u5979\u8BBE\u60F3\u7684\u90A3\u6837\uFF0C\u8427\u4F5B\u5974\u975E\u4F46\u4E0D\u6562\u542F\u9F7F\uFF0C\u751A\u81F3\u8FDE\u89C1\u5979\u90FD\u5BB3\u6015\uFF0C\u66F4\u4E0D\u7528\u8BF4\u63ED\u7A7F\u5979\u7684\u8EAB\u4F53\u3002\u8FD9\u6837\u67D4\u5F31\u7684\u5973\u4EBA\uFF0C\u7B49\u6740\u6389\u6155\u5BB9\u9F99\u4E4B\u540E\uFF0C\u81EA\u7136\u5C31\u6210\u4E86\u81EA\u5DF1\u80EF\u4E0B\u7684\u73A9\u7269\u3002\u4F46\u53E6\u4E00\u8FB9\uFF0C\u7EAA\u7709\u59A9\u5374\u4E0E\u7D2B\u73AB\u5BF8\u6B65\u4E0D\u79BB\uFF0C\u4F7F\u5979\u65E0\u6CD5\u67D3\u6307\u90A3\u4E2A\u88AB\u622A\u65AD\u56DB\u80A2\u7684\u8089\u6BB5\u3002\u8981\u5236\u4F4F\u6B66\u529F\u5C3D\u5931\u7684\u7EAA\u7709\u59A9\u548C\u65E0\u6CD5\u52A8\u4F5C\u7684\u73AB\u7470\u4ED9\u5B50\u5E76\u975E\u96BE\u4E8B\uFF0C\u4F46\u5982\u4F55\u7792\u8FC7\u6674\u96EA\u5374\u5927\u4F24\u8111\u7B4B\u2026\u2026</p><p>\u68B5\u96EA\u828D\u6E10\u6E10\u8FF7\u4E71\uFF0C\u5589\u4E2D\u4E0D\u7531\u81EA\u4E3B\u5730\u53D1\u51FA\u5A9A\u58F0\u3002\u5FFD\u7136\u8EAB\u4E0A\u7684\u7389\u4F53\u4E00\u52A8\uFF0C\u9759\u989C\u626C\u8D77\u7EA4\u6307\uFF0C\u53D1\u51FA\u4E00\u7F15\u52B2\u98CE\u3002\u68B5\u96EA\u828D\u8138\u8272\u53D1\u767D\uFF0C\u7D27\u5F20\u5730\u76EF\u7740\u9ED1\u6D1E\u6D1E\u7684\u95E8\u6237\u3002\u5979\u5185\u529F\u88AB\u5236\uFF0C\u611F\u5B98\u4E0E\u5E38\u4EBA\u65E0\u5F02\uFF0C\u773C\u524D\u53EA\u6709\u770B\u4E0D\u900F\u7684\u9ED1\u6697\u3002\u60F3\u5230\u81EA\u5DF1\u4E0E\u4E49\u5B50\u4EA4\u5408\u7684\u7F9E\u6001\u88AB\u4EBA\u7AA5\u89C1\uFF0C\u68B5\u96EA\u828D\u987F\u65F6\u60CA\u51FA\u4E86\u4E00\u8EAB\u51B7\u6C57\u3002</p><p>\u300C\u53EE\u53EE\u53EE\u300D\u51E0\u58F0\u8F7B\u54CD\uFF0C\u9759\u989C\u6253\u51FA\u7684\u94F6\u9488\u78B0\u5728\u4E86\u77F3\u58C1\u4E0A\u3002\u521A\u624D\u5979\u5E76\u6709\u5BDF\u89C9\u4EFB\u4F55\u5F02\u72B6\uFF0C\u7EAF\u7CB9\u662F\u4E00\u79CD\u4E60\u6B66\u4E4B\u4EBA\u7684\u76F4\u89C9\uFF0C\u611F\u5E94\u5230\u6709\u4EBA\u5728\u6697\u4E2D\u7AA5\u89C6\u3002\u5730\u5BAB\u7684\u5165\u53E3\u5728\u5927\u5385\u7684\u592A\u6781\u56FE\u4E0B\uFF0C\u6781\u5C11\u6709\u4EBA\u77E5\u6653\uFF0C\u9664\u4E86\u6674\u96EA\u548C\u592D\u592D\uFF0C\u66F4\u4E0D\u4F1A\u6709\u4EBA\u6562\u64C5\u81EA\u8FDB\u5165\u3002\u90A3\u5077\u7AA5\u7684\u7A76\u7ADF\u662F\u8C01\u5462\uFF1F\u96BE\u9053\u662F\u9519\u89C9\uFF1F</p><p>\u56DB\u5468\u4E00\u7247\u6B7B\u5BC2\uFF0C\u53EA\u6709\u6D41\u6C34\u58F0\u6DD9\u6DD9\u4F20\u6765\u3002\u9759\u989C\u6014\u4E86\u7247\u523B\uFF0C\u91CD\u65B0\u4FEF\u4E0B\u8EAB\u5B50\uFF0C\u5FFD\u7136\u5931\u7B11\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u5939\u5F97\u597D\u7D27\u2026\u2026\u300D</p><p>\u68B5\u96EA\u828D\u53D7\u60CA\u8FC7\u5EA6\uFF0C\u7389\u6237\u7D27\u9501\uFF0C\u7ADF\u7136\u5939\u5F97\u8089\u68D2\u8FDB\u9000\u4E0D\u5F97\u3002\u5979\u7F9E\u4E0D\u53EF\u652F\u5730\u6440\u4F4F\u7C89\u988A\uFF0C\u5374\u88AB\u9759\u989C\u4E00\u628A\u62B1\u4F4F\uFF0C\u6DF1\u6DF1\u543B\u4F4F\u7EA2\u5507\u3002\u5507\u820C\u7EA0\u7F20\u95F4\uFF0C\u9759\u989C\u4F38\u624B\u6309\u4F4F\u5979\u7684\u5C0F\u8179\uFF0C\u6162\u6162\u63C9\u634F\uFF0C\u4F7F\u7D27\u9501\u7684\u7389\u6237\u4E00\u70B9\u70B9\u653E\u677E\uFF0C\u6700\u540E\u8F7B\u8F7B\u62BD\u51FA\u9633\u5177\u3002</p><p>\u9759\u989C\u62BD\u9001\u7684\u52A8\u4F5C\u52A0\u500D\u6E29\u67D4\uFF0C\u68B5\u96EA\u828D\u96EA\u6ED1\u7684\u7389\u4F53\u5B9B\u5982\u591C\u8272\u4E0B\u7684\u767E\u5408\u5410\u9732\u82AC\u82B3\uFF0C\u6D53\u9999\u56DB\u6EA2\u3002\u7545\u7F8E\u7684\u4EA4\u6B22\u6C34\u4E73\u4EA4\u878D\u822C\u751C\u871C\uFF0C\u4F46\u9759\u989C\u5FC3\u5934\u7684\u9634\u5F71\u5374\u59CB\u7EC8\u6325\u4E4B\u4E0D\u53BB\u3002\u79CD\u5B50\u7075\u4E39\u4E00\u4E38\u8DB3\u77E3\uFF0C\u81EA\u5DF1\u534A\u6708\u6765\u6BCF\u65E5\u4E00\u4E38\uFF0C\u60F3\u6765\u4E0D\u81F4\u6709\u8BEF\u3002\u8EAB\u5904\u9669\u5730\uFF0C\u591C\u957F\u68A6\u591A\uFF0C\u5E94\u8BE5\u65E9\u4F5C\u51B3\u5B9A\u4E86\u3002\u82B1\u623F\u5FFD\u7136\u53D8\u5F97\u9ECF\u6ED1\uFF0C\u9759\u989C\u52A0\u5FEB\u62BD\u9001\uFF0C\u5728\u82B1\u5FC3\u72E0\u9876\u6570\u4E0B\uFF0C\u5C06\u7CBE\u6DB2\u5C04\u5728\u68B5\u96EA\u828D\u5267\u98A4\u7684\u871C\u7A74\u5185\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5404\u5730\u9009\u9001\u7684\u5904\u5B50\u9646\u7EED\u8FDB\u5BAB\uFF0C\u661F\u6708\u6E56\u5C5E\u4E0B\u63A7\u5236\u7684\u5927\u5C0F\u5E2E\u4F1A\u59CB\u7EC8\u4FDD\u6301\u5728\u4E09\u767E\u4EE5\u4E0A\u3002\u9053\u5206\u516D\u8109\uFF0C\u623F\u4E2D\u4E00\u652F\u6B63\u662F\u661F\u6708\u6E56\u8BF8\u957F\u8001\u3001\u62A4\u6CD5\u957F\u4FEE\u4E4B\u672F\uFF0C\u9009\u9001\u5904\u5B50\u5165\u5BAB\u4E0D\u8FC7\u4E3E\u624B\u4E4B\u52B3\u3002\u592D\u592D\u6574\u65E5\u5468\u65CB\u5728\u8FD9\u4E9B\u5904\u5B50\u4E4B\u95F4\uFF0C\u5BFB\u627E\u7740\u5408\u7528\u7684\u6027\u5668\u3002</p><p>\u9759\u989C\u4E00\u95E8\u5FC3\u601D\u90FD\u76EF\u7740\u7EAA\u7709\u59A9\uFF0C\u7B49\u5F85\u6155\u5BB9\u7D2B\u73AB\u72EC\u5904\u7684\u673A\u4F1A\u3002\u8FD9\u65E5\u508D\u665A\uFF0C\u673A\u4F1A\u7EC8\u4E8E\u6765\u4E86\u3002</p><p>\u51E4\u795E\u5C06\u9063\u4EBA\u9001\u6765\u8BF7\u67EC\uFF0C\u9080\u7EAA\u5983\u5A18\u5A18\u53D9\u65E7\u3002\u7EAA\u7709\u59A9\u601D\u7D22\u591A\u65F6\uFF0C\u7EC8\u4E8E\u5986\u626E\u4E00\u65B0\u524D\u53BB\u8D74\u7EA6\u3002\u8273\u51E4\u81EA\u767B\u5C9B\u4EE5\u6765\uFF0C\u65E2\u672A\u62DC\u89C1\u5C0F\u516C\u4E3B\uFF0C\u4E5F\u672A\u95EE\u5019\u7D2B\u73AB\u3002\u800C\u6674\u96EA\u4E5F\u5BF9\u5979\u51B7\u6DE1\u5F02\u5E38\uFF0C\u5F7C\u6B64\u7684\u82A5\u8482\u4F3C\u4E4E\u6BD4\u767D\u6C0F\u59D0\u59B9\u8FD8\u6DF1\u3002\u9759\u989C\u5BF9\u5979\u4EEC\u4E4B\u95F4\u7684\u6069\u6028\u7EA0\u845B\u4E0D\u751A\u4E86\u4E86\uFF0C\u4E5F\u4E0D\u653E\u5728\u5FC3\u4E0A\uFF0C\u6B64\u523B\u592D\u592D\u5728\u795E\u6BBF\u6311\u9009\u5904\u5B50\uFF0C\u6674\u96EA\u53BB\u4E39\u697C\u7167\u770B\u5B50\u5973\uFF0C\u5BAB\u4E2D\u53EA\u5269\u4E0B\u4E0D\u80FD\u52A8\u7684\u4E24\u5BAB\u5A18\u5A18\uFF0C\u6B63\u662F\u5343\u8F7D\u96BE\u9022\u7684\u826F\u673A\u3002</p><p>\u73AB\u7470\u4ED9\u5B50\u4E0D\u559C\u711A\u9999\uFF0C\u5BA4\u4E2D\u53EA\u4F9B\u4E86\u51E0\u675F\u9C9C\u82B1\u3002\u5979\u5367\u5728\u6447\u7BEE\u4E2D\uFF0C\u8138\u4FA7\u644A\u7740\u4E00\u518C\u4E66\u5377\uFF0C\u7F8E\u76EE\u5FAE\u95ED\uFF0C\u7761\u5F97\u6B63\u719F\u3002\u8FDC\u8FDC\u671B\u7740\u5979\u59E3\u4E3D\u7684\u9762\u5B54\uFF0C\u9759\u989C\u5FC3\u5934\u65F6\u800C\u75BE\u8DF3\uFF0C\u65F6\u800C\u6C89\u9759\u3002\u4E00\u6761\u9526\u5E15\u63A9\u5728\u5979\u6B8B\u7F3A\u7684\u8EAB\u4F53\u4E0A\uFF0C\u90A3\u5F20\u6D77\u68E0\u822C\u9999\u8273\u7684\u7761\u5BB9\uFF0C\u6709\u79CD\u4EE4\u4EBA\u7A92\u606F\u7684\u7F8E\u6001\u3002</p><p>\u9759\u989C\u8F7B\u8F7B\u8D70\u5230\u5979\u8EAB\u65C1\uFF0C\u4FEF\u8EAB\u7AEF\u8BE6\u7740\u5979\u73E0\u7389\u822C\u7684\u9762\u5BB9\uFF0C\u6162\u6162\u786C\u4E0B\u5FC3\u80A0\uFF0C\u6697\u6697\u60F3\u7740\u8BE5\u5982\u4F55\u4E0B\u624B\u73A9\u5F04\u8FD9\u5177\u6CA1\u6709\u624B\u811A\u7684\u8089\u4F53\u3002\u6216\u8005\u53EF\u4EE5\u628A\u5979\u6311\u5728\u8089\u68D2\u4E0A\uFF0C\u770B\u5979\u7684\u8089\u7A74\u80FD\u4E0D\u80FD\u7ECF\u4F4F\u6574\u5177\u8EAB\u4F53\u7684\u91CD\u91CF\uFF1B\u6216\u8005\u540A\u7740\u5979\u7684\u4E30\u4E73\u2026\u2026</p><p>\u957F\u957F\u7684\u776B\u6BDB\u5FFD\u7136\u4E00\u52A8\uFF0C\u7761\u719F\u7684\u73AB\u7470\u4ED9\u5B50\u7A81\u7136\u7741\u5F00\u773C\u6765\uFF0C\u671D\u5979\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u773C\u775B\u6E05\u4EAE\u5F97\u5F77\u4F5B\u4ECE\u672A\u7761\u7740\u3002\u9759\u989C\u5FC3\u4E0B\u6697\u60CA\uFF0C\u81EA\u5DF1\u7740\u5B9E\u7CCA\u6D82\uFF0C\u5979\u867D\u7136\u6CA1\u4E86\u624B\u811A\uFF0C\u4F46\u6B66\u529F\u8FD8\u5728\uFF0C\u79BB\u8FD9\u4E48\u8FD1\uFF0C\u5979\u81EA\u7136\u4F1A\u53D1\u89C9\u3002\u9759\u989C\u8FDE\u5FD9\u67D4\u58F0\u9053\uFF1A\u300C\u5A18\u5A18\uFF0C\u8981\u5974\u5A62\u6276\u60A8\u8D77\u6765\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u9976\u6709\u5174\u5473\u5730\u671B\u7740\u5979\uFF0C\u4F3C\u4E4E\u9759\u989C\u624D\u662F\u5931\u53BB\u56DB\u80A2\u4F9B\u4EBA\u89C2\u8D4F\u7684\u6837\u5B50\u3002\u9759\u989C\u4ECE\u672A\u89C1\u8FC7\u5982\u6B64\u6F84\u6F88\u660E\u51C0\u7684\u76EE\u5149\uFF0C\u50CF\u662F\u80FD\u4ECE\u5979\u773C\u4E2D\u4E00\u76F4\u671B\u5230\u5FC3\u91CC\u4E00\u822C\u3002\u540C\u6837\u662F\u4E0D\u80FD\u52A8\u4F5C\uFF0C\u8FD8\u4FDD\u7559\u56DB\u80A2\u7684\u8427\u4F5B\u5974\u600E\u4E48\u4E5F\u6CA1\u6709\u5982\u6B64\u5B81\u9759\u7684\u773C\u795E\uFF0C\u6BCF\u5F53\u88AB\u4EBA\u6446\u5E03\u5979\u762B\u8F6F\u7684\u80A2\u4F53\uFF0C\u8427\u4F5B\u5974\u90FD\u662F\u542B\u7F9E\u5E26\u6127\uFF0C\u4E3A\u81EA\u5DF1\u4E0D\u80FD\u89C1\u4EBA\u7684\u8EAB\u5B50\u800C\u7F9E\u803B\u3002\u800C\u7D2B\u73AB\u5374\u5BF9\u6B8B\u7F3A\u7684\u8089\u4F53\u6D51\u4E0D\u5728\u610F\uFF0C\u76EE\u5149\u5C31\u50CF\u5E38\u4EBA\u4E00\u6837\u5766\u7136\uFF0C\u751A\u81F3\u8FD8\u6709\u51E0\u5206\u6349\u6478\u4E0D\u5B9A\u7684\u7B11\u610F\u3002</p><p>\u300C\u4F60\u662F\u7537\u4EBA\u5427\uFF1F\u300D\u7D2B\u73AB\u8EBA\u5728\u9759\u989C\u81C2\u95F4\u5AE3\u7136\u4E00\u7B11\u3002\u770B\u7740\u9759\u989C\u76EE\u77AA\u53E3\u5446\u7684\u6837\u5B50\uFF0C\u5979\u8F7B\u7B11\u9053\uFF1A\u300C\u4F60\u8EAB\u4E0A\u6709\u7537\u4EBA\u7684\u5473\u9053\u5462\u3002\u300D</p><p>\u9759\u989C\u5FC3\u5934\u5267\u9707\uFF0C\u4E00\u628A\u62E7\u4F4F\u7D2B\u73AB\u96EA\u767D\u7684\u67D4\u9888\uFF0C\u5C31\u60F3\u6740\u4EBA\u706D\u53E3\u3002\u7D2B\u73AB\u9759\u9759\u671B\u7740\u5979\uFF0C\u773C\u91CC\u6EE1\u662F\u5632\u8BBD\u7684\u7B11\u610F\uFF0C\u8FD8\u6709\u4E00\u79CD\u65E0\u8C13\u751F\u6B7B\u7684\u6DE1\u7136\u3002</p><p>\u9759\u989C\u7F13\u7F13\u677E\u5F00\u624B\u6307\uFF0C\u6DE1\u7B11\u9053\uFF1A\u300C\u73AB\u7470\u4ED9\u5B50\u679C\u7136\u4E0D\u51E1\u3002\u300D</p><p>\u7D2B\u73AB\u7B11\u541F\u541F\u9053\uFF1A\u300C\u4F60\u8EAB\u4E0A\u597D\u9999\u5462\uFF0C\u65E0\u8BBA\u58F0\u97F3\u5BB9\u8C8C\uFF0C\u8FD8\u662F\u8D70\u8DEF\u7684\u6837\u5B50\uFF0C\u90FD\u50CF\u4E2A\u5973\u4EBA\u3002\u53EF\u60DC\u2026\u2026\u300D\u5979\u76EE\u5149\u79FB\u5230\u9759\u989C\u8170\u4E0B\uFF0C\u7B11\u9053\uFF1A\u300C\u5B83\u7684\u5473\u9053\u592A\u5927\u4E86\uFF0C\u7FD8\u5F97\u4E5F\u672A\u514D\u9AD8\u4E86\u4E9B\u3002\u300D</p><p>\u9759\u989C\u4F4E\u5934\u770B\u53BB\uFF0C\u624D\u53D1\u73B0\u8089\u68D2\u4E0D\u77E5\u4E0D\u89C9\u633A\u51FA\u4E00\u622A\uFF0C\u8FDE\u7F57\u88D9\u4E5F\u88AB\u652F\u8D77\u4E00\u5757\u3002\u5979\u51B7\u7B11\u9053\uFF1A\u300C\u5974\u5A62\u4E0D\u77E5\u68C0\u70B9\uFF0C\u8BA9\u5A18\u5A18\u89C1\u7B11\u4E86\u3002\u300D</p><p>\u300C\u786E\u5B9E\u662F\u4E0D\u5C0F\u5FC3\u3002\u5728\u8FD9\u91CC\u4E0D\u80FD\u7A7F\u4EB5\u88E4\uFF0C\u4E5F\u4E0D\u77E5\u9053\u628A\u5B83\u85CF\u597D\u2026\u2026\u300D\u7D2B\u73AB\u6DE1\u6DE1\u9053\uFF1A\u300C\u8FD9\u6837\u5B50\u600E\u4E48\u80FD\u62A5\u5F97\u4E86\u4EC7\u5462\uFF1F\u300D</p><p>\u9759\u989C\u5FC3\u5934\u4E00\u9635\u72C2\u8DF3\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u4F60\u600E\u77E5\u6211\u662F\u6765\u62A5\u4EC7\u7684\uFF1F\u300D</p><p>\u300C\u4E0D\u662F\u5417\uFF1F\u626E\u6210\u5973\u4EBA\u8FDB\u5230\u8FD9\u4E48\u810F\u7684\u5730\u65B9\uFF0C\u53C8\u5343\u65B9\u767E\u8BA1\u63A5\u8FD1\u6211\uFF0C\u300D\u7D2B\u73AB\u6D45\u7B11\u9053\uFF1A\u300C\u96BE\u9053\u4E0D\u662F\u8981\u6740\u6211\u5417\uFF1F\u300D\u5979\u504F\u7740\u5934\u60F3\u4E86\u60F3\uFF0C\u300C\u6211\u5728\u5916\u9762\u597D\u50CF\u6CA1\u6709\u4EC7\u5BB6\u5462\uFF0C\u591A\u534A\u662F\u4ED6\u4F5C\u7684\u4E86\u3002\u300D</p><p>\u9759\u989C\u51B7\u51B7\u9053\uFF1A\u300C\u4F60\u77E5\u9053\u5C31\u597D\u3002\u6155\u5BB9\u9F99\u6740\u4E86\u6211\u7236\u6BCD\u53CC\u4EB2\uFF0C\u6211\u8981\u6740\u4ED6\u59BB\u513F\u507F\u547D\u3002\u300D</p><p>\u7D2B\u73AB\u8F7B\u53F9\u9053\uFF1A\u300C\u4ED6\u4F5C\u7684\u5B7D\u592A\u591A\u4E86\uFF0C\u6211\u4E5F\u4E0D\u77E5\u9053\u4F60\u662F\u8C01\u7684\u5B69\u5B50\u3002\u5476\uFF0C\u8D76\u7D27\u52A8\u624B\uFF0C\u73B0\u5728\u9003\u8FD8\u5F97\u53CA\u3002\u300D</p><p>\u9759\u989C\u68EE\u7136\u9053\uFF1A\u300C\u4F60\u4E00\u4E2A\u4EBA\u600E\u6BD4\u5F97\u4E86\u6211\u7236\u6BCD\u53CC\u4EB2\u7684\u6027\u547D\uFF1F\u300D</p><p>\u7D2B\u73AB\u4E0D\u8010\u70E6\u5730\u8BF4\u9053\uFF1A\u300C\u50BB\u5B69\u5B50\uFF0C\u4F60\u4EE5\u4E3A\u81EA\u5DF1\u80FD\u6740\u5F97\u4E86\u4ED6\u5417\uFF1F\u542C\u963F\u59E8\u7684\u8BDD\uFF0C\u6740\u4E86\u6211\u5C31\u8D76\u7D27\u79BB\u5F00\uFF0C\u9003\u5F97\u8D8A\u8FDC\u8D8A\u597D\u3002\u300D</p><p>\u9759\u989C\u62AC\u624B\u4ECE\u6447\u7BEE\u4E0A\u53D6\u4E0B\u4E00\u679D\u7EA2\u73CA\u745A\uFF0C\u63E1\u5728\u638C\u4E2D\u3002\u53EA\u542C\u683C\u683C\u4E00\u9635\u8106\u54CD\uFF0C\u518D\u644A\u5F00\u624B\u65F6\uFF0C\u6574\u679D\u73CA\u745A\u5DF2\u7ECF\u5316\u4E3A\u7C89\u672B\u3002\u8FD9\u73CA\u745A\u4E43\u6D77\u4E2D\u5F02\u54C1\uFF0C\u575A\u903E\u94C1\u77F3\uFF0C\u5979\u80FD\u5F92\u624B\u7C89\u788E\uFF0C\u8FD9\u4EFD\u529F\u529B\u7740\u5B9E\u4E0D\u4FD7\u3002\u7D2B\u73AB\u5374\u6BEB\u4E0D\u5728\u610F\u5730\u7B11\u9053\uFF1A\u300C\u6211\u4EE5\u524D\u505A\u5F97\u6BD4\u4F60\u8FD8\u597D\u5462\uFF0C\u53EF\u8FD8\u662F\u53D8\u6210\u8FD9\u4E2A\u6837\u5B50\u2026\u2026\u597D\u4E86\uFF0C\u6211\u89C1\u8BC6\u4E86\u4F60\u7684\u529F\u592B\uFF0C\u5FEB\u4E9B\u6740\u4E86\u6211\u5427\u3002\u300D</p><p>\u9759\u989C\u6536\u56DE\u624B\u638C\uFF0C\u300C\u8FD9\u4E48\u6025\u7740\u6B7B\uFF0C\u771F\u7684\u662F\u4E0D\u60F3\u6D3B\u4E86\u5417\uFF1F\u2026\u2026\u597D\u6F02\u4EAE\u7684\u8EAB\u5B50\uFF0C\u4E0D\u597D\u597D\u73A9\u73A9\u600E\u4E48\u884C\u5462\uFF1F\u300D\u8BF4\u7740\u6380\u5F00\u853D\u4F53\u7684\u9526\u5E15\uFF0C\u9732\u51FA\u73AB\u7470\u4ED9\u5B50\u6B8B\u7F3A\u800C\u53C8\u5B8C\u7F8E\u7684\u7389\u4F53\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7EAA\u7709\u59A9\u7ACB\u5728\u8273\u51E4\u8EAB\u540E\uFF0C\u50CF\u5974\u5A62\u4E00\u6837\u7ED9\u5979\u63C9\u634F\u7740\u80A9\u8180\u3002\u8273\u51E4\u95ED\u7740\u773C\u4E00\u52A8\u4E0D\u52A8\uFF0C\u5FFD\u7136\u53F9\u9053\uFF1A\u300C\u6CA1\u60F3\u5230\u6211\u8FD9\u56DB\u4E2A\u5F92\u513F\u91CC\uFF0C\u5374\u662F\u4F60\u6700\u806A\u660E\u2026\u2026\u300D</p><p>\u7EAA\u7709\u59A9\u67D4\u58F0\u9053\uFF1A\u300C\u5F92\u513F\u8D44\u8D28\u5E73\u5E73\uFF0C\u600E\u6BD4\u5F97\u4E86\u4E24\u4F4D\u5E08\u59D0\u548C\u5C0F\u5E08\u59B9\u5170\u5FC3\u6167\u8D28\uFF0C\u60CA\u624D\u7EDD\u8273\u5462\uFF1F\u300D</p><p>\u8273\u51E4\u51B7\u7B11\u9053\uFF1A\u300C\u665A\u534E\u4E0D\u5230\u4E8C\u5341\u5C81\u4FBF\u6280\u60CA\u6C5F\u6E56\uFF0C\u5251\u6CD5\u5B66\u5F97\u6BD4\u6211\u6559\u5F97\u8FD8\u5FEB\uFF0C\u73B0\u5728\u4E0D\u8FC7\u662F\u6761\u6BCD\u72D7\uFF1B\u90A3\u4E2A\u8D31\u8D27\u5C31\u4E0D\u5FC5\u8BF4\u4E86\uFF0C\u90A3\u79CD\u4EBA\u4E0D\u4EBA\u9B3C\u4E0D\u9B3C\u7684\u6A21\u6837\uFF0C\u8FDE\u6BCD\u72D7\u4E5F\u4E0D\u5982\u3002\u9999\u8FDC\u5012\u597D\uFF0C\u65E9\u65E9\u5AC1\u4E86\u4EBA\uFF0C\u53C8\u65E9\u65E9\u6210\u4E86\u5BE1\u5987\uFF0C\u88AB\u4EBA\u5F04\u778E\u4E86\u5F53\u72D7\u73A9\uFF0C\u90A3\u4E09\u4E2A\u50BB\u74DC\u54EA\u80FD\u6BD4\u5F97\u4E86\u4F60\u7684\u4E07\u4E00\u2014\u2014\u9999\u8FDC\u5462\uFF1F\u600E\u4E48\u6CA1\u6765\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u7B11\u9053\uFF1A\u300C\u6797\u5E08\u59D0\u53C8\u5AC1\u4EBA\u4E86\u5462\u3002\u300D</p><p>\u300C\u54E6\uFF1F\u300D\u8273\u51E4\u4E00\u6014\uFF0C\u300C\u8C01\u4F1A\u8981\u5979\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u62BF\u5634\u4E00\u7B11\uFF0C\u300C\u5E08\u7236\u4E5F\u8BA4\u8BC6\u7684\uFF0C\u662F\u54B1\u4EEC\u71D5\u56FD\u7684\u5F53\u671D\u91CD\u5C06\uFF0C\u91D1\u5F00\u7532\u91D1\u5927\u5C06\u519B\u3002\u300D</p><p>\u8273\u51E4\u5927\u662F\u5947\u602A\uFF0C\u300C\u91D1\u5F00\u7532\u8DDF\u9999\u8FDC\u4EC7\u6DF1\u4F3C\u6D77\uFF0C\u4ED6\u53C8\u624B\u63E1\u91CD\u6743\uFF0C\u8981\u4EC0\u4E48\u5973\u4EBA\u6CA1\u6709\uFF0C\u9999\u8FDC\u600E\u4E48\u4F1A\u5AC1\u7ED9\u4ED6\uFF1F\u300D</p><p>\u300C\u8BF4\u6765\u8BDD\u957F\u4E86\u5462\u3002\u53BB\u5E74\u79CB\u5929\uFF0C\u4F4F\u5728\u5BAB\u91CC\u7684\u6797\u5E08\u59D0\u7A81\u7136\u6000\u4E86\u5B55\u3002\u7687\u4E0A\u5F53\u65F6\u5C31\u547D\u4EBA\u628A\u5979\u6D3B\u57CB\uFF0C\u8BA9\u5A18\u5A18\u529D\u4E86\u4E0B\u6765\u3002\u6797\u5E08\u59D0\u65E9\u5C31\u4E0D\u518D\u63A5\u5BA2\uFF0C\u8EAB\u8FB9\u53EA\u6709\u592A\u76D1\u5BAB\u5973\uFF0C\u8FD9\u5B55\u6000\u7684\u7740\u5B9E\u8E4A\u8DF7\u3002\u300D</p><p>\u8273\u51E4\u7B11\u9053\uFF1A\u300C\u9999\u8FDC\u88AB\u4EBA\u4E0A\u5F97\u6700\u591A\uFF0C\u8FDE\u9A74\u9A6C\u90FD\u6CA1\u5C11\u5E72\u5979\u7684\u70C2\u5C44\uFF0C\u90A3\u65F6\u5019\u6CA1\u6709\u6000\u5B55\uFF0C\u8FDB\u5BAB\u53CD\u800C\u5927\u4E86\u809A\u5B50\u2014\u2014\u83AB\u975E\u662F\u6709\u4EBA\u53BB\u5978\u5A18\u5A18\uFF0C\u987A\u4FBF\u628A\u5979\u4E5F\u5E72\u4E86\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u77E5\u9053\u5E08\u7236\u6DF1\u6068\u7D2B\u73AB\u5F97\u5BA0\uFF0C\u6EE1\u95E8\u5E08\u5F92\uFF0C\u53EA\u6709\u5C0F\u5E08\u59B9\u4E00\u4E2A\u6CA1\u6709\u505A\u8FC7\u5A4A\u5B50\uFF0C\u65F6\u4E0D\u65F6\u8981\u8BA5\u523A\u51E0\u53E5\uFF0C\u5979\u6CA1\u63A5\u8BDD\u5934\uFF0C\u53EA\u7B11\u9053\uFF1A\u300C\u6797\u5E08\u59D0\u4EE5\u524D\u662F\u7EDD\u4E86\u7ECF\u7684\uFF0C\u5165\u5BAB\u540E\u5C31\u6CA1\u518D\u670D\u836F\u3002\u5979\u4E00\u4E2A\u4EBA\u4F4F\u5728\u504F\u5BAB\uFF0C\u786E\u5B9E\u662F\u88AB\u4EBA\u95EF\u8FDB\u6765\u5E72\u4E86\u3002\u4F46\u5979\u5F53\u65F6\u6CA1\u6562\u8BF4\uFF0C\u76F4\u5230\u5927\u4E86\u809A\u5B50\u624D\u7792\u4E0D\u4E0B\u53BB\u3002\u300D</p><p>\u7ADF\u7136\u6709\u4EBA\u6562\u5728\u5BAB\u91CC\u884C\u5978\uFF0C\u8FD9\u80C6\u91CF\u59D4\u5B9E\u4E0D\u5C0F\uFF0C\u8273\u51E4\u597D\u5947\u5730\u95EE\u9053\uFF1A\u300C\u662F\u8C01\u8FD9\u4E48\u5927\u80C6\uFF1F\u300D</p><p>\u300C\u6797\u5E08\u59D0\u4E5F\u4E0D\u77E5\u9053\uFF0C\u5979\u778E\u4E86\u773C\u775B\uFF0C\u53EA\u77E5\u9053\u90A3\u4EBA\u662F\u4E2A\u7537\u4EBA\uFF0C\u522B\u7684\u4EC0\u4E48\u90FD\u4E0D\u77E5\u9053\u4E86\u3002\u7687\u4E0A\u9976\u4E86\u6797\u5E08\u59D0\u6027\u547D\uFF0C\u5374\u4E0D\u8BA9\u5979\u628A\u5B69\u5B50\u751F\u4E0B\u6765\u3002\u5A18\u5A18\u62A4\u7740\u5E08\u59D0\u8DDF\u7687\u4E0A\u8D8A\u95F9\u8D8A\u50F5\u3002\u4E00\u76F4\u62D6\u5230\u4ECA\u5E74\u6625\u5929\uFF0C\u592A\u533B\u8BCA\u8FC7\u8109\uFF0C\u8BF4\u6000\u7684\u662F\u4E2A\u7537\u5B69\uFF0C\u7687\u4E0A\u66F4\u751F\u6C14\u4E86\uFF0C\u8BF4\u8981\u751F\u4E2A\u5C0F\u5A4A\u5B50\u73A9\u73A9\u4E5F\u5C31\u7F62\u4E86\uFF0C\u65E2\u7136\u662F\u7537\u5B69\u7ACB\u523B\u5C31\u8981\u6253\u6389\u3002\u300D</p><p>\u8273\u51E4\u9053\uFF1A\u300C\u8FD9\u8DDF\u5AC1\u4EBA\u6709\u4EC0\u4E48\u5173\u7CFB\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u7B11\u9053\uFF1A\u300C\u8FD9\u4E8B\u4E0D\u77E5\u600E\u4E48\u8BA9\u91D1\u5927\u5C06\u519B\u77E5\u9053\u4E86\uFF0C\u786C\u7740\u5934\u76AE\u8BF4\u90A3\u5B69\u5B50\u662F\u4ED6\u7684\u3002\u4ED6\u8DDF\u6797\u5E08\u59D0\u4EC7\u6068\u6781\u6DF1\uFF0C\u90A3\u6B21\u5165\u5BAB\uFF0C\u4E0D\u77E5\u600E\u4E48\u9047\u4E0A\u6797\u5E08\u59D0\uFF0C\u4E00\u65F6\u5174\u8D77\u53C8\u5E72\u4E86\u5979\u4E00\u6B21\uFF0C\u6CA1\u60F3\u5230\u8FD9\u4E48\u5DE7\u5C31\u8BA9\u6797\u5E08\u59D0\u5927\u4E86\u809A\u5B50\u3002\u5927\u5C06\u519B\u4E00\u76F4\u4E1C\u5F81\u897F\u8BA8\uFF0C\u6CA1\u987E\u4E0A\u6210\u5BB6\u517B\u5B50\uFF0C\u542C\u8BF4\u662F\u4E2A\u7537\u5B69\uFF0C\u6709\u4E9B\u820D\u4E0D\u5F97\uFF0C\u624D\u7980\u660E\u4E86\u7687\u4E0A\uFF0C\u60F3\u8981\u56DE\u8FD9\u4E2A\u513F\u5B50\u3002\u300D</p><p>\u300C\u8981\u513F\u5B50\u4E5F\u5C31\u7F62\u4E86\uFF0C\u9999\u8FDC\u505A\u4E86\u90A3\u4E48\u591A\u5E74\u5A4A\u5B50\uFF0C\u91D1\u5F00\u7532\u4F55\u5FC5\u628A\u4E86\u5979\u5A36\u56DE\u53BB\uFF0C\u4E5F\u4E0D\u6015\u8FB1\u6CA1\u4E86\u8EAB\u4EFD\uFF1F\u300D</p><p>\u300C\u6797\u5E08\u59D0\u4EE5\u524D\u88AB\u5F04\u6210\u90A3\u4E2A\u6837\u5B50\uFF0C\u7687\u4E0A\u4E5F\u4E0D\u4F1A\u7EB3\u5979\u4E3A\u5983\uFF0C\u5A18\u5A18\u5C31\u5BF9\u91D1\u5927\u5C06\u519B\u8BF4\uFF0C\u8981\u513F\u5B50\u53EF\u4EE5\uFF0C\u4F46\u8981\u628A\u6797\u5E08\u59D0\u660E\u5A92\u6B63\u5A36\uFF0C\u63A5\u56DE\u53BB\u5F53\u592B\u4EBA\u3002\u91D1\u5927\u5C06\u519B\u6CA1\u529E\u6CD5\uFF0C\u53EA\u597D\u7B54\u5E94\u3002\u300D</p><p>\u8273\u51E4\u6C89\u9ED8\u7247\u523B\uFF0C\u5632\u7B11\u9053\uFF1A\u300C\u4EBA\u5BB6\u662F\u6BCD\u4EE5\u5B50\u8D35\uFF0C\u6797\u5A4A\u5B50\u662F\u6BCD\u4EE5\u5B50\u5AC1\u3002\u80FD\u5F53\u4E0A\u5C06\u519B\u592B\u4EBA\u4E5F\u662F\u5979\u7684\u9020\u5316\u3002\u300D</p><p>\u300C\u53EF\u4E0D\u662F\u5417\uFF1F\u6211\u4EEC\u79BB\u4EAC\u65F6\u6797\u5E08\u59D0\u7684\u513F\u5B50\u521A\u6EE1\u6708\uFF0C\u91D1\u5927\u5C06\u519B\u7B11\u5F97\u5408\u4E0D\u62E2\u5634\uFF0C\u8FDE\u5E26\u7740\u5BF9\u6797\u5E08\u59D0\u4E5F\u6709\u4E86\u51E0\u5206\u60C5\u4EFD\u3002\u4ED6\u6E3A\u4E86\u4E00\u76EE\uFF0C\u6797\u5E08\u59D0\u4E24\u53EA\u773C\u90FD\u8D54\u4E86\u4ED6\uFF0C\u53C8\u53D7\u4E86\u90A3\u4E48\u591A\u82E6\uFF0C\u5982\u679C\u770B\u5F00\u4E86\uFF0C\u672A\u5C1D\u4E0D\u662F\u4E00\u6869\u597D\u59FB\u7F18\u5462\u3002\u300D</p><p>\u8273\u51E4\u51B7\u7B11\u4E0D\u5DF2\uFF0C\u300C\u597D\u59FB\u7F18\u7740\u5B9E\u4E0D\u5C11\u3002\u4F60\u8DDF\u5979\u4E00\u4E2A\u4E3A\u5983\uFF0C\u4E00\u4E2A\u4E3A\u540E\uFF0C\u54EA\u4E2A\u5973\u4EBA\u80FD\u8DDF\u4F60\u4EEC\u6BD4\u5462\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u53F9\u9053\uFF1A\u300C\u6211\u8FD9\u4E2A\u5983\u5B50\u53EA\u662F\u7A7A\u540D\uFF0C\u5176\u5B9E\u4E0D\u8FC7\u662F\u5A18\u5A18\u7684\u8D34\u8EAB\u4E2B\u9B1F\u7F62\u4E86\u3002\u7687\u4E0A\u7ED9\u4E86\u6211\u540D\u4EFD\uFF0C\u53EA\u662F\u8BA9\u6211\u5C3D\u5FC3\u4F3A\u5019\u5A18\u5A18\u3002\u300D</p><p>\u300C\u4F60\u4F3A\u5019\u5F97\u771F\u7528\u5FC3\u5462\u2026\u2026\u5A18\u5A18\u53D8\u6210\u90A3\u4E2A\u6837\u5B50\uFF0C\u4E5F\u6709\u4F60\u7684\u529F\u52B3\u5427\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u624B\u6307\u4E00\u50F5\u3002\u8273\u51E4\u6DE1\u6DE1\u9053\uFF1A\u300C\u4F60\u4E0D\u505A\u6211\u4E5F\u4F1A\u505A\u3002\u53EA\u662F\u6211\u4E0D\u660E\u767D\u4E3A\u4EC0\u4E48\u4F1A\u662F\u4F60\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u51B7\u51B7\u9053\uFF1A\u300C\u5F92\u513F\u4E0D\u77E5\u9053\u5E08\u7236\u8BF4\u4EC0\u4E48\u5462\u3002\u300D</p><p>\u8273\u51E4\u82E5\u65E0\u5176\u4E8B\u5730\u8BF4\uFF1A\u300C\u5979\u5BF9\u4E0D\u8D77\u6211\u4EEC\u5E08\u5F92\u6EE1\u95E8\uFF0C\u8FD9\u662F\u4E0A\u5929\u7ED9\u5979\u7684\u62A5\u5E94\uFF0C\u4E0D\u5173\u4F60\u7684\u4E8B\uFF0C\u4E5F\u4E0D\u5173\u6211\u7684\u4E8B\u3002\u5979\u6B20\u665A\u534E\u7684\u624B\u811A\u3001\u6B20\u9999\u8FDC\u4E00\u53CC\u773C\u775B\u3001\u6B20\u6211\u7684\u5C31\u66F4\u591A\u4E86\u2026</p><p>\u2026\u53EF\u5979\u6B20\u4F60\u4EC0\u4E48\u5462\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u6C89\u9ED8\u534A\u664C\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u5979\u6740\u4E86\u8521\u5927\u54E5\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u4FCF\u8138\u53D8\u8272\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u6211\u558A\u4E00\u58F0\uFF0C\u4FDD\u8BC1\u5BAB\u91CC\u90FD\u80FD\u542C\u5230\uFF0C\u4F60\u4EE5\u4E3A\u81EA\u5DF1\u90A3\u70B9\u529F\u592B\u6BD4\u5F97\u4E86\u6211\u5973\u513F\u5417\uFF1F\u6211\u529D\u4F60\u8FD8\u662F\u5FEB\u4E9B\u8D70\uFF0C\u4E0D\u7136\u7B49\u4F1A\u513F\u60F3\u8D70\u4E5F\u8D70\u4E0D\u4E86\u3002\u300D</p><p>\u300C\u5A18\u5A18\u606F\u6012\uFF0C\u300D\u9759\u989C\u5E76\u8D77\u624B\u6307\uFF0C\u5939\u4F4F\u5979\u7684\u4E73\u5934\u5E73\u5E73\u63D0\u8D77\uFF0C\u300C\u60F3\u8BA9\u5A18\u5A18\u5B89\u9759\u7684\u6CD5\u5B50\u6709\u5F88\u591A\uFF0C\u4F46\u8FD9\u4F1A\u513F\u5BAB\u91CC\u6CA1\u4EBA\uFF0C\u5974\u5A62\u5012\u60F3\u542C\u542C\u5A18\u5A18\u53EB\u5F97\u6709\u591A\u54CD\u5462\u3002\u300D</p><p>\u80A5\u7855\u7684\u4E73\u7403\u7F13\u7F13\u62C9\u957F\uFF0C\u5AE3\u7EA2\u7684\u4E73\u5C16\u88AB\u6307\u5C16\u634F\u5F97\u53D8\u5F62\uFF0C\u9664\u4E86\u6155\u5BB9\u9F99\uFF0C\u7D2B\u73AB\u4ECE\u672A\u88AB\u4EFB\u4F55\u7537\u4EBA\u78B0\u89E6\u8FC7\u8EAB\u4F53\uFF0C\u6B64\u523B\u7ADF\u7136\u88AB\u4E00\u4E2A\u4E0D\u7537\u4E0D\u5973\u7684\u6DEB\u7269\u73A9\u5F04\uFF0C\u4E0D\u7531\u7389\u5BB9\u51B0\u51B7\uFF0C\u7F8E\u76EE\u751F\u5BD2\u3002\u9759\u989C\u6DE1\u7B11\u9053\uFF1A\u300C\u5A18\u5A18\u7684\u5976\u5B50\u597D\u5927\u5462\uFF0C\u8FD9\u4E48\u5AE9\u7684\u808C\u80A4\uFF0C\u5C0F\u5FC3\u4E0D\u8981\u6491\u7834\u4E86\u3002\u300D</p><p>\u9759\u989C\u77E5\u9053\u73AB\u7470\u4ED9\u5B50\u5DF2\u7ECF\u7EC3\u6210\u51E4\u51F0\u5B9D\u5178\u7B2C\u4E5D\u5C42\uFF0C\u4F46\u6B66\u529F\u518D\u9AD8\uFF0C\u56DB\u80A2\u88AB\u622A\u4E5F\u4E0D\u8FC7\u662F\u4E00\u6BB5\u4EFB\u4EBA\u5BB0\u5272\u7684\u7F8E\u8089\uFF0C\u5979\u6709\u6043\u65E0\u6050\uFF0C\u4E00\u624B\u6258\u7740\u4E73\u6839\u7528\u529B\u6293\u4E0B\u3002\u4E30\u817B\u7684\u4E73\u8089\u5E94\u624B\u800C\u9677\uFF0C\u4E73\u7403\u4E0A\u90E8\u9F13\u8D77\uFF0C\u80C0\u5F97\u51E0\u4E4E\u8981\u8FF8\u88C2\u5F00\u6765\u3002\u9759\u989C\u5DE6\u624B\u63E1\u4F4F\u4E73\u6839\uFF0C\u53F3\u624B\u7EA4\u6307\u7FD8\u8D77\uFF0C\u6309\u7740\u5C0F\u5DE7\u7684\u4E73\u5934\u7528\u529B\u5411\u4E73\u7403\u4E2D\u6363\u53BB\uFF0C\u5FAE\u4E00\u7528\u529B\uFF0C\u4E73\u6655\u4FBF\u5373\u51F9\u4E0B\uFF0C\u4E0D\u4EC5\u4E73\u5934\u9677\u5165\u4E73\u8089\uFF0C\u8FDE\u624B\u6307\u4E5F\u88AB\u541E\u6CA1\u4E86\u4E00\u4E2A\u6307\u8282\u3002\u4E73\u623F\u672C\u5C31\u654F\u611F\uFF0C\u6B64\u523B\u89E6\u53CA\u4E73\u817A\uFF0C\u66F4\u662F\u75DB\u5F7B\u5FC3\u80BA\u3002\u7D2B\u73AB\u54AC\u7D27\u7259\u5173\uFF0C\u75BC\u5F97\u7709\u89D2\u5FAE\u5FAE\u8DF3\u52A8\u3002</p><p>\u767D\u817B\u7684\u4E73\u7403\u5F77\u4F5B\u88AB\u7389\u6307\u523A\u7A7F\uFF0C\u4E73\u5934\u88AB\u6DF1\u6DF1\u63A8\u5165\u4E73\u8089\u4E2D\uFF0C\u5916\u9762\u770B\u4E0D\u5230\u4E00\u4E1D\u7EA2\u8272\u3002\u9759\u989C\u7FD8\u7740\u624B\u6307\u5728\u9999\u8F6F\u7684\u4E73\u7403\u5185\u56DB\u5904\u638F\u6316\uFF0C\u7B11\u9053\uFF1A\u300C\u4E0D\u77E5\u9053\u628A\u8089\u68D2\u63D2\u5230\u5A18\u5A18\u5976\u5B50\u91CC\u662F\u4EC0\u4E48\u6ECB\u5473\u2014\u2014\u54A6\uFF1F\u300D</p><p>\u9759\u989C\u76EE\u5149\u4E00\u8DF3\uFF0C\u53EA\u89C1\u73AB\u7470\u4ED9\u5B50\u5C0F\u8179\u4E0A\u7A81\u7136\u6D6E\u73B0\u51FA\u4E00\u53EA\u6EF4\u8840\u7684\u51E4\u51F0\uFF0C\u8212\u5C55\u7684\u51E4\u7FFC\u4ECE\u4E73\u7403\u4E0B\u7F18\u4E00\u76F4\u4F38\u5230\u8179\u4E0B\uFF0C\u5360\u636E\u4E86\u534A\u4E2A\u8EAF\u5E72\u3002\u5947\u602A\u7684\u662F\u5149\u6ED1\u5982\u8102\u7684\u5C0F\u8179\u4E0A\u770B\u4E0D\u51FA\u4EFB\u4F55\u4F24\u75D5\uFF0C\u8FD9\u7EB9\u8EAB\u5012\u50CF\u662F\u9694\u7740\u808C\u80A4\u523A\u5728\u4E86\u808C\u80A4\u4E4B\u4E0B\u3002</p><p>\u5FFD\u7136\u7D2B\u73AB\u6A31\u5507\u4E00\u5F20\uFF0C\u541F\u5531\u822C\u53D1\u51FA\u4E00\u58F0\u300C\u676D\u6602\u2014\u2014\u300D\uFF0C\u8BED\u8C03\u5148\u5E73\u540E\u626C\uFF0C\u4F59\u97F3\u62D6\u5F97\u5F88\u957F\uFF0C\u58F0\u97F3\u5E76\u4E0D\u5927\uFF0C\u4F46\u968F\u7740\u97F3\u8C03\u7684\u6291\u626C\u9759\u989C\u53EA\u89C9\u5FC3\u810F\u8C61\u88AB\u4EBA\u63E1\u4F4F\uFF0C\u7528\u529B\u638F\u51FA\uFF0C\u80F8\u4E2D\u987F\u65F6\u7A7A\u843D\u843D\u4E00\u7247\u3002\u5979\u6D51\u8EAB\u52B2\u529B\u4E00\u677E\uFF0C\u9669\u4E9B\u5750\u5012\u5728\u5730\u3002</p><p>\u88AB\u6363\u5F97\u51F9\u9677\u7684\u4E73\u7403\u5411\u5916\u4E00\u5F39\uFF0C\u6062\u590D\u4E86\u539F\u72B6\uFF0C\u4E73\u5934\u5FAE\u5FAE\u4E0A\u7FD8\uFF0C\u5728\u6C89\u7538\u7538\u7684\u4E73\u7403\u4E0A\u9707\u98A4\u4E0D\u5DF2\u3002\u9759\u989C\u6CA1\u60F3\u5230\u7D2B\u73AB\u8FD8\u6709\u53CD\u51FB\u7684\u4F59\u5730\uFF0C\u88AB\u5979\u7A81\u5982\u5176\u6765\u7684\u4E00\u559D\uFF0C\u5FC3\u795E\u7ACB\u544A\u5931\u5B88\uFF0C\u82E5\u8FD9\u4E00\u58F0\u529B\u9053\u518D\u5F3A\u4E0A\u51E0\u5206\uFF0C\u6050\u6015\u5C31\u8981\u5455\u8840\u5F53\u573A\u3002</p><p>\u7D2B\u73AB\u6CA1\u60F3\u5230\u5979\u6B66\u529F\u5982\u6B64\u4E0D\u4FD7\uFF0C\u81EA\u5DF1\u5341\u6210\u529F\u529B\u7684\u4E00\u559D\uFF0C\u4E5F\u672A\u80FD\u4F7F\u5979\u53D7\u4F24\u3002\u9759\u989C\u8C03\u606F\u534A\u664C\uFF0C\u8138\u8272\u624D\u6162\u6162\u5E73\u590D\u3002\u5979\u6276\u7740\u6447\u7BEE\u72E0\u72E0\u76EF\u7740\u7D2B\u73AB\uFF0C\u5FFD\u7136\u62AC\u624B\u671D\u5979\u8EAB\u4E0A\u6293\u53BB\u3002\u624B\u6307\u521A\u9012\u51FA\u6570\u5BF8\uFF0C\u4E0D\u7531\u300C\u54CE\u5440\u300D\u4E00\u58F0\u5A07\u547C\uFF0C\u5C0F\u817F\u5267\u75DB\u3002\u5979\u5FCD\u75DB\u4F4E\u5934\u770B\u53BB\uFF0C\u7ADF\u7136\u662F\u90A3\u6761\u6BCD\u72AC\u5F20\u53E3\u54AC\u5728\u817F\u4E0A\u3002</p><p>\u9759\u989C\u6839\u672C\u6CA1\u6709\u7559\u610F\u98CE\u665A\u534E\u8FD8\u5367\u5728\u7BEE\u4E0B\uFF0C\u6BD4\u8D77\u6765\u8FD9\u6761\u624B\u811A\u53EA\u5269\u4E0B\u534A\u622A\u7684\u73A9\u7269\u8FD8\u4E0D\u5982\u4E00\u6761\u96CC\u72AC\u6709\u5A01\u80C1\u3002\u5979\u62AC\u817F\u5C06\u5931\u53BB\u795E\u667A\u7684\u98CE\u665A\u534E\u8E22\u5230\u4E00\u8FB9\uFF0C\u987A\u52BF\u5C01\u4E86\u5979\u7684\u7A74\u9053\u3002\u98CE\u665A\u534E\u8EAB\u5B50\u4FA7\u4EF0\uFF0C\u4E00\u76F4\u5782\u5728\u8EAB\u4E0B\u7684\u4E73\u623F\u7FFB\u8D77\uFF0C\u4E73\u4E0A\u8D6B\u7136\u9732\u51FA\u4E00\u53EA\u8D2F\u7A7F\u4E73\u6655\u7684\u8089\u6D1E\uFF0C\u7ADF\u7136\u662F\u88AB\u4EBA\u628A\u4E73\u623F\u4E5F\u5F00\u4E86\u82DE\u3002</p><p>\u9759\u989C\u597D\u5947\u5730\u6311\u8D77\u5979\u7684\u8EAF\u4F53\uFF0C\u671D\u4E73\u6D1E\u5185\u638F\u53BB\u3002\u98CE\u665A\u534E\u75DB\u7684\u545C\u545C\u4F4E\u53EB\uFF0C\u5C31\u50CF\u4E00\u53EA\u53D7\u4F24\u7684\u5C0F\u6BCD\u72D7\u822C\u51C4\u607B\u54C0\u9E23\u3002\u7D2B\u73AB\u4F7F\u52B2\u632A\u52A8\u8EAF\u5E72\uFF0C\u5634\u91CC\u53EB\u9053\uFF1A\u300C\u4E0D\u8981\u78B0\u5979\uFF01\u300D</p><p>\u9759\u989C\u56DE\u7738\u4E00\u7B11\uFF0C\u6254\u4E0B\u98CE\u665A\u534E\uFF0C\u62AC\u638C\u671D\u7D2B\u73AB\u9888\u4E2D\u62CD\u53BB\uFF0C\u51C6\u5907\u5C01\u4E86\u5979\u7684\u54D1\u7A74\uFF0C\u4E34\u4E86\u624B\u8155\u5FAE\u8F6C\uFF0C\u5370\u5411\u5979\u8170\u95F4\u3002\u7D2B\u73AB\u79C0\u53D1\u626C\u8D77\uFF0C\u52C9\u5F3A\u6321\u4E86\u51E0\u4E0B\uFF0C\u6700\u540E\u8FD8\u662F\u88AB\u5979\u70B9\u4E2D\u7AE0\u95E8\u7A74\uFF0C\u5C01\u4E86\u5185\u529B\u3002\u4E0E\u7D2B\u73AB\u5185\u529B\u4E00\u4EA4\uFF0C\u9759\u989C\u624D\u53D1\u89C9\u5979\u7684\u771F\u6C14\u5E76\u4E0D\u50CF\u81EA\u5DF1\u60F3\u50CF\u4E2D\u90A3\u4E48\u5145\u6C9B\u3002\u5979\u7565\u4E00\u601D\u5FD6\uFF0C\u4FBF\u5373\u4E86\u7136\uFF0C\u6674\u96EA\u672A\u66FE\u4FEE\u70BC\u4FBF\u62E5\u6709\u51E4\u51F0\u5B9D\u5178\u7684\u771F\u6C14\uFF0C\u60F3\u6765\u662F\u7D2B\u73AB\u4F20\u529F\u7684\u7ED3\u679C\u4E86\u3002</p><p>\u5979\u4ECE\u6447\u7BEE\u4E2D\u53D6\u51FA\u7D2B\u73AB\u5149\u6E9C\u6E9C\u7684\u8089\u4F53\uFF0C\u653E\u5728\u6848\u4E0A\u4E00\u8FB9\u89C2\u8D4F\uFF0C\u4E00\u8FB9\u8D5E\u53F9\u9053\uFF1A\u300C\u5A18\u5A18\u7684\u8EAB\u5B50\u771F\u662F\u7F8E\u5F97\u65E0\u8BDD\u53EF\u8BF4\uFF0C\u6CA1\u6709\u4E86\u624B\u811A\u8FD8\u8FD9\u4E48\u6F02\u4EAE\uFF0C\u53C8\u767D\u53C8\u7EC6\uFF0C\u597D\u50CF\u4E00\u53EA\u7389\u74F6\u5462\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u9759\u9759\u8EBA\u5728\u6848\u4E0A\uFF0C\u5149\u6ED1\u7684\u8EAF\u5E72\u5F77\u4F5B\u4ECE\u672A\u751F\u8FC7\u56DB\u80A2\u4E00\u6837\u5B8C\u7F8E\uFF0C\u90A3\u5177\u73B2\u73D1\u6709\u81F4\u7684\u7389\u4F53\u662F\u5982\u6B64\u5B8C\u7F8E\uFF0C\u7ADF\u8BA9\u4EBA\u89C9\u5F97\u5176\u4ED6\u5973\u4EBA\u7684\u624B\u811A\u90FD\u663E\u5F97\u591A\u4F59\u3002\u4E1D\u7EF8\u822C\u7EC6\u6ED1\u7684\u808C\u80A4\u770B\u4E0D\u5230\u4E00\u4E1D\u76B1\u7EB9\uFF0C\u5B9B\u5982\u5145\u6EE1\u6C41\u6DB2\u822C\u9971\u6EE1\u3002</p><p>\u9759\u989C\u63E1\u4F4F\u7D2B\u73AB\u7EA4\u67D4\u7684\u8170\u80A2\uFF0C\u5C06\u5979\u5931\u53BB\u53CC\u817F\u7684\u4E0B\u4F53\u4E3E\u5230\u773C\u524D\uFF0C\u4ED4\u7EC6\u6B23\u8D4F\u73AB\u7470\u4ED9\u5B50\u79D8\u5904\u7684\u7F8E\u6001\u3002\u5979\u7684\u4E0B\u4F53\u7EA2\u767D\u5206\u660E\uFF0C\u6D01\u51C0\u5F02\u5E38\uFF0C\u67D4\u7F8E\u7684\u82B1\u74E3\u663E\u51FA\u5C11\u5987\u6210\u719F\u7684\u8273\u7EA2\uFF0C\u5D4C\u5728\u767D\u99A5\u99A5\u7684\u7389\u80A1\u95F4\uFF0C\u72B9\u5982\u4E00\u4EF6\u7CBE\u81F4\u7684\u827A\u672F\u54C1\uFF0C\u5149\u5F69\u593A\u76EE\u3002</p><p>\u7D2B\u73AB\u53C8\u7F9E\u53C8\u6012\uFF0C\u5979\u65E9\u770B\u51FA\u9759\u989C\u5FC3\u6000\u9B3C\u80CE\uFF0C\u5374\u6BEB\u4E0D\u5728\u610F\uFF0C\u751A\u81F3\u89C9\u5F97\u80FD\u5728\u54E5\u54E5\u7684\u4EC7\u4EBA\u624B\u4E2D\u7ED3\u675F\u751F\u547D\uFF0C\u672A\u5C1D\u4E0D\u662F\u4EF6\u597D\u4E8B\u3002\u53EF\u5979\u6CA1\u60F3\u5230\u9759\u989C\u7684\u4EC7\u6068\u4F1A\u8FD9\u4E48\u6DF1\uFF0C\u4E0D\u4F46\u8981\u53D6\u5979\u6027\u547D\uFF0C\u8FD8\u8981\u62FF\u81EA\u5DF1\u7684\u8EAB\u4F53\u6CC4\u6124\u3002</p><p>\u9759\u989C\u54C2\u7B11\u9053\uFF1A\u300C\u597D\u5AE9\u7684\u5C44\uFF0C\u4E0D\u77E5\u9053\u63D2\u8D77\u6765\u4EC0\u4E48\u6ECB\u5473\u3002\u300D</p><p>\u6CA1\u6709\u4EFB\u4F55\u9884\u5146\uFF0C\u4E00\u4E2A\u575A\u786C\u7684\u7269\u4F53\u4FBF\u72E0\u72E0\u63D2\u5165\u4F53\u5185\u3002</p>',89);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
