import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_56" tabindex="-1"><a class="header-anchor" href="#_56" aria-hidden="true">#</a> 56</h1><p>\u88AB\u5C01\u95ED\u7684\u610F\u8BC6\u4ECE\u6DF7\u6C8C\u4E2D\u7F13\u7F13\u6D6E\u51FA\uFF0C\u68B5\u96EA\u828D\u89C9\u5F97\u8EAB\u5B50\u5F88\u8F7B\uFF0C\u597D\u50CF\u4E00\u4E1D\u98DE\u7D6E\uFF0C\u5728\u865A\u65E0\u4E2D\u60A0\u60A0\u6D6E\u8361\u3002\u5979\u4E0D\u77E5\u9053\u81EA\u5DF1\u7761\u4E86\u591A\u4E45\uFF0C\u4E5F\u4E0D\u77E5\u9053\u4E3A\u4F55\u5165\u7761\u3002\u5979\u7741\u5F00\u773C\uFF0C\u5374\u4EC0\u4E48\u90FD\u770B\u4E0D\u89C1\u3002</p><p>\u6726\u80E7\u4E2D\uFF0C\u6709\u4EBA\u6258\u8D77\u5979\u7684\u8116\u9888\uFF0C\u63A5\u7740\u4E00\u5F20\u5E26\u7740\u8165\u6C14\u7684\u5634\u5DF4\u70ED\u70ED\u8986\u5728\u5507\u4E0A\u3002\u68B5\u96EA\u828D\u538C\u6076\u5730\u76B1\u8D77\u7709\u5934\uFF0C\u820C\u5C16\u95EA\u907F\u7740\u4E0D\u4E0E\u90A3\u5F20\u5634\u78B0\u89E6\u3002\u4F46\u5979\u7684\u95EA\u907F\u6BEB\u65E0\u529B\u6C14\uFF0C\u9999\u820C\u53EA\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u4FBF\u88AB\u4EBA\u5438\u4F4F\u3002\u63A5\u7740\u4E00\u80A1\u9ECF\u7A20\u7684\u6DB2\u4F53\u4ECE\u90A3\u4EBA\u820C\u4E0A\u6ED1\u843D\uFF0C\u6D8C\u5165\u5589\u4E2D\u3002\u90A3\u80A1\u6DB2\u4F53\u5145\u6EE1\u751C\u817B\u817B\u7684\u8165\u5473\uFF0C\u6DCC\u8FC7\u5589\u5934\u65F6\u53D8\u5F97\u53D1\u82E6\u3002\u5979\u6781\u529B\u633A\u52A8\u820C\u6839\uFF0C\u60F3\u628A\u5B83\u5410\u51FA\u6765\uFF0C\u4F46\u90A3\u4EBA\u820C\u5C16\u4E00\u6405\uFF0C\u5C06\u5979\u7684\u820C\u5934\u538B\u5F97\u52A8\u5F39\u4E0D\u5F97\u3002</p><p>\u96EA\u767D\u7684\u5589\u5934\u5FAE\u5FAE\u6ED1\u52A8\uFF0C\u5C06\u8165\u81BB\u7684\u9ECF\u6DB2\u541E\u5165\u8179\u4E2D\u3002\u90A3\u4EBA\u7684\u820C\u5934\u5728\u5979\u53E3\u4E2D\u6405\u5F04\u591A\u65F6\uFF0C\u7B49\u5979\u5B8C\u5168\u54BD\u4E0B\u9ECF\u6DB2\uFF0C\u624D\u604B\u604B\u4E0D\u820D\u5730\u677E\u5F00\u3002\u63A5\u7740\u4E00\u4E2A\u5973\u58F0\u5728\u8033\u8FB9\u4F4E\u7B11\u9053\uFF1A\u300C\u8FD9\u4E48\u7F8E\u5473\u7684\u7AE5\u5B50\u7CBE\uFF0C\u771F\u662F\u4FBF\u5B9C\u4F60\u4E86\u2026\u2026\u300D</p><p>\u68B5\u96EA\u828D\u7389\u8138\u53D1\u767D\uFF0C\u63A5\u7740\u5589\u5934\u5443\u5443\u8FDE\u58F0\uFF0C\u51E0\u6B32\u4F5C\u5455\u3002\u5979\u4E00\u751F\u8339\u7D20\uFF0C\u996E\u98DF\u6709\u534A\u70B9\u4E0D\u6D01\u90FD\u4E0D\u613F\u6CBE\u67D3\uFF0C\u4F55\u51B5\u662F\u541E\u4E0B\u4E00\u4E2A\u7537\u7AE5\u7684\u7CBE\u6DB2\u3002\u987F\u65F6\u7FFB\u8FC7\u8EAB\u5B50\uFF0C\u4F0F\u5728\u77F3\u6848\u4E0A\u5455\u5410\u8D77\u6765\u2026\u2026\u8EAB\u5B50\u4E00\u52A8\uFF0C\u5979\u624D\u53D1\u73B0\u81EA\u5DF1\u4EC0\u4E48\u90FD\u6CA1\u78B0\u5230\uFF0C\u624B\u811A\u5C31\u50CF\u6D88\u5931\u822C\u6BEB\u65E0\u77E5\u89C9\u3002</p><p>\u68B5\u96EA\u828D\u5634\u5507\u98A4\u6296\u8D77\u6765\uFF0C\u5979\u8BD5\u7740\u4E00\u63D0\u5185\u606F\uFF0C\u7ACB\u5373\u53D1\u89C9\u81EA\u5DF1\u7684\u7ECF\u7EDC\u5DF2\u7ECF\u5B8C\u5168\u6539\u53D8\uFF0C\u771F\u5143\u8FD8\u5728\uFF0C\u5374\u65E0\u6CD5\u8C03\u52A8\uFF0C\u5B83\u8131\u79BB\u4E86\u8EAB\u4F53\u7684\u63A7\u5236\uFF0C\u65C1\u82E5\u65E0\u4EBA\u5730\u81EA\u884C\u8FD0\u8F6C\uFF0C\u7EF4\u6301\u7740\u8089\u4F53\u7684\u751F\u673A\u3002\u5979\u6240\u719F\u6089\u7684\u8840\u8109\u4E5F\u540C\u6837\u53D8\u5F97\u964C\u751F\uFF0C\u5FC3\u810F\u6BCF\u4E00\u6B21\u8DF3\u52A8\uFF0C\u90FD\u5E26\u7740\u70BD\u70ED\u7684\u75DB\u695A\uFF0C\u4F3C\u4E4E\u4F53\u5185\u6D41\u52A8\u4E0D\u662F\u8840\u6DB2\uFF0C\u800C\u662F\u6EDA\u6C34\u3002\u5979\u6050\u60E7\u5730\u60CA\u53EB\u4E00\u58F0\uFF0C\u624D\u53D1\u73B0\u81EA\u5DF1\u7684\u58F0\u97F3\u4E5F\u88AB\u5265\u593A\u4E86\u3002</p><p>\u90A3\u4E2A\u6E29\u67D4\u7684\u5973\u58F0\u6B3E\u6B3E\u54CD\u8D77\uFF0C\u300C\u7761\u5F97\u597D\u4E48\uFF1F\u5982\u679C\u4E0D\u662F\u5230\u4E86\u65F6\u8FB0\uFF0C\u771F\u4E0D\u60F3\u5524\u9192\u4F60\u5462\u2026\u2026\u300D</p><p>\u81EA\u5DF1\u7761\u4E86\u591A\u4E45\uFF1F\u8FD9\u6BB5\u65F6\u95F4\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F\u5979\u662F\u8C01\uFF1F\u58F0\u97F3\u542C\u8D77\u6765\u597D\u719F\u6089\u2026\u2026\u68B5\u96EA\u828D\u6014\u6014\u542C\u7740\u90A3\u4E2A\u58F0\u97F3\u3002</p><p>\u300C\u8FD9\u5341\u51E0\u5E74\u6765\uFF0C\u6211\u8E0F\u904D\u5357\u6D77\uFF0C\u5C31\u662F\u4E3A\u4E86\u4F60\u8FD9\u820D\u5229\u4E4B\u4F53\u2026\u2026\u597D\u4E45\u6CA1\u548C\u4F60\u804A\u5929\u4E86\uFF0C\u96EA\u828D\uFF0C\u6211\u77E5\u9053\u4F60\u542C\u5F97\u89C1\u7684\u2026\u2026\u300D</p><p>\u5979\u60F3\u8D77\u6765\u4E86\uFF0C\u8FD9\u4E2A\u5973\u4EBA\u66FE\u7ECF\u662F\u5979\u7684\u597D\u53CB\u3002\u90A3\u65F6\u5979\u8FD8\u662F\u4E2A\u51FA\u5BB6\u4EBA\uFF0C\u4EE5\u8D85\u5353\u7684\u6B66\u529F\u88AB\u4EBA\u5C0A\u79F0\u4E3A\u96EA\u5CF0\u795E\u5C3C\uFF0C\u95E8\u4E0B\u56DB\u540D\u5F1F\u5B50\u60CA\u624D\u7EDD\u8273\uFF0C\u540D\u52A8\u6C5F\u6E56\u2026\u2026</p><p>\u5341\u516D\u5E74\u524D\uFF0C\u6B66\u6797\u4E2D\u4EBA\u90FD\u4EE5\u4E3A\u907F\u5C45\u4E16\u5916\u767E\u4F59\u5E74\u7684\u98D8\u6885\u5CF0\uFF0C\u4F1A\u4ECE\u8FD9\u4E00\u4EE3\u8D77\u6B63\u5F0F\u8E0F\u5165\u6C5F\u6E56\uFF0C\u8DFB\u8EAB\u4E0E\u5927\u5B5A\u7075\u9E6B\u5BFA\u548C\u4E5D\u534E\u5251\u6D3E\u9F50\u540D\u7684\u4E00\u6D41\u95E8\u6D3E\u3002\u4F46\u8FD9\u4E00\u5207\u5239\u90A3\u95F4\u70DF\u9500\u4E91\u6563\uFF0C\u6614\u65E5\u79CD\u79CD\u5982\u68A6\u5982\u5E7B\u5982\u9732\u73E0\u6CE1\u5F71\uFF0C\u8F6C\u77AC\u6D88\u901D\u5F97\u65E0\u5F71\u65E0\u8E2A\u3002\u98D8\u6885\u5CF0\u8BF8\u5973\u5C3D\u6570\u843D\u5165\u9B54\u7A9F\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E5F\u5728\u5386\u5C3D\u78E8\u96BE\u4E4B\u540E\u6613\u540D\u8273\u51E4\uFF0C\u6210\u4E3A\u661F\u6708\u6E56\u6700\u4EE4\u4EBA\u6050\u60E7\u7684\u6740\u624B\u3002</p><p>\u661F\u6708\u6E56\u6CA1\u6709\u6E05\u89C4\u6212\u5F8B\uFF0C\u8273\u51E4\u5C3D\u53EF\u4EE5\u7EB5\u60C5\u6DEB\u6B32\uFF0C\u65E5\u5B50\u8FC7\u5F97\u5341\u4E8C\u5206\u7684\u60EC\u610F\u3002\u4F46\u5979\u5FC3\u91CC\u59CB\u7EC8\u6709\u4E00\u4E2A\u5730\u65B9\u9690\u9690\u4F5C\u75DB\uFF0C\u90A3\u5C31\u662F\u300A\u51E4\u51F0\u5B9D\u5178\u300B\u3002\u8273\u51E4\u6BD5\u751F\u4FEE\u70BC\u98D8\u6885\u5CF0\u8FD9\u95E8\u795E\u529F\uFF0C\u8D39\u5C3D\u5343\u8F9B\u4E07\u82E6\u624D\u5728\u5931\u8EAB\u4E8E\u6155\u5BB9\u9F99\u4E4B\u9645\u7A81\u7834\u4E86\u7B2C\u4E03\u5C42\u3002\u90A3\u65F6\u5979\u4E0E\u5BAB\u4E3B\u65E5\u591C\u53CC\u4FEE\uFF0C\u4EB2\u5BC6\u65E0\u95F4\u3002\u6CA1\u60F3\u5230\u6B64\u540E\u5341\u4F59\u5E74\u5979\u7684\u300A\u51E4\u51F0\u5B9D\u5178\u300B\u518D\u65E0\u5BF8\u8FDB\uFF0C\u5374\u662F\u81EA\u5DF1\u6700\u5FC3\u7231\u7684\u5C0F\u5F92\u513F\u6155\u5BB9\u7D2B\u73AB\u4E00\u6C14\u7EC3\u6210\u7B2C\u4E5D\u5C42\u51E4\u6E05\u7D2B\u9E3E\uFF0C\u593A\u8D70\u4E86\u5BAB\u4E3B\u5BF9\u5979\u7684\u5BA0\u7231\u3002</p><p>\u8273\u51E4\u53C8\u5AC9\u53C8\u6068\uFF0C\u8BBE\u8BA1\u628A\u8273\u51A0\u7FA4\u82B3\u7684\u73AB\u7470\u4ED9\u5B50\u5F04\u6210\u56DB\u80A2\u4FF1\u65E0\u7684\u5E9F\u4EBA\uFF0C\u53EF\u7531\u4E8E\u5979\u7684\u5B50\u5BAB\u88AB\u593A\u80CE\u82B1\u6BC1\u53BB\uFF0C\u6700\u540E\u4E00\u5173\u9634\u4E0A\u52A0\u9634\u518D\u65E0\u4FEE\u6210\u7684\u53EF\u80FD\u3002\u8273\u51E4\u60F3\u5C3D\u529E\u6CD5\uFF0C\u751A\u81F3\u593A\u53BB\u8427\u4F5B\u5974\u7684\u5973\u80CE\u5316\u4E3A\u5DF1\u7528\uFF0C\u4F9D\u7136\u6BEB\u65E0\u7ED3\u679C\u3002\u5979\u767E\u822C\u54C0\u6C42\uFF0C\u624D\u4ECE\u53F6\u884C\u5357\u53E3\u91CC\u5F97\u77E5\u4E86\u4E00\u79CD\u501F\u52A9\u820D\u5229\u4E4B\u4F53\u4FEE\u6210\u5B9D\u5178\u7684\u6CD5\u5B50\u3002</p><p>\u6B64\u6CD5\u8981\u5148\u6311\u9009\u4E00\u540D\u8EAB\u5177\u81F3\u9634\u4E4B\u4F53\u7684\u5973\u5B50\uFF0C\u4EE5\u667A\u6167\u4E0E\u5B81\u9759\u4F7F\u5176\u517B\u6210\u7075\u5FC3\uFF1B\u540C\u65F6\u8FD8\u8981\u8BA9\u5979\u957F\u5E74\u6D78\u6DEB\u4E8E\u767E\u836F\u4E4B\u95F4\uFF0C\u4EE4\u5176\u8840\u901A\u8109\u987A\uFF0C\u6C14\u606F\u8FE5\u5F02\u5E38\u4EBA\u2014\u2014\u8FD9\u6837\u624D\u80FD\u5F97\u5230\u4E00\u5177\u820D\u5229\u4E4B\u4F53\u3002</p><p>\u8981\u5F97\u5230\u820D\u5229\u4E4B\u4F53\u5DF2\u662F\u5343\u96BE\u4E07\u96BE\uFF0C\u4F46\u8FD9\u53EA\u662F\u7B2C\u4E00\u6B65\u3002\u7B2C\u4E8C\u6B65\u8981\u7834\u53BB\u820D\u5229\u4F53\u7EF4\u62A4\u591A\u5E74\u7684\u8D1E\u6D01\uFF0C\u8BA9\u5979\u4E0E\u7537\u5B50\u4EA4\u5408\uFF0C\u53D7\u80CE\u6210\u5B55\uFF1B\u7136\u540E\u518D\u6539\u53D8\u5979\u7684\u7ECF\u8109\uFF0C\u4EE5\u8840\u8695\u3001\u836F\u9152\u52A0\u4EE5\u70B9\u5316\uFF0C\u5728\u6B64\u8FC7\u7A0B\u4E2D\u9700\u8981\u4FDD\u6301\u820D\u5229\u4E4B\u4F53\u7684\u7EDD\u5BF9\u5B89\u9759\uFF0C\u4F7F\u9152\u6DB2\u80FD\u878D\u5165\u8840\u8109\uFF0C\u6FC0\u53D1\u5176\u4F53\u5185\u7684\u5F02\u72B6\uFF1B\u540C\u65F6\u8FD8\u8981\u4FDD\u6301\u5979\u5FC3\u5934\u4E00\u70B9\u7075\u5149\u4E0D\u6627\uFF0C\u76F4\u5230\u80CE\u513F\u5728\u6BCD\u4F53\u6210\u719F\u3002\u6700\u540E\u5728\u516B\u4E2A\u6708\u65F6\u53D6\u51FA\u5973\u80CE\uFF0C\u5316\u4E3A\u5DF1\u6709\u624D\u7B97\u5927\u529F\u544A\u6210\u3002</p><p>\u6B64\u6CD5\u7E41\u96BE\u4E4B\u6781\uFF0C\u9664\u4E86\u53EF\u9047\u4E0D\u53EF\u6C42\u7684\u8FD0\u6C14\u4E4B\u5916\uFF0C\u8FD8\u8981\u65E0\u6BD4\u7684\u8010\u5FC3\uFF0C\u5373\u4F7F\u4E00\u5207\u987A\u5229\uFF0C\u4E5F\u9700\u8981\u4E00\u7532\u5B50\u7684\u65F6\u95F4\u3002</p><p>\u8273\u51E4\u7ACB\u65F6\u5C31\u60F3\u5230\u4E86\u68B5\u96EA\u828D\u3002\u8FD9\u4F4D\u5973\u795E\u533B\u5185\u5916\u53CC\u4FEE\uFF0C\u7075\u5FC3\u6167\u8D28\uFF0C\u7B80\u76F4\u5C31\u662F\u820D\u5229\u4E4B\u4F53\u7684\u4E0D\u4E8C\u4EBA\u9009\u3002\u5341\u4F59\u5E74\u6765\uFF0C\u5979\u8E0F\u904D\u5357\u6D77\uFF0C\u53EF\u68B5\u96EA\u828D\u5C31\u50CF\u6D88\u5931\u822C\uFF0C\u6CA1\u6709\u4EFB\u4F55\u97F3\u8BAF\u3002\u5982\u679C\u52A8\u7528\u661F\u6708\u6E56\u7684\u52BF\u529B\uFF0C\u8981\u627E\u51FA\u9999\u836F\u5929\u5973\u5E76\u975E\u96BE\u4E8B\uFF0C\u4F46\u8273\u51E4\u5FC3\u91CC\u6709\u9B3C\uFF0C\u53EA\u5728\u79C1\u4E0B\u627E\u5BFB\uFF0C\u9664\u4E86\u79C1\u4EA4\u751A\u597D\u7684\u767D\u6C0F\u59D0\u59B9\u4E4B\u5916\uFF0C\u6CA1\u6709\u900F\u9732\u534A\u70B9\u98CE\u58F0\u3002</p><p>\u5F97\u77E5\u68B5\u96EA\u828D\u7684\u51FA\u73B0\uFF0C\u8273\u51E4\u6B23\u559C\u82E5\u72C2\uFF0C\u66F4\u5999\u7684\u662F\u90A3\u4E2A\u6B7B\u4EBA\u5996\u4E0D\u4EC5\u5E2E\u5979\u7ED9\u68B5\u96EA\u828D\u7834\u4F53\u6388\u80CE\uFF0C\u800C\u4E14\u8FD8\u9B3C\u9B3C\u795F\u795F\u628A\u5979\u9001\u51FA\u661F\u6708\u6E56\uFF0C\u8FD9\u4E00\u5207\u90FD\u4FBF\u5B9C\u4E86\u5979\u8FD9\u4E2A\u8EB2\u5728\u80CC\u540E\u7684\u9EC4\u96C0\u3002</p><p>\u5236\u4F4F\u68B5\u96EA\u828D\u540E\uFF0C\u8273\u51E4\u7ACB\u5373\u5C01\u95ED\u4E86\u5979\u7684\u611F\u8BC6\uFF0C\u622A\u65AD\u4E86\u5979\u7684\u56DB\u80A2\uFF0C\u4F9D\u7167\u53F6\u884C\u5357\u7684\u6307\u70B9\u9010\u6B65\u6539\u6362\u4E86\u5979\u7684\u7ECF\u8109\u3002\u6B64\u65F6\u820D\u5229\u4E4B\u4F53\u5373\u6210\uFF0C\u624D\u5524\u9192\u4E86\u6C89\u7761\u8FBE\u767E\u65E5\u4E4B\u4E45\u7684\u9999\u836F\u5929\u5973\u3002</p><p>\u6307\u5C16\u5728\u9888\u4E0B\u4E00\u70B9\uFF0C\u771F\u6C14\u900F\u5165\u4F53\u5185\uFF0C\u8FD9\u672C\u6765\u662F\u5236\u4F4F\u54D1\u7A74\u7684\u5E73\u5E38\u624B\u6CD5\uFF0C\u4F46\u5728\u68B5\u96EA\u828D\u8EAB\u4E0A\u5374\u8D77\u4E86\u622A\u7136\u76F8\u53CD\u7684\u6548\u679C\u3002</p><p>\u300C\u5440\u2014\u2014\u300D\u60CA\u53EB\u58F0\u51B2\u5589\u800C\u51FA\uFF0C\u7A81\u5982\u5176\u6765\u7684\u58F0\u97F3\uFF0C\u628A\u68B5\u96EA\u828D\u81EA\u5DF1\u4E5F\u5413\u4F4F\u4E86\u3002</p><p>\u68B5\u96EA\u828D\u6014\u4E86\u4E00\u4E0B\uFF0C\u98A4\u58F0\u95EE\u9053\uFF1A\u300C\u4F60\u5728\u6211\u8EAB\u4E0A\u505A\u4E86\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u4F60\u731C\u2026\u2026\u300D\u8273\u51E4\u7B11\u541F\u541F\u7231\u629A\u7740\u5979\u7684\u5C0F\u8179\u3002</p><p>\u4ECE\u5979\u624B\u638C\u7684\u52A8\u4F5C\uFF0C\u68B5\u96EA\u828D\u89C9\u5BDF\u5230\u81EA\u5DF1\u8EAB\u4E0A\u4E00\u4E1D\u4E0D\u6302\uFF0C\u66F4\u7F9E\u4EBA\u7684\u662F\u5C0F\u8179\u7684\u5F27\u7EBF\u3002\u5706\u5706\u7684\uFF0C\u5149\u6ED1\u7684\u9686\u8D77\u2014\u2014\u90A3\u662F\u4E00\u53EA\u598A\u5A20\u7684\u5C0F\u8179\uFF0C\u81EA\u5DF1\u6B63\u9732\u7740\u6000\u5B55\u7684\u8179\u90E8\u88AB\u4EBA\u73A9\u5F04\u2026\u2026</p><p>\u7F9E\u803B\u4E4B\u9645\uFF0C\u68B5\u96EA\u828D\u5FFD\u7136\u610F\u8BC6\u5230\u8179\u90E8\u7684\u66F2\u7EBF\u8FC7\u4E8E\u7A81\u51FA\uFF0C\u5979\u8BB0\u5F97\u81EA\u5DF1\u521A\u521A\u53D7\u80CE\uFF0C\u53EF\u8179\u7403\u5374\u50CF\u2026\u2026\u5979\u4E60\u60EF\u6027\u5730\u53BB\u5207\u81EA\u5DF1\u7684\u8109\u76F8\uFF0C\u7A76\u7ADF\u662F\u4E09\u4E2A\u6708\u8FD8\u662F\u56DB\u4E2A\u6708\uFF0C\u662F\u7537\u662F\u5973\uFF0C\u89E6\u624B\u5C31\u80FD\u77E5\u6653\u3002</p><p>\u300C\u554A\uFF01\u6211\u7684\u624B\uFF01\u300D\u68B5\u96EA\u828D\u5C16\u53EB\u7740\uFF0C\u773C\u7403\u62DA\u547D\u8F6C\u52A8\uFF0C\u5374\u65E0\u6CD5\u7741\u5F00\u3002\u5979\u66FE\u7ECF\u6709\u4E00\u53CC\u7075\u5DE7\u65E0\u6BD4\u7684\u7389\u624B\uFF0C\u5047\u5982\u628A\u5929\u4E0B\u6240\u6709\u4EBA\u7684\u624B\u90FD\u6392\u5217\u4E0B\u6765\uFF0C\u68B5\u96EA\u828D\u90A3\u53CC\u582A\u4E0E\u795E\u4ED9\u5AB2\u7F8E\u7684\u5999\u624B\uFF0C\u5373\u4F7F\u4E0D\u6392\u7B2C\u4E00\uFF0C\u4E5F\u7EDD\u5BF9\u5728\u524D\u4E94\u540D\u4E4B\u5185\u3002\u53EF\u73B0\u5728\u81EA\u5DF1\u7ADF\u7136\u5931\u53BB\u4E86\u5B83\u3002</p><p>\u68B5\u96EA\u828D\u5FC3\u75BC\u5F97\u50CF\u8981\u88C2\u5F00\u4E00\u822C\uFF0C\u5979\u5927\u53E3\u5927\u53E3\u5598\u7740\u6C14\uFF0C\u6CEA\u6C34\u6EDA\u6EDA\u800C\u843D\u3002\u7A76\u7ADF\u662F\u8C01\u593A\u8D70\u4E86\u81EA\u5DF1\u7684\u624B\uFF0C\u6B8B\u5FCD\u5730\u6BC1\u6389\u4E86\u81EA\u5DF1\uFF1F</p><p>\u8273\u51E4\u5F97\u610F\u5730\u6B23\u8D4F\u7740\u5979\u5F92\u52B3\u7684\u6323\u624E\uFF0C\u624B\u638C\u4ECE\u5C0F\u8179\u5230\u808B\u4E0B\u5728\u5979\u4F53\u4FA7\u7F13\u7F13\u6E38\u8D70\uFF0C\u8D34\u7740\u5149\u6ED1\u7684\u808C\u80A4\u7545\u901A\u65E0\u963B\u5730\u6478\u5230\u9888\u4FA7\u3002\u5F53\u624B\u638C\u63A0\u8FC7\u80A9\u5934\uFF0C\u68B5\u96EA\u828D\u7A81\u7136\u6C89\u9ED8\u4E0B\u6765\uFF0C\u63A5\u7740\u776B\u6BDB\u4E0B\u6C81\u51FA\u51E0\u6EF4\u6676\u83B9\u7684\u6CEA\u82B1\uFF0C\u5979\u7EC8\u4E8E\u660E\u767D\u81EA\u5DF1\u7684\u8EAB\u4F53\u5C11\u4E86\u4EC0\u4E48\u3002</p><p>\u8273\u51E4\u629A\u6478\u7740\u8D5E\u53F9\u9053\uFF1A\u300C\u4F60\u7684\u8FE6\u7F57\u771F\u6C14\u771F\u662F\u795E\u5999\uFF0C\u4F24\u53E3\u6062\u590D\u5F97\u8FD9\u4E48\u597D\uFF0C\u5149\u6ED1\u5F97\u7B80\u76F4\u5C31\u50CF\u6CA1\u957F\u8FC7\u624B\u4E00\u6837\u2026\u2026\u300D</p><p>\u68B5\u96EA\u828D\u65E0\u58F0\u5730\u6DCC\u7740\u773C\u6CEA\uFF0C\u5A07\u7EA2\u7684\u4E73\u5C16\u5728\u54FD\u54BD\u4E2D\u4E0D\u4F4F\u98A4\u52A8\u3002</p><p>\u8273\u51E4\u67D4\u58F0\u5475\u54C4\u9053\uFF1A\u300C\u6CA1\u5173\u7CFB\uFF0C\u6211\u4F1A\u7167\u987E\u4F60\u7684\uFF0C\u5E2E\u4F60\u6D17\u6D74\u3001\u996E\u98DF\u2026\u2026\u8FD8\u6709\u6392\u4FBF\u3002\u300D\u90A3\u53EA\u624B\u7A81\u7136\u6309\u5728\u8179\u4E0B\uFF0C\u6307\u5C16\u63A2\u5165\u79D8\u5904\uFF0C\u5728\u654F\u611F\u7684\u5AE9\u8089\u4E0A\u4E00\u6345\u3002</p><p>\u300C\u554A\uFF01\u300D\u68B5\u96EA\u828D\u8FDE\u5FD9\u5408\u7D27\u53CC\u817F\u3002\u4F46\u4E0B\u80A2\u5374\u7A7A\u8361\u8361\u7684\uFF0C\u6CA1\u6709\u4EFB\u4F55\u80A2\u4F53\u53EF\u4EE5\u963B\u6321\u5979\u7684\u4FB5\u5165\u3002\u6050\u60E7\u4E0E\u7F9E\u803B\u7ADE\u76F8\u6251\u6765\uFF0C\u68B5\u96EA\u828D\u7389\u8138\u65F6\u7EA2\u65F6\u767D\uFF0C\u6CEA\u6C34\u6D9F\u6D9F\u3002</p><p>\u8273\u51E4\u683C\u683C\u5A07\u7B11\u9053\uFF1A\u300C\u96EA\u828D\u5BB3\u7F9E\u4E86\u5462\u3002\u300D\u5979\u5C06\u68B5\u96EA\u828D\u62B1\u5728\u6000\u4E2D\uFF0C\u5750\u5728\u77F3\u51E0\u4E0A\uFF0C\u7528\u80F8\u4E73\u78E8\u64E6\u7740\u5979\u7684\u7C89\u80CC\uFF0C\u67D4\u60C5\u6B3E\u6B3E\u5730\u8BF4\uFF1A\u300C\u8FD9\u6837\u591A\u597D\u554A\uFF0C\u8EAB\u5B50\u8F7B\u4E86\u597D\u591A\u3002\u597D\u53EF\u7231\u5462\u2026\u2026\u300D</p><p>\u68B5\u96EA\u828D\u7684\u4E73\u623F\u672C\u5C31\u4E30\u6DA6\u80A5\u7855\uFF0C\u6B64\u65F6\u6C81\u4E86\u4E73\uFF0C\u6108\u53D1\u9971\u6EE1\u6C89\u91CD\uFF0C\u4E0E\u8273\u51E4\u50B2\u4EBA\u7684\u53CC\u5CF0\u5DEE\u582A\u5F77\u4F5B\u3002\u8273\u51E4\u4E0B\u5DF4\u52FE\u7740\u68B5\u96EA\u828D\u7684\u80A9\u5934\uFF0C\u50CF\u5BA1\u89C6\u81EA\u5DF1\u8EAB\u4F53\u90A3\u6837\u5BA1\u89C6\u7740\u5979\u7684\u7389\u4F53\u3002\u900F\u8FC7\u4E73\u5CF0\u4E2D\u7684\u817B\u6C9F\uFF0C\u80FD\u770B\u5230\u4E00\u62B9\u767D\u817B\u7684\u9686\u8D77\u3002\u8273\u51E4\u7389\u4F53\u7A81\u7136\u53D8\u5F97\u70BD\u70ED\uFF0C\u5979\u8F7B\u8F7B\u629A\u6478\u7740\u90A3\u53EA\u6000\u5B55\u7684\u5C0F\u8179\uFF0C\u773C\u4E2D\u6D41\u9732\u51FA\u4E07\u822C\u601C\u7231\u3002\u604D\u60DA\u4E2D\uFF0C\u4E24\u5177\u8EAB\u4F53\u4F3C\u4E4E\u5408\u4E8C\u4E3A\u4E00\uFF0C\u5C31\u50CF\u4E00\u4E2A\u521A\u521A\u6000\u5B55\u7684\u7F8E\u5987\uFF0C\u5728\u65E0\u4EBA\u5904\u72EC\u81EA\u629A\u6478\u7740\u81EA\u5DF1\u7684\u5C0F\u8179\uFF0C\u6B23\u559C\u4E2D\u8FD8\u6709\u751C\u871C\u7684\u5E0C\u5180\u3002</p><p>\u8273\u51E4\u4ECE\u604D\u60DA\u4E2D\u60CA\u9192\u8FC7\u6765\uFF0C\u6000\u91CC\u7684\u8089\u6BB5\u4E00\u8FB9\u6D41\u6CEA\uFF0C\u4E00\u8FB9\u6323\u624E\uFF0C\u5374\u6CA1\u6709\u4EFB\u4F55\u58F0\u97F3\u3002\u8273\u51E4\u5FC3\u4E0B\u6697\u6697\u8D5E\u4F69\uFF0C\u5E73\u5E38\u5973\u5B50\u5230\u4E86\u8FD9\u4E2A\u5730\u6B65\u7EB5\u7136\u4E0D\u75AF\u4E5F\u4F1A\u6B62\u4E0D\u4F4F\u5730\u5927\u558A\u5927\u53EB\uFF0C\u68B5\u96EA\u828D\u7ADF\u7136\u8FDE\u54ED\u58F0\u90FD\u538B\u6291\u4F4F\uFF0C\u8FD9\u4EFD\u4FEE\u4E3A\u679C\u7136\u4E0D\u4FD7\u3002</p><p>\u5979\u62B1\u8D77\u68B5\u96EA\u828D\uFF0C\u7B11\u9053\uFF1A\u300C\u8FD8\u6709\u4E24\u9053\u70B9\u5FC3\u6CA1\u5403\u5462\u3002\u6000\u7740\u8EAB\u5B55\u8981\u6CE8\u610F\u996E\u98DF\uFF0C\u6211\u7ED9\u4F60\u51C6\u5907\u7684\u53EF\u662F\u73CD\u8D35\u7684\u7AE5\u5B50\u7CBE\u5462\u3002\u300D</p><p>\u773C\u775B\u5FFD\u7136\u5F20\u5F00\uFF0C\u5149\u7EBF\u900F\u8FC7\u776B\u6BDB\u4E0A\u7684\u6CEA\u82B1\uFF0C\u95EA\u70C1\u7740\u4E03\u5F69\u7684\u5149\u8292\u3002\u6EE1\u773C\u90FD\u662F\u767D\u8272\uFF0C\u5F2F\u66F2\u6210\u5947\u5F02\u5F62\u72B6\u7684\u623F\u95F4\u7531\u7EAF\u767D\u7684\u77F3\u5757\u780C\u6210\uFF0C\u4F4E\u5782\u7684\u5E37\u5E10\u662F\u4E91\u4E00\u822C\u7684\u767D\u7EB1\uFF0C\u53EA\u6709\u4E00\u5F20\u6D51\u5706\u7684\u77F3\u51E0\uFF0C\u9ED1\u5F97\u5F77\u4F5B\u4E00\u53E3\u67AF\u4E95\u3002</p><p>\u84C4\u4E86\u9752\u4E1D\u7684\u96EA\u5CF0\u795E\u5C3C\u5B9B\u5982\u6362\u4E86\u4E00\u4E2A\u4EBA\u822C\uFF0C\u5E73\u6DFB\u4E86\u8BB8\u591A\u5996\u5A06\u7684\u795E\u6001\u3002\u5979\u53F3\u624B\u7275\u7740\u4E00\u4E2A\u4FCA\u79C0\u7684\u5C0F\u7537\u5B69\uFF0C\u8EAB\u4E0A\u4E0D\u7740\u5BF8\u7F15\uFF0C\u9732\u7740\u767D\u5149\u5149\u7684\u7F8E\u8089\uFF0C\u9A9A\u5A9A\u5165\u9AA8\u3002\u90A3\u7537\u5B69\u770B\u4E0A\u53BB\u53EA\u6709\u516D\u5C81\uFF0C\u5E72\u51C0\u5F97\u5C31\u50CF\u4E00\u5E45\u6C34\u58A8\u753B\uFF0C\u4ED6\u7EAF\u51C0\u7684\u76EE\u5149\u597D\u5947\u5730\u671B\u8FC7\u6765\uFF0C\u4F7F\u68B5\u96EA\u828D\u7F9E\u60ED\u5F97\u4E0D\u6562\u62AC\u5934\u3002</p><p>\u8273\u51E4\u8BA9\u5C0F\u7537\u5B69\u5750\u5728\u77F3\u51E0\u4E0A\uFF0C\u7136\u540E\u6402\u7740\u68B5\u96EA\u828D\u4FEF\u4E0B\u8EAB\u53BB\uFF0C\u628A\u7537\u5B69\u8FD8\u672A\u53D1\u80B2\u7684\u5C0F\u9E21\u9E21\u542B\u5728\u5634\u91CC\u3002\u7537\u5B69\u7B11\u563B\u563B\u6643\u7740\u5C0F\u817F\uFF0C\u4F3C\u4E4E\u88AB\u963F\u59E8\u8214\u5F97\u5F88\u5F00\u5FC3\u3002</p><p>\u68B5\u96EA\u828D\u88AB\u8273\u51E4\u538B\u5728\u8EAB\u4E0B\uFF0C\u542C\u7740\u8033\u8FB9\u5438\u542E\u7684\u557E\u557E\u58F0\uFF0C\u4E0D\u7531\u9762\u7EA2\u8033\u8D64\u3002\u6700\u521D\u7684\u60CA\u60B8\u8FC7\u53BB\u4E4B\u540E\uFF0C\u5979\u5DF2\u7ECF\u660E\u767D\u8273\u51E4\u65AD\u7EDD\u4E86\u81EA\u5DF1\u6240\u6709\u53EF\u80FD\u7684\u673A\u4F1A\uFF0C\u65E0\u8BBA\u662F\u6323\u624E\u8FD8\u662F\u53CD\u6297\uFF0C\u90FD\u6BEB\u65E0\u610F\u4E49\u3002\u5979\u4E0D\u660E\u767D\u7684\u53EA\u662F\uFF1A\u8273\u51E4\u4E3A\u4F55\u8981\u5BF9\u5F85\u81EA\u5DF1\u3002</p><p>\u300C\u4ED6\u8FD8\u53EA\u662F\u4E2A\u5B69\u5B50\u2026\u2026\u300D</p><p>\u8273\u51E4\u5410\u51FA\u6E7F\u6F09\u6F09\u7684\u5C0F\u8089\u68D2\uFF0C\u7B11\u9053\uFF1A\u300C\u8FD9\u6837\u7684\u7AE5\u5B50\u7CBE\u624D\u7CBE\u7EAF\uFF0C\u4E0D\u7136\u4F60\u600E\u4E48\u80FD\u628A\u5B69\u5B50\u517B\u8FD9\u4E48\u597D\uFF1F\u300D</p><p>\u539F\u6765\u8FD9\u6BB5\u65E5\u5B50\u81EA\u5DF1\u4E00\u76F4\u662F\u9760\u7537\u7AE5\u7684\u7CBE\u6DB2\u4E3A\u751F\u2014\u2014\u68B5\u96EA\u828D\u53C8\u5E72\u5455\u8D77\u6765\u3002\u4F46\u5979\u80C3\u4E2D\u65E9\u5DF2\u7A7A\u7A7A\u5982\u91CE\uFF0C\u7CBE\u6DB2\u5165\u5589\u4FBF\u88AB\u5438\u6536\uFF0C\u4EC0\u4E48\u90FD\u672A\u5455\u51FA\u6765\u3002</p><p>\u8273\u51E4\u5927\u529B\u542E\u5438\u51E0\u4E0B\uFF0C\u7136\u540E\u5C06\u6CBE\u7740\u553E\u6DB2\u7684\u5C0F\u8089\u68D2\u9012\u5230\u68B5\u96EA\u828D\u5507\u8FB9\uFF0C\u7B11\u9053\uFF1A\u300C\u65B0\u9C9C\u7684\u7AE5\u5B50\u7CBE\uFF0C\u6700\u8865\u8EAB\u5B50\u5462\u3002\u300D</p><p>\u68B5\u96EA\u828D\u5455\u5410\u672A\u6B62\uFF0C\u4FBF\u88AB\u8273\u51E4\u634F\u5F00\u7259\u5173\uFF0C\u628A\u7537\u7AE5\u52C3\u8D77\u7684\u5C0F\u8089\u68D2\u585E\u5230\u53E3\u4E2D\u3002\u300C\u4E0D\u2026\u2026\u300D\u68B5\u96EA\u828D\u5403\u529B\u5730\u6447\u6643\u7740\u9999\u820C\u3002\u7ADF\u7136\u8BA9\u4E00\u4E2A\u516D\u5C81\u7684\u7537\u5B69\u628A\u7CBE\u6DB2\u5C04\u5230\u5634\u91CC\uFF0C\u53EA\u60F3\u4E00\u60F3\u5979\u5C31\u7F9E\u5FFF\u6B32\u7EDD\u3002</p><p>\u4F46\u5979\u6CA1\u6709\u9009\u62E9\u3002\u90A3\u6839\u5C0F\u8089\u68D2\u5C31\u5728\u5979\u5507\u74E3\u95F4\u8DF3\u52A8\u7740\u55B7\u5C04\u8D77\u6765\uFF0C\u6E29\u70ED\u7684\u6DB2\u4F53\u55B7\u6E85\u5728\u53E3\u8154\u4E2D\uFF0C\u4E00\u7F15\u7F15\u6ED1\u843D\uFF0C\u5C06\u9999\u820C\u6D78\u5728\u4E00\u7247\u9ECF\u6ED1\u7684\u8165\u751C\u4E2D\u3002</p><p>\u68B5\u96EA\u828D\u7F8E\u76EE\u542B\u6CEA\uFF0C\u773C\u7741\u7741\u770B\u7740\u8273\u51E4\u62FF\u8D77\u5C0F\u8089\u68D2\uFF0C\u5728\u5979\u67D4\u8F6F\u7684\u5507\u74E3\u4E0A\u4ED4\u7EC6\u63E9\u62ED\uFF0C\u5C06\u7AE5\u6839\u4E0A\u7684\u6B8B\u7CBE\u4E00\u4E00\u62B9\u5165\u53E3\u4E2D\u3002</p><p>\u300C\u5F88\u597D\u5403\u7684\u554A\u3002\u300D\u8273\u51E4\u5C06\u5C04\u8FC7\u7CBE\u7684\u5C0F\u8089\u68D2\u653E\u5728\u5634\u91CC\uFF0C\u6D25\u6D25\u6709\u5473\u5730\u8214\u8210\u7740\uFF0C\u534A\u664C\u624D\u5410\u51FA\u6765\uFF0C\u5AE3\u7136\u7B11\u9053\uFF1A\u300C\u8FD8\u6709\u4E00\u4E2A\u5462\u3002\u300D</p><p>\u4E24\u4E2A\u7528\u8FC7\u7684\u7537\u7AE5\u88AB\u9001\u51FA\u5BC6\u5BA4\uFF0C\u7B49\u5F85\u4E09\u65E5\u540E\u518D\u6B21\u4F7F\u7528\u3002\u5269\u4E0B\u7684\u4E00\u4E2A\u89C1\u540C\u4F34\u90FD\u5DF2\u79BB\u5F00\uFF0C\u4E0D\u7981\u6709\u4E9B\u5BB3\u6015\uFF0C\u600E\u4E48\u4E5F\u4E0D\u613F\u4E56\u4E56\u5750\u7740\u8BA9\u963F\u59E8\u4EB2\u4ED6\u7684\u5C0F\u9E21\u9E21\u3002\u8273\u51E4\u54C4\u4E86\u534A\u664C\u4E0D\u89C1\u6548\u679C\uFF0C\u8138\u8272\u987F\u65F6\u53D8\u5F97\u72DE\u5389\u3002\u5979\u628A\u90A3\u4E2A\u7537\u7AE5\u6309\u5728\u51E0\u4E0A\uFF0C\u5F20\u53E3\u54AC\u4F4F\u4ED6\u7684\u7AE5\u6839\uFF0C\u9F13\u52B2\u4E00\u5438\uFF0C\u786C\u751F\u751F\u5C06\u4ED6\u7684\u5143\u7CBE\u6574\u4E2A\u5438\u51FA\u3002</p><p>\u7537\u5B69\u75BC\u5F97\u5927\u58F0\u54ED\u53EB\uFF0C\u5374\u88AB\u8273\u51E4\u6309\u4F4F\u52A8\u5F39\u4E0D\u5F97\u3002\u8273\u51E4\u4E00\u624B\u634F\u7740\u68B5\u96EA\u828D\u7684\u4E0B\u5DF4\uFF0C\u4E00\u624B\u6258\u7740\u7537\u5B69\u7C89\u5AE9\u7684\u5C0F\u5C41\u80A1\uFF0C\u53EA\u89C1\u767D\u8272\u7684\u5143\u7CBE\u4ECE\u7A1A\u5AE9\u7684\u5C0F\u9E21\u9E21\u4E2D\u4E0D\u65AD\u6D8C\u51FA\uFF0C\u6D41\u5230\u4E0B\u65B9\u5A07\u8273\u7684\u7EA2\u5507\u4E2D\u3002</p><p>\u7AE5\u7CBE\u8D8A\u6D41\u8D8A\u591A\uFF0C\u51E0\u4E4E\u704C\u6EE1\u4E86\u68B5\u96EA\u828D\u7684\u53E3\u8154\uFF0C\u5979\u88AB\u8FEB\u4F38\u76F4\u5589\u5499\uFF0C\u4EFB\u7531\u7AE5\u7CBE\u6ED1\u8FC7\u98DF\u9053\uFF0C\u6D41\u5165\u8179\u5185\u3002\u5FFD\u7136\u7CBE\u6DB2\u4E00\u6DE1\uFF0C\u8F6C\u773C\u53D8\u5F97\u8840\u7EA2\u3002\u68B5\u96EA\u828D\u60CA\u6050\u5730\u77AA\u5927\u773C\u775B\uFF0C\u90A3\u80A1\u8840\u6CC9\u6E85\u5728\u5507\u4E0A\uFF0C\u5C31\u50CF\u70E7\u7EA2\u7684\u94C1\u6C34\u70EB\u5F97\u5979\u5FC3\u5934\u62BD\u75DB\u3002</p><p>\u54ED\u53EB\u58F0\u6E10\u6E10\u5FAE\u5F31\uFF0C\u7CBE\u5C3D\u8840\u6D41\u7684\u7537\u7AE5\u6323\u52A8\u8D8A\u6765\u8D8A\u8F7B\uFF0C\u6700\u540E\u6253\u4E86\u4E2A\u54C6\u55E6\uFF0C\u8EAB\u5B50\u9759\u6B62\u4E0B\u6765\u3002\u8273\u51E4\u64A9\u8D77\u5E37\u5E55\uFF0C\u4E00\u80A1\u6D53\u91CD\u7684\u8840\u8165\u7ACB\u523B\u91CD\u91CD\u538B\u6765\u3002\u5979\u6293\u8D77\u90A3\u4E2A\u7537\u7AE5\uFF0C\u968F\u624B\u4E22\u5165\u5E37\u540E\uFF0C\u53EA\u542C\u556A\u7684\u4E00\u58F0\uFF0C\u4F3C\u4E4E\u6254\u5728\u4E86\u4E00\u6EE9\u8089\u6CE5\u4E0A\u3002\u63A5\u7740\u4E00\u9635\u866B\u8C56\u7684\u5F02\u52A8\u54CD\u8D77\uFF0C\u5E26\u7740\u4EE4\u4EBA\u6BDB\u9AA8\u609A\u7136\u7684\u53FD\u53FD\u58F0\u2026\u2026</p><p>\u8273\u51E4\u82E5\u65E0\u5176\u4E8B\u5730\u653E\u4E0B\u5E37\u5E55\uFF0C\u7B11\u9053\uFF1A\u300C\u4F60\u5403\u5B8C\u70B9\u5FC3\uFF0C\u8FD9\u4F1A\u513F\u8BE5\u6211\u5403\u4E86\u5462\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6E56\u4E0A\u7684\u98CE\u5F88\u5927\uFF0C\u5145\u6EE1\u8083\u6740\u610F\u5473\u7684\u79CB\u98CE\u4ECE\u5C71\u5CE6\u7684\u7F3A\u53E3\u6CFB\u5165\uFF0C\u5C06\u4E24\u4FA7\u7684\u5C71\u6797\u5439\u62C2\u5F97\u4E00\u7247\u91D1\u9EC4\uFF0C\u4F46\u79CB\u98CE\u672A\u53CA\u5904\u4ECD\u662F\u8471\u830F\u6EE1\u76EE\uFF0C\u4E00\u5C42\u5C42\u8272\u5F69\u9C9C\u660E\u3002\u79CB\u9AD8\u4E91\u6DE1\uFF0C\u5B81\u9759\u7684\u661F\u6708\u6E56\u5728\u9633\u5149\u4E0B\u6CDB\u8D77\u7CBC\u7CBC\u7EC6\u6CE2\uFF0C\u5F77\u4F5B\u4E00\u5E45\u5439\u7EC9\u7684\u78A7\u6BEF\u95EA\u70C1\u7740\u5B9D\u84DD\u7684\u5149\u8292\u3002</p><p>\u8427\u4F5B\u5974\u501A\u7740\u6447\u7BEE\u5B89\u7136\u5750\u4E0B\uFF0C\u6674\u96EA\u6015\u5979\u4F53\u5F31\u53D7\u5BD2\uFF0C\u7279\u610F\u62FF\u4E86\u4E00\u9886\u72D0\u88D8\u7ED9\u5979\u62AB\u4E0A\u3002\u76DB\u88C5\u63A9\u6620\u4E0B\u7684\u7F8E\u5987\u6108\u53D1\u96CD\u5BB9\u534E\u8D35\uFF0C\u5076\u5C14\u5357\u98DE\u7684\u7FA4\u96C1\u5212\u8FC7\u957F\u7A7A\uFF0C\u5979\u90FD\u4F1A\u50CF\u5C0F\u5973\u5B69\u90A3\u6837\u6EE1\u773C\u6B23\u559C\u5730\u9065\u671B\u534A\u664C\uFF0C\u53EA\u662F\u90A3\u6B23\u559C\u80CC\u540E\u63A9\u85CF\u7740\u65E0\u9650\u7684\u51C4\u51C9\u3002</p><p>\u7D2B\u73AB\u4E5F\u5750\u4E86\u8D77\u6765\uFF0C\u8BF4\u9053\uFF1A\u300C\u7B2C\u4E00\u6B21\u770B\u5230\u661F\u6708\u6E56\uFF0C\u89C9\u5F97\u8FD9\u6E56\u597D\u5C0F\uFF0C\u5C31\u50CF\u6389\u5728\u5C71\u91CC\u7684\u4E00\u5757\u7389\u4F69\uFF0C\u4F38\u624B\u5C31\u80FD\u62FF\u8D77\u6765\uFF0C\u8D70\u8FD1\u4E86\u624D\u77E5\u9053\u5B83\u5F88\u5927\uFF0C\u8D70\u8FDB\u6765\u624D\u77E5\u9053\u5B83\u6BD4\u60F3\u50CF\u4E2D\u8FD8\u5927\u3002\u300D\u5979\u5E7D\u5E7D\u53F9\u9053\uFF1A\u300C\u4E00\u65E6\u8D70\u8FDB\u6765\uFF0C\u4E00\u8F88\u5B50\u90FD\u8D70\u4E0D\u51FA\u53BB\u4E86\u2026\u2026\u300D</p><p>\u9759\u989C\u7B11\u9053\uFF1A\u300C\u5A18\u5A18\u60F3\u51FA\u53BB\uFF0C\u5974\u5A62\u9001\u4F60\u597D\u4E86\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u60CA\u8BB6\u5730\u779F\u4E86\u5979\u4E00\u773C\uFF0C\u4E0D\u77E5\u9053\u8FD9\u4E2A\u4E56\u5DE7\u7684\u5A62\u5973\u4ECA\u5929\u600E\u4E48\u5982\u6B64\u5484\u5484\u903C\u4EBA\u3002</p><p>\u9759\u989C\u5B9E\u662F\u8FEB\u4E0D\u5F97\u5DF2\uFF0C\u5979\u6697\u81EA\u4F30\u7B97\uFF0C\u6155\u5BB9\u9F99\u5165\u51AC\u4FBF\u4F1A\u6D3E\u4EBA\u63A5\u7D2B\u73AB\u7B49\u4EBA\u56DE\u4EAC\uFF0C\u5C4A\u65F6\u5979\u65E0\u8BBA\u5982\u4F55\u4E5F\u8981\u968F\u884C\uFF0C\u6240\u4F59\u65F6\u95F4\u5DF2\u7ECF\u4E0D\u591A\u3002\u56E0\u6B64\u5979\u65C1\u6572\u4FA7\u51FB\uFF0C\u53EA\u76FC\u7D2B\u73AB\u80FD\u5012\u5411\u81EA\u5DF1\u4E00\u8FB9\uFF0C\u5230\u65F6\u4FBF\u53EF\u901A\u8FC7\u7D2B\u73AB\u7B97\u8BA1\u6155\u5BB9\u9F99\uFF0C\u597D\u62A5\u4EC7\u96EA\u6068\u3002</p><p>\u6674\u96EA\u5FFD\u7136\u6307\u7740\u6C34\u9762\u4E0A\u4E00\u4E2A\u53D1\u4EAE\u7684\u7269\u4F53\uFF0C\u8BF4\u9053\uFF1A\u300C\u7EAA\u963F\u59E8\uFF0C\u90A3\u662F\u4EC0\u4E48\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u7EC6\u5FC3\u770B\u4E86\u4E00\u4F1A\u513F\uFF0C\u624D\u8BF4\u9053\uFF1A\u300C\u597D\u50CF\u662F\u4E00\u7247\u868C\u58F3\u3002\u300D</p><p>\u300C\u5662\uFF0C\u539F\u6765\u8FD9\u5C31\u662F\u9E6C\u868C\u76F8\u4E89\u7684\u868C\u4E86\u3002\u300D\u6674\u96EA\u968F\u624B\u62C8\u8D77\u4E00\u7247\u6D6E\u840D\uFF0C\u66F2\u6307\u5F39\u53BB\uFF0C\u9694\u7740\u6570\u4E08\u7684\u8DDD\u79BB\u7ADF\u5C06\u868C\u58F3\u51FB\u5F97\u7C89\u788E\u3002\u5979\u64A9\u6C34\u6D17\u7740\u624B\u6307\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u5B83\u5982\u679C\u61C2\u5F97\u4E0D\u5F00\u53E3\u5C31\u597D\u4E86\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u67D4\u67D4\u7B11\u9053\uFF1A\u300C\u516C\u4E3B\u7684\u5185\u529B\u53C8\u6709\u7CBE\u8FDB\u4E86\u5462\u3002\u300D</p><p>\u9759\u989C\u5FC3\u5934\u4E00\u60CA\uFF0C\u62AC\u773C\u671D\u7D2B\u73AB\u671B\u53BB\uFF0C\u6B63\u770B\u5230\u5979\u660E\u5982\u79CB\u6C34\u7684\u7738\u5B50\u3002\u7D2B\u73AB\u5927\u6709\u6DF1\u610F\u5730\u76EF\u4E86\u5979\u4E00\u773C\uFF0C\u7136\u540E\u8F6C\u8FC7\u76EE\u5149\uFF0C\u300C\u6211\u7D2F\u4E86\uFF0C\u6674\u6674\uFF0C\u628A\u4F1E\u5F20\u5F00\u5427\u3002\u300D</p><p>\u6241\u821F\u8D8A\u8361\u8D8A\u8FDC\uFF0C\u6E56\u9762\u6E10\u6E10\u6536\u62E2\uFF0C\u5728\u5C71\u811A\u8F7B\u8F7B\u4E00\u7ED5\uFF0C\u5F62\u6210\u4E00\u4E2A\u5E73\u9759\u7684\u6E56\u6E7E\u3002\u6E7E\u65C1\u5C71\u77F3\u5D99\u5CCB\uFF0C\u9752\u85E4\u7FE0\u53F6\u8499\u7EDC\u6447\u7F00\uFF0C\u53C2\u5DEE\u62AB\u62C2\uFF0C\u5F77\u4F5B\u4E00\u7247\u7247\u7EFF\u4E91\u6D6E\u5728\u6C34\u4E0A\u3002</p><p>\u4F17\u4EBA\u79FB\u821F\u5C31\u5CB8\uFF0C\u6491\u7740\u7EA2\u4F1E\u7684\u5C0F\u8239\u5728\u7EFF\u53F6\u4E2D\u60A0\u7136\u5DDD\u884C\uFF0C\u8239\u4E0A\u7684\u5973\u5B50\u6216\u5750\u6216\u5367\uFF0C\u5B9B\u5982\u8F7D\u7740\u4E00\u8239\u540D\u82B1\u3002\u5929\u5DF2\u8FC7\u4E86\u5348\u65F6\uFF0C\u6674\u96EA\u62E3\u4E86\u4E00\u5904\u5E72\u51C0\u7684\u89D2\u843D\uFF0C\u6296\u624B\u5C06\u8239\u7CFB\u5728\u6811\u4E0A\uFF0C\u7136\u540E\u6258\u8D77\u8427\u4F5B\u5974\uFF0C\u8F7B\u8F7B\u8DC3\u4E0A\u5DE8\u5CA9\u3002\u90A3\u5757\u5DE8\u5CA9\u53EA\u9AD8\u51FA\u6C34\u9762\u5C3A\u8BB8\uFF0C\u8272\u6CFD\u4E39\u7EA2\uFF0C\u751A\u662F\u5947\u5F02\u3002\u5468\u56F4\u6E56\u5C71\u63A9\u6620\uFF0C\u7EFF\u6811\u73AF\u56F4\uFF0C\u662F\u4E2A\u96BE\u5F97\u7684\u50FB\u9759\u5904\u3002</p><p>\u6674\u96EA\u51C6\u5907\u5F97\u751A\u662F\u5468\u5168\uFF0C\u821F\u4E0A\u8FD8\u5907\u4E86\u4E00\u53EA\u7EA2\u6CE5\u5C0F\u706B\u7089\u3002\u7EAA\u7709\u59A9\u633D\u8896\u751F\u7740\u70AD\u706B\uFF0C\u5979\u51FA\u8EAB\u8C6A\u95E8\uFF0C\u70F9\u8C03\u624B\u6BB5\u7740\u5B9E\u9AD8\u660E\uFF0C\u4E0D\u591A\u65F6\u4FBF\u505A\u4E86\u51E0\u6837\u7CBE\u81F4\u7684\u5C0F\u83DC\u3002</p><p>\u516D\u5973\u6709\u4E00\u534A\u90FD\u9700\u4EBA\u7167\u6599\uFF0C\u6674\u96EA\u672C\u60F3\u670D\u4F8D\u6BCD\u4EB2\uFF0C\u4F46\u770B\u5230\u9759\u989C\u9762\u9732\u5C34\u5C2C\uFF0C\u4FBF\u4E0D\u7740\u75D5\u8FF9\u5730\u5C06\u7897\u9012\u7ED9\u9759\u989C\uFF0C\u81EA\u5DF1\u53BB\u5582\u5916\u5A46\u3002\u8427\u4F5B\u5974\u4E00\u76F4\u4E0D\u77E5\u9053\u9759\u989C\u5F53\u65E5\u662F\u5982\u4F55\u5F3A\u66B4\u4E86\u5979\uFF0C\u4F46\u6BCF\u89C1\u5230\u8FD9\u4E2A\u5A07\u4FCF\u7684\u5973\u5B50\uFF0C\u5979\u5C31\u6709\u4E9B\u5FC3\u60B8\uFF0C\u5F85\u9759\u989C\u8D70\u5230\u4E00\u8FB9\uFF0C\u624D\u5077\u5077\u677E\u4E86\u53E3\u6C14\u3002</p><p>\u98CE\u665A\u534E\u7684\u795E\u667A\u88AB\u836F\u7269\u5F7B\u5E95\u6BC1\u53BB\uFF0C\u53C8\u66FE\u4E0E\u51E0\u6761\u5DE8\u72AC\u540C\u56DA\u4E00\u5BA4\u591A\u65F6\uFF0C\u884C\u52A8\u4E3E\u6B62\u90FD\u5DF2\u72AC\u5316\uFF0C\u7EAA\u7709\u59A9\u53EA\u597D\u5C0F\u5FC3\u5730\u5254\u53BB\u9C7C\u523A\uFF0C\u5C06\u83DC\u80B4\u62E8\u5728\u76D8\u4E2D\uFF0C\u8BA9\u5979\u81EA\u884C\u8214\u98DF\u3002</p><p>\u7D2B\u73AB\u968F\u610F\u7528\u4E86\u51E0\u53E3\uFF0C\u4FBF\u6447\u5934\u4E0D\u518D\u5403\u4E86\u3002\u5979\u501A\u5728\u7BEE\u7B50\u8FB9\u7F18\uFF0C\u51FA\u795E\u5730\u671B\u7740\u78A7\u7A7A\u98D8\u6D6E\u7684\u767D\u4E91\uFF0C\u4E45\u4E45\u6CA1\u6709\u4F5C\u58F0\u3002\u8427\u4F5B\u5974\u4E5F\u4F4F\u4E86\u53E3\uFF0C\u6084\u6084\u5728\u6674\u96EA\u8033\u8FB9\u8BF4\u4E86\u53E5\u4EC0\u4E48\u3002\u6674\u96EA\u6709\u4E9B\u5FF8\u6029\u5730\u653E\u4E0B\u76CF\u7897\uFF0C\u5BF9\u7EAA\u7709\u59A9\u8BF4\u9053\uFF1A\u300C\u7EAA\u963F\u59E8\uFF0C\u8BA9\u9759\u989C\u5E26\u4F60\u5230\u9644\u8FD1\u8D70\u8D70\u597D\u5417\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u4F1A\u8FC7\u610F\u6765\uFF0C\u8FDE\u5FD9\u542B\u7B11\u7B54\u5E94\u3002\u9759\u989C\u5FC3\u4E0B\u7EB3\u95F7\uFF0C\u4E5F\u53EA\u597D\u6276\u7740\u7EAA\u7709\u59A9\u79BB\u5F00\u3002</p><p>\u7B49\u4E24\u4EBA\u8D70\u8FDC\uFF0C\u6674\u96EA\u624D\u7B11\u7740\u89E3\u5F00\u8427\u4F5B\u5974\u7684\u8863\u895F\uFF0C\u9732\u51FA\u91CC\u9762\u6708\u767D\u8272\u7684\u4EB5\u8863\u3002\u4EB5\u8863\u4E0A\u5370\u7740\u4E24\u56E2\u6E7F\u6E7F\u7684\u75D5\u8FF9\uFF0C\u968F\u4E4B\u98D8\u6765\u4E00\u80A1\u6D53\u6D53\u7684\u4E73\u9999\u3002\u8427\u4F5B\u5974\u4E0E\u7D2B\u73AB\u4E00\u6837\uFF0C\u8FD9\u4E9B\u5E74\u6765\u90FD\u6CA1\u6709\u505C\u4E73\uFF0C\u56E0\u4E3A\u6155\u5BB9\u9F99\u6700\u559C\u6B22\u7684\u996E\u54C1\uFF0C\u5C31\u662F\u5979\u4EEC\u7684\u4E73\u6C41\u3002\u8427\u4F5B\u5974\u4E73\u6C41\u53C8\u591A\u53C8\u6D53\uFF0C\u6BCF\u9694\u56DB\u4E2A\u65F6\u8FB0\u5C31\u8981\u6392\u7A7A\u4E00\u6B21\uFF0C\u4ECA\u65E5\u8BEF\u4E86\u65F6\u8FB0\uFF0C\u4E73\u623F\u4E00\u76F4\u6DA8\u51FA\u5976\u6C34\uFF0C\u624D\u5FCD\u4E0D\u4F4F\u8BA9\u6674\u96EA\u652F\u5F00\u9759\u989C\u3002</p><p>\u62C9\u5F00\u4EB5\u8863\uFF0C\u90A3\u53CC\u9971\u6EE1\u7684\u4E30\u4E73\u6C89\u7538\u7538\u633A\u5728\u80F8\u524D\uFF0C\u50CF\u704C\u6EE1\u6C34\u4E00\u6837\u6C89\u91CD\u3002\u8D64\u88F8\u7684\u4E73\u8089\u88AB\u79CB\u98CE\u62C2\u8FC7\uFF0C\u7ACB\u523B\u7EF7\u7D27\uFF0C\u8273\u7EA2\u7684\u4E73\u5934\u968F\u4E4B\u6C81\u51FA\u4E00\u80A1\u767D\u7A20\u7684\u5976\u6C41\uFF0C\u6EE1\u6000\u6D53\u9999\u56DB\u6EA2\u3002\u6674\u96EA\u4F4E\u5934\u5728\u542B\u4F4F\u4E73\u5934\uFF0C\u8F7B\u8F7B\u4E00\u5438\uFF0C\u8427\u4F5B\u5974\u80C0\u75DB\u7684\u4E73\u623F\u8F7B\u677E\u4E0B\u6765\uFF0C\u4E0D\u7531\u8F7B\u8F7B\u54FC\u4E86\u4E00\u58F0\u3002</p><p>\u6674\u96EA\u8F6E\u6D41\u5438\u542E\u7740\u4E24\u53EA\u4E73\u623F\uFF0C\u534A\u664C\u53EA\u5438\u7A7A\u4E86\u4E00\u5C0F\u534A\uFF0C\u5979\u53EA\u597D\u5524\u6765\u98CE\u665A\u534E\uFF0C\u8BA9\u5979\u4E00\u5757\u513F\u6765\u5403\u3002\u98CE\u665A\u534E\u5DF2\u7ECF\u628A\u7F69\u886B\u5B8C\u5168\u6495\u788E\uFF0C\u4F0F\u5728\u8427\u4F5B\u5974\u6000\u4E2D\uFF0C\u4E0D\u65F6\u6447\u7740\u81C0\u90E8\uFF0C\u6D3B\u50CF\u4E00\u6761\u5403\u5976\u7684\u6BCD\u72D7\u3002\u5979\u5927\u53E3\u5927\u53E3\u5403\u5173\uFF0C\u4E73\u6C41\u4ECE\u5507\u95F4\u4E0D\u4F4F\u6EF4\u4E0B\uFF0C\u6DCB\u6DCB\u6F13\u6F13\u6D12\u5F97\u8427\u4F5B\u5974\u6EE1\u8EAB\u90FD\u662F\u3002\u6674\u96EA\u53EA\u597D\u638F\u51FA\u4E1D\u5DFE\uFF0C\u5728\u5979\u80F8\u4E0A\u4E0D\u505C\u62B9\u62ED\u3002\u5979\u56DE\u8FC7\u5934\uFF0C\u53EA\u89C1\u6BCD\u4EB2\u95ED\u7740\u773C\uFF0C\u5507\u89D2\u9732\u51FA\u4E00\u4E1D\u82E5\u6709\u82E5\u65E0\u7684\u7B11\u610F\u3002\u90A3\u7B11\u5BB9\u4F3C\u4E4E\u662F\u751C\u871C\uFF0C\u53C8\u4F3C\u4E4E\u662F\u51C4\u82E6\uFF0C\u8FD8\u4F3C\u4E4E\u662F\u65E0\u559C\u65E0\u5FE7\u7684\u6C89\u9759\u3002</p><p>\u7EAA\u7709\u59A9\u4F18\u96C5\u5730\u626D\u52A8\u8170\u80A2\uFF0C\u867D\u7136\u662F\u5728\u5C71\u6797\u4E2D\uFF0C\u5979\u5374\u50CF\u8D70\u5728\u4EAC\u57CE\u7684\u4E94\u51E4\u697C\u4E0A\u4E00\u6837\u4EEA\u6001\u4E07\u65B9\u3002\u5047\u5982\u8427\u4F5B\u5974\u548C\u7D2B\u73AB\u8FD8\u80FD\u884C\u8D70\uFF0C\u60F3\u6765\u8981\u6BD4\u5979\u66F4\u6447\u66F3\u591A\u59FF\uFF0C\u4F46\u6B64\u523B\uFF0C\u9759\u989C\u4E0D\u5F97\u4E0D\u627F\u8BA4\uFF0C\u6155\u5BB9\u9F99\u7684\u4E09\u4E2A\u5983\u5B50\u91CC\uFF0C\u53EA\u6709\u5979\u624D\u80FD\u50CF\u662F\u6BCD\u4EEA\u5929\u4E0B\u7684\u7687\u540E\u3002</p><p>\u7EAA\u7709\u59A9\u8D70\u5F97\u7D2F\u4E86\uFF0C\u5979\u505C\u4E0B\u811A\u6B65\uFF0C\u5148\u628A\u4E00\u5757\u4E1D\u5DFE\u94FA\u5728\u6811\u5E72\u7684\u6A2A\u679D\u4E0A\uFF0C\u624D\u501A\u5728\u4E0A\u9762\u6B47\u606F\u3002\u9759\u989C\u4E0D\u77E5\u8BE5\u8BF4\u4EC0\u4E48\u624D\u597D\uFF0C\u53EA\u597D\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u5A18\u5A18\u600E\u4E48\u6210\u4E86\u90A3\u4E2A\u6837\u5B50\uFF1F\u8BA9\u4EBA\u770B\u4E86\u597D\u96BE\u53D7\u2026\u2026\u300D</p><p>\u7EAA\u7709\u59A9\u5FAE\u5FAE\u7B11\u9053\uFF1A\u300C\u6614\u6709\u91CE\u72D0\u542C\u7985\uFF0C\u4E00\u5F92\u95EE\uFF1A\u5927\u5584\u667A\u58EB\u53EF\u843D\u56E0\u679C\uFF1F\u91CE\u72D0\u66F0\uFF1A\u4E0D\u843D\u56E0\u679C\u3002\u5C31\u6B64\u6CA6\u843D\u755C\u9053\u3002\u540E\u6709\u5927\u5FB7\u767B\u53F0\u5EA7\u8BB2\uFF0C\u91CE\u72D0\u95EE\u66F0\uFF1A\u4FEE\u5F97\u4F5B\u5FC3\u53EF\u843D\u56E0\u679C\uFF1F</p><p>\u5927\u5FB7\u66F0\uFF1A\u4E0D\u6627\u56E0\u679C\u3002\u300D\u5979\u62C8\u8D77\u4E00\u7247\u843D\u82B1\uFF0C\u300C\u7EB7\u7EAD\u4E16\u95F4\uFF0C\u8C01\u80FD\u5206\u5F97\u6E05\u4EC0\u4E48\u662F\u56E0\uFF1F\u4EC0\u4E48\u662F\u679C\uFF1F\u4F60\u3001\u6211\u3001\u5979\u2026\u2026\u90FD\u662F\u56E0\uFF0C\u90FD\u662F\u679C\u3002\u4F55\u5FC5\u518D\u6267\u4E8E\u56E0\u679C\uFF1F\u300D</p><p>\u9759\u989C\u542C\u5F97\u5446\u4E86\uFF0C\u5F53\u521D\u542C\u8BF4\u98D8\u6885\u5CF0\u8BF8\u5973\u5148\u540E\u9677\u4E8E\u661F\u6708\u6E56\uFF0C\u5979\u53EA\u89C9\u5F97\u90A3\u4E9B\u5973\u5B50\u50BB\u5F97\u53EF\u7B11\uFF0C\u6B64\u523B\u5979\u624D\u77E5\u9053\u81EA\u5DF1\u592A\u4F4E\u4F30\u4E86\u5979\u4EEC\u3002</p><p>\u7EAA\u7709\u59A9\u5C0F\u5FC3\u5730\u5C06\u843D\u82B1\u653E\u5728\u4E00\u5757\u5E72\u51C0\u7684\u77F3\u5934\u4E0A\uFF0C\u7136\u540E\u8BF4\u9053\uFF1A\u300C\u56DE\u53BB\u5427\u3002\u300D</p><p>\u7D2B\u73AB\u8FDF\u8FDF\u6CA1\u6709\u52A8\u8EAB\uFF0C\u5979\u95F2\u9002\u5730\u671B\u7740\u98CE\u666F\uFF0C\u50CF\u662F\u7279\u610F\u6765\u6D88\u78E8\u65F6\u95F4\u4E00\u822C\u60A0\u7136\u3002\u76F4\u5230\u65E5\u6CA1\u897F\u5C71\uFF0C\u5BD2\u610F\u6E10\u8D77\uFF0C\u4F17\u4EBA\u624D\u4E58\u821F\u56DE\u5C9B\u3002</p>',82);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
