import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_59" tabindex="-1"><a class="header-anchor" href="#_59" aria-hidden="true">#</a> 59</h1><p>\u6155\u5BB9\u9F99\u5BF9\u5979\u6B66\u529F\u4E4B\u5F3A\uFF0C\u771F\u6C14\u4E4B\u8BE1\u5F02\u4E5F\u5927\u89C9\u610F\u5916\uFF0C\u4ED6\u5DE6\u624B\u4E24\u6307\u4F38\u51FA\uFF0C\u4E0D\u7D27\u4E0D\u6162\u62C8\u4F4F\u4E24\u679A\u94F6\u9488\uFF0C\u53E6\u5916\u56DB\u679A\u94F6\u9488\u79BB\u4ED6\u8FD8\u6709\u5BF8\u8BB8\uFF0C\u4FBF\u88AB\u9707\u98DE\u3002\u9759\u989C\u54AC\u7259\u6536\u56DE\u76EE\u5149\uFF0C\u8FD0\u529F\u9A71\u51FA\u817F\u4E0A\u7684\u5BD2\u610F\uFF0C\u5DE6\u817F\u5411\u540E\u5288\u5F00\uFF0C\u5B9B\u5982\u5728\u7A7A\u4E2D\u6253\u5F00\u4E00\u67C4\u7389\u6247\u822C\u62A1\u4E86\u4E2A\u96EA\u4EAE\u7684\u534A\u5706\uFF0C\u70B9\u5728\u5730\u4E0A\u3002\u5979\u7684\u957F\u88D9\u5B8C\u5168\u7FFB\u5230\u8170\u4E0B\uFF0C\u6B64\u65F6\u4E00\u756A\u6323\u624E\uFF0C\u4E0D\u4EC5\u4E24\u817F\u66B4\u9732\u65E0\u9057\uFF0C\u8FDE\u96EA\u81C0\u4E5F\u6574\u4E2A\u9732\u51FA\uFF0C\u5149\u6DA6\u5982\u7389\u7684\u53CC\u817F\u4E00\u4E0A\u4E00\u4E0B\u7B14\u76F4\u5206\u5F00\uFF0C\u817F\u95F4\u9C9C\u7F8E\u7684\u7389\u6237\u8C61\u88AB\u4EBA\u5265\u5F00\u822C\u655E\u9732\u51FA\u6765\u3002</p><p>\u9759\u989C\u9762\u6C89\u5982\u6C34\uFF0C\u53F3\u811A\u865A\u8E22\uFF0C\u8BD5\u56FE\u6323\u8131\u6155\u5BB9\u9F99\u7684\u628A\u63E1\u3002\u6155\u5BB9\u9F99\u63E1\u5F97\u5E76\u4E0D\u7D27\uFF0C\u4F46\u65E0\u8BBA\u9759\u989C\u5982\u4F55\u7528\u529B\uFF0C\u59CB\u7EC8\u90FD\u65E0\u6CD5\u6323\u8131\u4ED6\u7684\u624B\u638C\u3002\u9759\u989C\u51B7\u7740\u8138\u66F2\u8D77\u4E0A\u8EAB\uFF0C\u4E94\u6307\u5982\u94A9\u76F4\u523A\u6155\u5BB9\u9F99\u80EF\u95F4\u3002\u6155\u5BB9\u9F99\u51B7\u7B11\u4E00\u58F0\uFF0C\u5E76\u672A\u51FA\u624B\u62E6\u683C\uFF0C\u800C\u662F\u66F2\u6307\u5C06\u90A3\u4E24\u679A\u94F6\u9488\u5F39\u5F80\u7A7A\u5904\u3002</p><p>\u9759\u989C\u6B63\u8BE7\u5F02\u95F4\uFF0C\u5FFD\u7136\u8E1D\u4E0A\u4E00\u7D27\uFF0C\u5A07\u8EAF\u88AB\u62A1\u5F97\u98DE\u4E86\u8D77\u6765\u3002\u5979\u52C9\u5F3A\u62AC\u8D77\u8EAB\u5B50\uFF0C\u53EA\u89C9\u4E73\u5C16\u5267\u75DB\uFF0C\u90A3\u4E24\u679A\u5C04\u5F80\u7A7A\u5904\u7684\u94F6\u9488\u6B63\u843D\u5728\u4E73\u4E0A\uFF0C\u4ECE\u4E73\u5934\u8D2F\u5165\u4E73\u623F\uFF0C\u5728\u7FE0\u8863\u4E0A\u6E85\u51FA\u4E24\u6735\u8840\u82B1\u3002\u75BC\u75DB\u4E2D\uFF0C\u9644\u5728\u9488\u4E0A\u7684\u52B2\u6C14\u8D81\u865A\u800C\u5165\uFF0C\u51B0\u80F6\u822C\u51DD\u7ED3\u5728\u7ECF\u7EDC\u95F4\uFF0C\u5C06\u5979\u7684\u771F\u6C14\u5B8C\u5168\u9501\u4F4F\u3002</p><p>\u300A\u623F\u5FC3\u661F\u76D1\u300B\u4E00\u5411\u4EE5\u8BE1\u5E7B\u83AB\u6D4B\u89C1\u957F\uFF0C\u4F46\u9759\u989C\u5148\u5931\u4E00\u62DB\uFF0C\u88AB\u6155\u5BB9\u9F99\u62FF\u4F4F\u811A\u8E1D\uFF0C\u62DB\u672F\u4E0A\u5904\u5904\u53D7\u5236\u3002\u5355\u4EE5\u5185\u529F\u800C\u8BBA\uFF0C\u5979\u7684\u300A\u623F\u5FC3\u661F\u76D1\u300B\u8FD8\u672A\u878D\u6C47\u8D2F\u901A\uFF0C\u5438\u53D6\u7684\u771F\u5143\u867D\u591A\uFF0C\u5374\u672A\u80FD\u5C3D\u6570\u5316\u89E3\uFF0C\u8F83\u4E4B\u6155\u5BB9\u9F99\u5DF2\u81F3\u5927\u6210\u7684\u592A\u4E00\u7ECF\u4E0D\u557B\u4E8E\u5929\u58E4\u4E4B\u522B\uFF0C\u4E00\u4EA4\u624B\u4FBF\u4E0B\u98CE\uFF0C\u53EA\u80FD\u5A74\u513F\u822C\u88AB\u4ED6\u73A9\u5F04\u3002</p><p>\u9759\u989C\u7F9E\u6068\u4EA4\u52A0\uFF0C\u5FCD\u4F4F\u4E73\u4E0A\u7684\u5267\u75DB\uFF0C\u62AC\u624B\u671D\u6000\u4E2D\u4E00\u63A2\uFF0C\u624D\u7701\u8D77\u81EA\u5DF1\u7684\u62A4\u8EAB\u5315\u9996\u5DF2\u7ECF\u7ED9\u4E86\u4E49\u6BCD\u3002\u5979\u98A4\u7740\u624B\u671D\u80F8\u524D\u62B9\u53BB\uFF0C\u6307\u5C16\u63A0\u8FC7\u4E73\u5934\uFF0C\u987F\u65F6\u53C8\u662F\u4E00\u9635\u5267\u75DB\uFF0C\u90A3\u4E24\u679A\u94F6\u9488\u6CA1\u5165\u4E73\u5185\uFF0C\u53EA\u5269\u4E00\u70B9\u7EC6\u5C0F\u7684\u9488\u5C3E\u5D4C\u5728\u4E73\u5934\u4E0A\uFF0C\u6025\u5207\u95F4\u54EA\u91CC\u62D4\u5F97\u51FA\u6765\uFF1F</p><p>\u66FC\u5999\u7684\u7389\u4F53\u5728\u6155\u5BB9\u9F99\u624B\u4E2D\u8F97\u8F6C\u76D8\u65CB\uFF0C\u8EAB\u4E0D\u7531\u5DF1\u5730\u505A\u51FA\u79CD\u79CD\u5A9A\u8273\u7684\u59FF\u6001\u3002\u9759\u989C\u73E0\u9497\u6ED1\u843D\uFF0C\u6563\u5F00\u7684\u79C0\u53D1\u8C61\u88AB\u72C2\u98CE\u5439\u5377\u7684\u70DF\u971E\u822C\u5728\u8138\u4FA7\u98D8\u821E\uFF0C\u8D64\u88F8\u7684\u4E0B\u4F53\u83B9\u767D\u5982\u7389\uFF0C\u5728\u9ED1\u6697\u4E2D\u5206\u5916\u593A\u76EE\u3002\u5979\u7389\u8138\u96EA\u767D\uFF0C\u7EC6\u767D\u7684\u7259\u9F7F\u7D27\u7D27\u54AC\u7740\u5507\u74E3\uFF0C\u795E\u60C5\u51C4\u8273\u52A8\u4EBA\u3002</p><p>\u6155\u5BB9\u9F99\u62CE\u7740\u5C11\u5973\u7EA4\u7EC6\u7684\u7389\u8E1D\uFF0C\u9976\u6709\u5174\u81F4\u5730\u6B23\u8D4F\u7740\u5979\u52A8\u4EBA\u7684\u9999\u8EAF\uFF0C\u76F4\u5982\u628A\u5979\u7684\u7389\u4F53\u5F53\u6210\u4E86\u4E00\u4EF6\u73A9\u7269\uFF0C\u5728\u624B\u4E0A\u53CD\u8986\u8D4F\u73A9\u3002\u9759\u989C\u6D01\u767D\u7684\u80A2\u4F53\u968F\u624B\u7FFB\u6EDA\u65CB\u821E\uFF0C\u7389\u817F\u5F00\u5408\u95F4\u6D41\u9732\u51FA\u65E0\u7A77\u8273\u6001\u3002</p><p>\u9759\u989C\u52C9\u5F3A\u805A\u8D77\u6B8B\u4F59\u7684\u529F\u529B\uFF0C\u594B\u529B\u671D\u4ED6\u624B\u4E0A\u653B\u53BB\u3002\u6155\u5BB9\u9F99\u8F7B\u8511\u5730\u77A5\u4E86\u5979\u4E00\u773C\uFF0C\u624B\u8155\u8F7B\u8F7B\u4E00\u6296\uFF0C\u53EA\u542C\u683C\u7684\u4E00\u58F0\u8106\u54CD\uFF0C\u90A3\u6761\u5149\u6DA6\u5982\u7389\u7684\u7C89\u817F\u5E94\u624B\u800C\u65AD\u3002\u63A5\u7740\u6155\u5BB9\u9F99\u62AC\u8D77\u624B\uFF0C\u5C06\u5931\u53BB\u53CD\u6297\u4E4B\u529B\u7684\u5C11\u5973\u8FDC\u8FDC\u629B\u5F00\u3002</p><p>\u546F\u7684\u4E00\u58F0\uFF0C\u9759\u989C\u91CD\u91CD\u843D\u5728\u5730\u4E0A\uFF0C\u843D\u5904\u5E76\u975E\u575A\u786C\u7684\u5C71\u77F3\uFF0C\u9976\u662F\u5982\u6B64\uFF0C\u6CA1\u6709\u4E86\u62A4\u4F53\u771F\u6C14\u7684\u9759\u989C\u8FD8\u662F\u6454\u5F97\u773C\u524D\u53D1\u9ED1\uFF0C\u5468\u8EAB\u9AA8\u9ABC\u6B32\u788E\uFF0C\u8EAB\u5B50\u8FD8\u50CF\u5728\u7A7A\u4E2D\u98DE\u821E\u822C\u4E00\u9635\u9635\u7729\u6655\u3002\u5979\u6A31\u5507\u4E00\u5F20\uFF0C\u5410\u51FA\u4E00\u53E3\u9C9C\u8840\uFF0C\u5598\u606F\u7740\u52C9\u5F3A\u7741\u5F00\u773C\u775B\u3002</p><p>\u5165\u76EE\u662F\u4E00\u4E1B\u91D1\u8272\u7684\u6BDB\u53D1\uFF0C\u4E00\u6761\u4F53\u6001\u5A01\u731B\u7684\u5DE8\u72AC\u6602\u9996\u7FD8\u5C3E\uFF0C\u6B63\u9A91\u5728\u4E00\u4E2A\u7F8E\u5987\u4E30\u8174\u7684\u96EA\u81C0\u4E0A\u7740\u529B\u5978\u6DEB\u3002\u65C1\u8FB9\u521A\u521A\u7834\u4F53\u7684\u65B0\u5A18\u7F9E\u6DA9\u5730\u6380\u5F00\u7EA2\u76D6\u5934\uFF0C\u4E00\u624B\u4ECE\u7F8E\u5987\u809B\u4E2D\u63A2\u5165\u3002\u9694\u7740\u534A\u900F\u660E\u7684\u808C\u80A4\uFF0C\u80FD\u770B\u5230\u5979\u7684\u7EA4\u624B\u4E00\u76F4\u4F38\u5230\u7F8E\u5987\u8179\u8154\u6DF1\u5904\uFF0C\u6258\u4F4F\u4E86\u704C\u6EE1\u72D7\u7CBE\u7684\u5B50\u5BAB\u2026\u2026\u5979\u60F3\u8D77\u6765\u4E86\uFF0C\u8FD9\u662F\u5979\u4EB2\u624B\u505A\u7684\u706F\u7B3C\u3002</p><p>\u592D\u592D\u4E0D\u7701\u4EBA\u4E8B\u5730\u5012\u5728\u8F6E\u53F0\u4E0B\uFF0C\u7531\u4E8E\u6000\u5B55\u7684\u7F18\u6545\uFF0C\u5979\u7684\u8863\u5E26\u53EA\u662F\u8F7B\u8F7B\u4E00\u633D\uFF0C\u5728\u6CB3\u6C34\u51B2\u5237\u4E0B\u5DF2\u7ECF\u677E\u5F00\u3002\u4EB5\u88E4\u88AB\u51B2\u5230\u8E1D\u95F4\uFF0C\u50CF\u6C34\u8349\u4E00\u6837\u98D8\u6D6E\u7740\u3002\u767D\u767D\u7684\u53CC\u817F\u5FAE\u5FAE\u626C\u8D77\uFF0C\u5F77\u4F5B\u4E24\u679D\u98D8\u6447\u4E0D\u5B9A\u7684\u7389\u73CA\u745A\u3002\u8863\u886B\u6563\u5F00\uFF0C\u5706\u9F13\u9F13\u7684\u5C0F\u8179\u6D6E\u51FA\u6C34\u9762\u5C11\u8BB8\uFF0C\u767D\u817B\u5F97\u8000\u76EE\uFF0C\u4E0B\u9762\u7FD8\u7740\u4E00\u6839\u53C8\u767D\u53C8\u5AE9\u7684\u5C0F\u8089\u68D2\u3002\u5FFD\u7136\u95F4\uFF0C\u5979\u8179\u4E0B\u4E00\u98A4\uFF0C\u6D8C\u51FA\u4E00\u80A1\u7EA2\u7EA2\u7684\u6DB2\u4F53\uFF0C\u50CF\u86C7\u4E00\u6837\u4ECE\u4E24\u817F\u95F4\u873F\u8712\u4F38\u957F\u3002</p><p>\u300C\u592D\u592D\u2026\u2026\u300D\u9759\u989C\u53EB\u9053\uFF0C\u5979\u4F38\u51FA\u624B\uFF0C\u60F3\u628A\u6000\u7740\u81EA\u5DF1\u5B69\u5B50\u7684\u5C0F\u6BCD\u72D7\u6402\u5728\u6000\u4E2D\u3002</p><p>\u4E73\u4E0A\u4F20\u6765\u6495\u88C2\u822C\u7684\u5267\u75DB\uFF0C\u6155\u5BB9\u9F99\u8D1F\u624B\u800C\u7ACB\uFF0C\u9762\u65E0\u8868\u60C5\u5730\u8E29\u5728\u9759\u989C\u4E73\u4E0A\uFF0C\u5C06\u9AD8\u8038\u7684\u4E73\u7403\u8E29\u5F97\u6241\u5706\u3002\u5D4C\u5728\u4E73\u8089\u4E2D\u7684\u94F6\u9488\u8D8A\u8FDB\u8D8A\u6DF1\uFF0C\u9488\u5C16\u522E\u5728\u80F8\u9AA8\u4E0A\uFF0C\u4F20\u6765\u4EE4\u4EBA\u9AA8\u9165\u7684\u6C99\u6C99\u58F0\u3002\u9759\u989C\u75BC\u5F97\u5A25\u7709\u62E7\u7D27\uFF0C\u98A4\u7740\u624B\u62B1\u4F4F\u6155\u5BB9\u9F99\u7684\u9774\u5B50\uFF0C\u7AED\u529B\u63A8\u6421\u3002</p><p>\u9999\u8F6F\u7684\u4E73\u7403\u5728\u6155\u5BB9\u9F99\u811A\u4E0B\u6EDA\u6765\u6EDA\u53BB\uFF0C\u9C9C\u8840\u900F\u8FC7\u62B9\u80F8\uFF0C\u6253\u6E7F\u4E86\u8584\u8584\u7684\u7FE0\u886B\u3002\u771F\u6C14\u88AB\u5236\u7684\u9759\u989C\u53EA\u9664\u4E0B\u5E73\u5E38\u5973\u5B50\u7684\u529B\u6C14\uFF0C\u6839\u672C\u64BC\u4E0D\u52A8\u90A3\u53EA\u91CD\u82E5\u5C71\u77F3\u7684\u786C\u9774\uFF0C\u5979\u80FD\u611F\u89C9\u5230\u94F6\u9488\u9876\u5728\u9AA8\u9ABC\uFF0C\u88AB\u8E29\u5F97\u6E10\u6E10\u5F2F\u66F2\uFF0C\u4E73\u8089\u56DB\u5904\u6EDA\u6EA2\uFF0C\u4E0D\u7B49\u94F6\u9488\u5F2F\u66F2\u5C31\u4F1A\u88AB\u8E29\u5F97\u7206\u88C2\u3002</p><p>\u523B\u9AA8\u94BB\u5FC3\u7684\u75BC\u75DB\u8DB3\u4EE5\u4EE4\u4EBA\u75AF\u72C2\uFF0C\u53EF\u9759\u989C\u5374\u6B7B\u6B7B\u54AC\u7740\u7259\u5173\uFF0C\u4E00\u58F0\u4E0D\u542D\u3002\u5979\u60F3\u8FC7\u79CD\u79CD\u5BF9\u4ED8\u6155\u5BB9\u9F99\u7684\u8BA1\u7B56\uFF0C\u5374\u6CA1\u60F3\u5230\u4F1A\u5728\u6BEB\u65E0\u9632\u5907\u7684\u5883\u51B5\u4E0B\u4E0E\u4ED6\u906D\u9047\u3002\u5341\u4F59\u5E74\u82E6\u5FC3\u79EF\u8651\u60F3\u8981\u590D\u4EC7\uFF0C\u53EF\u7B11\u4EC0\u4E48\u90FD\u6CA1\u505A\u5230\uFF0C\u5C31\u8981\u50CF\u877C\u8681\u822C\u6B7B\u5728\u4ED6\u811A\u4E0B\u3002</p><p>\u9759\u989C\u75DB\u5F97\u65E0\u6CD5\u5F00\u53E3\uFF0C\u4F46\u773C\u4E2D\u6D41\u9732\u51FA\u7684\u6068\u610F\u6BD4\u8BED\u8A00\u66F4\u6E05\u6670\u3002\u6068\u5F97\u90A3\u4E48\u6DF1\uFF0C\u90A3\u4E48\u8FDC\uFF0C\u90A3\u4E48\u4E45\u3002</p><p>\u6155\u5BB9\u9F99\u7A81\u7136\u7B11\u4E86\u8D77\u6765\uFF0C\u4ED6\u62AC\u8D77\u811A\uFF0C\u5F85\u4E73\u7403\u6062\u590D\u539F\u72B6\u53C8\u518D\u6B21\u8E29\u4E0B\uFF0C\u8BA9\u5F2F\u66F2\u7684\u94F6\u9488\u5728\u4E73\u8089\u6405\u51FA\u65B0\u7684\u4F24\u53E3\uFF0C\u60A0\u7136\u9053\uFF1A\u300C\u9F99\u6218\u91CE\u7684\u513F\u5B50\u679C\u7136\u591F\u786C\u6C14\u3002\u300D</p><p>\u9759\u989C\u6CA1\u6709\u592A\u591A\u7684\u5403\u60CA\uFF0C\u4ED6\u65E0\u7F18\u65E0\u6545\u5730\u6765\u5230\u5730\u5BAB\uFF0C\u4E0D\u4F1A\u53EA\u662F\u4E3A\u4E86\u6B23\u8D4F\u8FD9\u76CF\u82B1\u706F\u3002\u81F3\u4E8E\u81EA\u5DF1\u7684\u8EAB\u4EFD\u662F\u5982\u4F55\u6CC4\u6F0F\u7684\uFF0C\u5979\u5DF2\u7ECF\u6CA1\u6709\u4F59\u529B\u53BB\u731C\u6D4B\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u811A\u8FD8\u8E29\u5728\u9759\u989C\u4E73\u4E0A\uFF0C\u5F13\u8170\u6495\u5F00\u5979\u7684\u895F\u9886\uFF0C\u5C06\u53E6\u4E00\u53EA\u5B8C\u597D\u7684\u4E73\u623F\u63E1\u5728\u624B\u4E2D\uFF0C\u63C9\u634F\u7740\u8BF4\u9053\uFF1A\u300C\u2026\u2026\u5976\u5B50\u4E5F\u6BD4\u4F60\u6DEB\u8D31\u7684\u5A18\u4EB2\u575A\u633A\u3002\u300D</p><p>\u9759\u989C\u7AED\u529B\u5410\u51FA\u4E00\u53E3\u8840\u6CAB\uFF0C\u671D\u6155\u5BB9\u9F99\u8138\u4E0A\u553E\u53BB\u3002\u6155\u5BB9\u9F99\u4FA7\u8EAB\u907F\u5F00\uFF0C\u63E1\u4F4F\u5979\u7684\u4E73\u623F\u91CD\u91CD\u4E00\u62E7\u3002\u9759\u989C\u624B\u6307\u6B7B\u6B7B\u62A0\u7740\u677F\u7F1D\uFF0C\u75BC\u5F97\u5A07\u8EAF\u4E71\u98A4\uFF0C\u90A3\u53EA\u96EA\u5AE9\u7684\u4E73\u7403\u88AB\u626D\u5F97\u53D8\u5F62\uFF0C\u5D4C\u5728\u91CC\u9762\u7684\u94F6\u9488\u6405\u7834\u4E86\u4E73\u8089\uFF0C\u4ECE\u7EA2\u5AE9\u7684\u4E73\u5934\u5192\u51FA\u4E00\u4E32\u7EC6\u5C0F\u8840\u73E0\u3002\u6155\u5BB9\u9F99\u637B\u7740\u7EC6\u6ED1\u7684\u4E73\u8089\uFF0C\u5C06\u94F6\u9488\u4ECE\u4E73\u8089\u4E2D\u786C\u751F\u751F\u6324\u51FA\u3002\u4ED6\u5FAE\u7B11\u7740\u6B23\u8D4F\u9759\u989C\u8138\u4E0A\u7684\u75DB\u82E6\uFF0C\u7136\u540E\u4ECE\u6000\u4E2D\u53D6\u51FA\u4E24\u5F20\u767D\u8272\u7684\u4E8B\u7269\u3002</p><p>\u9759\u989C\u7F8E\u76EE\u731B\u7136\u77AA\u5927\uFF0C\u63A5\u7740\u75DB\u82E6\u5730\u54B3\u55FD\u8D77\u6765\u3002\u90A3\u662F\u4E24\u53EA\u7ECF\u8FC7\u97A3\u5236\u7684\u76AE\u56CA\uFF0C\u6491\u5F00\u65F6\u5F77\u4F5B\u4E00\u53EA\u7389\u7897\uFF0C\u67D4\u8F6F\u800C\u53C8\u575A\u97E7\u3002\u4E0A\u9762\u5206\u522B\u523A\u7740\u4E00\u884C\u58A8\u5B57\uFF1A\u516B\u6781\u95E8\u638C\u95E8\u592B\u4EBA\u3001\u661F\u6708\u6E56\u6DEB\u5974\u5510\u989C\u3002</p><p>\u90A3\u662F\u6BCD\u4EB2\u7559\u7ED9\u81EA\u5DF1\u552F\u4E00\u7684\u9057\u7269\uFF0C\u90A3\u4E24\u53EA\u5148\u88AB\u4EBA\u523A\u4E0A\u6587\u5B57\uFF0C\u53C8\u88AB\u6740\u4E0B\u7684\u4E73\u623F\u3002</p><p>\u6765\u5230\u661F\u6708\u6E56\u4E4B\u524D\uFF0C\u5979\u628A\u8FD9\u5BF9\u4E73\u623F\u57CB\u5728\u4E86\u6D41\u97F3\u6EAA\u7554\uFF0C\u9759\u83BA\u59B9\u59B9\u7684\u5893\u4E2D\uFF0C\u6CA1\u60F3\u5230\u8FDE\u8FD9\u4E5F\u88AB\u4ED6\u77E5\u9053\u4E86\u3002</p><p>\u300C\u6211\u8BB0\u5F97\u8FD9\u53EA\u662F\u53F3\u4E73\u3002\u300D\u6155\u5BB9\u9F99\u62FF\u628A\u4E00\u53EA\u523A\u7740\u6DEB\u5974\u5510\u989C\u5B57\u6837\u7684\u76AE\u56CA\u5957\u5728\u9759\u989C\u7684\u7C89\u4E73\u4E0A\uFF0C\u7B11\u4E86\u7B11\uFF0C\u7136\u540E\u7528\u90A3\u6839\u6EF4\u8840\u7684\u94F6\u9488\u4ECE\u4E0A\u9762\u5E73\u5E73\u7A7F\u8FC7\uFF0C\u5C06\u6BCD\u5973\u4FE9\u7684\u4E73\u5934\u7A7F\u5728\u4E00\u8D77\u3002</p><p>\u9759\u989C\u7684\u4E73\u623F\u6BD4\u6BCD\u4EB2\u8FD8\u8981\u4E30\u7855\uFF0C\u90A3\u5C42\u67D4\u97E7\u7684\u76AE\u80A4\u88AB\u6ED1\u817B\u7684\u4E73\u8089\u5B8C\u5168\u80C0\u6EE1\uFF0C\u50CF\u96EA\u7403\u822C\u5728\u80F8\u524D\u98A4\u5FAE\u5FAE\u4E0D\u4F4F\u8F7B\u6296\u3002\u90A3\u4E32\u5B57\u8FF9\u968F\u4E4B\u98A4\u52A8\uFF0C\u5C31\u50CF\u6BCD\u4EB2\u7684\u4E73\u623F\u5728\u5979\u8EAB\u4E0A\u590D\u6D3B\u4E00\u822C\u3002</p><p>\u300C\u6740\u4E86\u6211\u2026\u2026\u300D\u9759\u989C\u98A4\u58F0\u8BF4\u9053\u3002</p><p>\u6155\u5BB9\u9F99\u5F39\u4E86\u5F39\u6EA2\u8840\u7684\u4E73\u5934\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u4E0D\u3002\u300D</p><p>\u300C\u5728\u8FD9\u91CC\uFF0C\u6B7B\u751F\u90FD\u7531\u6211\u6765\u5B9A\u593A\u3002\u300D\u6155\u5BB9\u9F99\u53C9\u4F4F\u9759\u989C\u7684\u67D4\u9888\uFF0C\u5C06\u5979\u4E3E\u5230\u7A7A\u4E2D\u3002</p><p>\u9759\u989C\u534A\u5E45\u8863\u886B\u88AB\u6495\u5230\u8170\u95F4\uFF0C\u88F8\u7740\u4E00\u53EA\u767D\u767D\u7684\u4E73\u623F\u3002\u5DE6\u80A9\u67D3\u8840\u7684\u8863\u895F\u6CBE\u5728\u808C\u80A4\u4E0A\uFF0C\u968F\u7740\u4E73\u7403\u7684\u98A4\u6296\u4E00\u5760\u4E00\u5760\u7F13\u7F13\u6ED1\u843D\u3002\u5979\u7684\u80A9\u5F88\u767D\uFF0C\u50CF\u5973\u4EBA\u4E00\u6837\u53C8\u7EC6\u53C8\u6ED1\u3002\u7834\u788E\u7684\u8863\u886B\u6CA1\u6709\u5728\u4E0A\u9762\u505C\u7559\u592A\u4E45\uFF0C\u4FBF\u6389\u843D\u4E0B\u6765\uFF0C\u8DF3\u51FA\u4E00\u53EA\u6EF4\u8840\u7684\u7389\u4E73\u3002\u5979\u7684\u4E73\u5934\u88AB\u8E29\u5F97\u80BF\u8D77\uFF0C\u4E73\u773C\u65AD\u65AD\u7EED\u7EED\u6EA2\u51FA\u9C9C\u8840\uFF0C\u5C06\u767D\u7389\u822C\u7684\u4E73\u7403\u67D3\u5F97\u901A\u7EA2\u3002</p><p>\u300C\u5982\u679C\u4F60\u4E0D\u51FA\u624B\uFF0C\u53EF\u80FD\u4F1A\u7792\u8FC7\u6211\u3002\u300D\u6155\u5BB9\u9F99\u637B\u7740\u5979\u7684\u4E73\u5934\u8BF4\u9053\uFF1A\u300C\u53EF\u60DC\u4F60\u4F4E\u4F30\u4E86\u6C90\u957F\u8005\u7684\u773C\u529B\u3002\u867D\u7136\u5728\u7518\u9732\u5BFA\u4F60\u906E\u4F4F\u9762\u5B54\uFF0C\u53D8\u4E86\u58F0\u97F3\uFF0C\u6C90\u957F\u8005\u8FD8\u662F\u8FA8\u51FA\u4E86\u4F60\u7684\u4F53\u5F62\u3002\u4ED6\u5B88\u4E86\u4F60\u56DB\u4E2A\u6708\uFF0C\u5C06\u4F60\u7684\u6765\u9F99\u53BB\u8109\u67E5\u5F97\u4E00\u6E05\u4E8C\u695A\u2026\u2026\u6211\u5F88\u5947\u602A\u4F60\u4E3A\u4F55\u4E0D\u6740\u6389\u90A3\u4E2A\u7434\u58F0\u82B1\u5F71\uFF0C\u53EA\u5E72\u4E86\u5979\u4E09\u5929\uFF0C\u5979\u5C31\u4EC0\u4E48\u90FD\u8BF4\u4E86\u3002\u6C90\u957F\u8005\u628A\u6D41\u97F3\u6EAA\u6398\u5730\u4E09\u5C3A\uFF0C\u627E\u5230\u4E86\u4E00\u4E9B\u6709\u8DA3\u7684\u4E1C\u897F\u3002\u300D</p><p>\u9759\u989C\u5589\u5934\u683C\u683C\u4F5C\u54CD\uFF0C\u6298\u65AD\u7684\u5C0F\u817F\u5728\u8EAB\u4E0B\u8F7B\u8F7B\u6447\u6643\u3002\u5979\u4E0A\u8EAB\u7389\u4E73\u8892\u9732\uFF0C\u6389\u843D\u7684\u8863\u886B\u60AC\u5728\u8155\u4E0A\uFF0C\u6CBE\u7740\u6591\u6591\u8840\u8FF9\u3002</p><p>\u6155\u5BB9\u9F99\u51DD\u89C6\u7740\u5979\u7684\u773C\u775B\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u90A3\u4E2A\u5973\u5B50\u7684\u5C38\u4F53\u8FD8\u5B8C\u597D\u5982\u65B0\u3002\u867D\u7136\u5C11\u4E86\u9634\u6237\uFF0C\u4F46\u6211\u7684\u5C5E\u4E0B\u8FD8\u662F\u5F88\u6EE1\u610F\u3002\u4F60\u653E\u5FC3\uFF0C\u4ED6\u4EEC\u73A9\u8FC7\u4E4B\u540E\u5C31\u5E2E\u4F60\u6BC1\u5C38\u706D\u8FF9\uFF0C\u62FF\u5979\u5582\u4E86\u72D7\u3002\u300D</p><p>\u9759\u989C\u832B\u7136\u671B\u7740\u865A\u7A7A\uFF0C\u5979\u4E00\u76F4\u4EE5\u4E3A\u9AB0\u5B50\u662F\u5728\u81EA\u5DF1\u624B\u4E2D\uFF0C\u6B64\u65F6\u624D\u77E5\u9053\uFF0C\u8FD9\u573A\u8D4C\u535A\u5979\u5F88\u65E9\u4EE5\u524D\u5C31\u8F93\u4E86\u3002\u60F3\u5230\u9759\u83BA\u59B9\u59B9\u5A07\u5AE9\u7684\u80F4\u4F53\u88AB\u7FA4\u72D7\u5206\u98DF\u7684\u60E8\u72B6\uFF0C\u9759\u989C\u624B\u811A\u4E0D\u7981\u98A4\u6296\u8D77\u6765\u3002</p><p>\u8F6E\u53F0\u7F13\u7F13\u8F6C\u5165\u9ED1\u6697\uFF0C\u5C06\u6D78\u5728\u6C34\u4E2D\u7684\u5C11\u5973\u629B\u5728\u8EAB\u540E\u3002\u6D1E\u623F\u7684\u9648\u8BBE\u534E\u4E3D\u65E0\u5339\uFF0C\u5927\u7EA2\u56CD\u5B57\u4E0B\uFF0C\u65B0\u5A18\u6BCD\u5973\u65E0\u58F0\u5730\u4F8D\u5949\u7740\u4E00\u5934\u4F5C\u4E3A\u65B0\u90CE\u7684\u5DE8\u72AC\uFF0C\u5982\u679C\u53EF\u80FD\uFF0C\u9759\u989C\u5B81\u613F\u4E0E\u5979\u4EEC\u6BCD\u5973\u4E92\u6362\u2026\u2026</p><p>\u300C\u5341\u4E94\u5E74\u524D\u6211\u6CA1\u6709\u6740\u4F60\uFF0C\u73B0\u5728\u4E5F\u4E0D\u4F1A\u8F7B\u6613\u6740\u4E86\u4F60\u3002\u300D\u6155\u5BB9\u9F99\u6DE1\u6DE1\u8BF4\u9053\u3002\u5F53\u5E74\u90A3\u4E2A\u7537\u5B69\u575A\u6BC5\u7684\u76EE\u5149\u53C8\u4E00\u6B21\u6D6E\u4E0A\u5FC3\u5934\uFF0C\u8FD9\u4E00\u5929\u4ED6\u5DF2\u7ECF\u7B49\u4E86\u5F88\u4E45\u3002\u81EA\u4ECE\u770B\u5230\u90A3\u6839\u6728\u6869\u88AB\u4ED6\u7528\u7A1A\u5AE9\u7684\u7259\u9F7F\u751F\u751F\u54AC\u65AD\uFF0C\u4ED6\u5C31\u5728\u7B49\u5F85\u8FD9\u4E00\u5929\uFF0C\u7B49\u5F85\u90A3\u4E2A\u8DDF\u81EA\u5DF1\u76F8\u4F3C\u7684\u5B69\u5B50\u4F1A\u56DE\u6765\u627E\u4ED6\u62A5\u4EC7\u3002\u4F46\u4ED6\u6CA1\u60F3\u5230\u6765\u7684\u662F\u4E00\u4E2A\u5973\u4EBA\u3002</p><p>\u300C\u5472\u300D\u7684\u4E00\u58F0\uFF0C\u957F\u88D9\u88AB\u5F53\u4E2D\u6495\u5F00\uFF0C\u4E00\u76F4\u88C2\u5230\u5C0F\u8179\u3002\u4E24\u6761\u4FEE\u957F\u7684\u7389\u817F\u7389\u7BB8\u822C\u5E76\u5728\u4E00\u8D77\uFF0C\u767D\u5AE9\u7684\u817F\u7F1D\u95F4\uFF0C\u9732\u51FA\u4E00\u4E1B\u4E4C\u4EAE\u7684\u6BDB\u53D1\uFF0C\u7EA4\u67D4\u5982\u4E1D\u3002</p><p>\u6155\u5BB9\u9F99\u6258\u7740\u5979\u7684\u819D\u5F2F\u5411\u4E0A\u62AC\u8D77\uFF0C\u7D27\u5E76\u7684\u7389\u817F\u7F13\u7F13\u655E\u5F00\uFF0C\u96EA\u767D\u7684\u7389\u80A1\u95F4\u7FFB\u51FA\u4E24\u7247\u5A07\u7EA2\u67D4\u817B\u7684\u5AE9\u8089\u3002\u6155\u5BB9\u9F99\u6EE1\u610F\u7684\u6B23\u8D4F\u7740\u9759\u989C\u7684\u7F9E\u5904\uFF0C\u300C\u8FD9\u5C31\u662F\u90A3\u4E2A\u5973\u5B50\u7684\u9634\u6237\u5427\u3002\u300D\u6155\u5BB9\u9F99\u5206\u5F00\u9759\u989C\u7684\u82B1\u65B0\uFF0C\u51B0\u51C9\u7684\u624B\u6307\u6345\u5165\u8089\u7A74\uFF0C\u300C\u6311\u5F97\u4E0D\u9519\uFF0C\u679C\u7136\u5F88\u5AE9\u2026\u2026\u300D</p><p>\u9759\u989C\u8EAB\u5B50\u6296\u4E86\u4E00\u4E0B\uFF0C\u53C8\u9759\u4E86\u4E0B\u6765\uFF0C\u6728\u7136\u4EFB\u4ED6\u638F\u5F04\uFF0C\u4F3C\u4E4E\u4E00\u5177\u6CA1\u6709\u77E5\u89C9\u7684\u73A9\u5076\u3002</p><p>\u6155\u5BB9\u9F99\u62C9\u5F00\u9ED1\u8863\uFF0C\u80EF\u4E0B\u6602\u8D77\u4E00\u6761\u72F0\u72DE\u7684\u8089\u68D2\u3002\u9759\u989C\u4ECE\u672A\u89C1\u8FC7\u5982\u6B64\u53EF\u6016\u7684\u9633\u5177\uFF0C\u90A3\u7B80\u76F4\u4E0D\u662F\u4EBA\u7C7B\u6240\u80FD\u62E5\u6709\u7684\u4E8B\u7269\uFF0C\u957F\u8FD1\u5C3A\u8BB8\u7684\u8089\u68D2\u4E0A\uFF0C\u904D\u5E03\u4E86\u9897\u7C92\u3001\u8089\u523A\u3001\u7A81\u8D77\u3001\u7EB9\u8DEF\u3001\u8089\u7624\u2026\u2026\u9633\u5177\u6839\u90E8\u8FD8\u6709\u4E00\u4E1B\u624B\u6307\u7C97\u7EC6\u7684\u89E6\u624B\uFF0C\u6574\u6839\u8089\u68D2\u5C31\u50CF\u4E00\u4EF6\u51F6\u6B8B\u7684\u5229\u5668\uFF0C\u5996\u90AA\u4E4B\u6781\u3002</p><p>\u6CA1\u6709\u4EFB\u4F55\u524D\u620F\uFF0C\u72F0\u72DE\u7684\u5DE8\u7269\u4FBF\u72E0\u72E0\u6345\u5165\u871C\u7A74\u3002\u9759\u989C\u4E0E\u6674\u96EA\u3001\u592D\u592D\u6DEB\u73A9\u65F6\u867D\u7136\u4E5F\u8BA9\u5979\u4EEC\u63D2\u8FC7\uFF0C\u4F46\u5979\u4EEC\u4E24\u4E2A\u6240\u7528\u7269\u4F53\u52A0\u8D77\u6765\u4E5F\u4E0D\u8DB3\u6155\u5BB9\u9F99\u4E00\u534A\u7684\u7C97\u957F\u3002\u6BD4\u5F00\u82DE\u66F4\u5F3A\u70C8\u7684\u75DB\u695A\u4ECE\u8EAB\u4E0B\u5347\u8D77\uFF0C\u5F77\u4F5B\u8179\u8154\u88AB\u8089\u68D2\u8D2F\u7A7F\uFF0C\u5C06\u6574\u4E2A\u9634\u6237\u5B8C\u5168\u6495\u88C2\u3002</p><p>\u9759\u989C\u5403\u529B\u5730\u6602\u8D77\u5934\uFF0C\u53D1\u51FA\u4E00\u58F0\u5C48\u8FB1\u800C\u53C8\u75DB\u82E6\u7684\u60B2\u9E23\uFF0C\u6323\u624E\u7740\u5408\u7D27\u53CC\u817F\u3002\u5979\u96EA\u767D\u7684\u7389\u4F53\u659C\u5728\u7A7A\u4E2D\uFF0C\u5706\u6DA6\u7684\u7F8E\u81C0\u9876\u5728\u6155\u5BB9\u9F99\u8179\u4E0B\uFF0C\u4E00\u6761\u817F\u88AB\u6155\u5BB9\u9F99\u62B1\u5728\u81C2\u5F2F\uFF0C\u53E6\u4E00\u6761\u817F\u7AD6\u5782\u7740\uFF0C\u811A\u5C16\u79BB\u5730\u6570\u5BF8\u4E00\u8361\u4E00\u8361\u5212\u7740\u5708\u5B50\u3002\u7FE0\u886B\u957F\u88D9\u90FD\u892A\u5728\u8170\u95F4\uFF0C\u4E24\u53EA\u4E73\u623F\u9AD8\u9AD8\u633A\u8D77\uFF0C\u4E00\u53EA\u767D\u51C0\u7684\u7389\u4E73\u523A\u7740\u6DEB\u5974\u5B57\u6837\uFF0C\u4E73\u5934\u5E73\u5E73\u523A\u7740\u4E00\u679A\u94F6\u9488\uFF0C\u53E6\u4E00\u53EA\u4E30\u817B\u7684\u4E73\u7403\u5916\u8868\u770B\u4E0D\u5230\u4EFB\u4F55\u4F24\u75D5\uFF0C\u5374\u88AB\u9C9C\u8840\u67D3\u5F97\u901A\u7EA2\u3002\u7834\u788E\u7684\u8863\u886B\u4ECE\u8170\u4E0B\u957F\u957F\u62D6\u5230\u5730\u9762\uFF0C\u968F\u7740\u8089\u68D2\u7684\u633A\u5F04\u5728\u96EA\u767D\u7684\u5706\u81C0\u4E0B\u6447\u6765\u6643\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u65E0\u60C5\u5730\u6298\u78E8\u7740\u9759\u989C\u7684\u5AE9\u7A74\uFF0C\u4E0D\u591A\u65F6\u7389\u6237\u4FBF\u80BF\u4E86\u8D77\u6765\uFF0C\u7EC6\u5AE9\u7684\u6D25\u53E3\u88AB\u8089\u523A\u5212\u51FA\u9053\u9053\u8840\u75D5\u3002\u9759\u989C\u6CEA\u6D41\u6EE1\u9762\uFF0C\u5979\u6323\u624E\u7740\u626D\u52A8\u8EAB\u4F53\uFF0C\u62DA\u547D\u6495\u6253\u7740\u81EA\u5DF1\u7684\u751F\u6B7B\u4EC7\u4EBA\u3002\u5979\u6574\u4E2A\u8EAB\u5B50\u90FD\u60AC\u5728\u6155\u5BB9\u9F99\u8EAB\u4E0A\uFF0C\u6839\u672C\u65E0\u4ECE\u4F7F\u529B\uFF0C\u8FD9\u4E9B\u626D\u52A8\u548C\u6323\u624E\u53EA\u80FD\u8BA9\u6155\u5BB9\u9F99\u66F4\u52A0\u5174\u594B\u3002\u4ED6\u62B1\u4F4F\u9759\u989C\u7EA4\u8F6F\u7684\u8170\u80A2\uFF0C\u7528\u529B\u4E00\u62C9\uFF0C\u300C\u556A\u53FD\u300D\u4E00\u58F0\u817B\u54CD\uFF0C\u9759\u989C\u7684\u7F8E\u81C0\u6253\u5728\u6155\u5BB9\u9F99\u817F\u95F4\uFF0C\u5DE8\u5927\u7684\u9633\u5177\u6574\u4E2A\u94BB\u5165\u4F53\u5185\uFF0C\u5979\u54C0\u53EB\u7740\u633A\u8D77\u7389\u817F\uFF0C\u7528\u767D\u5AE9\u7684\u7389\u8DB3\u4F7F\u52B2\u8E6C\u7740\u6155\u5BB9\u9F99\u7684\u80F8\u819B\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u9F13\u771F\u6C14\uFF0C\u86F0\u4F0F\u5728\u8179\u4E0B\u7684\u89E6\u624B\u7ACB\u523B\u626C\u8D77\uFF0C\u626F\u4F4F\u9759\u989C\u5A07\u67D4\u7684\u82B1\u74E3\u6495\u5230\u6700\u5927\uFF0C\u7136\u540E\u5BF9\u51C6\u90A3\u7247\u6BB7\u7EA2\u7684\u871C\u8089\u72C2\u731B\u5730\u63D2\u4E86\u8FDB\u53BB\u3002\u8FD9\u4E00\u4E0B\u6BD4\u521A\u624D\u8FDB\u5F97\u66F4\u6DF1\uFF0C\u9759\u989C\u53EA\u89C9\u82B1\u5FC3\u88AB\u649E\u5F97\u6ED1\u5230\u4E00\u65C1\uFF0C\u8FDE\u5B50\u5BAB\u90FD\u88AB\u8FD9\u5DE8\u5927\u7684\u51B2\u51FB\u649E\u5F97\u79FB\u4F4D\uFF0C\u72ED\u7D27\u7684\u8089\u7A74\u51E0\u4E4E\u88AB\u5DE8\u9633\u6491\u788E\uFF0C\u4E00\u80A1\u6495\u88C2\u7684\u5267\u75DB\u4ECE\u8179\u8154\u4F20\u6765\uFF0C\u75DB\u5F97\u5979\u4E24\u773C\u53D1\u9ED1\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u597D\u5AE9\u7684\u597C\u6237\uFF0C\u8FD9\u662F\u4F60\u732E\u7ED9\u6211\u7684\u796D\u54C1\u5417\uFF1F\u300D\u8BF4\u7740\u8170\u8EAB\u4E00\u6C89\u3002</p><p>\u9759\u989C\u4F4E\u5782\u7684\u53F3\u811A\u91CD\u91CD\u78B0\u5728\u5730\u4E0A\uFF0C\u5979\u300C\u554A\u5440\u300D\u5C16\u53EB\u4E00\u58F0\uFF0C\u5A07\u8EAF\u5267\u70C8\u5730\u98A4\u6296\u8D77\u6765\u3002\u811A\u638C\u5728\u5730\u4E0A\u4E00\u6491\uFF0C\u5C0F\u817F\u7684\u65AD\u9AA8\u7ACB\u523B\u4EA4\u9519\u7740\u9876\u5728\u4E00\u8D77\uFF0C\u75DB\u5F97\u5979\u6D51\u8EAB\u76F4\u5192\u51B7\u6C57\u3002</p><p>\u6155\u5BB9\u9F99\u538B\u7740\u5979\u7684\u53F3\u817F\uFF0C\u62B1\u7740\u5979\u7684\u96EA\u81C0\u6765\u56DE\u62BD\u9001\uFF0C\u65AD\u88C2\u7684\u817F\u9AA8\u78E8\u64E6\u7740\u683C\u683C\u4F5C\u54CD\uFF0C\u51E0\u4E4E\u4F7F\u9759\u989C\u75DB\u5F97\u6655\u5012\u3002\u65E0\u6BD4\u7684\u75DB\u695A\u4F7F\u5979\u6D51\u8EAB\u6536\u7D27\uFF0C\u672C\u5C31\u72ED\u7A84\u7684\u8089\u7A74\u6108\u53D1\u7D27\u5BC6\uFF0C\u5C31\u50CF\u4E00\u53EA\u6ED1\u8F6F\u67D4\u97E7\u7684\u8089\u5957\u88F9\u5728\u8089\u68D2\u4E0A\uFF0C\u4F7F\u6155\u5BB9\u9F99\u62BD\u9001\u95F4\u5FEB\u611F\u500D\u589E\u3002</p><p>\u8FD9\u6837\u7684\u5F3A\u66B4\u5BF9\u9759\u989C\u6765\u8BF4\u5E76\u4E0D\u964C\u751F\u3002\u88AB\u67F3\u9E23\u6B67\u72CE\u73A9\u7684\u90A3\u6BB5\u65E5\u5B50\uFF0C\u5979\u4E5F\u906D\u53D7\u8FC7\u76F8\u4F3C\u7684\u8FB1\u8650\uFF0C\u4F46\u6CA1\u6709\u4E00\u6B21\u5982\u6B64\u75DB\u82E6\u3002\u67F3\u9E23\u6B67\u53EA\u662F\u4EBA\u7C97\u66B4\u7684\u79BD\u517D\uFF0C\u800C\u6155\u5BB9\u9F99\u4E0D\u662F\u3002\u4ED6\u50CF\u4E00\u4E2A\u6B8B\u5FCD\u7684\u730E\u624B\uFF0C\u7528\u7CBE\u7EC6\u800C\u53C8\u51C6\u786E\u7684\u52A8\u4F5C\uFF0C\u6063\u610F\u8E42\u8E8F\u7740\u81EA\u5DF1\u7684\u730E\u7269\u3002\u4ED6\u7684\u6BCF\u4E00\u4E2A\u52A8\u4F5C\u90FD\u4F7F\u5979\u6700\u5927\u9650\u5EA6\u7684\u5F97\u5230\u75DB\u82E6\uFF0C\u5DE7\u5999\u7684\u5C31\u50CF\u4E00\u4E2A\u9B54\u9B3C\u3002</p><p>\u75DB\u82E6\u8D85\u8FC7\u4E86\u9759\u989C\u6240\u80FD\u627F\u53D7\u7684\u6781\u9650\uFF0C\u5979\u6323\u624E\u3001\u6D41\u6CEA\u3001\u54ED\u53EB\u2026\u2026\u50CF\u4E00\u4E2A\u6B63\u5E38\u5C11\u5973\u822C\uFF0C\u5728\u4EC7\u4EBA\u7684\u66B4\u8650\u4E2D\u8F6F\u5F31\u7684\u51C4\u7136\u54C0\u568E\u3002</p><p>\u5979\u4ECE\u6765\u6CA1\u6709\u50CF\u8FD9\u6837\u75DB\u6068\u8FC7\u81EA\u5DF1\u7684\u8EAB\u4F53\u3002\u90A3\u4E9B\u8D39\u5C3D\u5FC3\u601D\u624D\u5F97\u5230\u7684\u5973\u6027\u7279\u5F81\uFF0C\u6210\u4E3A\u88AB\u4EBA\u65BD\u66B4\u7684\u6700\u4F73\u9009\u62E9\u3002\u5DE6\u4E73\u4F3C\u4E4E\u88AB\u94F6\u9488\u6405\u6210\u4E00\u56E2\u788E\u8089\uFF0C\u9971\u80C0\u4E73\u7403\u80BF\u5F97\u53D1\u4EAE\uFF0C\u6447\u52A8\u4E2D\u4F3C\u4E4E\u968F\u65F6\u90FD\u4F1A\u70B8\u88C2\uFF0C\u8FF8\u51FA\u6D46\u6D41\u822C\u7684\u8840\u8089\u3002\u7389\u6237\u88AB\u5DE8\u9633\u6345\u5F04\u5F97\u80BF\u6210\u4E00\u56E2\uFF0C\u5E03\u6EE1\u8089\u523A\u7684\u8089\u7624\u8C61\u62F3\u5934\u4E00\u6837\u5728\u4F53\u5185\u6405\u52A8\uFF0C\u90A3\u4E9B\u89E6\u624B\u65E0\u5B54\u4E0D\u5165\uFF0C\u4E0D\u4EC5\u94BB\u5165\u8089\u7A74\uFF0C\u751A\u81F3\u8FD8\u63D2\u8FDB\u5979\u7A7A\u7A7A\u5982\u4E5F\u7684\u5C3F\u9053\uFF0C\u628A\u5BC6\u95ED\u7684\u8089\u5B54\u5B8C\u5168\u6345\u5F00\u3002</p><p>\u9759\u989C\u53CC\u624B\u5782\u5728\u8EAB\u4E0B\uFF0C\u88AB\u8863\u886B\u7F20\u5728\u4E00\u8D77\uFF0C\u4E00\u6761\u7389\u817F\u88AB\u6155\u5BB9\u9F99\u625B\u5728\u80A9\u5934\uFF0C\u767D\u5AE9\u7684\u7389\u8DB3\u5728\u4ED6\u80A9\u540E\u4E00\u7FD8\u4E00\u7FD8\u3002\u53E6\u4E00\u6761\u817F\u652F\u5728\u5730\u4E0A\uFF0C\u5C0F\u817F\u5F2F\u6298\u5904\u4E00\u7247\u6DE4\u9752\u3002\u7CBE\u5FC3\u68B3\u7406\u7684\u53D1\u9AFB\u62AB\u6563\u5F00\u6765\uFF0C\u8033\u5782\u4E0A\u7684\u660E\u73E0\u5F77\u4F5B\u4E24\u9897\u7855\u5927\u7684\u6CEA\u6EF4\uFF0C\u5728\u7C89\u816E\u4E0A\u6447\u8361\u3002</p><p>\u300C\u4E0D\u8981\u2026\u2026\u300D\u9759\u989C\u51C4\u58F0\u54C0\u6C42\u9053\uFF0C\u73E0\u6CEA\u7EB7\u7136\u800C\u843D\u3002\u6B64\u65F6\u5979\u4E0D\u518D\u662F\u90A3\u4E2A\u4E3A\u4EC7\u6068\u800C\u6D3B\u7684\u590D\u4EC7\u8005\uFF0C\u800C\u662F\u4E00\u4E2A\u5728\u6076\u9B54\u6467\u6B8B\u4E0B\u6218\u6817\u7684\u5C11\u5973\u3002\u5979\u54C0\u6C42\u7740\u81EA\u5DF1\u7684\u4EC7\u4EBA\uFF0C\u54C0\u6C42\u4ED6\u4E0D\u8981\u518D\u6298\u78E8\u81EA\u5DF1\u67D4\u5AE9\u7684\u5668\u5B98\u3002\u5979\u66FE\u4EE5\u4E3A\u90A3\u662F\u5979\u590D\u4EC7\u7684\u5668\u5177\uFF0C\u6B64\u65F6\u624D\u77E5\u9053\uFF0C\u8FD9\u7F8E\u5999\u7684\u8089\u4F53\u53EA\u4F1A\u7ED9\u4EC7\u4EBA\u6B22\u6109\uFF0C\u7559\u7ED9\u81EA\u5DF1\u7684\uFF0C\u552F\u6709\u5C48\u8FB1\u548C\u75DB\u82E6\u3002</p><p>\u767D\u5AE9\u7684\u7389\u4F53\u5B9B\u5982\u98D8\u843D\u7684\u82B1\u74E3\u6389\u5728\u53F0\u4E0A\u3002\u9759\u989C\u5408\u7D27\u53CC\u817F\uFF0C\u4E00\u624B\u63A9\u5728\u8179\u4E0B\uFF0C\u75DB\u82E6\u5730\u626D\u52A8\u7740\u8EAB\u5B50\u3002\u6155\u5BB9\u9F99\u4F3C\u7B11\u975E\u7B11\u5730\u671B\u7740\u5979\uFF0C\u8BF4\u9053\uFF1A\u300C\u722C\u8D77\u6765\uFF0C\u4F60\u77E5\u9053\u4E00\u6761\u6BCD\u72D7\u8BE5\u600E\u4E48\u505A\u7684\u3002\u300D</p><p>\u9759\u989C\u62D6\u7740\u4F24\u817F\uFF0C\u6323\u624E\u7740\u722C\u8D77\u6765\uFF0C\u8DEA\u5728\u5730\u4E0A\uFF0C\u7136\u540E\u7528\u7EDE\u5728\u4E00\u8D77\u7684\u53CC\u624B\uFF0C\u98A4\u6296\u7740\u5C06\u957F\u88D9\u62C9\u5230\u8170\u4E0A\uFF0C\u9732\u51FA\u96EA\u5AE9\u7684\u5C41\u80A1\uFF0C\u7136\u540E\u63B0\u5F00\u81C0\u8089\uFF0C\u5C06\u7EA2\u80BF\u7684\u8089\u7A74\u5C55\u73B0\u5728\u6155\u5BB9\u9F99\u9762\u524D\u3002\u5979\u8BB8\u591A\u6B21\u5728\u4E0D\u540C\u7684\u7537\u4EBA\u9762\u524D\u8FD9\u6837\u505A\u8FC7\uFF0C\u5374\u6CA1\u6709\u4E00\u6B21\u5982\u6B64\u5C48\u8FB1\u3002\u88AB\u4EBA\u5F3A\u8FEB\u7740\uFF0C\u4E3B\u52A8\u6446\u51FA\u96CC\u4F0F\u7684\u59FF\u52BF\uFF0C\u8BA9\u4EC7\u4EBA\u4EAB\u7528\u81EA\u5DF1\u7684\u8089\u4F53\u2026\u2026</p><p>\u300C\u6C42\u4E3B\u4EBA\u4EAB\u7528\u5974\u5A62\u7684\u8D31\u5C44\u2026\u2026\u300D\u9759\u989C\u98A4\u58F0\u8BF4\u9053\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u6740\u4F60\u7236\u4EB2\uFF0C\u5978\u4F60\u5A18\u4EB2\u7684\u4EC7\u4EBA\u600E\u4E48\u6210\u4E86\u4E3B\u4EBA\uFF1F\u8FD8\u662F\u5BF9\u4F60\u7239\u5A18\u8BF4\u5427\uFF0C\u544A\u8BC9\u4ED6\u4EEC\u4F60\u6709\u591A\u6DEB\u8D31\u3002\u300D</p><p>\u9759\u989C\u54AC\u7740\u5507\u74E3\uFF0C\u534A\u664C\u8BF4\u9053\uFF1A\u300C\u7239\u2026\u5A18\u2026\u2026\u5B69\u513F\u6485\u7740\u5C41\u80A1\u2026\u2026\u554A\u2026\u2026\u300D\u5979\u62E7\u7D27\u7709\u5934\uFF0C\u5FCD\u53D7\u7740\u5DE8\u7269\u6345\u5165\u7684\u75DB\u695A\uFF0C\u300C\u2026\u2026\u88AB\u6740\u4E86\u4F60\u4EEC\u7684\u4EC7\u4EBA\u2026\u2026\u731B\u5E72\u5B69\u513F\u7684\u8D31\u5C44\u2026\u2026\u300D</p><p>\u75DB\u82E6\u8C61\u96E8\u540E\u7684\u6625\u8349\uFF0C\u4E00\u5C42\u5C42\u8513\u5EF6\u5F00\u6765\u3002\u9759\u989C\u5979\u52C9\u5F3A\u8131\u51FA\u4E00\u53EA\u7389\u624B\uFF0C\u6491\u7740\u6728\u53F0\uFF0C\u5B9B\u5982\u4E00\u53EA\u51C4\u7F8E\u7684\u767D\u9E3D\uFF0C\u655B\u8D77\u7EB7\u4E71\u7684\u7FBD\u7FFC\u4F0F\u5728\u5730\u4E0A\uFF0C\u8840\u6DCB\u6DCB\u7684\u4E73\u7403\u6241\u6241\u538B\u5728\u8EAB\u4E0B\uFF0C\u6EF4\u8840\u7684\u4E73\u5934\u51E0\u4E4E\u5D4C\u5165\u6728\u677F\u7684\u7F1D\u9699\u3002\u5706\u81C0\u9AD8\u9AD8\u7FD8\u8D77\uFF0C\u655E\u5F00\u9C9C\u7F8E\u7684\u8089\u4F53\uFF0C\u88AB\u6155\u5BB9\u9F99\u5E72\u5F97\u6B7B\u53BB\u6D3B\u6765\u3002</p><p>\u5979\u60B2\u54C0\u5730\u53D1\u73B0\uFF0C\u5973\u6027\u7684\u8EAB\u4F53\u7ADF\u7136\u5982\u6B64\u6DEB\u8D31\uFF0C\u5728\u7537\u4EBA\u8086\u610F\u6DEB\u8FB1\u4E0B\uFF0C\u4E0D\u4EC5\u6BEB\u65E0\u53CD\u6297\u4E4B\u529B\uFF0C\u800C\u4E14\u8FD8\u8C26\u5351\u5730\u6C81\u51FA\u871C\u6DB2\uFF0C\u5C06\u4ED6\u4EEC\u6240\u4F7F\u7528\u7684\u8089\u7A74\u53D8\u5F97\u6E7F\u6ED1\uFF0C\u597D\u8BA9\u7537\u4EBA\u7684\u62BD\u9001\u6108\u53D1\u5FEB\u610F\u2026\u2026</p><p>\u75BC\u75DB\u6709\u589E\u65E0\u51CF\uFF0C\u4F7F\u5979\u500D\u611F\u5C48\u8FB1\u7684\u662F\uFF1A\u86F0\u4F0F\u5728\u8089\u4F53\u6DF1\u5904\u7684\u5FEB\u611F\u6697\u4E2D\u6084\u7136\u6ECB\u751F\u3002</p><p>\u63D2\u5728\u4F53\u5185\u7684\u9633\u5177\u660E\u663E\u611F\u89C9\u5230\u4E86\u5979\u7684\u803B\u6001\uFF0C\u633A\u5F04\u5F97\u66F4\u52A0\u8086\u65E0\u5FCC\u60EE\u3002</p><p>\u8FD9\u662F\u9759\u989C\u7B2C\u4E00\u6B21\u5B8C\u5168\u4F5C\u4E3A\u5973\u4EBA\u6765\u63A5\u53D7\u7537\u4EBA\u7684\u63D2\u5165\uFF0C\u53EF\u4EE5\u8BF4\uFF0C\u8FD9\u662F\u5979\u7684\u521D\u591C\uFF0C\u88AB\u4EC7\u4EBA\u593A\u8D70\u7684\u521D\u591C\u3002\u5979\u8FD8\u6CA1\u6709\u6765\u5F97\u53CA\u4F53\u4F1A\u81EA\u5DF1\u8EAB\u4F53\u7684\u5965\u79D8\uFF0C\u5C31\u88AB\u52A8\u5730\u5C1D\u5230\u4E86\u4E00\u4E2A\u5973\u4EBA\u7684\u75DB\u82E6\uFF0C\u8FD8\u6709\u803B\u8FB1\u7684\u5FEB\u611F\u3002\u5979\u5728\u75BC\u75DB\u4E2D\u547B\u541F\u51FA\u6765\uFF0C\u5C31\u50CF\u4E00\u53EA\u53D1\u60C5\u7684\u6BCD\u72D7\uFF0C\u5728\u5C60\u5200\u4E0B\u75DB\u82E6\u5730\u6D6A\u53EB\u7740\u3002</p><p>\u6BD4\u8D77\u6155\u5BB9\u9F99\u72C2\u731B\u800C\u53C8\u6DEB\u90AA\u7684\u9633\u5177\uFF0C\u5979\u8DB3\u4EE5\u4F7F\u6674\u96EA\u548C\u592D\u592D\u5931\u795E\u7684\u6280\u5DE7\u4E0D\u8FC7\u662F\u4E00\u4E2A\u7A1A\u5AE9\u7684\u5B69\u5B50\u3002\u5979\u8FD9\u624D\u660E\u767D\uFF0C\u4E3A\u4F55\u8427\u4F5B\u5974\u7684\u5C41\u773C\u4F1A\u6709\u90A3\u4E48\u60CA\u4EBA\u7684\u627F\u53D7\u529B\uFF0C\u4E5F\u610F\u8BC6\u5230\u6155\u5BB9\u9F99\u5E76\u6CA1\u6709\u523B\u610F\u53BB\u73A9\u5F04\u6674\u96EA\u3002\u9762\u5BF9\u4ED6\u65E0\u575A\u4E0D\u6467\u7684\u5DE8\u9633\uFF0C\u9759\u989C\u751A\u81F3\u6000\u7591\u5F53\u521D\u7684\u8BA1\u7B56\u662F\u5426\u80FD\u591F\u594F\u6548\u3002\u5047\u5982\u90A3\u53EA\u871C\u6854\u73B0\u5728\u653E\u5728\u81EA\u5DF1\u4F53\u5185\uFF0C\u4E0D\u662F\u88AB\u8089\u68D2\u6363\u788E\uFF0C\u5C31\u662F\u88AB\u6345\u5165\u5B50\u5BAB\u91CC\u4E86\u2026\u2026</p><p>\u300C\u554A\u2014\u2014\u300D\u9759\u989C\u6602\u8D77\u8793\u9996\uFF0C\u88AB\u5DE8\u9633\u8D2F\u7A7F\u7684\u767D\u5AE9\u5C41\u80A1\u6781\u529B\u633A\u8D77\uFF0C\u8089\u7A74\u5267\u98A4\u7740\u55B7\u51FA\u9634\u7CBE\u3002</p><p>\u6728\u53F0\u7F13\u7F13\u65CB\u8F6C\uFF0C\u559C\u6C14\u6D0B\u6D0B\u7684\u6D1E\u623F\u518D\u6B21\u8F6C\u5230\u6CB3\u7554\u3002\u4E00\u4E2A\u82B1\u74E3\u4F3C\u7684\u5C11\u5973\u4F0F\u5728\u65B0\u5A18\u6BCD\u5973\u4E4B\u95F4\uFF0C\u8863\u88D9\u51CC\u4E71\u5782\u5728\u8170\u4E0A\uFF0C\u9732\u51FA\u96EA\u6ED1\u7684\u7389\u4F53\u3002\u4E30\u6EE1\u7684\u4E73\u623F\u5F77\u4F5B\u4E00\u53EA\u88AB\u538B\u7834\u7684\u8840\u7403\uFF0C\u5C06\u80F8\u524D\u7684\u6728\u677F\u67D3\u5F97\u901A\u7EA2\uFF0C\u5979\u6485\u7740\u5C41\u80A1\uFF0C\u5A07\u5AE9\u7684\u8089\u7A74\u88AB\u4E00\u6761\u89E6\u76EE\u60CA\u5FC3\u7684\u5DE8\u9633\u51F6\u731B\u62BD\u9001\u7740\uFF0C\u6E29\u6DA6\u7684\u871C\u6DB2\u6DF7\u7740\u9C9C\u8840\u4ECE\u80A1\u95F4\u6EA2\u51FA\uFF0C\u987A\u7740\u96EA\u767D\u7684\u5927\u817F\u4E00\u8DEF\u6DCC\u4E0B\uFF0C\u7559\u4E0B\u51E0\u9053\u873F\u8712\u7684\u8840\u75D5\u3002</p><p>\u5DE8\u9633\u7684\u633A\u5165\u4F7F\u9759\u989C\u7981\u4E0D\u4F4F\u53C8\u4E00\u6B21\u6D6A\u53EB\u4E86\u51FA\u6765\uFF0C\u5979\u62AC\u8D77\u51C4\u8499\u7684\u7F8E\u76EE\uFF0C\u671D\u53F0\u4E0B\u770B\u53BB\u3002\u592D\u592D\u7684\u4EB5\u88E4\u65E9\u5DF2\u88AB\u6D41\u6C34\u51B2\u8D70\uFF0C\u767D\u751F\u751F\u7684\u4E0B\u4F53\u5728\u6C34\u9762\u8F7B\u8F7B\u98D8\u6447\uFF0C\u5A07\u67D4\u5F97\u5F77\u4F5B\u4E00\u74E3\u843D\u82B1\u3002\u9C9C\u8840\u4ECE\u4E0B\u4F53\u6E90\u6E90\u6D8C\u51FA\uFF0C\u5F77\u4F5B\u6D41\u5E72\u4E86\u5168\u8EAB\u7684\u8840\u6DB2\u3002</p><p>\u9759\u989C\u6014\u6014\u671B\u7740\u5979\uFF0C\u7136\u540E\u5782\u4E0B\u8793\u9996\uFF0C\u6728\u7136\u5A9A\u53EB\u4E00\u58F0\uFF0C\u7EE7\u7EED\u626D\u52A8\u7C89\u81C0\uFF0C\u8FCE\u5408\u7740\u8EAB\u540E\u7684\u633A\u5F04\u3002</p><p>\u6155\u5BB9\u9F99\u8F7B\u8511\u5730\u633A\u52A8\u9633\u5177\uFF0C\u5C06\u9759\u989C\u5E72\u5F97\u51C4\u53EB\u8FDE\u58F0\uFF0C\u5728\u4ED6\u7A7F\u900F\u82B1\u5FC3\u7684\u540C\u65F6\uFF0C\u9759\u989C\u54ED\u7740\u53C8\u4E00\u6B21\u8FBE\u5230\u9AD8\u6F6E\u3002\u770B\u7740\u5979\u6EA2\u8840\u7684\u871C\u7A74\u6536\u7F29\u7740\u55B7\u51FA\u7CBE\u8840\u7684\u51C4\u8273\u6DEB\u6001\uFF0C\u6155\u5BB9\u9F99\u9119\u5937\u4E2D\u4E0D\u7981\u53C8\u6709\u4E9B\u9057\u61BE\u3002\u4ED6\u66FE\u4EE5\u4E3A\u5728\u4EC7\u6068\u4E0E\u6298\u78E8\u4E2D\u957F\u5927\u7684\u5979\uFF0C\u4F1A\u662F\u53C8\u4E00\u4E2A\u81EA\u5DF1\u3002\u770B\u6765\u5979\u4E0D\u8FC7\u662F\u4E00\u4E2A\u4F4E\u8D31\u7684\u6DEB\u7269\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u7B11\u4E00\u58F0\uFF0C\u5728\u5979\u4F53\u5185\u5267\u70C8\u5730\u55B7\u5C04\u51FA\u6765\u3002\u4F3C\u4E4E\u662F\u77E5\u9053\u6298\u78E8\u5DF2\u8FD1\u5C3E\u58F0\uFF0C\u5C11\u5973\u4E3E\u8D77\u8840\u8FF9\u6591\u6591\u7684\u96EA\u81C0\uFF0C\u7528\u7D27\u82E5\u5904\u5B50\u7684\u8089\u7A74\u629A\u6170\u7740\u55B7\u53D1\u7684\u5DE8\u9633\uFF0C\u90A3\u79CD\u4E56\u5DE7\u7684\u6DEB\u8D31\u6A21\u6837\uFF0C\u5012\u8BA9\u6155\u5BB9\u9F99\u6709\u4E9B\u820D\u4E0D\u5F97\u5C31\u4E9B\u53D6\u5979\u6027\u547D\uFF0C\u53CD\u6B63\u662F\u5148\u5978\u540E\u6740\uFF0C\u591A\u5978\u51E0\u6B21\u4E5F\u662F\u4E00\u6837\u2026\u2026</p><p>\u9759\u989C\u5F13\u8D77\u8170\u80A2\uFF0C\u8BA9\u8DF3\u52A8\u7684\u8089\u68D2\u6DF1\u6DF1\u6954\u5165\u4F53\u5185\uFF0C\u5FFD\u7136\u5979\u7389\u81C2\u4E00\u626C\uFF0C\u95EA\u7535\u822C\u671D\u6155\u5BB9\u9F99\u8170\u4E0B\u6325\u53BB\u3002\u6C89\u6D78\u5728\u5C04\u7CBE\u5FEB\u611F\u7684\u6155\u5BB9\u9F99\u6765\u4E0D\u53CA\u52A8\u4F5C\uFF0C\u7C89\u62F3\u4FBF\u51C6\u786E\u5730\u843D\u5728\u8170\u773C\u4E0A\uFF0C\u6E85\u8D77\u4E00\u56E2\u8840\u82B1\u3002</p>',70);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
