import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_61" tabindex="-1"><a class="header-anchor" href="#_61" aria-hidden="true">#</a> 61</h1><p>\u6155\u5BB9\u9F99\u62B1\u8D77\u7D2B\u73AB\uFF0C\u5C06\u5979\u653E\u5728\u819D\u4E0A\uFF0C\u7814\u4E86\u4E00\u679A\u753B\u7709\u96C6\u9999\u4E38\uFF0C\u4ECE\u6848\u4E0A\u6361\u8D77\u4E00\u652F\u7709\u7B14\uFF0C\u8F7B\u8F7B\u63CF\u62B9\u7389\u4EBA\u7CBE\u81F4\u7684\u9EDB\u7709\u3002\u7D2B\u73AB\u8170\u90E8\u4EE5\u4E0B\u88AB\u4E00\u5E45\u7EA2\u7F57\u5305\u88F9\u7740\uFF0C\u88F8\u9732\u51FA\u96EA\u7389\u822C\u7684\u80F8\u4E73\uFF0C\u5979\u67D4\u987A\u5730\u4F9D\u5728\u54E5\u54E5\u638C\u4E2D\uFF0C\u7709\u68A2\u773C\u89D2\u6EE1\u662F\u751C\u751C\u7684\u7B11\u610F\u3002</p><p>\u300C\u8D8A\u6765\u8D8A\u50CF\u5A18\u4E86\u2026\u2026\u300D\u6155\u5BB9\u9F99\u8F7B\u53F9\u9053\u3002</p><p>\u8427\u4F5B\u5974\u4FA7\u8EAB\u5367\u5728\u69BB\u4E0A\uFF0C\u4E00\u6761\u767D\u5AE9\u7684\u7389\u817F\u642D\u5728\u88AB\u5916\uFF0C\u5706\u81C0\u7389\u7403\u822C\u7FD8\u8D77\uFF0C\u96EA\u817B\u4E2D\u5FAE\u5FAE\u9732\u51FA\u4E00\u62B9\u8273\u5149\u3002\u8FD9\u51E0\u65E5\u5979\u524D\u540E\u4E24\u4E2A\u8089\u7A74\u4E0D\u77E5\u88AB\u513F\u5B50\u6233\u5F04\u4E86\u591A\u5C11\u6B21\uFF0C\u865A\u5F31\u7684\u8EAB\u4F53\u65E9\u5DF2\u652F\u6491\u4E0D\u4F4F\uFF0C\u4F46\u5BB9\u989C\u5374\u76CA\u53D1\u5A07\u8273\uFF0C\u7EB5\u7136\u5728\u7761\u68A6\u4E2D\uFF0C\u8FD8\u6D41\u6EA2\u7740\u8FF7\u4EBA\u7684\u98CE\u60C5\u3002</p><p>\u7D2B\u73AB\u8212\u9002\u5730\u95ED\u4E0A\u773C\u775B\uFF0C\u68A6\u5453\u822C\u8F7B\u58F0\u8BF4\u9053\uFF1A\u300C\u597D\u597D\u753B\u554A\uFF0C\u753B\u597D\u4E86\uFF0C\u4ECA\u665A\u59B9\u59B9\u8DDF\u5A18\u4E00\u6837\u2026\u2026\u7528\u5C41\u773C\u513F\u670D\u4F8D\u54E5\u54E5\u2026\u2026\u300D</p><p>\u300C\u5514\uFF1F\u300D\u7709\u7B14\u505C\u4E86\u4E0B\u6765\uFF0C\u6155\u5BB9\u9F99\u6000\u7591\u81EA\u5DF1\u662F\u4E0D\u662F\u542C\u9519\u4E86\u3002</p><p>\u7D2B\u73AB\u5FFD\u7136\u4E00\u6311\u7709\u5934\uFF0C\u505A\u4E86\u4E2A\u9B3C\u8138\uFF0C\u683C\u683C\u7B11\u9053\uFF1A\u300C\u753B\u574F\u4E86\uFF0C\u4ECA\u665A\u54E5\u54E5\u73A9\u4E0D\u6210\u4EBA\u5BB6\u7684\u5C41\u773C\u513F\u5566\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u677F\u7740\u8138\uFF0C\u7B49\u5979\u7B11\u5B8C\u624D\u62FF\u8D77\u94DC\u955C\uFF0C\u300C\u5476\u3002\u300D</p><p>\u300C\u54A6\uFF1F\u300D\u7D2B\u73AB\u5DE6\u53F3\u7AEF\u8BE6\uFF0C\u5374\u89C1\u4E24\u9053\u5F2F\u7709\u72B9\u5982\u65B0\u6708\uFF0C\u521A\u624D\u5979\u6545\u610F\u6311\u8D77\u7709\u5CF0\uFF0C\u90A3\u652F\u7709\u7B14\u987A\u52BF\u62B9\u8FC7\uFF0C\u5374\u662F\u4E1D\u6BEB\u672A\u4E71\u3002</p><p>\u773C\u89C1\u6155\u5BB9\u9F99\u5BBD\u8863\u89E3\u5E26\uFF0C\u633A\u8D77\u5DE8\u9633\uFF0C\u7D2B\u73AB\u6025\u5FD9\u8BF4\u9053\uFF1A\u300C\u4EBA\u5BB6\u8BF4\u7684\u662F\u4ECA\u665A\u2026\u2026\u300D</p><p>\u300C\u8FD9\u4F1A\u513F\u5DF2\u7ECF\u8FC7\u4E86\u9149\u65F6\u3002\u300D\u6155\u5BB9\u9F99\u4E0D\u7531\u5206\u8BF4\u5730\u89E3\u5F00\u7EA2\u7F57\u3002</p><p>\u300C\u7B49\u4E00\u4E0B\uFF01\u4F60\u8FD8\u6CA1\u6709\u7ED9\u4EBA\u5BB6\u5316\u5B8C\u5986\u5462\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u6CA1\u5173\u7CFB\uFF0C\u54E5\u54E5\u5148\u63D2\u8FDB\u53BB\uFF0C\u518D\u6162\u6162\u7ED9\u4F60\u5316\u3002\u300D</p><p>\u7D2B\u73AB\u4F5C\u8327\u81EA\u7F1A\uFF0C\u6B64\u65F6\u907F\u65E0\u53EF\u907F\uFF0C\u53EA\u597D\u54C0\u6C42\u9053\uFF1A\u300C\u59B9\u59B9\u8BF4\u9519\u4E86\uFF0C\u597D\u54E5\u54E5\uFF0C\u4F60\u4E0D\u8981\u5F04\u4EBA\u5BB6\u540E\u9762\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u6CA1\u6709\u8131\u53BB\u8863\u7269\uFF0C\u5C31\u90A3\u4E48\u5750\u7740\uFF0C\u89E3\u4E0B\u7D2B\u73AB\u5305\u88F9\u5728\u4E0B\u4F53\u7684\u7EA2\u7F57\uFF0C\u633A\u8D77\u72F0\u72DE\u7684\u9633\u5177\uFF0C\u9876\u5728\u7D2B\u73AB\u81C0\u95F4\u3002\u7D2B\u73AB\u300C\u54CE\u5440\u300D\u4E00\u58F0\uFF0C\u62E7\u7D27\u7709\u5934\uFF0C\u7F8E\u76EE\u6CEB\u7136\u6B32\u6EF4\u3002\u6155\u5BB9\u9F99\u4E0D\u52A8\u58F0\u8272\uFF0C\u575A\u786C\u7684\u9F9F\u5934\u94BB\u5165\u81C0\u7F1D\uFF0C\u5728\u5C0F\u5DE7\u7684\u809B\u83CA\u4E0A\u9876\u4E86\u9876\uFF0C\u4FBF\u6B32\u8FDB\u5165\u3002</p><p>\u300C\u597D\u54E5\u54E5\uFF0C\u4EBA\u5BB6\u540E\u9762\u597D\u4E45\u6CA1\u7528\uFF0C\u54E5\u54E5\u8089\u68D2\u8FD9\u4E48\u7C97\uFF0C\u4F1A\u628A\u4EBA\u5BB6\u540E\u9762\u5F04\u88C2\u7684\u2026\u2026\u597D\u75BC\u2026\u2026\u300D</p><p>\u300C\u6BCF\u5929\u63D2\u51E0\u6B21\u5C31\u4E0D\u4F1A\u75BC\u4E86\u3002\u4F60\u770B\u5A18\uFF0C\u6BCF\u6B21\u5E72\u5C41\u773C\u513F\u90FD\u4F1A\u9AD8\u5174\u5F97\u6CC4\u4E86\u8EAB\u5B50\u5462\u3002\u300D\u6155\u5BB9\u9F99\u8BF4\u7740\u9876\u4F4F\u83CA\u809B\uFF0C\u7F13\u7F13\u7528\u529B\u3002</p><p>\u7D2B\u73AB\u98A6\u7709\u4E5E\u6C42\u9053\uFF1A\u300C\u8F7B\u4E00\u70B9\u2026\u2026\u4E0D\u8981\u786C\u63D2\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u7740\u9000\u51FA\u8089\u68D2\uFF0C\u5728\u7D2B\u73AB\u8EAB\u524D\u6643\u4E86\u6643\u3002\u7D2B\u73AB\u65E0\u5948\u5730\u8BF4\u9053\uFF1A\u300C\u597D\u5566\uFF0C\u4EBA\u5BB6\u5E2E\u4F60\u8214\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u628A\u7EA2\u7F57\u94FA\u5728\u5730\u4E0A\uFF0C\u62B1\u7740\u7D2B\u73AB\u653E\u5728\u4E0A\u9762\uFF0C\u7528\u817F\u7A33\u4F4F\u5979\u7684\u8EAF\u5E72\u3002\u7D2B\u73AB\u5F20\u5F00\u5C0F\u5634\uFF0C\u8F7B\u8F7B\u542B\u4F4F\u9F9F\u5934\uFF0C\u6E29\u67D4\u5730\u541E\u5410\u8D77\u6765\u3002\u5979\u7684\u53E3\u8154\u6E29\u6DA6\u5F97\u5F77\u4F5B\u4E00\u6C6A\u871C\u6CC9\uFF0C\u5507\u74E3\u67D4\u8F6F\u9999\u6696\uFF0C\u6ED1\u817B\u7684\u5C0F\u820C\u7075\u5DE7\u800C\u53C8\u7EC6\u81F4\uFF0C\u8089\u68D2\u63D2\u8FDB\u91CC\u9762\uFF0C\u5C31\u518D\u4E0D\u613F\u62D4\u51FA\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u5F53\u7D2B\u73AB\u53C8\u4E00\u6B21\u4F38\u76F4\u5589\u5499\uFF0C\u541E\u5165\u8089\u68D2\uFF0C\u7528\u5934\u7684\u8F6F\u8089\u5305\u88F9\u7740\u9F9F\u5934\u65F6\uFF0C\u6155\u5BB9\u9F99\u60C5\u4E0D\u81EA\u7981\u5730\u55B7\u53D1\u8D77\u6765\u3002\u7D2B\u73AB\u7D27\u7D27\u542B\u4F4F\u8089\u68D2\uFF0C\u4EFB\u7531\u5B83\u5728\u81EA\u5DF1\u53E3\u8154\u5185\u5C3D\u60C5\u55B7\u5C04\u3002\u7B49\u8089\u68D2\u7684\u9707\u98A4\u505C\u606F\uFF0C\u7D2B\u73AB\u7528\u7EA2\u5507\u88F9\u4F4F\u68D2\u8EAB\uFF0C\u5C0F\u5FC3\u5730\u5410\u51FA\u9633\u5177\uFF0C\u7136\u540E\u626D\u8FC7\u67D4\u9888\u3002</p><p>\u300C\u4E0D\u8BB8\u5410\u3002\u300D</p><p>\u7D2B\u73AB\u4E0D\u60C5\u613F\u5730\u76B1\u8D77\u9F3B\u5B50\uFF0C\u6700\u540E\u8FD8\u4E56\u4E56\u54BD\u4E0B\u7CBE\u6DB2\u3002\u6155\u5BB9\u9F99\u5C06\u5979\u62B1\u5230\u6000\u4E2D\uFF0C\u659F\u4E86\u676F\u9999\u9732\u5582\u5979\u559D\u4E0B\u3002\u7D2B\u73AB\u4E00\u5C0F\u53E3\u4E00\u5C0F\u53E3\u559D\u7740\uFF0C\u5C06\u53E3\u4E2D\u7684\u7CBE\u6DB2\u5473\u9053\u51B2\u6DE1\u3002\u559D\u5B8C\u540E\uFF0C\u5979\u626D\u52A8\u8170\u80A2\uFF0C\u6B63\u5F85\u5F00\u53E3\uFF0C\u81C0\u4E0B\u5374\u78B0\u5230\u4E00\u4E2A\u786C\u786C\u7684\u7269\u4F53\u3002\u7D2B\u73AB\u4F4E\u5934\u4E00\u770B\uFF0C\u987F\u65F6\u82E6\u8D77\u5C0F\u8138\uFF0C\u300C\u4F60\u521A\u521A\u5728\u4EBA\u5BB6\u5634\u91CC\u5C04\u8FC7\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5475\u54C4\u9053\uFF1A\u300C\u5B83\u8FD8\u60F3\u5728\u4F60\u5C41\u5C41\u91CC\u5C04\u4E00\u6B21\u554A\uFF0C\u522B\u6015\uFF0C\u54E5\u54E5\u4F1A\u5F88\u8F7B\u7684\u2026\u2026\u300D</p><p>\u6CBE\u6EE1\u553E\u6DB2\u7684\u8089\u68D2\u7B14\u76F4\u7AD6\u8D77\uFF0C\u4E1D\u6BEB\u6CA1\u6709\u8F6F\u5316\u7684\u8FF9\u8C61\u3002\u6155\u5BB9\u9F99\u5C06\u7D2B\u73AB\u9760\u5728\u8EAB\u4E0A\uFF0C\u4E24\u624B\u62B1\u4F4F\u5979\u7684\u81C0\u7403\uFF0C\u5C06\u6ED1\u817B\u7684\u96EA\u81C0\u8F7B\u8F7B\u5206\u5F00\uFF0C\u7136\u540E\u63E1\u4F4F\u5979\u7EA4\u8F6F\u7684\u8170\u80A2\uFF0C\u65CB\u8F6C\u7740\u671D\u4E0B\u5750\u53BB\u3002</p><p>\u5149\u6D01\u7684\u7389\u4F53\u5728\u4ED6\u624B\u4E2D\u67D4\u67D4\u8F6C\u52A8\uFF0C\u5A07\u5AE9\u7684\u83CA\u809B\u88AB\u9633\u5177\u7814\u78E8\u7740\u7F13\u7F13\u5F20\u5F00\u3002\u7D2B\u73AB\u4F0F\u5728\u6155\u5BB9\u9F99\u80A9\u5934\uFF0C\u54AC\u4F4F\u5507\u74E3\u4E1D\u4E1D\u5438\u7740\u51C9\u6C14\uFF0C\u4E24\u56E2\u80A5\u8F6F\u9999\u5AE9\u7684\u7855\u4E73\u7D27\u7EF7\u7EF7\u5E76\u5728\u80F8\u524D\u3002</p><p>\u5FFD\u7136\u95F4\uFF0C\u96EA\u6ED1\u7684\u5706\u81C0\u5411\u4E0B\u4E00\u6C89\uFF0C\u67D4\u5AE9\u7684\u809B\u6D1E\u5DF2\u7ECF\u541E\u6CA1\u4E86\u9F9F\u5934\u3002</p><p>\u7C97\u957F\u7684\u8089\u68D2\u7F13\u7F13\u6324\u5165\u83CA\u809B\uFF0C\u5C06\u5C0F\u5DE7\u7684\u5C41\u773C\u513F\u6491\u5F97\u6D51\u5706\u3002\u7D2B\u73AB\u6700\u662F\u6015\u75DB\uFF0C\u9976\u662F\u4EE5\u524D\u88AB\u4ED6\u5E72\u8FC7\u591A\u6B21\uFF0C\u7B49\u9633\u5177\u5B8C\u5168\u8FDB\u5165\u96EA\u81C0\uFF0C\u5979\u4E5F\u75DB\u5F97\u6CEA\u773C\u5A46\u5A11\u3002\u6155\u5BB9\u9F99\u543B\u4F4F\u5979\u7684\u8033\u5782\uFF0C\u4E00\u8FB9\u8F7B\u8F7B\u566C\u54AC\uFF0C\u4E00\u8FB9\u67D4\u58F0\u9053\uFF1A\u300C\u597D\u4E9B\u4E86\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u6447\u4E86\u6447\u5934\uFF0C\u826F\u4E45\u53C8\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u767D\u751F\u751F\u7684\u8089\u6BB5\u7AD6\u5728\u6155\u5BB9\u9F99\u8EAB\u524D\uFF0C\u5C31\u50CF\u5750\u5728\u4ED6\u817F\u4E0A\u4E00\u6837\u3002\u81C0\u8089\u88AB\u6324\u5F97\u5206\u5F00\uFF0C\u4E2D\u95F4\u63D2\u7740\u4E00\u6839\u5996\u5F02\u7684\u9633\u5177\u3002\u6155\u5BB9\u9F99\u677E\u5F00\u624B\uFF0C\u7D2B\u73AB\u7389\u4F53\u4E00\u6ED1\uFF0C\u53C8\u7A33\u4F4F\u4E86\u3002\u6DF1\u5165\u80A0\u9053\u7684\u8089\u68D2\u94C1\u94F8\u822C\u575A\u786C\uFF0C\u5F77\u4F5B\u4E00\u6839\u53C8\u7C97\u53C8\u957F\u7684\u6728\u6954\uFF0C\u5C06\u7D2B\u73AB\u7684\u8EAF\u5E72\u7262\u7262\u9489\u5728\u80EF\u95F4\uFF0C\u5B8C\u5168\u5360\u6709\u4E86\u5979\u8089\u4F53\u7684\u7A7A\u95F4\u3002</p><p>\u6155\u5BB9\u9F99\u6402\u7740\u5979\u5982\u96EA\u7684\u5A07\u8EAF\uFF0C\u8EAB\u5B50\u524D\u503E\uFF0C\u7D2B\u73AB\u987A\u52BF\u5411\u540E\u5012\u53BB\uFF0C\u6155\u5BB9\u9F99\u677E\u5F00\u624B\uFF0C\u4E0D\u7528\u4EBA\u6276\uFF0C\u4FBF\u88AB\u8089\u68D2\u659C\u659C\u6311\u4F4F\u3002</p><p>\u6155\u5BB9\u9F99\u6253\u5F00\u5986\u53F0\u4E0A\u7684\u80ED\u8102\u6C34\u7C89\uFF0C\u8F7B\u8F7B\u6311\u4E86\u5C11\u8BB8\uFF0C\u4ED4\u7EC6\u6D82\u5728\u7D2B\u73AB\u7684\u5507\u74E3\u4E0A\u3002\u8FD9\u662F\u4E0A\u597D\u7684\u7EA2\u84DD\u82B1\u80ED\u8102\uFF0C\u6155\u5BB9\u9F99\u6307\u5C16\u62B9\u8FC7\uFF0C\u7D2B\u73AB\u56E0\u75DB\u695A\u800C\u5931\u8272\u7684\u7389\u8138\u5239\u90A3\u95F4\u8273\u5149\u56DB\u5C04\uFF0C\u7F8E\u5F97\u7B80\u76F4\u4E0D\u4F3C\u771F\u4EBA\u3002\u6155\u5BB9\u9F99\u6258\u8D77\u5979\u7684\u4E73\u7403\uFF0C\u5C06\u8CF8\u4F59\u7684\u80ED\u8102\u6D82\u5728\u4E73\u5C16\u4E0A\uFF0C\u90A3\u5BF9\u4E73\u5934\u987F\u65F6\u6CDB\u8D77\u5B9D\u77F3\u822C\u7684\u5149\u6CFD\uFF0C\u886C\u7740\u5982\u96EA\u7684\u9999\u8EAF\uFF0C\u6108\u53D1\u593A\u76EE\u3002</p><p>\u7D2B\u73AB\u7684\u795E\u60C5\u4F3C\u75DB\u4F3C\u559C\uFF0C\u6C34\u6C6A\u6C6A\u7684\u7F8E\u76EE\u6CE2\u5149\u6D41\u8F6C\uFF0C\u5A07\u5A9A\u65E0\u4FE6\u3002\u6155\u5BB9\u9F99\u8D8A\u770B\u8D8A\u7231\uFF0C\u63D2\u5728\u809B\u5185\u7684\u8089\u68D2\u786C\u5F97\u50CF\u8981\u7206\u88C2\u4E00\u822C\u3002\u4ED6\u6276\u7740\u7D2B\u73AB\u80A9\u5934\uFF0C\u8F7B\u8F7B\u4E00\u8F6C\uFF0C\u7C89\u5AE9\u7684\u96EA\u81C0\u5305\u88F9\u7740\u9633\u5177\uFF0C\u50CF\u4E00\u56E2\u6ED1\u817B\u7684\u6CB9\u8102\u5728\u8179\u4E0A\u8F7B\u65CB\u3002\u7D27\u5BC6\u6E29\u6696\u7684\u80A0\u58C1\u7ED9\u6DF1\u9677\u5176\u4E2D\u7684\u8089\u68D2\u5E26\u6765\u5F02\u6837\u7684\u78E8\u64E6\u611F\uFF0C\u5AE9\u8089\u987A\u7740\u4E00\u4E2A\u65B9\u5411\u6ED1\u8FC7\uFF0C\u5F77\u4F5B\u4E00\u6B21\u6CA1\u6709\u5C3D\u5934\uFF0C\u4E5F\u4E0D\u4F1A\u505C\u6B47\u7684\u63D2\u5165\u3002</p><p>\u7D2B\u73AB\u9760\u5728\u6155\u5BB9\u9F99\u80F8\u819B\u4E0A\uFF0C\u6C89\u7A33\u7684\u5FC3\u8DF3\u900F\u8FC7\u7C89\u80CC\uFF0C\u4E00\u76F4\u4F20\u5230\u80F8\u524D\u3002\u90A3\u4E24\u7C92\u6BB7\u7EA2\u7684\u4E73\u5934\u968F\u7740\u4ED6\u7684\u5FC3\u8DF3\uFF0C\u4E00\u98A4\u4E00\u98A4\uFF0C\u5F77\u4F5B\u6574\u4E2A\u4EBA\u90FD\u878D\u5316\u5728\u4ED6\u6000\u4E2D\u3002</p><p>\u300C\u597D\u559C\u6B22\u8FD9\u6837\u5B50\u2026\u2026\u300D\u7D2B\u73AB\u6795\u5728\u6155\u5BB9\u9F99\u80A9\u5934\u8F7B\u58F0\u8BF4\u9053\uFF1A\u300C\u559C\u6B22\u54E5\u54E5\u7684\u5927\u8089\u68D2\uFF0C\u63D2\u5728\u4EBA\u5BB6\u8EAB\u4F53\u91CC\u9762\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u62E5\u7740\u5979\u82AC\u82B3\u7684\u7389\u4F53\uFF0C\u4E24\u624B\u63E1\u4F4F\u7D2B\u73AB\u6ED1\u8F6F\u7684\u4E73\u5C16\uFF0C\u547C\u5438\u7740\u5979\u7684\u53D1\u9999\uFF0C\u53EA\u5FAE\u5FAE\u70B9\u4E86\u70B9\u5934\uFF0C\u4EC0\u4E48\u90FD\u6CA1\u6709\u8BF4\u3002\u7D2B\u73AB\u773C\u775B\u5374\u4EAE\u4E86\u8D77\u6765\uFF0C\u300C\u771F\u7684\u5417\uFF1F\u300D\u63A5\u7740\u53C8\u62C5\u8D77\u5FC3\u6765\uFF0C\u300C\u51B2\u513F\u90A3\u4E48\u5C0F\u3002\u300D</p><p>\u300C\u4E0D\u7528\u7BA1\u4ED6\u4EEC\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u60F3\u4E86\u60F3\uFF0C\u53C8\u95EE\u9053\uFF1A\u300C\u54E5\u54E5\u820D\u5F97\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u90A3\u4E9B\u4E8B\u3002\u5F88\u65E0\u804A\u3002\u300D</p><p>\u300C\u8FD8\u6709\u5A18\u554A\u3002\u300D</p><p>\u300C\u5F53\u7136\u3002\u6211\u4EEC\u4E00\u8D77\u3002\u300D</p><p>\u7D2B\u73AB\u626C\u8D77\u8138\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u54E5\u54E5\uFF0C\u6765\u5E72\u4EBA\u5BB6\u7684\u5C41\u773C\u513F\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u52A8\u4E86\u52A8\u8089\u68D2\uFF0C\u300C\u4E0D\u6015\u75BC\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u5AE9\u809B\u67D4\u67D4\u6536\u7D27\uFF0C\u8FCE\u5408\u7740\u4ED6\u7684\u52A8\u4F5C\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u4EBA\u5BB6\u597D\u5F00\u5FC3\u2026\u2026\u54E5\u54E5\u60F3\u600E\u4E48\u73A9\uFF0C\u59B9\u59B9\u90FD\u4F9D\u4F60\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5C06\u7389\u4EBA\u4FEF\u8EAB\u653E\u5728\u69BB\u4E0A\uFF0C\u6367\u7740\u5979\u7684\u96EA\u81C0\uFF0C\u8F7B\u67D4\u5730\u62BD\u9001\u8D77\u6765\u3002\u7D2B\u73AB\u77ED\u77ED\u7684\u8EAB\u4F53\u8C61\u732B\u54AA\u4E00\u6837\u8737\u5728\u4ED6\u8EAB\u4E0B\uFF0C\u633A\u7740\u5706\u81C0\uFF0C\u8BA9\u90A3\u6839\u72DE\u5389\uFF0C\u800C\u53C8\u67D4\u60C5\u4F3C\u6C34\u7684\u8089\u68D2\uFF0C\u6DF1\u6DF1\u6954\u5165\u4F53\u5185\u3002</p><p>\u7565\u5FAE\u7684\u75DB\u695A\u548C\u5E9E\u5927\u7684\u80C0\u8FEB\u611F\uFF0C\u4F7F\u7D2B\u73AB\u95ED\u7740\u773C\uFF0C\u5C0F\u58F0\u53EB\u4E86\u8D77\u6765\u3002\u5979\u653E\u677E\u809B\u808C\uFF0C\u5F85\u7A84\u7D27\u7684\u809B\u6D1E\u9002\u5E94\u4E86\u8089\u68D2\u7684\u7C97\u957F\uFF0C\u7D2B\u73AB\u4FA7\u8FC7\u8138\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u53EF\u4EE5\u7528\u4E00\u70B9\u529B\u2026\u2026\u300D</p><p>\u7741\u5F00\u773C\u775B\uFF0C\u7D2B\u73AB\u8138\u4E00\u4E0B\u5B50\u7EA2\u4E86\uFF0C\u8427\u4F5B\u5974\u4E0D\u77E5\u4F55\u65F6\u9192\u6765\uFF0C\u6B63\u7B11\u541F\u541F\u671B\u7740\u5979\u3002\u770B\u5230\u5A18\u4EB2\u773C\u4E2D\u7684\u7B11\u610F\uFF0C\u7D2B\u73AB\u7F9E\u4E0D\u53EF\u652F\u5730\u57CB\u4E0B\u5934\u3002\u809B\u91CC\u7684\u8089\u68D2\u7A81\u7136\u7528\u529B\uFF0C\u867D\u7136\u77E5\u9053\u4ED6\u662F\u6545\u610F\u7684\uFF0C\u7D2B\u73AB\u8FD8\u662F\u88AB\u54E5\u54E5\u63D2\u5F97\u53EB\u4E86\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u521A\u89E3\u5F00\u8863\u670D\uFF0C\u8427\u4F5B\u5974\u5C31\u60CA\u53EB\u9053\uFF1A\u300C\u9F99\u54E5\u54E5\uFF0C\u4F60\u600E\u4E48\u53D7\u4F24\u4E86\uFF1F\u300D</p><p>\u7D2B\u73AB\u52C9\u5F3A\u626D\u8FC7\u67D4\u9888\uFF0C\u53EA\u89C1\u6155\u5BB9\u9F99\u8170\u4FA7\u88AB\u9510\u7269\u523A\u51FA\u4E00\u4E2A\u7EC6\u7EC6\u7684\u4F24\u53E3\uFF0C\u9C9C\u8840\u5DF2\u7ECF\u51DD\u56FA\u3002</p><p>\u300C\u5979\u6B7B\u4E86\u5417\uFF1F\u300D\u8FD9\u91CC\u53EA\u6709\u4E00\u4E2A\u4EBA\u6562\u5411\u4ED6\u51FA\u624B\uFF0C\u7D2B\u73AB\u5947\u602A\u7684\u662F\u4ED6\u4E3A\u4EC0\u4E48\u4F1A\u53D7\u4F24\u3002</p><p>\u300C\u6CA1\u6709\u3002\u300D</p><p>\u300C\u2026\u2026\u4F60\u77E5\u9053\u5979\u662F\u8C01\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u5507\u89D2\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u300C\u5F88\u5014\u5F3A\u7684\u5B69\u5B50\u3002\u300D\u4ED6\u4EEC\u90FD\u8BB0\u5F97\u90A3\u6839\u88AB\u54AC\u65AD\u7684\u6728\u6869\u3002</p><p>\u7D2B\u73AB\u671B\u7740\u4ED6\u7684\u773C\u775B\u7F13\u7F13\u8BF4\u9053\uFF1A\u300C\u5979\u60F3\u5A36\u6674\u6674\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u6014\uFF0C\u65CB\u5373\u660E\u767D\u8FC7\u6765\uFF0C\u6293\u8D77\u4E00\u53EA\u7389\u676F\u63BC\u5F97\u7C89\u788E\u3002</p><p>\u7D2B\u73AB\u7B49\u4E86\u7247\u523B\uFF0C\u8BF4\u9053\uFF1A\u300C\u4F60\u4E0D\u80FD\u518D\u7BA1\u6674\u6674\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u68EE\u7136\u9053\uFF1A\u300C\u6211\u662F\u5979\u7236\u4EB2\u3002\u300D</p><p>\u300C\u4F60\u6B20\u5979\u7684\uFF01\u300D\u7D2B\u73AB\u8BF4\u7740\uFF0C\u773C\u89D2\u8FF8\u51FA\u6CEA\u82B1\u3002</p><p>\u8427\u4F5B\u5974\u6700\u6015\u5144\u59B9\u4FE9\u5435\u67B6\uFF0C\u5979\u8737\u7740\u8EAB\u5B50\uFF0C\u53C8\u662F\u62C5\u5FC3\u53C8\u5BB3\u6015\u3002\u4F46\u8FD9\u6B21\u66B4\u6012\u7684\u6155\u5BB9\u9F99\u5F88\u5FEB\u5E73\u9759\u4E0B\u6765\uFF0C\u4ED6\u601D\u7D22\u534A\u664C\uFF0C\u62AC\u624B\u5E2E\u7D2B\u73AB\u62ED\u53BB\u6CEA\u6C34\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u7531\u4F60\u4F5C\u4E3B\u5427\u3002\u300D</p><p>\u7D2B\u73AB\u6447\u5934\u6CE3\u9053\uFF1A\u300C\u6211\u4E5F\u6B20\u5979\u7684\u3002\u300D</p><p>\u60F3\u8D77\u5973\u513F\uFF0C\u7D2B\u73AB\u4FBF\u4F24\u5FC3\u5F97\u96BE\u4EE5\u81EA\u5DF2\u3002\u6155\u5BB9\u9F99\u89C1\u7389\u4EBA\u6CEA\u5982\u6CC9\u6D8C\uFF0C\u53EA\u597D\u8F6C\u79FB\u8BDD\u9898\uFF0C\u6545\u4F5C\u8F7B\u677E\u5730\u8BF4\u9053\uFF1A\u300C\u602A\u4E0D\u5F97\u592D\u592D\u4F1A\u6000\u4E86\u8EAB\u5B55\uFF0C\u539F\u6765\u662F\u5979\u505A\u7684\u597D\u4E8B\u3002\u300D</p><p>\u300C\u554A\uFF1F\u300D\u7D2B\u73AB\u679C\u7136\u6B62\u4F4F\u54ED\u6CE3\uFF0C\u6115\u7136\u95EE\u9053\uFF1A\u300C\u600E\u4E48\u53EF\u80FD\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u6447\u5934\u7B11\u53F9\u9053\uFF1A\u300C\u8FD9\u4E9B\u5C0F\u5BB6\u4F19\uFF0C\u7740\u5B9E\u6709\u4E9B\u82B1\u6837\u5462\u3002\u300D\u4ED6\u7406\u4E86\u7406\u7D2B\u73AB\u7684\u79C0\u53D1\uFF0C\u300C\u7B97\u4E86\uFF0C\u4E0D\u7BA1\u5979\u4EEC\u4E86\u3002\u5979\u4EEC\u559C\u6B22\uFF0C\u5C31\u968F\u5979\u4EEC\u7684\u610F\u597D\u4E86\u3002\u300D</p><p>\u8BF4\u7740\u4ED6\u633D\u4F4F\u8427\u4F5B\u5974\u7684\u7EA4\u8DB3\uFF0C\u5C06\u5979\u4E24\u6761\u7389\u817F\u62C9\u5F97\u7B14\u76F4\uFF0C\u5206\u6210\u4E00\u5B57\uFF0C\u7B11\u9053\uFF1A\u300C\u5A18\u65E2\u7136\u9192\u4E86\uFF0C\u5C31\u8DDF\u513F\u5B50\u4E00\u540C\u4E50\u4E50\u5427\u3002\u300D</p><p>\u8427\u4F5B\u5974\u8FD8\u5728\u53D1\u6014\uFF0C\u76F4\u5230\u7389\u6237\u88AB\u513F\u5B50\u62E8\u5F04\u7684\u6DEB\u6DB2\u56DB\u6EA2\uFF0C\u5979\u624D\u56DE\u8FC7\u795E\u6765\uFF0C\u5A07\u5598\u7EC6\u7EC6\u5730\u8BF4\u9053\uFF1A\u300C\u9F99\u54E5\u54E5\uFF0C\u4F60\u521A\u624D\u8BF4\u2026\u2026\u592D\u592D\u2026\u2026\u6000\u4E86\u8EAB\u5B55\u2026\u2026\u662F\u771F\u7684\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u5BF9\u81EA\u5DF1\u90A3\u4E00\u811A\u5FC3\u91CC\u6709\u6570\uFF0C\u4ED6\u6CA1\u60F3\u53D6\u592D\u592D\u7684\u6027\u547D\uFF0C\u4F46\u90A3\u4E2A\u5B7D\u79CD\uFF0C\u591A\u534A\u8981\u80CE\u6B7B\u8179\u4E2D\u3002\u300C\u55EF\uFF0C\u5927\u6982\u6709\u4E09\u56DB\u4E2A\u6708\u4E86\u5427\u3002\u300D</p><p>\u8427\u4F5B\u5974\u600E\u4E48\u4E5F\u60F3\u4E0D\u901A\u592D\u592D\u600E\u4E48\u4F1A\u6000\u5B55\uFF0C\u4E5F\u8BB8\u662F\u83E9\u8428\u5782\u601C\uFF0C\u628A\u5979\u53D8\u4E3A\u5973\u8EAB\u2026\u2026</p><p>\u300C\u592D\u592D\u8FD8\u6CA1\u6709\u6000\u8FC7\u5B55\u5462\uFF0C\u6211\u8981\u6559\u6559\u5979\u2026\u2026\u300D\u8427\u4F5B\u5974\u7738\u4E2D\u900F\u51FA\u6B23\u559C\u7684\u5149\u8292\uFF0C\u5C31\u50CF\u4E00\u4E2A\u542C\u5230\u5973\u513F\u6000\u5B55\u7684\u6BCD\u4EB2\u90A3\u6837\u5145\u6EE1\u559C\u60A6\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4ECE\u701A\u6D77\u88AD\u6765\u7684\u5BD2\u98CE\u957F\u9A71\u4E07\u91CC\uFF0C\u5374\u88AB\u8FDE\u7EF5\u7684\u7EC8\u5357\u7FA4\u5CF0\u963B\u5728\u5317\u9E93\uFF0C\u5C71\u5317\u5DF2\u662F\u745E\u96EA\u7EB7\u98DE\uFF0C\u7FA4\u5CF0\u4E4B\u95F4\u7684\u661F\u6708\u6E56\u4F9D\u7136\u78A7\u6C34\u6A2A\u6D41\u3002</p><p>\u4E00\u5915\u957F\u8C08\u540E\uFF0C\u6C90\u58F0\u4F20\u518D\u6B21\u51FA\u5C71\uFF0C\u62A4\u9001\u6155\u5BB9\u6C0F\u7684\u4E24\u4E2A\u5B69\u5B50\u8FD4\u56DE\u6D1B\u9633\uFF0C\u5357\u5F81\u4E4B\u4E8B\u4E5F\u968F\u4E4B\u5043\u65D7\u606F\u9F13\u3002</p><p>\u5927\u5B5A\u7075\u9E6B\u5BFA\u65B9\u4E08\u6CAE\u6E20\u5927\u5E08\u62B1\u75C5\u5728\u8EAB\uFF0C\u4E0D\u518D\u8FC7\u95EE\u6C5F\u6E56\u4E4B\u4E8B\uFF1B\u4E5D\u534E\u5251\u6D3E\u9009\u4E86\u65B0\u638C\u95E8\uFF0C\u51CC\u98CE\u5802\u8840\u6848\u88AB\u79D8\u5BC6\u5C01\u9501\uFF0C\u7434\u58F0\u82B1\u5F71\u5931\u8E2A\u4E4B\u4E8B\u4E5F\u6E10\u6E10\u6DE1\u53BB\u3002\u661F\u6708\u6E56\u4ECD\u6F5C\u5728\u6697\u5904\uFF0C\u5F77\u4F5B\u4E00\u5934\u75B2\u5026\u7684\u5996\u517D\uFF0C\u5728\u9ED1\u6697\u4E2D\u9759\u9759\u7761\u53BB\u3002</p><p>\u8273\u51E4\u6536\u7F57\u7684\u7AE5\u5B50\u8FD8\u5728\u6E90\u6E90\u4E0D\u65AD\u5730\u9001\u6765\u3002\u534A\u6708\u524D\uFF0C\u65B0\u6765\u7684\u5341\u4E8C\u540D\u7AE5\u5B50\u4E0A\u5C9B\u65F6\uFF0C\u6B63\u9047\u4E0A\u53F6\u884C\u5357\u3002\u4E5F\u662F\u673A\u7F18\u5DE7\u5408\uFF0C\u53F6\u884C\u5357\u4E00\u773C\u770B\u4E2D\u4E86\u5176\u4E2D\u4E00\u4E2A\u5C0F\u7AE5\uFF0C\u5F53\u5373\u6536\u6765\u505A\u4E86\u5F92\u5F1F\u3002\u90A3\u5C0F\u5B69\u513F\u89C1\u4F19\u4F34\u4EEC\u90FD\u8DDF\u7740\u4E00\u4E2A\u6F02\u4EAE\u963F\u59E8\u79BB\u5F00\uFF0C\u53EA\u6709\u81EA\u5DF1\u88AB\u4E00\u4E2A\u8001\u5934\u5E26\u8D70\uFF0C\u5F53\u573A\u5927\u54ED\u8D77\u6765\uFF0C\u53F6\u884C\u5357\u54C4\u4E86\u51E0\u5929\u624D\u597D\u4E86\u4E9B\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u8FD8\u6709\u4E00\u4EF6\u4E8B\u2014\u2014\u592D\u592D\u8981\u4E34\u4EA7\u4E86\u3002</p><p>\u7B97\u6765\u5979\u6000\u5B55\u521A\u6EE1\u4E94\u4E2A\u6708\uFF0C\u4F46\u51E0\u5929\u524D\u80CE\u513F\u5C31\u52A8\u4E86\u8D77\u6765\u3002\u7B2C\u4E00\u6B21\u88AB\u5B69\u5B50\u8E22\u5230\uFF0C\u592D\u592D\u5413\u5F97\u6367\u7740\u809A\u5B50\uFF0C\u4E00\u52A8\u4E5F\u4E0D\u6562\u52A8\u3002\u8FD8\u662F\u8427\u4F5B\u5974\u544A\u8BC9\u5973\u513F\uFF0C\u90A3\u662F\u80CE\u52A8\uFF0C\u5B69\u5B50\u5C31\u5FEB\u8981\u51FA\u6765\u4E86\u3002\u592D\u592D\u8FD8\u6709\u4E9B\u8FF7\u60D8\u3002\u8427\u4F5B\u5974\u8BF4\uFF0C\u4F60\u8981\u5F53\u5A18\u4E86\u3002\u5979\u624D\u5077\u5077\u7B11\u4E86\u8D77\u6765\u3002</p><p>\u6674\u96EA\u7ACB\u5373\u547D\u4EBA\u5E03\u7F6E\u4E86\u5BAB\u6BBF\uFF0C\u5C06\u592D\u592D\u6BCD\u5B50\u9001\u5230\u9759\u5BA4\uFF0C\u53C8\u6311\u4E86\u5341\u51E0\u540D\u5E72\u51C0\u7684\u4F8D\u5973\uFF0C\u4E00\u5929\u5341\u4E8C\u4E2A\u65F6\u8FB0\u5728\u65C1\u4F3A\u5019\u3002</p><p>\u5341\u4E00\u6708\u5341\u4E5D\uFF0C\u6E05\u6668\u65F6\u5206\u592D\u592D\u4FBF\u611F\u5230\u8179\u4E2D\u9635\u75DB\u3002\u6674\u96EA\u95FB\u8BAF\u8D76\u6765\uFF0C\u4E00\u9762\u547D\u4EBA\u53BB\u8BF7\u53F6\u884C\u5357\uFF0C\u4E00\u9762\u901A\u77E5\u4E86\u9759\u989C\u548C\u8427\u4F5B\u5974\u3002</p><p>\u6155\u5BB9\u9F99\u50CF\u6765\u65F6\u4E00\u6837\u6084\u65E0\u58F0\u606F\u5730\u79BB\u5F00\u4E86\u661F\u6708\u6E56\uFF0C\u540C\u65F6\u5E26\u8D70\u4E86\u7D2B\u73AB\u3002\u8427\u4F5B\u5974\u6302\u5FF5\u5973\u513F\uFF0C\u8FD8\u7559\u5728\u5BAB\u91CC\u7B49\u592D\u592D\u5206\u5A29\u3002\u4F17\u4EBA\u90FD\u677E\u4E86\u53E3\u6C14\uFF0C\u7EDD\u53E3\u4E0D\u63D0\u6155\u5BB9\u9F99\u5230\u6765\u7684\u4E8B\uFF0C\u597D\u50CF\u4ED6\u672A\u66FE\u6765\u8FC7\u4E00\u822C\u3002\u9759\u989C\u4F24\u52BF\u5DF2\u597D\u4E86\u8BB8\u591A\uFF0C\u4F46\u8138\u8272\u8FD8\u6709\u4E9B\u82CD\u767D\uFF0C\u9971\u53D7\u6467\u6B8B\u7684\u5DE6\u4E73\u52C9\u5F3A\u7652\u5408\uFF0C\u4F46\u5DF2\u65E0\u590D\u6614\u65E5\u7684\u575A\u633A\uFF0C\u8D70\u52A8\u95F4\u4E00\u5760\u4E00\u5760\uFF0C\u4F3C\u4E4E\u4E73\u8089\u4E2D\u8FD8\u523A\u7740\u4E00\u7BF7\u9510\u9488\u3002</p><p>\u6674\u96EA\u770B\u51FA\u5979\u7684\u75DB\u695A\uFF0C\u6276\u5979\u5750\u5728\u6905\u4E0A\u3002\u8584\u8584\u7684\u9633\u5149\u7A7F\u8FC7\u4E91\u972D\uFF0C\u6D12\u5165\u6BBF\u5185\u3002\u9759\u989C\u772F\u8D77\u773C\u775B\uFF0C\u4F3C\u4E4E\u5DF2\u7ECF\u5F88\u4E45\u6CA1\u89C1\u8FC7\u9633\u5149\u4E86\uFF0C\u8FD9\u6837\u8000\u773C\uFF0C\u771F\u6709\u4E9B\u4E0D\u4E60\u60EF\u2026\u2026</p><p>\u5BAB\u6BBF\u7684\u88C5\u9970\u534E\u4E3D\u800C\u4E0D\u5F20\u626C\uFF0C\u56E0\u4E3A\u5728\u8FD9\u91CC\u5206\u5A29\u7684\u662F\u4E00\u4F4D\u6CA1\u6709\u540D\u4EFD\u7684\u516C\u4E3B\u3002\u56DB\u58C1\u5F20\u6302\u7740\u9AD8\u53CA\u6BBF\u9876\u7684\u5E37\u5E5B\uFF0C\u5E37\u5E5B\u8FB9\u7F18\u7528\u91D1\u7EBF\u7EE3\u7740\u8FDE\u7EF5\u4E0D\u5230\u5934\u7684\u5982\u610F\u7EB9\u9970\uFF0C\u5927\u7EA2\u7684\u5E18\u5E37\u88AB\u9633\u5149\u4E00\u6620\uFF0C\u987F\u65F6\u660E\u4EAE\u8D77\u6765\uFF0C\u4F7F\u5F97\u6BCF\u4E2A\u4EBA\u8138\u4E0A\u90FD\u6D0B\u6EA2\u7740\u559C\u8272\u3002\u7A97\u68C2\u4E0A\u7ED3\u6EE1\u5F69\u5E26\u3001\u94F6\u94C3\uFF0C\u505A\u6210\u4ED9\u9E64\u5F62\u72B6\u7684\u85B0\u7089\u71C3\u7740\u5B89\u795E\u7684\u6A80\u9999\uFF0C\u6B64\u65F6\u6B63\u70E7\u5F97\u6EDA\u70ED\u3002</p><p>\u6BBF\u5185\u6B63\u4E2D\u653E\u7740\u4E00\u5F20\u9526\u69BB\uFF0C\u592D\u592D\u8EBA\u5728\u67D4\u8F6F\u7684\u88AB\u8925\u95F4\uFF0C\u5982\u4E91\u7684\u9752\u4E1D\u94FA\u5728\u6795\u4E0A\uFF0C\u8138\u4E0A\u6BEB\u65E0\u8840\u8272\u3002\u5979\u7D27\u5F20\u5730\u5410\u7740\u6C14\uFF0C\u5706\u6EDA\u6EDA\u7684\u809A\u5B50\u5728\u88AB\u4E0B\u4E00\u6643\u4E00\u6643\u3002\u7D27\u90BB\u7740\u5E8A\u69BB\u662F\u4E00\u5F20\u8F6F\u6905\uFF0C\u76DB\u88C5\u7684\u7F8E\u5987\u5750\u5728\u4E0A\u9762\uFF0C\u4E0D\u80FD\u52A8\u4F5C\u7684\u7389\u624B\u653E\u5728\u592D\u592D\u638C\u4E2D\uFF0C\u67D4\u58F0\u8BF4\u9053\uFF1A\u300C\u4E0D\u8981\u6015\uFF0C\u7F13\u7F13\u5438\u6C14\u5C31\u4E0D\u90A3\u4E48\u75BC\u4E86\u2026\u2026\u300D</p><p>\u592D\u592D\u9F3B\u5C16\u5192\u51FA\u51B7\u6C57\uFF0C\u5FFD\u7136\u300C\u554A\u5440\u300D\u4E00\u58F0\uFF0C\u5C16\u53EB\u9053\uFF1A\u300C\u6D41\u8840\u4E86\uFF01\u300D\u5979\u5927\u5F20\u7684\u4E24\u817F\u4E00\u9635\u98A4\u6296\uFF0C\u54ED\u9053\uFF1A\u300C\u597D\u591A\u8840\u2026\u2026\u300D</p><p>\u9759\u989C\u5FC3\u5934\u72C2\u8DF3\uFF0C\u592D\u592D\u6000\u80CE\u4E94\u6708\u4FBF\u5373\u4E34\u4EA7\uFF0C\u80CE\u513F\u591A\u534A\u5DF2\u7ECF\u592D\u6298\u4E86\u3002\u5979\u8D77\u8EAB\u63E1\u4F4F\u592D\u592D\u51B0\u51C9\u7684\u5C0F\u624B\uFF0C\u62C9\u4F4F\u88AB\u8925\u51C6\u5907\u6380\u5F00\u3002\u6674\u96EA\u5374\u6BEB\u4E0D\u60CA\u614C\uFF0C\u53EA\u62BF\u5634\u4E00\u7B11\uFF0C\u547D\u4F8D\u5973\u5E2E\u5979\u64E6\u6C57\u3002</p><p>\u8FD9\u8FB9\u8427\u4F5B\u5974\u5FAE\u7B11\u9053\uFF1A\u300C\u75BC\u4E0D\u75BC\uFF1F\u300D</p><p>\u300C\u597D\u75BC\u3002\u300D\u592D\u592D\u5E94\u58F0\u8BF4\u9053\uFF0C\u5F85\u614C\u4E71\u8FC7\u53BB\u624D\u8BB6\u9053\uFF1A\u300C\u597D\u50CF\u2026\u2026\u4E0D\u5F88\u75BC\u2026\u2026\u300D</p><p>\u300C\u90A3\u662F\u7F8A\u6C34\u7834\u4E86\u3002\u300D\u8427\u4F5B\u5974\u67D4\u58F0\u8BF4\u9053\uFF1A\u300C\u5C0F\u5B9D\u5B9D\u5728\u544A\u8BC9\u4F60\uFF0C\u5979\u5C31\u8981\u4ECE\u4F60\u809A\u5B50\u91CC\u51FA\u6765\u4E86\u5462\u3002\u300D</p><p>\u592D\u592D\u5149\u6E9C\u6E9C\u7684\u7389\u817F\u6CBE\u6EE1\u4E86\u900F\u660E\u7684\u6DB2\u4F53\uFF0C\u4EA7\u95E8\u5FAE\u5FAE\u5F20\u5F00\uFF0C\u80FD\u770B\u5230\u7565\u5E26\u6DF7\u6D4A\u7684\u7F8A\u6C34\u6B63\u4ECE\u4E2D\u6C69\u6C69\u6DCC\u51FA\u3002\u9759\u989C\u677E\u4E86\u53E3\u6C14\uFF0C\u633D\u4F4F\u592D\u592D\u7684\u5DE6\u624B\uFF0C\u5750\u5728\u69BB\u4E0A\u3002</p><p>\u8427\u4F5B\u5974\u8F7B\u7B11\u9053\uFF1A\u300C\u8BB0\u5F97\u4ECA\u5929\u662F\u4EC0\u4E48\u65E5\u5B50\u5417\uFF1F\u300D</p><p>\u592D\u592D\u832B\u7136\u626C\u8D77\u8138\u3002</p><p>\u300C\u662F\u592D\u592D\u7684\u751F\u65E5\u2014\u2014\u592D\u592D\u6EE1\u5341\u516D\u5C81\u4E86\u5462\u3002\u300D</p><p>\u300C\u554A\uFF1F\u300D\u592D\u592D\u65E9\u5FD8\u4E86\u8FD9\u56DE\u4E8B\uFF0C\u8FD8\u771F\u5DE7\uFF0C\u6B63\u597D\u5728\u81EA\u5DF1\u751F\u65E5\u8FD9\u5929\u5206\u5A29\u3002\u300C\u5A18\uFF0C\u300D</p><p>\u592D\u592D\u53EF\u601C\u516E\u516E\u5730\u8BF4\uFF1A\u300C\u4F60\u751F\u592D\u592D\u54EA\u5929\u6709\u6CA1\u6709\u8FD9\u4E48\u75BC\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u5507\u89D2\u98A4\u4E86\u4E00\u4E0B\uFF0C\u90A3\u5929\u5979\u6B63\u5728\u53D7\u513F\u5B50\u8D23\u7F5A\uFF0C\u88AB\u97AD\u6253\u5F97\u65E9\u4EA7\uFF0C\u751F\u4E0B\u4E86\u81EA\u5DF1\u8DDF\u513F\u5B50\u4E71\u4F26\u7684\u592D\u592D\u2026\u2026</p><p>\u300C\u4E0D\u75BC\u7684\u3002\u300D\u8427\u4F5B\u5974\u67D4\u58F0\u8BF4\u9053\u3002</p><p>\u53F6\u884C\u5357\u8FDC\u8FDC\u5750\u5728\u4E00\u65C1\uFF0C\u70B9\u8457\u4E66\u5377\u6559\u5F92\u513F\u8BA4\u5B57\u3002\u4ED6\u8EAB\u4E3A\u661F\u6708\u6E56\u7B2C\u4E00\u795E\u533B\uFF0C\u672C\u4E0D\u5C51\u4E8E\u505A\u8FD9\u79CD\u63A5\u751F\u7684\u52FE\u5F53\u3002\u53EA\u4E0D\u8FC7\u592D\u592D\u7684\u5B50\u5BAB\u662F\u4ED6\u4EB2\u624B\u690D\u5165\uFF0C\u6B64\u523B\u53C8\u63D0\u524D\u56DB\u4E2A\u6708\u5206\u5A29\uFF0C\u6015\u81EA\u5DF1\u7684\u4F5C\u54C1\u51FA\u4E86\u610F\u5916\uFF0C\u624D\u52C9\u5F3A\u5750\u5728\u4E00\u8FB9\u3002</p><p>\u6674\u96EA\u548C\u8427\u4F5B\u5974\u90FD\u751F\u8FC7\u5B69\u5B50\uFF0C\u6B64\u65F6\u8427\u4F5B\u5974\u5728\u592D\u592D\u8EAB\u8FB9\u5B89\u6170\uFF0C\u6674\u96EA\u633D\u8D77\u8863\u8896\uFF0C\u7528\u70ED\u6C34\u6D17\u4E86\u624B\uFF0C\u51C6\u5907\u63A5\u751F\u3002\u9759\u989C\u5C0F\u5FC3\u5730\u6478\u4E86\u6478\u592D\u592D\u7684\u8179\u7403\uFF0C\u95EE\u9053\uFF1A\u300C\u4E94\u4E2A\u6708\u90FD\u4F1A\u8FD9\u4E48\u5927\u5417\uFF1F\u300D</p><p>\u300C\u4E5F\u4E0D\u4E00\u5B9A\uFF0C\u59D0\u59D0\u4E24\u4E2A\u6708\u809A\u5B50\u5C31\u9686\u4E86\u8D77\u6765\uFF0C\u6709\u7684\u4E09\u56DB\u4E2A\u6708\u8FD8\u770B\u4E0D\u51FA\u6765\u5462\u3002\u300D</p><p>\u6674\u96EA\u6709\u4E9B\u5947\u602A\uFF0C\u8FD9\u4E9B\u5929\u9F99\u54E5\u54E5\u9891\u9891\u8BAF\u95EE\u6000\u5B55\u7684\u4E8B\uFF0C\u96BE\u9053\u5979\u53D1\u73B0\u81EA\u5DF1\u7684\u7678\u6C34\u8FDF\u4E86\u5417\uFF1F</p><p>\u9759\u989C\u4E00\u4E00\u8BB0\u5728\u5FC3\u91CC\uFF0C\u5979\u4E0E\u68B5\u96EA\u828D\u76F8\u5904\u591A\u5E74\uFF0C\u5BF9\u533B\u7406\u4E5F\u7565\u901A\u4E00\u4E8C\uFF0C\u6B64\u65F6\u624B\u6307\u6263\u7740\u592D\u592D\u7684\u8109\u95E8\uFF0C\u8138\u4E0A\u5E26\u7740\u8C01\u4E5F\u770B\u4E0D\u61C2\u7684\u795E\u60C5\u3002</p><p>\u592D\u592D\u7684\u5B50\u5BAB\u5F00\u59CB\u6536\u7F29\uFF0C\u5979\u5927\u53E3\u5927\u53E3\u5438\u4E86\u7740\u6C14\uFF0C\u767D\u767D\u7684\u5C0F\u8138\u4E0A\u4E0D\u4F4F\u6E17\u51FA\u51B7\u6C57\u3002</p><p>\u6674\u96EA\u5C06\u6795\u5934\u57AB\u5728\u5979\u8170\u4E0B\uFF0C\u8C03\u6574\u7740\u80CE\u4F4D\uFF0C\u8BA9\u80CE\u513F\u80FD\u987A\u5229\u6ED1\u51FA\u4F53\u8154\u3002</p><p>\u300C\u5F20\u5F00\u817F\u2026\u2026\u5438\u4E00\u53E3\u6C14\u2026\u2026\u4E0D\u8981\u5410\uFF0C\u5411\u4E0B\u7528\u529B\u2026\u2026\u300D\u8427\u4F5B\u5974\u5FC3\u75BC\u5730\u671B\u7740\u5973\u513F\uFF0C\u67D4\u58F0\u6307\u70B9\u7740\u5979\u600E\u4E48\u751F\u5B69\u5B50\u3002</p><p>\u592D\u592D\u4E24\u817F\u5F2F\u66F2\u7740\u652F\u5728\u4F53\u4FA7\uFF0C\u81C0\u90E8\u5FAE\u5FAE\u62AC\u8D77\uFF0C\u618B\u7740\u6C14\u7AED\u529B\u5411\u4E0B\u4F7F\u52B2\u3002\u767D\u817B\u7684\u8179\u7403\u968F\u7740\u5BAB\u7F29\u7F13\u7F13\u5411\u4E0B\u6C89\u53BB\uFF0C\u6BB7\u7EA2\u7684\u4EA7\u95E8\u5706\u5706\u5F20\u5F00\uFF0C\u7389\u961C\u88AB\u62C9\u5F97\u53D8\u5E73\uFF0C\u4E0A\u9762\u4E00\u6761\u767D\u5AE9\u7684\u5C0F\u8089\u68D2\u8F6F\u8F6F\u5782\u5728\u4E00\u8FB9\uFF0C\u968F\u7740\u8179\u7403\u7684\u9707\u52A8\u626D\u6765\u626D\u53BB\uFF0C\u5F77\u4F5B\u4E00\u6761\u53EF\u7231\u7684\u767D\u5C0F\u86C7\u3002</p><p>\u5BAB\u9888\u5DF2\u7ECF\u5F20\u5F00\uFF0C\u7B49\u5F85\u7740\u8FD8\u672A\u8DB3\u6708\u7684\u80CE\u513F\u7A7F\u8FC7\u3002\u75BC\u75DB\u8D8A\u6765\u8D8A\u5F3A\u70C8\uFF0C\u592D\u592D\u62E7\u7D27\u7709\u5934\uFF0C\u7C89\u5AE9\u7684\u5C0F\u5C41\u80A1\u75DB\u82E6\u5730\u62AC\u8D77\u843D\u4E0B\u3002\u90A3\u662F\u4E00\u79CD\u5E78\u798F\u7684\u75DB\u695A\uFF0C\u6CA1\u6709\u4EC0\u4E48\u6BD4\u5206\u5A29\u7684\u75BC\u75DB\u66F4\u52A0\u751C\u871C\u3002\u2026\u2026\u5FFD\u7136\u592D\u592D\u7389\u8138\u4E00\u50F5\uFF0C\u98A4\u58F0\u53EB\u9053\uFF1A\u300C\u59D0\u59D0\u2026\u2026\u300D</p>',104);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
