import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="\u7B2C\u4E8C\u5341\u516D\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u516D\u7AE0" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u516D\u7AE0</h1><p>\u4E0D\u77E5\u7531\u4F55\u65F6\u5F00\u59CB\uFF0C\u9634\u68EE\u7684\u9B54\u6811\u5E95\u7ADF\u964D\u4E34\u4E0B\u4E94\u5757\u6D6E\u8361\u5728\u5929\u7A7A\u4E2D\u7684\u5DE8\u77F3\u7891\uFF0C\u7531\u53E4\u8001\u7684\u7B26\u6587\u73AF\u7ED5\u7740\u8089\u773C\u96BE\u8FA8\u7684\u78A7\u8424\u9634\u7075\u4EEC\uFF0C\u4EA4\u9519\u7030\u6F2B\u7740\u6E90\u6E90\u4E0D\u7EDD\u3001\u8D8A\u6765\u8D8A\u6D53\u70C8\u7684\u90AA\u6076\u5996\u6C14\uFF0C\u5C06\u56DB\u5468\u4E00\u5207\u6C14\u6D41\u5168\u6570\u541E\u566C\u6B86\u5C3D.</p><p>\u4E2D\u592E\u7684\u5706\u5F62\u77F3\u7891\u4E0B\u5012\u7ACB\u6346\u7ED1\u7740\u4E00\u540D\u5973\u5B50\uFF0C\u6D51\u8EAB\u91D1\u8272\u7684\u6BDB\u53D1\u8BA9\u5979\u770B\u8D77\u6765\u5341\u5206\u7279\u6B8A\uFF0C\u624B\u81C2\u4E0A\u6BDB\u8338\u8338\u7684\u6C57\u817A\u4E0E\u7F8E\u578B\u6027\u611F\u7684\u4E30\u6EE1\u8EAF\u4F53\uFF0C\u770B\u8D77\u6765\u5012\u50CF\u6781\u4E86\u4E00\u5934\u88AB\u7CBE\u5FC3\u6253\u626E\u8FC7\u7684\u6BCD\u5F62\u72AC\u3002</p><p>\u300C\u5514\u2026\u2026\u5514\u5514\u2026\u2026\u300D\u5634\u91CC\u88AB\u4E00\u56E2\u767D\u8272\u9ECF\u6DB2\u5835\u4F4F\u8BF4\u4E0D\u51FA\u8BDD\u7684\u5973\u4EBA\uFF0C\u53EA\u80FD\u652F\u652F\u543E\u543E\u7684\u75DB\u82E6\u561F\u56D4\u7740\u3002</p><p>\u8FD9\u56DB\u5468\u7684\u4E00\u5207\u4F3C\u4E4E\u8BE1\u5F02\u5230\u4E86\u6781\u70B9\uFF0C\u5145\u65A5\u7684\u67AF\u6728\u6811\u7C50\u53D7\u5230\u5996\u6C14\u7684\u5F71\u54CD\uFF0C\u53D8\u5F97\u6709\u5982\u9CD7\u86C7\u822C\u67D4\u8F6F\u800C\u873F\u8712\uFF0C\u5E76\u5C06\u5979\u7684\u80A2\u4F53\u7262\u7262\u5206\u6210\u5927\u5B57\u578B\uFF0C\u7F9E\u803B\u7684\u6E7F\u6DA6\u4E0B\u4F53\u5C31\u8FD9\u6837\u8D64\u88F8\u88F8\u7684\u5B8C\u5168\u66DD\u9732\u7740\uFF0C\u4EFB\u7531\u80A1\u95F4\u900F\u660E\u7684\u767D\u8272\u9ECF\u6DB2\u6BEB\u65E0\u906E\u63A9\u7684\u5782\u4E0B\u6EF4\u843D\u3002</p><p>\u300C\u5514\u5514\u2026\u2026\u5495\u565C\u2026\u2026\u545C\u2026\u2026\u300D\u8FD9\u6761\u6D51\u8EAB\u8D64\u88F8\u88AB\u7D27\u7F1A\u5728\u77F3\u7891\u7684\u7F8E\u5F62\u72AC\u6B63\u662F\u6A31\u5B50\uFF0C\u591A\u65E5\u4EE5\u6765\u88AB\u8FD9\u4E9B\u5815\u843D\u4E3A\u6076\u9B54\u5974\u96B6\u7684\u4EB2\u4EBA\u51CC\u8650\u4E0B\uFF0C\u8EAB\u5FC3\u90FD\u50CF\u4E0D\u505C\u62BD\u6410\u4E0E\u6EA2\u8840\u822C\u7684\u83AB\u540D\u51B7\u98A4\u3002</p><p>\u75DB\u82E6\u7684\u6298\u78E8\u4E0D\u77E5\u9053\u8981\u5230\u4EC0\u4E48\u65F6\u5019\u624D\u7ED3\u675F\uFF0C\u4F46\u5979\u5FC3\u7406\u660E\u767D\uFF0C\u82E5\u6B64\u60C5\u51B5\u7EE7\u7EED\u6076\u5316\u4E0B\u53BB\u7684\u8BDD\uFF0C\u4E0D\u4EC5\u795E\u4EE3\u5BB6\u5C06\u6C38\u52AB\u4E0D\u590D\uFF0C\u5C31\u8FDE\u6574\u4E2A\u4EBA\u7C7B\u7684\u4E16\u754C\u6050\u6015\u4E5F\u96BE\u9003\u88AB\u706D\u7EDD\u5974\u5F79\u7684\u60B2\u60E8\u5384\u8FD0.</p><p>\u300C\u563B\u563B\u563B\u2026\u2026\u53EF\u601C\u7684\u6A31\u5B50\uFF0C\u4F60\u73B0\u5728\u7684\u8FD9\u79CD\u773C\u795E\u2026\u2026\u50CF\u4F3C\u5728\u6028\u6068\u4F60\u7684\u4E24\u4F4D\u597D\u59CA\u59CA\u5417\uFF1F\u300D\u8EAB\u4E0A\u7A7F\u7740\u4E00\u4EF6\u6027\u611F\u534E\u4E3D\u7684\u8D64\u8273\u548C\u670D\uFF0C\u96EA\u767D\u5230\u611F\u53D7\u4E0D\u51FA\u4E00\u4E1D\u8840\u6C14\u7684\u82CD\u767D\u808C\u80A4\uFF0C\u7F8E\u6708\u6D51\u8EAB\u6240\u6D41\u9732\u51FA\u6765\u7684\u6C14\u606F\uFF0C\u4F3C\u4E4E\u6BD4\u8D77\u4E4B\u524D\u66F4\u52A0\u663E\u5F97\u7EDD\u8272\u5996\u5A9A\u800C\u4E0D\u50CF\u4E2A\u4EBA\u7C7B\u3002</p><p>\u300C\u5514\u5514\uFF01\u300D\u6A31\u5B50\u7684\u5634\u91CC\u53D1\u4E0D\u51FA\u60B2\u9E23\uFF0C\u53EA\u80FD\u4EE5\u54C0\u4F24\u7684\u53CC\u773C\u7528\u6094\u6068\u4E07\u5206\u7684\u795E\u60C5\u6012\u89C6\u7740\u5BF9\u65B9\uFF0C\u5C3D\u7BA1\u6700\u7EC8\u660E\u767D\u4E86\u8FD9\u4E00\u5207\u90FD\u662F\u6709\u4EBA\u4ECE\u4E2D\u641E\u9B3C\uFF0C\u4F46\u4E5F\u53EA\u80FD\u602A\u81EA\u5DF1\u6CA1\u80FD\u53CA\u65E9\u53D1\u73B0\uFF0C\u624D\u8BA9\u4E00\u5207\u906D\u9047\u53D8\u5F97\u5982\u6B64\u4E0D\u53EF\u633D\u56DE\u3002</p><p>\u300C\u600E\u4E48\uFF1F\u4F60\u60F3\u8BF4\u4EC0\u4E48\uFF1F\u8FD9\u6837\u5BB9\u6613\u53D1\u60C5\u7684\u8EAB\u4F53\u8FD8\u5728\u4E0D\u505C\u5174\u594B\u7684\u98A4\u6296\u5462\u2026\u2026\u662F\u60F3\u52FE\u5F15\u7537\u4EBA\u4E86\u5417\uFF1F\u300D\u7F8E\u6708\u8D70\u8FD1\u6A31\u5B50\u8EAB\u65C1\uFF0C\u6545\u610F\u5C06\u7389\u6307\u7528\u529B\u634F\u4F4F\u90A3\u5BF9\u5FAE\u5FAE\u52C3\u8D77\u7684\u4E73\u5934\u5E76\u7528\u5632\u8BBD\u7684\u53E3\u543B\u523A\u6FC0\u5979\u3002</p><p>\u300C\u55EF\u55EF\u2026\u2026\u5514\u2026\u2026\u5514\uFF01\u300D\u53CC\u773C\u6124\u6068\u822C\u7684\u6CE8\u89C6\u7740\u81EA\u5DF1\u59EA\u5973\uFF0C\u4F46\u4E0D\u4E89\u6C14\u7684\u8EAB\u5B50\u5374\u5F88\u5FEB\u7684\u7EA2\u6DA6\u8D77\u6765\uFF0C\u53D7\u5230\u523A\u6FC0\u7684\u4E73\u8C46\u4E0A\u4E5F\u7ACB\u523B\u786C\u633A\u7684\u7AD6\u7ACB\u8D77\u6765\u3002</p><p>\u300C\u5594\u2026\u2026\u6211\u5012\u662F\u5FD8\u4E86\u4F60\u73B0\u5728\u7684\u76AE\u9AA8\u662F\u9644\u5728\u4E00\u6761\u6BCD\u72D7\u8EAB\u4E0A\uFF0C\u563B\u563B\uFF0C\u8BE5\u8BF4\u662F\u60F3\u8981\u516C\u72D7\u6170\u85C9\u60F3\u7684\u7D27\u5427\uFF1F\u300D\u7F8E\u6708\u7684\u5632\u8BBD\u5BF9\u65BC\u4E00\u5411\u52C7\u6562\u679C\u65AD\u7684\u6A31\u5B50\u6765\u8BF4\uFF0C\u7B80\u76F4\u662F\u4E00\u79CD\u65E0\u53EF\u6551\u8D4E\u7684\u6076\u6BD2\u8BC5\u5492\u3002</p><p>\u6A31\u5B50\u8EAB\u4F53\u6FC0\u52A8\u7684\u60F3\u8981\u7528\u529B\u6323\u624E\uFF0C\u65E0\u5948\u56DB\u80A2\u73B0\u5728\u5374\u88AB\u7030\u6F2B\u7684\u6811\u7C50\u7ED9\u7262\u7262\u7F20\u4F4F\uFF0C\u6709\u5982\u8F6F\u81C2\u6500\u5EF6\u5728\u8EAB\u4F53\u56DB\u5468\u654F\u611F\u90E8\u4F4D\u4E0A\u6413\u5F04\u7740\uFF0C\u5641\u5FC3\u7684\u89E6\u611F\u8BA9\u5979\u6839\u672C\u65E0\u6CD5\u96C6\u4E2D\u610F\u8BC6\u7684\u76F4\u6253\u54C6\u55E6\u3002</p><p>\u300C\u563B\u563B\uFF0C\u8309\u8389\u5B50\u4F60\u8BF4\u2026\u2026\u8BE5\u600E\u4E48\u5904\u7F6E\u4F60\u8FD9\u53EF\u7231\u7684\u5C0F\u59B9\u5462\uFF1F\u300D\u7F8E\u6708\u8F6C\u8FC7\u8EAB\u5BF9\u65BC\u4E0D\u8FDC\u5904\u7684\u4E00\u6761\u9ED1\u5F71\u95EE\u9053\u3002</p><p>\u300C\u54FC\u3002\u300D\u73B0\u8EAB\u7684\u7F8E\u4EBA\u5DF2\u7ECF\u6362\u4E0A\u4E86\u4E00\u4EF6\u9C9C\u7EA2\u8272\u7684\u4EAE\u4E3D\u76AE\u8863\uFF0C\u4E0D\u518D\u662F\u5148\u524D\u53EF\u6015\u7684\u602A\u7269\u6A21\u6837\uFF0C\u8EAB\u4E0A\u7CBE\u7DFB\u7684\u9970\u54C1\u50CF\u523A\u9488\u822C\u7A7F\u5165\u81EA\u5DF1\u654F\u9510\u7684\u6027\u5668\u5B98\uFF0C\u5996\u8273\u7684\u6DEB\u8650\u795E\u91C7\u5145\u65A5\u5728\u5979\u6BCF\u4E00\u540B\u7684\u808C\u80A4\u91CC\u9762\uFF0C\u624B\u4E2D\u6301\u7740\u7279\u5236\u7684\u4E5D\u5C3E\u97AD\uFF0C\u5145\u6EE1\u6027\u611F\u5374\u53C8\u4EE4\u4EBA\u6BDB\u9AA8\u609A\u7136\u822C\u7684\u795E\u97F5\uFF0C\u900F\u9732\u7684\u7ADF\u662F\u65E0\u6BD4\u9634\u5BD2\u4E0E\u51B0\u51B7\u3002</p><p>\u300C\u4F60\u8DDF\u767E\u5408\u5B50\u4E0D\u4EC5\u4F24\u4E86\u6211\uFF0C\u8FD8\u6597\u80C6\u72AF\u4E0B\u4F24\u5BB3\u4E3B\u4EBA\u9B54\u7075\u8FD9\u7B49\u4E0D\u53EF\u9976\u6055\u7684\u6ED4\u5929\u5927\u7F6A\uFF0C\u5C31\u7B97\u8EAB\u4E3A\u4F60\u7684\u59CA\u59CA\uFF0C\u4E5F\u5FC5\u987B\u8BA9\u4F60\u53D7\u70B9\u60E9\u7F5A\u624D\u884C\u3002\u300D\u8138\u4E0A\u5145\u6EE1\u7740\u90AA\u6076\u6C14\u606F\u7684\u8309\u8389\u5B50\u7528\u5979\u52FE\u9B42\u5374\u51B7\u9177\u7684\u773C\u795E\u77AA\u7740\u6A31\u5B50\uFF0C\u8BE1\u5F02\u7684\u795E\u6001\uFF0C\u4F3C\u4E4E\u5C06\u8981\u5BF9\u6A31\u5B50\u8FDB\u884C\u4EC0\u4E48\u53EF\u6015\u7684\u62A5\u590D\u3002</p><p>\u300C\u5514\u2026\u2026\u5514\u2026\u2026\u300D\u770B\u7740\u8309\u8389\u5B50\u624B\u91CC\u53D1\u4EAE\u7684\u9ED1\u76AE\u97AD\uFF0C\u6A31\u5B50\u6D51\u8EAB\u4E0D\u7981\u98A4\u6817\u4E0D\u5DF2\uFF0C\u59CA\u59CA\u7684\u90A3\u53CC\u773C\u795E\u65E9\u5DF2\u4E0D\u50CF\u4EBA\u7C7B\uFF0C\u50CF\u4F3C\u4E00\u5934\u51F6\u72E0\u7684\u6076\u72FC\u2026\u2026\u4E0D\uFF0C\u5982\u65AF\u822C\u7684\u6076\u6BD2\u2026\u2026</p><p>\u5E94\u8BE5\u8BF4\u662F\u4E00\u53CC\u4F1A\u5403\u4EBA\u7684\u773C\u624D\u5BF9\uFF01</p><p>\u300C\u5288\u5566\uFF01\u300D\u5267\u70C8\u53C8\u6BEB\u65E0\u9884\u8B66\u7684\u80CC\u540E\u4E00\u97AD\uFF0C\u8BA9\u6A31\u5B50\u8EAC\u76F4\u7684\u75DB\u82E6\u54C0\u53F7\uFF0C\u54ED\u4E0D\u51FA\u58F0\u97F3\u7684\u5634\u5DF4\u91CC\u5507\u9F7F\u90FD\u5728\u98A4\u6296\u7740\uFF0C\u4E00\u79CD\u4ECE\u6765\u90FD\u6CA1\u6709\u8FC7\u7684\u523A\u75DB\uFF0C\u50CF\u4FB5\u5165\u5230\u9AA8\u5B50\u91CC\u822C\u8BA9\u4EBA\u65E0\u6CD5\u5FCD\u53D7\u3002</p><p>\u300C\u5514\uFF01\u5514\u2026\u2026\u5641\u2026\u2026\u54B3\u54B3\u2026\u2026\u300D\u6D88\u9000\u4E0D\u53BB\u7684\u75BC\u75DB\u6301\u7EED\u7684\u523A\u6FC0\u7740\u6A31\u5B50\uFF0C\u53EB\u4E5F\u53EB\u4E0D\u51FA\u6765\u7684\u8EAB\u4F53\u62DA\u547D\u5192\u51FA\u51B7\u6C57\uFF0C\u6FC0\u52A8\u7684\u80A2\u4F53\u5F00\u59CB\u4E0D\u53D7\u7EA6\u675F\u7684\u75DB\u82E6\u6323\u624E\u3002</p><p>\u300C\u600E\u4E48\uFF1F\u624D\u5403\u4E86\u4E00\u97AD\u5C31\u6296\u6210\u8FD9\u6837\u2026\u2026\u8FD8\u5DEE\u5F97\u8FDC\u5462\u3002\u300D\u8309\u8389\u5B50\u624B\u4E2D\u7279\u6B8A\u7684\u4E5D\u5C3E\u76AE\u97AD\u4F3C\u4E4E\u9644\u7740\u6709\u4EC0\u4E48\u5947\u7279\u9B54\u529B\uFF0C\u7559\u5728\u6A31\u5B50\u8840\u75D5\u7684\u808C\u80A4\u4E0A\u4F3C\u4E4E\u50CF\u8981\u71C3\u70E7\u8D77\u6765\u822C\u707C\u70E7\u5230\u6700\u654F\u611F\u7684\u795E\u7ECF\u91CC\u53BB\uFF0C\u5149\u5403\u4E0B\u8FD9\u4E00\u97AD\uFF0C\u5C31\u6BD4\u53D7\u5C3D\u7384\u4EBA\u6298\u78E8\u534A\u65E5\u8FD8\u6765\u5F97\u75DB\u82E6\u4E07\u5206\u3002</p><p>\u300C\u547C\u2026\u2026\u547C\u2026\u2026\u5514\u5514\u5514\uFF01\u300D</p><p>\u66F4\u8BA9\u4EBA\u611F\u5230\u6050\u60E7\u7684\u662F\uFF0C\u5C31\u5728\u706B\u7EA2\u7684\u4F24\u53E3\u4E0A\u5374\u4E0D\u65AD\u5E76\u51FA\u8840\u4E1D\uFF0C\u7D2B\u8272\u9752\u8272\u7684\u8840\u7BA1\u7600\u9ED1\u4E86\u5979\u7EC6\u767D\u7684\u5A07\u5AE9\u808C\u80A4\uFF0C\u6269\u6563\u7684\u6BD2\u8840\u597D\u50CF\u6B63\u5728\u8840\u7BA1\u91CC\u4E0D\u65AD\u8513\u5EF6\uFF0C\u75DB\u82E6\u7684\u523A\u6FC0\u8BA9\u6A31\u5B50\u538B\u6291\u4E0D\u4F4F\u7684\u62DA\u547D\u6323\u624E\u3002</p><p>\u300C\u518D\u6765\u8BE5\u6253\u5728\u54EA\u5462\uFF1F\u300D\u8309\u8389\u5B50\u629A\u6478\u7740\u59B9\u59B9\u6E7F\u6DCB\u6DCB\u4E0D\u65AD\u5192\u51FA\u51B7\u6C57\u7684\u5A07\u8EAF\uFF0C\u50CF\u5728\u627E\u5BFB\u51FA\u8EAB\u4F53\u4E2D\u6700\u5A07\u5AE9\u7684\u654F\u611F\u5904\uFF0C\u5BF9\u65BC\u8FD9\u526F\u66FE\u7ECF\u4F54\u7528\u8FC7\u7684\u8EAB\u4F53\uFF0C\u8309\u8389\u5B50\u53EF\u662F\u77AD\u5982\u6307\u638C\u3002</p><p>\u300C\u5288\uFF01\u300D\u72E0\u72E0\u7684\u4E00\u97AD\uFF0C\u8FD9\u6B21\u7ADF\u7136\u6253\u5728\u7F3A\u4E86\u9634\u8482\u7684\u8089\u5507\u4E0A\uFF01</p><p>\u300C\u5514\uFF01\u5514\u5514\uFF01\u300D\u6A31\u5B50\u63A7\u5236\u4E0D\u4F4F\u7684\u6CEA\u6C34\u7ACB\u523B\u6E83\u5824\u822C\u6D12\u843D\uFF0C\u6BD4\u8D77\u53D7\u5230\u672A\u5A5A\u592B\u7684\u6B8B\u5FCD\u5BF9\u5F85\uFF0C\u4EB2\u751F\u59CA\u59CA\u8FD9\u822C\u7684\u51B7\u8840\u9634\u5BD2\uFF0C\u66F4\u662F\u8BA9\u8FD9\u540D\u52C7\u65BC\u9762\u5BF9\u4E00\u5207\u7684\u575A\u5F3A\u5973\u6027\u518D\u4E5F\u627F\u53D7\u4E0D\u4E86\u7684\u75DB\u54ED\u5931\u58F0\u3002</p><p>\u300C\u8FC7\u763E\u5417\uFF1F\u8FD9\u79CD\u523A\u6FC0\u53EF\u662F\u4F1A\u4E0A\u763E\u5462\u2026\u2026\u5288\uFF01\u300D</p><p>\u53C8\u662F\u4E00\u8BB0\u8BA9\u7EC6\u5AE9\u808C\u80A4\u707C\u70E7\u8D77\u6765\u7684\u706B\u543B\u6253\u5728\u53D1\u786C\u4E73\u8C46\u4E0A\uFF0C\u8309\u8389\u5B50\u4E0D\u4EC5\u6E05\u695A\u6240\u6709\u6700\u654F\u611F\u7684\u90E8\u4F4D\uFF0C\u8FD8\u5341\u5206\u77AD\u89E3\u5982\u4F55\u8BA9\u5973\u4EBA\u5728\u75DB\u82E6\u65E0\u52A9\u4E4B\u4E2D\u66F4\u52A0\u7684\u5931\u63A7\u5815\u843D\u3002</p><p>\u5F53\u6A31\u5B50\u7684\u8EAB\u4F53\u8FD8\u6CA1\u6765\u5F97\u53CA\u9002\u5E94\u5982\u6B64\u6781\u7AEF\u7684\u75DB\u82E6\u65F6\uFF0C\u7B2C\u56DB\u4E0B\u7684\u6BD2\u97AD\u7ADF\u7136\u53C8\u6253\u5728\u9634\u5507\u76F8\u8FD1\u7684\u4F4D\u7F6E\u4E0A\uFF0C\u672C\u4EE5\u7652\u5408\u7684\u8089\u8482\u4F24\u53E3\u518D\u4E5F\u627F\u53D7\u4E0D\u4E86\u7684\u55B7\u51FA\u5C3F\u6DB2\u822C\u7684\u9C9C\u8840\uFF0C\u7EC6\u7EC6\u7684\u8840\u4E1D\u5C06\u9ED1\u4EAE\u7684\u76AE\u97AD\u67D3\u4E0A\u4E00\u5C42\u5254\u900F\u6676\u4EAE\u7684\u60BD\u7F8E\u7EA2\u5F69\u3002</p><p>\uFF08\u554A\u554A\uFF01\u6B7B\u2026\u2026\u8981\u6B7B\u4E86\u2026\u2026\u554A\u2026\u2026\u4E3A\u2026\u2026\u4E3A\u751A\u2026\u2026\u4F1A\u8FD9\u6837\u2026\u2026\uFF09\u5947\u602A\u7684\u662F\uFF0C\u5C3D\u7BA1\u8309\u8389\u5B50\u6CA1\u6709\u66F4\u8FDB\u4E00\u6B65\u7684\u8D23\u7F5A\uFF0C\u4F46\u6FC0\u70C8\u7684\u75DB\u82E6\u5374\u6CA1\u6709\u4E1D\u6BEB\u7F13\u548C\u6216\u538B\u6291\u7684\u8FF9\u8C61\uFF0C\u8111\u5B50\u91CC\u75DB\u5230\u5FEB\u8981\u7A92\u606F\u7684\u6A31\u5B50\uFF0C\u5374\u53EA\u80FD\u611F\u53D7\u5230\u96BE\u8FC7\u7684\u714E\u71AC\u53D8\u5F97\u65E0\u6BD4\u6E05\u6670\u5230\u53EB\u4EBA\u53D1\u72C2\u3002</p><p>\u300C\u563B\u563B\u2026\u2026\u653E\u5FC3\u5427\uFF0C\u8FD9\u70B9\u5C0F\u4F24\u8FD8\u4E0D\u81F3\u65BC\u8BA9\u4F60\u7A92\u606F\u6216\u6655\u6B7B\uFF0C\u6E17\u5165\u4F53\u5185\u7684\u6DEB\u8650\u4E4B\u8840\u4F1A\u6291\u5236\u4F60\u8840\u6D41\u7684\u8F93\u9001\u901F\u5EA6\u2026\u2026\u662F\u4E0D\u662F\u6E10\u6E10\u7684\u5F00\u59CB\u89C9\u5F97\u610F\u8BC6\u8D8A\u6765\u8D8A\u6E05\u9192\uFF0C\u75BC\u75DB\u53D8\u5F97\u50CF\u866B\u8680\u822C\u4E0D\u65AD\u6269\u5927\u5462\uFF1F\u300D</p><p>\u300C\u5514\u2026\u2026\u5514\u545C\u2026\u2026\u300D\u4F24\u53E3\u7684\u7600\u8840\u6E10\u6E10\u7684\u67D3\u4E0A\u7D2B\u9752\u7EA2\u9ED1\u5404\u79CD\u8840\u7BA1\u5E76\u88C2\u540E\u7684\u53EF\u6015\u6A21\u6837\uFF0C\u5C3D\u7BA1\u53CC\u773C\u65E9\u5DF2\u6A21\u7CCA\u7684\u770B\u4E0D\u6E05\u4E00\u5207\uFF0C\u4F46\u6A31\u5B50\u4ECD\u5F3A\u5FCD\u7740\u610F\u8BC6\u4E0D\u80AF\u5C48\u670D\u3002</p><p>\u300C\u5288\uFF01\u2026\u2026\u5288\uFF01\u2026\u2026\u5288\uFF01\u2026\u2026\u5288\uFF01\u300D\u65E0\u60C5\u7684\u86C7\u97AD\u4E00\u6B21\u53C8\u4E00\u6B21\u7684\u6325\u843D\u5728\u6A31\u5B50\u7EC6\u7DFB\u7684\u808C\u80A4\u4E0A\uFF0C\u80C0\u5F00\u7684\u7600\u8840\u5C31\u6837\u6E17\u5165\u5230\u8840\u7BA1\u4E2D\u5206\u4F48\u7684\u6BCF\u4E00\u540B\u808C\u8089\u91CC\u9762\uFF0C\u6700\u540E\uFF0C\u5982\u6B64\u5A07\u6EF4\u6EF4\u7684\u6210\u719F\u7F8E\u5973\u7ADF\u7ED9\u81EA\u5DF1\u59CA\u59CA\u6298\u78E8\u6210\u4E00\u56E2\u8089\u7403\u822C\u6DD2\u51C9\u4E0E\u53EF\u6015\uFF0C\u4F46\u4F53\u65E0\u5B8C\u80A4\u4E0B\u5F97\u610F\u8BC6\u5374\u8FD8\u6CA1\u5C48\u670D\u7684\u72E0\u72E0\u76EF\u7740\u8309\u8389\u5B50\u7684\u53CC\u773C\u3002</p><p>\u300C\u563B\u2026\u2026\u5567\u5567\u5567\u2026\u2026\u771F\u662F\u6B8B\u4E0D\u5FCD\u7779\u7684\u6A21\u6837\uFF0C\u539F\u6765\u2026\u2026\u8FD9\u5C31\u662F\u4F60\u4EEC\u59CA\u59B9\u95F4\u7684\u597D\u60C5\u8C0A.\u300D\u4E00\u65C1\u89C2\u770B\u7684\u7F8E\u6708\uFF0C\u6B64\u65F6\u5374\u5DF2\u5FCD\u4E0D\u4F4F\u7684\u5A07\u58F0\u5A9A\u7B11\u8D77\u6765\u3002</p><p>\u300C\u563F\u2026\u2026\u563F\u2026\u2026\u8FD8\u6CA1\u5B8C\u5462\u2026\u2026\u54FC\uFF0C\u4F60\u8FD9\u6761\u4E0B\u7B49\u7684\u70C2\u5973\u72D7\uFF0C\u522B\u4EE5\u4E3A\u8FD9\u6837\u5C31\u7ED3\u675F\u4E86\u5462\uFF0C\u8FD9\u53EA\u662F\u4E3A\u4E86\u8BA9\u900F\u9B54\u97AD\u7684\u8650\u6027\u5F97\u4EE5\u53D1\u6325\u7F62\u4E86\uFF0C\u7B49\u5230\u6DEB\u8840\u5B8C\u5168\u51DD\u56FA\u540E\uFF0C\u66F4\u7F8E\u5999\u7684\u300C\u75DB\u300D\u624D\u4F1A\u52A0\u500D\u8212\u7545\uFF01\u300D</p><p>\u53EF\u6015\u7684\u53D8\u6001\u60C5\u4E8B\u4F3C\u4E4E\u8FD8\u6CA1\u6709\u843D\u5E55\uFF0C\u80BF\u5F97\u50CF\u4EBA\u7403\u4E00\u6837\u7684\u6A31\u5B50\uFF0C\u514B\u5236\u4E0D\u4F4F\u6D51\u8EAB\u6FC0\u70C8\u7684\u98A4\u6296\uFF0C\u505C\u6B62\u4E0D\u4E86\u59CA\u59CA\u624B\u4E0A\u76AE\u97AD\u7684\u89E6\u51FB\uFF0C\u71E5\u70ED\u7684\u9A9A\u52A8\u4E0E\u523A\u75DB\u5C31\u50CF\u6709\u866B\u5B50\u94BB\u5165\u76AE\u5C42\u5185\u822C\u5267\u70C8\uFF0C\u5FEB\u8981\u71C3\u70E7\u8D77\u6765\u7684\u808C\u80A4\u91CC\u5374\u4E0D\u65AD\u4EA7\u751F\u51FA\u65E0\u6CD5\u60F3\u50CF\u7684\u75DB\u82E6\uFF0C\u6EF4\u51FA\u6EE1\u6EE1\u6E29\u70ED\u7684\u6E7F\u6C57\u4E0E\u8EAB\u4F53\u6DF7\u6742\u7684\u9ECF\u6DB2\u4EA4\u7ED3\u7684\u6563\u53D1\u51FA\u9635\u9635\u8165\u5473\u3002</p><p>\u300C\u563F\u563F\u2026\u2026\u5DF2\u7ECF\u5DEE\u4E0D\u591A\u4E86\uFF0C\u518D\u6765\u2026\u2026\u8BE5\u662F\u8BA9\u4F60\u89E3\u8131\u7684\u65F6\u523B\u2026\u2026\u300D\u8309\u8389\u5B50\u6076\u6BD2\u7684\u53CC\u773C\u7EFD\u653E\u51FA\u90AA\u6076\u7684\u7EA2\u5149\uFF0C\u4EBA\u5F62\u822C\u7684\u8EAB\u8EAF\u4E5F\u5F00\u59CB\u9010\u6E10\u8715\u53D8\u6210\u539F\u672C\u5DE8\u578B\u8718\u86DB\u7684\u53EF\u6015\u6A21\u6837\u3002</p><p>\uFF08\u554A\u554A\u2026\u2026\u6211\u2026\u2026\u8981\u6B7B\u4E86\u5417\uFF1F\uFF09\u6A31\u5B50\u7684\u53CC\u773C\u7981\u4E0D\u4F4F\u6D41\u4E0B\u5782\u6B7B\u524D\u7684\u6CEA\u6C34\uFF0C\u770B\u7740\u59CA\u59CA\u7684\u90A3\u53CC\u660E\u4EAE\u5927\u773C\u7ADF\u6E10\u6E10\u7684\u53D8\u6210\u4E4C\u9ED1\u800C\u900F\u4EAE\uFF0C\u6700\u7EC8\u6709\u5982\u5267\u6BD2\u7684\u9ED1\u5BE1\u5987\u4E00\u6837\uFF0C\u809A\u660E\u5B50\u7684\u4E0A\u7F18\u4F38\u51FA\u4E24\u6761\u50CF\u9570\u5200\u4F3C\u7684\u624B\u81C2\uFF0C\u6676\u4EAE\u4EAE\u7684\u5229\u722A\u7740\u5B9E\u8BA9\u6A31\u5B50\u754F\u60E7\u5FC3\u5BD2\u5230\u65E0\u6CD5\u5F62\u5BB9\u3002</p><p>\u300C\u5636\u2026\u2026\u5636\u5636\u2026\u2026\u4F60\u8FD9\u80AE\u9AD2\u7684\u6BCD\u72D7\u2026\u2026\u5BF9\u4E00\u6761\u5BA0\u7269\u6765\u8BF4\uFF0C\u8FD9\u6837\u788D\u773C\u7684\u624B\u811A\u90FD\u662F\u591A\u4F59\u7684\u2026\u2026\u300D</p><p>\uFF08\u554A\uFF01\u4EC0\u2026\u2026\u4EC0\u4E48\u2026\u2026\u4E0D\u2026\u2026\u4E0D\u8981\u2026\u2026\u8FC7\u6765\u2026\u2026\u5514\uFF01\uFF09\u300C\u5514\uFF01\u5657\u5431\uFF01\u300D</p><p>\u53EF\u6015\u7684\u60C5\u666F\uFF0C\u8BA9\u8FD9\u6D51\u8EAB\u662F\u4F24\u7684\u91D1\u53D1\u5973\u5B50\u56DB\u80A2\u5927\u91CF\u7684\u55B7\u51FA\u9C9C\u8840\uFF0C\u5DE8\u5927\u7684\u866B\u722A\uFF0C\u7ADF\u50CF\u5229\u5203\u822C\u7684\u5212\u8FC7\u6A31\u5B50\u80A2\u4F53\uFF0C\u53D7\u62D8\u675F\u7684\u5973\u4F53\u5237\u5237\u7684\u51E0\u58F0\u4FBF\u6454\u843D\u5728\u5730\uFF0C\u9057\u7559\u5728\u7C50\u8513\u4E0A\u5F97\u56DB\u80A2\u8FD8\u4E0D\u505C\u6D41\u4E0B\u9ED1\u8272\u7684\u6D53\u6C41\u3002</p><p>\u300C\u547C\u2026\u2026\u547C\u2026\u2026\u5514\u5514\u2026\u2026\u5641\u5455\u2026\u2026\u300D\u6D51\u8EAB\u4E0D\u505C\u62BD\u6410\u6EA2\u8840\uFF0C\u6BD4\u6B7B\u8FD8\u8981\u60BD\u60E8\u7684\u6A31\u5B50\u53E3\u9F3B\u4E2D\u4E0D\u65AD\u545B\u51FA\u5455\u5410\u7684\u79FD\u7269\uFF0C\u53CC\u773C\u7684\u795E\u60C5\u65E9\u5DF2\u9EBB\u6728\uFF0C\u5F77\u5F7F\u5C31\u5728\u6781\u7AEF\u6B8B\u5FCD\u7684\u8650\u5211\u4E4B\u540E\uFF0C\u5373\u5C06\u72EC\u81EA\u9762\u5BF9\u6B7B\u4EA1\u7684\u964D\u4E34.</p><p>\u5931\u53BB\u80A2\u4F53\u7684\u5973\u4EBA\u762B\u75EA\u5728\u4E00\u5806\u8840\u8165\u4E0E\u6C61\u79FD\u4E4B\u4E2D\uFF0C\u6B8B\u7834\u7684\u8EAF\u4F53\u7ADF\u4ECE\u56DB\u80A2\u6574\u9F50\u7684\u65AD\u53E3\u5904\u5F00\u59CB\u957F\u51FA\u9ED1\u82BD\uFF0C\u6162\u6162\u7684\u8FD8\u628A\u6EA2\u8840\u4F24\u5904\u7ED9\u5305\u8986\u8D77\u6765\u3002</p><p>\u300C\u5636\u5636\u2026\u2026\u5636\u2026\u2026\u563F\u2026\u2026\u300D\u8309\u8389\u5B50\u4F38\u51FA\u9570\u5200\u822C\u5229\u722A\u63D2\u5165\u81EA\u5DF1\u59B9\u59B9\u7684\u5DE6\u80F8\u819B\uFF0C\u5F77\u5F7F\u50CF\u52FE\u8D77\u4E00\u56E2\u5E9F\u5F03\u6BC1\u574F\u7684\u73A9\u5076\u822C\u5BF9\u7740\u6A31\u5B50\u51B7\u7B11\u3002</p><p>\u300C\u5636\u2026\u2026\u4E0B\u8D31\u7684\u6BCD\u72D7\u2026\u2026\u8FD8\u8BB0\u4E0D\u8BB0\u5F97\u4F60\u6700\u4EB2\u7231\u7684\u672A\u5A5A\u592B\uFF1F\u300D\u5C31\u5728\u6B64\u65F6\u8309\u8389\u5B50\u4EBA\u5F62\u7684\u4E0A\u534A\u8EAB\u91CC\uFF0C\u624B\u4E2D\u5374\u591A\u51FA\u4E86\u53E6\u4E00\u4EF6\u50CF\u4F3C\u51CC\u8650\u7528\u7684\u6027\u5177\uFF0C\u4E00\u6761\u534A\u622A\u7684\u5012\u52FE\u8089\u68D2\u7ADF\u7136\u50CF\u866B\u5B50\u4E00\u6837\u7684\u4E0D\u65AD\u8815\u52A8\u7740\u3002</p><p>\u300C\u547C\u2026\u2026\u547C\u2026\u2026\u300D\u6A31\u5B50\u7684\u53CC\u773C\u5DF2\u7ECF\u65E0\u6CD5\u805A\u7126\uFF0C\u51E0\u4E4E\u8FDE\u547C\u5438\u90FD\u5FEB\u8981\u505C\u6B62\u7684\u865A\u5F31\u611F\u8BA9\u5979\u98A4\u6296\u4E86\u51E0\u4E0B\uFF0C\u4F3C\u4E4E\u5BF9\u65BC\u66FE\u7ECF\u76F8\u7231\u8FC7\u7684\u7537\u4EBA\u8FD8\u65E0\u6CD5\u5B8C\u5168\u91CA\u6000\u3002</p><p>\u300C\u90A3\u4E2A\u6CA1\u7528\u7684\u8822\u8D27\u2026\u2026\u5DF2\u7ECF\u88AB\u5F53\u6210\u5B69\u5B50\u4EEC\u7684\u9972\u6599\u2026\u2026\u5636\u5636\u2026\u2026\u552F\u4E00\u7559\u4E0B\u6761\u7EAA\u5FF5\u4E1C\u897F\u2026\u2026\u5C31\u662F\u51C6\u5907\u7528\u5728\u4F60\u8EAB\u4E0A\u7684\u2026\u2026\u300D\u8309\u8389\u5B50\u53EA\u662F\u8214\u4E86\u8214\u624B\u4E2D\u7684\u534A\u622A\u9633\u5177\uFF0C\u6CA1\u60F3\u5230\u6643\u52A8\u7684\u6DEB\u7269\u672B\u7AEF\uFF0C\u7ADF\u7136\u5F00\u59CB\u4F38\u957F\u51FA\u4E00\u6761\u6761\u50CF\u4E1D\u7EBF\u822C\u7684\u8840\u7BA1\uFF0C\u5982\u540C\u53D8\u79CD\u4E4C\u8D3C\u822C\u9A87\u4EBA\u3002</p><p>\u300C\u547C\u2026\u2026\u547C\u2026\u2026\u545C\u545C\u2026\u2026\u300D\u6A31\u5B50\u6025\u4FC3\u7684\u5598\u606F\u7740\uFF0C\u53CC\u773C\u9664\u4E86\u4E0D\u505C\u6D41\u6CEA\u4E4B\u5916\uFF0C\u4EFB\u4F55\u4E8B\u60C5\u5979\u90FD\u65E9\u5DF2\u963B\u6B62\u4E0D\u4E86\u3002</p><p>\u8D6B\u7136\u95F4\uFF0C\u6A31\u5B50\u53D1\u73B0\u5230\u8FD9\u6761\u6DEB\u8089\u68D2\u7684\u524D\u7F18\u7ADF\u8FD8\u8854\u7740\u4E00\u6839\u6676\u4EAE\u7684\u6307\u73AF\uFF0C\u5F77\u5F7F\u8FDE\u66FE\u7CFB\u5728\u81EA\u5DF1\u79C1\u5904\u4E0A\u5507\u73AF\u4E00\u5757\u901D\u53BB\u7684\u9634\u8482\uFF0C\u4E5F\u88AB\u878D\u5165\u5728\u4E86\u8FD9\u6761\u6DEB\u7269\u4E0A\u3002</p><p>\u300C\u563B\u563B\u563B\uFF0C\u771F\u662F\u7F8E\u6781\u2026\u2026\u5C0F\u7384\u4EBA\u4E00\u5B9A\u5F88\u6EE1\u610F\u81EA\u5DF1\u8FD9\u6837\u7684\u6700\u7EC8\u7ED3\u5C40\u2026\u2026\u6C38\u8FDC\u7684\u2026\u2026\u7559\u5728\u7231\u4EBA\u8EAB\u4F53\u91CC\uFF0C\u54C8\u54C8\u2026\u2026\u54C8\u2026\u2026\u300D</p><p>\u7F8E\u6708\u4EB2\u773C\u76EE\u7779\u8FD9\u6837\u4E00\u573A\u6DEB\u90AA\u7684\u8650\u620F\uFF0C\u5979\u4F3C\u4E4E\u5F88\u6EE1\u610F\u8309\u8389\u5B50\u5982\u4ECA\u5BF9\u59B9\u59B9\u7684\u5904\u7F6E\u65B9\u5F0F\uFF0C\u773C\u770B\u7740\u5373\u5C06\u8FDB\u5165\u9AD8\u6F6E\u7684\u65F6\u523B\uFF0C\u6D51\u8EAB\u90AA\u6C14\u7684\u5C11\u5973\u4E5F\u5F00\u59CB\u9732\u51FA\u90A3\u4E0D\u5C5E\u65BC\u4EBA\u7C7B\u7684\u6DEB\u866B\u89E6\u9B1A.</p><p>\u6162\u6162\u7684\uFF0C\u4E00\u6761\u6761\u50CF\u8840\u4E1D\u822C\u7684\u7EC6\u5FAE\u8109\u7EBF\uFF0C\u4ECE\u8089\u6839\u672B\u7AEF\u4FB5\u5165\u6A31\u5B50\u7684\u9634\u8482\u4F24\u53E3\uFF0C\u9165\u75D2\u5230\u8BA9\u4EBA\u5934\u76AE\u53D1\u9EBB\u7684\u523A\u75DB\u611F\uFF0C\u8BA9\u53D7\u4F24\u7684\u79C1\u5904\u4E0D\u7531\u5F97\u6E7F\u6ED1\u6ED1\u5C3F\u4E86\u4E00\u5927\u7247\u3002</p><p>\u300C\u5514\u547C\u2026\u2026\u547C\u547C\u2026\u2026\u300D\u5C31\u5728\u6A31\u5B50\u4E00\u53E3\u6C14\u8FD8\u5598\u4E0D\u8FC7\u6765\u7684\u538B\u529B\u4E2D\uFF0C\u534A\u6761\u7684\u840E\u7F29\u6DEB\u830E\u5374\u5DF2\u51E0\u4E4E\u5B8C\u5168\u88AB\u6B96\u5165\u5728\u6A31\u5B50\u7684\u9634\u5507\u4E0A\u65B9\uFF0C\u5165\u4FB5\u7684\u8840\u7BA1\u5FEB\u901F\u7684\u4E0E\u4F53\u5185\u795E\u7ECF\u63A5\u5408\u5728\u4E00\u8D77\uFF0C\u6BEB\u65E0\u9884\u8B66\u7684\u4E24\u6761\u86C7\u830E\u4ECE\u8309\u8389\u5B50\u5C3E\u7AEF\u98DE\u7A9C\u800C\u51FA\u540E\uFF0C\u5C31\u76F4\u63A5\u704C\u6CE8\u4F3C\u7684\u731B\u529B\u94BB\u5165\u59B9\u59B9\u7684\u79C1\u5904\u4E0E\u809B\u95E8\u91CC\uFF01</p><p>\u300C\u5514\uFF01\u5514\uFF01\u300D\u5634\u91CC\u4F9D\u7136\u65E0\u6CD5\u8A00\u8BED\u7684\u6A31\u5B50\u62DA\u547D\u7684\u54C0\u53EB\u7740\uFF0C\u4E0B\u4F53\u633A\u7740\u4E00\u6761\u8F6F\u6389\u540E\u7684\u840E\u7F29\u9634\u830E\uFF0C\u5374\u5728\u6B64\u65F6\u52C3\u52C3\u7684\u575A\u786C\u6602\u9996\uFF0C\u9732\u51FA\u90A3\u4E11\u964B\u4E4C\u9ED1\u7684\u5927\u9F9F\u5934.</p><p>\u300C\u563B\u563B\u2026\u2026\u8FD9\u6761\u8089\u830E\u4E0D\u4EC5\u662F\u6A31\u5B50\u4F5C\u4E3A\u5974\u96B6\u7684\u8BC1\u660E\uFF0C\u4E5F\u662F\u8EAB\u4E3A\u4E00\u5934\u4E13\u5C5E\u6BCD\u72D7\u5F88\u91CD\u8981\u7684\u7279\u5FB5\u4E4B\u4E00\uFF0C\u5636\u5636\u2026\u2026\u300D</p><p>\u65E0\u9884\u8B66\u88AB\u86C7\u830E\u94BB\u5165\u7684\u8089\u6D1E\u5185\uFF0C\u5206\u529E\u4E0D\u51FA\u75DB\u82E6\u8FD8\u662F\u75DB\u5FEB\u822C\u7684\u611F\u89C9\u8BA9\u6A31\u5B50\u62DA\u547D\u60F3\u5C16\u53EB\uFF0C\u8111\u5B50\u91CC\u5B8C\u5168\u6DF7\u6C8C\u4E0D\u6E05\u7684\u5979\uFF0C\u5982\u4ECA\u5374\u5F77\u5F7F\u5728\u6BD4\u6B7B\u8FD8\u8981\u96BE\u53D7\u4E00\u767E\u500D\u7684\u5730\u72F1\u91CC\uFF0C\u88AB\u63A8\u4E0A\u4E86\u4E5D\u9704\u4E91\u5916\u7684\u5929\u5802\u4E0A\u4E00\u6837\uFF0C\u6240\u6709\u8089\u4F53\u5185\u7684\u5B54\u6D1E\u6B63\u5728\u4E0D\u505C\u7528\u529B\u7684\u53D1\u6CC4\u7EFD\u653E\u3002</p><p>\u300C\u545C\u545C\u2026\u2026\u5641\u2026\u2026\u5514\u2026\u2026\u300D\u539F\u672C\u5C31\u4E0D\u80AF\u5C48\u670D\u7684\u53CC\u773C\uFF0C\u5982\u4ECA\uFF0C\u7ADF\u7136\u5F00\u59CB\u53D8\u5F97\u5982\u6B64\u5F02\u5E38\u6DF7\u6D4A\uFF01</p><p>\u300C\u5657\u5431\uFF01\u5657\u5431\uFF01\u5657\u5657\u2026\u2026\u300D\u524D\u6240\u672A\u6709\u7684\u654F\u9510\u611F\u53D7\u6B63\u5728\u4E00\u6761\u654F\u611F\u65E0\u6BD4\u7684\u65B0\u9634\u8482\u4E0A\u75AF\u72C2\u6FC0\u5C04\u7740\u4E73\u767D\u7CBE\u6DB2\uFF0C\u524D\u540E\u6D1E\u7684\u4F38\u7F29\u5957\u5F04\u8BA9\u9971\u53D7\u6DEB\u6BD2\u566C\u4F53\u7684\u6A31\u5B50\u8EAB\u4F53\uFF0C\u7B2C\u4E00\u6B21\u5F3A\u70C8\u7684\u611F\u53D7\u5230\u8089\u4F53\u5B8C\u5168\u88AB\u4EBA\u4F54\u6709\u822C\u7684\u5931\u63A7\u2026\u2026</p><p>\u300C\u563F\u2026\u2026\u563F\u2026\u2026\u773C\u775B\u4E5F\u5F00\u59CB\u53D8\u8272\u4E86\u2026\u2026\u6A31\u2026\u2026\u6A31\u5B50\u2026\u2026\u6DEB\u4E71\u4E4B\u8840\u5DF2\u7ECF\u5B8C\u5168\u6E17\u5165\u4F60\u7684\u8EAB\u4F53\u91CC\u5462\u2026\u2026\u300D</p><p>\u300C\u547C\u2026\u2026\u547C\u2026\u2026\u300D\u6781\u529B\u62BD\u6410\u7684\u5973\u4EBA\u8089\u4F53\u6B63\u88AB\u5728\u5996\u5316\u540E\u7684\u59CA\u59CA\u8E42\u8E8F\u4E0B\u4E0D\u65AD\u5815\u843D\uFF0C\u53D1\u767D\u7684\u8BB0\u5FC6\u91CC\u5934\u53EA\u80FD\u7661\u7661\u50BB\u50BB\u7684\u547B\u541F\u7740\uFF0C\u4E00\u79CD\u65E0\u6BD4\u96BE\u8A00\u7684\u8212\u7545\u89E3\u8131\u5C31\u5728\u4F53\u5185\u6DEB\u830E\u4E00\u6B21\u53C8\u4E00\u6B21\u7684\u55B7\u53D1\u4E2D\uFF0C\u5FEB\u901F\u7D27\u5BC6\u7684\u586B\u6EE1\u4E86\u6A31\u5B50\u610F\u8BC6\u6A21\u7CCA\u7684\u5168\u90E8\u4E00\u5207\u3002</p>',60);function t(i,c){return a}var o=e(r,[["render",t]]);export{o as default};
