import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="\u7B2C\u4E8C\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u7AE0" aria-hidden="true">#</a> \u7B2C\u4E8C\u7AE0</h1><p>\u5FAE\u5FAE\u7684\u51C9\u98CE\u5439\u62C2\u5728\u5C11\u5E74\u7684\u8138\u988A\u4E0A\uFF0C\u4E5F\u8BB8\u662F\u5728\u7761\u68A6\u4E2D\u6D41\u4E86\u4E00\u8EAB\u6C57\u7684\u5173\u7CFB\uFF0C\u590F\u5B63\u7684\u9634\u96E8\u5929\u6C14\u4E2D\u4ECD\u5E26\u6765\u4E00\u4E9B\u5BD2\u610F\u3002</p><p>\u660F\u660F\u6C88\u6C88\u7684\u5E78\u7537\u4E0D\u7531\u81EA\u4E3B\u7684\u6296\u5570\u8D77\u6765\uFF0C\u597D\u50CF\u8EAB\u4E0A\u6CA1\u6709\u7A7F\u7740\u4EFB\u4F55\u8863\u7269\uFF0C\u8EAB\u4F53\u7F29\u6210\u4E00\u56E2\uFF0C\u5C31\u8FDE\u5BFB\u5E38\u7684\u5730\u677F\u90FD\u611F\u89C9\u6709\u65E0\u6BD4\u7684\u51C9\u610F\u3002</p><p>\u300C\u5514\u5514\u2026\u2026\u597D\u51B7\u2026\u2026\u8FD9\u91CC\u662F\u54EA\u91CC\u2026\u2026\u300D\u7787\u7740\u773C\u775B\u7684\u5C11\u5E74\u53EA\u89C9\u5F97\u56DB\u5468\u4E00\u7247\u9ED1\u6697\u3002</p><p>\u300C\u4E3B\u4EBA\u2026\u2026\u4F60\u9192\u8FC7\u6765\u4E86\u5462\u2026\u2026\u300D\u719F\u6089\u7684\u58F0\u97F3\u5728\u5E78\u7537\u7684\u8033\u8FB9\u54CD\u8D77\u3002</p><p>\u300C\u4F60\u2026\u2026\u4F60\u662F\u8C01\uFF1F\u300D\u5377\u66F2\u7684\u5E78\u7537\u671B\u7740\u58F0\u97F3\u7684\u65B9\u5411\u770B\u53BB\uFF0C\u53EA\u89C1\u4E00\u4E2A\u6726\u80E7\u7684\u8EAB\u5F71\u5F80\u81EA\u5DF1\u7684\u65B9\u5411\u8D70\u8FC7\u6765\uFF0C\u7EA4\u7EC6\u7684\u96EA\u767D\u5A07\u8EAF\u4E00\u4E1D\u4E0D\u6302\u7684\u505C\u4E0B\u811A\u6B65\uFF0C\u5439\u5F39\u53EF\u7834\u7684\u94F6\u6708\u53CC\u816E\u4E0A\u63A8\u6EE1\u4E86\u795E\u79D8\u7684\u7B11\u5BB9\u3002</p><p>\u300C\u662F\u4F60\u2026\u2026\u300D\u5E78\u7537\u7684\u8138\u4E0A\u8DDF\u7740\u4E5F\u7EA2\u4E86\u8D77\u6765\uFF0C\u6709\u751F\u4EE5\u6765\u4ECE\u672A\u8FD9\u4E48\u63A5\u8FD1\u7684\u770B\u8FC7\u5B8C\u5168\u88F8\u4F53\u7684\u5C11\u5973\u8EAB\u4F53\uFF0C\u60B8\u52A8\u7684\u601D\u7EEA\u4E0D\u4EC5\u8BA9\u80A1\u95F4\u8D77\u4E86\u5F3A\u70C8\u53CD\u5E94\uFF0C\u5C31\u8FDE\u773C\u775B\u4E5F\u50CF\u7740\u9B54\u4E00\u6837\u7684\u4E0D\u65AD\u7741\u5927\u3002</p><p>\u300C\u5475\u5475\u2026\u2026\u600E\u4E48\u8FD9\u4E48\u8001\u5B9E\u5462\u2026\u2026\u5C0F\u5F1F\u5F1F\u5DF2\u7ECF\u957F\u5927\u4E86\uFF0C\u771F\u6709\u8DA3\u2026\u2026\u300D\u5C11\u5973\u751C\u7F8E\u7684\u58F0\u97F3\u83BA\u83BA\u7684\u7B11\u7740\uFF0C\u90A3\u526F\u7F8E\u4E3D\u7684\u989C\u9762\u4F3C\u4E4E\u5DF2\u7ECF\u5230\u4E86\u65E0\u6CD5\u7528\u8A00\u8BED\u6765\u5F62\u5BB9\uFF0C\u5F77\u5F7F\u8BA9\u5E78\u7537\u6709\u79CD\u4E0D\u771F\u5B9E\u7684\u9519\u89C9.</p><p>\u300C\u522B\u2026\u2026\u522B\u770B\u2026\u2026\u300D\u5E78\u7537\u906E\u4F4F\u81EA\u5DF1\u7684\u4E0B\u4F53\u96BE\u582A\u7684\u65E0\u5904\u53EF\u8EB2\uFF0C\u4F46\u81EA\u5DF1\u7684\u773C\u775B\u5374\u4E0D\u65F6\u5077\u5077\u7784\u7740\u5BF9\u65B9\u901A\u4F53\u96EA\u5AE9\u7684\u767D\u7389\u808C\u80A4\u65E0\u6CD5\u79FB\u5F00.</p><p>\u300C\u8FD8\u8FD9\u4E48\u5BB3\u7F9E\uFF0C\u90A3\u4E3B\u4EBA\u7684\u5904\u7537\u5C31\u8BF7\u4EA4\u7ED9\u6211\u5427\u2026\u2026\u300D\u88F8\u4F53\u7684\u7F8E\u5973\u8138\u86CB\u4F3C\u4E4E\u4F1A\u968F\u7740\u5E78\u7537\u7684\u601D\u7EEA\u8D77\u4F0F\u53D8\u5316\u4E00\u6837\uFF0C\u5F53\u4ED6\u5BB3\u7F9E\u5782\u9996\u7684\u4F4E\u5934\u65F6\uFF0C\u773C\u89D2\u4E0D\u7ECF\u610F\u7684\u53D1\u89C9\u5999\u9F84\u5C11\u5973\u7684\u8138\u9762\u4F3C\u4E4E\u53C8\u53D8\u5F97\u66F4\u52A0\u59A9\u5A9A\u800C\u4EE4\u4EBA\u65E0\u6CD5\u6297\u62D2\u3002</p><p>\u300C\u8FD9\u91CC\u80BF\u7684\u5F88\u96BE\u53D7\u5427\u2026\u2026\u563B\u563B\u2026\u2026\u8BA9\u6211\u5E2E\u4F60\u6D88\u6D88\u2026\u2026\u300D</p><p>\u300C\u5514\u2026\u2026\u55EF\u554A\u2026\u2026\u300D\u6E7F\u6DA6\u7684\u5C0F\u5634\u7D27\u7D27\u7684\u5957\u4F4F\u5E78\u7537\u53D1\u80C0\u7684\u5C0F\u8089\u68D2\uFF0C\u89E6\u611F\u4F3C\u4E4E\u8DDF\u81EA\u5DF1\u624B\u6DEB\u6709\u7740\u975E\u5E38\u5927\u7684\u4E0D\u540C\uFF0C\u5C24\u5176\u662F\u8FD9\u6837\u7F8E\u4E3D\u7684\u5973\u5B50\u66FF\u81EA\u5DF1\u53E3\u4EA4\uFF0C\u4EA2\u594B\u7684\u601D\u7EEA\u8BA9\u654F\u611F\u7684\u9633\u5177\u98A4\u6296\u7684\u51E0\u4E4E\u968F\u65F6\u90FD\u51C6\u5907\u8981\u5C04\u51FA\u6765\u4E00\u6837\u3002</p><p>\u300C\u554A\u2026\u2026\u554A\u554A\u2026\u2026\u300D</p><p>\u300C\u5480\u2026\u2026\u8214\u2026\u2026\u5480\u5480\u2026\u2026\u542E\u542E\u2026\u2026\u300D\u5C11\u5973\u56DE\u7738\u4E00\u7B11\u5F97\u7EE7\u7EED\u8214\u5F04\u7740\uFF0C\u6E29\u70ED\u7684\u820C\u5C16\u8FD0\u7528\u9AD8\u8D85\u7684\u6280\u5DE7\u5728\u5C11\u5E74\u777E\u4E38\u4E0E\u9F20\u595A\u90E8\u4F4D\u4E0A\u6765\u56DE\u5438\u5F04\uFF0C\u7C89\u7EA2\u7684\u5C0F\u5634\u518D\u5EA6\u5957\u5728\u9F9F\u5934\u7684\u5730\u65B9\u65F6\uFF0C\u5174\u594B\u7684\u80C0\u7EA2\u9634\u830E\u5374\u5DF2\u5FCD\u4E0D\u4F4F\u7684\u5C06\u6D53\u7A20\u7684\u767D\u767D\u7CBE\u6DB2\uFF0C\u4E0D\u5C0F\u5FC3\u5C04\u5728\u5C11\u5973\u7684\u5634\u5507\u4E0E\u9F3B\u6A11\u4E0A\u9762\u3002</p><p>\u300C\u5514\u554A\u2026\u2026\u300D</p><p>\u300C\u5BF9\u2026\u2026\u5BF9\u4E0D\u8D77\u2026\u2026\u5514\u2026\u2026\u300D\u5BF9\u65BC\u81EA\u5DF1\u7684\u7CD7\u6001\u611F\u5230\u7F9E\u6127\u4E0D\u5DF2\u7684\u5E78\u7537\u72FC\u72C8\u7684\u8BF4\u9053\u3002</p><p>\u300C\u53C8\u6D53\u53C8\u8165\u7684\u5473\u9053\u2026\u2026\u771F\u7F8E\u5473\u2026\u2026\u300D\u5C11\u5973\u5BF9\u5E78\u7537\u7684\u6B49\u610F\u4E00\u70B9\u90FD\u4E0D\u4EE5\u4E3A\u610F\uFF0C\u820C\u5934\u91CC\u597D\u50CF\u8214\u98DF\u7740\u5341\u5206\u73CD\u8D35\u7684\u4E1C\u897F\u4E00\u6837\uFF0C\u4E00\u70B9\u90FD\u4E0D\u6D6A\u8D39\u7684\u628A\u9ECF\u7A20\u72B6\u7684\u6DB2\u4F53\u5168\u5403\u5230\u5634\u5DF4\u91CC\u53BB\u3002</p><p>\u300C\u8212\u670D\u5417\uFF1F\u563B\u563B\u2026\u2026\u563B\u2026\u2026\u300D\u5C11\u5973\u4E4C\u9ED1\u7684\u5927\u773C\u775B\u671B\u7740\u6EE1\u8138\u901A\u7EA2\u7684\u5E78\u7537\u7B11\u9053\uFF0C\u76F4\u76EF\u7684\u5E78\u7537\u70B9\u70B9\u5934\u540E\u624D\u5F00\u5FC3\u7684\u7B11\u9053\u3002</p><p>\u300C\u4F60\u2026\u2026\u4F60\u4E0D\u662F\u771F\u7684\u5BAB\u5B88\u5FA1\u5427\uFF0C\u4F60\u5230\u5E95\u53EB\u4EC0\u4E48\u540D\u5B57\uFF1F\u300D\u5E78\u7537\u5C3D\u7BA1\u641E\u4E0D\u6E05\u695A\u72B6\u51B5\uFF0C\u4F46\u6BD5\u7ADF\u8FD8\u4E0D\u662F\u4E2A\u50BB\u5B50\uFF0C\u4ED6\u4E0D\u6562\u6B63\u773C\u7684\u770B\u7740\u6D3B\u751F\u751F\u8DEA\u5728\u81EA\u5DF1\u9762\u524D\u7684\u7EDD\u8272\u7F8E\u5973\uFF0C\u53EA\u662F\u7981\u4E0D\u4F4F\u597D\u5947\u7684\u541E\u541E\u5410\u5410\u95EE\u9053\u3002</p><p>\u300C\u6211\u53EB\u5996\u591C\u3002\u300D\u8FD9\u6B21\uFF0C\u5C11\u5973\u5766\u7387\u7684\u7405\u7405\u8BF4\u9053\u3002</p><p>\u300C\u4E0D\u8FC7\u8FD9\u662F\u4EE5\u524D\u4E3B\u4EBA\u7ED9\u6211\u53D6\u7684\u540D\u5B57\uFF0C\u4ECE\u4ECA\u4EE5\u540E\uFF0C\u4E3B\u4EBA\u4F60\u7231\u53EB\u6211\u4EC0\u4E48\u540D\u5B57\u6211\u5C31\u662F\u4EC0\u4E48\u5462\u2026\u2026\u300D</p><p>\u300C\u4E3A\u4EC0\u4E48\u2026\u2026\u4F60\u2026\u2026\u4E3A\u4F55\u8981\u53EB\u6211\u4E3B\u4EBA\uFF1F\u300D</p><p>\u300C\u563B\u2026\u2026\u8FD9\u4E2A\u95EE\u9898\u2026\u2026\u4EE5\u540E\u4F60\u81EA\u7136\u5C31\u4F1A\u660E\u767D\u7684\u2026\u2026\u300D\u5996\u591C\u6CA1\u6709\u591A\u8BF4\u4EC0\u4E48\uFF0C\u62C9\u7740\u5E78\u7537\u7684\u624B\u5F80\u81EA\u5DF1\u79C1\u5904\u7684\u5730\u65B9\u5C31\u5C06\u5BF9\u65B9\u7684\u6307\u5C16\u585E\u4E86\u8FDB\u53BB\u3002</p><p>\u300C\u554A\u554A\u2026\u2026\u4F60\u2026\u2026\u300D\u5E78\u7537\u4ECE\u5C0F\u5C31\u5728\u5C01\u95ED\u4FDD\u5B88\u7684\u5973\u6027\u73AF\u5883\u4E2D\u6210\u957F\uFF0C\u6839\u672C\u5C31\u6CA1\u60F3\u5230\u5BF9\u65B9\u4F1A\u505A\u51FA\u5982\u6B64\u5927\u80C6\u76F4\u63A5\u7684\u653E\u8361\u4E3E\u52A8\u3002</p><p>\u300C\u55EF\u554A\u2026\u2026\u8FD9\u2026\u2026\u8FD9\u91CC\u2026\u2026\u5F88\u597D\u2026\u2026\u6478\u8FD9\u91CC\u2026\u2026\u300D\u5996\u591C\u8138\u4E0A\u4E5F\u5174\u594B\u7684\u5A07\u58F0\u547B\u541F\u7740\uFF0C\u5F15\u9886\u7740\u5C11\u5E74\u7684\u6307\u5C16\u5728\u795E\u79D8\u53C8\u6E7F\u6DA6\u65E0\u6BD4\u7684\u7D27\u95ED\u5AE9\u7A74\u4E2D\u641C\u5BFB\u7740\uFF0C\u4E00\u76F4\u5230\u53D1\u6296\u7684\u624B\u6307\u89E6\u78B0\u5230\u51B0\u51B7\u7684\u786C\u7269\u65F6\uFF0C\u5E78\u7537\u9690\u7EA6\u624D\u53D1\u89C9\u4E2D\u6307\u597D\u50CF\u94A9\u4F4F\u4E86\u4EC0\u4E48\u7EC6\u5C0F\u94F6\u73AF\u7684\u94C1\u7247\u4E00\u6837\u3002</p><p>\u300C\u54C8\u2026\u2026\u662F\u90A3\u91CC\u2026\u2026\u554A\u554A\u2026\u2026\u62C9\u2026\u2026\u62C9\u5F00\u6765\u2026\u2026\u300D\u5996\u591C\u7684\u8138\u4E0A\u53D8\u5F97\u5174\u594B\u65E0\u6BD4\uFF0C\u597D\u50CF\u5C11\u5E74\u89E6\u78B0\u5230\u5979\u6700\u654F\u611F\u7684\u90E8\u4F4D\u4E00\u6837\uFF0C\u6E34\u6C42\u7684\u58F0\u97F3\u4E0D\u65AD\u50AC\u4FC3\uFF0C\u767D\u7EC6\u7684\u7C89\u81C2\u6293\u7D27\u5BF9\u65B9\u7684\u624B\u8155\u53D1\u6D6A\u822C\u7684\u54C0\u53EB\u9053\u3002</p><p>\u300C\u62C9\u2026\u2026\u62C9\u2026\u2026\u5FEB\u62C9\u51FA\u6765\u2026\u2026\u554A\u554A\u2026\u2026\u554A\u554A\u554A\u2026\u2026\u300D\u5996\u591C\u7684\u5598\u606F\u58F0\u8D8A\u6765\u8D8A\u5927\uFF0C\u4F46\u80C6\u5C0F\u7684\u5E78\u7537\u624B\u6307\u5374\u53D1\u6296\u7684\u6CA1\u6709\u52C7\u6C14\u5C06\u90A3\u85CF\u5728\u8089\u7A74\u6DF1\u5904\u7684\u94F6\u73AF\u7ED9\u62C9\u51FA\u4F53\u5916\uFF0C\u624B\u6307\u505C\u7559\u5728\u7F8E\u5999\u7684\u5AE9\u7A74\u91CC\u8D8A\u4E45\uFF0C\u53D1\u8F6F\u7684\u8089\u68D2\u5C31\u4E0D\u81EA\u89C9\u7684\u53C8\u53D8\u5F97\u575A\u786C\u65E0\u6BD4\u800C\u6DA8\u75DB\u96BE\u8010\u3002</p><p>\u300C\u554A\u554A\u2026\u2026\u522B\u2026\u2026\u522B\u6015\u2026\u2026\u62C9\u2026\u2026\u62C9\u2026\u2026\u300D\u624B\u6307\u505C\u7559\u5728\u5996\u591C\u7684\u4E0B\u4F53\u8D8A\u4E45\uFF0C\u53D1\u60C5\u7684\u5A07\u5A9A\u8089\u4F53\u5C31\u8D8A\u52A0\u6FC0\u52A8\u96BE\u8010\uFF0C\u5DF2\u7ECF\u5B8C\u5168\u6E7F\u900F\u7684\u9A9A\u7A74\u5185\u518D\u4E5F\u53D7\u4E0D\u4E86\u5E78\u7537\u624B\u6307\u7684\u6C9F\u5F04\u4E0B\uFF0C\u5996\u591C\u7684\u53CC\u624B\u5F00\u59CB\u5F15\u5BFC\u7740\u5C11\u5E74\u4E00\u70B9\u4E00\u6EF4\u7684\u5C06\u6DF1\u57CB\u7684\u94F6\u73AF\u5F80\u5916\u62C9\u2026\u2026</p><p>\u300C\u5514\u2026\u2026\u55EF\uFF1F\u2026\u2026\u55EF\u2026\u2026\u554A\u554A\uFF01\u300D\u5E78\u7537\u8D8A\u6765\u8D8A\u89C9\u5F97\u4E0D\u592A\u5BF9\u52B2\uFF0C\u6C9F\u5F04\u51FA\u7684\u5C0F\u94F6\u73AF\u597D\u50CF\u62C9\u70BC\u4E00\u6837\uFF0C\u88AB\u4ED6\u4E00\u5206\u4E00\u5206\u7684\u5F80\u4E0A\u62BD\u5F00\u65F6\uFF0C\u5996\u591C\u7684\u5C0F\u5AE9\u7A74\u7ADF\u7136\u50CF\u5206\u5F00\u7684\u74E3\u819C\u8089\u7247\u4E0D\u505C\u9519\u5F00\uFF0C\u8D8A\u6765\u8D8A\u6F6E\u6E7F\u7684\u5185\u74A7\u5411\u4E0A\u8513\u5EF6\u5F00\u6765\uFF0C\u6574\u4E2A\u4EBA\u7684\u8EAB\u4F53\u6700\u540E\u7ADF\u50CF\u8863\u670D\u5939\u514B\u4E00\u6837\u5C31\u88AB\u5256\u6210\u4E86\u4E24\u534A\u3002</p><p>\u300C\u4F60\u2026\u2026\u4F60\u2026\u2026\u554A\u554A\u554A\uFF01\uFF01\u300D\u5E78\u7537\u5185\u5FC3\u611F\u5230\u65E0\u6BD4\u60CA\u6050\u5BB3\u6015\u7684\u5C16\u53EB\u51FA\u6765\uFF0C\u4F46\u4ECD\u662F\u6D3B\u751F\u751F\u8089\u4F53\u7684\u8BE1\u5F02\u8089\u529E\u5374\u4E0D\u505C\u55B7\u51FA\u9ECF\u6DB2\u4E0E\u9C9C\u8840\u7684\u6251\u5411\u5E78\u7537\u7684\u8EAB\u4F53\u4E0A\uFF0C\u5B9B\u5982\u5DE8\u5927\u7684\u8089\u5507\u6251\u5728\u8EAB\u4E0A\uFF0C\u4EE4\u4ED6\u8FDE\u53EB\u7684\u673A\u4F1A\u90FD\u6765\u4E0D\u53CA\uFF0C\u6574\u4E2A\u4EBA\u5C31\u5DF2\u7ECF\u88AB\u5305\u9644\u541E\u566C\u5728\u5996\u591C\u7EA4\u7EC6\u7626\u5C0F\u7684\u8EAF\u58F3\u5185\uFF01</p><p>\u300C\u5495\u565C\u2026\u2026\u5495\u2026\u2026\u565C\u2026\u2026\u300D\u5E78\u7537\u53EA\u89C9\u5F97\u8EAB\u4F53\u65E0\u6BD4\u7684\u7D27\u7EF7\u96BE\u53D7\uFF0C\u7741\u4E0D\u5F00\u7684\u53CC\u773C\u8BA9\u4ED6\u6050\u60E7\u7684\u4E0D\u65AD\u6323\u624E\u5C16\u53EB\uFF0C\u4F46\u5C31\u5728\u4E00\u77AC\u95F4\u7684\u65F6\u95F4\u91CC\u4ED6\u53D1\u89C9\u5230\u81EA\u5DF1\u773C\u775B\u7ADF\u7136\u65E9\u5DF2\u5F20\u5F00\u6765\u4E86\uFF0C\u800C\u5148\u524D\u7684\u6240\u6709\u4E0D\u9002\u4E5F\u597D\u50CF\u7A81\u7136\u4E4B\u95F4\u5C31\u5168\u90E8\u6D88\u5931\u4E0D\u89C1\u4E86\u4E00\u6837\u3002</p><p>\u300C\u554A\u554A\u2026\u2026\u8FD9\u2026\u2026\u8FD9\u662F\u600E\u4E48\u56DE\u4E8B\u2026\u2026\u554A\uFF01\u300D\u5168\u8EAB\u9ECF\u547C\u547C\u7684\u611F\u89C9\u8BA9\u5E78\u7537\u6709\u79CD\u65E2\u96BE\u53D7\u53C8\u75DB\u5FEB\u7684\u9519\u89C9\uFF0C\u89C6\u7EBF\u4E00\u79FB\u5230\u81EA\u5DF1\u4E0B\u4F53\u65F6\uFF0C\u8D6B\u7136\u5374\u53D1\u73B0\u80F8\u524D\u957F\u51FA\u4E86\u4E00\u5BF9\u80A5\u7F8E\u5706\u6ED1\u7684\u5927\u5976\u5B50\uFF0C\u800C\u4E14\u9634\u4E1B\u4E0B\u9762\u7684\u8089\u68D2\u6B64\u65F6\u7ADF\u5DF2\u4E0D\u7FFC\u800C\u98DE.</p><p>\u300C\u8FD9\u2026\u2026\u54C0\u554A\u2026\u2026\u8FD9\u2026\u2026\u300D\u4E0D\u4EC5\u5982\u6B64\uFF0C\u5F53\u4ED6\u7684\u6307\u5934\u4F38\u5230\u81EA\u5DF1\u539F\u672C\u5E94\u8BE5\u5B58\u5728\u7684\u6027\u5668\u5B98\u4E0A\u5934\u65F6\uFF0C\u8FD8\u53D1\u89C9\u5230\u7A00\u758F\u7684\u9634\u6BDB\u4E0B\u65B9\u7ADF\u591A\u51FA\u6765\u4E24\u7247\u5982\u5047\u5305\u6362\u7684\u5C0F\u5AE9\u5507\uFF0C\u7099\u70ED\u7684\u5507\u8089\u4E0A\u8FD8\u9690\u7EA6\u53EF\u4EE5\u611F\u89C9\u5230\u91CC\u9762\u6240\u5206\u6CCC\u51FA\u6765\u7684\u6E7F\u6DA6\u6DEB\u6DB2\u2026\u2026</p><p>\u300C\u6211\u2026\u2026\u6211\u7684\u4E1C\u897F\u2026\u2026\u5728\u2026\u2026\u5728\u91CC\u9762\uFF1F\u55EF\u2026\u2026\u554A\u554A\u2026\u2026\u300D\u80A1\u6DA8\u53D1\u9EBB\u7684\u611F\u89C9\u5728\u5973\u6027\u5316\u7684\u8089\u5507\u5185\u4F20\u6765\u9635\u9635\u9A9A\u52A8\u53D1\u75D2\u7684\u6ECB\u5473\uFF0C\u5168\u8EAB\u9178\u8F6F\u96BE\u53D7\u7684\u5E78\u7537\u5FCD\u4E0D\u4F4F\u7684\u53D1\u51FA\u5982\u540C\u5C11\u5973\u822C\u7684\u547B\u541F\u58F0\u3002</p><p>\u300C\u563B\u2026\u2026\u4E3B\u4EBA\u2026\u2026\u8FD9\u2026\u2026\u8FD9\u6837\u660E\u767D\u4E86\u5417\uFF1F\u300D\u5996\u591C\u719F\u6089\u7684\u58F0\u97F3\u7ADF\u7531\u5E78\u7537\u81EA\u5DF1\u5634\u5DF4\u91CC\u4F20\u4E86\u51FA\u6765\u3002</p><p>\u300C\u4F60\u2026\u2026\u6211\u2026\u2026\u600E\u4E48\u4E86\u2026\u2026\u600E\u4E48\u4F1A\u8FD9\u6837\u2026\u2026\u300D</p><p>\u300C\u563B\u563B\u2026\u2026\u5996\u591C\u672C\u6765\u5C31\u662F\u7CBE\u7075\u822C\u7684\u7075\u4F53\u2026\u2026\u53EF\u4EE5\u662F\u4F60\u5938\u4E0B\u7684\u6DEB\u5974\uFF0C\u4E5F\u53EF\u4EE5\u6210\u4E3A\u50CF\u4F60\u8EAB\u4E0A\u6240\u5E7B\u60F3\u7684\u8863\u7269\u4E00\u6837\u2026\u2026\u53EA\u8981\u4E3B\u4EBA\u5FC3\u91CC\u60F3\u8981\u4EC0\u4E48\u2026\u2026\u5996\u591C\u90FD\u80FD\u6210\u4E3A\u4E3B\u4EBA\u5FC3\u4E2D\u7684\u5404\u79CD\u6B32\u671B\u2026\u2026\u300D</p><p>\u300C\u4EC0\u2026\u2026\u4EC0\u4E48\u2026\u2026\u300D\u867D\u7136\u5E78\u7537\u4E0B\u4F53\u5DF2\u7ECF\u53D8\u6210\u8DDF\u5973\u4EBA\u4E00\u6837\u5149\u6E9C\u4E00\u7247\uFF0C\u4F46\u611F\u89C9\u4E0A\u81EA\u5DF1\u7684\u8089\u68D2\u5374\u597D\u50CF\u88AB\u5341\u5206\u6E29\u6696\u7684\u5C42\u5C42\u8089\u819C\u7ED9\u5305\u56F4\u8D77\u6765\uFF0C\u6BCF\u4EA7\u751F\u4E00\u4E1D\u4E1D\u6643\u52A8\uFF0C\u79C1\u5904\u91CC\u9762\u65E9\u5DF2\u52C3\u8D77\u7684\u9634\u830E\u5C31\u89C9\u5F97\u5341\u5206\u5174\u594B\u5230\u60F3\u5C04\u7CBE\u7684\u5730\u6B65\u3002</p><p>\u300C\u6211\u2026\u2026\u5514\u2026\u2026\u554A\u554A\uFF01\u600E\u4E48\u4F1A\u8FD9\u6837\u2026\u2026\u300D\u5E78\u7537\u5F3A\u5FCD\u7740\u60F3\u5C04\u7CBE\u7684\u5FF5\u5934\u6323\u624E\u7684\u56DB\u5904\u4E71\u6643\uFF0C\u5F53\u4ED6\u6CE8\u89C6\u5230\u81EA\u5DF1\u773C\u524D\u7684\u4E00\u9762\u5927\u94F6\u955C\u7684\u540C\u65F6\uFF0C\u8BB6\u5F02\u7684\u601D\u7EEA\u66F4\u662F\u6FC0\u52A8\u4E0D\u6BD4\u3002</p><p>\u56E0\u4E3A\u6620\u5165\u773C\u5E18\u7684\u5F62\u8C61\u5DF2\u7ECF\u4E0D\u518D\u662F\u5C11\u5E74\u90A3\u4FCA\u4FCF\u5FE7\u90C1\u7684\u8EAB\u5F71\uFF0C\u800C\u5DF2\u5C11\u5973\u822C\u5A07\u5AE9\u767D\u6670\u3001\u5982\u5047\u5305\u6362\u7684\u5996\u591C\u9B54\u5973\u6A21\u6837\u2026\u2026</p><p>\u300C\u4E3B\u4EBA\u7684\u6E34\u671B\u4E0D\u662F\u62E5\u6709\u50CF\u8FD9\u6837\u7F8E\u5999\u7684\u8EAB\u4F53\u5417\uFF1F\u4EE5\u540E\u2026\u2026\u4E0D\u7BA1\u4F55\u65F6\u90FD\u53EF\u4EE5\u5C06\u5996\u591C\u8EAB\u4F53\u5F53\u6210\u8863\u670D\u4E00\u6837\u7A7F\u4E0A\uFF0C\u4E5F\u53EF\u4EE5\u968F\u65F6\u968F\u5730\u7A7F\u4E0A\u81EA\u5DF1\u6700\u559C\u7231\u7684\u6027\u611F\u8863\u7269\uFF0C\u554A\u54C8\u2026\u2026\u300D\u5996\u591C\u4E00\u9762\u8BC9\u8BF4\u7684\u540C\u65F6\uFF0C\u5E78\u7537\u4F3C\u4E4E\u7ADF\u80FD\u591F\u611F\u53D7\u5230\u5979\u7684\u5174\u594B\u4E0E\u523A\u6FC0\u3002</p><p>\u300C\u6211\u2026\u2026\u8FD9\u2026\u2026\u554A\u54C8\u2026\u2026\u554A\u554A\u2026\u2026\u300D\u4E0D\u77E5\u81EA\u5DF1\u624B\u6307\u6B63\u5728\u62A0\u5F04\u7740\u9A9A\u7A74\u5185\u7684\u6E7F\u5507\u4E0E\u786C\u6838\u7684\u4ED6\uFF0C\u5C31\u8FD9\u6837\u5FEB\u901F\u7684\u968F\u540C\u8FD9\u5C11\u5973\u7684\u8EAB\u8EAF\uFF0C\u7B2C\u4E00\u6B21\u4F53\u9A8C\u5230\u5973\u4EBA\u8907\u6742\u7EF5\u5BC6\u7684\u7EDD\u9876\u9AD8\u6F6E\u3002</p><p>\u8BE1\u8C32\u7684\u662F\uFF0C\u6E7F\u7A74\u4E2D\u4E0D\u4EC5\u6EA2\u51FA\u5927\u91CF\u6676\u4EAE\u7684\u871C\u6DB2\u5916\uFF0C\u8FD8\u6DF7\u6742\u6709\u7537\u6027\u6D4A\u767D\u7684\u9ECF\u7A20\u6DB2\u4F53.</p><p>\u300C\u554A\u54C8\u2026\u2026\u54C8\u2026\u2026\u547C\u2026\u2026\u54C8\u2026\u2026\u54C8\u2026\u2026\u300D\u540C\u65F6\u4EA7\u751F\u4E24\u79CD\u6027\u5668\u5B98\u7684\u9AD8\u6F6E\u523A\u6FC0\uFF0C\u8BA9\u6D51\u8EAB\u62BD\u6410\u7684\u5E78\u7537\u51E0\u4E4E\u5174\u594B\u5230\u8981\u6655\u8FC7\u53BB\u4E86\u4E00\u6837\uFF0C\u4ECE\u6765\u6CA1\u60F3\u8FC7\u53D1\u6CC4\u4F1A\u6709\u5982\u6B64\u8907\u6742\u800C\u7F8E\u5999\u7684\u611F\u89C9\uFF0C\u7EDD\u7F8E\u7684\u8EAB\u4F53\u5185\u5C31\u5F00\u59CB\u8513\u5EF6\u51FA\u4E00\u79CD\u66F4\u52A0\u5F3A\u70C8\u9700\u7D22\u7684\u6B32\u671B\u3002</p><p>\u300C\u554A\u2026\u2026\u771F\u2026\u2026\u771F\u7684\u53EF\u4EE5\u5B8C\u6210\u6211\u60F3\u8981\u7684\u613F\u671B\u5417\uFF1F\u300D\u624D\u521A\u5C04\u7CBE\u7684\u80C6\u5C0F\u5C11\u5E74\uFF0C\u602F\u58F0\u58F0\u7684\u7591\u95EE\u9053\u3002</p><p>\u300C\u5230\u4E86\u73B0\u5728\u8FD8\u611F\u5230\u6000\u7591\u5417\uFF1F\u300D\u5996\u591C\u7684\u8BDD\u521A\u8BF4\u5B8C\uFF0C\u5E78\u7537\u773C\u524D\u7ADF\u7ACB\u523B\u51FA\u73B0\u597D\u51E0\u6392\u540A\u6302\u5F0F\u7684\u5185\u8863\u67B6\uFF0C\u800C\u4E14\u4E0A\u5934\u6BCF\u4E00\u4EF6\u6027\u611F\u73A9\u610F\u53EF\u5168\u90FD\u662F\u5E78\u7537\u5FC3\u76EE\u4E2D\u6240\u559C\u7231\u7684\u90A3\u79CD\u7C7B\u578B\u6A21\u6837\uFF0C\u6709\u857E\u4E1D\u3001\u82B1\u8FB9\u751A\u81F3\u662F\u76AE\u9769\u5236\u54C1\uFF0C\u6BCF\u4E00\u4EF6\u90FD\u662F\u7CBE\u96D5\u7EC6\u7422\u822C\u7684\u7EC6\u817B\u3001\u8D34\u8EAB\u3002</p><p>\u300C\u7A7F\u6234\u770B\u770B\u2026\u2026\u6709\u4E86\u8FD9\u6837\u7684\u8EAB\u4F53\u518D\u63A5\u89E6\u5982\u6B64\u7F8E\u5999\u7684\u4E1C\u897F\u540E\uFF0C\u5C06\u4F1A\u6709\u5B8C\u5168\u4E0D\u540C\u7684\u611F\u53D7\u4EA7\u751F\u5462\u2026\u2026\u300D\u5996\u591C\u7684\u58F0\u97F3\u5F77\u5F7F\u50CF\u9ED1\u6697\u4E2D\u6307\u5F15\uFF0C\u4E00\u70B9\u4E00\u6EF4\u7684\u5F15\u5BFC\u7740\u5E78\u7537\u4F53\u9A8C\u7740\u4ECE\u6765\u4E0D\u66FE\u60F3\u89C1\u7684\u5149\u602A\u523A\u6FC0\u3002</p><p>\u300C\u8212\u2026\u2026\u8212\u670D\u2026\u2026\u554A\u554A\u2026\u2026\u300D\u4E0A\u8EAB\u4ED4\u7EC6\u7684\u5957\u4E0A\u4E00\u4EF6\u7EA2\u8272\u9732\u80F8\u76AE\u9769\uFF0C\u5E78\u7537\u53C8\u6311\u4E86\u4E00\u4EF6\u6700\u8F7B\u8584\u8273\u4E3D\u7684\u82B1\u8FB9\u4E1D\u889C\u5957\u5728\u811A\u4E0A\uFF0C\u9165\u9EBB\u9EBB\u7684\u611F\u89C9\u76F4\u51B2\u5927\u8111.</p><p>\u300C\u554A\u54C8\u2026\u2026\u554A\u554A\u2026\u2026\u300D\u5931\u63A7\u7684\u53CC\u624B\u7ACB\u523B\u60F3\u81EA\u6170\u5374\u7D27\u63E1\u4E0D\u5230\u8089\u68D2\uFF0C\u53EA\u6709\u4E0D\u505C\u62A0\u5F04\u7740\u53D1\u75D2\u6F6E\u6E7F\u7684\u5C0F\u8089\u5507\uFF0C\u4E00\u65F6\u95F4\u8FD8\u638C\u63E1\u4E0D\u5230\u5973\u6027\u624B\u6DEB\u7684\u65B9\u6CD5\u3002</p><p>\u300C\u563F\u563F\u2026\u2026\u5F88\u8212\u670D\u5427\u2026\u2026\u63A5\u7740\u4F60\u8FD8\u4F1A\u60F3\u8981\u54C1\u5690\u66F4\u591A\u3001\u66F4\u7F8E\u5999\u7684\u6ECB\u5473\u5462\u2026\u2026\u300D</p><p>\u300C\u4EC0\u2026\u2026\u4E48\u2026\u2026\u5514\u5641\u2026\u2026\u300D\u8EAB\u4F53\u5F88\u5FEB\u9677\u5165\u6781\u5EA6\u4EA2\u594B\u7684\u624B\u6DEB\u72B6\u6001\u4E2D\uFF0C\u5E78\u7537\u65E0\u6CD5\u7406\u89E3\u5996\u591C\u8BDD\u4E2D\u7684\u610F\u601D\uFF0C\u4F46\u654F\u9510\u7684\u629A\u6478\u89E6\u611F\u5374\u5F88\u5FEB\u7531\u53CC\u811A\u9010\u6E10\u8513\u5EF6\u5230\u81EA\u5DF1\u7684\u8EAB\u4F53\u56DB\u80A2\u3002</p><p>\u300C\u5514\u2026\u2026\u662F\u8C01\u2026\u2026\u554A\u554A\uFF01\u300D\u5E78\u7537\u53D1\u89C9\u8EAB\u8FB9\u7ADF\u4E0D\u77E5\u4F55\u65F6\u591A\u51FA\u6765\u4E86\u4E09\u540D\u4E11\u964B\u7684\u58EE\u6C49\uFF0C\u4E00\u6837\u6D51\u8EAB\u8D64\u88F8\u88F8\u7684\u76EE\u9732\u51F6\u5149\uFF0C\u90AA\u6076\u8D2A\u5A6A\u7684\u5634\u89D2\u7661\u7661\u7684\u5BF9\u7740\u4ED6\u72C2\u7B11\u3002</p><p>\u300C\u4F60\u4EEC\u662F\u8C01\uFF1F\u4E0D\u2026\u2026\u4E0D\u8981\uFF01\u653E\u5F00\u6211\u2026\u2026\u5641\u5514\u2026\u2026\u300D\u5E78\u7537\u5F02\u53D8\u6210\u5C11\u5973\u822C\u7684\u8EAB\u4F53\u65E0\u529B\u52A0\u4EE5\u62B5\u6297\uFF0C\u88AB\u6293\u4F4F\u7684\u7EA4\u7EC6\u811A\u8E1D\u5F88\u5FEB\u7684\u4FBF\u88AB\u7537\u4EBA\u4EEC\u56FA\u5B9A\u62D8\u675F\u4F4F\uFF0C\u4E0D\u505C\u7231\u629A\u7684\u626D\u634F\u629A\u6478\u4EE4\u4ED6\u4E00\u9762\u89C9\u5F97\u5641\u5FC3\u4E0D\u5DF2\uFF0C\u4E00\u9762\u53C8\u602A\u5F02\u7684\u611F\u5230\u4EA2\u594B.</p><p>\u300C\u563F\u563F\u563F\u2026\u2026\u563F\u2026\u2026\u597D\u53EF\u7231\u7684\u5C0F\u5973\u5B69\uFF0C\u5978\u5979\u2026\u2026\u300D\u6076\u4E11\u7684\u7537\u4EBA\u597D\u50CF\u91CE\u517D\u4E00\u6837\uFF0C\u8FDE\u601D\u60F3\u90FD\u8DDF\u5355\u7EAF\u7684\u6027\u517D\u6CA1\u6709\u4E24\u6837\uFF0C\u4E00\u540D\u6EE1\u5634\u5782\u6EE1\u553E\u6DB2\u7684\u5641\u5FC3\u6C49\u5B50\uFF0C\u55C5\u4E86\u55C5\u90A3\u5C11\u5973\u767D\u7389\u822C\u7684\u751C\u7F8E\u5473\u9053\u540E\uFF0C\u5C31\u5C06\u4ED6\u7ED9\u5012\u8F6C\u8FC7\u8EAB\uFF0C\u628A\u81EA\u5DF1\u624B\u81C2\u822C\u7C97\u5927\u7684\u6DEB\u5177\u7ED9\u6413\u8FDB\u5230\u7EC6\u5C0F\u6E7F\u6DA6\u7684\u9A9A\u5507\u5185\u3002</p><p>\u300C\u554A\uFF01\u554A\u2026\u2026\u554A\u554A\uFF01\u300D\u4F5C\u68A6\u90FD\u4E0D\u6562\u76F8\u4FE1\u81EA\u5DF1\u4F1A\u88AB\u5982\u6B64\u51F6\u6076\u7684\u91CE\u4EBA\u5F3A\u5978\uFF0C\u5E78\u7537\u8DDF\u7B2C\u4E00\u6B21\u8EAB\u7ECF\u4EBA\u4E8B\u7684\u5C11\u5973\u6CA1\u6709\u4E24\u6837\uFF0C\u75DB\u82E6\u54ED\u6CE3\u7684\u627F\u53D7\u7740\u4E00\u6B21\u7C97\u66B4\u8FC7\u4E00\u6B21\u7684\u731B\u70C8\u649E\u51FB\u3002</p><p>\u300C\u5978\u5979\u2026\u2026\u5F3A\u5978\u5979\u2026\u2026\u563B\u563B\u563B\u2026\u2026\u300D\u53E6\u5916\u4E24\u540D\u4E11\u7537\u4E5F\u5206\u522B\u627E\u597D\u5C11\u5973\u8EAB\u4E0A\u7684\u5AE9\u7A74\u4F4D\u7F6E\uFF0C\u5C06\u90A3\u8165\u81ED\u65E0\u6BD4\u7684\u4E1C\u897F\u5C31\u94BB\u8FDB\u5230\u5BF9\u65B9\u7684\u654F\u611F\u90E8\u4F4D\u5185\uFF0C\u6599\u60F3\u4E0D\u5230\u4F1A\u662F\u5982\u6B64\u6FC0\u70C8\u7684\u8EAB\u8EAF\u7ACB\u523B\u5C31\u5641\u5410\u51FA\u80C3\u6DB2\uFF0C\u8089\u5507\u7981\u4E0D\u8D77\u51E0\u4E0B\u7684\u62BD\u9001\u4FBF\u5931\u7981\u7684\u5C3F\u51FA\u9EC4\u6D4A\u7684\u6C41\u6DB2\u3002</p><p>\u300C\u545C\u2026\u2026\u6211\u2026\u2026\u4E0D\u662F\u2026\u2026\u4E0D\u8981\u2026\u2026\u5641\u545C\u2026\u2026\u300D\u7A7F\u4E0A\u300C\u5973\u4F53\u300D\u624D\u521A\u8212\u670D\u6CA1\u6709\u591A\u4E45\uFF0C\u5E78\u7537\u5374\u5F77\u5F7F\u7ACB\u523B\u5C31\u6389\u8FDB\u5230\u65E0\u6BD4\u75DB\u82E6\u7684\u6DF1\u6E0A\u4E00\u6837\uFF0C\u54C0\u568E\u7684\u58F0\u97F3\u6C42\u52A9\u65E0\u95E8\u7684\u88AB\u7537\u4EBA\u4EEC\u6301\u7EED\u8E42\u8E8F\uFF0C\u5D29\u6E83\u7684\u6CEA\u6C34\u6325\u4E0D\u5C3D\u9178\u695A\u7684\u75BC\u75DB\u4E0E\u9690\u9690\u53D1\u51FA\u96BE\u4EE5\u60F3\u50CF\u7684\u5947\u5F02\u523A\u6FC0\u3002</p><p>\u300C\u6551\u2026\u2026\u6551\u6551\u6211\u2026\u2026\u5996\u2026\u2026\u591C\u2026\u2026\u6211\u4E0D\u8981\u4E86\u2026\u2026\u554A\u554A\u2026\u2026\u6211\u2026\u2026\u554A\uFF01\u300D</p><p>\u300C\u563B\u563B\u2026\u2026\u522B\u6015\u2026\u2026\u7B2C\u4E00\u6B21\u662F\u8FD9\u6837\u7684\u2026\u2026\u563B\u563B\u563B\u563B\u2026\u2026\u300D\u5B9B\u5982\u5632\u8BBD\u822C\u7684\u7B11\u58F0\u5728\u5E78\u7537\u7684\u8033\u8FB9\u60F3\u8D77\uFF0C\u5996\u591C\u5F77\u5F7F\u6E05\u695A\u7740\u8FD9\u4E00\u5207\u5C06\u53D1\u751F\u7684\u60E8\u5267\uFF0C\u4EFB\u7531\u5982\u6B64\u53EF\u6015\u7684\u4E8B\u60C5\u7EE7\u7EED\u7684\u5EF6\u4F38\u4E0B\u53BB\u3002</p><p>\u300C\u5641\u5455\u2026\u2026\u5641\u5641\u2026\u2026\u5495\u565C\u2026\u2026\u5455\u5455\u2026\u2026\u300D\u773C\u795E\u6700\u540E\u5B8C\u5168\u60E8\u767D\u7684\u5C11\u5E74\u795E\u7ECF\u5DF2\u7ECF\u7D27\u7EF7\u5230\u4E86\u51E0\u4E4E\u9519\u4E71\u7684\u5730\u6B65\u65F6\uFF0C\u5374\u5728\u6B64\u65F6\u6B64\u523B\uFF0C\u4E0D\u65AD\u63A5\u53D7\u7740\u7537\u4EBA\u4EEC\u4E00\u80A1\u53C8\u4E00\u80A1\u6DEB\u6D4A\u5641\u5FC3\u7684\u6CDB\u9EC4\u6D53\u6C41\u3002</p><p>\u300C\u6B27\u554A\u2026\u2026\u554A\u2026\u2026\u5455\u5455\u2026\u2026\u300D\u5E78\u7537\u7A81\u7136\u89C9\u5F97\u81EA\u5DF1\u5FC3\u810F\u5267\u70C8\u7684\u62BD\u75DB\uFF0C\u5F77\u5F7F\u88AB\u4EC0\u4E48\u5C16\u9510\u7684\u950B\u5229\u9F7F\u7259\u7ED9\u4E00\u53E3\u54AC\u65AD\u4F3C\u7684\uFF0C\u5931\u53BB\u5FC3\u8DF3\u7684\u540C\u65F6\u610F\u8BC6\u4E5F\u53D8\u5F97\u8D8A\u6765\u8D8A\u52A0\u6A21\u7CCA\uFF0C\u6781\u7AEF\u75DB\u82E6\u4E0E\u83AB\u540D\u60A6\u4E50\u6700\u540E\u9010\u6E10\u4EA4\u7EC7\u6210\u6DF7\u6C8C\u4E0D\u6E05\u654F\u9510\u77E5\u89C9.</p><p>\u300C\u563B\u563B\u563B\u563B\u2026\u2026\u8FC7\u763E\u5427\u2026\u2026\u5728\u4F60\u7B2C\u4E00\u6B21\u7684\u6D17\u793C\u4E2D\u540C\u65F6\u5931\u53BB\u5FC3\u810F\u662F\u591A\u4E48\u75DB\u5FEB\u7684\u4E00\u4EF6\u4E8B\uFF0C\u6162\u6162\u7684\u4E3B\u4EBA\u5C31\u4F1A\u9010\u6E10\u6E05\u9192\uFF0C\u800C\u4F60\u7684\u8EAB\u4F53\u4E5F\u4F1A\u6162\u6162\u7684\u4E00\u70B9\u4E00\u6EF4\u8DDF\u7740\u53D8\u5316\u2026\u2026\u300D</p><p>\u300C\u4E0D\u4EC5\u5C06\u62E5\u6709\u81F3\u9AD8\u65E0\u4E0A\u7684\u6DEB\u9B54\u7CBE\u6C14\uFF0C\u800C\u4E14\u8EAB\u4F53\u4E5F\u5C06\u540C\u65F6\u5177\u5907\u6709\u9634\u9633\u4E24\u6027\u7684\u7EDD\u4F26\u6027\u5668\u2026\u2026\u8FD9\u6837\u2026\u2026\u624D\u914D\u5F97\u4E0A\u4E3B\u4EBA\u539F\u672C\u5C0A\u8D35\u65E0\u5339\u7684\u771F\u5B9E\u6A21\u6837\u2026\u2026\u300D</p><p>\u300C\u563F\u563F\u563F\u563F\u2026\u2026\u563B\u563B\u2026\u2026\u300D\u9634\u90AA\u7684\u8BE1\u8C32\u7B11\u58F0\u5C31\u5728\u4E00\u5E45\u5E45\u60BD\u60E8\u53D8\u6001\u7684\u8089\u8650\u6DEB\u620F\u4E2D\uFF0C\u6301\u7EED\u7684\u5F3A\u5978\u7740\u4E00\u540D\u6DF1\u9677\u8FF7\u79BB\u7684\u5A07\u8273\u7F8E\u8089\u8EAB\u8EAF\uFF0C\u4E0D\u660E\u767D\u4F55\u65F6\u5C06\u4F1A\u7EC8\u6B62\uFF0C\u53EA\u542C\u89C1\u65AD\u65AD\u7EED\u7EED\u7684\u54C0\u568E\u60E8\u53EB\u58F0\u6301\u7EED\u7684\u56DE\u8361\u4E0D\u5DF2\uFF0C\u65E0\u6B62\u65E0\u4F11\u2026\u2026</p>',64);function t(i,c){return a}var _=e(r,[["render",t]]);export{_ as default};
