import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="\u7B2C\u5341\u4E5D\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E5D\u7AE0" aria-hidden="true">#</a> \u7B2C\u5341\u4E5D\u7AE0</h1><p>\u5E7D\u6697\u7684\u6DF7\u6C8C\u4E4B\u4E2D\uFF0C\u5904\u5728\u6D74\u5395\u4E2D\u7684\u5C0F\u7F8E\u83CA\u53EA\u89C9\u5F97\u5BD2\u610F\u8D8A\u6765\u8D8A\u6D53\uFF0C\u5728\u51C9\u723D\u7684\u590F\u591C\u4E2D\uFF0C\u4E0D\u77E5\u9053\u4E3A\u4F55\u8EAB\u4F53\u5374\u4E00\u76F4\u62DA\u547D\u7684\u6253\u7740\u54C6\u55E6\u3002</p><p>\u5929\u82B1\u677F\u4E0A\u7684\u706F\u5149\u4E0D\u77E5\u9053\u4E3A\u4F55\u5FFD\u660E\u5FFD\u4EAE\u7740\uFF0C\u5185\u5FC3\u611F\u5230\u5341\u5206\u6050\u60E7\u7684\u7F8E\u83CA\u4E00\u523B\u90FD\u4E0D\u60F3\u591A\u5446\uFF0C\u4F46\u4E0D\u77E5\u4ECE\u4EC0\u4E48\u65F6\u5019\u5F00\u59CB\uFF0C\u5979\u5374\u6765\u72ED\u5C0F\u7684\u5395\u6240\u51FA\u53E3\u4E5F\u627E\u4E0D\u5230\u65B9\u5411\u3002</p><p>\u300C\u554A\u2026\u2026\u8FD9\u662F\u600E\u4E48\u4E00\u56DE\u4E8B\uFF1F\u963F\u59E8\u2026\u2026\u5988\u5988\uFF01\u300D\u5FC3\u91CC\u4E0D\u505C\u5BB3\u6015\u5450\u558A\u7684\u5C0F\u5973\u5B69\uFF0C\u5F88\u5FEB\u53C8\u9677\u5165\u5230\u89C6\u7EBF\u4E00\u7247\u6F06\u9ED1\u7684\u6050\u6016\u666F\u8C61\u4E2D\uFF0C\u53EA\u80FD\u4E00\u9762\u6478\u7D22\u7740\u8FDB\u6765\u7684\u65B9\u5411\uFF0C\u5374\u5BF9\u56DB\u5468\u73AF\u5883\u611F\u5230\u65E0\u6BD4\u964C\u751F\u3002</p><p>\u8FD9\u6837\u7684\u7A7A\u95F4\u4E2D\uFF0C\u4E0D\u518D\u662F\u81EA\u5DF1\u6240\u719F\u6089\u7684\u73AF\u5883\uFF0C\u597D\u50CF\u88AB\u7A81\u7136\u8C03\u6362\u5230\u53E6\u5916\u4E00\u4E2A\u65F6\u7A7A\u822C\uFF0C\u8BA9\u5973\u5B69\u611F\u5230\u65E0\u6BD4\u7684\u964C\u751F\u4E0E\u4E0D\u5B89\u3002</p><p>\u300C\u54C7\uFF01\u2026\u2026\u54C7\u2026\u2026\u54C7\u2026\u2026\u54C7\uFF01\u300D\u7A81\u7136\u95F4\u4E00\u9053\u5A74\u513F\u7684\u54ED\u6CE3\u58F0\uFF0C\u5212\u5F00\u4E86\u773C\u524D\u7684\u4E00\u5207\u9ED1\u6697\uFF0C\u987A\u7740\u58F0\u97F3\u7684\u65B9\u5411\u5FEB\u8DD1\uFF0C\u5C0F\u7F8E\u83CA\u53EA\u5E0C\u671B\u80FD\u5C3D\u5FEB\u5954\u5F80\u4EBA\u591A\u7684\u5730\u65B9\uFF0C\u80FD\u591F\u6292\u89E3\u5185\u5FC3\u90A3\u7D27\u5F20\u7126\u8651\u7684\u5FAC\u5FA8\u601D\u7EEA.</p><p>\u300C\u6709\u6CA1\u6709\u4EBA\u554A\u2026\u2026\u6709\u6CA1\u2026\u2026\u554A\uFF01\u300D\u4E5F\u8BB8\u662F\u56E0\u4E3A\u8DD1\u592A\u5FEB\u7684\u5173\u7CFB\uFF0C\u811A\u8E1D\u4E0D\u826F\u65BC\u884C\u7684\u5E7C\u5973\u4E0D\u5C0F\u5FC3\u7684\u8DCC\u4E86\u4E00\u8DE4\uFF0C\u4F46\u5979\u6CA1\u6709\u54ED\u6CE3\uFF0C\u6293\u7740\u53D7\u4F24\u7684\u811A\u8E1D\u4E00\u9762\u547C\u558A\u7740\u4E00\u9762\u5F80\u524D\u65B9\u8D70\u53BB\u3002</p><p>\u300C\u54C7\u54C7\u2026\u2026\u54C7\u54C7\u2026\u2026\u54C7\u2026\u2026\u54C7\uFF01\u300D\u5F88\u5FEB\u7684\u5C31\u5728\u7F8E\u83CA\u7684\u773C\u524D\u5374\u51FA\u73B0\u4E86\u4E00\u5E45\u5F71\u50CF\u822C\u7684\u753B\u9762\uFF0C\u4E00\u540D\u5987\u5973\u9759\u9759\u7684\u8EBA\u5728\u6DF1\u9ED1\u7684\u6728\u5E8A\u4E0A\uFF0C\u8EAB\u65C1\u7684\u4FDD\u6BCD\u624B\u4E2D\u62B1\u7740\u54C7\u54C7\u5927\u54ED\u7684\u5C0F\u7537\u5A74\uFF0C\u53E6\u4E00\u8FB9\u753B\u9762\uFF0C\u5374\u662F\u4E00\u540D\u7537\u5B50\uFF0C\u624B\u91CC\u62FF\u7740\u767D\u4EAE\u5315\u9996\u4E0D\u505C\u8D70\u5411\u4FDD\u6BCD\u7684\u9762\u524D\u4F5C\u52BF\u8981\u523A\u4E0B\u53BB\u2026\u2026</p><p>\u300C\u554A\u554A\uFF01\u300D\u53EF\u6015\u7684\u753B\u9762\u7A81\u5982\u5176\u7136\u7684\u5413\u574F\u7F8E\u83CA\uFF0C\u5927\u58F0\u5C16\u53EB\u7684\u5C0F\u5E7C\u5973\u7ACB\u523B\u8DCC\u5750\u5728\u5730\u7684\u54ED\u4E86\u51FA\u6765\u3002</p><p>\u300C\u4E0D\uFF01\u4E0D\u8981\uFF01\u300D\u7A81\u7136\u95F4\uFF0C\u4F1F\u5927\u7684\u6BCD\u7231\u8BA9\u5E8A\u4E0A\u7684\u6BCD\u4EB2\u594B\u4E0D\u987E\u8EAB\u7684\u51B2\u4E0A\u524D\u4FDD\u62A4\u7537\u5A74\uFF0C\u753B\u9762\u91CC\u624B\u4E2D\u62FF\u7740\u51F6\u5668\u7684\u7537\u4EBA\u5F88\u5FEB\u88AB\u4E24\u5973\u5408\u529B\u7684\u63A8\u4E86\u5F00\u6765\uFF0C\u4F46\u7537\u5B50\u5374\u662F\u6EE1\u8179\u54C0\u4F24\u7684\u5BF9\u7740\u5987\u4EBA\u8BF4\u9053\u3002</p><p>\u300C\u65E5\u7167\u2026\u2026\u8FD9\u53C8\u4F55\u82E6\u2026\u2026\u4ED6\u4E00\u5B9A\u4E0D\u80FD\u6D3B\u547D\u554A\uFF01\u300D</p><p>\u300C\u4E0D\uFF01\u4E0D\u53EF\u4EE5\u2026\u2026\u4ED6\u7684\u6211\u7684\u5B69\u5B50\uFF01\u300D</p><p>\u300C\u4ED6\u751F\u7684\u65F6\u8FB0\u547D\u683C\u6709\u95EE\u9898\u2026\u2026\u662F\u4E2A\u6076\u6839\u554A\uFF01\u800C\u4E14\u2026\u2026\u53C8\u662F\u4F60\u8DDF\u6211\u7684\u79C1\u751F\u5B50\u2026\u2026\u738B\u82E5\u56DE\u6765\u77E5\u9053\u4E86\u2026\u2026\u6240\u6709\u4EBA\u90FD\u4E00\u6837\u4F1A\u6CA1\u547D\u7684\uFF01\u300D</p><p>\u300C\u6211\u4E0D\u7BA1\u2026\u2026\u545C\u545C\u2026\u2026\u6211\u7684\u5B69\u5B50\u2026\u2026\u662F\u6211\u7684\u5B69\u5B50\u2026\u2026\u300D\u5987\u4EBA\u6B7B\u547D\u62B1\u7740\u6A31\u5B50\u8BF4\u4EC0\u4E48\u4E5F\u4E0D\u80AF\u653E\u5F00\uFF0C\u4EFB\u7531\u90A3\u4E2A\u60C5\u592B\u7684\u7537\u5B50\u8BF4\u7834\u5634\u5507\u4E5F\u4E0D\u80FD\u5BB3\u6B7B\u81EA\u5DF1\u7684\u5B69\u5B50\u3002</p><p>\u300C\u8FD9\u2026\u2026\u8FD9\u662F\u2026\u2026\u300D\u7F8E\u83CA\u5982\u4ECA\u5DF2\u88AB\u773C\u524D\u7684\u666F\u8C61\u753B\u9762\u7ED9\u5B8C\u5168\u5438\u5F15\u4F4F\uFF0C\u4E0D\u660E\u767D\u8FD9\u4E9B\u7A7F\u7740\u6253\u626E\u597D\u50CF\u53E4\u4EE3\u4EBA\u7684\u5BF9\u8BDD\u5230\u5E95\u662F\u600E\u4E48\u4E00\u56DE\u4E8B\uFF0C\u6B63\u5F53\u4F38\u624B\u8981\u89E6\u6478\u753B\u9762\u65F6\uFF0C\u63A5\u7740\u5F71\u50CF\u53C8\u518D\u5EA6\u7684\u6539\u53D8\u4E86\u3002</p><p>\u300C\u54E5\u54E5\u2026\u2026\u4E0D\u8981\uFF01\u545C\u545C\u2026\u2026\u4E0D\u2026\u2026\u300D\u8FD9\u4E00\u6B21\uFF0C\u753B\u9762\u4E2D\u51FA\u73B0\u4E86\u5374\u662F\u4E00\u5BF9\u5E74\u5E7C\u7684\u5C0F\u5973\u7AE5\u8DDF\u5C0F\u5C11\u5E74\uFF0C\u7537\u5B69\u7528\u624B\u6390\u4F4F\u59B9\u59B9\u7684\u8033\u6735\u6076\u72E0\u72E0\u7684\u5F80\u4E00\u95F4\u5947\u602A\u7684\u5BC6\u5BA4\u91CC\u8D70\u53BB\uFF0C\u5E76\u4E14\uFF0C\u8FD8\u987A\u624B\u5C06\u539A\u91CD\u7684\u94C1\u95E8\u7ED9\u9501\u4E0A\u3002</p><p>\u300C\u4E0D\u8981\u2026\u2026\u545C\u545C\u2026\u2026\u54E5\u54E5\u4E0D\u8981\uFF01\u300D\u753B\u9762\u91CC\u7684\u5C0F\u5973\u5B69\u5E74\u7EAA\u4F3C\u4E4E\u6BD4\u7F8E\u83CA\u8FD8\u6709\u5E74\u5E7C\uFF0C\u4E24\u9897\u7075\u7738\u6CEA\u773C\u6C6A\u6C6A\u7684\u5927\u58F0\u54ED\u6CE3\uFF0C\u53EA\u89C1\u5C11\u5E74\u4E00\u70B9\u4E5F\u4E0D\u75BC\u60DC\u59B9\u59B9\u7684\u63B4\u4E86\u5979\u51E0\u4E2A\u5DF4\u638C\uFF0C\u751A\u81F3\u6076\u8A00\u6076\u8BED\u7684\u5927\u58F0\u6050\u5413\u624D\u4EE4\u5973\u5B69\u62BD\u62BD\u564E\u564E\u7684\u4E0D\u6562\u54ED\u51FA\u58F0\u97F3\u3002</p><p>\u300C\u54FC\u2026\u2026\u90A3\u4E9B\u53EF\u6076\u8BE5\u6B7B\u7684\u5927\u4EBA\u53EA\u4F1A\u6B3A\u8D1F\u6211\u800C\u5DF2\u2026\u2026\u603B\u6709\u4E00\u5929\u6211\u4F1A\u628A\u4ED6\u4EEC\u90FD\u6740\u6B7B\uFF01\u54FC\u2026\u2026\u4ED6\u4EEC\u6B3A\u8D1F\u6211\u2026\u2026\u6211\u5C31\u8981\u6B3A\u8D1F\u4ED6\u4EEC\u6700\u75BC\u7231\u7684\u5C0F\u591C\uFF01\u300D\u5C11\u5E74\u53CC\u773C\u53D8\u5F97\u6BB7\u7EA2\u800C\u53EF\u6015\uFF0C\u5F77\u5F7F\u5C31\u50CF\u4E2A\u5C0F\u6076\u9B54\u4E00\u822C\uFF0C\u4F38\u51FA\u624B\u6307\u5C06\u59B9\u59B9\u7684\u80CC\u90E8\u7ED9\u634F\u5230\u6DE4\u9752\u3002</p><p>\u300C\u554A\u554A\u2026\u2026\u4E0D\u2026\u2026\u9976\u4E86\u6211\u2026\u2026\u554A\u4F55\u2026\u2026\u545C\u545C\u2026\u2026\u545C\u54C7\u2026\u2026\u300D\u5C0F\u5973\u5B69\u5FCD\u4E0D\u4F4F\u75DB\u7684\u5927\u58F0\u54ED\u6CE3\uFF0C\u7136\u8FD9\u91CC\u5F77\u5F7F\u662F\u5EA7\u5F88\u9690\u5BC6\u7684\u7279\u6B8A\u5BC6\u5BA4\uFF0C\u4EFB\u7531\u5973\u5B69\u5982\u4F55\u54ED\u6CE3\uFF0C\u5C31\u662F\u529E\u6CD5\u5C06\u58F0\u97F3\u7ED9\u4F20\u9012\u51FA\u53BB\u3002</p><p>\u300C\u6B7B\u5C0F\u591C\u2026\u2026\u6240\u6709\u4EBA\u90FD\u53EA\u4F1A\u75BC\u4F60\u800C\u5DF2\u2026\u2026\u54E5\u54E5\u73B0\u5728\u4E5F\u597D\u597D\u75BC\u4F60\uFF01\u8981\u662F\u4F60\u6562\u544A\u8BC9\u4EFB\u4F55\u4EBA\uFF0C\u6211\u5C31\u6740\u6B7B\u4F60\u77E5\u9053\u5417\uFF1F\u300D\u6CA1\u60F3\u5230\u5E74\u7EAA\u8F7B\u8F7B\u7684\u5C0F\u7537\u5B69\u7ADF\u7136\u4F1A\u8BF4\u51FA\u8FD9\u822C\u65E0\u60C5\u53EF\u6015\u7684\u6076\u6BD2\u8BDD\u8BED\uFF0C\u53EA\u89C1\u59B9\u59B9\u7684\u81C2\u8180\u4E0A\u5DF2\u7ECF\u4F48\u6EE1\u4E86\u5927\u5927\u5C0F\u5C0F\u7684\u5404\u79CD\u6DE4\u4F24\uFF0C\u4F46\u60E8\u5FCD\u7684\u54E5\u54E5\u5374\u8FD8\u4E00\u70B9\u4E5F\u6CA1\u6709\u505C\u624B\u7684\u610F\u601D\u3002</p><p>\u300C\u4F4F\u2026\u2026\u4F4F\u624B\uFF01\u300D\u7F8E\u83CA\u4F3C\u4E4E\u518D\u4E5F\u53D7\u4E0D\u4E86\u8FD9\u6837\u53EF\u6015\u7684\u753B\u9762\uFF0C\u4E00\u4F38\u624B\u7684\u540C\u65F6\uFF0C\u773C\u524D\u7684\u5F71\u50CF\u53C8\u5F00\u59CB\u8F6C\u53D8\u6210\u53E6\u5916\u4E00\u5E45\u573A\u666F\u3002</p><p>\u300C\u5514\u2026\u2026\u54E5\u2026\u2026\u554A\u554A\u2026\u2026\u300D\u773C\u524D\u4F3C\u4E4E\u4F9D\u7136\u662F\u90A3\u5BF9\u5144\u59B9\uFF0C\u4F46\u5C11\u5E74\u7684\u8EAB\u5F62\u660E\u663E\u6BD4\u4E4B\u524D\u8981\u957F\u5927\u5F88\u591A\uFF0C\u800C\u5973\u7AE5\u7684\u5E74\u7EAA\u5219\u4E0E\u73B0\u5728\u7684\u7F8E\u83CA\u76F8\u4EFF\u3002</p><p>\u300C\u563B\u563B\u2026\u2026\u5C0F\u591C\u2026\u2026\u5DF2\u7ECF\u8FC7\u4E86\u534A\u5929\u591A\u7684\u65F6\u95F4\uFF0C\u4E00\u5B9A\u60F3\u54E5\u54E5\u60F3\u7684\u8981\u547D\u5427\u2026\u2026\u300D</p><p>\u5C11\u5E74\u7684\u58F0\u97F3\u4F3C\u4E4E\u5F00\u59CB\u53D8\u58F0\uFF0C\u5E94\u8BE5\u662F\u5230\u4E86\u9752\u6625\u671F\u7684\u5E74\u7EAA\uFF0C\u59B9\u59B9\u7684\u4F53\u578B\u5219\u8FD8\u662F\u5A07\u5C0F\u7684\u5E7C\u5973\u578B\u6001\uFF0C\u4F46\u80BF\u5927\u7684\u809A\u5B50\u4F3C\u4E4E\u900F\u9732\u7740\u6709\u4E9B\u5F02\u5E38\u3002</p><p>\u300C\u62FF\u2026\u2026\u62FF\u51FA\u6765\u2026\u2026\u6C42\u6C42\u4F60\u2026\u2026\u6211\u6C42\u6C42\u4F60\u2026\u2026\u554A\u2026\u2026\u554A\u5455\u2026\u2026\u300D\u5973\u7AE5\u7684\u8EAB\u4F53\u597D\u50CF\u5341\u5206\u865A\u5F31\u7684\u62DA\u547D\u98A4\u6296\uFF0C\u5C24\u5176\u809A\u5B50\u597D\u50CF\u5267\u70C8\u7EDE\u75DB\u4E00\u822C\u7684\u7FFB\u6EDA\u547B\u541F\u3002</p><p>\u300C\u522B\u88C5\u6B7B\u2026\u2026\u6015\u75DB\u7684\u8BDD\u5C31\u5FEB\u70B9\u8FC7\u6765\u8BA9\u6211\u8212\u670D\u2026\u2026\u300D\u6CA1\u60F3\u5230\u5C31\u5728\u54E5\u54E5\u7684\u793A\u610F\u4E4B\u4E0B\uFF0C\u5C0F\u5973\u5B69\u7ADF\u7136\u5F3A\u81EA\u538B\u6291\u7740\u75DB\u82E6\uFF0C\u52C9\u5F3A\u722C\u8D77\u8EAB\u6765\u89E3\u5F00\u54E5\u54E5\u88E4\u7BA1\uFF0C\u5F20\u5F00\u5C0F\u5634\u5C31\u5E2E\u4E00\u6761\u80AE\u9AD2\u7684\u8089\u68D2\u542B\u8214\u8D77\u6765\u3002</p><p>\u300C\u563B\u563B\u2026\u2026\u8FD9\u51E0\u5929\u6211\u6545\u610F\u4E0D\u6D17\u5B83\uFF0C\u5473\u9053\u4E00\u5B9A\u5F88\u6D53\u5F88\u597D\u5403\u5427\uFF1F\u300D\u5C3D\u7BA1\u5C11\u5E74\u770B\u5F97\u51FA\u59B9\u59B9\u96BE\u8FC7\u7684\u8868\u60C5\u5FEB\u8981\u5455\u5410\u4E00\u822C\uFF0C\u4F46\u4ED6\u4F9D\u7136\u81EA\u5F97\u5176\u4E50\u7684\u7528\u529B\u628A\u80BF\u5927\u7684\u8089\u68D2\u7ED9\u585E\u7684\u66F4\u6DF1\uFF0C\u76F4\u6469\u64E6\u7740\u5589\u5499\u8BA9\u5979\u771F\u7684\u5455\u5410\u624D\u53C8\u7EE7\u7EED\u5F00\u59CB\u6298\u78E8\u5E7C\u5973\u3002</p><p>\u300C\u53EB\u4F60\u6BCF\u5929\u90FD\u8981\u7EC3\u4E60\u541E\u54E5\u54E5\u7684\u4E1C\u897F\uFF0C\u90FD\u7EC3\u5230\u54EA\u91CC\u53BB\u4E86\uFF1F\u600E\u4E48\u8FD9\u4E48\u7B28\u624B\u7B28\u811A\u2026\u2026\u8214\u4E7E\u5B83\uFF01\u300D</p><p>\u300C\u5BF9\u2026\u2026\u5BF9\u4E0D\u8D77\u2026\u2026\u554A\u554A\uFF01\u300D\u5C11\u5973\u4E00\u9762\u8BA4\u9519\uFF0C\u4E00\u9762\u8FD8\u62DA\u547D\u8214\u7740\u5C11\u5E74\u8089\u68D2\u4E0A\u6240\u6B8B\u7559\u4E0B\u7684\u5455\u5410\u6B8B\u6E23\uFF0C\u4E00\u79CD\u88AB\u975E\u4EBA\u5BF9\u5F85\u8FC7\u7684\u6298\u78E8\uFF0C\u4F3C\u4E4E\u5728\u8FD9\u5E74\u5E7C\u7684\u5973\u5B69\u5FC3\u7075\u4E2D\uFF0C\u5DF2\u7ECF\u9020\u6210\u4E86\u96BE\u4EE5\u78E8\u706D\u7684\u53EF\u6015\u9634\u5F71\u3002</p><p>\u300C\u563B\u563B\u2026\u2026\u544A\u8BC9\u6211\u2026\u2026\u6211\u517B\u7684\u5C0F\u4E1C\u897F\u73B0\u5728\u600E\u4E48\u6837\u4E86\uFF1F\u563F\u563F\u2026\u2026\u6211\u65E9\u5C31\u8FEB\u4E0D\u6025\u5F85\u60F3\u770B\u770B\u2026\u2026\u300D\u5C11\u5E74\u4F3C\u4E4E\u5728\u671F\u5F85\u7740\u4EC0\u4E48\u6709\u8DA3\u7684\u4E8B\u4E00\u6837\uFF0C\u547D\u4EE4\u59B9\u59B9\u8F6C\u8FC7\u8EAB\u53BB\uFF0C\u8DDF\u7740\u626F\u5F00\u5973\u5B69\u7684\u8863\u88E4\uFF0C\u5C31\u5728\u5E7C\u5973\u7C89\u7EA2\u8272\u7684\u809B\u95E8\u4E0A\uFF0C\u8D6B\u7136\u5370\u7740\u4E00\u6392\u7EA2\u8272\u7684\u5C01\u5370\u5492\u3002</p><p>\u63A5\u7740\uFF0C\u5C11\u5E74\u5C31\u5C06\u624B\u638C\u653E\u5728\u59B9\u59B9\u7684\u6C9F\u80A1\u95F4\uFF0C\u5E76\u9ED8\u5FF5\u7740\u4E00\u6BB5\u53E4\u602A\u7684\u7279\u6B8A\u5492\u8BED\uFF0C\u7537\u5B69\u4F3C\u4E4E\u66FE\u7ECF\u5B66\u4E60\u8FC7\u541F\u5531\u9B54\u6CD5\uFF0C\u53EA\u89C1\u7EA2\u8272\u7684\u5370\u8BB0\u624D\u4E00\u6D88\u901D\uFF0C\u5973\u5B69\u7684\u809B\u95E8\u5185\u7ACB\u523B\u6709\u6839\u6BDB\u8338\u8338\u7684\u4E1C\u897F\u4F38\u51FA\u4E86\u5C41\u773C\u5916\u2026\u2026</p><p>\u300C\u554A\u554A\uFF01\u300D\u7F8E\u83CA\u4E0D\u4EC5\u9A6C\u4E0A\u88AB\u773C\u524D\u7684\u666F\u8C61\u7ED9\u5413\u574F\u4E86\uFF0C\u5E76\u4E14\u8FD8\u4E0D\u7531\u81EA\u4E3B\u7684\u4F38\u624B\u5230\u81EA\u5DF1\u540E\u9762\u7684\u809B\u95E8\u4E0A\uFF0C\u4E00\u79CD\u8BF4\u4E0D\u51FA\u7684\u6050\u60E7\u4E0E\u719F\u6089\u611F\u89C9\u6B63\u5728\u8111\u6D77\u4E2D\u9010\u6E10\u6210\u5F62\u3002</p><p>\u300C\u563B\u563B\u2026\u2026\u53EF\u7231\u7684\u5C0F\u4E1C\u897F\u2026\u2026\u5728\u91CC\u9762\u4E00\u5B9A\u5F88\u4E0D\u8212\u670D\u5427\uFF0C\u5403\u9971\u4E86\u6CA1\u6709\uFF1F\u59B9\u59B9\u6240\u751F\u4EA7\u51FA\u6765\u7684\u4E1C\u897F\u597D\u4E0D\u597D\u5403\uFF1F\u54C8\u54C8\u54C8\u54C8\u2026\u2026\u300D\u5F88\u5FEB\u7684\u4E00\u6761\u50CF\u4F3C\u880D\u5B50\u53C8\u5411\u8783\u87F9\u7684\u6DF7\u5408\u602A\u866B\u5C31\u7531\u5C11\u5973\u7684\u80A0\u9053\u5185\u722C\u4E86\u51FA\u6765\u2026\u2026</p><p>\u7537\u5B69\u4F3C\u4E4E\u5341\u8DB3\u50CF\u4E2A\u53D8\u6001\u7684\u5C0F\u6076\u9B54\u4E00\u822C\uFF0C\u4E00\u9762\u5632\u7B11\u7740\u5C11\u5973\uFF0C\u4E00\u9762\u628A\u73A9\u7740\u4ED6\u90A3\u6761\u5641\u5FC3\u53EF\u6015\u7684\u5927\u866B\u5B50\uFF0C\u4EFB\u7531\u5B83\u6500\u722C\u5728\u5C11\u5973\u7684\u56DB\u80A2\u80CC\u90E8\u4E0A\u3002</p><p>\u300C\u547C\u2026\u2026\u547C\u2026\u2026\u5641\u5455\u2026\u2026\u300D\u5C31\u5728\u602A\u866B\u722C\u51FA\u5973\u5B69\u7684\u8089\u4F53\u5916\u540C\u65F6\uFF0C\u518D\u4E5F\u5FCD\u8010\u4E0D\u4F4F\u6781\u7AEF\u75DB\u82E6\u7684\u5C0F\u5E7C\u5973\uFF0C\u809B\u95E8\u5185\u95ED\u9501\u4E0D\u4F4F\u65AD\u65AD\u7EED\u7EED\u55B7\u51FA\u5C4E\u4FBF\uFF0C\u7C89\u7EA2\u8272\u7684\u5C0F\u9634\u5507\u4E5F\u5F00\u59CB\u4E0D\u65AD\u7684\u6EA2\u51FA\u8D8A\u6765\u8D8A\u591A\u7684\u9EC4\u8272\u5C3F\u6DB2\u3002</p><p>\u300C\u554A\u2026\u2026\u300D\u770B\u5230\u8FD9\u6837\u7684\u753B\u9762\u65F6\uFF0C\u7F8E\u83CA\u7A81\u7136\u611F\u5230\u8EAB\u4F53\u4E00\u9635\u8E81\u70ED\u96BE\u5F53\uFF0C\u5C24\u5176\u81EA\u5DF1\u809B\u95E8\u5185\u7684\u76F8\u540C\u4F4D\u7F6E\u4E0A\uFF0C\u4F3C\u4E4E\u4E5F\u5728\u4E0D\u81EA\u89C9\u7684\u6536\u7F29\u8815\u52A8\u7740\u2026\u2026</p><p>\u300C\u563B\u563B\u2026\u2026\u5C0F\u4E1C\u897F\u2026\u2026\u4F60\u60F3\u6362\u5230\u524D\u9762\u7684\u5730\u65B9\u53BB\u5417\uFF1F\u563F\u2026\u2026\u4E0D\u884C\u2026\u2026\u8FD8\u4E0D\u884C\uFF0C\u82E5\u662F\u59B9\u59B9\u524D\u9762\u7684\u5730\u65B9\u4E5F\u88AB\u523A\u7834\u7684\u8BDD\uFF0C\u90A3\u4EE5\u540E\u5C31\u4E0D\u80FD\u7EE7\u627F\u300C\u795E\u5973\u300D\u7684\u804C\u4F4D\u4E86\u2026\u2026\u5230\u65F6\u4E00\u5B9A\u4F1A\u88AB\u4EBA\u53D1\u73B0\u7684\uFF0C\u4E0D\u884C\u2026\u2026\u8981\u4E56\u4E56\u5594\u2026\u2026\u300D\u5C11\u5E74\u4F3C\u4E4E\u5BF9\u4E00\u6761\u6BD2\u866B\u6BD4\u5BF9\u81EA\u5DF1\u59B9\u59B9\u8FD8\u8981\u75BC\u7231\u3002</p><p>\u300C\u545C\u545C\u2026\u2026\u505C\u6B62\u2026\u2026\u5FEB\u505C\u6B62\uFF01\u300D\u770B\u7740\u8FD9\u822C\u4E0D\u65AD\u91CD\u8907\u7740\u7684\u53EF\u6015\u666F\u8C61\uFF0C\u65E9\u5DF2\u6BDB\u9AA8\u609A\u7136\u3001\u6447\u6447\u6B32\u5760\u7684\u5C0F\u7F8E\u83CA\uFF0C\u5FCD\u4E0D\u4F4F\u53C8\u4F38\u624B\u66F4\u6DF1\u5165\u8FDB\u5230\u753B\u9762\u4E2D\uFF0C\u5374\u6CA1\u60F3\u5230\u4E00\u80A1\u5F3A\u5927\u7684\u5438\u529B\u5C31\u8FD9\u6837\u7684\u628A\u5979\u7ED9\u5B8C\u5168\u7684\u5438\u4E86\u8FDB\u53BB\uFF01</p><p>\u300C\u554A\u554A\u2026\u2026\u8FD9\u2026\u2026\u8FD9\u662F\u2026\u2026\u300D\u8FD9\u6B21\u2026\u2026\u7F8E\u83CA\u662F\u771F\u7684\u5BB3\u6015\u4E86\uFF0C\u56E0\u4E3A\u56DB\u5468\u7684\u73AF\u5883\u5DF2\u7ECF\u4E0D\u518D\u662F\u5E73\u9762\u7684\u5F71\u50CF\u800C\u5DF2\uFF0C\u7537\u5B69\u7684\u8EAB\u5F71\u5386\u5386\u5728\u76EE\u7684\u77D7\u7ACB\u773C\u524D\uFF0C\u56DB\u5468\u7684\u73AF\u5883\u4E5F\u5168\u90FD\u53D8\u6210\u4E3A\u8DDF\u771F\u5B9E\u7684\u5B9E\u4F53\u4E00\u822C\u3002</p><p>\u300C\u54A6\u2026\u2026\u4F60\u521A\u521A\u5728\u53D1\u4EC0\u4E48\u5446\uFF1F\u300D\u5C11\u5E74\u7ADF\u7136\u5BF9\u7740\u7F8E\u83CA\u8FD9\u6837\u95EE\u9053\uFF0C\u597D\u50CF\u7F8E\u83CA\u6B63\u662F\u4ED6\u90A3\u4E2A\u60B2\u60E8\u53EF\u601C\u7684\u59B9\u59B9\u4E00\u6837\uFF0C\u4F38\u624B\u5C31\u5F80\u5979\u90A3\u8FD8\u4E3A\u53D1\u80B2\u7684\u80F8\u90E8\u4E0A\u6478\u53BB\u3002</p><p>\u300C\u545C\u554A\u2026\u2026\u4E0D\u8981\u2026\u2026\u554A\u554A\uFF01\u300D\u7F8E\u83CA\u66F4\u8BB6\u5F02\u7684\u60CA\u89C9\u4E86\uFF0C\u81EA\u5DF1\u679C\u771F\u5C31\u50CF\u662F\u65B9\u7E94\u90A3\u4E2A\u53D7\u5C3D\u6298\u78E8\u7684\u5C0F\u5973\u5B69\uFF0C\u9F13\u9F13\u7684\u809A\u5B50\u91CC\u4F3C\u4E4E\u6709\u4E1C\u897F\u6B63\u5728\u8815\u52A8\u7740\uFF0C\u4E00\u79CD\u6414\u75D2\u523A\u75DB\u7684\u53E4\u602A\u611F\u53D7\u6B63\u5728\u5979\u7684\u8EAB\u4F53\u5185\u4E0D\u65AD\u6269\u6563\u3002</p><p>\u300C\u563B\u563B\u2026\u2026\u5C0F\u591C\u2026\u2026\u4F60\u7ADF\u7136\u8D76\u53CD\u6297\u6211\uFF1F\u563F\u563F\u2026\u2026\u5F88\u597D\uFF0C\u8FD9\u6837\u5F88\u597D\uFF0C\u4F60\u5DF2\u7ECF\u6709\u4E09\u3001\u56DB\u5E74\u90FD\u6CA1\u6709\u505A\u51FA\u8FD9\u79CD\u53CD\u5E94\u4E86\u2026\u2026\u662F\u56E0\u4E3A\u5FEB\u751F\u4E0B\u5C0F\u5B9D\u5B9D\u6240\u9020\u6210\u7684\u539F\u56E0\u5417\uFF1F\u300D</p><p>\u300C\u4EC0\u2026\u2026\u4E48\u2026\u2026\u4F60\u8BF4\u4EC0\u4E48\uFF1F\u300D\u8EAB\u4EFD\u53D8\u6210\u5C0F\u591C\u7684\u7F8E\u83CA\u8EAB\u4F53\u4E0D\u4F4F\u7684\u5F80\u540E\u9000\uFF0C\u773C\u524D\u7684\u8FD9\u540D\u5C11\u5E74\u4E0D\u4EC5\u662F\u4E2A\u4E0D\u6298\u4E0D\u6263\u7684\u5C0F\u6076\u9B54\uFF0C\u800C\u4E14\u4EC0\u4E48\u6837\u53EF\u6015\u7684\u4E8B\u4E5F\u505A\u5F97\u51FA\u6765\uFF0C\u542C\u4ED6\u53E3\u4E2D\u8FD9\u4E48\u8BF4\u9053\uFF0C\u4E0D\u77E5\u53C8\u6709\u4EC0\u4E48\u6050\u6016\u7684\u9634\u8C0B\u53D1\u751F\u5728\u5979\u8EAB\u4E0A\uFF01</p><p>\u300C\u563B\u563B\u2026\u2026\u4E3A\u4E86\u8BA9\u4F60\u5728\u4FDD\u6709\u5904\u5973\u4E4B\u8EAB\u7684\u60C5\u51B5\u4E0B\u751F\u51FA\u8FD9\u5B69\u5B50\u2026\u2026\u6211\u53EF\u8D39\u4E86\u4E0D\u5C11\u529F\u592B\uFF0C\u751A\u81F3\u8FD8\u5077\u770B\u8FC7\u5E08\u5085\u7684\u79D8\u5BC6\u6CD5\u5178\u2026\u2026\u54FC\u54FC\uFF0C\u5728\u7236\u4EB2\u90A3\u4E2A\u6B7B\u8001\u5934\u56DE\u6765\u524D\uFF0C\u4E00\u5B9A\u4F1A\u8BA9\u4F60\u751F\u4E0B\u2026\u2026\u300D</p><p>\u300C\u554A\u2026\u2026\u554A\uFF01\u4E0D\u2026\u2026\u4E0D\u8981\u2026\u2026\u522B\u8FC7\u6765\u2026\u2026\u4E0D\u8981\uFF01\u300D\u75AF\u72C2\u7684\u98A4\u6296\u7684\u54ED\u53EB\u7740\uFF0C\u90AA\u6076\u7684\u9634\u5F71\u5982\u4ECA\u5DF2\u7ECF\u5B8C\u5168\u7684\u7B3C\u7F69\u5728\u8FD9\u5BB3\u6015\u83AB\u540D\u7684\u5C0F\u7A1A\u5973\u8EAB\u4E0A\uFF0C\u4E0D\u660E\u767D\u4E3A\u4F55\u4F1A\u6F14\u53D8\u5230\u8FD9\u822C\u610F\u60F3\u4E0D\u5230\u7684\u53EF\u6015\u60C5\u5883\u4E4B\u4E2D\uFF0C\u65E0\u8F9C\u7684\u5C11\u5973\u4E0D\u505C\u7684\u7F29\u745F\u9003\u907F\uFF0C\u9003\u907F\u90A3\u4E2A\u88AB\u79F0\u505A\u54E5\u54E5\u7684\u6076\u9B54\uFF0C\u7A76\u7ADF\u2026\u2026\u5C06\u4F1A\u5982\u4F55\u7684\u6298\u78E8\u81EA\u5DF1\u2026\u2026</p>',45);function t(i,c){return r}var o=e(a,[["render",t]]);export{o as default};
