import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="\u7B2C\u5341\u4E03\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E03\u7AE0" aria-hidden="true">#</a> \u7B2C\u5341\u4E03\u7AE0</h1><p>\u6E05\u6668\uFF0C\u521A\u9732\u8138\u7684\u592A\u9633\u4E00\u70B9\u4E00\u6EF4\u5C06\u5149\u8292\u5C04\u5411\u4E86\u5B81\u9759\u7684\u5927\u5730\uFF0C\u6240\u6709\u7684\u4E8B\u7269\u4F3C\u4E4E\u6B63\u8981\u5F00\u59CB\u6D3B\u7EDC\u8D77\u6765\u65F6\uFF0C\u8EAB\u4E3A\u5DEB\u5973\u5BFC\u5E08\u7684\u795E\u4EE3\u6A31\u5B50\uFF0C\u603B\u662F\u7B2C\u4E00\u4E2A\u8FDB\u5165\u51C0\u8EAB\u7684\u6C64\u6D74\u91CC\u76E5\u6D17\u68B3\u5986\uFF0C\u5E76\u51C6\u5907\u597D\u7686\u4E0B\u6765\u5C06\u6559\u5BFC\u5DEB\u5973\u4EEC\u7684\u8BFE\u524D\u5DE5\u4F5C\u3002</p><p>\u5979\u6BCF\u5929\u603B\u662F\u5982\u6B64\u7684\u4E00\u677F\u4E00\u773C\u4E0D\u66FE\u61C8\u6020\uFF0C\u521A\u5979\u4E24\u4E2A\u59CA\u59CA\u4E00\u6837\uFF0C\u90FD\u662F\u6027\u60C5\u5F88\u5F3A\u7684\u5973\u4EBA\uFF0C\u624D\u4E8C\u5341\u4E94\u5C81\u7684\u5E74\u7EAA\uFF0C\u5374\u5DF2\u6709\u6210\u719F\u5973\u6027\u7684\u6C88\u7A33\u4E0E\u5E72\u7EC3\u3002</p><p>\u5979\u7684\u8138\u86CB\u5929\u751F\u5C31\u5F88\u767D\u6670\uFF0C\u751A\u81F3\u6BD4\u8309\u8389\u5B50\u6216\u767E\u5408\u5B50\u90FD\u8981\u96EA\u5AE9\uFF0C\u65E0\u7455\u7684\u74DC\u5B50\u8138\u86CB\u663E\u7684\u6709\u4E9B\u6D88\u7626\uFF0C\u5149\u6ED1\u7684\u808C\u80A4\u5F88\u5C11\u65BD\u4E0A\u4EFB\u4F55\u8102\u7C89\uFF0C\u67F3\u7709\u51E4\u773C\u7684\u9510\u5229\u795E\u8272\u8BA9\u4EBA\u4E00\u773C\u5C31\u80FD\u660E\u767D\uFF0C\u5979\u662F\u5C5E\u65BC\u53CD\u5E94\u673A\u667A\u5341\u5206\u806A\u654F\u7684\u90A3\u79CD\u5973\u4EBA\u3002</p><p>\u56E0\u4E3A\u5979\u7684\u806A\u660E\u673A\u8B66\uFF0C\u5316\u89E3\u4E86\u795E\u4EE3\u5BB6\u4E00\u573A\u65E0\u53EF\u9884\u6599\u7684\u53EF\u6015\u9634\u8C0B\uFF0C\u80FD\u5728\u76F8\u9694\u6570\u767E\u5E74\u7684\u65F6\u95F4\u540E\u518D\u6B21\u987A\u5229\u6D88\u706D\u6389\u4E00\u5934\u5343\u5E74\u7684\u6076\u6BD2\u6DEB\u9B54\uFF0C\u7167\u7406\u793E\u91CC\u662F\u8BE5\u5927\u8086\u5E86\u795D\u624D\u5BF9\uFF0C\u4F46\u5728\u8FD9\u51E0\u5929\u7684\u795E\u4EE3\u5BB6\u4E2D\u5374\u6CA1\u6709\u4E00\u4E1D\u83B7\u80DC\u89E3\u8131\u7684\u79CD\u79CD\u559C\u60A6\uFF0C\u53CD\u800C\u662F\u60B2\u617C\u6101\u4E91\u7684\u8BE1\u5F02\u6C14\u6C1B\uFF0C\u4E0D\u505C\u7684\u7B3C\u7F69\u5728\u5145\u6EE1\u9ED1\u6697\u9634\u5F71\u7684\u83AB\u540D\u6050\u60E7\u4E2D\u3002</p><p>\u5C3D\u7BA1\u6076\u9B54\u7684\u610F\u5FF5\u5DF2\u7ECF\u786E\u5B9A\u5728\u4E24\u4EF6\u81F3\u5B9D\u7684\u50AC\u5316\u4E0B\u987A\u5229\u6D88\u706D\uFF0C\u4F46\u6A31\u5B50\u59CB\u7EC8\u4E0D\u77E5\u5728\u62C5\u5FC3\u4EC0\u4E48\u4F3C\u7684\u65E0\u6CD5\u91CA\u6000\uFF0C\u52A0\u4E0A\u59CA\u59CA\u7684\u4E00\u5BF9\u5B50\u5973\uFF0C\u5E78\u7537\u4E0E\u7F8E\u83CA\u81F3\u4ECA\u4ECD\u660F\u8FF7\u672A\u9192\uFF0C\u6C14\u82E5\u6E38\u4E1D\u7684\u5E78\u7537\u751A\u81F3\u8FD8\u4E0D\u65F6\u5455\u51FA\u5927\u91CF\u7684\u9ED1\u8272\u6C61\u7269\uFF0C\u865A\u5F31\u7684\u60C5\u51B5\u5F77\u5F7F\u968F\u65F6\u90FD\u53EF\u80FD\u4F1A\u731D\u6B7B\u4E00\u6837\u3002</p><p>\u53E6\u4E00\u65B9\u9762\u5927\u59CA\u7684\u60C5\u51B5\u4E5F\u5E76\u4E0D\u592A\u4E50\u89C2\uFF0C\u5C3D\u7BA1\u5979\u7684\u4EBA\u662F\u5B8C\u597D\u65E0\u7F3A\u7684\u6551\u56DE\u6765\u4E86\uFF0C\u4F46\u6BCF\u5F53\u591C\u91CC\u6765\u4E34\u65F6\uFF0C\u767E\u5408\u5B50\u603B\u662F\u4F1A\u4E0D\u65F6\u53D1\u51FA\u68A6\u9B47\u822C\u7684\u6DD2\u5389\u54C0\u568E\uFF0C\u53EF\u60F3\u800C\u77E5\u5F53\u65F6\u53D7\u5230\u4FB5\u72AF\u65F6\u7684\u9634\u5F71\u5DF2\u6709\u591A\u6DF1\uFF0C\u767D\u5929\u6709\u65F6\u751A\u81F3\u4F1A\u7A81\u7136\u660F\u53A5\u800C\u9677\u5165\u6655\u8FF7\uFF0C\u8BA9\u4EBA\u4E0D\u7531\u5F97\u5BF9\u5979\u7684\u8EAB\u4F53\u8B66\u8BAF\u611F\u5230\u62C5\u5FE7\u8D77\u6765\u3002</p><p>\u81F3\u65BC\u795E\u4EE3\u5BB6\u552F\u4E00\u8FD8\u6B63\u5E38\u7684\u6210\u5458\uFF0C\u9664\u4E86\u6A31\u5B50\u81EA\u5DF1\u4EE5\u5916\uFF0C\u5C31\u53EA\u5269\u4E0B\u7F8E\u6708\u8FD9\u4E2A\u5B64\u82E6\u7684\u5973\u5B69\u5B50\u4E86\uFF0C\u5931\u53BB\u6BCD\u4EB2\u7684\u4F9D\u8A17\u4E4B\u540E\uFF0C\u867D\u7136\u8FD8\u6709\u4E24\u4E2A\u963F\u59E8\u4F1A\u7167\u987E\u5979\uFF0C\u4F46\u5979\u90A3\u66FE\u7ECF\u5F00\u6717\u6D3B\u6CFC\u7684\u5929\u771F\u7B11\u989C\u4E2D\uFF0C\u5374\u53EF\u80FD\u6C38\u8FDC\u7684\u90FD\u5931\u53BB\u4E86\u4EE5\u5F80\u81EA\u5728\u5F00\u5FC3\u7684\u6B22\u559C\u6A21\u6837\u3002</p><p>\u4E00\u60F3\u5230\u8FD9\uFF0C\u6A31\u5B50\u7684\u5185\u5FC3\u5C31\u65E0\u6CD5\u5E73\u9759\u4E0B\u6765\uFF0C\u5979\u544A\u8BC9\u81EA\u5DF1\u7EDD\u5BF9\u4E0D\u80FD\u8BA9\u5BB6\u4EBA\u518D\u5EA6\u53D1\u751F\u4EFB\u4F55\u4E00\u70B9\u610F\u5916\uFF0C\u4E0D\u7BA1\u53D1\u751F\u4EC0\u4E48\u4E8B\uFF0C\u5979\u4E00\u5B9A\u4F1A\u6709\u529E\u6CD5\u8BA9\u8FD9\u4E2A\u5BB6\u6062\u590D\u5F80\u6614\u7684\u6E29\u99A8\u548C\u8C10.</p><p>\u51C0\u8FC7\u8EAB\u540E\uFF0C\u6A31\u5B50\u7ACB\u523B\u5C31\u5230\u767E\u5408\u5B50\u7684\u623F\u95F4\u53BB\uFF0C\u51C6\u5907\u5546\u8BAE\u540E\u7EED\u7684\u91CD\u5EFA\u5DE5\u4F5C\u65F6\uFF0C\u5374\u53D1\u89C9\u4E00\u5411\u65E9\u8D77\u7684\u59CA\u59CA\u4ECD\u672A\u8D77\u8EAB\uFF0C\u623F\u95E8\u5185\u751A\u81F3\u9690\u7EA6\u7684\u4F20\u6765\u4E00\u4E1D\u4F4E\u8FF7\u5A07\u5598\u7684\u547B\u541F\u58F0\uFF0C\u6A31\u5B50\u5BB3\u81CA\u7684\u8138\u86CB\u4E0D\u7981\u597D\u5947\u7684\u62AC\u5934\u671B\u4E86\u4E00\u773C\uFF0C\u8DDF\u7740\u5374\u662F\u6EE1\u8138\u7F9E\u7EA2\u7684\u5FEB\u6B65\u79BB\u5F00.</p><p>\u300C\u55EF\u554A\u2026\u2026\u554A\u554A\u2026\u2026\u554A\u2026\u2026\u300D\u623F\u5185\u7684\u7F8E\u5987\u7ADF\u7136\u8D64\u88F8\u7740\u4E0A\u8EAB\u7231\u629A\u7740\u5A07\u8EAF\uFF0C\u96BE\u8FC7\u7684\u8138\u8272\u4E2D\u50CF\u4F3C\u65E0\u6CD5\u5F97\u5230\u6EE1\u8DB3\u4E00\u6837\u7684\u62E8\u5F04\u7740\u81EA\u5DF1\u6E7F\u6DA6\u7684\u9A9A\u5507\uFF0C\u624B\u6DEB\u4E2D\u7684\u7231\u6DB2\u8FD8\u6CBE\u6E7F\u4E86\u767D\u8272\u7684\u5E8A\u5355\uFF0C\u7099\u70ED\u7684\u4F53\u6E29\u5728\u6210\u719F\u7684\u80F4\u4F53\u4E0A\u5B9B\u5982\u62B9\u4E0A\u4E00\u5C42\u6CB9\u8102\uFF0C\u9A9A\u52A8\u7684\u8EAB\u5F62\u5728\u9ED1\u6697\u4E2D\u5FAE\u5FAE\u7684\u62BD\u6410\u7740\u3002</p><p>\u300C\u59CA\u2026\u2026\u600E\u2026\u2026\u600E\u4E48\u4F1A\u2026\u2026\u300D\u6A31\u5B50\u5185\u5FC3\u96BE\u8FC7\u6323\u624E\u7740\u8981\u4E0D\u8981\u8FDB\u53BB\uFF0C\u4E0D\u613F\u591A\u60F3\u59CA\u59CA\u7684\u8EAB\u4F53\u662F\u54EA\u91CC\u51FA\u4E86\u4EC0\u4E48\u95EE\u9898\uFF0C\u629A\u6478\u7740\u81EA\u5DF1\u6B63\u5728\u53D1\u70EB\u7684\u53CC\u816E\uFF0C\u5FEB\u6B65\u7684\u4FBF\u79BB\u5F00\u767E\u5408\u5B50\u5BDD\u5BA4\u5916\u7684\u8D70\u9053\u3002</p><p>\u6700\u9634\u9669\u7684\u6076\u9B54\u5982\u4ECA\u65E9\u5DF2\u6B7B\u5728\u59CA\u59CA\u8DDF\u81EA\u5DF1\u7684\u624B\u91CC\uFF0C\u518D\u52A0\u5165\u767E\u5408\u5B50\u7684\u8EAB\u4F53\u73B0\u5728\u5DF2\u7ECF\u88AB\u4E00\u80A1\u6700\u5F3A\u5927\u7684\u81EA\u6211\u5C01\u5370\u529B\u91CF\u4FDD\u62A4\u7740\uFF0C\u6CA1\u53EF\u80FD\u8EAB\u4E0A\u8FD8\u6B8B\u5B58\u6709\u4EC0\u4E48\u65E0\u6CD5\u88AB\u7075\u80FD\u9664\u5374\u7684\u9B54\u529B\u624D\u5BF9\uFF0C\u8F83\u5408\u7406\u7684\u89E3\u91CA\u5E94\u662F\u5F53\u65F6\u7559\u4E0B\u7684\u6DF1\u523B\u9634\u5F71\u4ECD\u65E0\u6CD5\u7531\u767E\u5408\u5B50\u8111\u4E2D\u9664\u6389\uFF0C\u82E5\u679C\u771F\u662F\u8FD9\u6837\uFF0C\u6A31\u5B50\u4E5F\u53EA\u80FD\u9ED8\u9ED8\u7684\u7948\u7977\u59CA\u59CA\u80FD\u65E9\u65E5\u514B\u670D\u81EA\u5DF1\u5185\u5FC3\u4E2D\u7684\u8FD9\u5C42\u969C\u788D\u3002</p><p>\u6A31\u5B50\u505C\u4E0B\u811A\u6B65\u7684\u8BD5\u56FE\u8BA9\u81EA\u5DF1\u9057\u5FD8\u6389\u65B9\u624D\u770B\u5230\u7684\u4E00\u5207\u753B\u9762\uFF0C\u6574\u7406\u60A3\u5F97\u60A3\u5931\u7684\u60C5\u7EEA\u540E\uFF0C\u4E00\u9762\u4EBA\u5DF2\u7ECF\u5230\u4E86\u7F8E\u83CA\u7684\u623F\u95F4\u5916\u5934.</p><p>\u300C\u5C0F\u83CA\u2026\u2026\u5C0F\u83CA\uFF0C\u4F60\u8D77\u6765\u4E86\u5417\uFF1F\u300D\u5C4B\u5916\u7684\u95E8\u8FD8\u534A\u5F00\u7740\u5185\uFF0C\u5C4B\u5185\u4F3C\u4E4E\u4E5F\u6CA1\u6709\u4EFB\u4F55\u7684\u56DE\u5E94\uFF0C\u56DB\u5468\u4E00\u5207\u5B89\u9759\u7684\u4EE4\u4EBA\u611F\u5230\u6709\u4E9B\u4E0D\u5B89\uFF0C\u6A31\u5B50\u7684\u795E\u60C5\u4E0D\u7531\u5F97\u5C31\u5F00\u59CB\u7D27\u5F20\u4E86\u8D77\u6765\u3002</p><p>\u300C\u7F8E\u83CA\u2026\u2026\u7F8E\u83CA\uFF01\u4E0D\u597D\u2026\u2026\u300D\u6A31\u5B50\u5FC3\u91CC\u6697\u6697\u89C9\u5F97\u4E0D\u5999\uFF0C\u65BC\u662F\u4E00\u95F4\u623F\u4E00\u95F4\u623F\u7684\u5F00\u59CB\u5BFB\u627E\uFF0C\u5F53\u4ED6\u51C6\u5907\u53EB\u7F8E\u6708\u4E00\u5757\u5E2E\u5FD9\u7684\u540C\u65F6\uFF0C\u5374\u53D1\u89C9\u7F8E\u6708\u7684\u623F\u95E8\u4E5F\u662F\u534A\u5F00\u7740\uFF0C\u5E76\u4E14\u8D70\u5ECA\u4E0A\u8FD8\u6CBE\u7740\u4E0D\u5C11\u5947\u602A\u7684\u767D\u8272\u6DB2\u4F53\u2026\u2026</p><p>\u300C\u7F8E\u6708\uFF01\u300D\u5C3D\u7BA1\u6A31\u5B50\u7684\u5FC3\u91CC\u5DF2\u7ECF\u8D77\u4E86\u5F88\u5927\u7684\u8B66\u6212\uFF0C\u4F46\u5728\u62C9\u5F00\u623F\u95E8\u7684\u90A3\u4E00\u5239\u90A3\uFF0C\u5374\u7ACB\u523B\u88AB\u773C\u524D\u7684\u8BE1\u8C32\u753B\u9762\u7ED9\u5B8C\u5168\u5438\u5F15\u4F4F\u3002</p><p>\u6D51\u8EAB\u8D64\u88F8\u7684\u7F8E\u6708\u7ADF\u88AB\u4E00\u56E2\u56E2\u767D\u8272\u6D53\u7A20\u7684\u602A\u5F02\u9ECF\u6DB2\u7ED9\u7D27\u7D27\u7684\u62D8\u675F\u5728\u81EA\u5DF1\u5C4B\u6A11\u7684\u623F\u67F1\u4E4B\u4E0B\uFF0C\u6D51\u8EAB\u4E0A\u4E0B\u597D\u50CF\u6CBE\u6EE1\u4E86\u7CBE\u6DB2\u8DDF\u5976\u6C34\u7B49\u7C98\u547C\u547C\u7684\u767D\u8272\u4E1C\u897F\uFF0C\u534A\u4E7E\u7684\u6DEB\u7269\u4F3C\u4E4E\u9ECF\u6027\u7279\u5F3A\uFF0C\u7D27\u7B8D\u7684\u7C98\u6DB2\u8BA9\u5C11\u5973\u4E1D\u6BEB\u4E5F\u65E0\u6CD5\u6323\u8131\u7684\u4E0D\u65AD\u547B\u541F\u3002</p><p>\u300C\u545C\u545C\u2026\u2026\u5514\u2026\u2026\u545C\u2026\u2026\u300D\u7F8E\u6708\u7684\u5634\u5DF4\u91CC\u4E5F\u88AB\u767D\u8272\u7684\u9ECF\u6DB2\u7ED9\u585E\u7684\u8BF4\u4E0D\u51FA\u8BDD\u6765\uFF0C\u773C\u795E\u95F4\u8FF7\u79BB\u7684\u770B\u4E0D\u6E05\u695A\u89C6\u7EBF\u4E00\u6837\uFF0C\u8EAB\u4F53\u7684\u56DB\u5468\u8FD8\u4E0D\u65F6\u722C\u884C\u7740\u5E7C\u5C0F\u7684\u8815\u866B\u5728\u5979\u8EAB\u4E0A\u56F4\u7ED5.</p><p>\u300C\u7F8E\u6708\uFF01\u65E0\u62CF\u6C99\u5179\u7948\u591A\u2026\u2026\u65E0\u62CF\u6C99\u2026\u2026\u300D\u6A31\u5B50\u7709\u95F4\u6DF1\u9501\u4E0D\u7531\u5206\u8BF4\uFF0C\u796D\u8D77\u4E86\u770B\u5BB6\u7684\u7834\u90AA\u5492\u8BED\uFF0C\u53EA\u89C1\u7F8E\u6708\u8EAB\u4E0A\u7684\u5C0F\u866B\u5B50\u7ACB\u523B\u5C31\u50CF\u7740\u4E86\u706B\u7130\u822C\u7126\u5316\u6389\u843D\u5728\u5730\uFF0C\u6ECB\u6ECB\u4F5C\u54CD\u7684\u5168\u6570\u7070\u98DE\u6E6E\u706D\u7684\u788E\u6210\u7126\u70AD\u7EC6\u6C99\u3002</p><p>\u300C\u8FD9\u2026\u2026\u8FD9\u662F\u600E\u4E48\u4E00\u56DE\u4E8B\uFF1F\u600E\u4E48\u4F1A\u8FD9\u6837\u5462\uFF1F\u300D\u6A31\u5B50\u9664\u5374\u6389\u7F8E\u6708\u8EAB\u4E0A\u7684\u8BE1\u5F02\u866B\u5B50\u540E\uFF0C\u4FBF\u6781\u5C3D\u6240\u80FD\u7684\u8981\u628A\u7F8E\u6708\u8EAB\u4F53\u7531\u67F1\u5B50\u4E0A\u7ED9\u62C6\u89E3\u4E0B\u6765\uFF0C\u4F46\u8FD9\u624D\u53D1\u73B0\u6E29\u70ED\u7684\u767D\u8272\u9ECF\u6DB2\u4E2D\uFF0C\u4E0D\u4EC5\u6709\u80A1\u8BF4\u4E0D\u51FA\u7684\u8165\u9999\uFF0C\u9ECF\u7A20\u7684\u7A0B\u5EA6\u66F4\u662F\u8D85\u4E4E\u81EA\u5DF1\u6240\u60F3\u50CF\u3002</p><p>\u300C\u8FD9\u2026\u2026\u8FD9\u5230\u5E95\u662F\u4EC0\u4E48\u4E1C\u897F\uFF1F\u300D\u5F88\u5FEB\u7684\u5979\u7684\u53CC\u624B\u56DB\u80A2\u4E5F\u88AB\u8FD9\u79CD\u767D\u8272\u7684\u9ECF\u6DB2\u7ED9\u6CBE\u6D4A\u7684\u5230\u5904\u90FD\u662F\u800C\u96BE\u4EE5\u6446\u8131\uFF0C\u6D4A\u767D\u7684\u6DB2\u4F53\u4E00\u88AB\u62C9\u5F00\u5C31\u5B9B\u5982\u8718\u86DB\u7F51\u72B6\u822C\u7684\u7EA0\u7ED3\u6210\u4E1D\uFF0C\u575A\u97E7\u7684\u9ECF\u6027\u5C3D\u7BA1\u6A31\u5B50\u5982\u4F55\u62E8\u89E3\u5C31\u662F\u65E0\u6CD5\u987A\u5229\u5C06\u7F8E\u6708\u7ED9\u8131\u79BB\u5F00\u6765\u3002</p><p>\u300C\u554A\u2026\u2026\u8FD9\u2026\u2026\u8FD9\u662F\u2026\u2026\u300D\u66F4\u7CDF\u7CD5\u7684\u60C5\u51B5\u662F\u6A31\u5B50\u4E0D\u4EC5\u62C6\u4E0D\u4E0B\u7F8E\u6708\u7684\u8EAB\u4F53\uFF0C\u9ECF\u7A20\u7684\u6DB2\u4F53\u66F4\u4F3C\u4E4E\u7ECF\u8FC7\u62C9\u626F\u800C\u8D8A\u6765\u8D8A\u81A8\u80C0\u8513\u5EF6\uFF0C\u6700\u540E\u8FDE\u6551\u4EBA\u7684\u6A31\u5B50\u4E5F\u96BE\u4EE5\u79FB\u52A8\u7684\u88AB\u8FD9\u79CD\u8BF4\u4E0D\u51FA\u6709\u591A\u4E48\u602A\u5F02\u7684\u767D\u8272\u9ECF\u6DB2\u7ED9\u56F0\u4F4F\u4E86\u3002</p><p>\u300C\u563F\u563F\u2026\u2026\u300D\u7A81\u7136\u4E00\u58F0\u51B7\u7B11\u7684\u5973\u4EBA\u58F0\u97F3\u5728\u6A31\u5B50\u8EAB\u540E\u54CD\u8D77\u3002\uFF08\u6587_\u5187_\u4EBA\uFF0D\u5187-\u4E66\uFF0D\u5C4B\uFF0D\u8D3C\u5427\uFF3Aei\uFF18\u3002\uFF23\uFF2F\uFF2D\u7535\u5B50\u4E66\uFF09</p><p>\u300C\u8C01\uFF1F\u662F\u8C01\uFF01\u300D\u6A31\u5B50\u6050\u60E7\u7684\u5F80\u56DE\u5934\u770B\u53BB\uFF0C\u5374\u89C1\u5E7D\u6697\u7684\u623F\u95F4\u5185\u591A\u51FA\u4E86\u4E00\u9053\u9634\u5F71\uFF0C\u5728\u770B\u4E0D\u51FA\u7684\u5FAE\u5149\u4E2D\u5FEB\u901F\u79FB\u52A8\u3002</p><p>\u6050\u6016\u7684\u6C14\u606F\u8D8A\u6765\u8D8A\u7030\u6F2B\u7740\u6574\u4E2A\u5BC6\u95ED\u7684\u7A7A\u95F4\u91CC\u9762\uFF0C\u6A31\u5B50\u5185\u5FC3\u7684\u538B\u529B\u8D8A\u6765\u8D8A\u611F\u5230\u62C5\u5FE7\u6050\u614C\uFF0C\u56E0\u4E3A\u5979\u7684\u53CC\u624B\u5DF2\u7ECF\u65E0\u6CD5\u7ED3\u5370\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5982\u679C\u5979\u4E0D\u80FD\u5C3D\u5FEB\u8131\u79BB\u5F00\u8FD9\u8EAB\u9ECF\u7A20\u7684\u4E1C\u897F\u8BDD\uFF0C\u53EF\u80FD\u8FC7\u4E0B\u4E00\u4E2A\u53D8\u6210\u50CF\u7F8E\u6708\u4E00\u6837\u7684\u4FD8\u864F\uFF0C\u5C31\u4F1A\u662F\u5979\u4E86\u3002</p><p>\u300C\u5636\u5636\u2026\u2026\u5636\u2026\u2026\u5636\u2026\u2026\u300D\u5149\u5F71\u79FB\u52A8\u6240\u53D1\u51FA\u7684\u6469\u64E6\u58F0\u8BA9\u4EBA\u4E0D\u7531\u5F97\u6BDB\u9AA8\u609A\u7136\uFF0C\u4E0D\u505C\u9A9A\u52A8\u7684\u60C5\u5F62\u4F3C\u4E4E\u50CF\u5728\u5305\u56F4\u730E\u7269\u4E00\u6837\uFF0C\u5F88\u5FEB\u7684\u7070\u6697\u7684\u7A7A\u95F4\u5185\u7ADF\u56DB\u5904\u5145\u6EE1\u4E86\u767D\u8272\u76F8\u540C\u7684\u9ECF\u7A20\u6DB2\u4F53\uFF0C\u4E00\u9053\u4ECE\u5929\u5C04\u5165\u7684\u7EA2\u8272\u5F02\u5149\u8BA9\u6A31\u5B50\u7ACB\u523B\u7684\u660E\u767D\u5230\uFF0C\u81EA\u5DF1\u5DF2\u7ECF\u6DF1\u9677\u5728\u9B54\u7269\u6240\u521B\u9020\u51FA\u6765\u7684\u5F02\u5EA6\u7ED3\u754C\u4E4B\u4E2D\u3002</p><p>\u300C\u53EF\u6076\u2026\u2026\u597D\u7D27\u2026\u2026\u4F60\u662F\u8C01\uFF1F\u2026\u2026\u5FEB\u51FA\u6765\u2026\u2026\u300D\u6A31\u5B50\u65E0\u53EF\u5948\u4F55\u7684\u53EA\u80FD\u4EFB\u7531\u8FD9\u6837\u5371\u6025\u7684\u60C5\u51B5\u7EE7\u7EED\u53D1\u751F\uFF0C\u4E73\u767D\u8272\u7684\u5947\u5F02\u7A7A\u95F4\u9010\u6E10\u7684\u5C06\u6240\u6709\u50A2\u4FF1\u3001\u6446\u9970\u5168\u90E8\u8150\u8680\u541E\u6CA1\u8FDB\u53BB\uFF0C\u5F53\u7EA2\u8272\u7684\u5F02\u5149\u50CF\u70DB\u706B\u822C\u5C06\u6A31\u5B50\u4E0E\u7F8E\u6708\u56E2\u56E2\u56F4\u4F4F\u4E4B\u65F6\uFF0C\u5E73\u5766\u7684\u5730\u9762\u4E0A\u5374\u7A81\u7136\u4E0D\u65AD\u5192\u51FA\u5404\u79CD\u7EC6\u6BDB\u957F\u722A\u7684\u5C0F\u8718\u86DB\u3002</p><p>\u300C\u5514\u2026\u2026\u5514\uFF01\u554A\u2026\u2026\u5641\uFF01\u300D\u53EF\u6015\u7684\u5C0F\u4E1C\u897F\u4E0D\u65AD\u7684\u722C\u51FA\u6D1E\u53E3\uFF0C\u4EE4\u4EBA\u5641\u5FC3\u4F5C\u5455\u7684\u53EF\u6015\u753B\u9762\u4E0D\u65AD\u7684\u5F3A\u5217\u51B2\u51FB\u7740\u6A31\u5B50\u7684\u53CC\u773C\uFF0C\u8BB6\u5F02\u4E0D\u4F4F\u60CA\u5413\u7684\u611F\u89C9\u8BA9\u6700\u8BA8\u538C\u8718\u86DB\u7684\u6A31\u5B50\u80C3\u91CC\u5F00\u59CB\u7FFB\u8F6C\uFF0C\u6D51\u8EAB\u51B0\u51B7\u5230\u5FEB\u6655\u53A5\u8FC7\u53BB\u7684\u7D27\u5F20\u6C14\u606F\u8FDE\u773C\u6CEA\u90FD\u6FC0\u52A8\u7684\u6D41\u4E86\u4E0B\u6765\u3002</p><p>\u300C\u8D70\u5F00\uFF01\u5FEB\u8D70\u5F00\uFF01\u300D\u7EC6\u5C0F\u7684\u8718\u86DB\u4E0D\u505C\u5F80\u6A31\u5B50\u7684\u8EAB\u4E0A\u722C\u53BB\uFF0C\u52A8\u5F39\u4E0D\u5F97\u7684\u5927\u58F0\u5C16\u53EB\uFF0C\u5C31\u5728\u6B64\u65F6\u51B0\u51B7\u7684\u7B11\u58F0\u5374\u53C8\u82E5\u9690\u82E5\u73B0\u7684\u56DE\u8361\u5728\u5BC6\u5BA4\u91CC\u9762\uFF0C\u6478\u4E0D\u6E05\u65B9\u5411\u7684\u611F\u89C9\u8BA9\u6A31\u5B50\u7D27\u7EF7\u7684\u795E\u7ECF\u65E0\u6CD5\u81EA\u5236\u7684\u614C\u4E71\u8D77\u6765\u3002</p><p>\u300C\u6A31\u5B50\u4F60\u4EC0\u4E48\u65F6\u5019\u53D8\u5F97\u8FD9\u4E48\u80C6\u5C0F\u5462\uFF1F\u8FDE\u8FD9\u4E9B\u53EF\u7231\u7684\u5C0F\u4E1C\u897F\u90FD\u6015\u6210\u8FD9\u6837\u2026\u2026\u300D</p><p>\u6697\u5904\u7684\u5973\u4EBA\u8BF4\u8BDD\u6C14\u606F\u5341\u5206\u6C99\u54D1\uFF0C\u6709\u79CD\u8BF4\u4E0D\u51FA\u7684\u719F\u6089\u611F\u8BA9\u6A31\u5B50\u6D51\u8EAB\u53D1\u9EBB\u7684\u6253\u7740\u51B7\u98A4\u3002</p><p>\u300C\u8C01\u2026\u2026\u662F\u2026\u2026\u8309\u2026\u2026\u8309\u8389\u5B50\uFF1F\uFF01\u300D\u6A31\u5B50\u4E0D\u80AF\u7F6E\u4FE1\u5374\u53C8\u5F3A\u70C8\u76F4\u89C9\u7684\u542C\u51FA\u90A3\u662F\u8309\u8389\u5B50\u7684\u58F0\u97F3\uFF0C\u60CA\u6050\u7684\u5185\u5FC3\u4F3C\u4E4E\u5FD8\u8BB0\u4E86\u6500\u9644\u5168\u8EAB\u7684\u53EF\u6015\u866B\u5B50\uFF0C\u6E38\u7591\u7684\u76EE\u5149\u4E0D\u505C\u56DB\u5904\u5F20\u671B\u641C\u7D22\u7740\u53EF\u80FD\u76EE\u6807\u3002</p><p>\u300C\u54FC\u54FC\u2026\u2026\u54FC\u2026\u2026\u4F60\u8FD8\u8BB0\u5F97\u6211\u8FD9\u4E2A\u59CA\u59CA\u5417\uFF1F\u770B\u4F60\u8DDF\u767E\u5408\u5B50\u5230\u5E95\u5BF9\u6211\u505A\u4E86\u4EC0\u4E48\u6837\u7684\u597D\u4E8B\u2026\u2026\u300D\u6028\u6068\u7684\u58F0\u97F3\u5E7D\u5E7D\u7684\u65A5\u9053\u3002</p><p>\u300C\u4F60\u2026\u2026\u554A\uFF01\u300D\u66F4\u52A0\u60CA\u6050\u7684\u611F\u89C9\u8BA9\u6A31\u5B50\u865A\u5F31\u7684\u8EAB\u5B50\u9677\u5165\u51B0\u7A9F\uFF0C\u65E0\u6CD5\u79FB\u5F00\u7684\u773C\u775B\u76F4\u89C6\u7740\u524D\u65B9\u7A81\u7136\u73B0\u8EAB\u7684\u5355\u8584\u8EAB\u5F71\uFF0C\u6D51\u8EAB\u9AA8\u9ABC\u6CA1\u6709\u4E1D\u6BEB\u6BDB\u53D1\u76AE\u9762\u7684\u8840\u8089\u4EBA\u5F62\uFF0C\u5C31\u8FD9\u6837\u8D64\u88F8\u88F8\u7684\u7AD9\u7ACB\u5728\u6A31\u5B50\u9762\u524D\u3002</p><p>\u300C\u6211\u7684\u6837\u5B50\u5F88\u4E11\u5417\uFF1F\u54FC\u54FC\u2026\u2026\u4E3A\u4EC0\u4E48\u9732\u51FA\u8FD9\u79CD\u8868\u60C5\uFF1F\u300D\u8309\u8389\u5B50\u7684\u773C\u775B\u6B64\u65F6\u5DF2\u7ECF\u5B8C\u5168\u770B\u4E0D\u89C1\u77B3\u5B54\uFF0C\u7EA4\u7EC6\u7684\u8EAB\u5F62\u4F9D\u7136\u633A\u8D77\u4E00\u5BF9\u80A5\u539A\u65E0\u6BD4\u7684\u6CE2\u6D9B\u5DE8\u4E73\uFF0C\u50B2\u7136\u7684\u5728\u81EA\u5DF1\u59B9\u59B9\u773C\u524D\u629A\u5F04\u7740\u90A3\u4E0D\u6210\u4EBA\u5F62\u7684\u5927\u5976\u5B50\u3002</p><p>\u300C\u8309\u2026\u2026\u8389\u5B50\u2026\u2026\u522B\u8FD9\u6837\u2026\u2026\u300D\u6A31\u5B50\u96BE\u5FCD\u6FC0\u52A8\u7684\u60C5\u7EEA\uFF0C\u6B63\u8981\u51FA\u8A00\u963B\u6B62\u7684\u65F6\u5019\uFF0C\u8309\u8389\u5B50\u80A5\u5927\u7684\u4E73\u6655\u5185\u7ADF\u6FC0\u5C04\u51FA\u4E00\u9053\u9053\u4E73\u767D\u8272\u7684\u6ED1\u6DA6\u4E4B\u6DB2\uFF0C\u63A5\u89E6\u5230\u6A31\u5B50\u7684\u8138\u988A\u540E\uFF0C\u7ACB\u523B\u53D8\u6210\u8DDF\u56DB\u5468\u9ECF\u7A20\u7684\u4E73\u6C34\u4E00\u6837\uFF0C\u5C06\u6A31\u5B50\u7684\u8138\u7ED9\u7262\u7262\u8986\u76D6\u4F4F\uFF01</p><p>\u300C\u5514\u2026\u2026\u554A\u2026\u2026\u5641\u554A\u2026\u2026\u5455\u5455\u2026\u2026\u300D\u6A31\u5B50\u987F\u65F6\u7ADF\u89C9\u5F97\u65E0\u6CD5\u547C\u5438\uFF0C\u5F3A\u70C8\u9ECF\u6027\u7684\u6DB2\u4F53\u8BA9\u5979\u6D51\u8EAB\u96BE\u8FC7\u7684\u51E0\u4E4E\u968F\u65F6\u90FD\u8981\u4F11\u514B\u4E00\u6837\uFF0C\u8111\u6D77\u91CC\u4EA7\u751F\u6B7B\u4EA1\u7684\u6050\u60E7\u6B63\u5FEB\u901F\u7684\u5E2D\u5377\u5728\u5979\u5373\u5C06\u660F\u8FF7\u7684\u610F\u8BC6\u91CC\u9762\u3002</p><p>\u300C\u563B\u563B\u2026\u2026\u5C06\u4F60\u7684\u53E3\u9F3B\u906E\u8D77\u6765\u662F\u4E3A\u4E86\u51CF\u5C11\u4F60\u7B49\u4E00\u4E0B\u8FC7\u6E21\u5174\u594B\u7684\u54C0\u568E\u58F0\uFF0C\u9A6C\u4E0A\u4F60\u7684\u8FD9\u526F\u767D\u6670\u65E0\u7455\u7684\u597D\u8EAB\u5B50\u5C31\u4F1A\u4F53\u9A8C\u5230\u6D51\u8EAB\u53D8\u6210\u6027\u5668\u5B98\u5947\u5999\u7F8E\u611F\u2026\u2026\u300D\u8309\u8389\u5B50\u7684\u58F0\u97F3\u4E00\u505C\u6B62\uFF0C\u6307\u5C16\u597D\u50CF\u53D8\u6210\u6700\u9510\u5229\u7684\u94A9\u722A\u5F80\u6A31\u5B50\u8EAB\u4E0A\u4E00\u5212\uFF0C\u767D\u7D20\u6574\u9F50\u7684\u8FDE\u8EAB\u548C\u670D\u7ACB\u523B\u968F\u7740\u767D\u6D4A\u7684\u9ECF\u6DB2\u9010\u6E10\u6EB6\u89E3\uFF0C\u9732\u51FA\u5973\u4EBA\u767D\u6670\u65E0\u7455\u7684\u5982\u7389\u5A07\u8EAF.</p><p>\u300C\u563F\u2026\u2026\u8309\u8389\u5B50\u2026\u2026\u8FD8\u559C\u6B22\u6A31\u5B50\u7684\u8FD9\u526F\u8EAB\u5B50\u5417\uFF1F\u300D\u6CA1\u60F3\u5230\u4E00\u80A1\u719F\u6089\u7684\u5C11\u5973\u58F0\u97F3\u5728\u6A31\u5B50\u80CC\u540E\u54CD\u8D77\uFF0C\u6DF1\u77E5\u8BEF\u9677\u6076\u9B54\u9677\u9631\u7684\u5DEB\u5973\u5374\u600E\u4E48\u4E5F\u4E0D\u6562\u7F6E\u4FE1\uFF0C\u81EA\u5DF1\u6700\u4FE1\u4EFB\u7684\u59EA\u5973\u7F8E\u6708\uFF0C\u6B64\u65F6\u7ADF\u7136\u4F1A\u8DDF\u5DF2\u6B7B\u7684\u8309\u8389\u5B50\u8FDE\u6210\u4E00\u6C14\u3002</p><p>\u300C\u7528\u5979\u7684\u8EAB\u4F53\u6765\u5F53\u8089\u58F3\u4E4B\u540E\uFF0C\u4F60\u7684\u808C\u80A4\u81EA\u7136\u5C31\u80FD\u591F\u53D8\u6210\u8DDF\u5979\u73B0\u5728\u7684\u4E00\u6837\u7EC6\u7DFB\uFF0C\u82E5\u662F\u300C\u7A7F\u4E0A\u300D\u5979\u4EE5\u540E\uFF0C\u4E0D\u4EC5\u4F1A\u8BA9\u4F60\u7684\u9634\u9053\u6062\u590D\u50CF\u5E74\u8F7B\u65F6\u4E00\u6837\u7EA2\u5AE9\u7D27\u7F29\uFF0C\u5E76\u4E14\u8FD8\u4F1A\u957F\u51FA\u65B0\u7684\u5904\u5973\u819C\u5462\u2026\u2026\u300D</p><p>\u5728\u6A31\u5B50\u80CC\u540E\u7684\u7F8E\u6708\u5A07\u7B11\u7684\u6307\u70B9\u7740\u8309\u8389\u5B50\uFF0C\u5979\u7684\u53E3\u543B\u5DF2\u7ECF\u5931\u53BB\u4E86\u5C11\u5973\u5929\u771F\u65E0\u90AA\u7684\u5E94\u6709\u6A21\u6837\uFF0C\u53CD\u5012\u50CF\u4E2A\u7ECF\u9A8C\u8001\u9053\u53C8\u5996\u5A9A\u65E0\u6BD4\u7684\u53EF\u6015\u6DEB\u9B54\u2026\u2026</p><p>\u300C\u563B\u563B\u2026\u2026\u5BF9\u4E86\uFF0C\u5012\u5FD8\u4E86\u8FD9\u4E2A\u59B9\u59B9\u8FD8\u662F\u5B88\u8EAB\u5982\u7389\u7684\u5904\u5973\u5462\u2026\u2026\u55EF\u2026\u2026\u6211\u5DF2\u7ECF\u8FEB\u4E0D\u53CA\u5F85\u60F3\u8FDB\u5165\u5979\u7684\u8EAB\u4F53\u4E86\u2026\u2026\u5636\u5636\u2026\u2026\u300D\u8309\u8389\u5B50\u7528\u624B\u89E6\u6478\u6A31\u5B50\u4E0E\u7F8E\u6708\u8EAB\u4E0A\u7684\u4E73\u6C34\u9ECF\u6DB2\u540E\uFF0C\u4E24\u4EBA\u968F\u5373\u5C31\u7531\u67F1\u5B50\u4E0A\u5206\u4E86\u5F00\u6765\uFF0C\u5728\u5979\u7684\u624B\u5FC3\u4E4B\u4E2D\uFF0C\u4F3C\u4E4E\u6709\u7740\u63A7\u5236\u8FD9\u4E9B\u767D\u8272\u4E73\u6C41\u9ECF\u6027\u7684\u7279\u6B8A\u80FD\u529B\u3002</p><p>\u300C\u522B\u6025\u2026\u2026\u53EA\u8981\u5728\u5979\u7684\u9634\u6838\u4E0A\u5148\u6B96\u5165\u8FD9\u4E2A\u4EE5\u540E\u2026\u2026\u300D\u7F8E\u6708\u5C06\u52A8\u5F39\u4E0D\u5F97\u7684\u6A31\u5B50\u56FA\u5B9A\u597D\u59FF\u52BF\u540E\uFF0C\u5C0F\u5FC3\u7684\u5206\u5F00\u5979\u8EAB\u4E0A\u7684\u8863\u7269\uFF0C\u5BF9\u51C6\u5979\u9732\u51FA\u9ECF\u7A20\u7A00\u758F\u7684\u9634\u4E1B\u8089\u6838\u4E0A\u8F7B\u8F7B\u4E00\u5F39\uFF0C\u4F4E\u58F0\u5FF5\u4E86\u4E00\u6BB5\u5492\u8BED\uFF0C\u5C31\u5728\u81EA\u5DF1\u79C1\u5904\u5185\u53D6\u51FA\u4E00\u7247\u94F6\u73AF\u5E76\u5939\u5728\u5BF9\u65B9\u9634\u6838\u4E0A\uFF0C\u72E0\u5F88\u7684\u7528\u529B\u4E00\u7A7F\uFF01</p><p>\u300C\u6ECB\uFF01\u300D\u9C9C\u7EA2\u7684\u8840\u6EF4\u6DB2\u4E86\u51FA\u6765\uFF0C\u5F88\u5FEB\u7684\u7F8E\u6708\u4ED4\u7EC6\u5957\u597D\u7A7F\u900F\u7684\u94F6\u73AF\u5E76\u8F6C\u4E86\u4E00\u5708\uFF0C\u795E\u5947\u7684\u662F\uFF0C\u6676\u4EAE\u7684\u73AF\u53E3\u7ADF\u7D27\u5BC6\u7684\u6BEB\u65E0\u7F3A\u53E3\uFF0C\u50CF\u4E00\u4F53\u6210\u5F62\u7684\u7ED3\u5408\u5728\u6A31\u5B50\u654F\u9510\u7684\u6027\u5668\u4E0A\u3002</p><p>\u300C\u554A\u2026\u2026\u5455\u5455\uFF01\u300D\u6A31\u5B50\u6D51\u8EAB\u5267\u70C8\u7684\u9707\u52A8\u8D77\u6765\uFF0C\u6FC0\u70C8\u7684\u75DB\u695A\u8BA9\u5979\u7531\u96BE\u8FC7\u6655\u53A5\u4E2D\u5FCD\u53D7\u4E0D\u4F4F\u7684\u60F3\u5927\u58F0\u5C16\u53EB\uFF0C\u6700\u654F\u611F\u7684\u6027\u5668\u5B98\u88AB\u94F6\u73AF\u7262\u7262\u7A7F\u900F\u540E\uFF0C\u6E7F\u6ED1\u7684\u4E0B\u4F53\u7ACB\u523B\u5B8C\u5168\u5931\u7981\u7684\u6CC4\u51FA\u5C3F\u6DB2\u3002</p><p>\u300C\u563B\u563B\u563B\u2026\u2026\u8FD9\u9897\u8089\u58F3\u6DEB\u73AF\u5728\u79BB\u5F00\u6211\u7684\u8EAB\u4F53\u540E\uFF0C\u4FBF\u5C06\u6210\u4E3A\u4F60\u795E\u4EE3\u6A31\u5B50\u8089\u4F53\u4E0A\u7684\u4E00\u90E8\u4EFD\uFF0C\u4ECE\u4ECA\u5929\u8D77\u8EAB\u4F53\u5C06\u6CE8\u5B9A\u4E00\u8F88\u5B50\u50CF\u6027\u5668\u822C\u654F\u611F\uFF0C\u6BCF\u4E00\u540B\u808C\u80A4\u65F6\u65F6\u523B\u523B\u90FD\u50CF\u5305\u76AE\u5916\u9732\u4E00\u6837\uFF0C\u8FD8\u63A7\u5236\u4E0D\u4E86\u88AB\u4EBA\u638C\u63E1\u73A9\u5F04\u7684\u547D\u8FD0\u2026\u2026\u300D</p><p>\uFF08\u4E0D\u2026\u2026\u4E0D\u884C\u2026\u2026\u6551\u547D\u2026\u2026\u600E\u4E48\u4F1A\u8FD9\u6837\u2026\u2026\u4E0D\uFF01\uFF09\u6A31\u5B50\u65E0\u6CD5\u7F6E\u4FE1\u7684\u62DA\u547D\u6323\u624E\uFF0C\u96BE\u4EE5\u547C\u5438\u53C8\u65E0\u6CD5\u89C2\u770B\u7684\u53CC\u773C\u8BA9\u6050\u60E7\u66F4\u52A0\u5F3A\u70C8\uFF0C\u5C31\u5728\u5373\u5C06\u6655\u6B7B\u8FC7\u53BB\u7684\u540C\u65F6\uFF0C\u98A4\u52A8\u7684\u9634\u6838\u7A81\u7136\u5907\u7528\u529B\u7684\u62C9\u626F\uFF0C\u611F\u89C9\u4E00\u9635\u51B0\u51B7\u7684\u51C9\u610F\u7531\u809A\u5B50\u4E2D\u5256\u4E86\u5F00\u6765\uFF0C\u6CA1\u8FC7\u591A\u4E45\u6574\u4E2A\u4EBA\u5C31\u50CF\u88AB\u638F\u7A7A\u4E86\u4E00\u6837\uFF0C\u75DB\u82E6\u6E10\u6E10\u9EBB\u75FA\uFF0C\u4E00\u80A1\u6E7F\u9ECF\u9ECF\u7684\u611F\u89C9\u7531\u5589\u5499\u4E0B\u5206\u89E3\u5F00\u6765\u2026\u2026</p><p>\u300C\u563F\u563F\u2026\u2026\u5B8C\u5168\u90FD\u62C9\u5F00\u4E86\u2026\u2026\u8BA9\u6A31\u5B50\u597D\u597D\u770B\u6E05\u695A\u5979\u81EA\u5DF1\u7684\u8EAB\u4F53\u53D8\u6210\u4E86\u4EC0\u4E48\u6A21\u6837\u2026\u2026\u300D\u6A31\u5B50\u5728\u614C\u4E71\u4E2D\u53EA\u89C9\u773C\u775B\u4E00\u4EAE\uFF0C\u4F4E\u5934\u7684\u540C\u65F6\u5374\u53D1\u73B0\u5230\u81EA\u5DF1\u809A\u5B50\u88C2\u5F00\u4E86\u5927\u6D1E\uFF0C\u65C1\u8FB9\u7684\u808C\u80A4\u6709\u5982\u9634\u5507\u822C\u7684\u5145\u6EE1\u7C89\u5AE9\u7684\u76B1\u647A\uFF0C\u4E00\u4E1D\u4E1D\u6676\u83B9\u5254\u900F\u7684\u871C\u6DB2\u6CBE\u6EE1\u5168\u8EAB\u3002</p><p>\u300C\u8BB6\u5F02\u5417\uFF1F\u4F60\u7684\u8EAB\u4F53\u73B0\u5728\u53D8\u6210\u4E86\u522B\u4EBA\u7684\u8089\u58F3\uFF0C\u7B49\u5230\u6211\u751F\u957F\u6210\u8089\u540E\u53EF\u8FD8\u6709\u5F97\u4F60\u53D7\u5462\u2026\u2026\u300D\u8309\u8389\u5B50\u7684\u8BED\u6C14\u4E2D\u4E0D\u5E26\u4EFB\u4F55\u4E00\u4E1D\u4EBA\u6027\u822C\u8BF4\u9053\uFF0C\u4E11\u964B\u7684\u8138\u5E9E\u51B7\u51B7\u7684\u6CE8\u89C6\u7740\u6A31\u5B50\u6700\u540E\u4E00\u773C\u540E\uFF0C\u53CC\u624B\u94BB\u8FDB\u4E86\u59B9\u59B9\u7684\u809A\u5B50\u91CC\uFF0C\u786C\u751F\u751F\u7684\u7ADF\u5C31\u94BB\u8FDB\u5230\u6A31\u5B50\u7684\u8EAB\u4F53\u91CC\u9762\u3002</p>',50);function i(t,_){return r}var n=e(a,[["render",i]]);export{n as default};
