import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="\u7B2C\u4E8C\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u7AE0" aria-hidden="true">#</a> \u7B2C\u4E8C\u7AE0</h1><p>\u300C\u59D0\u59D0\u4F60\u8BF4\u627E\u5230\u4E86\u5F53\u5E74\u5BB3\u6B7B\u7238\u7238\u7684\u51F6\u624B\uFF1F\u771F\u7684\u5417\uFF1F\u300D\u4E00\u89C1\u5230\u59D0\u59D0\uFF0C\u7EA2\u68C9\u8FEB\u4E0D\u53CA\u5F85\u5730\u95EE\u3002</p><p>\u300C\u5C31\u662F\u8FD9\u4E2A\u4EBA\u3002\u300D\u59D0\u59D0\u8BF4\u8BDD\u4E5F\u5341\u5206\u5E72\u8106\uFF0C\u6478\u51FA\u4E00\u5F20\u7167\u7247\u63A8\u5230\u7EA2\u68C9\u9762\u524D\uFF0C\u300C\u4ED6\u53EB\u9F99\u54E5\uFF0C\u5916\u8868\u662F\u4E00\u5BB6\u5C0F\u5DE5\u5382\u7684\u5382\u957F\uFF0C\u5176\u5B9E\u662F\u4E2A\u9ED1\u793E\u4F1A\u7684\u5934\u76EE\uFF0C\u505A\u7684\u662F\u767D\u7C89\u751F\u610F\u3002\u6211\u8C03\u67E5\u8FC7\u4E86\uFF0C\u5F53\u5E74\u7238\u7238\u5C31\u662F\u8DDF\u4ED6\u5408\u4F5C\u4E4B\u540E\u51FA\u7684\u4E8B\uFF0C\u81EA\u4ECE\u7238\u7238\u6B7B\u540E\uFF0C\u4ED6\u7684\u516C\u53F8\u4E00\u591C\u95F4\u66B4\u5BCC\u8D77\u6765\u3002\u300D</p><p>\u300C\u8FD9\u4E2A\u9F99\u54E5\u6211\u77E5\u9053\u3002\u300D\u7EA2\u68C9\u770B\u4E86\u7167\u7247\u4E00\u773C\uFF0C\u6700\u8FD1\u5979\u7684\u5206\u961F\u4E00\u76F4\u5728\u8FFD\u4E00\u6761\u6BD2\u54C1\u6848\u7684\u7EBF\u7D22\uFF0C\u5DF2\u7ECF\u8DDF\u4E86\u5F88\u4E45\uFF0C\u90A3\u4E2A\u9886\u5934\u7684\u4FBF\u662F\u8FD9\u4E2A\u9F99\u54E5\u3002</p><p>\u300C\u4F60\u662F\u8BF4\uFF0C\u8FD9\u4E2A\u9F99\u54E5\u5F53\u5E74\u4E0D\u77E5\u9053\u7528\u4EC0\u4E48\u624B\u6BB5\uFF0C\u5403\u4E86\u7238\u7238\u516C\u53F8\u7684\u94B1\uFF0C\u8FD8\u5BB3\u6B7B\u4E86\u7238\u7238\uFF1F\u300D\u7EA2\u68C9\u7AEF\u8BE6\u7740\u7167\u7247\u4E2D\u7684\u7537\u4EBA\uFF0C\u90A3\u662F\u4E00\u4E2A\u56DB\u4E94\u5341\u5C81\u7684\u80D6\u5B50\uFF0C\u6EE1\u8138\u6A2A\u8089\uFF0C\u4E00\u770B\u5C31\u77E5\u4E0D\u662F\u5584\u7C7B\u3002</p><p>\u300C\u6211\u5DF2\u7ECF\u8C03\u67E5\u5F97\u5F88\u6E05\u695A\uFF0C\u5DEE\u4E0D\u591A\u53EF\u4EE5\u80AF\u5B9A\u5730\u8BF4\uFF0C\u5C31\u662F\u4ED6\u5E72\u7684\u3002\u5F53\u5E74\u8DDF\u7238\u7238\u5408\u4F5C\uFF0C\u4FB5\u541E\u4E86\u7238\u7238\u516C\u53F8\u51E0\u4E4E\u5168\u90E8\u7684\u8D44\u91D1\uFF0C\u8FD8\u7528\u516C\u53F8\u540D\u4E49\u501F\u4E86\u4E00\u5927\u7B14\u5916\u503A\u3002\u540E\u6765\u80AF\u5B9A\u662F\u88AB\u7238\u7238\u53D1\u73B0\uFF0C\u7ED3\u679C\u4ED6\u4E0B\u4E86\u6BD2\u624B\uFF01\u300D\u59D0\u59D0\u8BF4\u8D77\u90A3\u6BB5\u300C\u63A8\u6D4B\u300D\uFF0C\u8BED\u6C14\u5341\u5206\u6C89\u6291\u3002</p><p>\u300C\u55EF\uFF01\u5982\u679C\u771F\u662F\u4ED6\u7684\u8BDD\uFF0C\u6211\u4F1A\u4E0D\u653E\u8FC7\u4ED6\uFF01\u300D\u7EA2\u68C9\u72E0\u72E0\u5730\u76EF\u4E86\u7167\u7247\u4E0A\u7684\u7537\u4EBA\u4E00\u773C\uFF0C\u7AE5\u5E74\u65F6\u7684\u9634\u5F71\u91CD\u65B0\u7B3C\u4E0A\u5FC3\u5934\u3002\u7236\u4EB2\u6B7B\u540E\uFF0C\u5E74\u5E7C\u7684\u59D0\u59B9\u4FE9\u7ACB\u523B\u7531\u5BCC\u5BB6\u5C0F\u59D0\u53D8\u5F97\u4E00\u8D2B\u5982\u6D17\uFF0C\u5BB6\u4E1A\u88AB\u53D8\u5356\u7CBE\u5149\u4ECD\u7136\u65E0\u6CD5\u62B5\u507F\u5DE8\u989D\u7684\u503A\u52A1\uFF0C\u5E74\u8F7B\u7F8E\u4E3D\u7684\u6BCD\u4EB2\u542B\u8F9B\u8339\u82E6\u5730\u629A\u517B\u7740\u4E24\u4E2A\u5973\u513F\u6210\u4EBA\uFF0C\u5176\u4E2D\u7684\u82E6\u72B6\uFF0C\u59D0\u59B9\u4FE9\u4E0D\u582A\u56DE\u9996\u3002</p><p>\u59D0\u59B9\u4FE9\u90FD\u4ECE\u82E6\u96BE\u7684\u65E5\u5B50\u4E2D\u6371\u4E86\u8FC7\u6765\uFF0C\u4E3A\u7236\u4EB2\u62A5\u4EC7\u7684\u5FF5\u5934\u65E0\u65F6\u65E0\u523B\u4E0D\u7F20\u7ED5\u7740\u5979\u4EEC\u7684\u5FC3\u5934\u3002\u4ECE\u5C0F\u5979\u4EEC\u5C31\u8DDF\u7740\u7537\u5B69\u5B50\u4E00\u8D77\u5728\u8857\u5934\u4E0A\u53AE\u6DF7\uFF0C\u6253\u67B6\u5BF9\u5979\u4EEC\u6765\u8BF4\u72B9\u5982\u5E38\u5BB6\u4FBF\u996D\u4E00\u822C\uFF0C\u5373\u4F7F\u529B\u6C14\u4E0D\u5982\u7537\u5B69\u5B50\uFF0C\u4F46\u6574\u4E2A\u8857\u533A\u7684\u4EBA\u90FD\u77E5\u9053\u8C37\u5BB6\u7684\u4E24\u4E2A\u5973\u5B69\u662F\u6700\u96BE\u5543\u7684\u786C\u9AA8\u5934\uFF0C\u6253\u67B6\u4E00\u5B9A\u6B7B\u62FC\u5230\u5E95\uFF0C\u51B3\u4E0D\u8BA4\u8F93\uFF0C\u6240\u4EE5\u5979\u4EEC\u4F3C\u4E4E\u8FD8\u6CA1\u600E\u4E48\u6253\u8F93\u8FC7\u3002</p><p>\u540E\u6765\u59D0\u59D0\u8BFB\u4E66\u6210\u7EE9\u597D\uFF0C\u4E00\u76F4\u4E0A\u5230\u5927\u5B66\uFF0C\u5F53\u8D77\u4E86\u4E00\u540D\u4E13\u505A\u7F6A\u6848\u9898\u6750\u7684\u8BB0\u8005\u3002\u800C\u59B9\u59B9\uFF0C\u66F4\u662F\u52A0\u5165\u8B66\u754C\uFF0C\u4EB2\u624B\u6253\u51FB\u7F6A\u72AF\u3002\u6BCF\u5F53\u5979\u6293\u83B7\u4E00\u4E2A\u7F6A\u72AF\u7684\u65F6\u5019\uFF0C\u5979\u90FD\u4F1A\u5728\u5FC3\u4E2D\u6697\u6697\u5B89\u6170\uFF0C\u5979\u544A\u8BC9\u81EA\u5DF1\uFF1A\u8FD9\u4E2A\u4EBA\uFF0C\u53EF\u80FD\u5C31\u662F\u5BB3\u6B7B\u7236\u4EB2\u7684\u4EBA\uFF01</p><p>\u300C\u59D0\u59D0\uFF0C\u300D\u7EA2\u68C9\u9053\uFF1A\u300C\u4F60\u4E00\u76F4\u5728\u67E5\u8FD9\u79CD\u4E8B\u5F88\u5371\u9669\u7684\uFF0C\u5343\u4E07\u8981\u5C0F\u5FC3\u3002\u300D</p><p>\u300C\u653E\u5FC3\u5427\uFF0C\u59D0\u59D0\u662F\u8001\u6C5F\u6E56\uFF01\u300D\u59D0\u59D0\u671D\u59B9\u59B9\u7B11\u4E86\u4E00\u7B11\uFF0C\u5934\u7529\u4E00\u7529\uFF0C\u79C0\u53D1\u98DE\u626C\uFF0C\u98D8\u6563\u51FA\u6DE1\u6DE1\u7684\u9999\u6C34\u6C14\u5473\u3002</p><p>\u5979\u53EB\u8C37\u51B0\u67D4\uFF0C\u4E8C\u5341\u4E94\u5C81\uFF0C\u300A\u57CE\u5E02\u6668\u62A5\u300B\u7279\u7EA6\u8BB0\u8005\u3002\u548C\u59B9\u59B9\u7684\u4E00\u5934\u77ED\u5934\u4E0D\u540C\uFF0C\u51B0\u67D4\u7559\u4E86\u4E00\u5934\u76F4\u81F3\u8170\u90E8\u7684\u957F\u53D1\uFF0C\u67D3\u4E0A\u4E86\u6DE1\u6DE1\u7684\u6697\u7EA2\u8272\u3002\u4E00\u5BF9\u6807\u51C6\u7684\u51E4\u773C\u770B\u4E0A\u53BB\u59A9\u5A9A\u4E2D\u9732\u51FA\u51E0\u5206\u5A01\u4E25\uFF0C\u663E\u5F97\u5341\u5206\u7CBE\u660E\u5E72\u7EC3\u3002\u56E0\u6B64\u5373\u4F7F\u5E74\u7EAA\u5E76\u4E0D\u5927\u3001\u5373\u4F7F\u914D\u4E0A\u53E4\u5178\u5F0F\u7684\u9E45\u86CB\u578B\u8138\u86CB\u548C\u6A31\u6843\u5C0F\u53E3\uFF0C\u770B\u4E0A\u4ECD\u7136\u7ED9\u4EBA\u4EE5\u4E00\u79CD\u9887\u5386\u6CA7\u6851\u7684\u6210\u719F\u98CE\u97F5\u3002</p><p>\u800C\u51B0\u67D4\u9971\u6EE1\u7684\u80F8\u524D\u4EE5\u53CA\u7EA4\u7EC6\u7684\u8170\u90E8\uFF0C\u8EAB\u6750\u6781\u4E3A\u60F9\u706B\uFF0C\u90A3\u9AD8\u9AD8\u8038\u8D77\u7684\uFF26\u7F69\u676F\uFF0C\u8FDE\u59B9\u59B9\u90FD\u6709\u4E9B\u7FA1\u6155\u3002\u96BE\u602A\u59B9\u59B9\u6709\u65F6\u90FD\u8C03\u4F83\u4EE5\u5979\u7684\u5929\u4F7F\u9762\u5B54\u548C\u9B54\u9B3C\u8EAB\u6750\uFF0C\u53BB\u53C2\u52A0\u9009\u7F8E\u5FC5\u5B9A\u4F1A\u662F\u5927\u70ED\u4EBA\u9009\u3002</p><p>\u59D0\u59B9\u4FE9\u90FD\u7EE7\u627F\u4E86\u6BCD\u4EB2\u9AD8\u6311\u7684\u8EAB\u6750\uFF0C\u7EA2\u68C9\u6BD4\u59D0\u59D0\u7565\u9AD8\u4E00\u70B9\u3002\u4E0E\u4F5C\u4E3A\u6027\u611F\u7F8E\u5973\u7684\u59D0\u59D0\u6709\u70B9\u4E0D\u540C\u7684\u662F\uFF0C\u7EA2\u68C9\u7684\u8138\u86CB\u770B\u4E0A\u53BB\u975E\u5E38\u6E05\u7EAF\uFF0C\u4EE4\u4EBA\u5F88\u96BE\u60F3\u50CF\u5979\u662F\u4E00\u4F4D\u8F91\u72AF\u65E0\u6570\u7684\u82F1\u52C7\u8B66\u5B98\u3002\u4E00\u5BF9\u5706\u5706\u7684\u5927\u773C\u775B\u4E2D\u65E9\u5DF2\u89C1\u4E0D\u5230\u540C\u9F84\u5973\u5B69\u7684\u90A3\u79CD\u5929\u771F\uFF0C\u6362\u4E4B\u7684\u662F\u4E00\u79CD\u575A\u5B9A\u7684\u773C\u795E\uFF0C\u90A3\u79CD\u4E0D\u5BB9\u7F6E\u7591\u7684\u82F1\u6C14\uFF0C\u5F88\u662F\u6444\u4EBA\u5FC3\u9B44\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u548C\u5979\u76F8\u5904\u4E45\u4E86\u7684\u540C\u4E8B\u4EEC\uFF0C\u5F77\u5F7F\u90FD\u5FD8\u6389\u5979\u5176\u5B9E\u4E5F\u662F\u4E00\u4E2A\u7F8E\u4E3D\u7684\u5999\u9F84\u5C11\u5973\uFF0C\u65E9\u5DF2\u88AB\u5979\u90A3\u79CD\u4E0D\u5C48\u65E0\u754F\u7684\u6C14\u8D28\u6240\u6298\u670D\u3002\u5728\u5927\u5BB6\u7684\u773C\u4E2D\uFF0C\u7EA2\u68C9\u6839\u672C\u4E0A\u5C31\u662F\u4E00\u4E2A\u51FA\u8272\u7684\u6597\u58EB\u3002</p><p>\u300C\u4F60\u6700\u8FD1\u597D\u50CF\u53C8\u7626\u4E86\u3002\u300D\u51B0\u67D4\u4E13\u6CE8\u5730\u770B\u7740\u59B9\u59B9\u7684\u8138\uFF0C\u6709\u70B9\u5FC3\u75BC\u5730\u8BF4\u9053\u3002</p><p>\u300C\u54E6\uFF0C\u662F\u5417\uFF1F\u300D\u7EA2\u68C9\u4F3C\u4E4E\u5BF9\u6B64\u4E0D\u5982\u4F55\u5728\u4E4E\uFF0C\u300C\u8FD9\u4E2A\u9F99\u54E5\u73B0\u5728\u2026\u2026\u7238\u7238\u53BB\u4E16\u90A3\u4E48\u591A\u5E74\uFF0C\u5E94\u8BE5\u4E0D\u4F1A\u8FD8\u6709\u8BC1\u636E\u7559\u4E0B\u5427\u3002\u300D\u5979\u5173\u5FC3\u7684\u662F\u5982\u4F55\u4E3A\u7236\u62A5\u4EC7\u3002</p><p>\u300C\u6211\u60F3\u6709\u8BC1\u636E\u4E5F\u65E9\u5DF2\u9500\u6BC1\u4E86\u5427\u3002\u4E0D\u8FC7\u6211\u77E5\u9053\u4ED6\u4E00\u76F4\u8FD8\u5728\u505A\u767D\u7C89\u7684\u751F\u610F\uFF0C\u6211\u6B63\u5728\u8C03\u67E5\uFF0C\u6709\u4EC0\u4E48\u8FDB\u5C55\u6211\u9A6C\u4E0A\u901A\u77E5\u4F60\u3002\u300D\u51B0\u67D4\u8BF4\u3002</p><p>\u300C\u55EF\uFF01\u4F60\u5343\u4E07\u5C0F\u5FC3\u3002\u7B49\u6211\u529E\u5B8C\u624B\u5934\u8FD9\u4E2A\u6848\u5B50\uFF0C\u9A6C\u4E0A\u5C31\u52A0\u7D27\u6765\u67E5\u8FD9\u4E2A\u4EBA\u3002\u6211\u4E00\u5B9A\u8981\u4EB2\u624B\u628A\u4ED6\u6293\u8D77\u6765\uFF01\u300D\u7EA2\u68C9\u6DF1\u77E5\u6BD2\u8D29\u7684\u624B\u6BB5\uFF0C\u4E0D\u7981\u4E3A\u59D0\u59D0\u7684\u5B89\u5168\u6709\u4E9B\u62C5\u5FC3\u3002</p><p>\u9910\u5385\u91CC\uFF0C\u97F3\u4E50\u58F0\u4E00\u8F6C\uFF0C\u54CD\u8D77\u4E86\u719F\u6089\u7684\u65CB\u5F8B\u3002\u59D0\u59B9\u4FE9\u76F8\u770B\u4E00\u773C\uFF0C\u8F7B\u8F7B\u4E00\u7B11\uFF0C\u5FC3\u610F\u76F8\u901A\u5730\u540C\u65F6\u9759\u4E86\u4E0B\u6765\uFF0C\u542C\u7740\u8FD9\u9996\u5979\u4EEC\u81EA\u5C0F\u70ED\u7231\u7684\u6B4C\u66F2\u3002</p><p>\u300C\u7EA2\u68C9\u76DB\u653E\uFF0C\u5929\u6C14\u6696\u6D0B\u6D0B\uFF0C\u82F1\u59FF\u52C3\u53D1\u582A\u666F\u4EF0\u3002\u82F1\u96C4\u6811\uFF0C\u529B\u4E89\u5411\u4E0A\uFF0C\u5FD7\u6C14\u8C01\u80FD\u6321\u3002\u7EA2\u68C9\u6012\u653E\uFF0C\u9A71\u53BB\u4E25\u5BD2\uFF0C\u82B1\u6735\u7ADE\u5411\u9AD8\u679D\u653E\u3002\u82F1\u96C4\u6837\uFF0C\u4E07\u4F17\u5076\u50CF\uFF0C\u7EA2\u68C9\u72EC\u6709\u50B2\u9AA8\u5E72\u3002\u6211\u6B63\u76F4\u65E0\u504F\u82F1\u633A\u597D\u699C\u6837\uFF0C\u6709\u4E0A\u8FDB\u96C4\u5FC3\u575A\u51B3\u4E89\u5149\u3002\u7ED3\u68C9\u5B50\u501F\u98CE\u98D8\uFF0C\u56DB\u65B9\u6811\u82D7\u575A\u58EE\u3002\u7EA2\u68C9\u76DB\u653E\uFF0C\u5929\u6C14\u6696\u6D0B\u6D0B\uFF0C\u82F1\u59FF\u52C3\u53D1\u582A\u666F\u4EF0\u3002\u82F1\u96C4\u6811\uFF0C\u529B\u4E89\u5411\u4E0A\uFF0C\u7EA2\u68C9\u72EC\u6709\u50B2\u9AA8\u5E72\u3002\u300D</p><p>\u662F\u521A\u521A\u8FC7\u4E16\u7684\u6B4C\u575B\u5DE8\u661F\u7F57\u6587\u7684\u540D\u66F2\u300A\u7EA2\u68C9\u300B\uFF0C\u662F\u51B0\u67D4\u548C\u7EA2\u68C9\u4ECE\u5C0F\u6700\u559C\u6B22\u7684\u4E00\u9996\u6B4C\u3002\u5979\u4EEC\u66FE\u7ECF\u7701\u4E0B\u6574\u6574\u4E09\u4E2A\u6708\u7684\u96F6\u7528\u94B1\uFF0C\u53BB\u4E70\u8FD9\u4E00\u5F20\u4EE4\u5979\u4EEC\u610F\u5FD7\u52C3\u53D1\u7684\u5531\u7247\u3002</p><p>\u719F\u6089\u7684\u65CB\u5F8B\u8BA9\u5979\u4EEC\u53C8\u5F77\u5F7F\u56DE\u5230\u4E86\u7AE5\u5E74\uFF0C\u56DE\u5230\u4E86\u90A3\u8270\u82E6\u4F46\u5374\u8C6A\u60C5\u6EE1\u6000\u7684\u7AE5\u5E74\u3002</p><p>\u7EA2\u68C9\uFF0C\u4E0D\u4EC5\u4EC5\u662F\u8C37\u7EA2\u68C9\u7684\u540D\u5B57\uFF0C\u66F4\u662F\u5979\u7684\u5076\u50CF\uFF0C\u8FD8\u540C\u65F6\u4E5F\u662F\u59D0\u59D0\u51B0\u67D4\u666F\u4EF0\u7684\u82F1\u96C4\u6811\u3002</p><p>\u6BCF\u6B21\u542C\u5230\u8FD9\u9996\u6B4C\uFF0C\u90FD\u4EE4\u4EBA\u5FC3\u6F6E\u6F8E\u6E43\u3002\u59D0\u59B9\u4FE9\u9ED8\u9ED8\u5730\u542C\u7740\uFF0C\u8138\u4E0A\u6D41\u9732\u7740\u7B11\u5BB9\uFF0C\u76F4\u5230\u4E00\u66F2\u7EC8\u4E86\u3002</p><p>\u300C\u4F60\u5728\u529E\u7684\u662F\u4E00\u4EF6\u7ED1\u67B6\u6848\u662F\u5417\uFF1F\u300D\u59D0\u59D0\u547C\u4E86\u4E00\u53E3\u6C14\uFF0C\u95EE\u3002</p><p>\u300C\u4F60\u600E\u4E48\u77E5\u9053\uFF1F\u300D</p><p>\u300C\u563F\u563F\uFF0C\u6211\u662F\u5E72\u4EC0\u4E48\u7684\uFF1F\u300D\u51B0\u67D4\u7B11\u4E86\u4E00\u7B11\u3002\u4F5C\u4E3A\u4E13\u95E8\u62A5\u9053\u7F6A\u6848\u7684\u8BB0\u8005\uFF0C\u5979\u7684\u6D88\u606F\u7075\u901A\u5373\u4F7F\u5728\u540C\u884C\u4E2D\u4E5F\u662F\u95FB\u540D\u4E86\u7684\u3002</p><p>\u300C\u55EF\uFF01\u300D\u7EA2\u68C9\u6C89\u9ED8\u4E86\u3002\u8B66\u961F\u7684\u7EAA\u5F8B\u662F\u7EDD\u5BF9\u4E0D\u5141\u8BB8\u5C06\u6848\u60C5\u8FDB\u5C55\u5411\u5916\u6CC4\u9732\u7684\uFF0C\u5373\u4F7F\u662F\u5BF9\u6700\u53EF\u4FE1\u8D56\u7684\u4EB2\u4EBA\u3002</p><p>\u4E0D\u6599\u51B0\u67D4\u9053\uFF1A\u300C\u52AB\u9646\u8C6A\u8D27\u7269\u7684\u5E55\u540E\u4E3B\u4F7F\uFF0C\u5C31\u662F\u9F99\u54E5\u3002\u300D</p><p>\u300C\u54E6\uFF1F\u300D\u7EA2\u68C9\u795E\u60C5\u7ACB\u5373\u4E13\u6CE8\u8D77\u6765\uFF0C\u300C\u5BF9\u4E86\uFF0C\u90A3\u59D0\u59D0\u4F60\u6709\u6CA1\u6709\u542C\u8BF4\u8FC7\u4E00\u4E2A\u53EB\u8840\u7EA2\u68C9\u7684\u5973\u4EBA\uFF1F\u300D</p><p>\u5979\u60F3\u89E3\u5F00\u5FC3\u5185\u4E00\u4E2A\u7591\u56E2\u3002</p><p>\u51B0\u67D4\u8138\u8272\u5FAE\u5FAE\u4E00\u53D8\uFF0C\u9053\uFF1A\u300C\u8FD9\u4E2A\u4E0D\u6E05\u695A\u3002\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u300C\u6CA1\u4EC0\u4E48\u3002\u300D\u7EA2\u68C9\u53CD\u6B63\u4E5F\u4E0D\u662F\u592A\u5728\u4E4E\u8FD9\u4E2A\u95EE\u9898\uFF0C\u300C\u9F99\u54E5\u8981\u836F\u6750\u5E72\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u90A3\u6211\u5C31\u4E0D\u77E5\u9053\u4E86\u3002\u300D\u51B0\u67D4\u9053\uFF0C\u300C\u6211\u6B63\u5728\u67E5\u8FD9\u4E2A\u3002\u518D\u8BF4\uFF0C\u62A2\u52AB\u867D\u7136\u662F\u4E00\u6761\u5927\u7F6A\uFF0C\u4F46\u53EF\u80FD\u8FD8\u8981\u4E0D\u4E86\u4ED6\u7684\u547D\u2026\u2026\u300D</p><p>\u300C\u4F60\u7684\u610F\u601D\u662F\u8BF4\u2026\u2026\u300D\u7EA2\u68C9\u7684\u773C\u795E\u6E10\u6E10\u53D8\u5F97\u51F6\u72E0\u8D77\u6765\uFF0C\u300C\u4F60\u8981\u627E\u5230\u4ED6\u8D29\u6BD2\u7684\u8BC1\u636E\uFF1F\u300D</p><p>\u300C\u5BF9\uFF01\u300D\u51B0\u67D4\u7684\u773C\u795E\u4E5F\u6E10\u6E10\u9634\u51B7\u8D77\u6765\uFF0C\u300C\u6211\u77E5\u9053\u4ED6\u4E00\u76F4\u8DDF\u4E00\u4E2A\u5927\u5356\u5BB6\u4EA4\u6613\uFF0C\u800C\u4E14\u5F88\u5FEB\u5C31\u6709\u4E00\u5927\u6279\u6BD2\u54C1\u4F1A\u8FD0\u5230\u2026\u2026\u300D</p><p>\u300C\u662F\u5417\uFF1F\u300D\u7EA2\u68C9\u7565\u4E00\u6C89\u541F\uFF0C\u300C\u6211\u5C3D\u5FEB\u529E\u5B8C\u624B\u5934\u7684\u6848\u5B50\u3002\u59D0\u59D0\u4F60\u4E00\u5207\u5C0F\u5FC3\uFF0C\u592A\u5371\u9669\u7684\u4E8B\u5343\u4E07\u614E\u91CD\uFF0C\u7559\u7ED9\u6211\u53BB\u529E\u3002\u300D</p><p>\u51B0\u67D4\u5F00\u989C\u4E00\u7B11\uFF0C\u9053\uFF1A\u300C\u600E\u4E48\uFF1F\u4FE1\u4E0D\u8FC7\u59D0\u59D0\uFF1F\u300D</p><p>\u300C\u4E0D\u662F\u3002\u300D\u7EA2\u68C9\u9762\u8272\u51DD\u91CD\uFF0C\u300C\u4F46\u6211\u662F\u8B66\u5BDF\u3002\u518D\u8BF4\u8FD9\u79CD\u4E8B\u672C\u6765\u5C31\u5E94\u8BE5\u662F\u8B66\u5BDF\u53BB\u529E\u7684\uFF0C\u4F60\u53BB\u5077\u5077\u8C03\u67E5\u6BD2\u8D29\u5B9E\u5728\u592A\u5371\u9669\u3002\u300D</p><p>\u300C\u653E\u5FC3\u5427\uFF0C\u59D0\u59D0\u6709\u5206\u5BF8\u3002\u300D\u51B0\u67D4\u671D\u59B9\u59B9\u7B11\u4E00\u7B11\u3002</p><p>\u770B\u4E0A\u53BB\u59D0\u59D0\u662F\u8FD9\u4E48\u7684\u81EA\u4FE1\uFF0C\u7EA2\u68C9\u4E5F\u6DF1\u77E5\u59D0\u59D0\u7684\u80FD\u529B\uFF0C\u4F46\u5FC3\u4E2D\u7684\u62C5\u5FE7\uFF0C\u5374\u662F\u6325\u4E4B\u4E0D\u53BB\u3002\u4E00\u4E2A\u5E74\u8F7B\u7F8E\u4E3D\u7684\u5973\u8BB0\u8005\uFF0C\u8981\u662F\u88AB\u6BD2\u8D29\u53D1\u73B0\uFF0C\u90A3\u79CD\u540E\u679C\u7EA2\u68C9\u4E0D\u6562\u60F3\u50CF\u3002</p><p>\u300C\u5BF9\u4E86\uFF0C\u4E0B\u4E2A\u6708\u662F\u5988\u751F\u65E5\uFF0C\u4F60\u6253\u7B97\u600E\u4E48\u5E86\u795D\uFF1F\u300D\u51B0\u67D4\u5C94\u5F00\u8BDD\u9898\u3002</p><p>\u300C\u662F\u554A\uFF01\u6211\u90FD\u5FD8\u4E86\uFF0C\u770B\u6211\u771F\u662F\u7684\u2026\u2026\u300D\u7EA2\u68C9\u4E00\u62CD\u81EA\u5DF1\u7684\u989D\u5934\uFF0C\u300C\u8FD8\u80FD\u600E\u4E48\u5E86\u795D\uFF1F\u96BE\u9053\u641E\u4E2A\uFF30\uFF21\uFF32\uFF34\uFF39\uFF1F\u6211\u4EEC\u53EF\u90FD\u6CA1\u90A3\u4E2A\u65F6\u95F4\uFF0C\u518D\u8BF4\u5988\u4ECE\u6765\u90FD\u4E0D\u8981\u6211\u4EEC\u9EBB\u70E6\uFF0C\u8FDE\u9001\u70B9\u793C\u7269\u5979\u90FD\u4E00\u76F4\u8BF4\u4E0D\u8981\u4E0D\u8981\u3002\u5230\u65F6\u6211\u62E3\u4E00\u5927\u675F\u6700\u597D\u7684\u5EB7\u4E43\u99A8\u9001\u7ED9\u5988\u5988\u597D\u4E86\uFF0C\u6211\u4EEC\u90FD\u56DE\u5BB6\u5403\u996D\u5427\u3002\u300D</p><p>\u300C\u90A3\u6211\u4E70\u4E9B\u597D\u5403\u7684\u3002\u300D\u51B0\u67D4\u9053\u3002</p><p>\u56E0\u4E3A\u804C\u4E1A\u7684\u5173\u7CFB\uFF0C\u59D0\u59B9\u4E0D\u4EC5\u76F8\u4E92\u95F4\u5F88\u5C11\u78B0\u9762\uFF0C\u800C\u4E14\u4E24\u4E2A\u4EBA\u90FD\u5F88\u5C11\u56DE\u5BB6\uFF0C\u53EA\u7559\u4E0B\u6BCD\u4EB2\u4E00\u4E2A\u4EBA\u72EC\u81EA\u5B88\u7740\u90A3\u95F4\u65E7\u623F\u5B50\u3002</p><p>\u300C\u90A3\u5C31\u8FD9\u4E48\u8BF4\u5B9A\u4E86\uFF0C\u6211\u4EEC\u4E00\u5BB6\u4E09\u53E3\u597D\u50CF\u6709\u534A\u5E74\u6CA1\u4E00\u8D77\u5403\u8FC7\u996D\u4E86\u5427\uFF1F\u300D\u7EA2\u68C9\u4E00\u60F3\u5230\u4E0B\u4E2A\u793C\u62DC\u5C31\u53EF\u4EE5\u4E00\u5BB6\u56E2\u805A\u4E86\uFF0C\u5FC3\u60C5\u77AC\u95F4\u597D\u4E86\u4E0D\u5C11\u3002</p><p>\u300C\u5BF9\u4E86\u3002\u300D\u7EA2\u68C9\u5FFD\u9053\uFF0C\u300C\u6211\u524D\u51E0\u5929\u53BB\u67E5\u591C\u603B\u4F1A\uFF0C\u5988\u5988\u7684\u90A3\u9996\u300A\u82B1\u5F00\u82B1\u843D\u300B\u5230\u73B0\u5728\u8FD8\u5F88\u7EA2\u5462\uFF0C\u653E\u4E2A\u4E0D\u505C\u3002\u300D\u53CC\u624B\u6367\u7740\u5934\uFF0C\u7B11\u7B11\u5730\u5BF9\u59D0\u59D0\u8BF4\u3002</p><p>\u300C\u662F\u5417\uFF1F\u300D\u51B0\u67D4\u773C\u5149\u4E5F\u662F\u4E00\u4EAE\u3002\u5979\u4EEC\u7684\u6BCD\u4EB2\u5510\u7F9A\uFF0C\u5E74\u8F7B\u65F6\u662F\u4E00\u540D\u5341\u5206\u8D70\u7EA2\u7684\u6B4C\u661F\uFF0C\u6709\u4E0D\u5C11\u7ECF\u5178\u6B4C\u66F2\u5230\u73B0\u5728\u4ECD\u7136\u88AB\u4EBA\u4F20\u5531\u7740\uFF0C\u59D0\u59B9\u4FE9\u4E5F\u4E00\u76F4\u5F15\u4EE5\u4E3A\u8C6A\u3002</p><p>\u300C\u5988\u5E74\u8F7B\u7684\u65F6\u5019\u771F\u662F\u597D\u6F02\u4EAE\u2026\u2026\u300D\u7EA2\u68C9\u60A0\u60A0\u5730\u9053\uFF0C\u60F3\u50CF\u7740\u6BCD\u4EB2\u5F53\u5E74\u7684\u7F8E\u4E3D\u7684\u9A84\u50B2\uFF0C\u5FC3\u5934\u9690\u9690\u4F5C\u75BC\u3002\u90A3\u6837\u6F02\u4EAE\u7684\u4E00\u4E2A\u5973\u4EBA\uFF0C\u5E74\u7EAA\u8F7B\u8F7B\u5C31\u5B88\u4E86\u5BE1\uFF0C\u53D8\u6210\u4E00\u4E2A\u64CD\u513F\u5E26\u5973\u7684\u7BA1\u5BB6\u5A46\uFF0C\u5728\u5C81\u6708\u4E2D\u6E10\u6E10\u8001\u53BB\u3002</p><p>\u300C\u5988\u8FD9\u4E9B\u5E74\u6765\u4E5F\u771F\u82E6\u2026\u2026\u300D\u51B0\u67D4\u4E5F\u4F4E\u4E0B\u5934\u53BB\u3002</p><p>\u300C\u597D\u5566\u597D\u5566\uFF0C\u4E0D\u63D0\u4E86\u3002\u4E0B\u4E2A\u6708\u5341\u53F7\u662F\u5427\uFF0C\u5927\u5BB6\u51C6\u65F6\u54E6\u3002\u300D\u7EA2\u68C9\u53EF\u4E0D\u559C\u6B22\u957F\u65F6\u95F4\u6C89\u6D78\u5728\u4E0D\u6B22\u7684\u6C14\u6C1B\u91CC\u3002</p><p>\u300C\u90A3\u8BB0\u4F4F\u4E86\u54E6\uFF0C\u4E0D\u8BB8\u518D\u723D\u7EA6\u4E86\uFF01\u300D\u51B0\u67D4\u7B11\u9053\u3002\u5BF9\u7EA2\u68C9\u6765\u8BF4\uFF0C\u7B54\u5E94\u4E86\u56DE\u5BB6\u5403\u996D\u800C\u4E34\u65F6\u723D\u7EA6\u7684\u4E8B\uFF0C\u53EF\u8C13\u662F\u5E38\u5BB6\u4FBF\u996D\u3002</p><p>\u300C\u77E5\u9053\u5566\uFF01\u300D\u7EA2\u68C9\u7528\u6709\u70B9\u8C03\u76AE\u7684\u8BED\u6C14\u5BF9\u59D0\u59D0\u8BF4\uFF0C\u300C\u90A3\u6211\u6709\u4E8B\u5148\u8D70\u4E86\uFF0C\u6709\u4E8B\u53CA\u65F6\u8054\u7CFB\u3002\u300D</p><p>\u300C\u597D\u7684\uFF0C\u4F60\u53BB\u5FD9\u5427\u3002\u300D\u51B0\u67D4\u77E5\u9053\u59B9\u59B9\u662F\u4E2A\u5DE5\u4F5C\u72C2\uFF0C\u4F55\u51B5\u624B\u5934\u8FD8\u6709\u5F88\u6025\u7684\u6848\u5B50\u5728\u529E\uFF0C\u7ED1\u67B6\u6848\u53EF\u662F\u62D6\u4E0D\u5F97\u7684\u3002</p><p>\u51B0\u67D4\u4E5F\u5728\u56DE\u5BB6\u7684\u8DEF\u4E0A\uFF0C\u5BF9\u4E8E\u957F\u65F6\u95F4\u5BC4\u5C45\u5728\u5916\u7684\u4EBA\u6765\u8BF4\uFF0C\u5BB6\u5EAD\u56E2\u805A\u603B\u662F\u4E00\u4E2A\u6E29\u99A8\u751C\u871C\u7684\u68A6\u60F3\u3002</p><p>\u867D\u7136\u5927\u5BB6\u751F\u6D3B\u5728\u540C\u4E00\u4E2A\u57CE\u5E02\uFF0C\u4F46\u80FD\u591F\u805A\u5728\u4E00\u8D77\u7684\u65F6\u95F4\uFF0C\u5728\u8FD9\u51E0\u5E74\u4E2D\u5B9E\u5728\u662F\u592A\u5C11\u592A\u5C11\u4E86\u3002</p><p>\u6BCF\u5F53\u60F3\u5230\u6BCD\u4EB2\u4E00\u4E2A\u4EBA\u72EC\u81EA\u751F\u6D3B\uFF0C\u51B0\u67D4\u5FC3\u4E2D\u4E5F\u4F1A\u611F\u5230\u4E0D\u5B89\uFF0C\u4F46\u5979\u5B9E\u5728\u6CA1\u6709\u65F6\u95F4\u53BB\u966A\u5979\u3002\u4E0D\u8FC7\u6BCD\u4EB2\u7684\u751F\u65E5\uFF0C\u5979\u65E0\u8BBA\u5982\u4F55\u4E00\u5B9A\u8981\u8868\u793A\u4E00\u4E0B\u505A\u5973\u513F\u7684\u5B5D\u5FC3\u3002</p><p>\u300C\u59B9\u59B9\u4E5F\u4E00\u5B9A\u662F\u8FD9\u6837\u60F3\u7684\u3002\u300D\u51B0\u67D4\u5FC3\u9053\u3002\u5979\u73B0\u5728\u60F3\u56DE\u5BB6\u63A2\u671B\u4E00\u4E0B\u5988\u5988\u3002</p><p>\u7236\u4EB2\u8C37\u9752\u677E\u5F53\u5E74\u4E5F\u7B97\u662F\u4E2A\u5DE8\u5BCC\uFF0C\u6BCD\u4EB2\u5E74\u8F7B\u65F6\u4E5F\u4E00\u76F4\u662F\u9526\u8863\u7389\u98DF\u3002\u53EF\u662F\u5728\u90A3\u6B21\u53D8\u6545\u4E4B\u540E\uFF0C\u6BCD\u4EB2\u5F77\u5F7F\u6574\u4E2A\u4EBA\u90FD\u53D8\u4E86\uFF0C\u53D8\u5F97\u6C89\u9ED8\u5BE1\u8A00\uFF0C\u53D8\u5F97\u90C1\u90C1\u5BE1\u6B22\u3002</p><p>\u51B0\u67D4\u5B8C\u5168\u7406\u89E3\u6BCD\u4EB2\u8FD9\u5341\u51E0\u4E8C\u5341\u5E74\u6765\u7684\u82E6\u5904\uFF0C\u4EE5\u90A3\u4E48\u5E74\u8F7B\u6F02\u4EAE\u7684\u4E00\u4E2A\u5973\u4EBA\uFF0C\u9760\u7740\u4E00\u53CC\u7EA4\u7EA4\u7389\u624B\u517B\u5927\u4E86\u4E24\u4E2A\u5973\u513F\uFF0C\u4ECE\u539F\u6765\u7684\u6325\u91D1\u5982\u571F\u5230\u62A0\u7740\u94DC\u677F\u8FC7\u65E5\u5B50\uFF0C\u8FD9\u79CD\u5DE8\u53D8\u5E76\u4E0D\u662F\u6BCF\u4E00\u4E2A\u4EBA\u90FD\u627F\u53D7\u5F97\u4E86\u7684\u3002\u6BCF\u5F53\u60F3\u5230\u6BCD\u4EB2\uFF0C\u51B0\u67D4\u90FD\u4F1A\u6697\u6697\u5782\u6CEA\uFF0C\u5979\u77E5\u9053\u4E3A\u4E86\u5979\u4EEC\u59D0\u59B9\u4FE9\uFF0C\u6BCD\u4EB2\u505A\u51FA\u4E86\u591A\u5927\u7684\u727A\u7272\u3002</p><p>\u5979\u5FC3\u91CC\u660E\u767D\uFF0C\u8981\u4E0D\u662F\u6709\u8FD9\u4E24\u4E2A\u62D6\u6CB9\u74F6\uFF0C\u5F53\u5E74\u8FD8\u4E0D\u5230\u4E09\u5341\u5C81\u3001\u4ECD\u7136\u7F8E\u4E3D\u6027\u611F\u7684\u6BCD\u4EB2\u5B8C\u5168\u53EF\u4EE5\u7EE7\u7EED\u53BB\u5AC1\u4E2A\u5F88\u597D\u7684\u4EBA\u5BB6\u3002</p><p>\u51B0\u67D4\u6BD4\u59B9\u59B9\u7EA2\u68C9\u66F4\u4E86\u89E3\u6BCD\u4EB2\u4ED8\u51FA\u4E86\u591A\u5C11\u3002\u5728\u5979\u7684\u5FC3\u5934\uFF0C\u603B\u6709\u4E00\u4E2A\u7F20\u7ED5\u4E86\u5979\u5341\u51E0\u5E74\u7684\u9634\u5F71\uFF0C\u6325\u62B9\u4E0D\u53BB\u3002\u5979\u6CA1\u6709\u544A\u8BC9\u59B9\u59B9\uFF0C\u4E5F\u6CA1\u6709\u8D23\u602A\u6BCD\u4EB2\uFF0C\u5979\u53EA\u5728\u81EA\u5DF1\u5FC3\u5185\u6162\u6162\u54C1\u5C1D\u7740\u8FD9\u82E6\u6DA9\u7684\u6ECB\u5473\u3002</p><p>\u90A3\u4E00\u5E74\u5979\u53EA\u6709\u5341\u6765\u5C81\uFF0C\u6709\u4E00\u5929\uFF0C\u5979\u63D0\u524D\u653E\u5B66\u56DE\u5230\u5BB6\uFF0C\u7ED3\u679C\u5728\u5C4B\u540E\u7684\u7A97\u5916\uFF0C\u770B\u5230\u4E86\u81F3\u4ECA\u4ECD\u4EE4\u5979\u8138\u7EA2\u4E0D\u5DF2\u7684\u4E00\u5E55\u3002</p><p>\u900F\u8FC7\u6709\u4E00\u70B9\u7834\u70C2\u7684\u6728\u7A97\uFF0C\u662F\u6BCD\u5973\u4E09\u4EBA\u7684\u5367\u5BA4\uFF0C\u72ED\u5C0F\u7684\u7A7A\u95F4\u4E2D\u653E\u4E86\u4E24\u5F20\u7528\u65E7\u6728\u677F\u67B6\u8D77\u7684\u5E8A\uFF0C\u4E00\u5F20\u662F\u6BCD\u4EB2\u7684\uFF0C\u4E00\u5F20\u662F\u4E24\u59D0\u59B9\u7684\u3002\u90A3\u4E2A\u65F6\u5019\uFF0C\u6BCD\u4EB2\u5C31\u5728\u5979\u81EA\u5DF1\u7684\u5E8A\u4E0A\uFF0C\u800C\u5E8A\u4E0A\uFF0C\u540C\u65F6\u8FD8\u6709\u4E00\u4E2A\u4E0D\u8BA4\u8BC6\u7684\u7537\u4EBA\u3002</p><p>\u7537\u4EBA\u501A\u7740\u5899\u5750\u5728\u5E8A\u4E0A\uFF0C\u4E0A\u8863\u7684\u94AE\u6263\u5DF2\u7ECF\u89E3\u5F00\uFF0C\u9732\u51FA\u7ED3\u5B9E\u7684\u80F8\u808C\uFF0C\u4E0B\u8EAB\u4E0D\u7740\u7247\u7F15\uFF0C\u88E4\u5B50\u4E22\u5728\u59D0\u59B9\u4FE9\u7684\u5C0F\u5E8A\u4E0A\uFF0C\u800C\u4E00\u4E1D\u4E0D\u6302\u7684\u6BCD\u4EB2\uFF0C\u8DEA\u5728\u4ED6\u7684\u4E24\u817F\u4E4B\u95F4\uFF0C\u5C06\u5934\u4F0F\u5728\u4ED6\u7684\u80EF\u4E0B\uFF0C\u6709\u8282\u5F8B\u5730\u6446\u52A8\u7740\u3002</p><p>\u7531\u4E8E\u6BCD\u4EB2\u7684\u5E8A\u548C\u6728\u7A97\u4E4B\u5916\u8FD8\u9694\u7740\u540A\u7740\u868A\u5E10\u7684\u5C0F\u5E8A\uFF0C\u90A3\u7F1D\u6EE1\u8865\u4E01\u7684\u868A\u5E10\u4E0A\u7684\u4E00\u5C0F\u5757\u8865\u4E01\uFF0C\u6B63\u5728\u6321\u4F4F\u5C0F\u51B0\u67D4\u7684\u89C6\u7EBF\u3002\u5979\u6CA1\u80FD\u770B\u6E05\u7537\u4EBA\u7684\u8138\u3002</p><p>\u4F46\u90A3\u4EE4\u4EBA\u9707\u60CA\u7684\u4E00\u5E55\uFF0C\u5DF2\u8DB3\u4E8E\u4EE4\u5C0F\u51B0\u67D4\u7C89\u8138\u53D1\u70E7\u3002</p><p>\u6BCD\u4EB2\u90A3\u5C48\u66F2\u7740\u7684\u96EA\u767D\u80F4\u4F53\uFF0C\u72B9\u5982\u4E00\u4E2A\u5669\u68A6\u4E00\u822C\uFF0C\u5341\u51E0\u5E74\u6765\u4E00\u76F4\u5728\u51B0\u67D4\u7684\u5FC3\u5934\u4E0A\u6325\u4E4B\u4E0D\u53BB\uFF0C\u62DB\u5F15\u7740\u5979\u68A6\u4E2D\u5C48\u8FB1\u7684\u6CEA\u6C34\u3002\u5979\u5F77\u5F7F\u65E0\u65F6\u65E0\u523B\u90FD\u5728\u60F3\u50CF\u7740\u6BCD\u4EB2\u90A3\u4E2A\u65F6\u523B\u773C\u4E2D\u7684\u6CEA\u5149\uFF0C\u5373\u4F7F\u5979\u5F53\u65F6\u5E76\u6CA1\u6709\u80FD\u591F\u770B\u5F97\u6E05\u695A\u3002</p><p>\u7537\u4EBA\u8BF4\uFF1A\u300C\u5FEB\u70B9\u3002\u8001\u5B50\u723D\u5B8C\u4E86\uFF0C\u660E\u5929\u5C31\u7ED9\u4F60\u4E24\u4E2A\u5C0F\u599E\u7684\u5B66\u8D39\u3002\u4F60\u4ED6\u5988\u7684\uFF0C\u8001\u5B50\u7684\u503A\u4E00\u70B9\u90FD\u6CA1\u8FD8\uFF0C\u5C45\u7136\u8FD8\u5F97\u8001\u5B50\u5148\u5012\u8D34\u94B1\uFF01\u518D\u4E0D\u5FEB\u70B9\u8001\u5B50\u5E72\u8106\u62FF\u4F60\u53BB\u7A91\u5B50\u91CC\u5356\u7B97\u4E86\uFF01\u300D</p><p>\u6BCD\u4EB2\u6CA1\u6709\u4F5C\u58F0\uFF0C\u53EA\u662F\u8F7B\u8F7B\u98A4\u6296\u7740\u8EAB\u4F53\u3002\u5F53\u5979\u7684\u5934\u62AC\u8D77\u7684\u65F6\u5019\uFF0C\u51B0\u67D4\u770B\u5230\u4E86\u7537\u4EBA\u4E0B\u4F53\u90A3\u6839\u4E4C\u9ED1\u800C\u4E11\u964B\u7684\u8089\u68D2\u6B63\u671D\u5929\u9AD8\u4E3E\u7740\u3002\u8FD9\u662F\u5979\u7B2C\u4E00\u6B21\u770B\u5230\u7537\u4EBA\u7684\u9633\u5177\uFF0C\u5F53\u65F6\u5979\u51E0\u4E4E\u5F53\u573A\u5455\u4E86\u51FA\u6765\u3002\u4EE5\u81F4\u5230\u540E\u6765\uFF0C\u6BCF\u5F53\u5979\u770B\u5230\u7537\u4EBA\u4EEC\u88AB\u5979\u81EA\u5DF1\u6027\u611F\u7684\u8EAB\u6750\u60F9\u5F97\u6491\u7740\u9F13\u8D77\u7684\u88E4\u88C6\u65F6\uFF0C\u90FD\u7981\u4E0D\u4F4F\u4F1A\u6709\u628A\u4ED6\u90A3\u73A9\u610F\u513F\u5207\u4E0B\u6765\u7684\u51B2\u52A8\u3002</p><p>\u7537\u4EBA\u63A5\u7740\u6293\u4F4F\u6BCD\u4EB2\u7684\u5934\u53D1\uFF0C\u5C06\u5979\u6380\u7FFB\u5728\u5E8A\u4E0A\uFF0C\u5C06\u6BCD\u4EB2\u7684\u4E00\u6761\u817F\u625B\u5728\u80A9\u5934\uFF0C\u4E00\u53EA\u624B\u7528\u529B\u63C9\u634F\u7740\u6BCD\u4EB2\u4E30\u6EE1\u7684\u4E73\u623F\uFF0C\u4E00\u53EA\u624B\u6478\u5230\u6BCD\u4EB2\u7684\u80EF\u4E0B\uFF0C\u4E0D\u505C\u5730\u52A8\u4F5C\u7740\u3002\u867D\u7136\u6CA1\u80FD\u770B\u6E05\u7537\u4EBA\u7684\u90A3\u53EA\u624B\u7A76\u7ADF\u5728\u505A\u4EC0\u4E48\uFF0C\u4F46\u662F\u5C0F\u51B0\u67D4\u80FD\u591F\u771F\u5207\u5730\u611F\u53D7\u5230\u6BCD\u4EB2\u6240\u53D7\u5230\u7684\u5C48\u8FB1\uFF0C\u65E9\u719F\u7684\u5979\u5341\u4E00\u5C81\u5C31\u6765\u6F6E\u4E86\uFF0C\u5979\u61C2\u5F97\u5973\u4EBA\u7684\u7F9E\u5904\u5BF9\u4E00\u4E2A\u5973\u4EBA\u6765\u8BF4\u610F\u5473\u7740\u4EC0\u4E48\uFF0C\u800C\u4E3A\u4E86\u5973\u513F\uFF0C\u628A\u81EA\u5DF1\u7684\u7F9E\u5904\u8FD9\u6837\u4EA4\u7ED9\u7537\u4EBA\u73A9\u5F04\uFF0C\u66F4\u610F\u5473\u7740\u4EC0\u4E48\u3002</p><p>\u7537\u4EBA\u8BF4\uFF1A\u300C\u4ED6\u5988\u7684\u4F60\u4E0D\u4F1A\u53EB\u5E8A\u554A\uFF1F\u4E0D\u559C\u6B22\u7ED9\u6211\u641E\u7684\u8BDD\uFF0C\u563F\u563F\u2026\u2026\u4F60\u5927\u5973\u513F\u597D\u50CF\u4E0D\u5C0F\u4E86\u5427\uFF1F\u5012\u4E0D\u5982\u2026\u2026\u300D</p><p>\u300C\u4E0D\u8981\uFF01\u300D\u6BCD\u4EB2\u7ACB\u523B\u53EB\u9053\uFF0C\u53E3\u91CC\u5F00\u59CB\u53D1\u51FA\u4EE4\u5C0F\u51B0\u67D4\u8138\u7EA2\u8033\u8D64\u7684\u547B\u541F\u58F0\uFF0C\u65AD\u65AD\u7EED\u7EED\u8BF4\u9053\uFF1A\u300C\u522B\u8FD9\u6837\u2026\u2026\u5979\u8FD8\u5C0F\u2026\u2026\u300D</p><p>\u4E8E\u662F\u7537\u4EBA\u5206\u5F00\u6BCD\u4EB2\u7684\u53CC\u817F\uFF0C\u9732\u51FA\u6BCD\u4EB2\u4E0B\u4F53\u90A3\u4E4C\u9ED1\u7684\u6BDB\u4E1B\uFF0C\u7136\u540E\u633A\u52A8\u81EA\u5DF1\u90A3\u6839\u4EE4\u4EBA\u6076\u5FC3\u7684\u9633\u5177\uFF0C\u72E0\u72E0\u5730\u63D2\u4E86\u8FDB\u53BB\u3002</p><p>\u51B0\u67D4\u5DF2\u7ECF\u8BB0\u4E0D\u8D77\u81EA\u5DF1\u662F\u5982\u4F55\u79BB\u5F00\u90A3\u4E2A\u7A97\u53E3\u7684\uFF0C\u5979\u53EA\u8BB0\u5F97\u81EA\u5DF1\u5728\u5C4B\u5916\u7684\u7530\u56ED\u4E2D\u5446\u5446\u5730\u5750\u4E86\u5F88\u4E45\u5F88\u4E45\uFF0C\u76F4\u5230\u5929\u8272\u5B8C\u5168\u6697\u4E86\u4E0B\u6765\uFF0C\u624D\u6162\u541E\u541E\u5730\u56DE\u5BB6\u3002</p><p>\u7B2C\u4E8C\u5929\uFF0C\u5979\u679C\u7136\u62FF\u5230\u4E86\u65B0\u5B66\u671F\u7684\u5B66\u8D39\u3002\u5979\u6CA1\u6709\u53BB\u95EE\u6BCD\u4EB2\u94B1\u662F\u600E\u4E48\u6765\u7684\uFF0C\u5979\u60F3\u6BCD\u4EB2\u4E00\u5B9A\u5DF2\u7ECF\u4E3A\u6B64\u541E\u4E0B\u4E86\u4E0D\u77E5\u591A\u5C11\u5C48\u8FB1\u7684\u773C\u6CEA\u3002\u5979\u5DF2\u7ECF\u6B20\u4E86\u6BCD\u4EB2\u5F88\u591A\u4E86\uFF0C\u4E0D\u53EF\u4EE5\u518D\u53BB\u63ED\u8FD9\u65E0\u6CD5\u6108\u5408\u7684\u4F24\u75A4\u3002</p><p>\u51B0\u67D4\u53EA\u77E5\u9053\uFF0C\u5979\u4EEC\u5BB6\u91CC\u7684\u7ECF\u6D4E\u60C5\u51B5\u786E\u5B9E\u6709\u4E86\u4E00\u70B9\u513F\u597D\u8F6C\uFF0C\u4E00\u5BB6\u4E09\u53E3\u7684\u751F\u6D3B\u5B89\u5B9A\u4E86\u5F88\u591A\uFF0C\u6E10\u6E10\u518D\u6CA1\u6709\u503A\u4E3B\u627E\u4E0A\u95E8\u6765\u3002\u6BCD\u4EB2\u544A\u8BC9\u59D0\u59B9\u4FE9\u8FD9\u662F\u7236\u4EB2\u751F\u524D\u4E00\u4F4D\u670B\u53CB\u5E2E\u52A9\u7684\uFF0C\u4F46\u5F53\u65F6\u5E74\u5E7C\u7684\u51B0\u67D4\u5DF2\u7ECF\u770B\u51FA\u4E86\u6BCD\u4EB2\u7684\u795E\u8272\u5E76\u4E0D\u81EA\u7136\uFF0C\u5979\u77E5\u9053\u8FD9\u5C31\u662F\u6BCD\u4EB2\u7528\u5973\u4EBA\u6700\u5B9D\u8D35\u7684\u8D1E\u64CD\u6362\u6765\u7684\u3002\u5979\u4ECE\u6CA1\u4E3A\u6B64\u5728\u5FC3\u5185\u602A\u8D23\u8FC7\u6BCD\u4EB2\uFF0C\u5979\u544A\u8BC9\u81EA\u5DF1\uFF0C\u5982\u679C\u4E0D\u662F\u4E3A\u4E86\u5E74\u5E7C\u7684\u4E24\u59D0\u59B9\uFF0C\u6BCD\u4EB2\u5C31\u4E0D\u7528\u627F\u53D7\u8FD9\u6837\u7684\u82E6\u96BE\u548C\u5C48\u8FB1\uFF0C\u5979\u4E5F\u6CA1\u628A\u4E8B\u60C5\u544A\u8BC9\u59B9\u59B9\uFF0C\u5979\u4E0D\u5E0C\u671B\u59B9\u59B9\u8DDF\u5979\u4E00\u6837\u80CC\u4E0A\u8FD9\u6837\u4E00\u4E2A\u6C89\u91CD\u7684\u9634\u5F71\u3002</p><p>\u51B0\u67D4\u4E00\u8138\u75B2\u5026\u5730\u56DE\u5230\u4E86\u5BB6\uFF0C\u4F46\u5988\u5988\u5374\u4E0D\u5728\u5BB6\u3002\u5988\u5988\u53BB\u54EA\u513F\u4E86\u5462\uFF1F\u51B0\u67D4\u5E76\u4E0D\u6E05\u695A\u3002\u592A\u4E45\u6CA1\u6709\u56DE\u5BB6\u4E86\uFF0C\u6BCD\u4EB2\u73B0\u5728\u662F\u600E\u4E48\u6837\u751F\u6D3B\u7684\uFF0C\u59D0\u59B9\u4FE9\u90FD\u4E0D\u592A\u4E86\u89E3\u3002</p><p>\u4E3A\u4E86\u8C03\u67E5\u9F99\u54E5\u7684\u4E8B\uFF0C\u5979\u5DF2\u7ECF\u4F7F\u5C3D\u4E86\u6D51\u8EAB\u89E3\u6570\uFF0C\u4ECA\u665A\uFF0C\u5979\u8FD8\u4F1A\u6709\u884C\u52A8\u3002\u5979\u73B0\u5728\u9700\u8981\u597D\u597D\u5730\u4F11\u606F\u4E00\u4E0B\u3002</p><p>\u6D74\u6C60\u6B63\u5728\u6CE8\u5165\u70ED\u6C34\uFF0C\u8C37\u51B0\u67D4\u5F00\u59CB\u892A\u53BB\u8EAB\u4E0A\u7684\u8863\u670D\u3002\u5728\u6CA1\u6709\u4EBA\u7684\u6D74\u5BA4\u91CC\uFF0C\u51B0\u67D4\u53EF\u4EE5\u5C3D\u60C5\u5730\u6B23\u8D4F\u81EA\u5DF1\u7F8E\u5999\u7684\u8EAB\u6750\u3002</p><p>\uFF26\uFF0D\uFF23\uFF35\uFF30\u7684\u4E73\u7F69\u89E3\u4E86\u4E0B\u6765\uFF0C\u4E00\u5BF9\u5DE8\u4E73\u5F39\u4E86\u51FA\u6765\uFF0C\u5FAE\u5FAE\u5730\u4E0A\u4E0B\u8DF3\u52A8\u7740\uFF0C\u867D\u7136\u5C3A\u5BF8\u9887\u5927\uFF0C\u4F46\u5374\u5341\u5206\u7684\u575A\u633A\u7ED3\u5B9E\uFF0C\u5F39\u6027\u5341\u8DB3\uFF0C\u800C\u4E24\u53EA\u5C0F\u5DE7\u73B2\u73D1\u7684\u5C0F\u6A31\u6843\u989C\u8272\u5341\u5206\u9C9C\u5AE9\uFF0C\u5F77\u5F7F\u8FD8\u6CA1\u88AB\u7231\u629A\u8FC7\u7684\u5904\u5973\u4E00\u822C\u3002\u5E73\u65F6\uFF0C\u5149\u662F\u7A7F\u7740\u7A0D\u4E3A\u4F4E\u80F8\u7684\u4E0A\u8863\uFF0C\u90A3\u9732\u51FA\u6765\u7684\u6DE1\u6DE1\u4E73\u6C9F\uFF0C\u5C31\u8DB3\u4E8E\u8BA9\u89C1\u5230\u7684\u7537\u4EBA\u5782\u6D8E\u4E09\u5C3A\u3002</p><p>\u5728\u5DE5\u4F5C\u4E2D\uFF0C\u4EE5\u5979\u8FD9\u4E30\u6EE1\u7684\u80F8\u524D\uFF0C\u914D\u4E0A\u5979\u7F8E\u4E3D\u7684\u8138\u5B54\u548C\u9AD8\u6311\u7684\u8EAB\u6750\uFF0C\u518D\u65BD\u4EE5\u4E00\u70B9\u70B9\u5A9A\u52B2\uFF0C\u5C31\u8DB3\u4E8E\u8BA9\u7537\u4EBA\u4EEC\u795E\u9B42\u98A0\u5012\uFF0C\u65E0\u5F80\u4E0D\u5229\u3002</p><p>\u51B0\u67D4\u53CC\u624B\u8F7B\u8F7B\u5730\u6258\u7740\u81EA\u5DF1\u96EA\u767D\u800C\u575A\u633A\u7684\u5DE8\u4E73\uFF0C\u5BF9\u7740\u955C\u5B50\u4ECE\u5E95\u90E8\u8D77\u8F7B\u8F7B\u6309\u6469\u8D77\u6765\u3002\u4F5C\u4E3A\u4E00\u4E2A\u5E74\u8F7B\u7F8E\u8C8C\u7684\u5973\u4EBA\uFF0C\u62E5\u6709\u4E00\u5BF9\u5982\u6B64\u50B2\u4EBA\u7684\u4E73\u623F\u662F\u5341\u5206\u4EE4\u4EBA\u81EA\u8C6A\u7684\u4E8B\uFF0C\u5373\u4FBF\u8C37\u51B0\u67D4\u5E76\u4E0D\u662F\u90A3\u79CD\u559C\u6B22\u6253\u626E\u5316\u5986\u7684\u5973\u4EBA\uFF0C\u4F46\u5BF9\u4E8E\u8FDE\u81EA\u5DF1\u90FD\u611F\u5230\u9A84\u50B2\u7684\u4E73\u623F\uFF0C\u5979\u8FD8\u662F\u5341\u5206\u7684\u73CD\u60DC\u3002</p><p>\u6D74\u6C60\u7684\u70ED\u6C34\u5192\u8D77\u9635\u9635\u7684\u6C34\u96FE\uFF0C\u6E10\u6E10\u6A21\u7CCA\u4E86\u955C\u9762\u3002\u51B0\u67D4\u505C\u6B62\u4E86\u5BF9\u81EA\u5DF1\u4E73\u623F\u7684\u5475\u62A4\uFF0C\u6162\u6162\u8F6C\u8FC7\u8EAB\u4E0A\uFF0C\u89E3\u5F00\u6D45\u84DD\u8272\u7684\u5185\u88E4\u3002</p><p>\u5979\u5149\u6ED1\u7684\u540E\u80CC\u58EE\u800C\u4E0D\u7C97\uFF0C\u72B9\u5982\u96EA\u8102\u51DD\u6210\u4E00\u822C\uFF0C\u767D\u5F97\u5149\u4EAE\u3002\u987A\u7740\u4F18\u7F8E\u7684\u66F2\u7EBF\u5411\u4E0B\uFF0C\u5728\u4E0E\u81C0\u4E18\u7ED3\u5408\u5904\u7684\u53F3\u4E0B\u65B9\uFF0C\u6709\u4E00\u4E2A\u9C9C\u8273\u7684\u7EB9\u8EAB\u5149\u5F69\u593A\u76EE\uFF0C\u90A3\u662F\u4E00\u6735\u7EA2\u68C9\u82B1\u3002</p><p>\u4E94\u53F6\u706B\u7EA2\u8272\u82B1\u74E3\u659C\u5411\u53F3\u4E0A\u65B9\u655E\u5F00\uFF0C\u5408\u62B1\u4E2D\u662F\u4E00\u6839\u7EA4\u7EC6\u7684\u82B1\u854A\uFF0C\u903C\u771F\u5730\u597D\u4F3C\u6B63\u6B32\u8FCE\u98CE\u98DE\u626C\uFF0C\u6563\u53D1\u5B83\u88AB\u6CFD\u5929\u4E0B\u7684\u79CD\u5B50\uFF0C\u800C\u4E0B\u65B9\u90A3\u4E00\u6839\u77ED\u77ED\u7684\u82B1\u679D\uFF0C\u5F77\u5F7F\u4EE4\u4EBA\u8054\u60F3\u5230\u90A3\u82F1\u4F1F\u633A\u52C3\u7684\u7EA2\u68C9\u6811\uFF0C\u6B63\u5728\u5BD2\u98CE\u4E2D\u4F2B\u7ACB\u3002</p><p>\u7EA2\u68C9\u82B1\u7EA2\u5F97\u5341\u5206\u62A2\u773C\uFF0C\u90A3\u4E0D\u662F\u4E00\u822C\u7684\u7EA2\uFF0C\u662F\u8840\u7EA2\u2026\u2026</p>',87);function t(i,n){return a}var _=e(r,[["render",t]]);export{_ as default};
