import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_01" tabindex="-1"><a class="header-anchor" href="#_01" aria-hidden="true">#</a> 01</h1><p>\u300C\u4E0D\u53BB\uFF01\u300D\u7EB1\u5E10\u4E2D\u4E00\u58F0\u5A07\u559D\uFF0C\u98DE\u51FA\u4E00\u4E2A\u6795\u5934\u3002</p><p>\u5C0F\u5A62\u7B49\u4E86\u4E00\u4F1A\u513F\uFF0C\u5C0F\u58F0\u8BF4\uFF1A\u300C\u5C0F\u59D0\uFF0C\u5C31\u5269\u56DB\u4E94\u4E2A\u5BBE\u5BA2\u4E86\uFF0C\u90FD\u662F\u8001\u7237\u7684\u81F3\u4EA4\u597D\u53CB\uFF0C\u4F60\u5C31\u53BB\u4E00\u8D9F\u5427\u2026\u2026\u300D</p><p>\u7EB1\u5E10\u300C\u5237\u300D\u7684\u4E00\u58F0\u62C9\u5F00\uFF0C\u300C\u5A18\u90FD\u4E0D\u8981\u6211\u4E86\uFF0C\u8FD8\u89C1\u4EC0\u4E48\u5BA2\u4EBA\uFF01\u6211\u4E00\u4F1A\u513F\u56DE\u98D8\u6885\u5CF0\uFF0C\u53BB\u5F53\u5C3C\u59D1\uFF01\u300D\u6155\u5BB9\u7D2B\u73AB\u773C\u7736\u53D1\u7EA2\uFF0C\u6C14\u9F13\u9F13\u5730\u8BF4\u7740\uFF0C\u5C0F\u5DE7\u7684\u4E73\u623F\u5728\u4EAE\u7D2B\u8272\u7684\u80F8\u8863\u4E0B\u4E0D\u4F4F\u8DF3\u52A8\uFF0C\u767D\u5AE9\u7684\u808C\u80A4\u5E7D\u9999\u56DB\u6EA2\u3002</p><p>\u5C0F\u5A62\u4E56\u5DE7\u5730\u5750\u5230\u7D2B\u73AB\u8EAB\u540E\uFF0C\u633D\u8D77\u4E4C\u4EAE\u7684\u5934\u53D1\uFF0C\u4E00\u8FB9\u68B3\u4E00\u8FB9\u62BF\u5634\u7B11\u9053\uFF1A\u300C\u5C0F\u59D0\u7684\u5934\u53D1\u591A\u6F02\u4EAE\u554A\uFF0C\u5243\u6389\u53EF\u600E\u4E48\u820D\u5F97\uFF1F\u518D\u8BF4\u4E86\uFF0C\u5973\u5B69\u5BB6\u5243\u5149\u4E86\u5934\uFF0C\u591A\u4E0D\u597D\u770B\u54EA\u3002\u300D</p><p>\u300C\u600E\u4E48\u4E0D\u597D\u770B\uFF1F\u4F60\u6CA1\u89C1\u8FC7\u6211\u5E08\u7236\uFF0C\u5979\u5243\u4E86\u5934\u4E5F\u6F02\u4EAE\u5F97\u5F88\u5462\u3002\u300D</p><p>\u300C\u54CE\u5440\uFF0C\u5C11\u592B\u4EBA\u3001\u7EAA\u5C0F\u59D0\uFF0C\u8FD8\u6709\u5C0F\u59D0\u90FD\u957F\u5F97\u4ED9\u5973\u4F3C\u7684\uFF0C\u518D\u52A0\u4E0A\u96EA\u5CF0\u5E08\u592A\u548C\u98CE\u5973\u4FA0\uFF0C\u4F60\u4EEC\u98D8\u6885\u5CF0\u771F\u662F\u4ED9\u5973\u4F4F\u7684\u5730\u65B9\u5462\u3002\u300D\u5C0F\u5A62\u8273\u7FA1\u5730\u8BF4\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u60F3\u8D77\u5E08\u7236\u3001\u5E08\u59D0\uFF0C\u5E7D\u5E7D\u53F9\u4E86\u53E3\u6C14\uFF0C\u63A5\u7740\u53C8\u53D1\u8D77\u55D4\u6765\uFF0C\u300C\u6155\u5BB9\u80DC\u90A3\u4E2A\u5BB6\u4F19\u771F\u4E0D\u50CF\u8BDD\uFF01\u5A36\u4E86\u4E8C\u5E08\u59D0\u5C31\u591F\u5F97\u610F\u4E86\uFF0C\u5A18\u8FD8\u8981\u53BB\u7ED9\u4ED6\u70E7\u9999\u8FD8\u613F\u3002\u6211\u516D\u5E74\u90FD\u6CA1\u56DE\u6765\u4E86\uFF0C\u5A18\u4E5F\u4E0D\u591A\u966A\u966A\u6211\u8FD9\u4E2A\u5973\u513F\uFF0C\u771F\u662F\u592A\u504F\u5FC3\u4E86\u2026\u2026\u300D</p><p>\u5C0F\u5A62\u653E\u4E0B\u8C61\u7259\u68B3\uFF0C\u76D8\u8D77\u79C0\u53D1\uFF0C\u5B89\u6170\u9053\uFF1A\u300C\u592B\u4EBA\u4E00\u6765\u4E00\u56DE\u8981\u4E0D\u4E86\u4E94\u5929\uFF0C\u5C0F\u59D0\u8FD8\u80FD\u5728\u5BB6\u4F4F\u4E24\u4E2A\u6708\u5462\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u6EE1\u8138\u4E0D\u60C5\u613F\u5730\u7A7F\u4E0A\u6D45\u7EA2\u7EE3\u88D9\u3002\u6731\u5F64\u8272\u7684\u8170\u5E26\u4E00\u675F\uFF0C\u7ACB\u65F6\u663E\u5F97\u73B2\u73D1\u6709\u81F4\u3002\u5986\u53F0\u4E0A\u653E\u7740\u8102\u7C89\u9999\u672B\uFF0C\u5979\u7406\u90FD\u4E0D\u7406\uFF0C\u53EA\u62E3\u51FA\u4E00\u4E2A\u7389\u6263\u63E1\u5728\u624B\u4E2D\u3002</p><p>\u5C0F\u5A62\u6367\u7740\u6E05\u6C34\u8D70\u4E86\u8FDB\u6765\uFF0C\u5AE3\u7136\u5DE7\u7B11\u9053\uFF1A\u300C\u5C0F\u59D0\u5FEB\u4E9B\uFF0C\u6CAE\u6E20\u516C\u5B50\u8FD8\u5728\u5927\u5385\u7B49\u4F60\u5462\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u65F6\u503C\u4E71\u4E16\uFF0C\u5929\u4E0B\u6270\u6518\u4E0D\u4F11\u3002\u5317\u8D77\u5927\u6F20\uFF0C\u5357\u53CA\u86EE\u8352\uFF0C\u4E1C\u6EE8\u5927\u6D77\uFF0C\u897F\u81F3\u6D41\u6C99\u8FD9\u7247\u5E7F\u9614\u7684\u571F\u5730\u4E0A\uFF0C\u7FA4\u96C4\u5E76\u8D77\uFF0C\u5F7C\u6B64\u95F4\u653B\u4F10\u4E86\u65E0\u5B81\u65E5\u3002\u56DB\u5468\u7684\u5308\u5974\u3001\u7FAF\u3001\u6C10\u3001\u7F8C\u3001\u9C9C\u5351\u7B49\u5F02\u65CF\u8D81\u673A\u7EB7\u7EB7\u5317\u4E0A\u5357\u4E0B\uFF0C\u653B\u7565\u4E2D\u539F\u818F\u8174\u4E4B\u5730\u3002\u94C1\u8E44\u6240\u53CA\uFF0C\u76F4\u4E34\u6C5F\u6C49\u3002\u6155\u5BB9\u6C0F\u6B63\u662F\u6E90\u4E8E\u5317\u65B9\u7684\u9C9C\u5351\u5927\u65CF\u3002</p><p>\u767E\u5E74\u95F4\u5929\u4E0B\u6216\u5206\u6216\u5408\uFF0C\u7ACB\u56FD\u4EE5\u6570\u5341\u8BA1\u3002\u4F46\u591A\u534A\u65CB\u7ACB\u65CB\u706D\uFF0C\u957F\u8005\u6570\u5341\u5E74\uFF0C\u77ED\u8005\u4E0D\u8FC7\u5341\u4F59\u5E74\uFF0C\u5174\u4EA1\u5306\u5306\u8FC7\u624B\u3002\u8FD9\u53EF\u82E6\u4E86\u4E2D\u539F\u767E\u59D3\uFF0C\u5B9A\u5C45\u4E8E\u6B64\u7684\u6C49\u6C11\u5341\u5BA4\u4E5D\u7A7A\uFF0C\u5343\u91CC\u826F\u7530\u5C3D\u6210\u8352\u6F20\uFF0C\u9053\u8DEF\u4E24\u65C1\u767D\u9AA8\u7D2F\u7D2F\u3002</p><p>\u5929\u4E0B\u4E0D\u9756\uFF0C\u5374\u662F\u6B66\u6797\u76DB\u4E16\uFF0C\u6709\u5B9E\u529B\u8005\u65E0\u4E0D\u5272\u636E\u79F0\u9738\u3002\u6155\u5BB9\u7D2B\u73AB\u7684\u7236\u4EB2\u6155\u5BB9\u536B\u662F\u4F0F\u9F99\u6DA7\u7684\u5BE8\u4E3B\uFF0C\u7ACB\u5BE8\u5341\u4F59\u5E74\u6765\uFF0C\u5927\u5C0F\u6570\u5341\u6218\uFF0C\u65E0\u4E00\u8D25\u7EE9\uFF0C\u4F46\u4ED6\u5E76\u65E0\u91CE\u5FC3\uFF0C\u53EA\u662F\u7ED3\u5BE8\u81EA\u4FDD\uFF0C\u501A\u4ED7\u7740\u4F0F\u9F99\u6DA7\u8FD1\u5343\u4EBA\u9A6C\uFF0C\u62A4\u5F97\u5468\u906D\u6570\u4E61\u592A\u5E73\u800C\u5DF2\uFF0C\u56E0\u6B64\u5728\u6C5F\u6E56\u4E2D\u540D\u58F0\u5E76\u4E0D\u54CD\u4EAE\u3002\u6155\u5BB9\u7D2B\u73AB\u7684\u6BCD\u4EB2\u8427\u4F5B\u5974\uFF0C\u6700\u662F\u9762\u6148\u5FC3\u8F6F\uFF0C\u4E50\u5584\u597D\u65BD\uFF0C\u88AB\u4EBA\u79F0\u4E3A\u300C\u767E\u82B1\u89C2\u97F3\u300D\u3002</p><p>\u5341\u5C81\u65F6\u6155\u5BB9\u7D2B\u73AB\u88AB\u96EA\u5CF0\u795E\u5C3C\u6536\u4E3A\u5F92\u5F1F\uFF0C\u5C45\u4F4F\u5728\u96EA\u5C71\u4E4B\u5DC5\u7684\u98D8\u6885\u5CF0\u3002\u540C\u95E8\u8FD8\u6709\u4E09\u4F4D\u5E08\u59D0\u3002\u5927\u5E08\u59D0\u98CE\u665A\u534E\u662F\u96EA\u5CF0\u795E\u5C3C\u6536\u517B\u7684\u5B64\u513F\uFF0C\u5C3D\u5F97\u5E08\u7236\u771F\u4F20\uFF0C\u66FE\u4EE5\u4E00\u67C4\u6D41\u971C\u5251\u51FB\u6740\u6C5F\u4E1C\u56DB\u5BC7\uFF0C\u6280\u60CA\u6C5F\u6E56\uFF1B\u4E8C\u5E08\u59D0\u6797\u9999\u8FDC\u867D\u7136\u51FA\u8EAB\u4E66\u9999\u4E16\u5BB6\uFF0C\u8C8C\u7F8E\u5982\u82B1\uFF0C\u4F46\u4FA0\u809D\u4E49\u80C6\uFF0C\u82F1\u6C14\u8FC7\u4EBA\uFF0C\u51FA\u9053\u4E24\u5E74\u6765\uFF0C\u5BD2\u6708\u5200\u7684\u540D\u58F0\u5DF2\u7ECF\u76F4\u8FFD\u6D41\u971C\u5251\uFF1B\u4E09\u5E08\u59D0\u7EAA\u7709\u59A9\u5219\u76F8\u53CD\uFF0C\u5979\u662F\u8C6A\u95E8\u5343\u91D1\uFF0C\u51FA\u8EAB\u5F13\u9A6C\u4E16\u5BB6\uFF0C\u6027\u683C\u5374\u6700\u6E29\u5A49\u67D4\u987A\u3002</p><p>\u6BCD\u4EB2\u867D\u7136\u6148\u7231\uFF0C\u4F46\u5728\u7D2B\u73AB\u5B66\u827A\u8FD9\u4EF6\u4E8B\u5374\u6BEB\u4E0D\u901A\u878D\u3002\u5979\u5728\u98D8\u6885\u5CF0\u5B66\u827A\u516D\u5E74\uFF0C\u672A\u66FE\u4E0B\u5C71\u4E00\u6B65\u3002\u521A\u5F00\u59CB\u65F6\u8FD8\u4E3A\u6B64\u54ED\u9F3B\u5B50\uFF0C\u5E78\u597D\u5E08\u7236\u548C\u4E09\u4F4D\u5E08\u59D0\u5BF9\u5979\u7231\u62A4\u6709\u52A0\uFF0C\u6E10\u6E10\u4E5F\u5C31\u4E60\u60EF\u4E86\u98D8\u6885\u5CF0\u7684\u4E25\u5BD2\u3002</p><p>\u534A\u5E74\u524D\u54E5\u54E5\u6155\u5BB9\u80DC\u53BB\u98D8\u6885\u5CF0\u63A2\u59B9\u59B9\uFF0C\u7ED3\u8BC6\u4E86\u4E8C\u5E08\u59D0\u6797\u9999\u8FDC\u3002\u4E24\u4EBA\u4E00\u89C1\u949F\u60C5\uFF0C\u9042\u7ED3\u4E3A\u79E6\u664B\u4E4B\u597D\uFF0C\u4E94\u65E5\u524D\u5728\u4F0F\u9F99\u6DA7\u6210\u5A5A\uFF0C\u6155\u5BB9\u7D2B\u73AB\u8FD9\u624D\u56DE\u5BB6\u6682\u4F4F\u3002</p><p>\u5C0F\u5A62\u8BF4\u7684\u300C\u6CAE\u6E20\u516C\u5B50\u300D\u4E43\u662F\u6155\u5BB9\u5BB6\u4E16\u4EA4\u5B50\u5F1F\u6CAE\u6E20\u5C55\u626C\u3002\u7D2B\u73AB\u5728\u98D8\u6885\u5CF0\u516D\u5E74\uFF0C\u4ED6\u5012\u4E0A\u5C71\u4E86\u6570\u5341\u8D9F\uFF0C\u6BD4\u7D2B\u73AB\u5BB6\u4EBA\u53BB\u5F97\u8FD8\u52E4\uFF0C\u6BCF\u6B21\u7D2B\u73AB\u90FD\u4F1A\u5F00\u5FC3\u597D\u51E0\u5929\uFF0C\u5BF9\u8FD9\u4E2A\u9752\u6885\u7AF9\u9A6C\u7684\u5C0F\u54E5\u54E5\u597D\u611F\u500D\u589E\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u770B\u5230\u7D2B\u73AB\u8885\u8885\u5165\u5385\uFF0C\u6CAE\u6E20\u5C55\u626C\u5FCD\u4E0D\u4F4F\u9762\u9732\u5FAE\u7B11\u3002\u4ED6\u6BD4\u7D2B\u73AB\u5927\u4E86\u4E94\u5C81\uFF0C\u76F8\u8C8C\u4FCA\u7F8E\uFF0C\u8EAB\u957F\u7389\u7ACB\u3002\u56E0\u4E3A\u5468\u56F4\u8FD8\u5750\u7740\u51E0\u4E2A\u4EBA\uFF0C\u4ED6\u53EA\u6B20\u4E86\u6B20\u8EAB\uFF0C\u6CA1\u6709\u8BF4\u8BDD\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u5B66\u7740\u4E09\u5E08\u59D0\u7684\u6837\u5B50\uFF0C\u4E00\u4E00\u655B\u8EAB\u65BD\u793C\u3002</p><p>\u5728\u5EA7\u7684\u90FD\u662F\u6155\u5BB9\u536B\u76F8\u8BC6\u591A\u5E74\u7684\u8001\u53CB\u3002\u5A5A\u793C\u4E4B\u540E\u6155\u5BB9\u80DC\u4E0E\u59BB\u5B50\u8FDC\u8D74\u8700\u4E2D\u6797\u5BB6\u7701\u4EB2\uFF0C\u8D3A\u4EB2\u7684\u5BBE\u5BA2\u9646\u7EED\u79BB\u5F00\uFF0C\u8FD9\u51E0\u4F4D\u4E00\u76F4\u7559\u5230\u4ECA\u5929\u3002\u540D\u9707\u4E1C\u6D77\u7684\u5251\u4FA0\u4E1C\u65B9\u5E86\u7B11\u9053\uFF1A\u300C\u4F73\u513F\u5982\u9F99\uFF0C\u5A07\u5973\u4F3C\u51E4\uFF0C\u6155\u5BB9\u5144\u771F\u662F\u597D\u798F\u6C14\u3002\u300D</p><p>\u6155\u5BB9\u536B\u5E74\u903E\u4E94\u5341\uFF0C\u9762\u5982\u51A0\u7389\uFF0C\u957F\u9AEF\u5782\u80F8\uFF0C\u95FB\u8A00\u7B11\u9053\uFF1A\u300C\u4E1C\u65B9\u5144\u8FC7\u5956\u4E86\uFF0C\u5C0F\u5973\u6027\u60C5\u987D\u52A3\uFF0C\u8FDE\u671B\u8BF8\u4F4D\u591A\u591A\u6307\u70B9\u3002\u300D</p><p>\u6E58\u897F\u767D\u6C99\u6D3E\u7684\u638C\u95E8\u695A\u8FDE\u96C4\u7B11\u9053\uFF1A\u300C\u4EE4\u7231\u4E0B\u5C71\u4E0D\u8FC7\u6708\u4F59\uFF0C\u73AB\u7470\u4ED9\u5B50\u5DF2\u7ECF\u540D\u626C\u6C5F\u6E56\uFF0C\u6BD4\u6211\u4EEC\u8FD9\u4E9B\u8001\u8F88\u540D\u5934\u8FD8\u54CD\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u4FCF\u8138\u98DE\u7EA2\uFF0C\u5782\u5934\u770B\u5230\u6CAE\u6E20\u5C55\u626C\u602A\u602A\u7684\u7B11\u5BB9\uFF0C\u4E0D\u7981\u5FC3\u5E95\u6697\u6068\uFF0C\u5077\u5077\u77AA\u4E86\u4ED6\u4E00\u773C\u3002</p><p>\u6CAE\u6E20\u5C55\u626C\u8D77\u8EAB\u62B1\u62F3\u9053\uFF1A\u300C\u6155\u5BB9\u4F2F\u7236\uFF0C\u5C0F\u4F84\u8DEF\u9014\u9065\u8FDC\uFF0C\u4E0D\u53CA\u8046\u542C\u4F2F\u7236\u548C\u8BF8\u4F4D\u53D4\u53D4\u7684\u6559\u8BF2\uFF0C\u5148\u8BF7\u544A\u8F9E\u3002\u300D</p><p>\u6155\u5BB9\u536B\u89C6\u6CAE\u6E20\u5C55\u626C\u5982\u540C\u5B50\u4F84\uFF0C\u5BF9\u4E24\u4EBA\u611F\u60C5\u65E5\u6DF1\u4E50\u89C2\u5176\u6210\uFF0C\u89C1\u72B6\u9053\uFF1A\u300C\u73AB\u513F\uFF0C\u4F60\u66FF\u7239\u7239\u9001\u5C55\u626C\u4E00\u7A0B\u3002\u300D</p><p>\u7D2B\u73AB\u677F\u7740\u8138\u4E3E\u6B65\u51FA\u95E8\uFF0C\u8EAB\u540E\u4F20\u6765\u4E00\u7247\u5584\u610F\u7684\u7B11\u58F0\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8D70\u4E86\u51E0\u6B65\uFF0C\u7D2B\u73AB\u90A3\u70B9\u513F\u5C0F\u813E\u6C14\u5DF2\u7ECF\u98DE\u5230\u4E5D\u9704\u4E91\u5916\uFF0C\u6CAE\u6E20\u5C55\u626C\u6025\u6B65\u8FFD\u6765\uFF0C\u300C\u5514\uFF0C\u4F60\u8FD8\u5E26\u7740\u5B83\uFF1F\u7528\u7740\u65B9\u4FBF\u5417\uFF1F\u300D</p><p>\u6731\u7EA2\u8272\u7684\u8170\u5E26\u4E0A\u7CFB\u7740\u4E00\u53EA\u91D1\u9EC4\u8272\u7684\u5C0F\u5F29\uFF0C\u53EA\u6709\u624B\u638C\u5927\u5C0F\uFF0C\u505A\u5DE5\u7CBE\u81F4\u7EC6\u5DE7\uFF0C\u8FD9\u662F\u7D2B\u73AB\u5341\u4E8C\u5C81\u751F\u65E5\u65F6\u6CAE\u6E20\u5C55\u626C\u9001\u7ED9\u5979\u7684\u793C\u7269\u3002</p><p>\u7D2B\u73AB\u505C\u4E0B\u811A\u6B65\uFF0C\u628A\u624B\u5FC3\u91CC\u4E00\u76F4\u6525\u7740\u7684\u7389\u6263\u9012\u7ED9\u4ED6\u3002</p><p>\u300C\u8FD9\u662F\u4EC0\u4E48\uFF1F\u300D</p><p>\u7D2B\u73AB\u559C\u5B5C\u5B5C\u5730\u8BF4\uFF1A\u300C\u6F02\u4EAE\u5417\uFF1F\u300D</p><p>\u6CAE\u6E20\u5C55\u626C\u70B9\u70B9\u5934\uFF0C\u300C\u4F60\u7684\uFF1F\u300D</p><p>\u300C\u5434\u53D4\u7684\uFF0C\u4ED6\u5E74\u7EAA\u90A3\u4E48\u5927\uFF0C\u7559\u7740\u6CA1\u4EC0\u4E48\u7528\uFF0C\u5C31\u7ED9\u4E86\u6211\u3002\u5476\uFF0C\u9001\u7ED9\u4F60\u597D\u4E86\u3002\u300D</p><p>\u5434\u9707\u662F\u6155\u5BB9\u536B\u5F97\u529B\u624B\u4E0B\uFF0C\u6628\u65E5\u5348\u95F4\u62A4\u9001\u592B\u4EBA\u767E\u82B1\u89C2\u97F3\u53BB\u6D1B\u9633\u793C\u4F5B\u3002\u591A\u534A\u662F\u4E34\u884C\u524D\u7D2B\u73AB\u770B\u4E2D\u8FD9\u4E2A\u7389\u6263\uFF0C\u6B7B\u7F20\u786C\u78E8\u8981\u8FC7\u6765\u9001\u7ED9\u81EA\u5DF1\u3002\u6CAE\u6E20\u5C55\u626C\u54D1\u7136\u5931\u7B11\uFF0C\u4F46\u53C8\u5FC3\u4E0B\u611F\u52A8\uFF0C\u63A5\u8FC7\u8FD8\u5E26\u7740\u7D2B\u73AB\u4F53\u6E29\u7684\u7389\u6263\uFF0C\u4E00\u65F6\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002\u4ED6\u51DD\u89C6\u7740\u90A3\u5F20\u707F\u82E5\u4E91\u971E\u7684\u4FCF\u8138\uFF0C\u826F\u4E45\u624D\u7FFB\u8EAB\u4E0A\u9A6C\u3002</p><p>\u597D\u4E0D\u5BB9\u6613\u9001\u8D70\u4E86\u5BBE\u5BA2\uFF0C\u6155\u5BB9\u7D2B\u73AB\u6765\u5230\u9759\u5BA4\u76D8\u819D\u8FD0\u529F\u3002\u7F15\u7F15\u771F\u6C14\u4ECE\u4E39\u7530\u6563\u51FA\uFF0C\u6E38\u8D70\u4E8E\u56DB\u80A2\u767E\u9AB8\uFF0C\u6700\u540E\u91CD\u5F52\u4E8E\u4E39\u7530\uFF0C\u5F80\u590D\u4E0D\u5DF2\u3002</p><p>\u98D8\u6885\u5CF0\u8BF8\u5F1F\u5B50\u867D\u662F\u540C\u95E8\uFF0C\u4F46\u53EA\u6709\u7D2B\u73AB\u4E00\u4EBA\u5F97\u795E\u5C3C\u4F20\u6388\u300A\u51E4\u51F0\u5B9D\u5178\u300B\u3002\u76F8\u4F20\u6B64\u5B9D\u5178\u4E43\u662F\u672C\u6D3E\u5F00\u5C71\u4E4B\u7956\u8D56\u4EE5\u6210\u540D\u7684\u7EDD\u6280\u3002\u5386\u4EE3\u76F8\u4F20\uFF0C\u4FEE\u4E60\u5B9D\u5178\u53EA\u80FD\u662F\u5904\u5B50\u7684\u7EAF\u9634\u4E4B\u4F53\uFF0C\u4E00\u65E6\u7834\u8EAB\uFF0C\u5C06\u4F1A\u6709\u6027\u547D\u4E4B\u5FE7\uFF0C\u56E0\u6B64\u98D8\u6885\u5CF0\u8BF8\u4EE3\u638C\u95E8\u90FD\u662F\u51FA\u5BB6\u4EBA\u3002\u53EF\u81EA\u7956\u5E08\u4EE5\u964D\uFF0C\u4ECE\u672A\u6709\u4EBA\u7EC3\u81F3\u5927\u6210\uFF0C\u751A\u81F3\u8FDE\u8FBE\u5230\u7B2C\u4E03\u5C42\u7684\u90FD\u6781\u5C11\u3002</p><p>\u5B9D\u5178\u7CBE\u6DF1\u5E7D\u5FAE\uFF0C\u6709\u8BF8\u591A\u96BE\u660E\u4E4B\u5904\uFF0C\u4FEE\u884C\u4E0D\u6613\uFF0C\u5C24\u5176\u521D\u7EC3\u65F6\u51E0\u4E4E\u6CA1\u4EC0\u4E48\u6548\u679C\uFF0C\u8FDE\u96EA\u5CF0\u795E\u5C3C\u81EA\u5DF1\u4E5F\u662F\u7531\u522B\u6CD5\u5165\u624B\uFF0C\u6700\u540E\u624D\u7814\u4E60\u5B9D\u5178\u3002\u7EC3\u81F3\u7B2C\u4E03\u5C42\u65F6\uFF0C\u795E\u5C3C\u611F\u89C9\u5230\u5B9D\u5178\u5185\u8574\u85CF\u7740\u6781\u5927\u7684\u5A01\u529B\uFF0C\u5979\u89C1\u6155\u5BB9\u7D2B\u73AB\u6839\u9AA8\u5947\u4F73\uFF0C\u5E74\u7EAA\u53C8\u5C0F\uFF0C\u8FD9\u624D\u4F20\u4E8E\u5F53\u65F6\u521A\u5165\u95E8\u7684\u7D2B\u73AB\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u770B\u4E0A\u53BB\u5E74\u5E7C\u987D\u76AE\uFF0C\u5176\u5B9E\u6781\u6709\u6BC5\u529B\uFF0C\u516D\u5E74\u6765\u5979\u8FDB\u6B65\u795E\u901F\uFF0C\u51E4\u51F0\u5B9D\u5178\u5DF2\u7EC3\u81F3\u7B2C\u56DB\u5C42\uFF0C\u4F46\u7D2B\u73AB\u6B64\u65F6\u529F\u529B\u975E\u4F46\u8FDC\u4E0D\u53CA\u5927\u5E08\u59D0\u98CE\u665A\u534E\uFF0C\u6BD4\u4E09\u5E08\u59D0\u7EAA\u7709\u59A9\u4E5F\u5DEE\u5F97\u8FDC\uFF0C\u552F\u6709\u8F7B\u529F\u4E00\u9879\uFF0C\u8FDC\u8D85\u4FAA\u8F88\u3002</p><p>\u4E0E\u6B64\u540C\u65F6\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E5F\u5728\u7B2C\u4E03\u5C42\u518D\u65E0\u5BF8\u8FDB\uFF0C\u5B9D\u5178\u6B64\u540E\u7684\u6587\u5B57\u6666\u6DA9\u96BE\u660E\uFF0C\u6240\u8F7D\u72B6\u51B5\u51E0\u4E4E\u65E0\u4E00\u80FD\u4E0E\u7EC3\u529F\u65F6\u7684\u60C5\u5F62\u76F8\u540C\u3002\u96EA\u5CF0\u795E\u5C3C\u5BF9\u6B64\u767E\u601D\u4E0D\u5F97\u5176\u89E3\uFF0C\u7D2B\u73AB\u4E0B\u5C71\u65F6\u8FD8\u5429\u5490\u5979\u52E4\u52A0\u4FEE\u7EC3\uFF0C\u4EE5\u4FBF\u65E9\u65E5\u7EC3\u5230\u7B2C\u4E03\u5C42\uFF0C\u5E08\u5F92\u4E24\u4EBA\u597D\u4E92\u76F8\u53C2\u6821\uFF0C\u770B\u80FD\u5426\u89E3\u5F00\u5B9D\u5178\u4E4B\u8C1C\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5929\u8272\u8584\u66AE\uFF0C\u6155\u5BB9\u7D2B\u73AB\u7F13\u7F13\u6536\u529F\u3002\u4E8C\u6708\u7684\u5929\u6C14\u4E4D\u6696\u8FD8\u5BD2\uFF0C\u5979\u7AD9\u5728\u9636\u524D\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\uFF0C\u611F\u53D7\u7740\u5BB6\u4E61\u719F\u6089\u7684\u6C14\u606F\u3002</p><p>\u5FAE\u98CE\u62C2\u8FC7\uFF0C\u8863\u8882\u98DE\u626C\uFF0C\u5F85\u770B\u5230\u5EAD\u4E2D\u4E00\u62B9\u6DE1\u6DE1\u7684\u7EFF\u610F\uFF0C\u7D2B\u73AB\u773C\u775B\u4E00\u4EAE\uFF0C\u98DE\u4E5F\u4F3C\u7684\u5954\u8FDB\u4F0F\u9F99\u5802\uFF0C\u5174\u594B\u5730\u8BF4\uFF1A\u300C\u7239\uFF0C\u9662\u5B50\u91CC\u957F\u51FA\u4E00\u679D\u5C0F\u8349\u5462\u3002\u300D\u5979\u4E45\u5C45\u96EA\u5C71\uFF0C\u8FD9\u8FD8\u662F\u5934\u4E00\u6B21\u770B\u5230\u521D\u6625\u7684\u65B0\u8349\u3002</p><p>\u6155\u5BB9\u536B\u5FAE\u7B11\u7740\u62AC\u8D77\u5934\u6B63\u5F85\u8BF4\u8BDD\uFF0C\u5374\u89C1\u4E00\u4E2A\u624B\u4E0B\u5FEB\u6B65\u5165\u5385\u9012\u6765\u4E00\u53EA\u6728\u5323\uFF0C\u300C\u5BE8\u4E3B\uFF0C\u9002\u624D\u6709\u4EBA\u9001\u6765\u8FD9\u4E2A\u3002\u300D</p><p>\u6728\u5323\u5C3A\u8BB8\u5927\u5C0F\uFF0C\u8272\u6CFD\u4E4C\u9ED1\uFF0C\u76D2\u76D6\u4E0A\u76D8\u7740\u4E24\u6761\u6D82\u91D1\u98DE\u9F99\uFF0C\u5F20\u7259\u821E\u722A\uFF0C\u5374\u672A\u7559\u9898\u6B3E\u3002</p><p>\u6155\u5BB9\u536B\u5728\u6C5F\u6E56\u95EF\u8361\u591A\u5E74\uFF0C\u5FC3\u77E5\u6709\u5F02\uFF0C\u62D4\u51FA\u957F\u5200\u6311\u8D77\u6728\u76D2\u653E\u5728\u684C\u4E0A\uFF0C\u7EC6\u770B\u4E86\u534A\u664C\uFF0C\u7136\u540E\u9000\u540E\u4E00\u6B65\u6311\u5F00\u9501\u94AE\u3002</p><p>\u6728\u5323\u556A\u7684\u4E00\u58F0\u5F39\u5F00\uFF0C\u5468\u56F4\u8BF8\u4EBA\u7ACB\u523B\u8138\u4E0A\u53D8\u8272\u3002</p><p>\u6728\u5323\u91CC\u94FA\u7740\u4E00\u5757\u9C9C\u7EA2\u7684\u9526\u7F0E\uFF0C\u4E0A\u9762\u662F\u4E00\u5BF9\u7EA4\u5DE7\u7684\u5C0F\u811A\uFF0C\u80A4\u8272\u83B9\u767D\uFF0C\u521B\u53E3\u8840\u8FF9\u5C1A\u65B0\uFF0C\u5206\u660E\u662F\u521A\u4ECE\u5973\u5B50\u811A\u8E1D\u4E0A\u9F50\u9F50\u65A9\u4E0B\uFF01</p>',51);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
