import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_02" tabindex="-1"><a class="header-anchor" href="#_02" aria-hidden="true">#</a> 02</h1><p>\u79C0\u7F8E\u7684\u811A\u638C\u9759\u9759\u8E0F\u5728\u7EA2\u7EF8\u4E0A\uFF0C\u51C4\u8273\u65E0\u6BD4\u3002\u5931\u53BB\u8840\u8272\u7684\u808C\u80A4\u6676\u83B9\u5254\u900F\uFF0C\u8BA9\u4EBA\u4E0D\u7531\u60F3\u8D77\u4E3B\u4EBA\u7684\u8F7B\u76C8\u4F53\u6001\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u5BA1\u89C6\u534A\u664C\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4E0D\u662F\u6211\u5A18\u7684\u3002\u300D</p><p>\u6155\u5BB9\u536B\u677E\u4E86\u53E3\u6C14\uFF0C\u95EE\u9053\uFF1A\u300C\u662F\u8C01\u9001\u6765\u7684\uFF1F\u300D</p><p>\u90A3\u540D\u624B\u4E0B\u60CA\u5F97\u9762\u5982\u571F\u8272\uFF0C\u300C\u2026\u2026\u662F\u2026\u2026\u662F\u4E2A\u7A7F\u9EC4\u8863\u7684\u80D6\u5B50\u2026\u2026\u653E\u4E0B\u76D2\u5B50\u5C31\u8D70\u4E86\u2026\u2026\u300D</p><p>\u300C\u53BB\u8FFD\uFF01\u53EB\u8BB8\u3001\u5468\u3001\u6731\u3001\u5C24\u56DB\u4F4D\u9996\u9886\u5404\u5E26\u5341\u540D\u5144\u5F1F\u5206\u8DEF\u641C\u7D22\uFF0C\u95E8\u524D\u4E0E\u4ED6\u89C1\u8FC7\u9762\u7684\u5144\u5F1F\u90FD\u8DDF\u7740\u53BB\u3002\u8BB0\u4F4F\uFF0C\u4E0D\u8BB8\u58F0\u5F20\uFF01\u300D\u5429\u5490\u4E86\u624B\u4E0B\uFF0C\u6155\u5BB9\u536B\u51DD\u795E\u601D\u7D22\u81EA\u5DF1\u6709\u4F55\u4EC7\u5BB6\u3002</p><p>\u7D2B\u73AB\u6B64\u65F6\u770B\u51FA\u6B8B\u80A2\u4E5F\u5E76\u975E\u4E8C\u4F4D\u5E08\u59D0\u6240\u6709\uFF0C\u4FBF\u4ECE\u9B13\u89D2\u62D4\u4E0B\u94F6\u9497\uFF0C\u6311\u5F00\u76D2\u4E2D\u6240\u94FA\u7684\u9526\u7F0E\u3002</p><p>\u9526\u7F0E\u4E0B\u9732\u51FA\u4E00\u5F20\u4FE1\u7B3A\uFF0C\u7D2B\u73AB\u7565\u4E00\u8FC7\u76EE\uFF0C\u4FCF\u8138\u987F\u65F6\u6DA8\u5F97\u901A\u7EA2\uFF0C\u7389\u6307\u4E00\u5F39\uFF0C\u5C06\u4FE1\u7B3A\u9489\u5728\u6728\u5323\u4E0A\u3002</p><p>\u6DE1\u9EC4\u8272\u7684\u4FE1\u7EB8\u5728\u98CE\u4E2D\u8106\u8106\u4F5C\u54CD\uFF0C\u4E0A\u9762\u58A8\u8272\u7EB5\u6A2A\uFF1A\u5199\u7740\u51E0\u884C\u5927\u5B57\uFF1A\u300C\u4ECA\u591C\u5B50\u65F6\u732E\u51FA\u5B9D\u85CF\u3001\u6155\u5BB9\u7D2B\u73AB\u3002\u5426\u5219\u4F0F\u9F99\u6DA7\u9E21\u72AC\u4E0D\u7559\uFF01\u300D</p><p>\u5B57\u8FF9\u5251\u62D4\u5F29\u5F20\uFF0C\u6700\u540E\u843D\u6B3E\u7684\u300C\u9F99\u300D\u5B57\uFF0C\u5199\u5F97\u66F4\u662F\u8DCB\u6248\u5F20\u626C\uFF0C\u76F4\u6B32\u7834\u7EB8\u98DE\u53BB\uFF0C\u663E\u7136\u4E66\u8005\u529F\u529B\u6781\u6DF1\u3002</p><p>\u300C\u7239\uFF0C\u4EC0\u4E48\u5B9D\u85CF\uFF1F\u300D</p><p>\u6155\u5BB9\u536B\u6C89\u9ED8\u7247\u523B\uFF0C\u5FFD\u7136\u5251\u7709\u4E00\u6311\uFF0C\u8BF4\u9053\uFF1A\u300C\u4F0F\u9F99\u6DA7\u867D\u7136\u8D2B\u5F31\uFF0C\u4F46\u5411\u6765\u672C\u5206\uFF0C\u5BE8\u4E2D\u81EA\u7ED9\u81EA\u8DB3\uFF0C\u54EA\u6765\u7684\u5B9D\u85CF\uFF01\u300D\u58F0\u97F3\u867D\u54CD\uFF0C\u4F46\u4ED6\u5FC3\u91CC\u5374\u5FD0\u5FD1\u4E0D\u5B89\uFF0C\u300C\u8C01\uFF1F\u7A76\u7ADF\u662F\u8C01\uFF1F\u5C45\u7136\u77E5\u9053\u5B9D\u85CF\uFF1F\u8FD8\u70B9\u660E\u8981\u73AB\u513F\uFF0C\u83AB\u975E\u2026\u2026\u4E0D\u53EF\u80FD\uFF01\u300D</p><p>\u4ED6\u5FC3\u4E0B\u5FD0\u5FD1\u4E0D\u5B89\uFF0C\u300C\u5982\u679C\u771F\u7684\u662F\u5979\u627E\u5230\u8FD9\u91CC\uFF0C\u5B9A\u7136\u4E0D\u4F1A\u53EA\u8981\u73AB\u513F\uFF0C\u96BE\u9053\u592B\u4EBA\u5DF2\u7ECF\u2026\u2026\u300D</p><p>\u300C\u8001\u7237\u3001\u5C0F\u59D0\uFF0C\u5403\u996D\u4E86\u2026\u2026\u5440\uFF01\u300D\u8FDB\u6765\u7980\u62A5\u7684\u5C0F\u5A62\u63A8\u95E8\u770B\u89C1\u684C\u4E0A\u7684\u65AD\u8DB3\uFF0C\u4E0D\u7531\u82B1\u5BB9\u5931\u8272\u3002</p><p>\u7D2B\u73AB\u98D8\u8EAB\u6400\u8D77\u5C0F\u5A62\uFF0C\u63A9\u4E0A\u623F\u95E8\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u522B\u6015\u3002\u300D</p><p>\u5C0F\u5A62\u6218\u6218\u5162\u5162\u770B\u7740\u65AD\u8DB3\uFF0C\u7A81\u7136\u60CA\u53EB\u9053\uFF1A\u300C\u79C0\u513F\uFF01\u300D</p><p>\u6155\u5BB9\u536B\u548C\u7D2B\u73AB\u8138\u4E0A\u540C\u65F6\u53D8\u8272\uFF0C\u79C0\u513F\u662F\u6BCD\u4EB2\u7684\u8D34\u8EAB\u4E2B\u73AF\uFF0C\u6628\u65E5\u968F\u767E\u82B1\u89C2\u97F3\u540C\u53BB\u793C\u4F5B\uFF0C\u600E\u4E48\u4F1A\u88AB\u4EBA\u65A9\u65AD\u811A\u638C\u9001\u5230\u5BE8\u4E2D\uFF1F</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u767E\u82B1\u89C2\u97F3\u8427\u4F5B\u5974\u6B64\u65F6\u5DF2\u7ECF\u9047\u9669\u3002</p><p>\u6628\u65E5\u5348\u95F4\u5979\u5E26\u7740\u79C0\u513F\u3001\u5434\u9707\u548C\u516B\u540D\u968F\u4ECE\u4E00\u8DEF\u8D76\u5230\u4E34\u6CB3\u9547\uFF0C\u8DEF\u4E0A\u7A81\u7136\u9047\u88AD\u3002</p><p>\u6570\u5341\u540D\u9EC4\u8863\u6C49\u5B50\u5C06\u4F17\u4EBA\u56F4\u5728\u4E2D\u95F4\uFF0C\u4E00\u8A00\u4E0D\u53D1\u7684\u52A8\u624B\u65AF\u6740\u3002\u4E3A\u9996\u7684\u662F\u4E00\u4E2A\u9EC4\u888D\u80D6\u5B50\uFF0C\u770B\u4E0A\u53BB\u50CF\u4E2A\u5BCC\u5BB6\u7FC1\u822C\u6EE1\u9762\u7B11\u5BB9\uFF0C\u4F46\u638C\u529B\u6C89\u6D51\uFF0C\u4E0B\u624B\u6B7B\u8FA3\uFF0C\u6570\u62DB\u95F4\u4F0F\u9F99\u6DA7\u516B\u540D\u968F\u4ECE\u4FBF\u6216\u6B7B\u6216\u4F24\u3002\u5434\u9707\u89C1\u52BF\u4E0D\u5999\u8FDE\u5FD9\u6321\u5728\u8F66\u524D\uFF0C\u4E00\u8FB9\u6325\u5200\u5411\u90A3\u540D\u80D6\u5B50\u780D\u53BB\uFF0C\u4E00\u8FB9\u53EB\u9053\uFF1A\u300C\u592B\u4EBA\u5FEB\u8D70\uFF01\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u53EA\u542C\u8F66\u5916\u7EDD\u53EB\u4E4B\u58F0\u4E0D\u7EDD\u4E8E\u8033\uFF0C\u521A\u521A\u6380\u5F00\u8F66\u5EC9\uFF0C\u53C8\u662F\u4E00\u58F0\u60CA\u5929\u52A8\u5730\u7684\u5DE8\u543C\uFF0C\u63A5\u7740\u4E00\u56E2\u9EC4\u5F71\u72F8\u732B\u822C\u8DC3\u5165\u8F66\u4E2D\uFF0C\u4F38\u6307\u70B9\u5728\u4E3B\u5A62\u4E24\u4EBA\u8170\u95F4\u3002</p><p>\u4E00\u62DB\u5F97\u624B\uFF0C\u90A3\u4E2A\u9EC4\u8863\u80D6\u5B50\u5012\u662F\u5446\u4E86\u4E00\u4E0B\uFF0C\u300C\u767E\u82B1\u89C2\u97F3\u540D\u58F0\u6041\u54CD\uFF0C\u5C45\u7136\u4E0D\u4F1A\u6B66\u529F\uFF1F\u300D</p><p>\u95EA\u7535\u822C\u7684\u7A81\u88AD\u5DF2\u7ED3\u675F\uFF0C\u4E00\u4E2A\u9EC4\u8863\u4EBA\u62B1\u62F3\u9053\uFF1A\u300C\u5C60\u957F\u8001\uFF0C\u5916\u9762\u4E5D\u4EBA\u4E03\u6B7B\u4E8C\u4F24\uFF0C\u8BF7\u957F\u8001\u793A\u4E0B\u3002\u300D</p><p>\u90A3\u4E2A\u53EB\u5C60\u957F\u8001\u7684\u80D6\u5B50\u6446\u4E86\u6446\u624B\uFF0C\u300C\u4E0D\u7559\u6D3B\u53E3\u3002\u300D</p><p>\u9EC4\u8863\u6C49\u5B50\u5200\u67AA\u9F50\u65BD\uFF0C\u5C06\u4E24\u540D\u5782\u6B7B\u7684\u968F\u4ECE\u5C3D\u6570\u6740\u6B7B\uFF0C\u8FDE\u51E0\u5177\u5C38\u9996\u4E5F\u8865\u4E86\u51E0\u5200\u3002</p><p>\u6B8B\u5FCD\u7684\u5C60\u6740\u4F7F\u8427\u4F5B\u5974\u60CA\u9A87\u5F97\u51E0\u6B32\u6655\u5012\uFF0C\u5434\u9707\u9AD8\u5927\u7684\u8EAB\u4F53\u4EF0\u8EAB\u5012\u5728\u8F66\u65C1\uFF0C\u6574\u4E2A\u9762\u90E8\u548C\u524D\u80F8\u8840\u8089\u6A21\u7CCA\uFF0C\u50CF\u88AB\u5DE8\u77F3\u7838\u8FC7\u4E00\u822C\u6CA1\u6709\u534A\u5BF8\u5B8C\u597D\u7684\u76AE\u8089\u3002</p><p>\u9A6C\u8F66\u91CD\u65B0\u5954\u9A70\u8D77\u6765\u3002\u5C60\u957F\u8001\u6DEB\u7B11\u7740\u5728\u767E\u82B1\u89C2\u97F3\u5149\u6D01\u7684\u8138\u86CB\u4E0A\u634F\u4E86\u4E00\u628A\uFF0C\u4F38\u624B\u89E3\u5F00\u5979\u7684\u7A74\u9053\u3002\u8427\u4F5B\u5974\u628A\u80CC\u8D34\u5728\u8F66\u58C1\u4E0A\uFF0C\u7D27\u5F20\u5730\u76EF\u7740\u9762\u524D\u8FD9\u4E2A\u7B11\u772F\u772F\u7684\u80D6\u5B50\u98A4\u58F0\u95EE\u9053\uFF0C\u300C\u4F60\u4EEC\u662F\u4EC0\u4E48\u4EBA\uFF1F\u4E3A\u4F55\u884C\u51F6\u4F24\u4EBA\uFF1F\u300D</p><p>\u67D4\u9888\u88F9\u5728\u4E4C\u4EAE\u7684\u8C82\u88D8\u95F4\uFF0C\u66F4\u663E\u5F97\u5176\u767D\u5982\u96EA\u3002\u9AD8\u8038\u7684\u5706\u4E73\u968F\u7740\u9A6C\u8F66\u7684\u884C\u8FDB\u8F7B\u8F7B\u6447\u6643\uFF0C\u98CE\u97F5\u5341\u8DB3\u3002\u5979\u76F8\u8C8C\u4E0E\u7D2B\u73AB\u7565\u4F3C\uFF0C\u4F46\u76F8\u6BD4\u4E8E\u7D2B\u73AB\u5E74\u7EAA\u5C1A\u5E7C\u7684\u79C0\u4E3D\uFF0C\u767E\u82B1\u89C2\u97F3\u663E\u5F97\u66F4\u4E3A\u7F8E\u8273\u3002\u5979\u5E74\u7EAA\u4E0D\u8FC7\u4E09\u5341\u4F59\u5C81\uFF0C\u6C14\u8D28\u9AD8\u96C5\u534E\u8D35\uFF0C\u5B9B\u5982\u8D35\u5987\uFF0C\u7389\u5BB9\u7AEF\u5E84\u6B63\u5982\u89C2\u97F3\uFF0C\u600E\u4E48\u770B\u90FD\u4E0D\u50CF\u662F\u6B66\u6797\u5927\u8C6A\u7684\u59BB\u5BA4\u3002</p><p>\u5C60\u957F\u8001\u8272\u6B32\u5927\u52A8\uFF0C\u72DE\u7B11\u58F0\u4E2D\u4E00\u628A\u626F\u4F4F\u5979\u7684\u9526\u888D\u3002\u767E\u82B1\u89C2\u97F3\u60CA\u53EB\u7740\u5411\u65C1\u95EA\u907F\uFF0C\u4F46\u5979\u4E00\u4E2A\u5F31\u8D28\u5973\u6D41\u600E\u654C\u8FC7\u6B66\u529F\u9AD8\u5F3A\u7684\u5C60\u957F\u8001\uFF0C\u4E00\u6323\u4E4B\u4E0B\u4FBF\u88AB\u90A3\u4E2A\u80D6\u5B50\u62C9\u5230\u6000\u4E2D\u3002</p><p>\u5C60\u957F\u8001\u4E0D\u7406\u4F1A\u5979\u7684\u7591\u95EE\uFF0C\u4F38\u51FA\u820C\u5934\u5728\u767E\u82B1\u89C2\u97F3\u5A07\u7F8E\u7684\u8138\u5E9E\u4E0A\u4E00\u901A\u4E71\u8214\u3002\u8165\u81ED\u7684\u553E\u6DB2\u4F7F\u5979\u51E0\u6B32\u4F5C\u5455\uFF0C\u8427\u4F5B\u5974\u6323\u624E\u7740\u626D\u8FC7\u5934\uFF0C\u4E24\u624B\u7AED\u529B\u63A8\u6421\u3002\u5FFD\u7136\u8170\u95F4\u4E00\u51C9\uFF0C\u9526\u888D\u786C\u751F\u751F\u88AB\u6495\u4E0B\u4E00\u5757\u3002</p><p>\u8427\u4F5B\u5974\u6C14\u6068\u4EA4\u52A0\uFF0C\u4E00\u638C\u671D\u5C60\u957F\u8001\u90A3\u5F20\u4E11\u964B\u7684\u80A5\u8138\u4E0A\u6253\u53BB\u3002\u5C60\u957F\u8001\u4E0D\u95EA\u4E0D\u907F\uFF0C\u53CD\u800C\u5F20\u5F00\u5927\u5634\uFF0C\u5C06\u5979\u7684\u7389\u6307\u5659\u5728\u5634\u4E2D\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u4E00\u9635\u6076\u5FC3\uFF0C\u8FDE\u5FD9\u7F29\u624B\uFF0C\u6307\u4E0A\u5FAE\u5FAE\u4E00\u75DB\uFF0C\u6307\u8282\u5DF2\u88AB\u5C60\u957F\u8001\u54AC\u4F4F\u3002\u6E7F\u4E4E\u4E4E\u7684\u820C\u5934\u5728\u624B\u6307\u95F4\u94BB\u6765\u94BB\u53BB\uFF0C\u5982\u679C\u662F\u7D2B\u73AB\u80AF\u5B9A\u4F1A\u4E00\u628A\u5C06\u4ED6\u7684\u820C\u5934\u62FD\u4E0B\u6765\uFF0C\u4F46\u767E\u82B1\u89C2\u97F3\u5374\u662F\u56DB\u6307\u62FC\u547D\u5F20\u5F00\uFF0C\u8EB2\u907F\u90A3\u6761\u6076\u5FC3\u7684\u820C\u5934\u3002</p><p>\u8F66\u53A2\u4E2D\u300C\u54E7\u54E7\u300D\u58F0\u4E0D\u7EDD\u4E8E\u8033\uFF0C\u6BCF\u4E00\u58F0\u54CD\u8D77\uFF0C\u90FD\u6709\u4E00\u7247\u788E\u9526\u79BB\u5F00\u8EAB\u4F53\u3002\u5C60\u957F\u8001\u5341\u6307\u5B9B\u5982\u94C1\u94A9\uFF0C\u4E0D\u591A\u65F6\u4FBF\u5C06\u767E\u82B1\u89C2\u97F3\u7684\u9526\u888D\u6495\u788E\uFF0C\u9732\u51FA\u767D\u5AE9\u7684\u808C\u80A4\u3002</p><p>\u9EC4\u8863\u80D6\u5B50\u5341\u6307\u7FFB\u98DE\uFF0C\u50CF\u732B\u513F\u620F\u9F20\u822C\u5728\u5979\u8EAB\u4E0A\u56DB\u5904\u4E71\u6478\u3002\u4E09\u5341\u4F59\u5E74\u6765\u8427\u4F5B\u5974\u4EAB\u5C3D\u8363\u534E\u5BCC\u8D35\uFF0C\u5373\u4F7F\u5728\u4F0F\u9F99\u6DA7\u4F17\u4EBA\u4E5F\u5BF9\u5979\u5C0A\u5D07\u6709\u52A0\uFF0C\u4F55\u66FE\u53D7\u8FC7\u8FD9\u79CD\u7F9E\u8FB1\uFF1F\u767E\u82B1\u89C2\u97F3\u60CA\u60F6\u5931\u63AA\uFF0C\u8FDE\u8BDD\u90FD\u8BF4\u4E0D\u51FA\u6765\uFF0C\u53EA\u80FD\u53D1\u51FA\u65AD\u65AD\u7EED\u7EED\u7684\u60CA\u53EB\u3002\u5FFD\u7136\u80A1\u95F4\u4E00\u51C9\uFF0C\u90A3\u53CC\u5927\u624B\u4E00\u628A\u6342\u4F4F\u5979\u7684\u4E0B\u9634\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u5FC3\u9053\u4ECA\u65E5\u96BE\u514D\u53D7\u8FB1\uFF0C\u6BEB\u65E0\u53CD\u6297\u4E4B\u529B\u4E0B\uFF0C\u53EA\u597D\u51C4\u7136\u5408\u4E0A\u7F8E\u76EE\uFF0C\u644A\u5F00\u8EAB\u4F53\uFF0C\u4EFB\u4ED6\u4E3A\u6240\u6B32\u4E3A\u3002\u53EF\u90A3\u53EA\u624B\u53EA\u5728\u5A07\u67D4\u7684\u82B1\u74E3\u4E0A\u6765\u56DE\u63C9\u6413\uFF0C\u5E76\u672A\u6DF1\u5165\u3002</p><p>\u624B\u6307\u4E0B\u7EC6\u817B\u7684\u808C\u80A4\u4E1D\u822C\u987A\u6ED1\uFF0C\u5C60\u957F\u8001\u867D\u7136\u6B32\u706B\u4E2D\u70E7\uFF0C\u5374\u4E0D\u6562\u4FB5\u5165\u767E\u82B1\u89C2\u97F3\u7684\u8EAB\u5B50\uFF0C\u4ED6\u4E00\u628A\u62C9\u8D77\u79C0\u513F\uFF0C\u6495\u788E\u5979\u7684\u8863\u670D\u7FFB\u8EAB\u538B\u4E86\u4E0A\u53BB\u3002\u79C0\u513F\u75DB\u53EB\u4E00\u58F0\uFF0C\u80A1\u95F4\u6D41\u51FA\u4E00\u62B9\u65B0\u7EA2\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u773C\u91CC\u5145\u6EE1\u6CEA\u6C34\uFF0C\u62B1\u7740\u9999\u80A9\u745F\u7F29\u5728\u4E00\u65C1\u3002\u7B49\u5C60\u957F\u8001\u53D1\u6CC4\u5B8C\u517D\u6B32\uFF0C\u79C0\u513F\u5DF2\u7ECF\u6C14\u606F\u5944\u5944\u3002</p><p>\u9A6C\u8F66\u5728\u4E00\u6240\u9662\u5185\u505C\u4E0B\uFF0C\u5C60\u957F\u8001\u631F\u7740\u4E24\u4E2A\u8D64\u88F8\u7684\u5973\u4EBA\u8DF3\u4E0B\u9A6C\u8F66\u3002\u591C\u8272\u5DF2\u6DF1\uFF0C\u5802\u4E2D\u5374\u706F\u706B\u901A\u660E\uFF0C\u4E00\u4E2A\u7EA2\u8863\u6C49\u5B50\u8FCE\u51FA\u6765\u63A5\u8FC7\u4E24\u5973\uFF0C\u5728\u706F\u4E0B\u770B\u4E86\u4E00\u773C\uFF0C\u300C\u5567\u5567\u300D\u7B11\u9053\uFF1A\u300C\u767E\u82B1\u89C2\u97F3\u6709\u4E09\u5341\u591A\u5C81\u4E86\u5427\uFF0C\u6A21\u6837\u6BD4\u8FD9\u4E2B\u5934\u8FD8\u4FCA\u4FCF\uFF0C\u602A\u4E0D\u5F97\u5BAB\u4E3B\u5FF5\u5FF5\u4E0D\u5FD8\u3002\u300D</p><p>\u5C60\u957F\u8001\u9053\uFF1A\u300C\u767E\u82B1\u89C2\u97F3\u5BAB\u4E3B\u53EF\u662F\u4EA4\u5F85\u8FC7\u3002\u8FD9\u4E2B\u5934\u968F\u4FBF\u3002\u300D</p><p>\u7EA2\u8863\u6C49\u5B50\u563F\u563F\u4E00\u7B11\uFF0C\u628A\u79C0\u513F\u4E22\u5230\u5802\u4E2D\uFF0C\u559D\u9053\uFF1A\u300C\u628A\u5C41\u80A1\u62AC\u8D77\u6765\uFF01\u300D</p><p>\u79C0\u513F\u7565\u4E00\u8FDF\u7591\uFF0C\u7EA2\u8863\u6C49\u5B50\u62AC\u811A\u8E29\u5728\u5979\u624B\u4E0A\u72E0\u72E0\u4E00\u62E7\uFF0C\u5C0F\u624B\u7ACB\u523B\u8840\u8089\u6A21\u7CCA\u3002</p><p>\u5C60\u957F\u8001\u6478\u7740\u809A\u5B50\u7B11\u9053\uFF1A\u300C\u970D\u957F\u8001\u813E\u6C14\u706B\u7206\uFF0C\u4E0D\u50CF\u6211\u8FD9\u4E48\u597D\u8BF4\u8BDD\u3002\u5C0F\u59D1\u5A18\uFF0C\u4F60\u8FD8\u662F\u8001\u8001\u5B9E\u5B9E\u542C\u970D\u957F\u8001\u5429\u5490\uFF0C\u514D\u5F97\u5403\u82E6\u3002\u300D</p><p>\u79C0\u513F\u521A\u6EE1\u5341\u4E94\uFF0C\u6B64\u65F6\u53C8\u75DB\u53C8\u6015\uFF0C\u65E9\u5413\u5F97\u5446\u4E86\u3002\u970D\u957F\u8001\u89C1\u72B6\u53C8\u8981\u671D\u5979\u53E6\u4E00\u53EA\u624B\u4E0A\u8E29\u53BB\uFF0C\u767E\u82B1\u89C2\u97F3\u8FDE\u5FD9\u63A9\u5728\u7231\u5A62\u8EAB\u524D\uFF0C\u4E5E\u6C42\u9053\uFF1A\u300C\u5979\u521A\u521A\u7834\u4E86\u8EAB\u5B50\uFF0C\u5C31\u9976\u5979\u8FD9\u4E00\u6B21\u5427\u3002\u300D</p><p>\u970D\u957F\u8001\u6DEB\u90AA\u5730\u76EF\u7740\u8427\u4F5B\u5974\u719F\u7F8E\u7684\u8EAB\u4F53\uFF0C\u62C9\u5F00\u7EA2\u888D\u628A\u72F0\u72DE\u7684\u8089\u68D2\u9012\u5230\u767E\u82B1\u89C2\u97F3\u9762\u524D\uFF0C\u6311\u9017\u5730\u5728\u5979\u5507\u4E0A\u64E6\u4E86\u64E6\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u7389\u8138\u98DE\u7EA2\uFF0C\u8FDE\u5FD9\u4FA7\u8FC7\u5934\u53BB\u3002</p><p>\u300C\u513F\u5B50\u5973\u513F\u90FD\u751F\u4E0B\u6765\u4E86\uFF0C\u8FD8\u88C5\u4EC0\u4E48\u5904\u5973\u2026\u2026\u300D\u970D\u957F\u8001\u6B32\u706B\u5927\u52A8\uFF0C\u4F38\u624B\u5C31\u60F3\u53BB\u62C9\u767E\u82B1\u89C2\u97F3\u7684\u5934\u53D1\u3002\u5C60\u957F\u8001\u5E72\u54B3\u4E00\u58F0\uFF0C\u4ED6\u624D\u60BB\u60BB\u7136\u8F6C\u8FC7\u8EAB\u53BB\uFF0C\u559D\u9053\uFF1A\u300C\u6B7B\u4E2B\u5934\uFF0C\u628A\u5C44\u62AC\u8D77\u6765\uFF0C\u8BA9\u7237\u64CD\u6B7B\u4F60\uFF01\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u8FD8\u60F3\u54C0\u6C42\uFF0C\u5374\u628A\u5C60\u957F\u8001\u4E00\u628A\u62C9\u4F4F\uFF0C\u300C\u522B\u64CD\u5FC3\u5979\u4E86\uFF0C\u6709\u4F60\u4E50\u7684\u5462\u3002\u300D</p><p>\u5802\u89D2\u653E\u7740\u4E00\u5757\u9A6C\u978D\u72B6\u7684\u5DE8\u77F3\u3002\u5F2F\u62F1\u72B6\u7684\u77F3\u80CC\u4E0A\u6709\u4E00\u9053\u5BBD\u5BF8\u8BB8\uFF0C\u957F\u56DB\u5BF8\u5DE6\u53F3\u7684\u6C9F\u69FD\uFF0C\u91CC\u9762\u659C\u659C\u5D4C\u7740\u4E00\u6839\u7389\u77F3\u96D5\u5C31\u7684\u5706\u67F1\u72B6\u7269\u4F53\uFF0C\u8868\u9762\u96D5\u7740\u4E24\u6761\u76D8\u9F99\uFF0C\u9CDE\u7532\u98DE\u626C\u3002</p><p>\u77F3\u978D\u77F3\u68D2\u5200\u8FF9\u5C1A\u65B0\uFF0C\u5F53\u65F6\u662F\u65B0\u5236\u4E0D\u4E45\u3002</p><p>\u8427\u4F5B\u5974\u88AB\u8FD9\u4E2A\u602A\u5F02\u7684\u7269\u54C1\u5F04\u5F97\u6EE1\u5934\u96FE\u6C34\uFF0C\u53EA\u542C\u5C60\u957F\u8001\u7B11\u563B\u563B\u8BF4\u9053\uFF1A\u300C\u521A\u521A\u5236\u6210\uFF0C\u8BF7\u89C2\u97F3\u8BD5\u7528\u3002\u5662\uFF0C\u8FD9\u662F\u77F3\u9A74\uFF0C\u4EFF\u7167\u5B98\u5E9C\u6728\u9A74\u6240\u9020\uFF0C\u4E0D\u5408\u9002\u7684\u5730\u65B9\uFF0C\u8FD8\u8BF7\u5927\u58EB\u591A\u5305\u6DB5\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u4F18\u96C5\u7F8E\u8273\u7684\u4FCF\u8138\u4E0A\u6EE1\u662F\u60CA\u6050\u7F9E\u803B\u3002\u5979\u89C1\u4E24\u4EBA\u4E0D\u6562\u4FB5\u72AF\u81EA\u5DF1\uFF0C\u5FC3\u4E0B\u7565\u4E3A\u5B89\u5B9A\uFF0C\u6CA1\u60F3\u5230\u7ADF\u662F\u8981\u7559\u7740\u8EAB\u5B50\u8BA9\u77F3\u5236\u7684\u6DEB\u5177\u6765\u6298\u78E8\u3002\u8427\u4F5B\u5974\u82B1\u5BB9\u5931\u8272\uFF0C\u95EA\u8EAB\u6B32\u907F\uFF0C\u5374\u88AB\u5C60\u957F\u8001\u4E00\u628A\u62B1\u4F4F\u3002</p><p>\u7C97\u7CD9\u7684\u5927\u624B\u63E1\u4F4F\u819D\u5F2F\uFF0C\u5C06\u767E\u82B1\u89C2\u97F3\u4E24\u817F\u5206\u5F00\u3002\u5149\u6DA6\u7684\u7389\u80A1\u95F4\uFF0C\u5A07\u8273\u7684\u82B1\u74E3\u5FAE\u5FAE\u7EFD\u9732\uFF0C\u5BF9\u51C6\u77F3\u68D2\u6162\u6162\u5957\u4E86\u4E0B\u53BB\u3002</p><p>\u51B0\u51C9\u7684\u77F3\u68D2\u4E00\u70B9\u4E00\u70B9\u6CA1\u5165\u5AE9\u8089\uFF0C\u5148\u662F\u7389\u767D\u8272\u7684\u9F99\u5934\u6324\u5165\u8089\u7A74\uFF0C\u63A5\u7740\u662F\u9F99\u8EAB\u3001\u9F99\u722A\u3001\u9F99\u5C3E\u2026\u2026</p><p>\u8427\u4F5B\u5974\u5999\u76EE\u5706\u7741\uFF0C\u5341\u4F59\u672A\u88AB\u4EBA\u4FB5\u72AF\u8FC7\u7684\u8089\u4F53\u5374\u88AB\u5F02\u7269\u6345\u5165\uFF0C\u90A3\u79CD\u523A\u9AA8\u7684\u7F9E\u803B\uFF0C\u4F7F\u5979\u5FD8\u4E86\u75BC\u75DB\u3002</p>',55);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
