import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_03" tabindex="-1"><a class="header-anchor" href="#_03" aria-hidden="true">#</a> 03</h1><p>\u79C0\u513F\u5FCD\u75DB\u62AC\u8D77\u81C0\u90E8\uFF0C\u628A\u79D8\u5904\u5B8C\u5168\u66B4\u9732\u4E86\u51FA\u6765\u3002\u970D\u957F\u8001\u5BF9\u5979\u6EF4\u8840\u7684\u8089\u7A74\u6BEB\u4E0D\u7406\u4F1A\uFF0C\u8FF3\u76F4\u523A\u5165\u83CA\u809B\u3002\u4ED6\u7684\u8089\u68D2\u672C\u5C31\u7C97\u5927\uFF0C\u6B64\u65F6\u7565\u4E00\u8FD0\u529F\uFF0C\u987F\u65F6\u70BD\u70ED\u5982\u706B\uFF0C\u53EA\u62BD\u9001\u6570\u4E0B\uFF0C\u79C0\u513F\u4FBF\u6655\u4E86\u8FC7\u53BB\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u5DF2\u88AB\u5C60\u957F\u8001\u653E\u5728\u77F3\u978D\u4E0A\uFF0C\u4E24\u819D\u89E6\u5230\u5730\u9762\uFF0C\u5979\u6323\u624E\u7740\u60F3\u7AD9\u8D77\u6765\u3002\u970D\u957F\u8001\u624B\u4E2D\u5BD2\u5149\u4E00\u95EA\uFF0C\u5C06\u79C0\u513F\u90A3\u53EA\u5B8C\u597D\u7684\u624B\u638C\u9F50\u8155\u5272\u4E0B\u3002\u767E\u82B1\u89C2\u97F3\u88AB\u79C0\u513F\u7684\u60E8\u53EB\u5413\u5446\u4E86\uFF0C\u9762\u65E0\u8840\u8272\u7684\u770B\u7740\u4ECD\u5728\u62BD\u9001\u7684\u970D\u957F\u8001\u3002</p><p>\u970D\u957F\u8001\u62FF\u8D77\u90A3\u53EA\u65AD\u624B\u51B2\u767E\u82B1\u89C2\u97F3\u626C\u4E86\u626C\uFF1A\u300C\u5750\u597D\u4E86\u3002\u4E0D\u5C31\u662F\u6345\u4F60\u7684\u9A9A\u5C44\u5417\uFF1F\u53C8\u6B7B\u4E0D\u4E86\uFF01\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u770B\u7740\u65AD\u8155\u4E0A\u98DE\u6E85\u7684\u9C9C\u8840\uFF0C\u50CF\u88AB\u62BD\u53BB\u4E86\u5168\u8EAB\u529B\u6C14\uFF0C\u518D\u4E5F\u52A8\u5F39\u4E0D\u5F97\u3002</p><p>\u9ECE\u660E\u65F6\u5206\uFF0C\u9A6C\u8F66\u79BB\u5F00\u5927\u9662\u3002\u8F66\u53A2\u4E2D\u4E00\u4E2A\u6C14\u8D28\u534E\u8D35\u7684\u7F8E\u5987\u4F18\u96C5\u5730\u8DEA\u5750\u5728\u9752\u9ED1\u8272\u7684\u77F3\u978D\u4E0A\u3002\u4E91\u9B13\u6563\u4E71\uFF0C\u795E\u60C5\u51C4\u5A49\uFF0C\u59E3\u4E3D\u7684\u7389\u5BB9\u4E00\u7247\u82CD\u767D\uFF0C\u80F8\u524D\u6D51\u5706\u7684\u4E73\u623F\u968F\u7740\u8F66\u53A2\u7684\u98A0\u7C38\u6296\u52A8\u4E0D\u5DF2\u3002</p><p>\u540C\u6837\u98A0\u7C38\u7684\u8FD8\u6709\u90A3\u6839\u6DF1\u5165\u4F53\u5185\u7684\u77F3\u68D2\uFF0C\u63A5\u4E0A\u4E86\u89E6\u5230\u5730\u9762\u7684\u94DC\u8F6E\u540E\uFF0C\u5B83\u4FBF\u5F00\u59CB\u6446\u52A8\u8D77\u6765\u3002\u5347\u8D77\u65F6\u786C\u751F\u751F\u9876\u5230\u5B50\u5BAB\u5165\u53E3\uFF0C\u843D\u4E0B\u65F6\u53C8\u72E0\u72E0\u628A\u82B1\u5F84\u6491\u5F00\u3002\u7A00\u8584\u7684\u6DEB\u6C34\u65E9\u5DF2\u5E72\u6DB8\uFF0C\u8089\u58C1\u7531\u75BC\u75DB\u5230\u9EBB\u6728\uFF0C\u518D\u7531\u9EBB\u6728\u5230\u9635\u9635\u5267\u75DB\u3002\u5468\u800C\u590D\u59CB\uFF0C\u6C38\u65E0\u6B62\u6B47\u3002\u7F8E\u5987\u53CC\u76EE\u7D27\u95ED\uFF0C\u8033\u8FB9\u4F3C\u4E4E\u8FD8\u54CD\u7740\u5C0F\u5A62\u7684\u60E8\u53EB\u3002</p><p>\u970D\u72C2\u7130\u751F\u6027\u6B8B\u8650\u597D\u6740\uFF0C\u53D1\u6CC4\u5B8C\u517D\u6B32\u540E\uFF0C\u4FBF\u5174\u81F4\u52C3\u52C3\u7684\u6298\u78E8\u8D77\u79C0\u513F\u6765\u3002\u4ED6\u7528\u7EF3\u7D22\u5C06\u5973\u5B69\u7684\u80A9\u90E8\u548C\u817F\u6839\u624E\u7D27\uFF0C\u7136\u540E\u4E00\u5BF8\u4E00\u5BF8\u5272\u53BB\u79C0\u513F\u624B\u811A\u7EC6\u5AE9\u7684\u808C\u80A4\uFF0C\u6B23\u8D4F\u7740\u5C11\u5973\u7684\u54ED\u53EB\u85C9\u6B64\u53D6\u4E50\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u773C\u7741\u7741\u770B\u7740\u7231\u5A62\u56DB\u80A2\u6E10\u6E10\u53D8\u77ED\u3001\u6D88\u5931\uFF0C\u800C\u8EAF\u4F53\u4F9D\u7136\u5B8C\u7F8E\u5982\u6545\uFF0C\u53EA\u89C9\u5F97\u624B\u811A\u51B0\u51C9\uFF0C\u6CA1\u6709\u4E00\u70B9\u77E5\u89C9\u2026\u2026</p><p>\u4E0D\u77E5\u8D70\u4E86\u591A\u4E45\uFF0C\u9A6C\u8F66\u7EC8\u4E8E\u505C\u4F4F\uFF0C\u5F85\u9EC4\u8863\u4EBA\u5C06\u5979\u62B1\u79BB\u77F3\u978D\uFF0C\u9F99\u7EB9\u4E0A\u5DF2\u662F\u8840\u8FF9\u6591\u6591\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u8270\u96BE\u7684\u7741\u5F00\u773C\u775B\uFF0C\u9762\u524D\u8F7B\u7EB1\u822C\u7684\u8584\u96FE\u6B63\u7F15\u7F15\u6563\u5F00\uFF0C\u9732\u51FA\u4E00\u6CD3\u78A7\u6C34\u3002</p><p>\u6C34\u9762\u751A\u662F\u5BBD\u9614\uFF0C\u4F46\u5D4C\u5728\u5C71\u8170\u7FA4\u5CF0\u5408\u62B1\u4E4B\u4E2D\uFF0C\u5374\u663E\u5F97\u7CBE\u5DE7\u7EC6\u81F4\uFF0C\u5B9B\u5982\u4E00\u9897\u84DD\u5B9D\u77F3\u822C\u707C\u707C\u751F\u8F89\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4EA5\u65F6\u5C06\u81F3\uFF0C\u4F0F\u9F99\u5802\u9ED1\u6C89\u6C89\u4E00\u7247\u3002\u9664\u4E86\u51FA\u95E8\u7684\u51E0\u4F4D\u9996\u9886\u5916\uFF0C\u4F0F\u9F99\u6DA7\u7CBE\u9510\u5C3D\u5728\u4E8E\u6B64\u3002</p><p>\u6155\u5BB9\u536B\u773C\u4E2D\u7A81\u7136\u5BD2\u5149\u4E00\u95EA\uFF0C\u5438\u4E86\u53E3\u6C14\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u661F\u6708\u6E56\u7684\u5996\u5B7D\uFF0C\u8D76\u7D27\u51FA\u6765\u5427\u3002\u300D</p><p>\u5E9C\u95E8\u897F\u8FB9\u7684\u7BAD\u697C\u4E0A\u4F20\u51FA\u4E00\u58F0\u9634\u607B\u607B\u7684\u7B11\u58F0\uFF0C\u9EC4\u8863\u80D6\u5B50\u5C60\u957F\u8001\u957F\u8EAB\u800C\u8D77\uFF0C\u300C\u5728\u4E0B\u5C60\u6000\u6C89\uFF0C\u7279\u5949\u4E0A\u8584\u793C\u4E00\u4EFD\u3002\u300D\u8BF4\u7740\u6296\u624B\u6254\u4E0B\u4E00\u4E2A\u4EBA\u5934\u3002\u5B88\u5728\u4F0F\u9F99\u5802\u5916\u7684\u4EB2\u968F\u8DC3\u8EAB\u63A5\u8FC7\uFF0C\u521A\u89E6\u5230\u90A3\u9897\u5934\u9885\uFF0C\u4EBA\u8FD8\u5728\u534A\u7A7A\u4E2D\u7A81\u5730\u4E00\u50F5\uFF0C\u76F4\u76F4\u8DCC\u4E86\u4E0B\u6765\u3002</p><p>\u4E0D\u7528\u770B\u6155\u5BB9\u536B\u5C31\u77E5\u9053\u90A3\u9897\u4EBA\u5934\u662F\u81EA\u5DF1\u7684\u5C5E\u4E0B\uFF0C\u5982\u6B64\u9738\u9053\u7684\u6BD2\u836F\uFF0C\u66F4\u8BC1\u5B9E\u4E86\u4ED6\u7684\u60F3\u6CD5\u3002\u4F46\u5F53\u521D\u884C\u4E8B\u9690\u853D\uFF0C\u6CA1\u6709\u7559\u4E0B\u4EC0\u4E48\u8E2A\u8FF9\uFF0C\u4E3A\u4F55\u5341\u4F59\u5E74\u540E\u4F1A\u88AB\u4ED6\u4EEC\u627E\u95E8\u6765\uFF1F</p><p>\u6155\u5BB9\u7D2B\u73AB\u9759\u9759\u7ACB\u5728\u9636\u524D\uFF0C\u7CBE\u81F4\u7684\u9762\u988A\u5B9B\u5982\u4E03\u5B9D\u73AB\u7470\uFF0C\u5728\u591C\u4E2D\u6D41\u5149\u6EA2\u5F69\uFF0C\u4F3C\u4E4E\u8FD8\u6563\u53D1\u7740\u6DE1\u6DE1\u7684\u9999\u6C14\u3002</p><p>\u4E1C\u8FB9\u7684\u7BAD\u697C\u4E0A\u7AD9\u8D77\u4E00\u4E2A\u4E09\u5341\u6765\u5C81\u7684\u7C97\u58EE\u6C49\u5B50\uFF0C\u670D\u8272\u706B\u7EA2\uFF0C\u987B\u53D1\u6012\u5F20\uFF0C\u4ED6\u9AD8\u53EB\u9053\uFF1A\u300C\u6155\u5BB9\u536B\uFF01\u5B50\u65F6\u5C06\u81F3\uFF0C\u4F60\u8003\u8651\u6E05\u695A\u4E86\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u536B\u6DE1\u6DE1\u9053\uFF1A\u300C\u4E0D\u5FC5\u5E9F\u8BDD\uFF0C\u4E0B\u6765\u53D7\u6B7B\u5427\u3002\u300D</p><p>\u8EAB\u7740\u7EA2\u888D\u7684\u706B\u5802\u957F\u8001\u970D\u72C2\u7130\u6012\u543C\u4E00\u58F0\uFF0C\u62AC\u811A\u6311\u8D77\u4E00\u56E2\u96EA\u767D\u7684\u7269\u4F53\u4E22\u4E86\u4E0B\u6765\u3002</p><p>\u8FD9\u6B21\u6CA1\u6709\u4EBA\u518D\u6562\u51FA\u624B\u53BB\u63A5\uFF0C\u90FD\u773C\u7741\u7741\u770B\u7740\u5B83\u4ECE\u9AD8\u5904\u8DCC\u843D\uFF0C\u6FC0\u8D77\u4E00\u7247\u8840\u5149\u3002</p><p>\u90A3\u662F\u4E2A\u56DB\u80A2\u7686\u65E0\u7684\u5C11\u5973\uFF0C\u80A1\u95F4\u8FD8\u4E0D\u505C\u5730\u6D41\u7740\u9C9C\u8840\uFF0C\u53EA\u5269\u8EAF\u4F53\u7684\u8089\u6BB5\u7ADF\u7136\u8FD8\u5FAE\u5FAE\u8815\u52A8\u3002\u770B\u5230\u79C0\u513F\u7684\u60E8\u72B6\uFF0C\u6155\u5BB9\u536B\u8138\u8272\u5927\u53D8\u3002\u90A3\u4E2A\u5A07\u8D35\u7684\u5973\u5B50\u8981\u662F\u843D\u5165\u661F\u6708\u5BAB\u4E3B\u624B\u4E2D\uFF0C\u4F1A\u6709\u4EC0\u4E48\u6837\u7684\u906D\u9047\uFF1F\u4E00\u5411\u9547\u5B9A\u7684\u6155\u5BB9\u536B\u4E0D\u7531\u624B\u6307\u5FAE\u98A4\u3002</p><p>\u7A81\u7136\u91D1\u5149\u4E00\u95EA\uFF0C\u76F4\u76F4\u6CA1\u5165\u79C0\u513F\u7684\u80F8\u53E3\uFF0C\u53EA\u9732\u51FA\u4E00\u622A\u6D01\u767D\u7684\u7FBD\u6BDB\u3002\u6155\u5BB9\u7D2B\u73AB\u4E00\u7BAD\u5C04\u6B7B\u79C0\u513F\uFF0C\u514D\u5F97\u5979\u518D\u53D7\u82E6\uFF0C\u62AC\u5934\u76EF\u7740\u970D\u72C2\u7130\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u4E0B\u6765\u5427\u3002\u300D</p><p>\u66B4\u559D\u58F0\u4E2D\u970D\u72C2\u7130\u4ECE\u5341\u4F59\u4E08\u5916\u7684\u7BAD\u697C\u76F4\u6251\u4E0B\u6765\u3002</p><p>\u6155\u5BB9\u536B\u66FE\u4E0E\u661F\u6708\u6E56\u4E94\u957F\u8001\u4E4B\u4E00\u4EA4\u8FC7\u624B\uFF0C\u6DF1\u77E5\u5BF9\u65B9\u6781\u4E3A\u96BE\u60F9\uFF0C\u5F53\u5373\u633A\u5200\u6321\u5728\u5973\u513F\u8EAB\u524D\u3002</p><p>\u5899\u5934\u767E\u4F59\u4EBA\u540C\u65F6\u73B0\u8EAB\uFF0C\u5206\u7740\u7EA2\u9EC4\u4E24\u8272\uFF0C\u6B63\u662F\u661F\u6708\u6E56\u4E94\u884C\u95E8\u4E2D\u706B\u571F\u4E24\u5802\u5C5E\u4E0B\u3002</p><p>\u957F\u5251\u5BD2\u5149\u4F3C\u6C34\uFF0C\u6155\u5BB9\u7D2B\u73AB\u98D8\u8EAB\u62B5\u4F4F\u70C8\u7130\u3001\u731B\u70BD\u4E24\u540D\u706B\u5802\u9999\u4E3B\uFF0C\u8EAB\u540E\u4F0F\u9F99\u5802\u7CBE\u9510\u7EB7\u7EB7\u6740\u51FA\u3002</p><p>\u970D\u72C2\u7130\u7EA2\u888D\u4E00\u5C55\uFF0C\u4ECE\u8896\u4E2D\u638F\u51FA\u4E00\u5BF9\u706B\u7130\u72B6\u7684\u5947\u5F62\u5175\u5203\uFF0C\u4ED6\u7684\u706B\u7130\u4EE4\u662F\u6B66\u6797\u4E00\u7EDD\uFF0C\u53EF\u523A\u53EF\u52FE\uFF0C\u780D\u3001\u5207\u3001\u5288\u3001\u524A\u6837\u6837\u4FF1\u5168\uFF0C\u751A\u81F3\u53EF\u4EE5\u5957\u9501\u5BF9\u65B9\u5175\u5203\u3002</p><p>\u6155\u5BB9\u536B\u957F\u5200\u659C\u62B1\uFF0C\u5F85\u4ED6\u6C14\u52BF\u6500\u81F3\u5DC5\u5CF0\u65F6\u624D\u4E00\u5200\u5288\u4E0B\u3002\u970D\u72C2\u7130\u5DE6\u624B\u5C01\u683C\uFF0C\u53F3\u624B\u706B\u7130\u4EE4\u524D\u4F38\uFF0C\u76F4\u63D2\u6155\u5BB9\u536B\u7684\u80F8\u53E3\u3002</p><p>\u300C\u94DB\u300D\u7684\u4E00\u58F0\u5DE8\u54CD\uFF0C\u970D\u72C2\u7130\u53F3\u624B\u521A\u521A\u6325\u51FA\uFF0C\u5C31\u88AB\u6155\u5BB9\u536B\u4E00\u5200\u5288\u5F97\u5012\u98DE\u56DE\u53BB\u3002</p><p>\u6155\u5BB9\u536B\u4E0E\u970D\u72C2\u7130\u786C\u78B0\u786C\u62FC\u4E86\u4E00\u62DB\uFF0C\u5FC3\u4E0B\u5927\u5B9A\uFF0C\u9762\u524D\u8FD9\u4E2A\u706B\u5802\u957F\u8001\u529F\u529B\u8FD8\u4E0D\u53CA\u5F53\u5E74\u7684\u6C90\u58F0\u4F20\uFF0C\u5982\u679C\u5355\u6253\u72EC\u6597\u4E0D\u51FA\u4E09\u767E\u62DB\u5C31\u80FD\u8981\u4ED6\u7684\u5C0F\u547D\u3002</p><p>\u5C60\u6000\u6C89\u98DE\u8EAB\u63A0\u4E0B\uFF0C\u52A0\u5165\u6218\u56E2\u3002\u970D\u72C2\u7130\u6012\u543C\u8FDE\u58F0\uFF0C\u50CF\u56E2\u6012\u5F20\u7684\u70C8\u706B\u56F4\u7740\u6155\u5BB9\u536B\u72C2\u51FB\u731B\u649E\u3002\u5C60\u6000\u6C89\u5374\u9ED8\u4E0D\u4F5C\u58F0\uFF0C\u4ED6\u4F53\u5F62\u77EE\u80D6\uFF0C\u8EAB\u6CD5\u5374\u7075\u5982\u72F8\u732B\uFF0C\u4E0E\u970D\u72C2\u7130\u7684\u521A\u731B\u6070\u6210\u4E00\u5BF9\u3002\u7834\u5C71\u9525\u4E0E\u957F\u5200\u4EA4\u4E86\u4E00\u62DB\uFF0C\u5C60\u6000\u6C89\u8138\u4E0A\u7684\u559C\u8272\u987F\u65F6\u4E00\u626B\u800C\u7A7A\uFF0C\u4ED6\u6CA1\u60F3\u5230\u8FD9\u4E2A\u540D\u58F0\u5E76\u4E0D\u54CD\u4EAE\u7684\u6155\u5BB9\u536B\u529F\u529B\u5982\u6B64\u4E4B\u9AD8\uFF0C\u8F83\u4E4B\u6731\u90AA\u62A4\u6CD5\u4E5F\u5F31\u4E0D\u4E86\u591A\u5C11\u3002</p><p>\u518D\u8FC7\u6570\u62DB\uFF0C\u4ED6\u80D6\u8138\u4E00\u98A4\uFF0C\u5931\u58F0\u53EB\u9053\uFF1A\u300C\u6DF7\u5143\u6C14\uFF01\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u95FB\u58F0\u4E0D\u7531\u82B3\u5FC3\u5FAE\u9707\u3002\u7236\u4EB2\u4ECE\u672A\u4F20\u8FC7\u5979\u4EEC\u5144\u59B9\u6B66\u529F\uFF0C\u54E5\u54E5\u6155\u5BB9\u80DC\u4E5F\u662F\u827A\u51FA\u65C1\u95E8\uFF0C\u4ECA\u65E5\u89C1\u7239\u7239\u7684\u6B66\u529F\u5982\u6B64\u4E4B\u9AD8\u5DF2\u662F\u5927\u51FA\u610F\u5916\uFF0C\u73B0\u5728\u53C8\u542C\u8BF4\u7236\u4EB2\u7EC3\u7684\u662F\u6DF7\u5143\u6C14\u66F4\u662F\u5927\u60D1\u4E0D\u89E3\u3002\u5979\u66FE\u542C\u5E08\u7236\u8BF4\u8FC7\u6DF7\u5143\u6C14\u5A01\u529B\u60CA\u4EBA\uFF0C\u4F46\u7EC3\u8FD9\u95E8\u5185\u529F\u5FC5\u5F97\u7AE5\u7537\u4E4B\u8EAB\uFF0C\u53EF\u7236\u4EB2\u5374\u662F\u5A36\u59BB\u751F\u5B50\u2026\u2026</p><p>\u4F0F\u9F99\u5802\u4F17\u536B\u4E0D\u662F\u661F\u6708\u6E56\u5E2E\u4F17\u7684\u5BF9\u624B\uFF0C\u4E0D\u591A\u65F6\u4FBF\u6B7B\u4F24\u7D2F\u7D2F\u3002\u571F\u5802\u5DE8\u77F3\u3001\u8F7B\u5C18\u4E24\u540D\u9999\u4E3B\u89C1\u5DF1\u65B9\u5DF2\u7A33\u64CD\u80DC\u5238\uFF0C\u7ACB\u523B\u8F6C\u8EAB\u4E0E\u70C8\u7130\u3001\u731B\u70BD\u4E24\u4EBA\u5408\u653B\u7D2B\u73AB\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u72EC\u6597\u4E24\u4EBA\u8FD8\u6709\u4E9B\u5403\u529B\uFF0C\u89C1\u72B6\u7ACB\u523B\u957F\u5251\u4E00\u7FFB\u523A\u5411\u5DE8\u77F3\u9999\u4E3B\uFF0C\u5DE8\u77F3\u957F\u76FE\u626C\u8D77\uFF0C\u539A\u80CC\u5200\u4ECE\u76FE\u4E0B\u7A7F\u51FA\uFF0C\u75BE\u5288\u7D2B\u73AB\u8170\u95F4\u3002</p><p>\u957F\u5251\u5728\u76FE\u4E0A\u8F7B\u8F7B\u4E00\u70B9\uFF0C\u7D2B\u73AB\u501F\u529B\u98D8\u8EAB\u800C\u8D77\uFF0C\u4E24\u81C2\u4F38\u5C55\uFF0C\u7EA2\u8863\u98D8\u98DE\uFF0C\u5B9B\u5982\u7EA2\u4E91\u98D8\u821E\u822C\u659C\u659C\u63A0\u4E0A\u5802\u524D\u7684\u77F3\u5C4F\u3002\u5979\u7684\u51E4\u51F0\u5B9D\u5178\u53EA\u7EC3\u81F3\u7B2C\u56DB\u5C42\uFF0C\u529F\u529B\u4E0D\u8DB3\uFF0C\u4F46\u8F7B\u529F\u5374\u662F\u8D85\u4E4E\u5BFB\u5E38\u3002\u77F3\u5C4F\u672C\u662F\u7EDD\u5730\uFF0C\u6B64\u65F6\u5BF9\u4E8E\u957F\u4E8E\u8F7B\u529F\u7684\u6155\u5BB9\u7D2B\u73AB\u6765\u8BF4\uFF0C\u65E2\u514D\u88AB\u4F17\u4EBA\u56F4\u653B\uFF0C\u53C8\u53EF\u968F\u65F6\u63A0\u5411\u56DB\u5904\u6A90\u5899\u89D2\u697C\uFF0C\u7EDD\u5730\u53CD\u800C\u6210\u4E86\u8FDB\u53EF\u653B\u9000\u53EF\u5B88\u7684\u5B9D\u5730\u3002</p><p>\u6155\u5BB9\u536B\u4E00\u8FDE\u4E09\u5200\uFF0C\u5C06\u5C60\u6000\u6C89\u5288\u5F97\u8FDE\u8FDE\u5012\u9000\uFF0C\u63A5\u7740\u56DE\u624B\u5728\u8EAB\u540E\u5212\u4E86\u4E00\u4E2A\u534A\u5706\uFF0C\u8FEB\u5F00\u970D\u72C2\u7130\uFF0C\u7834\u4E86\u4E24\u4EBA\u7684\u5408\u51FB\u3002\u970D\u72C2\u7130\u8138\u8272\u8840\u7EA2\uFF0C\u6012\u76EE\u5706\u77AA\uFF0C\u888D\u8896\u5145\u6EE1\u4E86\u98CE\u822C\u9F13\u8D77\u3002</p><p>\u6155\u5BB9\u536B\u77E5\u9053\u4E94\u884C\u706B\u5802\u64C5\u4F7F\u96F7\u706B\uFF0C\u8EAB\u5F62\u5FAE\u6643\uFF0C\u5DF2\u7ECF\u7ED5\u5230\u5C60\u6000\u6C89\u8EAB\u540E\uFF0C\u597D\u8BA9\u970D\u72C2\u7130\u6295\u9F20\u5FCC\u5668\uFF0C\u540C\u65F6\u957F\u5200\u75BE\u51FA\uFF0C\u76F4\u53D6\u5C60\u957F\u8001\u8170\u95F4\u3002\u5C60\u6000\u6C89\u626D\u8EAB\u7528\u7834\u5C71\u9525\u6321\u4F4F\u5200\u950B\uFF0C\u4F46\u88AB\u8FD9\u52BF\u5927\u529B\u731B\u7684\u4E00\u62DB\u5288\u7684\u5750\u5230\u5730\u4E0A\u3002\u6155\u5BB9\u536B\u5F97\u52BF\u4E0D\u9976\u4EBA\uFF0C\u4F53\u5185\u771F\u6C14\u4E00\u8F6C\uFF0C\u5408\u8EAB\u671D\u5C60\u6000\u6C89\u5934\u4E0A\u5288\u53BB\u3002\u5C60\u6000\u6C89\u52C9\u5F3A\u63D0\u8D77\u7834\u5C71\u9525\u6321\u5728\u9762\u524D\uFF0C\u770B\u6765\u4E07\u96BE\u62B5\u6321\u3002</p><p>\u661F\u6708\u6E56\u8BF8\u4EBA\u5FC3\u6068\u624B\u8FA3\uFF0C\u970D\u72C2\u7130\u5F88\u53EF\u80FD\u4F1A\u4E0D\u987E\u5C60\u6000\u6C89\u751F\u6B7B\u608D\u7136\u4F7F\u51FA\u96F7\u706B\uFF0C\u6155\u5BB9\u536B\u957F\u5200\u5288\u51FA\uFF0C\u540C\u65F6\u7559\u4E86\u4E09\u5206\u529B\u6C14\u6212\u5907\u8EAB\u540E\u3002</p><p>\u5750\u4EE5\u5F85\u6BD9\u7684\u5C60\u6000\u6C89\u5FFD\u7136\u5927\u5634\u4E00\u5F20\uFF0C\u4E00\u4E1B\u9EC4\u6C99\u7BAD\u77E2\u822C\u5288\u5934\u76D6\u8138\u671D\u6155\u5BB9\u536B\u5C04\u53BB\u3002</p><p>\u8FD9\u6837\u5927\u9762\u79EF\u7684\u7EC6\u5C0F\u6697\u5668\u6839\u672C\u65E0\u6CD5\u62B5\u6321\uFF0C\u6155\u5BB9\u536B\u8FDE\u5FD9\u95ED\u76EE\u8FD0\u529F\u786C\u751F\u751F\u7684\u53D7\u6B64\u4E00\u51FB\uFF0C\u624B\u4E2D\u957F\u5200\u52A0\u901F\u843D\u4E0B\u3002</p><p>\u300C\u5F53\u300D\u7684\u4E00\u58F0\u5DE8\u54CD\uFF0C\u957F\u5200\u91CD\u91CD\u5288\u5728\u7834\u5C71\u9525\u4E0A\uFF0C\u5C06\u94A2\u9525\u751F\u751F\u7838\u5165\u5C60\u6000\u6C89\u80F8\u53E3\uFF0C\u987F\u65F6\u7838\u65AD\u4E86\u4E09\u6839\u808B\u9AA8\u3002</p><p>\u4F46\u8FD9\u62DB\u542B\u6C99\u5C04\u5F71\u4E43\u662F\u5C60\u6000\u6C89\u62A4\u8EAB\u7EDD\u6280\uFF0C\u5F53\u65E5\u5434\u9707\u5C31\u662F\u88AB\u4ED6\u5982\u6B64\u4E00\u62DB\u51FB\u6740\uFF0C\u9762\u5BB9\u5C3D\u6BC1\u3002\u6155\u5BB9\u536B\u867D\u7136\u5185\u529F\u7CBE\u7EAF\uFF0C\u8138\u4E0A\u4E5F\u4E0D\u514D\u8840\u82B1\u56DB\u6E85\uFF0C\u4E24\u773C\u987F\u65F6\u76F2\u4E86\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u4ECE\u77F3\u5C4F\u4E0A\u98DE\u8EAB\u800C\u8D77\uFF0C\u957F\u5251\u76F4\u523A\u970D\u72C2\u7130\u7709\u95F4\u3002\u970D\u72C2\u7130\u4E24\u624B\u4E00\u4E3E\uFF0C\u706B\u7130\u4EE4\u6321\u4F4F\u957F\u5251\uFF0C\u987A\u52BF\u4E00\u7EDE\u9501\u4F4F\u5251\u8EAB\u3002</p><p>\u5175\u5203\u4EA4\u51FB\uFF0C\u7D2B\u73AB\u957F\u5251\u51E0\u4E4E\u8131\u624B\uFF0C\u5979\u5FC3\u795E\u4E0D\u4E71\uFF0C\u53F3\u624B\u5728\u5251\u67C4\u540E\u4E00\u63A8\uFF0C\u540C\u65F6\u5DE6\u624B\u633D\u8D77\u5C0F\u5F29\uFF0C\u4E24\u9053\u91D1\u5149\u4E00\u4E0A\u4E00\u4E0B\u5206\u5C04\u970D\u72C2\u7130\u80F8\u8179\u3002</p><p>\u5F85\u970D\u72C2\u7130\uFF08~\u6587\uFF09\u4FA7\u8EAB\u95EA\u907F\uFF0C\u7D2B\u73AB\u85C9\u673A\uFF08~\u4EBA\uFF09\u6276\u8D77\u7236\u4EB2\uFF0C\u53F3\u8DB3\u4E00\u70B9\uFF08~\u4E66\uFF09\u63A0\u4E0A\u77F3\u5C4F\uFF0C\u63A5\u7740\u6BEB\u4E0D\uFF08~\u5C4B\uFF09\u505C\u987F\u7684\u8DC3\u5411\u9AD8\u5899\u3002\u5B88\u5728\u5899\u4E0A\u7684\u4E24\u540D\u7EA2\u8863\u6C49\u5B50\u4E3E\u5200\u780D\u5288\uFF0C\u53EA\u542C\u300C\u53EE\u53EE\u300D\u4E24\u58F0\u8F7B\u54CD\uFF0C\u4E24\u4EBA\u94A2\u5200\u622A\u65AD\uFF0C\u7FFB\u8EAB\u6389\u4E0B\u9AD8\u5899\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u7528\u5E08\u7236\u6240\u4F20\u7684\u62A4\u8EAB\u5B9D\u5200\u300C\u7247\u7389\u300D\u51FB\u6740\u4E24\u4EBA\uFF0C\u62A2\u4E0A\u5899\u5934\u3002\u6B63\u5F85\u8DC3\u4E0B\uFF0C\u7A81\u7136\u8033\u65C1\u4E00\u58F0\u5DE8\u54CD\uFF0C\u7236\u4EB2\u80CC\u4E0A\u95EA\u8D77\u4E00\u56E2\u706B\u5149\u3002</p><p>\u970D\u72C2\u7130\u63B7\u51FA\u7834\u7A7A\u96F7\uFF0C\u7ACB\u523B\u8DC3\u8D77\u3002\u5F85\u70DF\u96FE\u6563\u5C3D\u540E\uFF0C\u4ED6\u624D\u53D1\u73B0\u9053\u8DEF\u4E0A\u7A7A\u8361\u8361\u6CA1\u6709\u4E00\u4E2A\u4EBA\u5F71\u3002</p><p>\u5C60\u6000\u6C89\u8EAB\u8D1F\u91CD\u4F24\uFF0C\u6155\u5BB9\u7D2B\u73AB\u53C8\u4ECE\u81EA\u5DF1\u624B\u91CC\u98DE\u4E86\u51FA\u53BB\uFF0C\u5BAB\u4E3B\u4F20\u4E0B\u7684\u547D\u4EE4\u4E00\u6837\u90FD\u6CA1\u5B8C\u6210\uFF0C\u970D\u72C2\u7130\u4E0D\u7531\u5FC3\u5934\u53D1\u6025\u3002\u4ED6\u4E24\u4E2A\u6708\u524D\u4E0E\u5C60\u6000\u6C89\u521A\u521A\u88AB\u63D0\u5347\u4E3A\u957F\u8001\uFF0C\u6B63\u662F\u52AA\u529B\u62A5\u6548\u5BAB\u4E3B\u77E5\u9047\u4E4B\u6069\u7684\u65F6\u5019\uFF0C\u5374\u95F9\u4E86\u4E2A\u4E24\u624B\u7A7A\u7A7A\uFF0C\u60F3\u5230\u5BAB\u4E3B\u7684\u624B\u6BB5\uFF0C\u970D\u72C2\u7130\u51B7\u6C57\u90FD\u51FA\u6765\u4E86\uFF0C\u4E00\u9762\u53D1\u52A8\u4EBA\u624B\u8FFD\u6355\uFF0C\u4E00\u9762\u628A\u4F0F\u9F99\u5802\u7FFB\u4E86\u4E2A\u5E95\u671D\u5929\uFF0C\u641C\u5BFB\u5B9D\u85CF\u7684\u7EBF\u7D22\u3002</p>',51);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
