import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_04" tabindex="-1"><a class="header-anchor" href="#_04" aria-hidden="true">#</a> 04</h1><p>\u4F0F\u9F99\u5802\u5185\u5916\u4F0F\u5C38\u5904\u5904\uFF0C\u4E8C\u767E\u4F59\u8FD1\u536B\u5DF2\u5C3D\u6570\u88AB\u6B7C\u3002\u53EA\u5269\u5341\u51E0\u540D\u5E74\u8F7B\u5973\u5B50\u4F9B\u4F17\u4EBA\u6CC4\u6124\u3002</p><p>\u970D\u72C2\u7130\u5DF2\u7ECF\u8FDE\u7EED\u5978\u6B7B\u4E24\u540D\u5973\u5B50\u3002\u4ED6\u8EAB\u5177\u5F02\u529F\uFF0C\u4E00\u8FD0\u6C14\u9633\u5177\u7ACB\u5373\u70BD\u70ED\u5982\u706B\u3002\u88AB\u4ED6\u5978\u6DEB\u7684\u4E24\u540D\u5973\u5B50\u5C3D\u662F\u4E0B\u4F53\u7126\u9ED1\uFF0C\u5982\u906D\u706B\u711A\u3002\u4F59\u4E0B\u7684\u5973\u5B50\u770B\u5230\u59D0\u59B9\u9648\u5C38\u5802\u4E2D\u60E8\u8C61\u90FD\u5413\u7684\u5664\u82E5\u5BD2\u8749\u3002</p><p>\u8FD9\u4E9B\u5973\u5B50\u6839\u672C\u4E0D\u53EF\u80FD\u77E5\u9053\u5B9D\u85CF\u7684\u6D88\u606F\uFF0C\u64D2\u4E0B\u5979\u4EEC\u53EA\u4E3A\u5C60\u6740\u53D6\u4E50\u3002\u970D\u72C2\u7130\u957F\u5200\u4E00\u6325\uFF0C\u5C06\u4E00\u540D\u5973\u5B50\u62E6\u8170\u780D\u6210\u4E24\u622A\uFF0C\u7136\u540E\u8214\u4E86\u8214\u5200\u950B\u4E0A\u7684\u9C9C\u8840\uFF0C\u5634\u89D2\u626F\u51FA\u4E00\u4E2A\u72F0\u72DE\u7684\u7B11\u5BB9\u3002</p><p>\u4F17\u5973\u7EB7\u7EB7\u60CA\u53EB\u7740\u9003\u907F\uFF0C\u5374\u88AB\u56DB\u5468\u5E2E\u4F17\u5200\u67AA\u5E76\u4E3E\u9A71\u5165\u5802\u4E2D\u3002\u970D\u72C2\u7130\u72C2\u7B11\u7740\u5728\u88F8\u5973\u4E2D\u7A7F\u63D2\u5288\u524A\uFF0C\u5200\u950B\u5230\u5904\u767D\u5AE9\u7684\u808C\u80A4\u987F\u65F6\u8840\u5149\u4E4D\u73B0\uFF0C\u7C89\u817F\u7389\u81C2\u56DB\u4E0B\u7EB7\u98DE\uFF0C\u4E0D\u591A\u65F6\u4ED6\u5DF2\u662F\u6D51\u8EAB\u6D74\u8840\u3002\u970D\u72C2\u7130\u6740\u5F97\u6027\u8D77\uFF0C\u629B\u4E0B\u957F\u5200\uFF0C\u5355\u51ED\u8D64\u624B\u6495\u788E\u5973\u4F53\u3002\u770B\u5230\u8FD9\u8840\u8165\u7684\u4E00\u5E55\uFF0C\u5468\u56F4\u7684\u5E2E\u4F17\u5C3D\u662F\u76EE\u9732\u51F6\u5149\u3002</p><p>\u970D\u72C2\u7130\u53CD\u624B\u6293\u4F4F\u4E00\u53EA\u4E73\u623F\u5C06\u5B83\u634F\u5F97\u7C89\u788E\uFF0C\u7136\u540E\u8E29\u4F4F\u5973\u4EBA\u7684\u811A\u8E1D\uFF0C\u4F38\u624B\u63E1\u4F4F\u53E6\u4E00\u817F\u7684\u819D\u5F2F\u731B\u7136\u4E00\u626F\uFF0C\u628A\u90A3\u4E2A\u5973\u5B50\u4ECE\u4E24\u817F\u4E2D\u751F\u751F\u6495\u88C2\u3002</p><p>\u970D\u72C2\u7130\u62CE\u7740\u4E00\u53EA\u96EA\u767D\u7684\u5927\u817F\u7F13\u7F13\u8F6C\u8EAB\u3002\u4ED6\u770B\u4E0A\u53BB\u72B6\u5982\u75AF\u9B54\uFF0C\u5176\u5B9E\u5FC3\u91CC\u5FD0\u5FD1\u4E0D\u5B89\uFF1A\u6050\u6015\u8FD9\u662F\u6700\u540E\u4E00\u6B21\u5FEB\u6D3B\u4E86\uFF0C\u539F\u6765\u706B\u3001\u571F\u4E24\u5802\u957F\u8001\u90FD\u662F\u56E0\u4E3A\u5C0F\u4E8B\u89C1\u8BDB\uFF0C\u8FD9\u4E00\u6B21\u2026\u2026</p><p>\u5802\u4E2D\u53EA\u5269\u6700\u540E\u4E00\u540D\u5973\u5B50\uFF0C\u762B\u8F6F\u5728\u5730\uFF0C\u745F\u745F\u53D1\u6296\u3002\u970D\u72C2\u7130\u6162\u6162\u8D70\u4E86\u8FC7\u6765\uFF0C\u624B\u91CC\u7684\u5927\u817F\u62D6\u7740\u534A\u7247\u8EAB\u5B50\uFF0C\u90A3\u53EA\u5B8C\u597D\u7684\u4E73\u623F\u8FD8\u5728\u5FAE\u5FAE\u98A4\u52A8\u3002\u4ED6\u51B7\u7B11\u4E00\u58F0\uFF0C\u62AC\u811A\u5C06\u5973\u4EBA\u8E22\u4E86\u8D77\u6765\uFF0C\u6296\u624B\u63B7\u51FA\u4E00\u652F\u957F\u77DB\u3002\u957F\u77DB\u5728\u7A7A\u4E2D\u4E00\u95EA\uFF0C\u4ECE\u79D8\u5904\u8D2F\u4F53\u800C\u5165\uFF0C\u300C\u817E\u300D\u7684\u4E00\u58F0\u9489\u5728\u6A2A\u6881\u4E0A\u3002</p><p>\u96EA\u767D\u7684\u5973\u4F53\u4E00\u9635\u75C9\u631B\uFF0C\u518D\u4E5F\u4E0D\u52A8\u4E86\u3002\u9C9C\u8840\u987A\u7740\u9732\u5728\u9634\u5916\u7684\u67AA\u6746\u4E00\u8FDE\u4E32\u6EF4\u843D\u4E0B\u6765\u3002</p><p>\u5929\u9645\u54CD\u8D77\u4E00\u9635\u300C\u9686\u9686\u300D\u95F7\u54CD\uFF0C\u8FD9\u662F\u4ECA\u5E74\u7B2C\u4E00\u58F0\u6625\u96F7\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7236\u4EB2\u5BBD\u9614\u7684\u540E\u80CC\u4E00\u7247\u7126\u9ED1\uFF0C\u9C9C\u8840\u9732\u73E0\u822C\u6E17\u51FA\uFF0C\u6E10\u6E10\u8FDE\u5728\u4E00\u8D77\u3002\u6155\u5BB9\u7D2B\u73AB\u5FC3\u5934\u9178\u695A\uFF0C\u53EB\u4E86\u58F0\uFF1A\u300C\u7239\u7239\u3002\u300D\u773C\u6CEA\u6251\u6251\u7C0C\u7C0C\u843D\u4E86\u4E0B\u6765\u3002</p><p>\u6155\u5BB9\u536B\u80CC\u90E8\u88AB\u7834\u7A7A\u96F7\u70B8\u788E\uFF0C\u4F24\u5904\u6DF1\u53EF\u89C1\u9AA8\uFF0C\u5E78\u597D\u5185\u529F\u7CBE\u6E5B\uFF0C\u8FD8\u80FD\u52C9\u5F3A\u62A4\u4F4F\u5FC3\u8109\u3002\u4ED6\u5598\u4E86\u53E3\u6C14\uFF0C\u8BF4\u9053\uFF1A\u300C\u653E\u2026\u2026\u6211\u4E0B\u6765\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u6447\u4E86\u6447\u5934\uFF0C\u300C\u7B49\u5230\u4E86\u5C71\u4E0B\uFF0C\u6211\u4EEC\u53BB\u627E\u5927\u592B\u3002\u300D</p><p>\u300C\u6765\u4E0D\u53CA\u4E86\u2026\u2026\u5FEB\u4E9B\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u94F6\u7259\u4E00\u54AC\uFF0C\u817E\u8EAB\u800C\u8D77\uFF0C\u6CA1\u5165\u5C71\u6797\u3002</p><p>\u6155\u5BB9\u536B\u76D8\u819D\u5750\u4E0B\uFF0C\u95ED\u76EE\u8C03\u606F\u7247\u523B\uFF0C\u8BF4\u9053\uFF1A\u300C\u4ED6\u4EEC\u662F\u661F\u6708\u6E56\u5E2E\u4F17\u3002\u5341\u516D\u5E74\u524D\u9634\u5BAB\u4E3B\u7387\u4F17\u6765\u88AD\uFF0C\u6211\u62FC\u6B7B\u6551\u51FA\u4F60\u4EEC\u6BCD\u5973\uFF0C\u4F46\u5931\u843D\u4E86\u4F60\u54E5\u54E5\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u60CA\u9053\uFF1A\u300C\u6211\u54E5\u54E5\uFF1F\u300D</p><p>\u6155\u5BB9\u536B\u82E6\u7B11\u4E00\u4E0B\uFF0C\u6CA1\u6709\u56DE\u7B54\u5979\u7684\u7591\u95EE\uFF0C\u300C\u4F60\u53BB\u627E\u795E\u5C3C\uFF0C\u8BF7\u5979\u51FA\u624B\u76F8\u52A9\u3002\u300D\u4ED6\u5598\u4E86\u53E3\u6C14\uFF0C\u53D7\u4F24\u7684\u773C\u4E2D\u7A81\u7136\u6D8C\u51FA\u4E00\u4E32\u8840\u6CEA\uFF0C\u300C\u4F60\u6BCD\u4EB2\u88AB\u63B3\u5165\u661F\u6708\u6E56\uFF0C\u4E00\u65F6\u4E0D\u4F1A\u4FBF\u6B7B\u2026\u2026\u627E\u5230\u6BCD\u4EB2\uFF0C\u5979\u4F1A\u544A\u8BC9\u4F60\u6240\u6709\u7684\u4E8B\uFF0C\u5C0F\u5FC3\u661F\u6708\u5BAB\u4E3B\uFF0C\u90A3\u5996\u5987\u884C\u4E8B\u5FC3\u72E0\u624B\u8FA3\uFF0C\u624B\u4E0B\u80FD\u4EBA\u5F02\u58EB\u6781\u591A\uFF0C\u5355\u662F\u4E94\u957F\u8001\u2026\u2026\u4FBF\u4E0D\u6613\u5BF9\u4ED8\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u867D\u7136\u6EE1\u8179\u7591\u95EE\uFF0C\u4F46\u4E0D\u6562\u6253\u65AD\u7236\u4EB2\u7684\u8BDD\u3002\u5979\u5C4F\u4F4F\u547C\u5438\uFF0C\u628A\u4E00\u5B57\u4E00\u53E5\u90FD\u8BB0\u5728\u5FC3\u4E2D\u3002</p><p>\u300C\u5BF9\u4F60\u6BCD\u4EB2\u8BF4\uFF0C\u6155\u5BB9\u536B\u65E0\u80FD\uFF0C\u867D\u6B7B\u6709\u6127\u2026\u2026\u300D</p><p>\u300C\u7239\uFF01\u300D</p><p>\u6155\u5BB9\u536B\u7AED\u529B\u54BD\u4E86\u53E3\u6C14\uFF0C\u4F38\u6307\u5728\u5730\u753B\u4E86\u4E00\u4E2A\u4F3C\u82B1\u4F3C\u4E91\u7684\u56FE\u5F62\uFF0C\u518D\u5F00\u53E3\u65F6\u58F0\u97F3\u7A81\u7136\u53D8\u5F97\u5C16\u7EC6\uFF1A\u300C\u5B9D\u5E93\u2026\u2026\u5728\u2026\u2026\u7EC8\u5357\u2026\u2026\u5F2F\u5C9B\u2026\u2026\u5929\u5730\u541B\u4EB2\u5E08\u2026\u2026\u8D3E\u94F6\u601D\u2026\u2026\u4E01\u8D35\u2026\u2026\u5FE0\u2026\u2026\u300D\u8BDD\u97F3\u672A\u843D\uFF0C\u8EAB\u5B50\u5DF2\u7ECF\u50F5\u786C\u3002</p><p>\u9A6C\u8E44\u58F0\u4ECE\u8FDC\u5904\u4F20\u6765\uFF0C\u6155\u5BB9\u7D2B\u73AB\u62B9\u5E72\u6CEA\u6C34\uFF0C\u671D\u7236\u4EB2\u7684\u5C38\u8EAB\u78D5\u4E86\u4E00\u4E2A\u5934\u3002\u7EA2\u8863\u4E00\u5C55\uFF0C\u8F7B\u4E91\u822C\u5411\u5C71\u4E0B\u63A0\u53BB\u3002</p><p>\u96F7\u58F0\u9690\u9690\u54CD\u8D77\uFF0C\u63A5\u7740\u6625\u96E8\u6D12\u843D\uFF0C\u4F3C\u4E4E\u8981\u6D17\u53BB\u8FD9\u4E00\u8DEF\u4E0A\u7684\u9C9C\u8840\u548C\u6CEA\u6C34\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u83AB\u540D\u5176\u5999\u7684\u88AB\u4EBA\u65BD\u4EE5\u6DEB\u5211\uFF0C\u5E26\u5230\u964C\u751F\u7684\u6E56\u8FB9\uFF0C\u767E\u82B1\u89C2\u97F3\u65E9\u5DF2\u8FDE\u65E5\u7684\u6DEB\u8650\u6298\u78E8\u5F97\u6728\u7136\uFF0C\u53EA\u6014\u6014\u770B\u7740\u78A7\u84DD\u7684\u6E56\u6C34\u3002</p><p>\u6E56\u6C34\u4E2D\u6620\u51FA\u4E00\u5F20\u7AEF\u5E84\u7F8E\u4E3D\u7684\u8138\u5E9E\uFF0C\u5723\u6D01\u4E2D\u5E26\u7740\u51C4\u5A49\u7684\u82E6\u96BE\u3002</p><p>\u4F46\u5728\u821F\u5B50\u773C\u91CC\u53EF\u6CA1\u6709\u4EC0\u4E48\u5723\u6D01\u3002\u4ED6\u7684\u773C\u73E0\u5B50\u5728\u8FD9\u4E2A\u8D64\u88F8\u7684\u7F8E\u5987\u8EAB\u4E0A\u6EF4\u6E9C\u6E9C\u4E71\u8F6C\uFF0C\u5FC3\u91CC\u76F4\u53D1\u75D2\u3002\u5C60\u957F\u8001\u8FD9\u6B21\u63B3\u6765\u7684\u5973\u4EBA\u53EF\u771F\u662F\u7EDD\u8272\uFF0C\u6309\u5BAB\u91CC\u7684\u89C4\u77E9\uFF0C\u7528\u4E0D\u4E86\u51E0\u5929\u5C31\u80FD\u8F6E\u5230\u81EA\u5DF1\u4E86\u3002\u77A7\u8FD9\u8EAB\u7EC6\u76AE\u5AE9\u8089\uFF0C\u64CD\u8D77\u6765\u80AF\u5B9A\u8212\u670D\u5F97\u8981\u6B7B\u2026\u2026</p><p>\u6E56\u4E2D\u662F\u4E00\u4E2A\u6708\u7259\u5F62\u7684\u5C9B\u5C7F\uFF0C\u6708\u7259\u4E2D\u95F4\u7684\u78A7\u6CE2\u4E2D\u5D4C\u7740\u4E00\u5757\u5DE8\u7901\uFF0C\u4E0A\u9762\u6811\u7740\u4E00\u6839\u9AD8\u5927\u7684\u65D7\u6746\uFF0C\u5374\u672A\u6302\u65D7\u53F7\uFF0C\u4E24\u8005\u9065\u9065\u76F8\u5BF9\uFF0C\u5B9B\u5982\u661F\u6708\u3002</p><p>\u5CB8\u4E0A\u4E24\u540D\u7D2B\u8863\u4EBA\u4E0A\u8239\u6276\u4E0B\u767E\u82B1\u89C2\u97F3\uFF0C\u5C9B\u4E0A\u7684\u9053\u8DEF\u7686\u7531\u9752\u77F3\u94FA\u5C31\uFF0C\u6574\u6D01\u5F02\u5E38\u3002</p><p>\u5C9B\u5C7F\u6B63\u4E2D\u662F\u4E00\u5EA7\u602A\u77F3\u5D99\u5CCB\u7684\u5C71\u5CF0\uFF0C\u4E00\u5EA7\u5DCD\u5CE8\u7684\u5927\u6BBF\u501A\u5C71\u800C\u5EFA\uFF0C\u6C14\u52BF\u96C4\u4F1F\u3002</p><p>\u6BBF\u5185\u5E7D\u6697\u51B0\u51B7\uFF0C\u5373\u4F7F\u662F\u767D\u5929\u8FD8\u70B9\u7740\u706B\u70AC\u7167\u660E\u3002\u706B\u5149\u6447\u66F3\u4E2D\uFF0C\u5DE8\u67F1\u4E0A\u7684\u76D8\u9F99\u50CF\u662F\u6D3B\u7269\u822C\u9690\u9690\u800C\u52A8\u3002</p><p>\u4E00\u4E2A\u9762\u8272\u82CD\u767D\u7684\u7537\u5B50\u9762\u65E0\u8868\u60C5\u5730\u5750\u5728\u5927\u6BBF\u4E4B\u4E0A\uFF0C\u5EA7\u4E0B\u7684\u5B9D\u6905\u9576\u91D1\u5D4C\u7389\uFF0C\u534E\u4E3D\u6BD4\u65E0\uFF0C\u8EAB\u540E\u6811\u7740\u4E00\u6247\u9AD8\u5927\u7684\u7389\u5236\u5C4F\u98CE\u3002</p><p>\u968F\u884C\u7684\u7D2B\u8863\u4EBA\u8DEA\u4E0B\u6717\u58F0\u9053\uFF1A\u300C\u7980\u5BAB\u4E3B\uFF0C\u767E\u82B1\u89C2\u97F3\u5DF2\u7ECF\u5949\u547D\u62BC\u5230\u3002\u300D</p><p>\u5BAB\u4E3B\u6446\u4E86\u6446\u624B\u3002\u4F17\u4EBA\u7ACB\u523B\u9000\u51FA\u5927\u6BBF\uFF0C\u63A9\u4E0A\u6BBF\u95E8\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u7F9E\u6DA9\u5730\u63A9\u4F4F\u80F8\u4E73\uFF0C\u51C4\u58F0\u9053\uFF1A\u300C\u4F60\u6211\u7D20\u4E0D\u76F8\u8BC6\uFF0C\u4E3A\u4F55\u8981\u8FD9\u6837\u5BF9\u6211\uFF01\u300D</p><p>\u51C4\u5A49\u7684\u58F0\u97F3\u5728\u6BBF\u4E2D\u9690\u9690\u56DE\u54CD\u3002</p><p>\u90A3\u5BAB\u4E3B\u8138\u8272\u82CD\u767D\uFF0C\u9AD8\u633A\u7684\u9F3B\u6881\u663E\u51FA\u4ED6\u80E1\u4EBA\u7684\u8840\u7EDF\u3002\u95FB\u8A00\u6DE1\u6DE1\u9053\uFF1A\u300C\u4F60\u662F\u8427\u4F5B\u5974\uFF1F\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u4E00\u6123\uFF0C\u5979\u8FD8\u62B1\u7740\u4E00\u7EBF\u5E0C\u671B\uFF0C\u4EE5\u4E3A\u4ED6\u4EEC\u662F\u63B3\u9519\u4E86\u4EBA\uFF0C\u6B64\u523B\u5F97\u77E5\u5BF9\u65B9\u7684\u76EE\u6807\u6B63\u662F\u81EA\u5DF1\uFF0C\u5FC3\u5E95\u4E0D\u7531\u5347\u8D77\u4E00\u80A1\u5BD2\u610F\uFF0C\u98A4\u6296\u7740\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u300C\u4F60\u77E5\u7F6A\u5417\uFF1F\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u6014\u6014\u6447\u4E86\u6447\u5934\u3002</p><p>\u90A3\u7537\u5B50\u8138\u8272\u9634\u51B7\uFF0C\u4E24\u773C\u5E7D\u5E7D\u770B\u7740\u5979\uFF0C\u4EFF\u4F5B\u6EE1\u8154\u6068\u610F\u3002\u826F\u4E45\uFF0C\u4ED6\u7AD9\u8D77\u8EAB\u6765\uFF0C\u7F13\u7F13\u8D70\u5230\u767E\u82B1\u89C2\u97F3\u8EAB\u8FB9\uFF0C\u6258\u8D77\u5979\u7684\u4E0B\u5DF4\uFF0C\u4ED4\u7EC6\u5BA1\u89C6\u3002</p><p>\u8427\u4F5B\u5974\u53C8\u7F9E\u53C8\u6025\uFF0C\u626D\u5934\u907F\u5F00\uFF0C\u300C\u4F60\u7A76\u7ADF\u8981\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u90A3\u7537\u5B50\u4E0B\u988C\u4E00\u6536\u9F13\u8D77\u4E24\u9053\u808C\u8089\uFF0C\u663E\u7136\u662F\u54AC\u7D27\u7259\u5173\u3002\u300C\u4F60\u77E5\u7F6A\u5417\uFF1F\u300D</p><p>\u300C\u2026\u2026\u4E0D\u77E5\u9053\u3002\u300D</p><p>\u300C\u556A\u300D\uFF0C\u5BAB\u4E3B\u4E00\u638C\u6247\u5728\u767E\u82B1\u89C2\u97F3\u5A07\u7F8E\u7684\u7389\u8138\u4E0A\uFF0C\u7559\u4E0B\u4E94\u9053\u9C9C\u7EA2\u7684\u6307\u75D5\u3002</p><p>\u8427\u4F5B\u5974\u5012\u5728\u5730\u4E0A\uFF0C\u60CA\u6050\u5730\u6342\u7740\u8138\u86CB\uFF0C\u5413\u5F97\u4E0D\u6562\u4F5C\u58F0\u3002</p><p>\u5BAB\u4E3B\u624B\u6307\u5FAE\u5FAE\u53D1\u98A4\uFF0C\u66B4\u559D\u9053\uFF1A\u300C\u6765\u4EBA\uFF01\u300D</p><p>\u6BBF\u89D2\u95EA\u51FA\u4E24\u4E2A\u7D2B\u8863\u4EBA\uFF0C\u5782\u624B\u542C\u4EE4\u3002</p><p>\u5BAB\u4E3B\u6307\u7740\u90A3\u4E2A\u6CBE\u7740\u767E\u82B1\u89C2\u97F3\u8840\u8FF9\u7684\u77F3\u978D\u9053\uFF1A\u300C\u628A\u8FD9\u8D31\u4EBA\u67B6\u4E0A\u53BB\uFF01\u4E0D\u8BB8\u505C\uFF01\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u60B2\u547C\u4E00\u58F0\uFF0C\u731B\u7136\u671D\u91D1\u9F99\u76D8\u67F1\u649E\u53BB\uFF0C\u5982\u679C\u4E00\u76F4\u8FD9\u6837\u88AB\u4EBA\u6DEB\u8FB1\uFF0C\u771F\u4E0D\u5982\u6B7B\u4E86\u5E72\u51C0\u3002</p><p>\u5BAB\u4E3B\u624B\u6307\u4E00\u5F39\uFF0C\u9694\u7A7A\u5C01\u4E86\u5979\u7684\u7A74\u9053\u3002\u7F13\u7F13\u8BF4\uFF1A\u300C\u6DEB\u5987\u6709\u6728\u9A74\u4E4B\u5211\uFF0C\u8FD9\u77F3\u9A74\u662F\u6211\u7279\u610F\u547D\u4EBA\u6253\u5236\uFF0C\u5C31\u662F\u4E3A\u4E86\u60E9\u7F5A\u4F60\u8FD9\u4E2A\u4E0B\u8D31\u65E0\u803B\u7684\u6DEB\u5987\uFF01\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u5982\u95FB\u6674\u5929\u9739\u96F3\uFF0C\u81EA\u5DF1\u5E73\u751F\u8D1E\u6D01\u65E0\u4E8F\uFF0C\u600E\u4E48\u4F1A\u88AB\u4EBA\u79F0\u4E4B\u4E3A\u6DEB\u5987\uFF0C\u66F4\u8981\u53D7\u6B64\u803B\u5211\uFF1F\u6551\u82E6\u6551\u96BE\u7684\u89C2\u4E16\u97F3\u83E9\u8428\uFF0C\u8FD9\u7A76\u7ADF\u662F\u600E\u4E48\u56DE\u4E8B\uFF1F\u8FD9\u4EBA\u80AF\u5B9A\u662F\u5F04\u9519\u4E86\uFF01\u5979\u6709\u5FC3\u7533\u8FA9\uFF0C\u4F46\u5BAB\u4E3B\u4E3A\u4E86\u9632\u6B62\u54AC\u820C\u81EA\u5C3D\uFF0C\u4E00\u5E76\u5C01\u4E86\u5979\u7684\u54D1\u7A74\uFF0C\u56E0\u6B64\u867D\u7136\u6709\u6EE1\u8179\u7684\u59D4\u5C48\uFF0C\u5374\u4E00\u5B57\u90FD\u8BF4\u4E0D\u51FA\u6765\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u5FC3\u714E\u5982\u6CB8\uFF0C\u67D4\u9888\u4E00\u4FA7\u660F\u4E86\u8FC7\u53BB\u3002\u7D2B\u8863\u4EBA\u6258\u7740\u5979\u7684\u8170\u8EAB\uFF0C\u9762\u65E0\u8868\u60C5\u7EE7\u7EED\u63A8\u7740\u660F\u8FF7\u7684\u7F8E\u5987\u7ED5\u6BBF\u800C\u884C\u3002</p><p>\u75DB\u6068\u591A\u5E74\u7684\u5973\u4EBA\u7EC8\u4E8E\u843D\u5230\u81EA\u5DF1\u624B\u4E2D\uFF0C\u90A3\u5BAB\u4E3B\u6EE1\u5FC3\u5FEB\u610F\uFF0C\u4E0D\u7531\u4EF0\u5929\u957F\u7B11\uFF0C\u58F0\u9707\u6BBF\u5B87\u3002</p><p>\u6BBF\u5185\u8F98\u8F98\u4E4B\u58F0\u4E0D\u7EDD\u4E8E\u8033\uFF0C\u4F18\u7F8E\u7684\u8EAB\u4F53\u5728\u77F3\u978D\u4E0A\u524D\u4EF0\u540E\u5408\uFF0C\u79C0\u53D1\u98DE\u626C\u3002\u6BDB\u8338\u8338\u7684\u8C82\u88D8\u4E2D\u90A3\u5F20\u7CBE\u81F4\u7684\u7389\u5BB9\u795E\u60C5\u60E8\u6DE1\uFF0C\u6BB7\u7EA2\u7684\u4E73\u5934\u5728\u767D\u5AE9\u7684\u4E73\u7403\u4E0A\u4E0D\u4F4F\u8DF3\u52A8\uFF0C\u5728\u706B\u5149\u4E2D\u5212\u51FA\u9053\u9053\u8BF1\u4EBA\u7684\u7EA2\u5F71\u3002</p><p>\u5A40\u5A1C\u7684\u8170\u80A2\u4E00\u70B9\u90FD\u4E0D\u50CF\u662F\u751F\u8FC7\u4E24\u4E2A\u5B69\u5B50\u7684\u5987\u4EBA\uFF0C\u4ECD\u662F\u73B2\u73D1\u6709\u81F4\u3002\u4FEE\u957F\u7684\u5927\u817F\u65E0\u529B\u7684\u4ECE\u9752\u9ED1\u7684\u77F3\u5934\u8FB9\u5782\u4E0B\uFF0C\u5149\u6DA6\u5982\u8102\u3002\u80A1\u95F4\u51FA\u4E00\u4E1B\u4E4C\u4EAE\u7684\u6BDB\u53D1\uFF0C\u968F\u7740\u77F3\u68D2\u7684\u6446\u52A8\uFF0C\u6BDB\u53D1\u4E0B\u7EA2\u8273\u67D4\u7F8E\u7684\u5AE9\u8089\u65F6\u9690\u65F6\u73B0\u3002</p><p>\u5BAB\u4E3B\u51B0\u51B7\u7684\u773C\u795E\u6D41\u9732\u51FA\u4E00\u4E1D\u4F24\u611F\uFF0C\u4ED6\u6B7B\u6B7B\u76EF\u7740\u90A3\u5177\u6210\u719F\u7F8E\u8273\u7684\u8089\u4F53\uFF0C\u773C\u4E2D\u6CDB\u8D77\u4E00\u5C42\u8840\u7EA2\uFF0C\u7A81\u7136\u8EAB\u5F62\u4E00\u95EA\uFF0C\u9B3C\u9B45\u822C\u6D88\u5931\u5728\u5C4F\u98CE\u4E4B\u540E\u3002</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
