import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_05" tabindex="-1"><a class="header-anchor" href="#_05" aria-hidden="true">#</a> 05</h1><p>\u6668\u66E6\u4E2D\u5347\u8D77\u51E0\u7F15\u708A\u70DF\uFF0C\u5927\u6CB3\u7389\u5E26\u822C\u7ED5\u8FC7\u4E00\u7247\u623F\u5C4B\u3002\u8FDE\u5E74\u7684\u6218\u4E71\uFF0C\u6C11\u95F4\u751F\u6D3B\u751A\u82E6\uFF0C\u4E34\u6CB3\u9547\u867D\u662F\u65B9\u5706\u767E\u91CC\u6700\u5927\u7684\u9547\u5B50\uFF0C\u4E5F\u6CA1\u6709\u591A\u5C11\u4EBA\u5BB6\u3002\u6B64\u5730\u662F\u9EC4\u6CB3\u4E0A\u6E38\uFF0C\u6570\u5341\u5E74\u524D\u8FD8\u662F\u5927\u7247\u519C\u7530\uFF0C\u5982\u4ECA\u585E\u5916\u8BF8\u65CF\u94C1\u9A91\u7EB7\u81F3\uFF0C\u996E\u9A6C\u9EC4\u6CB3\uFF0C\u5B9A\u5C45\u4E8E\u6B64\u7684\u6C49\u4EBA\u5341\u5BA4\u4E5D\u7A7A\uFF0C\u826F\u7530\u5DF2\u5C3D\u6210\u7267\u573A\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u7CBE\u75B2\u529B\u5C3D\u7684\u7F13\u6B65\u5165\u9547\uFF0C\u5979\u542C\u5434\u53D4\u8BF4\u8FC7\u6B64\u5730\u6709\u9A6C\u96C6\uFF0C\u51C6\u5907\u4E70\u9A6C\u4EE3\u6B65\uFF0C\u4E0D\u6599\u4E00\u591C\u6625\u96E8\uFF0C\u9547\u4E0A\u4E86\u65E0\u4EBA\u8FF9\u3002</p><p>\u7D2B\u73AB\u6014\u4E86\u4E00\u4F1A\u513F\uFF0C\u6B64\u53BB\u98D8\u6885\u5CF0\u6570\u5343\u91CC\u4E4B\u9065\uFF0C\u81EA\u5DF1\u5B64\u8EAB\u4E00\u4EBA\u53C8\u6CA1\u6709\u5728\u6C5F\u6E56\u884C\u8D70\u7684\u7ECF\u9A8C\uFF0C\u8FD9\u8BE5\u5982\u4F55\u662F\u597D\u2026\u2026\u4E09\u5E08\u59D0\u6240\u5C45\u7684\u6D1B\u9633\u4E0E\u4E8C\u5E08\u59D0\u6240\u5C45\u7684\u4E34\u909B\u90FD\u5728\u9014\u4E2D\uFF0C\u4E0D\u5982\u5148\u53BB\u6D1B\u9633\u5BFB\u7EAA\u5E08\u59D0\u5E2E\u5FD9\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u62AC\u8D77\u5934\uFF0C\u5374\u89C1\u51E0\u540D\u7EA2\u8863\u6C49\u5B50\u4ECE\u957F\u8857\u5C3D\u5934\u7F13\u7F13\u8D70\u6765\u3002\u5979\u8FDE\u5FD9\u8F6C\u8EAB\uFF0C\u5FC3\u91CC\u6697\u6697\u53EB\u82E6\u3002\u540E\u9762\u662F\u51E0\u4E2A\u9EC4\u8863\u4EBA\uFF0C\u540C\u6837\u624B\u6301\u5175\u5203\u6B65\u6B65\u7D27\u903C\u3002\u63A5\u7740\u4E24\u4FA7\u5899\u5934\u6D8C\u51FA\u5341\u4F59\u9053\u8EAB\u5F71\uFF0C\u5F53\u5148\u4E00\u5973\u8EAB\u6750\u5A07\u5C0F\uFF0C\u6B63\u662F\u6628\u591C\u4EA4\u8FC7\u624B\u7684\u571F\u5802\u9999\u4E3B\u8F7B\u5C18\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u5A07\u5412\u4E00\u58F0\uFF0C\u98DE\u8EAB\u800C\u8D77\uFF0C\u53CD\u624B\u62D4\u51FA\u7247\u7389\uFF0C\u51C6\u5907\u6545\u6280\u91CD\u65BD\uFF0C\u731D\u4E0D\u53CA\u9632\u4E0B\u5148\u9664\u53BB\u5899\u9876\u7684\u654C\u4EBA\uFF0C\u89C5\u673A\u9003\u751F\u3002</p><p>\u5899\u9876\u7684\u9EC4\u8863\u6C49\u5B50\u4E24\u81C2\u4E00\u5C55\uFF0C\u624B\u91CC\u62FF\u7684\u5374\u662F\u86FE\u7709\u523A\uFF0C\u4ED6\u4E0D\u95EA\u4E0D\u907F\uFF0C\u76F4\u523A\u7D2B\u73AB\u7684\u5589\u5934\u3001\u4E0B\u9634\uFF0C\u51FA\u624B\u4E0B\u6D41\u3002\u7D2B\u73AB\u5FC3\u91CC\u6697\u9A82\uFF0C\u63D0\u6C14\u7FFB\u8EAB\u800C\u8D77\uFF0C\u907F\u5F00\u86FE\u7709\u523A\uFF0C\u843D\u5728\u4E86\u5899\u5934\u3002</p><p>\u4EA4\u4E86\u4E24\u62DB\uFF0C\u7D2B\u73AB\u7693\u8155\u4E00\u7FFB\uFF0C\u7247\u7389\u5149\u8292\u95EA\u52A8\uFF0C\u5C06\u90A3\u4EBA\u7684\u94A2\u523A\u3001\u624B\u6307\u9F50\u9F50\u524A\u65AD\uFF0C\u63A5\u7740\u72E0\u72E0\u780D\u5165\u90A3\u4EBA\u80F8\u819B\u3002</p><p>\u4F46\u6B64\u65F6\u661F\u6708\u6E56\u5E2E\u4F17\u5DF2\u7ECF\u628A\u5979\u56F4\u5728\u4E2D\u95F4\uFF0C\u8F7B\u5C18\u3001\u70C8\u7130\u5404\u636E\u4E00\u7AEF\u6CBF\u5899\u6B65\u6B65\u7D27\u903C\uFF0C\u5DE8\u77F3\u3001\u731B\u70BD\u5219\u5206\u7AD9\u4E24\u4FA7\uFF0C\u5468\u56F4\u4E03\u9EC4\u516B\u7EA2\u5341\u4E94\u540D\u5E2E\u4F17\u56F4\u6210\u5706\u5708\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u7EA4\u8DB3\u8E0F\u5728\u5899\u5934\uFF0C\u77ED\u5200\u6A2A\u80F8\uFF0C\u7EA2\u8882\u98DE\u626C\u3002</p><p>\u8FD9\u4E0B\u9EBB\u70E6\u4E86\uFF0C\u6050\u6015\u662F\u9003\u4E0D\u6389\u3002\u7A76\u7ADF\u662F\u529B\u62FC\u800C\u6B7B\uFF0C\u8FD8\u662F\u675F\u624B\u5C31\u64D2\uFF0C\u85C9\u673A\u62A5\u4EC7\uFF1F</p><p>\u661F\u7738\u5BD2\u5149\u4E00\u95EA\uFF0C\u7D2B\u73AB\u63E1\u7D27\u5200\u67C4\uFF0C\u5014\u5F3A\u5730\u626C\u8D77\u5934\u3002\u5C31\u8BA9\u54E5\u54E5\u66FF\u81EA\u5DF1\u62A5\u4EC7\u597D\u4E86\u3002</p><p>\u9762\u524D\u662F\u70C8\u7130\u7684\u94DC\u9524\uFF0C\u8EAB\u540E\u662F\u8F7B\u5C18\u7684\u67F3\u53F6\u5200\uFF0C\u4E24\u65C1\u8FD8\u6709\u5DE8\u77F3\u7684\u94C1\u76FE\u548C\u731B\u70BD\u7684\u539A\u80CC\u780D\u5200\u7708\u7708\u76F8\u8BC6\uFF0C\u661F\u6708\u6E56\u5341\u4E8C\u9999\u4E3B\u6709\u56DB\u4EBA\u5728\u6B64\uFF0C\u5899\u5934\u90A3\u4E2A\u9C9C\u82B1\u822C\u7684\u7F8E\u5C11\u5973\u5DF2\u662F\u63D2\u7FC5\u96BE\u98DE\u3002</p><p>\u70C8\u7130\u66B4\u559D\u4E00\u58F0\uFF0C\u94DC\u9524\u5212\u51FA\u4E00\u6761\u9EC4\u5F71\u731B\u7838\u4E0B\u6765\u3002\u6155\u5BB9\u7D2B\u73AB\u50CF\u88AB\u9524\u98CE\u6FC0\u8D77\u822C\u5411\u540E\u98D8\u98DE\uFF0C\u7247\u7389\u76F4\u523A\u8F7B\u5C18\u80A9\u5934\u3002\u8F7B\u5C18\u76F8\u8C8C\u4FCF\u4E3D\uFF0C\u624B\u4E2D\u7684\u67F3\u53F6\u5200\u5BBD\u4E0D\u8DB3\u4E09\u6307\uFF0C\u957F\u4EC5\u6709\u4E00\u5C3A\uFF0C\u5C31\u50CF\u662F\u5C0F\u5B69\u7684\u73A9\u5177\u3002</p><p>\u773C\u89C1\u7247\u7389\u523A\u6765\uFF0C\u5979\u8170\u8EAB\u4E00\u659C\uFF0C\u67F3\u53F6\u5200\u95EA\u7535\u822C\u5288\u51FA\uFF0C\u7ADF\u662F\u5BF8\u6B65\u4E0D\u8BA9\u3002\u7D2B\u73AB\u7EA4\u624B\u4E00\u6C89\uFF0C\u671D\u5200\u80CC\u524A\u53BB\uFF0C\u8F7B\u5C18\u77E5\u9053\u5979\u5B9D\u5200\u5389\u5BB3\uFF0C\u62DB\u52BF\u5E76\u672A\u7528\u8001\uFF0C\u624B\u8155\u5212\u4E86\u4E2A\u534A\u5706\uFF0C\u5200\u5C16\u6311\u5411\u7D2B\u73AB\u67D4\u9888\u3002</p><p>\u82E5\u5728\u5E73\u65F6\uFF0C\u7D2B\u73AB\u501A\u4ED7\u5B9D\u5200\u7684\u950B\u9510\u81EA\u7136\u4E0D\u60E7\uFF0C\u4F46\u6B64\u65F6\u8EAB\u8FB9\u5927\u654C\u73AF\u4F3A\uFF0C\u5982\u679C\u88AB\u5979\u7F20\u4F4F\uFF0C\u90A3\u5C31\u4E0D\u7528\u6253\u4E86\u3002\u8111\u540E\u98CE\u58F0\u4E00\u7D27\uFF0C\u7D2B\u73AB\u53CD\u624B\u6321\u4F4F\u70C8\u7130\u7684\u94DC\u9524\uFF0C\u5DE6\u624B\u633D\u4F4F\u5C0F\u5F29\u5BF9\u51C6\u8F7B\u5C18\u7684\u80F8\u53E3\u677E\u5F00\u673A\u62EC\u3002</p><p>\u7247\u7389\u5728\u94DC\u9524\u4E0A\u5212\u4E86\u4E00\u9053\u6DF1\u7EA6\u5BF8\u8BB8\u7684\u5200\u53E3\uFF0C\u7D2B\u73AB\u53F3\u624B\u88AB\u9707\u5F97\u53D1\u9EBB\u3002\u8F7B\u5C18\u8EB2\u5F00\u5F29\u77E2\uFF0C\u4E24\u811A\u50CF\u6CBE\u5728\u5899\u5934\u4E00\u822C\uFF0C\u67D4\u8170\u5E73\u5C55\uFF0C\u4E00\u62DB\u67D4\u98CE\u5377\u53F6\u524A\u5411\u7D2B\u73AB\u8170\u95F4\u3002\u5200\u5149\u95EA\u52A8\uFF0C\u5C3D\u91CF\u8BA9\u5979\u6478\u4E0D\u6E05\u5200\u950B\u6240\u5728\u3002\u6155\u5BB9\u7D2B\u73AB\u5A07\u5412\u4E00\u58F0\uFF0C\u51FA\u4EBA\u610F\u6599\u5730\u4ECE\u5899\u5934\u817E\u8EAB\u800C\u8D77\u76F4\u6251\u5DE8\u77F3\u3002</p><p>\u5DE8\u77F3\u7684\u94C1\u76FE\u957F\u5200\u53EF\u653B\u53EF\u5B88\uFF0C\u5BF9\u7D2B\u73AB\u6765\u8BF4\u6700\u4E0D\u5229\u4E8E\u901F\u6218\uFF0C\u5F53\u4E0B\u5176\u4F59\u4E09\u4F4D\u9999\u4E3B\u90FD\u60F3\u5230\u5979\u662F\u58F0\u4E1C\u51FB\u897F\uFF0C\u5E76\u672A\u56F4\u653B\u53CD\u800C\u6563\u5F00\u5B88\u4F4F\u53BB\u8DEF\u3002</p><p>\u91D1\u94C1\u4EA4\u9E23\u58F0\u4E0D\u65AD\u54CD\u8D77\uFF0C\u7247\u523B\u95F4\u94C1\u76FE\u4FBF\u88AB\u780D\u51FA\u5341\u4F59\u6761\u5200\u75D5\u3002\u5DE8\u77F3\u85CF\u8EAB\u76FE\u540E\uFF0C\u5FC3\u4E0B\u6697\u559C\uFF0C\u8FD9\u5C0F\u4E2B\u5934\u770B\u6765\u53EA\u662F\u5E7C\u7A1A\uFF0C\u5979\u5B9D\u5200\u518D\u5229\uFF0C\u6BD5\u7ADF\u53EA\u6709\u4E94\u5BF8\u6765\u957F\uFF0C\u600E\u4E48\u53EF\u80FD\u780D\u788E\u94C1\u76FE\uFF1F\u51B5\u4E14\u5185\u529B\u4E0D\u8DB3\uFF0C\u518D\u780D\u51E0\u4E0B\uFF0C\u4E0D\u7528\u6253\uFF0C\u5979\u81EA\u5DF1\u5C31\u7D2F\u8DB4\u4E0B\u4E86\u3002</p><p>\u7D2B\u73AB\u54AC\u7259\u53C8\u662F\u72E0\u72E0\u4E00\u5200\uFF0C\u5200\u5C16\u7A7F\u900F\u5BF8\u8BB8\u539A\u7684\u94C1\u76FE\uFF0C\u4F46\u81EA\u5DF1\u4E5F\u88AB\u9707\u9000\u4E00\u6B65\u3002\u5979\u4F3C\u4E4E\u521A\u521A\u660E\u767D\u8FC7\u6765\uFF0C\u4E0D\u518D\u4E0E\u5DE8\u77F3\u786C\u62FC\uFF0C\u4FA7\u8EAB\u5F80\u76FE\u4E0A\u4E00\u9760\uFF0C\u6321\u5F00\u8EAB\u540E\u4E00\u540D\u7EA2\u8863\u6C49\u5B50\u7684\u94DC\u68CD\uFF0C\u7136\u540E\u501F\u52BF\u8DC3\u8D77\u3002</p><p>\u5DE8\u77F3\u4E00\u821E\u957F\u5200\u6B63\u5F85\u8FFD\u51FB\uFF0C\u7A81\u7136\u80F8\u53E3\u5267\u75DB\u3002\u4ED6\u4E0D\u6562\u7F6E\u4FE1\u7684\u770B\u7740\u80F8\u524D\u63D2\u7740\u6D01\u767D\u7684\u7BAD\u7FBD\uFF0C\u72C2\u543C\u4E00\u58F0\uFF0C\u91CD\u91CD\u5012\u5728\u96EA\u4E2D\u3002</p><p>\u8F7B\u5C18\u773C\u5C16\uFF0C\u770B\u51FA\u7D2B\u73AB\u5148\u5728\u76FE\u4E0A\u780D\u51FA\u7F3A\u53E3\uFF0C\u7136\u540E\u85C9\u673A\u6697\u6697\u7F1D\u9699\u4E2D\u5C04\u5165\u4E00\u7BAD\u3002\u5DE8\u77F3\u7528\u60EF\u4E86\u94C1\u76FE\uFF0C\u6839\u672C\u672A\u66FE\u7559\u610F\u80F8\u53E3\u8981\u5BB3\uFF0C\u6BEB\u65E0\u9632\u5907\u4E0B\u987F\u65F6\u9001\u547D\u3002\u8F7B\u5C18\u8FDE\u58F0\u5A07\u559D\uFF0C\u5341\u4F59\u540D\u5E2E\u4F17\u5206\u6210\u4E09\u7EC4\uFF0C\u5404\u7531\u4E00\u540D\u9999\u4E3B\u5E26\u9886\u8F6E\u756A\u8FDB\u903C\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u7AED\u529B\u76F8\u6297\uFF0C\u4E0D\u591A\u65F6\u5DF2\u9999\u6C57\u6DCB\u6F13\u3002\u597D\u5728\u5979\u5B9D\u5200\u5728\u624B\uFF0C\u5BAB\u4E3B\u53C8\u5429\u5490\u8FC7\u5FC5\u987B\u751F\u64D2\uFF0C\u4F17\u4EBA\u4E5F\u4E0D\u6562\u75DB\u4E0B\u6740\u624B\uFF0C\u5426\u5219\u65E9\u5DF1\u5C38\u6A2A\u5C31\u5730\u3002</p><p>\u7D2B\u73AB\u5200\u5F29\u9F50\u65BD\uFF0C\u53C8\u4F24\u4E86\u56DB\u540D\u5E2E\u4F17\uFF0C\u4F46\u5DE6\u81C2\u4E5F\u4E2D\u4E86\u4E00\u68CD\uFF0C\u5267\u75DB\u5F7B\u9AA8\u3002\u5979\u81EA\u77E5\u96BE\u4EE5\u5E78\u514D\uFF0C\u6D78\u900F\u9C9C\u8840\u7684\u7EA2\u886B\u4E00\u95EA\u62A2\u8EAB\u800C\u51FA\uFF0C\u62DB\u62DB\u8FDB\u653B\uFF0C\u5B8C\u5168\u662F\u4EE5\u547D\u640F\u547D\u7684\u6253\u6CD5\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u597D\u4E0D\u8981\u8138\uFF01\u300D\u957F\u8857\u5C3D\u5934\u4F20\u6765\u4E00\u58F0\u5A07\u559D\u3002</p><p>\u7D2B\u73AB\u987E\u4E0D\u5F97\u56DE\u5934\u53BB\u770B\uFF0C\u5148\u907F\u8FC7\u70C8\u7130\u7684\u94DC\u9524\uFF0C\u53C8\u524A\u65AD\u4E86\u4E00\u628A\u94A2\u53C9\u3002\u800C\u731B\u70BD\u7684\u539A\u80CC\u5200\u79BB\u819D\u76D6\u5DF2\u4E0D\u8DB3\u4E24\u5BF8\uFF0C\u7D2B\u73AB\u63E1\u7D27\u7247\u7389\uFF0C\u54AC\u7259\u76EF\u7740\u731B\u70BD\u7C97\u9ED1\u7684\u8116\u5B50\uFF0C\u62FC\u7740\u4E24\u6761\u817F\u88AB\u5E9F\uFF0C\u4E5F\u8981\u780D\u4E0B\u4ED6\u7684\u8111\u888B\u3002</p><p>\u731B\u70BD\u6B63\u6697\u559C\u5F97\u624B\uFF0C\u5FFD\u7136\u542C\u5230\u8111\u540E\u98CE\u58F0\u7A81\u7D27\uFF0C\u4ED6\u987E\u4E0D\u5F97\u51FB\u5012\u7D2B\u73AB\uFF0C\u8FDE\u5FD9\u8F6C\u8EAB\u62B5\u6321\u3002\u300C\u556A\u300D\uFF0C\u4E00\u679A\u6697\u5668\u6B63\u4E2D\u9762\u95E8\uFF0C\u6253\u5F97\u731B\u70BD\u9F3B\u8840\u957F\u6D41\uFF0C\u4ED6\u5FC3\u91CC\u731B\u5730\u4E00\u60CA\uFF0C\u4ED4\u7EC6\u770B\u65F6\u5374\u662F\u4E00\u56E2\u96EA\u7403\u3002\u731B\u70BD\u6C14\u5F97\u66B4\u559D\u4E00\u58F0\uFF0C\u5FFD\u7136\u4E00\u9635\u5BD2\u98CE\u5439\u5728\u80CC\u4E0A\uFF0C\u51B0\u51C9\u7684\u611F\u89C9\u76F4\u5165\u5FC3\u810F\u3002</p><p>\u7D2B\u73AB\u85C9\u673A\u523A\u6B7B\u731B\u70BD\uFF0C\u5598\u7740\u6C14\u62AC\u8D77\u5934\u6765\uFF0C\u53EA\u89C1\u4E24\u540D\u767D\u8863\u5C11\u5973\u8774\u8776\u822C\u5728\u5200\u5149\u5251\u5F71\u4E2D\u7FE9\u7FE9\u8D77\u821E\u3002\u5979\u4E0D\u53CA\u591A\u60F3\uFF0C\u8FF3\u76F4\u6740\u5165\u6218\u56E2\u3002</p><p>\u661F\u6708\u6E56\u7684\u56DB\u540D\u9999\u4E3B\u5DF2\u635F\u5176\u4E8C\uFF0C\u5341\u516D\u540D\u5E2E\u4F17\u4E5F\u6298\u4E86\u534A\u6570\u3002\u70C8\u7130\u3001\u8F7B\u5C18\u89C1\u4E24\u5973\u6B66\u529F\u4E0D\u5F31\uFF0C\u4E07\u96BE\u8BA8\u5F97\u597D\u53BB\uFF0C\u53EA\u5F97\u5C16\u5578\u4E00\u58F0\uFF0C\u94E9\u7FBD\u800C\u5F52\u3002</p><p>\u7D2B\u73AB\u624B\u8DB3\u9178\u8F6F\uFF0C\u4E00\u8DE4\u8DCC\u5750\u5728\u96EA\u5730\u4E0A\u3002\u90A3\u4E24\u540D\u767D\u8863\u5C11\u5973\u5954\u8FC7\u6765\u6400\u4F4F\u5979\u7684\u624B\u81C2\uFF0C\u5431\u5431\u55B3\u55B3\u5730\u95EE\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u6837\u5440\uFF1F\u8FD9\u4E48\u591A\u8840\uFF0C\u4F60\u4F24\u5728\u54EA\u513F\u4E86\uFF1F\u75DB\u4E0D\u75DB\uFF1F\u4ED6\u4EEC\u5230\u5E95\u662F\u8C01\uFF1F\u4F60\u53EB\u4EC0\u4E48\u540D\u5B57\u5440\uFF1F\u59D0\u59D0\uFF0C\u4F60\u957F\u5F97\u597D\u6F02\u4EAE\u554A\u2026\u2026\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u82E6\u7B11\u7740\u8BF4\uFF1A\u300C\u4F60\u4EEC\u4E00\u4E2A\u4E00\u4E2A\u95EE\u597D\u4E0D\u597D\u2026\u2026\u300D</p><p>\u300C\u6211\u53EB\u767D\u7389\u83BA\uFF0C\u5979\u53EB\u767D\u7389\u9E42\uFF0C\u6211\u4EEC\u662F\u59D0\u59B9\uFF0C\u5B6A\u751F\u7684\u54CE\u3002\u300D</p><p>\u7D2B\u73AB\u5598\u7740\u53E3\u7B11\u9053\uFF1A\u300C\u602A\u4E0D\u5F97\u4F60\u4EEC\u4FE9\u957F\u5F97\u4E00\u6837\uFF0C\u8FDE\u9152\u7A9D\u90FD\u751F\u5F97\u4E00\u6BEB\u4E0D\u5DEE\u5462\u3002\u300D</p><p>\u4E24\u59D0\u59B9\u5403\u5403\u7684\u7B11\u4E86\u8D77\u6765\u3002\u5979\u4EEC\u5E74\u7EAA\u4E0E\u6155\u5BB9\u7D2B\u73AB\u76F8\u4EFF\uFF0C\u808C\u80A4\u80DC\u96EA\uFF0C\u7709\u679D\u5982\u753B\uFF0C\u65E0\u8BBA\u53D1\u5F0F\u3001\u88C5\u9970\u90FD\u4E00\u6A21\u4E00\u6837\uFF0C\u5C31\u50CF\u5F7C\u6B64\u7684\u5F71\u5B50\u5A07\u7F8E\u65E0\u4E8C\u3002</p><p>\u7D2B\u73AB\u52A8\u4E86\u52A8\u5DE6\u81C2\uFF0C\u53D1\u73B0\u80A9\u9AA8\u65E0\u6059\uFF0C\u987F\u65F6\u653E\u4E0B\u5FC3\u6765\uFF0C\u300C\u6211\u53EB\u6155\u5BB9\u7D2B\u73AB\uFF0C\u591A\u8C22\u4E24\u2026\u2026\u300D</p><p>\u8FD8\u6CA1\u8BF4\u5B8C\uFF0C\u767D\u6C0F\u59D0\u59B9\u4FBF\u60CA\u559C\u5730\u53EB\u4E86\u8D77\u6765\uFF0C\u300C\u54CE\u5440\uFF0C\u4F60\u5C31\u662F\u73AB\u7470\u4ED9\u5B50\u554A\uFF0C\u602A\u4E0D\u5F97\u751F\u5F97\u8FD9\u4E48\u7F8E\u5462\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E00\u6014\uFF0C\u6CA1\u60F3\u5230\u81EA\u5DF1\u7684\u540D\u58F0\u7ADF\u7136\u8FD9\u4E48\u54CD\u4EAE\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u662F\u5B89\u5B9A\u516B\u6781\u95E8\u5F1F\u5B50\uFF0C\u56DE\u5BB6\u9014\u4E2D\u8DEF\u8FC7\u4E34\u6CB3\u9547\uFF0C\u4E0D\u6599\u9047\u89C1\u540D\u58F0\u9E4A\u8D77\u7684\u73AB\u7470\u4ED9\u5B50\uFF0C\u59D0\u59B9\u4FE9\u90FD\u662F\u559C\u4E0D\u81EA\u80DC\u3002</p><p>\u767D\u7389\u83BA\u95EE\u9053\uFF1A\u300C\u4ED6\u4EEC\u662F\u4EC0\u4E48\u4EBA\uFF1F\u8FD9\u4E48\u591A\u7537\u4EBA\u6B3A\u8D1F\u4F60\u4E00\u4E2A\uFF0C\u771F\u4E0D\u8981\u8138\uFF01\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u72B9\u8C6B\u4E86\u4E00\u4E0B\uFF0C\u5766\u7136\u5C06\u5BB6\u4E2D\u906D\u9047\u7684\u60E8\u53D8\u5408\u76D8\u6258\u51FA\u3002\u4E07\u4E00\u81EA\u5DF1\u843D\u5165\u4E86\u654C\u624B\uFF0C\u4E5F\u597D\u6709\u4EBA\u77E5\u9053\u662F\u600E\u4E48\u56DE\u4E8B\uFF0C\u5982\u679C\u80FD\u5C06\u8BAF\u606F\u4F20\u5230\u54E5\u54E5\u5AC2\u5AC2\u8033\u4E2D\u6700\u597D\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u542C\u5B8C\uFF0C\u4E0D\u7EA6\u800C\u540C\u7684\u62CD\u62CD\u80F8\u53E3\uFF0C\u300C\u90A3\u5E2E\u5BB6\u4F19\u771F\u662F\u592A\u574F\u4E86\uFF01\u300D</p><p>\u7D2B\u73AB\u795E\u8272\u9EEF\u7136\uFF0C\u6C89\u9ED8\u4E00\u4F1A\u513F\uFF0C\u5C55\u989C\u9053\uFF1A\u300C\u5E78\u597D\u9047\u4E0A\u4E24\u4F4D\u59D0\u59D0\uFF0C\u6551\u4E86\u5C0F\u59B9\u3002\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u5C0F\u624B\u6525\u6210\u62F3\u5934\uFF0C\u5F02\u53E3\u540C\u58F0\u5730\u8BF4\u9053\uFF1A\u300C\u884C\u4FA0\u4ED7\u4E49\u6B63\u662F\u6211\u4EEC\u4E60\u6B66\u4E4B\u4EBA\u7684\u8D23\u4EFB\uFF01\u300D</p><p>\u542C\u5230\u8FD9\u6837\u8001\u6C14\u6A2A\u79CB\u7684\u8BDD\u8BED\u7531\u4E24\u4E2A\u9EC4\u83BA\u822C\u6E05\u8106\u7684\u58F0\u97F3\u8BF4\u51FA\u6765\uFF0C\u7D2B\u73AB\u5DEE\u70B9\u5931\u7B11\u51FA\u58F0\u3002\u4F46\u770B\u5230\u5979\u4EEC\u575A\u4FE1\u4E0D\u7591\u7684\u8868\u60C5\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5FC3\u5934\u4E00\u9635\u611F\u52A8\u3002</p><p>\u4E09\u4EBA\u6765\u5230\u7EE6\u53BF\u5DF2\u662F\u5348\u540E\uFF0C\u7D2B\u73AB\u7EE7\u7EED\u671D\u897F\u5357\u8D70\u6D1B\u9633\u3001\u5DF4\u8700\u53BB\u5BFB\u5E08\u7236\uFF0C\u767D\u7389\u83BA\u3001\u767D\u7389\u9E42\u5219\u6539\u9053\u4E1C\u884C\u3002\u4E09\u4EBA\u8BA4\u8BC6\u65F6\u95F4\u867D\u77ED\uFF0C\u5374\u662F\u4E00\u89C1\u5982\u6545\u3002\u6155\u5BB9\u7D2B\u73AB\u559C\u6B22\u8FD9\u5BF9\u59D0\u59B9\u82B1\u7684\u70ED\u5FC3\u548C\u53EF\u7231\uFF0C\u767D\u6C0F\u59D0\u59B9\u66F4\u559C\u6B22\u8FD9\u4E2A\u52C7\u6C14\u5341\u8DB3\u7684\u7F8E\u8C8C\u5C11\u5973\u3002\u4E34\u522B\u65F6\u4E09\u4EBA\u4F9D\u4F9D\u4E0D\u820D\uFF0C\u767D\u6C0F\u59D0\u59B9\u66F4\u8BA9\u51FA\u4E00\u5339\u9A6C\uFF0C\u9001\u7ED9\u7D2B\u73AB\u3002</p><p>\u767D\u7389\u83BA\u62B1\u7740\u9A6C\u5934\u8BF4\u9053\uFF1A\u300C\u5C0F\u767D\u4F60\u53EF\u8981\u4E56\u4E56\u542C\u8BDD\uFF0C\u8BB0\u4F4F\u628A\u7D2B\u73AB\u59D0\u59D0\u9001\u5230\u98D8\u6885\u5CF0\u554A\u3002\u300D</p><p>\u767D\u9A6C\u50CF\u662F\u542C\u61C2\u4E86\u4F3C\u7684\u626C\u4E86\u626C\u5934\uFF0C\u53D1\u51FA\u4E00\u58F0\u957F\u5636\u3002</p><p>\u767D\u7389\u9E42\u62FF\u51FA\u4E00\u4EF6\u5927\u7EA2\u62AB\u98CE\uFF0C\u63A9\u4F4F\u7D2B\u73AB\u8EAB\u4E0A\u7684\u8840\u8FF9\uFF0C\u53C8\u5305\u4E86\u51E0\u4EF6\u8863\u670D\u9012\u7ED9\u5979\uFF0C\u8FD9\u624D\u6325\u624B\u544A\u522B\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u76EE\u9001\u59D0\u59B9\u4FE9\u6D88\u5931\u5728\u5730\u5E73\u7EBF\u4E0A\uFF0C\u53F9\u4E86\u4E00\u53E3\u6C14\uFF0C\u8F6C\u8EAB\u671B\u7740\u5357\u65B9\u7684\u832B\u832B\u6797\u91CE\u3002</p><p>\u8FD9\u91CC\u79BB\u4E09\u5E08\u59D0\u6240\u5C45\u7684\u6D1B\u9633\u8FD8\u6709\u4E09\u5929\u7684\u8DEF\u7A0B\uFF0C\u53BB\u98D8\u6885\u5CF0\u6700\u5C11\u8FD8\u9700\u4E00\u4E2A\u6708\u3002</p><p>\u5979\u6478\u4E86\u6478\u9A6C\u5934\uFF0C\u300C\u5C0F\u767D\uFF0C\u4F60\u53EF\u8981\u628A\u6211\u9001\u5230\u98D8\u6885\u5CF0\u554A\u3002\u300D\u867D\u7136\u6A21\u4EFF\u7740\u767D\u7389\u83BA\u7684\u53E3\u6C14\uFF0C\u9017\u81EA\u5DF1\u5F00\u5FC3\uFF0C\u4F46\u7D2B\u73AB\u773C\u795E\u5374\u5145\u6EE1\u4E86\u843D\u5BDE\u3002</p>',52);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
