import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_06" tabindex="-1"><a class="header-anchor" href="#_06" aria-hidden="true">#</a> 06</h1><p>\u300C\u6DEB\u5987\uFF0C\u4F60\u77E5\u7F6A\u4E86\u5417\uFF1F\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u88AB\u75DB\u82E6\u548C\u7F9E\u8FB1\u6298\u78E8\u5F97\u795E\u667A\u6A21\u7CCA\uFF0C\u7A74\u9053\u521A\u4E00\u89E3\u5F00\uFF0C\u4FBF\u6D51\u8EAB\u762B\u8F6F\uFF0C\u8FDE\u5934\u90FD\u652F\u4E0D\u8D77\u6765\u3002</p><p>\u5BAB\u4E3B\u5E7D\u6DF1\u7684\u773C\u775B\u51B7\u51B7\u770B\u7740\u9762\u524D\u7684\u7F8E\u5987\uFF0C\u81EA\u5DF1\u8FD9\u4E48\u591A\u5E74\u7684\u75DB\u82E6\u3001\u59D4\u5C48\u3001\u803B\u8FB1\u90FD\u662F\u56E0\u4E3A\u8FD9\u4E2A\u72E0\u6BD2\u7684\u5973\u4EBA\u3002</p><p>\u4E5F\u8BB8\u72E0\u6BD2\u7684\u5973\u4EBA\u90FD\u957F\u5F97\u7279\u522B\u7F8E\uFF0C\u6216\u8005\u6F02\u4EAE\u7684\u5973\u4EBA\u603B\u662F\u5FC3\u80A0\u6076\u6BD2\u2014\u2014\u81F3\u5C11\uFF0C\u4ED6\u6240\u9047\u5230\u7684\u5973\u4EBA\u90FD\u662F\u5982\u6B64\u3002\u82E6\u7B49\u8FD9\u4E48\u591A\u5E74\uFF0C\u7EC8\u4E8E\u6709\u673A\u4F1A\u60E9\u7F5A\u8FD9\u4E2A\u72E0\u6BD2\u7684\u6DEB\u5987\uFF0C\u4ED6\u5174\u594B\u7684\u624B\u811A\u53D1\u98A4\u3002\u8427\u4F5B\u5974\uFF0C\u6211\u8981\u8BA9\u4F60\u540E\u6094\u81EA\u5DF1\u6240\u505A\u4F5C\u7684\u4E00\u5207\uFF01</p><p>\u4E00\u80A1\u8F6F\u7EF5\u7EF5\u7684\u6E29\u6696\u611F\u89C9\u6625\u98CE\u822C\u62C2\u8FC7\u8EAB\u4F53\uFF0C\u767E\u82B1\u89C2\u97F3\u6162\u6162\u7741\u5F00\u773C\u775B\u3002\u8170\u81C0\u88AB\u4E00\u53CC\u575A\u786C\u7684\u624B\u638C\u62B1\u4F4F\uFF0C\u767D\u5AE9\u7684\u80CC\u810A\u9760\u5728\u4E00\u4E2A\u7537\u5B50\u6000\u4E2D\uFF0C\u5149\u6DA6\u7684\u5927\u817F\u5DE6\u53F3\u5206\u5F00\uFF0C\u626F\u6210\u4E00\u6761\u76F4\u7EBF\u3002\u4E00\u7247\u6ED1\u4E0D\u6E9C\u624B\u7684\u51DD\u8102\u95F4\uFF0C\u80BF\u80C0\u7684\u8089\u7A74\u9AD8\u9AD8\u9F13\u8D77\uFF0C\u8273\u7EA2\u80A5\u539A\u7684\u5AE9\u8089\u9C9C\u82B1\u822C\u76DB\u5F00\u3002</p><p>\u53D1\u89C9\u81EA\u5DF1\u7F9E\u4EBA\u7684\u59FF\u52BF\uFF0C\u767E\u82B1\u89C2\u97F3\u987F\u65F6\u9762\u7EA2\u8FC7\u8033\u3002\u4F46\u8EAB\u5B50\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u624B\u811A\u5C31\u88AB\u8EAB\u540E\u90A3\u4E24\u6761\u624B\u81C2\u94A2\u7B8D\u822C\u5939\u7D27\u3002\u5979\u6323\u624E\u7740\u53EB\u9053\uFF1A\u300C\u4F60\u8BA4\u9519\u4EBA\u4E86\u2026\u2026\u6211\u2026\u2026\u6211\u4E0D\u662F\u90A3\u6837\u5B50\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u9762\u65E0\u8868\u60C5\uFF0C\u51B7\u51B7\u8BF4\uFF1A\u300C\u6DEB\u5987\uFF0C\u4F60\u8FD8\u8981\u72E1\u8FA9\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u4E00\u5411\u4F18\u96C5\u534E\u8D35\uFF0C\u4ECE\u5BB9\u6709\u81F4\uFF0C\u4F46\u6B64\u65F6\u5FC3\u91CC\u60F6\u6025\uFF0C\u4E0D\u7531\u6CEA\u6C34\u6EDA\u6EDA\u800C\u4E0B\uFF0C\u54ED\u7740\u8BF4\uFF1A\u300C\u2026\u2026\u6211\u662F\u4F0F\u9F99\u6DA7\u5BE8\u4E3B\u6155\u5BB9\u536B\u7684\u59BB\u5B50\uFF0C\u6839\u672C\u5C31\u4E0D\u8BA4\u8BC6\u4F60\u2026\u2026\u6211\u4E5F\u6CA1\u6709\u505A\u8FC7\u574F\u4E8B\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u731B\u7136\u6012\u559D\u4E00\u58F0\uFF0C\u4E00\u638C\u5C06\u9576\u91D1\u5D4C\u7389\u7684\u5B9D\u5EA7\u6276\u624B\u62CD\u4E86\u4E2A\u7C89\u788E\u3002</p><p>\u5DE8\u54CD\u8FC7\u540E\uFF0C\u5927\u6BBF\u91CC\u4E00\u7247\u6B7B\u5BC2\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u88AB\u4ED6\u7684\u66B4\u6012\u5413\u5F97\u5A07\u8EAF\u98A4\u6296\uFF0C\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u5BAB\u4E3B\u80F8\u53E3\u7684\u8D77\u4F0F\u6162\u6162\u5E73\u7F13\u4E0B\u6765\uFF0C\u4F38\u624B\u6309\u5728\u767E\u82B1\u89C2\u97F3\u4E0B\u4F53\u5A07\u67D4\u7684\u82B1\u74E3\u4E0A\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u6323\u624E\u7740\u626D\u5F00\u8EAB\u4F53\uFF0C\u53EB\u9053\uFF1A\u300C\u522B\u78B0\u6211\uFF01\u300D</p><p>\u300C\u54FC\uFF01\u4F60\u8FD9\u4E2A\u8D31\u4EBA\uFF0C\u4EE5\u4E3A\u6211\u4F1A\u64CD\u4F60\u7684\u8D31\u5C44\u5417\uFF1F\u8FD9\u6837\u4E0B\u8D31\u7684\u6DEB\u5987\uFF0C\u8FD8\u4E0D\u914D\u8BA9\u6211\u6765\u64CD\uFF01\u300D\u5BAB\u4E3B\u54AC\u7259\u8BF4\u7740\uFF0C\u62FF\u51FA\u4E00\u6839\u7C97\u5927\u7684\u91D1\u9F99\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u4FCF\u76EE\u5706\u7741\uFF0C\u60CA\u53EB\u9053\uFF1A\u300C\u4E0D\u8981\uFF01\u4E0D\u8981\u554A\uFF01\u300D</p><p>\u5BAB\u4E3B\u6EE1\u8138\u6068\u610F\u5730\u63E1\u7740\u91D1\u9F99\uFF0C\u5C06\u72F0\u72DE\u7684\u9F99\u9996\uFF0C\u6162\u6162\u4F38\u5411\u767E\u82B1\u89C2\u97F3\u4E0B\u8179\u3002</p><p>\u51B0\u51C9\u7684\u91D1\u5C5E\u89E6\u5230\u7389\u6237\u4E0A\u67D4\u5AE9\u7684\u8089\u7247\uFF0C\u767E\u82B1\u89C2\u97F3\u5C16\u53EB\u9053\uFF1A\u300C\u4F60\u6740\u4E86\u6211\u5427\uFF01\u6740\u4E86\u6211\u5427\u2026\u2026\u300D</p><p>\u300C\u90A3\u592A\u4FBF\u5B9C\u4F60\u4E86\u3002\u300D\u5BAB\u4E3B\u51B7\u51B7\u9053\u3002</p><p>\u575A\u786C\u7684\u91D1\u9F99\u6CA1\u5165\u9C9C\u7EA2\u7684\u5AE9\u8089\uFF0C\u9CDE\u7532\u522E\u5728\u8089\u58C1\u4E0A\uFF0C\u4F20\u4F20\u9635\u9635\u75DB\u695A\u3002\u767E\u82B1\u89C2\u97F3\u6EE1\u8138\u6CEA\u5149\uFF0C\u75DB\u4E0D\u6B32\u751F\u7684\u653E\u58F0\u5927\u54ED\u3002</p><p>\u5979\u54ED\u5F97\u8D8A\u5389\u5BB3\uFF0C\u5BAB\u4E3B\u5C31\u8D8A\u5F00\u5FC3\u3002\u4ED6\u624B\u4E2D\u4E00\u7528\u529B\uFF0C\u5C3A\u8BB8\u957F\u7684\u91D1\u9F99\u786C\u751F\u751F\u6345\u5165\u8FD1\u534A\u3002\u767E\u82B1\u89C2\u97F3\u54ED\u58F0\u4E00\u987F\uFF0C\u7EA2\u8273\u8273\u7684\u5C0F\u5634\u50F5\u5728\u534A\u7A7A\uFF0C\u75DB\u5F97\u5598\u4E0D\u8FC7\u6C14\u6765\u3002</p><p>\u300C\u554A\u2026\u2026\u554A\u2026\u554A\u2026\u2026\u5440\uFF01\u54CE\u5440\u2026\u2026\u300D</p><p>\u91D1\u9F99\u8FDB\u51FA\u95F4\uFF0C\u767E\u82B1\u89C2\u97F3\u75DB\u53EB\u8FDE\u58F0\u3002\u672C\u6765\u5DF2\u7ECF\u53D7\u4F24\u7684\u79D8\u5904\u88AB\u8FD9\u4E2A\u964C\u751F\u7537\u5B50\u4E00\u756A\u7C97\u66B4\u7684\u6345\u5F04\uFF0C\u53C8\u6D41\u51FA\u8840\u6765\u3002\u6BB7\u7EA2\u7684\u8840\u8FF9\u987A\u7740\u9CDE\u7247\u7684\u7EB9\u8DEF\uFF0C\u4E00\u76F4\u6DCC\u5230\u90A3\u53EA\u51B7\u51B0\u51B0\u7684\u624B\u638C\u4E0A\u3002</p><p>\u773C\u4E2D\u770B\u7740\u7FFB\u5377\u7684\u5AE9\u8089\uFF0C\u9F3B\u95F4\u55C5\u7740\u9888\u4E2D\u53D1\u9645\u7684\u82AC\u82B3\uFF0C\u5BAB\u4E3B\u5FC3\u4E2D\u6B32\u706B\u4E0E\u6068\u610F\u4EA4\u7EC7\u5728\u4E00\u8D77\uFF0C\u8D8A\u70E7\u8D8A\u65FA\uFF0C\u51E0\u4E4E\u5FCD\u4E0D\u4F4F\u8981\u6254\u6389\u91D1\u9F99\uFF0C\u628A\u81EA\u5DF1\u66F4\u4E3A\u72F0\u72DE\u7684\u5DE8\u9633\u62BD\u5230\u90A3\u4E2A\u6E29\u8F6F\u6ED1\u817B\u7684\u8089\u7A74\u5185\uFF0C\u72E0\u72E0\u64CD\u5F04\u4E00\u756A\u3002\u4ED6\u7684\u547C\u5438\u6E10\u6E10\u6025\u4FC3\u8D77\u6765\uFF0C\u5FFD\u7136\u4E24\u624B\u63E1\u4F4F\u8427\u4F5B\u5974\u7684\u819D\u5F2F\u4E00\u5206\u3002\u6C89\u91CD\u7684\u91D1\u9F99\u4ECE\u8840\u6DCB\u6DCB\u7684\u8089\u7A74\u6ED1\u843D\uFF0C\u300C\u5F53\u300D\u7684\u4E00\u58F0\uFF0C\u91CD\u91CD\u6389\u5728\u5730\u4E0A\u3002</p><p>\u5BAB\u4E3B\u628A\u51E0\u8FD1\u660F\u8FF7\u7684\u767E\u82B1\u89C2\u97F3\u653E\u5728\u6B8B\u7F3A\u7684\u5B9D\u6905\u4E0A\uFF0C\u4F38\u624B\u89E3\u5F00\u8863\u886B\u3002\u624B\u6307\u521A\u78B0\u5230\u8863\u895F\uFF0C\u53C8\u50F5\u4F4F\u4E86\u3002\u4ED6\u4EF0\u5929\u770B\u7740\u9ED1\u6C89\u6C89\u7684\u6BBF\u9876\uFF0C\u79CD\u79CD\u60E8\u75DB\u6CDB\u4E0A\u5FC3\u5934\u3002\u601D\u7D22\u95F4\uFF0C\u5589\u7ED3\u4E0A\u4E0B\u5FAE\u52A8\uFF0C\u5FC3\u795E\u6FC0\u8361\u3002</p><p>\u300C\u4ED6\u5988\u7684\uFF0C\u64CD\u8FD9\u4E2A\u4EBA\u5C3D\u53EF\u592B\u7684\u70C2\u5A4A\u5B50\uFF0C\u6CA1\u5F97\u6C61\u4E86\u81EA\u5DF1\u7684\u9E21\u5DF4\uFF01\u300D\u7247\u523B\u540E\uFF0C\u5BAB\u4E3B\u6162\u6162\u76F4\u8D77\u8170\uFF0C\u6325\u624B\u4E00\u638C\u6253\u5728\u767E\u82B1\u89C2\u97F3\u7F8E\u7389\u822C\u7684\u4FCF\u8138\u4E0A\u3002</p><p>\u660F\u660F\u6C89\u6C89\u4E2D\uFF0C\u767E\u82B1\u89C2\u97F3\u542C\u5230\u4ED6\u8BF4\uFF1A\u300C\u6211\u8FD8\u7ED9\u4F60\u8FD9\u4E2A\u6DEB\u5987\u51C6\u5907\u4E86\u4E00\u5339\u7389\u9A6C\u3002\u53BB\u5C1D\u5C1D\u5B83\u7684\u6ECB\u5473\u2026\u2026\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E0D\u6562\u5728\u57CE\u9547\u5185\u505C\u7559\uFF0C\u5728\u8DEF\u4E0A\u5BFB\u4E86\u6237\u519C\u5BB6\uFF0C\u5A49\u8A00\u6C42\u4F4F\u3002\u90A3\u6237\u519C\u5BB6\u89C1\u5230\u7D2B\u73AB\u7684\u76F8\u8C8C\u51E0\u4E4E\u4EE5\u4E3A\u662F\u4ED9\u5973\u4E0B\u51E1\uFF0C\u614C\u5FD9\u6536\u62FE\u4E86\u6700\u597D\u7684\u4E00\u95F4\u4F4F\u623F\uFF0C\u53C8\u53D6\u6765\u88AB\u8925\uFF0C\u6253\u626B\u5E72\u51C0\u3002</p><p>\u7D2B\u73AB\u89C1\u90A3\u5BB6\u4E3B\u4EBA\u8FD8\u8981\u51FA\u95E8\u501F\u7C73\u716E\u7ED9\u81EA\u5DF1\u5403\uFF0C\u5FC3\u91CC\u8FC7\u610F\u4E0D\u53BB\uFF0C\u8FDE\u5FD9\u62C9\u4F4F\u90A3\u4E2A\u5987\u4EBA\uFF0C\u300C\u5927\u5A18\uFF0C\u522B\u8D39\u5FC3\u4E86\uFF0C\u6211\u8DDF\u4F60\u4EEC\u4E00\u540C\u5403\u597D\u4E86\u3002\u300D</p><p>\u90A3\u5987\u4EBA\u6413\u4E86\u6413\u56F4\u88D9\uFF0C\u671F\u671F\u827E\u827E\u5730\u8BF4\uFF1A\u300C\u90A3\u2026\u2026\u90A3\u2026\u2026\u90A3\u53EF\u4E0D\u6210\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u597D\u8BF4\u6B79\u8BF4\uFF0C\u624D\u7559\u4F4F\u4E86\u5979\u3002</p><p>\u4E0D\u591A\u65F6\uFF0C\u996D\u83DC\u7AEF\u4E0A\u6765\u3002\u662F\u4E00\u7897\u7C9F\u7C73\uFF0C\u4E00\u7897\u814C\u7F57\u535C\u3002\u7D2B\u73AB\u8D76\u4E86\u4E00\u5929\u7684\u8DEF\uFF0C\u7C9F\u7C73\u867D\u7136\u7C97\u783A\uFF0C\u4E5F\u5403\u5F97\u5341\u5206\u9999\u751C\u3002</p><p>\u6B63\u5403\u95F4\uFF0C\u95E8\u8FB9\u5FFD\u7136\u9732\u51FA\u4E00\u4E2A\u5C0F\u5C0F\u7684\u4EBA\u5934\u3002\u7D2B\u73AB\u62AC\u773C\u770B\u53BB\uFF0C\u5374\u662F\u4E2A\u4E03\u516B\u5C81\u7684\u5B69\u5B50\uFF0C\u773C\u5DF4\u5DF4\u770B\u7740\u5979\u90A3\u7897\u9EC4\u6F84\u6F84\u7684\u7C9F\u7C73\u3002\u7D2B\u73AB\u62DB\u4E86\u62DB\u624B\uFF0C\u300C\u5C0F\u5F1F\u5F1F\uFF0C\u4F60\u8FC7\u6765\u3002\u300D</p><p>\u624B\u521A\u521A\u626C\u8D77\uFF0C\u90A3\u5B69\u5B50\u5C31\u8FDE\u5FD9\u8DD1\u5F00\u4E86\u3002</p><p>\u7D2B\u73AB\u5FC3\u91CC\u7EB3\u95F7\uFF0C\u6084\u6084\u8D70\u5230\u7A97\u8FB9\u5F20\u671B\u3002</p><p>\u6B6A\u6B6A\u659C\u659C\u7684\u53A8\u623F\u91CC\u5F71\u5F71\u7EF0\u7EF0\u5750\u7740\u4E00\u7FA4\u4EBA\uFF0C\u91CC\u9762\u6CA1\u6709\u70B9\u706F\uFF0C\u770B\u4E0D\u6E05\u9762\u76EE\u3002\u5979\u6697\u6697\u63E1\u7D27\u77ED\u5200\uFF0C\u79FB\u5230\u95E8\u8FB9\u3002</p><p>\u95E8\u5916\u811A\u6B65\u58F0\u54CD\uFF0C\u6709\u4EBA\u671D\u8FD9\u8FB9\u8D70\u4E86\u8FC7\u6765\u3002</p><p>\u7D2B\u73AB\u4E00\u628A\u6263\u4F4F\u90A3\u4EBA\u7684\u8109\u95E8\u3002\u300C\u5440\u300D\u7684\u4E00\u58F0\u60CA\u547C\uFF0C\u4E00\u4E2A\u4E1C\u897F\u4ECE\u90A3\u4EBA\u624B\u91CC\u6389\u4E86\u4E0B\u6765\u3002</p><p>\u85C9\u7740\u5BA4\u5185\u7684\u706B\u5149\uFF0C\u7D2B\u73AB\u770B\u51FA\u90A3\u4EBA\u662F\u623F\u4E1C\u7684\u5927\u5973\u513F\uFF0C\u77E5\u9053\u81EA\u5DF1\u98CE\u58F0\u9E64\u5456\uFF0C\u95F9\u4E86\u8BEF\u4F1A\uFF0C\u8FDE\u5FD9\u811A\u5C16\u4E00\u6311\uFF0C\u628A\u90A3\u4E2A\u8FD8\u672A\u843D\u5230\u5730\u4E0A\u7684\u4E1C\u897F\u6311\u4E86\u8D77\u6765\u3002</p><p>\u7D2B\u73AB\u770B\u7740\u90A3\u7897\u9ED1\u4E4E\u4E4E\u7684\u4E1C\u897F\u4E0D\u7531\u4E00\u6123\uFF0C\u300C\u8FD9\u662F\u4EC0\u4E48\uFF1F\u829D\u9EBB\uFF1F\u300D</p><p>\u5973\u5B69\u60CA\u9B42\u672A\u5B9A\uFF0C\u300C\u7A17\u2026\u2026\u7A17\u5B50\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u76B1\u4E86\u76B1\u7709\u5934\uFF0C\u300C\u7A17\u5B50\uFF1F\u62FF\u7A17\u5B50\u5E72\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u7ED9\u2026\u2026\u7ED9\u5976\u5976\u9001\u996D\u2026\u2026\u300D</p><p>\u300C\u7ED9\u4F60\u5976\u5976\u5403\u8FD9\u4E2A\uFF1F\u300D\u7D2B\u73AB\u5403\u4E86\u4E00\u60CA\uFF0C\u5FFD\u7136\u660E\u767D\u8FC7\u6765\uFF0C\u300C\u4F60\u4EEC\u5403\u7684\u4EC0\u4E48\uFF1F\u300D</p><p>\u90A3\u5973\u5B69\u4F4E\u4E0B\u5934\u9ED8\u4E0D\u4F5C\u58F0\u3002</p><p>\u4E24\u4EBA\u6C89\u9ED8\u7247\u523B\uFF0C\u7D2B\u73AB\u628A\u7897\u653E\u5230\u5973\u5B69\u624B\u91CC\uFF0C\u6162\u6162\u8D70\u5230\u684C\u8FB9\u3002\u7C9F\u7C73\u7684\u9999\u6C14\u9635\u9635\u98D8\u6765\uFF0C\u5979\u5374\u6CA1\u6709\u534A\u70B9\u98DF\u6B32. \u7528\u6765\u7167\u660E\u7684\u706B\u628A\u6E10\u6E10\u7184\u706D\uFF0C\u7D2B\u73AB\u8EBA\u5728\u7095\u4E0A\uFF0C\u5446\u5446\u770B\u7740\u571F\u576F\u4E2D\u9732\u51FA\u7684\u7A3B\u8349\uFF0C\u5FC3\u91CC\u4E94\u5473\u6742\u9648\u3002</p><p>\u4E24\u5929\u524D\u5979\u8FD8\u662F\u4E2A\u4E0D\u77E5\u4EBA\u4E16\u82E6\u607C\u7684\u5C0F\u5973\u5B69\uFF0C\u65E0\u5FE7\u65E0\u8651\uFF0C\u53EA\u4F1A\u4E3A\u4E86\u65E9\u4E0A\u6CA1\u80FD\u7761\u4F1A\u513F\u61D2\u89C9\u800C\u4E0D\u5F00\u5FC3\u3002\u77ED\u77ED\u4E24\u5929\u65F6\u95F4\uFF0C\u5979\u5C1D\u5230\u4E86\u751F\u79BB\u6B7B\u522B\u6ECB\u5473\uFF0C\u4E5F\u770B\u5230\u4E86\u4EBA\u4E16\u95F4\u7684\u82E6\u96BE\u3002\u539F\u6765\u4EBA\u4E16\u95F4\u4F1A\u800C\u8FD9\u4E48\u591A\u82E6\u695A\u2026\u2026</p><p>\u60F3\u7740\u60F3\u7740\uFF0C\u7D2B\u73AB\u5FC3\u5934\u4E00\u9178\uFF0C\u6014\u6014\u843D\u4E0B\u6CEA\u6765\u3002</p><p>\u7B2C\u4E8C\u5929\u4E00\u65E9\uFF0C\u7D2B\u73AB\u6084\u7136\u79BB\u5F00\uFF0C\u4E34\u884C\u524D\uFF0C\u5979\u628A\u8EAB\u4E0A\u7684\u91D1\u9497\u3001\u94F6\u956F\uFF0C\u751A\u81F3\u8FDE\u8170\u91CC\u7684\u4F69\u7389\u4E5F\u62FF\u51FA\u6765\uFF0C\u4E00\u5E76\u653E\u5728\u684C\u4E0A\u3002\u5979\u77E5\u9053\u8FD9\u5E76\u65E0\u6CD5\u6539\u53D8\u4ED6\u4EEC\u7684\u547D\u8FD0\uFF0C\u4F46\u5979\u4E0D\u80FD\u4E0D\u8FD9\u4E48\u505A\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E09\u5929\u540E\uFF0C\u6155\u5BB9\u7D2B\u73AB\u7EA2\u8863\u767D\u9A6C\u8FDB\u5165\u6D1B\u9633\u57CE\u3002</p><p>\u5982\u4ECA\u5929\u4E0B\u5272\u88C2\u4E3A\u5341\u4F59\u4E2A\u56FD\u5BB6\uFF0C\u4E92\u76F8\u653B\u4F10\u4E0D\u65AD\u3002\u5176\u4E2D\u4EE5\u5B9A\u90FD\u6D1B\u9633\u7684\u5468\u56FD\u6700\u4E3A\u5F3A\u5927\u3002\u5468\u5E1D\u59DA\u5174\u672C\u662F\u71D5\u56FD\u91CD\u5C06\uFF0C\u7BE1\u4F4D\u79F0\u5E1D\u5DF2\u8FD1\u4E8C\u5341\u5E74\u3002</p><p>\u6D1B\u9633\u57CE\u5899\u9AD8\u5927\uFF0C\u6C14\u52BF\u6062\u5F18\uFF0C\u662F\u5929\u4E0B\u6709\u540D\u7684\u575A\u57CE\uFF0C\u540C\u65F6\u4E5F\u662F\u6700\u4E3A\u7E41\u534E\u7684\u90FD\u5E02\u3002</p><p>\u7D2B\u73AB\u987E\u4E0D\u4E0A\u89C2\u8D4F\u8FD9\u5EA7\u540D\u57CE\u7684\u98CE\u8C8C\uFF0C\u4F9D\u7167\u4E09\u5E08\u59D0\u6240\u8BF4\u7684\u65B9\u4F4D\u5BFB\u627E\u7EAA\u5927\u5C06\u519B\u5E9C\u3002</p><p>\u7EAA\u7709\u59A9\u6BCD\u4EB2\u65E9\u4EA1\uFF0C\u7236\u4EB2\u7EAA\u91CD\u4F9D\u7167\u6BCD\u4EB2\u7684\u9057\u613F\u9001\u5979\u62DC\u96EA\u5CF0\u795E\u5C3C\u4E3A\u5E08\u3002\u5979\u867D\u7136\u51FA\u8EAB\u8C6A\u8D35\uFF0C\u5374\u6CA1\u800C\u4E00\u70B9\u76DB\u6C14\u51CC\u4EBA\u7684\u50B2\u6001\uFF0C\u4E3E\u6B62\u5A34\u96C5\uFF0C\u6027\u683C\u67D4\u987A\uFF0C\u662F\u4E00\u4E2A\u6E29\u5A49\u591A\u793C\u7684\u5927\u5BB6\u95FA\u79C0\u3002</p><p>\u5979\u6BD4\u7D2B\u73AB\u65E9\u5165\u95E8\u4E00\u5E74\uFF0C\u4E24\u4EBA\u5E74\u9F84\u76F8\u5DEE\u4E24\u5C81\uFF0C\u4EB2\u5982\u59D0\u59B9\u3002\u98D8\u6885\u5929\u5929\u6C14\u82E6\u5BD2\uFF0C\u6BCF\u5E74\u6700\u51B7\u7684\u4E09\u4E2A\u6708\uFF0C\u7EAA\u91CD\u90FD\u4F1A\u63A5\u5973\u513F\u56DE\u5BB6\u3002\u8FD9\u4E00\u6B21\u4E8C\u5E08\u59D0\u6797\u9999\u8FDC\u4E0E\u7D2B\u73AB\u7684\u54E5\u54E5\u6155\u5BB9\u80DC\u6210\u4EB2\uFF0C\u5979\u4E5F\u4E00\u540C\u8D76\u5230\u4F0F\u9F99\u6DA7\uFF0C\u5A5A\u793C\u4E4B\u540E\u624D\u56DE\u5230\u6D1B\u9633\u3002</p><p>\u7EAA\u7709\u59A9\u559C\u51FA\u671B\u5916\u5730\u633D\u8D77\u7D2B\u73AB\u7684\u624B\uFF0C\u300C\u4F60\u600E\u4E48\u6765\u4E86\u5440\uFF01\u624B\u8FD9\u4E48\u51C9\uFF0C\u5FEB\u8FDB\u6765\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u4E4D\u89C1\u4EB2\u4EBA\uFF0C\u51E0\u5929\u6765\u7684\u4F24\u5FC3\u3001\u59D4\u5C48\u4E00\u4E0B\u5B50\u6D8C\u4E0A\u5FC3\u5934\uFF0C\u62BD\u62BD\u54BD\u54BD\u5730\u54ED\u4E86\u8D77\u6765\u3002</p><p>\u7EAA\u7709\u59A9\u89C1\u5E08\u59B9\u795E\u60C5\u6709\u5F02\uFF0C\u8FDE\u5FD9\u628A\u5979\u62C9\u8FDB\u81EA\u5DF1\u7684\u95FA\u623F\u3002</p><p>\u5BA4\u5185\u6E29\u6696\u5982\u6625\uFF0C\u91D1\u4E1D\u7F20\u6210\u7684\u718F\u7089\u91CC\u98D8\u7740\u7F15\u7F15\u8F7B\u70DF\u3002\u4E00\u4E2A\u534E\u670D\u5C11\u5973\u5750\u5728\u5E8A\u5934\uFF0C\u89C1\u4E24\u4EBA\u8FDB\u6765\uFF0C\u6B3E\u6B3E\u8D77\u8EAB\u3002</p><p>\u300C\u8FD9\u662F\u6211\u5E08\u59B9\uFF0C\u6155\u5BB9\u7D2B\u73AB\u3002\u8FD9\u4F4D\u662F\u4E03\u516C\u4E3B\uFF0C\u4ECA\u5929\u6765\u627E\u6211\u73A9\u3002\u300D\u7EAA\u7709\u59A9\u4E00\u8FB9\u7ED9\u4E24\u4EBA\u4ECB\u7ECD\uFF0C\u4E00\u8FB9\u628A\u7D2B\u73AB\u7684\u62AB\u98CE\u53D6\u4E0B\u6765\uFF0C\u300C\u4F60\u4E0D\u662F\u4E00\u5411\u7A7F\u7EA2\u8863\u7684\u5417\uFF1F\u600E\u4E48\u6362\u4E86\u767D\u8272\u7684\uFF0C\u8FD9\u662F\u6E58\u7EF8\uFF0C\u505A\u5DE5\u5F88\u7CBE\u81F4\u554A\u3002\u300D</p><p>\u542C\u5230\u5E08\u59D0\u5B9B\u5982\u5BB6\u5E38\u7684\u8BDD\u8BED\uFF0C\u7D2B\u73AB\u6162\u6162\u5E73\u9759\u4E0B\u6765\uFF0C\u56D4\u7740\u9F3B\u5B50\u5C0F\u58F0\u8BF4\uFF1A\u300C\u8FD9\u662F\u501F\u4EBA\u5BB6\u7684\uFF0C\u6211\u7684\u8863\u670D\u6CBE\u4E86\u8840\uFF0C\u7A7F\u4E0D\u6210\u4E86\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u4E00\u60CA\uFF0C\u300C\u8C01\u7684\u8840\uFF1F\u8DEF\u4E0A\u9047\u5230\u5F3A\u76D7\u4E86\u5417\uFF1F\u300D</p><p>\u300C\u6211\u6740\u4E86\u51E0\u4E2A\u4EBA\u3002\u300D\u7D2B\u73AB\u300C\u54C7\u300D\u7684\u4E00\u58F0\u53C8\u54ED\u4E86\u8D77\u6765\uFF0C\u300C\u5E08\u59D0\uFF0C\u6211\u7239\u6B7B\u4E86\u3002\u300D</p><p>\u300C\u554A\uFF1F\u300D\u7EAA\u7709\u59A9\u62B1\u4F4F\u7D2B\u73AB\u7684\u80A9\u5934\uFF0C\u300C\u600E\u4E48\u4F1A\u2026\u2026\u7D2B\u73AB\uFF0C\u5148\u522B\u54ED\uFF0C\u6162\u6162\u8BF4\u3002\u300D</p><p>\u4E03\u516C\u4E3B\u89C1\u4E24\u59D0\u59B9\u6709\u8BDD\u8981\u8BF4\uFF0C\u8D76\u5FD9\u8D77\u8EAB\u798F\u4E86\u4E00\u798F\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u7EAA\u59D0\u59D0\u3001\u6155\u5BB9\u59D0\u59D0\uFF0C\u6211\u5148\u544A\u8F9E\u4E86\u3002\u300D\u4E24\u4EBA\u628A\u5979\u9001\u5230\u95E8\u5916\uFF0C\u5916\u9762\u81EA\u6709\u5BAB\u5973\u3001\u592A\u76D1\u4F3A\u5019\u3002</p><p>\u4E03\u516C\u4E3B\u6697\u6697\u770B\u7740\u7D2B\u73AB\u7EC6\u767D\u7684\u624B\u6307\uFF0C\u5FC3\u4E0B\u8273\u7FA1\u4E0D\u5DF2\u3002\u4E00\u822C\u751F\u4E3A\u5973\u513F\uFF0C\u504F\u751F\u5979\u4EEC\u80FD\u81EA\u7531\u81EA\u5728\u7684\u7EB5\u6A2A\u6C5F\u6E56\uFF0C\u5FEB\u610F\u6069\u4EC7\u3002\u81EA\u5DF1\u5C31\u6CA1\u6709\u8FD9\u4E2A\u798F\u6C14\u4E86\uFF0C\u53EA\u80FD\u4E00\u8F88\u5B50\u6DF1\u5C45\u5BAB\u4E2D\u3002</p><p>\u73AF\u4F69\u4E4B\u58F0\u8FDC\u53BB\uFF0C\u6155\u5BB9\u7D2B\u73AB\u64E6\u5E72\u4E86\u6CEA\u6C34\uFF0C\u5C06\u8FD9\u51E0\u5929\u53D1\u751F\u7684\u4E8B\u4E00\u4E00\u544A\u8BC9\u5E08\u59D0\u3002</p><p>\u7EAA\u7709\u59A9\u542C\u7F62\uFF0C\u6C89\u541F\u9053\uFF1A\u300C\u5E08\u7236\u66FE\u8BF4\u8FC7\u4F2F\u7236\u7684\u6B66\u529F\u6781\u9AD8\u660E\uFF0C\u5728\u5F53\u4ECA\u6B66\u6797\u4E5F\u662F\u6709\u6570\u7684\u9AD8\u624B\u3002\u661F\u6708\u6E56\u7A76\u7ADF\u662F\u4F55\u65B9\u795E\u5723\uFF0C\u4F1A\u6709\u8FD9\u4E48\u591A\u5F3A\u5F92\uFF1F\u300D</p><p>\u7D2B\u73AB\u6447\u4E86\u6447\u5934\uFF0C\u300C\u6211\u7239\u6CA1\u6709\u6765\u5F97\u53CA\u8BF4\u2026\u2026\u300D</p><p>\u7EAA\u7709\u59A9\u89C1\u5E08\u59B9\u773C\u5708\u53C8\u7EA2\u4E86\uFF0C\u8FDE\u5FD9\u6E29\u8A00\u5BBD\u6170\u3002</p>',72);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
