import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_07" tabindex="-1"><a class="header-anchor" href="#_07" aria-hidden="true">#</a> 07</h1><p>\u6BBF\u89D2\u653E\u7740\u4E00\u5339\u4E0E\u771F\u9A6C\u540C\u6837\u5927\u5C0F\u7684\u7389\u9A6C\uFF0C\u9B03\u6BDB\u98DE\u626C\uFF0C\u524D\u817F\u4E00\u66F2\u4E00\u76F4\uFF0C\u50CF\u662F\u521A\u4ECE\u6BBF\u5916\u8DD1\u6765\u4E00\u822C\uFF0C\u6829\u6829\u5982\u751F\u3002\u9A6C\u80CC\u4E0A\u6709\u4E00\u56E2\u6DF1\u5165\u77F3\u7EB9\u7684\u8840\u8272\uFF0C\u5B9B\u5982\u5929\u6210\u3002\u6B63\u4E2D\u7AD6\u7740\u4E00\u652F\u7C97\u5927\u7684\u7389\u68D2\uFF0C\u8272\u5448\u5FAE\u7EA2\uFF0C\u4E0A\u9762\u9542\u523B\u82B1\u7EB9\uFF0C\u5E03\u6EE1\u9897\u7C92\u3002\u68D2\u8EAB\u4E0D\u77E5\u7528\u4F55\u7269\u78E8\u5236\uFF0C\u706F\u706B\u4E2D\u5149\u6655\u6D41\u6DCC\uFF0C\u6ED1\u6DA6\u65E0\u6BD4\u3002</p><p>\u5BAB\u4E3B\u6258\u7740\u767E\u82B1\u89C2\u97F3\u6B63\u5F85\u653E\u5979\u4E0A\u53BB\uFF0C\u6BBF\u5916\u7A81\u7136\u54CD\u8D77\u4E00\u4E2A\u6E05\u4E3D\u7684\u5973\u58F0\uFF1A\u300C\u542F\u7980\u5BAB\u4E3B\uFF0C\u6155\u5BB9\u536B\u7684\u5C38\u4F53\u5DF2\u7ECF\u5E26\u5230\u3002\u300D</p><p>\u300C\u554A\uFF1F\u300D\u767E\u82B1\u89C2\u97F3\u4E0E\u5BAB\u4E3B\u540C\u65F6\u53EB\u9053\u3002\u4F46\u767E\u82B1\u89C2\u97F3\u58F0\u95F4\u91CC\u5145\u6EE1\u4E86\u60CA\u9A87\uFF0C\u5BAB\u4E3B\u5374\u662F\u6EE1\u5FC3\u6B22\u559C\u3002</p><p>\u6155\u5BB9\u536B\u7ED3\u6EE1\u51B0\u971C\u7684\u957F\u987B\u7A00\u7A00\u843D\u843D\uFF0C\u6CA1\u6709\u4E00\u4E1D\u751F\u6C14\u3002\u5BAB\u4E3B\u4F38\u811A\u72E0\u72E0\u8E29\u5728\u6155\u5BB9\u536B\u8138\u4E0A\uFF0C\u72C2\u7B11\u9053\uFF1A\u300C\u6B7B\u5F97\u597D\uFF01\u6B7B\u5F97\u597D\uFF01\u6155\u5BB9\u7D2B\u73AB\u5462\uFF1F\u300D</p><p>\u8F7B\u5C18\u4F4E\u58F0\u8BF4\uFF1A\u300C\u6155\u5BB9\u536B\u6B66\u529F\u9AD8\u5F3A\uFF0C\u5C60\u957F\u8001\u8EAB\u8D1F\u91CD\u4F24\u3002\u5C5E\u4E0B\u7B49\u7AED\u529B\u76D8\u67E5\uFF0C\u672A\u627E\u5230\u5B9D\u85CF\u7684\u7EBF\u7D22\u3002\u6155\u5BB9\u7D2B\u73AB\u6740\u6B7B\u5DE8\u77F3\u3001\u731B\u70BD\u4E24\u4F4D\u9999\u4E3B\u7A81\u56F4\u9003\u751F\uFF0C\u970D\u957F\u8001\u6B63\u5E26\u4EBA\u6CBF\u9014\u8FFD\u8D76\u3002\u300D</p><p>\u5BAB\u4E3B\u5FC3\u4E0B\u4E00\u51DB\uFF0C\u5BAB\u4E2D\u4E94\u957F\u8001\u3001\u5341\u4E8C\u9999\u4E3B\u90FD\u662F\u6C5F\u6E56\u4E2D\u4E00\u6D41\u7684\u597D\u624B\uFF0C\u53C8\u662F\u6709\u5907\u800C\u6765\uFF0C\u6CA1\u60F3\u5230\u51FA\u52A8\u4E86\u706B\u3001\u571F\u4E24\u5802\uFF0C\u8FD8\u65E0\u6CD5\u751F\u64D2\u6155\u5BB9\u7D2B\u73AB\uFF0C\u96EA\u5CF0\u795E\u5C3C\u95E8\u4E0B\u7ADF\u7136\u5982\u6B64\u4E86\u5F97\u3002</p><p>\u9ED8\u60F3\u7247\u523B\uFF0C\u5BAB\u4E3B\u677E\u5F00\u811A\uFF0C\u8138\u8272\u4E00\u677F\uFF0C\u300C\u5982\u6B64\u65E0\u80FD\uFF01\u4EA4\u5F85\u7684\u4E24\u4EF6\u4E8B\u4E00\u4EF6\u90FD\u6CA1\u529E\u6210\uFF01\u8F7B\u5C18\uFF0C\u4F60\u53EF\u77E5\u7F6A\uFF1F\u300D</p><p>\u8F7B\u5C18\u8EAB\u5B50\u4E00\u98A4\uFF0C\u4F0F\u5728\u5730\u4E0A\u78D5\u5934\u9053\uFF1A\u300C\u5C5E\u4E0B\u77E5\u7F6A\uFF0C\u6C42\u5BAB\u4E3B\u6069\u5178\uFF0C\u8BA9\u5C5E\u4E0B\u6234\u7F6A\u7ACB\u529F\u3002\u300D</p><p>\u5BAB\u4E3B\u51B7\u51B7\u54FC\u4E86\u4E00\u58F0\uFF0C\u76EE\u5149\u5728\u8F7B\u5C18\u98A4\u6296\u7684\u79C0\u53D1\u3001\u80A9\u5934\u4E0A\u6765\u56DE\u626B\u89C6\u3002\u773C\u89D2\u4E00\u626B\uFF0C\u7A81\u7136\u770B\u5230\u6155\u5BB9\u536B\u988C\u4E0B\u5149\u6E9C\u6E9C\u4E00\u7247\uFF0C\u90A3\u4E1B\u957F\u987B\u5DF2\u5C3D\u6570\u8131\u843D\u3002\u4ED6\u5FC3\u4E0B\u8D77\u7591\uFF0C\u987E\u4E0D\u4E0A\u7406\u4F1A\u8F7B\u5C18\uFF0C\u6478\u624B\u5728\u6155\u5BB9\u536B\u4E0B\u5DF4\u4E0A\u6478\u4E86\u6478\u3002\u89E6\u624B\u5B9B\u5982\u51B0\u971C\uFF0C\u4F46\u5149\u6ED1\u5982\u77F3\uFF0C\u7ADF\u7136\u8FDE\u6BDB\u56CA\u4E5F\u6CA1\u6709\u4E00\u4E2A\u3002</p><p>\u4ED6\u4E00\u8A00\u4E0D\u53D1\u7684\u6495\u5F00\u6155\u5BB9\u536B\u7684\u4E0B\u88F3\uFF0C\u4ED4\u7EC6\u770B\u4E86\u4E00\u773C\uFF0C\u8EAB\u4F53\u987F\u65F6\u50F5\u4F4F\u3002\u534A\u664C\u540E\uFF0C\u4ED6\u7A81\u7136\u4EF0\u5929\u7206\u53D1\u51FA\u4E00\u9635\u72C2\u7B11\uFF0C\u300C\u8FD9\u4E2A\u8001\u4E1C\u897F\u7ADF\u7136\u662F\u4E2A\u592A\u76D1\uFF01\u54C8\u54C8\u54C8\u54C8\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u6536\u655B\u7B11\u5BB9\uFF0C\u5C0F\u5FC3\u7684\u6276\u8D77\u767E\u82B1\u89C2\u97F3\uFF0C\u8138\u4E0A\u7684\u6068\u610F\u6DE1\u4E86\u8BB8\u591A\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u6CA1\u60F3\u5230\u4ED6\u662F\u4E2A\u9609\u4EBA\u2026\u2026\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u5FE7\u4F24\u5730\u770B\u7740\u300C\u4E08\u592B\u300D\u7684\u5C38\u4F53\uFF0C\u6CEA\u6C34\u4E00\u6EF4\u6EF4\u843D\u5728\u9AD8\u8038\u7684\u5706\u4E73\u4E0A\uFF0C\u5BF9\u5BAB\u4E3B\u7684\u8BDD\u604D\u82E5\u672A\u95FB\u3002\u8FD9\u5341\u4F59\u5E74\u6765\uFF0C\u4ED6\u5BF9\u81EA\u5DF1\u5FE0\u5FC3\u803F\u803F\uFF0C\u5F53\u521D\u82E5\u4E0D\u662F\u4ED6\u62FC\u751F\u76F8\u6551\uFF0C\u81EA\u5DF1\u6BCD\u5973\u65E9\u5DF2\u5316\u4E3A\u5200\u4E0B\u51A4\u9B42\u4E86\u3002\u540C\u65F6\u4E5F\u5FC3\u4E2D\u8BB6\u5F02\uFF0C\u96BE\u9053\u5BAB\u4E3B\u662F\u56E0\u4E3A\u81EA\u5DF1\u5AC1\u4E86\u4EBA\u800C\u8981\u60E9\u7F5A\u81EA\u5DF1\uFF1F\u4ED6\u7A76\u7ADF\u662F\u8C01\uFF1F</p><p>\u81EA\u5DF1\u6240\u53D7\u7684\u82E6\u695A\u539F\u6765\u53EA\u662F\u8BEF\u4F1A\u2026\u2026\u8427\u4F5B\u5974\u8D8A\u60F3\u5FC3\u4E2D\u8D8A\u662F\u9178\u695A\uFF0C\u773C\u6CEA\u662F\u8D8A\u6D41\u8D8A\u591A\u3002</p><p>\u5BAB\u4E3B\u4FEF\u8EAB\u62B1\u8D77\u767E\u82B1\u89C2\u97F3\u9999\u8F6F\u7684\u8EAB\u4F53\uFF0C\u76EE\u5149\u8F6C\u5230\u8F7B\u5C18\u8EAB\u4E0A\uFF0C\u7ACB\u523B\u8F6C\u51B7\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u4F60\u968F\u6211\u6765\u3002\u300D</p><p>\u8F7B\u5C18\u8FDE\u5FD9\u8D77\u8EAB\uFF0C\u8DDF\u7740\u5BAB\u4E3B\u8D70\u5230\u5C4F\u98CE\u4E4B\u540E\u3002</p><p>\u5C4F\u98CE\u540E\u662F\u4E00\u6247\u5C0F\u95E8\uFF0C\u5BAB\u4E3B\u4E00\u6273\u673A\u5173\uFF0C\u5C0F\u95E8\u65E0\u58F0\u65E0\u606F\u5730\u6ED1\u5165\u77F3\u58C1\uFF0C\u9732\u51FA\u4E00\u6761\u957F\u957F\u7684\u752C\u9053\u3002</p><p>\u8F7B\u5C18\u8FD8\u662F\u7B2C\u4E00\u6B21\u8FDB\u5165\u8FD9\u4E2A\u5BAB\u4E2D\u7981\u5730\uFF0C\u4E0D\u7531\u5FC3\u4E0B\u5FD0\u5FD1\u3002\u752C\u9053\u4E24\u65C1\u5E76\u5217\u7740\u5341\u95F4\u77F3\u5BA4\uFF0C\u6D1E\u9876\u4E00\u6761\u7EBF\u5D4C\u7740\u5341\u4F59\u679A\u7855\u5927\u7684\u660E\u73E0\uFF0C\u6563\u53D1\u51FA\u6DE1\u6DE1\u7684\u8367\u8F89\u3002</p><p>\u752C\u9053\u8D70\u5C3D\u4E4B\u540E\uFF0C\u9762\u524D\u73B0\u51FA\u4E00\u4E2A\u5706\u5F62\u7684\u5927\u5385\uFF0C\u9AD8\u7EA6\u5341\u4E08\uFF0C\u5F62\u72B6\u6D51\u5706\uFF0C\u5927\u5385\u4E2D\u592E\u662F\u4E00\u4E2A\u534A\u4EBA\u9AD8\u7684\u5706\u53F0\uFF0C\u8272\u5206\u9ED1\u767D\uFF0C\u4EA4\u7EC7\u6210\u4E00\u4E2A\u6D51\u5706\u7684\u592A\u6781\u56FE\u3002\u5927\u5385\u9876\u90E8\u9576\u7740\u4E00\u4E2A\u94F6\u767D\u8272\u7684\u6708\u7259\uFF0C\u4E0D\u77E5\u662F\u4F55\u7269\u5236\u6210\uFF0C\u7ADF\u7136\u50CF\u771F\u6708\u4EAE\u4E00\u822C\u53D1\u51FA\u6E05\u51B7\u7684\u5149\u8292\u3002\u6708\u7259\u5468\u56F4\u5D4C\u6EE1\u5927\u5927\u5C0F\u5C0F\u7684\u660E\u73E0\uFF0C\u5B9B\u5982\u7FA4\u661F\u6367\u6708\u3002</p><p>\u9664\u4E86\u8FDB\u6765\u7684\u90A3\u6761\u752C\u9053\uFF0C\u5468\u56F4\u8FD8\u6709\u56DB\u6247\u77F3\u95E8\u3002\u8F7B\u5C18\u7B97\u7740\u8DEF\u7A0B\u8FDC\u8FD1\uFF0C\u77E5\u9053\u6B64\u65F6\u5DF2\u6DF1\u5165\u6000\u6708\u5CF0\u4E2D\u90E8\uFF0C\u4E0D\u7531\u5FC3\u4E0B\u9A87\u5F02\u3002\u81EA\u5DF1\u88AB\u6536\u5165\u661F\u6708\u6E56\u95E8\u4E0B\u5DF2\u7ECF\u5341\u4F59\u5E74\uFF0C\u5374\u4ECE\u6765\u4E0D\u77E5\u9053\u4E3B\u6BBF\u540E\u8FD8\u6709\u8FD9\u4E48\u5E9E\u5927\u7684\u5EFA\u7B51\u3002</p><p>\u5BAB\u4E3B\u62B1\u7740\u4F24\u75DB\u6B32\u7EDD\u7684\u8D35\u5987\u5F84\u76F4\u8D70\u5165\u5BF9\u9762\u77F3\u95E8\uFF0C\u95E8\u540E\u53C8\u662F\u4E00\u6761\u5411\u4E0A\u7684\u752C\u9053\uFF0C\u4E24\u6392\u5E76\u5217\u7740\u6570\u95F4\u77F3\u5BA4\u3002\u752C\u9053\u5C3D\u5934\u6700\u9AD8\u5904\u662F\u4E00\u4E2A\u534E\u4E3D\u65E0\u6BD4\u7684\u7389\u95E8\uFF0C\u95E8\u6963\u4E0A\u523B\u7740\u4E00\u4E2A\u5C0F\u5C0F\u7684\u7532\u5B57\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5BA4\u5185\u8986\u76D6\u7740\u539A\u539A\u7684\u6BDB\u76AE\uFF0C\u5C3D\u662F\u7EAF\u767D\u989C\u8272\uFF0C\u7ED2\u6BDB\u76F4\u6CA1\u811A\u8E1D\u3002\u6B63\u4E2D\u662F\u4E00\u5F20\u5DE8\u5E8A\uFF0C\u9526\u887E\u7EE3\u88AB\u5B9B\u5982\u82B1\u4E1B\u3002</p><p>\u5BAB\u4E3B\u628A\u767E\u82B1\u89C2\u97F3\u653E\u5728\u5E8A\u4E0A\uFF0C\u62C9\u8FC7\u9526\u88AB\uFF0C\u987A\u624B\u62C2\u4E86\u5979\u7684\u7A74\u9053\u3002\u8427\u4F5B\u5974\u8EAB\u4E0A\u4E09\u5929\u6765\u7B2C\u4E00\u6B21\u78B0\u5230\u6E29\u6696\u7684\u88AB\u8925\uFF0C\u5979\u75B2\u5026\u5DF2\u6781\uFF0C\u4E0D\u8FC7\u65F6\u4FBF\u6C89\u6C89\u7761\u53BB\u3002</p><p>\u5BAB\u4E3B\u76EF\u7740\u8DEA\u5728\u5730\u4E0A\u6218\u6817\u7684\u4FCF\u4E3D\u5973\u5B50\uFF0C\u5FFD\u7136\u4E00\u7B11\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u628A\u8863\u670D\u8131\u4E86\u3002\u300D</p><p>\u8F7B\u5C18\u4E0D\u6562\u6020\u6162\uFF0C\u7ACB\u5373\u89E3\u5F00\u7C73\u9EC4\u8272\u7684\u52B2\u88C5\uFF0C\u892A\u53BB\u88D9\u890C\uFF0C\u7136\u540E\u9664\u4E0B\u8EAB\u524D\u7684\u62B9\u80F8\u3002</p><p>\u5979\u867D\u7136\u5DF2\u5E74\u8FD1\u4E09\u5341\uFF0C\u4F46\u957F\u5E74\u4FEE\u4E60\u5185\u5BB6\u771F\u6C14\uFF0C\u8EAB\u4F53\u4F9D\u7136\u50CF\u5C11\u5973\u822C\u73B2\u73D1\u6709\u81F4\u3002\u5F53\u5BAB\u4E3B\u51B0\u51B7\u7684\u624B\u6307\u78B0\u5230\u80A9\u5934\uFF0C\u8F7B\u5C18\u4E0D\u7531\u98A4\u6296\u4E86\u4E00\u4E0B\u3002</p><p>\u300C\u600E\u4E48\uFF1F\u4E0D\u4E50\u610F\u5417\uFF1F\u300D</p><p>\u8F7B\u5C18\u5FD9\u9053\uFF1A\u300C\u5C5E\u4E0B\u4E0D\u6562\u3002\u300D</p><p>\u300C\u54FC\uFF0C\u6211\u770B\u4F60\u597D\u50CF\u6709\u4E9B\u4E0D\u5F00\u5FC3\u554A\u3002\u300D</p><p>\u8F7B\u5C18\u867D\u7136\u8EAB\u5728\u9B54\u6559\uFF0C\u4F46\u4E00\u5411\u6D01\u8EAB\u81EA\u597D\uFF0C\u5341\u4F59\u5E74\u6765\u4ECE\u672A\u8BA9\u7537\u5B50\u8FD1\u8EAB\uFF0C\u6B64\u65F6\u542C\u5230\u5BAB\u4E3B\u53E3\u6C14\u4E0D\u5584\uFF0C\u8FDE\u5FD9\u52C9\u5F3A\u6324\u51FA\u4E00\u4E2A\u7B11\u8138\uFF0C\u4F4E\u58F0\u8BF4\uFF1A\u300C\u8C22\u4E3B\u5B50\u6069\u5178\u3002\u300D</p><p>\u300C\u600E\u4E48\uFF1F\u8FD8\u8BA9\u6211\u4F3A\u5019\u4F60\u5417\uFF1F\u300D\u5BAB\u4E3B\u61D2\u61D2\u8BF4\u3002</p><p>\u8F7B\u5C18\u8FDE\u5FD9\u819D\u884C\u5230\u5BAB\u4E3B\u8EAB\u524D\uFF0C\u4FEF\u9996\u89E3\u5F00\u4ED6\u7684\u8863\u886B\u3002\u5F53\u770B\u6E05\u5BAB\u4E3B\u8EAB\u4E0B\u72F0\u72DE\u5DE8\u7269\uFF0C\u8F7B\u5C18\u7684\u4FCF\u8138\u987F\u65F6\u5413\u5F97\u96EA\u767D\u3002</p><p>\u90A3\u6839\u9633\u5177\u8FD8\u672A\u52C3\u8D77\u5DF2\u6709\u534A\u5C3A\u957F\u77ED\uFF0C\u9F9F\u5934\u8DB3\u6709\u513F\u62F3\u5927\u5C0F\uFF0C\u7D2B\u7EA2\u53D1\u4EAE\u3002\u68D2\u8EAB\u4E0A\u87BA\u65CB\u72B6\u7ED5\u7740\u4E00\u5708\u5708\u7684\u7A81\u8D77\uFF0C\u50CF\u662F\u5D4C\u7740\u4E00\u9897\u9897\u6697\u7EA2\u8272\u5706\u73E0\u3002\u68D2\u8EAB\u4E2D\u90E8\u9F13\u8D77\u4E00\u5708\u8089\u7624\uFF0C\u7624\u4E0A\u904D\u5E03\u8089\u523A\uFF0C\u7136\u540E\u53C8\u7EC6\u4E86\u4E0B\u53BB\uFF0C\u4E00\u76F4\u5230\u9633\u5177\u6839\u90E8\u3002\u6839\u90E8\u4E0E\u5C0F\u8179\u76F8\u8FDE\u7684\u5730\u65B9\u50CF\u7AE0\u9C7C\u822C\u4F38\u51FA\u4E00\u5708\u957F\u5982\u4EBA\u6307\u7684\u89E6\u624B\uFF0C\u4F46\u6BD4\u624B\u6307\u7EC6\u4E86\u8BB8\u591A\uFF0C\u6570\u4E0D\u6E05\u591A\u5C11\u3002</p><p>\u770B\u5230\u5982\u6B64\u6050\u6016\u7684\u602A\u7269\uFF0C\u8F7B\u5C18\u5FC3\u91CC\u546F\u546F\u4E71\u8DF3\uFF0C\u8111\u4E2D\u4E00\u7247\u6DF7\u4E71\u3002</p><p>\u5BAB\u4E3B\u7B49\u7684\u4E0D\u8010\u70E6\uFF0C\u7565\u4E00\u8FD0\u529F\uFF0C\u90A3\u4E9B\u89E6\u624B\u300C\u556A\u300D\u7684\u4E00\u58F0\u5408\u7D27\uFF0C\u88F9\u4F4F\u68D2\u8EAB\uFF0C\u6324\u5F97\u5BC6\u4E0D\u900F\u98CE\u3002</p><p>\u8F7B\u5C18\u60CA\u9192\u8FC7\u6765\uFF0C\u8270\u96BE\u7684\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u5F20\u53E3\u542B\u4F4F\u5BAB\u4E3B\u7684\u9F9F\u5934\u3002\u5979\u62FC\u547D\u4F38\u76F4\u4E86\u8116\u5B50\uFF0C\u5C3D\u91CF\u541E\u5165\u3002\u4F46\u5BAB\u4E3B\u7684\u9633\u5177\u5B9E\u5728\u8FC7\u4E8E\u957F\u5927\uFF0C\u9F9F\u5934\u5DF2\u7ECF\u6324\u5165\u54BD\u5589\uFF0C\u5634\u5507\u624D\u521A\u521A\u78B0\u5230\u90A3\u4E9B\u8089\u523A\u3002</p><p>\u5979\u5589\u4E2D\u505A\u7740\u541E\u54BD\u52A8\u4F5C\uFF0C\u88AB\u68D2\u8EAB\u7D27\u7D27\u538B\u4F4F\u7684\u820C\u5934\u4F7F\u52B2\u5377\u52A8\uFF0C\u8214\u5F04\u4E0A\u9762\u7684\u9897\u7C92\uFF0C\u67D4\u8F6F\u7684\u7EA2\u5507\u95F4\uFF0C\u5012\u751F\u7684\u8089\u523A\u8D77\u4F0F\u4E0D\u5B9A\u3002\u5BF9\u4E8E\u90A3\u4E9B\u89E6\u624B\uFF0C\u5979\u7684\u53E3\u8154\u5DF2\u7ECF\u65E0\u80FD\u4E3A\u529B\uFF0C\u53EA\u80FD\u77A7\u7740\u5B83\u4EEC\u5728\u773C\u524D\u5FFD\u5C48\u5FFD\u4F38\uFF0C\u793A\u5A01\u822C\u52A8\u4E2A\u4E0D\u505C\u3002</p><p>\u8089\u68D2\u6E10\u6E10\u52C3\u8D77\uFF0C\u575A\u786C\u4F3C\u94C1\uFF0C\u6B7B\u6B7B\u6491\u5F00\u7259\u5173\uFF0C\u9F9F\u5934\u6324\u5728\u5589\u4E2D\uFF0C\u585E\u5F97\u8F7B\u5C18\u5598\u4E0D\u8FC7\u6C14\u6765\u3002\u5FFD\u7136\u5589\u4E2D\u4E00\u677E\uFF0C\u9F9F\u5934\u9000\u4E86\u51FA\u6765\u3002\u68D2\u8EAB\u4E0A\u7684\u9897\u7C92\u6253\u5728\u7259\u9F7F\u4E0A\u9690\u9690\u4F5C\u54CD\u3002</p><p>\u52C3\u8D77\u7684\u8089\u68D2\u957F\u8FD1\u5C3A\u8BB8\uFF0C\u7C97\u5982\u5C0F\u513F\u81C2\uFF0C\u6CBE\u6EE1\u53E3\u6C34\u7684\u7A81\u8D77\u4E00\u9897\u9897\u95EA\u52A8\u7740\u5996\u5F02\u7684\u5149\u8292\u3002</p><p>\u8F7B\u5C18\u8EAB\u4E3A\u5341\u4E8C\u9999\u4E3B\u4E4B\u4E00\uFF0C\u9762\u5BF9\u518D\u5F3A\u786C\u7684\u5BF9\u624B\u4E5F\u672A\u66FE\u6015\u8FC7\uFF0C\u4F46\u6B64\u65F6\u770B\u7740\u8FD9\u6839\u9633\u5177\uFF0C\u5FC3\u91CC\u4E0D\u7531\u6CDB\u8D77\u9635\u9635\u5BD2\u610F\u3002\u5979\u7EC6\u58F0\u54C0\u6C42\u9053\uFF1A\u300C\u6C42\u4E3B\u5B50\u8F7B\u4E00\u4E9B\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u51B7\u7B11\u4E00\u58F0\uFF0C\u300C\u4F60\u81EA\u5DF1\u4E0A\u6765\u5427\uFF0C\u8F7B\u91CD\u968F\u4F60\u3002\u300D</p><p>\u8F7B\u5C18\u9762\u7EA2\u8033\u8D64\u5730\u8DEA\u4F0F\u5728\u5BAB\u4E3B\u8EAB\u4E0A\uFF0C\u4E24\u624B\u5148\u5728\u79D8\u5904\u63C9\u6413\u4E00\u4F1A\u513F\uFF0C\u5F85\u4E45\u672A\u7ECF\u4EBA\u4E8B\u7684\u82B1\u5F84\u6E17\u51FA\u871C\u9732\uFF0C\u624D\u5BF9\u51C6\u9633\u5177\u7F13\u7F13\u5750\u4E0B\u3002</p><p>\u7C97\u5927\u7684\u9F9F\u5934\u6324\u5165\u82B1\u74E3\uFF0C\u50CF\u706B\u70ED\u7684\u62F3\u5934\u4F38\u5165\u4F53\u5185\u3002\u8F7B\u5C18\u54AC\u7D27\u7259\u5173\u7528\u529B\u6C89\u8170\uFF0C\u87BA\u7EB9\u72B6\u7684\u9897\u7C92\u5212\u5728\u8089\u58C1\u4E0A\u9635\u9635\u9178\u75BC\uFF0C\u5F53\u90A3\u4E2A\u8089\u7624\u6CA1\u5165\u82B1\u74E3\uFF0C\u9876\u5728\u9634\u9053\u53E3\u65F6\uFF0C\u8F7B\u5C18\u518D\u4E5F\u5750\u4E0D\u4E0B\u53BB\uFF0C\u53EA\u597D\u8038\u8EAB\u9000\u51FA\uFF0C\u518D\u4F7F\u529B\u5411\u4E0B\u3002\u867D\u7136\u5957\u5F04\u591A\u65F6\uFF0C\u8089\u7624\u59CB\u7EC8\u5361\u5728\u8089\u7A74\u4E4B\u5916\u3002</p><p>\u5979\u5BB3\u6015\u5BAB\u4E3B\u751F\u6C14\uFF0C\u6084\u6084\u770B\u4E86\u4ED6\u4E00\u773C\u3002</p><p>\u5BAB\u4E3B\u4F3C\u4E4E\u5E76\u4E0D\u5728\u610F\u8089\u68D2\u672A\u80FD\u5C3D\u5174\uFF0C\u8138\u4E0A\u6302\u7740\u6DE1\u6DE1\u7684\u7B11\u610F\uFF0C\u4F38\u624B\u73A9\u5F04\u7740\u5979\u7684\u4E73\u5C16\u3002</p><p>\u8F7B\u5C18\u677E\u4E86\u53E3\u6C14\uFF0C\u5706\u81C0\u629B\u4E0A\u629B\u4E0B\uFF0C\u52A8\u4F5C\u66F4\u52A0\u5356\u529B\u3002\u4E60\u60EF\u4E86\u90A3\u4E9B\u9897\u7C92\u4E4B\u540E\uFF0C\u75DB\u695A\u6E10\u6E10\u6D88\u6563\uFF0C\u4E45\u65F7\u7684\u79D8\u5904\u4F20\u6765\u9635\u9635\u76F4\u5165\u9AA8\u9AD3\u7684\u9165\u9EBB\uFF0C\u8089\u7A74\u5185\u6DEB\u6C34\u6DCB\u6F13\u3002</p><p>\u534A\u4E2A\u65F6\u8FB0\u4E4B\u540E\uFF0C\u8F7B\u5C18\u5A07\u8EAF\u4E00\u98A4\uFF0C\u5DF2\u7136\u6CC4\u4E86\u8EAB\u5B50\u3002\u5BAB\u4E3B\u89C1\u72B6\u7FFB\u8EAB\u800C\u8D77\uFF0C\u5C06\u8F7B\u5C18\u538B\u5728\u5E8A\u4E0A\uFF0C\u4E0B\u8EAB\u4E00\u633A\uFF0C\u5DE8\u9633\u72E0\u72E0\u63D2\u5165\u6E29\u6696\u591A\u6C41\u7684\u8089\u7A74\uFF0C\u8FDE\u8089\u7624\u4E5F\u6CA1\u5165\u5176\u4E2D\u3002</p><p>\u8F7B\u5C18\u4F4E\u53EB\u4E00\u58F0\uFF0C\u53EA\u89C9\u67D4\u5AE9\u7684\u8089\u7A74\u88AB\u575A\u786C\u7684\u68D2\u8EAB\u5B8C\u5168\u6491\u6EE1\uFF0C\u9F9F\u5934\u7D27\u7D27\u62B5\u4F4F\u5B50\u5BAB\u5165\u53E3\uFF0C\u53C8\u9178\u53C8\u9EBB\u3002\u9897\u7C92\u3001\u8089\u523A\u78E8\u64E6\u5728\u8089\u58C1\u4E0A\uFF0C\u65E0\u5FAE\u4E0D\u81F3\uFF0C\u7559\u5728\u4F53\u5185\u7684\u89E6\u624B\u50CF\u624B\u6307\u822C\u62C2\u5F04\u7740\u82B1\u8482\uFF0C\u4E0B\u4F53\u5FEB\u611F\u8FDE\u8FDE\u3002</p><p>\u63A5\u7740\u8089\u68D2\u9000\u51FA\uFF0C\u8F7B\u5C18\u624D\u4E5F\u611F\u89C9\u5230\u8089\u523A\u7684\u771F\u6B63\u5A01\u529B\uFF0C\u7C97\u5927\u7684\u8089\u7624\u672C\u5DF2\u7ECF\u6C14\u52BF\u51CC\u4EBA\uFF0C\u6B64\u65F6\u4E0A\u9762\u5BC6\u5E03\u7684\u5012\u523A\u4E00\u6839\u6839\u90FD\u52FE\u5728\u8089\u58C1\u4E0A\uFF0C\u51E0\u4E4E\u628A\u5979\u7684\u9B42\u9B44\u90FD\u52FE\u4E86\u51FA\u6765\u3002\u8089\u7624\u53EA\u8FDB\u51FA\u51E0\u4E0B\uFF0C\u8F7B\u5C18\u4FBF\u5C16\u53EB\u7740\u6D51\u8EAB\u98A4\u6296\uFF0C\u9634\u7CBE\u55B7\u6D8C\u3002</p><p>\u5BAB\u4E3B\u51B7\u51B7\u4E00\u7B11\uFF0C\u4E0B\u8EAB\u731B\u7136\u4E00\u633A\uFF0C\u5C31\u5728\u9634\u7CBE\u55B7\u51FA\u7684\u540C\u65F6\uFF0C\u72E0\u72E0\u523A\u5165\uFF0C\u8FD9\u6B21\u8FDE\u89E6\u624B\u7684\u5C16\u7AEF\u4E5F\u6CA1\u5165\u8F7B\u5C18\u4F53\u5185\u3002</p><p>\u8F7B\u5C18\u6EE1\u8138\u6F6E\u7EA2\uFF0C\u661F\u7738\u534A\u7741\u534A\u95ED\uFF0C\u624B\u811A\u65E0\u529B\u7684\u5FAE\u5FAE\u62BD\u52A8\uFF0C\u611F\u53D7\u7740\u90A3\u80A1\u83AB\u5927\u7684\u5FEB\u611F\uFF0C\u53E3\u91CC\u53D1\u51FA\u65AD\u65AD\u7EED\u7EED\u7684\u547B\u541F\u3002</p><p>\u5BAB\u4E3B\u53C8\u62BD\u9001\u7247\u523B\uFF0C\u5F85\u5979\u8EAB\u5B50\u53C8\u4E00\u6B21\u706B\u70ED\u8D77\u6765\uFF0C\u7ACB\u5373\u633A\u8170\u957F\u9A71\u76F4\u5165\u3002\u4ED6\u7684\u9633\u5177\u65E9\u5DF2\u62B5\u81F3\u9634\u9053\u672B\u7AEF\uFF0C\u8FD9\u6B21\u523A\u5165\u4ED6\u6CA1\u6709\u7ACB\u523B\u62D4\u51FA\uFF0C\u800C\u662F\u7EE7\u7EED\u524D\u4F38\u3002\u72ED\u5C0F\u7684\u5B50\u5BAB\u53E3\u88AB\u9F9F\u5934\u6324\u5F97\u8FDE\u8FDE\u5012\u9000\uFF0C\u9634\u9053\u5185\u518D\u6CA1\u6709\u4E00\u4E1D\u7A7A\u9699\u3002</p><p>\u8F7B\u5C18\u98A4\u6296\u7740\u7B49\u5F85\u53C8\u4E00\u6B21\u9AD8\u6F6E\uFF0C\u5FFD\u7136\u9634\u9053\u53E3\u5904\u4E00\u7D27\uFF0C\u90A3\u4E9B\u6CA1\u5165\u4F53\u5185\u7684\u89E6\u624B\u7FFB\u5377\u8FC7\u6765\uFF0C\u52FE\u4F4F\u9634\u9053\u53E3\u7684\u5AE9\u8089\uFF0C\u5411\u5916\u626F\u52A8\uFF0C\u8089\u68D2\u987A\u7740\u88AB\u626F\u76F4\u7684\u8089\u58C1\u76F4\u5165\u8089\u7A74\u6DF1\u5904\u3002\u63A5\u7740\u4F53\u5185\u4E00\u9707\uFF0C\u9F9F\u5934\u5DF2\u7ECF\u6324\u5165\u5BAB\u9888\u3002</p><p>\u8F7B\u5C18\u75DB\u5F97\u5C16\u53EB\u8D77\u6765\uFF0C\u300C\u4E3B\u5B50\u2026\u2026\u4E3B\u5B50\u2026\u2026\u8F7B\u4E00\u4E9B\u2026\u2026\u522B\u518D\u8FDB\u4E86\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u51B7\u7B11\u9053\uFF1A\u300C\u4E0D\u8212\u670D\u5417\uFF1F\u300D</p><p>\u8F7B\u5C18\u5FCD\u75DB\u9053\uFF1A\u300C\u4F3A\u5019\u4E3B\u5B50\u2026\u2026\u662F\u5C5E\u4E0B\u7684\u798F\u6C14\u2026\u2026\u300D</p><p>\u300C\u4E0D\u613F\u610F\u4E3B\u5B50\u8FD9\u6837\u64CD\u4F60\u5417\uFF1F\u300D</p><p>\u8F7B\u5C18\u542B\u6CEA\u8BF4\uFF1A\u300C\u5C5E\u4E0B\u4EBA\u662F\u4E3B\u5B50\u7684\uFF0C\u4E3B\u5B50\u60F3\u600E\u4E48\u2026\u2026\u64CD\uFF0C\u5C31\u600E\u4E48\u64CD\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u54FC\u4E86\u4E00\u58F0\uFF0C\u8170\u8EAB\u4F7F\u529B\u3002\u8F7B\u5C18\u867D\u7136\u6B66\u529F\u9AD8\u5F3A\uFF0C\u4E5F\u75DB\u5F97\u9762\u5BB9\u626D\u66F2\uFF0C\u4F46\u53EA\u80FD\u54AC\u7259\u6B7B\u6B7B\u5FCD\u7740\u3002</p><p>\u8089\u68D2\u6495\u5F00\u5BAB\u9888\u6324\u5165\u5B50\u5BAB\uFF0C\u7EC8\u4E8E\u505C\u4E86\u4E0B\u6765\u3002\u8F7B\u5C18\u75DB\u5F97\u6B7B\u53BB\u6D3B\u6765\uFF0C\u521A\u521A\u677E\u4E86\u4E00\u53E3\u6C14\uFF0C\u5FFD\u7136\u4F53\u5185\u4E00\u70ED\uFF0C\u8089\u68D2\u50CF\u706B\u67F1\u822C\u7099\u70ED\u8D77\u6765\u3002\u63A5\u7740\u771F\u6C14\u50CF\u88AB\u9633\u5177\u5438\u5F15\u4E00\u822C\u6D8C\u51FA\u4E39\u7530\u3002</p><p>\u8F7B\u5C18\u5927\u60CA\u5931\u8272\uFF0C\u8FDE\u5FD9\u6491\u8D77\u8EAB\u5B50\uFF0C\u60F3\u9000\u51FA\u8089\u68D2\u3002\u8FD9\u65F6\u5979\u624D\u53D1\u73B0\u81EA\u5DF1\u624B\u811A\u8F6F\u7EF5\u7EF5\u6CA1\u6709\u4E00\u70B9\u529B\u9053\u3002\u800C\u521A\u624D\u7684\u4E24\u6B21\u9AD8\u6F6E\u4F7F\u5979\u5168\u8EAB\u6536\u7D27\uFF0C\u8FDE\u5728\u4F53\u5185\u4E0D\u65AD\u6D41\u52A8\u7684\u771F\u6C14\u4E5F\u90FD\u84C4\u5165\u4E86\u4E39\u7530\u3002</p><p>\u8F7B\u5C18\u60F3\u653E\u58F0\u5927\u53EB\uFF0C\u53EF\u820C\u5934\u4E5F\u4E0D\u542C\u4F7F\u5524\u3002\u5979\u5446\u5446\u770B\u7740\u5BAB\u4E3B\uFF0C\u4E0D\u660E\u767D\u4ED6\u4E3A\u4EC0\u4E48\u8981\u5438\u53D6\u81EA\u5DF1\u529F\u529B\u3002\u5373\u4F7F\u628A\u81EA\u5DF1\u643E\u5E72\uFF0C\u4ED6\u6700\u591A\u53EA\u80FD\u5438\u53D6\u81EA\u5DF1\u4E00\u534A\u7684\u529F\u529B\uFF0C\u53E6\u4E00\u534A\u90FD\u5728\u91C7\u8865\u4E2D\u767D\u767D\u6D6A\u8D39\u3002\u81EA\u5DF1\u8EAB\u4E3A\u4E0B\u5C5E\uFF0C\u81EA\u7136\u4F1A\u62FC\u6B7B\u6548\u529B\uFF0C\u4F55\u5FC5\u8D39\u6B64\u5468\u6298\uFF1F</p><p>\u90A3\u5F20\u82CD\u767D\u7684\u9762\u5BB9\u6E10\u6E10\u6A21\u7CCA\uFF0C\u7EC8\u4E8E\u6D88\u5931\u5728\u9ED1\u6697\u91CC\u2026\u2026</p>',64);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
