import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_08" tabindex="-1"><a class="header-anchor" href="#_08" aria-hidden="true">#</a> 08</h1><p>\u6155\u5BB9\u7D2B\u73AB\u5728\u7EAA\u5E9C\u4F4F\u4E86\u4E00\u591C\uFF0C\u7B2C\u4E8C\u5929\u4E0E\u7EAA\u7709\u59A9\u5E76\u9A91\u5357\u4E0B\u3002\u4E24\u5973\u4E00\u8DEF\u663C\u884C\u591C\u4F0F\uFF0C\u98CE\u9910\u9732\u5BBF\uFF0C\u53D7\u5C3D\u5954\u6CE2\u4E4B\u82E6\u3002\u4E4D\u7136\u4ECE\u5C06\u519B\u5E9C\u7684\u9526\u8863\u7389\u98DF\u843D\u5230\u8352\u6548\u91CE\u5916\uFF0C\u5A07\u602F\u602F\u7684\u7EAA\u7709\u59A9\u56FA\u7136\u6CA1\u6709\u4E00\u53E5\u6028\u8A00\uFF0C\u6155\u5BB9\u7D2B\u73AB\u4E5F\u672A\u8BF4\u8FC7\u8C22\u5B57\uFF0C\u4E24\u4EBA\u90FD\u628A\u6B64\u4E8B\u89C6\u4E3A\u7406\u6240\u5F53\u7136\u3002</p><p>\u597D\u5728\u4E24\u5973\u5185\u529F\u4E0D\u5F31\uFF0C\u5C3D\u6297\u5F97\u4F4F\u98CE\u5BD2\u3002</p><p>\u8FDB\u5165\u9655\u5357\u540E\uFF0C\u8DEF\u4E0A\u9887\u4E3A\u4E0D\u9756\u3002\u4ECA\u5E74\u5929\u6C14\u4E25\u5BD2\uFF0C\u585E\u5916\u7267\u6C11\u9A6C\u755C\u591A\u6B7B\uFF0C\u56E0\u6B64\u5357\u4E0B\u63A0\u593A\u5B9A\u5C45\u519C\u6237\u7684\u98DF\u7269\u8D22\u4EA7\u3002\u5360\u636E\u5173\u4E2D\u7684\u79E6\u519B\u8FDE\u6218\u8FDE\u8D25\uFF0C\u6839\u672C\u6321\u4E0D\u4F4F\u5982\u72FC\u4F3C\u864E\u7684\u7267\u65CF\u3002\u5B98\u5E9C\u81EA\u987E\u4E0D\u6687\uFF0C\u90A3\u8FD8\u6709\u5DE5\u592B\u8D48\u6D4E\u707E\u6C11\uFF1F\u9655\u5357\u591A\u5C71\u4E4B\u5730\uFF0C\u672C\u5C31\u8D2B\u7620\uFF0C\u8FD9\u4E00\u756A\u4FB5\u6270\u4E4B\u540E\uFF0C\u987F\u65F6\u6D41\u6C11\u56DB\u8D77\u3002</p><p>\u8DEF\u4E0A\u6709\u51E0\u8D77\u4EA6\u6C11\u4EA6\u532A\u7684\u76D7\u8D3C\u89C1\u662F\u4E24\u4E2A\u6F02\u4EAE\u5973\u5B69\u5B64\u8EAB\u884C\u8DEF\uFF0C\u60F3\u635E\u4E9B\u4FBF\u5B9C\uFF0C\u4F46\u8FD9\u4E9B\u62FF\u60EF\u9504\u5934\u3001\u9489\u8019\u7684\u4E4C\u5408\u4E4B\u4F17\u600E\u662F\u96EA\u5CF0\u795E\u5C3C\u4E24\u4F4D\u9AD8\u5F92\u7684\u5BF9\u624B\u3002\u7EAA\u7709\u59A9\u548C\u6155\u5BB9\u7D2B\u73AB\u7565\u65BD\u5C0F\u6280\uFF0C\u6CE2\u6F9C\u4E0D\u60CA\u7684\u7A7F\u5DDE\u8FC7\u5E9C\uFF0C\u4E8C\u6708\u4E8C\u5341\u4E5D\uFF0C\u4E24\u4EBA\u62B5\u8FBE\u6E05\u5316\u3002</p><p>\u8FDB\u5165\u8700\u5730\uFF0C\u4E24\u4EBA\u90FD\u677E\u4E86\u4E00\u53E3\u6C14\u3002\u76F8\u6BD4\u4E8E\u4E2D\u539F\u4E89\u6218\u4E0D\u4F11\uFF0C\u5DDD\u8700\u7684\u5E73\u9759\u65E0\u5F02\u4E8E\u5929\u5E9C\u3002</p><p>\u5230\u6E05\u5316\u540E\uFF0C\u4E0D\u4F46\u7EAA\u7709\u59A9\u8EAB\u4E0A\u6240\u5E26\u7684\u5927\u7B14\u94F6\u4E24\u82B1\u4E2A\u5E72\u51C0\uFF0C\u8FDE\u6155\u5BB9\u7D2B\u73AB\u5F53\u65E5\u5728\u7EE6\u53BF\u5B98\u5E93\u76D7\u51FA\u7684\u91D1\u94F6\u4E5F\u88AB\u8FD9\u4E2A\u4E09\u5E08\u59D0\u7528\u5F97\u5DEE\u4E0D\u591A\u4E86\u3002\u8FD9\u5012\u4E0D\u662F\u7EAA\u7709\u59A9\u81EA\u5DF1\u4EAB\u7528\uFF0C\u800C\u662F\u5979\u89C1\u4E0D\u5F97\u707E\u6C11\u7684\u60E8\u72B6\uFF0C\u4E00\u8DEF\u65BD\u820D\u3002</p><p>\u5728\u5BA2\u5E97\u6155\u5BB9\u7D2B\u73AB\u6570\u4E86\u6570\u94F6\u5B50\uFF0C\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u6211\u8BB0\u5F97\u51FA\u6765\u7684\u65F6\u5019\u54B1\u4EEC\u5E26\u4E86\u6709\u8FD1\u5343\u4E24\u94F6\u5B50\u5427\uFF0C\u6709\u6211\u4E00\u534A\u91CD\u5462\u3002\u4F60\u8FD8\u8BF4\u5E26\u5F97\u591A\u4E86\uFF0C\u591F\u54B1\u4EEC\u8D70\u5230\u516B\u4E07\u91CC\u5916\u7684\u6606\u4ED1\u5C71\u3002\u77A7\uFF0C\u8FD9\u4F1A\u513F\u8FD8\u5269\u5341\u4E8C\u4E24\u2026\u2026\u300D</p><p>\u5176\u5B9E\u4E0D\u53EA\u7EAA\u7709\u59A9\u4ECE\u6765\u4E0D\u8FC7\u95EE\u8FD9\u4E9B\u4E8B\uFF0C\u6155\u5BB9\u7D2B\u73AB\u4EE5\u524D\u4E5F\u4EE5\u4E3A\u94F6\u5B50\u53EA\u662F\u7528\u6765\u6253\u94F6\u5668\u3001\u9996\u9970\u7684\u3002\u82E5\u975E\u7ECF\u6B64\u5927\u53D8\uFF0C\u5979\u8FD8\u4E0D\u77E5\u9053\u81EA\u5DF1\u5F53\u65E5\u7528\u7684\u5C0F\u91D1\u7BAD\uFF0C\u4E00\u652F\u5C31\u591F\u5E73\u5E38\u4EBA\u5BB6\u534A\u5E74\u7684\u5F00\u9500\uFF0C\u73B0\u5728\u60F3\u60F3\u5C31\u89C9\u5F97\u540E\u6094\u3002</p><p>\u7EAA\u7709\u59A9\u51D1\u8FC7\u6765\u7741\u7740\u6BD4\u6155\u5BB9\u7D2B\u73AB\u66F4\u5929\u771F\u65E0\u90AA\u7684\u5927\u773C\u770B\u4E86\u770B\uFF0C\u5C0F\u5FC3\u7FFC\u7FFC\u7684\u95EE\u9053\uFF1A\u300C\u7701\u7740\u4E9B\uFF0C\u591F\u54B1\u4EEC\u5230\u98D8\u6885\u5CF0\u4E86\u5427\uFF1F\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u53F9\u4E86\u53E3\u6C14\uFF0C\u6536\u8D77\u94F6\u5B50\uFF1A\u300C\u54B1\u4EEC\u4E24\u4E2A\u53C8\u5403\u4E0D\u4E86\u591A\u5C11\uFF0C\u5DEE\u4E0D\u591A\u591F\u5427\u3002\u300D</p><p>\u94F6\u4E24\u591A\u5C11\u7EAA\u7709\u59A9\u5E76\u4E0D\u5728\u610F\uFF0C\u5979\u6CA1\u628A\u8FD9\u4E8B\u653E\u5728\u5FC3\u4E0A\uFF0C\u300C\u7D2B\u73AB\uFF0C\u4F60\u6628\u5929\u8BF4\u7EC3\u529F\u65F6\u89C9\u5F97\u6709\u4E9B\u5F02\u6837\uFF0C\u662F\u600E\u4E48\u56DE\u4E8B\uFF1F\u8FD9\u4F1A\u513F\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u7709\u5934\u5FAE\u76B1\uFF0C\u300C\u6211\u4E5F\u8BF4\u4E0D\u6E05\u695A\uFF0C\u597D\u50CF\u805A\u6C14\u65F6\u53D8\u5F97\u66F4\u6162\u4E86\uFF0C\u4E39\u7530\u597D\u50CF\u76DB\u4E0D\u4E0B\u4F3C\u7684\u5411\u5916\u6EA2\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u5E76\u672A\u7EC3\u8FC7\u51E4\u51F0\u5B9D\u5178\uFF0C\u4E0D\u77E5\u5176\u4E2D\u7684\u8BC0\u8981\uFF0C\u95FB\u8A00\u7EC6\u60F3\u7247\u523B\uFF0C\u300C\u662F\u4E0D\u662F\u8FC7\u4E8E\u6C42\u6210\uFF0C\u7EC3\u5F97\u592A\u52E4\uFF0C\u51FA\u4E86\u5C94\u5B50\uFF1F\u7A74\u9053\u4E0A\u6709\u6CA1\u6709\u611F\u89C9\uFF1F\u300D</p><p>\u7D2B\u73AB\u6447\u4E86\u6447\u5934\uFF0C\u300C\u51E4\u51F0\u5B9D\u5178\u4E0D\u8D70\u7A74\u9053\u7684\u3002\u4E5F\u6CA1\u4EC0\u4E48\u4E0D\u597D\u7684\u611F\u89C9\uFF0C\u5C31\u50CF\u2026\u2026\u5C31\u50CF\u62E3\u4E86\u4E00\u5927\u5806\u94F6\u5B50\uFF0C\u80CC\u4E0D\u52A8\uFF01\u300D</p><p>\u7EAA\u7709\u59A9\u6251\u54E7\u4E00\u58F0\u7B11\u4E86\u51FA\u6765\uFF0C\u300C\u4F60\u5440\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u4E5F\u7B11\u4E86\u8D77\u6765\u3002</p><p>\u8FC7\u4E86\u7247\u523B\uFF0C\u7D2B\u73AB\u8138\u4E0A\u7684\u7B11\u610F\u6E10\u9000\u3002\u60F3\u8D77\u7236\u4EB2\u7684\u60E8\u6B7B\uFF0C\u6BCD\u4EB2\u843D\u5165\u654C\u624B\uFF0C\u4E0D\u7531\u67D4\u80A0\u767E\u8F6C\u3002</p><p>\u300C\u522B\u62C5\u5FC3\uFF0C\u56DE\u5C71\u95EE\u95EE\u5E08\u7236\u597D\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u52C9\u5F3A\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u4F46\u6CA1\u6709\u8BF4\u8BDD\u3002</p><p>\u7EAA\u7709\u59A9\u67D4\u58F0\u8BF4\uFF1A\u300C\u53BB\u5916\u9762\u6563\u6563\u6B65\u597D\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u70B9\u4E86\u70B9\u5934\uFF0C\u957F\u4E86\u957F\u5410\u4E86\u53E3\u6C14\uFF0C\u7EA4\u624B\u5728\u684C\u4E0A\u8F7B\u8F7B\u4E00\u62CD\uFF0C\u53C9\u7740\u5C0F\u86EE\u8170\u8BF4\u9053\uFF1A\u300C\u5927\u4E08\u592B\u751F\u5728\u5929\u5730\u95F4\uFF0C\u81EA\u5F53\u5FEB\u610F\u6069\u4EC7\uFF0C\u6C14\u51B2\u5317\u6597\uFF0C\u4F55\u5FC5\u505A\u5C0F\u513F\u5973\u4E4B\u53F9\uFF01\u300D</p><p>\u58F0\u97F3\u521A\u52B2\u6709\u529B\uFF0C\u8001\u6C14\u6A2A\u79CB\uFF0C\u628A\u7EAA\u7709\u59A9\u5413\u4E86\u4E00\u8DF3\u3002</p><p>\u7D2B\u73AB\u8BF4\u5B8C\uFF0C\u683C\u683C\u7B11\u4E86\u8D77\u6765\uFF0C\u300C\u5C0F\u83BA\u5C0F\u9E42\u90A3\u5929\u7684\u53E3\u6C14\u5C31\u662F\u8FD9\u6837\u3002\u300D\u63A5\u7740\u628A\u90A3\u65E5\u767D\u6C0F\u59D0\u59B9\u7684\u8C6A\u8A00\u58EE\u8BED\u544A\u8BC9\u5E08\u59D0\u3002</p><p>\u8BF4\u5B8C\u540E\u5979\u671B\u7740\u7A97\u5916\uFF0C\u6C89\u9ED8\u7247\u523B\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u6211\u542C\u4E86\u4E4B\u540E\u597D\u611F\u52A8\u2026\u2026\u300D</p><p>\u7EAA\u7709\u59A9\u9053\uFF1A\u300C\u5979\u4EEC\u8BF4\u7684\u4E0D\u9519\uFF0C\u4F60\u7B11\u4EC0\u4E48\u5462\uFF1F\u300D</p><p>\u300C\u592A\u53EF\u7231\u4E86\uFF0C\u5979\u4EEC\u6BD4\u6211\u8FD8\u5C0F\u534A\u5C81\u5462\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u6015\u7D2B\u73AB\u53C8\u60F3\u8D77\u4F24\u5FC3\u4E8B\uFF0C\u8FDE\u5FD9\u62C9\u7740\u5979\u51FA\u95E8\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5929\u8272\u5DF2\u665A\uFF0C\u8857\u9053\u7A7A\u843D\u843D\u6CA1\u6709\u4EC0\u4E48\u4EBA\u5F71\u3002\u5979\u4EEC\u53EA\u662F\u968F\u4FBF\u8D70\u8D70\uFF0C\u4E5F\u4E0D\u5728\u4E4E\u5E02\u9762\u51B7\u6E05\u3002\u4E24\u5973\u62C9\u7740\u624B\u5E76\u80A9\u800C\u884C\uFF0C\u6709\u4E00\u642D\u6CA1\u4E00\u642D\u7684\u8BF4\u4E9B\u95F2\u8BDD\uFF0C\u5F7C\u6B64\u6709\u79CD\u4EB2\u60C5\u822C\u7684\u6E29\u99A8\u3002</p><p>\u8DEF\u8FB9\u6709\u5BB6\u9152\u8086\u8FD8\u4EAE\u7740\u706F\u706B\uFF0C\u6155\u5BB9\u7D2B\u73AB\u773C\u775B\u4E00\u4EAE\uFF0C\u300C\u5E08\u59D0\uFF0C\u54B1\u4EEC\u53BB\u559D\u9152\u5427\uFF01\u300D</p><p>\u4E24\u4E2A\u5973\u5B69\u5B50\u5B66\u4EBA\u5BB6\u559D\u9152\uFF0C\u7EAA\u7709\u59A9\u54D1\u7136\u5931\u7B11\uFF0C\u4F46\u8FD8\u662F\u6E29\u5A49\u5730\u968F\u5E08\u59B9\u8D70\u5165\u9152\u8086\u3002</p><p>\u8001\u677F\u89C1\u662F\u4E24\u4E2A\u7F8E\u8C8C\u5973\u5B50\uFF0C\u4E0D\u7531\u6123\u4E86\u4E00\u4E0B\uFF0C\u8FCE\u4E0A\u6765\u5C0F\u5FC3\u5730\u95EE\uFF1A\u300C\u4E24\u4F4D\u8981\u7528\u4E9B\u4EC0\u4E48\uFF1F\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u6307\u4E86\u6307\u65C1\u8FB9\u684C\u4E0A\uFF0C\u300C\u8DDF\u4ED6\u4EEC\u4E00\u6837\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u62FF\u51FA\u4E1D\u5DFE\u628A\u684C\u6905\u62B9\u4E86\u4E00\u904D\uFF0C\u7136\u540E\u5C06\u4E1D\u5DFE\u4E00\u56E2\u6254\u5230\u5899\u89D2\u3002\u6155\u5BB9\u7D2B\u73AB\u7B11\u9053\uFF1A\u300C\u8FD9\u4E00\u8DEF\u4F60\u90FD\u6254\u4E86\u767E\u5341\u6761\u624B\u7EE2\u4E86\u5427\uFF0C\u54EA\u6709\u8FD9\u4E48\u5E72\u51C0\u7684\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u8138\u4E0A\u4E00\u7EA2\uFF0C\u53EA\u8BF4\u4E86\u53E5\uFF0C\u300C\u8FD9\u91CC\u5012\u8FD8\u5E72\u51C0\u3002\u300D</p><p>\u65C1\u8FB9\u90A3\u5F20\u684C\u5B50\u5750\u4E86\u56DB\u3001\u4E94\u4E2A\u4EBA\uFF0C\u5DF2\u7ECF\u559D\u5F97\u534A\u9189\uFF0C\u770B\u5230\u4E24\u5973\u7684\u8273\u8272\u90FD\u662F\u76EE\u77AA\u53E3\u5446\u3002</p><p>\u7EAA\u7709\u59A9\u5F88\u5C11\u5728\u5916\u9762\u629B\u5934\u9732\u9762\uFF0C\u5728\u8DEF\u4E0A\u4E3A\u4E86\u906E\u5C18\uFF0C\u4E00\u76F4\u5E26\u7740\u9762\u7EB1\u3002\u6B64\u65F6\u88AB\u51E0\u4E2A\u7537\u4EBA\u8086\u65E0\u5FCC\u60EE\u7684\u4E0A\u4E0B\u6253\u91CF\uFF0C\u4E0D\u7531\u9762\u7EA2\u8FC7\u8033\u3002\u5979\u628A\u5934\u626D\u5230\u4E00\u8FB9\uFF0C\u4E0D\u6562\u63A5\u89E6\u4ED6\u4EEC\u7684\u76EE\u5149\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u79C0\u7709\u4E00\u626C\uFF0C\u5C0F\u624B\u671D\u684C\u4E0A\u91CD\u91CD\u5730\u4E00\u62CD\uFF0C\u5A07\u58F0\u559D\u9053\uFF1A\u300C\u770B\u4EC0\u4E48\u770B\uFF01\u6EDA\u51FA\u53BB\uFF01\u300D</p><p>\u90A3\u51E0\u4E2A\u4EBA\u5DF2\u7ECF\u6709\u4E86\u516B\u5206\u9189\u610F\uFF0C\u89C1\u8FD9\u4E2A\u82B1\u74E3\u4F3C\u7684\u7F8E\u4EBA\u513F\u5927\u53D1\u96CC\u5A01\uFF0C\u7F8E\u6001\u5341\u8DB3\uFF0C\u90FD\u662F\u5FC3\u75D2\u96BE\u6414\u3002\u5F53\u5148\u4E00\u4E2A\u6447\u6447\u6643\u6643\u7AD9\u4E86\u8D77\u6765\uFF0C\u4F38\u624B\u5C31\u671D\u7D2B\u73AB\u8138\u4E0A\u6478\u53BB\uFF0C\u9189\u718F\u718F\u5730\u8BF4\uFF1A\u300C\u5C0F\u7F8E\u4EBA\u513F\uFF0C\u4F60\u53EB\u2026\u2026\u300D</p><p>\u8BDD\u6CA1\u8BF4\u5B8C\uFF0C\u6574\u4E2A\u4EBA\u5FFD\u7136\u51CC\u7A7A\u98DE\u8D77\uFF0C\u6295\u5230\u5E97\u5916\u7684\u9ED1\u6697\u91CC\uFF0C\u534A\u664C\u624D\u300C\u546F\u300D\u7684\u4E00\u58F0\u843D\u5728\u5730\u4E0A\uFF0C\u7136\u540E\u518D\u65E0\u58F0\u606F\u3002</p><p>\u5269\u4E0B\u7684\u51E0\u4E2A\u4EBA\u6123\u6123\u770B\u7740\u7D2B\u73AB\uFF0C\u4E0D\u77E5\u9053\u53D1\u751F\u4E86\u4EC0\u4E48\u4E8B\u3002</p><p>\u300C\u8FD8\u4E0D\u6EDA\uFF01\u300D</p><p>\u8FD9\u7FA4\u4EBA\u90FD\u662F\u5F53\u5730\u75DE\u5B50\uFF0C\u4E00\u5411\u6A2A\u884C\u9738\u9053\u60EF\u4E86\uFF0C\u5176\u4E2D\u4E00\u6761\u58EE\u6C49\u6012\u543C\u4E00\u58F0\u2014\u2014\u4ED6\u6CA1\u6562\u5BF9\u7D2B\u73AB\u4E0B\u624B\uFF0C\u800C\u662F\u671D\u80CC\u5BF9\u7740\u4ED6\u7684\u7EAA\u7709\u59A9\u767D\u7389\u822C\u7684\u67D4\u9888\u4E2D\u6253\u53BB\u3002</p><p>\u5927\u6C49\u7C97\u58EE\u7684\u8EAB\u4F53\u4E5F\u7ACB\u523B\u98DE\u8D77\uFF0C\u6295\u5230\u95E8\u5916\u3002\u90A3\u4E2A\u516C\u4E3B\u822C\u5C0A\u8D35\u7684\u5973\u5B50\u9759\u9759\u5750\u5728\u90A3\u91CC\uFF0C\u597D\u50CF\u4EC0\u4E48\u90FD\u6CA1\u505A\uFF0C\u53EA\u662F\u624B\u91CC\u591A\u4E86\u5757\u6D01\u767D\u7684\u4E1D\u5DFE\u3002</p><p>\u7EAA\u7709\u59A9\u62FF\u7740\u88F9\u624B\u7684\u4E1D\u5DFE\u72B9\u8C6B\u7740\u8981\u4E0D\u8981\u6254\u6389\u3002\u8FD8\u6709\u4E09\u4E2A\u4EBA\uFF0C\u6216\u8005\u7B49\u4E00\u4E0B\u518D\u6254\u597D\u4E86\u3002</p><p>\u5979\u5FC3\u6148\u624B\u8F6F\uFF0C\u5E76\u672A\u50CF\u7D2B\u73AB\u4E00\u6837\u628A\u4EBA\u7529\u5230\u5341\u51E0\u4E08\u5916\uFF0C\u4E5F\u4E0D\u7BA1\u90A3\u91CC\u662F\u5899\u662F\u5730\uFF0C\u4F1A\u4E0D\u4F1A\u628A\u4EBA\u6454\u6B7B\u3002\u90A3\u4E2A\u5927\u6C49\u843D\u5728\u95E8\u5916\u4F17\u4EBA\u80FD\u770B\u5230\u7684\u5730\u65B9\uFF0C\u8DB4\u5728\u5730\u4E0A\u54CE\u5537\u54CE\u5537\u53EB\u4E2A\u4E0D\u505C\u3002</p><p>\u5E97\u4E3B\u4EBA\u770B\u7684\u77A0\u76EE\u7ED3\u820C\uFF0C\u4E0D\u77E5\u9053\u4ECA\u5929\u5E97\u91CC\u6765\u7684\u8FD9\u4E24\u4F4D\u662F\u4ED9\u5973\u8FD8\u662F\u5996\u7CBE\u3002</p><p>\u5269\u4E0B\u7684\u4E09\u4E2A\u4EBA\u8FD8\u6CA1\u660E\u767D\u8FC7\u6765\uFF0C\u538B\u7740\u55D3\u5B50\u558A\u9053\uFF1A\u300C\u5927\u725B\uFF0C\u5927\u725B\u2026\u2026\u8FD9\u662F\u600E\u4E48\u56DE\u4E8B\uFF1F\u300D</p><p>\u5927\u725B\u4E00\u8FB9\u53EB\u75DB\uFF0C\u4E00\u8FB9\u8BF4\uFF1A\u300C\u2026\u2026\u5996\u2026\u2026\u5996\u7CBE\u2026\u2026\u5979\u4EEC\u4F1A\u5996\u672F\u2026\u2026\u300D</p><p>\u4E09\u4E2A\u4EBA\u5012\u62BD\u4E00\u53E3\u51C9\u6C14\uFF0C\u50BB\u50BB\u770B\u7740\u8FD9\u4E24\u4E2A\u7F8E\u82E5\u5929\u4ED9\u7684\u5973\u5B50\u3002\u6CA1\u60F3\u5230\u559D\u9152\u4F1A\u559D\u51FA\u5996\u7CBE\u6765\uFF0C\u8FD9\u53EF\u600E\u4E48\u662F\u597D\uFF1F</p><p>\u7D2B\u73AB\u53C8\u91CD\u91CD\u4E00\u62CD\uFF0C\u5A07\u559D\u9053\uFF1A\u300C\u8FD8\u4E0D\u5FEB\u6EDA\uFF01\u300D</p><p>\u90A3\u4E09\u4E2A\u4EBA\u62B1\u5934\u9F20\u7A9C\uFF0C\u7EAA\u7709\u59A9\u8FDE\u5FD9\u6254\u6389\u7EF8\u5DFE\uFF0C\u770B\u89C1\u7D2B\u73AB\u6293\u8D77\u8DD1\u7684\u6700\u6162\u7684\u4E00\u4E2A\u53C8\u8981\u6254\u51FA\uFF0C\u8FDE\u5FD9\u8BF4\uFF1A\u300C\u8F7B\u4E00\u4E9B\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u7B11\uFF0C\u6296\u624B\u628A\u90A3\u4EBA\u7529\u4E86\u51FA\u53BB\u3002</p><p>\u773C\u770B\u90A3\u4EBA\u5C31\u8981\u843D\u5230\u5730\u4E0A\uFF0C\u5FFD\u7136\u8EAB\u5B50\u4E00\u987F\uFF0C\u5B9A\u5728\u534A\u7A7A\u3002</p><p>\u4E24\u5973\u4E00\u6115\uFF0C\u53EA\u89C1\u90A3\u4EBA\u50CF\u660F\u4E86\u822C\u56DB\u80A2\u8F6F\u7EF5\u7EF5\u5782\u4E0B\uFF0C\u8EAB\u4F53\u5374\u7F13\u7F13\u51CC\u7A7A\u98DE\u4E86\u56DE\u6765\u3002</p><p>\u90A3\u5177\u8EAB\u4F53\u811A\u4E0D\u70B9\u5730\u7684\u79FB\u5230\u95E8\u65C1\uFF0C\u8FD9\u624D\u6389\u5728\u5730\u4E0A\uFF0C\u8EAB\u540E\u9732\u51FA\u4E00\u4E2A\u9762\u5BB9\u53E4\u96C5\u7684\u8001\u4EBA\u3002</p><p>\u4ED6\u8EAB\u6750\u7626\u957F\uFF0C\u7A7F\u7740\u4E00\u88AD\u6D17\u5F97\u53D1\u767D\u7684\u7EFF\u888D\u3002\u67AF\u7626\u7684\u624B\u6307\u50CF\u6811\u6839\u4E00\u6837\u6709\u529B\uFF0C\u8138\u4E0A\u50CF\u5E72\u786C\u7684\u6811\u76AE\u822C\u5E03\u6EE1\u76B1\u7EB9\uFF0C\u6CA1\u6709\u4E00\u4E1D\u8868\u60C5\u3002\u53EA\u6709\u4E24\u53EA\u773C\u775B\u7CBE\u5149\u95EA\u95EA\uFF0C\u770B\u4E0A\u53BB\u8FD8\u50CF\u4E2A\u6D3B\u4EBA\u3002\u988C\u4E0B\u94F6\u987B\u98D8\u626C\u4E2D\uFF0C\u90A3\u4EBA\u7F13\u6B65\u5165\u5BA4\u3002</p>',58);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
