import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_09" tabindex="-1"><a class="header-anchor" href="#_09" aria-hidden="true">#</a> 09</h1><p>\u6155\u5BB9\u7D2B\u73AB\u548C\u7EAA\u7709\u59A9\u5BF9\u671B\u4E00\u773C\uFF0C\u770B\u51FA\u5F7C\u6B64\u7684\u60CA\u8BB6\u3002</p><p>\u90A3\u8001\u8005\u4E00\u64A9\u888D\u89D2\uFF0C\u7F13\u7F13\u5750\u4E0B\uFF0C\u79BB\u4E24\u4EBA\u9694\u4E86\u5F20\u684C\u5B50\u3002</p><p>\u5E97\u8001\u677F\u60CA\u7591\u4E0D\u5B9A\u7684\u8D70\u8FC7\u53BB\uFF0C\u8EAC\u8170\u8D54\u7740\u5C0F\u5FC3\u8BF4\u9053\uFF1A\u300C\u60A8\u8001\u8981\u4E9B\u4EC0\u4E48\uFF1F\u300D</p><p>\u8001\u8005\u4E00\u8A00\u4E0D\u53D1\uFF0C\u53EA\u4ECE\u5934\u5230\u811A\u6253\u91CF\u7740\u4E24\u5973\u3002\u88AB\u4ED6\u7684\u76EE\u5149\u626B\u8FC7\uFF0C\u7D2B\u73AB\u548C\u7EAA\u7709\u59A9\u90FD\u6709\u79CD\u88AB\u4EBA\u8131\u5149\u8863\u670D\u7684\u611F\u89C9\u3002\u7EAA\u5A9A\u59A9\u7F9E\u7684\u624B\u8DB3\u65E0\u63AA\uFF0C\u5782\u4E0B\u5934\uFF0C\u5FC3\u91CC\u4E00\u9635\u7D27\u5F20\u3002</p><p>\u7D2B\u73AB\u6A21\u4EFF\u7740\u7236\u4EB2\u7684\u4E3E\u6B62\uFF0C\u62B1\u62F3\u6C89\u58F0\u9053\uFF1A\u300C\u6562\u95EE\u8001\u4E08\u5C0A\u59D3\u5927\u540D\uFF1F\u300D</p><p>\u8001\u8005\u8F7B\u54B3\u4E86\u4E00\u58F0\uFF0C\u300C\u6C90\uFF0C\u6C90\u58F0\u4F20\u3002\u300D</p><p>\u7D2B\u73AB\u89C1\u4ED6\u5E76\u65E0\u6076\u610F\uFF0C\u89E3\u91CA\u8BF4\uFF1A\u300C\u8FD9\u51E0\u4E2A\u4EBA\u5BF9\u6211\u4EEC\u59D0\u59B9\u65E0\u793C\uFF0C\u56E0\u6B64\u7565\u52A0\u60E9\u6CBB\uFF0C\u6CA1\u60F3\u5230\u60CA\u52A8\u4E86\u6C90\u8001\u4E08\uFF0C\u62B1\u6B49\u3002\u300D</p><p>\u6C90\u58F0\u4F20\u8427\u745F\u7684\u767D\u53D1\u5FAE\u5FAE\u4E00\u6447\uFF0C\u5E72\u5DF4\u5DF4\u7684\u8BF4\uFF1A\u300C\u4F60\u9519\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u6115\uFF0C\u95EE\u9053\uFF1A\u300C\u9519\u4E86\uFF1F\u4EC0\u4E48\u9519\u4E86\uFF1F\u300D</p><p>\u6C90\u58F0\u4F20\u773C\u4E2D\u7CBE\u5149\u4E00\u95EA\u5373\u6536\uFF0C\u82CD\u8001\u7684\u58F0\u97F3\u6BEB\u65E0\u611F\u60C5\uFF1A\u300C\u65E2\u7136\u751F\u4E3A\u5973\u5B50\uFF0C\u4FBF\u8981\u4EE5\u7537\u4EBA\u4E3A\u5C0A\u3002\u83AB\u8BF4\u88AB\u4ED6\u4EEC\u770B\u51E0\u773C\uFF0C\u5C31\u662F\u4EBA\u5BB6\u8981\u5978\u4F60\u4EEC\u7684\u8EAB\u5B50\uFF0C\u4F60\u4EEC\u4E5F\u8BE5\u4E56\u4E56\u628A\u817F\u5F20\u5F00\u3002\u300D</p><p>\u4ED6\u770B\u4E0A\u53BB\u53E4\u96C5\u62D9\u6734\uFF0C\u6CA1\u60F3\u8BF4\u8BDD\u5374\u8FD9\u4E48\u4E0B\u6D41\uFF0C\u6155\u5BB9\u7D2B\u73AB\u548C\u7EAA\u7709\u59A9\u8138\u4E00\u4E0B\u5B50\u6DA8\u5F97\u901A\u7EA2\uFF0C\u7D2B\u73AB\u63E1\u4F4F\u7247\u7389\uFF0C\u5389\u559D\u9053\uFF1A\u300C\u4F60\u662F\u4EC0\u4E48\u4EBA\uFF01\u300D</p><p>\u300C\u661F\u6708\u6E56\u6728\u5802\u957F\u8001\uFF0C\u6C90\u58F0\u4F20\u3002\u300D</p><p>\u4E24\u5973\u76F8\u987E\u5931\u58F0\u3002\u770B\u4ED6\u6709\u6043\u65E0\u6050\u7684\u6837\u5B50\uFF0C\u5FC5\u6709\u60CA\u4EBA\u827A\u4E1A\u3002\u7EAA\u7709\u59A9\u542C\u7D2B\u73AB\u8BF4\u8FC7\u970D\u72C2\u7130\u548C\u5C60\u6000\u6C89\u7684\u529F\u529B\uFF0C\u5FC3\u91CC\u5012\u4E5F\u4E0D\u662F\u5341\u5206\u6050\u614C\u3002\u5FC3\u60F3\uFF0C\u5982\u679C\u5355\u4ED6\u4E00\u4EBA\u4E5F\u4E0D\u96BE\u5BF9\u4ED8\u3002</p><p>\u7D2B\u73AB\u60F3\u7684\u4E5F\u662F\u4E00\u822C\uFF0C\u4E09\u5E08\u59D0\u7684\u529F\u529B\u8F83\u4E4B\u970D\u72C2\u7130\u76F8\u5DEE\u65E0\u51E0\uFF0C\u540C\u662F\u957F\u8001\uFF0C\u6C90\u58F0\u4F20\u4E5F\u5F3A\u4E0D\u5230\u54EA\u91CC\uFF0C\u5408\u59D0\u59B9\u4E24\u4EBA\u4E4B\u529B\uFF0C\u7EDD\u4E0D\u4F1A\u8F93\u7ED9\u4ED6\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u9000\u540E\u4E00\u6B65\uFF0C\u4E0E\u7EAA\u7709\u59A9\u5E76\u80A9\u800C\u7ACB\uFF0C\u5927\u58F0\u559D\u9053\uFF1A\u300C\u661F\u6708\u6E56\u7684\u5996\u5B7D\uFF0C\u90FD\u51FA\u6765\u5427\uFF01\u300D</p><p>\u5E97\u5916\u9ED1\u6C89\u6C89\u6BEB\u65E0\u52A8\u9759\u3002\u6C90\u58F0\u4F20\u4F4E\u53F9\u4E00\u58F0\uFF0C\u300C\u5BF9\u4ED8\u4F60\u4EEC\u4E24\u4E2A\u5C0F\u5973\u5A03\uFF0C\u8001\u592B\u8FD8\u8981\u4EC0\u4E48\u5E2E\u624B\u3002\u300D</p><p>\u7D2B\u73AB\u51B7\u7B11\u9053\uFF1A\u300C\u597D\u5927\u7684\u53E3\u6C14\uFF01\u6211\u5012\u8981\u770B\u770B\u4F60\u6709\u4EC0\u4E48\u672C\u4E8B\uFF01\u300D\u8BF4\u7740\u98DE\u8EAB\u800C\u8D77\uFF0C\u7247\u7389\u76F4\u523A\u6C90\u58F0\u4F20\u5934\u9876\u3002\u6709\u5E08\u59D0\u5728\u65C1\u63A0\u9635\uFF0C\u5979\u629B\u5F00\u987E\u8651\uFF0C\u62A2\u5148\u51FA\u624B\u3002</p><p>\u6C90\u58F0\u4F20\u4E00\u52A8\u4E0D\u52A8\uFF0C\u5F85\u5B9D\u5200\u79BB\u8111\u95E8\u4E0D\u8DB3\u4E24\u5BF8\u624D\u8EAB\u5B50\u4E00\u5F39\uFF0C\u8DC3\u5230\u534A\u7A7A\u4E00\u6307\u70B9\u5411\u7D2B\u73AB\u8170\u95F4\u3002\u7D2B\u73AB\u5E94\u6307\u98D8\u98DE\uFF0C\u56DE\u624B\u65A9\u5411\u6C90\u58F0\u4F20\u7684\u624B\u81C2\u3002\u7EAA\u7709\u59A9\u7389\u624B\u4E00\u626C\uFF0C\u957F\u8FBE\u5230\u4E03\u5C3A\u7684\u4E1D\u5E26\u903C\u5411\u8001\u8005\u80F8\u53E3\u3002</p><p>\u6C90\u58F0\u4F20\u4E0D\u52A8\u58F0\u8272\uFF0C\u6307\u5C16\u5728\u5E26\u7AEF\u4E00\u70B9\uFF0C\u4E1D\u5E26\u7ACB\u523B\u5012\u5377\u56DE\u6765\u3002\u7EAA\u7709\u59A9\u56FA\u7136\u662F\u5FC3\u4E0B\u5927\u60CA\uFF0C\u6C90\u58F0\u4F20\u4E5F\u662F\u51FA\u4E4E\u610F\u6599\u3002\u8FD9\u4E00\u6307\u4ED6\u5DF2\u7528\u4E0A\u5341\u6210\u529F\u529B\uFF0C\u539F\u60F3\u4E00\u62DB\u5C31\u8BA9\u5979\u53D7\u4F24\u5410\u8840\u3002\u4F46\u7EAA\u7709\u59A9\u7EA4\u624B\u4E00\u6296\u4E1D\u5E26\u4FBF\u53C8\u63A0\u4E86\u8FC7\u6765\uFF0C\u529B\u9053\u53CD\u800C\u66F4\u5F3A\u3002\u770B\u5979\u4E0D\u8FC7\u5341\u4E03\u516B\u5C81\u5E74\u7EAA\uFF0C\u8FD9\u95E8\u7275\u4E1D\u624B\u7684\u529F\u592B\u53EF\u7740\u5B9E\u4E86\u5F97\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8DB3\u5C16\u5728\u6881\u4E0A\u8F7B\u8F7B\u4E00\u70B9\uFF0C\u7BAD\u77E2\u822C\u6FC0\u5C04\u8FC7\u6765\uFF0C\u660E\u6643\u6643\u7684\u950B\u5203\u5E26\u7740\u54E8\u54CD\u76F4\u5288\u6C90\u58F0\u4F20\u9888\u4E2D\u3002\u7EAA\u7709\u59A9\u540C\u65F6\u8DC3\u8D77\u8EAB\u6765\uFF0C\u53CC\u624B\u4E1D\u5E26\u56F4\u6210\u4E00\u4E2A\u5706\u5F62\u5C06\u8FD9\u4E2A\u6728\u5802\u957F\u8001\u5708\u5728\u4E2D\u95F4\u3002</p><p>\u7247\u7389\u8FEB\u8FD1\u9762\u95E8\u5FFD\u5C14\u5411\u4E0B\u4E00\u6C89\uFF0C\u5212\u5411\u5DE6\u808B\uFF0C\u6C90\u58F0\u4F20\u624B\u638C\u4E00\u7FFB\uFF0C\u7ADF\u7136\u8D64\u624B\u593A\u5200\u3002</p><p>\u7D2B\u73AB\u5FC3\u4E0B\u4E00\u559C\uFF0C\u8FD9\u628A\u5B9D\u5200\u524A\u94C1\u5982\u6CE5\uFF0C\u4F60\u529F\u529B\u518D\u5389\u5BB3\uFF0C\u8FD9\u4E00\u4E0B\u4E5F\u8981\u628A\u4F60\u4E94\u6839\u6307\u5934\u90FD\u5207\u4E0B\u6765\u3002</p><p>\u7EAA\u7709\u59A9\u770B\u51FA\u6C90\u58F0\u4F20\u624B\u6307\u7B95\u5F20\uFF0C\u62C7\u6307\u3001\u98DF\u6307\u3001\u4E2D\u6307\u5BF9\u51C6\u5200\u80CC\u6263\u53BB\uFF0C\u540C\u65F6\u65E0\u540D\u6307\u548C\u5C3E\u6307\u5FAE\u5FAE\u7FD8\u8D77\uFF0C\u4E00\u65E6\u6CBE\u4E0A\u5B9D\u5200\uFF0C\u7ACB\u523B\u4FBF\u4F1A\u5C01\u4E86\u7D2B\u73AB\u624B\u4E0A\u8C37\u53E3\u8BF8\u7A74\uFF0C\u8FDE\u5FD9\u53EB\u9053\uFF1A\u300C\u5C0F\u5FC3\uFF01\u300D</p><p>\u300C\u556A\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u50CF\u662F\u6728\u5757\u51FB\u5728\u5200\u80CC\u4E0A\u3002\u7D2B\u73AB\u4E07\u96BE\u4E4B\u4E2D\u62C9\u4F4F\u7EAA\u7709\u59A9\u7684\u4E1D\u5E26\u65CB\u8EAB\u98DE\u51FA\uFF0C\u6C90\u58F0\u4F20\u7684\u624B\u6307\u5F39\u4E2D\u7247\u7389\uFF0C\u7ACB\u523B\u4F20\u6765\u4E00\u9635\u6D51\u539A\u65E0\u5339\u7684\u771F\u6C14\u3002\u7D2B\u73AB\u4E39\u7530\u4E2D\u6C14\u8F6E\u6025\u8F6C\uFF0C\u5C06\u771F\u6C14\u963B\u4F4F\u3002</p><p>\u6C90\u58F0\u4F20\u770B\u51FA\u7D2B\u73AB\u529F\u529B\u8F83\u7EAA\u7709\u59A9\u76F8\u5DEE\u8BB8\u591A\uFF0C\u6709\u628A\u63E1\u4E00\u6307\u5C31\u53EF\u9707\u843D\u5B9D\u5200\uFF0C\u53EF\u771F\u6C14\u521A\u5165\u7ECF\u7EDC\u4FBF\u88AB\u4E00\u80A1\u65CB\u8F6C\u7684\u6C14\u8F6E\u9707\u6563\uFF0C\u7ADF\u7136\u51FA\u624B\u65E0\u529F\u3002\u4ED6\u8138\u4E0A\u4E00\u5982\u53E4\u4E95\u65E0\u6CE2\uFF0C\u5E73\u638C\u62CD\u5F00\u4E1D\u5E26\uFF0C\u4E24\u6307\u5982\u94A9\u76F4\u63D2\u7D2B\u73AB\u8179\u4E0B\uFF0C\u624B\u6CD5\u9634\u6BD2\u3002</p><p>\u7D2B\u73AB\u607C\u5F97\u4FCF\u8138\u901A\u7EA2\uFF0C\u56DE\u624B\u8FD8\u4E86\u4E00\u62DB\u3002\u6C90\u58F0\u4F20\u6216\u6307\u6216\u638C\uFF0C\u53EA\u4E0D\u79BB\u7D2B\u73AB\u4E73\u3001\u9634\u4E24\u5904\uFF0C\u51FA\u624B\u4E0B\u6D41\u5351\u9119\u3002\u7D2B\u73AB\u5DE6\u652F\u53F3\u7ECC\uFF0C\u582A\u582A\u907F\u8FC7\u51E0\u62DB\uFF0C\u5FC3\u5934\u6C14\u6781\uFF0C\u5F53\u6C90\u58F0\u4F20\u5E72\u7626\u7684\u624B\u6307\u518D\u6B21\u4F38\u6765\uFF0C\u5979\u6325\u5200\u671D\u90A3\u6839\u53EF\u6076\u624B\u6307\u72E0\u72E0\u5288\u53BB\u3002\u6C90\u58F0\u4F20\u4E24\u6307\u4E00\u5408\uFF0C\u5939\u4F4F\u5200\u8EAB\uFF0C\u63A5\u7740\u624B\u638C\u4E00\u8F6C\u3002</p><p>\u771F\u6C14\u987A\u7740\u5200\u8EAB\u76F4\u5165\u624B\u81C2\uFF0C\u7D2B\u73AB\u53EA\u89C9\u5F97\u624B\u4E2D\u50CF\u63E1\u7740\u4E00\u4E2A\u731B\u7136\u70B8\u5F00\u7684\u7834\u7A7A\u96F7\uFF0C\u9707\u5F97\u624B\u6307\u53D1\u9EBB\u3002\u5E78\u597D\u7EAA\u7709\u59A9\u7684\u4E1D\u5E26\u53CA\u65F6\u8D76\u5230\uFF0C\u903C\u5F97\u6C90\u58F0\u4F20\u56DE\u624B\u62B5\u6321\u3002</p><p>\u7D2B\u73AB\u4E00\u8FDE\u9000\u4E86\u4E09\u6B65\u624D\u505C\u4F4F\uFF0C\u8F6C\u5FF5\u4E00\u60F3\u660E\u767D\u521A\u624D\u6C90\u58F0\u4F20\u7684\u62DB\u672F\u6B63\u662F\u4E3A\u4E86\u6FC0\u8D77\u81EA\u5DF1\u7684\u6012\u610F\uFF0C\u4EE5\u81F4\u771F\u6C14\u4E0D\u7EAF\u3002\u4E8E\u662F\u5148\u8C03\u606F\u7247\u523B\u7A33\u4F4F\u5FC3\u795E\uFF0C\u624D\u91CD\u65B0\u52A0\u5165\u6218\u56E2\u3002</p><p>\u4E24\u4E2A\u707F\u5982\u671D\u971E\u7684\u660E\u8273\u5C11\u5973\u56F4\u7740\u4E00\u4E2A\u5E72\u7626\u7684\u767D\u53D1\u8001\u5934\u5728\u9152\u8086\u5185\u6076\u6597\u4E0D\u5DF2\u3002\u660F\u6697\u7684\u706F\u5149\u4E0B\uFF0C\u4E24\u5973\u8863\u8882\u98D8\u626C\uFF0C\u5B9B\u5982\u4ED9\u5B50\u7FE9\u7FE9\u8D77\u821E\u3002\u8001\u8005\u50CF\u5E72\u67AF\u7684\u6811\u5E72\u822C\u9762\u65E0\u8868\u60C5\uFF0C\u4E24\u624B\u6216\u62CD\u6216\u5F39\uFF0C\u5E76\u65E0\u82B1\u5DE7\uFF0C\u5374\u62DB\u62DB\u76F4\u62B5\u8981\u5BB3\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u548C\u7EAA\u7709\u59A9\u8D8A\u6597\u8D8A\u662F\u5FC3\u60CA\uFF0C\u8FD9\u4E2A\u6C90\u58F0\u4F20\u529F\u592B\u53EF\u8981\u6BD4\u970D\u72C2\u7130\u548C\u5C60\u6000\u6C89\u5F3A\u591A\u4E86\u3002\u4E24\u4EBA\u4E0D\u77E5\u9053\u6C90\u58F0\u4F20\u4E8C\u5341\u5E74\u524D\u4FBF\u662F\u661F\u6708\u6E56\u957F\u8001\uFF0C\u800C\u970D\u3001\u5C60\u4E24\u4EBA\u521A\u5F53\u957F\u8001\u624D\u4E24\u4E2A\u6708\u3002</p><p>\u4E09\u4EBA\u7FFB\u7FFB\u6EDA\u6EDA\u6597\u4E86\u534A\u4E2A\u65F6\u8FB0\uFF0C\u7D2B\u73AB\u548C\u7EAA\u7709\u59A9\u90FD\u662F\u9999\u6C57\u6DCB\u6F13\u3002\u4E24\u4EBA\u5185\u529B\u4E0D\u8DB3\u4E0E\u6C90\u58F0\u4F20\u786C\u62FC\uFF0C\u4FBF\u63E1\u7740\u4E00\u6839\u4E1D\u5E26\uFF0C\u4E92\u76F8\u501F\u529B\u6E38\u6597\u3002\u5979\u4EEC\u540C\u95E8\u5B66\u827A\uFF0C\u5408\u51FB\u4E4B\u672F\u751A\u7CBE\uFF0C\u4E00\u65F6\u95F4\u4E0E\u6C90\u58F0\u4F20\u6597\u4E86\u4E2A\u65D7\u9F13\u76F8\u5F53\u3002</p><p>\u7D2B\u73AB\u77A7\u51C6\u65F6\u673A\uFF0C\u6273\u52A8\u85CF\u5728\u8863\u5185\u7684\u5C0F\u5F29\u3002\u4E24\u70B9\u5BD2\u5149\u300C\u6251\u300D\u7684\u4E00\u58F0\u6CA1\u5165\u7EFF\u888D\uFF0C\u76F4\u76F4\u9489\u5165\u6C90\u58F0\u4F20\u7684\u80F8\u53E3\uFF0C\u6C90\u58F0\u4F20\u5E72\u7626\u7684\u8EAB\u5B50\u5411\u540E\u4FBF\u5012\u3002</p><p>\u7D2B\u73AB\u559C\u5F62\u4E8E\u8272\uFF0C\u8D81\u52BF\u6325\u51FA\u7247\u7389\u3002\u6C90\u58F0\u4F20\u53CC\u76EE\u5FAE\u95ED\uFF0C\u604D\u82E5\u672A\u89C9\uFF0C\u5F85\u5200\u98CE\u53CA\u4F53\uFF0C\u7A81\u7136\u4E00\u817F\u652F\u5730\uFF0C\u8EAB\u5B50\u8F66\u8F6E\u822C\u65CB\u8F6C\u8FC7\u6765\uFF0C\u811A\u8DDF\u8E22\u5728\u7D2B\u73AB\u7684\u80CC\u5FC3\u3002\u7D2B\u73AB\u88AB\u8E22\u7684\u6A2A\u98DE\u51FA\u53BB\uFF0C\u6A31\u5507\u4E00\u5F20\uFF0C\u55B7\u51FA\u4E00\u53E3\u9C9C\u8840\uFF0C\u8138\u8272\u60E8\u767D\u3002</p><p>\u7EAA\u7709\u59A9\u62A2\u8EAB\u6321\u5728\u7D2B\u73AB\u8EAB\u524D\uFF0C\u4E1D\u5E26\u5206\u88AD\u6C90\u58F0\u4F20\u53CC\u76EE\uFF0C\u6025\u9053\uFF1A\u300C\u5FEB\u8D70\uFF01\u300D</p><p>\u7D2B\u73AB\u5FC3\u5FF5\u7535\u8F6C\uFF0C\u6C90\u58F0\u4F20\u529F\u529B\u60CA\u4EBA\uFF0C\u8FDE\u5F3A\u5F29\u4E5F\u6562\u786C\u6321\uFF0C\u81EA\u5DF1\u8D2A\u529F\u6025\u8FDB\uFF0C\u4E2D\u8BA1\u53D7\u4F24\uFF0C\u8FD9\u6B21\u662F\u4E00\u8D25\u6D82\u5730\uFF0C\u5982\u679C\u81EA\u5DF1\u7559\u4E0B\u6765\u7EDD\u6321\u4E0D\u4E86\u6C90\u58F0\u4F20\u4E09\u62DB\uFF0C\u53EA\u4F1A\u62D6\u7D2F\u5E08\u59D0\u3002\u4E00\u54AC\u7259\uFF0C\u4ECE\u5E97\u95E8\u98D8\u8EAB\u98DE\u51FA\u3002</p><p>\u7EAA\u7709\u59A9\u6C89\u4E0B\u5FC3\u6765\uFF0C\u7275\u4E1D\u624B\u7EDD\u6280\u5C3D\u5C55\uFF0C\u4E1D\u5E26\u5982\u6025\u96E8\u822C\u5411\u6C90\u58F0\u4F20\u653B\u53BB\u3002\u5979\u529F\u529B\u8FDC\u8FC7\u6155\u5BB9\u7D2B\u73AB\uFF0C\u6C90\u58F0\u4F20\u4E5F\u4E0D\u6562\u5927\u610F\u3002</p><p>\u62C6\u4E86\u56DB\u4E94\u5341\u62DB\uFF0C\u6C90\u58F0\u4F20\u67E5\u89C9\u51FA\u7EAA\u7709\u59A9\u771F\u6C14\u7565\u6709\u4E0D\u6D4E\uFF0C\u7A81\u7136\u53CC\u81C2\u4E00\u5206\uFF0C\u5C06\u4E1D\u5E26\u7F20\u5728\u81C2\u4E2D\u3002\u7EAA\u7709\u59A9\u7389\u624B\u4E00\u62AC\uFF0C\u4E1D\u5E26\u5212\u51FA\u4E00\u4E2A\u5706\u5708\uFF0C\u5957\u5411\u6C90\u58F0\u4F20\u7684\u8116\u5B50\u3002</p><p>\u6C90\u58F0\u4F20\u773C\u4E2D\u7CBE\u5149\u5927\u76DB\uFF0C\u90A3\u4E2A\u5706\u5708\u53EA\u8FDB\u4E86\u5BF8\u8BB8\u4FBF\u5FEB\u6377\u65E0\u4F26\u7684\u5012\u5377\u8FC7\u6765\uFF0C\u6B63\u6263\u5728\u7EAA\u7709\u59A9\u8155\u4E2D\u3002</p><p>\u7EAA\u7709\u59A9\u5927\u60CA\u5931\u8272\uFF0C\u8FDE\u5FD9\u56DE\u624B\u3002\u6C90\u58F0\u4F20\u624B\u81C2\u4E00\u7D27\uFF0C\u786C\u751F\u751F\u628A\u7EAA\u7709\u59A9\u626F\u5230\u6000\u4E2D\uFF0C\u63A5\u7740\u4FBF\u5C01\u4E86\u5979\u7684\u7A74\u9053\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8DEF\u4E0A\u53C8\u5410\u4E86\u4E24\u53E3\u8840\uFF0C\u52C9\u529B\u5954\u56DE\u5BA2\u6808\uFF0C\u597D\u5728\u6C90\u58F0\u4F20\u679C\u7136\u662F\u5B64\u8EAB\u4E00\u4EBA\uFF0C\u82E5\u518D\u6709\u4E00\u4E2A\u6B66\u529F\u4E00\u822C\u7684\u5E2E\u4F17\uFF0C\u5979\u6B64\u65F6\u4E5F\u62B5\u6321\u4E0D\u4E86\u3002\u7D2B\u73AB\u5F84\u76F4\u8D8A\u5899\u63A0\u81F3\u9A6C\u5ECA\uFF0C\u843D\u5728\u5C0F\u767D\u80CC\u4E0A\uFF0C\u6325\u5200\u65A9\u65AD\u7F30\u7EF3\uFF0C\u62C9\u7740\u5E08\u59D0\u7684\u5750\u9A91\u671D\u57CE\u95E8\u9A70\u53BB\u3002</p><p>\u6C90\u58F0\u4F20\u90A3\u4E00\u811A\u52B2\u529B\u5341\u8DB3\uFF0C\u7D2B\u73AB\u771F\u6C14\u6B64\u523B\u8FD8\u65E0\u6CD5\u51DD\u805A\uFF0C\u80F8\u53E3\u70E6\u95F7\u6B32\u6B7B\u3002\u5979\u6DF1\u5438\u7F13\u5410\uFF0C\u8C03\u606F\u4E39\u7530\u6563\u4E71\u7684\u771F\u6C14\uFF0C\u518D\u9001\u5230\u80CC\u5FC3\u7684\u4F24\u5904\u3002</p><p>\u6E05\u8106\u7684\u9A6C\u8E44\u7A81\u7136\u53D8\u5F97\u6C89\u95F7\uFF0C\u5DF2\u7ECF\u8FC7\u4E86\u77F3\u780C\u7684\u5927\u8DEF\uFF0C\u8E0F\u4E0A\u6CE5\u571F\u3002\u63A5\u7740\u7D2B\u73AB\u542C\u5230\u8033\u8FB9\u4E00\u8FDE\u4E32\u300C\u683C\u683C\u300D\u8F7B\u54CD\uFF0C\u7247\u523B\u540E\uFF0C\u5979\u624D\u610F\u8BC6\u5230\u662F\u81EA\u5DF1\u7259\u9F7F\u76F8\u51FB\u7684\u58F0\u97F3\u3002</p><p>\u7D2B\u73AB\u662F\u5728\u62C5\u5FC3\u5E08\u59D0\u3002\u7565\u4E00\u60F3\u8D77\u6C90\u58F0\u4F20\u5F53\u521D\u6240\u8BF4\u7684\u8BDD\uFF0C\u5979\u5C31\u5FC3\u5982\u5200\u7EDE\uFF0C\u624B\u811A\u4E5F\u4E3A\u4E4B\u51B0\u51B7\u3002\u7981\u4E0D\u4F4F\u4F0F\u5728\u4E86\u9A6C\u80CC\u4E0A\uFF0C\u4F38\u624B\u6402\u4F4F\u5C0F\u767D\u7684\u8116\u5B50\uFF0C\u628A\u8138\u57CB\u5728\u5B83\u957F\u957F\u7684\u9B13\u6BDB\u95F4\u3002</p><p>\u300C\u7EAA\u5E08\u59D0\u2026\u2026\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6B64\u523B\u5DF2\u8FD1\u4EA5\u65F6\uFF0C\u8FD9\u4E2A\u51B7\u6E05\u7684\u9152\u8086\u91CC\u5374\u5750\u6EE1\u4E86\u4EBA\uFF0C\u6BCF\u4E2A\u4EBA\u90FD\u5750\u7684\u7B14\u76F4\uFF0C\u4EF0\u8138\u770B\u7740\u7A7A\u4E2D\u3002</p><p>\u7A7A\u4E2D\u60AC\u6302\u7740\u4E00\u4E2A\u5A07\u6EF4\u6EF4\u7684\u7F8E\u5973\u3002\u7F8A\u8102\u822C\u7684\u53F3\u624B\u548C\u53F3\u811A\u88AB\u4E00\u6839\u4E1D\u5E26\u7F1A\u4F4F\uFF0C\u4E1D\u5E26\u7684\u53E6\u4E00\u7AEF\u7F20\u7ED5\u5728\u6881\u4E0A\uFF0C\u5A07\u8EAF\u6A2A\u653E\u3002\u633D\u6210\u4E91\u9AFB\u7684\u79C0\u53D1\u56E0\u4E3A\u6253\u6597\u800C\u6563\u4E71\u5F00\u6765\uFF0C\u5982\u70DF\u5982\u96FE\u3002</p><p>\u7CBE\u81F4\u7684\u9762\u5BB9\u867D\u7136\u6EE1\u8138\u98DE\u7EA2\uFF0C\u4ECD\u63A9\u4E0D\u4F4F\u8C6A\u95E8\u5343\u91D1\u7684\u534E\u8D35\u6C14\u606F\u3002\u5979\u5DE6\u624B\u3001\u5DE6\u811A\u8F6F\u8F6F\u5782\u4E0B\u3002\u7FE0\u7EFF\u8272\u7684\u8863\u886B\u5728\u7A7A\u4E2D\u5FAE\u5FAE\u6296\u52A8\u3002</p><p>\u6C90\u58F0\u4F20\u4EF0\u5929\u9ED8\u60F3\u7247\u523B\u3002\u81EA\u5DF1\u786E\u5B9E\u5927\u610F\u4E86\uFF0C\u56E0\u4E3A\u6C42\u751F\u5FC3\u5207\uFF0C\u4E00\u63A5\u5230\u6D88\u606F\u4E0D\u7B49\u67AF\u679D\u3001\u65B0\u53F6\u7B49\u624B\u4E0B\u6765\u5230\uFF0C\u4FBF\u5355\u9A91\u51FA\u9A6C\u3002\u8FD9\u6B21\u867D\u7136\u64D2\u4F4F\u7EAA\u7709\u59A9\uFF0C\u4F46\u7EC8\u7A76\u8BA9\u6155\u5BB9\u7D2B\u73AB\u9003\u8D70\u3002</p><p>\u8FD9\u4F4D\u5BAB\u4E3B\u559C\u6012\u65E0\u5B9A\uFF0C\u6570\u6708\u95F4\u706B\u3001\u571F\u4E24\u5802\u7684\u957F\u8001\u90FD\u56E0\u5C0F\u9519\u800C\u88AB\u5904\u6B7B\uFF0C\u81EA\u5DF1\u2026\u2026\u4ED6\u6697\u53F9\u4E00\u58F0\uFF0C\u4F38\u624B\u633D\u8D77\u7EAA\u7709\u59A9\u7684\u79C0\u53D1\uFF0C\u624B\u6307\u5728\u5979\u5A07\u7F8E\u7684\u8138\u86CB\u7EC6\u7EC6\u6478\u6332\uFF0C\u72B9\u8C6B\u7740\u662F\u4E0D\u662F\u628A\u5979\u5B8C\u74A7\u9001\u81F3\u5BAB\u4E2D\u3002</p><p>\u7EAA\u7709\u59A9\u53C8\u7F9E\u53C8\u6025\uFF0C\u73E0\u6CEA\u987A\u7740\u660E\u7389\u822C\u7684\u9762\u5BB9\u6EDA\u6EDA\u800C\u843D\u3002</p><p>\u6C90\u58F0\u4F20\u8BA1\u8F83\u5DF2\u5B9A\uFF0C\u82CD\u58F0\u53F9\u9053\uFF1A\u300C\u4F60\u8EAB\u4E3A\u5973\u5B50\uFF0C\u4F55\u5FC5\u5B66\u90A3\u4E9B\u6B66\u529F\uFF1F\u300D</p><p>\u4ED6\u4F3C\u4E4E\u60F3\u8D77\u4E00\u4E9B\u4E45\u8FDC\u7684\u5F80\u4E8B\uFF0C\u773C\u795E\u9EEF\u6DE1\u8D77\u6765\uFF0C\u50CF\u662F\u81EA\u8A00\u81EA\u8BED\u822C\u5583\u5583\u8BF4\uFF1A\u300C\u5373\u4F7F\u7EC3\u5230\u5929\u4E0B\u7B2C\u4E00\u7684\u5730\u6B65\uFF0C\u4E5F\u6446\u8131\u4E0D\u4E86\u8EAB\u4E3A\u5973\u5B50\u7684\u5BBF\u547D\u2026\u2026\u300D</p><p>\u5E72\u67AF\u7684\u624B\u638C\u987A\u7740\u6ED1\u817B\u7684\u808C\u80A4\u6ED1\u5165\u8863\u9886\uFF0C\u7EAA\u7709\u59A9\u52A8\u5F39\u4E0D\u5F97\uFF0C\u53EA\u80FD\u4EFB\u4ED6\u8F7B\u8584\uFF0C\u4E0D\u7981\u7F9E\u6124\u6B32\u6B7B\u3002</p>',55);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
