import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_10" tabindex="-1"><a class="header-anchor" href="#_10" aria-hidden="true">#</a> 10</h1><p>\u300C\u597D\uFF0C\u597D\u3002\u53C8\u7EC6\u53C8\u6ED1\uFF0C\u53C8\u9999\u53C8\u8F6F\uFF0C\u679C\u7136\u662F\u5927\u5BB6\u95FA\u79C0\uFF0C\u517B\u7684\u4E00\u8EAB\u597D\u76AE\u8089\u3002\u300D\u6C90\u58F0\u4F20\u6DE1\u6DE1\u8BF4\u7740\uFF0C\u5E03\u6EE1\u76B1\u7EB9\u7684\u7626\u8138\u4E0A\u6CA1\u6709\u4E00\u4E1D\u8868\u60C5\u3002</p><p>\u7EAA\u7709\u59A9\u80F8\u524D\u7684\u8863\u895F\u5728\u4ED6\u624B\u4E0A\u7F13\u7F13\u8D77\u4F0F\uFF0C\u95EA\u51FA\u4E00\u7247\u6F6E\u6C34\u822C\u7684\u7FE0\u5149\u3002\u7EAA\u7709\u59A9\u624B\u4E0D\u80FD\u52A8\uFF0C\u53E3\u4E0D\u80FD\u8A00\u3002\u5979\u751F\u6027\u5BB3\u7F9E\uFF0C\u88AB\u964C\u751F\u4EBA\u770B\u4E0A\u4E00\u773C\u5C31\u4F1A\u8138\u7EA2\u534A\u5929\u3002\u6BCD\u4EB2\u901D\u4E16\u65F6\u7EAA\u7709\u59A9\u624D\u4E5D\u5C81\uFF0C\u4ECE\u90A3\u65F6\u8D77\u5979\u5C31\u6CA1\u8BA9\u4EBA\u89C1\u8FC7\u81EA\u5DF1\u7684\u8EAB\u5B50\uFF0C\u66F4\u8863\u6C90\u6D74\u7684\u65F6\u5019\uFF0C\u8FDE\u8D34\u8EAB\u7684\u5C0F\u5A62\u4E5F\u4E0D\u8BA9\u8FDB\u5C4B\u3002</p><p>\u6B64\u65F6\u5374\u5728\u4F17\u76EE\u777D\u777D\u4E4B\u4E0B\uFF0C\u60AC\u5728\u7A7A\u4E2D\u88AB\u4E00\u4E2A\u964C\u751F\u7684\u8001\u5934\u5728\u8EAB\u4E0A\u4E71\u6478\uFF0C\u8FD9\u756A\u7F9E\u8FB1\u5BF9\u5979\u6765\u8BF4\u6BD4\u6B7B\u8FD8\u8981\u75DB\u82E6\u3002\u6676\u83B9\u7684\u73E0\u6CEA\u4ECE\u5FAE\u7EA2\u7684\u773C\u7736\u91CC\u4E00\u8FDE\u4E32\u7684\u843D\u5728\u5730\u4E0A\u3002</p><p>\u6C90\u58F0\u4F20\u5728\u7EAA\u7709\u59A9\u80F8\u4E73\u4E0A\u6478\u5F04\u591A\u65F6\uFF0C\u7136\u540E\u987A\u7740\u4E1D\u7EF8\u822C\u67D4\u6ED1\u7684\u808C\u80A4\u5411\u4E0B\u671D\u8170\u8179\u6478\u53BB\u3002\u7EAA\u7709\u59A9\u8170\u95F4\u675F\u7740\u4E00\u6761\u534E\u4E3D\u7684\u7F0E\u5E26\uFF0C\u6321\u4F4F\u4E86\u4ED6\u7684\u624B\u6307\u3002\u6C90\u58F0\u4F20\u6728\u7136\u56DE\u624B\u637B\u4F4F\u7EAA\u7709\u59A9\u5A07\u5AE9\u7684\u4E73\u5C16\uFF0C\u624B\u81C2\u5FAE\u5FAE\u4E00\u6491\u3002\u53EA\u89C1\u7EAA\u7709\u59A9\u80F8\u524D\u7684\u8863\u670D\u4E00\u9707\uFF0C\u8170\u95F4\u7684\u7F0E\u5E26\u7ACB\u523B\u65AD\u5F00\u3002</p><p>\u7EAA\u7709\u59A9\u5FC3\u4E0B\u5927\u9A87\uFF0C\u4E00\u534A\u662F\u56E0\u4E3A\u5916\u8863\u655E\u9732\uFF0C\u53E6\u4E00\u534A\u5219\u662F\u56E0\u4E3A\u6C90\u58F0\u4F20\u8FD9\u624B\u529F\u592B\u3002</p><p>\u5E03\u6599\u672C\u6765\u5C31\u8F6F\u4E0D\u53D7\u529B\uFF0C\u4ED6\u53EA\u78B0\u7740\u80F8\u53E3\u4E00\u7247\uFF0C\u5185\u529B\u6240\u5230\u5904\uFF0C\u5C45\u7136\u5C06\u8170\u5E26\u90FD\u9707\u65AD\u4E86\u3002</p><p>\u8FD9\u822C\u7CBE\u7EAF\u7684\u5185\u529B\uFF0C\u53EA\u6015\u5927\u5E08\u59D0\u4E5F\u6709\u6240\u4E0D\u53CA\u3002</p><p>\u8170\u5E26\u7FFB\u5377\u7740\u843D\u5728\u5730\u4E0A\uFF0C\u7FE0\u7EFF\u8272\u7684\u5916\u8863\u7ACB\u523B\u5782\u4E0B\u4E00\u5E45\uFF0C\u9732\u51FA\u91CC\u9762\u4E00\u5C42\u8584\u8584\u7684\u76AE\u8863\u3002\u6C90\u58F0\u4F20\u624B\u81C2\u63D2\u5728\u8863\u9886\u5185\uFF0C\u6014\u4E86\u4E00\u4E0B\uFF0C\u300C\u8FD9\u822C\u6C34\u7EA2\u8272\u7684\u517D\u76AE\u7740\u5B9E\u7F55\u89C1\u3002\u83AB\u975E\u662F\u4E1C\u6D77\u7684\u9C9B\u8863\uFF1F\u7EAA\u91CD\u662F\u4ECE\u4F55\u5904\u5F04\u6765\u7684\uFF1F\u300D</p><p>\u7EAA\u7709\u59A9\u7A98\u8FEB\u4E4B\u6781\uFF0C\u867D\u7136\u7A74\u9053\u88AB\u5C01\uFF0C\u6A31\u5507\u8FD8\u662F\u6B62\u4E0D\u4F4F\u98A4\u6296\u8D77\u6765\uFF0C\u5145\u6EE1\u6CEA\u6C34\u7684\u53CC\u773C\u4E2D\u5199\u6EE1\u60CA\u6050\u3002</p><p>\u6C90\u58F0\u4F20\u5BF9\u8FD9\u4EF6\u76AE\u8863\u5012\u662F\u5F88\u4E0A\u5FC3\uFF0C\u300C\u5472\u300D\u7684\u4E00\u58F0\u6495\u53BB\u5916\u8863\uFF0C\u7136\u540E\u4E00\u7C92\u7C92\u89E3\u5F00\u767D\u7389\u96D5\u6210\u7684\u8863\u6263\uFF0C\u64A9\u8D77\u76AE\u8863\u4E00\u89D2\u7EC6\u7EC6\u5BA1\u89C6\u3002\u534A\u664C\u540E\u624D\u677E\u4E86\u53E3\u6C14\uFF0C\u7D27\u76B1\u7684\u7709\u5934\u7F13\u7F13\u5C55\u5F00\u3002</p><p>\u6C34\u7EA2\u8272\u7684\u76AE\u8863\u4E0B\u662F\u4E00\u4EF6\u767D\u8272\u7684\u7EE3\u82B1\u62B9\u80F8\uFF0C\u88F9\u5728\u73B2\u73D1\u6709\u81F4\u7684\u5A07\u8EAF\u4E0A\uFF0C\u50CF\u4E00\u62B9\u6D41\u52A8\u7684\u6708\u5149\u3002</p><p>\u6C90\u58F0\u4F20\u4FEF\u5728\u7EAA\u7709\u59A9\u67D4\u767D\u7EC6\u957F\u7684\u8116\u9888\u4E2D\u55C5\u4E86\u53C8\u55C5\uFF0C\u67AF\u7626\u7684\u8138\u4E0A\u663E\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u300C\u4E0D\u9519\uFF0C\u4E0D\u9519\uFF0C\u679C\u7136\u662F\u4E2A\u7231\u5E72\u51C0\u7684\u59D1\u5A18\uFF0C\u8D76\u8DEF\u8FD8\u64E6\u7740\u8299\u84C9\u9732\u3002\u5475\u5475\uFF0C\u8FD9\u6837\u7EDD\u8272\u5904\u5B50\uFF0C\u5012\u662F\u4FBF\u5B9C\u8001\u592B\u4E86\u3002\u300D\u4ED6\u76F4\u8D77\u4F5D\u507B\u7684\u8170\u80CC\uFF0C\u5728\u6BEB\u65E0\u53CD\u6297\u4E4B\u529B\u7684\u5C11\u5973\u80A9\u4E0A\u4E00\u63A8\uFF0C\u9999\u8F6F\u7684\u5A07\u8EAF\u5E94\u624B\u6447\u52A8\uFF0C\u655E\u5F00\u7684\u8863\u886B\u8FCE\u98CE\u98D8\u626C\u3002</p><p>\u8F7B\u76C8\u7684\u8EAB\u4F53\u4E0D\u80FD\u81EA\u4E3B\u5730\u5728\u7A7A\u4E2D\u65CB\u8F6C\u8D77\u6765\uFF0C\u7EAA\u7709\u59A9\u7D27\u7D27\u95ED\u7740\u773C\u775B\uFF0C\u4F46\u8FD8\u80FD\u611F\u89C9\u5468\u56F4\u90A3\u5341\u51E0\u9053\u8272\u8FF7\u8FF7\u7684\u76EE\u5149\u3002\u8138\u4E0A\u9888\u4E0A\u50CF\u71C3\u70E7\u822C\u6CDB\u8D77\u4E00\u5C42\u8273\u7EA2\uFF0C\u8FDE\u62B9\u80F8\u4E0B\u4E0D\u505C\u6643\u52A8\u7684\u7389\u4E73\u4E5F\u9690\u9690\u900F\u51FA\u7EA2\u8272\u3002</p><p>\u6C90\u58F0\u4F20\u4ECE\u5979\u8EAB\u4E0A\u626F\u6389\u5916\u8863\u7684\u788E\u7247\uFF0C\u7136\u540E\u6162\u541E\u541E\u63E1\u4F4F\u5979\u7EA4\u7EC6\u7684\u624B\u8155\u5C06\u76AE\u8863\u8131\u4E0B\u534A\u5E45\uFF0C\u63A5\u7740\u9664\u53BB\u5F13\u978B\u3002\u63E1\u7740\u5C0F\u5DE7\u6676\u83B9\u7684\u811A\u638C\u629A\u6478\u7247\u523B\uFF0C\u624D\u6162\u6162\u653E\u4E0B\u3002\u7834\u788E\u7684\u4EB5\u8863\u5931\u53BB\u675F\u7F1A\u987A\u7740\u5149\u6DA6\u7684\u5927\u817F\u4E00\u76F4\u6ED1\u5230\u811A\u8E1D\uFF0C\u987F\u4E86\u4E00\u4E0B\uFF0C\u7F13\u7F13\u6389\u843D\u3002</p><p>\u62B9\u80F8\u8F7B\u5782\uFF0C\u9732\u51FA\u80A1\u95F4\u8FF7\u4EBA\u7684\u8089\u8272\uFF0C\u7EAA\u7709\u59A9\u8138\u7EA2\u7684\u51E0\u4E4E\u6E17\u51FA\u8840\u6765\uFF0C\u6D51\u8EAB\u6218\u6817\u3002</p><p>\u6C90\u58F0\u4F20\u5E72\u54B3\u4E00\u58F0\uFF0C\u6325\u624B\u64A9\u8D77\u7EFF\u888D\uFF0C\u5C31\u5728\u4F17\u76EE\u777D\u777D\u4E4B\u4E0B\u638F\u51FA\u4E0E\u4ED6\u5916\u8C8C\u4E00\u822C\u67AF\u5E72\u7626\u957F\u7684\u9633\u5177\uFF0C\u8D70\u5230\u7EAA\u7709\u59A9\u5927\u5F20\u7684\u4E24\u817F\u95F4\uFF0C\u53F9\u606F\u9053\uFF1A\u300C\u60F3\u5F53\u5E74\uFF0C\u50CF\u4F60\u8FD9\u822C\u529F\u592B\u5BB9\u8C8C\u7684\u5973\u5B50\uFF0C\u8001\u592B\u64CD\u8FC7\u6CA1\u6709\u4E00\u767E\u4E5F\u6709\u516B\u5341\u3002\u5982\u4ECA\u5E74\u7EAA\u5927\u4E86\uFF0C\u5174\u81F4\u4E5F\u6DE1\u4E86\u8BB8\u591A\uFF0C\u53EF\u60DC\u53EF\u60DC\u2026\u2026\u300D</p><p>\u8FD9\u822C\u4EFB\u4EBA\u4EA4\u5ABE\u7684\u7F9E\u4EBA\u6A21\u6837\uFF0C\u4F7F\u7EAA\u7709\u59A9\u51E0\u4E4E\u5410\u8840\u3002\u82E5\u975E\u7A74\u9053\u88AB\u5236\uFF0C\u5979\u5373\u523B\u4FBF\u54AC\u820C\u81EA\u5C3D\u3002\u80F8\u524D\u5FFD\u7684\u4E00\u51C9\uFF0C\u5E26\u7740\u5979\u4F53\u6E29\u7684\u62B9\u80F8\u6389\u5728\u5730\u4E0A\uFF0C\u88AB\u6C90\u58F0\u4F20\u4E00\u811A\u8E29\u4F4F\u3002</p><p>\u67AF\u7626\u7684\u8001\u4EBA\u795E\u60C5\u8427\u7D22\uFF0C\u6D51\u7136\u4E0D\u4F3C\u6B63\u8981\u8FDB\u5165\u8FD9\u5177\u5A07\u8273\u6B32\u6EF4\u7684\u4F53\u5185\u6A21\u6837\u3002\u4ED6\u5728\u7EAA\u7709\u59A9\u80F8\u524D\u63C9\u634F\u591A\u65F6\uFF0C\u9760\u7740\u90A3\u4E24\u56E2\u67D4\u97E7\u7684\u7F8E\u8089\u523A\u6FC0\uFF0C\u9633\u5177\u624D\u6E10\u6E10\u52C3\u8D77\u3002</p><p>\u5979\u4E24\u817F\u4E00\u4E0A\u4E00\u4E0B\uFF0C\u7AD6\u6210\u4E00\u6761\u96EA\u4EAE\u76F4\u7EBF\uFF0C\u817F\u7F1D\u95F4\u5904\u5B50\u7684\u82B1\u74E3\u88AB\u626F\u5F97\u5FAE\u5FAE\u5F20\u5F00\uFF0C\u6BB7\u7EA2\u593A\u76EE\u3002\u60AC\u5728\u7A7A\u4E2D\u7684\u534A\u8FB9\u8EAB\u5B50\u8FD8\u76D6\u7740\u90A3\u4EF6\u6CA1\u6709\u6495\u788E\u7684\u76AE\u8863\uFF0C\u767D\u5AE9\u7684\u53F3\u4E73\u5927\u534A\u63A9\u5728\u6C34\u7EA2\u8272\u7684\u76AE\u8863\u4E0B\uFF0C\u5728\u5E72\u7626\u7684\u624B\u638C\u95F4\u65F6\u9690\u65F6\u73B0\u3002\u67D4\u9888\u8F6F\u8F6F\u5782\u5728\u8EAB\u4FA7\uFF0C\u56E0\u7F9E\u6DA9\u800C\u6DA8\u7EA2\u7684\u808C\u80A4\u663E\u5F97\u6108\u52A0\u5A07\u8273\u3002</p><p>\u5F53\u5E03\u6EE1\u9752\u7B4B\u7684\u624B\u6307\u6478\u5230\u8179\u4E0B\u7684\u5AE9\u8089\uFF0C\u7EAA\u7709\u59A9\u8138\u4E0A\u7684\u8840\u8272\u731B\u7136\u9000\u53BB\uFF0C\u767D\u5F97\u4EFF\u4F5B\u900F\u660E\u3002</p><p>\u6C90\u58F0\u4F20\u9605\u5973\u65E0\u6570\uFF0C\u867D\u7136\u7EAA\u7709\u59A9\u51FA\u8EAB\u8C6A\u95E8\uFF0C\u53C8\u662F\u96EA\u5CF0\u795E\u5C3C\u95E8\u4E0B\u9AD8\u5F92\uFF0C\u800C\u4E14\u5929\u751F\u4E3D\u8272\uFF0C\u4ED6\u4E5F\u672A\u653E\u5728\u5FC3\u4E0A\u3002\u4E24\u6307\u63D2\u5165\u5A07\u5AE9\u7684\u82B1\u74E3\u4E00\u5206\uFF0C\u4E0D\u7528\u770B\u4E2D\u6307\u4FBF\u6309\u5728\u82B1\u8482\u4E0A\u3002</p><p>\u4E00\u80A1\u82E5\u6709\u82E5\u65E0\u7684\u771F\u6C14\u4ECE\u6307\u5C16\u9001\u51FA\uFF0C\u7247\u523B\u95F4\u82B1\u74E3\u5185\u4FBF\u9999\u9732\u6697\u6EF4\u3002</p><p>\u4ED6\u63E1\u4F4F\u534A\u786C\u7684\u9633\u5177\u82E6\u7B11\u4E00\u4E0B\uFF0C\u5C06\u9F9F\u5934\u6324\u5165\u7EAA\u7709\u59A9\u4E0B\u4F53\u98A4\u6296\u7684\u5AE9\u8089\u5185\uFF0C\u4E24\u624B\u6276\u5728\u8170\u540E\u7F13\u7F13\u8FDB\u5165\u3002</p><p>\u677E\u624B\u540E\u5373\u523B\u5408\u7D27\u7684\u4E24\u7247\u7EA2\u8089\u6E10\u6E10\u9F13\u6210\u5706\u5F62\uFF0C\u7EAA\u7709\u59A9\u6D51\u8EAB\u7684\u8840\u6DB2\u90FD\u51DD\u4F4F\u4E86\uFF0C\u53E3\u9F3B\u95F4\u547C\u5438\u505C\u987F\uFF0C\u5168\u90E8\u5FC3\u795E\u90FD\u653E\u5728\u88AB\u5F02\u7269\u4E0D\u65AD\u8FDB\u5165\u7684\u79D8\u5904\u3002</p><p>\u6B63\u5728\u7D27\u7A84\u6ED1\u817B\u7684\u8089\u7A74\u5185\u7A7F\u884C\u7684\u9F9F\u5934\u4E00\u987F\uFF0C\u89E6\u5230\u4E00\u7247\u67D4\u97E7\u7684\u8584\u819C\u3002\u6C90\u58F0\u4F20\u4E00\u633A\u8EAB\uFF0C\u9633\u5177\u5C45\u7136\u6CA1\u80FD\u523A\u7A7F\u8584\u819C\u3002\u4ED6\u51B7\u54FC\u4E00\u58F0\uFF0C\u9ED8\u8FD0\u7384\u529F\uFF0C\u8089\u68D2\u987F\u65F6\u575A\u786C\u4F3C\u94C1\u3002\u518D\u5F80\u5185\u4E00\u9001\uFF0C\u7ACB\u523B\u6574\u6839\u6CA1\u5165\u3002</p><p>\u7EAA\u7709\u59A9\u75BC\u5F97\u79C0\u7709\u62E7\u6210\u4E00\u56E2\uFF0C\u6CEA\u6C34\u8FDE\u73E0\u4EF7\u6EDA\u843D\u3002\u5E78\u597D\u6C90\u58F0\u4F20\u5E76\u6CA1\u6709\u5728\u5979\u4F53\u5185\u505C\u7559\u592A\u957F\u65F6\u95F4\uFF0C\u53EA\u62BD\u9001\u7247\u523B\uFF0C\u82CD\u8001\u7684\u9633\u5177\u6296\u52A8\u7740\u5C04\u51FA\u51E0\u6EF4\u7CBE\u6DB2\u4FBF\u9000\u4E86\u51FA\u6765\u3002</p><p>\u9976\u662F\u5982\u6B64\uFF0C\u7EAA\u7709\u59A9\u67D4\u7F8E\u7684\u82B1\u74E3\u95F4\u4E5F\u662F\u843D\u7EA2\u65E0\u6570\u3002\u5979\u5411\u6709\u6D01\u7656\uFF0C\u522B\u4EBA\u7528\u8FC7\u7684\u4E1C\u897F\uFF0C\u5979\u8F7B\u6613\u4E5F\u4E0D\u613F\u53BB\u78B0\u3002\u6B64\u523B\u7ADF\u7136\u88AB\u4EBA\u5728\u4F53\u5185\u5C04\u7CBE\uFF0C\u8FD9\u51E0\u4E4E\u6BD4\u5931\u53BB\u8D1E\u6D01\u66F4\u8BA9\u5979\u5FC3\u788E\u3002</p><p>\u300C\u8001\u4E86\uFF0C\u4E0D\u4E2D\u7528\u4E86\u2026\u2026\u300D\u6C90\u58F0\u4F20\u5174\u81F4\u7D22\u7136\u7684\u4F4E\u53F9\u4E00\u58F0\uFF0C\u88F9\u7D27\u7834\u65E7\u7684\u7EFF\u888D\u3002\u7136\u540E\u7F13\u6B65\u8D70\u5230\u7EAA\u7709\u59A9\u9762\u524D\uFF0C\u624B\u6307\u8F7B\u62C2\uFF0C\u5DF2\u89E3\u5F00\u5979\u988A\u4E0A\u7684\u7A74\u9053\u3002</p><p>\u7EAA\u7709\u59A9\u6BEB\u4E0D\u72B9\u8C6B\u5730\u54AC\u5411\u820C\u5934\u3002\u53EF\u7259\u9F7F\u521A\u521A\u78B0\u5230\u820C\u8089\uFF0C\u5634\u4E2D\u5C31\u591A\u4E86\u4E00\u4E2A\u786C\u5982\u94C1\u77F3\u7684\u7269\u4F53\u3002</p><p>\u6C90\u58F0\u4F20\u4F38\u6307\u6321\u4F4F\u5979\u81EA\u5C3D\u7684\u4F01\u56FE\uFF0C\u76EE\u4E2D\u4E00\u5BD2\uFF0C\u53C8\u5C01\u4E86\u5979\u7684\u7A74\u9053\u3002\u4E24\u773C\u5728\u7EAA\u7709\u59A9\u8EAB\u4F53\u4E0A\u51B7\u51B7\u626B\u89C6\u7247\u523B\uFF0C\u6311\u8D77\u5979\u7684\u8033\u73AF\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u7EAA\u5927\u5C06\u519B\u679C\u7136\u8C6A\u5BCC\uFF0C\u8FD9\u4E24\u7C92\u660E\u73E0\u8D77\u7801\u4E5F\u5F97\u4E09\u5343\u4E24\u94F6\u5B50\u3002\u53EF\u60DC\u53EF\u60DC\uFF0C\u8FD9\u4E2A\u660E\u73E0\u822C\u7684\u5973\u513F\u5374\u4E0D\u503C\u94B1\u2026\u2026\u300D\u4ED6\u6DE1\u6DE1\u8BF4\u7740\uFF0C\u5F39\u6307\u89E3\u5F00\u5927\u725B\u7684\u7A74\u9053\u3002</p><p>\u6C90\u58F0\u4F20\u5BF9\u7EAA\u7709\u59A9\u6BEB\u4E0D\u601C\u60DC\uFF0C\u5C45\u7136\u50CF\u5BF9\u5F85\u4E00\u4E2A\u4E0B\u8D31\u5993\u5973\u822C\uFF0C\u8BA9\u5728\u5EA7\u7684\u4E94\u4E2A\u6CFC\u76AE\u4E00\u4E00\u5978\u6DEB\u8FD9\u4E2A\u96EA\u5CF0\u795E\u5C3C\u9AD8\u5F92\uFF0C\u7EAA\u5E9C\u5343\u91D1\u3002</p><p>\u7EAA\u7709\u59A9\u5E73\u751F\u8FDE\u7537\u5B50\u7684\u624B\u90FD\u6CA1\u6709\u78B0\u8FC7\uFF0C\u88AB\u4E00\u4E2A\u884C\u5C06\u5C31\u6728\u7684\u8001\u5934\u5978\u6DEB\u5DF2\u7ECF\u75DB\u4E0D\u6B32\u751F\uFF0C\u6B64\u65F6\u53C8\u88AB\u8857\u5934\u6CFC\u76AE\u8E42\u8E8F\uFF0C\u5589\u4E2D\u9C9C\u8840\u4E00\u53E3\u63A5\u4E00\u53E3\u6EA2\u51FA\uFF0C\u4E0D\u591A\u65F6\u4FBF\u660F\u4E86\u8FC7\u53BB\u3002</p><p>\u6C90\u58F0\u4F20\u89C1\u7EAA\u7709\u59A9\u53CC\u76EE\u7D27\u95ED\uFF0C\u4E00\u52A8\u4E0D\u52A8\uFF0C\u4FBF\u51FA\u624B\u628A\u5979\u6551\u9192\uFF0C\u597D\u8BA9\u5979\u773C\u7741\u7741\u770B\u7740\u4E00\u4E2A\u53C8\u4E00\u4E2A\u7537\u4EBA\u8F6E\u756A\u8FDB\u5165\u81EA\u5DF1\u4F53\u5185\u3002\u6BCF\u4E00\u4EBA\u5E72\u5B8C\uFF0C\u6C90\u58F0\u4F20\u7ACB\u5373\u653E\u4EBA\uFF0C\u5F85\u4E94\u4EBA\u8D70\u5C3D\u540E\uFF0C\u6700\u540E\u8FDE\u5E97\u8001\u677F\u4E5F\u5206\u4E86\u4E00\u676F\u7FB9\u3002</p><p>\u5929\u8272\u6E10\u4EAE\uFF0C\u7EAA\u7709\u59A9\u4E0B\u4F53\u7EA2\u8089\u7FFB\u5377\uFF0C\u80BF\u6210\u4E00\u56E2\uFF0C\u9C9C\u8840\u628A\u5782\u4E0B\u7684\u5927\u817F\u67D3\u5F97\u901A\u7EA2\uFF0C\u4E00\u76F4\u6D41\u5230\u767D\u5AE9\u7684\u811A\u5C16\u4E0B\u3002\u8089\u7A74\u5185\u704C\u6EE1\u4E03\u4E2A\u7537\u4EBA\u7684\u7CBE\u6DB2\uFF0C\u6B63\u70B9\u70B9\u6EF4\u6EF4\u6EDA\u843D\u51FA\u6765\u3002\u5F53\u6C90\u58F0\u4F20\u89E3\u5F00\u5979\u7684\u7A74\u9053\uFF0C\u7EAA\u7709\u59A9\u50CF\u6B7B\u4E86\u822C\u62AB\u7740\u534A\u5E45\u76AE\u8863\uFF0C\u8F6F\u7EF5\u7EF5\u4F0F\u5728\u5730\u4E0A\uFF0C\u67D4\u7F8E\u5149\u6DA6\u7684\u8EAB\u4F53\u5FAE\u5FAE\u62BD\u6410\u3002</p><p>\u4E24\u4E2A\u7EFF\u8863\u4EBA\u63A8\u95E8\u800C\u5165\uFF0C\u89C1\u5230\u6C90\u58F0\u4F20\u548C\u5730\u4E0A\u7684\u7EAA\u7709\u59A9\u90FD\u662F\u559C\u5F62\u4E8E\u8272\uFF0C\u62B1\u62F3\u9053\uFF1A\u300C\u606D\u559C\u957F\u8001\u7ACB\u5F97\u5927\u529F\uFF01\u300D</p><p>\u6C90\u58F0\u4F20\u53F9\u4E86\u53E3\u6C14\uFF0C\u9053\uFF1A\u300C\u4EC0\u4E48\u5927\u529F\uFF1F\u4E0D\u6B7B\u5C31\u7B97\u8D70\u8FD0\u4E86\u3002\u8FD9\u662F\u90A3\u4E2A\u5C0F\u4E2B\u5934\u7684\u5E08\u59D0\u2026\u2026\u8D70\u5427\uFF0C\u5148\u56DE\u5BAB\u8986\u547D\u3002\u628A\u5979\u5E26\u4E0A\uFF0C\u8DEF\u4E0A\u4E5F\u597D\u6709\u4E2A\u6D88\u9063\u3002\u300D</p><p>\u7EAA\u7709\u59A9\u604D\u82E5\u672A\u95FB\uFF0C\u53EA\u4F0F\u5728\u51B0\u51B7\u7684\u5730\u4E0A\uFF0C\u5446\u5446\u770B\u7740\u773C\u524D\u90A3\u6761\u62B9\u80F8\u3002\u63C9\u6210\u4E00\u56E2\u7684\u6708\u767D\u8272\u4E1D\u7EF8\u5E03\u6EE1\u811A\u5370\uFF0C\u4E0A\u9762\u5AE9\u9EC4\u7684\u5C0F\u82B1\u5DF2\u88AB\u4F17\u4EBA\u8DF5\u8E0F\u5F97\u770B\u4E0D\u51FA\u672C\u6765\u9762\u76EE\u2026\u2026</p>',38);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
