import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_100" tabindex="-1"><a class="header-anchor" href="#_100" aria-hidden="true">#</a> 100</h1><p>\u5F53\u66F9\u5DDE\u4F1A\u7684\u91D1\u521A\u62F3\u6768\u5B8F\u88AB\u6155\u5BB9\u9F99\u4E00\u62F3\u51FB\u788E\u80A9\u80DB\uFF0C\u7A0B\u4E00\u9E4F\u7EC8\u4E8E\u610F\u8BC6\u5230\u81EA\u5DF1\u8BE5\u600E\u4E48\u505A\u4E86\u3002\u4ED6\u5927\u559D\u4E00\u58F0\uFF0C\u5E26\u7740\u81EA\u5DF1\u7684\u4E24\u540D\u624B\u4E0B\u8FD4\u8EAB\u671D\u7075\u7389\u6251\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u58F0\u51B7\u7B11\uFF0C\u5288\u624B\u593A\u8FC7\u6BB5\u73B2\u7684\u67F3\u53F6\u5200\uFF0C\u5200\u5149\u4E00\u95EA\uFF0C\u6BB5\u73B2\u7684\u53CC\u624B\u5DF2\u79BB\u4F53\u800C\u53BB\u3002</p><p>\u4E0D\u8FC7\u4E00\u523B\u949F\uFF0C\u56F4\u653B\u7684\u767D\u9053\u7FA4\u4FA0\u5DF2\u6709\u534A\u6570\u5012\u5728\u8840\u6CCA\u4E4B\u4E2D\uFF0C\u6B64\u65F6\u4F17\u4EBA\u624D\u89C1\u8BC6\u4E86\u661F\u6708\u6E56\u7684\u72E0\u8FA3\uFF0C\u4E8C\u5341\u4F59\u4EBA\u5C3D\u662F\u4E00\u62DB\u4FBF\u80A2\u6B8B\u81C2\u65AD\uFF0C\u5374\u65E0\u4E00\u4EBA\u6B92\u547D\u3002\u904D\u5730\u7684\u4F24\u8005\u6323\u624E\u54C0\u53F7\uFF0C\u60E8\u4E0D\u5FCD\u7779\u3002\u770B\u5230\u7A0B\u4E00\u9E4F\u7684\u4E3E\u52A8\uFF0C\u5176\u4ED6\u4EBA\u4E5F\u90FD\u660E\u767D\u8FC7\u6765\uFF0C\u4E00\u7A9D\u8702\u671D\u7075\u7389\u6740\u53BB\uFF0C\u53EA\u6C42\u80FD\u79BB\u90A3\u4E2A\u715E\u661F\u8D8A\u8FDC\u8D8A\u597D\u3002</p><p>\u8F6C\u773C\u95F4\uFF0C\u6155\u5BB9\u9F99\u8EAB\u524D\u53EA\u5269\u4E0B\u4E00\u540D\u5BF9\u624B\u3002</p><p>\u6155\u5BB9\u9F99\u9976\u6709\u5174\u8DA3\u5730\u6253\u91CF\u7740\u5979\uFF0C\u6DE1\u7B11\u9053\uFF1A\u300C\u6211\u4E0D\u4F24\u4F60\uFF0C\u4F1A\u7ED9\u4F60\u7559\u4E2A\u5168\u5C38\u3002\u300D</p><p>\u5BB9\u9526\u5589\u5934\u53D1\u5E72\uFF0C\u53EA\u60F3\u8F6C\u8EAB\u9003\u8DD1\uFF0C\u5374\u65E0\u6CD5\u629B\u4E0B\u73B2\u59D0\u2026\u2026</p><p>\u7075\u7389\u9762\u5BF9\u8FD9\u7FA4\u7EA2\u4E86\u773C\u7684\u9AD8\u624B\u4ECD\u662F\u4E00\u6D3E\u4ECE\u5BB9\uFF0C\u4ED6\u4E00\u770B\u4E09\u4EBA\u7684\u6B65\u6CD5\uFF0C\u4FBF\u77E5\u51B2\u5728\u6700\u524D\u9762\u7684\u7A0B\u4E00\u9E4F\u6697\u7559\u4E86\u51E0\u5206\u529B\u6C14\u3002</p><p>\u679C\u7136\uFF0C\u79BB\u7075\u7389\u8FD8\u6709\u4E24\u4E08\uFF0C\u7A0B\u4E00\u9E4F\u7A81\u7136\u811A\u6B65\u4E00\u505C\uFF0C\u4E0E\u4E24\u540D\u624B\u4E0B\u9519\u8EAB\u800C\u8FC7\uFF0C\u63A5\u7740\u62D4\u5730\u800C\u8D77\u3002</p><p>\u521A\u521A\u63A0\u4E0A\u6811\u679D\uFF0C\u90A3\u4E2A\u5E94\u8BE5\u88AB\u624B\u4E0B\u7F20\u4F4F\u7684\u9053\u4EBA\u500F\u5FFD\u8DC3\u5230\u5934\u9876\uFF0C\u811A\u5C16\u5728\u4ED6\u80A9\u4E0A\u4E00\u70B9\u3002\u7A0B\u4E00\u9E4F\u5934\u4E0B\u811A\u4E0A\u7B14\u76F4\u6389\u4E0B\u6811\u6765\uFF0C\u6697\u53EB\u300C\u6211\u547D\u4F11\u77E3\u2026\u2026\u300D</p><p>\u7075\u7389\u6B63\u5F85\u5236\u4E0A\u524D\u4F4F\u4ED6\u7684\u7A74\u9053\uFF0C\u5FFD\u7136\u5FC3\u751F\u8B66\u5146\u3002</p><p>\u7D2B\u73AB\u7B49\u7684\u5C31\u662F\u8FD9\u4E00\u523B\uFF0C\u7075\u7389\u8DC3\u8D77\u7684\u540C\u65F6\uFF0C\u5979\u4E5F\u4E00\u8DC3\u800C\u8D77\uFF0C\u6BEB\u4E0D\u72B9\u8C6B\u5730\u62B1\u7740\u6BCD\u4EB2\u63A0\u5165\u67AB\u6797\u3002</p><p>\u7075\u7389\u53C8\u60CA\u53C8\u6C14\uFF0C\u987E\u4E0D\u5F97\u622A\u6740\u4F17\u4EBA\uFF0C\u8FDE\u5FD9\u6298\u8EAB\u8FFD\u8D76\u3002</p><p>\u300C\u547C\u300D\u7684\u4E00\u58F0\uFF0C\u88F9\u7740\u8427\u4F5B\u5974\u7684\u8C82\u88D8\u6C89\u7538\u7538\u5F80\u6A2A\u91CC\u98DE\u51FA\uFF0C\u6295\u5411\u5C71\u5D16\u3002\u7075\u7389\u77E5\u9053\u5982\u592B\u4EBA\u5728\u5BAB\u4E3B\u5FC3\u76EE\u4E2D\u7684\u4EFD\u91CF\uFF0C\u4E0D\u6562\u7A0D\u6709\u8FDF\u7591\uFF0C\u6025\u6025\u5C55\u5F00\u8EAB\u5F62\uFF0C\u5C31\u5730\u4E00\u4E2A\u7FFB\u6EDA\uFF0C\u7A33\u7A33\u63A5\u4F4F\u8C82\u88D8\u3002</p><p>\u752B\u4E00\u5165\u624B\uFF0C\u7075\u7389\u7ACB\u77E5\u4E0D\u5999\uFF0C\u8C82\u88D8\u4F9D\u65E7\uFF0C\u91CC\u9762\u7684\u8427\u4F5B\u5974\u5374\u65E0\u5F71\u65E0\u8E2A\u3002</p><p>\u8FD9\u65F6\u7FA4\u4FA0\u53C8\u51B2\u4E86\u8FC7\u6765\uFF0C\u5200\u67AA\u5E76\u4E3E\u671D\u62E6\u8DEF\u7684\u7075\u7389\u780D\u6765\u3002\u7075\u7389\u81EA\u8D1F\u6587\u6B66\u53CC\u5168\uFF0C\u5374\u88AB\u5C0F\u4E2B\u5934\u6446\u4E86\u4E00\u9053\uFF0C\u5FC3\u4E0B\u6C14\u607C\uFF0C\u4E0D\u5F85\u8D77\u8EAB\u4FBF\u626C\u8D77\u8C82\u88D8\uFF0C\u5C06\u5F53\u5148\u4E00\u4EBA\u6253\u5F97\u6D51\u8EAB\u662F\u8840\u3002</p><p>\u9AD8\u624B\u867D\u7136\u90FD\u5DF2\u79BB\u5F00\uFF0C\u4F46\u751F\u6B7B\u5173\u5934\uFF0C\u4F17\u4EBA\u90FD\u62FC\u4E0A\u5341\u4E8C\u5206\u7684\u529B\u6C14\uFF0C\u4EE5\u7075\u7389\u4E4B\u80FD\u4E00\u65F6\u95F4\u4E5F\u88AB\u7F20\u5F97\u8131\u8EAB\u4E0D\u5F97\u3002</p><p>\u4E00\u6761\u4EBA\u5F71\u9B3C\u9B45\u822C\u98D8\u4E86\u8FC7\u6765\uFF0C\u53CC\u638C\u5728\u4E24\u4EBA\u5934\u4E0A\u4E00\u6309\uFF0C\u501F\u52BF\u671D\u6797\u4E2D\u63A0\u53BB\u3002\u7075\u7389\u538B\u529B\u4E00\u8F7B\uFF0C\u7ACB\u5373\u4E22\u5F00\u8C82\u88D8\uFF0C\u62C2\u5C18\u4E0A\u626B\u4E0B\u6311\u5C06\u7FA4\u4FA0\u6321\u5728\u72ED\u7A84\u7684\u5C71\u8DEF\u95F4\u3002</p><p>\u6155\u5BB9\u9F99\u62C5\u5FC3\u7684\u5C31\u662F\u5C0F\u4E2B\u5934\u8D81\u4E71\u9003\u8D70\uFF0C\u6240\u4EE5\u624D\u65BD\u8BA1\u652F\u8D70\u5706\u76F8\u7B49\u4EBA\u3002\u4FEE\u70BC\u591A\u65F6\u7684\u592A\u4E00\u7ECF\u6025\u5267\u6500\u4E0A\u5DC5\u5CF0\uFF0C\u55C5\u89C9\u3001\u89C6\u89C9\u77AC\u65F6\u63D0\u9AD8\u767E\u500D\uFF0C\u4ED6\u987A\u7740\u82E5\u6709\u82E5\u65E0\u7684\u8309\u8389\u82B1\u9999\uFF0C\u4E00\u8DEF\u8FFD\u8FDB\u67AB\u6797\u6DF1\u5904\u3002</p><p>\u7D2B\u73AB\u4E0D\u987E\u4E00\u5207\u5730\u50AC\u53D1\u51E4\u51F0\u771F\u6C14\uFF0C\u8FF3\u76F4\u7A7F\u8FC7\u67AB\u6797\u3002\u8FD9\u6837\u7684\u673A\u4F1A\u52BF\u96BE\u518D\u6709\uFF0C\u6B64\u65F6\u4E0D\u8D70\uFF0C\u8FD9\u8F88\u5B50\u5C31\u4E0D\u7528\u79BB\u5F00\u90A3\u4E2A\u9B54\u7A9F\u4E86\u3002\u5979\u73B0\u5728\u6700\u5927\u7684\u95EE\u9898\u4E0D\u662F\u53EA\u5269\u4E09\u6210\u7684\u529F\u529B\uFF0C\u4E5F\u4E0D\u662F\u6000\u91CC\u7684\u8427\u4F5B\u5974\uFF0C\u800C\u662F\u8179\u4E2D\u7684\u90A3\u4E2A\u5B7D\u79CD\uFF01\u521A\u5C55\u5F00\u8F7B\u529F\uFF0C\u90A3\u5BB6\u4F19\u5C31\u4E0D\u5B89\u4EFD\u5730\u52A8\u4E86\u8D77\u6765\u3002</p><p>\u8427\u4F5B\u5974\u4FCF\u8138\u96EA\u767D\uFF0C\u60CA\u6050\u5730\u671B\u7740\u5973\u513F\uFF0C\u4EE5\u5979\u7684\u67D4\u5F31\uFF0C\u6839\u672C\u60F3\u4E0D\u5230\u5973\u513F\u7ADF\u7136\u6562\u9003\u8DD1\u3002\u5982\u679C\u8BA9\u6293\u5230\u2026\u2026</p><p>\u7D2B\u73AB\u7684\u8138\u4E0A\u663E\u51FA\u4E00\u5C42\u5E76\u975E\u8840\u8272\u7684\u8273\u7EA2\uFF0C\u8FD9\u662F\u51E4\u51F0\u5B9D\u5178\u6781\u529B\u8FD0\u8F6C\u7684\u5F81\u5146\u3002\u8FD9\u4E9B\u65E5\u5B50\u5979\u4E0E\u6155\u5BB9\u9F99\u4E0D\u65AD\u5728\u4EA4\u5408\u4E2D\u53CC\u4FEE\u795E\u529F\uFF0C\u5F7C\u6B64\u90FD\u5927\u83B7\u5176\u5229\u3002\u79BB\u5F00\u9F99\u57CE\u4E4B\u540E\uFF0C\u5979\u53D1\u73B0\u81EA\u5DF1\u4E0D\u77E5\u4E0D\u89C9\u4E2D\u5DF2\u7ECF\u5230\u8FBE\u7B2C\u4E03\u5C42\u51E4\u9E23\u671D\u9633\uFF0C\u867D\u7136\u8FD8\u4E0D\u53CA\u5E08\u7236\u529F\u529B\u7CBE\u6E5B\u6DF1\u539A\uFF0C\u4F46\u5DF2\u975E\u540C\u5C0F\u53EF\u3002</p><p>\u501A\u4ED7\u7EDD\u9876\u8F7B\u529F\uFF0C\u53EA\u5269\u4E09\u6210\u529F\u529B\u7684\u7D2B\u73AB\u53D1\u6325\u51FA\u4E0D\u900A\u5F80\u65E5\u7684\u9AD8\u901F\uFF0C\u8F6C\u773C\u4FBF\u63A0\u51FA\u91CC\u8BB8\u3002\u5979\u63D0\u4E86\u53E3\u771F\u6C14\uFF0C\u5E73\u5E73\u8D8A\u8FC7\u4E09\u4E08\u7684\u8DDD\u79BB\uFF0C\u671D\u4E00\u6839\u62C7\u6307\u7C97\u7684\u6811\u679D\u843D\u4E0B\u3002</p><p>\u300C\u5361\u300D\uFF0C\u5E72\u67AF\u7684\u6811\u679D\u7ECF\u4E0D\u4F4F\u4E24\u4E2A\u5927\u4EBA\u548C\u4E24\u4E2A\u80CE\u513F\u7684\u91CD\u91CF\uFF0C\u7ACB\u65F6\u6298\u65AD\u3002\u7D2B\u73AB\u843D\u5730\u4E00\u4E2A\u8E09\u8DC4\uFF0C\u5979\u8FDE\u5FD9\u6258\u7A33\u6BCD\u4EB2\uFF0C\u81EA\u5DF1\u8179\u5185\u5374\u662F\u4E00\u9635\u75BC\u75DB\u3002\u80CE\u513F\u4F3C\u4E4E\u4E0D\u6EE1\u610F\u6BCD\u4F53\u7684\u5267\u70C8\u8FD0\u52A8\uFF0C\u6124\u6124\u7136\u8E22\u6253\u8D77\u6765\u3002</p><p>\u300C\u54FC\u3002\u300D\u719F\u6089\u7684\u51B7\u54FC\u58F0\u5728\u8EAB\u540E\u54CD\u8D77\u3002</p><p>\u7D2B\u73AB\u5FC3\u5FF5\u7535\u8F6C\uFF0C\u4F0F\u5728\u6BCD\u4EB2\u8033\u8FB9\u5C0F\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u6211\u4E00\u5B9A\u4F1A\u6765\u6551\u4F60\u3002\u300D\u8A00\u7F62\uFF0C\u5C55\u81C2\u5C06\u8427\u4F5B\u5974\u671D\u4FA7\u540E\u65B9\u629B\u53BB\u3002</p><p>\u8427\u4F5B\u5974\u5931\u58F0\u60CA\u547C\uFF0C\u773C\u770B\u81EA\u5DF1\u8981\u6454\u5728\u6811\u5E72\u4E0A\uFF0C\u5706\u7741\u7684\u7F8E\u76EE\u6B7B\u6B7B\u95ED\u7D27\u3002</p><p>\u6155\u5BB9\u9F99\u7565\u4E00\u72B9\u8C6B\uFF0C\u8FD8\u662F\u54AC\u7740\u7259\u63A5\u8FC7\u6BCD\u4EB2\u3002</p><p>\u8427\u4F5B\u5974\u8EAB\u5B50\u4E00\u6C89\uFF0C\u843D\u5728\u4E00\u53CC\u575A\u5B9E\u7684\u624B\u81C2\u4E0A\u3002\u5979\u53C8\u5BB3\u6015\u53C8\u59D4\u5C48\u5730\u53EB\u4E86\u58F0\uFF1A\u300C\u9F99\u54E5\u2026\u2026\u54E5\u2026\u2026\u300D\u73E0\u6CEA\u7EB7\u7136\u800C\u843D\u3002</p><p>\u7D2B\u73AB\u4E00\u624B\u6276\u7740\u5C0F\u8179\uFF0C\u5F3A\u5FCD\u7740\u8179\u5185\u7684\u9707\u52A8\uFF0C\u817E\u8EAB\u633D\u4F4F\u4E00\u679D\u6EF4\u8840\u7684\u67AB\u679D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u591C\u8272\u5982\u58A8\uFF0C\u67AB\u6797\u518D\u6CA1\u6709\u90A3\u79CD\u523A\u76EE\u7684\u7EA2\u8272\uFF0C\u53EA\u9ED1\u6C89\u6C89\u6302\u5728\u679D\u68A2\uFF0C\u5B9B\u5982\u4E00\u4E32\u51DD\u56FA\u7684\u8840\u8FF9\u3002</p><p>\u6155\u5BB9\u9F99\u62D4\u5F00\u74F6\u585E\uFF0C\u72E0\u72E0\u704C\u4E86\u53E3\u9152\uFF0C\u76EE\u5149\u51B7\u51B0\u51B0\u626B\u8FC7\u5168\u573A\u3002\u5176\u5B9E\u4E0D\u5FC5\u7528\u76EE\u5149\uFF0C\u5355\u662F\u68EE\u5BD2\u7684\u6740\u6C14\u4FBF\u4EE4\u4EBA\u4E0D\u5BD2\u800C\u6817\uFF0C\u4ED6\u9634\u90C1\u7684\u9762\u8272\uFF0C\u8FDE\u7075\u7389\u4E5F\u5C0F\u5FC3\u8D77\u6765\u3002</p><p>\u826F\u4E45\uFF0C\u6155\u5BB9\u9F99\u653E\u4E0B\u9152\u74F6\uFF0C\u6DE1\u6DE1\u7684\u8BF4\u9053\uFF1A\u300C\u4F60\u8F7B\u529F\u5F88\u597D\u3002\u6000\u7740\u5B69\u5B50\u8FD8\u80FD\u8DD1\u8FD9\u4E48\u5FEB\u3002\u300D</p><p>\u7D2B\u73AB\u51B7\u51B7\u9053\uFF1A\u300C\u89E3\u5F00\u6211\u7684\u7A74\u9053\uFF0C\u6211\u8DDF\u4F60\u518D\u6BD4\u4E00\u6B21\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u600E\u4F1A\u88AB\u5979\u6FC0\u4F4F\uFF0C\u6700\u521D\u64D2\u4E0B\u7D2B\u73AB\u3002\u662F\u5F53\u5979\u7CBE\u529B\u8017\u5C3D\u624D\u4E00\u51FB\u594F\u6548\u3002\u8FD9\u6B21\u8DDF\u4E00\u4E2A\u6000\u5B55\u4E94\u4E2A\u6708\u7684\u5C0F\u4E2B\u5934\u5728\u67AB\u6797\u8FFD\u9010\u4E86\u4E00\u523B\u949F\u624D\u628A\u5979\u64D2\u4E0B\uFF0C\u771F\u8BA9\u5979\u6062\u590D\u4E86\u5341\u6210\u529F\u529B\uFF0C\u540E\u679C\u96BE\u6599\u2014\u2014\u5979\u600E\u4E48\u53D8\u5F97\u8FD9\u4E48\u5389\u5BB3\uFF1F\u6155\u5BB9\u9F99\u7740\u5B9E\u4E0D\u89E3\u3002\u73B0\u5728\u5927\u5C40\u5DF2\u5B9A\uFF0C\u4ECA\u540E\u7EDD\u4E0D\u80FD\u518D\u7ED9\u5979\u4EFB\u4F55\u673A\u4F1A\uFF01</p><p>\u8427\u4F5B\u5974\u63D0\u5FC3\u540A\u80C6\uFF0C\u751F\u6015\u513F\u5B50\u4F1A\u6298\u78E8\u5973\u513F\u3002\u5E78\u597D\u6155\u5BB9\u9F99\u53EA\u8BF4\u4E86\u4E00\u53E5\u4FBF\u4E0D\u518D\u7406\u4F1A\u7D2B\u73AB\u3002\u4F46\u4ED6\u7684\u7B2C\u4E00\u53E5\u8BDD\u5374\u8BA9\u5979\u60CA\u5F97\u77AA\u5927\u7F8E\u76EE\u3002</p><p>\u300C\u90A3\u4E2A\u8001\u5BB6\u4F19\u662F\u4F60\u7684\u59D8\u5934\u5417\uFF1F\u300D</p><p>\u300C\u4E0D\u2026\u2026\u4E0D\u662F\u4E0D\u662F\u2026\u2026\u300D\u8427\u4F5B\u5974\u62FC\u547D\u6447\u5934\u3002</p><p>\u300C\u770B\u4ED6\u7684\u60C5\u5F62\u5F88\u6709\u4E9B\u53EF\u7591\u2026\u2026\u4F60\u4EE5\u524D\u52FE\u5F15\u8FC7\u4ED6\uFF1F\u300D</p><p>\u300C\u6CA1\u6709\u2026\u2026\u300D\u7F8E\u5987\u5E26\u7740\u54ED\u8154\u5206\u8FA9\u9053\u3002</p><p>\u6155\u5BB9\u9F99\u7B49\u8427\u4F5B\u5974\u6025\u5F97\u54ED\u51FA\u6765\uFF0C\u624D\u6162\u58F0\u9053\uFF1A\u300C\u505A\u6211\u7684\u5C0F\u59BE\u5C31\u8981\u5B88\u5987\u9053\uFF0C\u5C11\u8DDF\u522B\u7684\u7537\u4EBA\u7709\u6765\u773C\u53BB\uFF0C\u5E84\u91CD\u4E9B\uFF01\u77E5\u9053\u4E86\u5417\uFF1F\u300D</p><p>\u300C\u77E5\u9053\u4E86\u2026\u2026\u300D</p><p>\u5BB9\u9526\u7684\u547C\u5438\u8D8A\u6765\u8D8A\u6025\u4FC3\uFF0C\u5F53\u6155\u5BB9\u9F99\u76EE\u5149\u505C\u5728\u8EAB\u4E0A\uFF0C\u5979\u7684\u5FC3\u8DF3\u4E00\u4E0B\u5B50\u505C\u4F4F\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u300C\u4F60\u5F88\u597D\u3002\u4E0D\u7528\u50CF\u5979\u4EEC\u4E00\u6837\u3002\u300D</p><p>\u5468\u56F4\u51E0\u5177\u767D\u767D\u7684\u8EAB\u4F53\u4E0D\u4F4F\u6323\u624E\u8815\u52A8\u3002\u9752\u9633\u5927\u4FA0\u7530\u542F\u4E1C\u7684\u9057\u5B40\u6BB5\u73B2\u4E00\u8DB3\u88AB\u94A2\u53C9\u9489\u5728\u6811\u4E0A\uFF0C\u65AD\u4E86\u4E00\u6761\u817F\u7684\u738B\u5FB7\u62FF\u7740\u5B9A\u9633\u5200\u628A\u4E00\u6839\u62F3\u5934\u7C97\u7684\u679D\u5E72\u524A\u6210\u6954\u72B6\uFF0C\u7136\u540E\u6258\u7740\u6BB5\u73B2\u7684\u8170\u81C0\uFF0C\u5C06\u9634\u6237\u5BF9\u7740\u5C3A\u8BB8\u957F\u7684\u6728\u6954\u5957\u4E0B\u3002\u6BB5\u73B2\u51C4\u58F0\u60E8\u53EB\uFF0C\u5931\u53BB\u4E24\u624B\u7684\u65AD\u81C2\u62FC\u547D\u821E\u52A8\u3002</p><p>\u738B\u5FB7\u9762\u65E0\u8868\u60C5\uFF0C\u4E24\u624B\u4E00\u4F7F\u529B\uFF0C\u6728\u6954\u8D2F\u4F53\u800C\u5165\uFF0C\u786C\u751F\u751F\u628A\u5C11\u5987\u9489\u5728\u6811\u5E72\u4E0A\u3002</p><p>\u62D4\u8D77\u94A2\u53C9\uFF0C\u6BB5\u73B2\u9AD8\u4E3E\u7684\u7C89\u817F\u987F\u65F6\u6ED1\u843D\u4E0B\u6765\u3002\u9EEF\u6DE1\u7684\u5149\u7EBF\u4E0B\uFF0C\u53EA\u89C1\u4E00\u5177\u96EA\u767D\u7684\u5973\u4F53\u51CC\u7A7A\u6A2A\u653E\uFF0C\u4E0A\u8EAB\u540E\u4EF0\uFF0C\u80F8\u524D\u53EA\u5269\u4E24\u4E2A\u8840\u6D1E\uFF0C\u5706\u4E73\u65E9\u5DF2\u65E0\u5F71\u65E0\u8E2A\u3002\u5979\u53CC\u6761\u7389\u817F\u5782\u5728\u8EAB\u4E0B\uFF0C\u4E0B\u4F53\u7D27\u7D27\u8D34\u7740\u7C97\u7CD9\u7684\u6811\u76AE\uFF0C\u9ED1\u8272\u7684\u9C9C\u8840\u55B7\u5728\u6811\u4E0A\uFF0C\u53C8\u6E85\u843D\u5728\u5C0F\u8179\u4E0A\uFF0C\u7136\u540E\u4E00\u5E76\u987A\u7740\u6811\u5E72\u6DCC\u843D\u3002</p><p>\u65C1\u8FB9\u82B1\u6E90\u5E2E\u5E2E\u4E3B\u66F2\u7389\u5A07\u4EF0\u8EAB\u5367\u5728\u4E24\u68F5\u67AB\u6811\u4E4B\u95F4\u3002\u5979\u7684\u4E24\u53EA\u5C0F\u817F\u88AB\u9F50\u9F50\u65A9\u843D\uFF0C\u4E24\u628A\u957F\u5200\u7A7F\u900F\u5706\u6DA6\u7684\u5927\u817F\uFF0C\u5DE6\u53F3\u9489\u5728\u5730\u4E0A\uFF0C\u81C0\u4E0B\u5219\u653E\u7740\u4E00\u53EA\u4E0D\u77E5\u4F55\u4EBA\u7684\u5934\u9885\uFF0C\u5C06\u5979\u4E0B\u4F53\u9AD8\u9AD8\u9876\u8D77\u3002\u65AD\u8DB3\u7AD6\u7740\u652F\u5728\u8111\u540E\uFF0C\u8BA9\u5979\u5934\u62AC\u8D77\uFF0C\u80FD\u770B\u6E05\u81EA\u5DF1\u88AB\u5978\u6DEB\u7684\u6A21\u6837\u3002</p><p>\u6B63\u5728\u5978\u6DEB\u5979\u7684\u662F\u94C1\u9CA8\u5E2E\u526F\u5E2E\u4E3B\u6C99\u5FD7\u52C7\uFF0C\u6BCF\u4E00\u6B21\u62BD\u9001\uFF0C\u66F2\u7389\u5A07\u90FD\u4F1A\u53D1\u51FA\u4E00\u58F0\u91CE\u517D\u822C\u5636\u54D1\u7684\u53EB\u58F0\u3002\u7B49\u6C99\u5FD7\u52C7\u597D\u4E0D\u5BB9\u6613\u62D4\u51FA\u9633\u5177\uFF0C\u66F2\u7389\u5A07\u4E0B\u4F53\u5DF2\u662F\u8840\u8089\u6A21\u7CCA\uFF0C\u518D\u6CA1\u6709\u4E00\u5757\u5B8C\u6574\u7684\u808C\u4F53\u3002\u4ED4\u7EC6\u770B\u53BB\uFF0C\u6C99\u5FD7\u52C7\u7C97\u957F\u9633\u5177\u5176\u5B9E\u662F\u7F20\u7740\u4E00\u5708\u8896\u7BAD\u3002</p><p>\u4E00\u6746\u65AD\u67AA\u7834\u7A7A\u98DE\u6765\uFF0C\u7A7F\u900F\u6C99\u5FD7\u52C7\u7684\u80F8\u819B\u3002</p><p>\u300C\u6CA1\u7528\u7684\u4E1C\u897F\u3002\u300D\u6155\u5BB9\u9F99\u9A82\u4E86\u4E00\u53E5\u3002\u7136\u540E\u5BF9\u7740\u5782\u7740\u66F2\u7389\u5A07\u9053\uFF1A\u300C\u50CF\u8FD9\u6837\u88AB\u4EBA\u64CD\u6B7B\uFF0C\u66F2\u5E2E\u4E3B\u4E0D\u6B62\u4E0B\u8D31\u4E86\u3002\u300D\u8BF4\u7740\u5BF9\u8DEA\u5728\u4E00\u65C1\u7684\u7A0B\u4E00\u9E4F\u5BD2\u58F0\u9053\uFF1A\u300C\u8BA9\u5979\u53D1\u6D6A\u3002\u300D</p><p>\u7A0B\u4E00\u9E4F\u4E00\u8DEF\u4E0A\u5BF9\u66F2\u7389\u5A07\u5618\u5BD2\u95EE\u6696\uFF0C\u9887\u6709\u51E0\u5206\u610F\u601D\uFF0C\u6CA1\u60F3\u5230\u6700\u7EC8\u5374\u770B\u7740\u5979\u88AB\u4EBA\u73A9\u5F97\u7A00\u70C2\u3002\u4F46\u8FD9\u4F1A\u513F\u4FDD\u547D\u8981\u7D27\uFF0C\u4ED6\u63E1\u7740\u67AA\u950B\uFF0C\u5C0F\u5FC3\u5730\u6345\u5165\u770B\u4E0D\u6E05\u6A21\u6837\u7684\u8089\u6D1E\u5185\u6363\u5F04\u8D77\u6765\u3002</p><p>\u66F2\u7389\u5A07\u53EB\u58F0\u8D8A\u6765\u8D8A\u4F4E\uFF0C\u5979\u4E00\u53EA\u4E73\u623F\u76AE\u80A4\u88AB\u6574\u4E2A\u5265\u6389\uFF0C\u53EA\u5269\u4E00\u4E2A\u8840\u7403\u5728\u80F8\u524D\u4E71\u6643\uFF0C\u4EFB\u51ED\u7A0B\u4E00\u9E4F\u5982\u4F55\u5356\u529B\uFF0C\u5F7B\u5E95\u6BC1\u574F\u7684\u4E0B\u4F53\u4E5F\u518D\u65E0\u4E1D\u6BEB\u611F\u89C9\uFF0C\u751A\u81F3\u8FDE\u75DB\u90FD\u6CA1\u6709\u3002</p><p>\u53E6\u4E24\u540D\u5973\u5B50\u88AB\u780D\u65AD\u56DB\u80A2\uFF0C\u5145\u505A\u6155\u5BB9\u9F99\u548C\u7075\u7389\u7684\u5EA7\u6905\uFF0C\u5979\u4EEC\u8FD8\u672A\u66FE\u65AD\u6C14\uFF0C\u4E0D\u65F6\u5728\u4E24\u4EBA\u8EAB\u4E0B\u53D1\u51FA\u75DB\u82E6\u7684\u58F0\u97F3\u3002</p><p>\u88AB\u4FD8\u8005\u4E2D\uFF0C\u552F\u4E00\u5B89\u597D\u7684\uFF0C\u5C31\u662F\u5BB9\u9526\u3002</p><p>\u6728\u6954\u5728\u6BB5\u73B2\u5C0F\u8179\u4E0A\u65B9\u9876\u8D77\u4E00\u4E2A\u9AD8\u9AD8\u7684\u9510\u5C16\uFF0C\u5FFD\u7136\u6811\u679D\u7A7F\u7834\u808C\u80A4\uFF0C\u8840\u6DCB\u6DCB\u9732\u5728\u4F53\u5916\uFF0C\u96EA\u767D\u7684\u809A\u8179\u7559\u4E0B\u4E00\u6761\u5BBD\u957F\u7684\u4F24\u53E3\uFF0C\u5C11\u5987\u6A2A\u653E\u7684\u8EAB\u4F53\u731B\u7136\u4E00\u9707\uFF0C\u91CD\u91CD\u78B0\u7740\u6811\u5E72\uFF0C\u6728\u6954\u5361\u5728\u803B\u9AA8\u95F4\uFF0C\u786C\u6886\u6886\u6311\u5728\u534A\u7A7A\u3002</p><p>\u6155\u5BB9\u9F99\u629B\u51FA\u9152\u74F6\u5C06\u738B\u5FB7\u5934\u9885\u7838\u5F97\u7C89\u788E\uFF0C\u9A82\u9053\uFF1A\u300C\u5C16\u7AEF\u600E\u4E48\u4E0D\u524A\u6210\u5706\u7684\uFF01\u300D</p><p>\u770B\u4E86\u5BB9\u9526\u4E00\u773C\uFF0C\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u98CE\u665A\u534E\u5C31\u5728\u6211\u6559\uFF0C\u56E0\u4E3A\u88AB\u72D7\u64CD\u5F97\u591A\u4E86\uFF0C\u73B0\u5728\u4E5F\u53D8\u6210\u4E86\u4E00\u6761\u6BCD\u72D7\u2026\u2026\u53EF\u60DC\u672C\u5BAB\u6709\u8981\u4E8B\u5728\u8EAB\uFF0C\u4E0D\u80FD\u5E26\u4F60\u53BB\u770B\u3002\u300D</p><p>\u5BB9\u9526\u62B1\u7740\u80A9\u5934\u8737\u7F29\u5728\u6811\u5F71\u4E2D\uFF0C\u4F4E\u58F0\u62BD\u6CE3\u7740\u3002</p><p>\u6155\u5BB9\u9F99\u53F9\u4E86\u53E3\u6C14\uFF0C\u95EE\u9053\uFF1A\u300C\u4F60\u662F\u5904\u5B50\u5417\uFF1F\u300D</p><p>\u5BB9\u9526\u5A07\u8EAF\u4E00\u6296\uFF0C\u6CA1\u6709\u70B9\u5934\uFF0C\u4E5F\u6CA1\u6709\u6447\u5934\u3002</p><p>\u300C\u55EF\uFF0C\u90A3\u5C31\u662F\u4E86\u3002\u6211\u60F3\u4F60\u4E5F\u4E0D\u613F\u88AB\u8FD9\u4E9B\u732A\u72D7\u810F\u4E86\u8EAB\u5B50\u3002\u300D\u6155\u5BB9\u9F99\u4ECE\u8896\u91CC\u6478\u51FA\u4E00\u6839\u534A\u5C3A\u957F\u7684\u7269\u4F53\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u628A\u5B83\u653E\u8FDB\u53BB\uFF0C\u672C\u5BAB\u5C31\u4E0D\u8BA9\u4EFB\u4F55\u4EBA\u78B0\u4F60\u3002\u300D\u4ED6\u770B\u5230\u5BB9\u9526\u7684\u795E\u8272\uFF0C\u53C8\u52A0\u4E86\u4E00\u53E5\uFF0C\u300C\u672C\u5BAB\u8BF4\u8BDD\u7B97\u8BDD\u3002\u300D</p><p>\u5BB9\u9526\u6014\u4E86\u534A\u664C\uFF0C\u63A5\u8FC7\u8361\u661F\u97AD\uFF0C\u4E0D\u77E5\u6240\u63AA\u5730\u62FF\u5728\u624B\u4E2D\u3002</p><p>\u300C\u8863\u670D\u8131\u6389\u2026\u2026\u817F\u6253\u5F00\u2026\u2026\u5BF9\u4E86\u2026\u2026\u63D2\u8FDB\u53BB\u2026\u2026\u6709\u4E00\u70B9\u75DB\uFF0C\u4E0D\u5FC5\u6015\u3002\u300D</p><p>\u4E00\u8FDE\u4E32\u7684\u60E8\u72B6\u65E9\u5DF2\u5413\u5F97\u5C11\u5973\u9762\u65E0\u8840\u8272\u3002\u8C01\u80FD\u60F3\u5230\u8FD9\u4E2A\u8C8C\u4F3C\u548C\u6C14\u7684\u82F1\u4FCA\u7537\u5B50\u7ADF\u662F\u8FD9\u822C\u55DC\u8840\u7684\u6076\u9B54\u3002\u6240\u6709\u7684\u654C\u4EBA\uFF0C\u5728\u4ED6\u773C\u4E2D\u6839\u672C\u7B97\u4E0D\u4E0A\u662F\u4EBA\u2026\u2026\u5979\u54AC\u4F4F\u7EA2\u5507\uFF0C\u4E00\u8FB9\u6D41\u6CEA\u4E00\u8FB9\u628A\u97AD\u67C4\u9876\u5728\u79D8\u5904\u3002\u6F06\u9ED1\u7684\u97AD\u67C4\u6CA1\u5165\u7C89\u7EA2\u7684\u82B1\u74E3\u5185\uFF0C\u5BB9\u9526\u7528\u529B\u4E00\u9001\uFF0C\u4EB2\u624B\u7528\u4E00\u6839\u602A\u5F02\u7684\u624B\u67C4\u6345\u7834\u4E86\u81EA\u5DF1\u7684\u5904\u5B50\u4E4B\u8EAF\u3002</p><p>\u6CBE\u5230\u5904\u5B50\u7684\u8840\u8FF9\uFF0C\u8361\u661F\u97AD\u7684\u4E03\u5B9D\u67C4\u7ACB\u523B\u5149\u5F69\u5927\u73B0\u3002\u7729\u76EE\u7684\u73E0\u5149\u900F\u8FC7\u767D\u5AE9\u7684\u808C\u80A4\uFF0C\u5C06\u5C11\u5973\u7684\u8179\u8154\u6620\u6210\u4E00\u76CF\u5149\u8292\u56DB\u5C04\u7684\u706F\u7B3C\u3002</p><p>\u6155\u5BB9\u9F99\u62E5\u7740\u8427\u4F5B\u5974\u4F4E\u58F0\u7B11\u9053\uFF1A\u300C\u5A18\uFF0C\u597D\u770B\u5417\uFF1F\u8FD9\u91CC\u9762\u662F\u4F60\u7684\u624B\u7B4B\u811A\u7B4B\uFF0C\u5B69\u513F\u603B\u662F\u820D\u4E0D\u5F97\u7528\u5462\u3002\u53EA\u597D\u8BA9\u5B83\u8FD9\u6837\u5C1D\u4E9B\u9C9C\u8840\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u6CA1\u6709\u4F5C\u58F0\uFF0C\u7D2B\u73AB\u5374\u542C\u51FA\u4ED6\u58F0\u97F3\u91CC\u6709\u79CD\u5FC3\u4E0D\u5728\u7109\u7684\u610F\u5473\uFF0C\u4F3C\u4E4E\u5E76\u4E0D\u5728\u610F\u773C\u524D\u8FD9\u4E9B\u8840\u8165\u3002</p><p>\u79CB\u98CE\u626B\u8FC7\u67AB\u6797\uFF0C\u67AF\u53F6\u4EFF\u4F5B\u98D8\u98DE\u7684\u9C9C\u8840\u8427\u8427\u800C\u843D\u3002</p><p>\u51C4\u51B7\u7684\u5C71\u6797\u95F4\uFF0C\u4E00\u4E32\u4E03\u5F69\u7684\u5BD2\u661F\u5189\u5189\u5347\u8D77\uFF0C\u6447\u66F3\u7740\u8D8A\u8FC7\u6797\u68A2\uFF0C\u7F13\u7F13\u5347\u4E0A\u5E7D\u6697\u7684\u82CD\u7A79\u3002</p><p>\u5931\u8840\u8FC7\u591A\uFF0C\u5BB9\u9526\u7684\u5C38\u4F53\u7B3C\u7F69\u7740\u4E00\u5C42\u6726\u80E7\u7684\u67D4\u767D\u5149\u8292\u3002\u4F18\u7F8E\u7684\u5A07\u8EAF\u6829\u6829\u5982\u751F\u3002</p><p>\u5728\u5979\u8179\u8154\u6DF1\u5904\uFF0C\u6EF4\u8840\u7684\u661F\u5149\u5B8C\u5168\u4E0D\u53D7\u808C\u80A4\u7684\u906E\u63A9\uFF0C\u4EFF\u4F5B\u51B0\u51B7\u7684\u773C\u775B\uFF0C\u95EA\u52A8\u7740\u5947\u7279\u7684\u5149\u5F69\u3002</p><p>\u60E8\u5389\u7684\u54C0\u53F7\u968F\u98CE\u901D\u53BB\uFF0C\u53EA\u7559\u4E0B\u4E00\u4E1D\u6D74\u8840\u7684\u53F9\u606F\u58F0\u3002</p>',74);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
