import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_101" tabindex="-1"><a class="header-anchor" href="#_101" aria-hidden="true">#</a> 101</h1><p>\u4E5D\u6708\u5341\u4E03\uFF0C\u7EC8\u5357\u3002</p><p>\u6E56\u5C71\u4F9D\u7136\uFF0C\u677E\u67CF\u4F9D\u7136\uFF0C\u795E\u6BBF\u524D\u7684\u8FCE\u5BBE\u72AC\u4E5F\u662F\u4F9D\u7136\u3002</p><p>\u542C\u5230\u811A\u6B65\u58F0\uFF0C\u8737\u5367\u5728\u9634\u5F71\u91CC\u7684\u88F8\u5973\u7ACB\u5373\u4F0F\u5730\u4E3E\u81C0\uFF0C\u5927\u58F0\u8BF4\u9053\uFF1A\u300C\u98D8\u6885\u5CF0\u7B2C\u4E94\u4EE3\u5F1F\u5B50\uFF0C\u795E\u6559\u8D31\u5974\uFF0C\u5BD2\u6708\u5200\u6797\u5A4A\u5B50\u9999\u8FDC\uFF0C\u8BF7\u4E3B\u5B50\u4EAB\u7528\u3002\u300D</p><p>\u811A\u6B65\u58F0\u5306\u5306\u8D70\u8FC7\uFF0C\u6D88\u5931\u5728\u9AD8\u9AD8\u7684\u795E\u6BBF\u5185\u3002\u6797\u9999\u8FDC\u7B49\u4E86\u7247\u523B\uFF0C\u7D27\u7EF7\u7684\u8089\u4F53\u7F13\u7F13\u677E\u61C8\u4E0B\u6765\u3002\u5979\u8212\u4E86\u53E3\u6C14\uFF0C\u6084\u6084\u632A\u5230\u65C1\u8FB9\uFF0C\u8D34\u7740\u680F\u6746\u4F0F\u5728\u5927\u7406\u77F3\u9636\u4E0A\u3002</p><p>\u77F3\u677F\u53C8\u786C\u53C8\u51B7\uFF0C\u597D\u5728\u5F88\u5149\u6ED1\uFF0C\u6BD4\u300C\u5BB6\u300D\u91CC\u8FD8\u8212\u670D\u2026\u2026\u7A7A\u6D1E\u7684\u53CC\u773C\u671B\u7740\u5929\u9645\uFF0C\u5C11\u5987\u51FA\u795E\u5730\u60F3\u7740\uFF1A\u5929\u6C14\u4E00\u65E5\u65E5\u51C9\u4E86\uFF0C\u4E0D\u77E5\u9053\u81EA\u5DF1\u80FD\u4E0D\u80FD\u6491\u8FC7\u8FD9\u4E2A\u51AC\u5B63\u2026\u2026\u98D8\u6885\u5CF0\u7EC8\u5E74\u79EF\u96EA\uFF0C\u90A3\u65F6\u81EA\u5DF1\u7684\u5185\u529F\u5F88\u5145\u6C9B\uFF0C\u5E76\u6CA1\u6709\u89C9\u5F97\u51B7\u2026\u2026\u5C71\u4E0A\u7684\u6885\u82B1\u771F\u7F8E\u2026\u2026\u53EF\u60DC\u6211\u518D\u4E5F\u770B\u4E0D\u89C1\u4E86\u2026\u2026</p><p>\u5931\u660E\u7684\u6797\u9999\u8FDC\u5E76\u6CA1\u6709\u53D1\u73B0\uFF0C\u521A\u624D\u5306\u5306\u8DEF\u8FC7\u7684\u4EBA\u4E2D\uFF0C\u6709\u4E00\u4E2A\u5973\u5B50\u7559\u4E86\u4E0B\u6765\uFF0C\u9759\u6084\u6084\u7ACB\u5728\u65C1\u8FB9\u3002</p><p>\u51E0\u540D\u5E2E\u4F17\u5FEB\u6B65\u8D70\u4E0A\u53F0\u9636\uFF0C\u8DDD\u79BB\u8FD8\u6709\u4E08\u8BB8\uFF0C\u6797\u9999\u8FDC\u5DF2\u7ECF\u6446\u597D\u59FF\u52BF\uFF0C\u5927\u58F0\u8BF4\u9053\uFF1A\u300C\u98D8\u6885\u5CF0\u7B2C\u4E94\u4EE3\u5F1F\u5B50\uFF0C\u8D31\u5974\u2026\u2026\u300D</p><p>\u300C\u5C11\u592B\u4EBA\uFF01\u300D\u51E0\u4EBA\u9F50\u58F0\u8BF4\u9053\u3002</p><p>\u6797\u9999\u8FDC\u8EAB\u4F53\u4E00\u50F5\uFF0C\u65E9\u5DF2\u8BF4\u719F\u7684\u53E5\u5B50\u7EE7\u7EED\u6D41\u51FA\uFF0C\u300C\u2026\u2026\u6797\u5A4A\u5B50\u9999\u8FDC\uFF0C\u8BF7\u4E3B\u5B50\u2026\u2026\u300D\u58F0\u97F3\u8D8A\u8BF4\u8D8A\u5C0F\uFF0C\u7EC8\u4E8E\u505C\u4F4F\u3002\u4F46\u8FD9\u6B21\u5374\u6CA1\u6709\u4EBA\u6765\u60E9\u7F5A\u5979\u7684\u4E0D\u606D\u3002</p><p>\u300C\u55EF\u3002\u300D\u6155\u5BB9\u7D2B\u73AB\u6DE1\u6DE1\u5E94\u4E86\u4E00\u58F0\u3002</p><p>\u79BB\u5BAB\u65F6\u8FD8\u662F\u521D\u590F\uFF0C\u73B0\u5728\u5DF2\u662F\u79CB\u672B\u3002\u4E0D\u8FC7\u4E94\u4E2A\u6708\u7684\u65F6\u95F4\uFF0C\u82F1\u6C14\u8FEB\u4EBA\u7684\u4E8C\u5E08\u59D0\u5374\u6210\u4E86\u8FD9\u822C\u6A21\u6837\u3002\u8C01\u80FD\u60F3\u5230\uFF0C\u7EB5\u6A2A\u6C5F\u6E56\u672A\u5C1D\u4E00\u8D25\u7684\u5BD2\u6708\u5200\u4F1A\u9762\u4E0D\u6539\u5BB9\u5730\u8BF4\u51FA\u90A3\u4E9B\u5C48\u8FB1\u7684\u8BDD\u8BED\u3002\u5AC2\u5AC2\u5403\u4E86\u5F88\u591A\u82E6\u5427\u2026\u2026</p><p>\u7D2B\u73AB\u62C9\u8D77\u6597\u7BF7\uFF0C\u65CB\u5373\u6539\u53D8\u4E3B\u610F\uFF0C\u53EA\u6DE1\u6DE1\u8BF4\u4E86\u53E5\uFF0C\u300C\u53F6\u62A4\u6CD5\u7684\u836F\u771F\u597D\u3002\u76AE\u80A4\u8FD8\u5F88\u597D\u5462\u3002\u300D\u4FBF\u5934\u4E5F\u4E0D\u56DE\u5730\u767B\u4E0A\u53F0\u9636\u3002</p><p>\u6797\u9999\u8FDC\u50F5\u8DEA\u9636\u4E0A\uFF0C\u76F4\u5230\u6709\u4EBA\u62FD\u8D77\u5934\u53D1\uFF0C\u5979\u624D\u5F20\u5F00\u5634\uFF0C\u773C\u6CEA\u503E\u6CC4\u800C\u51FA\u3002\u54B8\u6DA9\u7684\u6DB2\u4F53\u6EF4\u5728\u4EE4\u4EBA\u4F5C\u5455\u7684\u9633\u7269\u4E0A\uFF0C\u53C8\u88AB\u7EA2\u5507\u9999\u820C\u5377\u5165\u53E3\u5185\u3002\u6797\u9999\u8FDC\u8FA8\u4E0D\u51FA\u5B83\u662F\u56E0\u4E3A\u7F9E\u6127\u3001\u5E0C\u671B\uFF0C\u8FD8\u662F\u56E0\u4E3A\u90A3\u58F0\u97F3\u7684\u51B7\u6F20\u800C\u6D41\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u4E00\u8FB9\u98DE\u5FEB\u5730\u7FFB\u9605\u60C5\u62A5\uFF0C\u4E00\u8FB9\u542C\u6C90\u58F0\u4F20\u8BB2\u89E3\u3002\u4E24\u4E2A\u65F6\u8FB0\u540E\uFF0C\u5DF2\u638C\u63E1\u4E86\u6559\u4E2D\u7684\u5927\u81F4\u60C5\u51B5\u3002</p><p>\u6155\u5BB9\u9F99\u6BEB\u4E0D\u9690\u7792\u5730\u5C06\u5B9D\u85CF\u843D\u7A7A\u4E4B\u4E8B\u5408\u76D8\u6258\u51FA\uFF0C\u6700\u540E\u82E6\u7B11\u7740\u9053\uFF1A\u300C\u62A4\u6CD5\u6240\u8A00\u6781\u662F\uFF0C\u6307\u671B\u5B9D\u85CF\u662F\u4E0D\u6210\u7684\u3002\u5509\uFF0C\u8FD9\u4E00\u8D9F\u4E00\u4E8B\u65E0\u6210\uFF0C\u5F92\u60F9\u8BA5\u7B11\u2026\u2026\u300D</p><p>\u6C90\u58F0\u4F20\u8138\u4E0A\u96BE\u5F97\u5730\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u300C\u5BAB\u4E3B\u8FD9\u4E00\u8D9F\u6536\u670D\u957F\u9E70\u4F1A\uFF0C\u593A\u53D6\u6D1B\u9633\uFF1B\u5728\u585E\u5317\u5168\u6B7C\u516B\u6781\u95E8\uFF1B\u53C8\u5728\u6DBF\u90E1\u51FB\u6E83\u5341\u4E03\u6D3E\u8054\u76DF\u3002\u5982\u4ECA\u5B89\u5B9A\u516B\u6781\u95E8\u52BF\u529B\u5DF2\u88AB\u91D1\u5802\u8FDE\u6839\u62D4\u8D77\uFF0C\u5173\u4E2D\u957F\u5B89\u5DF2\u5C3D\u5165\u795E\u6559\u638C\u63E1\uFF1B\u5341\u65E5\u524D\u4E0A\u8C37\u5206\u8235\u6838\u70B9\u6E05\u695A\uFF0C\u5341\u4E94\u4E2A\u5E2E\u6D3E\u56DB\u5341\u4E03\u540D\u9AD8\u624B\u547D\u4E27\u67AB\u6797\uFF0C\u73B0\u4E0B\u8BF8\u5802\u6B63\u9010\u4E00\u63A5\u6536\u3002\u300D\u4ED6\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u300C\u4F55\u51B5\u5BAB\u4E3B\u8FD8\u5B9A\u4E0B\u9F99\u57CE\u8FD9\u4E00\u6839\u672C\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u5401\u4E86\u53E3\u6C14\uFF0C\u300C\u6C90\u62A4\u6CD5\u52A8\u624B\u597D\u5FEB\uFF0C\u6CA1\u6709\u6D6A\u8D39\u534A\u70B9\u65F6\u95F4\u3002\u300D\u4ED6\u795E\u8272\u51DD\u91CD\u8D77\u6765\uFF0C\u300C\u5728\u9F99\u57CE\u5EFA\u519B\u5F0A\u5904\u751A\u591A\uFF0C\u4EE5\u62A4\u6CD5\u4E4B\u89C1\uFF0C\u8BE5\u5982\u4F55\u5904\u51B3\uFF1F\u300D</p><p>\u300C\u7CAE\u98DF\u7531\u6D77\u8DEF\u8FD0\u53BB\uFF0C\u5F53\u53EF\u907F\u4EBA\u8033\u76EE\u3002\u4ECE\u4E1C\u83B1\u5A01\u8FDC\u5821\u5230\u9F99\u57CE\uFF0C\u6D77\u9646\u4E00\u6708\u5373\u53EF\u5230\u8FBE\u3002\u4FE1\u9E3D\u4E0D\u53CA\u8BAD\u7EC3\uFF0C\u6211\u5DF2\u547D\u71D5\u4E91\u4E00\u5E26\u7684\u5E2E\u4F1A\u5C06\u591A\u4F59\u4FE1\u9E3D\u5C3D\u6570\u9001\u5F80\u4E0A\u8C37\uFF0C\u7EDF\u4E00\u9001\u81F3\u9F99\u57CE\u3002\u63A5\u4FE1\u5E94\u53EF\u65E0\u59A8\uFF0C\u81F3\u4E8E\u4F20\u4EE4\uFF0C\u5C31\u5148\u4F20\u81F3\u4E0A\u8C37\u3002\u5F85\u4E09\u4E2A\u6708\u540E\u4FE1\u9E3D\u7EC3\u6BD5\uFF0C\u5373\u53EF\u76F4\u9001\u9F99\u57CE\uFF0C\u6765\u56DE\u7EA6\u9700\u5341\u4E09\u65E5\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u70B9\u70B9\u5934\uFF0C\u300C\u4E5F\u53EA\u597D\u5982\u6B64\u3002\u300D</p><p>\u7B49\u4E24\u4EBA\u8C08\u5B8C\uFF0C\u53F6\u884C\u5357\u8D77\u8EAB\u9053\uFF1A\u300C\u8FD8\u6709\u4E24\u65E5\u593A\u80CE\u82B1\u5373\u53EF\u6210\u5F62\uFF0C\u5BAB\u4E3B\u8981\u4E0D\u8981\u5148\u770B\u4E00\u4E0B\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u7565\u4E00\u601D\u7D22\uFF0C\u7B11\u9053\uFF1A\u300C\u5C4A\u65F6\u518D\u770B\u4E0D\u8FDF\u3002\u96EA\u5CF0\u8D31\u4EBA\u73B0\u5728\u5982\u4F55\uFF1F\u6797\u5A4A\u5B50\u8C03\u6559\u5F97\u4E0D\u9519\u3002\u300D</p><p>\u53F6\u884C\u5357\u53F9\u9053\uFF1A\u300C\u96EA\u5CF0\u5FC3\u5FD7\u521A\u5F3A\u4E4B\u6781\uFF0C\u6628\u65E5\u4E00\u540D\u5C5E\u4E0B\u4E00\u65F6\u4E0D\u614E\uFF0C\u8FD8\u88AB\u5979\u54AC\u6210\u91CD\u4F24\u2026\u2026\u300D</p><p>\u300C\u54E6\uFF1F\u300D\u6155\u5BB9\u9F99\u4E00\u6014\uFF0C\u65CB\u5373\u54C8\u54C8\u5927\u7B11\uFF0C\u300C\u8FD9\u8D31\u4EBA\u8FD8\u771F\u80FD\u633A\uFF01\u300D\u4ED6\u76EE\u5149\u5E7D\u5E7D\u4E00\u95EA\uFF0C\u58F0\u97F3\u51B7\u9759\u4E0B\u6765\uFF0C\u300C\u4F20\u4EE4\u5C5E\u4E0B\u5404\u5E2E\u62E3\u9009\u5904\u5B50\u3002\u6BCF\u4E24\u65E5\uFF0C\u5BAB\u4E2D\u9700\u7528\u4E00\u4EBA\u3002\u300D</p><p>\u53F6\u884C\u5357\u4E00\u542C\u4FBF\u77E5\u7528\u9014\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u5BAB\u4E3B\uFF0C\u8FD8\u5929\u8BC0\u867D\u53EF\u901F\u6210\uFF0C\u4F46\u5BF9\u9F0E\u7089\u9009\u62E9\u6781\u4E25\uFF0C\u7E41\u590D\u96BE\u7EC3\uFF0C\u5904\u5904\u51F6\u9669\u2026\u2026\u300D</p><p>\u6C90\u58F0\u4F20\u4E5F\u9053\uFF1A\u300C\u81EA\u592A\u51B2\u5BAB\u4E3B\u529F\u8D25\u5782\u6210\u540E\uFF0C\u767E\u4F59\u5E74\u6765\u518D\u65E0\u4EBA\u7EC3\u8FC7\u6B64\u529F\uFF0C\u8BF7\u5BAB\u4E3B\u4E09\u601D\u3002\u300D</p><p>\u300C\u987E\u4E0D\u5F97\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u5927\u5B5A\u7075\u9E6B\u5BFA\u6B63\u5728\u7EC8\u5357\u4E0E\u9F99\u57CE\u4E2D\u95F4\uFF0C\u662F\u6211\u5FC3\u8179\u5927\u60A3\uFF0C\u6211\u4E0E\u5706\u76F8\u4EA4\u8FC7\u624B\uFF0C\u4ED6\u7684\u53C2\u7985\u638C\u4E0D\u6613\u5BF9\u4ED8\u3002\u300D</p><p>\u6C90\u58F0\u4F20\u8FD8\u5728\u505A\u6700\u540E\u7684\u52AA\u529B\uFF0C\u300C\u73B0\u4E0B\u6211\u6559\u5B9E\u529B\u5927\u589E\uFF0C\u4E0D\u5982\u5C3D\u8D77\u7CBE\u9510\uFF0C\u51B3\u6218\u6E05\u51C9\u5C71\u3002\u300D</p><p>\u300C\u65F6\u95F4\u53EA\u6709\u4E0D\u8DB3\u4E24\u5E74\uFF0C\u90A3\u91CC\u8FD8\u80FD\u62BD\u8C03\u4EBA\u624B\u2026\u2026\u300D</p><p>\u6C90\u58F0\u4F20\u548C\u53F6\u884C\u5357\u6C89\u9ED8\u4E0B\u6765\uFF0C\u5BAB\u4E3B\u884C\u4E8B\u672A\u514D\u592A\u6025\uFF0C\u4E24\u5E74\u4E4B\u5185\u8D77\u4E8B\uFF0C\u80DC\u7B97\u6781\u5C11\u2026\u2026\u53EA\u597D\u60F3\u529E\u6CD5\u591A\u6293\u673A\u4F1A\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u5F53\u7D2B\u73AB\u4E0D\u5B58\u5728\u822C\uFF0C\u6728\u7740\u8138\u626C\u957F\u800C\u8FC7\u3002\u5012\u662F\u53F6\u884C\u5357\u505C\u4E0B\u811A\u6B65\uFF0C\u4ED4\u7EC6\u770B\u7740\u5979\u7684\u6C14\u8272\uFF0C\u76B1\u7709\u9053\uFF1A\u300C\u5DF2\u7ECF\u4E94\u4E2A\u6708\u4E86\uFF0C\u600E\u4E48\u8FD8\u6562\u5984\u7528\u771F\u6C14\uFF1F\u4E0D\u8981\u547D\u4E86\u5417\uFF1F\u624B\u4F38\u51FA\u6765\u3002\u300D</p><p>\u7D2B\u73AB\u4E56\u4E56\u4F38\u51FA\u624B\u8155\uFF0C\u8BA9\u4ED6\u8BCA\u8109\u3002</p><p>\u53F6\u884C\u5357\u9762\u8272\u6E10\u6E10\u5E73\u548C\uFF0C\u534A\u664C\u540E\u5FAE\u7B11\u9053\uFF1A\u300C\u8FD9\u5B69\u5B50\u6C14\u8840\u4E4B\u58EE\uFF0C\u5B9E\u662F\u5C11\u6709\u3002\u300D</p><p>\u7D2B\u73AB\u67D4\u58F0\u9053\uFF1A\u300C\u6211\u60F3\u89C1\u89C1\u5E08\u7236\u3002\u300D</p><p>\u6B66\u51E4\u522B\u9662\u7684\u623F\u95E8\u5F62\u540C\u865A\u8BBE\uFF0C\u65E0\u8BBA\u4EFB\u4F55\u4EBA\u4EFB\u4F55\u65F6\u5019\uFF0C\u53EA\u8981\u60F3\u6765\u5C31\u53EF\u4EE5\u4EE5\u4E00\u6587\u94B1\u7684\u4EE3\u4EF7\u8D70\u8FDB\u8FD9\u6247\u95E8\u3002\u56E0\u6B64\u7D2B\u73AB\u8FDB\u95E8\u5148\u770B\u5230\u7684\uFF0C\u5C31\u662F\u90A3\u53E3\u5927\u7F38\u3002\u7F38\u5185\u5806\u6EE1\u94DC\u94B1\uFF0C\u6570\u91CF\u96BE\u8BA1\u3002</p><p>\u5BA4\u5185\u6302\u7740\u4E00\u5E45\u539A\u539A\u7684\u5E03\u5EC9\uFF0C\u9ED1\u6C89\u6C89\u5EC9\u95F4\u7A81\u5140\u5730\u7FD8\u7740\u4E00\u53EA\u96EA\u81C0\uFF0C\u5149\u6E9C\u6E9C\u53C8\u5706\u53C8\u5927\uFF0C\u5B9B\u5982\u94F6\u76C6\u3002\u80A1\u95F4\u76DB\u5F00\u7684\u8089\u82B1\u7FFB\u51FA\u8DB3\u6709\u4E24\u624B\u5927\u5C0F\uFF0C\u7EA2\u561F\u561F\u4E00\u7247\u3002\u5265\u6389\u5305\u76AE\u7684\u8089\u82BD\u50CF\u4E00\u6839\u9C9C\u7EA2\u7684\u624B\u6307\uFF0C\u633A\u7136\u800C\u7ACB\u3002\u968F\u7740\u6C89\u91CD\u7684\u547C\u5438\uFF0C\u8089\u82B1\u5FAE\u5FAE\u7FD5\u5408\uFF0C\u5AE9\u8089\u95F4\u51E0\u7F15\u900F\u660E\u6DB2\u4F53\uFF0C\u5FAE\u6643\u7740\u9ECF\u4E4E\u4E4E\u62D6\u5728\u81C0\u4E0B\uFF0C\u8D8A\u5782\u8D8A\u957F\u3002</p><p>\u9976\u662F\u7D2B\u73AB\u65E9\u6709\u51C6\u5907\uFF0C\u770B\u5230\u53EA\u5269\u6027\u5668\u5728\u5916\uFF0C\u8FDE\u5A3C\u5993\u4E5F\u4E0D\u5982\u7684\u5E08\u7236\uFF0C\u4E5F\u4E0D\u7981\u5FC3\u5934\u523A\u75DB\u3002\u9F3B\u95F4\u4E00\u9178\uFF0C\u6CEA\u6C34\u5DF2\u6A21\u7CCA\u4E86\u53CC\u773C\u3002\u5979\u8FDE\u5FD9\u6293\u4E86\u628A\u94DC\u94B1\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u8FD9\u4E48\u591A\uFF0C\u5E72\u4EC0\u4E48\u7528\u7684\uFF1F\u300D\u85C9\u6B64\u63A9\u9970\u81EA\u5DF1\u7684\u5931\u6001\u3002</p><p>\u4E00\u5C55\u773C\uFF0C\u4E00\u5F20\u53D1\u9EC4\u7684\u7EB8\u5F20\u843D\u5165\u773C\u5EC9\u3002</p><p>\u544A\u793A\u8FB9\u89D2\u5DF2\u7136\u7834\u788E\u5377\u6298\uFF0C\u4F46\u5B57\u8FF9\u4ECD\u7136\u6E05\u6670\u53EF\u8FA8\u2014\u2014\u300C\u8D31\u4EBA\u96EA\u5CF0\uFF0C\u4E3A\u5974\u795E\u6559\uFF0C\u51E1\u6211\u5E2E\u4F17\uFF0C\u4E00\u6587\u4E00\u64CD\u3002\u300D</p><p>\u7D2B\u73AB\u624B\u4E00\u677E\uFF0C\u94DC\u94B1\u53EE\u53EE\u5F53\u5F53\u6389\u5728\u7F38\u5185\u3002</p><p>\u6E05\u8106\u7684\u91D1\u5C5E\u58F0\u54CD\u5F7B\u6597\u5BA4\uFF0C\u90A3\u6735\u8089\u82B1\u4E00\u9635\u6536\u7F29\uFF0C\u5410\u51FA\u4E00\u80A1\u6E05\u4EAE\u7684\u6DEB\u6C34\u3002</p><p>\u7D2B\u73AB\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u6380\u5F00\u5E03\u5EC9\uFF0C\u987F\u65F6\u82B1\u5BB9\u5931\u8272\u3002</p><p>\u5165\u76EE\u662F\u4E00\u4E2A\u5360\u636E\u534A\u4E2A\u8EAB\u4F53\u7684\u8089\u7403\uFF0C\u6D51\u5706\u767D\u5AE9\uFF0C\u6BD4\u6000\u5B55\u4E94\u6708\u7684\u7D2B\u73AB\u8FD8\u5927\u4E86\u4E24\u500D\u6709\u4F59\u3002\u7EC6\u5AE9\u7684\u76AE\u80A4\u88AB\u6491\u5F97\u7206\u88C2\u822C\u8584\u8584\u4E00\u5C42\uFF0C\u51E0\u4E4E\u80FD\u770B\u5230\u5B50\u5BAB\u5185\u7269\u4F53\u7684\u8815\u52A8\u3002</p><p>\u4EC5\u4EC5\u4E94\u4E2A\u6708\uFF0C\u80CE\u513F\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u4F1A\u8FD9\u4E48\u5927\u3002\u5B8C\u5168\u51FA\u4E8E\u76F4\u89C9\uFF0C\u7D2B\u73AB\u611F\u89C9\u5230\uFF0C\u90A3\u4E2A\u6B63\u5728\u5E08\u7236\u4F53\u5185\u751F\u957F\u7684\u7269\u4F53\u7EDD\u975E\u4EBA\u7C7B\uFF0C\u800C\u662F\u4E00\u4E2A\u5438\u53D6\u8840\u8089\u7CBE\u534E\u7684\u5F02\u7269\u3002</p><p>\u5979\u538B\u4E0B\u614C\u4E71\u7684\u5FC3\u7EEA\uFF0C\u63A2\u5934\u671D\u5185\u770B\u53BB\u3002</p><p>\u4E00\u77AC\u95F4\uFF0C\u7D2B\u73AB\u4EE5\u4E3A\u81EA\u5DF1\u8BA4\u9519\u4EBA\u4E86\u3002\u8EBA\u5728\u5EC9\u540E\u7684\u5973\u5B50\u67D4\u9888\u4FA7\u5728\u4E00\u65C1\uFF0C\u5982\u4E91\u7684\u79C0\u53D1\u906E\u4F4F\u4E86\u9762\u5B54\u3002\u8BB0\u5FC6\u91CC\uFF0C\u5E08\u7236\u6C38\u8FDC\u90FD\u662F\u5934\u6234\u5C3C\u5E3D\uFF0C\u6E05\u6E05\u723D\u723D\u7684\u6837\u5B50\u3002\u82E5\u4E0D\u662F\u80A9\u5934\u5DF2\u7ECF\u957F\u5728\u8089\u4E2D\u7684\u5F2F\u94A9\uFF0C\u7D2B\u73AB\u771F\u4EE5\u4E3A\u8FD9\u662F\u4E2A\u964C\u751F\u7684\u5973\u4EBA\u3002</p><p>\u64A9\u5F00\u79C0\u53D1\uFF0C\u9732\u51FA\u4E00\u5F20\u719F\u6089\u7684\u9762\u5BB9\u3002\u957F\u51FA\u4E00\u5934\u9752\u4E1D\u7684\u96EA\u5CF0\u795E\u5C3C\uFF0C\u770B\u4E0A\u53BB\u50CF\u4E00\u4E2A\u7F8E\u8C8C\u7684\u6210\u719F\u5973\u4EBA\uFF0C\u6E05\u51B7\u7684\u9762\u5BB9\u4E5F\u67D4\u548C\u4E86\u8BB8\u591A\u3002\u5979\u53CC\u76EE\u7D27\u95ED\uFF0C\u663E\u7136\u6B63\u5728\u660F\u7761\u3002\u768E\u82E5\u51B0\u971C\u7684\u8138\u8272\u53D8\u5F97\u5FAE\u9EC4\uFF0C\u80F8\u524D\u50B2\u4EBA\u7684\u80A5\u4E73\u4E0E\u5C0F\u8179\u6BD4\u8D77\u6765\u5C3A\u5BF8\u4E5F\u4E0D\u518D\u60CA\u4EBA\uFF0C\u4EFF\u4F5B\u5168\u8EAB\u7684\u7CBE\u534E\u90FD\u88AB\u5B50\u5BAB\u5185\u7684\u5F02\u7269\u5438\u51C0\uFF0C\u5F62\u5BB9\u6194\u60B4\u3002</p><p>\u7D2B\u73AB\u62AC\u624B\u6342\u4F4F\u53E3\u9F3B\uFF0C\u62FC\u547D\u6B62\u4F4F\u60B2\u58F0\u3002\u5E08\u7236\u5728\u7761\u68A6\u91CC\u542C\u5230\u94DC\u94B1\u7684\u58F0\u97F3\uFF0C\u8EAB\u4F53\u5C31\u81EA\u53D1\u505A\u597D\u51C6\u5907\u3002\u8FD9\u4E94\u4E2A\u6708\u7684\u65E5\u65E5\u591C\u591C\uFF0C\u5979\u7A76\u7ADF\u53D7\u8FC7\u591A\u5C11\u51CC\u8FB1\u2026\u2026</p><p>\u53F6\u884C\u5357\u82CD\u58F0\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u4E0D\u5FC5\u96BE\u8FC7\u3002\u8001\u592B\u672A\u66FE\u7528\u8FC7\u836F\uFF0C\u5E08\u592A\u795E\u667A\u4E00\u76F4\u662F\u6E05\u695A\u7684\u3002\u8EAB\u4F53\u867D\u7136\u53D7\u4E9B\u82E6\u695A\uFF0C\u4F46\u5206\u5A29\u540E\u4FBF\u53EF\u6062\u590D\u5982\u521D\u3002\u300D</p><p>\u8BF4\u8BDD\u95F4\uFF0C\u795E\u5C3C\u7684\u5C0F\u8179\u53C8\u5F00\u59CB\u8815\u52A8\u8D77\u6765\u3002\u90A3\u4E0D\u662F\u6B63\u5E38\u7684\u80CE\u52A8\uFF0C\u800C\u50CF\u662F\u4E00\u4E2A\u7403\u4F53\u5728\u91CC\u9762\u4E0D\u4F4F\u65CB\u8F6C\uFF0C\u6BCF\u4E00\u6B21\u65CB\u8F6C\uFF0C\u90FD\u4F1A\u7275\u52A8\u5168\u8EAB\u7684\u808C\u80A4\u3002\u7D2B\u73AB\u4F38\u624B\u6B32\u6478\uFF0C\u53C8\u5BB3\u6015\u5730\u7F29\u4E86\u56DE\u6765\u3002</p><p>\u300C\u4EC0\u4E48\u4E1C\u897F\uFF1F\u300D\u5979\u8F7B\u58F0\u95EE\u9053\u3002</p><p>\u300C\u593A\u80CE\u82B1\u3002\u300D\u53F6\u884C\u5357\u7B54\u9053\uFF1A\u300C\u5438\u6536\u5973\u5B50\u7684\u529F\u529B\uFF0C\u6709\u4E94\u79CD\u65B9\u6CD5\u3002\u4F46\u5E08\u592A\u6240\u4FEE\u5185\u529F\u6027\u8D28\u5947\u5F02\uFF0C\u8BF8\u822C\u6CD5\u95E8\u5747\u65E0\u8BA1\u53EF\u65BD\u3002\u8001\u592B\u601D\u7D22\u591A\u65E5\uFF0C\u690D\u5165\u593A\u80CE\u82B1\u662F\u75DB\u82E6\u6700\u5C0F\u7684\u4E00\u79CD\uFF0C\u5BF9\u8EAB\u4F53\u7684\u4F24\u5BB3\u4E5F\u6700\u5C0F\u3002\u300D</p><p>\u300C\u662F\u5417\uFF1F\u300D\u7D2B\u73AB\u671B\u7740\u96EA\u5CF0\u795E\u5C3C\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u90A3\u8981\u591A\u8C22\u53F6\u62A4\u6CD5\u4E86\u2026\u2026\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u6CA1\u6709\u542C\u5230\u4E24\u4EBA\u7684\u5BF9\u8BDD\u3002</p><p>\u5F53\u593A\u80CE\u82B1\u690D\u5165\u4F53\u5185\u65F6\uFF0C\u5979\u600E\u4E48\u4E5F\u60F3\u4E0D\u5230\uFF0C\u90A3\u9897\u6307\u5C16\u5927\u5C0F\u7684\u79CD\u5B50\u4F1A\u5728\u4E94\u4E2A\u6708\u5185\u75AF\u72C2\u751F\u957F\u8FD1\u5343\u500D\u3002\u9760\u7CBE\u6DB2\u751F\u957F\u7684\u5996\u82B1\uFF0C\u5360\u636E\u4E86\u795E\u5723\u7684\u5B50\u5BAB\uFF0C\u65E0\u65F6\u65E0\u523B\u4E0D\u5728\u643E\u53D6\u7740\u5979\u7684\u8840\u8089\u548C\u771F\u5143\u3002\u5373\u4F7F\u662F\u7761\u68A6\u4E2D\uFF0C\u51B7\u6C57\u8FD8\u4E0D\u4F4F\u6D41\u51FA\u3002\u5931\u53BB\u6C34\u5206\u7684\u76AE\u80A4\u5982\u540C\u51CB\u96F6\u7684\u82B1\u74E3\uFF0C\u6E10\u6E10\u67AF\u840E\u3002</p><p>\u5047\u5982\u5979\u77E5\u9053\u4E24\u5929\u4E4B\u540E\u5C31\u4F1A\u89E3\u8131\uFF0C\u4F1A\u4E0D\u4F1A\u5728\u68A6\u4E2D\u7B11\u51FA\u6765\u5462\uFF1F\u8FD8\u662F\u5B81\u613F\u8FDE\u81EA\u5DF1\u7684\u751F\u547D\u4E5F\u4E00\u5E76\u89E3\u8131\u2026\u2026</p>',58);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
