import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_102" tabindex="-1"><a class="header-anchor" href="#_102" aria-hidden="true">#</a> 102</h1><p>\u300C\u2026\u2026\u571F\u5802\u5341\u4E03\uFF0C\u5171\u8BA1\u4E00\u767E\u4E09\u5341\u516D\u4E2A\u5E2E\u4F1A\uFF1B\u5C0F\u8005\u767E\u4F59\u4EBA\uFF0C\u5927\u8005\u56DB\u5343\u4F59\u4EBA\uFF0C\u5171\u8BA1\u4E94\u4E07\u4E8C\u5343\u56DB\u767E\u4E09\u5341\u4EBA\uFF1B\u6559\u4E2D\u76F4\u5C5E\u5E2E\u4F17\u65B0\u589E\u4E00\u5343\u56DB\u767E\u96F6\u4E03\u4EBA\uFF0C\u5171\u8BA1\u4E8C\u5343\u4E03\u767E\u4EBA\uFF0C\u5728\u5C9B\u5185\u7684\u6709\u516B\u767E\u516D\u5341\u4EBA\u3002\u300D\u5C60\u6000\u6C89\u8BF4\u5B8C\u9000\u5230\u4E00\u8FB9\u3002</p><p>\u300C\u5728\u5468\u56FD\u5883\u5185\u7684\u53EA\u6709\u4E8C\u5341\u4E03\u4E2A\u5E2E\u4F1A\uFF0C\u672A\u514D\u592A\u5C11\u3002\u300D\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u4E0B\u4EE4\uFF0C\u4E0D\u62D8\u4E94\u5802\u6240\u5B9A\u65B9\u4F4D\uFF0C\u4E00\u5E76\u4E1C\u8FDB\u3002\u300D</p><p>\u56DB\u9547\u8986\u706D\u540E\uFF0C\u4E94\u884C\u95E8\u4FBF\u63A5\u7BA1\u4E86\u904D\u5E03\u5929\u4E0B\u7684\u9644\u5C5E\u5E2E\u4F1A\u3002\u7EC8\u5357\u4EE5\u4E1C\u539F\u672C\u662F\u6728\u5802\u52BF\u529B\u8303\u56F4\uFF0C\u4EE5\u91D1\u5802\u5B9E\u529B\u4E4B\u5F3A\u4E5F\u65E0\u7F18\u67D3\u6307\u3002\u6B64\u65F6\u5BAB\u4E3B\u4E00\u8A00\u800C\u5B9A\uFF0C\u6728\u5802\u957F\u8001\u7075\u7389\u4E5F\u6BEB\u65E0\u5F02\u8BAE\u3002</p><p>\u63A5\u4E0B\u6765\uFF0C\u4F17\u4EBA\u5F00\u59CB\u7B79\u5212\u5982\u4F55\u6311\u9009\u5E2E\u4F17\u7EC4\u5EFA\u90E8\u66F2\uFF0C\u4EE5\u53CA\u8FD0\u9001\u5175\u9A6C\uFF0C\u6536\u62E2\u94B1\u7CAE\u7B49\u4E8B\u3002</p><p>\u51B3\u65AD\u4E2D\uFF0C\u6155\u5BB9\u9F99\u4E0D\u671F\u7136\u60F3\u5230\uFF0C\u9F99\u57CE\u4E4B\u884C\uFF0C\u6700\u5927\u7684\u6536\u83B7\u4E5F\u8BB8\u662F\u4FE1\u5FC3\u3002\u800C\u4E0D\u50CF\u4EE5\u524D\uFF0C\u4EC5\u4EC5\u662F\u91CE\u5FC3\u548C\u4EC7\u6068\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u591C\u8272\u5DF2\u6DF1\uFF0C\u7D2B\u73AB\u5374\u6BEB\u65E0\u7761\u610F\u3002\u5979\u89E3\u5F00\u8863\u670D\uFF0C\u9759\u9759\u51DD\u89C6\u81EA\u5DF1\u7684\u5C0F\u8179\u3002</p><p>\u67D4\u7F8E\u7684\u8170\u80A2\u81C3\u80BF\u53D8\u5F62\uFF0C\u8179\u90E8\u9686\u8D77\u4E00\u4E2A\u5706\u6DA6\u7684\u5F27\u7EBF\uFF0C\u770B\u4E0D\u5230\u7684\u4E0B\u4F53\uFF0C\u603B\u662F\u6709\u79CD\u6E7F\u6E7F\u7684\u611F\u89C9\u3002</p><p>\u81EA\u4ECE\u90A3\u65E5\u9003\u8DD1\u5931\u8D25\u4E4B\u540E\uFF0C\u715E\u8D39\u82E6\u5FC3\u4E0E\u6155\u5BB9\u9F99\u7EF4\u6301\u7684\u5FAE\u5999\u60C5\u612B\u906D\u5230\u5F7B\u5E95\u7834\u574F\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u518D\u50CF\u4EE5\u524D\u90A3\u6837\u5BF9\u5979\u7231\u62A4\u6709\u52A0\uFF0C\u65E0\u8BBA\u4EBA\u524D\u4EBA\u540E\u90FD\u662F\u51B7\u7136\u76F8\u5411\u3002\u751A\u81F3\u5728\u4EA4\u5408\u4E2D\u4E5F\u4E0D\u518D\u987E\u53CA\u5979\u7684\u611F\u53D7\uFF0C\u53EA\u662F\u4E00\u5473\u633A\u5F04\uFF0C\u53D1\u6CC4\u5B8C\u540E\u8D77\u8EAB\u4FBF\u8D70\uFF0C\u5B8C\u5168\u628A\u5979\u5F53\u6210\u4E2A\u6CC4\u6B32\u7684\u5DE5\u5177\uFF0C\u518D\u6CA1\u6709\u4E1D\u6BEB\u7684\u6E29\u5B58\u548C\u7231\u610F\u3002</p><p>\u7D2B\u73AB\u8F7B\u8F7B\u629A\u6478\u7740\u5C0F\u8179\uFF0C\u82E6\u6DA9\u5730\u60F3\u5230\uFF0C\u81EA\u5DF1\u82E5\u4E0D\u662F\u4ED6\u4EB2\u59B9\u59B9\uFF0C\u80FD\u5E2E\u4ED6\u751F\u517B\u4ED6\u60F3\u8981\u7684\u767D\u75F4\u540E\u4EE3\uFF0C\u4E5F\u8BB8\u65E9\u5C31\u50CF\u5E08\u7236\u5E08\u59D0\u4E00\u6837\uFF0C\u88AB\u6254\u51FA\u53BB\u8BA9\u4EBA\u6298\u78E8\u5230\u6B7B\u5427\u2026\u2026\u4E4B\u6240\u4EE5\u8FD8\u80FD\u7559\u5728\u8FD9\u91CC\uFF0C\u7EF4\u6301\u57FA\u672C\u7684\u4F53\u9762\u548C\u5C0A\u4E25\uFF0C\u90FD\u662F\u56E0\u4E3A\u4F60\u8FD9\u4E2A\u5B7D\u79CD\u2026\u2026</p><p>\u7D2B\u73AB\u5BF9\u7740\u8179\u5185\u7684\u5B69\u5B50\u5583\u5583\u8BF4\u9053\uFF1A\u300C\u751F\u4E0B\u6765\uFF0C\u4F60\u5C31\u4F1A\u662F\u4E2A\u767D\u75F4\u3002\u5A18\u8FD8\u8981\u7ED9\u4F60\u751F\u51E0\u4E2A\u767D\u75F4\u59B9\u59B9\uFF0C\u8BA9\u4F60\u4EEC\u732A\u72D7\u4E00\u6837\u751F\u4E0B\u767D\u75F4\u7684\u5B50\u5973\u2026\u2026\u662F\u4E0D\u662F\u5F88\u53EF\u6015\u5462\uFF1F\u300D</p><p>\u5979\u5634\u89D2\u9732\u51FA\u4E00\u4E1D\u51B7\u9177\u7684\u7B11\u610F\uFF0C\u300C\u5A18\u771F\u60F3\u6740\u4E86\u4F60\u5462\uFF01\u6216\u8005\u4F60\u5C31\u6B7B\u5728\u5A18\u809A\u5B50\u91CC\uFF0C\u8D81\u65E9\u5230\u522B\u4EBA\u5BB6\u8F6C\u4E16\u6258\u751F\u3002\u59D3\u4EC0\u4E48\u90FD\u597D\uFF0C\u53EA\u8981\u4E0D\u518D\u59D3\u8FD9\u4E2A\u5929\u6740\u7684\u6155\u5BB9\uFF01\u300D</p><p>\u8BF4\u7740\u73AB\u7470\u4ED9\u5B50\u6CEA\u6D41\u6EE1\u9762\uFF0C\u65E0\u58F0\u5730\u6078\u54ED\u8D77\u6765\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8427\u4F5B\u5974\u4E5F\u6CA1\u6709\u5165\u7720\u3002\u4E94\u4E2A\u6708\u4E0D\u505C\u7684\u5954\u6CE2\uFF0C\u5A07\u5F31\u7684\u8EAB\u4F53\u65E9\u5DF2\u75B2\u5026\u4E0D\u582A\uFF0C\u5F53\u91CD\u65B0\u8EBA\u5728\u8FD9\u5EA7\u51B7\u6E05\u7684\u77F3\u5BAB\u5185\uFF0C\u5979\u5374\u6709\u79CD\u56DE\u5230\u5BB6\u4E2D\u7684\u5B89\u5B9A\u611F\uFF0C\u751A\u81F3\u8FD8\u6709\u4E9B\u8BB8\u6E29\u6696\u3002\u82E5\u4E0D\u662F\u8FD8\u5728\u671F\u5F85\u67D0\u4E9B\u4E8B\u60C5\uFF0C\u53EF\u80FD\u65E9\u5C31\u7761\u7740\u4E86\u3002</p><p>\u8427\u4F5B\u5974\u4E00\u751F\u53D7\u5C3D\u5BA0\u7231\uFF0C\u5C31\u50CF\u4E00\u682A\u67D4\u5F31\u7684\u7EC6\u85E4\uFF0C\u603B\u8981\u4F9D\u4ED8\u4E8E\u9AD8\u5927\u7684\u6811\u5E72\u3002\u5F53\u4E00\u5207\u6323\u624E\u90FD\u65E0\u6CD5\u6539\u53D8\u547D\u8FD0\u4E4B\u540E\uFF0C\u5979\u4FBF\u629B\u5F00\u4EBA\u6BCD\u7684\u5C0A\u4E25\uFF0C\u5FC3\u7518\u60C5\u613F\u732E\u51FA\u81EA\u5DF1\u7684\u8089\u4F53\u548C\u67D4\u60C5\uFF0C\u6765\u6362\u53D6\u513F\u5B50\u7684\u7231\u62A4\uFF0C\u504E\u4F9D\u5728\u4ED6\u6000\u4E2D\uFF0C\u8EB2\u907F\u98CE\u96E8\u3002</p><p>\u9526\u88AB\u53C8\u9999\u53C8\u6696\uFF0C\u7F8E\u5987\u50CF\u4E00\u4E2A\u6000\u6625\u7684\u5C11\u5973\uFF0C\u9759\u6084\u6084\u8EBA\u5728\u8FD9\u4E2A\u8BA9\u5979\u53D7\u8FC7\u65E0\u5C3D\u51CC\u8FB1\u7684\u77F3\u5BA4\u5185\uFF0C\u6000\u7740\u751C\u871C\u7684\u559C\u60A6\uFF0C\u671F\u5F85\u7740\u60C5\u90CE\u7684\u5230\u6765\u3002</p><p>\u77F3\u95E8\u8F7B\u8F7B\u63A8\u5F00\uFF0C\u8427\u4F5B\u5974\u987F\u65F6\u7F8E\u76EE\u4E00\u4EAE\u3002</p><p>\u300C\u5A18\u3002\u300D\u5374\u662F\u5973\u513F\u7684\u58F0\u97F3\u3002</p><p>\u8427\u4F5B\u5974\u4FCF\u8138\u98DE\u7EA2\uFF0C\u50CF\u88AB\u649E\u7834\u5FC3\u4E8B\u822C\uFF0C\u4E00\u8138\u7F9E\u6DA9\u7684\u504F\u8FC7\u5934\uFF0C\u4E0B\u610F\u8BC6\u5730\u54AC\u5F04\u7740\u5507\u74E3\u3002</p><p>\u300C\u5A18\uFF0C\u4F60\u4E5F\u7761\u4E0D\u7740\u5417\uFF1F\u300D\u867D\u7136\u5BAB\u91CC\u6CA1\u6709\u5176\u4ED6\u4EBA\uFF0C\u7D2B\u73AB\u8FD8\u662F\u538B\u4F4E\u4E86\u58F0\u97F3\u3002\u5979\u8F7B\u8F7B\u9664\u53BB\u978B\u889C\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u5973\u513F\u548C\u4F60\u4E00\u8D77\u7761\u5427\u3002\u300D</p><p>\u8427\u4F5B\u5974\u7EA2\u7740\u8138\u55EF\u4E86\u4E00\u58F0\uFF0C\u67D4\u987A\u5730\u628A\u5934\u9888\u653E\u5728\u5973\u513F\u81C2\u95F4\u3002\u7D2B\u73AB\u4E00\u6014\uFF0C\u80F8\u53E3\u8FA3\u8FA3\u7684\uFF0C\u5206\u4E0D\u6E05\u4EC0\u4E48\u6ECB\u5473\u3002\u5979\u672C\u6765\u60F3\u50CF\u5C0F\u65F6\u5019\u90A3\u6837\uFF0C\u4F0F\u5728\u6BCD\u4EB2\u6000\u91CC\uFF0C\u95FB\u7740\u6BCD\u4EB2\u7684\u4F53\u9999\u5165\u7761\u3002\u53EF\u6BCD\u4EB2\u8FD9\u79CD\u5A07\u67D4\uFF0C\u5374\u50CF\u662F\u81EA\u5DF1\u53EF\u7231\u7684\u5C0F\u59B9\u59B9\u3002\u7D2B\u73AB\u5FC3\u91CC\u82E6\u7B11\uFF0C\u6CA1\u6709\u94BB\u8FDB\u6BCD\u4EB2\u6000\u91CC\uFF0C\u53CD\u800C\u8212\u5C55\u7389\u81C2\uFF0C\u6402\u4F4F\u8427\u4F5B\u5974\u7684\u9999\u80A9\uFF0C\u628A\u4E0B\u5DF4\u653E\u5728\u5979\u7684\u53D1\u4E0A\u3002</p><p>\u7B49\u610F\u8BC6\u5230\u81EA\u5DF1\u4E0D\u8BE5\u6709\u7684\u61E6\u5F31\u4E3E\u52A8\uFF0C\u8427\u4F5B\u5974\u8138\u7EA2\u5F97\u66F4\u5389\u5BB3\u4E86\u3002\u5979\u53EA\u597D\u5728\u5FC3\u91CC\u5B89\u6170\u81EA\u5DF1\uFF1A\u5979\u662F\u7231\u90CE\u7684\u6B63\u59BB\uFF0C\u81EA\u5DF1\u53EA\u662F\u4E2A\u5C0F\u59BE\u3002</p><p>\u7D2B\u73AB\u62C9\u8D77\u7EE3\u88AB\uFF0C\u76D6\u4F4F\u4E24\u4EBA\u540C\u6837\u9686\u8D77\u7684\u5C0F\u8179\u3002\u5F53\u7EE3\u88AB\u78B0\u5230\u80F8\u53E3\uFF0C\u8427\u4F5B\u5974\u53D1\u51FA\u4E00\u58F0\u4F4E\u4F4E\u7684\u547B\u541F\u3002</p><p>\u300C\u600E\u4E48\u4E86\uFF1F\u300D\u7D2B\u73AB\u8FDE\u5FD9\u505C\u4E0B\u624B\u3002</p><p>\u7F8E\u5987\u55EB\u5685\u7740\u8BF4\u9053\uFF1A\u300C\u6709\u4E9B\u80C0\u2026\u2026\u300D</p><p>\u8BF4\u5B8C\u8FD9\u53E5\uFF0C\u6BCD\u5973\u4FE9\u4FBF\u6C89\u9ED8\u4E0B\u6765\u3002</p><p>\u7D2B\u73AB\u6EE1\u5FC3\u7684\u8BDD\u8981\u8BF4\uFF0C\u5374\u89C9\u5F97\u96BE\u4EE5\u542F\u9F7F\uFF0C\u53EA\u597D\u62E5\u7740\u6BCD\u4EB2\u8F7B\u8F7B\u6447\u6643\u3002\u60F3\u8D77\u5E08\u7236\u7684\u60E8\u72B6\uFF0C\u7D2B\u73AB\u5FC3\u91CC\u4E0D\u7981\u6D6E\u51FA\u8FD9\u6837\u7684\u5FF5\u5934\uFF1A\u5A18\u8FD9\u6837\u5C48\u4ECE\uFF0C\u4E5F\u8BB8\u662F\u552F\u4E00\uFF0C\u4E5F\u662F\u6B63\u786E\u7684\u9009\u62E9\u3002\u5047\u5982\u518D\u6709\u4E00\u6B21\u673A\u4F1A\uFF0C\u6211\u53EF\u80FD\u4E0D\u4F1A\u518D\u5E26\u4F60\u4E00\u540C\u79BB\u5F00\u3002</p><p>\u5728\u8FD9\u91CC\uFF0C\u4F60\u4F1A\u5FEB\u4E50\u7684\u5427\uFF0C\u7EB5\u7136\u662F\u7578\u5F62\u7684\u751F\u6D3B\u2026\u2026</p><p>\u6E10\u6E10\u5730\uFF0C\u7D2B\u73AB\u7684\u773C\u76AE\u6C89\u91CD\u8D77\u6765\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7EE3\u8925\u88AB\u731B\u7136\u63ED\u5F00\uFF0C\u7D2B\u73AB\u4ECE\u7761\u68A6\u4E2D\u60CA\u9192\u8FC7\u6765\uFF0C\u4E0D\u53CA\u60CA\u53EB\uFF0C\u4E00\u53EA\u51B0\u51B7\u7684\u624B\u638C\u4FBF\u63A2\u5165\u8179\u4E0B\uFF0C\u786C\u6886\u6886\u5730\u6345\u5165\u79D8\u5904\u3002</p><p>\u7D2B\u73AB\u54AC\u7D27\u7259\u5173\uFF0C\u4E3B\u52A8\u655E\u5F00\u53CC\u817F\u3002\u53EF\u624B\u638C\u7684\u52A8\u4F5C\u5F88\u91CD\uFF0C\u5DF2\u7ECF\u5F04\u75BC\u4E86\u5979\u3002\u5F53\u90A3\u6839\u624B\u6307\u94BB\u5165\u4F53\u5185\uFF0C\u7C97\u66B4\u5730\u6405\u52A8\u65F6\uFF0C\u5979\u7981\u4E0D\u4F4F\u62E7\u7740\u7709\u5934\uFF0C\u8F7B\u8F7B\u75DB\u53EB\u4E00\u58F0\u3002</p><p>\u90A3\u4EBA\u90FD\u770B\u5728\u773C\u91CC\uFF0C\u5374\u6BEB\u4E0D\u7406\u4F1A\u3002\u7B49\u79D8\u5904\u7565\u5FAE\u6E7F\u6DA6\uFF0C\u5DE8\u7269\u7ACB\u523B\u63D2\u8FDB\u8089\u7A74\u3002</p><p>\u8089\u68D2\u6CA1\u6709\u534A\u70B9\u601C\u60DC\u5730\u6495\u5F00\u5AE9\u8089\uFF0C\u51F6\u72E0\u62BD\u9001\uFF0C\u7D2B\u73AB\u628A\u7EA4\u6307\u54AC\u5728\u5634\u91CC\uFF0C\u62FC\u547D\u5FCD\u8010\u4E0B\u4F53\u7684\u75DB\u695A\u3002</p><p>\u5F53\u8089\u7A74\u75DB\u5F97\u96BE\u4EE5\u5FCD\u53D7\u65F6\uFF0C\u9633\u5177\u7EC8\u4E8E\u8DF3\u52A8\u7740\u5C04\u51FA\u6D53\u7CBE\u3002</p><p>\u6155\u5BB9\u9F99\u62D4\u51FA\u9633\u5177\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u6EDA\u3002\u300D</p><p>\u4E0D\u5E26\u4E1D\u6BEB\u611F\u60C5\u7684\u8BDD\u8BED\u4E00\u4E0B\u5B50\u51FB\u788E\u4E86\u7D2B\u73AB\u7684\u82B3\u5FC3\u3002\u5979\u6014\u4E86\u7247\u523B\uFF0C\u6309\u7740\u75BC\u75DB\u7684\u4E0B\u4F53\uFF0C\u4E00\u6B65\u4E00\u6B65\u632A\u52A8\u7740\u79BB\u5F00\u77F3\u5BA4\u3002</p><p>\u56DE\u5230\u5BA4\u5185\uFF0C\u5979\u4FBF\u4F0F\u5728\u5E8A\u4E0A\u75DB\u54ED\u5931\u58F0\u3002\u5373\u4F7F\u662F\u5993\u5973\uFF0C\u4E5F\u4F1A\u6BD4\u81EA\u5DF1\u591A\u51E0\u5206\u5C0A\u4E25\u3002</p><p>\u8427\u4F5B\u5974\u88AB\u513F\u5B50\u7684\u7C97\u66B4\u65E0\u60C5\u5413\u5F97\u8138\u8272\u96EA\u767D\uFF0C\u602F\u751F\u751F\u770B\u4E86\u4ED6\u4E00\u773C\uFF0C\u53C8\u98DE\u5FEB\u5730\u5782\u4E0B\u773C\u775B\u3002</p><p>\u300C\u5A18\uFF0C\u7B11\u4E00\u4E2A\u3002\u300D\u4EFF\u4F5B\u521A\u624D\u7684\u51B7\u9177\u7EDD\u60C5\u51FA\u81EA\u53E6\u4E00\u4E2A\u4EBA\u7684\u53E3\u543B\uFF0C\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\u51FA\u5947\u7684\u6E29\u67D4\u3002</p><p>\u8427\u4F5B\u5974\u542B\u7F9E\u5E26\u559C\u5730\u770B\u4E86\u4ED6\u4E00\u773C\uFF0C\u82B1\u6735\u822C\u7684\u8138\u4E0A\u7EFD\u51FA\u4E00\u4E2A\u751C\u7F8E\u7684\u7B11\u5BB9\u3002</p><p>\u300C\u7B11\u5F97\u771F\u7F8E\uFF01\u300D\u6155\u5BB9\u9F99\u628A\u7F8E\u5987\u62E5\u5728\u6000\u91CC\uFF0C\u4E00\u8FB9\u4E0D\u5B89\u5206\u5730\u4E0A\u4E0B\u5176\u624B\uFF0C\u4E00\u8FB9\u7B11\u9053\uFF1A\u300C\u7D2F\u4E86\u4E00\u6574\u5929\uFF0C\u5A18\u8FD9\u6837\u4E00\u7B11\uFF0C\u5B69\u513F\u5C31\u7CBE\u795E\u5341\u8DB3\u2014\u2014\u8FD8\u80FD\u72E0\u72E0\u64CD\u4F60\u4E00\u756A\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u6655\u751F\u7389\u988A\uFF0C\u6108\u53D1\u5A07\u7F8E\u3002</p><p>\u6155\u5BB9\u9F99\u6258\u8D77\u5979\u7684\u4E0B\u5DF4\uFF0C\u300C\u5A18\u7B11\u5F97\u771F\u751C\uFF0C\u5F53\u5E74\u5A18\u4E5F\u662F\u8FD9\u6837\u5BF9\u6155\u5BB9\u7941\u7B11\u7684\u5417\uFF1F\u300D</p><p>\u6B64\u65F6\u5BF9\u4ED6\u4EEC\u6765\u8BF4\uFF0C\u6155\u5BB9\u7941\u7684\u540D\u5B57\u5DF2\u7ECF\u4E0D\u518D\u662F\u7981\u5FCC\uFF0C\u8427\u4F5B\u5974\u5A07\u5A9A\u6A2A\u751F\u5730\u77A5\u4E86\u4ED6\u4E00\u773C\uFF0C\u7EC6\u58F0\u9053\uFF1A\u300C\u4ED6\u4EE5\u524D\u4E5F\u662F\u8FD9\u6837\u5B50\u5566\u2026\u2026\u5F04\u5F97\u5A18\u597D\u75BC\uFF0C\u8FD8\u8BA9\u5A18\u7B11\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u8138\u574F\u7B11\u5730\u8BF4\uFF1A\u300C\u90A3\u65F6\u5A18\u559C\u6B22\u8BA9\u4ED6\u64CD\u540E\u9762\u5462\uFF1F\u8FD8\u662F\u64CD\u4F60\u7684\u5C44\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u5624\u549B\u4E00\u58F0\uFF0C\u7F9E\u7B54\u7B54\u9053\uFF1A\u300C\u4EBA\u5BB6\u90A3\u5730\u65B9\u662F\u9F99\u54E5\u54E5\u7834\u7684\u5462\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u5927\u7B11\uFF0C\u300C\u559C\u6B22\u54E5\u54E5\u64CD\u4F60\u7684\u5C41\u773C\u513F\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u5634\u5507\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u53C8\u8FDE\u5FD9\u54AC\u4F4F\u3002</p><p>\u300C\u55EF\uFF1F\u4F60\u8BF4\u4EC0\u4E48\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u5C0F\u5634\u8D34\u5728\u6155\u5BB9\u9F99\u8033\u8FB9\uFF0C\u58F0\u5982\u868A\u868B\u5730\u8BF4\u9053\uFF1A\u300C\u5C31\u662F\u88AB\u9F99\u54E5\u54E5\u5E72\u51FA\u5C4E\u6765\uFF0C\u5A18\u4E5F\u662F\u559C\u6B22\u7684\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7684\u5DE8\u68D2\u4E00\u4E0B\u7AD6\u5F97\u7B14\u76F4\uFF0C\u4ED6\u5728\u8427\u4F5B\u5974\u5507\u4E0A\u7528\u529B\u4E00\u543B\uFF0C\u5598\u7740\u6C14\u8BF4\uFF1A\u300C\u771F\u662F\u4E2A\u8FF7\u6B7B\u4EBA\u7684\u5996\u7CBE\uFF01\u300D</p><p>\u7F8E\u5987\u5403\u5403\u4F4E\u7B11\uFF0C\u773C\u6CE2\u6D41\u8F6C\u95F4\uFF0C\u604D\u60DA\u53C8\u56DE\u5230\u5C11\u5973\u65F6\u5149\u3002</p><p>\u8FD9\u4F4D\u5927\u71D5\u7687\u5983\u80FD\u5BA0\u51A0\u540E\u5BAB\uFF0C\u9664\u4E86\u5929\u751F\u4E3D\u8D28\uFF0C\u66F4\u662F\u56E0\u4E3A\u5979\u7684\u98CE\u60C5\u4E07\u79CD\u3002\u6D41\u4EA1\u4F0F\u9F99\u6DA7\u7684\u5341\u4F59\u5E74\u4E2D\uFF0C\u5979\u5FC3\u65E0\u65C1\u9E5C\uFF0C\u4E00\u610F\u5411\u4F5B\uFF0C\u88AB\u79F0\u4E3A\u7AEF\u5E84\u5723\u6D01\u7684\u300C\u767E\u82B1\u89C2\u97F3\u300D\u3002\u6B64\u65F6\u538B\u6291\u591A\u5E74\u7684\u67D4\u5A9A\u4E00\u671D\u5C55\u9732\uFF0C\u4EFB\u662F\u77F3\u4EBA\u4E5F\u4E3A\u4E4B\u795E\u9B42\u98A0\u5012\u3002</p><p>\u6155\u5BB9\u9F99\u628A\u8089\u68D2\u63D2\u8FDB\u6ED1\u817B\u7684\u81C0\u8089\u95F4\uFF0C\u9876\u4F4F\u5DF2\u6C81\u51FA\u871C\u6C41\u7684\u83CA\u6D1E\uFF0C\u5FC3\u91CC\u5374\u60F3\u7740\u53E6\u4E00\u5177\u540C\u6837\u4F18\u7F8E\u7684\u80F4\u4F53\u3002</p><p>\u5979\u8FD9\u4F1A\u513F\u54ED\u5F97\u5F88\u4F24\u5FC3\u5427\u3002</p><p>\u706B\u70ED\u7684\u8089\u68D2\u7F13\u7F13\u5145\u6EE1\u83CA\u6D1E\uFF0C\u8427\u4F5B\u5974\u661F\u76EE\u534A\u95ED\uFF0C\u7EA2\u5507\u5FAE\u5206\uFF0C\u67D4\u987A\u5730\u653E\u677E\u8EAB\u4F53\uFF0C\u8BA9\u513F\u5B50\u7684\u9633\u5177\u7B14\u76F4\u633A\u5165\u76F4\u80A0\u6DF1\u5904\uFF0C\u88AB\u711A\u60C5\u818F\u6539\u9020\u8FC7\u7684\u809B\u8089\u6ED1\u5AE9\u5F02\u5E38\uFF0C\u5E76\u4E14\u8FD8\u5206\u6CCC\u51FA\u5927\u91CF\u7684\u871C\u6C41\uFF0C\u4F7F\u8089\u68D2\u8F7B\u6613\u4FBF\u5168\u6839\u800C\u5165\u3002</p><p>\u6155\u5BB9\u9F99\u653E\u4E0B\u8427\u4F5B\u5974\u7684\u8170\u80A2\uFF0C\u9633\u5177\u5FAE\u5FAE\u4E00\u9000\uFF0C\u89C9\u51FA\u83CA\u6D1E\u7684\u6E7F\u6ED1\u540E\uFF0C\u7ACB\u5373\u4E00\u51FB\u5230\u5E95\u3002</p><p>\u8427\u4F5B\u5974\u7389\u817F\u5E73\u5206\uFF0C\u8F6F\u8F6F\u5782\u5728\u69BB\u4E0A\uFF0C\u5706\u81C0\u659C\u659C\u7FD8\u8D77\uFF0C\u79D8\u5904\u6B63\u66B4\u9732\u5728\u9633\u5177\u6839\u90E8\u7684\u89E6\u624B\u4E0B\u3002\u90A3\u4E9B\u7EC6\u957F\u800C\u6709\u529B\u7684\u89E6\u624B\u6216\u52FE\u6216\u6311\uFF0C\u4EFF\u4F5B\u5341\u51E0\u7075\u6D3B\u7684\u624B\u6307\u5728\u5AE9\u8089\u95F4\u638F\u6478\u3002</p><p>\u4F46\u8FD9\u4E00\u5207\u90FD\u6BD4\u4E0D\u4E0A\u809B\u95E8\u91CC\u90A3\u6839\u7C97\u58EE\u7684\u9633\u5177\u3002\u5996\u5F02\u7684\u8089\u68D2\uFF0C\u4F3C\u4E4E\u5E26\u7740\u7EC6\u5FAE\u7684\u7535\u6D41\uFF0C\u8FDB\u51FA\u95F4\u90A3\u79CD\u4FB5\u8680\u4E00\u5207\u7684\u5FEB\u611F\uFF0C\u8BA9\u5979\u96BE\u4EE5\u6291\u5236\u7684\u6218\u6817\u8D77\u6765\u3002\u4E0D\u591A\u65F6\uFF0C\u7F8E\u5987\u4FBF\u8BED\u65E0\u4F26\u6B21\u5730\u5A9A\u53EB\u8FDE\u58F0\u3002</p><p>\u7F8E\u5987\u6B32\u4ED9\u6B32\u6B7B\u7684\u67D4\u5A9A\u795E\u60C5\uFF0C\u4F7F\u6155\u5BB9\u9F99\u6B32\u706B\u52C3\u53D1\uFF0C\u62BD\u9001\u5F97\u6108\u52A0\u7528\u529B\u3002</p><p>\u8427\u4F5B\u5974\u4E0B\u4F53\u4EFF\u4F5B\u4E00\u7247\u5E26\u7740\u751C\u9999\u7684\u8FF7\u4EBA\u6CBC\u6CFD\uFF0C\u6DEB\u6DB2\u3001\u871C\u6C41\u4EA4\u76F8\u8FF8\u6D8C\uFF0C\u8089\u68D2\u8FDB\u51FA\u95F4\u53D1\u51FA\u300C\u53FD\u53FD\u300D\u7684\u6C34\u58F0\u3002\u7B4B\u8171\u4FF1\u5E9F\u7684\u56DB\u80A2\uFF0C\u767D\u7389\u822C\u644A\u5728\u534E\u4E3D\u7684\u9526\u88AB\u4E4B\u4E2D\uFF0C\u9999\u8F6F\u7684\u5A07\u8EAF\u4E0A\uFF0C\u5706\u6DA6\u7684\u7389\u4E73\u524D\u629B\u540E\u7529\uFF0C\u8DF3\u52A8\u4E0D\u5DF2\u3002\u540C\u6837\u8DF3\u52A8\u7740\u7684\u8FD8\u6709\u5979\u7684\u5C0F\u8179\u3002</p><p>\u7B97\u6765\u5979\u6000\u5B55\u5DF2\u7ECF\u516D\u4E2A\u6708\u4E86\uFF0C\u6D51\u5706\u7684\u5C0F\u8179\u50CF\u4E00\u53EA\u767D\u4EAE\u7684\u76AE\u7403\uFF0C\u5728\u4E24\u4EBA\u8EAB\u4F53\u95F4\u6C89\u7538\u7538\u5730\u6447\u6643\u7740\u3002</p><p>\u6155\u5BB9\u9F99\u629A\u6478\u7740\u8427\u4F5B\u5974\u7684\u5C0F\u8179\uFF0C\u8138\u4E0A\u6302\u7740\u6DE1\u6DE1\u7684\u7B11\u5BB9\uFF0C\u300C\u5973\u4EBA\u771F\u662F\u6DEB\u8D31\uFF0C\u8DDF\u8C01\u4EA4\u5408\u90FD\u4F1A\u6000\u5B55\u2014\u2014\u662F\u4E0D\u662F\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u5A07\u5598\u7740\u9053\uFF1A\u300C\u662F\u2026\u2026\u54E5\u54E5\u2026\u2026\u559C\u6B22\u5566\u2026\u2026\u4EBA\u5BB6\u53EA\u5BF9\u54E5\u54E5\u6DEB\u8D31\u2026\u2026\u7ED9\u54E5\u54E5\u751F\u5B69\u5B50\u2026\u2026\u300D</p><p>\u300C\u662F\u5417\uFF1F\u300D\u6155\u5BB9\u9F99\u63E1\u4F4F\u4E24\u53EA\u4E30\u7F8E\u7684\u7389\u4E73\u7528\u529B\u634F\u4E0B\uFF0C\u300C\u4F60\u4E0D\u59D3\u6155\u5BB9\uFF0C\u6709\u59B9\u59B9\u7ED9\u6211\u751F\u5B69\u5B50\u5C31\u591F\u4E86\u3002\u300D</p><p>\u300C\u5440\uFF01\u300D\u4E73\u623F\u80C0\u88C2\u822C\u7684\u5267\u75DB\u4F7F\u8427\u4F5B\u5974\u75DB\u53EB\u5931\u58F0\uFF0C\u300C\u597D\u75BC\uFF01\u54E5\u54E5\u4E0D\u8981\u634F\u4E86\u3002\u300D</p><p>\u80A5\u5AE9\u7684\u4E73\u8089\u5E94\u624B\u800C\u9677\uFF0C\u6BB7\u7EA2\u7684\u4E73\u5934\u9AD8\u9AD8\u633A\u8D77\uFF0C\u808C\u80A4\u4E0A\u6BCF\u4E00\u4E2A\u7EC6\u5C0F\u7684\u7EB9\u8DEF\u90FD\u6E05\u6670\u53EF\u8FA8\u3002\u67D4\u8F6F\u7684\u8089\u7403\u5185\u4F3C\u4E4E\u5145\u6EE1\u6DB2\u4F53\uFF0C\u5728\u6307\u4E0B\u6ED1\u6765\u6ED1\u53BB\u3002\u6155\u5BB9\u9F99\u5FC3\u4E0B\u5947\u602A\uFF0C\u4E0D\u987E\u6BCD\u4EB2\u75DB\u5F97\u4FCF\u8138\u53D1\u767D\uFF0C\u4E94\u6307\u4E00\u7D27\u3002\u7A81\u7136\u95F4\uFF0C\u4E00\u9053\u4EAE\u7EBF\u822C\u7684\u6D53\u767D\u6DB2\u4F53\u4ECE\u4E73\u773C\u5185\u6FC0\u5C04\u800C\u51FA\uFF0C\u5E26\u7740\u4E00\u80A1\u719F\u6089\u800C\u53C8\u4E45\u8FDC\u7684\u9999\u6C14\u843D\u5728\u6155\u5BB9\u9F99\u8138\u4E0A\u3002</p><p>\u8427\u4F5B\u5974\u773C\u4E2D\u9732\u51FA\u4E00\u4E1D\u96BE\u582A\u7684\u7F9E\u8272\uFF0C\u6155\u5BB9\u9F99\u6014\u4E86\u7247\u523B\uFF0C\u7A81\u7136\u653E\u58F0\u5927\u7B11\uFF0C\u6307\u95F4\u6DCB\u6DCB\u6F13\u6F13\uFF0C\u5C3D\u662F\u6E29\u70ED\u7684\u4E73\u6C41\u3002</p><p>\u65F6\u9694\u5341\u516D\u5E74\uFF0C\u8427\u4F5B\u5974\u53C8\u4E00\u6B21\u51FA\u4E73\u4E86\u3002</p>',73);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
