import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_103" tabindex="-1"><a class="header-anchor" href="#_103" aria-hidden="true">#</a> 103</h1><p>\u5927\u6982\u662F\u6155\u5BB9\u9F99\u4E0B\u8FC7\u547D\u4EE4\uFF0C\u8D70\u51FA\u795E\u6BBF\u65F6\uFF0C\u6CA1\u6709\u4E00\u4E2A\u4EBA\u8FC7\u6765\u963B\u62E6\uFF0C\u4E5F\u6CA1\u6709\u4EBA\u8DDF\u5728\u540E\u9762\u76D1\u89C6\u5979\u7684\u4E00\u4E3E\u4E00\u52A8\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u62AB\u4E86\u4E00\u4EF6\u5BBD\u677E\u7684\u7F69\u886B\uFF0C\u633A\u7740\u9686\u8D77\u7684\u809A\u5B50\uFF0C\u6162\u6162\u8D70\u4E0B\u53F0\u9636\u3002</p><p>\u5929\u8272\u521A\u521A\u9ECE\u660E\uFF0C\u5DF2\u7ECF\u6709\u4E94\u516D\u6839\u8089\u68D2\u5149\u987E\u8FC7\u9636\u524D\u7684\u8FCE\u5BBE\u72AC\u3002\u6709\u4E9B\u662F\u503C\u5B8C\u591C\u54E8\uFF0C\u53D1\u6CC4\u4E00\u4E0B\u56DE\u53BB\u7761\u89C9\uFF1B\u6709\u4E9B\u662F\u503C\u5C97\u524D\u6765\u632F\u4F5C\u4E00\u4E0B\u7CBE\u795E\u3002</p><p>\u4E0E\u6559\u4E2D\u5176\u4ED6\u5973\u5974\u76F8\u6BD4\uFF0C\u6797\u9999\u8FDC\u88AB\u9609\u5272\u7684\u8EAB\u4F53\u522B\u6709\u4E00\u756A\u6B8B\u5FCD\u7684\u4E50\u8DA3\u3002\u5C24\u5176\u662F\u75DB\u52A0\u6298\u78E8\u4E4B\u4E0B\uFF0C\u5979\u7684\u987A\u4ECE\u53EA\u6709\u53E6\u4E00\u4E2A\u59D3\u98CE\u7684\u6BCD\u72D7\u53EF\u4EE5\u6BD4\u8F83\u3002</p><p>\u300C\u5321\u5577\u300D\uFF0C\u6709\u4EBA\u628A\u4E00\u4E2A\u94C1\u6876\u653E\u5728\u6797\u9999\u8FDC\u9762\u524D\u3002\u5931\u660E\u7684\u5C11\u5987\u7ACB\u5373\u62AC\u8D77\u5934\uFF0C\u4FA7\u8033\u503E\u542C\u3002</p><p>\u4E00\u52FA\u3001\u4E24\u52FA\u3002\u53EA\u6709\u4E24\u52FA\u3002\u95FB\u5230\u98DF\u7269\u7684\u5473\u9053\uFF0C\u9965\u80A0\u8F98\u8F98\u7684\u5C11\u5987\u6CA1\u6709\u7ACB\u523B\u53BB\u5403\uFF0C\u800C\u53EA\u662F\u5F20\u5F00\u5C0F\u5634\uFF0C\u7B49\u5F85\u65E9\u9910\u524D\u7684\u96F6\u98DF\u3002\u679C\u7136\uFF0C\u4E00\u6839\u8089\u68D2\u9876\u5728\u8138\u4E0A\u3002\u6797\u9999\u8FDC\u8FDE\u5FD9\u6478\u7D22\u7740\u5C06\u8089\u68D2\u4E00\u53E3\u541E\u4E0B\uFF0C\u719F\u7EC3\u5730\u8214\u5F04\u8D77\u6765\u3002</p><p>\u300C\u5403\u6162\u70B9\uFF0C\u8FD8\u8981\u7559\u4E9B\u5582\u4F60\u5E08\u7236\u90A3\u4E2A\u5A4A\u5B50\u5462\u3002\u300D</p><p>\u6797\u9999\u8FDC\u5634\u4E2D\u5514\u5514\u58F0\u54CD\uFF0C\u7EA2\u5507\u7528\u529B\u88F9\u7D27\u9633\u5177\u3002</p><p>\u7D2B\u73AB\u8FDC\u8FDC\u9760\u7740\u53E6\u4E00\u4FA7\u680F\u6746\uFF0C\u8F7B\u624B\u8F7B\u811A\u7684\u8D70\u4E0B\u77F3\u9636\u3002\u5F53\u5979\u7ED5\u8FC7\u5C71\u811A\uFF0C\u56DE\u5934\u671B\u53BB\u65F6\uFF0C\u4E8C\u5E08\u59D0\u5DF2\u7ECF\u541E\u4E0B\u7CBE\u6DB2\uFF0C\u6B63\u4E00\u8FB9\u4E3E\u81C0\u627F\u53D7\u5978\u6DEB\uFF0C\u4E00\u8FB9\u628A\u7F8E\u4E3D\u7684\u8138\u5E9E\u57CB\u5728\u4E08\u592B\u60E8\u767D\u7684\u5934\u76D6\u9AA8\u4E2D\u8214\u98DF\u90A3\u4E9B\u5269\u996D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8D70\u8FDB\u90A3\u4E2A\u696E\u7EA2\u7684\u77F3\u6D1E\uFF0C\u4E00\u80A1\u6D53\u91CD\u7684\u9633\u7CBE\u548C\u4F53\u81ED\u4FBF\u6251\u9762\u800C\u6765\u3002\u73AB\u7470\u4ED9\u5B50\u76B1\u8D77\u7709\u5934\uFF0C\u559D\u9000\u5B88\u536B\u5F84\u76F4\u6765\u5230\u4E01\u5BA4\u3002</p><p>\u7B80\u964B\u7684\u5E8A\u69BB\u4E00\u7247\u51CC\u4E71\uFF0C\u4E0A\u9762\u5374\u6CA1\u6709\u4EBA\u3002\u98CE\u665A\u534E\u8737\u7F29\u5728\u77F3\u5BA4\u7684\u89D2\u843D\u91CC\uFF0C\u8138\u4E0A\u8FD8\u6302\u7740\u7591\u7591\u7684\u7B11\u5BB9\u3002</p><p>\u81EA\u5DF1\u5165\u95E8\u65F6\uFF0C\u5927\u5E08\u59D0\u5DF2\u7ECF\u827A\u6210\u3002\u6709\u65F6\u5979\u4F1A\u7A81\u7136\u4E0B\u5C71\uFF0C\u51E0\u5929\u6216\u8005\u51E0\u4E2A\u6708\u540E\u53C8\u82E5\u65E0\u5176\u4E8B\u7684\u56DE\u5230\u5C71\u4E0A\u3002\u7136\u540E\u4E0D\u4E45\uFF0C\u5C31\u4F1A\u542C\u8BF4\u5728\u67D0\u5730\u884C\u51F6\u4F5C\u6076\u7684\u532A\u5F92\u88AB\u98D8\u6885\u5CF0\u98CE\u5973\u4FA0\u683C\u6740\u3002</p><p>\u7D2B\u73AB\u4E00\u5929\u5929\u957F\u5927\uFF0C\u6D41\u971C\u5251\u8FD9\u4E2A\u540D\u5B57\u5728\u6B66\u6797\u4E2D\u4E5F\u8D8A\u6765\u8D8A\u54CD\u3002\u5728\u5979\u5FC3\u76EE\u4E2D\uFF0C\u5927\u5E08\u59D0\u5C31\u50CF\u5E08\u7236\u4E00\u6837\uFF0C\u4ECE\u6765\u4E0D\u4F1A\u88AB\u4EFB\u4F55\u4EBA\u51FB\u8D25\uFF0C\u6C38\u8FDC\u90FD\u662F\u4E00\u624B\u63E1\u5251\uFF0C\u4E00\u624B\u64A9\u8D77\u79C0\u53D1\uFF0C\u7B11\u541F\u541F\u7684\u6837\u5B50\u3002</p><p>\u4F46\u6B64\u65F6\u7684\u6D41\u971C\u5251\u5374\u8D64\u88F8\u88F8\u8737\u7740\u8EAB\u4F53\uFF0C\u8EBA\u5728\u4E00\u5757\u76B1\u5DF4\u5DF4\u7684\u80AE\u810F\u88AB\u5355\u4E0A\u3002\u767D\u5AE9\u7684\u8EAB\u4F53\u5E26\u7740\u672A\u892A\u7684\u9752\u80BF\uFF0C\u8FD8\u6709\u51E0\u9053\u6DF1\u6DF1\u7684\u8840\u75D5\u3002</p><p>\u9F13\u80C0\u7684\u5C0F\u8179\u4F7F\u7D2B\u73AB\u96BE\u4EE5\u8E72\u4E0B\u6765\uFF0C\u4ED4\u7EC6\u6253\u91CF\u8FD9\u4E2A\u66FE\u7ECF\u98CE\u59FF\u52A8\u4EBA\u7684\u4FA0\u5973\u3002\u5979\u63A9\u4E0A\u77F3\u95E8\uFF0C\u5403\u529B\u5730\u8DEA\u5728\u5E08\u59D0\u8EAB\u65C1\uFF0C\u7528\u4E1D\u5DFE\u64E6\u53BB\u5979\u904D\u4F53\u7684\u6C61\u6E0D\u3002</p><p>\u5927\u5E08\u59D0\u3001\u4E8C\u5E08\u59D0\u3001\u5E08\u7236\uFF0C\u8FD8\u6709\u8FDC\u5728\u6D1B\u9633\u7684\u4E09\u5E08\u59D0\u3002\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u81EA\u5DF1\u7B97\u662F\u5E78\u8FD0\u7684\u4E86\uFF0C\u6BD5\u7ADF\u81EA\u5DF1\u7684\u8089\u4F53\u53EA\u88AB\u4E00\u4E2A\u4EBA\u72EC\u4EAB\u2026\u2026\u800C\u5E08\u7236\u3001\u5E08\u59D0\u4EEC\u52A8\u4EBA\u7684\u8EAB\u4F53\u5374\u8981\u88AB\u65E0\u6570\u4EBA\u5978\u6DEB\u3002\u4E0D\u4EC5\u5982\u6B64\uFF0C\u5927\u5E08\u59D0\u88AB\u65AD\u81C2\u3001\u4E8C\u5E08\u59D0\u88AB\u5E7D\u95ED\u3001\u4E09\u5E08\u59D0\u88AB\u9001\u5165\u5993\u9662\u63A5\u5BA2\uFF0C\u5E08\u7236\u751A\u81F3\u88AB\u5F53\u4F5C\u517B\u80B2\u5996\u7269\u7684\u5DE5\u5177\u3002</p><p>\u7D2B\u73AB\u54BD\u4E0B\u70ED\u6CEA\uFF0C\u64E6\u51C0\u5E08\u59D0\u5507\u89D2\u7684\u7CBE\u6DB2\u6C61\u6E0D\u3002</p><p>\u98CE\u665A\u534E\u7741\u5F00\u53CC\u76EE\uFF0C\u832B\u7136\u770B\u4E86\u5979\u4E00\u773C\uFF0C\u7A81\u7136\u5F20\u53E3\u54AC\u4F4F\u7D2B\u73AB\u7684\u624B\u6307\u3002</p><p>\u7D2B\u73AB\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u7F29\u624B\u3002\u98CE\u665A\u534E\u722C\u8D77\u6765\uFF0C\u4E00\u8FB9\u5440\u5440\u5730\u4F4E\u53EB\uFF0C\u4E00\u8FB9\u62FC\u547D\u5730\u6447\u52A8\u5706\u81C0\u3002</p><p>\u7D2B\u73AB\u8FD9\u65F6\u624D\u8D6B\u7136\u53D1\u73B0\uFF0C\u5979\u81C0\u540E\u7FD8\u7740\u4E00\u6839\u7C97\u77ED\u7684\u5C3E\u5DF4\uFF0C\u6BDB\u53D1\u8038\u7136\uFF0C\u4FE8\u7136\u662F\u4E00\u6761\u72D7\u5C3E\u3002</p><p>\u4F46\u4ECE\u7C97\u7EC6\u770B\u6765\uFF0C\u957F\u5EA6\u53EA\u6709\u6574\u6761\u72D7\u5C3E\u4E09\u5206\u4E4B\u4E00\uFF0C\u50CF\u662F\u622A\u65AD\u4E00\u822C\u3002</p><p>\u7D2B\u73AB\u60F3\u770B\u660E\u767D\uFF0C\u521A\u4E00\u8D77\u8EAB\uFF0C\u98CE\u665A\u534E\u4E5F\u968F\u4E4B\u8F6C\u8EAB\uFF0C\u5934\u524D\u81C0\u540E\u5730\u56F4\u7740\u7D2B\u73AB\u6253\u8F6C\uFF0C\u6D3B\u50CF\u4E00\u6761\u6B22\u5FEB\u7684\u6BCD\u72D7\u3002</p><p>\u7D2B\u73AB\u538B\u4E0B\u6050\u60E7\uFF0C\u7565\u4E00\u601D\u7D22\uFF0C\u7528\u4E1D\u5DFE\u7ED1\u4F4F\u5979\u7684\u53CC\u773C\uFF0C\u7136\u540E\u7ED5\u5230\u98CE\u665A\u534E\u8EAB\u540E\uFF0C\u8F7B\u8F7B\u63B0\u5F00\u7C89\u81C0\u3002</p><p>\u98CE\u665A\u534E\u50CF\u53EA\u671F\u5F85\u4EA4\u5ABE\u7684\u6BCD\u72AC\uFF0C\u9891\u9891\u6643\u52A8\u5706\u81C0\u3002\u5F53\u7D2B\u73AB\u63E1\u4F4F\u72D7\u5C3E\uFF0C\u5979\u6447\u6643\u5F97\u6108\u53D1\u5389\u5BB3\uFF0C\u8089\u7A74\u4E5F\u540C\u65F6\u6DCC\u51FA\u6DEB\u6C34\u3002</p><p>\u9ED1\u8272\u7684\u72D7\u5C3E\u6DF1\u6DF1\u5D4C\u5728\u7EA2\u6DA6\u7684\u809B\u6D1E\u4E2D\uFF0C\u4E00\u70B9\u7F1D\u9699\u4E5F\u65E0\u3002\u7D2B\u73AB\u5411\u5916\u5FAE\u5FAE\u7528\u529B\uFF0C\u624D\u53D1\u73B0\u72D7\u5C3E\u6DF1\u5165\u80A0\u9053\u5C3A\u8BB8\uFF0C\u672B\u7AEF\u7D27\u7D27\u5361\u4F4F\uFF0C\u4EFF\u4F5B\u6709\u4E00\u4E2A\u5DE8\u5927\u7684\u62F3\u5934\u6491\u5728\u91CC\u9762\u3002</p><p>\u300C\u75DB\u4E0D\u75DB\uFF1F\u300D\u7D2B\u73AB\u98A4\u58F0\u95EE\u9053\u3002</p><p>\u98CE\u665A\u534E\u4F3C\u4E4E\u611F\u89C9\u5230\u8EAB\u4F53\u7684\u75BC\u75DB\uFF0C\u7EC6\u7709\u67D4\u67D4\u62E7\u8D77\u3002\u542C\u5230\u7D2B\u73AB\u7684\u8BE2\u95EE\uFF0C\u5979\u5F20\u53E3\u2014\u2014\u300C\u6C6A\u6C6A\u300D\u53EB\u4E86\u4E24\u58F0\u3002</p><p>\u7D2B\u73AB\u5438\u4E86\u53E3\u6C14\uFF0C\u63E1\u4F4F\u72D7\u5C3E\u7F13\u7F13\u62D4\u51FA\u3002\u98CE\u665A\u534E\u9AD8\u9AD8\u4E3E\u5706\u81C0\uFF0C\u65AD\u81C2\u653E\u5728\u8EAB\u524D\uFF0C\u53E6\u4E00\u53EA\u5B8C\u597D\u7684\u624B\u81C2\u6321\u4F4F\u9762\u5B54\uFF0C\u75BC\u4E0D\u53EF\u652F\u5730\u5C0F\u58F0\u54C0\u53EB\u3002</p><p>\u809B\u7AA6\u7FFB\u5377\uFF0C\u96EA\u767D\u7684\u81C0\u95F4\u5410\u9732\u51FA\u4E00\u5708\u9C9C\u7EA2\u5AE9\u8089\uFF0C\u8D8A\u6765\u8D8A\u957F\u3002\u72D7\u5C3E\u6E10\u6E10\u53D8\u957F\uFF0C\u4ECE\u809B\u4E2D\u62D4\u51FA\u7684\u90E8\u5206\u88AB\u8840\u8FF9\u6253\u6E7F\uFF0C\u5947\u602A\u7684\u662F\u5374\u6CA1\u6709\u4E00\u70B9\u79FD\u7269\u3002</p><p>\u7B49\u5C3A\u4F59\u957F\u7684\u72D7\u5C3E\u5B8C\u5168\u62D4\u51FA\uFF0C\u80A0\u9053\u5DF2\u7FFB\u51FA\u4E09\u5BF8\u957F\u77ED\uFF0C\u809B\u6D1E\u5185\u9C9C\u8840\u6DCB\u6F13\u3002\u4F46\u75DB\u82E6\u8FD8\u5728\u540E\u9762\u3002\u4ECE\u5F20\u5F00\u7684\u809B\u95E8\u5411\u5185\u770B\u53BB\uFF0C\u72D7\u5C3E\u672B\u7AEF\u7CFB\u7740\u7684\u662F\u4E00\u4E2A\u7C97\u5927\u7684\u6728\u585E\u3002\u6728\u585E\u8FB9\u7F18\u6491\u5728\u809B\u6D1E\u5185\uFF0C\u770B\u4E0D\u6E05\u7C97\u7EC6\u3002</p><p>\u6728\u585E\u8FDE\u7740\u72D7\u5C3E\u7684\u4E00\u7AEF\u662F\u4E00\u4E2A\u5E73\u9762\uFF0C\u7565\u4E00\u7528\u529B\uFF0C\u6574\u4E2A\u809B\u6D1E\u90FD\u5411\u5916\u9F13\u8D77\uFF0C\u50CF\u662F\u8131\u4F53\u800C\u51FA\u4E00\u822C\u3002</p><p>\u7D2B\u73AB\u6253\u4E86\u4E2A\u54C6\u55E6\uFF0C\u72E0\u4E0B\u5FC3\u9053\uFF1A\u300C\u5E08\u59D0\uFF0C\u4F60\u5FCD\u7740\u4E9B\u3002\u300D</p><p>\u300C\u5575\u300D\u7684\u4E00\u58F0\uFF0C\u4F34\u7740\u98CE\u665A\u534E\u7684\u60E8\u53EB\uFF0C\u8840\u6DCB\u6DCB\u7684\u6728\u585E\u88AB\u751F\u751F\u62D4\u51FA\u3002\u63A5\u7740\u79EF\u84C4\u5DF2\u4E45\u7684\u79FD\u7269\u6DF7\u7740\u9C9C\u8840\u55B7\u6D8C\u800C\u51FA\u3002</p><p>\u98CE\u665A\u534E\u4E24\u819D\u7740\u5730\uFF0C\u9AD8\u9AD8\u4E3E\u8D77\u96EA\u767D\u7684\u5706\u81C0\u3002\u81C0\u7F1D\u95F4\u7834\u88C2\u7684\u83CA\u809B\u4E0D\u65AD\u7FD5\u5F20\uFF0C\u6D8C\u51FA\u7EA2\u9EC4\u6DF7\u6742\u7684\u6C61\u7269\uFF0C\u4E00\u56E2\u56E2\u6389\u5728\u4E24\u817F\u4E4B\u95F4\u3002\u4E0D\u591A\u65F6\uFF0C\u4FEE\u957F\u7684\u7389\u817F\u95F4\u4FBF\u7C98\u6EE1\u4EE4\u4EBA\u4F5C\u5455\u7684\u6C61\u79FD\u3002</p><p>\u7D2B\u73AB\u82E6\u6DA9\u5730\u6254\u4E0B\u72D7\u5C3E\u3002\u8C01\u4F1A\u76F8\u4FE1\u6D41\u971C\u5251\u98CE\u665A\u534E\u7ADF\u4F1A\u633A\u7740\u5C41\u80A1\u55B7\u5C4E\u2026\u2026\u4F46\u8FD9\u662F\u661F\u6708\u6E56\u3002</p><p>\u65E9\u5DF2\u4E60\u60EF\u5E2E\u6BCD\u4EB2\u6E05\u7406\u8EAB\u4F53\u7684\u7D2B\u73AB\u6258\u7740\u5C0F\u8179\u7AD9\u5728\u4E00\u65C1\uFF0C\u7B49\u6C61\u7269\u6392\u5C3D\uFF0C\u5979\u62FF\u51FA\u4E1D\u5DFE\uFF0C\u60F3\u5E2E\u5E08\u59D0\u62ED\u62B9\u5E72\u51C0\u3002\u4F46\u4E1D\u5DFE\u53C8\u8584\u53C8\u5C0F\uFF0C\u5979\u53EA\u597D\u628A\u4E1D\u5DFE\u5377\u8D77\uFF0C\u585E\u5728\u5E08\u59D0\u6D41\u8840\u7684\u809B\u6D1E\u91CC\uFF0C\u7136\u540E\u5750\u5728\u69BB\u4E0A\uFF0C\u547C\u547C\u5598\u6C14\u3002</p><p>\u809A\u5B50\u8D8A\u6765\u8D8A\u5927\uFF0C\u884C\u52A8\u4E5F\u7B28\u62D9\u4E86\u8BB8\u591A\u3002\u5982\u679C\u8981\u627E\u5230\u5B9D\u85CF\uFF0C\u8FD8\u662F\u8D81\u4E34\u4EA7\u524D\u8D76\u7D27\u884C\u52A8\u3002\u7D2B\u73AB\u95ED\u7740\u773C\uFF0C\u53F3\u624B\u8F7B\u8F7B\u6376\u6253\u81EA\u5DF1\u7684\u8170\u80A2\u3002\u5FFD\u7136\u5DE6\u624B\u4E00\u70ED\uFF0C\u88AB\u4E00\u5F20\u6E29\u6DA6\u7684\u5C0F\u5634\u542B\u4F4F\u70ED\u5207\u5730\u8214\u5F04\u8D77\u6765\u3002</p><p>\u90A3\u5F20\u5634\u4F3C\u4E4E\u628A\u624B\u6307\u5F53\u6210\u4E86\u5E38\u542B\u7684\u7269\u4F53\uFF0C\u6ED1\u5AE9\u7684\u5C0F\u820C\u4ECE\u6307\u5C16\u4E00\u8DEF\u6253\u7740\u8F6C\u8214\u5230\u6307\u6839\uFF0C\u540C\u65F6\u8FD8\u7528\u5634\u5507\u88F9\u7D27\uFF0C\u6765\u56DE\u6446\u52A8\u5934\u90E8\u3002</p><p>\u611F\u53D7\u7740\u9999\u820C\u65E0\u5FAE\u4E0D\u81F3\u5730\u670D\u4F8D\uFF0C\u7D2B\u73AB\u5FC3\u91CC\u4E0D\u7981\u53D1\u6C89\u3002\u5BF9\u5931\u53BB\u795E\u667A\u7684\u5E08\u59D0\u6765\u8BF4\uFF0C\u8981\u5B66\u4F1A\u8FD9\u6837\u719F\u7EC3\u7684\u53E3\u6280\uFF0C\u7A76\u7ADF\u8981\u5403\u591A\u5C11\u82E6\u5934\u3002\u8FD9\u6837\u60F3\u6765\uFF0C\u5979\u8EAB\u4E0A\u7684\u4F24\u75D5\u5C31\u4E0D\u96BE\u7406\u89E3\u4E86\u3002</p><p>\u300C\u5C11\u592B\u4EBA\u3002\u300D</p><p>\u300C\u6EDA\uFF01\u300D\u7D2B\u73AB\u7684\u58F0\u97F3\u4E0D\u4EC5\u6709\u6155\u5BB9\u9F99\u7684\u51B7\u9177\uFF0C\u8FD8\u6709\u538B\u6291\u4E0D\u4F4F\u7684\u6068\u610F\u3002</p><p>\u300C\u542F\u7980\u5C11\u592B\u4EBA\uFF0C\u8FD9\u662F\u98CE\u5974\u7684\u65E9\u9910\uFF0C\u5C5E\u4E0B\u653E\u5728\u8FD9\u91CC\u3002\u300D\u90A3\u4EBA\u987F\u4E86\u4E00\u4E0B\uFF0C\u53E3\u6C14\u6108\u53D1\u8C26\u5351\uFF0C\u300C\u970D\u957F\u8001\u6628\u591C\u8D76\u56DE\u795E\u6559\uFF0C\u6307\u540D\u8981\u98CE\u5974\u4F3A\u5019\uFF0C\u8BF7\u5C11\u592B\u4EBA\u2026\u2026\u300D</p><p>\u300C\u6EDA\uFF01\u300D\u7D2B\u73AB\u6012\u559D\u4E00\u58F0\uFF0C\u98CE\u665A\u534E\u50CF\u53D7\u60CA\u5C0F\u72D7\uFF0C\u98DE\u5FEB\u5730\u722C\u5230\u89D2\u843D\u91CC\uFF0C\u60CA\u6050\u5730\u770B\u7740\u5979\u3002</p><p>\u7D2B\u73AB\u5F20\u53E3\u60F3\u9053\u6B49\uFF0C\u65CB\u5373\u60F3\u8D77\u5E08\u59D0\u6839\u672C\u4E0D\u77E5\u9053\u4EC0\u4E48\u9053\u6B49\u3002\u5979\u53F9\u4E86\u53E3\u6C14\uFF0C\u8E52\u8DDA\u7740\u628A\u996D\u76C6\u653E\u5728\u98CE\u665A\u534E\u8EAB\u524D\uFF0C\u7136\u540E\u5F84\u76F4\u5728\u58C1\u4E0A\u5BC6\u5BC6\u9EBB\u9EBB\u7684\u7EB9\u9970\u95F4\u57CB\u5934\u5BFB\u627E\u3002</p><p>\u4E00\u523B\u949F\u540E\uFF0C\u9884\u671F\u7684\u8F7B\u54CD\u9690\u9690\u4F20\u6765\uFF0C\u7D2B\u73AB\u75B2\u5026\u5730\u63D2\u597D\u91D1\u9497\uFF0C\u6162\u6162\u8F6C\u8FC7\u8EAB\u5B50\u3002</p><p>\u98CE\u665A\u534E\u62AC\u7740\u8138\u5C0F\u5FC3\u5730\u770B\u7740\u5979\uFF0C\u79C0\u7F8E\u7684\u8138\u5E9E\u4E0A\u6CBE\u6EE1\u996D\u7C92\u6C64\u6C41\u3002\u770B\u6765\u98CE\u5E08\u59D0\u5DF2\u7ECF\u4E0D\u77E5\u9053\u7528\u624B\uFF0C\u800C\u662F\u76F4\u63A5\u8DB4\u5728\u76C6\u4E0A\u8214\u98DF\u3002</p><p>\u770B\u5230\u98CE\u665A\u534E\u8FF7\u8499\u7684\u773C\u795E\uFF0C\u7D2B\u73AB\u6536\u8D77\u773C\u6CEA\uFF0C\u52C9\u5F3A\u6324\u51FA\u4E00\u4E1D\u7B11\u5BB9\uFF0C\u4F38\u624B\u8F7B\u67D4\u5730\u629A\u6478\u5E08\u59D0\u7684\u5934\u9888\u3002\u5979\u7529\u5F00\u8138\u4E0A\u7684\u6CEA\u73E0\uFF0C\u5374\u65E0\u6CD5\u7529\u5F00\u5FC3\u91CC\u7684\u6127\u759A\u3002\u90FD\u662F\u81EA\u5DF1\u7684\u575A\u6301\uFF0C\u624D\u4F7F\u5E08\u59D0\u88AB\u5173\u5728\u72AC\u5BA4\u4E2D\uFF0C\u4E0E\u6076\u72AC\u4E3A\u4F0D\u2026\u2026\u6700\u7EC8\u5E08\u59D0\u4E5F\u53D8\u6210\u4E86\u4E00\u6761\u6BCD\u72D7\u3002</p><p>\u98CE\u665A\u534E\u4E5F\u611F\u89C9\u5230\u9762\u524D\u8FD9\u4E2A\u5927\u809A\u5B50\u5973\u5B69\u5F88\u4EB2\u5207\uFF0C\u5E76\u4E0D\u50CF\u5176\u4ED6\u4EBA\u90A3\u6837\u603B\u662F\u8BA9\u5979\u75BC\u75DB\u3002\u4F3C\u4E4E\u662F\u53D7\u5230\u9F13\u52B1\uFF0C\u98CE\u665A\u534E\u6447\u7740\u90A3\u6761\u65E0\u5F62\u7684\u5C3E\u5DF4\uFF0C\u722C\u5230\u7D2B\u73AB\u811A\u4E0B\uFF0C\u7528\u9F3B\u5C16\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u55C5\u63A2\u5979\u7684\u5473\u9053\u3002</p><p>\u300C\u5BF9\u4E0D\u8D77\u554A\uFF0C\u5927\u5E08\u59D0\u2026\u2026\u300D\u7D2B\u73AB\u5C0F\u58F0\u8BF4\u7740\uFF0C\u64A9\u8D77\u98CE\u665A\u534E\u7684\u957F\u53D1\u3002</p><p>\u50CF\u662F\u5728\u56DE\u5E94\u5979\u7684\u6127\u759A\uFF0C\u98CE\u665A\u534E\u7A81\u7136\u62AC\u8D77\u4E00\u6761\u5927\u817F\uFF0C\u4E0B\u4F53\u55B7\u51FA\u9053\u6DE1\u9EC4\u7684\u6DB2\u4F53\uFF0C\u7528\u6807\u51C6\u7684\u6492\u5C3F\u52A8\u4F5C\uFF0C\u4E3A\u59D0\u59B9\u4FE9\u4E94\u4E2A\u6708\u6765\u7B2C\u4E00\u6B21\u89C1\u9762\u5212\u4E0A\u4E00\u4E2A\u5B8C\u7F8E\u7684\u53E5\u53F7\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6B64\u65F6\uFF0C\u5728\u5E7D\u6697\u7684\u77F3\u5BAB\u5185\uFF0C\u4E00\u4E2A\u7CBE\u58EE\u7684\u7537\u5B50\u6B63\u4F0F\u5728\u7F8E\u5987\u6000\u4E2D\u7528\u529B\u5438\u542E\u3002</p><p>\u7F8E\u5987\u4E0B\u5DF4\u5FAE\u62AC\uFF0C\u7EA2\u5507\u95F4\u9038\u51FA\u4E00\u7F15\u7F15\u8361\u4EBA\u5FC3\u9B44\u5730\u4F4E\u53EB\u3002\u7A81\u7136\u5A07\u7F8E\u7684\u9762\u4E0A\u9732\u75BC\u75DB\u7684\u8868\u60C5\uFF0C\u300C\u54CE\u5440\u2026\u2026\u54E5\u54E5\u653E\u53E3\u554A\uFF0C\u5A18\u7684\u5976\u6C41\u5DF2\u7ECF\u88AB\u4F60\u5438\u5E72\u5566\u2026\u2026\u75BC\u554A\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u5410\u51FA\u4E73\u5934\uFF0C\u634F\u7740\u5438\u542E\u4E00\u7A7A\u7684\u4E73\u623F\u7B11\u9053\uFF1A\u300C\u5A18\u7684\u5976\u771F\u597D\u559D\uFF0C\u4EE5\u540E\u6BCF\u5929\u90FD\u8BA9\u5B69\u513F\u559D\uFF0C\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u597D\u554A\uFF0C\u300D\u8427\u4F5B\u5974\u7EC6\u58F0\u7EC6\u6C14\u5730\u8BF4\uFF1A\u300C\u5A18\u8EAB\u4E0A\u7684\u90FD\u662F\u9F99\u54E5\u54E5\u7684\u2026\u2026\u4E0D\u53EA\u5976\u6C34\uFF0C\u8FD8\u6709\u5A18\u7684\u5C41\u773C\u513F\uFF0C\u5A18\u7684\u9634\u6237\uFF0C\u8FD8\u6709\u5A18\u7684\u5B50\u5BAB\u2026\u2026\u90FD\u662F\u9F99\u513F\u7684\u2026\u2026\u300D\u88AB\u513F\u5B50\u7EA0\u7F20\u4E00\u591C\u7684\u7F8E\u5987\u8F7B\u8BC9\u7740\u660F\u660F\u5165\u7761\uFF0C\u8138\u4E0A\u8FD8\u5E26\u7740\u4E00\u4E1D\u5E78\u798F\u7684\u7B11\u5BB9\u3002</p><p>\u6155\u5BB9\u9F99\u770B\u4E86\u6BCD\u4EB2\u534A\u664C\uFF0C\u7528\u4E00\u5757\u5E72\u51C0\u7684\u5C3F\u5E03\u5305\u4F4F\u7F8E\u5987\u7684\u4E0B\u4F53\uFF0C\u7136\u540E\u6084\u7136\u79BB\u53BB\u3002</p>',58);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
