import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_104" tabindex="-1"><a class="header-anchor" href="#_104" aria-hidden="true">#</a> 104</h1><p>\u542C\u5B8C\u6155\u5BB9\u9F99\u7684\u4E00\u756A\u8BDD\uFF0C\u53F6\u884C\u5357\u6BD4\u542C\u8BF4\u4ED6\u8981\u4FEE\u70BC\u8FD8\u5929\u8BC0\u8FD8\u8981\u60CA\u9A87\uFF0C\u300C\u8BF7\u5BAB\u4E3B\u4E09\u601D\uFF01\u6B64\u4E3E\u767E\u5BB3\u800C\u65E0\u4E00\u5229\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u8138\u8272\u4E00\u6C89\uFF0C\u300C\u80FD\u5236\u4F4F\u5979\uFF0C\u5C31\u662F\u6700\u5927\u7684\u5229\u76CA\u3002\u300D</p><p>\u300C\u5C11\u592B\u4EBA\u5982\u4ECA\u5DF2\u7ECF\u8EAB\u6000\u516D\u7532\uFF0C\u884C\u52A8\u4E0D\u4FBF\uFF0C\u4F55\u5FC5\u518D\u65BD\u6B64\u672F\uFF1F\u5F53\u65E5\u767D\u6C99\u6D3E\u9001\u5230\u79D8\u65B9\uFF0C\u5C5E\u4E0B\u66FE\u7ECF\u53CD\u8986\u63A8\u7A76\u533B\u7406\uFF0C\u6B64\u672F\u4EE5\u5BF9\u8EAB\u4F53\u5371\u5BB3\u6781\u5927\uFF0C\u82E5\u4E0D\u8F85\u4EE5\u836F\u7269\u4FBF\u4F1A\u8840\u8089\u4FF1\u6BC1\uFF0C\u800C\u4EE5\u836F\u7269\u76F8\u8F85\uFF0C\u540E\u679C\u2026\u2026\u300D</p><p>\u300C\u6211\u610F\u5DF2\u51B3\uFF0C\u4E0D\u5FC5\u591A\u8BF4\uFF01\u300D\u6155\u5BB9\u9F99\u4E00\u53E3\u6253\u65AD\u4ED6\u7684\u8BDD\u3002</p><p>\u53F6\u884C\u5357\u762B\u5750\u5728\u6905\u4E0A\uFF0C\u826F\u4E45\u540E\uFF0C\u957F\u53F9\u4E00\u58F0\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u2026\u2026\u80DC\u7684\u8001\u5A46\u2026\u2026\u54CE\u5537\u2026\u2026\u300D</p><p>\u5C11\u5987\u88AB\u4EBA\u5728\u81C0\u4E0A\u72E0\u8E22\u4E00\u811A\uFF0C\u989D\u5934\u300C\u546F\u300D\u7684\u649E\u5728\u5927\u7406\u77F3\u4E0A\uFF0C\u82E5\u4E0D\u662F\u4E0B\u4F53\u7CFB\u7684\u94C1\u94FE\uFF0C\u8FD9\u4E00\u4E0B\u5C31\u8981\u88AB\u8E22\u51FA\u4E08\u8BB8\uFF0C\u4F46\u4E5F\u56E0\u6B64\u803B\u9AA8\u5267\u75DB\u3002</p><p>\u5979\u987E\u4E0D\u5F97\u75BC\u75DB\uFF0C\u8FDE\u5FD9\u722C\u8D77\u6765\u91CD\u65B0\u8DEA\u597D\uFF0C\u4E24\u624B\u63B0\u5F00\u96EA\u81C0\uFF0C\u8BA9\u4E3B\u5B50\u4EEC\u80FD\u987A\u5229\u5730\u8E22\u5230\u4ED6\u60F3\u8E22\u7684\u5730\u65B9\uFF0C\u53E3\u4E2D\u8BF4\u9053\uFF1A\u300C\u8D31\u5974\u6797\u5A4A\u5B50\u662F\u98D8\u6885\u5CF0\u4E8C\u5F1F\u5B50\uFF0C\u5E08\u7236\u662F\u88AB\u4EBA\u64CD\u6B7B\u53C8\u64CD\u6D3B\u8FC7\u6765\u7684\u8D31\u5974\u96EA\u5CF0\uFF1B\u5927\u5E08\u59D0\u662F\u88AB\u91CE\u732A\u5F00\u82DE\u7684\u98CE\u5A4A\u5B50\uFF1B\u4E09\u5E08\u59B9\u662F\u53C8\u6DEB\u53C8\u8D31\u7684\u7EAA\u5A4A\u5B50\u3002\u300D</p><p>\u300C\u4F60\u4E3A\u4EC0\u4E48\u5728\u8FD9\u513F\uFF1F\u300D\u6709\u4EBA\u602A\u58F0\u602A\u6C14\u5730\u95EE\u9053\u3002</p><p>\u300C\u6797\u5A4A\u5B50\u5AC1\u7684\u6B7B\u9B3C\u8001\u516C\u5F97\u7F6A\u4E86\u5BAB\u4E3B\uFF0C\u6797\u5A4A\u5B50\u662F\u66FF\u6B7B\u9B3C\u8001\u516C\u6055\u7F6A\uFF0C\u5728\u795E\u6559\u8BA9\u5927\u7237\u4EEC\u968F\u4FBF\u64CD\uFF0C\u64CD\u6B7B\u4E3A\u6B62\u3002\u300D</p><p>\u300C\u600E\u4E48\u53D8\u6210\u8FD9\u4E2A\u6837\u5B50\uFF1F\u300D</p><p>\u300C\u8D31\u5974\u4E0D\u957F\u773C\u775B\uFF0C\u5AC1\u4E86\u4E2A\u6B7B\u9B3C\u7537\u4EBA\uFF0C\u6CA1\u6709\u8BA9\u795E\u6559\u5927\u7237\u7ED9\u6797\u5A4A\u5B50\u7684\u8D31\u5C44\u5F00\u82DE\uFF0C\u4E3B\u5B50\u4EEC\u5C31\u628A\u8D31\u5974\u7684\u773C\u775B\u523A\u778E\u4E86\uFF1B\u8D31\u5974\u4E0D\u597D\u597D\u6328\u64CD\uFF0C\u8FD8\u60F3\u9003\u8DD1\u8BA9\u522B\u4EBA\u64CD\uFF0C\u5C31\u88AB\u94C1\u94FE\u7A7F\u7740\u8D31\u5C44\u9501\u5728\u680F\u6746\u4E0A\uFF1B\u8D31\u5974\u53C8\u8822\u53C8\u4E11\uFF0C\u4E3B\u5B50\u4EEC\u5C31\u628A\u8D31\u5974\u7684\u4E73\u5934\u5272\u4E86\uFF0C\u628A\u8D31\u5974\u7684\u8D31\u5C44\u5272\u5E72\u51C0\uFF0C\u8BA9\u8D31\u5974\u80FD\u597D\u597D\u6055\u7F6A\u2026\u2026\u300D\u6797\u9999\u8FDC\u5927\u58F0\u8BF4\u7740\u8FD9\u4E9B\u4E0B\u8D31\u4E4B\u6781\u7684\u8BDD\u8BED\uFF0C\u5C06\u81EA\u5DF1\u7CDF\u8E4B\u5F97\u4F53\u65E0\u5B8C\u80A4\u3002</p><p>\u88AB\u8FD9\u756A\u8BDD\u6FC0\u53D1\u517D\u6B32\uFF0C\u7AD9\u5728\u6797\u9999\u8FDC\u8EAB\u540E\u7684\u6C49\u5B50\u72DE\u7B11\u9053\uFF1A\u300C\u63B0\u597D\u4F60\u7684\u70C2\u5C44\uFF01\u5927\u7237\u8981\u64CD\u4F60\u4E86\uFF01\u300D</p><p>\u654F\u611F\u7684\u6027\u5668\u88AB\u7834\u574F\u6B86\u5C3D\uFF0C\u5927\u591A\u65F6\u5019\u53EA\u80FD\u9760\u5C04\u5728\u4F53\u5185\u7684\u9633\u7CBE\u6765\u6E7F\u6DA6\u3002\u8BF4\u5B8C\u8FD9\u6BB5\u8BDD\uFF0C\u8089\u7A74\u5DF2\u7ECF\u5E72\u6DB8\u3002\u6797\u9999\u8FDC\u4E00\u8FB9\u5F3A\u5FCD\u7740\u4EA4\u5408\u7684\u75DB\u82E6\uFF0C\u4E00\u8FB9\u6717\u58F0\u9053\uFF1A\u300C\u6797\u5A4A\u5B50\u6BCF\u88AB\u64CD\u4E00\u6B21\uFF0C\u7F6A\u5B7D\u5C31\u5C0F\u4E00\u5206\uFF0C\u7B49\u88AB\u5927\u7237\u4EEC\u64CD\u6B7B\uFF0C\u5C31\u6055\u4E86\u7F6A\u3002\u591A\u8C22\u5927\u7237\u3002\u300D</p><p>\u7D2B\u73AB\u770B\u5230\u8FD9\u4E00\u5E55\uFF0C\u53EA\u89C9\u4E00\u9635\u523B\u9AA8\u7684\u75B2\u60EB\uFF0C\u518D\u6CA1\u6709\u529B\u6C14\u53BB\u559D\u6B62\u90A3\u4E9B\u4EE5\u51CC\u8FB1\u5973\u4EBA\u4E3A\u4E50\u7684\u79BD\u517D\u3002</p><p>\u300C\u5AC2\u5AC2\u2026\u2026\u300D\u7D2B\u73AB\u5FC3\u5934\u6EF4\u8840\uFF0C\u4F46\u6797\u9999\u8FDC\u5374\u6CA1\u6709\u4EC0\u4E48\u75DB\u82E6\u7684\u8868\u60C5\uFF0C\u957F\u65F6\u95F4\u6BEB\u4E0D\u95F4\u65AD\u7684\u6B8B\u5FCD\u6298\u78E8\uFF0C\u82F1\u6C14\u8FEB\u4EBA\u7684\u5BD2\u6708\u5200\u5DF2\u7ECF\u5B8C\u5168\u6D88\u5931\u65E0\u8FF9\uFF0C\u53EA\u5269\u4E0B\u4E00\u4E2A\u540C\u6837\u76F8\u8C8C\u7684\u6797\u5A4A\u5B50\u3002</p><p>\u770B\u5230\u6240\u6709\u7684\u4EB2\u4EBA\u90FD\u56E0\u4E3A\u81EA\u5DF1\u800C\u9971\u53D7\u6298\u78E8\uFF0C\u6216\u6B8B\u6216\u4F24\u65E0\u4E00\u5E78\u514D\uFF0C\u5C11\u5973\u6DF1\u6DF1\u75DB\u6068\u7740\u81EA\u5DF1\u7684\u65E0\u80FD\u4E3A\u529B\uFF0C\u751A\u81F3\u5728\u5FC3\u5E95\u56E0\u4E3A\u81EA\u5DF1\u6BEB\u53D1\u65E0\u4F24\u5730\u65C1\u89C2\u800C\u9690\u9690\u4F5C\u75DB\u3002</p><p>\u4F46\u5F88\u5FEB\u5979\u5C31\u53EF\u4EE5\u505A\u4E00\u4E9B\u8865\u507F\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u9886\u5979\u6765\u5230\u5145\u6EE1\u836F\u9999\u7684\u77F3\u5BA4\uFF0C\u53F6\u884C\u5357\u5C31\u4E00\u76F4\u5728\u6C89\u9ED8\u3002</p><p>\u7D2B\u73AB\u89C9\u51FA\u6C14\u6C1B\u6709\u5F02\uFF0C\u6545\u4F5C\u8F7B\u677E\u5730\u8BF4\u9053\uFF1A\u300C\u8001\u5934\u513F\uFF0C\u662F\u4E0D\u662F\u592A\u95F2\u4E86\uFF1F\u60F3\u627E\u4EBA\u8BF4\u8BF4\u8BDD\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u5E72\u54B3\u4E86\u4E00\u58F0\uFF0C\u7528\u76EE\u5149\u5411\u65C1\u8FB9\u4E00\u6307\uFF0C\u8270\u96BE\u5730\u8BF4\u9053\uFF1A\u300C\u8BF7\u5C11\u592B\u4EBA\u8EBA\u5230\u90A3\u91CC\u6765\u3002\u300D</p><p>\u90A3\u5F20\u77F3\u6848\u7D2B\u73AB\u65E9\u5DF2\u89C1\u8FC7\uFF0C\u5F53\u65E5\u767D\u6C0F\u59D0\u59B9\u5C31\u662F\u8EBA\u5728\u4E0A\u9762\u7A7F\u4E0A\u4E73\u94C3\u9634\u94C3\u3002\u7D2B\u73AB\u5FC3\u91CC\u6253\u9F13\uFF0C\u83AB\u4E0D\u6210\u8FD9\u8001\u5BB6\u4F19\u5931\u5FC3\u75AF\u4E86\uFF1F\u8981\u7ED9\u81EA\u5DF1\u4E5F\u6234\u4E0A\u90A3\u79CD\u53EF\u803B\u7684\u4E1C\u897F\uFF1F</p><p>\u5012\u8981\u770B\u770B\u4ED6\u846B\u82A6\u91CC\u5356\u7684\u4EC0\u4E48\u836F\uFF01\u7D2B\u73AB\u4E00\u54AC\u7259\uFF0C\u5750\u5728\u77F3\u5E8A\u4E0A\uFF0C\u5F20\u5F00\u53CC\u81C2\u5938\u5F20\u5730\u4F38\u4E86\u4E2A\u61D2\u8170\uFF0C\u8BF4\u9053\uFF1A\u300C\u672C\u592B\u4EBA\u6BCF\u5929\u633A\u7740\u809A\u5B50\u8D70\u6765\u8D70\u53BB\uFF0C\u597D\u7D2F\u7684\u3002\u53F6\u4F2F\u4F2F\u80FD\u4E0D\u80FD\u60F3\u4E2A\u6CD5\u5B50\u8BA9\u8FD9\u5BB6\u4F19\u5FEB\u70B9\u751F\u51FA\u6765\uFF1F\u6211\u4E5F\u597D\u5C11\u53D7\u4E9B\u7F6A\u3002\u300D</p><p>\u53F6\u884C\u5357\u6CA1\u6709\u56DE\u7B54\uFF0C\u800C\u662F\u7AEF\u7740\u4E00\u4E2A\u94DC\u76C6\uFF0C\u4E00\u4E2A\u76DB\u9488\u7684\u6728\u5323\u3002\u4ED6\u628A\u94DC\u76C6\u653E\u5728\u7089\u5B50\u4E0A\uFF0C\u7136\u540E\u4ECE\u67DC\u4E2D\u6478\u51FA\u4E00\u4E2A\u5BC6\u5C01\u7684\u94DC\u58F6\uFF0C\u5012\u51FA\u4E00\u676F\u7D2B\u9ED1\u7684\u6DB2\u4F53\uFF0C\u6E17\u6C34\u6405\u5300\u3002</p><p>\u597D\u50CF\u662F\u8981\u6765\u771F\u7684\u4E86\u3002\u7D2B\u73AB\u5FC3\u4E00\u4E0B\u4E86\u60AC\u4E86\u8D77\u6765\uFF0C\u8083\u5BB9\u9053\uFF1A\u300C\u90A3\u5BB6\u4F19\u8981\u600E\u4E48\u5BF9\u4ED8\u6211\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u50CF\u662F\u88AB\u7089\u70DF\u718F\u5230\uFF0C\u773C\u7736\u6709\u4E9B\u53D1\u7EA2\uFF0C\u300C\u542C\u8BF4\u4F60\u9014\u4E2D\u8BD5\u56FE\u9003\u8DD1\u2026\u2026\u8F7B\u529F\u5F88\u597D\u2026\u2026\u300D</p><p>\u300C\u54FC\uFF01\u5982\u679C\u6211\u80FD\u6740\u4E86\u4ED6\uFF0C\u5C31\u4E0D\u5FC5\u9003\u4E86\u3002\u600E\u4E48\uFF1F\u90A3\u5BB6\u4F19\u8981\u5E9F\u6211\u7684\u817F\uFF1F\u300D\u7D2B\u73AB\u4E00\u8FB9\u8BF4\uFF0C\u4E00\u8FB9\u6253\u91CF\u53F6\u884C\u5357\u7684\u8138\u8272\uFF0C\u5FC3\u91CC\u4E0D\u7965\u7684\u611F\u89C9\u8D8A\u6765\u8D8A\u6D53\u3002</p><p>\u5E72\u7626\u7684\u624B\u6307\u4F38\u5230\u80F8\u524D\uFF0C\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u8863\u9886\u7684\u8774\u8776\u6263\u4E4D\u7136\u5206\u5F00\uFF0C\u9732\u51FA\u4E00\u62B9\u6676\u83B9\u7684\u80A4\u5149\u3002\u7D2B\u73AB\u4E0D\u77E5\u9053\u4ED6\u8981\u5E72\u4EC0\u4E48\uFF0C\u4F46\u603B\u4E0D\u4F1A\u662F\u597D\u4E8B\uFF0C\u5979\u5F3A\u7B11\u9053\uFF1A\u300C\u8001\u5934\u513F\uFF0C\u4F60\u522B\u4E71\u6765\u2026\u2026\u300D</p><p>\u8863\u886B\u892A\u5230\u80A9\u540E\uFF0C\u4E00\u80A1\u5BD2\u610F\u76F4\u5165\u5FC3\u5E95\u3002\u8584\u8584\u7684\u4EB5\u8863\u4E0B\uFF0C\u4E24\u53EA\u5F62\u72B6\u4F18\u7F8E\u7684\u9999\u4E73\u4E0D\u4F4F\u98A4\u52A8\uFF0C\u663E\u793A\u51FA\u5C11\u5973\u60CA\u6050\u7684\u5FC3\u60C5\u3002\u5F53\u53F6\u884C\u5357\u6380\u8D77\u4EB5\u8863\u65F6\uFF0C\u7D2B\u73AB\u518D\u65E0\u6CD5\u6545\u4F5C\u9547\u5B9A\uFF0C\u8FDE\u5FD9\u628A\u4E24\u81C2\u62B1\u5728\u80F8\u524D\uFF0C\u6C34\u7075\u7075\u7684\u53CC\u773C\u6115\u7136\u770B\u7740\u8FD9\u4E2A\u7528\u533B\u672F\u6B8B\u5BB3\u8FC7\u81EA\u5DF1\u6240\u6709\u4EB2\u4EBA\uFF0C\u505A\u5B7D\u65E0\u6570\u7684\u8001\u5934\u3002</p><p>\u300C\u59D3\u53F6\u7684\uFF0C\u4E71\u89E3\u6211\u7684\u8863\u670D\uFF0C\u4F60\u4E0D\u6015\u4ED6\u6740\u4E86\u4F60\u5417\uFF1F\u300D\u7D2B\u73AB\u58F0\u97F3\u5F88\u8F7B\u3002</p><p>\u300C\u4F60\u4EE5\u4E3A\u81EA\u5DF1\u5F88\u4E86\u4E0D\u8D77\u5417\uFF1F\u300D\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\u5728\u95E8\u65C1\u54CD\u8D77\uFF0C\u300C\u4E5F\u8BB8\u4F60\u5149\u7740\u8EAB\u5B50\u5C31\u4E0D\u4F1A\u4E71\u8DD1\u4E86\u3002\u4F46\u6211\u5B9E\u5728\u662F\u4E0D\u653E\u5FC3\u3002\u300D\u4ED6\u5E7D\u5E7D\u770B\u7740\u7D2B\u73AB\uFF0C\u300C\u6240\u4EE5\u6211\u8BF7\u53F6\u62A4\u6CD5\u51FA\u624B\u2026\u2026\u300D</p><p>\u300C\u8981\u62BD\u6211\u7684\u7B4B\u5417\uFF1F\u8FD8\u662F\u788E\u6211\u7684\u9AA8\uFF1F\u300D\u60F3\u5230\u4ED6\u4EEC\u7684\u624B\u6BB5\uFF0C\u7D2B\u73AB\u5A07\u8EAF\u7981\u4E0D\u4F4F\u6218\u6817\u8D77\u6765\uFF0C\u5979\u98A4\u58F0\u9053\uFF1A\u300C\u4F60\u6562\u8FD9\u4E48\u505A\uFF0C\u6211\u5C31\u6B7B\u4F60\u770B\uFF01\u300D</p><p>\u300C\u62B1\u7740\u4E00\u56E2\u4E0D\u4F1A\u52A8\u7684\u8089\uFF0C\u64CD\u8D77\u6765\u6709\u4EC0\u4E48\u8DA3\u5473\uFF1F\u300D\u6155\u5BB9\u9F99\u51B7\u51B7\u9053\uFF1A\u300C\u90A3\u79CD\u4E1C\u897F\u6709\u5A18\u4E00\u4E2A\u5C31\u591F\u4E86\u3002\u6211\u53EA\u8981\u4F60\u8DD1\u8D77\u6765\u6CA1\u90A3\u5FEB\u5C31\u884C\u3002\u300D</p><p>\u53F6\u884C\u5357\u5C06\u4E00\u5757\u6D01\u767D\u7684\u6BDB\u5DFE\u6D78\u5728\u6CB8\u817E\u7684\u94DC\u76C6\u91CC\uFF0C\u5E26\u4E0A\u76AE\u624B\u5957\uFF0C\u6162\u6162\u63C9\u6413\u7740\u8BF4\uFF1A\u300C\u8BF7\u5BAB\u4E3B\u4E09\u601D\u2026\u2026\u300D</p><p>\u53F6\u8001\u5934\u62BD\u7B4B\u5265\u76AE\u4ECE\u6765\u90FD\u662F\u624B\u8D77\u5200\u843D\uFF0C\u6CA1\u6709\u534A\u5206\u72B9\u8C6B\uFF0C\u8FD9\u6B21\u771F\u662F\u5927\u4E8B\u4E0D\u5999\u4E86\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8D8A\u542C\u8D8A\u614C\uFF0C\u62AC\u8EAB\u6B32\u8D77\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u628A\u6309\u4F4F\u5979\u7684\u80A9\u5934\uFF0C\u4F38\u624B\u626F\u6389\u4EB5\u8863\u3002\u9165\u4E73\u5728\u624B\u81C2\u95F4\u60CA\u614C\u5730\u8DF3\u8DC3\u7740\uFF0C\u5149\u6DA6\u5982\u8102\uFF0C\u60F9\u4EBA\u7231\u601C\u3002</p><p>\u53F6\u884C\u5357\u53F9\u4E86\u53E3\u6C14\uFF0C\u628A\u70ED\u817E\u817E\u7684\u6BDB\u5DFE\u6309\u5728\u7D2B\u73AB\u80A9\u5934\u3002</p><p>\u7D2B\u73AB\u53EA\u89C9\u80A9\u4E0A\u4E00\u70EB\uFF0C\u63A5\u7740\u9EBB\u9165\u9165\u6CA1\u4E86\u77E5\u89C9\u3002</p><p>\u90A3\u4E9B\u7D2B\u9ED1\u8272\u7684\u836F\u6C34\u4EFF\u4F5B\u4E00\u9053\u9B54\u5492\uFF0C\u8F7B\u6613\u4FBF\u62B9\u53BB\u4E86\u8EAB\u4E0A\u7684\u611F\u8BC6\u3002\u5C11\u5973\u76F4\u633A\u633A\u8EBA\u5728\u77F3\u6848\u4E0A\uFF0C\u4E0A\u8863\u88AB\u62C9\u5230\u8170\u9645\uFF0C\u767D\u99A5\u99A5\u7684\u7389\u4E73\u5E76\u5728\u80F8\u524D\uFF0C\u53C8\u9999\u53C8\u8F6F\u6676\u83B9\u53EF\u7231\u3002\u6D51\u5706\u7684\u4E73\u5CF0\u4E0A\uFF0C\u4E24\u7C92\u5C0F\u5DE7\u7684\u4E73\u5934\u5FAE\u5FAE\u7FD8\uFF0C\u7EA2\u5AE9\u8FF7\u4EBA\u3002</p><p>\u53F6\u884C\u5357\u4E22\u5F00\u6BDB\u5DFE\uFF0C\u63EA\u6389\u624B\u5957\uFF0C\u6DF1\u6DF1\u5438\u4E86\u53E3\u6C14\u3002\u9759\u4E0B\u5FC3\u6765\uFF0C\u661F\u6708\u6E56\u533B\u795E\u773C\u4E2D\u987F\u65F6\u7CBE\u5149\u56DB\u5C04\u3002</p><p>\u4ED6\u4E2D\u6307\u4E00\u6311\uFF0C\u300C\u55D2\u300D\u7684\u4E00\u58F0\u6253\u5F00\u6728\u5323\uFF0C\u4E00\u652F\u94F6\u9488\u500F\u5FFD\u8DF3\u51FA\uFF0C\u6296\u624B\u523A\u5728\u7D2B\u73AB\u4E73\u6839\u5904\u3002</p><p>\u4ED6\u884C\u533B\u591A\u5E74\uFF0C\u8BA4\u7A74\u5947\u51C6\uFF0C\u94F6\u9488\u4E00\u523A\u800C\u5165\uFF0C\u9488\u5C16\u6DF1\u5165\u4E24\u5BF8\uFF0C\u76F4\u62B5\u4E73\u817A\u3002\u4ED6\u770B\u4E5F\u4E0D\u770B\uFF0C\u53CD\u624B\u4E00\u642D\uFF0C\u53C8\u4E00\u6839\u94F6\u9488\u8DF3\u5230\u6307\u5C16\uFF0C\u65CB\u5373\u4ECE\u53E6\u4E00\u4FA7\u523A\u8FDB\u4E73\u6839\u3002</p><p>\u7D2B\u73AB\u8EAB\u4E0D\u80FD\u52A8\uFF0C\u53E3\u4E0D\u80FD\u5F20\uFF0C\u773C\u7741\u7741\u770B\u7740\u94F6\u9488\u4E00\u6839\u4E00\u6839\u523A\u5165\u9EBB\u6728\u7684\u4E73\u623F\u5185\uFF0C\u5FC3\u91CC\u53C8\u662F\u7D27\u5F20\u53C8\u662F\u5947\u602A\u3002\u4E0D\u60F3\u8BA9\u81EA\u5DF1\u65BD\u5C55\u8F7B\u529F\uFF0C\u6709\u5B83\u4EC0\u4E48\u4E8B\uFF1F</p><p>\u50CF\u662F\u56DE\u7B54\u5979\u7684\u7591\u60D1\uFF0C\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u5F53\u65E5\u5728\u6D1B\u9633\u90A3\u4E2A\u53EB\u660E\u5170\u7684\u5C0F\u5A4A\u5B50\uFF0C\u4F60\u8FD8\u8BB0\u5F97\u5427\u3002\u5C0F\u5C0F\u5E74\u7EAA\u5C31\u6709\u90A3\u4E48\u5BF9\u5927\u5976\u662F\u4E0D\u662F\u5F88\u5947\u602A\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u7ACB\u523B\u60F3\u8D77\u6CAE\u6E20\u660E\u5170\u90A3\u53CC\u4E0D\u6210\u6BD4\u4F8B\u7684\u5DE8\u4E73\uFF0C\u4E0E\u6B66\u9675\u65F6\u76F8\u6BD4\uFF0C\u77ED\u77ED\u4E24\u4E2A\u6708\uFF0C\u5979\u7684\u4E73\u623F\u5C31\u5927\u4E86\u6570\u500D\u2026\u2026\u96BE\u9053\u2026\u2026</p><p>\u300C\u6CA1\u9519\u3002\u73B0\u5728\u4F60\u6000\u7740\u5B69\u5B50\uFF0C\u884C\u52A8\u8D77\u6765\u4E0D\u592A\u65B9\u4FBF\uFF0C\u4F46\u5B69\u5B50\u603B\u662F\u4F1A\u751F\u4E0B\u6765\u7684\u3002\u5982\u679C\u5E26\u7740\u4E24\u53EA\u6C89\u7538\u7538\u7684\u5927\u5976\u5B50\uFF0C\u4F60\u7684\u8F7B\u529F\u5C31\u4F1A\u6253\u4E2A\u6298\u6263\u5427\u3002\u300D</p><p>\u300C\u767D\u6C99\u6D3E\u7684\u836F\u65B9\u6709\u4E00\u4E2A\u7F3A\u9677\uFF0C\u867D\u7136\u53EF\u4EE5\u4F7F\u4E73\u623F\u66B4\u589E\uFF0C\u4F46\u4EE5\u540E\u65E0\u6CD5\u5206\u6CCC\u4E73\u6C41\u3002</p><p>\u6709\u52B3\u53F6\u62A4\u6CD5\u8D39\u5FC3\uFF0C\u5B8C\u5584\u4E86\u836F\u65B9\u3002\u4E0D\u4EC5\u4F1A\u4EA7\u4E73\uFF0C\u800C\u4E14\u5976\u6C34\u6E90\u6E90\u4E0D\u7EDD\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5934\u6655\u76EE\u7729\uFF0C\u4F3C\u4E4E\u770B\u5230\u81EA\u5DF1\u8D39\u529B\u5730\u6367\u7740\u4FE9\u6BD4\u8EAB\u4F53\u8FD8\u5927\u7684\u4E73\u623F\uFF0C\u4E00\u6B65\u4E00\u632A\uFF0C\u4E73\u6C41\u55B7\u5F97\u5230\u5904\u90FD\u662F\u2026\u2026\u5979\u5589\u5934\u683C\u683C\u4F5C\u54CD\uFF0C\u79C0\u7738\u671B\u7740\u6155\u5BB9\u9F99\uFF0C\u6D41\u9732\u51FA\u4E5E\u601C\u7684\u610F\u5473\u3002</p><p>\u300C\u5BB3\u6015\uFF1F\u665A\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u54E5\u54E5\u4E0D\u820D\u5F97\u62BD\u4F60\u7684\u7B4B\uFF0C\u788E\u4F60\u7684\u9AA8\uFF0C\u53EA\u597D\u7528\u8FD9\u4E2A\u529E\u6CD5\u8BA9\u4F60\u4E56\u4E00\u70B9\u3002\u300D</p><p>\u8BF4\u8BDD\u95F4\uFF0C\u7D2B\u73AB\u53F3\u4E73\u5DF2\u7ECF\u523A\u5165\u4E5D\u6839\u94F6\u9488\u3002\u94F6\u9488\u6216\u5E73\u6216\u7AD6\uFF0C\u6216\u76F4\u6216\u659C\uFF0C\u5206\u522B\u4ECE\u4E73\u6655\u3001\u4E73\u6839\u3001\u4E73\u4FA7\u523A\u5230\u4E73\u817A\u9644\u8FD1\uFF0C\u4E00\u652F\u652F\u5728\u7C89\u5AE9\u7684\u4E73\u7403\u4E0A\u95EA\u52A8\u5BD2\u5149\u3002</p><p>\u521A\u624D\u7684\u836F\u7269\u4F3C\u4E4E\u662F\u9EBB\u9189\u4E4B\u7528\uFF0C\u62B9\u8FC7\u4E4B\u540E\uFF0C\u81EA\u5DF1\u7684\u4E73\u623F\u4FBF\u50CF\u662F\u79BB\u4F53\u800C\u53BB\uFF0C\u94F6\u9488\u5165\u4F53\u7D2B\u73AB\u5E76\u6CA1\u6709\u611F\u89C9\u5230\u75BC\u75DB\uFF0C\u751A\u81F3\u8FDE\u8840\u8FF9\u6CA1\u6709\u3002\u770B\u7740\u53F6\u884C\u5357\u62FF\u51FA\u4E00\u76D2\u9ED1\u8272\u7684\u836F\u818F\u6D82\u5728\u4E73\u623F\u4E0A\uFF0C\u7D2B\u73AB\u50CF\u662F\u5728\u65C1\u770B\u7740\u522B\u4EBA\u7684\u4E73\u623F\u88AB\u6D82\u7684\u6F06\u9ED1\u3002\u90A3\u4E00\u77AC\u95F4\uFF0C\u5979\u751A\u81F3\u89C9\u5F97\u5F88\u53EF\u7B11\u3002</p><p>\u4F46\u5C11\u5973\u5E76\u6CA1\u6709\u7B11\u51FA\u6765\u3002</p><p>\u53F6\u884C\u5357\u624B\u6307\u4E0A\u4E0B\u7FFB\u98DE\uFF0C\u4F9D\u6B21\u637B\u8FC7\u4E5D\u6839\u94F6\u9488\uFF0C\u7528\u5185\u529B\u6FC0\u53D1\u4E73\u817A\u3002\u4ED6\u7684\u5185\u529B\u5E76\u4E0D\u5F3A\u52B2\uFF0C\u4F46\u6BCF\u4E00\u9053\u771F\u6C14\u90FD\u6070\u5230\u597D\u5904\uFF0C\u7EDD\u65E0\u534A\u5206\u591A\u4F59\u6216\u8005\u4E0D\u8DB3\u3002</p><p>\u5438\u6536\u4E86\u836F\u818F\u7684\u4E73\u623F\u5728\u5185\u529B\u50AC\u53D1\u4E0B\uFF0C\u4ECE\u5185\u90E8\u4F20\u6765\u4E00\u9635\u9690\u9690\u7684\u80C0\u75DB\u3002\u63A5\u7740\u80C0\u75DB\u8513\u5EF6\u5F00\u6765\uFF0C\u6BCF\u4E00\u5BF8\u4E73\u8089\u4F3C\u4E4E\u90FD\u88AB\u6FC0\u6D3B\uFF0C\u4E0D\u4F4F\u6323\u624E\u8DF3\u52A8\u3002\u9165\u4E73\u4E0A\u7684\u836F\u818F\u8D8A\u6765\u8D8A\u6DE1\uFF0C\u6E10\u81F3\u65E0\u8E2A\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u96EA\u767D\u7684\u4E73\u7403\u4EFF\u4F5B\u5145\u6C14\u822C\u81A8\u80C0\u8D77\u6765\u3002</p><p>\u7D2B\u73AB\u60CA\u6050\u5730\u770B\u7740\u81EA\u5DF1\u4E00\u624B\u53EF\u63E1\u7684\u5C0F\u5DE7\u5AE9\u4E73\u4E4D\u7136\u589E\u5927\uFF0C\u5FC3\u5934\u9707\u98A4\u65E0\u6BD4\u3002\u66F4\u96BE\u4EE5\u627F\u53D7\u7684\u662F\u90A3\u80A1\u5267\u75DB\uFF0C\u4E73\u623F\u4EFF\u4F5B\u8981\u7206\u88C2\u5F00\u6765\u3002\u7EC6\u5AE9\u7684\u808C\u80A4\u5BF8\u5BF8\u7EF7\u7D27\uFF0C\u51E0\u4E4E\u65E0\u6CD5\u5BB9\u7EB3\u66B4\u589E\u7684\u4E73\u8089\u3002\u8FDE\u4E73\u6655\u4E5F\u968F\u4E4B\u6269\u5C55\uFF0C\u53EA\u6709\u7CBE\u81F4\u7684\u4E73\u5934\u4F9D\u7136\u5982\u6545\u3002</p><p>\u6676\u83B9\u7684\u96EA\u80A4\u5FFD\u7136\u5192\u51FA\u4E00\u7C92\u8840\u7EA2\uFF0C\u63A5\u7740\u53C8\u662F\u4E00\u7C92\uFF0C\u7247\u523B\u95F4\uFF0C\u5149\u6D01\u7684\u7389\u4E73\u4E0B\u663E\u51FA\u4E00\u53EA\u9AD8\u4E3E\u7684\u51E4\u7FFC\u3002\u90A3\u662F\u5728\u7956\u9675\u523A\u4E0B\u7684\u7EB9\u8EAB\uFF0C\u6155\u5BB9\u9F99\u6BCF\u4E00\u9488\u90FD\u7528\u771F\u6C14\u5728\u76AE\u80A4\u4E0B\u9020\u6210\u65E0\u6CD5\u7652\u5408\u7684\u4F24\u53E3\uFF0C\u5E73\u65F6\u4E00\u65E0\u5F02\u72B6\uFF0C\u4E00\u65E6\u52A8\u60C5\u6216\u8005\u7231\u60CA\uFF0C\u8840\u884C\u52A0\u901F\uFF0C\u7EB9\u8EAB\u4FBF\u4F1A\u6D6E\u73B0\u51FA\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u7528\u624B\u6307\u5728\u6ED1\u817B\u7684\u808C\u80A4\u4E0A\u52FE\u5212\u7740\u51E4\u51F0\u7684\u8F6E\u5ED3\uFF0C\u6162\u6162\u5782\u4E0B\u76EE\u5149\uFF0C\u770B\u7740\u59B9\u59B9\u9F13\u80C0\u7684\u5C0F\u8179\uFF0C\u8138\u4E0A\u6CA1\u6709\u4E00\u4E1D\u8868\u60C5\u3002</p>',62);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
