import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_105" tabindex="-1"><a class="header-anchor" href="#_105" aria-hidden="true">#</a> 105</h1><p>\u6C89\u7761\u4E2D\u7684\u8427\u4F5B\u5974\u7A81\u7136\u4E00\u9635\u5FC3\u60B8\uFF0C\u60CA\u9192\u8FC7\u6765\u3002</p><p>\u5979\u6025\u4FC3\u5730\u5598\u4E86\u4E24\u53E3\u6C14\uFF0C\u8212\u89E3\u90A3\u80A1\u83AB\u540D\u7684\u60CA\u60B8\u3002\u6628\u591C\u9AD8\u6F6E\u8FC7\u540E\uFF0C\u8EAB\u4F53\u7684\u9965\u6E34\u6682\u65F6\u5E73\u606F\u4E0B\u6765\uFF0C\u6B64\u65F6\u7741\u5F00\u773C\u775B\uFF0C\u65E0\u8FB9\u7684\u51C4\u51C9\u548C\u9178\u695A\u987F\u65F6\u6D8C\u4E0A\u5FC3\u5934\u3002</p><p>\u7F8E\u5987\u832B\u7136\u671B\u7740\u51B0\u51B7\u7684\u77F3\u5BA4\uFF0C\u5583\u5583\u9053\uFF1A\u300C\u7941\u54E5\uFF0C\u4EBA\u5BB6\u2026\u2026\u771F\u7684\u662F\u6CA1\u529E\u6CD5\u2026\u2026\u9F99\u513F\u597D\u5389\u5BB3\u2026\u2026\u300D\u5979\u5C0F\u58F0\u5462\u54DD\u7740\uFF0C\u6CEA\u6C34\u4E00\u6EF4\u6EF4\u4ECE\u9B13\u89D2\u6EDA\u843D\uFF0C\u300C\u4ED6\u6740\u8D77\u4EBA\u6765\u773C\u90FD\u4E0D\u7728\u2014\u2014\u5974\u5974\u4E0D\u662F\u6015\u6B7B\uFF0C\u4EBA\u5BB6\u6B7B\u8FC7\u51E0\u6B21\uFF0C\u53EF\u4ED6\u603B\u4E0D\u8BA9\u6211\u6B7B\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u54FD\u54BD\u7740\u8BF4\uFF1A\u300C\u7941\u54E5\uFF0C\u5974\u5974\u518D\u8DDF\u4F60\u8BF4\u8FD9\u4E00\u6B21\u8BDD\uFF0C\u4EE5\u540E\u518D\u4E0D\u70E6\u4F60\u4E86\u3002\u6211\u672C\u6765\u60F3\u5230\u9634\u95F4\u518D\u4F3A\u5019\u4F60\uFF0C\u53EF\u5974\u5974\u8EAB\u4F53\u8FD9\u4E48\u810F\uFF0C\u4EE5\u540E\u53EA\u80FD\u4E0B\u5730\u72F1\u2026\u2026\u6211\u5C31\u8DDF\u9F99\u513F\u4E00\u8D77\u4E0B\u5730\u72F1\uFF0C\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u5979\u6CEA\u6D41\u6EE1\u9762\uFF0C\u51C4\u7136\u9053\uFF1A\u300C\u2026\u2026\u6211\u5FC3\u597D\u75BC\uFF0C\u4F46\u53C8\u6CA1\u529E\u6CD5\u2026\u2026\u771F\u7684\u6CA1\u529E\u6CD5\u2026\u2026\u73AB\u513F\u6BD4\u6211\u5F3A\u5F97\u591A\u4E5F\u6CA1\u529E\u6CD5\u2026\u2026\u7941\u54E5\u2026\u2026\u4F60\u4E0D\u8981\u602A\u6211\u2026\u2026\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5440\u5440\u2026\u2026\u6C6A\u6C6A\u2026\u2026\u300D\u5973\u5B50\u51C4\u5389\u800C\u602A\u5F02\u7684\u60E8\u53EB\uFF0C\u4ECE\u706B\u5802\u5927\u5385\u4E0D\u4F4F\u4F20\u51FA\u3002</p><p>\u98CE\u665A\u534E\u56DB\u80A2\u5267\u98A4\uFF0C\u75DB\u5F97\u51B7\u6C57\u6DCB\u6F13\uFF0C\u4F46\u5979\u4E0D\u77E5\u9053\uFF0C\u4E5F\u4E0D\u6562\u9003\u907F\uFF0C\u53EA\u80FD\u52AA\u529B\u633A\u7740\u5706\u81C0\uFF0C\u4EFB\u90A3\u4E2A\u7EA2\u888D\u6076\u4EBA\u73A9\u5F04\u3002</p><p>\u970D\u72C2\u7130\u72DE\u7B11\u7740\u7528\u529B\u4E00\u6345\uFF0C\u534A\u6761\u624B\u81C2\u786C\u751F\u751F\u63D2\u8FDB\u5A07\u5AE9\u7684\u8089\u7A74\u5185\u3002\u5373\u4F7F\u88AB\u767E\u822C\u8E42\u8E8F\uFF0C\u8FD9\u6837\u7684\u6467\u6B8B\u4E5F\u975E\u98CE\u665A\u534E\u6240\u80FD\u627F\u53D7\u3002\u7EA2\u5AE9\u7684\u8089\u7A74\u987F\u65F6\u88AB\u6495\u5F00\u4E24\u9053\u4F24\u53E3\uFF0C\u9C9C\u8840\u6DCB\u6F13\u3002</p><p>\u970D\u72C2\u7130\u88AB\u9609\u5272\u4E4B\u540E\uFF0C\u624B\u6BB5\u8D8A\u53D1\u6B8B\u5FCD\uFF0C\u6570\u6708\u6765\u4ED6\u7387\u9886\u706B\u5802\u4EBA\u9A6C\u63A5\u8FDE\u706D\u6389\u5341\u4F59\u4E2A\u4E0D\u670D\u4ECE\u7684\u5E2E\u4F1A\uFF0C\u6BCF\u6B21\u51FA\u624B\u5FC5\u7136\u662F\u9E21\u72AC\u4E0D\u7559\u3002\u5BF9\u4E8E\u5973\u4EBA\uFF0C\u66F4\u662F\u7528\u5C3D\u624B\u6BB5\u4E00\u4E00\u6467\u6B8B\u81F3\u6B7B\uFF0C\u76F4\u81F3\u5206\u5C38\u800C\u540E\u5FEB\u3002\u4F46\u4ED6\u53EF\u4E0D\u60F3\u8FD9\u4E48\u65E9\u5F04\u6B7B\u98CE\u665A\u534E\uFF0C\u90A3\u672A\u514D\u592A\u4FBF\u5B9C\u8FD9\u4E2A\u8D31\u4EBA\u3002</p><p>\u7B97\u7740\u5BAB\u4E3B\u5C06\u8981\u8FD4\u56DE\u661F\u6708\u6E56\uFF0C\u4ED6\u4FBF\u5306\u5FD9\u8D76\u56DE\uFF0C\u671F\u5F85\u5BAB\u4E3B\u5B9E\u73B0\u5F53\u521D\u7684\u627F\u8BFA\uFF0C\u8BA9\u81EA\u5DF1\u6062\u590D\u9633\u6839\u3002</p><p>\u4ED6\u5988\u7684\uFF01\u8001\u5B50\u975E\u7528\u81EA\u5DF1\u7684\u9E21\u5DF4\u628A\u4F60\u6D3B\u6D3B\u64CD\u6B7B\uFF01\u970D\u72C2\u7130\u624B\u81C2\u4E00\u9707\uFF0C\u98CE\u665A\u534E\u53EA\u89C9\u4E00\u6839\u94C1\u67F1\u76F4\u76F4\u6345\u5165\u8179\u5185\uFF0C\u51E0\u4E4E\u51FB\u788E\u4E86\u5B50\u5BAB\u3002</p><p>\u300C\u556A\u300D\u7684\u4E00\u58F0\uFF0C\u98CE\u665A\u534E\u98DE\u51FA\u4E08\u8BB8\uFF0C\u8F6F\u7EF5\u7EF5\u6389\u5728\u5730\u4E0A\u3002\u9C9C\u8840\u4ECE\u4FEE\u957F\u7684\u7389\u817F\u95F4\u6CC9\u6C34\u822C\u5954\u6D8C\u800C\u51FA\u3002</p><p>\u970D\u72C2\u7130\u62CE\u8D77\u5931\u53BB\u77E5\u89C9\u7684\u6D41\u971C\u5251\uFF0C\u50CF\u62FF\u7740\u4E00\u5757\u62B9\u5E03\u822C\uFF0C\u7528\u767D\u5AE9\u7684\u8089\u4F53\u64E6\u53BB\u81C2\u4E0A\u7684\u8840\u8FF9\u3002\u518D\u6709\u4E24\u5929\uFF0C\u5C31\u8BA9\u4F60\u5C1D\u5C1D\u970D\u7237\u9E21\u5DF4\u7684\u5389\u5BB3\uFF01</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u665A\u98CE\u62C2\u8FC7\uFF0C\u6B66\u51E4\u522B\u9662\u7684\u6A90\u89D2\u4F20\u6765\u8F7B\u60A6\u7684\u94C3\u58F0\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u970D\u7136\u7741\u5F00\u53CC\u76EE\uFF0C\u773C\u4E2D\u7CBE\u5149\u95EA\u52A8\uFF0C\u5B9B\u7136\u662F\u5F53\u65E5\u7EB5\u6A2A\u65E0\u654C\u7684\u8FEB\u4EBA\u6740\u6C14\u3002</p><p>\u9EC4\u8C46\u5927\u7684\u6C57\u73E0\u4ECE\u989D\u89D2\u6EDA\u843D\uFF0C\u96EA\u5CF0\u795E\u5C3C\u54AC\u7D27\u7259\u5173\uFF0C\u7AED\u529B\u805A\u8D77\u6563\u4E71\u7684\u771F\u6C14\uFF0C\u505A\u51FA\u6700\u540E\u4E00\u51FB\u3002</p><p>\u9AD8\u9686\u7684\u5C0F\u8179\u4E00\u9635\u62BD\u52A8\uFF0C\u5BC4\u5C45\u5176\u4E2D\u7684\u5F02\u7269\u53D7\u60CA\u822C\u7FFB\u6EDA\u4E0D\u5DF2\u3002\u4E00\u67F1\u9999\u7684\u5DE5\u592B\u540E\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E00\u58F0\u95F7\u54FC\uFF0C\u7389\u5BB9\u8840\u8272\u892A\u5C3D\uFF0C\u4E00\u7F15\u9C9C\u8840\u4ECE\u5634\u89D2\u6D41\u51FA\u3002\u63A5\u7740\u5C0F\u8179\u7A81\u7136\u505C\u6B62\u8815\u52A8\u3002</p><p>\u75BC\u75DB\u548C\u803B\u8FB1\u90FD\u5DF2\u9EBB\u6728\uFF0C\u5FC3\u5E95\u7684\u6068\u610F\u5374\u4E0E\u65E5\u4FF1\u589E\u3002\u88AB\u4EBA\u4EE5\u5351\u9119\u7684\u624B\u6BB5\u64D2\u4E0B\uFF0C\u8FC7\u7740\u6697\u65E0\u5929\u65E5\u7684\u65E5\u5B50\uFF0C\u8FDE\u8425\u5993\u90FD\u4E0D\u5982\u7684\u53EA\u9732\u51FA\u6027\u5668\uFF0C\u5668\u5177\u822C\u4EFB\u4EBA\u5978\u6DEB\u3002\u6BCF\u4E00\u6B21\u88AB\u4EBA\u8FDB\u5165\uFF0C\u5979\u90FD\u6068\u4E0D\u5F97\u5C06\u654C\u4EBA\u6D3B\u6D3B\u6495\u788E\u3002</p><p>\u540E\u6765\u53C8\u88AB\u4EBA\u5728\u5B50\u5BAB\u5185\u690D\u5165\u5F02\u7269\uFF0C\u62FF\u81EA\u5DF1\u7684\u8089\u4F53\u5F53\u4F5C\u5BC4\u4F53\u3002\u770B\u7740\u81EA\u5DF1\u7684\u5C0F\u8179\u4E00\u5929\u5929\u53D8\u5927\uFF0C\u5F02\u7269\u75AF\u72C2\u5730\u541E\u566C\u7740\u81EA\u5DF1\u8840\u8089\uFF0C\u96EA\u5CF0\u795E\u5C3C\u66F4\u662F\u6068\u5F97\u5FC3\u5934\u6EF4\u8840\u3002</p><p>\u4E00\u65E6\u80FD\u751F\u79BB\u6B64\u5904\uFF0C\u6211\u4E00\u5B9A\u5C06\u4F60\u4EEC\u4E00\u4E2A\u4E2A\u788E\u5C38\u4E07\u6BB5\uFF01</p><p>\u53EF\u96EA\u5CF0\u795E\u5C3C\u77E5\u9053\uFF0C\u81EA\u5DF1\u751F\u79BB\u7684\u673A\u4F1A\u6BD4\u53D1\u751F\u5947\u8FF9\u8FD8\u4F4E\u3002</p><p>\u7ECF\u53F6\u884C\u5357\u5999\u624B\u65BD\u6CBB\uFF0C\u6298\u65AD\u7684\u624B\u811A\u8FD8\u80FD\u4FDD\u6301\u539F\u72B6\uFF0C\u4F46\u518D\u4E5F\u65E0\u6CD5\u52A8\u4F5C\u3002\u5373\u4F7F\u624B\u811A\u5B89\u597D\uFF0C\u6311\u9732\u7684\u7B4B\u8171\u4E5F\u8BA9\u5979\u52A8\u5F39\u4E0D\u5F97\uFF0C\u5982\u679C\u5F3A\u884C\u6323\u624E\uFF0C\u7B4B\u8171\u7ACB\u65AD\u3002\u51B5\u4E14\u8FD8\u6709\u7CBE\u94A2\u6253\u5236\u7684\u94FE\u9501\u2026\u2026</p><p>\u5982\u679C\u53EA\u6709\u8FD9\u4E9B\uFF0C\u96EA\u5CF0\u795E\u5C3C\u8FD8\u6709\u610F\u505A\u6B8A\u6B7B\u4E4B\u4E89\u3002\u7136\u800C\u8FD8\u6709\u4E00\u9053\u5211\u5236\u3002\u80A9\u5934\u7684\u4F24\u53E3\u7652\u5408\u4E4B\u540E\uFF0C\u65E5\u6708\u94A9\u5DF2\u7ECF\u957F\u5728\u7435\u7436\u9AA8\u95F4\uFF0C\u5373\u4F7F\u5979\u5168\u76DB\u4E4B\u65E5\uFF0C\u4E5F\u4E0D\u53EF\u80FD\u518D\u7528\u5185\u529B\u903C\u51FA\u3002</p><p>\u5728\u65E5\u6708\u94A9\u5BD2\u70ED\u5404\u5F02\u7684\u6C14\u6D41\u51B2\u51FB\u4E0B\uFF0C\u771F\u6C14\u51E0\u4E4E\u65E0\u6CD5\u805A\u5165\u4E39\u7530\u3002\u4F46\u593A\u80CE\u82B1\u7684\u690D\u5165\u5374\u7ED9\u4E86\u795E\u5C3C\u4E00\u4E2A\u673A\u4F1A\u3002</p><p>\u593A\u80CE\u82B1\u662F\u661F\u6708\u6E56\u7ECF\u8FC7\u6570\u4EE3\u7814\u7A76\uFF0C\u4EE5\u6570\u5343\u4EBA\u7684\u751F\u547D\u4E3A\u4EE3\u4EF7\u521B\u51FA\u7684\u5947\u7269\uFF0C\u4E00\u65E6\u5236\u6210\uFF0C\u4E0D\u4EC5\u5728\u6CBB\u7597\u4F24\u52BF\u4E2D\u795E\u6548\u60CA\u4EBA\uFF0C\u53EF\u751F\u767D\u9AA8\u7EED\u6B8B\u547D\uFF0C\u66F4\u91CD\u8981\u7684\u662F\uFF1A\u53EF\u5438\u53D6\u5973\u5B50\u7684\u771F\u5143\uFF0C\u4F9B\u64AD\u79CD\u8005\u91C7\u670D\u3002</p><p>\u593A\u80CE\u82B1\u82B1\u79CD\u6E90\u81EA\u5929\u7AFA\u4E00\u79CD\u5F02\u83B2\uFF0C\u8FD9\u79CD\u83B2\u82B1\u751F\u547D\u529B\u6781\u5F3A\uFF0C\u5386\u7ECF\u5343\u5E74\u4ECD\u53EF\u53D1\u82BD\u3002</p><p>\u83B2\u5B50\u5148\u7ECF\u8FC7\u79D8\u65B9\u6CE1\u5236\uFF0C\u6539\u9020\u5176\u6210\u5206\uFF0C\u690D\u5165\u5973\u4F53\u4E4B\u540E\u4FBF\u4F1A\u76F4\u63A5\u4ECE\u83B2\u5B50\u5F00\u82B1\u3002\u5B83\u4EE5\u7CBE\u6DB2\u4E3A\u98DF\uFF0C\u4E0E\u5BC4\u4E3B\u7684\u8840\u8089\u76F8\u8FDE\uFF0C\u6BCF\u4E00\u663C\u591C\u5F00\u5408\u4E00\u6B21\uFF0C\u65E5\u843D\u800C\u653E\uFF0C\u65E5\u51FA\u800C\u5408\uFF0C\u5728\u751F\u957F\u4E2D\u4E0D\u65AD\u5438\u53D6\u5973\u4F53\u7684\u7CBE\u5143\uFF0C\u6700\u7EC8\u805A\u96C6\u4E8E\u83B2\u84EC\u4E4B\u4E2D\u3002</p><p>\u4E5F\u6B63\u662F\u56E0\u6B64\uFF0C\u96EA\u5CF0\u795E\u5C3C\u624D\u80FD\u5728\u7435\u7436\u9AA8\u88AB\u7A7F\u7684\u60C5\u51B5\u4E0B\u5BF9\u5B83\u8FDB\u884C\u53CD\u51FB\u3002</p><p>\u593A\u80CE\u82B1\u7EFD\u5F00\u65F6\u4F1A\u5BF9\u4E39\u7530\u4EA7\u751F\u4E00\u80A1\u5438\u529B\uFF0C\u501F\u6B64\u5438\u529B\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5C06\u6563\u4E71\u7684\u771F\u6C14\u6C47\u5165\u4E39\u7530\uFF0C\u7136\u540E\u7528\u51E4\u51F0\u5B9D\u5178\u72EC\u7279\u7684\u884C\u529F\u65B9\u6CD5\u805A\u6C14\u6210\u8F6E\uFF0C\u4E0E\u593A\u80CE\u82B1\u76F8\u6297\u8861\u3002</p><p>\u867D\u7136\u65E0\u6CD5\u5F7B\u5E95\u51FB\u788E\u593A\u80CE\u82B1\uFF0C\u4F46\u96EA\u5CF0\u795E\u5C3C\u9ED8\u8FD0\u529F\u529B\uFF0C\u4E94\u4E2A\u6708\u6765\u53EA\u635F\u5931\u4E86\u4E00\u6210\u5DE6\u53F3\u7684\u771F\u5143\u3002\u80FD\u6709\u8FD9\u6837\u7684\u6210\u7EE9\uFF0C\u9664\u4E86\u51E4\u51F0\u5B9D\u5178\u672C\u8EAB\u7684\u5947\u5F02\u4E4B\u5916\uFF0C\u6700\u96BE\u5F97\u7684\u662F\u795E\u5C3C\u7684\u6BC5\u529B\u3002\u82E5\u6362\u505A\u5176\u4ED6\u5973\u5B50\uFF0C\u9971\u53D7\u6298\u78E8\u4E4B\u540E\u53EA\u60F3\u4EE5\u6B7B\u89E3\u8131\uFF0C\u53EA\u4F1A\u653E\u4EFB\u593A\u80CE\u82B1\u5438\u5C3D\u81EA\u5DF1\u7684\u771F\u5143\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u54BD\u4E0B\u4E00\u53E3\u9C9C\u8840\uFF0C\u773C\u4E2D\u7684\u7CBE\u5149\u4F9D\u65E7\uFF0C\u5FC3\u5E95\u5374\u5728\u53D1\u7D27\u3002\u81EA\u5DF1\u8FD8\u80FD\u6491\u591A\u4E45\u5462\uFF1F</p><p>\u593A\u80CE\u82B1\u6210\u719F\u5728\u9645\uFF0C\u660E\u65E5\u5C31\u8981\u5206\u5A29\u3002\u8FD9\u4E00\u5929\u4E5F\u662F\u96EA\u5CF0\u795E\u5C3C\u4E94\u4E2A\u6708\u6765\u552F\u4E00\u6CA1\u6709\u88AB\u4EBA\u5978\u6DEB\u7684\u4E00\u5929\u3002\u5979\u5C48\u8FB1\u5730\u8EBA\u5728\u94C1\u67B6\u4E0A\uFF0C\u80A1\u95F4\u5931\u53BB\u5305\u76AE\u7684\u82B1\u8482\u8D64\u88F8\u88F8\u7FD8\u5728\u8089\u82B1\u4E2D\uFF0C\u9690\u9690\u95EA\u52A8\u7740\u5996\u5F02\u7684\u8273\u7EA2\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u9EBB\u836F\u836F\u6548\u892A\u53BB\uFF0C\u7D2B\u73AB\u5589\u5934\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u53D1\u51FA\u4E00\u58F0\u4F4E\u5A49\u7684\u547B\u541F\u3002\u539F\u6765\u5BBD\u677E\u7684\u4EB5\u8863\u5DF2\u7ECF\u65E0\u6CD5\u518D\u7A7F\uFF0C\u4E0A\u4F53\u53EA\u76D6\u7740\u4E00\u6761\u8584\u8584\u7684\u5E8A\u5355\u3002\u6D01\u767D\u7684\u7EC6\u7EE2\u4E0B\uFF0C\u9690\u9690\u7EA6\u7EA6\u663E\u51FA\u4E24\u56E2\u5C3A\u5BF8\u60CA\u4EBA\u7684\u8089\u7403\u3002\u5979\u6323\u624E\u7740\u60F3\u62AC\u8D77\u8EAB\u5B50\uFF0C\u53C8\u88AB\u80F8\u524D\u7684\u91CD\u91CF\u62D6\u4F4F\uFF0C\u7CBE\u75B2\u529B\u5C3D\u5730\u5012\u4E86\u4E0B\u53BB\uFF0C\u6CEA\u6C34\u7F13\u7F13\u6D41\u8FC7\u7389\u988A\u3002</p><p>\u53F6\u884C\u5357\u6C89\u7740\u8138\u5750\u5728\u4E00\u65C1\uFF0C\u4ED4\u7EC6\u5207\u7740\u8109\u8C61\u3002\u826F\u4E45\uFF0C\u4ED6\u4F4E\u53F9\u4E00\u58F0\uFF0C\u300C\u5C11\u592B\u4EBA\u8D44\u8D28\u6781\u597D\uFF0C\u6BCD\u5B50\u65E0\u6059\u3002\u300D</p><p>\u7D2B\u73AB\u54AC\u7740\u7EA2\u5507\uFF0C\u7CBE\u81F4\u7684\u7389\u8138\u4E0A\u6EE1\u662F\u4F24\u75DB\uFF0C\u62BD\u54BD\u9053\uFF1A\u300C\u6211\u2026\u2026\u6211\u53D8\u6210\u602A\u7269\u4E86\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u6D3B\u4E86\u516D\u5341\u591A\u5E74\uFF0C\u4ECE\u6765\u6CA1\u54C4\u8FC7\u5973\u4EBA\uFF0C\u6B64\u65F6\u62FF\u634F\u7740\u67D4\u58F0\u5B89\u6170\u9053\uFF1A\u300C\u6CA1\u4E8B\u6CA1\u4E8B\uFF0C\u53EA\u662F\u5927\u4E86\u4E00\u70B9\u7F62\u4E86\u2026\u2026\u8FD9\u6837\u770B\u7740\u4E5F\u5F88\u6F02\u4EAE\u2026\u2026\u300D</p><p>\u300C\u54FC\uFF01\u4F60\u6562\u5BF9\u6211\u7684\u8EAB\u5B50\u6307\u6307\u70B9\u70B9\uFF01\u7F8E\u4E0D\u7F8E\u5173\u4F60\u5C41\u4E8B\uFF01\u300D\u7D2B\u73AB\u706B\u6C14\u6781\u5927\uFF0C\u8981\u4E0D\u662F\u8EAB\u4F53\u4E4F\u529B\uFF0C\u65E9\u8D77\u8EAB\u628A\u53F6\u884C\u5357\u9A82\u4E2A\u72D7\u8840\u55B7\u5934\u3002</p><p>\u53F6\u884C\u5357\u7B11\u5475\u5475\u9053\uFF1A\u300C\u662F\u662F\uFF0C\u8001\u592B\u5B5F\u6D6A\u4E86\u2026\u2026\u4F46\u5BAB\u4E3B\u521A\u624D\u770B\u4E86\u4E5F\u5F88\u6EE1\u610F\u5462\u2026\u300D</p><p>\u300C\u5478\uFF01\u90A3\u4E2A\u738B\u516B\u86CB\uFF01\u300D\u7D2B\u73AB\u6068\u6068\u4E00\u6376\u77F3\u6848\uFF0C\u7275\u52A8\u4E86\u4E73\u623F\u7684\u5F02\u72B6\uFF0C\u4E0D\u7531\u5F97\u300C\u54CE\u54DF\u300D\u75DB\u53EB\u4E00\u58F0\uFF0C\u542B\u6CEA\u9053\uFF1A\u300C\u6211\u90FD\u75BC\u6B7B\u4E86\uFF0C\u4ED6\u8FD8\u4E71\u634F\uFF01\u300D</p><p>\u53F6\u884C\u5357\u4E00\u8FB9\u8FD0\u529F\u5E2E\u5979\u63A8\u8840\u8FC7\u5BAB\uFF0C\u7597\u4F24\u6B62\u75DB\uFF0C\u4E00\u8FB9\u548C\u6C14\u5730\u8BF4\uFF1A\u300C\u522B\u6015\uFF0C\u660E\u5929\u5C31\u4E0D\u75BC\u4E86\u3002\u4EE5\u540E\u53EA\u8981\u6BCF\u5929\u62B9\u836F\u2026\u2026\u300D</p><p>\u300C\u4EC0\u4E48\uFF1F\u6BCF\u5929\u62B9\u836F\uFF1F\u4E3A\u4EC0\u4E48\uFF1F\u300D\u5C11\u5973\u5BDF\u89C9\u5230\u5F02\u5E38\uFF0C\u4E0D\u7981\u9AD8\u58F0\u5C16\u53EB\u3002</p><p>\u53F6\u884C\u5357\u5C34\u5C2C\u5730\u6536\u56DE\u624B\uFF0C\u300C\u8FD9\u4E2A\u2026\u2026\u65F6\u95F4\u592A\u7D27\uFF0C\u8001\u592B\u6CA1\u80FD\u53C2\u900F\u836F\u65B9\u7684\u5965\u5999\u2026\u300D</p><p>\u300C\u6CA1\u53C2\u900F\u4F60\u5C31\u62FF\u6211\u505A\u8BD5\u9A8C\uFF01\u300D\u7D2B\u73AB\u7F8E\u76EE\u77AA\u5F97\u8001\u5927\u3002</p><p>\u300C\u5C5E\u4E0B\u4E5F\u4E0D\u613F\u610F\u2026\u2026\u4F46\u5BAB\u4E3B\u2026\u2026\u300D</p><p>\u300C\u5478\uFF01\u4E0D\u8BB8\u63D0\u4ED6\uFF01\u8BF4\uFF01\u4E3A\u4EC0\u4E48\u8FD8\u8981\u62B9\u836F\uFF01\u300D</p><p>\u300C\u55EF\uFF0C\u662F\u8FD9\u6837\u7684\uFF1A\u6B64\u836F\u4ECE\u624B\u5C11\u9633\u7ECF\u5165\u624B\uFF0C\u523A\u6FC0\u4E09\u7126\uFF0C\u80FD\u4F7F\u4E73\u8089\u6025\u5267\u589E\u957F\u3002\u4F46\u539F\u836F\u65B9\u7528\u7684\u82CD\u672F\u4E00\u5473\uFF0C\u4F1A\u4F24\u53CA\u4E73\u817A\uFF0C\u56E0\u6B64\u8001\u592B\u6539\u7528\u94C5\u6C34\u6240\u70BC\u7684\u9EC4\u82BD\u66FF\u4EE3\uFF0C\u4E0D\u788D\u751F\u673A\u2026\u2026\u300D</p><p>\u300C\u5C11\u5E9F\u8BDD\uFF01\u300D\u7D2B\u73AB\u58F0\u8272\u4FF1\u5389\u3002</p><p>\u53F6\u884C\u5357\u8E0C\u8E87\u7247\u523B\uFF0C\u9053\u51FA\u539F\u59D4\u3002</p><p>\u4FEE\u6539\u836F\u65B9\u4E4B\u540E\u867D\u7136\u4E0D\u5F71\u54CD\u6CCC\u4E73\uFF0C\u4F46\u5982\u679C\u4E0D\u4F7F\u7528\u8F85\u52A9\u836F\u7269\uFF0C\u4E73\u623F\u5C31\u4F1A\u840E\u7F29\uFF0C\u751A\u81F3\u4F1A\u6C14\u8840\u9006\u8F6C\uFF0C\u5371\u53CA\u751F\u547D\u3002\u73B0\u5728\u7D2B\u73AB\u4F7F\u4E0D\u51FA\u5185\u529F\uFF0C\u65E0\u6CD5\u7528\u771F\u6C14\u8C03\u7406\u8840\u8109\uFF0C\u963B\u6B62\u4E73\u623F\u53D8\u5F02\uFF0C\u53EA\u6709\u7528\u836F\u7269\u6765\u9632\u8303\u3002</p><p>\u7D2B\u73AB\u542C\u7F62\u51DD\u795E\u601D\u7D22\u7247\u523B\u3002\u4E0E\u53F6\u884C\u5357\u6253\u4E86\u8FD9\u4E48\u4E45\u4EA4\u9053\uFF0C\u7ECF\u8FC7\u6570\u6B21\u8840\u6CEA\u6559\u8BAD\uFF0C\u5BF9\u4ED6\u8BF4\u8BDD\u7684\u65B9\u5F0F\u65E9\u5DF2\u5FC3\u91CC\u6709\u6570\u3002\u5982\u679C\u771F\u8FD9\u4E48\u7B80\u5355\uFF0C\u4ED6\u4E5F\u4E0D\u4F1A\u541E\u541E\u5410\u5410\uFF0C\u4FBF\u79D8\u4E00\u6837\u6B32\u8A00\u53C8\u6B62\uFF0C\u8FD9\u8001\u5BB6\u4F19\u80AF\u5B9A\u8FD8\u6709\u8BDD\u6CA1\u8BF4\u3002</p><p>\u7D2B\u73AB\u7F8E\u76EE\u4E00\u77AC\uFF0C\u51B7\u51B0\u51B0\u76EF\u7740\u53F6\u884C\u5357\uFF0C\u300C\u59D3\u53F6\u7684\uFF0C\u660E\u8BF4\u5427\uFF0C\u8FD8\u6709\u4EC0\u4E48\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u6014\u4E86\u534A\u664C\uFF0C\u82E6\u7B11\u9053\uFF1A\u300C\u662F\u8FD8\u6709\u4E00\u70B9\u2026\u2026\u5C11\u592B\u4EBA\u5343\u4E07\u4E0D\u8981\u614C\u5F20\uFF0C\u8001\u592B\u4E00\u5B9A\u4F1A\u627E\u529E\u6CD5\u89E3\u51B3\u2026\u2026\u300D\u4ED6\u770B\u4E86\u54AC\u7259\u5207\u9F7F\u7684\u7D2B\u73AB\u4E00\u773C\uFF0C\u8214\u4E86\u8214\u53D1\u5E72\u7684\u5634\u5507\uFF0C\u6162\u6162\u9053\uFF1A\u300C\u65E0\u8BBA\u662F\u836F\u7269\u8F85\u52A9\uFF0C\u8FD8\u662F\u4EE5\u771F\u6C14\u8C03\u7406\uFF0C\u8FD9\u4E2A\u2026\u2026\u5C11\u592B\u4EBA\u7684\u2026\u2026\u90FD\u4F1A\u4E00\u76F4\u957F\u4E0B\u53BB\u2026\u2026\u300D</p><p>\u300C\u554A\uFF01\u300D\u7D2B\u73AB\u4E00\u58F0\u5C16\u53EB\uFF0C\u4E00\u628A\u63EA\u4F4F\u53F6\u884C\u5357\u7684\u80E1\u5B50\uFF0C\u7528\u9707\u7834\u8033\u819C\u7684\u97F3\u91CF\u53EB\u9053\uFF1A\u300C\u8FD8\u4F1A\u957F\uFF01\uFF1F\u300D</p><p>\u53F6\u884C\u5357\u75BC\u5F97\u76F4\u54A7\u5634\uFF0C\u300C\u5C11\u592B\u4EBA\u5207\u83AB\u52A8\u6C14\uFF0C\u5C5E\u4E0B\u672C\u60F3\u8C03\u597D\u836F\u65B9\u518D\u8BD5\uFF0C\u4F46\u5BAB\u4E3B\u6267\u610F\u5982\u6B64\u2026\u2026\u4F60\u653E\u5FC3\u4F60\u653E\u5FC3\uFF0C\u5B83\u957F\u5F97\u5F88\u6162\u2026\u2026\u4E00\u5E74\u5185\u6211\u80AF\u5B9A\u627E\u51FA\u89E3\u836F\uFF01\u300D</p><p>\u300C\u6709\u591A\u6162\uFF01\u300D\u7D2B\u73AB\u6B7B\u6D3B\u90FD\u8981\u95EE\u4E2A\u660E\u767D\uFF0C\u5982\u679C\u6BCF\u5929\u957F\u4E00\u65A4\uFF0C\u660E\u5929\u5C31\u53EF\u81EA\u6740\u4E86\u3002</p><p>\u300C\u4F9D\u5C11\u592B\u4EBA\u7684\u8EAB\u4F53\u8D44\u8D28\uFF0C\u4E00\u5E74\u6700\u591A\u53EA\u957F\u51FA\u5C11\u592B\u4EBA\u624B\u90A3\u4E48\u5927\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u4E3E\u8D77\u81EA\u5DF1\u7684\u7EA4\u624B\u6BD4\u91CF\u4E86\u4E00\u4E0B\uFF0C\u611F\u89C9\u8FD8\u53EF\u4EE5\u63A5\u53D7\uFF0C\u63E1\u62F3\u6253\u5728\u53F6\u884C\u5357\u8138\u4E0A\uFF0C\u559D\u9053\uFF1A\u300C\u534A\u5E74\u5185\u7ED9\u6211\u627E\u51FA\u89E3\u836F\uFF01\u300D</p><p>\u53F6\u884C\u5357\u8FDE\u58F0\u5E94\u662F\uFF0C\u5FC3\u91CC\u5374\u6697\u6697\u9053\uFF1A\u300C\u63E1\u6210\u62F3\u5934\u5C31\u5BF9\u4E86\u2026\u2026\u300D</p><p>\u6323\u52A8\u95F4\uFF0C\u5E8A\u5355\u5782\u4E0B\u4E00\u89D2\uFF0C\u4E00\u53EA\u6D51\u5706\u7684\u8089\u7403\u9690\u9690\u9732\u51FA\u4E00\u62B9\u96EA\u767D\uFF0C\u5927\u5C0F\u8DB3\u6709\u539F\u6765\u4E24\u500D\u3002</p>',63);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
