import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_106" tabindex="-1"><a class="header-anchor" href="#_106" aria-hidden="true">#</a> 106</h1><p>\u300C\u2026\u2026\u5B8B\u56FD\u5883\u5185\u60C5\u51B5\u5C31\u662F\u8FD9\u6837\u3002\u300D\u970D\u72C2\u7130\u8FD9\u4F1A\u513F\u66B4\u623E\u4E4B\u8272\u5C3D\u53BB\uFF0C\u751A\u81F3\u6709\u4E9B\u8BDA\u60F6\u8BDA\u6050\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u7F6E\u53EF\u5426\uFF0C\u628A\u73A9\u7740\u4E03\u5B9D\u67C4\u6DE1\u6DE1\u9053\uFF1A\u300C\u5927\u5B5A\u7075\u9E6B\u5BFA\u60C5\u5F62\u5982\u4F55\uFF1F\u300D</p><p>\u300C\u5706\u76F8\u56DE\u5BFA\u540E\u4FBF\u95ED\u5173\u53C2\u7985\uFF0C\u5706\u5149\u3001\u5706\u6F84\u548C\u5C1A\u90FD\u6CA1\u6709\u52A8\u9759\uFF0C\u770B\u6765\u662F\u4E0D\u518D\u8FFD\u7A76\u5706\u901A\u7684\u6B7B\u56E0\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u9ED8\u7B97\u534A\u664C\uFF0C\u5706\u76F8\u95ED\u5173\u4E4B\u4E8B\u5927\u6709\u5F02\u5904\uFF0C\u83AB\u975E\u662F\u53D1\u73B0\u4E86\u4F17\u4EBA\u7684\u5C38\u4F53\uFF1F</p><p>\u970D\u72C2\u7130\u5927\u7740\u80C6\u5B50\u8BF4\u9053\uFF1A\u300C\u5BAB\u4E3B\uFF0C\u5C5E\u4E0B\u7684\u4F24\u52BF\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u9053\uFF1A\u300C\u5BAB\u4E3B\u65E9\u5DF2\u547D\u8001\u592B\u7ED9\u4F60\u6CBB\u4F24\uFF0C\u5E78\u597D\u65E5\u524D\u5D11\u4ED1\u5C71\u9001\u6765\u6700\u540E\u4E00\u5473\u836F\uFF0C\u4E09\u5929\u4E4B\u5185\u5373\u53EF\u914D\u597D\u3002\u300D</p><p>\u970D\u72C2\u7130\u5927\u559C\u8FC7\u671B\uFF0C\u5F53\u4E0B\u5343\u6069\u4E07\u8C22\u3002</p><p>\u970D\u72C2\u7130\u5750\u5230\u4E00\u65C1\uFF0C\u7075\u7389\u4ECE\u8896\u91CC\u6478\u51FA\u4E00\u5C01\u4E66\u4FE1\uFF0C\u300C\u91D1\u957F\u8001\u98DE\u9E3D\u4F20\u4E66\uFF1A\u8D6B\u8FDE\u96C4\u4E0E\u96C1\u95E8\u4E09\u5947\u5DF2\u81F3\u9F99\u57CE\uFF0C\u5E26\u53BB\u4E24\u5343\u5339\u826F\u9A79\uFF1B\u77F3\u880D\u7559\u5B88\uFF1B\u5B89\u5B50\u5B8F\u8FD4\u56DE\u795E\u6559\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u8349\u8349\u4E00\u770B\uFF0C\u9012\u7ED9\u6C90\u58F0\u4F20\u3002\u540E\u8005\u6162\u541E\u541E\u770B\u5B8C\uFF0C\u8BF4\u9053\uFF1A\u300C\u4EE5\u5C5E\u4E0B\u4E4B\u89C1\uFF0C\u5E94\u5373\u523B\u4ECE\u4E94\u884C\u95E8\u5404\u9009\u4EBA\u9A6C\u8D76\u8D74\u9F99\u57CE\uFF0C\u7531\u91D1\u957F\u8001\u7EDF\u4E00\u8C03\u5EA6\u3002\u8BF7\u5BAB\u4E3B\u5B9A\u593A\u3002\u300D</p><p>\u300C\u907F\u5F00\u6E14\u9633\uFF0C\u4ECE\u6D77\u8DEF\u5317\u4E0A\u3002\u300D\u6155\u5BB9\u9F99\u4E0D\u52A0\u601D\u7D22\u5730\u7B54\u9053\u3002</p><p>\u5C60\u6000\u6C89\u5E94\u58F0\u9053\uFF1A\u300C\u6628\u65E5\u4F20\u6765\u6D88\u606F\uFF0C\u5A01\u8FDC\u5821\u5DF2\u88AB\u571F\u5802\u6536\u5F52\u795E\u6559\uFF0C\u5C31\u7531\u4E1C\u83B1\u5165\u6D77\u5982\u4F55\uFF1F\u300D</p><p>\u300C\u597D\u3002\u300D\u6155\u5BB9\u9F99\u957F\u8EAB\u800C\u8D77\uFF0C\u300C\u672C\u5BAB\u660E\u65E5\u95ED\u5173\u4FEE\u70BC\u8FD8\u5929\u8BC0\uFF0C\u6559\u4E2D\u8BF8\u4E8B\u7531\u6C90\u62A4\u6CD5\u7EDF\u7B79\u7B56\u5212\u3002\u534A\u5E74\u4E4B\u540E\uFF0C\u6211\u8981\u5728\u9F99\u57CE\u770B\u5230\u4E00\u652F\u4E94\u5343\u4EBA\u7684\u7CBE\u9A91\u3002\u300D</p><p>\u4F17\u4EBA\u8F70\u7136\u5E94\u8BFA\uFF0C\u4E00\u4E00\u544A\u9000\u3002</p><p>\u300C\u5BAB\u4E3B\u3002\u300D\u6BBF\u5185\u53EA\u5269\u53F6\u884C\u5357\u4E00\u4EBA\uFF0C\u300C\u593A\u80CE\u82B1\u5DF2\u7ECF\u5927\u529F\u544A\u6210\uFF0C\u53EF\u4EE5\u4F7F\u7528\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6C89\u9ED8\u7247\u523B\uFF0C\u6709\u4E9B\u62FF\u4E0D\u51C6\u5730\u8BF4\uFF1A\u300C\u5047\u5982\u90A3\u8D31\u5C3C\u7EC3\u7684\u771F\u662F\u51E4\u51F0\u5B9D\u5178\uFF0C\u4F1A\u4E0D\u4F1A\u5BF9\u592A\u4E00\u7ECF\u6709\u5BB3\uFF1F\u300D</p><p>\u7EC3\u8FC7\u51E4\u51F0\u5B9D\u5178\u7684\u5C11\u4E4B\u53C8\u5C11\uFF0C\u53F6\u884C\u5357\u4E5F\u65E0\u4ECE\u89E3\u7B54\uFF0C\u4F46\u661F\u6708\u6E56\u5386\u4EE3\u5BAB\u4E3B\u90FD\u53EA\u4FEE\u592A\u4E00\u7ECF\uFF0C\u800C\u5C06\u51E4\u51F0\u5B9D\u5178\u91CD\u91CD\u5C01\u9501\uFF0C\u5176\u4E2D\u5FC5\u6709\u7F18\u6545\u2026\u2026</p><p>\u53F6\u884C\u5357\u659F\u914C\u7740\u9053\uFF1A\u300C\u5BAB\u4E3B\u6240\u7591\u6709\u7406\u3002\u5C5E\u4E0B\u591A\u6B21\u63A2\u7A76\u96EA\u5CF0\u884C\u529F\u4E4B\u6CD5\uFF0C\u786E\u5B9E\u4E0E\u592A\u4E00\u7ECF\u80CC\u9053\u800C\u9A70\uFF0C\u6C34\u706B\u96BE\u5BB9\u3002\u4F46\u8FD9\u53EA\u662F\u884C\u529F\u76F8\u5F02\uFF0C\u771F\u5143\u672C\u8D28\u5E76\u65E0\u533A\u522B\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u4E00\u7B11\uFF0C\u300C\u8BF7\u5C11\u592B\u4EBA\u4E00\u540C\u53BB\u5427\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6628\u591C\u53F6\u884C\u5357\u8FDE\u54C4\u5E26\u529D\uFF0C\u6700\u540E\u53C8\u7528\u4E86\u5B89\u795E\u6563\uFF0C\u603B\u7B97\u8BA9\u7D2B\u73AB\u5B89\u5B9A\u4E0B\u6765\u3002</p><p>\u4E00\u89C9\u9192\u6765\uFF0C\u5B89\u795E\u6563\u7684\u836F\u6548\u8FD8\u672A\u892A\u5C3D\u3002\u7D2B\u73AB\u6014\u6014\u8EBA\u5728\u69BB\u4E0A\uFF0C\u773C\u4E2D\u53C8\u662F\u8FF7\u8499\u53C8\u662F\u4E0D\u89E3\u3002</p><p>\u5446\u4E86\u534A\u664C\uFF0C\u5979\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u62C9\u8D77\u67D4\u6BEF\uFF0C\u98DE\u5FEB\u5730\u770B\u4E86\u4E00\u773C\uFF0C\u7F8E\u76EE\u987F\u65F6\u77AA\u5F97\u6D51\u5706\uFF0C\u8FDE\u5FD9\u63A9\u4F4F\u80F8\u4E73\uFF0C\u5FC3\u91CC\u546F\u546F\u76F4\u8DF3\u3002</p><p>\u90A3\u4E2A\u5669\u68A6\u7ADF\u7136\u662F\u771F\u7684\u2026\u2026\u800C\u4E14\u770B\u8D77\u6765\u6BD4\u68A6\u91CC\u7684\u8FD8\u8981\u5927\u2026\u2026\u7D2B\u73AB\u5C0F\u5634\u4E00\u6241\uFF0C\u545C\u545C\u54ED\u4E86\u8D77\u6765\u3002</p><p>\u53F6\u884C\u5357\u63A8\u95E8\u800C\u5165\uFF0C\u770B\u5230\u54ED\u5F97\u68A8\u82B1\u5E26\u96E8\u7684\u5C11\u5973\u4E0D\u7531\u5FC3\u4E0B\u559F\u53F9\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u5C11\u592B\u4EBA\u3002\u300D</p><p>\u7D2B\u73AB\u626C\u8D77\u6EE1\u662F\u73E0\u6CEA\u7684\u4FCF\u8138\uFF0C\u51C4\u51C4\u5207\u5207\u53EB\u4E86\u58F0\u300C\u53F6\u4F2F\u4F2F\u2026\u2026\u300D\u63A5\u7740\u6251\u5230\u4ED6\u6000\u91CC\u653E\u58F0\u75DB\u54ED\uFF0C\u300C\u600E\u4E48\u8FD9\u4E2A\u6837\u5B50\u2026\u2026\u6211\u2026\u2026\u6211\u4E0D\u6D3B\u4E86\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u77E5\u9053\u662F\u836F\u6548\u5728\u53D1\u4F5C\uFF0C\u4E00\u591C\u95F4\u4E73\u623F\u53C8\u6DA8\u5927\u8BB8\u591A\uFF0C\u5F53\u4E0B\u5B89\u6170\u9053\uFF1A\u300C\u522B\u6015\u522B\u6015\uFF0C\u5DF2\u7ECF\u7A33\u5B9A\u4E86\u7A33\u5B9A\u4E86\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u53EA\u662F\u4E00\u4E2A\u52B2\u513F\u7684\u75DB\u54ED\uFF0C\u53F6\u884C\u5357\u53EA\u597D\u5C94\u5F00\u8BDD\u9898\uFF1A\u300C\u4EE4\u5E08\u96EA\u5CF0\u5E08\u592A\u2026\u2026\u300D</p><p>\u54ED\u58F0\u987F\u6B62\uFF0C\u7D2B\u73AB\u8B66\u89C9\u5730\u62AC\u8D77\u5934\u3002</p><p>\u53F6\u884C\u5357\u677E\u4E86\u53E3\u6C14\uFF0C\u8BF4\u9053\uFF1A\u300C\u5BAB\u4E3B\u547D\u5C5E\u4E0B\u8BF7\u5C11\u592B\u4EBA\u53BB\u6B66\u51E4\u522B\u9662\u3002\u300D</p><p>\u300C\u5E72\u561B\uFF1F\u300D</p><p>\u300C\u90A3\u4E2A\u8D31\u4EBA\u8981\u4E0B\u79CD\u4E86\uFF0C\u300D\u4E00\u4E2A\u4EBA\u5FEB\u6B65\u5165\u5BA4\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u8BA9\u4F60\u53BB\u770B\u770B\u5973\u4EBA\u600E\u4E48\u751F\u5B69\u5B50\u3002\u300D</p><p>\u300C\u6155\u5BB9\u9F99\uFF01\u300D\u65B0\u4EC7\u65E7\u6068\u6D8C\u4E0A\u5FC3\u5934\uFF0C\u7D2B\u73AB\u6012\u9A82\u9053\uFF1A\u300C\u4F60\u8FD9\u4E2A\u6DF7\u86CB\uFF01\u751F\u4E2A\u5B69\u5B50\u6CA1\u5C41\u773C\u513F\uFF01\u300D</p><p>\u300C\u54E6\uFF1F\u54EA\u4E2A\u5B69\u5B50\uFF1F\u4F60\u809A\u5B50\u91CC\u7684\uFF0C\u8FD8\u662F\u5A18\u809A\u5B50\u91CC\u7684\uFF1F\u300D</p><p>\u7D2B\u73AB\u54D1\u53E3\u65E0\u8A00\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u7B11\u4E00\u58F0\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u8D70\u3002\u300D</p><p>\u300C\u6211\u4E0D\u53BB\u3002\u300D\u7D2B\u73AB\u65A9\u9489\u622A\u94C1\u5730\u8BF4\u3002</p><p>\u53F6\u884C\u5357\u6015\u5BAB\u4E3B\u53D1\u6012\uFF0C\u8FDE\u5FD9\u529D\u9053\uFF1A\u300C\u53BB\u5427\u53BB\u5427\u3002\u300D</p><p>\u300C\u6211\u2026\u2026\u300D\u59D4\u5C48\u7684\u6CEA\u6C34\u7EB7\u7136\u800C\u843D\uFF0C\u7D2B\u73AB\u6CE3\u9053\uFF1A\u300C\u8FD9\u4E2A\u6837\u5B50\uFF0C\u6211\u8FD8\u600E\u4E48\u89C1\u4EBA\u5462\u2026\u2026\u300D</p><p>\u4E00\u591C\u4E4B\u95F4\uFF0C\u73B2\u73D1\u7684\u7389\u4E73\u7A81\u7136\u53D8\u6210\u4E00\u5BF9\u5C0F\u897F\u74DC\u822C\u7684\u8089\u5F39\uFF0C\u5355\u60F3\u60F3\u522B\u4EBA\u60CA\u8BE7\u7684\u76EE\u5149\uFF0C\u7D2B\u73AB\u5C31\u60F3\u4E00\u5934\u78B0\u6B7B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4ECA\u65E5\u593A\u80CE\u82B1\u4E00\u53CD\u5E38\u89C4\uFF0C\u4ECE\u9ECE\u660E\u8D77\u5C31\u6781\u529B\u6536\u7F29\u81A8\u80C0\uFF0C\u50CF\u662F\u8981\u7834\u4F53\u800C\u51FA\u4E00\u822C\u5728\u4F53\u5185\u4E0D\u4F4F\u52A8\u4F5C\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u9762\u5982\u91D1\u7EB8\uFF0C\u7AED\u529B\u4E0E\u593A\u80CE\u82B1\u7684\u5438\u529B\u76F8\u6297\u3002\u600E\u5948\u593A\u80CE\u82B1\u65E0\u4F11\u65E0\u6B62\uFF0C\u4E00\u76F4\u7EA0\u7F20\u5230\u5348\u672B\u65F6\u5206\uFF0C\u623F\u95E8\u7A81\u7136\u4E00\u54CD\uFF0C\u8D70\u8FDB\u6765\u51E0\u6761\u4EBA\u5F71\u3002</p><p>\u5176\u4E2D\u4E00\u4EBA\u4E00\u5F39\u94DC\u7F38\uFF0C\u5728\u6D51\u539A\u7684\u91D1\u94C1\u58F0\u4E2D\u6717\u7136\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u597D\u751F\u5356\u529B\uFF0C\u4E94\u4E2A\u6708\u7ADF\u80FD\u63A5\u5230\u8FD9\u4E48\u591A\u8D35\u5BA2\u3002\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u7389\u4F53\u4E00\u7D27\uFF0C\u7259\u5173\u54AC\u5F97\u683C\u683C\u4F5C\u54CD\u3002\u8FD9\u4E2A\u4EBA\u7684\u58F0\u97F3\u5BF9\u5979\u6765\u8BF4\u53EF\u8C13\u662F\u523B\u9AA8\u94ED\u5FC3\uFF0C\u7EB5\u7136\u7C89\u8EAB\u788E\u9AA8\u4E5F\u65E0\u6CD5\u5FD8\u8BB0\u3002</p><p>\u300C\u4E00\u6587\u4E00\u64CD\u2026\u2026\u7CDF\u7CD5\uFF0C\u672C\u5BAB\u5FD8\u4E86\u5E26\u94B1\u3002\u300D\u5E03\u5EC9\u5237\u5730\u62C9\u5F00\uFF0C\u523A\u76EE\u7684\u9633\u5149\u7ACB\u523B\u5145\u6EE1\u964B\u5BA4\u3002</p><p>\u6155\u5BB9\u9F99\u63A2\u5934\u9053\uFF1A\u300C\u5E08\u592A\u7684\u5904\u5B50\u4E4B\u8EAF\u8FD8\u662F\u5728\u4E0B\u7834\u7684\uFF0C\u4F5C\u4E3A\u5E08\u592A\u7684\u7B2C\u4E00\u4E2A\u7537\u4EBA\uFF0C\u8FD9\u6B21\u5C31\u514D\u8D39\u597D\u4E86\u3002\u300D</p><p>\u7D27\u95ED\u7684\u53CC\u773C\u731B\u7136\u7741\u5F00\uFF0C\u96EA\u5CF0\u795E\u5C3C\u5207\u9F7F\u9A82\u9053\uFF1A\u300C\u755C\u7272\uFF01\u6211\u96EA\u5CF0\u2026\u2026\u300D\u53EA\u8BF4\u4E86\u534A\u53E5\u5979\u4FBF\u50F5\u4F4F\u4E86\u3002</p><p>\u773C\u524D\u662F\u4E00\u5F20\u719F\u6089\u7684\u9762\u5B54\uFF0C\u5F2F\u7709\u79C0\u76EE\u4F9D\u7136\u5982\u5F80\u65E5\u822C\u660E\u5A9A\u52A8\u4EBA\uFF0C\u53EF\u8EAB\u4F53\u5374\u8FE5\u7136\u76F8\u5F02\u3002</p><p>\u300C\u73AB\u513F\u2026\u2026\u4F60\u6000\u4E86\u4ED6\u7684\u5B69\u5B50\uFF1F\u300D</p><p>\u88AB\u6155\u5BB9\u9F99\u62B1\u5728\u81C2\u95F4\u7684\u7D2B\u73AB\u7A74\u9053\u53D7\u5236\uFF0C\u65E0\u6CD5\u906E\u63A9\u81EA\u5DF1\u7684\u7A98\u6001\uFF0C\u53EA\u597D\u52C9\u5F3A\u70B9\u70B9\u4E86\u5934\u3002</p><p>\u300C\u4F60\u7684\u2026\u2026\u4F60\u7684\u8EAB\u5B50\u2026\u2026\u300D\u795E\u5C3C\u671B\u7740\u7231\u5F92\u80F8\u524D\u5F02\u4E4E\u5BFB\u5E38\u7684\u9AD8\u8038\uFF0C\u8138\u4E0A\u6EE1\u662F\u96BE\u4EE5\u7F6E\u4FE1\u7684\u60CA\u8BB6\u4E4B\u8272\u3002</p><p>\u8584\u8584\u7684\u7EF8\u886B\u6839\u672C\u65E0\u6CD5\u5BB9\u7EB3\u90A3\u4E24\u53EA\u7855\u5927\u7684\u8089\u7403\uFF0C\u8863\u895F\u53EA\u80FD\u52C9\u5F3A\u6263\u5728\u4E00\u8D77\uFF0C\u655E\u5F00\u7684\u8863\u7F1D\u4E2D\uFF0C\u9732\u51FA\u4E24\u534A\u96EA\u767D\u7684\u7403\u4F53\uFF0C\u4E2D\u95F4\u662F\u6DF1\u6DF1\u7684\u4E73\u6C9F\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u662F\u4E07\u91CC\u65E0\u4E00\u7684\u8C6A\u4E73\uFF0C\u4E0D\u77E5\u6BD4\u6211\u5BB6\u5A18\u5B50\u5982\u4F55\u2026\u2026\u300D\u8BF4\u7740\u6402\u4F4F\u7D2B\u73AB\u7684\u67D4\u80A9\u5411\u540E\u4E00\u63B0\uFF0C\u5C11\u5973\u5A07\u8EAF\u633A\u76F4\uFF0C\u6447\u6447\u6B32\u5760\u7684\u8774\u8776\u7EBD\u4E4D\u7136\u5206\u5F00\uFF0C\u4E24\u56E2\u96EA\u8089\u4E00\u8DC3\u800C\u51FA\uFF0C\u5B9B\u5982\u6D3B\u7269\u822C\u5728\u80F8\u524D\u8DF3\u52A8\u4E0D\u5DF2\u3002</p><p>\u7D2B\u73AB\u4F4E\u53EB\u4E00\u58F0\uFF0C\u6025\u5FD9\u4FA7\u8FC7\u8138\uFF0C\u4FCF\u8138\u901A\u7EA2\u3002</p><p>\u4E73\u6839\u4ECD\u662F\u539F\u6765\u7684\u7C97\u7EC6\uFF0C\u4E24\u624B\u6070\u6070\u4E00\u63E1\uFF0C\u4E73\u7403\u5374\u731B\u7136\u6DA8\u5927\u4E09\u500D\u6709\u4F59\uFF0C\u539F\u672C\u7CBE\u81F4\u7684\u4E73\u5CF0\u53D8\u5F97\u6D51\u5706\uFF0C\u4EFF\u4F5B\u4E24\u53EA\u719F\u900F\u7684\u5C0F\u897F\u74DC\u60AC\u5728\u80F8\u524D\u3002\u96EA\u4EAE\u7684\u808C\u80A4\u5BF8\u5BF8\u7EF7\u7D27\uFF0C\u4F3C\u4E4E\u8F7B\u8F7B\u4E00\u5F39\u5C31\u4F1A\u7206\u5F00\u3002</p><p>\u9886\u53E3\u548C\u8863\u6446\u7684\u7EBD\u6263\u4F9D\u7136\u5B8C\u597D\uFF0C\u5DE8\u4E73\u51ED\u7A7A\u751F\u51FA\u822C\u4ECE\u7D27\u5BC6\u7684\u8863\u895F\u4E2D\u6324\u51FA\uFF0C\u8FB9\u7F18\u5DF2\u7ECF\u8D85\u8FC7\u4E86\u8EAB\u4F53\u7684\u5BBD\u5EA6\u3002\u8DF3\u52A8\u4E2D\u4E73\u7403\u4ECD\u80FD\u4FDD\u6301\u633A\u62D4\u4E4B\u6001\uFF0C\u53EF\u89C1\u5B83\u7684\u5F39\u6027\u548C\u575A\u633A\u3002</p><p>\u4E73\u7403\u7684\u8DF3\u52A8\u6E10\u6E10\u9759\u6B62\uFF0C\u6155\u5BB9\u9F99\u7784\u4E00\u773C\u795E\u5C3C\u7684\u80A5\u4E73\uFF0C\u53C8\u770B\u4E00\u773C\u7D2B\u73AB\uFF0C\u6BD4\u8F83\u4E86\u534A\u664C\uFF0C\u300C\u770B\u8D77\u6765\u76F8\u5DEE\u65E0\u51E0\uFF0C\u60F3\u6BD4\u51FA\u4E2A\u80DC\u8D1F\u561B\u2026\u2026\u300D\u6700\u540E\u6258\u8D77\u5C11\u5973\u7684\u4E73\u7403\u8F7B\u8F7B\u4E00\u629B\uFF0C\u7B11\u9053\uFF1A\u300C\u53EA\u6709\u5272\u4E0B\u6765\u79F0\u79F0\u4E86\u3002\u300D</p><p>\u8C08\u7B11\u95F4\uFF0C\u795E\u5C3C\u8179\u7403\u4E00\u9635\u4E71\u6EDA\uFF0C\u5438\u5F15\u4E86\u4F17\u4EBA\u7684\u76EE\u5149\u3002</p><p>\u6155\u5BB9\u9F99\u653E\u5F00\u7D2B\u73AB\uFF0C\u4FEF\u8EAB\u89C2\u5BDF\u795E\u5C3C\u7684\u4EA7\u95E8\u3002</p><p>\u5145\u8840\u7684\u8089\u82B1\u6108\u53D1\u80BF\u80C0\uFF0C\u6155\u5BB9\u9F99\u4E24\u624B\u626F\u4F4F\u82B1\u74E3\u8FB9\u7F18\u4E00\u6323\uFF0C\u80A5\u7F8E\u7684\u8089\u82B1\u8212\u5377\u7740\u7FFB\u5F00\uFF0C\u9732\u51FA\u5C0F\u6307\u822C\u4E00\u622A\u7EA2\u6DA6\u7684\u8089\u82BD\u3002\u6307\u5C16\u6390\u4F4F\u8089\u82BD\u4E2D\u7684\u5C0F\u94BB\u5411\u4E0A\u4E00\u634B\uFF0C\u96EA\u5CF0\u795E\u5C3C\u95F7\u54FC\u4E00\u58F0\uFF0C\u4E0B\u4F53\u7684\u5AE9\u8089\u987F\u65F6\u4E00\u9635\u4E71\u98A4\u3002\u518D\u7EFD\u5F00\u65F6\uFF0C\u9C9C\u7EA2\u7684\u82B1\u74E3\u5185\u5DF2\u662F\u6DEB\u6DB2\u6A2A\u6D41\u3002</p><p>\u300C\u8FD9\u6837\u7684\u8D31\u5C44\uFF0C\u8FD8\u771F\u914D\u4F60\u8FD9\u6837\u7684\u8D31\u8D27\uFF01\u300D\u6155\u5BB9\u9F99\u7F9E\u8FB1\u7740\u633A\u8EAB\u76F4\u5165\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5DF2\u662F\u5B55\u6EE1\u5F85\u4EA7\uFF0C\u8179\u5185\u88AB\u593A\u80CE\u82B1\u6574\u4E2A\u6491\u6EE1\uFF0C\u518D\u65E0\u4E00\u4E1D\u7A7A\u9699\u3002\u9F9F\u5934\u521A\u521A\u6CA1\u5165\u8089\u7A74\uFF0C\u4FBF\u78B0\u5230\u7A81\u8D77\u7684\u82B1\u5FC3\uFF0C\u6155\u5BB9\u9F99\u594B\u529B\u4E00\u633A\uFF0C\u5706\u6EDA\u6EDA\u7684\u5C0F\u8179\u5411\u4E0A\u4E00\u8DF3\uFF0C\u7C97\u5927\u7684\u9633\u5177\u4FBF\u5B8C\u5168\u6CA1\u5165\u82B1\u5F84\u3002</p><p>\u5B50\u5BAB\u5185\u7684\u8089\u7403\u5728\u9F9F\u5934\u524D\u6ED1\u6765\u6ED1\u53BB\uFF0C\u522B\u6709\u4E00\u756A\u4E50\u8DA3\u3002\u6155\u5BB9\u9F99\u6025\u63D0\u731B\u63D2\uFF0C\u50CF\u8981\u6363\u788E\u593A\u80CE\u82B1\u822C\u51F6\u72E0\u5730\u62BD\u9001\u7740\u3002</p><p>\u767D\u4EAE\u7684\u8179\u7403\u524D\u540E\u7FFB\u6EDA\uFF0C\u4E0D\u4EC5\u7D2B\u73AB\u60CA\u9A87\u6B32\u7EDD\uFF0C\u8FDE\u53F6\u884C\u5357\u4E5F\u6697\u6697\u76B1\u8D77\u7709\u5934\u3002\u8FD9\u6837\u7528\u529B\uFF0C\u4E07\u4E00\u7834\u819C\u5C31\u9EBB\u70E6\u4E86\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5374\u6CA1\u6709\u8FD9\u4E9B\u7684\u62C5\u5FC3\uFF0C\u5728\u6155\u5BB9\u9F99\u7C97\u66B4\u7684\u6345\u5F04\u4E0B\uFF0C\u5979\u8FDE\u6C14\u90FD\u5598\u4E0D\u8FC7\u6765\uFF0C\u53EA\u89C9\u82B1\u5FC3\u50CF\u88AB\u91CD\u7269\u731B\u51FB\u822C\u9178\u75DB\u65E0\u6BD4\u3002\u6345\u4E86\u5341\u4F59\u4E0B\u540E\uFF0C\u5B50\u5BAB\u4E00\u9635\u5267\u75DB\uFF0C\u63A5\u7740\u4FBF\u62BD\u6410\u8D77\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u5BF9\u795E\u5C3C\u4FEE\u70BC\u7684\u529F\u6CD5\u6709\u6240\u6000\u7591\uFF0C\u56E0\u6B64\u5BF9\u593A\u80CE\u82B1\u662F\u5426\u5E73\u5B89\u5E76\u4E0D\u5341\u5206\u5728\u610F\u3002</p><p>\u6B64\u65F6\u89C1\u795E\u5C3C\u7684\u8179\u7403\u6D51\u5706\u53EF\u4EB2\uFF0C\u5E72\u8106\u5408\u8EAB\u6251\u5728\u795E\u5C3C\u4F53\u4E0A\u3002</p><p>\u8179\u7403\u987F\u65F6\u50CF\u88AB\u538B\u788E\u822C\u53D8\u5F97\u6241\u5E73\uFF0C\u5B50\u5BAB\u5185\u6495\u88C2\u7684\u75DB\u695A\u4F7F\u96EA\u5CF0\u795E\u5C3C\u5FCD\u4E0D\u4F4F\u51C4\u58F0\u60E8\u53EB\uFF0C\u5979\u53CC\u76EE\u53D1\u7EA2\uFF0C\u7389\u4F53\u5374\u4E00\u65E0\u8840\u8272\uFF0C\u53EA\u6709\u51B7\u6C57\u6EDA\u6EDA\u800C\u843D\uFF0C\u8FDE\u88F8\u9732\u7684\u7B4B\u8171\u4E5F\u4E00\u4E00\u7EF7\u7D27\u3002</p><p>\u6155\u5BB9\u9F99\u634F\u4F4F\u795E\u5C3C\u7684\u4E73\u5934\uFF0C\u7528\u529B\u63C9\u634F\u7740\u5176\u4E2D\u9576\u5D4C\u7684\u94BB\u77F3\u3002\u5728\u4ED6\u8EAB\u4E0B\uFF0C\u96EA\u767D\u7684\u8179\u7403\u5FFD\u5706\u5FFD\u6241\uFF0C\u6BCF\u4E00\u6B21\u90FD\u9669\u9669\u7206\u88C2\u3002</p><p>\u4ECE\u6628\u65E5\u8D77\u96EA\u5CF0\u795E\u5C3C\u5C31\u95ED\u95E8\u5F85\u4EA7\uFF0C\u88AB\u711A\u60C5\u818F\u6539\u9020\u8FC7\u7684\u4E0B\u4F53\u7ECF\u8FC7\u4E00\u6574\u5929\u7684\u95F2\u7F6E\uFF0C\u6B63\u9965\u6E34\u96BE\u5F53\u3002\u5728\u6155\u5BB9\u9F99\u8FD9\u6837\u6B8B\u5FCD\u7684\u5F3A\u66B4\u4E0B\uFF0C\u53EF\u8C13\u662F\u82E6\u4E50\u53C2\u534A\u3002\u4E73\u5934\u548C\u82B1\u8482\u5185\u7684\u94BB\u77F3\u5C3D\u88AB\u626F\u52A8\uFF0C\u654F\u611F\u7684\u4E73\u773C\u75DB\u75D2\u96BE\u5F53\uFF0C\u82B1\u8482\u66F4\u662F\u88AB\u9633\u5177\u4E0B\u7684\u89E6\u624B\u626F\u5F97\u7B14\u76F4\u3002\u4E00\u523B\u949F\u540E\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4E00\u58F0\u5C16\u53EB\uFF0C\u79D8\u5904\u9634\u7CBE\u98DE\u6E85\u3002</p><p>\u300C\u6545\u5730\u91CD\u6E38\uFF0C\u611F\u6168\u826F\u591A\u554A\u3002\u300D\u6155\u5BB9\u9F99\u626C\u58F0\u9053\uFF1A\u300C\u4E0A\u4E00\u6B21\u672C\u5BAB\u7ED9\u4F60\u5F00\u82DE\uFF0C\u91CC\u9762\u53C8\u7D27\u53C8\u7A84\u3002\u5306\u5306\u6570\u6708\uFF0C\u8FD9\u8D31\u5C44\u5DF2\u7ECF\u662F\u5BBE\u5BA2\u76C8\u95E8\uFF0C\u88AB\u64CD\u5F97\u677E\u677E\u57AE\u57AE\u2026\u2026\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u8EAB\u4F53\u7684\u98A4\u6296\u8FD8\u672A\u505C\u6B62\uFF0C\u4FBF\u4E00\u53E3\u72E0\u72E0\u553E\u5728\u6155\u5BB9\u9F99\u8138\u4E0A\uFF0C\u9A82\u9053\uFF1A\u300C\u5351\u9119\u65E0\u803B\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u7528\u529B\u4E00\u9876\uFF0C\u9876\u5F97\u795E\u5C3C\u4E24\u773C\u7FFB\u767D\uFF0C\u6B63\u5F85\u5F00\u53E3\u8C03\u7B11\uFF0C\u7A81\u7136\u89C9\u5F97\u82B1\u5FC3\u5904\u4F20\u6765\u4E00\u9635\u5438\u529B\uFF0C\u7CBE\u5173\u4E00\u677E\uFF0C\u9633\u7CBE\u55B7\u5C04\u800C\u51FA\u3002\u5BAB\u9888\u53E3\u4EFF\u4F5B\u4E00\u5F20\u5C0F\u5DE7\u7684\u5634\u5DF4\uFF0C\u5C06\u9633\u7CBE\u4E00\u6EF4\u4E0D\u6F0F\u5730\u5438\u542E\u5E72\u51C0\uFF0C\u751A\u81F3\u8FD8\u51D1\u5728\u9A6C\u773C\u4E0A\u50CF\u8981\u5438\u53D6\u4ED6\u7684\u771F\u5143\u3002</p><p>\u6155\u5BB9\u9F99\u8FDE\u5FD9\u62BD\u8EAB\u800C\u51FA\uFF0C\u76EE\u89C6\u7740\u8179\u7403\u7684\u8F6C\u52A8\u3002</p>',75);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
