import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_107" tabindex="-1"><a class="header-anchor" href="#_107" aria-hidden="true">#</a> 107</h1><p>\u593A\u80CE\u82B1\u5206\u5A29\u5728\u5373\uFF0C\u53F6\u884C\u5357\u95EA\u8EAB\u4E0A\u524D\uFF0C\u5C06\u4E00\u4E2A\u5E26\u5F39\u6027\u7684\u94A2\u4E1D\u73AF\u7EB3\u5165\u7FD5\u5F20\u7684\u8089\u7A74\u4E2D\uFF0C\u7136\u540E\u62FF\u51FA\u4E00\u6839\u5706\u5934\u7684\u957F\u67C4\u94A2\u5939\uFF0C\u6162\u6162\u63A2\u5165\u8089\u7A74\uFF0C\u5939\u4F4F\u5C3D\u5934\u7684\u5AE9\u8089\u5411\u5916\u626F\u52A8\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u547C\u5438\u505C\u987F\uFF0C\u989D\u5934\u9752\u7B4B\u66B4\u9732\u3002\u7D2B\u73AB\u77AA\u5927\u773C\u775B\uFF0C\u4E00\u7728\u4E0D\u7728\u5730\u770B\u7740\u94A2\u5939\u4E0A\u7684\u90A3\u56E2\u5AE9\u8089\u3002</p><p>\u94A2\u4E1D\u73AF\u5F39\u8D77\u540E\u5C06\u8089\u7A74\u6491\u6210\u4E00\u4E2A\u62F3\u5934\u5927\u5C0F\u7684\u8089\u6D1E\uFF0C\u8089\u58C1\u4E0A\u7EC6\u5AE9\u7684\u8936\u76B1\u88AB\u5C3D\u6570\u62C9\u5E73\uFF0C\u663E\u51FA\u4E00\u7247\u5149\u6DA6\u7684\u8273\u7EA2\u3002</p><p>\u5728\u8FD9\u7247\u6DEB\u9761\u7684\u8273\u7EA2\u4E2D\uFF0C\u4E00\u6839\u96EA\u4EAE\u7684\u94A2\u5939\u7F13\u7F13\u9000\u51FA\uFF0C\u575A\u786C\u7684\u94A2\u94C1\u95F4\uFF0C\u5939\u7740\u4E00\u56E2\u5A07\u5AE9\u6ED1\u817B\u7684\u7EA2\u8089\u3002\u5AE9\u8089\u7A81\u8D77\u513F\u62F3\u5927\u5C0F\uFF0C\u9876\u7AEF\u6B63\u4E2D\u6709\u4E00\u4E2A\u5C0F\u5C0F\u7684\u5706\u5B54\uFF0C\u6B63\u5728\u94A2\u5939\u8FB9\u7F18\u9690\u9690\u62BD\u52A8\u3002</p><p>\u53F6\u884C\u5357\u5BDF\u89C9\u5E76\u65E0\u5F02\u72B6\uFF0C\u4E0D\u7531\u677E\u4E86\u53E3\u6C14\uFF0C\u7B11\u9053\uFF1A\u300C\u8FD9\u5C31\u662F\u5973\u5B50\u7684\u82B1\u5FC3\u4E86\u3002\u300D</p><p>\u7D2B\u73AB\u95FB\u58F0\u987F\u65F6\u6253\u4E86\u51B7\u6218\uFF0C\u5979\u77E5\u9053\u5973\u5B50\u7684\u82B1\u5FC3\u5728\u82B1\u5F84\u5C3D\u5934\uFF0C\u6DF1\u85CF\u4F53\u5185\uFF0C\u5373\u4F7F\u4EA4\u5408\u4E2D\u4E5F\u4E0D\u4E00\u5B9A\u80FD\u78B0\u5230\u3002\u800C\u4E14\u67D4\u5AE9\u5F02\u5E38\uFF0C\u7565\u5FAE\u4E00\u89E6\u4FBF\u6D51\u8EAB\u9178\u9EBB\u3002</p><p>\u73B0\u5728\u5E08\u7236\u7684\u6700\u654F\u611F\u7684\u90E8\u4F4D\u7ADF\u7136\u88AB\u94A2\u5939\u62C9\u5230\u8089\u7A74\u8FB9\u7F18\u2026\u2026\u5979\u671B\u7740\u54AC\u7259\u575A\u5FCD\u7684\u96EA</p><p>\u5CF0\u795E\u5C3C\uFF0C\u4E0B\u4F53\u4F3C\u4E4E\u4E5F\u611F\u53D7\u4E86\u90A3\u79CD\u75DB\u695A\u3002</p><p>\u300C\u54E6\uFF1F\u8FD9\u5C31\u662F\u5E08\u592A\u559D\u5927\u4F19\u9633\u7CBE\u7684\u90A3\u4E2A\u5730\u65B9\uFF1F\u300D\u6155\u5BB9\u9F99\u62AC\u624B\u62E8\u5F04\u7740\u90A3\u56E2\u5AE9\u8089\uFF0C\u624B\u6307\u63A2\u5165\u82B1\u5FC3\u6345\u4E86\u6345\uFF0C\u7B11\u9053\uFF1A\u300C\u6BD4\u5979\u7684\u8D31\u5C44\u53EF\u7D27\u591A\u4E86\u3002\u300D</p><p>\u7531\u4E8E\u795E\u5C3C\u5E76\u975E\u662F\u6B63\u5E38\u598A\u5A20\uFF0C\u53F6\u884C\u5357\u652F\u597D\u94A2\u5939\u540E\uFF0C\u5148\u7528\u53CC\u638C\u5728\u795E\u5C3C\u767D\u817B\u7684\u809A\u76AE\u4E0A\u63C9\u6478\u7247\u523B\uFF0C\u7136\u540E\u5C06\u4E00\u6839\u624B\u6307\u7C97\u7EC6\u7684\u949D\u5934\u6728\u68CD\u63D2\u8FDB\u7EC6\u5AE9\u7684\u82B1\u5FC3\u4E4B\u4E2D\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u7389\u4F53\u5FAE\u5FAE\u98A4\u6296\uFF0C\u75DB\u82E6\u5730\u652F\u8D77\u67D4\u9888\uFF0C\u6C57\u6C34\u987A\u7740\u79C0\u53D1\u4E00\u6EF4\u6EF4\u6DCC\u843D\u3002\u6700\u9690\u79D8\u7684\u5668\u5B98\u88AB\u66B4\u9732\u5728\u5149\u5929\u5316\u65E5\u4E4B\u4E0B\uFF0C\u90A3\u79CD\u88AB\u4EBA\u89E3\u5256\u7684\u803B\u8FB1\u611F\uFF0C\u751A\u81F3\u76D6\u8FC7\u4E86\u8EAB\u4F53\u7684\u75BC\u75DB\u3002</p><p>\u6728\u68CD\u63D2\u5165\u5BF8\u8BB8\uFF0C\u4FBF\u78B0\u5230\u4E00\u5C42\u8584\u819C\u3002\u53F6\u884C\u5357\u529B\u900F\u6307\u5C16\uFF0C\u6728\u68CD\u72E0\u72E0\u7A7F\u7834\u80CE\u819C\uFF0C\u7136\u540E\u8FC5\u901F\u62D4\u51FA\u3002\u5AE9\u7EA2\u7684\u8089\u5B54\u7ACB\u65F6\u6536\u7D27\uFF0C\u63A5\u7740\u5411\u5916\u4E00\u9F13\uFF0C\u4E00\u80A1\u8840\u6C34\u55B7\u6CC9\u822C\u4ECE\u96EA\u767D\u7684\u53CC\u817F\u95F4\u6FC0\u5C04\u800C\u51FA\u3002</p><p>\u5F85\u8840\u6C34\u6D41\u5C3D\uFF0C\u53F6\u884C\u5357\u6309\u4F4F\u795E\u5C3C\u7684\u8179\u7403\uFF0C\u7528\u529B\u4E0B\u63A8\u3002\u6D51\u5706\u7684\u8089\u7403\u4ECE\u5C0F\u8179\u964D\u5230\u80A1\u95F4\uFF0C\u795E\u5C3C\u9634\u961C\u7A81\u8D77\uFF0C\u8089\u82B1\u5B8C\u5168\u7FFB\u5F00\uFF0C\u7EC6\u5C0F\u7684\u82B1\u5FC3\u968F\u7740\u8179\u4E0A\u7684\u529B\u9053\uFF0C\u4E00\u9707\u4E00\u9707\u5730\u6E10\u6E10\u7EFD\u5F00\u3002</p><p>\u53F6\u884C\u5357\u5C0F\u6307\u4E00\u52FE\uFF0C\u626F\u6389\u94A2\u4E1D\u73AF\u3002\u5931\u53BB\u652F\u6491\u7684\u8089\u7A74\u5E76\u672A\u5408\u7D27\uFF0C\u53CD\u800C\u56E0\u4E3A\u82B1\u5F84\u88AB\u8179\u5185\u7684\u5F02\u7269\u538B\u77ED\u800C\u7EFD\u5F97\u66F4\u5F00\u3002</p><p>\u5E9E\u5927\u7684\u5706\u7403\u6574\u4E2A\u671D\u82B1\u5FC3\u6324\u53BB\uFF0C\u5728\u4E24\u817F\u95F4\u9F13\u6210\u4E00\u56E2\u3002\u65E0\u8BBA\u662F\u9634\u5507\u3001\u9634\u9053\u8FD8\u662F\u5B50\u5BAB\u9888\uFF0C\u90FD\u88AB\u6324\u5F97\u53D8\u5F62\u3002</p><p>\u7D27\u7A84\u7684\u82B1\u5FC3\u5728\u7D2B\u73AB\u773C\u524D\u7EFD\u5F00\u5BF8\u8BB8\u4E00\u4E2A\u5706\u6D1E\uFF0C\u53EF\u4EE5\u6E05\u695A\u5730\u770B\u5230\u4E00\u56E2\u8840\u7EA2\u7684\u8089\u7403\u5728\u6D1E\u53E3\u5185\u6296\u52A8\uFF0C\u4EFF\u4F5B\u4E00\u4E2A\u62AB\u7740\u8840\u819C\u7684\u5996\u9B54\u62FC\u547D\u6495\u626F\u7740\u7834\u4F53\u800C\u51FA\u3002</p><p>\u300C\u5440\u2014\u2014\u300D\u4E00\u58F0\u51C4\u5389\u5730\u5C16\u53EB\u5212\u7834\u8033\u819C\uFF0C\u7D2B\u73AB\u5413\u5F97\u4FCF\u8138\u96EA\u767D\uFF0C\u5FC3\u810F\u9669\u4E9B\u505C\u6B62\u8DF3\u52A8\u3002</p><p>\u575A\u5FCD\u826F\u4E45\u7684\u96EA\u5CF0\u795E\u5C3C\uFF0C\u7EC8\u4E8E\u5FCD\u4E0D\u4F4F\u75DB\u53EB\u8D77\u6765\uFF0C\u5979\u7389\u4F53\u4E71\u98A4\uFF0C\u82E5\u975E\u56DB\u80A2\u9AA8\u9ABC\u88AB\u5E9F\uFF0C\u65E0\u4ECE\u4F7F\u529B\uFF0C\u8FD9\u4E00\u4E0B\u4FBF\u4F1A\u6323\u65AD\u5979\u7684\u624B\u7B4B\u811A\u7B4B\u3002</p><p>\u300C\u4E0D\u5C31\u662F\u751F\u4E2A\u602A\u80CE\uFF0C\u7528\u5F97\u7740\u53EB\u8FD9\u4E48\u54CD\uFF1F\u300D\u6155\u5BB9\u9F99\u51B7\u7B11\u9053\uFF1A\u300C\u5F53\u65E5\u56DB\u95EF\u795E\u6559\u5A01\u98CE\u54EA\u513F\u53BB\u4E86\uFF1F\u300D</p><p>\u300C\u4F4F\u624B\uFF01\u300D\u7D2B\u73AB\u54ED\u53EB\u9053\uFF1A\u300C\u6C42\u6C42\u4F60\uFF0C\u653E\u8FC7\u6211\u5E08\u7236\u5427\u2026\u2026\u300D</p><p>\u300C\u4ECA\u65E5\u82E5\u4E0D\u53D6\u51FA\u593A\u80CE\u82B1\uFF0C\u5B83\u4FBF\u4F1A\u541E\u566C\u8840\u8089\uFF0C\u300D\u53F6\u884C\u5357\u9053\uFF1A\u300C\u76F4\u81F3\u5E08\u592A\u8840\u8089\u65E0\u5B58\u3002\u300D</p><p>\u5B50\u5BAB\u9888\u5F20\u5F00\u5230\u513F\u62F3\u5927\u5C0F\u65F6\uFF0C\u53F6\u884C\u5357\u64CD\u8D77\u4E00\u628A\u7279\u5236\u7684\u94C1\u6746\uFF0C\u8FF3\u76F4\u523A\u5165\u82B1\u5FC3\u3002\u94C1\u6746\u7A7F\u7834\u8089\u7403\u8868\u9762\u7684\u8840\u819C\uFF0C\u53D1\u51FA\u4E00\u9635\u4E0D\u5C5E\u4E8E\u8089\u4F53\u7684\u300C\u683C\u683C\u300D\u58F0\u54CD\uFF0C\u63A2\u5165\u5B50\u5BAB\u6DF1\u5904\u3002</p><p>\u4E00\u6273\u673A\u6263\uFF0C\u94C1\u6746\u524D\u7AEF\u5F39\u51FA\u51E0\u6839\u5012\u94A9\uFF0C\u7262\u7262\u52FE\u4F4F\u593A\u80CE\u82B1\u3002\u53F6\u884C\u5357\u677E\u5F00\u795E\u5C3C\u7684\u5C0F\u8179\uFF0C\u4E00\u63D0\u94C1\u6746\uFF0C\u8840\u7EA2\u7684\u7403\u4F53\u5411\u5916\u4E00\u6323\uFF0C\u82B1\u5FC3\u5E94\u624B\u4E4D\u5F00\u3002</p><p>\u60E8\u53EB\u58F0\u500F\u5FFD\u4E2D\u6B62\uFF0C\u96EA\u5CF0\u795E\u5C3C\u75DB\u5F97\u6B7B\u53BB\u6D3B\u6765\uFF0C\u5979\u62FC\u547D\u62F1\u8D77\u8EAB\u4F53\uFF0C\u4E00\u53E3\u6C14\u54FD\u5728\u5589\u5934\uFF0C\u65E0\u6CD5\u5410\u51FA\u3002</p><p>\u6B64\u65F6\u5B50\u5BAB\u9888\u5DF2\u7ECF\u88AB\u62C9\u5230\u4F53\u5916\uFF0C\u82B1\u5FC3\u3001\u8089\u7A74\u3001\u82B1\u74E3\uFF0C\u5A07\u8273\u7684\u5AE9\u8089\u4E00\u5C42\u5C42\u8D34\u5728\u8179\u5185\u7684\u7403\u4F53\u4E0A\uFF0C\u8D8A\u7EFD\u8D8A\u5927\u3002\u6700\u5916\u5C42\u80A5\u539A\u7684\u82B1\u74E3\u88AB\u626F\u6210\u4E00\u9053\u7EC6\u7EC6\u7684\u7EA2\u8FB9\uFF0C\u8089\u7A74\u7EA2\u5AE9\u7FFB\u5410\uFF0C\u82B1\u5FC3\u5DF2\u7ECF\u6491\u5230\u6781\u9650\uFF0C\u8272\u6CFD\u53D8\u5F97\u900F\u660E\u3002</p><p>\u5728\u8FD9\u4E9B\u7F8E\u5999\u5973\u4F53\u7684\u5668\u5B98\u4E4B\u95F4\uFF0C\u5DE8\u5927\u7684\u7403\u4F53\u5E26\u7740\u4E1D\u4E1D\u7F15\u7F15\u4E0E\u5BAB\u8154\u76F8\u8FDE\u7684\u8840\u7EA2\u8109\u7BA1\u9010\u6E10\u8131\u79BB\u6BCD\u4F53\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5168\u8EAB\u7684\u529B\u6C14\u4F3C\u4E4E\u90FD\u96C6\u4E2D\u5728\u4E0B\u4F53\uFF0C\u8FDE\u60E8\u53EB\u58F0\u4E5F\u6C99\u54D1\u8D77\u6765\u3002\u9634\u961C\u4E0A\u65B9\u7EC6\u5BC6\u7684\u8840\u7BA1\u4E00\u4E00\u6D6E\u73B0\uFF0C\u5374\u4E00\u7247\u82CD\u767D\uFF0C\u4EFF\u4F5B\u5370\u5728\u8179\u7403\u4E0A\u7684\u9752\u8272\u7EB9\u9970\u3002\u5979\u4ECE\u6765\u6CA1\u6709\u50CF\u8FD9\u4E00\u523B\u4E00\u6837\u6E34\u671B\u6B7B\u53BB\uFF0C\u53EA\u6C42\u80FD\u6446\u8131\u8FD9\u79CD\u75DB\u82E6\u7684\u6298\u78E8\u3002</p><p>\u768E\u6D01\u65E0\u7455\u7684\u7389\u80A1\u95F4\uFF0C\u4E00\u56E2\u8840\u7403\u6E10\u6E10\u589E\u5927\u3002\u5E9E\u5927\u7684\u4F53\u79EF\u5C06\u5973\u5B50\u4E0B\u4F53\u7684\u5668\u5B98\u5C3D\u6570\u6495\u88C2\uFF0C\u4E0D\u591A\u65F6\uFF0C\u96EA\u5CF0\u795E\u5C3C\u8179\u4E0B\u5DF2\u662F\u9C9C\u8840\u6DCB\u6F13\u3002\u5979\u6D51\u8EAB\u51B0\u51B7\uFF0C\u7EA2\u5507\u53D8\u5F97\u53D1\u6298\uFF0C\u53EB\u58F0\u8D8A\u6765\u8D8A\u5FAE\u5F31\uFF0C\u610F\u8BC6\u4E5F\u6E10\u6E10\u6A21\u7CCA\u3002\u8179\u4E0B\u7684\u5668\u5B98\u4F3C\u4E4E\u88AB\u5F02\u7269\u5C3D\u6570\u626F\u843D\uFF0C\u4EE4\u4EBA\u75AF\u72C2\u7684\u75DB\u695A\u6DF1\u5165\u4F53\u8154\uFF0C\u767D\u817B\u7684\u5C0F\u8179\u5267\u70C8\u5730\u62BD\u52A8\u7740\uFF0C\u5B50\u5BAB\u6BEB\u65E0\u89C4\u5F8B\u5730\u6781\u529B\u6536\u7F29\u3002</p><p>\u5C31\u5728\u7D2B\u73AB\u54AC\u7834\u81EA\u5DF1\u7684\u5634\u5507\u65F6\uFF0C\u300C\u5575\u300D\u7684\u4E00\u58F0\u5DE8\u54CD\uFF0C\u4E00\u56E2\u9C9C\u7EA2\u7684\u7403\u4F53\u7EC8\u4E8E\u6389\u843D\u51FA\u6765\u3002</p><p>\u75DB\u4E0D\u6B32\u751F\u7684\u96EA\u5CF0\u795E\u5C3C\u53D1\u51FA\u6700\u540E\u4E00\u58F0\u60E8\u53EB\uFF0C\u65CB\u5373\u5931\u53BB\u77E5\u89C9\u3002\u6E7F\u6F09\u6F09\u7684\u79C0\u53D1\u95F4\uFF0C\u7389\u8138\u5BD2\u51B0\u822C\u900F\u660E\uFF0C\u677E\u5F1B\u4E0B\u6765\u7684\u5C0F\u8179\u8FD8\u5728\u4E0D\u65F6\u62BD\u52A8\u3002\u9AD8\u4E3E\u7684\u79D8\u5904\u88AB\u9C9C\u8840\u67D3\u5F97\u4E00\u7247\u901A\u7EA2\uFF0C\u5AE9\u8089\u4F3C\u4E4E\u5931\u53BB\u751F\u547D\uFF0C\u6728\u7136\u5F20\u7740\u8840\u8089\u6A21\u7CCA\u7684\u5165\u53E3\u3002</p><p>\u6155\u5BB9\u9F99\u7B11\u541F\u541F\u770B\u7740\u7D2B\u73AB\uFF0C\u300C\u5BB3\u6015\u5417\uFF1F\u5973\u4EBA\u751F\u5B69\u5B50\u90FD\u662F\u8FD9\u6837\uFF0C\u5A18\u5C31\u662F\u8FD9\u6837\u751F\u7684\u6211\uFF0C\u4E5F\u662F\u8FD9\u6837\u751F\u7684\u4F60\u3002\u4F60\u4EE5\u540E\u4E5F\u4F1A\u8FD9\u6837\u751F\u4E0B\u6211\u7684\u5B69\u5B50\u3002\u4E0D\u540C\u7684\u662F\u2014\u2014\u8FD9\u4E2A\u8D31\u8D27\u4E0D\u914D\u751F\u4EBA\uFF0C\u53EA\u80FD\u751F\u4E0B\u4E9B\u602A\u7269\uFF01\u300D</p><p>\u7D2B\u73AB\u76EE\u5149\u4E00\u76F4\u505C\u5728\u53F6\u884C\u5357\u624B\u4E2D\u7684\u94C1\u6746\u4E0A\u3002\u8089\u7403\u8DB3\u6709\u5A74\u513F\u5927\u5C0F\uFF0C\u5F62\u72B6\u6D51\u5706\u3002\u8868\u9762\u5C3D\u662F\u4ECE\u5BAB\u8154\u4E0A\u751F\u751F\u626F\u843D\u7684\u8840\u8089\u3002</p><p>\u53F6\u884C\u5357\u62FF\u8D77\u94F6\u9488\uFF0C\u5728\u7403\u4F53\u4E0A\u8F7B\u8F7B\u4E00\u5212\uFF0C\u6495\u5F00\u6EF4\u8840\u7684\u8584\u819C\u3002\u8584\u819C\u4E0B\u662F\u4E00\u4E2A\u8089\u7EA2\u8272\u7684\u82B1\u82DE\uFF0C\u63A5\u89E6\u7A7A\u6C14\u540E\uFF0C\u82B1\u74E3\u7A81\u7136\u7EFD\u5F00\uFF0C\u9732\u51FA\u5176\u4E2D\u5C0F\u5C0F\u7684\u83B2\u84EC\u3002</p><p>\u7ADF\u7136\u7528\u5973\u4EBA\u517B\u80B2\u80CE\u513F\u7684\u5B50\u5BAB\u517B\u80B2\u51FA\u8FD9\u6837\u7684\u5996\u7269\uFF0C\u661F\u6708\u6E56\u7A76\u7ADF\u505A\u8FC7\u591A\u5C11\u7F6A\u5B7D\uFF1F</p><p>\u7D2B\u73AB\u9ED8\u9ED8\u60F3\u7740\uFF0C\u9EEF\u7136\u5782\u4E0B\u76EE\u5149\u3002</p><p>\u53F6\u884C\u5357\u5254\u4E0B\u83B2\u84EC\uFF0C\u6D78\u5728\u4E00\u676F\u4E73\u767D\u8272\u7684\u6DB2\u4F53\u4E2D\uFF0C\u8FD9\u624D\u8212\u4E86\u53E3\u6C14\uFF0C\u6EE1\u662F\u76B1\u7EB9\u7684\u8138\u4E0A\u4E5F\u4E0D\u7981\u9732\u51FA\u7B11\u610F\u3002</p><p>\u6155\u5BB9\u9F99\u4E3E\u676F\u7AEF\u8BE6\u7247\u523B\uFF0C\u53F9\u9053\uFF1A\u300C\u96EA\u5CF0\u8D3C\u5C3C\u867D\u7136\u6DEB\u8D31\uFF0C\u529F\u529B\u786E\u5B9E\u4E0D\u4FD7\uFF0C\u4E0D\u77E5\u8FD9\u5176\u4E2D\u6709\u5979\u51E0\u8BB8\u771F\u5143\u2026\u2026\u300D</p><p>\u53F6\u884C\u5357\u9887\u4E3A\u81EA\u8D1F\u5730\u8BF4\u9053\uFF1A\u300C\u795E\u6559\u5386\u4EE3\u76F8\u4F20\uFF0C\u593A\u80CE\u82B1\u4E00\u682A\u4FBF\u53EF\u5438\u5C3D\u771F\u5143\u3002\u6B64\u6B21\u65E0\u8BBA\u70BC\u5236\u3001\u690D\u79CD\u3001\u5582\u517B\u3001\u593A\u80CE\uFF0C\u90FD\u7531\u8001\u592B\u4E00\u624B\u64CD\u6301\uFF0C\u5982\u4ECA\u5E08\u592A\u7684\u529F\u529B\u6700\u591A\u8FD8\u5269\u4E09\u6210\u3002\u300D</p><p>\u4ED6\u642D\u4F4F\u96EA\u5CF0\u795E\u5C3C\u7684\u8109\u95E8\uFF0C\u9762\u8272\u987F\u65F6\u5927\u53D8\u3002</p><p>\u6155\u5BB9\u9F99\u548C\u7D2B\u73AB\u8BB6\u7136\u671B\u53BB\uFF0C\u53EA\u89C1\u53F6\u884C\u5357\u7709\u5934\u7D27\u9501\uFF0C\u5DE6\u624B\u5207\u5B8C\u53C8\u5207\u53F3\u624B\uFF0C\u8138\u8272\u8D8A\u6765\u8D8A\u96BE\u770B\u3002</p><p>\u534A\u664C\u540E\uFF0C\u53F6\u884C\u5357\u76F4\u8D77\u8170\uFF0C\u4E00\u8A00\u4E0D\u53D1\u5730\u62FF\u8D77\u4E00\u6839\u5F2F\u5C3A\uFF0C\u4F38\u5165\u5BAB\u9888\uFF0C\u5F00\u59CB\u6E05\u7406\u96EA\u5CF0\u795E\u5C3C\u7684\u5BAB\u8154\u3002</p><p>\u5F2F\u5C3A\u5728\u795E\u5C3C\u4F53\u5185\u4E0D\u4F4F\u8FDB\u51FA\uFF0C\u5B50\u5BAB\u5185\u6B8B\u4F59\u7684\u8840\u8089\u5757\u5757\u5265\u843D\uFF0C\u6BCF\u6E05\u51FA\u4E00\u56E2\uFF0C\u53F6\u884C\u5357\u8138\u8272\u5C31\u9634\u6C89\u4E00\u5206\uFF0C\u6700\u540E\u4ED6\u653E\u4E0B\u94C1\u5C3A\uFF0C\u53F9\u9053\uFF1A\u300C\u8001\u592B\u65E0\u80FD\uFF0C\u6709\u8D1F\u5BAB\u4E3B\u6240\u6258\u2026\u2026\u300D</p><p>\u5207\u8109\u65F6\u4ED6\u624D\u53D1\u73B0\uFF0C\u96EA\u5CF0\u795E\u5C3C\u4F53\u5185\u6563\u4E71\u7684\u771F\u6C14\u4F9D\u7136\u5F3A\u52B2\uFF0C\u593A\u80CE\u82B1\u6240\u5438\u53D6\u7684\u771F\u5143\u7EDD\u4E0D\u8D85\u8FC7\u4E24\u6210\u3002</p><p>\u53F6\u884C\u5357\u767E\u601D\u4E0D\u5F97\u5176\u89E3\uFF0C\u53EA\u597D\u6E05\u7406\u5BAB\u8154\uFF0C\u300C\u5F85\u96EA\u5CF0\u590D\u539F\uFF0C\u534A\u5E74\u540E\u5C5E\u4E0B\u518D\u7528\u4E00\u6B21\u593A\u80CE\u82B1\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6CA1\u60F3\u5230\u53F6\u884C\u5357\u8FD8\u6709\u5931\u624B\u7684\u65F6\u5019\uFF0C\u9887\u611F\u610F\u5916\u5730\u770B\u7740\u96EA\u5CF0\u795E\u5C3C\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u5C06\u8FD9\u8D31\u4EBA\u56DA\u5728\u5305\u623F\uFF0C\u534A\u5E74\u540E\u518D\u8BD5\u4E00\u6B21\u3002\u4E0D\u884C\u5C31\u5E9F\u4E86\u5979\u7684\u5185\u529F\u3002\u300D</p><p>\u5E08\u7236\u80A1\u95F4\u4ECD\u655E\u7740\u8840\u6DCB\u6DCB\u7684\u5927\u6D1E\uFF0C\u534A\u5E74\u540E\u8FD8\u8981\u518D\u7ECF\u53D7\u4E00\u6B21\u540C\u6837\u7684\u6298\u78E8\uFF0C\u7D2B\u73AB\u51C4\u58F0\u9053\uFF1A\u300C\u54E5\uFF0C\u6211\u6C42\u4F60\u4E86\uFF0C\u522B\u518D\u6298\u78E8\u5979\u4E86\u2026\u2026\u6211\u2014\u2014\u300D\u4E0D\u7B49\u5979\u8BF4\u5B8C\uFF0C\u6155\u5BB9\u9F99\u4FBF\u51B7\u7B11\u4E00\u58F0\uFF0C\u62C2\u8896\u800C\u53BB\u3002</p><p>\u53F6\u884C\u5357\u5E2E\u5979\u89E3\u5F00\u7A74\u9053\u540E\uFF0C\u7D2B\u73AB\u4ECD\u5446\u5446\u5750\u5728\u6905\u4E2D\u3002\u5446\u5446\u770B\u7740\u90A3\u5177\u51C4\u60E8\u7684\u5973\u4F53\u75DB</p><p>\u82E6\u7684\u62BD\u52A8\uFF1B\u5446\u5446\u770B\u7740\u6709\u4EBA\u8FDB\u6765\u677E\u5F00\u5973\u4F53\u4E0A\u7684\u94C1\u9563\uFF1B\u5446\u5446\u770B\u7740\u5979\u88AB\u4EBA\u62D6\u8D70\uFF1B\u5446\u5446\u770B\u7740\u5730\u4E0A\u6D12\u843D\u7684\u8840\u8FF9\u2026\u2026</p><p>\u53F6\u884C\u5357\u6536\u62FE\u597D\u593A\u80CE\u82B1\uFF0C\u624D\u53D1\u89C9\u7D2B\u73AB\u7684\u5F02\u6837\uFF0C\u8FDE\u5FD9\u5728\u5979\u80CC\u4E0A\u8F7B\u62CD\u4E00\u638C\u3002</p><p>\u7D2B\u73AB\u300C\u54C7\u300D\u7684\u5410\u51FA\u4E00\u53E3\u9C9C\u8840\uFF0C\u773C\u775B\u6162\u6162\u6062\u590D\u5149\u5F69\u3002\u5979\u6162\u6162\u62C9\u597D\u8863\u895F\uFF0C\u52C9\u5F3A\u63A9\u4F4F\u81EA\u5DF1\u7684\u4E73\u5CF0\uFF0C\u7136\u540E\u8C22\u7EDD\u4E86\u53F6\u884C\u5357\u7684\u6551\u6CBB\uFF0C\u8E52\u8DDA\u7740\u79BB\u5F00\u6B66\u51E4\u522B\u9662\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u65E5\u5F71\u897F\u659C\uFF0C\u79CB\u98CE\u5939\u6742\u7740\u661F\u661F\u70B9\u70B9\u7684\u9633\u5149\u98D8\u5728\u8EAB\u4E0A\uFF0C\u4F20\u6765\u4E00\u4E1D\u6DE1\u6DE1\u7684\u6696\u610F\u3002</p><p>\u5C11\u5973\u62B1\u7740\u80F8\u53E3\uFF0C\u6162\u6162\u62AC\u8D77\u4ED9\u5B50\u822C\u7684\u7389\u5BB9\uFF0C\u95ED\u4E0A\u773C\uFF0C\u611F\u53D7\u7740\u843D\u65E5\u7684\u4F59\u6656\u3002</p><p>\u8863\u8882\u98DE\u626C\uFF0C\u5A07\u8EAF\u66F2\u7EBF\u6BD5\u9732\u3002\u7136\u800C\u8FD9\u5177\u66FC\u5999\u5A40\u5A1C\u7684\u8EAB\u4F53\u4E0A\uFF0C\u5374\u6709\u7740\u5706\u6EDA\u6EDA\u7684\u5C0F\u8179\u548C\u4E00\u5BF9\u4EE4\u4EBA\u96BE\u4EE5\u7F6E\u4FE1\u7684\u5DE8\u4E73\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u5C11\u5973\u8138\u4E0A\u51C4\u7136\u7684\u60B2\u4F24\u6E10\u6E10\u6DE1\u53BB\uFF0C\u6700\u540E\u53D8\u6210\u59A9\u5A9A\u7684\u7B11\u5BB9\u3002\u800C\u5979\u773C\u89D2\u672A\u5E72\u7684\u6CEA\u75D5\uFF0C\u5219\u4F7F\u8FD9\u7B11\u5BB9\u6108\u53D1\u8BA9\u4EBA\u5FC3\u75BC\u3002</p><p>\u4E00\u9635\u5608\u6742\u7684\u5B09\u95F9\u58F0\u4ECE\u677E\u6797\u65C1\u4F20\u51FA\uFF0C\u51E0\u540D\u7EA2\u8863\u6C49\u5B50\u56F4\u7740\u4E00\u5177\u96EA\u767D\u7684\u8089\u4F53\uFF0C\u4E00\u8FB9\u8E22\u6253\u4E00\u8FB9\u8D70\u6765\u3002</p><p>\u90A3\u4E2A\u5973\u4EBA\u65AD\u4E86\u4E00\u81C2\uFF0C\u60B2\u9E23\u7740\u8270\u96BE\u5730\u722C\u884C\uFF0C\u8EAB\u4E0A\u7684\u8840\u8FF9\u6BD4\u5468\u56F4\u4EBA\u7684\u670D\u8272\u8FD8\u8981\u9C9C\u7EA2\u3002</p><p>\u300C\u970D\u7237\u771F\u591F\u72E0\u7684\uFF0C\u786C\u6345\u8FDB\u53BB\u4E00\u5C3A\u591A\u957F\u2026\u2026\u300D</p><p>\u300C\u563F\u563F\uFF0C\u8FD9\u5A4A\u5B50\u53FD\u54C7\u4E71\u53EB\uFF0C\u53EA\u6015\u80A0\u5B50\u90FD\u6345\u65AD\u4E86\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u770B\u7740\u5973\u5B50\u81C0\u95F4\u7684\u9C9C\u8840\uFF0C\u8138\u4E0A\u4ECD\u6302\u7740\u6DE1\u6DE1\u7684\u7B11\u5BB9\u3002\u5979\u5934\u4E5F\u4E0D\u56DE\u5730\u8BF4\uFF1A\u300C\u53F6\u4F2F\u4F2F\uFF0C\u4F60\u540E\u5929\u662F\u4E0D\u662F\u8981\u7ED9\u970D\u957F\u8001\u6CBB\u4F24\u2026\u2026\u300D</p>',61);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
