import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_108" tabindex="-1"><a class="header-anchor" href="#_108" aria-hidden="true">#</a> 108</h1><p>\u970D\u72C2\u7130\u4ECE\u6765\u6CA1\u60F3\u8FC7\u81EA\u5DF1\u4F1A\u6B7B\u5F97\u5982\u6B64\u5C48\u8FB1\uFF0C\u66F4\u6CA1\u60F3\u5230\u81EA\u5DF1\u4F1A\u6B7B\u5728\u4E00\u4E2A\u5185\u529F\u88AB\u5236\u7684\u5F31\u5973\u5B50\u624B\u4E2D\u3002</p><p>\u5341\u6708\u521D\u4E00\uFF0C\u662F\u970D\u72C2\u7130\u7684\u796D\u65E5\u3002</p><p>\u8FD9\u5929\u4E2D\u5348\uFF0C\u4ED6\u65E9\u65E9\u8D76\u5230\u795E\u6BBF\uFF0C\u7ECF\u5BAB\u4E3B\u7279\u8BB8\uFF0C\u51C6\u4E88\u8FDB\u5165\u5723\u5BAB\u63A5\u53D7\u6CBB\u7597\u3002\u4E5F\u8BB8\u5C31\u662F\u89E6\u72AF\u4E86\u5723\u5BAB\u4E0D\u8BB8\u62A4\u6CD5\u4EE5\u4E0B\u6559\u4F17\u8FDB\u5165\u7684\u7981\u4EE4\uFF0C\u4ED6\u518D\u4E5F\u6CA1\u80FD\u8D70\u51FA\u5723\u5BAB\u3002</p><p>\u5F00\u59CB\u4E00\u5207\u6B63\u5E38\uFF0C\u5728\u53F6\u884C\u5357\u8BE2\u95EE\u4ED6\u7528\u4F55\u7269\u4EE3\u66FF\u65F6\uFF0C\u970D\u72C2\u7130\u6BEB\u4E0D\u72B9\u8C6B\u5730\u9009\u62E9\u4E86\u9A6C\u97AD\u3002</p><p>\u300C\u6700\u597D\u662F\u62F3\u5934\u90A3\u4E48\u7C97\uFF0C\u80F3\u818A\u90A3\u4E48\u957F\u7684\u5927\u5BB6\u4F19\uFF01\u300D\u970D\u72C2\u7130\u6EE1\u8138\u7EA2\u5149\uFF0C\u53EA\u7B49\u6362\u4E0A\u4E00\u53EA\u9707\u70C1\u53E4\u4ECA\u7684\u5DE8\u7269\uFF0C\u597D\u64CD\u6B7B\u98CE\u665A\u534E\u90A3\u4E2A\u8BE5\u6B7B\u7684\u6B7B\u5A4A\u5B50\u3002</p><p>\u53F6\u884C\u5357\u7528\u9EBB\u6CB8\u6563\u5C06\u970D\u72C2\u7130\u8EAB\u4F53\u9EBB\u9189\u540E\uFF0C\u7B11\u5475\u5475\u53BB\u5BFB\u9A6C\u97AD\u3002\u4ED6\u4E5F\u6CA1\u60F3\u5230\u81EA\u5DF1\u7B2C\u4E00\u6B21\u6362\u9633\u624B\u672F\u4F1A\u5931\u8D25\u5F97\u8FD9\u4E48\u5F7B\u5E95\u3002</p><p>\u7247\u523B\u5DE5\u592B\uFF0C\u970D\u72C2\u7130\u5DF2\u7ECF\u5728\u5FC3\u91CC\u4E50\u5475\u5475\u7684\u8FDE\u7EED\u5978\u6B7B\u98CE\u665A\u534E\u4E24\u6B21\u3002\u7B49\u4ED6\u51C6\u5907\u7528\u53E3\u4EA4\u628A\u8FD9\u4E2A\u6B7B\u5A4A\u5B50\u6D3B\u6D3B\u564E\u6B7B\u7684\u65F6\u5019\uFF0C\u5FFD\u7136\u773C\u89D2\u4E00\u95EA\uFF0C\u6709\u4EBA\u8D70\u8FDB\u5BA4\u5185\u3002</p><p>\u9996\u5148\u6620\u5165\u773C\u5EC9\u7684\u662F\u4E00\u5BF9\u98A4\u5FAE\u5FAE\u7684\u8089\u7403\uFF0C\u9664\u4E86\u96EA\u5CF0\u795E\u5C3C\u90A3\u5BF9\u8C6A\u4E73\u4E4B\u5916\uFF0C\u4ED6\u8FD8\u6CA1\u89C1\u8FC7\u6709\u8C01\u80FD\u957F\u51FA\u8FD9\u6837\u5927\u7684\u5976\u5B50\uFF0C\u51B5\u4E14\u8FD9\u4EBA\u8EAB\u6750\u6BD4\u795E\u5C3C\u8981\u5A07\u5C0F\u73B2\u73D1\u5F97\u591A\u3002</p><p>\u4ED6\u7528\u529B\u7FFB\u8D77\u773C\u73E0\uFF0C\u60F3\u770B\u6E05\u7A76\u7ADF\u662F\u8C01\uFF0C\u597D\u8BA9\u5979\u4E5F\u5C1D\u5C1D\u81EA\u5DF1\u9A6C\u97AD\u7684\u5389\u5BB3\u3002\u5F53\u770B\u6E05\u6765\u4EBA\u7684\u9762\u5BB9\u540E\uFF0C\u4ED6\u7ACB\u5373\u6253\u6D88\u4E86\u8FD9\u4E2A\u5FF5\u5934\u3002</p><p>\u5982\u679C\u5929\u4E0B\u8FD8\u6709\u4E00\u4E2A\u4EBA\u662F\u4ED6\u7EDD\u5BF9\u4E0D\u80FD\u78B0\u7684\uFF0C\u90A3\u4E48\u5C31\u662F\u773C\u524D\u8FD9\u4E00\u4F4D\u2014\u2014\u661F\u6708\u6E56\u7684\u5C11\u592B\u4EBA\u4E86\u3002</p><p>\u4ED6\u7AED\u529B\u60F3\u6324\u51FA\u4E1D\u7B11\u5BB9\uFF0C\u6BD5\u7ADF\u4EE5\u524D\u66FE\u5F97\u7F6A\u8FC7\u5C11\u592B\u4EBA\uFF0C\u867D\u7136\u5979\u53EA\u662F\u5BAB\u4E3B\u7684\u73A9\u7269\uFF0C\u6709\u673A\u4F1A\u8FD8\u662F\u8981\u5C3D\u91CF\u641E\u597D\u5173\u7CFB\u3002</p><p>\u4E0D\u8FC7\u4ED6\u518D\u4E5F\u6CA1\u6709\u673A\u4F1A\u4E86\u3002</p><p>\u970D\u72C2\u7130\u5F88\u4E0D\u7406\u89E3\uFF0C\u4E3A\u4EC0\u4E48\u5C11\u592B\u4EBA\u4F1A\u6765\u6478\u81EA\u5DF1\uFF0C\u800C\u4E14\u6478\u8FC7\u4E4B\u540E\uFF0C\u4ECE\u80F8\u53E3\u5230\u4E0B\u9634\u7ADF\u7136\u50CF\u89E3\u5F00\u8863\u670D\u4E00\u6837\uFF0C\u6574\u6574\u9F50\u9F50\u88C2\u5F00\u4E00\u9053\u53E3\u5B50\u3002</p><p>\u5F53\u90A3\u9053\u53E3\u5B50\u5192\u51FA\u9C9C\u8840\u65F6\uFF0C\u970D\u72C2\u7130\u7EC8\u4E8E\u660E\u767D\u8FC7\u6765\u3002</p><p>\u4E00\u523B\u949F\u540E\u53F6\u884C\u5357\u63D0\u7740\u4E00\u6302\u957F\u957F\u7684\u9A6C\u97AD\u56DE\u5230\u77F3\u5BA4\uFF0C\u770B\u5230\u7684\u60C5\u51B5\u662F\u8FD9\u6837\u7684\uFF1A\u970D\u72C2\u7130\u88AB\u4EBA\u5F00\u80A0\u7834\u809A\uFF0C\u53EF\u601C\u7684\u662F\u8FD8\u6CA1\u6709\u6B7B\u3002\u5047\u5982\u53EA\u662F\u5982\u6B64\uFF0C\u53F6\u884C\u5357\u8FD8\u6709\u628A\u63E1\u5C06\u4F24\u53E3\u7F1D\u5408\uFF0C\u6551\u4ED6\u4E00\u6761\u6027\u547D\u3002\u4F46\u970D\u72C2\u7130\u66B4\u9732\u7684\u5185\u810F\u95F4\u8FD8\u5192\u7740\u9752\u70DF\u2014\u2014\u4E39\u7089\u7684\u70AD\u706B\u5F88\u6574\u9F50\u5730\u4ECE\u8D64\u88F8\u7684\u80F8\u9AA8\uFF0C\u4E00\u76F4\u6446\u653E\u5230\u76D8\u80A0\u4E0A\u3002</p><p>\u970D\u72C2\u7130\u76F4\u52FE\u52FE\u7684\u773C\u795E\uFF0C\u8BA9\u53F6\u884C\u5357\u4E5F\u4E0D\u7981\u6253\u4E86\u4E2A\u54C6\u55E6\uFF0C\u95ED\u5173\u4FEE\u70BC\u8FD8\u5929\u8BC0\u7684\u6155\u5BB9\u9F99\u4EC0\u4E48\u90FD\u6CA1\u8BF4\uFF0C\u53EA\u662F\u628A\u90A3\u4E2A\u9001\u6765\u7EC3\u529F\u7684\u5904\u5973\u6D3B\u6D3B\u5978\u6B7B\uFF0C\u7136\u540E\u5C06\u6EF4\u8840\u7684\u9633\u5177\u6345\u5165\u7D2B\u73AB\u809B\u5185\uFF0C\u628A\u5979\u5E72\u5F97\u91CD\u4F24\u660F\u8FF7\u3002\u6574\u4E2A\u8FC7\u7A0B\u4E2D\u4E24\u4EBA\u8C01\u90FD\u6CA1\u6709\u5F00\u53E3\u3002</p><p>\u7D2B\u73AB\u5728\u69BB\u4E0A\u8EBA\u4E86\u4E94\u5929\u624D\u80FD\u591F\u8D77\u8EAB\u3002\u53C8\u8FC7\u4E86\u4E94\u5929\uFF0C\u4E5E\u4F0F\u7A77\u9686\u3001\u767D\u6C0F\u59D0\u59B9\u3001\u5B89\u5B50\u5B8F\u5148\u540E\u56DE\u5230\u5BAB\u4E2D\uFF0C\u751F\u6D3B\u4ECD\u50CF\u4EE5\u5F80\u90A3\u6837\u5E73\u6DE1\u7684\u7EE7\u7EED\u4E0B\u53BB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u51E0\u4E4E\u8DB3\u4E0D\u51FA\u5BA4\uFF0C\u6BCF\u5929\u53EA\u62BD\u51FA\u4E00\u4E2A\u65F6\u8FB0\u4E0E\u8427\u4F5B\u5974\u7F20\u7EF5\u4E00\u756A\uFF0C\u7528\u7CBE\u6DB2\u6ECB\u6DA6\u6BCD\u4EB2\uFF0C\u518D\u9971\u996E\u4E00\u901A\u9C9C\u4E73\uFF0C\u7136\u540E\u624D\u5524\u6765\u7D2B\u73AB\u3002\u5BF9\u7D2B\u73AB\u4ED6\u61D2\u5F97\u518D\u53BB\u8BF4\u7B11\uFF0C\u66F4\u6CA1\u6709\u4E00\u4E1D\u6E29\u5B58\u548C\u601C\u60DC\uFF0C\u751A\u81F3\u4E0D\u662F\u628A\u5979\u5F53\u6210\u6CC4\u6B32\u5DE5\u5177\uFF0C\u800C\u4EC5\u4EC5\u662F\u4E00\u5177\u70BC\u529F\u7684\u9F0E\u7089\u3002\u6BCF\u6B21\u76F4\u63A5\u628A\u5979\u6309\u5728\u5730\u4E0A\u4E00\u901A\u72E0\u64CD\uFF0C\u6C72\u53D6\u9634\u7CBE\u540E\u5C31\u50CF\u5783\u573E\u4E00\u6837\u628A\u5979\u6254\u5F00\u3002</p><p>\u6BCF\u9694\u4E00\u65E5\uFF0C\u90FD\u4F1A\u6709\u4E00\u4E2A\u7F8E\u8C8C\u7684\u5904\u5B50\u88AB\u9001\u8FDB\u5BAB\u4E2D\uFF0C\u6709\u4E9B\u5F53\u573A\u5C31\u9999\u9500\u7389\u6B92\uFF0C\u6709\u4E9B\u8FD8\u80FD\u5269\u4E0B\u4E00\u53E3\u6C14\u3002\u5E78\u5B58\u7684\u5C11\u5973\u90FD\u88AB\u9001\u5F80\u9F99\u57CE\u52B3\u519B\u3002</p><p>\u8FD9\u4E00\u5207\u6155\u5BB9\u9F99\u90FD\u4E0D\u52A0\u7406\u4F1A\uFF0C\u4ED6\u660E\u767D\u81EA\u5DF1\u5F53\u521D\u662F\u9760\u91C7\u8865\u7EC3\u529F\uFF0C\u6839\u57FA\u5176\u5B9E\u751A\u6D45\uFF0C\u56E0\u6B64\u5FC3\u65E0\u65C1\u9E5C\u5730\u82E6\u4FEE\u592A\u4E00\u7ECF\u548C\u8FD8\u5929\u8BC0\uFF0C\u5C06\u4F53\u5185\u7684\u5404\u79CD\u771F\u6C14\u4E00\u4E00\u5316\u4E3A\u5DF1\u6709\u3002</p><p>\u56E0\u4E3A\u4E73\u623F\u7684\u7F18\u6545\uFF0C\u7D2B\u73AB\u4E5F\u5F88\u5C11\u51FA\u95E8\uFF0C\u53EA\u5076\u5C14\u4E0E\u6BCD\u4EB2\u804A\u5929\u89E3\u95F7\u3002\u6BCD\u5973\u76F8\u5BF9\u65F6\uFF0C\u603B\u662F\u5F3A\u989C\u6B22\u7B11\u7684\u65F6\u5019\u591A\u3002\u5F53\u521D\u8427\u4F5B\u5974\u770B\u5230\u5973\u513F\u8EAB\u4F53\u7684\u5F02\u5E38\uFF0C\u54ED\u4E86\u6574\u6574\u4E24\u5929\u3002\u6700\u540E\u5374\u5BF9\u6155\u5BB9\u9F99\u767E\u822C\u5949\u8FCE\uFF0C\u5C3D\u5C55\u5A9A\u6001\u3002</p><p>\u8FD9\u4E3E\u52A8\u7A76\u7ADF\u662F\u8BA8\u4ED6\u7684\u6B22\u5FC3\u6765\u4FDD\u62A4\u81EA\u5DF1\uFF0C\u8FD8\u662F\u60F3\u878D\u6D3D\u4E00\u5BB6\u4E09\u53E3\u7684\u5173\u7CFB\uFF0C\u8BA9\u513F\u5973\u80FD\u6B22\u597D\u5982\u521D\uFF0C\u8FDE\u5979\u81EA\u5DF1\u4E5F\u8BF4\u4E0D\u6E05\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u542C\u8BF4\u4E86\u970D\u72C2\u7130\u60E8\u6B7B\u7684\u60C5\u5F62\uFF0C\u5BF9\u4E73\u623F\u4E0E\u813E\u6C14\u540C\u65F6\u66B4\u6DA8\u7684\u5C11\u592B\u4EBA\u66F4\u662F\u656C\u800C\u8FDC\u4E4B\uFF0C\u8C01\u4E5F\u4E0D\u6562\u591A\u8BF4\u95F2\u8BDD\u3002\u56E0\u6B64\u9664\u4E86\u6BCF\u65E5\u4E00\u523B\u949F\u7684\u5C48\u8FB1\u4E4B\u5916\uFF0C\u7D2B\u73AB\u7684\u7A7A\u95F2\u65F6\u95F4\u5F88\u591A\u3002\u591A\u5230\u5979\u6709\u65F6\u95F4\u5B66\u4F1A\u7528\u9497\u7C2A\u6253\u5F00\u95E8\u9501\u3002</p><p>\u5979\u8BB0\u5F97\uFF0C\u81EA\u5DF1\u7B2C\u4E00\u6B21\u6253\u5F00\u541B\u5B57\u752C\u9053\u90A3\u5929\uFF0C\u662F\u5341\u4E00\u6708\u5341\u4E03\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u661F\u6708\u6E56\u603B\u6559\u4F4D\u4E8E\u7EC8\u5357\u5C71\u95F4\u6E56\u4E2D\u7684\u4E00\u4E2A\u5C9B\u5C7F\u4E0A\u3002\u5C9B\u4E0A\u6709\u4E24\u6EAA\u4E00\u5CF0\uFF0C\u5CF0\u4E0B\u662F\u4E0D\u77E5\u4F55\u65F6\u8425\u9020\u7684\u5E9E\u5927\u5730\u5BAB\u3002</p><p>\u5730\u5BAB\u5206\u6210\u4E94\u6761\u752C\u9053\uFF0C\u957F\u77ED\u4E0D\u4E00\uFF0C\u65B9\u5411\u5404\u5F02\uFF0C\u6B63\u4E2D\u662F\u653E\u7F6E\u592A\u6781\u56FE\u7684\u5927\u5385\u3002\u5929\u5B57\u752C\u9053\u957F\u8FD1\u4E94\u5341\u4E08\uFF0C\u5341\u95F4\u77F3\u5BA4\u4EE5\u5929\u5E72\u4E3A\u5E8F\uFF0C\u662F\u5BAB\u4E3B\u5C45\u5904\uFF1B\u5730\u5B57\u752C\u9053\u957F\u8FD1\u4E09\u5341\u4E08\uFF0C\u5341\u4E8C\u95F4\u77F3\u5BA4\u4EE5\u5730\u652F\u4E3A\u5E8F\uFF0C\u5404\u517B\u795E\u7269\uFF0C\u662F\u661F\u6708\u6E56\u884C\u5211\u4E4B\u5730\uFF1B\u4EB2\u5B57\u752C\u9053\u957F\u7EA6\u767E\u4E08\uFF0C\u4EE5\u94C1\u6805\u77F3\u95E8\u4E0E\u5723\u5BAB\u963B\u9694\uFF0C\u4EE5\u5929\u5E72\u4E3A\u5E8F\uFF0C\u662F\u6559\u4E2D\u6027\u5974\u63A5\u5BA2\u5904\uFF1B\u5E08\u5B57\u752C\u9053\u957F\u7EA6\u5341\u4E08\uFF0C\u4EE5\u5929\u5E72\u4E3A\u5E8F\uFF0C\u662F\u62A4\u6CD5\u5C45\u6240\u3002</p><p>\u7D2B\u73AB\u552F\u4E00\u6CA1\u6709\u5230\u8FC7\u7684\uFF0C\u5C31\u662F\u541B\u5B57\u752C\u9053\u3002</p><p>\u517B\u7236\u4E34\u7EC8\u6240\u7559\u4E0B\u7684\u9057\u8A00\u63D0\u9053\uFF1A\u300C\u8D3E\u94F6\u601D\u3001\u4E01\u8D35\u4E2D\u3002\u300D\u6309\u5929\u5730\u541B\u4EB2\u5E08\u7684\u987A\u5E8F\uFF0C\u5979\u5DF2\u7ECF\u5728\u5929\u5B57\u7532\u5BA4\u3001\u5730\u5B57\u5BC5\u5BA4\u3001\u4EB2\u5B57\u4E01\u5BA4\u548C\u5E08\u5B57\u7678\u5BA4\u5206\u522B\u627E\u5230\u56DB\u5E45\u76F8\u540C\u7684\u56FE\u5F62\u3002</p><p>\u90A3\u4E48\u5269\u4E0B\u7684\u4E00\u4E2A\uFF0C\u5C31\u662F\u5728\u541B\u5B57\u5DF3\u5BA4\u4E86\u3002</p><p>\u5F53\u79EF\u6EE1\u7070\u5C18\u7684\u5927\u9501\u300C\u5361\u300D\u7684\u6253\u5F00\uFF0C\u7D2B\u73AB\u7684\u5FC3\u810F\u4E5F\u8DF3\u5230\u5589\u5499\u91CC\u3002</p><p>\u6B64\u65F6\u6155\u5BB9\u9F99\u6B63\u5728\u70BC\u529F\u3001\u53F6\u884C\u5357\u548C\u6C90\u58F0\u4F20\u90FD\u5728\u5BAB\u5916\uFF0C\u767D\u6C0F\u59D0\u59B9\u6B63\u5728\u5E2E\u6BCD\u4EB2\u6309\u6469\u8EAB\u4F53\uFF0C\u4E0D\u4F1A\u6709\u4EBA\u53D1\u73B0\u81EA\u5DF1\u7684\u884C\u52A8\u3002\u7D2B\u73AB\u6697\u6697\u5438\u4E86\u53E3\u6C14\uFF0C\u4E3E\u6B65\u8E0F\u5165\u8FD9\u4E2A\u672A\u77E5\u5730\u57DF\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u77F3\u95E8\u6709\u767D\u6C0F\u59D0\u59B9\u6253\u626B\uFF0C\u8FD8\u7B97\u5E72\u51C0\uFF0C\u4F46\u770B\u9501\u5B54\u5806\u79EF\u7684\u7070\u5C18\uFF0C\u53EA\u6015\u4E00\u4E8C\u5341\u5E74\u90FD\u6CA1\u6709\u6253\u5F00\u8FC7\uFF0C\u597D\u5728\u7A7A\u6C14\u5E76\u4E0D\u6D51\u6D4A\u3002</p><p>\u7D2B\u73AB\u4E00\u624B\u6258\u7740\u660E\u73E0\uFF0C\u4E00\u624B\u6276\u7740\u8170\u80A2\uFF0C\u633A\u7740\u5C0F\u8179\u8E52\u8DDA\u5730\u884C\u8D70\u7740\u3002\u5176\u65F6\u5DF2\u521D\u51AC\uFF0C\u4E3A\u4E86\u884C\u52A8\u65B9\u4FBF\uFF0C\u5979\u53EA\u7A7F\u4E86\u4E00\u4EF6\u7FFB\u6BDB\u7684\u9526\u8884\u3002\u4E03\u4E2A\u6708\u7684\u8EAB\u5B55\u5DF2\u662F\u5927\u8179\u4FBF\u4FBF\uFF0C\u5706\u9F13\u9F13\u7684\u809A\u5B50\u906E\u6CA1\u4E86\u89C6\u7EBF\uFF0C\u8BA9\u5979\u770B\u4E0D\u89C1\u81EA\u5DF1\u843D\u811A\u7684\u5730\u65B9\u3002\u4E3A\u4E86\u4FDD\u6301\u8EAB\u4F53\u7684\u5E73\u8861\uFF0C\u5979\u4E0D\u5F97\u4E0D\u4E0A\u8EAB\u7565\u5FAE\u540E\u4EF0\uFF0C\u624B\u638C\u6491\u4F4F\u7EA4\u8170\uFF0C\u514D\u5F97\u8FC7\u91CD\u7684\u4E73\u623F\u548C\u809A\u5B50\u4F7F\u81EA\u5DF1\u8DCC\u5012\u3002</p><p>\u752C\u9053\u4E00\u8DEF\u5411\u4E0B\uFF0C\u4E0E\u5176\u5B83\u51E0\u6761\u5806\u780C\u6574\u9F50\u7684\u752C\u9053\u76F8\u6BD4\uFF0C\u8FD9\u50CF\u662F\u6761\u672A\u5B8C\u6210\u7684\u752C\u9053\u3002</p><p>\u8D70\u51FA\u6570\u5341\u4E08\u540E\uFF0C\u7D2B\u73AB\u8D6B\u7136\u53D1\u73B0\uFF0C\u4E00\u8DEF\u4E0A\u7ADF\u672A\u770B\u5230\u4E00\u95F4\u77F3\u5BA4\u3002\u518D\u8D70\u4E08\u8BB8\uFF0C\u811A\u4E0B\u7684\u8DEF\u5F84\u5F00\u59CB\u5D0E\u5C96\u8D77\u6765\uFF0C\u800C\u4E24\u65C1\u7684\u77F3\u58C1\u4E5F\u53D8\u6210\u5D99\u5CCB\u7684\u5CA9\u77F3\uFF0C\u4F3C\u4E4E\u662F\u8D70\u5230\u4E86\u4E00\u6761\u5E7D\u6697\u7684\u5730\u9053\u4E2D\u3002</p><p>\u5468\u56F4\u7684\u7A7A\u6C14\u6E10\u6E10\u6F6E\u6E7F\uFF0C\u7D2B\u73AB\u9ED8\u7B97\u8FDC\u8FD1\uFF0C\u6B64\u65F6\u5E94\u8BE5\u5DF2\u8D70\u5230\u661F\u6708\u6E56\u5E95\u4E86\u3002\u671B\u4E86\u671B\u6DF1\u4E0D\u5E95\u7684\u752C\u9053\uFF0C\u5FC3\u91CC\u4E0D\u7981\u6709\u4E9B\u5BB3\u6015\uFF0C\u5979\u4E3E\u8D77\u660E\u73E0\uFF0C\u85C9\u7740\u6DE1\u6DE1\u7684\u73E0\u8F89\uFF0C\u56DB\u4E0B\u6253\u91CF\u8FD9\u4E2A\u6D1E\u7A74\u3002</p><p>\u6D1E\u9876\u5F88\u9AD8\uFF0C\u4E0A\u9762\u7AF9\u7B0B\u822C\u751F\u7740\u949F\u4E73\u77F3\uFF0C\u6D1E\u58C1\u5E03\u6EE1\u6C34\u73E0\uFF0C\u5728\u73E0\u8F89\u4E0B\u6676\u83B9\u95EA\u70C1\uFF0C\u524D\u65B9\u9ED1\u6C89\u6C89\u770B\u4E0D\u5C3D\u5934\u2026\u2026</p><p>\u4E00\u80A1\u5BD2\u98CE\u63A0\u8FC7\uFF0C\u7D2B\u73AB\u6FC0\u7075\u6253\u4E86\u4E2A\u51B7\u6218\uFF0C\u660E\u73E0\u5DEE\u70B9\u6ED1\u843D\u3002\u5979\u5403\u529B\u5730\u8F6C\u8FC7\u8EAB\u4F53\uFF0C\u60F3\u56DE\u53BB\u4F11\u606F\u4E00\u4E0B\uFF0C\u660E\u5929\u518D\u6765\u3002</p><p>\u521A\u4E00\u8F6C\u8EAB\uFF0C\u7D2B\u73AB\u5FFD\u7136\u9192\u609F\uFF0C\u300C\u5BD2\u98CE\uFF1F\u600E\u4E48\u4F1A\u6709\u98CE\uFF1F\u96BE\u9053\u8FD9\u4E2A\u6D1E\u7A74\u662F\u901A\u5411\u5916\u9762\u7684\uFF1F\u300D</p><p>\u5979\u6025\u5FD9\u6276\u7740\u77F3\u58C1\u8F6C\u8FC7\u8EAB\u6765\uFF0C\u54AC\u7259\u671D\u6D1E\u5E95\u8D70\u53BB\u3002</p><p>\u7ED5\u8FC7\u4E00\u4E1B\u9AD8\u5927\u7684\u77F3\u7B0B\uFF0C\u6D1E\u58C1\u4E0A\u7A81\u7136\u51FA\u73B0\u4E24\u95F4\u5E76\u5217\u7684\u77F3\u95E8\uFF0C\u7D2B\u73AB\u8E2E\u8D77\u811A\u5C16\uFF0C\u628A\u660E\u73E0\u9AD8\u9AD8\u4E3E\u8FC7\u5934\u9876\uFF0C\u53EA\u89C1\u4E0A\u9762\u5206\u522B\u9542\u523B\u7740\u300C\u5B50\u300D\u3001\u300C\u4E11\u300D\u4E8C\u5B57\u3002\u77F3\u5BA4\u7ADF\u7136\u79BB\u5723\u5BAB\u8FD9\u4E48\u8FDC\uFF1F\u7D2B\u73AB\u5FC3\u4E0B\u7EB3\u95F7\uFF0C\u7F13\u7F13\u671D\u4E0B\u8D70\u53BB\u3002</p><p>\u4EE5\u5730\u652F\u4E3A\u5E8F\u7684\u77F3\u5BA4\u6BEB\u65E0\u89C4\u5F8B\u5730\u6563\u843D\u5728\u6D1E\u4E2D\uFF0C\u6216\u662F\u534A\u5929\u770B\u4E0D\u5230\u4E00\u95F4\uFF0C\u6216\u662F\u4E24\u4E09\u95F4\u805A\u5728\u4E00\u8D77\u3002\u8D70\u5230\u7B2C\u516D\u95F4\u65F6\uFF0C\u95E8\u4E0A\u6B63\u662F\u4E00\u4E2A\u5C0F\u5C0F\u7684\u300C\u5DF3\u300D\u5B57\u3002</p><p>\u7D2B\u73AB\u72B9\u8C6B\u4E86\u4E00\u4E0B\uFF0C\u51B3\u5B9A\u8D70\u4E0B\u53BB\u5148\u627E\u5230\u51FA\u53E3\u3002</p><p>\u5F53\u5979\u4F30\u8BA1\u81EA\u5DF1\u8D70\u51FA\u4E03\u91CC\u8FDC\u8FD1\u65F6\uFF0C\u9762\u524D\u51FA\u73B0\u7684\u662F\u4E00\u5757\u5DE8\u77F3\u3002\u5979\u8146\u7740\u809A\u5B50\uFF0C\u6123\u6123\u7AD9\u5728\u6BEB\u65E0\u7F1D\u9699\u7684\u5DE8\u77F3\u524D\uFF0C\u7A81\u7136\u4E00\u79CD\u4E0A\u5F53\u7684\u59D4\u5C48\u6CDB\u4E0A\u5FC3\u5934\uFF0C\u9F3B\u5B50\u4E00\u9635\u53D1\u9178\u3002\u826F\u4E45\uFF0C\u7D2B\u73AB\u63C9\u4E86\u63C9\u53D1\u7EA2\u7684\u773C\u775B\uFF0C\u62D6\u7740\u6C89\u91CD\u7684\u8EAB\u4F53\u8D70\u4E0A\u56DE\u7A0B\u3002</p><p>\u65E0\u8BBA\u5982\u4F55\uFF0C\u80FD\u627E\u5230\u6700\u540E\u4E00\u95F4\u77F3\u5BA4\uFF0C\u80FD\u89E3\u5F00\u5B9D\u85CF\u4E4B\u79D8\u5C31\u591F\u4E86\u3002\u7D2B\u73AB\u4E00\u8DEF\u5B89\u6170\u81EA\u5DF1\uFF0C\u6253\u70B9\u8D77\u7CBE\u795E\u3002\u5373\u4F7F\u5982\u6B64\uFF0C\u8D70\u5230\u300C\u5DF3\u300D\u5BA4\u5979\u4E5F\u7D2F\u5F97\u7CBE\u75B2\u529B\u5C3D\uFF0C\u8EAB\u4E0A\u6E7F\u6E7F\u7684\u5C3D\u662F\u6C57\u6C34\u3002</p><p>\u7D2B\u73AB\u501A\u5728\u95E8\u4E0A\u6B47\u606F\u7247\u523B\uFF0C\u7136\u540E\u626C\u8D77\u7693\u8155\uFF0C\u62D4\u4E0B\u94F6\u9497\u3002</p><p>\u5979\u95ED\u4E0A\u773C\uFF0C\u5C4F\u606F\u51DD\u795E\uFF0C\u7528\u5FC3\u5206\u8FA8\u6307\u5C16\u7684\u7EC6\u5FAE\u611F\u89C9\u3002</p><p>\u4E00\u67F1\u9999\u5DE5\u592B\u540E\uFF0C\u9501\u5B54\u300C\u5361\u55D2\u300D\u4E00\u58F0\u8F7B\u54CD\u3002\u58F0\u97F3\u867D\u8F7B\uFF0C\u7D2B\u73AB\u5374\u5982\u91CA\u91CD\u8D1F\u7684\u957F\u957F\u51FA\u4E86\u53E3\u6C14\uFF0C\u5979\u633A\u8D77\u8170\u8EAB\uFF0C\u64A9\u8D77\u79C0\u53D1\u4ED4\u7EC6\u76D8\u597D\uFF0C\u7136\u540E\u7528\u7EDD\u4EE3\u7684\u98CE\u534E\u6B3E\u6B3E\u63A8\u5F00\u77F3\u95E8\u3002</p><p>\u77F3\u5BA4\u51FA\u4E4E\u610F\u6599\u7684\u72ED\u7A84\uFF0C\u9876\u591A\u53EA\u5BB9\u4E24\u4EBA\u5E76\u80A9\u800C\u7ACB\uFF0C\u6DF1\u4EC5\u4E09\u5C3A\u3002\u4F46\u5BF9\u7D2B\u73AB\u6765\u8BF4\uFF0C\u6700\u4E3B\u8981\u7684\u95EE\u9898\u662F\uFF1A\u77F3\u58C1\u4E0A\u5149\u6E9C\u6E9C\u7684\uFF0C\u522B\u8BF4\u7EB9\u9970\uFF0C\u8FDE\u4E00\u9053\u5212\u75D5\u90FD\u6CA1\u6709\u3002</p><p>\u7D2B\u73AB\u6574\u4E2A\u4EBA\u90FD\u50BB\u6389\u4E86\u3002</p><p>\u9ED1\u6697\u7684\u6D1E\u7A74\u91CC\uFF0C\u4E00\u4E2A\u8C8C\u82E5\u5929\u4ED9\u7684\u5C11\u5973\uFF0C\u5C0F\u5634\u5F20\u5F97\u6D51\u5706\uFF0C\u773C\u775B\u77AA\u5F97\u6BD4\u5634\u8FD8\u5706\uFF0C\u4E00\u624B\u6258\u7740\u5149\u8292\u95EA\u8000\u7684\u660E\u73E0\uFF0C\u4E00\u624B\u6276\u7740\u8170\u8EAB\uFF0C\u90A3\u79CD\u6115\u7136\u7684\u5A07\u4FCF\u6A21\u6837\u8DB3\u4EE5\u8BA9\u4EFB\u4F55\u4E00\u4E2A\u4EBA\u54D1\u7136\u5931\u7B11\u3002\u4F46\u5979\u773C\u4E2D\u6D53\u6D53\u7684\u4F24\u611F\u548C\u5931\u843D\uFF0C\u8FD8\u6709\u6DF1\u6DF1\u7684\u75B2\u5026\uFF0C\u5374\u50CF\u5229\u7BAD\u822C\u76F4\u523A\u5230\u4EBA\u4EEC\u5FC3\u5E95\u6700\u67D4\u8F6F\u7684\u90E8\u4F4D\u3002</p><p>\u51B5\u4E14\u5979\u8FD8\u8270\u96BE\u5730\u633A\u7740\u5C0F\u8179\uFF0C\u633A\u7740\u4E0E\u5C0F\u8179\u540C\u6837\u6C89\u91CD\u7684\u53CC\u4E73\u3002\u62D6\u7740\u8FD9\u6837\u7684\u8EAB\u4F53\uFF0C\u6BCF\u8D70\u4E00\u6B65\u5BF9\u5979\u6765\u8BF4\u90FD\u662F\u6298\u78E8\u3002\u4ED8\u51FA\u6570\u500D\u4E8E\u5E73\u5E38\u4EBA\u7684\u8F9B\u82E6\u4E4B\u540E\uFF0C\u7ED3\u679C\u5374\u4E00\u65E0\u6240\u6709\uFF0C\u90A3\u79CD\u7A7A\u8361\u8361\u5730\u5931\u8D25\u611F\uFF0C\u8F7B\u6613\u4FBF\u6495\u788E\u4E86\u5979\u7684\u575A\u5F3A\u3002</p><p>\u50CF\u662F\u4E0E\u73E0\u5149\u4E89\u8F89\uFF0C\u6676\u83B9\u7684\u6CEA\u6C34\u65AD\u7EBF\u7684\u73E0\u5B50\u822C\uFF0C\u4ECE\u5C11\u5973\u773C\u4E2D\u5954\u6D8C\u6D8C\u51FA\u3002</p>',57);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
