import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_109" tabindex="-1"><a class="header-anchor" href="#_109" aria-hidden="true">#</a> 109</h1><p>\u300C\u5982\u592B\u4EBA\uFF0C\u600E\u4E48\u62C9\u5C4E\u4E5F\u4E0D\u544A\u8BC9\u5974\u5A62\u4E00\u58F0\uFF1F\u300D\u867D\u7136\u81EA\u79F0\u5974\u5A62\uFF0C\u542C\u53E3\u6C14\u5012\u50CF\u662F\u4E3B\u5B50\u8D23\u602A\u5974\u4EC6\u4E00\u6837\u3002</p><p>\u8427\u4F5B\u5974\u5782\u4E0B\u776B\u6BDB\uFF0C\u50CF\u4E2A\u505A\u9519\u4E8B\u7684\u5B69\u5B50\u8F7B\u8F7B\u54AC\u7740\u5634\u5507\u3002</p><p>\u300C\u88C5\u4EC0\u4E48\u50BB\u5462\uFF01\u300D\u767D\u7389\u9E42\u51B7\u54FC\u4E00\u58F0\uFF0C\u5FEB\u624B\u5FEB\u811A\u5730\u89E3\u4E0B\u5C3F\u5E03\uFF0C\u987A\u52BF\u53C8\u5728\u8427\u4F5B\u5974\u5706\u6DA6\u7684\u7F8E\u81C0\u4E0A\u626D\u4E86\u4E00\u628A\u3002</p><p>\u8427\u4F5B\u5974\u602F\u751F\u751F\u9053\uFF1A\u300C\u597D\u59D0\u59D0\uFF0C\u662F\u6211\u4E0D\u5BF9\u2026\u2026\u300D</p><p>\u300C\u54DF\uFF0C\u4F60\u662F\u4E3B\u5B50\u7684\u5C0F\u59BE\uFF0C\u4E5F\u662F\u5974\u5A62\u7684\u534A\u4E2A\u4E3B\u5B50\uFF0C\u53EB\u59D0\uFF0C\u5974\u5A62\u600E\u4E48\u6562\u5F53\u5462\uFF1F\u300D</p><p>\u767D\u7389\u9E42\u89E3\u4E0B\u5C3F\u5E03\uFF0C\u5E76\u6CA1\u6709\u7ED9\u8427\u4F5B\u5974\u64E6\u53BB\u81C0\u4E0A\u7684\u6C61\u7269\uFF0C\u53CD\u800C\u628A\u6795\u5934\u585E\u5230\u5979\u9AD8\u9686\u7684\u8179\u4E0B\uFF0C\u8BA9\u5979\u6485\u7740\u810F\u516E\u516E\u7684\u5C41\u80A1\u8DB4\u5728\u5E8A\u4E0A\u3002</p><p>\u8427\u4F5B\u5974\u79C0\u76EE\u542B\u6CEA\uFF0C\u5979\u77E5\u9053\u600E\u4E48\u8BA8\u7537\u4EBA\u4EEC\u7684\u6B22\u5FC3\uFF0C\u5374\u4E0D\u77E5\u9053\u5982\u4F55\u4E0E\u8FD9\u4E24\u4E2A\u5974\u5A62\u76F8\u5904\u3002\u767D\u6C0F\u59D0\u59B9\u5374\u50CF\u4E0E\u5979\u6709\u6DF1\u4EC7\u5927\u6068\u4E00\u6837\uFF0C\u6BCF\u6BCF\u6A2A\u52A0\u6C61\u8FB1\u3002\u5979\u66FE\u88AB\u8FD9\u6837\u653E\u8FC7\u4E00\u6574\u5929\uFF0C\u5230\u513F\u5B50\u51FA\u5173\u65F6\u624D\u88AB\u6E05\u7406\u5E72\u51C0\u3002\u90A3\u65F6\u6C61\u7269\u5DF2\u7ECF\u5E72\u5728\u81C0\u4E0A\uFF0C\u6700\u540E\u4E0D\u5F97\u4E0D\u7528\u6E29\u6C34\u628A\u5B83\u6CE1\u5F00\u6D17\u51C0\u3002</p><p>\u4E00\u60F3\u5230\u81EA\u5DF1\u5C55\u89C8\u822C\u6485\u7740\u7F9E\u5904\uFF0C\u8BA9\u6EE1\u81C0\u7684\u6C61\u7269\u5728\u7A7A\u6C14\u4E2D\u6162\u6162\u98CE\u5E72\uFF0C\u8427\u4F5B\u5974\u5C31\u7F9E\u6124\u6B32\u6B7B\u3002\u90A3\u79CD\u5B64\u96F6\u96F6\u8DB4\u5728\u51B0\u51B7\u7684\u77F3\u5BA4\u5185\uFF0C\u5373\u6E3A\u5C0F\u53C8\u65E0\u52A9\u7684\u51C4\u51C9\uFF0C\u771F\u662F\u5BD2\u5F7B\u5FC3\u5E95\u3002</p><p>\u5979\u51E0\u6B21\u60F3\u5BF9\u513F\u5B50\u8BF4\u4E24\u5973\u5BF9\u81EA\u5DF1\u7684\u4E0D\u5C0A\u91CD\uFF0C\u4F46\u4E00\u65B9\u9762\u7F9E\u4E8E\u542F\u9F7F\uFF0C\u4E00\u65B9\u9762\u4E0D\u613F\u518D\u88AB\u5176\u4ED6\u4EBA\u770B\u5230\u81EA\u5DF1\u7684\u803B\u6001\u2014\u2014\u8BF4\u4E0D\u5B9A\u53E6\u6362\u7684\u5A62\u5973\u6BD4\u5979\u4EEC\u66F4\u7C97\u66B4\u3002\u6BD5\u7ADF\u81EA\u5DF1\u53EA\u662F\u59BE\u4F8D\u7684\u8EAB\u4EFD\uFF0C\u4E0D\u4F46\u56DB\u80A2\u4FF1\u5E9F\uFF0C\u800C\u4E14\u968F\u65F6\u8FD8\u4F1A\u5931\u7981\u2026\u2026\u8FD9\u6837\u7684\u8EAB\u4F53\u8FD8\u80FD\u6307\u671B\u522B\u4EBA\u7684\u5C0A\u91CD\u5417\uFF1F\u8427\u4F5B\u5974\u67D4\u80A0\u767E\u8F6C\uFF0C\u53EA\u597D\u9006\u6765\u987A\u53D7\uFF0C\u59D4\u5C48\u6C42\u5168\u3002</p><p>\u767D\u7389\u83BA\u671D\u8427\u4F5B\u5974\u81C0\u4E0A\u4E00\u62CD\uFF0C\u300C\u53C8\u4E3E\u7740\u5C41\u80A1\u6328\u64CD\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u4F4E\u53EB\u4E00\u58F0\uFF0C\u62BD\u7740\u9F3B\u5B50\u8BF4\uFF1A\u300C\u597D\u59D0\u59D0\uFF0C\u6C42\u4F60\u5E2E\u6211\u64E6\u5E72\u51C0\u5427\u2026\u2026\u300D</p><p>\u300C\u88C5\u4EC0\u4E48\u88C5\uFF01\u9A9A\u8D27\uFF01\u300D\u767D\u7389\u83BA\u54AC\u7259\u9A82\u9053\u3002</p><p>\u5E08\u5A18\u7684\u60E8\u6B7B\u5BF9\u59D0\u59B9\u4FE9\u7684\u6253\u51FB\u6781\u5927\u3002\u5BF9\u5979\u4EEC\u800C\u8A00\u4ECE\u6B64\u4E4B\u540E\uFF0C\u8FD9\u4E16\u4E0A\u518D\u6CA1\u6709\u4EFB\u4F55\u4EB2\u4EBA\uFF0C\u4E5F\u518D\u6CA1\u6709\u4EFB\u4F55\u5E0C\u671B\u3002\u5982\u540C\u4E00\u5207\u5931\u53BB\u4E86\u4FE1\u5FF5\u652F\u6491\u7684\u4EBA\u4E00\u6837\uFF0C\u5979\u4EEC\u5BF9\u5F3A\u8005\u66F2\u610F\u5949\u8FCE\uFF0C\u5BF9\u5F31\u8005\u5219\u6063\u610F\u51CC\u8FB1\uFF0C\u5728\u8D70\u6295\u65E0\u8DEF\u7684\u5730\u7A9F\u4E2D\u6323\u624E\u7740\u6C42\u5B58\uFF0C\u65E0\u7AEF\u7684\u53D1\u6CC4\u3002</p><p>\u800C\u6700\u4F73\u7684\u53D1\u6CC4\u5BF9\u8C61\uFF0C\u83AB\u8FC7\u4E8E\u8FD9\u4E2A\u67D4\u5F31\u7684\u7F8E\u5987\u4E86\u3002\u4E00\u6BB5\u6BEB\u65E0\u53CD\u6297\u80FD\u529B\u7684\u7F8E\u8089\uFF0C\u5BF9\u67D0\u4E9B\u4EBA\u6765\u8BF4\uFF0C\u503C\u5F97\u4E07\u822C\u601C\u60DC\uFF1B\u800C\u5BF9\u53E6\u4E00\u4E9B\u4EBA\u6765\u8BF4\uFF0C\u5219\u662F\u4E00\u5177\u65BD\u8650\u7684\u5999\u7269\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u4E0E\u6155\u5BB9\u5144\u59B9\u4E4B\u95F4\u6709\u7740\u679D\u7F20\u8513\u7ED5\uFF0C\u6570\u4E0D\u5C3D\u7684\u91CD\u91CD\u6069\u6028\uFF0C\u65E2\u6709\u5931\u8D1E\u3001\u53D7\u8FB1\u3001\u4E27\u4EB2\u7B49\u7B49\u5207\u9F7F\u4E4B\u75DB\uFF0C\u53C8\u6709\u56E0\u80CC\u53DB\u7684\u6127\u610F\u8F6C\u6362\u800C\u6765\u7684\u4EC7\u89C6\u4E0E\u654C\u610F\uFF0C\u8FD8\u6709\u4E00\u4E9B\u83AB\u540D\u7684\u5E7D\u6028\uFF0C\u79CD\u79CD\u96BE\u89E3\u7684\u60C5\u7EEA\u79EF\u90C1\u4E8E\u5FC3\uFF0C\u6709\u673A\u4F1A\u4FBF\u5728\u8427\u4F5B\u5974\u8EAB\u4E0A\u4E00\u53E4\u8111\u53D1\u6CC4\u51FA\u6765\u3002</p><p>\u5979\u662F\u5BAB\u4E3B\u7684\u4EB2\u5A18\uFF0C\u8981\u66FF\u513F\u5B50\u7684\u7F6A\u5B7D\u8FD8\u503A\uFF1B\u5979\u662F\u5C11\u592B\u4EBA\u7684\u4EB2\u5A18\uFF0C\u8981\u56E0\u5973\u513F\u7684\u50B2\u6162\u53D7\u7F5A\uFF1B\u800C\u4E14\u5979\u8FD8\u662F\u5BAB\u4E3B\u7684\u5C0F\u59BE\uFF0C\u593A\u8D70\u4E86\u5BAB\u4E3B\u7684\u5BA0\u7231\u2026\u2026</p><p>\u8427\u4F5B\u5974\u867D\u7136\u67D4\u5F31\uFF0C\u4F46\u4E5F\u662F\u4E2A\u806A\u6167\u5973\u5B50\uFF0C\u5373\u4F7F\u4E0D\u6E05\u695A\u8FD9\u91CC\u9762\u7684\u79CD\u79CD\u7F18\u7531\uFF0C\u4E5F\u80FD\u611F\u89C9\u5230\u5979\u4EEC\u7684\u6068\u610F\u3002\u751A\u81F3\u8FD8\u80FD\u611F\u89C9\u5230\u59D0\u59B9\u4FE9\u5E76\u975E\u751F\u6027\u5982\u6B64\uFF0C\u56E0\u6B64\u5979\u4E00\u5473\u5730\u4F4E\u58F0\u4E0B\u6C14\uFF0C\u5E0C\u671B\u7528\u81EA\u5DF1\u7684\u67D4\u987A\u6765\u5316\u89E3\u5979\u4EEC\u7684\u66B4\u623E\u3002</p><p>\u8FD9\u4E00\u6401\u5C31\u662F\u534A\u4E2A\u65F6\u8FB0\uFF0C\u5BA4\u5185\u867D\u4E0D\u751A\u51B7\uFF0C\u8EAB\u5A07\u4F53\u5F31\u7684\u7F8E\u5987\u8FD8\u662F\u51BB\u5F97\u745F\u745F\u53D1\u6296\uFF0C\u6C61\u7269\u5728\u96EA\u7389\u822C\u7684\u81C0\u95F4\u5E72\u7ED3\u53D1\u786C\uFF0C\u4F20\u6765\u4E00\u9635\u9635\u96BE\u582A\u7684\u523A\u75D2\u3002</p><p>\u300C\u59D0\uFF0C\u4E0D\u4F1A\u6709\u4E8B\u5427\uFF1F\u300D\u767D\u7389\u9E42\u6084\u6084\u8BF4\u3002</p><p>\u767D\u7389\u83BA\u61D2\u6D0B\u6D0B\u7741\u5F00\u79C0\u76EE\uFF0C\u779F\u4E86\u8427\u4F5B\u5974\u7684\u809A\u5B50\u4E00\u773C\uFF0C\u63D0\u9AD8\u58F0\u97F3\u8BF4\uFF1A\u300C\u7BA1\u5979\u5462\u3002\u53CD\u6B63\u4E3B\u5B50\u4E5F\u770B\u4E0D\u4E0A\u8FD9\u4E2A\u9A9A\u8D27\u809A\u91CC\u7684\u4E1C\u897F\u2014\u2014\u6307\u4E0D\u5B9A\u662F\u4EC0\u4E48\u602A\u7269\u5462\u3002\u300D</p><p>\u767D\u7389\u9E42\u7B11\u9053\uFF1A\u300C\u592B\u4EBA\u7684\u5C41\u80A1\u597D\u767D\u54E6\u2026\u2026\u6211\u8981\u662F\u4E2A\u7537\u4EBA\uFF0C\u4E5F\u60F3\u5F04\u5F04\u8FD9\u4E2A\u5927\u5C41\u80A1\u5462\u3002\u300D</p><p>\u300C\u60F3\u5F04\u8FD8\u4E0D\u5BB9\u6613\uFF1F\u300D\u767D\u7389\u83BA\u7AD9\u8D77\u6765\u4F38\u4E2A\u61D2\u8170\uFF0C\u6F2B\u4E0D\u7ECF\u5FC3\u5730\u9053\uFF1A\u300C\u592B\u4EBA\uFF0C\u54B1\u4EEC\u59D0\u59B9\u60F3\u5F04\u5F04\u4F60\u7684\u5C41\u80A1\uFF0C\u53EF\u4EE5\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u6CA1\u6709\u4F5C\u58F0\uFF0C\u53EA\u628A\u7389\u8138\u85CF\u5230\u88AB\u8925\u4E2D\u3002</p><p>\u767D\u7389\u83BA\u7FD8\u817F\u5750\u4E0B\uFF0C\u4E3E\u676F\u559D\u4E86\u4E00\u53E3\uFF0C\u987A\u624B\u5C06\u6B8B\u8336\u6CFC\u5728\u8427\u4F5B\u5974\u81C0\u95F4\u3002\u3010w\uFF37w.\u8D3C\u5427\uFF3Aei8\u3002\uFF23om\u7535\u5B50\u4E66\u3011</p><p>\u96EA\u767D\u7684\u81C0\u8089\u4E00\u9635\u6218\u6817\uFF0C\u7247\u523B\u540E\u8427\u4F5B\u5974\u4F4E\u58F0\u9053\uFF1A\u300C\u4E24\u4F4D\u59D0\u59D0\u5E2E\u6211\u64E6\u4E00\u4E0B\u5427\uFF0C\u4E00\u4F1A\u513F\u4ED6\u2026\u2026\u4ED6\u5C31\u8981\u51FA\u5173\u4E86\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u51B7\u54FC\u4E00\u58F0\uFF0C\u300C\u62FF\u5BAB\u4E3B\u6765\u5413\u6211\u5417\uFF1F\u8FD8\u6709\u534A\u4E2A\u65F6\u8FB0\u5462\uFF0C\u4F60\u5C31\u633A\u7740\u4E00\u5C41\u80A1\u5C4E\u6162\u6162\u7B49\u5427\u3002\u300D</p><p>\u513F\u5B50\u4E0D\u5728\uFF0C\u5973\u513F\u4E5F\u4E0D\u5728\uFF0C\u88AB\u629B\u5F03\u7684\u6050\u60E7\u6E10\u6E10\u6ECB\u957F\uFF0C\u5F53\u6B8B\u8336\u4E5F\u9010\u6E10\u5E72\u6DB8\uFF0C\u7F8E\u5987\u518D\u65E0\u6CD5\u5FCD\u53D7\u4E24\u5973\u6C89\u9ED8\u7684\u538B\u529B\uFF0C\u7528\u8FD1\u4E4E\u7EDD\u671B\u7684\u58F0\u97F3\u62BD\u564E\u9053\uFF1A\u300C\u4F60\u4EEC\u2026\u2026\u4F60\u4EEC\u8981\u600E\u4E48\u5F04\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u62CD\u624B\u7B11\u9053\uFF1A\u300C\u59D0\u59D0\u7528\u68CD\u5B50\u50CF\u5BAB\u4E3B\u90A3\u6837\u6345\u4F60\uFF0C\u4F60\u5C31\u50CF\u5E73\u65F6\u90A3\u6837\u5F00\u5F00\u5FC3\u5FC3\u5730\u53EB\u7ED9\u59D0\u59D0\u542C\uFF0C\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u4E0D\u2026\u2026\u4E0D\u597D\u2026\u2026\u300D\u8427\u4F5B\u5974\u54ED\u9053\uFF0C\u8FD9\u79CD\u5C48\u8FB1\u7684\u4E3E\u52A8\u8FDE\u9F99\u54E5\u54E5\u90FD\u4E0D\u4F1A\u8BA9\u5979\u505A\uFF0C\u4F55\u51B5\u662F\u88AB\u4E24\u4E2A\u5974\u5A62\u73A9\u5F04\u3002</p><p>\u300C\u556A\u300D\uFF0C\u767D\u7389\u83BA\u62FF\u7740\u4E00\u6839\u4E0D\u77E5\u4ECE\u54EA\u91CC\u627E\u6765\u7684\u68CD\u5B50\uFF0C\u91CD\u91CD\u6253\u5728\u8427\u4F5B\u5974\u81C0\u4E0A\u3002\u80A5\u7F8E\u96EA\u81C0\u4E00\u5F39\uFF0C\u6D6E\u51FA\u4E00\u9053\u7EA2\u5370\u3002</p><p>\u300C\u54CE\u5440\uFF01\u300D\u8427\u4F5B\u5974\u75DB\u53EB\u5931\u58F0\u3002</p><p>\u300C\u4E0D\u8BB8\u53EB\uFF01\u300D\u767D\u7389\u83BA\u538B\u7740\u55D3\u5B50\u4E00\u58F0\u5389\u559D\uFF0C\u7F8E\u5987\u7ACB\u5373\u5664\u58F0\uFF0C\u53EA\u4ECE\u5C0F\u5DE7\u7684\u7389\u9F3B\u53D1\u51FA\u75DB\u82E6\u7684\u547B\u541F\u3002</p><p>\u53C8\u6328\u4E86\u51E0\u4E0B\uFF0C\u8427\u4F5B\u5974\u7EC8\u4E8E\u6CE3\u58F0\u9053\uFF1A\u300C\u522B\u6253\u4E86\uFF0C\u6211\u613F\u610F\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u5F97\u610F\u7684\u4E00\u7B11\uFF0C\u6728\u68D2\u786C\u6886\u6886\u9876\u5728\u6CBE\u6EE1\u6C61\u7269\u7684\u81C0\u7F1D\u5185\uFF0C\u7565\u4E00\u7528\u529B\uFF0C\u4FBF\u50CF\u6345\u5165\u4E00\u56E2\u6ED1\u817B\u7684\u6CB9\u8102\u822C\u6ED1\u5165\u83CA\u809B\u3002</p><p>\u81EA\u5DF1\u5A07\u7F8E\u7684\u8EAB\u5B50\u4E00\u5411\u88AB\u7537\u4EBA\u89C6\u82E5\u73CD\u5B9D\uFF0C\u601C\u7231\u4E07\u5206\uFF0C\u51E0\u66FE\u88AB\u8FD9\u6837\u73A9\u5F04\u3002\u8427\u4F5B\u5974\u75DB\u803B\u96BE\u5F53\uFF0C\u7389\u8138\u901A\u7EA2\uFF0C\u809B\u4E2D\u4E00\u75BC\uFF0C\u6728\u68D2\u7C97\u66B4\u5730\u6405\u52A8\u7740\uFF0C\u767D\u7389\u83BA\u559D\u9053\uFF1A\u300C\u53EB\u554A\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u67D4\u9888\u5FAE\u98A4\uFF0C\u534A\u664C\u624D\u5E72\u5DF4\u5DF4\u5730\u4F4E\u53EB\u4E00\u58F0\u3002</p><p>\u767D\u7389\u83BA\u4E00\u6345\u5230\u5E95\uFF0C\u9A82\u9053\uFF1A\u300C\u9A9A\u8D27\uFF01\u4F60\u4E0D\u662F\u6700\u559C\u6B22\u88AB\u4EBA\u6345\u5C41\u773C\u513F\u4E86\u5417\uFF1F\u4E3B\u5B50\u64CD\u4F60\u7684\u65F6\u5019\u53EB\u5F97\u591A\u6D6A\u554A\uFF0C\u8FD9\u4F1A\u513F\u88C5\u4EC0\u4E48\u8282\u5987\u5462\uFF01\u597D\u597D\u53EB\uFF01\u300D</p><p>\u300C\u554A\uFF0C\u554A\u554A\u2026\u2026\u300D\u67D4\u5A9A\u800C\u51C4\u695A\u5730\u53EB\u58F0\u4E2D\uFF0C\u6CBE\u6EE1\u6C61\u7269\u7684\u6728\u68CD\u5728\u81C0\u95F4\u76F4\u8FDB\u76F4\u51FA\uFF0C\u83CA\u6D1E\u7FD5\u5408\uFF0C\u96EA\u81C0\u95F4\u4E00\u7247\u72FC\u85C9\u3002</p><p>\u767D\u7389\u83BA\u6363\u4E86\u7247\u523B\uFF0C\u628A\u6728\u68D2\u4EA4\u7ED9\u767D\u7389\u9E42\uFF0C\u81EA\u5DF1\u62FF\u7740\u6BDB\u5DFE\u5408\u7740\u8427\u4F5B\u5974\u7684\u6D6A\u53EB\uFF0C\u4E00\u677F\u4E00\u773C\u5730\u64E6\u6D17\u8D77\u6765\u3002</p><p>\u6700\u521D\u7684\u75BC\u75DB\u8FC7\u53BB\u4E4B\u540E\uFF0C\u6C81\u51FA\u871C\u6C41\u7684\u809B\u8089\u4E60\u60EF\u4E86\u6728\u68D2\u7684\u7C97\u7EC6\u548C\u575A\u786C\uFF0C\u8427\u4F5B\u5974\u7684\u53EB\u58F0\u4E2D\u6E10\u6E10\u6709\u4E86\u4E00\u4E1D\u6B22\u6109\u3002</p><p>\u300C\u6162\u70B9\u513F\u2026\u2026\u597D\u4E86\u3002\u300D\u767D\u7389\u83BA\u6307\u70B9\u7740\u628A\u6728\u68D2\u5E26\u51FA\u7684\u6C61\u7269\u64E6\u51C0\uFF0C\u9119\u5937\u5730\u8BF4\u9053\uFF1A\u300C\u8FD9\u4E48\u810F\u2026\u2026\u4E3B\u5B50\u64CD\u8D77\u6765\u8FD8\u4E0D\u6076\u5FC3\u6B7B\uFF1F\u5C41\u773C\u513F\u7528\u529B\uFF01\u628A\u810F\u4E1C\u897F\u90FD\u62C9\u51FA\u6765\u3002\u300D</p><p>\u8427\u4F5B\u5974\u53C8\u7F9E\u53C8\u6127\uFF0C\u7AED\u529B\u6536\u7F29\uFF0C\u4F46\u8F6F\u5F31\u5730\u809B\u8089\u5374\u50CF\u4E00\u5F20\u65E0\u529B\u7684\u5C0F\u5634\uFF0C\u4F7F\u4E0D\u4E0A\u4E00\u70B9\u529B\u6C14\u3002</p><p>\u767D\u7389\u83BA\u4E0D\u8010\u70E6\u8D77\u6765\uFF0C\u4E00\u628A\u63EA\u4F4F\u7F8E\u5987\u7684\u53D1\u9AFB\uFF0C\u8D34\u5728\u5979\u8033\u8FB9\u9A82\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u8FD9\u4E48\u7B28\uFF01\u767D\u957F\u4E86\u8FD9\u4E48\u5927\u7684\u5C41\u80A1\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u5782\u6CEA\u9053\uFF1A\u300C\u6211\u2026\u2026\u6211\u2026\u2026\u300D</p><p>\u300C\u54A6\uFF1F\u300D\u767D\u7389\u83BA\u5947\u602A\u5730\u770B\u7740\u7F8E\u5987\u80F8\u524D\u3002\u9E45\u9EC4\u7684\u9526\u7F0E\u4E0A\u5370\u7740\u4E24\u56E2\u6E7F\u75D5\uFF0C\u5979\u4E00\u626F\u79C0\u53D1\uFF0C\u8427\u4F5B\u5974\u4E0A\u8EAB\u62AC\u8D77\uFF0C\u8DF3\u52A8\u7684\u5706\u4E73\u661F\u661F\u70B9\u70B9\u6E85\u51FA\u51E0\u6EF4\u4E73\u767D\u7684\u6DB2\u4F53\u3002</p><p>\u8427\u4F5B\u5974\u8EAB\u4E0B\u57AB\u7740\u6795\u5934\uFF0C\u8EAB\u5B50\u7684\u91CD\u91CF\u90FD\u538B\u5728\u80F8\u524D\uFF0C\u88AB\u4E24\u5973\u6345\u5F04\u534A\u664C\uFF0C\u6B64\u65F6\u4E0D\u81EA\u89C9\u5730\u53C8\u6C81\u8D77\u4E73\u6765\u3002</p><p>\u300C\u597D\u50CF\u5934\u5976\u725B\u54E6\u3002\u300D\u767D\u7389\u9E42\u51D1\u8FC7\u6765\u8BF4\u9053\u3002</p><p>\u5F53\u4E0B\u4E24\u5973\u6258\u7740\u8427\u4F5B\u5974\u8F6F\u7EF5\u7EF5\u5730\u8EAB\u4F53\uFF0C\u4E00\u4EBA\u62FF\u7740\u4E00\u53EA\u4E73\u563B\u563B\u54C8\u54C8\u5730\u6324\u5F04\u8D77\u6765\u3002</p><p>\u8427\u4F5B\u5974\u96BE\u582A\u5730\u4FA7\u8FC7\u8138\uFF0C\u5979\u88AB\u6446\u7740\u8DEA\u5750\u7684\u59FF\u52BF\uFF0C\u5A07\u8EAF\u540E\u4EF0\uFF0C\u9AD8\u633A\u7684\u7389\u4E73\u88AB\u634F\u5F97\u4E0D\u4F4F\u53D8\u5F62\uFF0C\u6BB7\u7EA2\u7684\u4E73\u5934\u5976\u6C41\u56DB\u6EA2\uFF0C\u4E0D\u591A\u65F6\u4E73\u95F4\u4FBF\u4E00\u7247\u6DCB\u6F13\uFF0C\u6D53\u767D\u7684\u6C41\u6DB2\u9ECF\u4E4E\u4E4E\u6CBE\u6EE1\u53CC\u4E73\u3002\u5979\u4E00\u8FB9\u5FCD\u53D7\u4E73\u4E0A\u7684\u75BC\u75DB\uFF0C\u4E00\u8FB9\u8FD8\u62C5\u5FC3\u4E24\u5973\u4E0D\u5C0F\u5FC3\u677E\u5F00\u624B\uFF0C\u63D2\u5728\u809B\u91CC\u7684\u68CD\u5B50\u4F1A\u523A\u7A7F\u81EA\u5DF1\u7684\u80A0\u9053\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6B63\u73A9\u5F97\u9AD8\u5174\uFF0C\u752C\u9053\u5185\u7A81\u7136\u54CD\u8D77\u4E00\u9635\u6C89\u91CD\u7684\u811A\u6B65\u58F0\u3002\u58F0\u97F3\u867D\u8F7B\uFF0C\u4F46\u5728\u8EAB\u5177\u516B\u6781\u95E8\u5185\u529F\u7684\u4E24\u5973\u8033\u4E2D\u5374\u5206\u5916\u6E05\u6670\uFF0C\u4E24\u4EBA\u8FDE\u5FD9\u653E\u4E0B\u8427\u4F5B\u5974\uFF0C\u6296\u624B\u62D4\u51FA\u6728\u68CD\uFF0C\u585E\u5230\u8925\u4E0B\uFF0C\u7136\u540E\u62FF\u7740\u6BDB\u5DFE\u88C5\u6A21\u4F5C\u6837\u5730\u7ED9\u7F8E\u5987\u64E6\u6D17\u3002</p><p>\u7D2B\u73AB\u8D39\u529B\u5730\u63A8\u5F00\u77F3\u95E8\uFF0C\u4E00\u773C\u770B\u53BB\u987F\u65F6\u52C3\u7136\u5927\u6012\u3002\u5979\u6258\u7740\u5C0F\u8179\u632A\u5230\u6BCD\u4EB2\u69BB\u524D\uFF0C\u4E00\u638C\u6253\u5728\u767D\u7389\u83BA\u8138\u4E0A\u3002</p><p>\u9762\u5BF9\u7D2B\u73AB\u5168\u65E0\u5185\u529B\u7684\u4E00\u638C\uFF0C\u767D\u7389\u83BA\u81EA\u53EF\u8F7B\u677E\u907F\u5F00\uFF0C\u4F46\u5979\u4E00\u6BEB\u4E5F\u4E0D\u6562\u52A8\uFF0C\u751A\u81F3\u4E0D\u6562\u8FD0\u529F\u62A4\u4F53\u3002</p><p>\u300C\u556A\u300D\uFF0C\u660E\u51C0\u7684\u8138\u5E9E\u4E0A\u7559\u4E0B\u4E94\u9053\u6307\u5370\u3002\u7D2B\u73AB\u6012\u9A82\u9053\uFF1A\u300C\u6211\u5A18\u6000\u7740\u5B69\u5B50\uFF0C\u4F60\u4EEC\u600E\u4E48\u6562\u8BA9\u5979\u8DB4\u7740\uFF1F\u627E\u6B7B\u5417\uFF1F\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u8FDE\u5FD9\u5C06\u8427\u4F5B\u5974\u7FFB\u8F6C\u8FC7\u6765\uFF0C\u4E00\u53E5\u8BDD\u4E5F\u4E0D\u6562\u8BF4\u3002</p><p>\u7D2B\u73AB\u770B\u5230\u6BCD\u4EB2\u80F8\u524D\u7684\u4E73\u6C41\uFF0C\u5FC3\u91CC\u53C8\u75DB\u53C8\u6012\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u8DEA\u4E0B\uFF01\u638C\u5634\uFF01\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u987A\u4ECE\u5730\u8DEA\u5728\u69BB\u4FA7\uFF0C\u626C\u624B\u671D\u81EA\u5DF1\u8138\u4E0A\u6253\u53BB\u3002</p><p>\u6E05\u8106\u5730\u638C\u63B4\u4E00\u58F0\u58F0\u54CD\u8D77\uFF0C\u8427\u4F5B\u5974\u60CA\u6050\u5730\u770B\u4E86\u4E24\u4EBA\u4E00\u773C\uFF0C\u8FDE\u5FD9\u9053\uFF1A\u300C\u4E0D\u602A\u5979\u4EEC\u2026\u2026\u662F\u6211\u8BA9\u5979\u4EEC\u8FD9\u6837\u7684\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5750\u5728\u6BCD\u4EB2\u8EAB\u65C1\uFF0C\u62E7\u7740\u7B28\u91CD\u7684\u8EAB\u5B50\u5E2E\u5979\u64E6\u53BB\u4E73\u6C41\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u4E0D\u7528\u7406\u8FD9\u4E24\u4E2A\u8D31\u4EBA\u3002\u300D\u63A5\u7740\u53C8\u57CB\u6028\u9053\uFF1A\u300C\u5A18\uFF0C\u5DF2\u7ECF\u516B\u4E2A\u6708\u4E86\uFF0C\u4F60\u5C0F\u5FC3\u4E00\u4E9B\u3002\u5B69\u5B50\u65E0\u6240\u8C13\uFF0C\u4F60\u4E07\u4E00\u6709\u4EC0\u4E48\u95EA\u5931\u53EF\u600E\u4E48\u662F\u597D\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u65E0\u5948\u5730\u70B9\u70B9\u5934\uFF0C\u4F46\u5973\u513F\u8BF4\u7684\u300C\u5B69\u5B50\u65E0\u6240\u8C13\u300D\u8BA9\u5979\u4E0D\u671F\u7136\u60F3\u8D77\u4E24\u5973\u8BF4\u8FC7\u8BDD\u2014\u2014\u9F99\u54E5\u54E5\u771F\u7684\u4E0D\u559C\u6B22\u6211\u7ED9\u4ED6\u751F\u5B69\u5B50\u5417\uFF1F</p><p>\u767D\u6C0F\u59D0\u59B9\u6068\u5F97\u54AC\u7259\u5207\u9F7F\uFF0C\u4F46\u5728\u5C11\u592B\u4EBA\u9762\u524D\u5374\u4E0D\u6562\u6D41\u9732\u5206\u6BEB\u3002\u4E24\u4EBA\u5BF9\u8427\u4F5B\u5974\u521A\u624D\u7684\u5F00\u8131\u6BEB\u4E0D\u9886\u60C5\uFF0C\u53CD\u800C\u628A\u8FD9\u7B14\u5E10\u53C8\u8BB0\u5230\u5979\u5934\u4E0A\u3002</p>',61);function t(i,_){return a}var d=e(r,[["render",t]]);export{d as default};
