import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_11" tabindex="-1"><a class="header-anchor" href="#_11" aria-hidden="true">#</a> 11</h1><p>\u6155\u5BB9\u7D2B\u73AB\u4E00\u53E3\u6C14\u7EB5\u9A6C\u72C2\u5954\u516D\u5341\u4F59\u91CC\uFF0C\u5B9E\u5728\u575A\u6301\u4E0D\u4F4F\u624D\u505C\u4E0B\u6765\u89C5\u5730\u7597\u4F24\u3002\u4E00\u8FB9\u8C03\u606F\u4E00\u8FB9\u601D\u7D22\uFF1A\u5982\u679C\u4E00\u8DEF\u6362\u9A6C\uFF0C\u56DB\u5929\u4E4B\u540E\u80FD\u8D76\u5230\u4E34\u909B\u3002\u867D\u7136\u4E0D\u6E05\u695A\u661F\u6708\u6E56\u5728\u4EC0\u4E48\u5730\u65B9\uFF0C\u4F46\u770B\u6C90\u58F0\u4F20\u8FDE\u4E0D\u53CA\u53EC\u5524\u5E2E\u624B\uFF0C\u5DF4\u8700\u5E94\u8BE5\u4E0D\u662F\u9B54\u5BAB\u7684\u52BF\u529B\u8303\u56F4\uFF0C\u5230\u65F6\u4E0E\u54E5\u54E5\u6155\u5BB9\u80DC\u3001\u5AC2\u5AC2\u6797\u9999\u8FDC\u4E00\u8D77\u51FA\u624B\uFF0C\u5B9A\u53EF\u6551\u51FA\u4E09\u5E08\u59D0\u3002</p><p>\u6C90\u58F0\u4F20\u5B64\u8EAB\u4E00\u4EBA\uFF0C\u5373\u4F7F\u6728\u5802\u5E2E\u5C5E\u9F50\u81F3\uFF0C\u6700\u4E0D\u6D4E\u4E09\u4EBA\u4E5F\u53EF\u8131\u8EAB\u3002\u5F85\u6551\u56DE\u7EAA\u5E08\u59D0\u540E\uFF0C\u518D\u4E00\u540C\u56DE\u98D8\u6885\u5CF0\u3002\u8BF7\u5E08\u7236\u548C\u5927\u5E08\u59D0\u4E0B\u5C71\uFF0C\u628A\u8FD9\u4E9B\u755C\u7272\u65A9\u5C3D\u6740\u7EDD\uFF0C\u62A5\u4EC7\u96EA\u6068\uFF01</p><p>\u5979\u6068\u6068\u7684\u5728\u77F3\u4E0A\u62CD\u4E86\u4E00\u638C\uFF0C\u4F24\u5904\u53C8\u662F\u4E00\u9635\u5267\u75DB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5DF2\u88AB\u6298\u78E8\u591A\u65E5\u7684\u8D35\u5987\u660F\u8FF7\u822C\u6C89\u6C89\u5165\u7761\uFF0C\u8FDE\u8EAB\u8FB9\u4E24\u4E2A\u4EBA\u7684\u4EA4\u5408\u3001\u6323\u624E\u90FD\u6CA1\u6709\u628A\u5979\u60CA\u9192\u3002</p><p>\u5BAB\u4E3B\u90FD\u7D27\u7D27\u538B\u5728\u8F7B\u5C18\u8EAB\u4E0A\uFF0C\u90A3\u6839\u5996\u5F02\u7684\u8089\u68D2\u5728\u89E6\u624B\u7684\u52A8\u4F5C\u4E0B\u4E0D\u65AD\u8FDB\u51FA\u7740\u5438\u53D6\u4E39\u7530\u5185\u7684\u771F\u6C14\u3002\u5C06\u8FD1\u4E24\u4E2A\u65F6\u8FB0\u4E4B\u540E\uFF0C\u4ED6\u624D\u62AC\u8D77\u8EAB\u6765\uFF0C\u5FAE\u5FAE\u4E00\u7B11\u3002</p><p>\u661F\u6708\u6E56\u5341\u4E8C\u9999\u4E3B\u4E4B\u4E00\u7684\u8F7B\u5C18\u8138\u8272\u96EA\u767D\uFF0C\u88AB\u5438\u5E72\u4E86\u7CBE\u5143\u7684\u8EAB\u4F53\u50CF\u5931\u53BB\u6C34\u4EFD\u7684\u82B1\u6735\u822C\u6194\u60B4\u3002</p><p>\u5BAB\u4E3B\u76D8\u819D\u5C06\u5438\u53D6\u6765\u7684\u7CBE\u5143\u5316\u5F52\u5DF1\u6709\uFF0C\u826F\u4E45\u624D\u7741\u5F00\u773C\u775B\uFF0C\u4F38\u6307\u9694\u7A7A\u671D\u77F3\u58C1\u4E0A\u5D4C\u7740\u7684\u94F6\u94C3\u4E00\u5F39\u3002</p><p>\u300C\u53EE\u300D\u7684\u4E00\u58F0\u6E05\u54CD\u4E4B\u540E\uFF0C\u4E00\u4E2A\u8138\u8272\u9752\u9EC4\u7684\u8001\u8005\u51FA\u73B0\u5728\u7389\u95E8\u65C1\u3002</p><p>\u5BAB\u4E3B\u8D77\u8EAB\u62B1\u62F3\uFF0C\u606D\u656C\u5730\u53EB\u4E86\u58F0\uFF1A\u300C\u53F6\u62A4\u6CD5\u3002\u300D</p><p>\u53F6\u884C\u5357\u4E00\u8A00\u4E0D\u53D1\u5730\u8D70\u5230\u5E8A\u524D\uFF0C\u4E00\u6307\u6309\u4F4F\u8F7B\u5C18\u8109\u95E8\u4E0A\u63A2\u4E86\u7247\u523B\uFF0C\u53EA\u8BF4\u4E86\u4E00\u53E5\uFF1A\u300C\u6B64\u5973\u6B66\u529F\u5DF2\u5E9F\u3002\u300D</p><p>\u5BAB\u4E3B\u9053\uFF1A\u300C\u8FD8\u52B3\u53F6\u62A4\u6CD5\u5904\u7406\u3002\u300D</p><p>\u53F6\u884C\u5357\u70B9\u4E86\u70B9\u5934\uFF0C\u6B63\u5F85\u53D6\u8FC7\u90A3\u4E2A\u5782\u6B7B\u7684\u5973\u5B50\uFF0C\u5BAB\u4E3B\u53C8\u8BF4\u9053\uFF1A\u300C\u8BF7\u53F6\u62A4\u6CD5\u770B\u770B\u5979\u7684\u60C5\u5F62\u3002\u300D</p><p>\u53F6\u884C\u5357\u5207\u4E86\u8427\u4F5B\u5974\u7684\u8109\u8C61\uFF0C\u4ECE\u6000\u4E2D\u53D6\u51FA\u4E24\u4E2A\u836F\u74F6\uFF0C\u300C\u9EC4\u8272\u5916\u6577\uFF0C\u7EA2\u8272\u548C\u725B\u4E73\u670D\u7528\uFF0C\u660E\u65E5\u5373\u53EF\u75CA\u7652. \u300D</p><p>\u5BAB\u4E3B\u628A\u767E\u82B1\u89C2\u97F3\u8840\u8FF9\u6591\u6591\u7684\u4E0B\u4F53\u64E6\u6D17\u5E72\u51C0\uFF0C\u7136\u540E\u6577\u4E0A\u836F\uFF0C\u53C8\u53D6\u6765\u725B\u4E73\u8C03\u597D\u836F\u6C41\uFF0C\u5C0F\u5FC3\u5730\u5582\u5230\u5979\u5634\u91CC\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u7761\u4E86\u8FD1\u56DB\u4E2A\u65F6\u8FB0\uFF0C\u6B64\u65F6\u60A0\u60A0\u9192\u8F6C\u3002\u770B\u6E05\u5BAB\u4E3B\u82CD\u767D\u7684\u9762\u5B54\uFF0C\u9999\u8273\u6210\u719F\u7684\u8EAB\u4F53\u7ACB\u523B\u8737\u7F29\u8D77\u6765\uFF0C\u60CA\u6050\u5730\u7741\u5927\u7F8E\u76EE\uFF0C\u4E0D\u77E5\u4ED6\u53C8\u8981\u600E\u4E48\u6298\u78E8\u81EA\u5DF1\u3002</p><p>\u5BAB\u4E3B\u8F7B\u8F7B\u653E\u4E0B\u7389\u7897\uFF0C\u521A\u60F3\u9732\u51FA\u4E2A\u6E29\u67D4\u7684\u7B11\u5BB9\uFF0C\u53C8\u8BB0\u8D77\u81EA\u5DF1\u7684\u6BD5\u751F\u6068\u4E8B\uFF0C\u4FBF\u51B7\u54FC\u4E00\u58F0\uFF0C\u6DE1\u6DE1\u8BF4\uFF1A\u300C\u4F60\u9192\u4E86\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u773C\u7736\u4E00\u7EA2\uFF0C\u542B\u6CEA\u8BF4\u9053\uFF1A\u300C\u4F60\u6740\u4E86\u6211\u5427\u2026\u2026\u522B\u518D\u6298\u78E8\u6211\u4E86\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u4FEF\u8EAB\u628A\u5979\u62B1\u5728\u6000\u4E2D\uFF0C\u9976\u662F\u4ED6\u73A9\u5F04\u8FC7\u65E0\u6570\u5973\u4EBA\uFF0C\u6B64\u65F6\u624B\u81C2\u89E6\u5230\u8427\u4F5B\u5974\u67D4\u8F6F\u7684\u4E73\u8089\uFF0C\u8FD8\u662F\u5FC3\u5934\u6FC0\u8361\u3002\u4ED6\u6697\u6697\u5438\u4E86\u53E3\u6C14\uFF0C\u7A33\u4F4F\u60C5\u7EEA\uFF0C\u6DE1\u6DE1\u8BF4\uFF1A\u300C\u6211\u5E26\u4F60\u89C1\u4E00\u4E2A\u4EBA\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u60CA\u53EB\u9053\uFF1A\u300C\u73AB\u513F\uFF1F\u4F60\u4EEC\u6293\u5230\u5979\u4E86\uFF1F\u300D\u8F6C\u5FF5\u4E00\u60F3\uFF0C\u53C8\u95EE\u9053\uFF1A\u300C\u96BE\u9053\u662F\u80DC\u513F\uFF1F\u4ED6\u8FD9\u4E48\u5FEB\u5C31\u56DE\u6765\u4E86\uFF1F\u300D</p><p>\u5BAB\u4E3B\u988C\u4E0B\u4E00\u7D27\uFF0C\u6CA1\u6709\u8BF4\u8BDD\uFF0C\u8FF3\u76F4\u62B1\u7740\u5979\u8D70\u5230\u5BA4\u89D2\u3002</p><p>\u5BA4\u89D2\u653E\u7740\u4E00\u4E2A\u56DB\u56DB\u65B9\u65B9\u6728\u7BB1\u822C\u7684\u4E1C\u897F\uFF0C\u4E0A\u9762\u76D6\u7740\u4E00\u5757\u9ED1\u7EF8\u3002\u5F53\u5BAB\u4E3B\u62C9\u4E0B\u9ED1\u7EF8\u65F6\uFF0C\u767E\u82B1\u89C2\u97F3\u4E0D\u7531\u5931\u58F0\u60CA\u547C\u3002</p><p>\u9ED1\u7EF8\u4E0B\u662F\u4E00\u4E2A\u9AD8\u53CA\u8170\u8EAB\u7684\u6728\u53F0\uFF0C\u53F0\u4E0A\u4F0F\u7740\u4E00\u4E2A\u8D64\u88F8\u7684\u5973\u4EBA\u3002\u4E24\u811A\u5E76\u5728\u4E00\u8D77\uFF0C\u53E0\u653E\u5728\u4E30\u6EE1\u7684\u5927\u817F\u4E0B\uFF0C\u624B\u81C2\u5DE6\u53F3\u4F38\u5C55\uFF0C\u5C31\u50CF\u4E00\u53EA\u505A\u6210\u6807\u672C\u7684\u8774\u8776\uFF0C\u88AB\u91D1\u7B8D\u7262\u7262\u56FA\u5B9A\u3002</p><p>\u9999\u5AE9\u7684\u808C\u80A4\u886C\u5728\u4E4C\u9ED1\u7684\u6728\u53F0\u4E0A\uFF0C\u663E\u5F97\u5176\u767D\u5982\u96EA\uFF0C\u6D51\u5706\u7684\u7389\u81C0\u671D\u4E0A\u62AC\u8D77\uFF0C\u81C0\u7F1D\u4E2D\u5206\uFF0C\u7C89\u7EA2\u7684\u809B\u6D1E\u548C\u8273\u7EA2\u7684\u8089\u7A74\u5C3D\u9732\u5728\u5916\u3002\u5355\u662F\u80CC\u5F71\uFF0C\u4FBF\u770B\u5F97\u51FA\u8FD9\u4E2A\u88AB\u803B\u8FB1\u56DA\u7981\u7684\u5973\u5B50\u5B9A\u662F\u7EDD\u8272\u4F73\u4EBA\u3002</p><p>\u5BAB\u4E3B\u628A\u767E\u82B1\u89C2\u97F3\u653E\u5728\u53F0\u65C1\u7684\u9AD8\u6905\u4E0A\uFF0C\u7136\u540E\u4E00\u633A\u5DE8\u9633\uFF0C\u63D2\u8FDB\u8273\u5973\u6ED1\u5AE9\u7684\u8089\u7A74\u3002</p><p>\u4ED6\u4F3C\u4E4E\u5BF9\u5979\u6000\u6709\u6DF1\u4EC7\u5927\u6068\uFF0C\u539F\u672C\u5C31\u5C3A\u5BF8\u60CA\u4EBA\u7684\u5927\u8089\u68D2\u5728\u4ED6\u523B\u610F\u65BD\u5C55\u4E0B\u7C97\u5982\u513F\u81C2\uFF0C\u904D\u5E03\u7684\u9897\u7C92\u3001\u8089\u523A\u5B8C\u5168\u7AD6\u8D77\uFF0C\u51E0\u4E4E\u8981\u628A\u7C89\u5AE9\u7684\u96EA\u81C0\u6345\u6210\u4E24\u534A\u3002</p><p>\u6839\u90E8\u7684\u89E6\u624B\u8F6E\u72B6\u5F20\u8D77\uFF0C\u5C06\u672C\u6765\u5C31\u5FEB\u88AB\u626F\u88C2\u7684\u9634\u6237\u5B8C\u5168\u6491\u5F00\uFF0C\u67D4\u7F8E\u7684\u82B1\u74E3\u5C42\u5C42\u7EFD\u653E\uFF0C\u5C31\u50CF\u4E00\u6735\u56F4\u7740\u5DE8\u9633\u5F00\u653E\u7684\u8089\u82B1\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u8FD8\u662F\u7B2C\u4E00\u6B21\u89C1\u5230\u8FD9\u822C\u5F62\u72B6\u7684\u9633\u5177\uFF0C\u89C1\u4ED6\u72C2\u731B\u7684\u52A8\u4F5C\uFF0C\u76F4\u5413\u5F97\u624B\u811A\u53D1\u98A4\u3002\u5982\u679C\u6362\u4F5C\u81EA\u5DF1\uFF0C\u8FD9\u7B49\u7C97\u7EC6\u7684\u9633\u5177\u53EA\u8FDB\u5165\u4E00\u534A\uFF0C\u5C31\u4F1A\u628A\u9634\u9053\u6495\u788E\uFF0C\u4F55\u51B5\u6345\u5230\u6839\u90E8\u5462\u3002</p><p>\u4F0F\u5728\u53F0\u4E0A\u7684\u8273\u5973\u5374\u50CF\u662F\u4E0D\u77E5\u75BC\u75DB\uFF0C\u53EA\u4E56\u4E56\u6446\u7740\u59FF\u52BF\u4EFB\u4ED6\u62BD\u9001\uFF0C\u7EC6\u767D\u7684\u624B\u6307\u9759\u7389\u822C\u7EB9\u4E1D\u4E0D\u52A8\u3002</p><p>\u5BAB\u4E3B\u4F3C\u4E4E\u53EA\u662F\u4E3A\u4E86\u8BA9\u5979\u75DB\u82E6\uFF0C\u5728\u8089\u7A74\u5185\u6298\u78E8\u7247\u523B\u4FBF\u628A\u9F9F\u5934\u62B5\u5728\u83CA\u809B\u4E0A\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u773C\u775B\u4E00\u4E0B\u5B50\u77AA\u5F97\u8001\u5927\uFF0C\u5979\u600E\u4E48\u90FD\u4E0D\u76F8\u4FE1\u8FD9\u4E48\u7C97\u5927\u7684\u8089\u68D2\u80FD\u8FDB\u5165\u90A3\u4E48\u7A84\u5C0F\u7684\u540E\u5EAD\u3002\u53EF\u9F9F\u5934\u5728\u5979\u7684\u6CE8\u89C6\u4E0B\u7F13\u6162\u4F46\u6BEB\u4E0D\u8FDF\u7591\u7684\u6324\u5165\u809B\u6D1E\uFF0C\u767D\u751F\u751F\u7684\u81C0\u8089\u5411\u4E24\u8FB9\u5206\u5F00\uFF0C\u5DE8\u9633\u4F3C\u4E4E\u76F4\u63A5\u523A\u5165\u81C0\u8089\uFF0C\u6CA1\u6709\u4E00\u4E1D\u7F1D\u9699\u3002\u8427\u4F5B\u5974\u50CF\u662F\u88AB\u90A3\u6839\u9633\u5177\u63D2\u5165\u81EA\u5DF1\u4F53\u5185\u822C\u6218\u6817\u8D77\u6765\uFF0C\u83CA\u809B\u9635\u9635\u53D1\u7D27\u3002</p><p>\u6B64\u65F6\u5979\u4E0D\u4F1A\u77E5\u9053\uFF0C\u6709\u4E00\u5929\u81EA\u5DF1\u4F1A\u4E5E\u6C42\u8FD9\u6839\u9633\u5177\u585E\u6EE1\u81EA\u5DF1\u7684\u83CA\u809B\u3002</p><p>\u8089\u68D2\u8270\u96BE\u7684\u585E\u5165\u4E00\u534A\uFF0C\u4E2D\u95F4\u7684\u8089\u7624\u88AB\u6321\u5728\u809B\u6D1E\u5916\u3002\u5BAB\u4E3B\u7565\u4E00\u6536\u529F\uFF0C\u7C97\u5927\u7684\u8089\u68D2\u7ACB\u523B\u53D8\u7EC6\uFF0C\u8170\u8179\u4E00\u633A\uFF0C\u8089\u7624\u4E0A\u7684\u5012\u523A\u987A\u5229\u5730\u6ED1\u5165\u540E\u5EAD\u3002\u63A5\u7740\u8089\u68D2\u53C8\u6062\u590D\u4E86\u539F\u6765\u7684\u7C97\u7EC6\uFF0C\u7D27\u7D27\u5730\u5361\u4F4F\u83CA\u95E8\uFF0C\u6CA1\u6709\u4E00\u4E1D\u7F1D\u9699\u3002</p><p>\u5BAB\u4E3B\u4F38\u624B\u4ECE\u8273\u5973\u80F8\u524D\u626F\u51FA\u4E00\u56E2\u6CB9\u5AE9\u7684\u4E73\u8089\uFF0C\u6307\u5C16\u6390\u7740\u4E73\u5934\u7528\u529B\u5411\u5916\u626F\u52A8\u3002\u4E73\u7403\u88AB\u626F\u6210\u5C16\u9525\u72B6\uFF0C\u7EA2\u8272\u7684\u4E73\u5C16\u51E0\u4E4E\u5FEB\u8981\u88AB\u63EA\u4E0B\u6765\u3002</p><p>\u300C\u62FF\u7740\u3002\u300D\u5BAB\u4E3B\u628A\u90A3\u7C92\u4E73\u5934\u9012\u5230\u767E\u82B1\u89C2\u97F3\u624B\u8FB9\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u8FDE\u5FD9\u628A\u4E24\u624B\u85CF\u5230\u80CC\u540E\uFF0C\u62FC\u547D\u6447\u7740\u5934\u3002\u4E73\u623F\u672C\u662F\u5973\u4EBA\u6700\u654F\u611F\u7684\u5668\u5B98\u4E4B\u4E00\uFF0C\u88AB\u8FD9\u6837\u73A9\u5F04\uFF0C\u90A3\u5973\u4EBA\u80AF\u5B9A\u4F1A\u75DB\u5F97\u53D7\u4E0D\u4E86\u3002</p><p>\u5BAB\u4E3B\u51B7\u7B11\u4E00\u4E0B\uFF0C\u624B\u6307\u4E00\u677E\uFF0C\u4E73\u5934\u7ACB\u523B\u5F39\u4E86\u56DE\u53BB\uFF0C\u80A5\u767D\u7684\u8089\u7403\u5728\u8EAB\u4E0A\u5267\u70C8\u5730\u8DF3\u52A8\u7740\u3002\u4ED6\u62AC\u8EAB\u9000\u51FA\u9633\u5177\uFF0C\u7C89\u7EA2\u8272\u7684\u809B\u8089\u88AB\u8089\u523A\u52FE\u7684\u7FFB\u5377\u51FA\u6765\uFF0C\u9732\u51FA\u4E00\u622A\u9C9C\u7EA2\u7684\u80A0\u9053\u3002</p><p>\u7B49\u9F9F\u5934\u79BB\u5F00\uFF0C\u5F39\u6027\u5341\u8DB3\u7684\u809B\u8089\u7ACB\u523B\u6536\u7D27\uFF0C\u6062\u590D\u4E86\u539F\u6765\u7684\u8FF7\u4EBA\u6A21\u6837\u3002</p><p>\u5BAB\u4E3B\u624B\u6307\u5728\u82B1\u8482\u4E0A\u8F7B\u8F7B\u4E00\u78B0\uFF0C\u8089\u7A74\u4E00\u9635\u6025\u98A4\uFF0C\u55B7\u51FA\u4E00\u80A1\u6D53\u767D\u7684\u9634\u7CBE\u3002\u4ED6\u62CD\u4E86\u62CD\u624B\uFF0C\u5FAE\u7B11\u7740\u62B1\u8D77\u6905\u4E0A\u7684\u7F8E\u5987\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u6839\u672C\u65E0\u529B\u53CD\u6297\u4E86\uFF0C\u4F46\u90A3\u6839\u8089\u68D2\u5B9E\u5728\u592A\u4EE4\u4EBA\u6050\u60E7\u4E86\u3002\u5979\u98A4\u58F0\u4E5E\u6C42\u9053\uFF1A\u300C\u4F60\u8BA9\u6211\u6B7B\u5427\u2026\u2026\u300D</p><p>\u5BAB\u4E3B\u8138\u8272\u4E00\u6C89\uFF0C\u6296\u624B\u628A\u5979\u4E22\u5728\u5E8A\u4E0A\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u5982\u679C\u4E0D\u60F3\u53D8\u6210\u90A3\u6837\u5C31\u81EA\u5DF1\u5F20\u5F00\u817F\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u4E24\u624B\u6342\u7740\u8138\u653E\u58F0\u5927\u54ED\uFF0C\u4E24\u817F\u8BA4\u547D\u7684\u6162\u6162\u5F20\u5F00\uFF0C\u7EFD\u9732\u51FA\u4F24\u52BF\u672A\u7652\u7684\u4E0B\u4F53\u3002\u5BAB\u4E3B\u4F38\u624B\u6491\u5728\u767E\u82B1\u89C2\u97F3\u8138\u4FA7\uFF0C\u5C06\u5A01\u731B\u65E0\u4FE6\u7684\u9633\u5177\u4F38\u5230\u5979\u4E24\u817F\u4E4B\u95F4\uFF0C\u5BF9\u51C6\u8089\u7A74\u51B7\u559D\u9053\uFF1A\u300C\u770B\u7740\u6211\uFF01\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u4E24\u624B\u54C6\u55E6\u4E00\u4E0B\uFF0C\u7F13\u7F13\u5206\u5F00\uFF0C\u9732\u51FA\u6EE1\u662F\u6CEA\u5149\u7684\u7F8E\u8273\u9762\u5BB9\u3002\u5979\u6CEA\u773C\u5A46\u5A11\u5730\u770B\u4E86\u5BAB\u4E3B\u4E00\u773C\uFF0C\u8FDE\u5FD9\u5411\u65C1\u907F\u5F00\u3002\u5F53\u76EE\u5149\u626B\u5230\u5BAB\u4E3B\u814B\u4E0B\u65F6\uFF0C\u5979\u7684\u8EAB\u4F53\u7A81\u7136\u50F5\u4F4F\uFF0C\u4E0D\u6562\u7F6E\u4FE1\u5730\u77AA\u5927\u773C\u775B\uFF0C\u8111\u4E2D\u5145\u6EE1\u4E86\u6000\u7591\u548C\u6050\u60E7\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5931\u53BB\u4E86\u7EAA\u7709\u59A9\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5B64\u8EAB\u4E00\u4EBA\u4EA1\u547D\u5929\u6DAF\u3002\u5979\u4E0D\u6562\u7A0D\u505A\u505C\u7559\uFF0C\u82E6\u5FCD\u4F24\u52BF\uFF0C\u4E00\u8DEF\u6025\u884C\uFF0C\u7EC8\u4E8E\u63D0\u524D\u4E00\u65E5\uFF0C\u5728\u4E09\u6708\u521D\u4E03\u508D\u665A\u8D76\u5230\u4E34\u909B\u3002</p><p>\u4E00\u8EAB\u5C11\u5987\u6253\u626E\u7684\u6797\u9999\u8FDC\u50CF\u4E00\u6735\u6012\u653E\u7684\u7261\u4E39\u8273\u5149\u8FEB\u4EBA\uFF0C\u51E4\u76EE\u987E\u76FC\u751F\u59FF\u3002\u5979\u6027\u683C\u8C6A\u723D\uFF0C\u5AC9\u6076\u5982\u4EC7\uFF0C\u51FA\u9053\u4E0D\u53CA\u4E09\u5E74\uFF0C\u5BD2\u6708\u5200\u7684\u540D\u58F0\u5DF2\u5A01\u9707\u6C5F\u6E56\u3002\u5979\u4E0E\u592B\u541B\u6155\u5BB9\u80DC\u4E00\u8DEF\u67D4\u60C5\u5BC6\u610F\uFF0C\u6628\u5929\u624D\u521A\u521A\u5230\u5BB6\uFF0C\u6B64\u523B\u542C\u5230\u4E00\u8F7B\u4E00\u91CD\u4E24\u5339\u9A6C\u671D\u5927\u95E8\u76F4\u5954\u800C\u6765\uFF0C\u4E0D\u7531\u5FC3\u4E0B\u8BB6\u7136\u3002</p><p>\u300C\u55D6\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u4E00\u9053\u5BD2\u5149\u4ECE\u95E8\u7F1D\u4E2D\u5212\u5165\uFF0C\u65A9\u65AD\u95E8\u95E9\u3002\u4E8B\u51FA\u7A81\u7136\uFF0C\u6797\u9999\u8FDC\u8EAB\u4E0A\u5E76\u65E0\u5175\u5203\uFF0C\u4F46\u5979\u770B\u51FA\u6765\u9A91\u5185\u529B\u5E73\u5E73\uFF0C\u8D64\u624B\u7ACB\u5728\u9636\u524D\uFF0C\u6697\u9053\uFF1A\u300C\u6765\u7684\u662F\u8C01\uFF1F\u300D</p><p>\u5BD2\u5149\u300C\u53EE\u300D\u7684\u4E00\u58F0\u624E\u5728\u5730\u4E0A\uFF0C\u6797\u9999\u8FDC\u76EE\u5149\u4E00\u8DF3\uFF0C\u8BA4\u51FA\u8FD9\u662F\u7D2B\u73AB\u7684\u4F69\u5200\u7247\u7389\u3002</p><p>\u63A5\u7740\u5927\u95E8\u88AB\u731B\u7136\u649E\u5F00\uFF0C\u4E00\u4EBA\u4E00\u9A6C\u51B2\u4E86\u8FDB\u6765\u3002\u6797\u9999\u8FDC\u98DE\u8EAB\u800C\u8D77\uFF0C\u5C06\u6C14\u606F\u5944\u5944\u7684\u5C0F\u59D1\u62B1\u4E0B\u9A6C\u6765\u3002</p><p>\u90A3\u5339\u6D51\u8EAB\u6CBE\u6EE1\u6CE5\u571F\u7684\u767D\u9A6C\u524D\u817F\u8DEA\u5012\uFF0C\u53D1\u51FA\u4E00\u58F0\u5636\u9E23\uFF0C\u53E3\u9F3B\u95F4\u767D\u6CAB\u56DB\u4E0B\u98DE\u6E85\uFF0C\u663E\u7136\u662F\u7ECF\u8FC7\u4E86\u957F\u9014\u8DCB\u6D89\u3002\u63A5\u7740\u4E00\u5339\u7A7A\u9A6C\u968F\u540E\u5954\u5165\uFF0C\u7ACB\u5728\u767D\u9A6C\u65C1\u547C\u547C\u7684\u5598\u7740\u6C14\u3002</p><p>\u6155\u5BB9\u80DC\u95FB\u58F0\u8D76\u81F3\uFF0C\u89C1\u59BB\u5B50\u62B1\u7740\u59B9\u59B9\u8FDB\u6765\u4E0D\u7531\u5927\u5403\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u63A0\u5230\u5899\u5934\u56DB\u4E0B\u4E86\u671B\uFF0C\u770B\u662F\u5426\u8FD8\u6709\u8FFD\u5175\u3002</p><p>\u5F85\u4ED6\u56DE\u5230\u5367\u623F\uFF0C\u6797\u9999\u8FDC\u6B63\u9762\u8272\u51DD\u91CD\u7684\u5750\u5728\u7D2B\u73AB\u80CC\u540E\u8FD0\u6C14\u7597\u4F24\u3002\u6155\u5BB9\u80DC\u4E0D\u6562\u6253\u6270\uFF0C\u4FBF\u7ACB\u5728\u4E00\u65C1\u5B88\u62A4\u3002</p><p>\u534A\u4E2A\u65F6\u8FB0\u540E\uFF0C\u6797\u9999\u8FDC\u653E\u5F00\u624B\uFF0C\u989D\u5934\u6E17\u51FA\u4E00\u5C42\u7EC6\u6C57\u3002</p><p>\u6155\u5BB9\u80DC\u95EE\u9053\uFF1A\u300C\u8C01\uFF1F\u300D</p><p>\u6797\u9999\u8FDC\u6447\u4E86\u6447\u5934\uFF1A\u300C\u8FD9\u4EBA\u529F\u529B\u6BD4\u5927\u5E08\u59D0\u8FD8\u80DC\u4E00\u7B79\uFF0C\u7D2B\u73AB\u80CC\u4E0A\u7ECF\u8109\u53D7\u4E86\u91CD\u4F24\uFF0C\u5979\u80FD\u6491\u5230\u73B0\u5728\uFF0C\u591A\u4E8F\u4E86\u51E4\u51F0\u5B9D\u5178\u3002\u300D</p><p>\u7D2B\u73AB\u82E6\u5FCD\u4E09\u5929\uFF0C\u5DF2\u7ECF\u5FC3\u529B\u6194\u60B4\uFF0C\u867D\u5F97\u6797\u9999\u8FDC\u6551\u6CBB\uFF0C\u8FD8\u662F\u660F\u8FF7\u4E0D\u9192\u3002\u6155\u5BB9\u80DC\u770B\u7740\u59B9\u59B9\u6CBE\u6EE1\u5C18\u571F\u7684\u7389\u8138\uFF0C\u4E0D\u7531\u4E00\u9635\u5FC3\u75BC\u3002</p><p>\u300C\u5728\u54EA\u91CC\u53D7\u7684\u4F24\uFF1F\u4E3A\u4EC0\u4E48\u5954\u6CE2\u6570\u5343\u91CC\u6765\u5230\u4E34\u909B\uFF1F\u96BE\u9053\u662F\u4F0F\u9F99\u6DA7\u51FA\u4E86\u4E8B\uFF1F\u654C\u4EBA\u662F\u8C01\uFF1F\u300D\u8FD9\u4E00\u8FDE\u4E32\u7684\u7591\u95EE\u6D8C\u4E0A\u592B\u59BB\u4E24\u4EBA\u5FC3\u5934\u3002</p><p>\u90A3\u5339\u7A7A\u9A6C\u53E3\u9F3B\u95F4\u55B7\u51FA\u5927\u56E2\u7684\u767D\u96FE\uFF0C\u6797\u9999\u8FDC\u7F8E\u76EE\u4E2D\u5FE7\u5FC3\u5FE1\u5FE1\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u8FD9\u662F\u7709\u59A9\u7684\u5750\u9A91\u3002\u300D</p>',59);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
