import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_111" tabindex="-1"><a class="header-anchor" href="#_111" aria-hidden="true">#</a> 111</h1><p>\u7533\u5BA4\u7684\u77F3\u95E8\u4E0E\u5176\u4ED6\u77F3\u5BA4\u76F8\u540C\uFF0C\u4F46\u7D2B\u73AB\u600E\u4E48\u4E5F\u6CA1\u60F3\u5230\u5E73\u5E38\u7684\u77F3\u95E8\u4E0B\u4F1A\u6709\u5982\u6B64\u5927\u7684\u7A7A\u95F4\u3002\u77F3\u5BA4\u9AD8\u4E0D\u89C1\u9876\uFF0C\u6BD4\u5DF3\u5BA4\u5927\u4E86\u5341\u500D\u6709\u4F59\uFF0C\u4E0E\u5176\u8BF4\u662F\u77F3\u5BA4\uFF0C\u4E0D\u5982\u8BF4\u662F\u4E00\u5904\u5929\u7136\u7684\u77F3\u7A9F\u3002</p><p>\u5730\u4E0A\u904D\u751F\u77F3\u7B0B\uFF0C\u7C97\u8005\u6BD4\u7D2B\u73AB\u6000\u5B55\u7684\u8170\u8EAB\u8FD8\u8981\u7C97\uFF0C\u7EC6\u8005\u4E0D\u8FC7\u624B\u6307\u5927\u5C0F\uFF0C\u5BC6\u82E5\u68EE\u6797\u3002\u51F8\u51F9\u4E0D\u5E73\u7684\u6D1E\u58C1\u4E0A\u523B\u7740\u8FDE\u7EF5\u4E0D\u65AD\u7684\u82B1\u7EB9\uFF0C\u4ECE\u95E8\u65C1\u4E00\u76F4\u5EF6\u4F38\u5230\u2026\u2026\u4E00\u56E2\u9ED1\u5F71\u80CC\u540E\u3002</p><p>\u7D2B\u73AB\u80C6\u5B50\u6781\u5927\uFF0C\u6240\u4EE5\u6562\u4E00\u4E2A\u4EBA\u8DD1\u5230\u77F3\u6D1E\u5185\uFF0C\u53EF\u5F53\u5979\u770B\u5230\u9ED1\u5F71\u4E2D\u4F38\u51FA\u51E0\u6839\u5C16\u5229\u7684\u67AF\u679D\u65F6\uFF0C\u624B\u91CC\u7684\u591C\u660E\u73E0\u4E00\u4E0B\u5B50\u4FBF\u6389\u5230\u5730\u4E0A\u3002</p><p>\u7D2B\u73AB\u89C9\u5F97\u81EA\u5DF1\u5934\u53D1\u90FD\u4E00\u6839\u6839\u76F4\u7AD6\u8D77\u6765\uFF0C\u60CA\u53EB\u4E00\u58F0\uFF0C\u8F6C\u8EAB\u5C31\u8DD1\u3002</p><p>\u5979\u4FCF\u8138\u96EA\u767D\uFF0C\u8111\u4E2D\u7FFB\u6765\u8986\u53BB\u53EA\u6709\u4E00\u53E5\u8BDD\uFF1A\u8FD9\u7A76\u7ADF\u662F\u4EC0\u4E48\u5996\u602A\uFF1F</p><p>\u521A\u8F6C\u8FC7\u8EAB\u5B50\uFF0C\u80CC\u540E\u7A81\u7136\u4F20\u6765\u4E00\u80A1\u5E9E\u7136\u7684\u5438\u529B\u3002\u7D2B\u73AB\u867D\u60CA\u4E0D\u4E71\uFF0C\u6323\u624E\u7740\u60F3\u6293\u4F4F\u95E8\u6846\u3002\u624B\u6307\u521A\u521A\u78B0\u5230\u51B0\u51C9\u77F3\u58C1\uFF0C\u6574\u4E2A\u4EBA\u4FBF\u5012\u98DE\u5165\u5185\u3002</p><p>\u300C\u546F\u300D\u7D2B\u73AB\u6389\u5728\u4E00\u4E2A\u786C\u6886\u6886\u6BDB\u8338\u8338\u7684\u7269\u4F53\u4E0A\uFF0C\u5979\u5413\u5F97\u5A07\u8EAF\u5267\u98A4\uFF0C\u4E00\u53E3\u6C14\u564E\u5728\u5589\u5934\uFF0C\u600E\u4E48\u4E5F\u53EB\u4E0D\u51FA\u6765\u3002</p><p>\u90A3\u67AF\u679D\u539F\u6765\u662F\u5996\u602A\u7684\u722A\u5B50\uFF0C\u8F7B\u8F7B\u4E00\u5212\u4FBF\u6495\u5F00\u4E86\u5979\u7684\u5939\u8884\uFF0C\u63A5\u7740\u4E00\u4E2A\u51B0\u51C9\u7684\u7269\u4F53\u4ECE\u88C2\u7F1D\u63A2\u5165\uFF0C\u6293\u4F4F\u73AB\u7470\u4ED9\u5B50\u767D\u5AE9\u7684\u8089\u4F53\u3002</p><p>\u300C\u5440\uFF01\u300D\u5F53\u90A3\u4E2A\u7C97\u7CD9\u7684\u722A\u5B50\u4ECE\u80A1\u95F4\u94BB\u5230\u8179\u4E0B\uFF0C\u91CD\u91CD\u52FE\u4F4F\u79D8\u5904\u65F6\uFF0C\u7D2B\u73AB\u5589\u5934\u4E00\u677E\uFF0C\u60CA\u53EB\u58F0\u968F\u4E4B\u54CD\u8D77\u3002</p><p>\u90A3\u5996\u602A\u4F3C\u4E4E\u6478\u51FA\u5979\u8179\u90E8\u7684\u5F02\u5E38\uFF0C\u4E8E\u662F\u505C\u4F4F\u52A8\u4F5C\u3002</p><p>\u843D\u5728\u5730\u4E0A\u7684\u660E\u73E0\uFF0C\u6EF4\u6E9C\u6E9C\u6EDA\u8FC7\u8D77\u4F0F\u7684\u5730\u9762\uFF0C\u79BB\u8EAB\u4F53\u8FD8\u6709\u4E08\u8BB8\u65F6\uFF0C\u7A81\u7136\u4E00\u8DC3\u800C\u8D77\uFF0C\u843D\u5728\u4E00\u53EA\u67AF\u7626\u7684\u624B\u638C\u4E2D\u3002</p><p>\u90A3\u4E2A\u624B\u638C\u53EA\u5269\u76AE\u5305\u9AA8\u5934\uFF0C\u5E03\u6EE1\u9ED1\u6CE5\uFF0C\u4F46\u5206\u660E\u662F\u4E00\u53EA\u4EBA\u624B\u638C\u3002\u7CBE\u7626\u7684\u624B\u8155\u4E0A\uFF0C\u6302\u7740\u4E00\u6839\u7EC6\u7EC6\u7684\u94C1\u94FE\u3002</p><p>\u7D2B\u73AB\u98A4\u6296\u7740\u626D\u8138\u770B\u53BB\uFF0C\u53EA\u89C1\u80CC\u540E\u662F\u4E00\u4E1B\u7ED3\u6210\u6BE1\u6BEF\u7684\u6BDB\u53D1\uFF0C\u5BC6\u5BC6\u9EBB\u9EBB\u7684\u5782\u5230\u5730\u4E0A\uFF0C\u6D3B\u50CF\u4E00\u4E2A\u5E9E\u5927\u7684\u8695\u8327\u3002</p><p>\u90A3\u4EBA\u4F3C\u4E4E\u53D7\u4E0D\u4E86\u73E0\u8F89\u7684\u5149\u660E\uFF0C\u7B49\u4E86\u7247\u523B\u624D\u62E8\u5F00\u6BDB\u53D1\uFF0C\u9732\u51FA\u4E00\u5F20\u6EE1\u662F\u6C61\u57A2\u7684\u9762\u5B54\uFF0C\u5BC6\u751F\u7684\u80E1\u987B\u51E0\u4E4E\u63A9\u6CA1\u4E86\u4ED6\u7684\u4E94\u5B98\uFF0C\u53EA\u6709\u4E00\u53CC\u773C\u775B\u5206\u5916\u660E\u4EAE\u3002</p><p>\u7D2B\u73AB\u89C9\u5F97\u5FC3\u810F\u5728\u5589\u5499\u8DF3\u4E2A\u4E0D\u505C\uFF0C\u53EA\u50BB\u50BB\u770B\u7740\u90A3\u4E2A\u602A\u4EBA\uFF0C\u8111\u5B50\u91CC\u4E00\u7247\u6DF7\u4E71\u3002</p><p>\u300C\u4F60\u3001\u662F\u3001\u4F55\u3001\u4EBA\uFF1F\u300D\u90A3\u602A\u4EBA\u58F0\u97F3\u6C99\u54D1\u800C\u53C8\u602A\u5F02\uFF0C\u50CF\u662F\u591A\u5E74\u6CA1\u6709\u8BF4\u8FC7\u8BDD\u822C\u8FDF\u7F13\u3002</p><p>\u300C\u6211\u2026\u2026\u6211\u662F\u5BAB\u91CC\u7684\u5974\u5A62\u2026\u2026\u300D</p><p>\u300C\u5974\u3001\u5A62\uFF1F\u300D\u90A3\u602A\u4EBA\u7A81\u7136\u6840\u6840\u602A\u7B11\u8D77\u6765\uFF0C\u300C\u5BAB\u91CC\u3001\u7F8E\u7684\u3001\u90FD\u3001\u88AB\u5979\u6740\u4E86\uFF0C\u54EA\u91CC\u3001\u8FD8\u6709\u4F60\u3001\u8FD9\u6837\u7684\u5974\u5A62\u3002\u300D\u4ED6\u7B11\u58F0\u5FFD\u6B62\uFF0C\u987B\u53D1\u98DE\u626C\uFF0C\u5239\u90A3\u95F4\u96C4\u5A01\u8FEB\u4EBA\u3002</p><p>\u7D2B\u73AB\u9762\u65E0\u4EBA\u8272\uFF0C\u8FD9\u4E2A\u602A\u7269\u4E0D\u77E5\u9053\u5728\u77F3\u7A9F\u5185\u9501\u4E86\u591A\u4E45\uFF0C\u8FDE\u661F\u6708\u6E56\u8FD9\u7B49\u5996\u90AA\u6BD5\u805A\u7684\u5730\u65B9\u90FD\u5BB9\u4E0D\u4E0B\u4ED6\uFF0C\u80AF\u5B9A\u662F\u5996\u5F97\u4E0D\u80FD\u518D\u5996\u4E86\u2026\u2026</p><p>\u8BF4\u4E86\u51E0\u53E5\u8BDD\u540E\uFF0C\u90A3\u4EBA\u8BED\u6C14\u867D\u7136\u8FD8\u6709\u4E9B\u602A\u5F02\uFF0C\u4F46\u6D41\u5229\u4E86\u4E00\u4E9B\u3002\u4ED6\u770B\u770B\u7D2B\u73AB\u7684\u5C0F\u8179\uFF0C\u9119\u5937\u5730\u8BF4\u9053\uFF1A\u300C\u5C45\u7136\u80FD\u5BB9\u5FCD\u5B55\u5987\u5B58\u5728\uFF0C\u661F\u6708\u6E56\u7ADF\u5815\u843D\u5230\u5982\u6B64\u5730\u6B65\u2026\u2026\u300D\u4ED6\u5927\u624B\u4E00\u7D27\uFF0C\u5389\u559D\u9053\uFF1A\u300C\u4F60\u7A76\u7ADF\u662F\u8C01\uFF1F\u300D</p><p>\u7D2B\u73AB\u7684\u773C\u6CEA\u4E00\u5411\u8BF4\u6765\u5C31\u6765\uFF0C\u51B5\u4E14\u771F\u662F\u5BB3\u6015\uFF0C\u88AB\u4ED6\u4E00\u559D\u987F\u65F6\u70ED\u6CEA\u76C8\u7736\uFF0C\u62BD\u564E\u9053\uFF1A\u300C\u6211\u662F\u5BAB\u4E3B\u7684\u5974\u5A62\u2026\u2026\u6000\u4E86\u5B69\u5B50\uFF0C\u88AB\u7F5A\u5230\u8FD9\u91CC\u6E05\u626B\u2026\u2026\u300D</p><p>\u90A3\u4EBA\u773C\u795E\u51CC\u5389\u5730\u76EF\u7740\u7D2B\u73AB\uFF0C\u6076\u72E0\u72E0\u5730\u8BF4\uFF1A\u300C\u53F6\u884C\u5357\u5C41\u672C\u4E8B\u6CA1\u6709\uFF0C\u6253\u80CE\u5012\u662F\u5728\u884C\uFF0C\u600E\u4E48\u4F1A\u653E\u8FC7\u4F60\uFF1F\u300D</p><p>\u7D2B\u73AB\u5FC3\u9053\uFF1A\u4ED6\u4FDD\u8FD9\u5B69\u5B50\u8FD8\u6765\u4E0D\u53CA\u5462\uFF0C\u5634\u91CC\u5374\u8BF4\uFF1A\u300C\u5BAB\u4E3B\u4E0D\u8BB8\u2026\u2026\u300D</p><p>\u90A3\u4EBA\u5947\u9053\uFF1A\u300C\u5B69\u5B50\u662F\u8C01\u7684\uFF1F\u6731\u90AA\u9752\u6811\uFF1F\u5C48\u82E6\u85E4\uFF1F\u300D</p><p>\u7D2B\u73AB\u6CE3\u9053\uFF1A\u300C\u5974\u5A62\u4E5F\u4E0D\u77E5\u9053\u2026\u2026\u300D</p><p>\u90A3\u4EBA\u54C8\u54C8\u5927\u7B11\uFF0C\u300C\u661F\u6708\u6E56\u7684\u5973\u4EBA\uFF0C\u54EA\u4E2A\u4E0D\u662F\u5343\u4EBA\u9A91\u4E07\u4EBA\u538B\u7684\uFF0C\u60F3\u627E\u7239\u90A3\u662F\u96BE\u4E86\u3002\u300D</p><p>\u4ED6\u5FFD\u7136\u62AC\u624B\u6307\u4E00\u5212\uFF0C\u5C16\u5229\u7684\u6307\u7532\u5207\u5F00\u80F8\u8863\uFF0C\u5C0F\u897F\u74DC\u822C\u5730\u6D51\u5706\u5DE8\u4E73\u7ACB\u5373\u8DF3\u8DC3\u800C\u51FA\u3002</p><p>\u4ED6\u4E00\u628A\u62E7\u4F4F\u80A5\u4E73\uFF0C\u300C\u597D\u5976\u597D\u5976\uFF0C\u82E5\u4E0D\u662F\u5974\u5A62\u4E5F\u4E0D\u4F1A\u88AB\u6539\u9020\u6210\u8FD9\u7B49\u6A21\u6837\u2014\u2014\u9634\u59EC\u7ADF\u7136\u6CA1\u6740\u4E86\u4F60\uFF1F\u4F60\u662F\u8427\u4F5B\u5974\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u8111\u4E2D\u4E00\u9707\uFF0C\u8FDE\u5FD9\u77E2\u53E3\u5426\u8BA4\u3002</p><p>\u300C\u8FD9\u4E48\u5927\u7684\u5976\u5B50\uFF0C\u53F6\u884C\u5357\u7684\u624B\u827A\u6709\u957F\u8FDB\u554A\u3002\u300D\u90A3\u4EBA\u4E00\u8FB9\u73A9\u5F04\u7D2B\u73AB\u7684\u4E73\u623F\uFF0C\u4E00\u8FB9\u81EA\u8A00\u81EA\u8BED\u9053\uFF1A\u300C\u8427\u4F5B\u5974\u8981\u80FD\u6D3B\u5230\u73B0\u5728\uFF0C\u4E5F\u6709\u4E09\u5341\u516D\u4E03\u4E86\uFF0C\u600E\u4E48\u4F1A\u8FD9\u822C\u5E74\u8F7B\u3002\u300D</p><p>\u4ED6\u63D0\u9AD8\u58F0\u97F3\uFF1A\u300C\u662F\u9634\u59EC\u8BA9\u4F60\u5F00\u5F97\u8FD9\u6247\u95E8\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u5FCD\u4F4F\u88AB\u4EBA\u73A9\u5F04\u7684\u7F9E\u610F\uFF0C\u61F5\u61F5\u5FE1\u5FE1\u5730\u6447\u4E86\u6447\u5934\uFF0C\u300C\u6211\u4E0D\u77E5\u9053\u8C01\u662F\u9634\u59EC\u3002\u300D</p><p>\u90A3\u4EBA\u4E00\u6115\uFF0C\u76EE\u4E2D\u9732\u51FA\u4E86\u590D\u6742\u4E4B\u6781\u7684\u795E\u8272\uFF0C\u7247\u523B\u540E\u5389\u58F0\u95EE\u9053\uFF1A\u300C\u73B0\u5728\u7684\u5BAB\u4E3B\u662F\u8C01\uFF1F\u300D</p><p>\u7D2B\u73AB\u89C1\u4ED6\u77E5\u9053\u6BCD\u4EB2\u7684\u540D\u5B57\uFF0C\u751F\u6015\u662F\u81EA\u5DF1\u7684\u4EC7\u5BB6\uFF0C\u8FDE\u6155\u5BB9\u7684\u59D3\u6C0F\u4E5F\u4E0D\u6562\u63D0\uFF0C\u53EA\u8BF4\u81EA\u5DF1\u662F\u5E74\u524D\u88AB\u63B3\u5165\u5BAB\u91CC\u7684\u5974\u5A62\uFF0C\u4EC0\u4E48\u90FD\u4E0D\u77E5\u9053\u3002\u6000\u4E86\u5B69\u5B50\u540E\u5BAB\u4E3B\u672C\u6765\u6740\u5979\uFF0C\u4F46\u6CA1\u820D\u5F97\u7B49\u7B49\u3002</p><p>\u90A3\u4EBA\u770B\u5230\u7D2B\u73AB\u5B55\u4E2D\u4ECD\u7136\u5A07\u8273\u6B32\u6EF4\u7684\u7F8E\u6001\uFF0C\u5012\u6709\u51E0\u5206\u76F8\u4FE1\uFF0C\u4ED6\u6709\u4E9B\u5931\u795E\u5730\u5583\u5583\u9053\uFF1A\u300C\u96BE\u9053\u9634\u59EC\u6B7B\u4E86\uFF1F\u300D\u534A\u664C\u540E\u53C8\u51B7\u7B11\u9053\uFF1A\u300C\u5929\u9053\u5FAA\u73AF\uFF0C\u62A5\u5E94\u4E0D\u723D\uFF0C\u5979\u4E5F\u65E9\u8BE5\u6B7B\u4E86\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u5C4F\u4F4F\u547C\u5438\uFF0C\u6084\u6084\u67E5\u770B\u58C1\u4E0A\u7684\u56FE\u5F62\uFF0C\u53EA\u89C1\u82B1\u7EB9\u4E0E\u5176\u4ED6\u77F3\u5BA4\u4E00\u822C\u65E0\u4E8C\uFF0C\u663E\u662F\u4E00\u4EBA\u6240\u523B\uFF0C\u6700\u540E\u5EF6\u4F38\u5230\u90A3\u4EBA\u80CC\u540E\u3002\u6B63\u770B\u5F97\u51FA\u795E\uFF0C\u5FFD\u7136\u8EAB\u5B50\u4E00\u6C89\uFF0C\u8DCC\u5728\u5730\u4E0A\u3002</p><p>\u90A3\u4EBA\u4E00\u624B\u6293\u7740\u5979\u7684\u534A\u53EA\u53F3\u4E73\uFF0C\u4E00\u624B\u63D2\u5230\u6ED1\u817B\u7684\u80A1\u95F4\u6063\u610F\u638F\u6478\uFF0C\u6DEB\u7B11\u9053\uFF1A\u300C\u8001\u5B50\u5728\u8FD9\u91CC\u56F0\u4E86\u8FD1\u51E0\u5341\u5E74\uFF0C\u96BE\u5F97\u80FD\u78B0\u4E0A\u4E2A\u5A46\u5A18\uFF0C\u867D\u7136\u662F\u5927\u809A\u5B50\uFF0C\u4E5F\u5C06\u5C31\u4E86\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u60CA\u9A87\u6B32\u7EDD\uFF0C\u867D\u7136\u88AB\u6155\u5BB9\u9F99\u6DEB\u8FB1\u591A\u65E5\uFF0C\u4F46\u4ECE\u6CA1\u6709\u7B2C\u4E8C\u4E2A\u7537\u4EBA\u6562\u73A9\u5F04\u81EA\u5DF1\u7684\u8EAB\u4F53\uFF0C\u8FD9\u4E00\u4E0B\u53EA\u6015\u8D1E\u8282\u4E0D\u4FDD\u2026\u2026</p><p>\u5979\u62FC\u547D\u63A8\u6387\u7740\u4ED6\u7684\u624B\u81C2\uFF0C\u5403\u529B\u5730\u95EE\u9053\uFF1A\u300C\u4F60\u662F\u8C01\uFF1F\u300D</p><p>\u90A3\u4EBA\u4EF0\u5929\u5927\u7B11\uFF0C\u58F0\u97F3\u5728\u77F3\u5BAB\u4E2D\u8FDC\u8FDC\u4F20\u5F00\uFF0C\u300C\u8001\u5B50\u662F\u661F\u6708\u6E56\u53F3\u4F7F\u9634\u957F\u91CE\uFF01\u300D</p><p>\u7D2B\u73AB\u88AB\u4ED6\u7684\u7B11\u58F0\u9707\u5F97\u5934\u6655\u76EE\u7729\uFF0C\u5F85\u4E0B\u4F53\u75BC\u75DB\u4F20\u6765\u624D\u7075\u53F0\u6682\u660E\u3002</p><p>\u661F\u6708\u6E56\u5BAB\u4E3B\u4EE5\u4E0B\u5206\u4E3A\u4E8C\u4F7F\u8005\u3001\u4E09\u62A4\u6CD5\u3001\u56DB\u795E\u5C06\u3001\u4E94\u957F\u8001\u548C\u516D\u4F9B\u5949\uFF0C\u6B64\u523B\u773C\u524D\u7684\u602A\u4EBA\u7ADF\u662F\u6570\u5341\u5E74\u524D\u4E0B\u843D\u4E0D\u660E\u7684\u4E8C\u4F7F\u8005\u4E4B\u4E00\uFF0C\u90A3\u521A\u624D\u7684\u5438\u529B\u4E0D\u662F\u5996\u6CD5\uFF0C\u800C\u662F\u5185\u529F\u4E86\u3002</p><p>\u602A\u4E0D\u5F97\u4ED6\u4F1A\u5BF9\u661F\u6708\u6E56\u4E0A\u4EE3\u4EBA\u7269\u5982\u6570\u5BB6\u73CD\u3002\u53EF\u4ED6\u600E\u4E48\u4F1A\u88AB\u4EBA\u56DA\u5728\u81EA\u5DF1\u6559\u5185\u7684\u5723\u5BAB\u5E95\u5C42\uFF1F\u4E3A\u4EC0\u4E48\u4E0D\u6740\u4ED6\uFF1F</p><p>\u7D2B\u73AB\u4ED4\u7EC6\u770B\u53BB\uFF0C\u53EA\u89C1\u4ED6\u8EAB\u5F62\u9AD8\u5927\uFF0C\u5750\u5728\u5730\u4E0A\u51E0\u4E4E\u6709\u81EA\u5DF1\u80F8\u4E73\u90A3\u4E48\u9AD8\u3002\u8863\u886B\u5C3D\u788E\uFF0C\u5E72\u7626\u7684\u80A9\u8170\u7F20\u7740\u91CD\u91CD\u94C1\u94FE\uFF0C\u9501\u5728\u77F3\u58C1\u4E4B\u4E0A\u3002\u518D\u5F80\u4E0B\u770B\uFF0C\u5374\u770B\u4E0D\u5230\u4ED6\u7684\u53CC\u817F\u2026\u2026</p><p>\u67AF\u7626\u7684\u624B\u6307\u5DF2\u7ECF\u94BB\u8FDB\u4F53\u5185\u62BD\u9001\u8D77\u6765\uFF0C\u7D2B\u73AB\u75DB\u5F97\u82B1\u5BB9\u5931\u8272\uFF0C\u5979\u7EA4\u624B\u7ED5\u8FC7\u8179\u4FA7\uFF0C\u75DB\u53EB\u9053\uFF1A\u300C\u522B\u6293\u2026\u2026\u75DB\u554A\u2026\u2026\u300D</p><p>\u9634\u957F\u91CE\u72DE\u7B11\u9053\uFF1A\u300C\u8001\u5B50\u6700\u559C\u6B22\u770B\u5973\u4EBA\u54ED\uFF0C\u957F\u5F97\u8D8A\u7F8E\uFF0C\u54ED\u8D77\u6765\u8D8A\u597D\u770B\uFF01\u300D\u8BF4\u7740\u6307\u7532\u523A\u5165\u8089\u58C1\u3002</p><p>\u7D2B\u73AB\u6500\u4F4F\u94C1\u94F8\u822C\u7684\u624B\u8155\u54ED\u9053\uFF1A\u300C\u6211\u8FD8\u6000\u7740\u5B69\u5B50\u2026\u2026\u300D</p><p>\u8BDD\u672A\u8BF4\u5B8C\uFF0C\u5C31\u88AB\u9634\u957F\u91CE\u4E00\u53E3\u8165\u81ED\u7684\u5410\u6CAB\u5410\u5230\u8138\u4E0A\uFF0C\u300C\u8001\u5B50\u6700\u6068\u7684\u5C31\u662F\u5927\u809A\u5B50\u5A46\u5A18\uFF0C\u89C1\u4E00\u4E2A\u8E29\u4E00\u4E2A\uFF01\u5206\u5F00\u817F\uFF01\u770B\u8001\u5B50\u600E\u4E48\u628A\u80CE\u513F\u7ED9\u626F\u51FA\u6765\uFF01\u300D</p><p>\u7D2B\u73AB\u4ECE\u672A\u9047\u5230\u8FD9\u79CD\u5371\u9669\uFF0C\u6B64\u65F6\u53EB\u5929\u4E0D\u5E94\u547C\u5730\u4E0D\u7075\uFF0C\u4E0B\u4F53\u7684\u75DB\u695A\u51E0\u4E4E\u6BD4\u7834\u4F53\u66F4\u751A\u3002\u5979\u53C8\u54ED\u53C8\u6C42\uFF0C\u90A3\u53EA\u624B\u53CD\u800C\u8D8A\u6765\u8D8A\u72E0\u3002</p><p>\u6323\u624E\u534A\u664C\uFF0C\u7D2B\u73AB\u7A81\u7136\u98A4\u58F0\u9053\uFF1A\u300C\u8001\u524D\u8F88\uFF0C\u4F60\u2026\u2026\u600E\u4E48\u4F1A\u88AB\u56F0\u5728\u8FD9\u91CC\u2026\u2026\u300D\u8F6F\u786C\u90FD\u4E0D\u884C\uFF0C\u53EA\u597D\u5206\u4ED6\u7684\u5FC3\u4E86\u3002</p><p>\u9634\u957F\u91CE\u679C\u7136\u505C\u4F4F\u4E86\u624B\uFF0C\u53CC\u76EE\u4E2D\u6068\u610F\u6D8C\u73B0\uFF0C\u54AC\u7259\u5207\u9F7F\u5730\u8BF4\uFF1A\u300C\u8FD8\u4E0D\u90FD\u662F\u90A3\u4E2A\u8D31\u4EBA\uFF01\u300D</p><p>\u4ED6\u7259\u9F7F\u683C\u683C\u4F5C\u54CD\uFF0C\u300C\u65E9\u77E5\u5982\u6B64\uFF0C\u8001\u5B50\u8D81\u5979\u8FD8\u5728\u5A18\u809A\u5B50\u91CC\u5C31\u8BE5\u628A\u5979\u5F04\u6B7B\uFF01\u300D</p><p>\u7D2B\u73AB\u8D62\u5F97\u7247\u523B\u5598\u606F\uFF0C\u4E00\u8FB9\u632A\u52A8\u8EAB\u4F53\uFF0C\u4E00\u8FB9\u95EE\u9053\uFF1A\u300C\u5979\u662F\u8C01\uFF1F\u4E3A\u4EC0\u4E48\u4F1A\u8FD9\u6837\u5BF9\u4F60\uFF1F\u300D</p><p>\u300C\u8C01\uFF01\uFF1F\u6211\u7684\u4E56\u5973\u513F\uFF01\u300D\u9634\u957F\u91CE\u543C\u9053\uFF1A\u300C\u90A3\u4E2A\u6B7B\u5A4A\u5B50\u8DDF\u5979\u5A18\u662F\u4E00\u8DEF\u8D27\u8272\uFF01\u4E3A\u4E86\u5F53\u5BAB\u4E3B\u8FDE\u8001\u7239\u90FD\u6562\u4E0B\u6BD2\u624B\uFF01\u8BA9\u6211\u902E\u5230\u5979\uFF0C\u8001\u5B50\u975E\u628A\u5979\u78BE\u6210\u7C89\u672B\uFF01\u300D\u4ED6\u6325\u821E\u7740\u53CC\u624B\uFF0C\u8EAB\u4E0A\u7684\u94C1\u94FE\u94EE\u94EE\u4F5C\u54CD\uFF0C\u72B6\u5982\u75AF\u9B54\u3002</p><p>\u7D2B\u73AB\u770B\u51C6\u65F6\u673A\uFF0C\u594B\u529B\u4E00\u6323\uFF0C\u8EB2\u5230\u4E00\u682A\u77F3\u7B0B\u4E4B\u540E\u3002</p><p>\u9634\u957F\u91CE\u56DE\u8FC7\u795E\u6765\uFF0C\u6012\u559D\u7740\u73AF\u81C2\u62B1\u80F8\uFF0C\u63A5\u7740\u624B\u81C2\u4E00\u626C\uFF0C\u500F\u5FFD\u957F\u51FA\u5C3A\u8BB8\uFF0C\u6B63\u6293\u4F4F\u7D2B\u73AB\u7684\u811A\u8E1D\u3002</p><p>\u300C\u53EE\u300D\uFF0C\u91D1\u5236\u7684\u5C0F\u5F29\u8FDE\u7740\u65AD\u88C2\u7684\u8863\u5E26\u6389\u5728\u949F\u4E73\u77F3\u65C1\u3002\u63A5\u7740\u300C\u5472\u5472\u300D\u58F0\u4E0D\u7EDD\u4E8E\u8033\uFF0C\u7D2B\u73AB\u7684\u7EF5\u8884\u7EE3\u8966\u7247\u523B\u4FBF\u88AB\u6495\u6210\u788E\u7247\uFF0C\u8D64\u6761\u6761\u6A2A\u9648\u5730\u4E0A\u3002</p><p>\u9634\u957F\u91CE\u8272\u5FC3\u5927\u52A8\uFF0C\u987E\u4E0D\u5F97\u626F\u51FA\u5A74\u513F\uFF0C\u4FBF\u62B1\u7740\u7D2B\u73AB\u767D\u751F\u751F\u7684\u7389\u81C0\u538B\u4E86\u4E0A\u53BB\u3002</p><p>\u88AB\u4ED6\u4E0B\u8179\u4E00\u8E6D\uFF0C\u7D2B\u73AB\u624D\u77E5\u9053\u4ED6\u7684\u53CC\u817F\u65E9\u5DF2\u88AB\u4EBA\u780D\u65AD\uFF0C\u7EB7\u4E71\u6BDB\u53D1\u4E2D\u53EA\u6709\u4E0A\u8EAB\u3002</p><p>\u5979\u4E00\u624B\u6491\u7740\u5730\u9762\uFF0C\u4E00\u624B\u4F38\u5230\u8179\u4E0B\uFF0C\u963B\u6321\u9634\u957F\u91CE\u7684\u63D2\u5165\u3002\u5982\u679C\u88AB\u8FD9\u4E2A\u534A\u4EBA\u534A\u5996\u7684\u602A\u7269\u5978\u6DEB\uFF0C\u4E0D\u7528\u522B\u4EBA\u77E5\u9053\uFF0C\u81EA\u5DF1\u4E5F\u4E0D\u60F3\u6D3B\u4E86\u3002</p><p>\u8FDC\u8FDC\u770B\u53BB\uFF0C\u88F9\u7740\u4E00\u56E2\u4E71\u53D1\u7684\u602A\u4EBA\u5C31\u50CF\u4E00\u4E2A\u7C97\u9ED1\u810F\u4E71\u7684\u8695\u8327\uFF0C\u8D34\u5728\u5C11\u5973\u6676\u83B9\u6D51\u5706\u7684\u96EA\u81C0\u4E0A\u4E0D\u4F4F\u633A\u5F04\u3002\u9634\u957F\u91CE\u9A91\u5728\u7D2B\u73AB\u81C0\u4E0A\u8E6D\u4E86\u534A\u5929\uFF0C\u8138\u8272\u6E10\u6E10\u96BE\u770B\u8D77\u6765\u3002</p><p>\u4E00\u56E2\u51B7\u51B0\u51B0\u7684\u8F6F\u8089\u5728\u81C0\u7F1D\u95F4\u78B0\u6765\u78B0\u53BB\uFF0C\u5374\u59CB\u7EC8\u6CA1\u6709\u63D2\u5165\uFF0C\u7D2B\u73AB\u4E5F\u89C9\u5F97\u5947\u602A\u3002</p><p>\u300C\u556A\u300D\uFF0C\u4F7F\u4E86\u534A\u5929\u52B2\u4E5F\u6CA1\u80FD\u52C3\u8D77\u7684\u9634\u957F\u91CE\u6325\u624B\u671D\u7D2B\u73AB\u81C0\u4E0A\u91CD\u51FB\u4E00\u638C\uFF0C\u867D\u7136\u6CA1\u6709\u7528\u4E0A\u5185\u529B\uFF0C\u96EA\u81C0\u4E5F\u88AB\u6253\u5F97\u4E00\u7247\u4E4C\u9752\u3002\u4ED6\u72E0\u72E0\u5550\u4E86\u4E00\u53E3\uFF0C\u300C\u4ED6\u5988\u7684\uFF0C\u78B0\u4E0A\u5927\u809A\u5B50\u5A46\u5A18\u771F\u662F\u6666\u6C14\uFF01\u8BA9\u8001\u5B50\u5148\u628A\u4F60\u809A\u5B50\u91CC\u7684\u8D31\u79CD\u638F\u51FA\u6765\uFF01\u300D</p><p>\u7D2B\u73AB\u54ED\u9053\uFF1A\u300C\u524D\u8F88\u9976\u4E86\u6211\u5427\uFF0C\u90A3\u6837\u5974\u5A62\u4F1A\u6B7B\u7684\u2026\u2026\u5974\u5A62\u6B7B\u4E86\u8C01\u8FD8\u6765\u4F3A\u5019\u4F60\u5462\u2026\u2026\u300D</p><p>\u9634\u957F\u91CE\u602A\u7B11\u9053\uFF1A\u300C\u4F60\u8FD8\u60F3\u6D3B\u7740\u51FA\u53BB\u5417\uFF1F\u8001\u5B50\u597D\u4E45\u6CA1\u5403\u9C9C\u8089\u4E86\uFF0C\u8FD9\u5BF9\u5927\u5976\u5403\u8D77\u6765\u4E00\u5B9A\u4E0D\u9519\uFF01\u4ED6\u5988\u7684\uFF0C\u8001\u5B50\u7701\u70B9\u513F\u5403\uFF0C\u4E00\u5929\u53EA\u5403\u4E00\u53EA\uFF0C\u5403\u5B8C\u4E4B\u524D\u7EDD\u4E0D\u4F1A\u8BA9\u4F60\u6B7B\u3002</p><p>\u54C8\u54C8\uFF0C\u80CE\u513F\u4E5F\u662F\u5927\u8865\u4E4B\u7269\u3002\u4E00\u5C38\u4E24\u5403\uFF0C\u771F\u662F\u4FBF\u5B9C\u8001\u5B50\u4E86\uFF01\u300D</p><p>\u5E7D\u6697\u7684\u77F3\u7A9F\u4E2D\uFF0C\u7D2B\u73AB\u767D\u5AE9\u7684\u8089\u4F53\u6108\u53D1\u9C9C\u7F8E\uFF0C\u98A4\u6296\u7684\u5DE8\u4E73\u548C\u6D51\u5706\u7684\u5C0F\u8179\uFF0C\u65E0\u4E0D\u95EA\u52A8\u7740\u660E\u8273\u7684\u8089\u5149\u3002\u5979\u8737\u8D77\u5A07\u8EAF\uFF0C\u4E24\u624B\u63A9\u7740\u80F8\u4E73\u548C\u5C0F\u8179\uFF0C\u6CE3\u58F0\u9053\uFF1A\u300C\u6C42\u6C42\u4F60\u4E0D\u8981\u5403\u6211\u2026\u2026\u4ECE\u660E\u5929\u8D77\u5974\u5A62\u6BCF\u5929\u7ED9\u4F60\u5E26\u5403\u7684\uFF0C\u9E21\u9E2D\u9C7C\u8089\u4EC0\u4E48\u90FD\u6709\u2026\u2026\u6211\u2026\u2026\u6211\u6BCF\u5929\u8FD8\u6765\u4F3A\u5019\u60A8\u8001\u4EBA\u5BB6\uFF0C\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u9634\u957F\u91CE\u5495\u561F\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u76EE\u5149\u95EA\u95EA\u5730\u8BF4\uFF1A\u300C\u4F60\u6BCF\u5929\u90FD\u6253\u626B\u8FD9\u91CC\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u8FDE\u8FDE\u70B9\u5934\u3002</p><p>\u300C\u90A3\u597D\uFF0C\u5148\u6765\u7ED9\u8001\u5B50\u8214\u8214\u9E21\u5DF4\uFF01\u300D</p>',71);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
