import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_112" tabindex="-1"><a class="header-anchor" href="#_112" aria-hidden="true">#</a> 112</h1><p>\u6E05\u6668\uFF0C\u4E24\u5973\u5C31\u5F00\u59CB\u4E86\u4ECA\u5929\u7684\u6E38\u620F\uFF0C\u5979\u4EEC\u5148\u628A\u9526\u88AB\u6380\u8D77\u90FD\u76D6\u5728\u8427\u4F5B\u5974\u8138\u4E0A\uFF0C\u53C8\u628A\u5979\u8863\u886B\u89E3\u5C3D\u3002\u69BB\u4E0A\u53EA\u5269\u4E00\u6BB5\u65E0\u9996\u7684\u96EA\u767D\u5973\u4F53\uFF0C\u5706\u8179\u9999\u4E73\u5C3D\u6570\u66B4\u9732\u5728\u5916\u3002\u7F8E\u5987\u7389\u817F\u5E73\u5206\uFF0C\u5C3F\u5E03\u88AB\u89E3\u5F00\u5927\u534A\uFF0C\u5149\u6DA6\u7684\u96EA\u80A1\u7EA4\u6BEB\u6BD5\u9732\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6084\u6084\u8D70\u5230\u69BB\u65C1\uFF0C\u6309\u4E24\u4EBA\u7684\u8BA1\u5212\u8981\u5148\u6293\u4F4F\u5979\u7684\u817F\uFF0C\u5012\u5288\u7740\u62CE\u8D77\u6765\u3002\u5148\u5413\u5979\u4E2A\u534A\u6B7B\uFF0C\u7136\u540E\u518D\u72E0\u72E0\u6298\u78E8\u5979\u4E00\u756A\u3002\u624B\u521A\u521A\u4F38\u51FA\uFF0C\u7F8E\u5987\u8EAB\u5B50\u5FFD\u7136\u4E00\u52A8\uFF0C\u767D\u817B\u7684\u8179\u7403\u4E00\u9F13\u4E00\u9F13\uFF0C\u50CF\u662F\u7528\u529B\u618B\u6C14\u7684\u6837\u5B50\u3002\u5C31\u5728\u4E24\u5973\u773C\u524D\uFF0C\u9C9C\u7EA2\u7684\u83CA\u809B\u5B9B\u5982\u4E00\u5F20\u8815\u52A8\u7684\u5C0F\u5634\uFF0C\u7F13\u7F13\u5410\u51FA\u4E00\u622A\u9EC4\u6D4A\u8272\u7684\u6C61\u7269\u3002</p><p>\u8427\u4F5B\u5974\u4F3C\u4E4E\u5341\u5206\u7528\u529B\uFF0C\u9694\u7740\u9526\u88AB\u8FD8\u80FD\u542C\u5230\u5979\u7684\u95F7\u54FC\u3002\u96EA\u767D\u7684\u81C0\u8089\u4E0D\u4F4F\u6536\u7F29\uFF0C\u79FD\u7269\u4ECE\u677E\u5F1B\u7684\u809B\u6D1E\u91CC\u8D8A\u4F38\u8D8A\u957F\uFF0C\u5979\u7684\u547B\u541F\u4E5F\u8D8A\u6765\u8D8A\u54CD\uFF0C\u79FD\u7269\u901A\u8FC7\u83CA\u809B\u7684\u5FEB\u611F\uFF0C\u4F7F\u5979\u6BCF\u4E00\u5BF8\u808C\u80A4\u90FD\u5E78\u798F\u5730\u6218\u6817\u8D77\u6765\uFF0C\u8FDE\u65E0\u4EBA\u89E6\u6478\u7684\u79D8\u5904\u4E5F\u53D8\u5F97\u5145\u8840\u80BF\u80C0\uFF0C\u4E00\u80A1\u80A1\u6D8C\u51FA\u871C\u6DB2\u3002</p><p>\u5F53\u6C61\u7269\u6389\u5728\u5E8A\u8925\u4E0A\uFF0C\u8427\u4F5B\u5974\u4E0B\u4F53\u4E00\u9635\u6536\u5408\uFF0C\u88AB\u4E2D\u53D1\u51FA\u4E00\u58F0\u67D4\u5A9A\u7684\u4F4E\u53EB\uFF0C\u5B9B\u7136\u662F\u5F53\u65E5\u4E0E\u6155\u5BB9\u9F99\u4EA4\u5408\u65F6\u6781\u7AEF\u6B22\u6109\u7684\u5A9A\u58F0\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u76F8\u987E\u8BB6\u7136\uFF0C\u5F85\u770B\u5230\u8427\u4F5B\u5974\u4E00\u8FB9\u55EF\u55EF\u5514\u5514\u7684\u4F4E\u5598\uFF0C\u4E00\u8FB9\u518D\u6B21\u6392\u51FA\u6C61\u7269\u65F6\uFF0C\u4E24\u5973\u624D\u660E\u767D\u8FC7\u6765\u2014\u2014\u8FD9\u4E2A\u8C8C\u4F3C\u7AEF\u5E84\u7684\u7F8E\u5987\uFF0C\u7ADF\u7136\u5728\u6392\u4FBF\u4E2D\u83B7\u5F97\u5FEB\u611F\u3002</p><p>\u4E24\u5973\u5BF9\u89C6\u4E00\u773C\uFF0C\u5237\u5730\u63ED\u5F00\u9526\u88AB\u3002</p><p>\u8427\u4F5B\u5974\u8273\u4E3D\u7684\u7389\u8138\u987F\u65F6\u8840\u8272\u5168\u65E0\uFF0C\u5979\u5446\u5446\u770B\u7740\u51B7\u7B11\u7684\u4E24\u5973\uFF0C\u534A\u664C\u624D\u671F\u671F\u827E\u827E\u8BF4\u9053\uFF1A\u300C\u59D0\u59D0\u2026\u2026\u6211\u53C8\u62C9\u4E86\u2026\u2026\u300D</p><p>\u300C\u53EB\u554A\uFF1F\u600E\u4E48\u4E0D\u53EB\u4E86\uFF1F\u300D\u767D\u7389\u83BA\u659C\u773C\u7768\u89C6\u7740\u51B7\u51B7\u9053\u3002</p><p>\u8427\u4F5B\u5974\u4FCF\u8138\u4E00\u4E0B\u5B50\u53D8\u5F97\u901A\u7EA2\u3002\u711A\u60C5\u818F\u4E0D\u4EC5\u4F7F\u5979\u7684\u83CA\u809B\u654F\u611F\u4E07\u5206\uFF0C\u800C\u4E14\u8089\u4F53\u603B\u5728\u9965\u6E34\u4E4B\u4E2D\u3002\u513F\u5B50\u6BCF\u5929\u4E00\u6B21\u7684\u809B\u4EA4\u6839\u672C\u65E0\u6CD5\u6EE1\u8DB3\u8EAB\u4F53\u7684\u9700\u8981\uFF0C\u81EA\u5DF1\u53C8\u65E0\u6CD5\u81EA\u6170\uFF0C\u53EA\u597D\u9760\u6392\u4FBF\u65F6\u7528\u7CAA\u4FBF\u78E8\u64E6\u809B\u8089\u6765\u83B7\u5F97\u5FEB\u611F\u3002</p><p>\u8FD9\u7B49\u7F9E\u4E8B\u83AB\u8BF4\u88AB\u4EBA\u770B\u5230\uFF0C\u5C31\u662F\u81EA\u5DF1\u60F3\u4E00\u60F3\u90FD\u96BE\u582A\u5F97\u8981\u6B7B\u3002\u53EF\u8089\u4F53\u7684\u9965\u6E34\u4E00\u65E6\u71C3\u8D77\uFF0C\u65E9\u5DF2\u4E0D\u518D\u77DC\u6301\u7684\u767E\u82B1\u89C2\u97F3\u4FBF\u6C89\u6EBA\u4E8E\u8089\u6B32\u4E4B\u4E2D\uFF0C\u518D\u987E\u4E0D\u5F97\u7F9E\u803B\u548C\u7F6A\u6076\u3002</p><p>\u6B64\u523B\u4E8B\u60C5\u88AB\u4EBA\u63ED\u7A7F\uFF0C\u90A3\u79CD\u803B\u8FB1\u5C31\u50CF\u5728\u4E07\u4EBA\u9762\u524D\u88AB\u8FEB\u4E0E\u4EBA\u4EA4\u5408\u4E00\u822C\u3002\u5979\u7EA2\u5507\u98A4\u6296\u534A\u664C\uFF0C\u4E5E\u6C42\u9053\uFF1A\u300C\u597D\u59D0\u59D0\uFF0C\u6C42\u4F60\u4EEC\u5343\u4E07\u4E0D\u8981\u544A\u8BC9\u9F99\u54E5\u54E5\u2026\u2026\u8FD8\u6709\u6211\u5973\u513F\u2026\u2026\u300D</p><p>\u300C\u4EC0\u4E48\u9F99\u54E5\u54E5\uFF01\u4E0D\u8981\u8138\u7684\u4E1C\u897F\uFF01\u662F\u5BAB\u4E3B\u548C\u5C11\u592B\u4EBA\uFF01\u300D</p><p>\u300C\u6211\u660E\u767D\u4E86\uFF0C\u6C42\u4F60\u4EEC\u5343\u4E07\u4E0D\u8981\u544A\u8BC9\u5BAB\u4E3B\u548C\u5C11\u592B\u4EBA\u3002\u300D</p><p>\u767D\u7389\u83BA\u626C\u8D77\u8138\uFF0C\u7528\u9F3B\u5B54\u54FC\u4E86\u4E00\u58F0\uFF0C\u300C\u8FD9\u4E48\u5927\u7684\u4E8B\uFF0C\u5173\u4E4E\u4E3B\u5B50\u7684\u8138\u9762\uFF0C\u5974\u5A62\u53EF\u4E0D\u6562\u9690\u7792\u3002\u300D</p><p>\u8427\u4F5B\u5974\u6CE3\u6D95\u8FDE\u8FDE\uFF0C\u82E5\u975E\u624B\u811A\u762B\u8F6F\uFF0C\u6B64\u523B\u4FBF\u8981\u8DEA\u5728\u4E24\u5973\u9762\u524D\u8BA8\u9976\uFF0C\u300C\u597D\u59D0\u59D0\u2026\u2026\u6211\u4EE5\u540E\u4E00\u5B9A\u542C\u8BDD\u2026\u2026\u59D0\u59D0\u4E0D\u662F\u559C\u6B22\u5F04\u6211\u7684\u5C41\u80A1\u5417\uFF1F\u6211\u8BA9\u4F60\u4EEC\u5F04\u2026\u2026\u597D\u4E0D\u597D\u3002\u300D</p><p>\u767D\u7389\u83BA\u4E0D\u5C51\u5730\u6487\u6487\u5634\uFF0C\u300C\u53C8\u810F\u53C8\u81ED\uFF0C\u88AB\u4EBA\u73A9\u70C2\u7684\u8D31\u5C41\u80A1\uFF0C\u4F60\u4EE5\u4E3A\u59D1\u5976\u5976\u559C\u6B22\u73A9\u5417\uFF1F\u300D</p><p>\u300C\u597D\u59D0\u59D0\uFF0C\u4F60\u8BA9\u6211\u505A\u4EC0\u4E48\u90FD\u53EF\u4EE5\u2026\u2026\u53EA\u6C42\u2026\u2026\u300D\u8427\u4F5B\u5974\u54ED\u5F97\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u7F8E\u5987\u5C48\u8FB1\u7684\u795E\u6001\u7ED9\u4E86\u4E24\u5973\u6781\u5927\u7684\u6EE1\u8DB3\uFF0C\u4F46\u767D\u7389\u83BA\u4ECD\u4E0D\u4F9D\u4E0D\u9976\uFF1A\u300C\u4F60\u8FD9\u4E2A\u5E9F\u7269\u8FD8\u80FD\u505A\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u6211\u2026\u2026\u6211\u2026\u2026\u300D</p><p>\u300C\u54FC\u54FC\uFF0C\u4F60\u4EE5\u540E\u5C31\u5F53\u6211\u4EEC\u59D0\u59B9\u7684\u73A9\u7269\uFF0C\u6211\u4EEC\u60F3\u600E\u4E48\u73A9\u4F60\u5C31\u600E\u4E48\u73A9\u4F60\uFF0C\u8BA9\u4F60\u54ED\u5C31\u54ED\uFF0C\u8BA9\u4F60\u7B11\u5C31\u7B11\u3002\u300D</p><p>\u300C\u597D\u597D\u3002\u300D\u8427\u4F5B\u5974\u8FDE\u5FD9\u70B9\u5934\uFF0C\u300C\u4ECE\u4ECA\u4EE5\u540E\u6211\u5C31\u662F\u83BA\u59D0\u59D0\u548C\u9E42\u59D0\u59D0\u7684\u73A9\u7269\uFF0C\u59D0\u59D0\u4EEC\u8BF4\u4EC0\u4E48\u6211\u90FD\u7B54\u5E94\u2026\u2026\u300D</p><p>\u300C\u5148\u7B11\u4E00\u4E2A\u3002\u300D</p><p>\u8427\u4F5B\u5974\u5FD9\u6324\u51FA\u4E00\u4E1D\u7B11\u5BB9\uFF0C\u7389\u988A\u4E0A\u8FD8\u6302\u7740\u6676\u83B9\u7684\u6CEA\u73E0\uFF0C\u80A4\u5149\u4E3D\u8272\uFF0C\u51C4\u5A49\u52A8\u4EBA\u3002</p><p>\u300C\u59D0\u59D0\u8BA9\u4F60\u7B11\u5F97\u518D\u5F00\u5FC3\u4E00\u4E9B\u2026\u2026\u300D\u767D\u7389\u9E42\u7B11\u563B\u563B\u8BF4\u7740\uFF0C\u4ECE\u8925\u4E0B\u6478\u51FA\u90A3\u6839\u810F\u516E\u516E\u5730\u6728\u68CD\uFF0C\u5C06\u8427\u4F5B\u5974\u809B\u6D1E\u4E2D\u7684\u534A\u622A\u6C61\u7269\u6345\u4E86\u56DE\u53BB\u3002</p><p>\u8427\u4F5B\u5974\u4E00\u8FB9\u6D41\u6CEA\uFF0C\u4E00\u8FB9\u5F3A\u7B11\u7740\u4EFB\u6728\u68CD\u7B14\u76F4\u6345\u5165\u4F53\u5185\u3002\u5F85\u6728\u68CD\u62BD\u9001\u8D77\u6765\uFF0C\u5979\u8FD8\u8981\u4F9D\u4E24\u5973\u7684\u5429\u5490\u6D6A\u53EB\u8FDE\u58F0\uFF0C\u5FC3\u91CC\u7684\u6ECB\u5473\u82E6\u4E0D\u582A\u8A00\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u7B11\u9010\u989C\u5F00\uFF0C\u6363\u5F97\u6108\u53D1\u7528\u529B\u3002\u7EA2\u5AE9\u7684\u809B\u8089\u4EFF\u4F5B\u4E00\u6735\u76DB\u5F00\u7684\u9C9C\u82B1\uFF0C\u7EFD\u5F00\u5A07\u8273\u7684\u82B1\u74E3\uFF0C\u5C06\u80AE\u810F\u7684\u6728\u68CD\u5C3D\u6570\u541E\u4E0B\u3002\u4E0D\u591A\u65F6\uFF0C\u6CBE\u6EE1\u6C61\u7269\u7684\u83CA\u809B\u6E10\u6E10\u6E7F\u6DA6\uFF0C\u70BD\u70ED\u7684\u809B\u8089\u4EFF\u4F5B\u4E00\u5F20\u70ED\u60C5\u7684\u5C0F\u5634\uFF0C\u7D27\u7D27\u88F9\u4F4F\u68D2\u8EAB\u3002\u800C\u8427\u4F5B\u5974\u67D4\u5A9A\u7684\u53EB\u58F0\u91CC\uFF0C\u4E5F\u591A\u4E86\u4E00\u5206\u6E7F\u6E7F\u7684\u6C34\u610F\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5C31\u5728\u7F8E\u5987\u5728\u8089\u6B32\u4E2D\u8FF7\u5931\u7684\u540C\u65F6\uFF0C\u6155\u5BB9\u7D2B\u73AB\u9762\u4E34\u7740\u7EC8\u751F\u65E0\u6CD5\u5FD8\u6000\u7684\u5C48\u8FB1\u3002</p><p>\u7D2B\u73AB\u62B1\u4F4F\u5C0F\u8179\uFF0C\u8DEA\u4F0F\u7740\u5C06\u81FB\u9996\u6162\u6162\u63A2\u5165\u9634\u53F3\u4F7F\u810F\u4E71\u7684\u6BDB\u53D1\u4E4B\u4E2D\u3002</p><p>\u9634\u957F\u91CE\u88AB\u9501\u5728\u58C1\u95F4\uFF0C\u884C\u52A8\u4E0D\u79BB\u65B9\u5BF8\uFF0C\u5927\u5C0F\u4FBF\u90FD\u76F4\u63A5\u62C9\u5728\u8EAB\u4E0B\u3002\u6BDB\u53D1\u5185\u8FEB\u4EBA\u7684\u6076\u81ED\u51E0\u4E4E\u4F7F\u7D2B\u73AB\u7A92\u606F\u3002\u5979\u5C4F\u4F4F\u547C\u5438\uFF0C\u6478\u7D22\u7740\u62FF\u4F4F\u9634\u51B7\u6C61\u79FD\u7684\u9633\u5177\uFF0C\u5F80\u5507\u95F4\u9001\u53BB\u3002</p><p>\u7EA2\u5507\u521A\u521A\u78B0\u5230\u68D2\u8EAB\uFF0C\u7D2B\u73AB\u7ACB\u5373\u5589\u5934\u4F5C\u54CD\uFF0C\u6B62\u4E0D\u4F4F\u9635\u9635\u4F5C\u5455\u3002\u5979\u8138\u8272\u82CD\u767D\u5730\u94BB\u51FA\u4E71\u53D1\uFF0C\u6025\u4FC3\u5730\u5598\u7740\u6C14\u3002</p><p>\u96BE\u5F97\u80FD\u78B0\u4E0A\u4E2A\u9001\u4E0A\u95E8\u6765\u7684\u5973\u4EBA\uFF0C\u6570\u5341\u5E74\u4E0D\u77E5\u8089\u5473\u7684\u9634\u957F\u91CE\u65E9\u5DF2\u6B32\u706B\u711A\u8EAB\u3002\u4F46\u4E8B\u4E0E\u613F\u8FDD\uFF0C\u957F\u5E74\u5E2D\u5730\u800C\u5750\uFF0C\u6E7F\u51B7\u7684\u5BD2\u6C14\u4FB5\u8680\u4E4B\u4E0B\uFF0C\u9633\u5177\u6B32\u632F\u65E0\u529B\u3002</p><p>\u300C\u4ED6\u5988\u7684\uFF01\u4F60\u4E2A\u8D31\u5974\u8FD8\u6562\u5ACC\u8001\u5B50\u810F\uFF01\u7ED9\u6211\u8214\uFF01\u300D\u9634\u957F\u91CE\u4E00\u628A\u62E7\u4F4F\u7D2B\u73AB\u4E73\u6839\uFF0C\u628A\u4E2A\u897F\u74DC\u822C\u7684\u5706\u4E73\u6525\u5728\u624B\u4E2D\u3002\u4E94\u6307\u7565\u4E00\u7528\u529B\uFF0C\u96EA\u767D\u7684\u4E73\u7403\u7ACB\u5373\u5145\u8840\u53D1\u7EA2\uFF0C\u5C0F\u5DE7\u7684\u4E73\u5934\u66F4\u662F\u6BB7\u7EA2\u593A\u76EE\u3002</p><p>\u5267\u75DB\u6FC0\u8D77\u4E86\u5C11\u5973\u7684\u5014\u5F3A\u3002\u7D2B\u73AB\u54AC\u7D27\u7259\u5173\uFF0C\u5B81\u613F\u4E73\u623F\u88AB\u751F\u751F\u63EA\u6389\u4E5F\u4E0D\u518D\u8BA8\u9976\u3002</p><p>\u300C\u556A\u300D\u7684\u4E00\u58F0\u8106\u54CD\uFF0C\u9634\u957F\u91CE\u4E00\u5DF4\u638C\u6253\u5728\u7D2B\u73AB\u4E73\u4E0A\u3002</p><p>\u4E73\u7403\u4E00\u4FA7\u7ACB\u523B\u6D6E\u8D77\u4E94\u9053\u9752\u7D2B\u8272\u7684\u5370\u8FF9\uFF0C\u9AD8\u9AD8\u80BF\u8D77\u3002</p><p>\u4E73\u623F\u50CF\u88AB\u5229\u5203\u5207\u5F00\u822C\u970D\u970D\u4F5C\u75DB\uFF0C\u7D2B\u73AB\u75DB\u5F97\u51B7\u6C57\u76F4\u5192\uFF0C\u624B\u811A\u4E5F\u4E0D\u7531\u81EA\u4E3B\u5730\u62BD\u52A8\u8D77\u6765\u3002</p><p>\u4E00\u76F4\u54ED\u54ED\u6D95\u6D95\u7684\u7F8E\u5C11\u5973\u50CF\u7A81\u7136\u53D8\u4E86\u4E2A\u4EBA\u822C\u5F3A\u786C\uFF0C\u4EFB\u51ED\u5706\u4E73\u88AB\u634F\u5F97\u80BF\u6DA8\u6B32\u88C2\uFF0C\u8272\u6CFD\u7531\u7EA2\u5230\u7D2B\uFF0C\u6447\u6447\u6B32\u5760\u3002\u53EA\u95ED\u7740\u7F8E\u76EE\uFF0C\u4E00\u8A00\u4E0D\u53D1\u3002\u9634\u957F\u91CE\u5FC3\u4E0B\u5927\u6012\uFF0C\u4E00\u624B\u63EA\u8D77\u4E73\u623F\uFF0C\u4E00\u624B\u63E1\u6307\u6210\u62F3\uFF0C\u84C4\u52BF\u8981\u671D\u7D2B\u73AB\u8179\u4E0A\u6253\u53BB\u3002</p><p>\u4E00\u6EF4\u6E05\u4EAE\u7684\u6C34\u73E0\u4ECE\u949F\u4E73\u77F3\u4E0A\u6ED1\u843D\uFF0C\u6389\u5728\u7D2B\u73AB\u82CD\u767D\u7684\u989D\u5934\u3002\u5979\u7A81\u7136\u7741\u5F00\u773C\uFF0C\u5E73\u9759\u5730\u8BF4\uFF1A\u300C\u653E\u5F00\u6211\u3002\u6211\u8214\u3002\u300D</p><p>\u9634\u957F\u91CE\u4E00\u62F3\u6253\u6298\u8EAB\u65C1\u7684\u77F3\u7B0B\uFF0C\u6296\u624B\u677E\u5F00\u7D2B\u73AB\u7684\u4E73\u7403\u3002</p><p>\u6F6E\u6E7F\u800C\u53C8\u9634\u51B7\u7684\u77F3\u7A9F\u5185\uFF0C\u8D64\u88F8\u7684\u5B55\u5987\u6447\u6643\u7740\u9752\u80BF\u7684\u4E73\u623F\u548C\u6D51\u5706\u7684\u5C0F\u8179\uFF0C\u5A07\u8273\u7684\u4FCF\u8138\u51D1\u5411\u6C61\u79FD\u7684\u602A\u7269\u8EAB\u4E0B\u3002\u72B9\u5982\u5730\u72F1\u4E2D\u7684\u82B1\u95F4\u4ED9\u5B50\uFF0C\u6B63\u5728\u628A\u8089\u4F53\u732E\u7ED9\u72F0\u72DE\u7684\u6076\u9B54\u3002</p><p>\u64A9\u8D77\u9B13\u89D2\u6563\u4E71\u7684\u53D1\u4E1D\uFF0C\u7D2B\u73AB\u5F20\u5F00\u7EA2\u5507\uFF0C\u7389\u5BB9\u65E0\u6CE2\u7684\u542B\u4F4F\u51B0\u51B7\u7684\u8089\u68D2\u3002\u5979\u7684\u52A8\u4F5C\u7565\u663E\u751F\u758F\uFF0C\u4F46\u5341\u5206\u5C3D\u529B\uFF0C\u9999\u820C\u4E0D\u4EC5\u5212\u8FC7\u9F9F\u5934\uFF0C\u8FD8\u5C06\u5305\u76AE\u5185\u7684\u6C61\u57A2\u4E00\u4E00\u8214\u5C3D\u3002</p><p>\u65B9\u624D\u4E73\u623F\u65E0\u6CD5\u6297\u62D2\u7684\u5267\u75DB\u4E2D\uFF0C\u7D2B\u73AB\u7B2C\u4E00\u6B21\u611F\u89C9\u5230\u6B7B\u4EA1\u79BB\u81EA\u5DF1\u5982\u6B64\u4E4B\u8FD1\u3002\u5BF9\u5979\u6765\u8BF4\uFF0C\u6B7B\u4EA1\u672C\u8EAB\u4E5F\u8BB8\u5E76\u4E0D\u53EF\u6015\uFF0C\u771F\u6B63\u53EF\u6015\u7684\u662F\u6D3B\u5728\u75DB\u82E6\u4E2D\u7684\u4EB2\u4EBA\u3002\u5C11\u5973\u5C06\u6CEA\u6C34\u548C\u57A2\u7269\u4E00\u5E76\u541E\u5165\u8179\u5185\uFF0C\u6697\u6697\u9053\uFF1A\u65E0\u8BBA\u9047\u5230\u4EC0\u4E48\u5883\u51B5\uFF0C\u6211\u4E00\u5B9A\u52AA\u529B\u6D3B\u4E0B\u53BB\uFF0C\u76F4\u5230\u628A\u4F60\u4EEC\u5168\u90FD\u89E3\u6551\u51FA\u6765\u3002</p><p>\u8214\u4E86\u534A\u4E2A\u65F6\u8FB0\uFF0C\u9633\u5177\u4ECD\u7136\u6BEB\u65E0\u52A8\u9759\u3002\u7D2B\u73AB\u7684\u5507\u820C\u4ECD\u50CF\u6700\u521D\u4E00\u6837\u7528\u529B\uFF0C\u9634\u957F\u91CE\u5374\u4E0D\u8010\u70E6\u4E86\u3002\u4ED6\u7422\u78E8\u534A\u5929\uFF0C\u5927\u6982\u5728\u5730\u7262\u7684\u65F6\u95F4\u592A\u957F\uFF0C\u5FD8\u4E86\u5973\u4EBA\u7684\u6A21\u6837\u5427\u3002</p><p>\u9634\u957F\u91CE\u62E7\u4F4F\u7D2B\u73AB\u7684\u79C0\u53D1\uFF0C\u628A\u5979\u63A8\u5F97\u4EF0\u5750\u5728\u5730\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u63B0\u5F00\u4F60\u7684\u5C44\uFF0C\u73A9\u7ED9\u8001\u5B50\u770B\u770B\uFF01\u300D</p><p>\u7D2B\u73AB\u5A07\u8EAF\u4E00\u98A4\uFF0C\u65CB\u5373\u5E73\u9759\u4E0B\u6765\u3002\u5979\u7528\u624B\u80CC\u64E6\u53BB\u5507\u89D2\u7684\u6B8B\u6DB2\uFF0C\u7136\u540E\u9760\u5728\u4E00\u6839\u77F3\u7B0B\u4E0A\uFF0C\u66F2\u819D\u5206\u5F00\u53CC\u817F\u3002</p><p>\u5706\u6EDA\u6EDA\u7684\u5C0F\u8179\u963B\u788D\u4E86\u7D2B\u73AB\u7684\u89C6\u7EBF\uFF0C\u5979\u4E0D\u77E5\u9053\u81EA\u5DF1\u4E0B\u4F53\u5DF2\u7ECF\u544A\u522B\u4E86\u5C11\u5973\u7F9E\u6DA9\u7684\u7C89\u7EA2\u3002\u7CBE\u81F4\u7684\u7389\u6237\u5B9B\u5982\u4E00\u6735\u82AC\u82B3\u7684\u9C9C\u82B1\uFF0C\u4FCF\u751F\u751F\u5D4C\u5728\u8179\u4E0B\u3002\u56E0\u598A\u5A20\u800C\u5145\u8840\u7684\u82B1\u74E3\u5F62\u72B6\u9971\u6EE1\uFF0C\u8272\u6CFD\u9C9C\u8273\uFF0C\u6BCF\u4E00\u4E2A\u7EC6\u5C0F\u7684\u8936\u76B1\u90FD\u53D8\u5F97\u5706\u6DA6\uFF0C\u5145\u6EE1\u6210\u719F\u7684\u97F5\u5473\u3002\u6B64\u65F6\uFF0C\u5A07\u5AE9\u7684\u82B1\u74E3\u95F4\u8FD8\u6CBE\u7740\u4E00\u7F15\u523A\u76EE\u7684\u6BB7\u7EA2\uFF0C\u90A3\u662F\u8089\u7A74\u6DF1\u5904\u7684\u4F24\u53E3\u6240\u6DCC\u51FA\u7684\u9C9C\u8840\u3002</p><p>\u9634\u957F\u91CE\u8214\u8214\u5634\u5507\uFF0C\u602A\u7B11\u9053\uFF1A\u300C\u5927\u809A\u5A4A\u5B50\uFF0C\u4F60\u7684\u5C44\u597D\u751F\u6807\u81F4\uFF0C\u6BD4\u8001\u5B50\u7684\u8D31\u5973\u4EBA\u8FD8\u5F3A\u4E9B\u3002\u88AB\u591A\u5C11\u4EBA\u64CD\u8FC7\uFF1F\u300D</p><p>\u300C\u2026\u2026\u4E0D\u77E5\u9053\u3002\u300D</p><p>\u300C\u6731\u90AA\u9752\u6811\u90A3\u738B\u516B\u86CB\u4E5F\u4E0D\u4F1A\u8BA9\u4F60\u95F2\u7740\uFF0C\u6BCF\u5929\u5C11\u8BF4\u4E5F\u8981\u88AB\u64CD\u4E2A\u56DB\u4E94\u5341\u56DE\u5427\u3002\u6478\u8D77\u6765\u8FD8\u7D27\u51D1\u51D1\u7684\u2014\u2014\u8FC7\u6765\u8BA9\u8001\u5B50\u770B\u6E05\u4E9B\uFF01\u300D</p><p>\u7D2B\u73AB\u5403\u529B\u5730\u722C\u8D77\u6765\uFF0C\u7AD9\u5728\u9634\u957F\u91CE\u9762\u524D\uFF0C\u6258\u8D77\u8179\u7403\uFF0C\u5C06\u79D8\u5904\u66B4\u9732\u5728\u4ED6\u707C\u707C\u7684\u76EE\u5149\u4E0B\u3002</p><p>\u9634\u957F\u91CE\u4E3E\u8D77\u591C\u660E\u73E0\uFF0C\u561F\u56D4\u7740\u8BF4\uFF1A\u300C\u4ED6\u5A18\u7684\uFF0C\u8981\u6709\u8361\u661F\u97AD\u91CC\u91CC\u5916\u5916\u90FD\u80FD\u770B\u4E2A\u6E05\u695A\u2026\u2026\u300D</p><p>\u85C9\u7740\u73E0\u8F89\u770B\u4E86\u7247\u523B\uFF0C\u9634\u957F\u91CE\u9762\u9732\u559C\u8272\uFF0C\u300C\u540D\u5668\uFF01\u771F\u4FBF\u5B9C\u90A3\u5E2E\u5154\u5D3D\u5B50\u4E86\u3002\u300D</p><p>\u4ED6\u628A\u9E3D\u86CB\u5927\u5C0F\u7684\u660E\u73E0\u6D45\u6D45\u585E\u5728\u8089\u7A74\u5185\uFF0C\u559D\u9053\uFF1A\u300C\u5FEB\u6478\uFF01\u8BA9\u8001\u5B50\u770B\u770B\u9634\u7CBE\u7684\u6210\u8272\uFF01\u300D</p><p>\u7D2B\u73AB\u80A1\u95F4\u5927\u653E\u5149\u660E\uFF0C\u73E0\u8F89\u6620\u7167\u4E0B\uFF0C\u7389\u6237\u6108\u53D1\u7EA2\u5AE9\u3002\u5979\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u7EA4\u624B\u7ED5\u5230\u8179\u4E0B\uFF0C\u5265\u5F00\u82B1\u74E3\uFF0C\u7EC6\u7EC6\u63C9\u6413\u3002</p><p>\u7EC6\u767D\u7684\u624B\u6307\u4EFF\u4F5B\u660E\u7389\u96D5\u5C31\uFF0C\u5728\u6ED1\u817B\u7684\u82B1\u74E3\u95F4\u67D4\u67D4\u7A7F\u68AD\uFF0C\u7F8E\u8273\u65E0\u6BD4\u3002\u9634\u957F\u91CE\u8D34\u5728\u7D2B\u73AB\u6C89\u7538\u7538\u7684\u5C0F\u8179\u4E0A\uFF0C\u76EE\u4E0D\u8F6C\u775B\u5730\u76EF\u7740\u5979\u7684\u52A8\u4F5C\uFF0C\u5589\u7ED3\u4E0A\u4E0B\u4E71\u6EDA\u3002</p><p>\u534A\u4E2A\u65F6\u8FB0\u540E\uFF0C\u7D2B\u73AB\u5A07\u8EAF\u4E00\u98A4\uFF0C\u7EA2\u561F\u561F\u7684\u8089\u7A74\u50CF\u4E00\u5F20\u987D\u76AE\u7684\u5C0F\u5634\uFF0C\u4E00\u80A1\u4E00\u80A1\u5410\u51FA\u6D4A\u767D\u7684\u9634\u7CBE\u3002</p><p>\u300C\u5176\u767D\u5982\u4E73\uFF0C\u5176\u6D53\u5982\u8102\uFF0C\u679C\u7136\u662F\u4E07\u91CC\u65E0\u4E00\u7684\u540D\u5668\u2026\u2026\u300D\u9634\u957F\u91CE\u9605\u5973\u65E0\u6570\uFF0C\u4E00\u89C1\u4FBF\u77E5\u7D2B\u73AB\u4E0D\u4EC5\u5929\u8D4B\u5F02\u7980\uFF0C\u800C\u4E14\u6709\u5947\u529F\u5728\u8EAB\u3002</p><p>\u59D7\u59D7\u6765\u8FDF\u7684\u9AD8\u6F6E\u8017\u5C3D\u4E86\u7D2B\u73AB\u7684\u4F53\u529B\uFF0C\u5979\u53CC\u817F\u4E00\u8F6F\uFF0C\u5728\u9AD8\u6F6E\u4E2D\u660F\u8FF7\u8FC7\u53BB\u3002</p>',60);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
