import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_113" tabindex="-1"><a class="header-anchor" href="#_113" aria-hidden="true">#</a> 113</h1><p>\u8427\u4F5B\u5974\u4E0B\u4F53\u4E00\u7247\u72FC\u85C9\u3002\u5979\u5DF2\u7ECF\u88AB\u767D\u6C0F\u59D0\u59B9\u6574\u6574\u6298\u78E8\u4E86\u56DB\u4E2A\u65F6\u8FB0\uFF0C\u5728\u8FD9\u56DB\u4E2A\u65F6\u8FB0\u4E2D\uFF0C\u6728\u68D2\u6BEB\u4E0D\u505C\u6B47\u5730\u8F6E\u756A\u8FDB\u5165\u5979\u7684\u4E24\u4E2A\u8089\u7A74\uFF0C\u5C31\u8FDE\u5348\u996D\u65F6\uFF0C\u4E5F\u4E00\u76F4\u63D2\u5728\u4F53\u5185\u3002</p><p>\u9891\u7E41\u7684\u9AD8\u6F6E\u4F7F\u7F8E\u5987\u7CBE\u75B2\u529B\u5C3D\uFF0C\u5F53\u6728\u68D2\u53C8\u4E00\u6B21\u8FDB\u5165\u83CA\u809B\uFF0C\u8427\u4F5B\u5974\u545C\u54BD\u9053\uFF1A\u300C\u597D\u59D0\u59D0\uFF0C\u8BA9\u6211\u4F11\u606F\u4E00\u4F1A\u513F\u5427\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u624B\u8155\u4E5F\u6709\u4E9B\u53D1\u9178\uFF0C\u5979\u91CD\u91CD\u4E00\u63A8\uFF0C\u5C06\u80A0\u9053\u5185\u6EE1\u6EA2\u7684\u871C\u6C41\u548C\u6C61\u7269\u6324\u5F97\u56DB\u4E0B\u98DE\u6E85\uFF0C\u8FD9\u624D\u51B7\u7B11\u9053\uFF1A\u300C\u8FD8\u6709\u4E09\u6B21\uFF0C\u51D1\u591F\u5341\u6B21\u4ECA\u5929\u5C31\u653E\u8FC7\u4F60\u3002\u300D</p><p>\u8427\u4F5B\u5974\u542B\u6CEA\u9053\uFF1A\u300C\u5974\u5BB6\u7684\u8D31\u5C44\u5DF2\u7ECF\u88AB\u643E\u5E72\u4E86\u2026\u2026\u300D</p><p>\u300C\u54DF\u2026\u2026\u300D\u767D\u7389\u83BA\u62D6\u957F\u58F0\u97F3\uFF0C\u624B\u8155\u4E00\u62E7\uFF0C\u6728\u68CD\u5728\u83CA\u6D1E\u5185\u65CB\u8F6C\u4E00\u5468\uFF0C\u4E0D\u7B49\u8427\u4F5B\u5974\u53EB\u75DB\uFF0C\u4FBF\u62D4\u4E86\u51FA\u6765\u3002\u63A5\u7740\u72E0\u72E0\u6345\u5165\u67D4\u7F8E\u7684\u82B1\u74E3\u4E2D\u3002</p><p>\u8427\u4F5B\u5974\u8179\u7403\u4E00\u9635\u6643\u8361\uFF0C\u7EA2\u5507\u98A4\u6296\u3002</p><p>\u6CBE\u6EE1\u6C61\u7269\u7684\u6728\u68D2\u91CD\u65B0\u62D4\u51FA\u65F6\uFF0C\u5DF2\u53D8\u5F97\u5E72\u5E72\u51C0\u51C0\u3002\u5438\u9971\u4E86\u6DEB\u6DB2\u3001\u871C\u6C41\u7684\u68D2\u8EAB\u53C8\u5149\u53C8\u6ED1\uFF0C\u51E0\u4E4E\u80FD\u6620\u51FA\u8273\u7EA2\u7684\u8089\u8272\u3002</p><p>\u5F00\u59CB\u8427\u4F5B\u5974\u66FE\u4E5E\u6C42\u4E24\u5973\u5C06\u6728\u68D2\u64E6\u51C0\u518D\u63D2\u8FDB\u81EA\u5DF1\u9634\u4E2D\uFF0C\u7ED3\u679C\u662F\u5979\u7528\u9999\u820C\u628A\u6C61\u7269\u8214\u51C0\u3002\u5F53\u810F\u81ED\u7684\u6728\u68D2\u518D\u4E00\u6B21\u4F38\u8FDB\u4E0B\u4F53\uFF0C\u5979\u4E00\u53E5\u8BDD\u90FD\u4E0D\u6562\u8BF4\u3002\u90A3\u4E00\u523B\uFF0C\u7F8E\u5987\u610F\u8BC6\u5230\u81EA\u5DF1\u7684\u8089\u4F53\u4ECE\u91CC\u5230\u5916\uFF0C\u518D\u6CA1\u6709\u534A\u5206\u6D01\u51C0\u3002</p><p>\u79D8\u5904\u7684\u60B8\u52A8\u4E2D\uFF0C\u8427\u4F5B\u5974\u604D\u7136\u60F3\u8D77\u4E00\u4E2A\u6545\u4E8B\uFF1A\u6709\u4E00\u4E2A\u4EBA\u4E70\u4E86\u53CC\u65B0\u978B\uFF0C\u7B2C\u4E00\u6B21\u7A7F\u5C31\u78B0\u4E0A\u96E8\u5929\u3002\u5F00\u59CB\u4ED6\u5F88\u5C0F\u5FC3\u5730\u907F\u5F00\u6CE5\u6CDE\u3002\u4F46\u8D70\u5230\u534A\u8DEF\uFF0C\u4E00\u4E0D\u5C0F\u5FC3\u5F00\u810F\u4E86\u978B\u5B50\u3002\u540E\u6765\u6CE5\u6C34\u8D8A\u6765\u8D8A\u591A\uFF0C\u987E\u5FCC\u8D8A\u6765\u8D8A\u5C11\u2026\u2026\u7F8E\u5987\u75B2\u5026\u5730\u7B11\u4E86\u4E00\u4E0B\uFF0C\u653E\u677E\u7D27\u5F20\u7684\u808C\u8089\u3002</p><p>\u6728\u68D2\u5728\u8089\u7A74\u53FD\u53FD\u4F5C\u54CD\uFF0C\u6B63\u5728\u6324\u5976\u7684\u767D\u7389\u9E42\u7B11\u9053\uFF1A\u300C\u8D31\u4EBA\uFF0C\u91CC\u9762\u8FD8\u6709\u597D\u591A\u6C34\u513F\u5462\u3002\u300D</p><p>\u300C\u59D0\u59D0\u8BF4\u7684\u662F\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u4E00\u8FB9\u6363\uFF0C\u4E00\u8FB9\u5728\u7F8E\u5987\u82B1\u8482\u4E0A\u4E00\u6390\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u53C8\u5FD8\u4E86\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u4F4E\u4F4E\u5598\u4E86\u53E3\u6C14\uFF0C\u300C\u554A\u2026\u2026\u554A\u2026\u2026\u300D\u5A9A\u53EB\u8D77\u6765\u3002</p><p>\u6728\u68D2\u5728\u9634\u961C\u4E0B\u98DE\u821E\u7740\u76F4\u8FDB\u76F4\u51FA\uFF0C\u767D\u817B\u7684\u7389\u817F\u95F4\uFF0C\u6E85\u843D\u7740\u5F62\u5F62\u8272\u8272\u7684\u6DEB\u6C34\u3001\u871C\u6C41\u3001\u5C3F\u6DB2\u3001\u9634\u7CBE\u3001\u7CAA\u4FBF\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E0B\u4F53\u7684\u75BC\u75DB\u6CE2\u6D6A\u822C\u6D8C\u6765\uFF0C\u7D2B\u73AB\u60A0\u60A0\u9192\u8F6C\uFF0C\u53D1\u73B0\u81EA\u5DF1\u5934\u4E0B\u811A\u4E0A\uFF0C\u5782\u5728\u534A\u7A7A\u3002\u4E00\u56E2\u6BDB\u8338\u8338\u7684\u7269\u4F53\u6B63\u5728\u81EA\u5DF1\u80A1\u95F4\u4E0D\u4F4F\u8D77\u843D\u3002\u5979\u8EAB\u5B50\u5FAE\u5FAE\u4E00\u52A8\uFF0C\u624D\u53D1\u73B0\u81EA\u5DF1\u4E24\u817F\u88AB\u90A3\u4EBA\u5F2F\u66F2\u7740\u642D\u5728\u80A9\u4E0A\uFF0C\u4E00\u5F20\u8D2A\u5A6A\u5730\u5927\u53E3\u5728\u79D8\u5904\u53C8\u5438\u53C8\u54AC\u3002\u5979\u547B\u541F\u4E00\u58F0\uFF0C\u8F7B\u8F7B\u626D\u52A8\u8170\u80A2\uFF0C\u60F3\u6446\u8131\u90A3\u5F20\u6EE1\u662F\u80E1\u987B\u7684\u5634\u5DF4\u3002</p><p>\u4E0B\u4F53\u4E00\u75DB\uFF0C\u9634\u957F\u91CE\u628A\u82B1\u74E3\u54AC\u5728\u9F7F\u95F4\uFF0C\u53E3\u9F7F\u4E0D\u6E05\u5730\u8BF4\uFF1A\u300C\u518D\u52A8\uFF0C\u8001\u5B50\u5C31\u628A\u4F60\u7684\u5C44\u54AC\u6389\uFF01\u300D</p><p>\u7D2B\u73AB\u6342\u4F4F\u9762\u5B54\uFF0C\u65E0\u58F0\u7684\u62BD\u6CE3\u7740\u3002\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u5979\u5B81\u613F\u88AB\u6155\u5BB9\u9F99\u90A3\u4E2A\u6DF7\u86CB\u5F3A\u5978\u5341\u6B21\uFF0C\u4E5F\u4E0D\u613F\u88AB\u8FD9\u4E48\u4E2A\u602A\u7269\u770B\u4E00\u773C\u3002\u53EF\u73B0\u5728\u81EA\u5DF1\u7ADF\u7136\u9001\u4E0A\u95E8\u6765\uFF0C\u88AB\u4ED6\u8086\u610F\u6DEB\u8FB1\u3002</p><p>\u2026\u2026\u600E\u4E48\u4F1A\u8FD9\u6837\uFF1F</p><p>\u534A\u664C\uFF0C\u9634\u957F\u91CE\u6536\u56DE\u820C\u5934\uFF0C\u95EE\u9053\uFF1A\u300C\u4F60\u662F\u4F55\u6D3E\u5F1F\u5B50\uFF1F\u300D</p><p>\u300C\u516B\u2026\u2026\u516B\u6781\u95E8\u3002\u300D</p><p>\u7D2B\u73AB\u6CC4\u8EAB\u65F6\u9634\u957F\u91CE\u53D1\u89C9\u6709\u5F02\uFF0C\u63A2\u7A76\u4E4B\u4E0B\uFF0C\u624D\u53D1\u73B0\u6B64\u5973\u5E76\u672A\u88AB\u5E9F\u6389\u6B66\u529F\uFF0C\u800C\u662F\u88AB\u6559\u4E2D\u6781\u5C11\u7528\u7684\u91CD\u697C\u6C14\u9501\u5236\u4F4F\u5185\u606F\u3002</p><p>\u516B\u6781\u95E8\u5D1B\u8D77\u662F\u8FD1\u5341\u51E0\u5E74\u4E4B\u4E8B\uFF0C\u9634\u957F\u91CE\u88AB\u56DA\u65F6\u8FD8\u7B97\u4E0D\u4E0A\u540D\u95E8\u5927\u6D3E\u3002\u5373\u975E\u6559\u4E0B\u6240\u5C5E\u638C\u95E8\uFF0C\u53C8\u975E\u6559\u4E2D\u683D\u57F9\u7684\u540D\u82B1\uFF0C\u533A\u533A\u4E00\u4E2A\u5974\u5A62\uFF0C\u53EA\u51ED\u59FF\u8272\u7ADF\u53D7\u5230\u5982\u6B64\u6B3E\u5F85\uFF0C\u4ED6\u4E0D\u89C9\u5FC3\u4E0B\u5947\u602A\uFF1A\u8FD9\u5A46\u5A18\u53EA\u957F\u5F97\u6807\u81F4\u4E9B\uFF0C\u53C8\u751F\u5F97\u4E00\u4E2A\u597D\u5C44\uFF0C\u5C31\u88AB\u5F53\u6210\u5B9D\u8D1D\uFF0C\u53EF\u4E0D\u50CF\u662F\u795E\u6559\u7684\u4F5C\u98CE\u3002</p><p>\u9634\u957F\u91CE\u5BF9\u91CD\u697C\u6C14\u9501\u77E5\u4E4B\u751A\u6DF1\uFF0C\u900F\u8FC7\u5E26\u8109\u4E0E\u7D2B\u73AB\u51DD\u805A\u7684\u771F\u6C14\u7565\u4E00\u63A5\u89E6\uFF0C\u8D6B\u7136\u53D1\u73B0\u6B64\u5973\u771F\u6C14\u4E4B\u5F3A\u4E0E\u81EA\u5DF1\u76F8\u5DEE\u65E0\u51E0\uFF0C\u6BD4\u5F53\u65E5\u7684\u9634\u59EC\u8FD8\u8981\u5F3A\u4E0A\u51E0\u5206\u3002\u4ED6\u542C\u8BF4\u8FC7\u516B\u6781\u95E8\u7684\u516D\u5408\u529F\u522B\u5177\u4E00\u683C\uFF0C\u5374\u4E0D\u77E5\u5176\u5E95\u7EC6\u3002\u5982\u679C\u5979\u771F\u662F\u516B\u6781\u95E8\u5F1F\u5B50\uFF0C\u8FD9\u4E2A\u5B89\u5B9A\u7684\u5C0F\u5E2E\u4E3A\u4F55\u5BC2\u5BC2\u65E0\u540D\uFF1F</p><p>\u4EE5\u4E00\u4E2A\u5C48\u8FB1\u7684\u59FF\u52BF\u655E\u9732\u8EAB\u4F53\uFF0C\u88AB\u4EBA\u4E0B\u6D41\u5730\u54C1\u5482\u7F9E\u5904\uFF0C\u90A3\u79CD\u906D\u5230\u5F3A\u66B4\u7684\u803B\u8FB1\u4F7F\u7D2B\u73AB\u7F9E\u6124\u6B32\u7EDD\u3002\u767D\u817B\u7684\u4E73\u7403\u5782\u5728\u8138\u4FA7\uFF0C\u4E0D\u4F4F\u6643\u52A8\u7740\u6253\u5728\u6843\u816E\u4E0A\u3002\u5979\u7F9E\u6124\u5730\u62B1\u4F4F\u5706\u4E73\uFF0C\u601D\u7D22\u7740\u5982\u4F55\u8131\u8EAB\u3002</p><p>\u89D2\u843D\u91CC\u4F20\u6765\u6089\u6089\u7D22\u7D22\u7684\u8F7B\u54CD\uFF0C\u9634\u957F\u91CE\u4E24\u773C\u4E00\u7FFB\uFF0C\u4E94\u6307\u5F2F\u66F2\u4F5C\u52BF\uFF0C\u4E00\u80A1\u52B2\u6C14\u76F4\u903C\u8FC7\u53BB\u3002</p><p>\u300C\u5431\u5431\u300D\u51E0\u58F0\u54CD\u52A8\uFF0C\u4E00\u56E2\u9ED1\u5F71\u51CC\u7A7A\u843D\u5165\u9634\u957F\u91CE\u624B\u4E2D\u3002\u644A\u5F00\u624B\u638C\uFF0C\u5374\u662F\u4E00\u53EA\u7070\u6251\u6251\u7684\u8001\u9F20\u3002</p><p>\u9634\u957F\u91CE\u4E00\u53E3\u5C06\u8001\u9F20\u54AC\u4E0B\u534A\u53EA\uFF0C\u4E00\u8FB9\u5634\u56BC\uFF0C\u4E00\u8FB9\u9A82\u9A82\u54A7\u54A7\uFF1A\u300C\u4ED6\u5988\u7684\uFF0C\u8FD9\u6B7B\u8017\u5B50\u53C8\u7626\u53C8\u5C0F\uFF0C\u6CA1\u6ECB\u6CA1\u5473\u2026\u2026\u300D</p><p>\u8BF4\u8BDD\u95F4\uFF0C\u9F20\u6BDB\u9F20\u8840\u4ECE\u9F7F\u7F1D\u4E2D\u4E0D\u4F4F\u6389\u843D\uFF0C\u5269\u4E0B\u7684\u534A\u53EA\u9F20\u8EAB\u8FD8\u5728\u638C\u4E2D\u8815\u8815\u800C\u52A8\u3002</p><p>\u770B\u5230\u79CD\u6076\u5FC3\u800C\u53C8\u6050\u6016\u7684\u666F\u8C61\uFF0C\u7D2B\u73AB\u9669\u4E9B\u53C8\u6655\u4E86\u8FC7\u53BB\u3002</p><p>\u7247\u523B\u95F4\u4E00\u53EA\u6D3B\u751F\u751F\u7684\u8001\u9F20\u4FBF\u88AB\u9634\u957F\u91CE\u76AE\u9AA8\u65E0\u5B58\u7684\u541E\u5165\u809A\u5185\uFF0C\u4ED6\u610F\u72B9\u672A\u5C3D\u7684\u8214\u8214\u624B\u6307\uFF0C\u7136\u540E\u53C8\u671D\u7D2B\u73AB\u8EAB\u4E0B\u8214\u53BB\u3002</p><p>\u60F3\u5230\u4ED6\u521A\u5403\u8FC7\u8001\u9F20\u7684\u5634\u5DF4\uFF0C\u7D2B\u73AB\u6D51\u8EAB\u987F\u65F6\u6CDB\u8D77\u4E00\u5C42\u8089\u7C92\uFF0C\u5979\u8FDE\u5FD9\u8BF4\u9053\uFF1A\u300C\u524D\u8F88\uFF0C\u524D\u8F88\uFF0C\u7A0D\u7B49\u4E00\u4E0B\u3002\u300D</p><p>\u9634\u957F\u91CE\u62AC\u8D77\u5934\uFF0C\u76EE\u5149\u8D8A\u8FC7\u9AD8\u8038\u7684\u5C0F\u8179\uFF0C\u843D\u5728\u5979\u8138\u4E0A\u3002</p><p>\u7D2B\u73AB\u6446\u51FA\u4E00\u5F20\u7B11\u8138\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u5974\u5A62\u5728\u8FD9\u91CC\u5F85\u5F97\u592A\u4E45\u4E86\uFF0C\u6015\u5BAB\u91CC\u6709\u4EBA\u8D77\u7591\u2026\u2026\u6211\u660E\u5929\u518D\u6765\u966A\u60A8\u597D\u5417\uFF1F\u300D</p><p>\u9634\u957F\u91CE\u62E7\u4F4F\u5979\u7684\u819D\u5F2F\u7528\u529B\u4E00\u5206\uFF0C\u5C06\u7D2B\u73AB\u4E24\u817F\u63B0\u6210\u4E00\u5B57\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u8001\u5B50\u8FD8\u6CA1\u6709\u64CD\u4F60\uFF0C\u5C31\u60F3\u8DD1\uFF1F\u300D</p><p>\u7D2B\u73AB\u5A07\u5A9A\u5730\u7728\u7740\u773C\u775B\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5974\u5A62\u8FD8\u662F\u7B2C\u4E00\u6B21\u89C1\u5230\u9634\u53F3\u4F7F\u8FD9\u6837\u7684\u82F1\u96C4\u4EBA\u7269\uFF0C\u6068\u4E0D\u80FD\u966A\u5728\u524D\u8F88\u8EAB\u8FB9\uFF0C\u597D\u597D\u4F3A\u5019\u524D\u8F88\u3002\u53EF\u5974\u5A62\u53EA\u662F\u5BAB\u91CC\u7684\u4E0B\u4EBA\uFF0C\u5982\u679C\u88AB\u4EBA\u53D1\u73B0\uFF0C\u5974\u5A62\u53EA\u662F\u4E00\u6B7B\u800C\u5DF2\uFF0C\u4F46\u5982\u679C\u5BB3\u4E86\u524D\u8F88\uFF0C\u7F6A\u5B7D\u53EF\u5C31\u5927\u4E86\u2026\u2026\u300D</p><p>\u9634\u957F\u91CE\u4E00\u8138\u51B7\u7B11\uFF0C\u4ED6\u6A2A\u884C\u6C5F\u6E56\u7684\u65F6\u5019\uFF0C\u7D2B\u73AB\u8FD8\u5728\u5A18\u80CE\u91CC\uFF0C\u533A\u533A\u51E0\u53E5\u8BDD\u600E\u4E48\u80FD\u6253\u52A8\u4ED6\u3002</p><p>\u6E7F\u6F09\u6F09\u7684\u4E0B\u4F53\u655E\u9732\u5728\u5916\uFF0C\u5BD2\u610F\u4FB5\u4EBA\u3002\u7D2B\u73AB\u5FCD\u4F4F\u6218\u6817\uFF0C\u7EC6\u58F0\u9053\uFF1A\u300C\u9634\u53F3\u4F7F\u662F\u82F1\u96C4\u597D\u6C49\uFF0C\u65AD\u7136\u4E0D\u4F1A\u4E3A\u96BE\u5974\u5A62\u3002\u5974\u5A62\u56DE\u53BB\u540E\u7ED9\u60A8\u51C6\u5907\u4E00\u4E9B\u98DF\u7269\uFF0C\u660E\u5929\u7ED9\u60A8\u9001\u6765\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u9634\u957F\u91CE\u624B\u81C2\u4E00\u5C55\uFF0C\u6293\u4F4F\u7D2B\u73AB\u7684\u4E73\u5C16\uFF0C\u5C06\u5979\u63D0\u5230\u9762\u524D\uFF0C\u300C\u81ED\u5A4A\u5B50\uFF0C\u8001\u5B50\u770B\u4F60\u6C34\u7075\u7075\u767D\u5AE9\u5AE9\uFF0C\u5403\u8D77\u6765\u80AF\u5B9A\u591F\u5473\u3002\u300D</p><p>\u7D2B\u73AB\u770B\u7740\u4ED6\u950B\u5229\u7684\u7259\u9F7F\uFF0C\u5FC3\u5E95\u60E7\u610F\u5347\u8D77\u3002\u5979\u5403\u529B\u7684\u59A9\u5A9A\u4E00\u7B11\uFF0C\u5FFD\u7136\u8111\u4E2D\u7075\u5149\u4E00\u95EA\uFF0C\u8BF4\u9053\uFF1A\u300C\u524D\u8F88\u5728\u8FD9\u91CC\u56F0\u4E86\u8FD9\u4E48\u4E45\uFF0C\u5916\u754C\u4E00\u65E0\u6240\u77E5\uFF0C\u5974\u5A62\u8FD9\u5C31\u53BB\u7980\u62A5\u5BAB\u4E3B\uFF0C\u8BF7\u9634\u53F3\u4F7F\u56DE\u5BAB\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u54FC\u54FC\u2026\u2026\u300D\u9634\u957F\u91CE\u51B7\u7B11\u8FDE\u8FDE\uFF0C\u300C\u60F3\u627E\u4EBA\u6740\u6211\uFF1F\u300D</p><p>\u300C\u5974\u5A62\u4E0D\u6562\uFF01\u300D\u7D2B\u73AB\u4E00\u8138\u60F6\u7136\uFF0C\u300C\u5974\u5A62\u53EA\u662F\u60F3\u5E2E\u524D\u8F88\u79BB\u5F00\u6B64\u5904\u2026\u2026\u524D\u8F88\u6709\u6CA1\u6709\u4EC0\u4E48\u597D\u53CB\uFF1F\u5974\u5A62\u53EF\u4EE5\u5E2E\u60A8\u2026\u2026\u300D</p><p>\u9634\u957F\u91CE\u795E\u8272\u4E00\u52A8\uFF0C\u534A\u664C\u9053\uFF1A\u300C\u8001\u6C90\u8FD8\u6D3B\u7740\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u559C\u9053\uFF1A\u300C\u60A8\u662F\u8BF4\u6C90\u62A4\u6CD5\u5417\uFF1F\u8FD8\u5728\u3002\u300D</p><p>\u300C\u62A4\u6CD5\uFF1F\u51E0\u5341\u5E74\u624D\u6DF7\u5230\u62A4\u6CD5\uFF0C\u8001\u6C90\u771F\u662F\u767D\u6D3B\u4E86\u3002\u300D\u9634\u957F\u91CE\u51DD\u795E\u601D\u7D22\u534A\u664C\uFF0C\u53C8\u6447\u4E86\u6447\u5934\u3002\u5F53\u521D\u56E0\u4E3A\u9634\u59EC\u4E4B\u4E8B\uFF0C\u4E24\u4EBA\u867D\u7136\u6CA1\u6709\u7834\u8138\uFF0C\u4F46\u4E5F\u4E0D\u76F8\u5F80\u6765\u3002\u82E5\u975E\u4ED6\u7684\u538B\u5236\uFF0C\u6C90\u58F0\u4F20\u4E8C\u5341\u5E74\u524D\u5C31\u8BE5\u5F53\u4E0A\u62A4\u6CD5\u2026\u2026</p><p>\u6731\u90AA\u9752\u6811\u8DDF\u81EA\u5DF1\u5173\u7CFB\u4E00\u5411\u5E73\u5E38\uFF0C\u6559\u91CC\u5176\u4ED6\u6545\u65E7\u597D\u53CB\u57FA\u672C\u90FD\u88AB\u9634\u59EC\u6740\u4E86\u4E2A\u51C0\u5149\u2026\u2026</p><p>\u300C\u8001\u5C48\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u8BF4\uFF1A\u300C\u60A8\u662F\u8BF4\u5C48\u62A4\u6CD5\uFF1F\u300D</p><p>\u300C\u4F60\u53EA\u544A\u8BC9\u4ED6\u4E00\u4E2A\u4EBA\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u8FED\u58F0\u7684\u5E94\u662F\uFF0C\u53EA\u8981\u80FD\u79BB\u5F00\u8FD9\u91CC\uFF0C\u4ECA\u751F\u4ECA\u4E16\u90FD\u4E0D\u7528\u56DE\u6765\u4E86\u3002</p><p>\u9634\u957F\u91CE\u4F3C\u4E4E\u770B\u7A7F\u4E86\u5979\u7684\u5FC3\u601D\uFF0C\u624B\u638C\u5E73\u4F38\uFF0C\u9ED8\u8FD0\u7384\u529F\u3002\u4E0D\u591A\u65F6\uFF0C\u638C\u5FC3\u6E10\u6E10\u805A\u8D77\u4E00\u56E2\u9ED1\u6C14\u3002\u4ED6\u4E00\u638C\u5370\u5728\u7D2B\u73AB\u53F3\u4E73\u4E0B\uFF0C\u50B2\u7136\u9053\uFF1A\u300C\u9650\u4F60\u4E09\u65E5\u5185\u56DE\u5230\u8FD9\u91CC\u3002\u5982\u679C\u8D85\u8FC7\u4E09\u65E5\uFF0C\u9ED1\u715E\u638C\u529F\u529B\u53D1\u4F5C\uFF0C\u5148\u4ECE\u4F60\u8FD9\u5BF9\u5927\u5976\u5B50\u70C2\u8D77\uFF0C\u4E00\u76F4\u70C2\u5230\u5168\u8EAB\u2026\u2026\u563F\u563F\uFF0C\u50CF\u4F60\u8FD9\u4E48\u5A07\u6EF4\u6EF4\u7684\u7F8E\u4EBA\uFF0C\u4E0D\u51FA\u4E00\u4E2A\u6708\u5C31\u4F1A\u70C2\u6210\u4E00\u56E2\u72D7\u90FD\u4E0D\u4F1A\u7406\u7684\u81ED\u8089\u3002\u300D</p><p>\u7D2B\u73AB\u6253\u4E86\u5BD2\u5664\uFF0C\u5F3A\u7B11\u9053\uFF1A\u300C\u80FD\u4E3A\u9634\u53F3\u4F7F\u6548\u529B\uFF0C\u662F\u5974\u5A62\u7684\u798F\u6C14\u2026\u2026\u300D</p><p>\u9634\u957F\u91CE\u628A\u5979\u671D\u5730\u4E0A\u4E00\u4E22\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u518D\u6D6A\u4E00\u6B21\u7ED9\u8001\u5B50\u770B\u770B\uFF01\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5BD2\u610F\u8FEB\u4EBA\u7684\u752C\u9053\u5185\u4F1F\u6765\u4E00\u9635\u8F7B\u5FAE\u7684\u58F0\u606F\u3002\u4E00\u4E2A\u8D64\u88F8\u7684\u5C11\u5973\u62B1\u7740\u8179\u5185\u60B8\u52A8\u7684\u80CE\u513F\uFF0C\u5728\u9ED1\u6697\u4E2D\u6478\u7D22\u7740\u524D\u884C\u3002</p><p>\u65E0\u8BBA\u662F\u4F0F\u9F99\u6DA7\u7684\u5C0F\u516C\u4E3B\uFF0C\u8FD8\u662F\u98D8\u6885\u5CF0\u7684\u5C0F\u5E08\u59B9\uFF0C\u6291\u6216\u661F\u6708\u6E56\u7684\u5C11\u592B\u4EBA\uFF0C\u751A\u81F3\u662F\u6C5F\u6E56\u4E2D\u60CA\u9E3F\u4E00\u73B0\u7684\u73AB\u7470\u4ED9\u5B50\uFF0C\u6155\u5BB9\u7D2B\u73AB\u90FD\u662F\u4F17\u4EBA\u77A9\u76EE\u5173\u7231\u7684\u5929\u4E4B\u5A07\u5973\u3002</p><p>\u4F46\u5728\u8FD9\u4E2A\u5E7D\u6697\u7684\u5730\u7A74\u4E2D\uFF0C\u5979\u5E73\u751F\u7B2C\u4E00\u6B21\u610F\u8BC6\u5230\u81EA\u5DF1\u8FD8\u53EF\u80FD\u6CA6\u843D\u4E3A\u88AB\u4EBA\u4EFB\u610F\u72CE\u73A9\u7684\u5973\u4EBA\u3002</p><p>\u7D2B\u73AB\u4E00\u8FB9\u8270\u96BE\u8FC8\u6B65\uFF0C\u4E00\u8FB9\u843D\u6CEA\u3002\u5979\u5E76\u975E\u662F\u4E3A\u81EA\u5DF1\u7684\u906D\u9047\u54ED\u6CE3\uFF0C\u800C\u662F\u4E3A\u5E08\u7236\u3001\u5E08\u59D0\u4EE5\u81F3\u536B\u79C0\u7EB9\u3001\u859B\u6B23\u598D\u3001\u5510\u989C\u8FD9\u4E9B\u6A2A\u906D\u5F3A\u66B4\u7684\u5973\u5B50\u800C\u54ED\u6CE3\u3002</p><p>\u4E5F\u8BB8\u5979\u53EF\u4EE5\u4E0D\u5728\u4E4E\u8D1E\u6D01\uFF0C\u4F46\u5728\u66B4\u529B\u4E0B\u88AB\u8FEB\u732E\u51FA\u8089\u4F53\uFF0C\u4E0D\u518D\u6709\u667A\u6167\u3001\u6B66\u529F\u3001\u8EAB\u4EFD\u5730\u4F4D\u7684\u533A\u522B\uFF0C\u53EA\u80FD\u7528\u5973\u4EBA\u6700\u672C\u8D28\u7684\u6027\u5668\u6765\u53D6\u60A6\u4ED6\u4EBA\u800C\u82DF\u6D3B\u2026\u2026\u8FD9\u624D\u662F\u5973\u4EBA\u6700\u6DF1\u7684\u60B2\u54C0\u3002</p><p>\u7D2B\u73AB\u4ECE\u5207\u80A4\u4E4B\u75DB\u8BA4\u8BC6\u5230\uFF0C\u4E0E\u661F\u6708\u6E56\u500D\u53D7\u6DEB\u8650\u7684\u6027\u5974\u76F8\u6BD4\uFF0C\u81EA\u5DF1\u6709\u591A\u4E48\u5E78\u798F\u3002</p><p>\u800C\u5979\u4E5F\u7EC8\u4E8E\u660E\u767D\uFF0C\u4E3A\u4F55\u5AC2\u5AC2\u542C\u5230\u81EA\u5DF1\u7684\u58F0\u97F3\u4F1A\u5782\u4E0B\u5934\uFF0C\u4E3A\u4F55\u7EAA\u5E08\u59D0\u95EA\u70C1\u7684\u773C\u775B\u4F1A\u6709\u4E00\u4E1D\u5F02\u6837\u7684\u795E\u8272\u2026\u2026</p><p>\u90A3\u662F\u5AC9\u5992\u3002</p><p>\u540C\u6837\u7684\u5AC9\u5992\u4E5F\u5728\u7D2B\u73AB\u5FC3\u4E2D\u840C\u751F\uFF0C\u5047\u5982\u771F\u88AB\u90A3\u4E2A\u602A\u7269\u5F3A\u66B4\uFF0C\u50CF\u5E08\u59D0\u4EEC\u4E00\u6837\u4E07\u52AB\u4E0D\u590D\u7684\u8BDD\uFF0C\u5979\u4F1A\u5AC9\u5992\u6BCF\u4E2A\u5B8C\u74A7\u7684\u5904\u5B50\uFF0C\u5AC9\u5992\u6BCF\u4E2A\u8D1E\u6D01\u7684\u5987\u4EBA\uFF0C\u5AC9\u5992\u6BCF\u4E00\u4E2A\u4E0D\u5FC5\u62C5\u5FC3\u88AB\u51CC\u8FB1\u7684\u5973\u4EBA\u3002</p><p>\u7D2B\u73AB\u504E\u7740\u77F3\u58C1\u5750\u5728\u5730\u4E0A\uFF0C\u65E0\u58F0\u65E0\u606F\u5730\u6078\u54ED\u7740\u3002</p>',65);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
