import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_114" tabindex="-1"><a class="header-anchor" href="#_114" aria-hidden="true">#</a> 114</h1><p>\u51B0\u51B7\u7684\u8EAB\u4F53\u88AB\u6E29\u6696\u7684\u6CC9\u6C34\u6E10\u6E10\u878D\u5316\u3002\u7D2B\u73AB\u4EC0\u4E48\u90FD\u4E0D\u60F3\uFF0C\u4EC0\u4E48\u90FD\u4E0D\u505A\uFF0C\u50CF\u6194\u60B4\u7684\u82B1\u74E3\u5728\u6E29\u6CC9\u4E2D\u8212\u5C55\u80A2\u4F53\uFF0C\u8BA9\u6E05\u6F88\u800C\u6E29\u6DA6\u7684\u6E05\u6C34\uFF0C\u6D17\u53BB\u8EAB\u4E0A\u7684\u75DB\u695A\u3001\u5BD2\u51B7\u3001\u80AE\u810F\u548C\u5C48\u8FB1\u3002</p><p>\u5979\u5728\u6DD9\u6DD9\u7684\u6CC9\u6D41\u4E2D\u7761\u53BB\uFF0C\u957F\u957F\u7684\u776B\u6BDB\u4E0B\uFF0C\u8FD8\u5E26\u7740\u4E00\u4E1D\u6E7F\u6E7F\u7684\u6C34\u75D5\u3002\u4ECD\u662F\u5341\u516D\u5C81\u5C11\u5973\u7684\u5A07\u9765\uFF0C\u4E00\u808C\u4E00\u80A4\u65E0\u4E0D\u7CBE\u81F4\u52A8\u4EBA\u3002\u751A\u81F3\u90A3\u5BF9\u5C0F\u897F\u74DC\u822C\u7684\u5DE8\u4E73\u4E5F\u50CF\u65B0\u751F\u7684\u5A74\u513F\uFF0C\u5E26\u7740\u51E0\u5206\u5929\u771F\u7684\u7A1A\u5AE9\u3002</p><p>\u4F46\u4E30\u8174\u53F3\u4E73\u4E0B\uFF0C\u4E00\u5757\u6307\u5C16\u5927\u5C0F\u7684\u6DE1\u58A8\u75D5\u8FF9\uFF0C\u5374\u6F5C\u85CF\u7740\u65E0\u6BD4\u7684\u6740\u610F\u3002</p><p>\u826F\u4E45\uFF0C\u6C89\u9ED8\u7684\u5C11\u5973\u970D\u7136\u7AD9\u8D77\u3002\u53D7\u60CA\u7684\u6C34\u6EF4\u4ECE\u5A07\u8EAF\u4E0A\u4E32\u4E32\u6EDA\u843D\uFF0C\u4EFF\u4F5B\u65E0\u6570\u6676\u83B9\u7684\u6C34\u6676\u6E85\u5728\u6C60\u4E2D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5A18\u3002\u300D\u7D2B\u73AB\u7B11\u76C8\u76C8\u5750\u5728\u69BB\u8FB9\uFF0C\u300C\u4ECA\u5929\u597D\u4E9B\u4E86\u5417\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u52C9\u5F3A\u4E00\u7B11\uFF0C\u6CA1\u6709\u8BF4\u8BDD\u3002</p><p>\u300C\u6211\u6276\u4F60\u5750\u4E00\u4F1A\u513F\u5427\u3002\u300D\u7D2B\u73AB\u6258\u8D77\u6BCD\u4EB2\u7684\u540E\u9888\u3002</p><p>\u8427\u4F5B\u5974\u8FDE\u5FD9\u6447\u5934\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4E0D\u7528\u2026\u2026\u8BA9\u5A18\u8EBA\u4E00\u4F1A\u513F\u2026\u2026\u300D\u4E0B\u4F53\u4E24\u4E2A\u8089\u7A74\u90FD\u9178\u75BC\u80BF\u80C0\uFF0C\u5750\u8D77\u6765\u53EA\u4F1A\u66F4\u96BE\u53D7\u3002</p><p>\u521A\u624D\u767D\u7389\u9E42\u9B3C\u9B3C\u795F\u795F\u5730\u8DD1\u6765\u4E0E\u767D\u7389\u83BA\u54AC\u4E86\u534A\u5929\u8033\u6735\u3002\u7136\u540E\u767D\u7389\u83BA\u653E\u4E0B\u6728\u68CD\uFF0C\u7ED9\u5979\u64E6\u6D17\u4E86\u8EAB\u4F53\uFF0C\u6D82\u62B9\u4E86\u8309\u8389\u82B1\u6CB9\uFF0C\u6536\u62FE\u5F97\u6574\u6574\u9F50\u9F50\uFF0C\u5979\u4FBF\u77E5\u9053\uFF1A\u5973\u513F\u8981\u6765\u4E86\u3002</p><p>\u7D2B\u73AB\u4F3C\u4E4E\u6709\u4E9B\u5FC3\u4E8B\uFF0C\u5979\u652F\u9890\u4FA7\u8EBA\u5728\u8427\u4F5B\u5974\u8EAB\u8FB9\uFF0C\u8F7B\u8F7B\u629A\u6478\u7740\u6BCD\u4EB2\u5C0F\u8179\uFF0C\u300C\u8FD8\u6709\u4E00\u4E2A\u591A\u6708\u5C31\u8981\u751F\u4E86\u5427\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u8138\u4E0A\u4E00\u7EA2\uFF0C\u65CB\u5373\u53D8\u5F97\u96EA\u767D\u3002\u9F99\u54E5\u54E5\u6839\u672C\u4E0D\u559C\u6B22\u8FD9\u4E2A\u5B69\u5B50\uFF0C\u751F\u4E0B\u6765\u53C8\u6709\u4EC0\u4E48\u7528\uFF1F\u51B5\u4E14\u2026\u2026\u5979\u4EEC\u4E0B\u624B\u90A3\u4E48\u91CD\uFF0C\u80CE\u513F\u2026\u2026\u5979\u6CEA\u773C\u5A46\u5A11\u5730\u770B\u7740\u5973\u513F\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u4F60\u4E5F\u5FEB\u8981\u4E34\u4EA7\u4E86\uFF0C\u8D77\u5C45\u5F53\u5FC3\u4E9B\uFF0C\u4E0D\u8981\u7D2F\u7740\u3002\u300D</p><p>\u7D2B\u73AB\u53F9\u4E86\u53E3\u6C14\uFF0C\u4E0D\u60C5\u613F\u5730\u8BF4\uFF1A\u300C\u6211\u624D\u5341\u516D\u5C81\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u6D45\u7B11\u9053\uFF1A\u300C\u6211\u751F\u9F99\u2026\u2026\u300D\u5979\u987F\u4F4F\u4E86\uFF0C\u4E0D\u77E5\u9053\u8BE5\u8BF4\u9F99\u513F\u8FD8\u662F\u6309\u73B0\u5728\u7684\u79F0\u547C\u53EB\u9F99\u54E5\u54E5\uFF0C\u300C\u2026\u2026\u7B2C\u4E00\u80CE\uFF0C\u6BD4\u4F60\u8FD8\u5C0F\u4E00\u4E9B\u5462\u3002\u300D</p><p>\u300C\u662F\u4E0D\u662F\u5F88\u75DB\uFF1F\u300D\u7D2B\u73AB\u6700\u6015\u75DB\u3002</p><p>\u8427\u4F5B\u5974\u770B\u51FA\u5979\u7684\u62C5\u5FE7\uFF0C\u5B89\u6170\u9053\uFF1A\u300C\u6CA1\u4E8B\u7684\uFF0C\u6BCF\u4E2A\u5B69\u5B50\u90FD\u662F\u8FD9\u4E48\u751F\u4E0B\u6765\u7684\u3002\u300D</p><p>\u5979\u5C06\u4EA7\u80B2\u7684\u7ECF\u9A8C\u4E00\u4E00\u4F20\u6388\u7ED9\u5973\u513F\uFF0C\u5FFD\u7136\u95F4\uFF0C\u4E00\u9635\u5C16\u9510\u7684\u523A\u75DB\u5212\u7834\u5FC3\u5934\uFF0C\u8427\u4F5B\u5974\u6731\u5507\u4E0D\u7531\u62BD\u52A8\u8D77\u6765\u3002\u597D\u4E45\u90FD\u6CA1\u6709\u8FD9\u79CD\u505A\u6BCD\u4EB2\u7684\u611F\u89C9\u4E86\uFF0C\u6B64\u65F6\u770B\u7740\u5973\u513F\u768E\u6D01\u65E0\u7455\u7684\u9762\u5BB9\uFF0C\u5979\u7A81\u7136\u60F3\u8D77\u81EA\u5DF1\u8FD9\u4E2A\u6BCD\u4EB2\u662F\u591A\u4E48\u810F\u6D4A\u3002</p><p>\u7D2B\u73AB\u4EE5\u4E3A\u662F\u81EA\u5DF1\u5FE7\u5FC3\u5FE1\u5FE1\u7684\u6A21\u6837\u4F7F\u6BCD\u4EB2\u62C5\u5FC3\uFF0C\u8FDE\u5FD9\u5C55\u989C\u4E00\u7B11\uFF0C\u300C\u5973\u513F\u4E0D\u6015\uFF0C\u5230\u65F6\u8BA9\u53F6\u8001\u5934\u71AC\u76C6\u90A3\u79CD\u6C64\uFF0C\u5C31\u662F\u5F00\u819B\u7834\u809A\u4E5F\u4E0D\u4F1A\u89C9\u5F97\u75BC\u5462\u3002\u300D\u4EB2\u624B\u6740\u6389\u970D\u72C2\u7130\uFF0C\u662F\u7D2B\u73AB\u8FD1\u4E00\u5E74\u6765\u4EC5\u6709\u7684\u5F00\u5FC3\u4E8B\uFF0C\u4E3A\u6B64\u5367\u5E8A\u4E94\u5929\u4E5F\u5FC3\u7518\u60C5\u613F\u3002\u552F\u4E00\u9057\u61BE\u7684\u5C31\u662F\u970D\u72C2\u7130\u5F53\u65F6\u6CA1\u6709\u77E5\u89C9\u3002</p><p>\u8427\u4F5B\u5974\u5FC3\u4E2D\u6FC0\u8361\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u73AB\u513F\u2026\u2026\u300D</p><p>\u300C\u4EC0\u4E48\u73AB\u513F\uFF01\u300D\u6155\u5BB9\u9F99\u5BD2\u58F0\u559D\u9053\uFF0C\u5927\u6B65\u5165\u5BA4\u3002</p><p>\u8427\u4F5B\u5974\u5A07\u8EAF\u4E00\u6296\uFF0C\u745F\u7F29\u7740\u6539\u53E3\u9053\uFF1A\u300C\u59D0\u59D0\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u521A\u521A\u6563\u529F\uFF0C\u5F3A\u5065\u7684\u8EAB\u4F53\u5192\u7740\u7F15\u7F15\u767D\u6C14\uFF0C\u5374\u4E0D\u89C1\u4E00\u7C92\u6C57\u73E0\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u4E56\u5DE7\u5730\u8FCE\u4E0A\u53BB\uFF0C\u51C6\u5907\u542E\u5C3D\u8089\u68D2\u4E0A\u7684\u8840\u8FF9\u3002</p><p>\u300C\u6211\u6765\u3002\u300D\u7ECF\u5386\u4E86\u9634\u53F3\u4F7F\u7684\u8E42\u8E8F\u4E4B\u540E\uFF0C\u7D2B\u73AB\u5BF9\u767D\u6C0F\u59D0\u59B9\u7684\u6068\u610F\u6D88\u6DE1\u4E86\u8BB8\u591A\uFF0C\u5BF9\u81EA\u5DF1\u4EE5\u5F80\u7684\u559D\u9A82\u9690\u7EA6\u6709\u4E9B\u540E\u6094\uFF0C\u56E0\u6B64\u81EA\u544A\u594B\u52C7\uFF0C\u8981\u66FF\u59D0\u59B9\u4FE9\u505A\u8FD9\u4EF6\u9F8C\u9F8A\u4E4B\u4E8B\u3002</p><p>\u6155\u5BB9\u9F99\u773C\u4E2D\u9732\u51FA\u4E00\u4E1D\u8BB6\u8272\uFF0C\u4E24\u4EBA\u51B0\u51B7\u7684\u5173\u7CFB\u5DF2\u6709\u6570\u6708\uFF0C\u5C0F\u4E2B\u5934\u6BCF\u6B21\u90FD\u76F4\u7740\u8EAB\u5B50\uFF0C\u5C38\u4F53\u4E00\u6837\u732E\u51FA\u9634\u7CBE\u4FBF\u7B97\u4E86\u4E8B\uFF0C\u4ECE\u6765\u6CA1\u6709\u4E3B\u52A8\u4F3A\u5019\u8FC7\u4ED6\u3002\u4ECA\u5929\u662F\u600E\u4E48\u4E86\uFF1F</p><p>\u7279\u5236\u7684\u5939\u8884\u4F9D\u7136\u663E\u5F97\u7D27\u7EF7\uFF0C\u80A5\u7855\u7684\u5706\u4E73\u5C06\u8863\u895F\u6491\u8D77\u4E24\u56E2\u6D51\u5706\u3002\u7D2B\u73AB\u62D6\u7740\u7B28\u91CD\u7684\u8EAB\u4F53\uFF0C\u8DEA\u5728\u6155\u5BB9\u9F99\u8EAB\u524D\uFF0C\u7AED\u529B\u5F20\u5F00\u5A07\u8273\u7684\u5C0F\u5634\uFF0C\u542B\u4F4F\u9F9F\u5934\u3002\u76F8\u6BD4\u4E8E\u9634\u957F\u91CE\u7684\u6C61\u6D4A\u8165\u81ED\uFF0C\u6155\u5BB9\u9F99\u7684\u9633\u5177\u867D\u7136\u72F0\u72DE\uFF0C\u5374\u6709\u79CD\u5065\u5EB7\u800C\u53C8\u5F3A\u58EE\u7684\u6C14\u606F\u3002</p><p>\u521A\u8214\u4E86\u4E24\u4E0B\uFF0C\u6155\u5BB9\u9F99\u300C\u5575\u300D\u7684\u62D4\u51FA\u9F9F\u5934\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u9E21\u5DF4\u90FD\u4E0D\u4F1A\u8214\uFF0C\u6EDA\u4E00\u8FB9\u53BB\u3002\u300D</p><p>\u7D2B\u73AB\u6014\u6014\u8DEA\u5728\u5730\u4E0A\uFF0C\u7EEF\u8863\u95F4\u7389\u8138\u82CD\u767D\u3002</p><p>\u6155\u5BB9\u9F99\u5F84\u76F4\u4ECE\u7D2B\u73AB\u8EAB\u8FB9\u8D70\u8FC7\uFF0C\u7528\u6BDB\u5DFE\u64E6\u53BB\u8840\u8FF9\uFF0C\u7136\u540E\u6E29\u67D4\u5730\u62C9\u8D77\u8427\u4F5B\u5974\u8EAB\u4E0A\u7684\u9526\u88AB\u3002</p><p>\u534E\u4E3D\u7684\u5BDD\u5177\u5185\uFF0C\u96EA\u80A4\u9999\u808C\u8273\u5149\u56DB\u5C04\u3002\u5982\u6B64\u7F8E\u8273\u7684\u8EAB\u4F53\uFF0C\u5374\u5305\u88F9\u7740\u4E00\u5757\u7C97\u68C9\u5C3F\u5E03\uFF0C\u53EF\u7B11\u4E4B\u4F59\uFF0C\u5219\u662F\u4EE4\u4EBA\u5FC3\u5BD2\u7684\u6B8B\u5FCD\u548C\u51C4\u51C9\u3002</p><p>\u7F8E\u5987\u602F\u602F\u770B\u7740\u513F\u5B50\uFF0C\u60F3\u5A9A\u7B11\u5374\u53C8\u4E0D\u6562\u3002</p><p>\u6155\u5BB9\u9F99\u63B0\u5F00\u8427\u4F5B\u5974\u762B\u8F6F\u7684\u53CC\u817F\uFF0C\u4E00\u8FB9\u89E3\u5F00\u5C3F\u5E03\uFF0C\u4E00\u8FB9\u9053\uFF1A\u300C\u5A18\u4EB2\u4E56\u4E56\uFF0C\u4ECA\u5929\u53C8\u62C9\u5C4E\u4E86\u5417\uFF1F\u2026\u2026\u5443\uFF1F\u8FD9\u4E48\u591A\uFF1F\u300D</p><p>\u7F8E\u5987\u50CF\u5A74\u513F\u822C\u53C9\u7740\u53CC\u817F\uFF0C\u7C89\u81C0\u95F4\u6EE1\u662F\u79FD\u7269\u3002\u5979\u7F9E\u8D67\u5730\u5782\u4E0B\u773C\u5EC9\uFF0C\u7EC6\u82E5\u868A\u868B\u5730\u8BF4\uFF1A\u300C\u5A18\u4E00\u6574\u5929\u90FD\u6CA1\u6362\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u76EF\u7740\u767D\u6C0F\u59D0\u59B9\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u600E\u4E48\u4E0D\u6362\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u8FDE\u5FD9\u8BF4\u9053\uFF1A\u300C\u662F\u5A18\u4E0D\u8BA9\u5979\u4EEC\u6362\u7684\u2026\u2026\u5A18\u60F3\u8BA9\u54E5\u54E5\u4EB2\u624B\u7ED9\u4EBA\u5BB6\u6362\u5C3F\u5E03\u3002\u300D</p><p>\u767D\u7389\u83BA\u7ED9\u5979\u64E6\u5B8C\u8EAB\u5B50\uFF0C\u4E0D\u77E5\u4ECE\u54EA\u513F\u627E\u6765\u4E00\u5806\u79FD\u7269\u5305\u5728\u5979\u80A1\u95F4\uFF0C\u53C8\u6559\u5979\u8FD9\u756A\u8BF4\u8F9E\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u6014\uFF0C\u65CB\u5373\u54C8\u54C8\u5927\u7B11\u3002\u8EAB\u540E\u50F5\u8DEA\u7684\u7D2B\u73AB\u5FC3\u4E0B\u5374\u6108\u53D1\u5BD2\u51B7\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u5B89\u8BE6\u5730\u5750\u5728\u69BB\u4FA7\uFF0C\u53F3\u624B\u4F4E\u5782\u3002</p><p>\u8EAB\u524D\uFF0C\u4E00\u4E2A\u88F8\u8EAB\u4E3D\u4EBA\u6B63\u6D25\u6D25\u6709\u5473\u5730\u8214\u5F04\u5979\u7684\u624B\u6307\u3002</p><p>\u5B9D\u85CF\u7684\u7EBF\u7D22\u5B9A\u7136\u662F\u5728\u9634\u957F\u91CE\u8EAB\u540E\u7684\u77F3\u58C1\u4E0A\uFF0C\u4F46\u7D2B\u73AB\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u613F\u518D\u89C1\u90A3\u4E2A\u65E0\u817F\u602A\u7269\u3002\u4E00\u60F3\u4ED6\u4E71\u84EC\u84EC\u7684\u6BDB\u53D1\uFF0C\u8EAB\u4E0A\u4EE4\u4EBA\u4F5C\u5455\u7684\u6C14\u5473\uFF0C\u7D2B\u73AB\u5C31\u50CF\u505A\u4E86\u4E00\u4E2A\u53EF\u803B\u7684\u5669\u68A6\u3002\u5669\u68A6\u91CC\uFF0C\u81EA\u5DF1\u5C45\u7136\u5F53\u7740\u90A3\u4E2A\u602A\u7269\u7684\u9762\u4E24\u6B21\u624B\u6DEB\u2026\u2026</p><p>\u5979\u4E0D\u613F\u627F\u8BA4\uFF0C\u4F46\u65E0\u6CD5\u6B3A\u9A97\u81EA\u5DF1\u2014\u2014\u4E0E\u5192\u7740\u51CC\u8FB1\u7684\u5371\u9669\u63A5\u8FD1\u90A3\u4E2A\u602A\u7269\u76F8\u6BD4\uFF0C\u5979\u5B81\u613F\u53BB\u53D6\u60A6\u4EC7\u654C\u6155\u5BB9\u9F99\u3002</p><p>\u7D2B\u73AB\u7528\u624B\u6307\u91AE\u4E86\u4E9B\u871C\uFF0C\u518D\u6B21\u653E\u5230\u98CE\u665A\u534E\u5634\u4E2D\u3002</p><p>\u9999\u8F6F\u7684\u5C0F\u820C\u5FEB\u6377\u65E0\u4F26\u5730\u5212\u8FC7\u624B\u6307\uFF0C\u90A3\u79CD\u6ED1\u817B\u7684\u611F\u89C9\uFF0C\u8212\u670D\u5F97\u8BA9\u4EBA\u60F3\u547B\u541F\u3002\u7D2B\u73AB\u95ED\u4E0A\u773C\uFF0C\u5FAE\u5FAE\u5598\u7740\u6C14\uFF0C\u7EC6\u5FC3\u4F53\u4F1A\u5E08\u59D0\u820C\u5934\u7684\u52A8\u4F5C\u3002</p><p>\u81EA\u5DF1\u8FDE\u4E00\u6761\u72D7\u90FD\u6740\u4E0D\u4E86\uFF0C\u4F55\u51B5\u662F\u9634\u957F\u91CE\u90A3\u4E2A\u5996\u602A\u3002\u4EB2\u53CB\u75AF\u7684\u75AF\uFF0C\u6B8B\u7684\u6B8B\uFF0C\u8FDE\u4E2A\u5E2E\u624B\u90FD\u6CA1\u6709\uFF0C\u53EA\u597D\u4E0E\u4ED6\u5E72\u8017\u3002\u53EF\u4ED6\u5DF2\u7ECF\u5728\u5730\u7A9F\u6D3B\u4E86\u5341\u51E0\u5E74\uFF0C\u770B\u6837\u5B50\u8FD8\u80FD\u6D3B\u4E0A\u51E0\u5341\u5E74\u2026\u2026</p><p>\u7D2B\u73AB\u82E6\u6DA9\u5730\u54AC\u4F4F\u5634\u5507\u3002\u53EA\u80FD\u5148\u53D6\u60A6\u6155\u5BB9\u9F99\uFF0C\u6D88\u9664\u4ED6\u7684\u6212\u5FC3\uFF0C\u60F3\u529E\u6CD5\u6740\u6389\u4ED6\u62A5\u4EC7\u4E86\u3002\u81F3\u4E8E\u9003\u751F\u2026\u2026\u6216\u8005\u53EF\u4EE5\u8BA9\u661F\u6708\u6E56\u6BCF\u4EBA\u90FD\u559D\u4E0A\u4E00\u7897\u9EBB\u6CB8\u6563\uFF0C\u81EA\u5DF1\u5C31\u80FD\u4E3A\u6240\u6B32\u4E3A\u4E86\u3002</p><p>\u80FD\u4E0D\u80FD\u628A\u53F6\u8001\u5934\u7ED9\u8FF7\u5012\u5462\uFF1F\u7D2B\u73AB\u4EF0\u7740\u8138\u80E1\u601D\u4E71\u60F3\u3002\u4E0D\u884C\u5C31\u5A9A\u60D1\u4ED6\uFF0C\u5728\u7D27\u8981\u5173\u5934\u5927\u58F0\u54ED\u53EB\u51FA\u6765\uFF0C\u8BA9\u6155\u5BB9\u9F99\u4E00\u638C\u7ED3\u679C\u4E86\u8FD9\u4E2A\u8001\u5339\u592B\u3002\u8BA1\u7B56\u867D\u7136\u8001\u5957\uFF0C\u4F46\u5BF9\u6155\u5BB9\u9F99\u8FD9\u79CD\u6027\u673A\u80FD\u4EA2\u594B\u7684\u7537\u4EBA\u6765\u8BF4\uFF0C\u5E94\u8BE5\u6709\u6548\u5462\u3002</p><p>\u5979\u624B\u6307\u4E00\u52A8\uFF0C\u5173\u8282\u78B0\u5728\u98CE\u665A\u534E\u7259\u9F7F\u4E0A\u3002\u98CE\u665A\u534E\u7ACB\u5373\u4F0F\u4E0B\u8EAB\u5B50\uFF0C\u6050\u60E7\u5730\u8F7B\u98A4\u3002</p><p>\u7D2B\u73AB\u5FC3\u75BC\u5730\u6469\u6332\u7740\u5979\u7684\u80A9\u8180\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u522B\u6015\uFF0C\u5927\u5E08\u59D0\u2026\u2026\u300D</p><p>\u5979\u7528\u4E1D\u5E15\u64E6\u53BB\u98CE\u665A\u534E\u5507\u89D2\u7684\u53E3\u6C34\uFF0C\u5927\u5E08\u59D0\u867D\u7136\u53E3\u4E0D\u80FD\u8A00\uFF0C\u5374\u662F\u5979\u6240\u80FD\u627E\u5230\u6700\u597D\u7684\u8001\u5E08\u3002\u4ECE\u5730\u7A9F\u5F52\u6765\u540E\uFF0C\u5FC3\u5883\u8F6C\u53D8\u7684\u7D2B\u73AB\u4E0D\u6562\u518D\u89C1\u5AC2\u5AC2\u3002\u5979\u7EC8\u4E8E\u660E\u767D\uFF0C\u81EA\u5DF1\u7684\u65BD\u6069\uFF0C\u53EA\u80FD\u4F7F\u5AC2\u5AC2\u66F4\u52A0\u75DB\u82E6\u3002</p><p>\u8BD5\u60F3\uFF0C\u539F\u672C\u4EB2\u82E5\u59D0\u59B9\u7684\u540C\u4F34\u5982\u4ECA\u5374\u4E00\u4E3B\u4E00\u5974\uFF0C\u5373\u4F7F\u81EA\u5DF1\u65E0\u610F\u4EE5\u5782\u601C\u7684\u773C\u5149\u53BB\u770B\u5F85\u5AC2\u5AC2\uFF0C\u5AC2\u5AC2\u4E5F\u4E0D\u4F1A\u613F\u610F\u8BA9\u4EBA\u65C1\u89C2\u5979\u6240\u53D7\u7684\u51CC\u8FB1\u3002</p><p>\u53EA\u6709\u5728\u5927\u5E08\u59D0\u9762\u524D\uFF0C\u5979\u624D\u4E0D\u5FC5\u62C5\u5FC3\u8EAB\u4EFD\u60AC\u6B8A\u7684\u5C34\u5C2C\u3002</p><p>\u300C\u5927\u5E08\u59D0\uFF0C\u6211\u8BE5\u600E\u4E48\u529E\u5462\u2026\u2026\u300D</p><p>\u56DE\u7B54\u5979\u7684\uFF0C\u53EA\u6709\u6D41\u971C\u5251\u75F4\u75F4\u7684\u7B11\u5BB9\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5F64\u4E91\u5BC6\u5E03\uFF0C\u6700\u540E\u4E00\u4E1D\u9633\u5149\u4E5F\u6D88\u6CA1\u5728\u7FA4\u5CF0\u4E4B\u540E\u3002</p><p>\u7D2B\u73AB\u75B2\u5026\u5730\u5750\u5728\u66F2\u4EAD\u4E2D\uFF0C\u8FDC\u671B\u5C71\u8272\u3002</p><p>\u98D8\u6885\u5CF0\u4E00\u5E74\u56DB\u5B63\u90FD\u662F\u5927\u96EA\u7EB7\u98DE\u3002\u5076\u7136\u653E\u775B\uFF0C\u5E08\u59D0\u59B9\u4EEC\u4FBF\u8054\u8882\u5728\u5C71\u95F4\u6E38\u73A9\u3002\u81EA\u5DF1\u90A3\u65F6\u5019\u597D\u6DD8\u6C14\u554A\uFF0C\u5B66\u7740\u52AB\u8DEF\u6BDB\u8D3C\u7684\u624B\u6BB5\uFF0C\u7528\u4E86\u6574\u4E2A\u665A\u4E0A\u6316\u4E86\u4E00\u4E2A\u9677\u9631\u3002\u8BB0\u5F97\u81EA\u5DF1\u5F88\u5C0F\u5FC3\u5730\u626B\u53BB\u75D5\u8FF9\uFF0C\u7ED3\u679C\u8FD8\u662F\u88AB\u5927\u5E08\u59D0\u770B\u51FA\u7AEF\u502A\u3002\u5927\u5E08\u59D0\u5F53\u65F6\u62BF\u5634\u4E00\u7B11\uFF0C\u597D\u50CF\u7167\u4EAE\u96EA\u5730\u7684\u4E00\u62B9\u6708\u8272\uFF0C\u6837\u5B50\u7F8E\u6781\u4E86\u3002</p><p>\u5979\u4E00\u7B11\uFF0C\u5AC2\u5AC2\uFF0C\u90A3\u65F6\u8FD8\u662F\u4E8C\u5E08\u59D0\uFF0C\u4E5F\u770B\u4E86\u51FA\u6765\u3002\u4E8C\u5E08\u59D0\u5F53\u65E5\u7684\u6298\u679D\u624B\u5DF2\u7ECF\u6709\u4E86\u516B\u5206\u706B\u5019\uFF0C\u53EA\u4E00\u62DB\u5C31\u62E7\u4F4F\u4E86\u81EA\u5DF1\u7684\u5C0F\u8FAB\u5B50\uFF0C\u8FD8\u5A01\u80C1\u8BF4\u8981\u628A\u5C0F\u574F\u86CB\u57CB\u5728\u96EA\u5751\u91CC\u3002</p><p>\u6700\u5012\u9709\u7684\u662F\u4E09\u5E08\u59D0\uFF0C\u5979\u6025\u5306\u5306\u8D76\u6765\u6551\u81EA\u5DF1\uFF0C\u4E00\u4E0D\u5C0F\u5FC3\u6ED1\u8FDB\u9677\u9631\uFF0C\u5927\u5E08\u59D0\u3001\u4E8C\u5E08\u59D0\u90FD\u614C\u5FD9\u8DF3\u4E0B\u53BB\u6551\u5979\u2026\u2026</p><p>\u56DE\u5FC6\u95F4\uFF0C\u5FFD\u7136\u988A\u4E0A\u4E00\u51C9\u3002\u5C11\u5973\u8138\u4E0A\u7684\u5FAE\u7B11\u6E10\u6E10\u892A\u53BB\u3002\u5979\u4F38\u51FA\u624B\u638C\uFF0C\u5C06\u4E00\u6735\u8F7B\u76C8\u7684\u96EA\u82B1\u63A5\u5728\u767D\u7389\u822C\u7684\u638C\u5FC3\u4E2D\u3002</p><p>\u4E0B\u96EA\u4E86\u554A\u2026\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u9F99\u8D70\u8FDB\u77F3\u5BA4\uFF0C\u7D2B\u73AB\u4FBF\u6276\u7740\u809A\u5B50\uFF0C\u8E52\u8DDA\u5730\u8D70\u5230\u4ED6\u8EAB\u524D\uFF0C\u6E29\u67D4\u6B3E\u6B3E\u5730\u4E3A\u4ED6\u5BBD\u8863\u89E3\u5E26\u3002</p><p>\u5C0F\u4E2B\u5934\u771F\u662F\u8F6C\u6027\u4E86\u3002\u633A\u7740\u8FD9\u4E48\u5927\u4E2A\u809A\u5B50\uFF0C\u4EA4\u5408\u8D77\u6765\u4E00\u5B9A\u8F9B\u82E6\u4E07\u5206\u5427\u3002\u53EF\u5979\u8138\u4E0A\u59CB\u7EC8\u6302\u7740\u7B11\u610F\uFF0C\u800C\u4E14\u6280\u672F\u4F3C\u4E4E\u4E5F\u6709\u4E9B\u4E0D\u540C\uFF0C\u597D\u50CF\u5F88\u5356\u529B\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u53CC\u624B\u6795\u5728\u8111\u540E\uFF0C\u5728\u6CA1\u6709\u4EBA\u80FD\u770B\u5230\u7684\u773C\u795E\u6DF1\u5904\uFF0C\u85CF\u7740\u4E00\u4E1D\u6DE1\u6DE1\u7684\u4F24\u611F\u548C\u4F01\u76FC\u3002</p><p>\u7D2B\u73AB\u8DE8\u5750\u5728\u6155\u5BB9\u9F99\u8170\u4E0A\uFF0C\u8EAB\u5B50\u540E\u4EF0\uFF0C\u817E\u51FA\u7B28\u91CD\u7684\u5C0F\u8179\uFF0C\u7AED\u529B\u5957\u5F04\u3002\u7403\u72B6\u7684\u9999\u4E73\u5E03\u6EE1\u6C57\u6C34\uFF0C\u767D\u4EAE\u4EAE\uFF0C\u50CF\u4E00\u5BF9\u8DF3\u8DC3\u7684\u96EA\u7403\uFF0C\u53C8\u5706\u53C8\u5927\u3002</p><p>\u826F\u4E45\uFF0C\u5979\u98A4\u6296\u7740\u505C\u4F4F\u52A8\u4F5C\uFF0C\u7B49\u8089\u68D2\u7684\u9707\u98A4\u505C\u606F\uFF0C\u5979\u5403\u529B\u5730\u62AC\u8D77\u8EAB\u5B50\uFF0C\u4FEF\u8EAB\u542E\u5C3D\u9633\u5177\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u51B7\u4E00\u7B11\uFF0C\u62AC\u811A\u5C06\u5979\u8E22\u5230\u4E00\u65C1\uFF0C\u300C\u5973\u4EBA\u771F\u662F\u8D31\u8D27\u3002\u53EA\u6709\u4E0D\u628A\u5979\u5F53\u4EBA\uFF0C\u624D\u4F1A\u5B66\u4E56\u3002\u300D</p><p>\u6B8B\u7CBE\u6897\u5728\u5589\u5934\uFF0C\u53C8\u82E6\u53C8\u6DA9\u3002</p>',71);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
