import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_115" tabindex="-1"><a class="header-anchor" href="#_115" aria-hidden="true">#</a> 115</h1><p>\u5341\u4E00\u6708\u5341\u4E5D\uFF0C\u5C0F\u96EA\u521D\u6674\u3002</p><p>\u540E\u5C71\u662F\u5E96\u53A8\u6240\u5728\uFF0C\u81EA\u4ECE\u732A\u5708\u591A\u4E86\u4E00\u5934\u6BCD\u517D\u4E4B\u540E\uFF0C\u6559\u4F17\u4FBF\u8702\u6D8C\u800C\u81F3\u3002\u4F46\u6628\u591C\u4E00\u573A\u5C0F\u96EA\uFF0C\u4F7F\u8FD9\u91CC\u51B7\u6E05\u4E86\u8BB8\u591A\u3002</p><p>\u4E00\u4E2A\u4E94\u77ED\u8EAB\u6750\u7684\u6742\u5F79\u63D0\u7740\u4E00\u6876\u732A\u98DF\uFF0C\u9694\u7740\u6728\u680F\u7528\u957F\u67C4\u94F2\u8200\u5230\u6728\u69FD\u4E2D\u3002\u5341\u51E0\u5934\u80A5\u732A\u54FC\u54FC\u53FD\u53FD\u6324\u6210\u4E00\u56E2\uFF0C\u957F\u5634\u5728\u69FD\u91CC\u62F1\u6765\u62F1\u53BB\u3002</p><p>\u300C\u8D76\u7D27\u5403\uFF01\u8FD8\u6709\u6708\u628A\u5C31\u8FC7\u5E74\u4E86\u2026\u2026\u300D\u9972\u8005\u78D5\u4E86\u78D5\u6728\u94F2\uFF0C\u671D\u5708\u4E2D\u4E00\u6325\u3002</p><p>\u732A\u5708\u4E2D\u95F4\u88AB\u8E29\u6210\u4E00\u4E2A\u5C3A\u8BB8\u6DF1\u7684\u6CE5\u5751\u3002\u878D\u5316\u7684\u96EA\u6C34\u6DF7\u7740\u755C\u7272\u7684\u5C4E\u4FBF\u5C3F\u6C34\u805A\u5728\u5751\u91CC\uFF0C\u53C8\u810F\u53C8\u81ED\u3002</p><p>\u4E00\u6BB5\u8F6E\u5ED3\u6A21\u7CCA\u7684\u7269\u4F53\u534A\u6D78\u5728\u6CE5\u6CDE\u4E2D\uFF0C\u53EA\u6709\u9732\u5728\u6CE5\u6C34\u5916\u7684\u53E3\u9F3B\u548C\u6CE5\u6C34\u4E0A\u7684\u957F\u53D1\u4F9D\u7A00\u80FD\u770B\u51FA\u662F\u4E2A\u5973\u4EBA\u3002</p><p>\u6728\u94F2\u300C\u556A\u300D\u7684\u6253\u5728\u8089\u6BB5\u4E0A\uFF0C\u732A\u98DF\u6CBE\u5728\u9EDD\u9ED1\u7684\u6CE5\u6C34\u4E0A\uFF0C\u4EFF\u4F5B\u96F6\u661F\u7684\u96EA\u82B1\u3002</p><p>\u300C\u4ED6\u5A18\u7684\uFF0C\u4F60\u8FD9\u4E2A\u8D31\u8D27\u4E00\u6765\uFF0C\u5BB3\u5F97\u8001\u5B50\u7684\u732A\u4E00\u4E2A\u52B2\u513F\u5730\u6389\u8198\u3002\u8FC7\u5E74\u6CA1\u8089\u5403\u96BE\u9053\u5403\u4F60\uFF1F\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u8270\u96BE\u5730\u5410\u51FA\u4E00\u53E3\u6CE5\u6C34\uFF0C\u5728\u5751\u91CC\u8815\u52A8\u4E86\u4E00\u4E0B\u3002\u5979\u7684\u80A5\u4E73\u548C\u8EAF\u5E72\u90FD\u6CE1\u5728\u51B0\u51B7\u7684\u6CE5\u6C34\u4E2D\uFF0C\u53EA\u6709\u81C0\u90E8\u50CF\u98D8\u5728\u6C34\u9762\u4E0A\u4E00\u822C\uFF0C\u9732\u51FA\u6D51\u5706\u7684\u66F2\u7EBF\u3002</p><p>\u90A3\u6742\u5F79\u6478\u51FA\u4E00\u4E2A\u9152\u846B\u82A6\uFF0C\u559D\u4E86\u4E00\u53E3\u53BB\u53BB\u5BD2\u610F\uFF0C\u7136\u540E\u8DB4\u5728\u680F\u4E0A\uFF0C\u7528\u6728\u9539\u6233\u5F04\u7740\u6CE5\u6C34\u4E2D\u7684\u8089\u4F53\u5632\u7B11\u9053\uFF1A\u300C\u4EC0\u4E48\u5929\u4E0B\u7B2C\u4E00\u9AD8\u624B\uFF1F\u5728\u6211\u4EEC\u661F\u6708\u6E56\u8FDE\u5934\u6BCD\u732A\u90FD\u4E0D\u5982\uFF01\u8001\u6BCD\u732A\u8FD8\u4E0D\u662F\u5929\u5929\u6328\u64CD\uFF0C\u4F60\u4ED6\u5A18\u7684\u9664\u4E86\u6328\u64CD\u8FD8\u662F\u6328\u64CD\u2026\u2026\u300D</p><p>\u51E4\u51F0\u771F\u6C14\u663E\u793A\u51FA\u5B83\u7684\u5A01\u529B\uFF0C\u7EB5\u7136\u6563\u4E71\u96BE\u805A\uFF0C\u6D78\u5728\u523A\u9AA8\u7684\u96EA\u6CE5\u4E2D\uFF0C\u795E\u5C3C\u4ECD\u80FD\u52C9\u529B\u652F\u6491\u3002</p><p>\u5979\u88AB\u6254\u5230\u8FD9\u91CC\u5DF2\u7ECF\u6574\u6574\u4E24\u4E2A\u6708\uFF0C\u6BCF\u4E00\u5929\uFF0C\u8FD9\u4E2A\u6614\u65E5\u6B66\u6797\u540D\u6D3E\u7684\u638C\u95E8\u5C31\u50CF\u86C6\u866B\u4E00\u6837\u82DF\u6D3B\u5728\u80AE\u810F\u7684\u6CE5\u6CDE\u4E2D\u3002\u4E24\u4E2A\u6708\u4E0E\u732A\u7FA4\u4E3A\u4F0D\u7684\u65E5\u5B50\uFF0C\u7559\u7ED9\u5979\u7684\u53EA\u6709\u65E0\u4F11\u6B62\u7684\u5978\u6DEB\u548C\u51CC\u8FB1\u3002</p><p>\u4EE4\u4EBA\u60CA\u5947\u7684\u662F\uFF0C\u5979\u5C45\u7136\u8FD8\u6D3B\u7740\uFF0C\u4E0D\u4EC5\u6D3B\u7740\uFF0C\u5979\u8FD8\u2026\u2026</p><p>\u300C\u5403\u4E00\u53E3\u3002\u300D\u6742\u5F79\u4ECE\u5403\u5269\u7684\u732A\u98DF\u4E2D\u94F2\u4E86\u4E00\u9539\u9012\u5728\u96EA\u5CF0\u795E\u5C3C\u9762\u524D\u3002</p><p>\u8138\u4E0A\u7684\u6CE5\u6C34\u4E00\u6EF4\u6EF4\u843D\u5728\u9539\u4E2D\uFF0C\u9178\u81ED\u7684\u732A\u98DF\u6DF7\u7740\u7FA4\u732A\u7684\u53E3\u6C34\uFF0C\u5728\u51B0\u51B7\u7684\u7A7A\u6C14\u4E2D\u6563\u53D1\u7740\u6DE1\u6DE1\u7684\u70ED\u6C14\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u652F\u8D77\u6EE1\u662F\u6CE5\u57A2\u7684\u8138\u5E9E\uFF0C\u8DB4\u5728\u9539\u4E2D\u5C06\u732A\u98DF\u4E00\u53E3\u4E00\u53E3\u541E\u54BD\u4E0B\u53BB\u3002</p><p>\u6742\u5F79\u5472\u7740\u9EC4\u7259\u4E00\u4E50\uFF0C\u62FF\u8D77\u6728\u9539\uFF0C\u5C06\u732A\u98DF\u5C3D\u6570\u62B9\u5728\u96EA\u5CF0\u795E\u5C3C\u8138\u4E0A\u3002\u795E\u5C3C\u62D6\u7740\u6298\u65AD\u7684\u624B\u81C2\uFF0C\u5C06\u732A\u98DF\u4E00\u4E00\u8214\u51C0\uFF0C\u867D\u7136\u88AB\u5982\u6B64\u51CC\u8FB1\uFF0C\u5979\u4F9D\u7136\u795E\u8272\u5982\u5E38\uFF0C\u81EA\u6709\u4E00\u80A1\u51DB\u7136\u7684\u6C14\u8D28\u3002</p><p>\u300C\u4ED6\u5A18\u7684\uFF0C\u6BDB\u90FD\u62D4\u5149\u4E86\uFF0C\u8FD8\u88C5\u4EC0\u4E48\u516B\u54E5\u2026\u2026\u300D\u9972\u8005\u54A7\u54A7\u5634\uFF0C\u5C06\u795E\u5C3C\u7684\u8138\u5B54\u538B\u5230\u6CE5\u5751\u91CC\u3002</p><p>\u4E00\u5934\u80A5\u732A\u5403\u4E86\u4E2A\u534A\u9971\uFF0C\u4FBF\u6DCC\u7740\u6CE5\u6C64\u550F\u54E9\u5149\u8361\u5730\u7A9C\u4E86\u8FC7\u6765\u3002\u5B83\u4E5F\u662F\u719F\u95E8\u719F\u8DEF\uFF0C\u732A\u5634\u4F38\u5230\u795E\u5C3C\u80A1\u95F4\uFF0C\u5C06\u5979\u81C0\u90E8\u7565\u5FAE\u62F1\u8D77\uFF0C\u63A5\u7740\u5C31\u9A91\u5230\u795E\u5C3C\u8EAB\u4E0A\u3002</p><p>\u88AB\u80A5\u732A\u5728\u81C0\u540E\u731B\u7136\u4E00\u9876\uFF0C\u6CE1\u5728\u6CE5\u6CDE\u4E2D\u7684\u4E24\u6761\u5927\u817F\u987F\u65F6\u626C\u8D77\uFF0C\u7A00\u7A00\u6CA5\u6CA5\u6E85\u8D77\u4E00\u7247\u6CE5\u70B9\u3002</p><p>\u300C\u65E5\u4F60\u5A18\u54CE\uFF0C\u6709\u70B9\u513F\u52B2\u5E72\u4EC0\u4E48\u4E0D\u597D\uFF1F\u300D\u9972\u8005\u9A82\u9A82\u54A7\u54A7\u6325\u9539\u671D\u80A5\u732A\u80A9\u4E0A\u4E00\u901A\u72E0\u6253\uFF0C\u300C\u5979\u4F1A\u7ED9\u4F60\u751F\u732A\u5D3D\u513F\u5417\uFF1F\u300D</p><p>\u90A3\u80A5\u732A\u5C11\u8BF4\u4E5F\u6709\u4E94\u767E\u591A\u65A4\uFF0C\u6728\u9539\u6253\u5728\u80A9\u4E0A\u53EA\u5F53\u6414\u75D2\u3002\u7EC6\u957F\u7684\u9633\u5177\u4E00\u4F38\u4E00\u9876\uFF0C\u7ACB\u523B\u94BB\u8FDB\u8089\u82B1\u4E2D\uFF0C\u6324\u51FA\u4E00\u6EE9\u6CE5\u6CDE\u3002</p><p>\u80A5\u732A\u5F13\u7740\u8170\u4E00\u62F1\u4E00\u62F1\uFF0C\u5973\u4F53\u6E10\u6E10\u88AB\u62F1\u51FA\u6CE5\u5751\u3002\u5148\u662F\u67D4\u9888\uFF0C\u7136\u540E\u662F\u4E00\u5BF9\u8F7B\u84EC\u84EC\u7684\u80A5\u4E73\uFF0C\u63A5\u7740\u662F\u8170\u80A2\u3001\u5927\u817F\u3002</p><p>\u87BA\u65CB\u72B6\u7684\u732A\u97AD\u4E13\u4E3A\u63D2\u5165\u5B50\u5BAB\u800C\u751F\u3002\u8FDB\u5165\u4F53\u5185\u540E\u4FBF\u76F4\u76F4\u4F38\u8FDB\u5B50\u5BAB\u9888\uFF0C\u7565\u5E26\u5F2F\u5F27\u7684\u830E\u7AEF\u76F4\u63A5\u5728\u5BAB\u9888\u5185\u62BD\u9001\u8D77\u6765\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u7684\u8170\u80A2\u88AB\u9876\u5F97\u5411\u4E0A\u5F2F\u8D77\u8FD1\u4E4E\u76F4\u89D2\uFF0C\u5927\u817F\u5DE6\u53F3\u5E73\u5206\uFF0C\u659C\u659C\u7FD8\u8D77\uFF0C\u7834\u788E\u7684\u819D\u5173\u8282\u5374\u4E0D\u81EA\u7136\u7684\u5F2F\u6298\u4E0B\u6765\u3002\u4E24\u6761\u4E0D\u53D7\u63A7\u5236\u7684\u5C0F\u817F\u60AC\u5728\u817F\u4E0B\u6447\u6447\u6643\u6643\uFF0C\u6CE5\u6C34\u6DCC\u5E72\u7684\u5730\u65B9\uFF0C\u9690\u7EA6\u9732\u51FA\u89E6\u76EE\u60CA\u5FC3\u7684\u82CD\u767D\u3002</p><p>\u65E0\u8BBA\u662F\u4EBA\u662F\u517D\uFF0C\u5BF9\u96EA\u5CF0\u795E\u5C3C\u6765\u8BF4\u51E0\u4E4E\u90FD\u6CA1\u6709\u533A\u522B\u3002\u4E5F\u8BB8\u533A\u522B\u5728\u4E8E\uFF1A\u8FD9\u4E9B\u771F\u6B63\u7684\u79BD\u517D\u4E0D\u4F1A\u6709\u610F\u5F04\u75DB\u5979\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u6C89\u9ED8\u7684\u96EA\u5CF0\u795E\u5C3C\u5589\u5934\u7A81\u7136\u4E00\u52A8\uFF0C\u5267\u70C8\u5730\u5455\u5410\u8D77\u6765\u3002\u521A\u521A\u541E\u4E0B\u7684\u732A\u98DF\u6DF7\u7740\u6CE5\u6C34\u548C\u80C3\u6DB2\u4E00\u53E4\u8111\u5168\u5410\u4E86\u51FA\u6765\u3002\u5598\u606F\u8FD8\u672A\u505C\u6B62\uFF0C\u80A5\u732A\u53C8\u662F\u4E00\u62F1\uFF0C\u795E\u5C3C\u7684\u9762\u5B54\u91CD\u91CD\u8DCC\u5728\u81EA\u5DF1\u7684\u5455\u5410\u7269\u4E2D\u3002</p><p>\u795E\u5C3C\u5403\u529B\u5730\u626C\u8D77\u6C61\u79FD\u7684\u8138\u5E9E\uFF0C\u7741\u5F00\u773C\u775B\u3002</p><p>\u8FDC\u5904\u7684\u6885\u6811\u4E0B\uFF0C\u4E00\u4E2A\u7EA2\u8863\u5C11\u5973\u62E5\u7D27\u6597\u7BF7\uFF0C\u53EA\u5269\u4E00\u5BF9\u79C0\u76EE\u5728\u5916\u3002\u5979\u8FDC\u8FDC\u770B\u7740\u8FD9\u4E00\u5E55\uFF0C\u6CA1\u6709\u8BF4\u8BDD\uFF0C\u4E5F\u6CA1\u6709\u632A\u6B65\u3002</p><p>\u4E24\u4EBA\u9ED8\u9ED8\u5BF9\u89C6\u534A\u664C\uFF0C\u6700\u540E\u5C11\u5973\u8FDF\u7F13\u5730\u8F6C\u8FC7\u8EAB\uFF0C\u6162\u6162\u79BB\u5F00\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5A18\u3002\u300D</p><p>\u300C\u55EF\uFF1F\u300D</p><p>\u7D2B\u73AB\u5C06\u6309\u6469\u8FC7\u7684\u624B\u81C2\u585E\u5230\u88AB\u4E0B\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u4ED6\u5BF9\u4F60\u5F88\u597D\u2014\u2014\u4F60\u8981\u597D\u597D\u6D3B\u7740\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u7389\u8138\u4E00\u7EA2\uFF0C\u5BB3\u7F9E\u5730\u8BF4\uFF1A\u300C\u4ED6\u5A36\u7684\u662F\u4F60\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u51C4\u7136\u4E00\u7B11\uFF0C\u5FC3\u9053\uFF1A\u6211\u548C\u4F60\u4E00\u6837\uFF0C\u90FD\u53EA\u662F\u4ED6\u7684\u73A9\u7269\u3002\u4E0D\u8FC7\u2026\u2026\u8FD9\u6837\u7684\u65E5\u5B50\u6211\u518D\u4E5F\u8FC7\u4E0D\u4E0B\u53BB\u4E86\uFF0C\u5C31\u662F\u6B7B\uFF0C\u6211\u4E5F\u8981\u6539\u53D8\u81EA\u5DF1\u7684\u547D\u8FD0\u3002</p><p>\u8427\u4F5B\u5974\u8138\u4E0A\u5E26\u7740\u4E00\u62B9\u75C5\u6001\u7684\u8273\u7EA2\u3002\u6628\u665A\u5979\u88AB\u67B6\u5230\u5730\u4E0A\uFF0C\u5168\u9760\u809B\u4E2D\u7684\u6728\u68CD\u652F\u6491\u8DEA\u5750\u4E86\u4E00\u6574\u591C\uFF0C\u81F3\u4ECA\u6728\u68CD\u8FD8\u6CA1\u6709\u62D4\u51FA\u3002\u5E78\u597D\u6709\u5C3F\u5E03\u63A9\u7740\uFF0C\u624D\u6CA1\u8BA9\u5973\u513F\u770B\u51FA\u5F02\u6837\u3002</p><p>\u7D2B\u73AB\u6101\u7EEA\u6EE1\u8179\uFF0C\u8FD8\u8981\u5F3A\u989C\u6B22\u7B11\uFF0C\u5979\u6697\u81EA\u54BD\u4E0B\u6CEA\u6C34\uFF0C\u58F0\u97F3\u7565\u5E26\u53D1\u98A4\u5730\uFF1A\u300C\u5A18\uFF0C\u4F60\u5343\u4E07\u8981\u7167\u987E\u597D\u81EA\u5DF1\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u6709\u4E9B\u4E0D\u660E\u767D\uFF0C\u81EA\u5DF1\u996E\u98DF\u4FBF\u6EBA\u90FD\u9700\u8981\u522B\u4EBA\u5E2E\u5FD9\uFF0C\u8FD8\u80FD\u5982\u4F55\u7167\u6599\u81EA\u5DF1\uFF1F\u4F46\u809B\u5185\u7684\u80C0\u75DB\u4F7F\u5979\u65E0\u6CD5\u591A\u60F3\uFF0C\u5F53\u4E0B\u70B9\u5934\u7B54\u5E94\u3002</p><p>\u7D2B\u73AB\u89C1\u6BCD\u4EB2\u7CBE\u795E\u4E0D\u632F\uFF0C\u6EE1\u5FC3\u7684\u8BDD\u518D\u4E5F\u65E0\u6CD5\u8BF4\u51FA\u53E3\uFF0C\u53EA\u597D\u62B1\u4F4F\u6BCD\u4EB2\u7D27\u7D27\u4E00\u62E5\uFF0C\u7B11\u7740\u53BB\u4E86\u3002</p><p>\u8427\u4F5B\u5974\u88AB\u5973\u513F\u4E0D\u5BFB\u5E38\u7684\u4E3E\u52A8\u5F04\u5F97\u4E00\u6123\uFF0C\u65CB\u5373\u53C8\u88AB\u8089\u4F53\u6B32\u671B\u6240\u5F81\u670D\u3002\u7F8E\u5987\u4F4E\u4F4E\u547B\u541F\u4E00\u58F0\uFF0C\u809B\u8089\u4E00\u677E\u4E00\u7D27\uFF0C\u50CF\u4E00\u5F20\u7075\u6D3B\u7684\u5C0F\u5634\u541E\u5410\u7740\u6728\u68CD\uFF0C\u6DEB\u7325\u5730\u81EA\u5F97\u5176\u4E50\u7740\u3002</p><p>\u5979\u4E0D\u77E5\u9053\u521A\u624D\u90A3\u756A\u8BDD\u5176\u5B9E\u662F\u5973\u513F\u7684\u9057\u8A00\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u88AB\u6155\u5BB9\u9F99\u7684\u51B7\u9177\u6240\u51FB\u6E83\u7684\u7D2B\u73AB\uFF0C\u518D\u4E5F\u4E0D\u5E7B\u60F3\u80FD\u7528\u67D4\u60C5\u5A9A\u60D1\u8FD9\u4E2A\u79BD\u517D\u3002\u800C\u5E08\u7236\u7684\u60E8\u72B6\u5219\u6FC0\u53D1\u4E86\u5979\u7684\u52C7\u6C14\u3002</p><p>\u5E08\u7236\u4E0D\u4EC5\u8FD8\u6D3B\u7740\uFF0C\u800C\u4E14\u8FD8\u518D\u6B21\u6000\u5B55\u3002\u7D2B\u73AB\u5F88\u6E05\u695A\u5E08\u7236\u7684\u5455\u5410\u610F\u5473\u7740\u4EC0\u4E48\u3002\u521A\u521A\u6E05\u7A7A\u7684\u5B50\u5BAB\u53C8\u6000\u4E0A\u4E0D\u77E5\u8EAB\u4EFD\u7684\u80CE\u513F\uFF0C\u8FD9\u5BF9\u5E08\u7236\u8FD9\u6837\u7684\u65B9\u5916\u4EBA\u58EB\u662F\u591A\u4E48\u5927\u7684\u6253\u51FB\u2026\u2026</p><p>\u53EF\u5E08\u7236\u59CB\u7EC8\u6CA1\u6709\u653E\u5F03\u3002\u5373\u4F7F\u662F\u56DB\u80A2\u5173\u8282\u88AB\u5E9F\uFF0C\u7435\u7436\u9AA8\u88AB\u7A7F\uFF0C\u732A\u72D7\u822C\u6254\u5728\u6CE5\u6CDE\u4E2D\u7B49\u5F85\u6B7B\u4EA1\uFF0C\u5E08\u7236\u4ECD\u7136\u6323\u624E\u7740\u8981\u6D3B\u4E0B\u6765\u3002\u53EA\u6709\u6D3B\u7740\u624D\u6709\u5E0C\u671B\u2014\u2014\u5979\u4EFF\u4F5B\u542C\u5230\u5E08\u7236\u521A\u5389\u7684\u58F0\u97F3\uFF1A\u98D8\u6885\u5CF0\u5F1F\u5B50\u7EDD\u4E0D\u4F1A\u8F6F\u5F31\u7684\u60F3\u8981\u81EA\u6740\uFF01</p><p>\u7D2B\u73AB\u662F\u62B1\u7740\u5FC5\u6B7B\u7684\u51B3\u5FC3\u53BB\u9762\u5BF9\u9634\u957F\u91CE\u3002\u65E0\u8BBA\u4F1A\u53D7\u5230\u4EC0\u4E48\u6837\u51CC\u8FB1\uFF0C\u65E0\u8BBA\u5FC3\u91CC\u591A\u4E48\u6076\u5FC3\u6050\u60E7\uFF0C\u5979\u90FD\u8981\u518D\u6B21\u53BB\u9762\u5BF9\u90A3\u4E2A\u602A\u7269\u3002</p><p>\u5979\u77E5\u9053\uFF0C\u5373\u4F7F\u5B9D\u85CF\u5B58\u5728\uFF0C\u4F1A\u7ED9\u81EA\u5DF1\u5E26\u6765\u751F\u8DEF\u7684\u673A\u7387\u4E5F\u6781\u5176\u6E3A\u832B\u3002\u4F46\u4E3A\u4E86\u90A3\u4E07\u5206\u4E4B\u4E00\u7684\u5E0C\u671B\uFF0C\u5979\u5B81\u613F\u7528\u8D1E\u6D01\u3001\u8089\u4F53\uFF0C\u751A\u81F3\u751F\u547D\u53BB\u6362\u3002\u6216\u8005\u4EC0\u4E48\u90FD\u6CA1\u6709\uFF0C\u53EA\u4E3A\u4E86\u90A3\u4E2A\u53CD\u6297\u7684\u59FF\u52BF\uFF0C\u6216\u8005\u53CD\u6297\u672C\u8EAB\u3002</p><p>\u660E\u77E5\u9053\u6700\u540E\u4E00\u4E2A\u7EBF\u7D22\u5728\u90A3\u4E2A\u602A\u7269\u80CC\u540E\uFF0C\u5374\u56E0\u4E3A\u79CD\u79CD\u7406\u7531\u800C\u4E0D\u53BB\u5BFB\u627E\uFF0C\u5979\u4F1A\u6B7B\u4E0D\u7791\u76EE\u3002</p><p>\u300C\u662F\u3002\u6211\u4F1A\u6B7B\u4E0D\u7791\u76EE\u3002\u300D\u7D2B\u73AB\u5BF9\u81EA\u5DF1\u8F7B\u58F0\u8BF4\u3002</p><p>\u5979\u4ED4\u7EC6\u6D17\u51C0\u81EA\u5DF1\u6BCF\u4E00\u5BF8\u808C\u80A4\uFF0C\u7136\u540E\u64E6\u5E72\u79C0\u53D1\uFF0C\u76D8\u4E86\u4E00\u4E2A\u7CBE\u81F4\u7684\u53D1\u9AFB\u3002</p><p>\u94DC\u955C\u4E2D\u7684\u9752\u4E1D\u4E4C\u9ED1\u4EAE\u6CFD\uFF0C\u7EA4\u6307\u4EFF\u4F5B\u767D\u8272\u7684\u8776\u7FC5\uFF0C\u5728\u53D1\u95F4\u7FE9\u7FE9\u98DE\u821E\u3002</p><p>\u659C\u659C\u63D2\u4E86\u4E00\u652F\u7389\u7C2A\uFF0C\u4E00\u652F\u51E4\u9497\uFF0C\u518D\u5C06\u51E4\u53E3\u7684\u5782\u73E0\u4E00\u4E00\u7406\u987A\uFF0C\u7D2B\u73AB\u7FFB\u5F00\u6848\u4E0A\u4ECE\u672A\u6253\u5F00\u8FC7\u7684\u7F8A\u8102\u7389\u76D2\uFF0C\u6CBE\u4E86\u4E00\u70B9\u80ED\u8102\uFF0C\u5747\u5300\u5730\u6D82\u5728\u5507\u4E0A\u3002</p><p>\u80ED\u8102\u63A9\u4F4F\u4E86\u5507\u74E3\u5931\u8840\u7684\u82CD\u767D\uFF0C\u6563\u53D1\u7740\u8FF7\u4EBA\u7684\u73AB\u7470\u7EA2\u3002\u4EFF\u4F5B\u4ED9\u6307\u4E00\u70B9\uFF0C\u955C\u91CC\u7684\u5C11\u5973\u987F\u65F6\u9C9C\u6D3B\u8D77\u6765\u3002</p><p>\u5149\u6D01\u7684\u7389\u988A\u8FDC\u6BD4\u4EFB\u4F55\u9999\u7C89\u66F4\u52A0\u767D\u817B\u6ED1\u5AE9\uFF0C\u7D2B\u73AB\u53EA\u7406\u4E86\u7406\u776B\u6BDB\u548C\u5F2F\u7709\uFF0C\u8BA9\u81EA\u5DF1\u7684\u7F8E\u76EE\u6108\u53D1\u52A8\u4EBA\u3002</p><p>\u6700\u540E\uFF0C\u5979\u62FF\u51FA\u8309\u8389\u82B1\u6CB9\uFF0C\u7EC6\u81F4\u5730\u6D82\u904D\u5168\u8EAB\uFF0C\u8BA9\u5468\u8EAB\u6BCF\u4E00\u5BF8\u808C\u80A4\u90FD\u6676\u83B9\u6DA6\u6CFD\uFF0C\u5E26\u7740\u99A5\u90C1\u7684\u9999\u751C\u3002</p><p>\u5F53\u62B9\u5230\u4E73\u623F\u65F6\uFF0C\u7D2B\u73AB\u6258\u8D77\u53F3\u4E73\uFF0C\u4E73\u4E0B\u90A3\u4E2A\u9ED1\u70B9\u5DF2\u7ECF\u5927\u4E86\u4E00\u500D\u3002\u5979\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u5982\u679C\u8FD8\u6CA1\u80FD\u627E\u5230\u5B9D\u85CF\uFF0C\u5C31\u8BA9\u5B83\u70C2\u4E0B\u53BB\u597D\u4E86\u3002</p><p>\u6C89\u7538\u7538\u7684\u4E73\u7403\u5728\u624B\u91CC\u4E00\u9635\u8F7B\u98A4\uFF0C\u5F85\u62FF\u5F00\u624B\u638C\uFF0C\u5AE9\u7EA2\u7684\u4E73\u5934\u5DF2\u5FAE\u5FAE\u7FD8\u8D77\uFF0C\u50CF\u4E00\u4E2A\u64A9\u4EBA\u7684\u5FAE\u7B11\u3002</p><p>\u7D2B\u73AB\u7AD9\u8D77\u8EAB\u6765\u3002\u955C\u91CC\u7684\u5C11\u5973\u5706\u8179\u9AD8\u633A\uFF0C\u80A5\u4E73\u5E76\u4E3E\u3002\u5468\u8EAB\u808C\u80A4\u5982\u8102\u5982\u7389\uFF0C\u82B3\u9999\u56DB\u6EA2\u3002\u8EAB\u6000\u516D\u7532\u7684\u7D2B\u73AB\u4E0D\u4EC5\u4EC5\u6CA1\u6709\u7A0D\u51CF\u5A07\u8273\uFF0C\u53CD\u800C\u591A\u4E86\u4E00\u5206\u6175\u61D2\u7684\u98CE\u97F5\u3002</p><p>\u5979\u5148\u5E26\u4E0A\u6C34\u7EA2\u8272\u7684\u8F7B\u7F0E\u62B9\u80F8\uFF0C\u7136\u540E\u5957\u4E0A\u4E00\u4EF6\u96EA\u767D\u7684\u4E91\u7EF8\u4EB5\u8863\u3002\u7D2B\u73AB\u7CBE\u5FC3\u7CFB\u597D\u8863\u5E26\uFF0C\u633D\u4E86\u4E00\u4E2A\u76F8\u601D\u7ED3\uFF0C\u63A5\u7740\u62AB\u4E0A\u957F\u8FC7\u8170\u8179\u7684\u4E2D\u8863\uFF0C\u7CFB\u4E0A\u53CA\u819D\u7684\u5185\u88D9\u548C\u5782\u5230\u811A\u9762\u7684\u5916\u88D9\u3002\u8F7B\u8F7B\u4E00\u5C55\uFF0C\u88D9\u4E0A\u9C9C\u7EA2\u7684\u6843\u82B1\u4EFF\u4F5B\u6EE1\u8863\u7F24\u7EB7\u7684\u843D\u82F1\u7FE9\u7136\u8D77\u821E\u3002</p><p>\u6843\u82B1\u7EB7\u7EB7\u626C\u626C\u98D8\u5230\u8863\u895F\u8896\u5E95\u3002\u82B1\u74E3\u8D8A\u6765\u8D8A\u788E\uFF0C\u6700\u540E\u5C42\u5C42\u53E0\u53E0\u79EF\u6210\u4E00\u7247\u6DE1\u6DE1\u7684\u7C89\u7EA2\u3002</p><p>\u675F\u597D\u8863\u7269\uFF0C\u5C11\u5973\u5C06\u4E00\u4EF6\u9576\u9ED1\u6EDA\u8FB9\u7684\u7EC7\u9526\u5939\u8884\u5957\u5728\u5916\u9762\u3002\u8863\u895F\u65E0\u6CD5\u6263\u4E0A\uFF0C\u53EA\u80FD\u655E\u5F00\uFF0C\u62AB\u5728\u8179\u4FA7\u3002\u6700\u540E\u5979\u62FF\u51FA\u4E00\u6839\u4E1D\u7EE6\u5C06\u7389\u4F69\u7ED3\u5728\u8170\u4E0B\uFF0C\u518D\u6302\u4E0A\u9EC4\u91D1\u5C0F\u5F29\u3002</p><p>\u7D2B\u73AB\u6276\u7740\u5C0F\u8179\uFF0C\u6D45\u7B11\u7740\u671B\u5411\u955C\u91CC\u5343\u5A07\u767E\u5A9A\u7684\u5C11\u5973\u3002</p><p>\u5979\u77E5\u9053\uFF0C\u81EA\u5DF1\u5982\u6B64\u7CBE\u5FC3\u6253\u626E\uFF0C\u6700\u540E\u53EF\u80FD\u8FDE\u4E00\u4EF6\u5B8C\u6574\u7684\u8863\u895F\u90FD\u4E0D\u4F1A\u7559\u4E0B\u3002</p><p>\u6BD5\u751F\u7B2C\u4E00\u6B21\u7528\u5FC3\u5986\u626E\uFF0C\u5374\u662F\u8981\u5C06\u8FD9\u5177\u9C9C\u5AE9\u7684\u8089\u4F53\u732E\u7ED9\u4E00\u4E2A\u80AE\u810F\u7684\u602A\u7269\u3002\u81EA\u5DF1\u771F\u662F\u597D\u8D31\u5462\u2026\u2026</p><p>\u7D2B\u73AB\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u62C9\u8D77\u8863\u8896\uFF0C\u5C06\u4E00\u53EA\u7FE1\u7FE0\u624B\u956F\u5957\u5728\u971C\u96EA\u822C\u7684\u7693\u8155\u4E0A\u3002</p>',67);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
