import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_116" tabindex="-1"><a class="header-anchor" href="#_116" aria-hidden="true">#</a> 116</h1><p>\u9759\u5BA4\u5185\uFF0C\u6155\u5BB9\u9F99\u65E0\u7531\u5730\u4E00\u9635\u5FC3\u60B8\u3002</p><p>\u4ED6\u677E\u5F00\u4E86\u624B\u8BC0\uFF0C\u7F13\u7F13\u6563\u4E86\u529F\u3002</p><p>\u662F\u56E0\u4E3A\u5A18\uFF1F\u59B9\u59B9\uFF1F\u8FD8\u662F\u56E0\u4E3A\u4ECE\u524D\u7684\u65E5\u5B50\uFF1F\u6155\u5BB9\u9F99\u5176\u5B9E\u6CA1\u6709\u592A\u591A\u5962\u6C42\uFF0C\u53EA\u662F\u8840\u6DB2\u4E2D\u90A3\u4E9B\u6839\u6DF1\u8482\u56FA\u7684\u56E0\u5B50\uFF0C\u4F7F\u590D\u56FD\u6210\u4E3A\u6155\u5BB9\u6C0F\u6BCF\u4E00\u4E2A\u7537\u4EBA\u7684\u5BBF\u547D\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u4ED6\u53EA\u6C42\u751F\u547D\u4E2D\u6700\u91CD\u8981\u7684\u4E24\u4E2A\u5973\u4EBA\u80FD\u4E56\u4E56\u5F85\u5728\u8EAB\u8FB9\u3002\u4ED6\u751A\u81F3\u4E0D\u5962\u671B\u80FD\u83B7\u5F97\u4E24\u4EBA\u7684\u67D4\u60C5\uFF0C\u53EA\u8981\u5979\u4EEC\u4EBA\u5728\u6B64\u5904\uFF0C\u5728\u81EA\u5DF1\u89E6\u624B\u53EF\u53CA\u7684\u8303\u56F4\u5185\uFF0C\u5C48\u4F0F\u5728\u4ED6\u5F3A\u5065\u7684\u7FBD\u7FFC\u4E4B\u4E0B\u2014\u2014\u8FD9\u6837\u5C31\u597D\u3002\u81F3\u4E8E\u5973\u4EBA\u7684\u5FC3\u7075\uFF0C\u90A3\u662F\u7537\u4EBA\u80FD\u529B\u4E4B\u5916\u7684\u4E8B\uFF0C\u4E5F\u662F\u9633\u5177\u548C\u751F\u80B2\u4E4B\u5916\u7684\u4E8B\u3002</p><p>\u4ED6\u5728\u5FC3\u91CC\u629A\u8FC7\u90A3\u5F20\u6697\u6CE3\u7684\u5A07\u9765\u3002\u5374\u627E\u4E0D\u5230\u8BDD\u5BF9\u5979\u8BF4\u3002</p><p>\u826F\u4E45\uFF0C\u6155\u5BB9\u9F99\u4F4E\u53F9\u4E00\u58F0\uFF0C\u6536\u655B\u5FC3\u795E\uFF0C\u76D8\u819D\u5750\u5728\u592A\u6781\u56FE\u4E0A\uFF0C\u4E24\u624B\u5206\u6309\u9634\u9633\u9C7C\u7684\u53CC\u773C\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5E7D\u6697\u7684\u5730\u7A9F\u5185\uFF0C\u6DE1\u6DE1\u7684\u73E0\u8F89\u4EFF\u4F5B\u6307\u5C16\u7684\u4E00\u70B9\u7075\u5149\u3002\u6155\u5BB9\u7D2B\u73AB\u63D0\u7740\u88D9\u88FE\uFF0C\u73AB\u7470\u8272\u7684\u4FCF\u8138\u65E0\u5FE7\u65E0\u559C\uFF0C\u606C\u7136\u8D70\u5411\u672A\u77E5\u7684\u547D\u8FD0\u3002</p><p>\u77F3\u95E8\u4ECD\u50CF\u81EA\u5DF1\u79BB\u5F00\u65F6\u4E00\u6837\u6D1E\u5F00\u7740\u3002\u7D2B\u73AB\u501A\u5728\u95E8\u65C1\u5AE3\u7136\u4E00\u7B11\uFF0C\u300C\u9634\u53F3\u4F7F\uFF0C\u4F60\u597D\u2026\u2026\u300D</p><p>\u8BDD\u97F3\u672A\u843D\uFF0C\u5C11\u5973\u4FBF\u88AB\u4E00\u80A1\u72C2\u98D9\u5377\u5165\u6D1E\u7A9F\u3002</p><p>\u602A\u4EBA\u5C06\u7D2B\u73AB\u67D4\u8F6F\u7684\u8EAB\u5B50\u6254\u5728\u5730\u4E0A\uFF0C\u4E24\u624B\u6293\u53D6\u4F4F\u9886\u53E3\u4E00\u5206\uFF0C\u50CF\u5265\u7B0B\u4E00\u6837\uFF0C\u4E00\u628A\u5C06\u5C11\u5973\u5C42\u5C42\u8863\u7269\u5265\u4E86\u4E2A\u5E72\u51C0\u3002\u8F7B\u7EB1\u7EA2\u7EE1\u4E4D\u7136\u7834\u788E\uFF0C\u7CBE\u5FC3\u633D\u5C31\u7684\u76F8\u601D\u7ED3\u3001\u540C\u5FC3\u7ED3\u88AB\u7C97\u66B4\u5730\u62FD\u6210\u4E24\u6BB5\uFF0C\u96F6\u96F6\u788E\u788E\u7684\u9970\u7269\u6389\u4E86\u6EE1\u5730\u3002</p><p>\u9634\u957F\u91CE\u6293\u4F4F\u7D2B\u73AB\u7684\u80A5\u4E73\uFF0C\u4E0D\u7406\u4F1A\u5979\u7684\u75DB\u695A\uFF0C\u8FF3\u76F4\u5927\u529B\u63C9\u634F\u8D77\u6765\uFF0C\u300C\u81ED\u5A4A\u5B50\uFF0C\u8BDD\u7ED9\u8001\u5C48\u634E\u5230\u6CA1\u6709\uFF1F\u300D</p><p>\u867D\u7136\u65E9\u6709\u51C6\u5907\uFF0C\u4F46\u752B\u4E00\u89C1\u9762\u4FBF\u6A2A\u906D\u51CC\u8FB1\uFF0C\u7D2B\u73AB\u81EA\u7136\u800C\u7136\u4FBF\u4EA7\u751F\u51FA\u6297\u62D2\u548C\u538C\u6076\u611F\u3002\u5979\u60CA\u614C\u5730\u62E7\u4F4F\u65AD\u8896\uFF0C\u538B\u4F4F\u5FC3\u5E95\u7684\u803B\u8FB1\u548C\u6068\u610F\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u5C48\u62A4\u6CD5\u2026\u2026\u4E0D\u5728\u5BAB\u91CC\u2026\u2026\u300D</p><p>\u9634\u957F\u91CE\u52C3\u7136\u5927\u6012\uFF0C\u300C\u81ED\u5A4A\u5B50\uFF0C\u4F60\u6562\u9A97\u8001\u5B50\uFF1F\u300D</p><p>\u7D2B\u73AB\u8F7B\u53EB\u4E00\u58F0\uFF0C\u98A6\u7D27\u7709\u5934\uFF0C\u557C\u58F0\u9053\uFF1A\u300C\u5C48\u62A4\u6CD5\u771F\u4E0D\u5728\u5BAB\u4E2D\u2026\u2026\u5974\u5A62\u662F\u6015\u524D\u8F88\u53D7\u82E6\uFF0C\u5E26\u6765\u4E9B\u98DF\u7269\u2026\u2026\u300D\u5979\u8138\u73B0\u7F9E\u8272\uFF0C\u5624\u58F0\u9053\uFF1A\u300C\u8FD8\u6709\u5974\u5A62\u81EA\u5DF1\uFF0C\u6765\u4F3A\u5019\u524D\u8F88\u2026\u2026\u300D</p><p>\u9634\u957F\u91CE\u773C\u4E2D\u7CBE\u5149\u95EA\u70C1\uFF0C\u6700\u540E\u4E22\u5F00\u7600\u80BF\u7684\u4E73\u623F\uFF0C\u62E3\u8D77\u5730\u4E0A\u7684\u5305\u88F9\u3002\u5148\u6495\u5F00\u55C5\u4E86\u55C5\uFF0C\u8FD9\u624D\u653E\u8FDB\u5634\u91CC\u3002\u4ED6\u4E00\u8FB9\u5927\u5403\u5927\u56BC\uFF0C\u4E00\u8FB9\u542B\u542B\u7CCA\u7CCA\u5730\u8BF4\uFF1A\u300C\u81ED\u5A4A\u5B50\uFF0C\u4F60\u73A9\u8D77\u5C44\u6765\u53C8\u9A9A\u53C8\u6D6A\uFF0C\u8001\u5B50\u559C\u6B22\u5F97\u7D27\u3002\u518D\u73A9\u4E00\u6B21\u8BA9\u8001\u5B50\u4E50\u4E50\uFF01\u300D</p><p>\u7D2B\u73AB\u77E5\u9053\u8FD9\u662F\u5FC5\u6709\u7684\u7F9E\u8FB1\uFF0C\u5F53\u4E0B\u542B\u7B11\u9664\u53BB\u978B\u889C\uFF0C\u892A\u4E0B\u8863\u88D9\u3002\u7EA4\u6307\u63A2\u5165\u5A07\u7F8E\u7684\u82B1\u74E3\uFF0C\u5A9A\u6001\u6A2A\u751F\u5730\u6311\u5F04\u8D77\u6765\u3002</p><p>\u5728\u9634\u957F\u91CE\u7684\u559D\u4EE4\u4E0B\uFF0C\u7D2B\u73AB\u65F6\u800C\u4EF0\u5367\uFF0C\u4E24\u817F\u9AD8\u4E3E\uFF0C\u6273\u8D77\u7389\u80A1\uFF1B\u65F6\u800C\u8DEA\u4F0F\uFF0C\u633A\u7740\u96EA\u81C0\u6413\u634F\u82B1\u8482\uFF1B\u65F6\u800C\u5403\u529B\u5730\u633D\u8D77\u4E00\u817F\u652F\u5728\u77F3\u58C1\u4E0A\uFF0C\u5C06\u79D8\u5904\u51D1\u5230\u5996\u90AA\u773C\u524D\uFF0C\u8BA9\u4ED6\u80FD\u770B\u6E05\u6BCF\u4E00\u4E2A\u7EC6\u8282\u3002</p><p>\u7D2B\u73AB\u6E10\u6E10\u60C5\u52A8\uFF0C\u767D\u5AE9\u7684\u80A2\u4F53\u4E0A\uFF0C\u4E00\u53EA\u51E4\u51F0\u9690\u9690\u6D6E\u73B0\u3002</p><p>\u9634\u957F\u91CE\u5927\u662F\u5947\u602A\uFF0C\u5C06\u7D2B\u73AB\u62C9\u5230\u8EAB\u524D\u7EC6\u7EC6\u7AEF\u8BE6\u3002\u7D2B\u73AB\u8146\u7740\u5C0F\u8179\uFF0C\u6307\u70B9\u8BF4\uFF1A\u300C\u8FD9\u662F\u51E4\u8DB3\uFF0C\u5476\uFF0C\u5728\u5974\u5A62\u8FD9\u91CC\u2026\u2026\u300D</p><p>\u7231\u6DB2\u6E7F\u6DA6\u4E86\u5FAE\u80BF\u7684\u82B1\u74E3\uFF0C\u5AE9\u8089\u4EFF\u4F5B\u6D07\u6E7F\u7684\u80ED\u8102\uFF0C\u9971\u542B\u7740\u6B32\u6EF4\u7684\u8273\u7EA2\u3002\u8089\u7F1D\u5728\u767D\u7699\u7684\u7389\u6307\u4E0B\u65F6\u5F00\u65F6\u5408\uFF0C\u9690\u7EA6\u9732\u51FA\u5A07\u7F8E\u7684\u7A74\u53E3\u3002\u5373\u4F7F\u4E0E\u6155\u5BB9\u9F99\u6700\u6295\u5165\u7684\u4EA4\u5408\u4E2D\uFF0C\u7D2B\u73AB\u4E5F\u6CA1\u6709\u5982\u6B64\u4E0D\u9057\u4F59\u529B\u5730\u91CA\u653E\u8FC7\u81EA\u5DF1\u3002</p><p>\u300C\u8FD9\u662F\u51E4\u7FFC\uFF0C\u5974\u5A62\u8FD9\u8FB9\u5976\u5B50\u8FD8\u6709\u2026\u2026\u300D</p><p>\u300C\u81ED\u5A4A\u5B50\u8FD9\u5BF9\u5976\u5B50\u771F\u5927\uFF0C\u662F\u4E0D\u662F\u90A3\u53F6\u884C\u5357\u5F04\u6765\u4EC0\u4E48\u65B0\u836F\uFF1F\u300D\u9634\u957F\u91CE\u4F38\u51FA\u6CB9\u5149\u5149\u7684\u9ED1\u624B\uFF0C\u5939\u4F4F\u5C11\u5973\u7EA2\u5AE9\u7684\u4E73\u5934\u3002</p><p>\u7D2B\u73AB\u817B\u58F0\u9053\uFF1A\u300C\u9634\u53F3\u4F7F\u679C\u7136\u662F\u76EE\u5149\u5982\u70AC\uFF0C\u4E00\u773C\u5C31\u770B\u51FA\u5974\u5A62\u7684\u2026\u2026\u8D31\u5976\u662F\u6539\u9020\u8FC7\u7684\u3002\u300D\u5979\u633A\u8D77\u80F8\u819B\uFF0C\u8BA9\u90A3\u5BF9\u6CB9\u624B\u8086\u610F\u628A\u73A9\u81EA\u5DF1\u7684\u4E73\u7403\uFF0C\u5A07\u6EF4\u6EF4\u5730\u8BF4\uFF1A\u300C\u524D\u8F88\u559C\u6B22\u5974\u5A62\u7684\u5976\u5B50\u5417\uFF1F\u300D</p><p>\u9634\u957F\u91CE\u53CD\u624B\u4E00\u638C\uFF0C\u4E73\u7403\u8FDC\u8FDC\u6C64\u5F00\uFF0C\u53C8\u5F39\u5230\u80F8\u524D\uFF0C\u98A4\u5FAE\u5FAE\u6447\u6643\u7740\u6162\u6162\u663E\u51FA\u4E94\u9053\u6307\u75D5\uFF0C\u300C\u7403\uFF01\u4EBA\u4E0D\u4EBA\u9B3C\u4E0D\u9B3C\u7684\uFF0C\u8FD8\u8FD9\u4E48\u5F97\u610F\u3002\u771F\u4ED6\u5988\u7684\u4E0D\u8981\u8138\uFF01\u300D</p><p>\u7D2B\u73AB\u5FC3\u5934\u50CF\u88AB\u4EBA\u523A\u4E86\u4E00\u5200\uFF0C\u6EF4\u51FA\u8840\u6765\u3002\u5979\u7728\u7728\u773C\u775B\uFF0C\u54BD\u4E0B\u6CEA\u6C34\uFF0C\u8F7B\u58F0\u8BF4\u9053\uFF1A\u300C\u524D\u8F88\u6559\u8BAD\u7684\u662F\u2026\u2026\u300D</p><p>\u300C\u54AC\u4F4F\u3002\u300D\u9634\u957F\u91CE\u5FFD\u7136\u8BF4\u3002</p><p>\u7D2B\u73AB\u6123\u4E86\u4E00\u4E0B\uFF0C\u5F20\u53E3\u5C06\u6EE1\u662F\u6CB9\u6E0D\u7684\u4E73\u5C16\u54AC\u5728\u5634\u4E2D\u3002</p><p>\u300C\u90A3\u4E2A\u3002\u300D</p><p>\u7D2B\u73AB\u4E24\u624B\u6367\u8D77\u80A5\u4E73\uFF0C\u6258\u5230\u5634\u65C1\uFF0C\u5C06\u4E24\u53EA\u4E73\u5934\u4E00\u5E76\u54AC\u5728\u5507\u95F4\u3002\u5979\u7B2C\u4E00\u6B21\u611F\u53D7\u5230\u81EA\u5DF1\u4E73\u5934\u7684\u6ECB\u5473\u3002\u6ED1\u5AE9\u4E2D\u8FD8\u5E26\u7740\u67D4\u97E7\u7684\u5F39\u6027\uFF0C\u7F8E\u5F97\u8BA9\u4EBA\u5FCD\u4E0D\u4F4F\u60F3\u72E0\u72E0\u54AC\u4E0B\u3002\u80A5\u817B\u7684\u4E73\u8089\u50CF\u67D4\u8F6F\u7684\u6CE2\u6D9B\uFF0C\u4E00\u8361\u4E00\u8361\u8F7B\u8F7B\u78B0\u89E6\u7740\u5634\u5507\u3002</p><p>\u4E24\u53EA\u6D51\u5706\u7684\u4E73\u7403\u88AB\u626F\u6210\u9525\u5F62\uFF0C\u5411\u4E0A\u626C\u8D77\uFF0C\u4E73\u5C16\u6D88\u5931\u5728\u4E39\u5507\u7693\u9F7F\u4E4B\u95F4\u3002\u7D2B\u73AB\u5659\u7740\u81EA\u5DF1\u7684\u4E73\u5934\uFF0C\u98A4\u6296\u7740\u8FCE\u6765\u4E86\u4ECA\u5929\u7B2C\u4E00\u6B21\u9AD8\u6F6E\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8FD9\u4E00\u4E2A\u65F6\u8FB0\u6BD4\u4E00\u767E\u5E74\u8FD8\u957F\u3002</p><p>\u7D2B\u73AB\u4EF0\u8DEA\u5728\u9634\u957F\u91CE\u8EAB\u524D\uFF0C\u4E24\u819D\u5E73\u5206\uFF0C\u7528\u4E00\u622A\u5403\u5269\u7684\u9E21\u817F\u9AA8\u5728\u79D8\u5904\u6345\u5F04\u3002\u8FD9\u662F\u9634\u957F\u91CE\u7684\u5429\u5490\uFF0C\u4E0D\u8BB8\u5979\u78B0\u89E6\u5176\u5B83\u90E8\u4F4D\uFF0C\u53EA\u7528\u8FD9\u6839\u77ED\u5C0F\u7684\u9AA8\u5934\u628A\u81EA\u5DF1\u6345\u5230\u53D1\u6D6A\u3002</p><p>\u88AB\u9634\u957F\u91CE\u542E\u51C0\u7684\u9E21\u9AA8\u5F88\u5149\u6ED1\uFF0C\u63D2\u5728\u4F53\u5185\u5E76\u4E0D\u75BC\u75DB\u3002\u4F46\u90A3\u79CD\u7F9E\u8FB1\u5374\u6BD4\u75BC\u75DB\u66F4\u751A\u3002</p><p>\u9634\u957F\u91CE\u88AB\u9501\u5728\u58C1\u4E0A\uFF0C\u4EC5\u6709\u5BF8\u8BB8\u7684\u6D3B\u52A8\u7A7A\u95F4\u3002\u7D2B\u73AB\u539F\u672C\u51C6\u5907\u5728\u4EA4\u5408\u4E2D\u7528\u624B\u6307\u6478\u7D22\u77F3\u58C1\u7684\u7EB9\u9970\uFF0C\u6570\u6708\u6765\u7684\u5F00\u9501\u7ECF\u9A8C\uFF0C\u5979\u5BF9\u81EA\u5DF1\u6307\u4E0A\u7684\u89E6\u89C9\u6781\u6709\u4FE1\u5FC3\u3002\u4F46\u9634\u957F\u91CE\u5374\u592A\u4E0D\u4E89\u6C14\uFF0C\u65E0\u8BBA\u5979\u5982\u4F55\u52AA\u529B\uFF0C\u90A3\u6839\u9633\u5177\u90FD\u50CF\u6B7B\u86C7\u822C\u6BEB\u65E0\u52A8\u4F5C\u3002</p><p>\u601D\u7D22\u591A\u65F6\uFF0C\u7D2B\u73AB\u7F8E\u76EE\u4E00\u4EAE\uFF0C\u606D\u656C\u5730\u8BF4\u9053\uFF1A\u300C\u524D\u8F88\uFF0C\u5974\u5A62\u6765\u5E2E\u60A8\u6D17\u6D17\u8EAB\u5B50\uFF0C\u597D\u5417\uFF1F\u300D</p><p>\u8FD9\u4E48\u4E00\u8BF4\uFF0C\u9634\u957F\u91CE\u8EAB\u4E0A\u987F\u65F6\u75D2\u4E86\u8D77\u6765\u3002\u4E8C\u5341\u591A\u5E74\u6765\u6CA1\u6D17\u6FA1\uFF0C\u771F\u4E0D\u662F\u4EBA\u8FC7\u7684\u65E5\u5B50\u3002</p><p>\u4ED6\u8214\u4E86\u8214\u5E72\u88C2\u7684\u5634\u5507\uFF0C\u563F\u7136\u9053\uFF1A\u300C\u4E0D\u8BB8\u7528\u624B\u3002\u300D</p><p>\u7D2B\u73AB\u987A\u7740\u4ED6\u7684\u76EE\u5149\u770B\u5411\u81EA\u5DF1\u7684\u4E73\u623F\uFF0C\u987F\u65F6\u660E\u767D\u8FC7\u6765\u3002\u5979\u54AC\u7740\u7259\u5173\uFF0C\u6643\u4E86\u6643\u5706\u4E73\uFF0C\u300C\u524D\u8F88\u82E5\u4E0D\u5ACC\u5F03\uFF0C\u5974\u5A62\u5C31\u7528\u8FD9\u5BF9\u4E0D\u8981\u8138\u7684\u8D31\u5976\u4F3A\u5019\u60A8\u2026\u2026\u300D\u5979\u8BF4\u7740\u4E0B\u8D31\u7684\u8BDD\u8BED\uFF0C\u5FC3\u91CC\u7684\u6068\u610F\u6BD4\u7247\u7389\u7684\u950B\u8292\u8FD8\u8981\u9510\u5229\u3002</p><p>\u64A9\u5F00\u4E71\u84EC\u84EC\u7EA0\u7F20\u7684\u6BDB\u53D1\uFF0C\u9634\u957F\u91CE\u9B41\u68A7\u7684\u8EAB\u4F53\u7626\u9AA8\u5D99\u5CCB\uFF0C\u6D3B\u50CF\u4E00\u628A\u5E72\u67F4\u3002\u8F7B\u8F7B\u4E00\u78B0\uFF0C\u904D\u4F53\u7684\u6CE5\u57A2\u4FBF\u7EB7\u7136\u800C\u843D\u3002</p><p>\u7D2B\u73AB\u628A\u68C9\u8884\u7684\u788E\u7247\u653E\u5728\u77F3\u7A74\u7684\u79EF\u6C34\u4E2D\u6D78\u6E7F\uFF0C\u7136\u540E\u6BC5\u7136\u8D77\u8EAB\uFF0C\u5DE7\u7B11\u7740\u5C06\u96EA\u767D\u7684\u5A07\u8EAF\u8D34\u5728\u9634\u957F\u91CE\u5E72\u67AF\u7684\u8EAB\u4F53\u3002</p><p>\u808C\u80A4\u78E8\u64E6\u7684\u4EFF\u4F5B\u662F\u575A\u786C\u7684\u6811\u76AE\uFF0C\u9690\u9690\u4F5C\u75DB\u3002\u7D2B\u73AB\u7EA4\u624B\u4E00\u62E7\uFF0C\u6676\u83B9\u7684\u6C34\u6EF4\u4EFF\u4F5B\u9178\u695A\u7684\u6CEA\u6C34\u843D\u5728\u5706\u6DA6\u7684\u5AE9\u4E73\u4E0A\u3002\u5979\u50CF\u4E00\u4E2A\u6DF1\u60C5\u7684\u5C11\u5973\uFF0C\u5FC3\u7518\u60C5\u613F\u5730\u732E\u51FA\u82AC\u82B3\u7684\u8089\u4F53\uFF0C\u7136\u800C\u5979\u7684\u300C\u60C5\u90CE\u300D\uFF0C\u5374\u662F\u4E2A\u4E09\u5206\u50CF\u4EBA\uFF0C\u4E03\u5206\u50CF\u9B3C\u7684\u602A\u7269\u3002</p><p>\u6E29\u6DA6\u7684\u8089\u4F53\u4E0A\u4E0B\u8D77\u4F0F\uFF0C\u5C0F\u5DE7\u7684\u4E73\u5934\u7728\u773C\u4FBF\u67D3\u5F97\u4E4C\u9ED1\u3002\u4E73\u7403\u524D\u7AEF\u4EFF\u4F5B\u58A8\u6D82\u822C\uFF0C\u540E\u9762\u5374\u4F9D\u7136\u767D\u5AE9\u3002</p><p>\u8FD9\u6837\u662F\u4E0D\u662F\u6DEB\u8361\u5462\uFF1F\u4E3A\u4E86\u9003\u751F\u5C31\u5982\u6B64\u4F5C\u8DF5\u81EA\u5DF1\uFF0C\u4E0E\u90A3\u4E9B\u7528\u8089\u4F53\u6362\u53D6\u8863\u98DF\u7684\u5A3C\u5993\u53C8\u6709\u4EC0\u4E48\u533A\u522B\u5462\uFF1F</p><p>\u604D\u60DA\u95F4\uFF0C\u6000\u91CC\u8165\u81ED\u7684\u6C61\u57A2\u4EFF\u4F5B\u900F\u8FC7\u51E4\u51F0\u7EB9\u8EAB\uFF0C\u4E00\u70B9\u70B9\u4FB5\u5165\u7EAF\u6D01\u7684\u8089\u4F53\u4E4B\u5185\u3002</p><p>\u300C\u6211\u662F\u4E00\u4E2A\u5A4A\u5B50\u2026\u2026\u300D\u7D2B\u73AB\u8F7B\u58F0\u5BF9\u81EA\u5DF1\u8BF4\u3002</p><p>\u300C\u5E9F\u8BDD\uFF01\u661F\u6708\u6E56\u7684\u5973\u4EBA\u90FD\u662F\u5A4A\u5B50\uFF01\u300D\u9634\u957F\u91CE\u553E\u4E86\u4E00\u53E3\uFF0C\u6402\u4F4F\u7D2B\u73AB\u7684\u8170\u81C0\uFF0C\u62E8\u5F00\u6E7F\u6DCB\u6DCB\u7684\u82B1\u74E3\uFF0C\u62FC\u547D\u628A\u9633\u5177\u9001\u5165\u8089\u7A74\u3002</p><p>\u53D7\u5230\u538B\u8FEB\u7684\u80CE\u513F\u6323\u624E\u8D77\u6765\uFF0C\u5728\u5706\u6EDA\u6EDA\u7684\u5C0F\u8179\u5185\u53C8\u8E22\u53C8\u8E6C\u3002\u809A\u76AE\u6ED1\u817B\u7684\u52A8\u4F5C\u6FC0\u8D77\u4E86\u9634\u957F\u91CE\u7684\u6B32\u706B\uFF0C\u4ED6\u7D27\u7D27\u6293\u4F4F\u7D2B\u73AB\uFF0C\u63C9\u788E\u4E00\u822C\u78E8\u64E6\u7740\u5979\u7684\u5C0F\u8179\u3002</p><p>\u7D2B\u73AB\u5B50\u5BAB\u5267\u75DB\uFF0C\u8033\u8FB9\u4F3C\u4E4E\u542C\u5230\u80CE\u513F\u9AA8\u6298\u7684\u58F0\u97F3\u3002\u5979\u989D\u4E0A\u5192\u51FA\u51B7\u6C57\uFF0C\u5507\u4E0A\u7684\u80ED\u8102\u54AC\u5F97\u4E03\u96F6\u516B\u843D\u3002</p><p>\u5598\u4E86\u53E3\u6C14\uFF0C\u5C11\u5973\u7A81\u7136\u4E00\u7B11\uFF0C\u795E\u6001\u59A9\u5A9A\u4E4B\u6781\u3002\u63A5\u7740\u5C55\u5F00\u67D4\u81C2\uFF0C\u4EE5\u66F4\u5927\u7684\u529B\u6C14\u62B1\u7D27\u602A\u7269\u6BAD\u5C38\u822C\u80AE\u810F\u53D1\u81ED\u7684\u8EAB\u4F53\u3002</p><p>\u7D2B\u73AB\u4F7F\u51FA\u5403\u5976\u7684\u529B\u6C14\u6402\u7D27\u9634\u957F\u91CE\uFF0C\u5782\u5934\u671D\u4ED6\u80CC\u540E\u770B\u53BB\uFF0C\u540C\u65F6\u62D4\u4E0B\u51E4\u9497\u3002</p><p>\u5982\u6C34\u7684\u7F8E\u76EE\u7728\u4E86\u51E0\u7728\uFF0C\u6BCF\u7728\u4E00\u6B21\uFF0C\u90FD\u53D8\u5F97\u66F4\u5927\u3002\u7728\u4E86\u4E09\u6B21\u4E4B\u540E\uFF0C\u9ED1\u767D\u5206\u660E\u7684\u5927\u773C\u91CC\u7A81\u7136\u6D8C\u51FA\u4E00\u5C42\u6CEA\u6C34\u3002</p><p>\u51E4\u9497\u300C\u53EE\u300D\u7684\u843D\u5728\u5730\u4E0A\uFF0C\u63A5\u7740\u5C11\u5973\u51C4\u75DB\u7684\u54ED\u58F0\uFF0C\u54CD\u5F7B\u77F3\u7A9F\u3002</p><p>\u77F3\u58C1\u4E0A\u4EC0\u4E48\u90FD\u6CA1\u6709\u3002\u6CA1\u6709\u7EB9\u9970\uFF0C\u4E5F\u6CA1\u6709\u56FE\u5F62\u3002</p><p>\u6240\u6709\u7684\u727A\u7272\uFF0C\u90FD\u767D\u8D39\u4E86\u3002</p><p>\u9634\u957F\u91CE\u5BF9\u5979\u83AB\u540D\u5176\u5999\u7684\u75DB\u54ED\u6BEB\u4E0D\u5728\u610F\uFF0C\u8FD9\u54ED\u58F0\u53CD\u800C\u5524\u8D77\u4E86\u4ED6\u4E45\u8FDC\u7684\u8BB0\u5FC6\uFF0C\u6C89\u7761\u7684\u9633\u5177\u6E10\u6E10\u82CF\u9192\u8FC7\u6765\u3002</p><p>\u65E0\u8FB9\u7684\u5931\u671B\u548C\u523B\u9AA8\u7684\u4F24\u75DB\u5C06\u7D2B\u73AB\u7684\u575A\u5F3A\u51FB\u5F97\u7C89\u788E\u3002\u6D01\u767D\u4F18\u7F8E\u7684\u624B\u81C2\u8F6F\u7EF5\u7EF5\u5782\u5728\u8EAB\u540E\uFF0C\u518D\u6CA1\u6709\u4E00\u4E1D\u529B\u6C14\u3002\u819D\u5F2F\u88AB\u9634\u957F\u91CE\u67B6\u5728\u81C2\u95F4\uFF0C\u4E0B\u4F53\u7EA2\u8273\u8273\u7684\u82B1\u74E3\u7FD5\u5F20\u7740\uFF0C\u671D\u6E10\u6E10\u53D1\u786C\u7684\u8089\u68D2\u5957\u53BB\u3002</p><p>\u6B64\u65F6\u7D2B\u73AB\u53EA\u60F3\u4E00\u6B7B\u4E86\u4E4B\u3002\u62D6\u7740\u6000\u5B55\u7684\u8EAB\u4F53\u4EFB\u4EBA\u8E42\u8E8F\uFF0C\u4ED8\u51FA\u5C0A\u4E25\u3001\u8089\u4F53\u3001\u8D1E\u6D01\u2026\u2026\u7ED3\u679C\u843D\u5165\u4E00\u4E2A\u9A97\u5C40\uFF0C\u8FD9\u5BF9\u6EE1\u6000\u5E0C\u671B\u7684\u5C11\u5973\u800C\u8A00\uFF0C\u6B8B\u5FCD\u5F97\u4EE4\u5979\u80FD\u4EE5\u627F\u53D7\u3002</p><p>\u575A\u5B88\u7684\u4FE1\u5FF5\u4EFF\u4F5B\u5D29\u6E83\u7684\u4E03\u5B9D\u697C\u9601\uFF0C\u7247\u7247\u98D8\u821E\u7740\u7070\u98DE\u70DF\u706D\u3002\u60B8\u52A8\u7684\u80CE\u513F\u4EFF\u4F5B\u5728\u5E94\u5408\u6BCD\u4EB2\u7684\u75DB\u54ED\uFF0C\u5728\u8179\u5185\u4E00\u5760\u4E00\u5760\u671D\u5B50\u5BAB\u53E3\u6ED1\u53BB\u3002</p><p>\u800C\u5728\u5979\u8EAB\u4E0B\uFF0C\u590D\u6D3B\u7684\u9633\u5177\u7B14\u76F4\u7AD6\u8D77\uFF0C\u9F9F\u5934\u51E0\u4E4E\u89E6\u5230\u6E7F\u6DA6\u7684\u5AE9\u8089\u3002\u53EA\u5C5E\u4E00\u4EBA\u4E13\u4EAB\u7684\u8089\u7A74\uFF0C\u5373\u5C06\u8FCE\u6765\u7B2C\u4E8C\u652F\u9633\u5177\u3002</p><p>\u51B0\u51B7\u7684\u77F3\u7A9F\uFF0C\u75DB\u54ED\u7684\u5A07\u7F8E\u5B55\u5987\u548C\u80AE\u810F\u7684\u6B8B\u75BE\u602A\u5BA2\uFF0C\u8FD9\u4E00\u5207\uFF0C\u6784\u6210\u4E86\u4E00\u5E45\u51C4\u8273\u7684\u753B\u9762\uFF0C\u6C38\u8FDC\u7559\u5728\u67D0\u4E2A\u4EBA\u5FC3\u5E95\u3002</p><p>\u4E00\u80A1\u68EE\u5BD2\u7684\u6740\u610F\u5239\u90A3\u95F4\u5145\u6EE1\u77F3\u7A9F\uFF0C\u8FDE\u4E07\u53E4\u957F\u6EF4\u7684\u6C34\u73E0\u4E5F\u88AB\u51BB\u7ED3\u5728\u77F3\u7B0B\u5C16\u4E0A\u3002</p><p>\u9634\u957F\u91CE\u62AC\u5934\u4E00\u770B\uFF0C\u5931\u58F0\u53EB\u9053\uFF1A\u300C\u6155\u5BB9\u7941\uFF01\u300D</p><p>\u4E00\u9053\u9B3C\u706B\u822C\u7684\u5BD2\u5149\u5212\u7834\u9ED1\u6697\uFF0C\u4E16\u95F4\u7684\u4E00\u5207\u90FD\u9759\u6B62\u4E0B\u6765\uFF0C\u53EA\u5269\u4E0B\u8FD9\u4E00\u62B9\u51C4\u51B7\u5200\u5149\u3002</p>',66);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
