import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_117" tabindex="-1"><a class="header-anchor" href="#_117" aria-hidden="true">#</a> 117</h1><p>\u9634\u957F\u91CE\u4E0D\u6562\u6020\u6162\uFF0C\u80F8\u8179\u4E00\u632F\u5F39\u5F00\u7D2B\u73AB\uFF0C\u817E\u5F00\u624B\u81C2\uFF0C\u63A5\u7740\u5408\u638C\u671D\u5200\u950B\u5939\u53BB\u3002</p><p>\u8FD9\u67C4\u5200\u957F\u534A\u5C3A\uFF0C\u5BBD\u4EC5\u4E09\u6307\uFF0C\u4E00\u70B9\u51DB\u51BD\u7684\u9752\u5149\u5728\u5200\u950B\u4E0A\u65E0\u58F0\u65E0\u79D8\u7684\u5E7D\u5E7D\u95EA\u52A8\uFF0C\u4EFF\u4F5B\u4E00\u53EA\u5BC2\u5BDE\u7684\u6D41\u8424\u3002</p><p>\u7728\u773C\u95F4\uFF0C\u6D41\u8424\u4FBF\u5212\u8FC7\u4E09\u4E08\u7684\u8DDD\u79BB\uFF0C\u8DF3\u5230\u9634\u957F\u91CE\u4E24\u638C\u4E4B\u95F4\u3002\u7136\u540E\u5FAE\u5FAE\u4E00\u8DF3\uFF0C\u8F7B\u76C8\u5730\u8F6C\u4E86\u4E00\u4E2A\u5F27\u7EBF\u3002</p><p>\u9634\u957F\u91CE\u53F3\u638C\u4E00\u51C9\uFF0C\u56DB\u6307\u624B\u6307\u9F50\u6839\u800C\u65AD\u3002\u4ED6\u6CA1\u60F3\u5230\u77ED\u5200\u4F1A\u5982\u6B64\u950B\u9510\uFF0C\u767E\u5FD9\u4E2D\u731B\u7136\u540E\u7F29\uFF0C\u8EAB\u5B50\u51E0\u4E4E\u5D4C\u5165\u77F3\u58C1\u3002</p><p>\u300C\u683C\u683C\u300D\u51E0\u58F0\u8F7B\u54CD\uFF0C\u9634\u957F\u91CE\u53F3\u80F8\u56DB\u6839\u808B\u9AA8\u9F50\u9F50\u65A9\u65AD\u3002\u4F46\u8FD9\u4E00\u5200\u53BB\u52BF\u5DF2\u5C3D\uFF0C\u53EA\u5DEE\u5BF8\u8BB8\u4FBF\u53EF\u51FB\u788E\u5185\u810F\uFF0C\u5374\u4E0D\u5F97\u4E0D\u56DE\u6536\u3002\u9634\u957F\u91CE\u8EAB\u7ECF\u767E\u6218\uFF0C\u65E0\u6570\u6B21\u6B7B\u91CC\u9003\u751F\uFF0C\u5B9E\u662F\u52C7\u608D\u7EDD\u4F26\u4E4B\u8F88\u3002\u4ED6\u867D\u4F24\u4E0D\u4E71\uFF0C\u5DE6\u624B\u4E94\u6307\u5FFD\u5C48\u5FFD\u4F38\uFF0C\u6B63\u6253\u5728\u6765\u4EBA\u8155\u4E0A\u3002</p><p>\u77ED\u5200\u659C\u659C\u98DE\u8D77\uFF0C\u9489\u5165\u77F3\u58C1\u3002</p><p>\u6155\u5BB9\u9F99\u4FCA\u8138\u6BEB\u65E0\u8868\u60C5\uFF0C\u53F3\u638C\u4E00\u7FFB\u52FE\u4F4F\u9634\u957F\u91CE\u5DE6\u638C\uFF0C\u63A5\u7740\u5DE6\u624B\u6325\u51FA\uFF0C\u4E0E\u4ED6\u53F3\u638C\u62B5\u5728\u4E00\u8D77\u3002</p><p>\u9634\u957F\u91CE\u60CA\u9B42\u752B\u5B9A\uFF0C\u8BA4\u51FA\u6765\u4EBA\u5E76\u4E0D\u662F\u6155\u5BB9\u7941\u5929\u6740\u7684\u90A3\u4E2A\u5C0F\u767D\u8138\u3002\u4ED6\u88AB\u9501\u5728\u77F3\u58C1\u4E0A\uFF0C\u517C\u4E14\u4E24\u817F\u88AB\u780D\uFF0C\u884C\u52A8\u4E0D\u4FBF\uFF0C\u770B\u6765\u4EBA\u8FD9\u4E00\u5200\u7684\u58F0\u52BF\uFF0C\u6B66\u529F\u4E0D\u5728\u6559\u5185\u62A4\u6CD5\u4E4B\u4E0B\uFF0C\u82E5\u662F\u6E38\u6597\uFF0C\u81EA\u5DF1\u5FC5\u6B7B\u65E0\u7591\u3002</p><p>\u4E0D\u6210\u60F3\u8FD9\u5BB6\u4F19\u7A7A\u5F20\u4E86\u4E00\u5F20\u4FCA\u8138\uFF0C\u5374\u662F\u4E2A\u7B28\u86CB\uFF0C\u7ADF\u7136\u4EE5\u77ED\u5BF9\u957F\uFF0C\u8DDF\u81EA\u5DF1\u6BD4\u62FC\u5185\u529B\u3002\u8FD9\u5C0F\u5154\u5D3D\u5B50\u5C31\u7B97\u4ECE\u5A18\u80CE\u5F00\u59CB\u7EC3\u6B66\uFF0C\u53C8\u600E\u4E48\u80FD\u8DDF\u81EA\u5DF1\u51E0\u5341\u5E74\u7684\u529F\u529B\u76F8\u6BD4\uFF1F</p><p>\u9634\u957F\u91CE\u7709\u5934\u4E00\u626C\uFF0C\u52B2\u529B\u72C2\u6D8C\u800C\u51FA\uFF0C\u52A1\u5FC5\u8981\u628A\u8FD9\u5C0F\u5B50\u6BD9\u4E8E\u638C\u5E95\uFF0C\u593A\u8FC7\u5B9D\u5200\u65A9\u65AD\u9501\u94FE\u2014\u2014\u4ED6\u5A18\u7684\uFF0C\u8001\u5B50\u4E00\u8131\u8EAB\uFF0C\u7B2C\u4E00\u4EF6\u4E8B\u5C31\u662F\u90A3\u4E2A\u81ED\u5A4A\u5B50\u64CD\u5F97\u7A00\u70C2\uFF0C\u518D\u628A\u5BAB\u91CC\u7684\u5973\u4EBA\u7EDF\u7EDF\u5978\u6B7B\uFF0C\u4E00\u5410\u88AB\u56DA\u7684\u6028\u6C14\uFF01</p><p>\u4E24\u80A1\u771F\u6C14\u4E00\u89E6\uFF0C\u9634\u957F\u91CE\u8138\u8272\u987F\u65F6\u51DD\u91CD\u8D77\u6765\u3002\u8FD9\u4E2A\u5C0F\u5D3D\u5B50\u529F\u529B\u4E4B\u5F3A\u76F4\u8FFD\u81EA\u5DF1\u5F53\u5E74\uFF0C\u53EA\u662F\u662F\u771F\u6C14\u9A73\u6742\u4E0D\u7EAF\uFF0C\u4E0D\u80FD\u597D\u597D\u5229\u7528\u3002\u771F\u6C14\u4EA4\u950B\u7247\u523B\u4E4B\u540E\uFF0C\u4ED6\u8D6B\u7136\u53D1\u73B0\uFF0C\u9762\u524D\u8FD9\u4E2A\u5E74\u8F7B\u4EBA\u7528\u7684\u7ADF\u7136\u8EAB\u517C\u6559\u4E2D\u4E24\u95E8\u7EDD\u5B66\uFF1A\u592A\u4E00\u7ECF\u548C\u8FD8\u5929\u8BC0\uFF01</p><p>\u6155\u5BB9\u9F99\u5FC3\u4E0B\u6068\u6781\uFF0C\u4E0D\u613F\u4E0E\u4ED6\u4E45\u6597\uFF0C\u56E0\u6B64\u4E00\u4E0A\u6765\u4FBF\u786C\u62FC\u5185\u529B\uFF0C\u60F3\u4E00\u4E3E\u628A\u8FD9\u67AF\u5E72\u7684\u6BAD\u5C38\u7206\u6210\u8840\u96FE\u3002\u53EF\u8FD9\u65F6\u624D\u53D1\u73B0\u8FD9\u5BB6\u4F19\u529F\u529B\u6DF1\u539A\u5B9E\u4E3A\u751F\u5E73\u7F55\u89C1\uFF0C\u4E0E\u5F53\u521D\u7684\u96EA\u5CF0\u795E\u5C3C\u76F8\u6BD4\uFF0C\u53EA\u5DEE\u4E86\u534A\u7B79\u3002\u4F46\u4ED6\u8EAB\u8D1F\u91CD\u4F24\uFF0C\u8017\u4E5F\u8017\u6B7B\u8FD9\u4E2A\u738B\u516B\u86CB\u3002</p><p>\u4ED6\u7528\u4F59\u5149\u770B\u4E86\u770B\u660F\u8FF7\u7684\u7D2B\u73AB\uFF0C\u5FC3\u4E0B\u6C14\u6068\u4EA4\u52A0\uFF0C\u624B\u4E0A\u7684\u52B2\u9053\u6108\u53D1\u51F6\u731B\u3002\u5F53\u4E0B\u4E24\u4EBA\u56DB\u624B\u76F8\u62B5\uFF0C\u582A\u582A\u654C\u5BF9\uFF0C\u77F3\u7A9F\u987F\u65F6\u4E00\u7247\u6B7B\u5BC2\u3002</p><p>\u826F\u4E45\uFF0C\u7D2B\u73AB\u60A0\u60A0\u9192\u8F6C\u3002\u4E00\u7741\u773C\uFF0C\u5148\u770B\u5230\u5934\u4E0A\u7684\u77ED\u5200\uFF0C\u5979\u60F3\u4E5F\u4E0D\u60F3\uFF0C\u7ACB\u523B\u62D4\u51FA\u7247\u7389\uFF0C\u4E00\u5200\u65A9\u4E0B\u9634\u957F\u91CE\u7684\u5DE6\u81C2\u3002\u8FD9\u4E2A\u5996\u9B54\uFF0C\u5BB3\u5F97\u81EA\u5DF1\u4E27\u5C3D\u5C0A\u4E25\u3002</p><p>\u8840\u5149\u4E4D\u73B0\uFF0C\u9634\u957F\u91CE\u5DE6\u81C2\u9F50\u6839\u800C\u65AD\u3002\u4ED6\u81EA\u77E5\u5FC5\u6B7B\uFF0C\u4E00\u58F0\u66B4\u559D\uFF0C\u6B8B\u7F3A\u7684\u53F3\u638C\u594B\u529B\u63A8\u5F00\u6155\u5BB9\u9F99\uFF0C\u63A5\u7740\u671D\u7D2B\u73AB\u80F8\u53E3\u62B9\u53BB\u3002\u4E0D\u987E\u4E00\u5207\u5730\u8981\u5148\u4E00\u638C\u62CD\u6B7B\u8FD9\u4E2A\u8D31\u5A4A\u5B50\u3002</p><p>\u7D2B\u73AB\u63D0\u7740\u6EF4\u8840\u7684\u5229\u5203\uFF0C\u773C\u795E\u4E0D\u4F4F\u53D8\u5E7B\u3002\u4E00\u5200\u65A9\u4E0B\u4E4B\u540E\uFF0C\u5979\u4FBF\u540E\u6094\u4E86\u3002\u521A\u624D\u673A\u4F1A\u5343\u8F7D\u96BE\u9022\uFF0C\u5B9E\u5728\u5E94\u8BE5\u5148\u6740\u6389\u6155\u5BB9\u9F99\uFF0C\u518D\u5BF9\u4ED8\u9634\u957F\u91CE\u3002\u72B9\u8C6B\u95F4\uFF0C\u9634\u957F\u91CE\u5931\u53BB\u624B\u6307\u7684\u65AD\u638C\u91CD\u91CD\u62CD\u5728\u5979\u7684\u53CC\u4E73\u6B63\u4E2D\u3002</p><p>\u300C\u8D31\u5A4A\u5B50\uFF01\u4E00\u8D77\u53BB\u6B7B\u5427\uFF01\u300D\u9634\u957F\u91CE\u4E24\u773C\u653E\u5149\uFF0C\u72C2\u559D\u9053\u3002</p><p>\u7D2B\u73AB\u4E00\u58F0\u4E0D\u54CD\u5F80\u540E\u5012\u53BB\uFF0C\u50CF\u4E00\u7247\u82CD\u767D\u7684\u82B1\u74E3\uFF0C\u6084\u7136\u98D8\u843D\u5728\u5730\u3002</p><p>\u6155\u5BB9\u9F99\u76EE\u7726\u6B32\u88C2\uFF0C\u53CC\u638C\u9F50\u51FA\uFF0C\u91CD\u91CD\u62CD\u5728\u9634\u957F\u91CE\u80F8\u53E3\u3002\u5C06\u4ED6\u7684\u80F8\u9AA8\u51FB\u5F97\u7C89\u788E\u3002</p><p>\u9634\u957F\u91CE\u300C\u54D7\u300D\u7684\u5410\u51FA\u4E00\u53E3\u9C9C\u8840\uFF0C\u628A\u76EE\u5149\u4ECE\u7D2B\u73AB\u8EAB\u4E0A\u6536\u56DE\u3002\u6C89\u58F0\u9053\uFF1A\u300C\u4F60\u662F\u4F55\u4EBA\uFF1F\u300D</p><p>\u300C\u6155\u5BB9\u7941\u4E4B\u5B50\uFF0C\u661F\u6708\u6E56\u5BAB\u4E3B\u6155\u5BB9\u9F99\u3002\u300D</p><p>\u9634\u957F\u91CE\u773C\u795E\u4E00\u4EAE\uFF0C\u65CB\u5373\u6E10\u6E10\u9EEF\u6DE1\uFF0C\u300C\u9634\u59EC\u662F\u6B7B\u5728\u4F60\u624B\u91CC\u5417\uFF1F\u300D</p><p>\u300C\u4E0D\u9519\u3002\u5979\u751F\u524D\u53D7\u5C3D\u82E6\u695A\uFF0C\u6B7B\u540E\u88AB\u672C\u5BAB\u505A\u6210\u73A9\u7269\u2014\u2014\u517B\u773C\u5F97\u5F88\u5462\u3002\u9634\u53F3\u4F7F\u60F3\u89C1\u89C1\u5417\uFF1F\u300D</p><p>\u9634\u957F\u91CE\u653E\u58F0\u72C2\u7B11\uFF0C\u77F3\u7A9F\u4E2D\u7684\u6C34\u6EF4\u88AB\u7B11\u58F0\u6FC0\u8361\u5F97\u56DB\u4E0B\u98DE\u6E85\uFF0C\u7A81\u7136\u95F4\u7B11\u58F0\u6B62\u6B47\uFF0C\u518D\u65E0\u58F0\u606F\u3002</p><p>\u6155\u5BB9\u9F99\u8FDF\u7F13\u5730\u8F6C\u8FC7\u8EAB\u5B50\u3002</p><p>\u521A\u624D\u542C\u5230\u767D\u7389\u9E42\u7684\u5BC6\u544A\uFF0C\u8BF4\u5C11\u592B\u4EBA\u51E0\u6B21\u8DD1\u5230\u77F3\u7A9F\u8DDF\u4E00\u4E2A\u602A\u4EBA\u82DF\u5408\uFF0C\u6155\u5BB9\u9F99\u5DEE\u70B9\u8D70\u706B\u5165\u9B54\u3002\u4ED6\u6CA1\u60F3\u5728\u81EA\u5DF1\u5BAB\u5185\uFF0C\u5C45\u7136\u8FD8\u85CF\u7740\u4E00\u4E2A\u65E9\u5C31\u8BE5\u6B7B\u7684\u5BB6\u4F19\uFF0C\u800C\u81EA\u5DF1\u7684\u59BB\u5B50\u7ADF\u7136\u4F1A\u51E0\u6B21\u4E09\u756A\u5730\u59D4\u8EAB\u4E8E\u4ED6\u3002\u66B4\u6012\u4E4B\u4E0B\uFF0C\u6155\u5BB9\u9F99\u7ACB\u5373\u8D76\u5230\u77F3\u7A9F\u3002</p><p>\u5F53\u65F6\u4ED6\u60F3\u6CD5\u53EA\u6709\u4E00\u4E2A\uFF1A\u6740\u4E86\u5978\u592B\uFF0C\u5E9F\u4E86\u6DEB\u5987\uFF01</p><p>\u81EA\u5DF1\u7684\u5973\u4EBA\u7ADF\u7136\u6562\u7EA2\u674F\u51FA\u5899\uFF0C\u5373\u4F7F\u780D\u65AD\u5979\u7684\u624B\u811A\uFF0C\u505A\u6210\u4EBA\u5F58\u4E5F\u4E0D\u4E3A\u8FC7\uFF01\u65E0\u8BBA\u5982\u4F55\u4E5F\u4E0D\u80FD\u518D\u7EB5\u5BB9\u8FD9\u4E2A\u8D31\u4EBA\uFF01</p><p>\u7136\u800C\u6B64\u65F6\uFF0C\u90A3\u4E9B\u8840\u8165\u7684\u51B2\u52A8\u90FD\u6D88\u6563\u4E86\u3002\u4ED6\u592A\u6E05\u695A\u90A3\u4E00\u638C\u7684\u5A01\u529B\u4E86\uFF0C\u5373\u4F7F\u5979\u90A3\u70B9\u5FAE\u8584\u7684\u529F\u529B\u4ECD\u5728\uFF0C\u4E5F\u53EA\u80FD\u662F\u5FC3\u8109\u5C3D\u788E\u82B3\u9B42\u6773\u7136\u7684\u7ED3\u5C40\u3002</p><p>\u59B9\u59B9\u5B9B\u5982\u719F\u7761\u7684\u4ED9\u5B50\uFF0C\u9759\u6084\u6084\u8EBA\u5728\u5730\u4E0A\u3002</p><p>\u4ED6\u4F38\u624B\u628A\u59B9\u59B9\u63FD\u5728\u6000\u4E2D\uFF0C\u5F53\u624B\u638C\u629A\u6478\u7740\u5979\u6D51\u5706\u7684\u5C0F\u8179\u65F6\uFF0C\u6155\u5BB9\u9F99\u6B62\u4E0D\u4F4F\u98A4\u6296\u8D77\u6765\u3002\u552F\u4E00\u7684\u4EB2\u59B9\u59B9\u6000\u7740\u81EA\u5DF1\u7684\u5B69\u5B50\u9999\u9500\u7389\u6B92\uFF0C\u5F53\u65E5\u5728\u7956\u9675\u53D1\u4E0B\u7684\u8A93\u8A00\u5F7B\u5E95\u5316\u4E3A\u6CE1\u5F71\u2026\u2026</p><p>\u59D1\u4E14\u4E0D\u8BBA\u5979\u7684\u8840\u7EDF\uFF0C\u5355\u662F\u5979\u7684\u5BB9\u8C8C\u4FBF\u662F\u8FD9\u4E16\u4E0A\u96BE\u5F97\u7684\u5947\u73CD\u3002\u867D\u7136\u5979\u5C61\u5C61\u60F9\u81EA\u5DF1\u53D1\u6012\uFF0C\u4F46\u5979\u7684\u6BCF\u4E00\u6B21\u5FAE\u7B11\u90FD\u5370\u5728\u81EA\u5DF1\u5FC3\u5E95\u3002\u65E0\u8BBA\u662F\u72C2\u70ED\u7684\u4EA4\u5408\uFF0C\u8FD8\u662F\u62E5\u7740\u5979\u9999\u8F6F\u7684\u8EAB\u4F53\u9759\u89C2\u957F\u6CB3\u843D\u65E5\uFF0C\u5979\u5E26\u7ED9\u81EA\u5DF1\u7684\u559C\u60A6\u548C\u6EE1\u8DB3\u90FD\u662F\u4EFB\u4F55\u4EBA\u4E5F\u65E0\u6CD5\u4EE3\u66FF\u7684\u3002</p><p>\u8FD9\u4E00\u523B\u4ED6\u6D51\u5FD8\u4E86\u96C4\u5FC3\u9738\u4E1A\uFF0C\u6D51\u5FD8\u4E86\u8A93\u8A00\uFF0C\u53EA\u5E0C\u671B\u5979\u80FD\u6253\u4E2A\u5475\u6B20\uFF0C\u7F13\u7F13\u7741\u5F00\u773C\u775B\u2014\u2014\u5373\u4F7F\u6CA1\u6709\u5B69\u5B50\u4E5F\u65E0\u6240\u8C13\u4E86\u3002</p><p>\u7D2B\u73AB\u6253\u4E86\u4E2A\u5475\u6B20\uFF0C\u7F13\u7F13\u7741\u5F00\u773C\u775B\uFF0C\u7136\u540E\u7709\u5934\u4E00\u76B1\uFF0C\u5012\u62BD\u4E86\u4E00\u53E3\u51C9\u6C14\uFF0C\u8138\u4E0A\u9732\u51FA\u5403\u75DB\u7684\u5A07\u5A9A\u8868\u60C5\u3002</p><p>\u6155\u5BB9\u9F99\u987F\u65F6\u6123\u4F4F\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u8BF4\uFF01\u7A76\u7ADF\u662F\u600E\u4E48\u56DE\u4E8B\uFF1F\u300D\u6155\u5BB9\u9F99\u5BD2\u58F0\u9053\u3002</p><p>\u540E\u6094\u548C\u6124\u6068\u5B9B\u5982\u6BD2\u86C7\u7684\u5C16\u7259\uFF0C\u5C06\u67D4\u8F6F\u7684\u82B3\u5FC3\u54AC\u6210\u4E00\u7247\u7247\u3002\u771F\u662F\u592A\u50BB\u4E86\u554A\uFF0C\u53EA\u4E3A\u4E86\u4E00\u4E2A\u865A\u5047\u7684\u68A6\u5E7B\uFF0C\u5C31\u8F7B\u6613\u62FF\u81EA\u5DF1\u8EAB\u5B50\u505A\u4EA4\u6613\u2026\u2026\u7D2B\u73AB\u6014\u6014\u843D\u4E0B\u6CEA\u6765\u3002</p><p>\u300C\u556A\u300D\uFF0C\u6155\u5BB9\u9F99\u91CD\u91CD\u7ED9\u4E86\u5979\u4E00\u4E2A\u8033\u5149\uFF0C\u300C\u8D31\u4EBA\uFF01\u4F60\u80CC\u7740\u6211\u505A\u4E86\u4EC0\u4E48\u4E0D\u8981\u8138\u7684\u4E8B\uFF01\u300D</p><p>\u7D2B\u73AB\u300C\u54C7\u300D\u7684\u75DB\u54ED\u8D77\u6765\uFF0C\u8FD9\u4E00\u8033\u5149\u8BA9\u5979\u6E05\u9192\u8FC7\u6765\uFF0C\u60F3\u8D77\u81EA\u5DF1\u7684\u5904\u5883\u3002\u5F85\u54ED\u58F0\u6E10\u6B62\uFF0C\u4E00\u7BC7\u8C0E\u8BDD\u4E5F\u5DF2\u5B8C\u7A3F\u3002</p><p>\u5979\u62BD\u54BD\u7740\u8BF4\uFF1A\u300C\u4F60\u6574\u5929\u51B7\u7740\u8138\uFF0C\u4ECE\u6765\u90FD\u4E0D\u7406\u6211\u3002\u4EBA\u5BB6\u6574\u5929\u6CA1\u6709\u4E8B\u505A\uFF0C\u53EA\u597D\u5E2E\u4F60\u6574\u7406\u5BB6\u52A1\u2026\u2026\u300D</p><p>\u54ED\u58F0\u4E00\u54CD\uFF0C\u7D2B\u73AB\u809D\u80A0\u5BF8\u65AD\u5730\u6CE3\u9053\uFF1A\u300C\u8C01\u77E5\u9053\u8FD9\u91CC\u9762\u6709\u4E00\u4E2A\u602A\u7269\uFF0C\u6211\u6B66\u529F\u90FD\u88AB\u4F60\u5236\u4F4F\u4E86\uFF0C\u6253\u4E0D\u8FC7\u53C8\u9003\u4E0D\u6389\u2026\u2026\u4ED6\u2026\u2026\u4ED6\u8FD8\u5728\u4EBA\u5BB6\u8EAB\u4E0A\u62CD\u4E86\u4E00\u638C\uFF0C\u8BF4\u4E0D\u542C\u8BDD\u5C31\u4F1A\u70C2\u6389\u2026\u2026\u300D\u7D2B\u73AB\u59D4\u5C48\u5730\u6258\u8D77\u53F3\u4E73\uFF0C\u8BA9\u4ED6\u770B\u6E05\u9ED1\u715E\u638C\u7684\u5370\u8BB0\u3002</p><p>\u6709\u53F6\u884C\u5357\u5728\uFF0C\u6155\u5BB9\u9F99\u4E0D\u5FC5\u4E3A\u6B64\u5FE7\u5FC3\u3002\u81EA\u5DF1\u7684\u59BB\u5B50\u7ADF\u88AB\u8FD9\u79CD\u5C0F\u4F0E\u4FE9\u80C1\u8FEB\uFF0C\u4ED6\u8D8A\u60F3\u8D8A\u6C14\uFF0C\u6012\u543C\u9053\uFF1A\u300C\u4E3A\u8FD9\u4F60\u5C31\u8DB4\u5728\u8FD9\u7CAA\u5751\u91CC\uFF0C\u8BA9\u8FD9\u6761\u72D7\u90FD\u4E0D\u5982\u7684\u4E1C\u897F\u64CD\u5417\uFF01\u300D</p><p>\u7D2B\u73AB\u8138\u4E0A\u4E00\u7EA2\uFF0C\u4F4E\u58F0\u8BF4\uFF1A\u300C\u4ED6\u4E0D\u884C\u7684\u2026\u2026\u6CA1\u6709\u2026\u2026\u300D</p><p>\u300C\u6CA1\u6709\uFF1F\u8FD9\u5462\uFF1F\u300D\u6155\u5BB9\u9F99\u6307\u7740\u5979\u4E73\u4E0A\u8179\u4E0A\u7684\u6CE5\u57A2\u3002</p><p>\u7D2B\u73AB\u5FC3\u91CC\u4E00\u9178\uFF0C\u4E24\u624B\u6342\u4F4F\u7389\u8138\uFF0C\u54ED\u5F97\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u7C97\u66B4\u5730\u63B0\u5F00\u5979\u7684\u53CC\u817F\uFF0C\u4E0B\u8179\u9ECF\u4E4E\u4E4E\u6E7F\u6DCB\u6DCB\u8FD8\u5E26\u7740\u6CB9\u5149\uFF0C\u660E\u663E\u662F\u88AB\u4EBA\u4FB5\u72AF\u8FC7\u3002\u4E73\u4E0A\u548C\u81C0\u4E0A\u7684\u6293\u75D5\u5B9B\u7136\u2014\u2014\u81EA\u5DF1\u8FD8\u6CA1\u820D\u5F97\u5BF9\u5979\u8FD9\u4E48\u72E0\uFF0C\u5979\u5C45\u7136\u5C31\u628A\u9999\u55B7\u55B7\u5A07\u6EF4\u6EF4\u7684\u8EAB\u4F53\u9001\u7ED9\u4E00\u4E2A\u4E0B\u4E09\u6EE5\u7684\u4E1C\u897F\u2026\u2026\u73A9\u6B7B\u6D3B\u8BE5\uFF01</p><p>\u6155\u5BB9\u9F99\u8D8A\u60F3\u8D8A\u6C14\uFF0C\u867D\u7136\u4E0D\u4F1A\u518D\u7528\u780D\u65AD\u56DB\u80A2\u8FD9\u79CD\u66B4\u529B\u7684\u624B\u6BB5\uFF0C\u4F46\u5FC5\u8981\u7684\u60E9\u7F5A\u8FD8\u662F\u5FC5\u4E0D\u53EF\u5C11\u7684\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u770B\u7740\u513F\u5973\u7A81\u7136\u5165\u5BA4\uFF0C\u8427\u4F5B\u5974\u5FAE\u5FAE\u4E00\u6014\u3002\u5F85\u770B\u6E05\u4E24\u4EBA\u4E00\u4E2A\u6012\u6C14\u51B2\u51B2\uFF0C\u4E00\u4E2A\u6EE1\u8138\u662F\u6CEA\uFF0C\u7F8E\u5987\u4E0D\u7981\u5FC3\u4E0B\u53EB\u82E6\u3002</p><p>\u300C\u5A18\uFF0C\u4F60\u5973\u513F\u80CC\u5728\u6211\u5728\u5916\u9762\u52FE\u5F15\u7537\u4EBA\uFF0C\u4F60\u8BF4\u8BE5\u600E\u4E48\u5904\u7F6E\uFF1F\u300D\u6155\u5BB9\u9F99\u68EE\u7136\u9053\u3002</p><p>\u8427\u4F5B\u5974\u5927\u5403\u4E00\u60CA\uFF0C\u96BE\u4EE5\u7F6E\u4FE1\u5730\u770B\u7740\u5973\u513F\uFF0C\u771F\u8981\u505A\u51FA\u8FD9\u79CD\u4E8B\uFF0C\u4F9D\u513F\u5B50\u6B8B\u66B4\u7684\u624B\u6BB5\uFF0C\u5C31\u662F\u4E0D\u6740\u5979\u4E5F\u4F1A\u628A\u5979\u56DB\u80A2\u780D\u65AD\u2014\u2014\u6700\u5C11\u4E5F\u662F\u50CF\u81EA\u5DF1\u4E00\u6837\u88AB\u62BD\u53BB\u7B4B\u8171\u3002</p><p>\u300C\u73AB\u513F\uFF01\u300D\u7F8E\u5987\u7126\u6025\u4E07\u72B6\u5730\u53EB\u9053\u3002</p><p>\u300C\u6211\u6CA1\u6709\u2026\u2026\u6211\u662F\u88AB\u8FEB\u7684\u2026\u2026\u300D\u7D2B\u73AB\u59D4\u5C48\u5730\u8BF4\u3002</p><p>\u300C\u5974\u5A62\u542C\u5230\u5C11\u592B\u4EBA\u8BF4\uFF1A\u524D\u8F88\u82E5\u4E0D\u5ACC\u5F03\uFF0C\u5974\u5A62\u5C31\u7528\u8FD9\u5BF9\u4E0D\u8981\u8138\u7684\u8D31\u5976\u4F3A\u5019\u60A8\u2026\u2026\u300D\u767D\u7389\u83BA\u5B66\u7740\u7D2B\u73AB\u7684\u53E3\u6C14\u5728\u65C1\u6002\u607F\u3002\u59D0\u59B9\u4FE9\u6709\u5FC3\u7B97\u8BA1\u7D2B\u73AB\uFF0C\u6545\u610F\u7B49\u5979\u53D7\u5C3D\u51CC\u8FB1\u624D\u53BB\u901A\u77E5\u5BAB\u4E3B\u3002\u53EF\u60DC\u767D\u7389\u83BA\u6CA1\u6709\u76EE\u7779\u5F53\u65F6\u7684\u573A\u666F\uFF0C\u4E0D\u7136\u5979\u4E00\u5B9A\u4F1A\u62C9\u4F4F\u59B9\u59B9\uFF0C\u7B49\u9634\u957F\u91CE\u7684\u9633\u5177\u8FDB\u5165\u4E4B\u540E\u518D\u8BF4\u3002</p><p>\u4F46\u8FD9\u53E5\u8BDD\u5DF2\u7ECF\u8DB3\u591F\u3002\u6155\u5BB9\u9F99\u5982\u706B\u4E0A\u6D47\u6CB9\uFF0C\u4E00\u628A\u5C06\u7D2B\u73AB\u63A8\u5012\u5728\u5730\uFF0C\u54AC\u7259\u5207\u9F7F\u5730\u9A82\u9053\uFF1A\u300C\u8D31\u4EBA\uFF01\u300D</p><p>\u7D2B\u73AB\u53C8\u7F9E\u53C8\u6C14\uFF0C\u8138\u4E0A\u706B\u8FA3\u8FA3\u4E00\u7247\u3002</p><p>\u8427\u4F5B\u5974\u67D4\u58F0\u9053\uFF1A\u300C\u9F99\u54E5\u54E5\uFF0C\u73AB\u59D0\u59D0\u4E0D\u61C2\u4E8B\uFF0C\u5192\u72AF\u4E86\u60A8\uFF0C\u4F46\u5979\u2026\u2026\u300D\u7F8E\u5987\u58F0\u97F3\u98A4\u6296\u8D77\u6765\uFF0C\u300C\u5979\u8FD8\u6000\u7740\u5B69\u5B50\uFF0C\u53C8\u53D7\u4E86\u4F24\uFF0C\u60A8\u9976\u5979\u4E00\u6B21\u597D\u5417\uFF1F\u300D</p><p>\u300C\u8FD9\u79CD\u4E8B\u54EA\u6709\u9976\u8FC7\u7684\uFF1F\u6559\u5973\u65E0\u65B9\uFF0C\u4F60\u4E5F\u62C5\u7740\u5E72\u7CFB\uFF0C\u7ADF\u7136\u8FD8\u66FF\u5979\u6C42\u60C5\u3002\u300D</p><p>\u8427\u4F5B\u5974\u8EAB\u5B50\u4E00\u98A4\uFF0C\u4E0D\u6562\u518D\u8A00\u8BED\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u5F97\u610F\u6D0B\u6D0B\u5730\u6346\u4F4F\u7D2B\u73AB\u7684\u53CC\u624B\uFF0C\u5C06\u5979\u60AC\u5728\u534A\u7A7A\u4E2D\u3002\u6155\u5BB9\u9F99\u5BD2\u7740\u8138\uFF0C\u6478\u51FA\u8361\u661F\u97AD\uFF0C\u624B\u8155\u4E00\u6296\uFF0C\u91CD\u91CD\u6253\u5728\u7D2B\u73AB\u80CC\u4E0A\uFF0C\u300C\u8FD9\u662F\u5A18\u7684\u7B4B\u8171\uFF0C\u54E5\u54E5\u662F\u66FF\u5A18\u6765\u6559\u8BAD\u4F60\uFF01\u300D</p><p>\u6015\u4F24\u7740\u80CE\u6C14\uFF0C\u97AD\u6253\u53EA\u5728\u7C89\u80CC\u3002\u4ECE\u540E\u770B\u6765\uFF0C\u7D2B\u73AB\u5A07\u7F8E\u7684\u8EAB\u5F62\u4EFF\u4F5B\u4E00\u6761\u6C34\u6DCB\u6DCB\u7684\u7F8E\u4EBA\u9C7C\uFF0C\u5728\u7A7A\u4E2D\u8F7B\u6643\u3002</p><p>\u957F\u97AD\u4E00\u95EA\u800C\u8FC7\uFF0C\u4E00\u9053\u9C9C\u7EA2\u7684\u97AD\u75D5\u4EFF\u4F5B\u4ECE\u6C34\u5E95\u6D6E\u51FA\u4E00\u822C\uFF0C\u5E26\u7740\u6E7F\u6E7F\u7684\u75D5\u8FF9\uFF0C\u5370\u5728\u6676\u83B9\u7684\u808C\u80A4\u4E0A\u3002</p><p>\u8D64\u88F8\u7684\u5973\u4F53\u89E6\u7535\u822C\u75C9\u631B\u8D77\u6765\u3002\u7B4B\u8171\u672C\u5C31\u7EA4\u7EC6\uFF0C\u836F\u7269\u6CE1\u5236\u4E4B\u540E\u66F4\u662F\u67D4\u97E7\u5F02\u5E38\uFF0C\u7D2B\u73AB\u53EA\u89C9\u8EAB\u5B50\u50CF\u88AB\u5229\u5203\u5207\u5F00\u4E00\u822C\uFF0C\u4ECE\u53F3\u80A9\u5230\u5DE6\u81C0\u7559\u4E0B\u4E00\u6761\u7B14\u76F4\u7684\u706B\u7EBF\u3002\u5979\u4E24\u624B\u4EA4\u53C9\u63E1\u7D27\uFF0C\u5149\u6E9C\u6E9C\u7684\u7389\u817F\u62E7\u5728\u4E00\u8D77\uFF0C\u62FC\u547D\u514B\u5236\u3002</p>',65);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
