import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_118" tabindex="-1"><a class="header-anchor" href="#_118" aria-hidden="true">#</a> 118</h1><p>\u300C\u6C49\u4EBA\u53E4\u8BAD\uFF0C\u5973\u5B50\u8D1E\u8282\u7B2C\u4E00\u3002\u88AB\u4EBA\u770B\u5230\u4E0D\u8BE5\u770B\u7684\u90E8\u4F4D\u5C31\u5E94\u8BE5\u4E00\u6B7B\u6B89\u8282\u3002\u4F60\u7ADF\u7136\u8D64\u8EAB\u88F8\u4F53\u8BA9\u4EBA\u73A9\u5F97\u53C8\u810F\u53C8\u81ED\uFF01\u300D\u6155\u5BB9\u9F99\u66B4\u559D\u9053\uFF1A\u300C\u65E0\u803B\u7684\u6DEB\u5987\uFF01\u6211\u6155\u5BB9\u6C0F\u600E\u4E48\u4F1A\u6709\u4F60\u8FD9\u6837\u4E0B\u8D31\u7684\u5973\u4EBA\uFF01\u300D\u4ED6\u4E00\u5B57\u5B57\u54AC\u7259\u8BF4\u7740\uFF0C\u6BCF\u8BF4\u4E00\u5B57\u90FD\u5E26\u7740\u4E00\u58F0\u97AD\u54CD\uFF0C\u4E0D\u591A\u65F6\u7D2B\u73AB\u7C89\u80CC\u5DF2\u662F\u97AD\u75D5\u7D2F\u7D2F\u3002</p><p>\u97AD\u5B50\u843D\u4E0B\u867D\u7136\u6CA1\u6709\u76AE\u5F00\u8089\u7EFD\uFF0C\u4F46\u6BCF\u4E00\u51FB\u90FD\u75DB\u5F7B\u5FC3\u80BA\u3002\u7D2B\u73AB\u811A\u5C16\u7EF7\u7D27\u53C8\u65E0\u529B\u5730\u677E\u5F00\u3002\u60AC\u5728\u7A7A\u4E2D\u7684\u8EAB\u4F53\u6839\u672C\u65E0\u4ECE\u8EB2\u907F\uFF0C\u5979\u751A\u81F3\u4E0D\u77E5\u9053\u4E0B\u4E00\u97AD\u4F1A\u6253\u5728\u54EA\u91CC\u3002\u5F00\u59CB\u5979\u8FD8\u52C9\u529B\u652F\u6491\uFF0C\u5341\u51E0\u97AD\u4E4B\u540E\uFF0C\u5C11\u5973\u7EC8\u4E8E\u5FCD\u4E0D\u4F4F\u75DB\u53EB\u5931\u58F0\u3002</p><p>\u957F\u97AD\u8361\u6210\u4E00\u7247\u96EA\u767D\u7684\u5F71\u5B50\uFF0C\u4E00\u97AD\u4E00\u97AD\u6BEB\u4E0D\u505C\u987F\u5730\u6253\u5728\u7EC6\u5AE9\u7684\u808C\u80A4\u4E0A\uFF0C\u53D1\u51FA\u6E05\u8106\u7684\u8089\u54CD\u3002</p><p>\u300C\u54CE\u5440\u2026\u2026\u554A\u2026\u2026\u54CE\u54DF\u2026\u2026\u300D\u7D2B\u73AB\u5A07\u8EAF\u6447\u6643\u7740\uFF0C\u5706\u6EDA\u6EDA\u7684\u5C0F\u8179\u50CF\u88AB\u793A\u4F17\u4E00\u822C\u7A81\u5140\u3002\u80CC\u4E0A\u7684\u97AD\u75D5\u6E10\u6E10\u8FDE\u6210\u4E00\u4F53\uFF0C\u53D8\u6210\u4E00\u7247\u523A\u76EE\u7684\u6BB7\u7EA2\u3002</p><p>\u770B\u7740\u81EA\u5DF1\u7684\u7B4B\u8171\u91CD\u91CD\u6253\u5728\u5973\u513F\u8EAB\u4E0A\uFF0C\u5BF9\u8427\u4F5B\u5974\u800C\u8A00\uFF0C\u4E0D\u557B\u4E8E\u662F\u81EA\u5DF1\u4EB2\u624B\u97AD\u6253\u5973\u513F\u3002\u5979\u5408\u4E0A\u7F8E\u76EE\uFF0C\u5FC3\u810F\u968F\u7740\u5973\u513F\u7684\u75DB\u53EB\u9635\u9635\u62BD\u75BC\u3002</p><p>\u300C\u522B\u6253\u4E86\u2026\u2026\u300D\u8427\u4F5B\u5974\u4E0D\u7531\u81EA\u4E3B\u5730\u53EB\u4E86\u51FA\u6765\uFF0C\u5979\u754F\u7F29\u4E86\u4E00\u4E0B\uFF0C\u7ACB\u5373\u53C8\u9F13\u52A8\u8D77\u52C7\u6C14\uFF0C\u300C\u5A18\u66FF\u5979\u597D\u5417\uFF1F\u300D</p><p>\u300C\u5A18\uFF01\u300D\u7D2B\u73AB\u773C\u6CEA\u4E00\u4E0B\u5B50\u6D8C\u4E86\u51FA\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u51B7\u51B7\u770B\u7740\u6BCD\u5973\u4FE9\uFF0C\u300C\u653E\u5FC3\uFF0C\u4F60\u4E5F\u6709\u4EFD\u513F\u5462\u3002\u300D</p><p>\u7A7A\u4E2D\u7EB5\u6A2A\u7684\u97AD\u5F71\u6D88\u6563\u4E4B\u540E\uFF0C\u540A\u5728\u7A7A\u4E2D\u7684\u7D2B\u73AB\u5DF2\u7ECF\u5944\u5944\u4E00\u606F\u3002\u4ECE\u67D4\u80A9\u5230\u8170\u81C0\uFF0C\u9999\u96EA\u822C\u7684\u808C\u80A4\u53C8\u7EA2\u53C8\u80BF\uFF0C\u9762\u76EE\u5168\u975E\uFF0C\u91CD\u91CD\u53E0\u53E0\u7684\u97AD\u75D5\u4EA4\u7EC7\u5728\u4E00\u8D77\uFF0C\u50CF\u88AB\u9C9C\u8840\u67D3\u8FC7\u822C\u9C9C\u7EA2\u3002</p><p>\u300C\u4E0D\u8981\u8138\u7684\u8D31\u4EBA\uFF01\u597D\u751F\u60F3\u60F3\uFF0C\u600E\u4E48\u9075\u5B88\u5987\u9053\uFF0C\u4F3A\u5019\u4E08\u592B\uFF0C\u751F\u513F\u80B2\u5973\uFF01\u300D\u6155\u5BB9\u9F99\u5389\u559D\u9053\u3002</p><p>\u7D2B\u73AB\u53EB\u5F97\u55D3\u5B50\u90FD\u54D1\u4E86\uFF0C\u51B7\u6C57\u5728\u811A\u4E0B\u6C47\u6210\u4E00\u7247\u3002\u80CC\u4E0A\u7684\u76AE\u80A4\u50CF\u88AB\u6574\u4E2A\u63ED\u53BB\uFF0C\u53C8\u7528\u76D0\u6C34\u6D17\u8FC7\u4E00\u6837\u970D\u970D\u5267\u75DB\u3002\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u8D64\u8EAB\u60AC\u5728\u7A7A\u4E2D\u4EFB\u4EBA\u97AD\u7B1E\u7684\u7F9E\u803B\u88AB\u75DB\u695A\u6240\u63A9\u76D6\uFF0C\u6E10\u6E10\u9EBB\u6728\u3002\u800C\u66F4\u6DF1\u7684\u75DB\u82E6\uFF0C\u5219\u662F\u6EE1\u6000\u7684\u5E0C\u671B\u90FD\u5316\u4E3A\u6CE1\u5F71\u3002\u5931\u53BB\u7684\u4E0D\u4EC5\u4EC5\u662F\u5B9D\u85CF\u7684\u7EBF\u7D22\uFF0C\u8FD8\u6709\u9003\u751F\u7684\u5E0C\u671B\u548C\u5973\u4EBA\u7684\u8D1E\u6D01\u2026\u2026</p><p>\u300C\u8FD8\u6709\u4F60\u3002\u300D\u6155\u5BB9\u9F99\u626D\u8FC7\u8138\u3002</p><p>\u7F8E\u5987\u5A07\u8EAF\u7ACB\u523B\u98A4\u6296\u8D77\u6765\u3002</p><p>\u300C\u53EA\u4F1A\u751F\u4E0D\u4F1A\u6559\u2014\u2014\u517B\u51FA\u8FD9\u4E48\u4E2A\u4E0B\u8D31\u7684\u5973\u513F\uFF0C\u4F60\u8FD9\u5F53\u5A18\u7684\u662F\u600E\u4E48\u6559\u7684\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u54ED\u9053\uFF1A\u300C\u9F99\u54E5\u54E5\uFF0C\u59BE\u8EAB\u77E5\u9519\u4E86\u2026\u2026\u300D</p><p>\u300C\u4F4F\u53E3\uFF01\u300D\u6155\u5BB9\u9F99\u53CC\u76EE\u4E00\u5BD2\uFF0C\u300C\u9F99\u2014\u54E5\u2014\u54E5\u2014\uFF0C\u6211\u662F\u4F60\u513F\u5B50\u54CE\u3002\u6709\u4F60\u8FD9\u6837\u5F53\u5A18\u7684\u5417\uFF1F\u4F60\u8FD9\u4E2A\u4E0D\u8981\u8138\u7684\u8D31\u4EBA\uFF01\u300D</p><p>\u8427\u4F5B\u5974\u987F\u65F6\u9762\u7EA2\u8FC7\u8033\uFF0C\u513F\u5B50\u8FD9\u6837\u523A\u9AA8\u7684\u595A\u843D\u6316\u82E6\u4F7F\u7F8E\u5987\u803B\u8FB1\u4E07\u5206\uFF0C\u7389\u8138\u65F6\u7EA2\u65F6\u767D\uFF0C\u4F5C\u58F0\u4E0D\u5F97\u3002</p><p>\u300C\u5A18\u5E74\u7EAA\u5927\u4E86\uFF0C\u513F\u5B50\u5C31\u4E0D\u628A\u4F60\u540A\u8D77\u6765\u3002\u300D\u6155\u5BB9\u9F99\u987F\u4E86\u987F\uFF0C\u300C\u628A\u5979\u7684\u4E1C\u897F\u63A8\u8FC7\u6765\u3002\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u8106\u751F\u751F\u5730\u5E94\u4E86\u4E00\u58F0\uFF0C\u63A8\u8FDB\u6765\u4E2A\u5947\u602A\u7684\u7269\u4F53\u3002</p><p>\u8FD9\u7269\u4F53\u50CF\u4E00\u5F20\u5012\u6263\u7684\u677F\u51F3\uFF0C\u5C3A\u5BF8\u5374\u5927\u4E86\u8BB8\u591A\u3002\u56DB\u6761\u5012\u7ACB\u7684\u94F6\u67F1\u9AD8\u4E0E\u8170\u9F50\uFF0C\u67F1\u9876\u5404\u6709\u4E00\u4E2A\u7FFB\u8F6C\u7684\u74E6\u72B6\u51F9\u69FD\uFF0C\u91CC\u9762\u886C\u7740\u67D4\u8F6F\u7684\u4E1D\u68C9\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u606D\u606D\u656C\u656C\u5730\u67B6\u8D77\u8427\u4F5B\u5974\uFF0C\u5C06\u5979\u653E\u5728\u67B6\u4E0A\u3002\u8FD9\u6728\u67B6\u662F\u4E3A\u8427\u4F5B\u5974\u91CF\u8EAB\u5B9A\u505A\u7684\uFF0C\u4E13\u4E3A\u6362\u5C3F\u5E03\u800C\u7528\u3002\u6B64\u65F6\u7F8E\u5987\u88AB\u653E\u5728\u4E0A\u9762\uFF0C\u56DB\u53EA\u51F9\u69FD\u7ACB\u65F6\u4E25\u4E1D\u5408\u7F1D\u5730\u6263\u4F4F\u5979\u7684\u56DB\u80A2\u3002\u5C06\u51F9\u69FD\u62E7\u597D\u56FA\u5B9A\u4F4F\uFF0C\u8427\u4F5B\u5974\u4FBF\u88AB\u6446\u6210\u4F4E\u5934\u633A\u81C0\u7684\u5C48\u8FB1\u59FF\u52BF\uFF0C\u4E24\u817F\u66F4\u662F\u5E73\u5E73\u5206\u5F00\uFF0C\u9634\u6237\u548C\u83CA\u809B\u90FD\u88AB\u626F\u5230\u6700\u5927\u7684\u5BBD\u5EA6\u3002</p><p>\u300C\u6709\u4F60\u8FD9\u6837\u6DEB\u8D31\u7684\u5A18\u4EB2\uFF0C\u624D\u4F1A\u751F\u4E0B\u6765\u8FD9\u4E48\u6DEB\u8D31\u7684\u5973\u513F\uFF01\u6211\u4ECA\u5929\u8981\u4E3A\u6155\u5BB9\u5BB6\u60E9\u7F5A\u4F60\u4EEC\u5BF9\u4E0D\u8981\u8138\u7684\u8D31\u8D27\uFF01\u300D</p><p>\u8361\u661F\u97AD\u5BF9\u7740\u7F8E\u5987\u7684\u80A5\u81C0\u91CD\u91CD\u62BD\u4E0B\u3002</p><p>\u82E5\u8BBA\u5A07\u8D35\uFF0C\u4ECE\u672A\u4E60\u8FC7\u6B66\u7684\u8427\u4F5B\u5974\u6BD4\u7D2B\u73AB\u66F4\u751A\u3002\u7B2C\u4E00\u97AD\u4E0B\u53BB\uFF0C\u5979\u4FBF\u300C\u54C7\u300D\u7684\u4E00\u58F0\u54ED\u53EB\u8D77\u6765\u3002</p><p>\u582A\u582A\u6253\u4E86\u4E09\u97AD\uFF0C\u8427\u4F5B\u5974\u7684\u610F\u5FD7\u4FBF\u88AB\u5267\u75DB\u51FB\u6E83\u3002\u7D27\u7EF7\u7684\u8EAB\u4F53\u4E00\u677E\uFF0C\u4F4E\u5782\u7684\u80A5\u4E73\u50CF\u662F\u88AB\u6233\u7834\u7684\u76AE\u56CA\uFF0C\u6D01\u767D\u7684\u4E73\u6C41\u6EF4\u6EF4\u7B54\u7B54\u6389\u5728\u5730\u4E0A\u3002\u521A\u6253\u8FC7\u5341\u97AD\uFF0C\u7F8E\u5987\u4FBF\u660F\u6B7B\u8FC7\u53BB\uFF0C\u4E0D\u4EC5\u4E73\u6C41\u56DB\u8D31\uFF0C\u4E0B\u4F53\u66F4\u662F\u9EC4\u767D\u4EA4\u52A0\uFF0C\u5C4E\u5C3F\u9F50\u6D41\u3002</p><p>\u770B\u7740\u83CA\u809B\u6536\u7F29\u7740\u5410\u51FA\u6C61\u7269\u7684\u8273\u6001\uFF0C\u6155\u5BB9\u9F99\u6027\u6B32\u5927\u53D1\uFF0C\u4E0D\u7BA1\u4E09\u4E03\u5341\u4E00\uFF0C\u671D\u5A18\u4EB2\u80A5\u767D\u7684\u96EA\u81C0\u4E0A\u4E00\u901A\u72E0\u63CD\uFF0C\u7136\u540E\u97AD\u5B50\u4E00\u7AD6\uFF0C\u7B14\u76F4\u6253\u5728\u81C0\u7F1D\u5185\u3002</p><p>\u660F\u8FF7\u7684\u7F8E\u5987\u4E00\u58F0\u60B2\u9E23\uFF0C\u96EA\u81C0\u54C6\u55E6\u7740\u5939\u7D27\u3002</p><p>\u6155\u5BB9\u9F99\u6325\u624B\u6254\u6389\u8361\u661F\u97AD\uFF0C\u89E3\u5F00\u8863\u7269\uFF0C\u51C6\u5907\u72E0\u72E0\u64CD\u5979\u4E00\u756A\u51FA\u51FA\u706B\u6C14\u3002</p><p>\u5C31\u5728\u6B64\u65F6\uFF0C\u8427\u4F5B\u5974\u4E0B\u4F53\u8273\u7EA2\u7684\u82B1\u74E3\u7A81\u7136\u4E00\u9635\u7FFB\u5377\uFF0C\u63A5\u7740\u731B\u7136\u5410\u51FA\u4E00\u80A1\u6C79\u6D8C\u7684\u6E29\u70ED\u6DB2\u4F53\u3002\u6DB2\u4F53\u987A\u7740\u5149\u6D01\u7684\u9634\u961C\u4E00\u8DEF\u6D41\u5230\u4E73\u95F4\uFF0C\u6700\u540E\u4ECE\u5979\u4F4E\u5782\u7684\u4E0B\u988C\u548C\u4E73\u5C16\u5206\u522B\u843D\u5728\u5730\u4E0A\u3002</p><p>\u6155\u5BB9\u9F99\u5FC3\u4E0B\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u8E72\u8EAB\u7FFB\u5F00\u6BCD\u4EB2\u7684\u773C\u76AE\u3002</p><p>\u8427\u4F5B\u5974\u53CC\u76EE\u65E0\u795E\uFF0C\u80F8\u524D\u7684\u4E73\u6C41\u8FD8\u5728\u4E0D\u4F4F\u6EF4\u843D\uFF0C\u4E0E\u6B64\u540C\u65F6\uFF0C\u767D\u817B\u7684\u8179\u7403\u5F00\u59CB\u6709\u89C4\u5F8B\u7684\u6536\u7F29\u8D77\u6765\u3002</p><p>\u6BCD\u5973\u8FDE\u5FC3\uFF0C\u534A\u660F\u534A\u9192\u7684\u7D2B\u73AB\u7A81\u7136\u7741\u5F00\u773C\u775B\uFF0C\u5636\u58F0\u53EB\u9053\uFF1A\u300C\u5A18\uFF01\u4F60\u9192\u9192\u554A\uFF01\u4F60\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u5FC3\u91CC\u6253\u9F13\uFF0C\u4E0D\u77E5\u9053\u662F\u4E0D\u662F\u4E2D\u5348\u628A\u5979\u7684\u5C3F\u9053\u585E\u5F97\u592A\u4E45\uFF0C\u5F04\u51FA\u4E8B\u6765\u3002</p><p>\u6155\u5BB9\u9F99\u77E5\u9053\u662F\u7F8A\u6C34\u7834\u4E86\uFF0C\u73B0\u5728\u79BB\u6B63\u5E38\u5206\u5A29\u8FD8\u6709\u4E00\u4E2A\u6708\u7684\u65F6\u95F4\uFF0C\u53EF\u80FD\u662F\u521A\u624D\u4E0B\u624B\u592A\u91CD\uFF0C\u5BFC\u81F4\u6BCD\u4EB2\u65E9\u4EA7\u3002\u6B64\u4E8B\u6781\u4E3A\u5371\u9669\uFF0C\u4E00\u4E0D\u5C0F\u5FC3\u5C31\u662F\u4E00\u5C38\u4E24\u547D\u7684\u7ED3\u5C40\u3002\u4ED6\u8FD0\u529F\u62A4\u4F4F\u6BCD\u4EB2\u7684\u5FC3\u8109\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u5FEB\u8BF7\u53F6\u62A4\u6CD5\uFF01\u300D</p><p>\u53F6\u884C\u5357\u8FDF\u8FDF\u672A\u81F3\uFF0C\u8427\u4F5B\u5974\u7684\u60C5\u5F62\u5374\u6108\u53D1\u5371\u6025\u3002\u7F8A\u6C34\u5DF2\u7136\u6D41\u5C3D\uFF0C\u7FD5\u5F20\u7684\u8089\u7A74\u9732\u51FA\u8840\u7EA2\u7684\u5165\u53E3\uFF0C\u751A\u81F3\u80FD\u770B\u5230\u91CC\u9762\u4E00\u56E2\u9690\u7EA6\u7684\u9ED1\u8272\u6BDB\u53D1\u3002</p><p>\u6155\u5BB9\u9F99\u9759\u4E0B\u5FC3\u6765\uFF0C\u6BCD\u4EB2\u5DF2\u7ECF\u751F\u80B2\u8FC7\u4E24\u80CE\uFF0C\u8FD9\u4E00\u80CE\u53C8\u662F\u987A\u4EA7\uFF0C\u4E0D\u81F3\u4E8E\u6709\u751F\u547D\u5371\u9669\u3002\u767E\u5FD9\u4E2D\uFF0C\u4ED6\u56DE\u8FC7\u5934\u671B\u5411\u7D2B\u73AB\uFF0C\u773C\u4E2D\u6EE1\u662F\u7126\u8651\u3002\u6155\u5BB9\u9F99\u5FC3\u91CC\u6697\u6697\u540E\u6094\u3002\u6BCD\u4EB2\u548C\u59B9\u59B9\u90FD\u662F\u6000\u5B55\u5F85\u4EA7\uFF0C\u4E07\u4E00\u51FA\u4E86\u4EC0\u4E48\u4E8B\uFF0C\u81EA\u5DF1\u5C31\u62B1\u61BE\u7EC8\u8EAB\u4E86\u3002</p><p>\u300C\u5A18\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u300C\u8981\u751F\u5B69\u5B50\u4E86\u3002\u300D\u6155\u5BB9\u9F99\u5429\u5490\u767D\u7389\u9E42\u4E5F\u53BB\u5BFB\u627E\u53F6\u884C\u5357\uFF0C\u81EA\u5DF1\u6258\u7740\u6BCD\u4EB2\u7684\u5C0F\u8179\u7F13\u7F13\u4E0A\u63A8\u3002</p><p>\u5BAB\u7F29\u8D8A\u6765\u8D8A\u6025\uFF0C\u5BAB\u9888\u4E5F\u5B8C\u5168\u5C55\u5F00\u3002\u6CBE\u7740\u5C4E\u5C3F\u3001\u7F8A\u6C34\u7684\u7389\u80A1\u767D\u5AE9\u5149\u6DA6\uFF0C\u5B9B\u5982\u7C89\u5806\u96EA\u780C\u3002\u5C31\u5728\u8FD9\u7247\u96EA\u767D\u6B63\u4E2D\uFF0C\u5A07\u7F8E\u7684\u82B1\u74E3\u50CF\u4E00\u4E2A\u6495\u88C2\u7684\u5DE8\u5927\u4F24\u53E3\uFF0C\u5411\u5916\u9F13\u80C0\u7740\uFF0C\u5F20\u5F00\u4E00\u4E2A\u62F3\u5934\u5927\u5C0F\u7684\u9C9C\u7EA2\u7A74\u53E3\u3002</p><p>\u6155\u5BB9\u9F99\u5438\u4E86\u53E3\u6C14\uFF0C\u53C8\u770B\u4E86\u7D2B\u73AB\u4E00\u773C\uFF0C\u59B9\u59B9\u8FD8\u60AC\u5728\u7A7A\u4E2D\uFF0C\u8179\u7403\u4F3C\u4E4E\u4E5F\u6709\u4E9B\u4E0B\u5760\u2026\u2026</p><p>\u7D2B\u73AB\u6025\u5207\u6323\u624E\u7740\uFF0C\u300C\u4F60\u770B\u6211\u5E72\u4EC0\u4E48\uFF01\u5C0F\u5FC3\u5A18\uFF01\u300D</p><p>\u300C\u6211\u653E\u4F60\u4E0B\u6765\u3002\u300D</p><p>\u300C\u522B\u7BA1\u6211\uFF01\u5A18\u90FD\u6D41\u8840\u4E86\u2026\u2026\u300D\u5C11\u5973\u54ED\u9053\u3002</p><p>\u6155\u5BB9\u9F99\u626D\u8FC7\u5934\uFF0C\u5C4F\u606F\u51DD\u795E\uFF0C\u4E24\u6307\u63A2\u5165\u8089\u7A74\uFF0C\u8F7B\u67D4\u5730\u634F\u4F4F\u80CE\u513F\u7684\u5934\u9885\uFF0C\u7F13\u7F13\u7528\u529B\u5916\u62D4\u3002</p><p>\u8089\u7A74\u5C55\u5F00\u5230\u4E0D\u53EF\u601D\u8BAE\u7684\u5BBD\u5EA6\uFF0C\u5468\u56F4\u4E00\u5708\u7EA2\u7EA2\u7684\u5AE9\u8089\u88AB\u6491\u5F97\u53C8\u7EC6\u53C8\u8584\uFF0C\u51E0\u4E4E\u80FD\u770B\u5230\u80CE\u513F\u5728\u91CC\u9762\u7684\u52A8\u4F5C\u3002\u8427\u4F5B\u5974\u6B64\u65F6\u5DF2\u7136\u75DB\u9192\uFF0C\u5206\u5A29\u7684\u75DB\u695A\u4F7F\u5979\u4E00\u8FED\u58F0\u5730\u53EB\u7740\uFF1A\u300C\u9F99\u54E5\u54E5\u3001\u9F99\u54E5\u2026\u2026\u4EBA\u5BB6\u597D\u75BC\u554A\u2026\u2026\u9F99\u54E5\u54E5\u2026\u2026\u300D\u5BF9\u5979\u6765\u8BF4\uFF0C\u8FD9\u4E16\u95F4\u552F\u4E00\u80FD\u4F9D\u9760\u7684\uFF0C\u53EA\u6709\u8FD9\u4E2A\u4E0D\u4F4F\u7F9E\u8FB1\u5979\u7684\u4EB2\u751F\u513F\u5B50\u4E86\u3002</p><p>\u300C\u522B\u6015\u522B\u6015\uFF0C\u6709\u54E5\u54E5\u5728\u8FD9\u91CC\u2026\u2026\u300D\u6155\u5BB9\u9F99\u67D4\u58F0\u5B89\u6170\u9053\uFF0C\u4ED6\u6293\u4F4F\u673A\u4F1A\uFF0C\u8F7B\u8F7B\u4E00\u63D0\uFF0C\u6491\u81F3\u6781\u9650\u7684\u7A74\u8089\u4E4D\u541E\u4E4D\u5410\uFF0C\u80CE\u513F\u7684\u5934\u9885\u987A\u5229\u5730\u6ED1\u51FA\u8154\u4F53\uFF0C\u5E26\u51FA\u4E00\u80A1\u7EA2\u9EC4\u8272\u7684\u6DB2\u4F53\u3002</p><p>\u300C\u5440\uFF01\u300D\u8427\u4F5B\u5974\u4E00\u58F0\u5C16\u53EB\uFF0C\u8089\u7A74\u7684\u6536\u7F29\u6108\u53D1\u5267\u70C8\uFF0C\u62FC\u547D\u6536\u7D27\uFF0C\u53C8\u62FC\u547D\u5F20\u5F00\uFF0C\u50CF\u4E00\u5F20\u54ED\u6CE3\u7684\u5634\u5DF4\uFF0C\u4E00\u622A\u4E00\u622A\u5410\u51FA\u80CE\u513F\u7684\u80A9\u5934\u3001\u624B\u81C2\u3001\u8170\u81C0\u3002</p><p>\u867D\u7136\u89C1\u8FC7\u5E08\u7236\u7684\u5206\u5A29\uFF0C\u4F46\u90A3\u6B21\u4EA7\u4E0B\u7684\u53EA\u662F\u4E00\u4E2A\u602A\u80CE\u3002\u7D2B\u73AB\u77AA\u5927\u4FCF\u76EE\uFF0C\u773C\u770B\u7740\u4E00\u4E2A\u6BCD\u4EB2\u662F\u5982\u4F55\u75DB\u82E6\u4E07\u72B6\u5730\u4EA7\u4E0B\u4E00\u4E2A\u6D3B\u751F\u751F\u7684\u5A74\u513F\u3002</p><p>\u5341\u516D\u5E74\u524D\uFF0C\u81EA\u5DF1\u4E5F\u662F\u4ECE\u90A3\u91CC\u6765\u5230\u8FD9\u4E2A\u4E16\u754C\u2026\u2026</p><p>\u6155\u5BB9\u9F99\u4F7F\u51FA\u538B\u7BB1\u5E95\u7684\u529F\u592B\uFF0C\u5341\u6307\u67D4\u82E5\u65E0\u9AA8\u5730\u63E1\u4F4F\u80CE\u513F\uFF0C\u5C31\u662F\u63E1\u4E00\u5757\u8C46\u8150\u4E5F\u6CA1\u6709\u4ED6\u8FD9\u822C\u5C0F\u5FC3\u3002</p><p>\u8427\u4F5B\u5974\u8EAB\u4E0B\u6D53\u767D\u7684\u4E73\u6C41\u3001\u6D51\u6D4A\u7684\u7F8A\u6C34\u3001\u6DE1\u9EC4\u7684\u5C3F\u6DB2\u3001\u9EC4\u8272\u7684\u7CAA\u4FBF\u8FD8\u6709\u6BB7\u7EA2\u7684</p><p>\u8840\u8FF9\uFF0C\u5F62\u5F62\u8272\u8272\u6D41\u6210\u4E00\u7247\u3002\u5979\u76F4\u7740\u5589\u5499\uFF0C\u62FC\u547D\u5410\u6C14\uFF0C\u98A4\u6296\u7684\u7EA2\u5507\u82CD\u767D\u5F97\u5982\u540C\u6B7B\u4EBA\u3002</p><p>\u6155\u5BB9\u9F99\u8F7B\u8F7B\u4E00\u63D0\uFF0C\u300C\u5657\u5F8B\u300D\u4E00\u58F0\uFF0C\u4E00\u4E2A\u5149\u6E9C\u6E9C\u7684\u80CE\u513F\uFF0C\u5E26\u7740\u9ECF\u4E4E\u4E4E\u7684\u4F53\u6DB2\u843D\u5728\u638C\u4E2D\u3002</p><p>\u300C\u554A\u5440\u2026\u2026\u9F99\u54E5\u54E5\u9F99\u54E5\u54E5\u2026\u2026\u300D\u8427\u4F5B\u5974\u5931\u795E\u5730\u5C16\u53EB\u7740\uFF0C\u9F13\u80C0\u7684\u5C0F\u8179\u5947\u8FF9\u822C\u5E73\u590D\u4E0B\u6765\uFF0C\u677E\u5F1B\u7684\u8089\u7A74\u4E2D\uFF0C\u7275\u51FA\u4E00\u6839\u8840\u6DCB\u6DCB\u7684\u8110\u5E26\u3002</p><p>\u300C\u597D\u4E86\u597D\u4E86\uFF0C\u5A18\uFF0C\u6211\u4EEC\u7684\u5B69\u5B50\u5DF2\u7ECF\u751F\u4E0B\u6765\u4E86\u2026\u2026\u300D\u62B1\u7740\u81EA\u5DF1\u4E0E\u6BCD\u4EB2\u7684\u9AA8\u8840\uFF0C\u6155\u5BB9\u9F99\u7684\u58F0\u97F3\u4E5F\u6709\u4E9B\u98A4\u6296\u3002</p><p>\u8427\u4F5B\u5974\u7565\u5FAE\u6E05\u9192\u4E86\u4E00\u4E9B\uFF0C\u7B2C\u4E00\u53E5\u5148\u95EE\u9053\uFF1A\u300C\u7537\u5B69\u8FD8\u662F\u5973\u5B69\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u770B\u4E86\u4E00\u773C\uFF0C\u300C\u7537\u7684\u3002\u300D</p><p>\u6B64\u65F6\u7684\u8427\u4F5B\u5974\u6781\u7AEF\u654F\u611F\uFF0C\u5979\u542C\u51FA\u513F\u5B50\u58F0\u97F3\u91CC\u9690\u7EA6\u7684\u5931\u671B\uFF0C\u4E0D\u7531\u773C\u5708\u4E00\u7EA2\uFF0C\u62BD\u6CE3\u9053\uFF1A\u300C\u5BF9\u4E0D\u8D77\u2026\u2026\u300D</p><p>\u5BF9\u522B\u4EBA\u800C\u8A00\uFF0C\u90FD\u662F\u8981\u7537\u5B69\u4F20\u5B97\u63A5\u4EE3\uFF0C\u800C\u9F99\u54E5\u54E5\u5374\u4E0D\u9700\u8981\u5979\u7684\u7537\u5A74\uFF0C\u73AB\u513F\u6000\u7684\u624D\u662F\u4ED6\u7684\u7EE7\u627F\u4EBA\u3002</p><p>\u300C\u5BF9\u4E0D\u8D77\u2026\u2026\u300D\u8427\u4F5B\u5974\u6EE1\u6000\u5185\u759A\u5730\u5C0F\u58F0\u8BF4\u7740\uFF0C\u6211\u5E94\u8BE5\u7ED9\u9F99\u54E5\u54E5\u751F\u4E0B\u4E2A\u5973\u513F\uFF0C\u50CF\u73AB\u513F\u90A3\u6837\u6F02\u4EAE\u7684\u5973\u513F\uFF0C\u8BA9\u9F99\u54E5\u54E5\u5F00\u5FC3\u2026\u2026</p><p>\u300C\u50BB\u74DC\uFF0C\u8FD9\u662F\u54E5\u54E5\u7684\u7B2C\u4E00\u4E2A\u5B69\u5B50\uFF0C\u6211\u9AD8\u5174\u8FD8\u6765\u4E0D\u53CA\u5462\uFF0C\u522B\u54ED\u4E86\u2026\u2026\u7B11\u4E00\u7B11\uFF0C\u54E5\u54E5\u6700\u559C\u6B22\u4F60\u7B11\u4E86\u2026\u2026\u300D</p><p>\u7F8E\u5987\u611F\u52A8\u5730\u671B\u7740\u60C5\u90CE\uFF0C\u5634\u89D2\u62BD\u52A8\u7740\u9732\u51FA\u4E00\u4E2A\u51C4\u8273\u7684\u7B11\u5BB9\u3002\u53EA\u8981\u54E5\u54E5\u9AD8\u5174\uFF0C\u518D\u591A\u7684\u75BC\u75DB\u4E5F\u662F\u503C\u5F97\u7684\u3002</p><p>\u300C\u9F99\u54E5\u54E5\uFF0C\u5A18\u4E0B\u6B21\u7ED9\u4F60\u751F\u4E2A\u5973\u513F\u597D\u4E0D\u597D\u2026\u2026\u300D</p><p>\u300C\u597D\u554A\uFF01\u4F46\u518D\u6CA1\u6709\u4EBA\u4F1A\u50CF\u6211\u5A18\u8FD9\u4E48\u6F02\u4EAE\uFF0C\u8FD9\u4E48\u8FF7\u4EBA\u4E86\u2026\u2026\u300D\u6155\u5BB9\u9F99\u67D4\u58F0\u9053\u3002</p><p>\u53F6\u884C\u5357\u4E0E\u6C90\u58F0\u4F20\u6B63\u5728\u6E56\u4E0A\u6CDB\u821F\uFF0C\u542C\u8BF4\u5BAB\u4E2D\u60CA\u53D8\uFF0C\u8FDE\u84D1\u8863\u4E5F\u6765\u4E0D\u53CA\u53BB\u6389\u4FBF\u76F4\u5954\u5723\u5BAB\u3002</p><p>\u4ED6\u6C89\u7740\u8138\uFF0C\u4E00\u624B\u5207\u4F4F\u8427\u4F5B\u5974\u7684\u8109\u95E8\uFF0C\u4E00\u624B\u62CD\u5411\u6D51\u8EAB\u8840\u8FF9\u7684\u80CE\u513F\u3002\u4E0D\u591A\u65F6\u80CE\u513F\u624B\u811A\u4E00\u52A8\uFF0C\u5C0F\u5634\u5410\u51FA\u4E00\u80A1\u7F8A\u6C34\uFF0C\u63A5\u7740\u53D1\u51FA\u54CD\u4EAE\u7684\u54ED\u58F0\u3002</p><p>\u7D2B\u73AB\u53EA\u770B\u4E86\u5A74\u513F\u4E00\u773C\uFF0C\u4FBF\u59CB\u7EC8\u7D27\u5F20\u5730\u76EF\u7740\u6BCD\u4EB2\u3002</p><p>\u53F6\u884C\u5357\u526A\u65AD\u8110\u5E26\uFF0C\u547D\u767D\u6C0F\u59D0\u59B9\u6253\u6765\u6E29\u6CC9\uFF0C\u7ED9\u5A74\u513F\u6D17\u6D74\u3002\u81EA\u5DF1\u5219\u4ECE\u8427\u4F5B\u5974\u5B50\u5BAB\u5185\u62D6\u51FA\u80CE\u76D8\uFF0C\u6E05\u7406\u5E72\u51C0\uFF0C\u8FD9\u624D\u677E\u5F00\u7709\u5934\uFF0C\u7F13\u58F0\u9053\uFF1A\u300C\u606D\u559C\u5BAB\u4E3B\uFF0C\u6BCD\u5B50\u5E73\u5B89\u3002\u300D</p><p>\u7D2B\u73AB\u4E00\u53E3\u6C14\u7EC8\u4E8E\u900F\u4E86\u51FA\u6765\uFF0C\u5979\u67D4\u9888\u4E00\u4FA7\uFF0C\u660F\u4E86\u8FC7\u53BB\u3002</p>',70);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
