import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_119" tabindex="-1"><a class="header-anchor" href="#_119" aria-hidden="true">#</a> 119</h1><p>\u9634\u957F\u91CE\u72C2\u7B11\u7740\u62E7\u4F4F\u5979\u7684\u4E73\u623F\uFF0C\u300C\u4E0D\u8981\u8138\u7684\u5973\u4EBA\uFF01\u957F\u8FD9\u4E48\u5927\u4E00\u5BF9\u5976\u5B50\uFF0C\u771F\u4E0D\u8981\u8138\u2026\u2026\u300D</p><p>\u5E08\u7236\u5728\u6CE5\u5751\u91CC\u6323\u624E\u7740\uFF0C\u5979\u51DD\u89C6\u7740\u81EA\u5DF1\uFF0C\u300C\u73AB\u513F\uFF0C\u4F60\u5343\u4E07\u4E0D\u80FD\u6B7B\u3002\u8981\u6551\u6211\u4EEC\u51FA\u53BB\u2026\u2026\u300D</p><p>\u53C8\u6ED1\u53C8\u8F6F\u2026\u2026\u90A3\u662F\u98CE\u5E08\u59D0\u7684\u820C\u5934\u2026\u2026\u5C55\u626C\u54E5\u54E5\u65AD\u6389\u7684\u624B\u81C2\u2026\u2026\u75BC\u5417\u2026\u2026</p><p>\u80DC\u54E5\u54E5\u767D\u68EE\u68EE\u7684\u9AA8\u9AB8\uFF0C\u5AC2\u5AC2\u5149\u79C3\u79C3\u7684\u4E0B\u4F53\u3002\u300C\u98D8\u6885\u5CF0\u5F1F\u5B50\uFF0C\u6155\u5BB9\u80DC\u7684\u8001\u5A46\uFF0C\u8BA9\u5927\u7237\u4EEC\u64CD\u6B7B\u4E3A\u6B62\u2026\u2026\u300D</p><p>\u5A18\u4EB2\u75DB\u82E6\u5730\u8868\u60C5\u3002\u4E24\u817F\u95F4\uFF0C\u80CE\u513F\u6B63\u6323\u624E\u7740\u8131\u79BB\u6BCD\u4F53\u3002\u9732\u51FA\u8138\u4E86\u2026\u2026\u662F\u6211\u2026\u2026\u90A3\u4E2A\u80CE\u513F\u662F\u6211\u6155\u5BB9\u7D2B\u73AB\u2026\u2026</p><p>\u5FFD\u7136\u4E00\u53EA\u80AE\u810F\u5927\u624B\u4F38\u6765\uFF0C\u4E00\u628A\u62FD\u51FA\u8840\u6DCB\u6DCB\u7684\u80CE\u513F\uFF0C\u300C\u8001\u5B50\u6700\u70E6\u5927\u809A\u5A46\u5A18\uFF01\u300D</p><p>\u81EA\u5DF1\u53C8\u5C0F\u53C8\u8106\u5F31\uFF0C\u53EA\u80FD\u60CA\u6050\u5730\u770B\u7740\u4ED6\u638F\u51FA\u6EE1\u662F\u8089\u7C92\u3001\u5012\u523A\u7684\u9633\u5177\uFF0C\u671D\u8FD8\u662F\u5A74\u513F\u7684\u81EA\u5DF1\u4F38\u6765\u3002</p><p>\u300C\u64CD\u6B7B\u4F60\u8FD9\u4E2A\u81ED\u5A4A\u5B50\uFF0C\u5C31\u6709\u5B9D\u85CF\u4E86\u2026\u2026\u300D</p><p>\u4E00\u4E2A\u4EBA\u5F71\u7A81\u7136\u98DE\u51FA\uFF0C\u4E00\u5200\u65A9\u65AD\u90A3\u6839\u72F0\u72DE\u7684\u9633\u5177\u3002\u9C9C\u8840\u98DE\u6E85\u4E2D\uFF0C\u6155\u5BB9\u9F99\u7684\u9762\u5BB9\u6E10\u6E10\u6E05\u6670\u3002</p><p>\u300C\u4E0D\u8981\u8138\u7684\u8D31\u8D27\uFF01\u300D</p><p>\u5012\u5728\u5730\u4E0A\u602A\u7269\u626D\u52A8\u7740\uFF0C\u7A81\u7136\u628A\u6CA1\u6709\u624B\u6307\u7684\u65AD\u638C\u5370\u5230\u81EA\u5DF1\u80F8\u53E3\u2026\u2026</p><p>\u7D2B\u73AB\u731B\u7136\u60CA\u9192\u8FC7\u6765\uFF0C\u8EAB\u5B50\u4E0D\u4F4F\u6218\u6817\u3002\u5979\u5F80\u65C1\u8FB9\u8F7B\u8F7B\u4E00\u6478\uFF0C\u60F3\u627E\u5230\u90A3\u5177\u6E29\u6696\u7684\u8EAB\u4F53\u3002\u7136\u800C\u8EAB\u8FB9\u5374\u7A7A\u8361\u8361\u7684\uFF0C\u65E0\u4F9D\u65E0\u9760\u3002</p><p>\u5A18\u521A\u521A\u751F\u4E0B\u5B69\u5B50\uFF0C\u4ED6\u5728\u966A\u5A18\u2026\u2026</p><p>\u80CC\u4E0A\u7684\u97AD\u4F24\u9635\u9635\u523A\u75DB\uFF0C\u5C11\u5973\u53EA\u80FD\u6402\u7740\u809A\u5B50\uFF0C\u4FA7\u8EBA\u5728\u69BB\u4E0A\uFF0C\u832B\u7136\u7741\u7740\u773C\u775B\u3002</p><p>\u5979\u9690\u7EA6\u6709\u79CD\u611F\u89C9\u3002\u6709\u4E00\u4EF6\u81F3\u5173\u91CD\u8981\u7684\u4E8B\u60C5\uFF0C\u6B63\u5728\u5979\u4E0D\u77E5\u4E0D\u89C9\u4E2D\u53D1\u751F\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5F88\u75DB\uFF0C\u8D77\u4E0D\u6765\u3002\u300D\u4E09\u6B21\u8D76\u8D70\u767D\u7389\u83BA\u4E4B\u540E\uFF0C\u6155\u5BB9\u9F99\u4EB2\u81EA\u6765\u627E\u7D2B\u73AB\u65F6\uFF0C\u5979\u8FD9\u6837\u8BF4\u3002</p><p>\u53EF\u80FD\u662F\u521D\u4E3A\u4EBA\u7236\uFF0C\u6155\u5BB9\u9F99\u5E76\u6CA1\u6709\u751F\u6C14\uFF0C\u300C\u5A18\u597D\u4E9B\u4E86\uFF0C\u8FD9\u4F1A\u513F\u6B63\u5728\u5582\u5976\uFF0C\u6211\u6276\u4F60\u53BB\u770B\u770B\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u9ED8\u9ED8\u5750\u8D77\u6765\uFF0C\u7A81\u7136\u95EE\u9053\uFF1A\u300C\u4ED6\u7B97\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u5EB6\u5B50\u3002\u6CA1\u6709\u7EE7\u627F\u6743\u3002\u4E5F\u4E0D\u80FD\u59D3\u6155\u5BB9\u3002\u300D\u6155\u5BB9\u9F99\u6BEB\u4E0D\u8FDF\u7591\u5730\u7B54\u9053\uFF1A\u7D2B\u73AB\u70B9\u70B9\u5934\uFF0C\u300C\u5982\u679C\u662F\u5973\u513F\u5462\uFF1F\u300D</p><p>\u300C\u6155\u5BB9\u6C0F\u6240\u6709\u7537\u4EBA\u7684\u73A9\u7269\u3002\u300D</p><p>\u300C\u6211\u7684\u5973\u513F\u4E5F\u4E00\u6837\u5417\uFF1F\u300D</p><p>\u300C\u4E00\u6837\u3002\u4E0D\u8FC7\u5979\u4EEC\u6709\u751F\u80B2\u7684\u6743\u529B\u3002\u300D</p><p>\u7D2B\u73AB\u7B11\u4E86\u4E00\u4E0B\uFF0C\u300C\u6155\u5BB9\u9F99\uFF0C\u6211\u5F88\u4F69\u670D\u4F60\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6DE1\u6DE1\u9053\uFF1A\u300C\u4E0D\u5FC5\u5BA2\u6C14\u3002\u300D</p><p>\u7D2B\u73AB\u98A4\u6296\u8D77\u6765\uFF0C\u5636\u58F0\u9053\uFF1A\u300C\u90FD\u8BF4\u6211\u4E0D\u8981\u8138\uFF0C\u4F60\u624D\u771F\u4E0D\u8981\u8138\uFF01\u4F60\u662F\u75AF\u5B50\uFF01\u300D</p><p>\u300C\u4F60\u9519\u4E86\u3002\u6211\u662F\u738B\u8005\uFF0C\u6709\u6743\u529B\u5236\u8BA2\u89C4\u5219\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5A74\u513F\u88F9\u5F97\u53EA\u9732\u51FA\u4E00\u5F20\u76B1\u5DF4\u5DF4\u7684\u5C0F\u8138\uFF0C\u6B64\u65F6\u6B63\u8EBA\u5728\u6BCD\u4EB2\u8EAB\u4FA7\uFF0C\u8D2A\u5A6A\u5730\u5438\u542E\u7740\u4E73\u6C41\u3002</p><p>\u8427\u4F5B\u5974\u8138\u8272\u82CD\u767D\uFF0C\u610F\u5916\u7684\u65E9\u4EA7\u4F7F\u5979\u5341\u5206\u865A\u5F31\uFF0C\u4F46\u773C\u4E2D\u6D0B\u6EA2\u7684\u6BCD\u7231\u5374\u6D53\u5F97\u5316\u4E0D\u5F00\u3002</p><p>\u300C\u5C0F\u5B9D\u5B9D\uFF0C\u5FEB\u4E9B\u957F\u5927\uFF0C\u597D\u4E3A\u4F60\u7239\u7239\u6548\u529B\u2026\u2026\u300D\u5979\u5462\u54DD\u7740\uFF0C\u7528\u6E29\u67D4\u7684\u773C\u5149\u629A\u6478\u7740\u5A74\u513F\u3002\u5979\u591A\u60F3\u4EB2\u624B\u6478\u4E00\u6478\u81EA\u5DF1\u548C\u9F99\u54E5\u54E5\u7684\u7B2C\u4E00\u4E2A\u5B69\u5B50\uFF0C\u628A\u4ED6\u62B1\u5728\u6000\u91CC\u5582\u4ED6\u5403\u5976\u2026\u2026</p><p>\u300C\u5A18\u3002\u300D</p><p>\u300C\u73AB\u2026\u2026\u59D0\u59D0\u2026\u2026\u300D\u770B\u5230\u65C1\u8FB9\u7684\u6155\u5BB9\u9F99\uFF0C\u8427\u4F5B\u5974\u8FDE\u5FD9\u6539\u53E3\u3002</p><p>\u7D2B\u73AB\u53EA\u5F53\u6CA1\u6709\u542C\u5230\uFF0C\u8FF3\u76F4\u8D70\u5230\u69BB\u4FA7\uFF0C\u7528\u6307\u5C16\u8F7B\u8F7B\u70B9\u4E86\u70B9\u5A74\u513F\u7684\u9762\u5B54\uFF0C\u7B11\u9053\uFF1A\u300C\u8DDF\u5A18\u597D\u50CF\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7B11\u9053\uFF1A\u300C\u6211\u770B\u50CF\u6211\uFF0C\u5A18\uFF0C\u4F60\u8BF4\u5462\uFF1F\u300D</p><p>\u8427\u4F5B\u5974\u8F7B\u58F0\u9053\uFF1A\u300C\u9F99\u54E5\u54E5\u7684\u513F\u5B50\uFF0C\u5F53\u7136\u662F\u50CF\u9F99\u54E5\u54E5\u4E86\u2026\u2026\u300D</p><p>\u5A74\u513F\u5410\u51FA\u4E73\u5934\uFF0C\u5F20\u5F00\u5C0F\u5634\u6253\u4E86\u4E2A\u5C0F\u5C0F\u7684\u5475\u6B20\u3002\u8427\u4F5B\u5974\u60C5\u4E0D\u81EA\u7981\u8EAB\u5B50\u4E00\u52A8\uFF0C\u60F3\u628A\u5B69\u5B50\u62B1\u5728\u6000\u91CC\u75BC\u7231\u3002</p><p>\u7D2B\u73AB\u770B\u51FA\u6BCD\u4EB2\u7684\u6E34\u671B\uFF0C\u4E24\u624B\u5C0F\u5FC3\u5730\u6258\u8D77\u5A74\u513F\u3002\u752B\u4E00\u5165\u624B\u5979\u4FBF\u60CA\u53EB\u8D77\u6765\uFF0C\u300C\u8FD9\u4E48\u8F6F\uFF1F\u300D</p><p>\u300C\u4F60\u5C0F\u65F6\u5019\u4E5F\u4E00\u6837\u5462\u2026\u2026\u300D\u8427\u4F5B\u5974\u67D4\u67D4\u4E00\u7B11\u3002</p><p>\u7D2B\u73AB\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u628A\u5C0F\u8089\u56E2\u653E\u5230\u6BCD\u4EB2\u6000\u91CC\uFF0C\u7136\u540E\u62C9\u8D77\u5979\u7684\u624B\u638C\uFF0C\u8F7B\u8F7B\u6469\u6332\u7740\u5A74\u513F\u3002</p><p>\u6478\u4E86\u4E24\u4E0B\uFF0C\u8427\u4F5B\u5974\u773C\u4E2D\u7A81\u7136\u6D8C\u51FA\u4E24\u884C\u70ED\u6CEA\u3002</p><p>\u7D2B\u73AB\u4E5F\u9F3B\u4E2D\u53D1\u9178\uFF0C\u8FDE\u5FD9\u62B1\u4E0B\u5A74\u513F\uFF0C\u5E2E\u6BCD\u4EB2\u76D6\u597D\u88AB\u8925\uFF0C\u5F3A\u7B11\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u7761\u4E00\u4F1A\u513F\u5427\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6CA1\u6709\u8D77\u8EAB\uFF0C\u4ED6\u6CA1\u6709\u7406\u4F1A\u90A3\u4E2A\u7537\u5A74\uFF0C\u53EA\u6311\u5F04\u7740\u8427\u4F5B\u5974\u7684\u4E73\u5934\uFF0C\u5C06\u82B3\u9999\u7684\u4E73\u6C41\u6CBE\u5728\u6307\u95F4\u3002</p><p>\u7D2B\u73AB\u518D\u4E0D\u613F\u591A\u7559\u4E00\u523B\uFF0C\u5306\u5306\u79BB\u5F00\u3002</p><p>\u6BCD\u4EB2\u5A07\u5A9A\u7684\u58F0\u97F3\u4ECE\u95E8\u7F1D\u4E2D\u4F20\u6765\uFF0C\u300C\u9F99\u54E5\u54E5\uFF0C\u5A18\u4E00\u5B9A\u7ED9\u4F60\u751F\u4E2A\u5973\u513F\u2026\u2026\u300D</p><p>\u300C\u597D\u554A\uFF0C\u7ED9\u6211\u751F\u5BF9\u53CC\u80DE\u80CE\u5427\u2026\u2026\u300D</p><p>\u300C\u5A18\u8FD8\u6CA1\u751F\u8FC7\u53CC\u80DE\u80CE\u5462\u2026\u2026\u9F99\u54E5\u54E5\u60F3\u8981\uFF0C\u5A18\u5C31\u7ED9\u54E5\u54E5\u751F\u4E00\u5BF9\u53CC\u80DE\u80CE\u2026\u2026\u9F99\u54E5\u54E5\u4F1A\u4E0D\u4F1A\u5ACC\u5A18\u7684\u809A\u5B50\u592A\u5927\u2026\u2026\u300D</p><p>\u300C\u4E0D\u4F1A\uFF0C\u5A18\u5927\u7740\u809A\u5B50\u4E5F\u5F88\u7F8E\u554A\u3002\u300D</p><p>\u300C\u2026\u2026\u9F99\u54E5\u54E5\u4F1A\u559C\u6B22\u5979\u4EEC\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u58F0\u4F4E\u7B11\uFF0C\u300C\u65E9\u4E9B\u66FF\u6211\u751F\uFF0C\u7B49\u5A18\u4E94\u5341\u5927\u5BFF\u7684\u65F6\u5019\uFF0C\u513F\u5B50\u7ED9\u5979\u4EEC\u5F00\u82DE\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u9760\u5728\u752C\u9053\u4E0A\uFF0C\u80CC\u540E\u4F20\u6765\u77F3\u58C1\u68EE\u51B7\u7684\u5BD2\u610F\u3002\u65E0\u8BBA\u5982\u4F55\uFF0C\u5979\u90FD\u4E0D\u4F1A\u9119\u89C6\u6BCD\u4EB2\u7684\u3002</p><p>\u6BCD\u4EB2\u867D\u7136\u67D4\u5F31\uFF0C\u4F46\u59CB\u7EC8\u5728\u5C3D\u53EF\u80FD\u5730\u5173\u5FC3\u5979\u3001\u5E2E\u52A9\u5979\u3002</p><p>\u5C31\u50CF\u5E08\u7236\uFF0C\u65E0\u8BBA\u5E08\u7236\u53D8\u6210\u4EC0\u4E48\u6837\u5B50\uFF0C\u5979\u7684\u5C0A\u656C\u90FD\u4E0D\u4F1A\u6D88\u6DE1\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u5173\u4E0A\u7389\u95E8\uFF0C\u53C8\u63D2\u4E0A\u95E8\u95E9\u3002\u671D\u56DB\u4E0B\u770B\u4E86\u770B\uFF0C\u7136\u540E\u4E00\u63D0\u771F\u6C14\u3002</p><p>\u62D6\u7740\u81C3\u80BF\u7684\u8EAB\u4F53\uFF0C\u5C11\u5973\u8FD8\u8F7B\u5F97\u50CF\u4E00\u7247\u6811\u53F6\uFF0C\u65E0\u58F0\u65E0\u606F\u5730\u843D\u5728\u69BB\u4E0A\u3002</p><p>\u5979\u5446\u5446\u6276\u7740\u5C0F\u8179\uFF0C\u56DE\u5FC6\u8D77\u6628\u65E5\u7684\u79CD\u79CD\u60C5\u5F62\u3002</p><p>\u9634\u957F\u91CE\u90A3\u4E00\u638C\u529B\u9053\u5341\u8DB3\uFF0C\u53EF\u72C2\u6D8C\u7684\u771F\u6C14\u975E\u4F46\u6CA1\u6709\u9707\u788E\u5979\u7684\u5FC3\u8109\uFF0C\u53CD\u800C\u5C3D\u6570\u84C4\u5728\u81BB\u4E2D\u7A74\u5185\u3002</p><p>\u4ECE\u97AD\u6253\u90A3\u4E00\u523B\u5F00\u59CB\uFF0C\u7D2B\u73AB\u4FBF\u89C9\u51FA\u5F02\u5E38\u3002\u90A3\u56E2\u771F\u6C14\u4EFF\u4F5B\u662F\u91CD\u697C\u6C14\u9501\u7684\u514B\u661F\uFF0C\u4ECE\u81BB\u4E2D\u7A74\u5F00\u59CB\uFF0C\u7F13\u6162\u5374\u6BEB\u4E0D\u505C\u987F\u5730\u4E00\u5173\u4E00\u5173\u89E3\u5F00\u5979\u88AB\u5236\u7A74\u9053\u3002\u5F53\u5979\u4ECE\u660F\u8FF7\u4E2D\u9192\u6765\uFF0C\u53EA\u89C9\u4E39\u7530\u5185\u771F\u6C14\u5347\u817E\uFF0C\u4E45\u9501\u6C14\u6D77\u7684\u91CD\u697C\u6C14\u9501\u5DF2\u7ECF\u4E0D\u7FFC\u800C\u98DE\uFF0C\u800C\u4E14\u8FD8\u591A\u4E00\u80A1\u84EC\u52C3\u7684\u5F02\u79CD\u771F\u6C14\u3002\u7D2B\u73AB\u5927\u60D1\u4E0D\u89E3\uFF0C\u66F4\u4E0D\u6562\u8BA9\u4EBA\u770B\u51FA\u7AEF\u502A\uFF0C\u4FBF\u88C5\u505A\u80CC\u4F24\u672A\u7652\uFF0C\u8EB2\u5728\u5BA4\u5185\u3002</p><p>\u5979\u4E0D\u77E5\u9053\u662F\u90A3\u4E00\u5239\u90A3\u7684\u72B9\u8C6B\u6551\u4E86\u81EA\u5DF1\u7684\u6027\u547D\u3002</p><p>\u6628\u65E5\u5728\u5730\u7A9F\u91CC\uFF0C\u7D2B\u73AB\u6124\u6068\u4E4B\u4E0B\uFF0C\u4E00\u5200\u780D\u65AD\u9634\u957F\u91CE\u7684\u624B\u81C2\u3002\u63A5\u7740\u4FBF\u540E\u6094\u6CA1\u6293\u4F4F\u4E24\u4EBA\u4E0D\u80FD\u5206\u5FC3\u7684\u673A\u4F1A\u6740\u6389\u6155\u5BB9\u9F99\u3002</p><p>\u9634\u957F\u91CE\u770B\u89C1\u5979\u671B\u5411\u6155\u5BB9\u9F99\u7684\u773C\u795E\uFF0C\u4FBF\u77E5\u9053\u8FD9\u4E2A\u8D31\u5A4A\u5B50\u8DDF\u5C0F\u767D\u8138\u4E4B\u95F4\u7684\u4EC7\u6DF1\u4F3C\u6D77\u3002</p><p>\u4ED6\u65AD\u81C2\u6B8B\u638C\u8EAB\u8D1F\u91CD\u4F24\uFF0C\u81EA\u77E5\u65E0\u53EF\u5E78\u514D\uFF0C\u4E8E\u662F\u5F53\u673A\u7ACB\u65AD\u5728\u4E00\u77AC\u95F4\u505A\u51FA\u501F\u5200\u6740\u4EBA\u7684\u51B3\u5B9A\uFF0C\u7528\u6027\u547D\u8D4C\u4E0A\u4E00\u628A\uFF0C\u5C06\u771F\u6C14\u84C4\u5728\u7D2B\u73AB\u4F53\u5185\uFF0C\u5E2E\u5979\u89E3\u5F00\u91CD\u697C\u6C14\u9501\u7684\u675F\u7F1A\u3002\u4E3A\u4E86\u80FD\u8BA9\u5979\u6740\u6389\u6155\u5BB9\u9F99\u4E3A\u5DF1\u62A5\u4EC7\uFF0C\u9634\u957F\u91CE\u751A\u81F3\u5C06\u5168\u90E8\u771F\u5143\u90FD\u6E21\u7ED9\u4E86\u7D2B\u73AB\u3002</p><p>\u300C\u8D31\u5A4A\u5B50\uFF0C\u4E00\u8D77\u53BB\u6B7B\u5427\uFF01\u300D\u4ED6\u8FD9\u6837\u5492\u9A82\u9053\u3002</p><p>\u867D\u7136\u4E0D\u6E05\u695A\u5176\u4E2D\u7684\u66F2\u6298\uFF0C\u4F46\u529F\u529B\u7684\u6062\u590D\u5374\u5B9E\u5B9E\u5728\u5728\u3002\u7D2B\u73AB\u547C\u5438\u6025\u4FC3\u8D77\u6765\u3002\u8FD9\u4E00\u5207\u5E76\u4E0D\u662F\u68A6\uFF0C\u800C\u662F\u671F\u5F85\u591A\u65E5\u7684\u5947\u8FF9\u7EC8\u4E8E\u51FA\u73B0\u3002</p><p>\u5F53\u988A\u4E0A\u6FC0\u52A8\u7684\u8273\u7EA2\u6E10\u6E10\u892A\u53BB\u3002\u7D2B\u73AB\u76D8\u819D\u800C\u5750\uFF0C\u6C89\u5FC3\u9759\u6C14\uFF0C\u5C55\u5F00\u5185\u7701\u4E4B\u672F\u63A2\u7A76\u81EA\u5DF1\u5185\u529F\u7684\u8FDB\u5EA6\u3002</p><p>\u7D2B\u73AB\u8138\u4E0A\u9759\u82E5\u6B62\u6C34\uFF0C\u5FC3\u91CC\u5374\u6380\u8D77\u6ED4\u5929\u5DE8\u6D6A\u3002\u771F\u6C14\u7565\u4E00\u8FD0\u8F6C\uFF0C\u5979\u7ACB\u65F6\u4FBF\u77E5\u9053\u81EA\u5DF1\u4E0D\u77E5\u4E0D\u89C9\u4E2D\u63A5\u8FDE\u7A81\u7834\uFF0C\u5DF2\u7ECF\u8D85\u8D8A\u51E4\u51F0\u5B9D\u5178\u7B2C\u4E03\u5C42\u51E4\u9E23\u671D\u9633\uFF0C\u6500\u81F3\u5E08\u7236\u6570\u5341\u5E74\u82E6\u7EC3\u624D\u8270\u9669\u5706\u529F\u7684\u7B2C\u516B\u5C42\u51E4\u51F0\u4E8E\u98DE\u3002</p><p>\u5979\u4E0D\u77E5\u9053\u81EA\u5DF1\u6B64\u65F6\u5468\u8EAB\u7EA2\u5149\u95EA\u52A8\uFF0C\u90A3\u79CD\u70BD\u70ED\u7684\u6C14\u606F\u4E0E\u5F53\u65E5\u5927\u5C55\u795E\u5A01\u7684\u96EA\u5CF0\u795E\u5C3C\u4E00\u822C\u65E0\u4E8C\u3002\u800C\u4E24\u8005\u7684\u5DEE\u5F02\uFF0C\u4EC5\u4EC5\u662F\u5979\u7684\u529F\u529B\u5C1A\u6D45\uFF0C\u4E0D\u53CA\u795E\u5C3C\u7684\u6D51\u539A\u800C\u5DF2\u3002</p><p>\u6700\u521D\u7684\u559C\u60A6\u8FC7\u53BB\u4E4B\u540E\uFF0C\u7D2B\u73AB\u6162\u6162\u6536\u529F\u3002\u518D\u611A\u7B28\u7684\u4EBA\u4E5F\u4F1A\u660E\u767D\uFF0C\u80FD\u516B\u4E2A\u6708\u5185\u5C31\u80FD\u8FBE\u5230\u96EA\u5CF0\u795E\u5C3C\u82E6\u4FEE\u591A\u5E74\u7684\u5883\u754C\uFF0C\u4E0E\u6155\u5BB9\u9F99\u7684\u9634\u9633\u5408\u6D4E\u5173\u7CFB\u6781\u5927\u3002</p><p>\u60F3\u8D77\u81EA\u5DF1\u5728\u4EA4\u5408\u4E2D\u505A\u4F5C\u7684\u5A9A\u6001\uFF0C\u7D2B\u73AB\u51C4\u7136\u4E00\u7B11\uFF0C\u62B9\u53BB\u773C\u89D2\u7684\u6CEA\u6C34\uFF0C\u300C\u539F\u6765\u4E0D\u8981\u8138\u4E5F\u662F\u6709\u597D\u5904\u7684\u2026\u2026\u300D</p><p>\u4E0E\u6155\u5BB9\u9F99\u76F8\u6BD4\uFF0C\u4ECE\u5C0F\u7531\u540D\u5E08\u6307\u70B9\u7684\u7D2B\u73AB\u6839\u57FA\u8FDC\u8FC7\u4E8E\u9760\u91C7\u8865\u4E3A\u4E3B\u7684\u54E5\u54E5\u3002\u6155\u5BB9\u9F99\u4E5F\u662F\u6DF1\u77E5\u6B64\u4E8B\uFF0C\u56E0\u6B64\u4E0D\u987E\u4E00\u5207\u5730\u5F00\u59CB\u4FEE\u70BC\u8FD8\u5929\u8BC0\uFF0C\u4EE5\u5F25\u8865\u5185\u529F\u7684\u7F3A\u61BE\u3002</p><p>\u4E24\u4EBA\u6BCF\u65E5\u4EA4\u5408\uFF0C\u5F7C\u6B64\u4EA4\u6362\u771F\u5143\uFF0C\u529F\u529B\u5C1A\u6D45\u7684\u7D2B\u73AB\u540C\u65F6\u5C11\u4E86\u8BB8\u591A\u684E\u688F\uFF0C\u56E0\u6B64\u6240\u5F97\u7684\u76CA\u5904\u66F4\u80DC\u4E8E\u6155\u5BB9\u9F99\u3002</p><p>\u4F46\u5185\u529F\u4E00\u662F\u5FC3\u6CD5\u8FDB\u5883\uFF0C\u4E00\u662F\u82E6\u4FEE\u79EF\u7D2F\uFF0C\u4E24\u8005\u7F3A\u4E00\u4E0D\u53EF\u3002\u5C31\u50CF\u7D2B\u73AB\u6B64\u65F6\u540C\u6837\u8FDB\u5165\u51E4\u51F0\u5B9D\u5178\u7B2C\u516B\u5C42\uFF0C\u4F46\u53EA\u5728\u4EA4\u5408\u4E2D\u88AB\u52A8\u4FEE\u70BC\u7684\u771F\u6C14\u8FDC\u8FDC\u4E0D\u5982\u96EA\u5CF0\u795E\u5C3C\u3002\u6BD4\u8D77\u4FEE\u4E60\u592A\u4E00\u7ECF\u6709\u6210\u7684\u6155\u5BB9\u9F99\u4E5F\u662F\u96BE\u4EE5\u4F01\u53CA\u3002\u4F46\u9634\u957F\u91CE\u7684\u771F\u5143\u5374\u5F25\u8865\u4E86\u8FD9\u65B9\u9762\u7684\u4E0D\u8DB3\u3002</p><p>\u7D2B\u73AB\u8EAB\u968F\u610F\u52A8\uFF0C\u8F7B\u98D8\u98D8\u7ED5\u7740\u77F3\u5BA4\u8F6C\u4E86\u4E00\u5468\u3002\u5047\u5982\u8BA9\u6155\u5BB9\u9F99\u76EE\u7779\u6B64\u666F\uFF0C\u5B9A\u7136\u4F1A\u540E\u6094\u6CA1\u6709\u628A\u5979\u7684\u4E73\u623F\u589E\u5F97\u66F4\u5927\u3002</p><p>\u72C2\u559C\u4E4B\u540E\uFF0C\u4E45\u79EF\u7684\u6068\u610F\u548C\u4EC7\u6028\u8D8A\u6765\u53D1\u8D8A\u76DB\uFF0C\u5FC3\u5E95\u6740\u6C14\u6E10\u5389\u3002\u770B\u6155\u5BB9\u9F99\u4E0E\u9634\u957F\u91CE\u4EA4\u624B\u7684\u60C5\u5F62\uFF0C\u8FD9\u6DF7\u86CB\u529F\u529B\u4E5F\u662F\u5927\u8FDB\uFF0C\u82E5\u8981\u529B\u654C\u53EA\u6015\u96BE\u4EE5\u53D6\u80DC\u3002</p><p>\u90A3\u5C31\u5077\u88AD\u5427\u3002</p><p>\u7D2B\u73AB\u5AE3\u7136\u4E00\u7B11\uFF0C\u5C06\u79C0\u53D1\u8F7B\u8F7B\u64A9\u5230\u8033\u540E\uFF0C\u90A3\u79CD\u5A07\u4FCF\u7684\u795E\u60C5\uFF0C\u8C01\u4E5F\u770B\u4E0D\u51FA\u5979\u6B63\u51C6\u5907\u6740\u6389\u81EA\u5DF1\u5AE1\u4EB2\u54E5\u54E5\u2014\u2014\u540C\u65F6\u4E5F\u662F\u6709\u5408\u4F53\u4E4B\u6B22\u7684\u4E08\u592B\u548C\u809A\u91CC\u5B69\u5B50\u7684\u7236\u4EB2\u3002</p>',78);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
