import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_12" tabindex="-1"><a class="header-anchor" href="#_12" aria-hidden="true">#</a> 12</h1><p>\u5F53\u591C\u592B\u59BB\u4FE9\u8F6E\u6D41\u7ED9\u6155\u5BB9\u7D2B\u73AB\u8F93\u6C14\u7597\u4F24\u3002\u6155\u5BB9\u80DC\u867D\u7136\u5E74\u7EAA\u4E0D\u8FC7\u4E8C\u5341\u4F59\u5C81\uFF0C\u4F46\u5929\u8D44\u6781\u9AD8\uFF0C\u6B66\u529F\u51FA\u7C7B\u62D4\u8403\uFF0C\u5728\u6C5F\u6E56\u4E2D\u7684\u540D\u58F0\u8FDC\u8FC7\u4E8E\u6DF1\u85CF\u4E0D\u9732\u7684\u7236\u4EB2\u6155\u5BB9\u536B\u3002\u4ED6\u4E0E\u96EA\u5CF0\u795E\u5C3C\u5F1F\u5B50\u6797\u9999\u8FDC\u8054\u59FB\u7684\u6D88\u606F\u4E00\u4F20\u51FA\uFF0C\u7ACB\u523B\u5F15\u8D77\u8F70\u52A8\uFF0C\u56E0\u6B64\u4F0F\u9F99\u6DA7\u624D\u6709\u90A3\u4E48\u591A\u5BBE\u5BA2\u3002\u592B\u59BB\u4FE9\u6B22\u6B22\u559C\u559C\u7684\u5E76\u9A91\u5357\u4E0B\uFF0C\u6CA1\u60F3\u5230\u521A\u5230\u6797\u5BB6\uFF0C\u59B9\u59B9\u5C31\u8FFD\u4E86\u8FC7\u6765\uFF0C\u800C\u4E14\u8EAB\u8D1F\u91CD\u4F24\u3002</p><p>\u4E00\u8DEF\u98A0\u7C38\uFF0C\u7D2B\u73AB\u7684\u4F24\u52BF\u6108\u53D1\u6C89\u91CD\uFF0C\u76F4\u5230\u9ECE\u660E\u65F6\u5206\uFF0C\u5979\u624D\u7741\u5F00\u773C\u775B\uFF0C\u5FAE\u5F31\u7684\u53EB\u4E86\u58F0\uFF1A\u300C\u4E8C\u5E08\u59D0\u2026\u2026\u300D</p><p>\u6797\u9999\u8FDC\u70B9\u4E86\u70B9\u5934\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u522B\u8BF4\u8BDD\uFF0C\u4F60\u54E5\u54E5\u6B63\u5728\u7ED9\u4F60\u7597\u4F24\u3002\u300D</p><p>\u6155\u5BB9\u80DC\u4E24\u624B\u6309\u5728\u7D2B\u73AB\u80CC\u5FC3\uFF0C\u5934\u9876\u5192\u51FA\u7F15\u7F15\u767D\u6C14\u3002\u4E0D\u591A\u65F6\u7A97\u5916\u4F20\u6765\u4E00\u58F0\u9E21\u9E23\uFF0C\u4ED6\u7F13\u7F13\u6536\u529F\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u54E5\u54E5\u3001\u5AC2\u5AC2\u90FD\u5728\u8FD9\u91CC\uFF0C\u4F60\u653E\u5FC3\u4F11\u606F\u4E00\u4F1A\u513F\uFF0C\u6709\u4EC0\u4E48\u4E8B\u665A\u4E9B\u518D\u8BF4\u3002\u300D</p><p>\u7D2B\u73AB\u56F0\u5026\u6B32\u6B7B\uFF0C\u53EF\u8FD9\u4E9B\u5929\u6240\u53D7\u7684\u59D4\u5C48\u3001\u75DB\u82E6\u6897\u5728\u5FC3\u5934\uFF0C\u4E0D\u7531\u773C\u5708\u53D1\u7EA2\uFF0C\u6251\u5230\u6155\u5BB9\u80DC\u6000\u4E2D\u653E\u58F0\u5927\u54ED\uFF0C\u300C\u7239\u2026\u2026\u7239\u88AB\u4ED6\u4EEC\u6740\u6B7B\u4E86\u2026\u2026\u5A18\u4E5F\u88AB\u63B3\u8D70\u4E86\u2026\u2026\u300D</p><p>\u6155\u5BB9\u80DC\u864E\u8EAF\u4E00\u9707\uFF0C\u76EE\u4E2D\u7CBE\u5149\u5927\u76DB\uFF0C\u559D\u9053\uFF1A\u300C\u600E\u4E48\u56DE\u4E8B\uFF1F\u8C01\u4E0B\u7684\u624B\uFF1F\u300D</p><p>\u6797\u9999\u8FDC\u628A\u4ED6\u53D1\u98A4\u7684\u5927\u624B\u5408\u5728\u81EA\u5DF1\u6E29\u8F6F\u7684\u638C\u5FC3\u91CC\u63E1\u7D27\uFF0C\u5F85\u7D2B\u73AB\u8BF4\u5B8C\uFF0C\u5979\u7ACB\u5373\u8D77\u8EAB\u6574\u7406\u884C\u88C5\u3002</p><p>\u81EA\u5DF1\u8FC7\u95E8\u4E0D\u53CA\u4E00\u6708\uFF0C\u516C\u516C\u8EAB\u6B7B\uFF0C\u5A46\u5A46\u88AB\u63B3\uFF0C\u5C0F\u59D1\u4E5F\u8EAB\u8D1F\u91CD\u4F24\uFF0C\u8FD9\u4E2A\u95FB\u6240\u672A\u95FB\u7684\u661F\u6708\u6E56\u662F\u7A76\u7ADF\u4F55\u65B9\u5996\u5B7D\uFF0C\u7ADF\u7136\u8FD9\u4E48\u5389\u5BB3\uFF1F\u8FD8\u6709\u4E09\u5E08\u59B9\u7EAA\u7709\u59A9\u3002\u5979\u6B66\u529F\u867D\u7136\u4E0D\u53CA\u81EA\u5DF1\uFF0C\u4F46\u98D8\u6885\u5CF0\u5F1F\u5B50\u5C82\u662F\u6613\u4E0E\u4E4B\u8F88\uFF1F\u5979\u7684\u7275\u4E1D\u624B\u662F\u5E08\u95E8\u4E00\u7EDD\uFF0C\u7B49\u95F2\u6C5F\u6E56\u4E2D\u4EBA\u6839\u672C\u4E0D\u662F\u5BF9\u624B\u3002\u4E24\u4F4D\u5E08\u59B9\u8054\u624B\uFF0C\u5374\u88AB\u4E00\u4E2A\u7CDF\u8001\u5934\u5B50\u8D64\u624B\u7A7A\u62F3\u6253\u6210\u91CD\u4F24\u2026\u2026</p><p>\u6155\u5BB9\u80DC\u6068\u4E0D\u5F97\u7ACB\u523B\u56DE\u5230\u4F0F\u9F99\u6DA7\uFF0C\u67E5\u627E\u654C\u4EBA\u7684\u7EBF\u7D22\uFF0C\u7ED9\u7236\u4EB2\u62A5\u4EC7\u3002\u6797\u9999\u8FDC\u4E5F\u662F\u5FE7\u5FC3\u5982\u711A\uFF0C\u53EF\u7D2B\u73AB\u4F24\u52BF\u672A\u7652\uFF0C\u628A\u5979\u4E00\u4E2A\u4EBA\u7559\u5728\u8FD9\u91CC\u65E2\u4E0D\u5B89\u5168\u53C8\u4E0D\u653E\u5FC3\u3002\u5982\u679C\u4E24\u4EBA\u5206\u8DEF\u800C\u884C\uFF0C\u4E00\u65B9\u9762\u592B\u59BB\u4FE9\u6B63\u5982\u80F6\u4F3C\u6F06\uFF0C\u820D\u4E0D\u5F97\u5206\u5F00\uFF0C\u53E6\u4E00\u65B9\u9762\u5373\u4F7F\u8FFD\u4E0A\u6C90\u58F0\u4F20\uFF0C\u4E00\u4EBA\u4E4B\u529B\u96BE\u6709\u505A\u4E3A\u3002</p><p>\u5546\u8BAE\u7247\u523B\uFF0C\u4E24\u4EBA\u51B3\u5B9A\u5148\u628A\u7D2B\u73AB\u9001\u5230\u98D8\u6885\u5CF0\uFF0C\u7136\u540E\u518D\u8054\u8882\u5317\u4E0A\uFF0C\u867D\u7136\u4E00\u6765\u4E00\u56DE\u8981\u803D\u8BEF\u534A\u4E2A\u6708\u7684\u65F6\u95F4\uFF0C\u4F46\u8981\u5B89\u5168\u8BB8\u591A\u3002</p><p>\u7D2B\u73AB\u5FC3\u6025\u7740\u8981\u89C1\u5E08\u7236\uFF0C\u6267\u610F\u7ACB\u523B\u542F\u7A0B\u3002\u7ECF\u8FC7\u4E00\u591C\u6551\u6CBB\uFF0C\u5979\u7684\u4F24\u52BF\u5DF2\u7ECF\u5927\u6709\u597D\u8F6C\uFF0C\u6797\u9999\u8FDC\u4E5F\u6CA1\u6709\u76F8\u529D\uFF0C\u4E09\u4EBA\u5306\u5306\u79BB\u5F00\u4E34\u909B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u98D8\u6885\u5CF0\u5EA7\u843D\u4E8E\u5341\u4E07\u5927\u5C71\u4E4B\u4E2D\uFF0C\u5CF0\u4E0A\u5E38\u5E74\u79EF\u96EA\uFF0C\u751F\u6709\u4E07\u682A\u7EA2\u6885\u3002\u81EA\u767E\u4F59\u5E74\u524D\u5F00\u5C71\u5E08\u7956\u5728\u6B64\u7ACB\u6D3E\uFF0C\u5230\u96EA\u5CF0\u795E\u5C3C\u5DF2\u7ECF\u4F20\u4E86\u56DB\u4EE3\u3002</p><p>\u56DB\u4EE3\u4E2D\u98D8\u6885\u5CF0\u4E00\u8109\u5355\u4F20\uFF0C\u95E8\u4E0B\u5F1F\u5B50\u90FD\u843D\u53D1\u4E3A\u5C3C\u3002\u96EA\u5CF0\u795E\u5C3C\u6614\u5E74\u4E91\u6E38\u56DB\u6D77\uFF0C\u773C\u89C1\u5929\u4E0B\u7EB7\u6518\uFF0C\u751F\u7075\u837C\u70AD\uFF0C\u5FC3\u751F\u4E0D\u5FCD\u3002\u56E0\u6B64\u5979\u63A5\u638C\u95E8\u6237\u540E\u6253\u7834\u89C4\u77E9\uFF0C\u6536\u4E86\u56DB\u540D\u4FD7\u5BB6\u5F1F\u5B50\u3002\u5E0C\u671B\u5979\u4EEC\u80FD\u4EE5\u6B66\u529F\u6276\u5371\u6D4E\u56F0\u3002\u5979\u7684\u5F1F\u5B50\u4E5F\u4E0D\u8D1F\u5E08\u671B\uFF0C\u98CE\u665A\u534E\u4E0E\u6797\u9999\u8FDC\u90FD\u662F\u540D\u9707\u6C5F\u6E56\u7684\u4FA0\u5973\u3002</p><p>\u56DB\u5929\u540E\u4E09\u4EBA\u8FDB\u5165\u5DDD\u5357\u3002\u6B64\u65F6\u6B63\u503C\u4E09\u6708\uFF0C\u8FDE\u7EF5\u4E0D\u65AD\u7684\u7FA4\u5C71\u7EFF\u610F\u521D\u840C\uFF0C\u718F\u98CE\u62C2\u8863\u6E29\u6DA6\u5982\u9189\u3002</p><p>\u4E00\u8DEF\u4E0A\u54E5\u5AC2\u4E0D\u60DC\u8017\u8D39\u771F\u5143\u7AED\u529B\u76F8\u52A9\uFF0C\u6155\u5BB9\u7D2B\u73AB\u7684\u4F24\u52BF\u8F7B\u4E86\u8BB8\u591A\uFF0C\u4F46\u6C90\u58F0\u4F20\u6570\u5341\u5E74\u7684\u529F\u529B\u975E\u540C\u5C0F\u53EF\uFF0C\u80CC\u4E0A\u65F6\u4E0D\u65F6\u8FD8\u9690\u9690\u4F5C\u75DB\u3002\u8FD9\u8FD8\u662F\u6C90\u58F0\u4F20\u811A\u4E0B\u7559\u60C5\uFF0C\u56E0\u4E3A\u5BAB\u4E3B\u6709\u547D\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5FC5\u987B\u6D3B\u6349\uFF0C\u624D\u6CA1\u6709\u8E22\u65AD\u5979\u7684\u810A\u690E\u3002</p><p>\u4E2D\u5348\u65F6\u5206\uFF0C\u4E09\u4EBA\u6765\u5230\u9E70\u5634\u5CE1\u3002\u5CE1\u8C37\u957F\u7EA6\u4E00\u91CC\uFF0C\u5165\u53E3\u53EA\u5BB9\u4E00\u4EBA\u8FDB\u5165\uFF0C\u91CC\u9762\u5374\u751A\u662F\u5BBD\u7545\uFF0C\u6B63\u50CF\u4E00\u53EA\u9E70\u5634\u3002\u7D2B\u73AB\u80CC\u4E0A\u53C8\u75BC\u4E86\u8D77\u6765\u3002\u5979\u4F0F\u5728\u9A6C\u80CC\u4E0A\u6697\u81EA\u8C03\u606F\uFF0C\u8138\u4E0A\u5374\u5E26\u7740\u7B11\u5BB9\uFF0C\u300C\u77A7\uFF0C\u6843\u82B1\u90FD\u5F00\u4E86\u3002\u300D</p><p>\u6797\u9999\u8FDC\u770B\u51FA\u7AEF\u502A\uFF0C\u5FC3\u4E0B\u6697\u53F9\uFF0C\u987A\u7740\u5979\u7684\u5C0F\u624B\u770B\u4E86\u8FC7\u53BB\u3002\u5CED\u58C1\u4E0A\u679C\u7136\u751F\u7740\u4E00\u68F5\u866C\u5C48\u7684\u6843\u6811\uFF0C\u5AE9\u7EFF\u7684\u679D\u6860\u95F4\u4E00\u6735\u6843\u82B1\u8010\u4E0D\u4F4F\u6027\u5B50\u62A2\u5148\u4E00\u6B65\u6084\u7136\u5410\u9732\u82AC\u82B3\u3002</p><p>\u300C\u6C5F\u5317\u6DEE\u5357\u95F4\u6709\u4E2A\u6843\u5E84\uFF0C\u6BCF\u5E74\u6625\u5B63\u6843\u82B1\u4F3C\u6D77\uFF0C\u843D\u4E0B\u7684\u82B1\u74E3\u50CF\u96EA\u4E00\u6837\uFF0C\u53C8\u9999\u53C8\u6DF1\u3002\u7D2B\u73AB\uFF0C\u5230\u65F6\u54B1\u4EEC\u4E00\u540C\u53BB\u770B\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u773C\u5149\u4ECE\u90A3\u6735\u5C06\u5F00\u672A\u5F00\u7684\u6843\u82B1\u4E0A\u6536\u4E86\u56DE\u6765\uFF0C\u60A0\u7136\u795E\u5F80\u5730\u8BF4\u9053\uFF1A\u300C\u50CF\u96EA\u4E00\u6837\u554A\u2026\u2026\u300D</p><p>\u6797\u9999\u8FDC\u6B63\u5F85\u5F00\u53E3\uFF0C\u7A81\u7136\u5FC3\u751F\u8B66\u5146\u3002</p><p>\u5C71\u8DEF\u65C1\u7684\u5D16\u4E0A\u7ACB\u7740\u4E00\u4E2A\u52BF\u82E5\u731B\u864E\u7684\u5927\u6C49\u3002\u4ED6\u6EE1\u9762\u866C\u9AEF\uFF0C\u989D\u7F20\u94F6\u5E26\uFF0C\u62AB\u6563\u7684\u957F\u53D1\u5728\u98CE\u4E2D\u730E\u730E\u98DE\u821E\u3002\u94F6\u767D\u8272\u7684\u957F\u888D\u633D\u5728\u8170\u95F4\uFF0C\u8863\u8896\u9AD8\u9AD8\u634B\u8D77\uFF0C\u8155\u4E0A\u5E26\u7740\u91D1\u707F\u707F\u7684\u62A4\u8155\uFF0C\u957F\u5EA6\u76F4\u5230\u8098\u4E0A\uFF0C\u624B\u4E2D\u63E1\u7740\u4E00\u67C4\u94DC\u8F6E\u822C\u7684\u4E24\u9762\u5DE8\u65A7\u3002\u9AD8\u5927\u7684\u8EAB\u8EAF\u5145\u6EE1\u5A01\u6B66\u7684\u6C14\u52BF\uFF0C\u5728\u9633\u5149\u4E0B\u95EA\u95EA\u53D1\u5149\uFF0C\u72B6\u5982\u5929\u795E\u3002</p><p>\u4E09\u4EBA\u7ACB\u523B\u52D2\u9A6C\u505C\u6B65\uFF0C\u624B\u6309\u5175\u5203\u3002</p><p>\u300C\u5728\u4E0B\u91D1\u5F00\u7532\uFF0C\u661F\u6708\u6E56\u91D1\u5802\u957F\u8001\u3002\u300D\u90A3\u6761\u6C49\u5B50\u7684\u58F0\u97F3\u5E76\u4E0D\u54CD\u4EAE\uFF0C\u7D2B\u73AB\u4E24\u8033\u5374\u88AB\u9707\u5F97\u9690\u9690\u53D1\u9EBB\u3002</p><p>\u6155\u5BB9\u80DC\u7F13\u7F13\u62FF\u51FA\u65A9\u9A6C\u5200\uFF0C\u4ED6\u5BF9\u8FD9\u540D\u58F0\u52BF\u60CA\u4EBA\u7684\u5927\u654C\u4E1D\u6BEB\u4E0D\u6562\u6389\u4EE5\u8F7B\u5FC3\u3002\u6797\u9999\u8FDC\u63E1\u7740\u81EA\u5DF1\u7684\u5BD2\u6708\u5200\uFF0C\u5FC3\u795E\u5374\u653E\u5728\u8EAB\u540E\u3002</p><p>\u5CE1\u8C37\u5165\u53E3\u5904\u5192\u51FA\u51E0\u5341\u540D\u767D\u8863\u5E2E\u4F17\uFF0C\u5F53\u5148\u7684\u767D\u94F6\u9999\u4E3B\u6325\u624B\u6253\u51FA\u4E00\u679A\u94F6\u68AD\u3002\u6797\u9999\u8FDC\u770B\u4E5F\u4E0D\u770B\uFF0C\u53CD\u624B\u6311\u843D\u3002</p><p>\u91D1\u5802\u53E6\u4E00\u540D\u9999\u4E3B\u9752\u94DC\u4E3E\u8D77\u72FC\u7259\u68D2\u817E\u8EAB\u800C\u8D77\uFF0C\u671D\u6797\u9999\u8FDC\u80CC\u4E0A\u731B\u51FB\u8FC7\u53BB\u3002\u6797\u9999\u8FDC\u51E4\u76EE\u751F\u5BD2\uFF0C\u4E5F\u662F\u817E\u8EAB\u800C\u8D77\uFF0C\u51CC\u7A7A\u4EA4\u4E86\u4E00\u62DB\u3002\u91D1\u94C1\u4EA4\u9E23\u58F0\u54CD\u5F7B\u5CE1\u8C37\uFF0C\u9752\u94DC\u52BF\u5927\u62DB\u731B\u7684\u4E00\u51FB\u5C45\u7136\u88AB\u786C\u751F\u751F\u6321\u4E86\u56DE\u6765\u3002\u767D\u94F6\u8FDE\u5FD9\u62A2\u4E0A\uFF0C\u6325\u821E\u94F6\u67AA\u4E0E\u6797\u9999\u8FDC\u6218\u6210\u4E00\u56E2\u3002</p><p>\u91D1\u5802\u662F\u4E94\u884C\u95E8\u5B9E\u529B\u6700\u5F3A\u7684\u4E00\u95E8\uFF0C\u9664\u4E86\u767D\u94F6\u3001\u9752\u94DC\uFF0C\u8FD8\u6709\u9ED1\u94C1\u3001\u660E\u9521\u4E24\u540D\u9999\u4E3B\u5916\uFF0C\u6B64\u523B\u4E24\u4EBA\u6B63\u5E26\u7740\u5341\u4F59\u540D\u5E2E\u4F17\u5728\u524D\u62E6\u4F4F\u53BB\u8DEF\u3002</p><p>\u91D1\u5F00\u7532\u7387\u9886\u672C\u5802\u503E\u5DE2\u800C\u51FA\uFF0C\u5FD7\u5728\u5FC5\u5F97\u3002\u773C\u89C1\u6797\u9999\u8FDC\u4EE5\u4E00\u654C\u4E8C\u72B9\u5360\u4E0A\u98CE\uFF0C\u4E0D\u7531\u5FC3\u4E0B\u6697\u60CA\uFF0C\u4E00\u62A1\u5DE8\u65A7\uFF0C\u4ECE\u5D16\u4E0A\u98DE\u8EAB\u800C\u4E0B\u3002\u6155\u5BB9\u80DC\u7075\u5DE7\u7684\u7FFB\u4E86\u4E2A\u65A4\u6597\uFF0C\u907F\u5F00\u5DE8\u65A7\uFF0C\u65A9\u9A6C\u5200\u95EA\u7535\u822C\u65A9\u5411\u9ED1\u94C1\u9999\u4E3B\u8170\u95F4\u3002\u91D1\u5F00\u7532\u4E0D\u5F85\u53CC\u817F\u8E0F\u5730\uFF0C\u5DE8\u65A7\u4E00\u8F6C\uFF0C\u7531\u76F4\u5288\u53D8\u4E3A\u6A2A\u626B\uFF0C\u4ECD\u671D\u6155\u5BB9\u80DC\u80A9\u4E0A\u5288\u53BB\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u957F\u5251\u51FA\u9798\uFF0C\u7B56\u9A6C\u5411\u76F4\u5954\u91D1\u5F00\u7532\uFF0C\u540C\u65F6\u5C04\u51FA\u4E24\u679A\u5C0F\u5F29\u3002\u91D1\u5F00\u7532\u94DC\u65A7\u4E00\u7FFB\uFF0C\u50CF\u9762\u76FE\u724C\u822C\u6321\u5F00\u5F29\u77E2\uFF0C\u63A5\u7740\u5F20\u5F00\u5927\u624B\uFF0C\u6293\u5411\u7D2B\u73AB\u7684\u957F\u5251\u3002</p><p>\u82E5\u5728\u4EE5\u5F80\uFF0C\u7D2B\u73AB\u80AF\u5B9A\u4F1A\u8BD5\u8BD5\u4ED6\u7684\u5916\u529F\u7A76\u7ADF\u6709\u591A\u4E48\u5389\u5BB3\uFF0C\u7ADF\u6562\u8D64\u624B\u6765\u81EA\u5DF1\u7684\u957F\u5251\u3002</p><p>\u4F46\u5979\u51E0\u65E5\u524D\u521A\u5728\u6C90\u58F0\u4F20\u624B\u4E0B\u5403\u4E86\u5927\u4E8F\uFF0C\u6B64\u65F6\u4FBF\u5C0F\u5FC3\u8D77\u6765\u3002\u4E00\u62DB\u51E4\u51F0\u5C55\u7FC5\u633D\u51FA\u51E0\u6735\u5251\u82B1\uFF0C\u865A\u865A\u5B9E\u5B9E\u70B9\u5411\u91D1\u5F00\u7532\u80F8\u524D\u5927\u7A74\u3002</p><p>\u91D1\u5F00\u7532\u4E0D\u95EA\u4E0D\u907F\uFF0C\u53CD\u800C\u633A\u80F8\u8FCE\u5411\u5251\u950B\u3002\u300C\u53EE\u300D\u7684\u4E00\u58F0\u8106\u54CD\uFF0C\u5251\u950B\u523A\u5728\u91D1\u5802\u957F\u8001\u7684\u81BB\u4E2D\u7A74\u4E0A\u679C\u7136\u5982\u4E2D\u94C1\u77F3\u3002\u7D2B\u73AB\u8FD9\u4E00\u5251\u53EA\u662F\u63A2\u63A2\u865A\u5B9E\uFF0C\u89C1\u72B6\u7ACB\u5373\u6296\u8155\u523A\u5411\u91D1\u5F00\u7532\u7684\u53CC\u76EE\u3002\u91D1\u5F00\u7532\u773C\u775B\u4E00\u7728\u4E0D\u7728\uFF0C\u624B\u4E2D\u5DE8\u65A7\u72C2\u98CE\u822C\u63A0\u8D77\uFF0C\u76F4\u5288\u5251\u8EAB\u3002</p><p>\u7D2B\u73AB\u600E\u6562\u4E0E\u4ED6\u786C\u62FC\uFF1F\u8EAB\u5F62\u4E00\u6643\uFF0C\u6740\u5165\u62E6\u8DEF\u7684\u4EBA\u7FA4\u4E2D\u3002\u91D1\u5F00\u7532\u6B63\u5F85\u8FFD\u51FB\uFF0C\u5FFD\u542C\u8EAB\u540E\u4F20\u6765\u4E00\u58F0\u60E8\u53EB\uFF0C\u63A5\u7740\u5200\u98CE\u53CA\u4F53\u3002</p><p>\u6797\u9999\u8FDC\u5728\u6C5F\u6E56\u4E0A\u95EF\u8361\u5DF2\u4E45\uFF0C\u867D\u7136\u4E0E\u767D\u94F6\u3001\u9752\u94DC\u6597\u6210\u4E00\u56E2\uFF0C\u5374\u65F6\u523B\u7559\u610F\u7740\u53E6\u4E00\u8FB9\u7684\u60C5\u51B5\u3002\u5979\u4E0E\u6155\u5BB9\u80DC\u5FC3\u610F\u76F8\u901A\uFF0C\u90FD\u60F3\u5148\u5C3D\u91CF\u9664\u6389\u5176\u4F59\u9AD8\u624B\uFF0C\u518D\u5408\u529B\u4E0E\u91D1\u5F00\u7532\u8FD9\u4E2A\u5F3A\u654C\u6597\u4E0A\u4E00\u6597\u3002</p><p>\u53EF\u767D\u94F6\u3001\u9752\u94DC\u90FD\u662F\u8EAB\u7ECF\u767E\u6218\u7684\u9B54\u5BAB\u9AD8\u624B\uFF0C\u53EA\u4E0E\u5979\u6E38\u6597\uFF0C\u4F01\u56FE\u628A\u6797\u9999\u8FDC\u7F20\u4F4F\uFF0C\u8BA9\u957F\u8001\u5148\u6536\u62FE\u6155\u5BB9\u80DC\u3002\u6797\u9999\u8FDC\u51E0\u62DB\u4E00\u8FC7\uFF0C\u5DF2\u7ECF\u660E\u767D\u4ED6\u4EEC\u7684\u7528\u610F\uFF0C\u773C\u770B\u65E0\u6CD5\u901F\u6218\u901F\u51B3\uFF0C\u53EA\u597D\u65A9\u6740\u4E00\u540D\u5E2E\u4F17\uFF0C\u7A81\u8EAB\u5077\u88AD\u91D1\u5F00\u7532\u3002</p><p>\u91D1\u5F00\u7532\u65CB\u8EAB\u626C\u8D77\u5DE8\u65A7\uFF0C\u5FEB\u6377\u65E0\u4F26\u7684\u5288\u5728\u6797\u9999\u8FDC\u5F2F\u5200\u4E0A\u3002\u6797\u9999\u8FDC\u624B\u4E0A\u4E00\u9707\uFF0C\u8FDE\u5FD9\u8FD0\u529F\u62B5\u4F4F\u3002\u5DE8\u65A7\u5728\u91D1\u5F00\u7532\u624B\u4E2D\u5B9B\u5982\u4E00\u6839\u8F7B\u76C8\u7684\u7FBD\u6BDB\uFF0C\u91D1\u5149\u5DE6\u53F3\u7FFB\u98DE\uFF0C\u6BCF\u4E00\u4E0B\u90FD\u843D\u5728\u5200\u950B\u4E0A\u3002\u5230\u7B2C\u5341\u4E03\u62DB\uFF0C\u6797\u9999\u8FDC\u624B\u4E0A\u4E00\u677E\uFF0C\u5F2F\u5200\u5DF2\u88AB\u5DE8\u65A7\u51FB\u98DE\u3002</p><p>\u91D1\u5F00\u7532\u659C\u8EAB\u62A2\u4E0A\uFF0C\u6B63\u5F85\u5C01\u4F4F\u5979\u7684\u7A74\u9053\uFF0C\u5374\u4E0D\u6599\u6797\u9999\u8FDC\u53EA\u662F\u8BC8\u505A\u4E0D\u654C\uFF0C\u7EA4\u624B\u4E00\u626C\u629B\u51FA\u6570\u679A\u94A2\u9488\u3002\u6253\u5728\u81BB\u4E2D\u7A74\u4E0A\u7684\u4E24\u6839\u98DE\u9488\u88AB\u62A4\u5FC3\u94DC\u955C\u6321\u4F4F\u3002\u53E6\u4E24\u679A\u98DE\u9488\u76F4\u5C04\u773C\u4E2D\u3002</p><p>\u91D1\u5F00\u7532\u8FDE\u5FD9\u626C\u5934\uFF0C\u582A\u582A\u907F\u8FC7\u4E00\u679A\uFF0C\u5374\u88AB\u53E6\u4E00\u679A\u98DE\u9488\u659C\u659C\u5C04\u8FDB\u5DE6\u76EE\u3002\u4ED6\u751F\u6027\u608D\u52C7\uFF0C\u4E0D\u987E\u773C\u4E2D\u5267\u75DB\uFF0C\u4E00\u62F3\u51FB\u5728\u6797\u9999\u8FDC\u80F8\u53E3\u3002\u5C11\u5987\u5E94\u624B\u629B\u98DE\uFF0C\u8FD8\u672A\u843D\u5730\u5DF2\u88AB\u767D\u94F6\u3001\u9752\u94DC\u7262\u7262\u6293\u4F4F\u3002</p><p>\u6155\u5BB9\u80DC\u89C1\u59BB\u5B50\u88AB\u64D2\uFF0C\u66B4\u559D\u4E00\u58F0\uFF0C\u65A9\u9A6C\u5200\u5BD2\u5149\u5927\u76DB\uFF0C\u5288\u65AD\u660E\u9521\u9999\u4E3B\u7684\u4E09\u8282\u68CD\uFF0C\u4E00\u5200\u65A9\u4E0B\u4ED6\u7684\u5934\u9885\u3002\u63A5\u7740\u5200\u4EA4\u5DE6\u624B\uFF0C\u683C\u5F00\u9ED1\u94C1\u7684\u77ED\u621F\uFF0C\u53F3\u624B\u633D\u4F4F\u59B9\u59B9\u7684\u8170\u80A2\uFF0C\u5C06\u5979\u629B\u4E86\u51FA\u53BB\uFF0C\u559D\u9053\uFF1A\u300C\u5FEB\u8D70\uFF01\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u94F6\u7259\u4E00\u54AC\uFF0C\u63D0\u6C14\u8F7B\u8EAB\uFF0C\u811A\u5C16\u70B9\u5728\u5D16\u4E0A\uFF0C\u8F7B\u70DF\u822C\u9038\u51FA\u91CD\u56F4\u3002</p><p>\u767D\u94F6\u5E26\u7740\u5341\u4F59\u540D\u8F7B\u529F\u9AD8\u8D85\u7684\u5E2E\u4F17\u8854\u5C3E\u731B\u8FFD\uFF0C\u4F59\u4E0B\u4E94\u5341\u4F59\u4EBA\u7684\u5219\u56F4\u6210\u4E00\u56E2\uFF0C\u731B\u653B\u6155\u5BB9\u80DC\u3002\u6155\u5BB9\u80DC\u80CC\u9760\u7EDD\u58C1\uFF0C\u65A9\u9A6C\u5200\u89C1\u62DB\u62C6\u62DB\uFF0C\u6C89\u5FC3\u5E94\u6218\u3002</p><p>\u91D1\u5F00\u7532\u4E00\u628A\u62CE\u8D77\u6797\u9999\u8FDC\uFF0C\u5389\u559D\u9053\uFF1A\u300C\u653E\u4E0B\u5200\uFF01\u300D</p><p>\u6155\u5BB9\u80DC\u52BF\u82E5\u731B\u864E\uFF0C\u6325\u5200\u53C8\u4F24\u4E86\u4E00\u4EBA\u3002\u91D1\u5F00\u7532\u5931\u4E86\u5DE6\u76EE\uFF0C\u5FC3\u5934\u706B\u8D77\uFF0C\u6478\u51FA\u98DE\u9488\u62B5\u5728\u6797\u9999\u8FDC\u773C\u4E0A\uFF0C\u300C\u8D31\u5A4A\u5B50\uFF01\u6562\u523A\u778E\u8001\u5B50\u7684\u773C\u775B\uFF0C\u8001\u5B50\u628A\u4F60\u4E24\u4E2A\u62DB\u5B50\u90FD\u5E9F\u4E86\uFF01\u300D</p><p>\u6797\u9999\u8FDC\u9ED1\u767D\u5206\u660E\u7684\u7F8E\u76EE\u4E2D\u5BD2\u5149\u4F3C\u6C34\uFF0C\u6BEB\u4E0D\u793A\u5F31\u5730\u4E0E\u4ED6\u5BF9\u89C6\u3002</p><p>\u91D1\u5F00\u7532\u51B7\u54FC\u4E00\u58F0\uFF0C\u9510\u5229\u7684\u9488\u5C16\u5BF9\u51C6\u77B3\u5B54\uFF0C\u6BEB\u4E0D\u7559\u60C5\u5730\u523A\u8FDB\u79CB\u6C34\u822C\u7684\u79C0\u76EE\u3002</p><p>\u773C\u73E0\u4E00\u9635\u523A\u75DB\uFF0C\u9488\u5C16\u5347\u8D77\u4E00\u56E2\u7EA2\u4E91\uFF0C\u63A5\u7740\u4FBF\u9677\u5165\u65E0\u5C3D\u7684\u9ED1\u6697\u4E4B\u4E2D\u3002\u6797\u9999\u8FDC\u4E00\u58F0\u4E0D\u542D\uFF0C\u5634\u89D2\u5374\u4E0D\u7531\u62BD\u6410\u8D77\u6765\u3002\u4ECE\u4ECA\u5F80\u540E\uFF0C\u8272\u5F69\u7F24\u7EB7\u7684\u4E16\u754C\u5C31\u4E0E\u81EA\u5DF1\u7EDD\u7F18\uFF0C\u5269\u4E0B\u7684\u53EA\u6709\u9ED1\u6697\u2026\u2026\u5979\u4E0D\u77E5\u9053\u81EA\u5DF1\u9762\u4E34\u7684\u4E0D\u4EC5\u4EC5\u53EA\u662F\u9ED1\u6697\uFF0C\u800C\u662F\u6BD4\u9ED1\u6697\u66F4\u53EF\u6015\u7684\u5730\u72F1\u751F\u6D3B\u3002</p><p>\u9488\u5C16\u5728\u773C\u7403\u4E2D\u6405\u4E86\u6405\uFF0C\u518D\u62D4\u51FA\u65F6\uFF0C\u5BD2\u6708\u5200\u660E\u5A9A\u7075\u52A8\u7684\u5927\u773C\u5DF2\u6210\u4E86\u4E24\u6C6A\u8840\u6CCA\u3002</p><p>\u4E24\u679A\u98DE\u9488\u5206\u522B\u523A\u5728\u6797\u9999\u8FDC\u773C\u4E2D\uFF0C\u7EC6\u5C0F\u7684\u8840\u73E0\u4ECE\u9488\u5C3E\u6EF4\u6EF4\u843D\u4E0B\uFF0C\u50CF\u4E00\u4E32\u9C9C\u7EA2\u7684\u8840\u6CEA\u3002</p><p>\u91D1\u5F00\u7532\u8DC3\u4E0A\u5DE8\u77F3\uFF0C\u53EB\u9053\uFF1A\u300C\u6155\u5BB9\u80DC\uFF01\u77A7\u77A7\u54B1\u5BB6\u600E\u4E48\u64CD\u4F60\u778E\u773C\u7684\u8001\u5A46\uFF01\u300D</p><p>\u6155\u5BB9\u80DC\u9762\u6C89\u4F3C\u6C34\uFF0C\u5200\u950B\u4E00\u8F6C\uFF0C\u780D\u4E2D\u4E00\u4EBA\u7684\u5927\u817F\u3002\u90A3\u4EBA\u60E8\u53EB\u58F0\u4E2D\uFF0C\u91D1\u5F00\u7532\u626F\u788E\u6797\u9999\u8FDC\u7684\u8863\u886B\uFF0C\u5C06\u5979\u5A07\u5AE9\u7684\u8EAB\u4F53\u5E73\u644A\u5728\u5DE8\u77F3\u4E0A\uFF0C\u7136\u540E\u6258\u8D77\u96EA\u81C0\uFF0C\u633A\u8EAB\u800C\u5165\u3002</p>',52);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
