import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_120" tabindex="-1"><a class="header-anchor" href="#_120" aria-hidden="true">#</a> 120</h1><p>\u5341\u4E00\u6708\u4E8C\u5341\u4E00\u3002</p><p>\u591C\u95F4\u4E0B\u8D77\u4E86\u9E45\u6BDB\u5927\u96EA\uFF0C\u6574\u4E2A\u661F\u6708\u6E56\u90FD\u88AB\u5927\u96EA\u63A9\u76D6\uFF0C\u56DB\u4E0B\u7A7A\u65E0\u4EBA\u8FF9\uFF0C\u4E16\u5916\u6843\u6E90\u822C\u9759\u8C27\u5B89\u7965\u3002\u7D2B\u73AB\u63A9\u8D77\u6B66\u529F\uFF0C\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u8D70\u4E0B\u77F3\u9636\u3002</p><p>\u5C11\u5987\u62AB\u7740\u4E00\u5757\u7834\u5E03\uFF0C\u4ECD\u662F\u8EAC\u8170\u633A\u81C0\u7684\u59FF\u52BF\u3002\u80A1\u95F4\u7684\u9ECF\u6DB2\u5DF2\u7ECF\u51DD\u7ED3\u6210\u534A\u900F\u660E\u7684\u8584\u51B0\u3002</p><p>\u7D2B\u73AB\u8138\u82E5\u5BD2\u51B0\uFF0C\u76EE\u4E0D\u659C\u89C6\uFF0C\u5374\u628A\u4E00\u5207\u90FD\u6536\u5728\u773C\u5E95\u3002\u6B64\u65F6\u5979\u529F\u529B\u5DF2\u590D\uFF0C\u79BB\u4EA7\u671F\u8FD8\u6709\u4E24\u4E2A\u6708\u65F6\u95F4\u3002\u4E8E\u60C5\u4E8E\u7406\uFF0C\u90FD\u4E0D\u80FD\u518D\u7B49\uFF0C\u65E0\u8BBA\u5982\u4F55\u4E5F\u8981\u5728\u4ECA\u65E5\u9003\u79BB\u661F\u6708\u6E56\u3002\u4F46\u4EE5\u5979\u5F85\u4EA7\u4E4B\u8EAB\uFF0C\u6700\u591A\u53EA\u80FD\u6551\u8D70\u4E00\u4EBA\u3002</p><p>\u5A18\u521A\u751F\u80B2\uFF0C\u8EAB\u5B50\u865A\u5F31\uFF0C\u5355\u662F\u8FD9\u573A\u5927\u96EA\u4FBF\u4F1A\u8981\u4E86\u5979\u7684\u6027\u547D\uFF1B\u5927\u5E08\u59D0\u795E\u667A\u5C3D\u5931\uFF0C\u5373\u4F7F\u9003\u51FA\u53BB\u81EA\u5DF1\u4E5F\u65E0\u6CD5\u7167\u6599\uFF1B\u5AC2\u5AC2\u53D7\u7684\u6298\u78E8\u6700\u591A\uFF0C\u81EA\u7136\u8BE5\u628A\u5979\u6551\u8D70\uFF0C\u4F46\u662F\u4E00\u60F3\u8D77\u5E08\u7236\uFF0C\u7D2B\u73AB\u5FC3\u91CC\u5C31\u50CF\u9488\u523A\u4E00\u822C\u3002\u5E08\u7236\u4E3A\u6551\u81EA\u5DF1\u8EAB\u9677\u9B54\u7A9F\uFF0C\u518D\u591A\u7559\u4E00\u523B\u79BB\u6B7B\u4EA1\u5C31\u8FD1\u4E86\u4E00\u5206\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u6240\u53D7\u7684\u51CC\u8FB1\u592A\u8FC7\u9A87\u4EBA\uFF0C\u4E24\u4E2A\u6708\u95F4\u7D2B\u73AB\u53EA\u8FDC\u8FDC\u770B\u4E86\u5E08\u7236\u4E24\u6B21\uFF0C\u4ECE\u6765\u90FD\u4E0D\u6562\u9760\u8FD1\u3002\u8FD9\u6B21\u5979\u4E00\u76F4\u8D70\u5230\u680F\u8FB9\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u88AB\u4E00\u7FA4\u80AE\u810F\u7684\u80A5\u732A\u6324\u5728\u4E2D\u95F4\uFF0C\u8EAB\u4E0A\u4F24\u75D5\u7D2F\u7D2F\u3002\u5979\u95ED\u7740\u773C\uFF0C\u6298\u65AD\u7684\u624B\u811A\u88AB\u732A\u8E44\u8DF5\u8E0F\u5F97\u626D\u66F2\u7740\u3002\u80A9\u5934\u7684\u65E5\u6708\u94A9\u53C8\u88AB\u4EBA\u73A9\u4E50\u5730\u62D4\u51FA\u4E00\u534A\uFF0C\u8840\u8089\u7FFB\u5377\u3002\u9971\u53D7\u6467\u6B8B\u7684\u79D8\u5904\u63D2\u7740\u4E00\u6839\u6728\u9539\uFF0C\u4E00\u7AEF\u5361\u5728\u680F\u6746\u95F4\uFF0C\u4F7F\u5979\u9634\u961C\u633A\u8D77\u3002</p><p>\u6CEA\u6C34\u6A21\u7CCA\u4E86\u53CC\u773C\uFF0C\u7D2B\u73AB\u6309\u4F4F\u79EF\u96EA\u7684\u6728\u680F\uFF0C\u5F20\u53E3\u6B32\u547C\u3002</p><p>\u300C\u5C11\u3001\u5C11\u592B\u4EBA\uFF0C\u60A8\u3001\u60A8\u600E\u4E48\u6765\u4E86\uFF1F\u300D\u5582\u732A\u7684\u6742\u5F79\u4E0D\u77E5\u662F\u51B7\u662F\u614C\uFF0C\u7ED3\u7ED3\u5DF4\u5DF4\u8BF4\u7740\uFF0C\u4E00\u6B65\u4E00\u6ED1\u5730\u8DD1\u4E86\u8FC7\u6765\u3002</p><p>\u7D2B\u73AB\u6CA1\u6709\u4F5C\u58F0\u3002\u8FD9\u91CC\u79BB\u5C9B\u7F18\u4E0D\u8DB3\u4E24\u91CC\uFF0C\u4EE5\u5979\u73B0\u5728\u7684\u8F7B\u529F\u4E0D\u8FC7\u662F\u7247\u523B\u5DE5\u592B\u3002\u6E56\u9762\u5BBD\u6709\u4E94\u91CC\uFF0C\u62A2\u6761\u5C0F\u8239\u5212\u5230\u5CB8\u8FB9\u4E5F\u975E\u96BE\u4E8B\u2026\u2026</p><p>\u90A3\u6742\u5F79\u6D51\u7136\u4E0D\u77E5\u9053\u5C11\u592B\u4EBA\u6740\u5FC3\u6697\u8D77\uFF0C\u8D54\u7740\u7B11\u8138\u8BF4\uFF1A\u300C\u5C11\u3001\u5C11\u592B\u4EBA\u662F\u3001\u662F\u4E0D\u662F\u8981\u3001\u8981\u89C1\u5E08\u592A\uFF1F\u300D</p><p>\u7D2B\u73AB\u6C89\u9ED8\u7247\u523B\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u4E0D\u662F\u3002\u6211\u53EA\u662F\u8DEF\u8FC7\u3002\u300D</p><p>\u90A3\u6742\u5F79\u8FD8\u5F85\u518D\u8BF4\uFF0C\u5C11\u592B\u4EBA\u5DF2\u7ECF\u8F6C\u8EAB\u79BB\u5F00\u3002</p><p>\u300C\u81ED\u3001\u81ED\u5A4A\u5B50\uFF0C\u9539\u628A\u8212\u3001\u8212\u670D\u5427\uFF1F\u300D</p><p>\u96EA\u5CF0\u795E\u5C3C\u9ED8\u7136\u4E0D\u8BED\uFF0C\u773C\u89D2\u5374\u9690\u9690\u6E7F\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5C9B\u4E0A\u6212\u5907\u68EE\u4E25\uFF0C\u81EA\u4ECE\u5927\u529B\u5438\u7EB3\u90AA\u9053\u9AD8\u624B\u4E4B\u540E\uFF0C\u661F\u6708\u6E56\u5B9E\u529B\u5927\u589E\uFF0C\u5DF2\u662F\u4ECA\u975E\u6614\u6BD4\u3002\u9664\u4E86\u6C90\u58F0\u4F20\u3001\u7075\u7389\u3001\u5C60\u6000\u6C89\u3001\u8FD8\u6709\u5B89\u5B50\u5B8F\u7B49\u4EBA\uFF0C\u542C\u8BF4\u5BAB\u767D\u7FBD\u548C\u8D6B\u8FDE\u96C4\u4E5F\u90FD\u5728\u5BAB\u4E2D\u3002</p><p>\u81EA\u5DF1\u53EA\u6709\u4E00\u6B21\u673A\u4F1A\uFF0C\u82E5\u662F\u786C\u95EF\uFF0C\u53EA\u6015\u7EC8\u8EAB\u65E0\u6CD5\u9003\u51FA\u661F\u6708\u6E56\u3002</p><p>\u7D2B\u73AB\u54AC\u54AC\u7259\uFF0C\u6253\u5B9A\u4E3B\u610F\uFF1A\u5148\u5728\u5BAB\u91CC\u6740\u6389\u90A3\u4E2A\u6DF7\u86CB\uFF0C\u518D\u70E7\u6389\u795E\u6BBF\uFF0C\u8D81\u4E71\u6551\u8D70\u5E08\u7236\uFF0C\u7136\u540E\u5317\u4E0A\u6E05\u51C9\u5C71\uFF0C\u8BF7\u5927\u5B5A\u7075\u9E6B\u5BFA\u4E3B\u6301\u516C\u9053\uFF0C\u6551\u51FA\u6BCD\u4EB2\u3001\u5E08\u59D0\uFF0C\u5C06\u661F\u6708\u6E56\u65A9\u8349\u9664\u6839\uFF01</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u8FD9\u5EA7\u77F3\u5BAB\u8FDC\u4E0D\u6B62\u4F60\u770B\u5230\u7684\u89C4\u6A21\u3002\u300D\u6155\u5BB9\u9F99\u9053\u3002</p><p>\u508D\u665A\u51FA\u5173\u4E4B\u540E\uFF0C\u4ED6\u4FBF\u5524\u4E0A\u7D2B\u73AB\uFF0C\u5E26\u5979\u5230\u77F3\u7A9F\u6563\u6B65\u3002</p><p>\u6155\u5BB9\u9F99\u8F7B\u8F7B\u62E5\u7740\u7D2B\u73AB\u81C3\u80BF\u7684\u8170\u80A2\uFF0C\u7F13\u6B65\u800C\u884C\uFF0C\u300C\u661F\u6708\u6E56\u5728\u6B64\u7ACB\u6559\u5343\u6709\u4F59\u5E74\uFF0C\u59CB\u7EC8\u8425\u5EFA\u4E0D\u4F11\u3002\u4F20\u95FB\u8FD9\u4E0B\u9762\u8FD8\u6709\u4E2A\u5E9E\u5927\u7684\u5730\u5BAB\uFF0C\u53EA\u4E0D\u8FC7\u767E\u4F59\u5E74\u524D\u795E\u6559\u66FE\u906D\u5927\u53D8\uFF0C\u592A\u51B2\u5BAB\u4E3B\u4E0D\u5F97\u5DF2\u5C01\u95ED\u5730\u5BAB\uFF0C\u4E0E\u5927\u654C\u540C\u5F52\u4E8E\u5C3D\u3002\u7ED3\u679C\u5730\u5BAB\u518D\u65E0\u6CD5\u5F00\u542F\u3002\u300D\u4ED6\u6307\u7740\u541B\u5B57\u752C\u9053\uFF0C\u300C\u8FD9\u662F\u6570\u5341\u5E74\u524D\u65B0\u5EFA\u7684\u752C\u9053\uFF0C\u4E00\u76F4\u672A\u66FE\u5B8C\u5DE5\u3002\u300D</p><p>\u4E24\u4EBA\u76F8\u62E5\u800C\u884C\uFF0C\u7D2B\u73AB\u4E0D\u6562\u63D0\u6C14\u8FD0\u529F\uFF0C\u4E8E\u662F\u4E0D\u52A8\u58F0\u8272\u5730\u8BF4\uFF1A\u300C\u8FD9\u6761\u752C\u9053\u662F\u4E0D\u662F\u539F\u6765\u5C31\u6709\u7684\u5C71\u6D1E\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u6276\u7740\u5979\u5C0F\u5FC3\u5730\u7ED5\u5F00\u4E00\u5904\u4F4E\u6D3C\uFF0C\u300C\u8FD9\u91CC\u4ECE\u6765\u90FD\u6CA1\u5F00\u542F\u8FC7\uFF0C\u6211\u4E5F\u662F\u7B2C\u4E00\u6B21\u6765\u3002\u6CA1\u60F3\u9634\u957F\u91CE\u4F1A\u88AB\u56DA\u5728\u8FD9\u91CC\u3002\u300D</p><p>\u7D2B\u73AB\u8EAB\u5B50\u8F7B\u98A4\u4E00\u4E0B\u3002\u6155\u5BB9\u9F99\u8FDE\u5FD9\u5C94\u5F00\u8BDD\u9898\uFF1A\u300C\u770B\u8FD9\u5EA7\u77F3\u95E8\u7684\u6B3E\u5F0F\uFF0C\u53EA\u6015\u6709\u4E24\u4E09\u767E\u5E74\u3002\u770B\u6765\u8FD9\u6761\u752C\u9053\u7684\u94FA\u8BBE\u5E76\u975E\u5168\u662F\u65B0\u5EFA\u3002\u300D</p><p>\u7D2B\u73AB\u62AC\u5934\u770B\u53BB\uFF0C\u53EA\u89C1\u95E8\u6963\u4E0A\u9542\u7740\u4E00\u4E2A\u5C0F\u5C0F\u7684\u300C\u8FB0\u300D\u5B57\uFF0C\u4E0E\u5176\u4ED6\u752C\u9053\u7684\u6B3E\u5F0F\u4E00\u822C\u65E0\u4E8C\u3002\u5979\u5FC3\u91CC\u300C\u54AF\u767B\u300D\u4E00\u58F0\uFF0C\u5931\u58F0\u9053\uFF1A\u300C\u8FD9\u91CC\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u7591\u60D1\u5730\u770B\u4E86\u5979\u4E00\u773C\uFF0C\u300C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u7D2B\u73AB\u5FC3\u5FF5\u7535\u8F6C\uFF0C\u7701\u8D77\u7236\u4EB2\u8BF4\u7684\u5373\u4E0D\u662F\u300C\u7533\u300D\u4E5F\u5E76\u975E\u300C\u5DF3\u300D\uFF0C\u800C\u662F\u300C\u8FB0\u300D\uFF01</p><p>\u300C\u8FD9\u91CC\u2026\u2026\u7684\u82B1\u7EB9\u5F88\u6F02\u4EAE\uFF0C\u6211\u60F3\u8FDB\u53BB\u770B\u770B\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u773C\u5149\u5FAE\u5FAE\u95EA\u4E86\u4E00\u4E0B\uFF0C\u626D\u5F00\u95E8\u9501\u3002</p><p>\u770B\u5230\u90A3\u4E2A\u5F97\u4E4B\u4E0D\u6613\u7684\u7EB9\u9970\uFF0C\u7D2B\u73AB\u53CD\u800C\u5E73\u9759\u4E0B\u6765\u3002\u5979\u7528\u9497\u5C16\u52FE\u63CF\u7740\u523B\u75D5\uFF0C\u8BF4\u9053\uFF1A\u300C\u8FD9\u4E9B\u82B1\u7EB9\u597D\u7279\u522B\u2026\u2026\u300D\u5F53\u7740\u4ED6\u7684\u9762\u627E\u51FA\u5B9D\u85CF\u6700\u540E\u4E00\u4E2A\u5173\u952E\u7EBF\u7D22\uFF0C\u7D2B\u73AB\u5FC3\u91CC\u5374\u6CA1\u6709\u4E00\u4E1D\u5F97\u610F\u3002\u4E3A\u4E86\u5B83\uFF0C\u81EA\u5DF1\u4ED8\u51FA\u7684\u592A\u591A\u592A\u591A\u4E86\u3002</p><p>\u6155\u5BB9\u9F99\u9053\uFF1A\u300C\u8FD9\u662F\u2026\u2026\u300D</p><p>\u8BF4\u8BDD\u95F4\u9497\u5C16\u5DF2\u7ECF\u5212\u5230\u4E91\u9970\u6B63\u4E2D\u7684\u5C0F\u5B54\u5185\u3002\u8FD9\u6B21\u4F20\u6765\u7684\u4E0D\u662F\u6307\u5C16\u7684\u8F7B\u54CD\uFF0C\u800C\u662F\u811A\u4E0B\u7684\u4E00\u9635\u4F4E\u9E23\uFF0C\u72B9\u5982\u4E00\u53EA\u6D2A\u8352\u602A\u517D\u5728\u5730\u5C42\u6DF1\u5904\u53D1\u51FA\u6C89\u95F7\u7684\u5486\u54EE\uFF0C\u6574\u4E2A\u77F3\u5BAB\u90FD\u4E3A\u4E4B\u9707\u98A4\u3002</p><p>\u7D2B\u73AB\u8138\u8272\u96EA\u767D\uFF0C\u4E0D\u7B49\u5979\u53CD\u5E94\u8FC7\u6765\uFF0C\u6155\u5BB9\u9F99\u4E00\u53CC\u624B\u81C2\u5DF2\u7ECF\u95EA\u7535\u822C\u4F38\u6765\u2026\u2026\u4ED6\u53D1\u73B0\u4E86\uFF01</p><p>\u300C\u5C0F\u5FC3\uFF01\u300D\u6155\u5BB9\u9F99\u4E00\u628A\u62B1\u4F4F\u5413\u5446\u7684\u59B9\u59B9\uFF0C\u98DE\u4E5F\u4F3C\u7684\u63A0\u51FA\u77F3\u7A9F\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7D2B\u73AB\u8EBA\u5728\u6E29\u6CC9\u91CC\uFF0C\u7528\u6E29\u6696\u7684\u6CC9\u6C34\u5E73\u590D\u81EA\u5DF1\u7684\u60C5\u7EEA\u3002</p><p>\u521A\u624D\u7684\u9707\u52A8\u53EA\u662F\u4E00\u77AC\uFF0C\u77ED\u5F97\u8BA9\u4EBA\u4EE5\u4E3A\u8FD9\u53EA\u662F\u9519\u89C9\uFF0C\u90A3\u6DF7\u86CB\u8FDE\u95EE\u90FD\u6CA1\u95EE\u2014\u2014\u5927\u6982\u662F\u8FD9\u91CC\u7ECF\u5E38\u5730\u9707\u5427\u3002</p><p>\u7D2B\u73AB\u95ED\u7740\u773C\uFF0C\u6C89\u601D\u9053\uFF1A\u4E94\u95F4\u77F3\u5BA4\u90FD\u5DF2\u7ECF\u627E\u9F50\uFF0C\u5269\u4E0B\u7684\u5C31\u662F\u90A3\u4E2A\u300C\u4E2D\u300D\u4E86\u3002\u77F3\u5BAB\u7684\u4E2D\u5FC3\u53EA\u6709\u4E00\u4E2A\u592A\u6781\u56FE\u2014\u2014\u770B\u6765\u5B9D\u85CF\u7684\u5165\u53E3\u5C31\u662F\u5728\u592A\u6781\u56FE\u4E0B\u4E86\u3002</p><p>\u6C24\u6C32\u7684\u6C34\u96FE\u4E2D\uFF0C\u73AB\u7470\u4ED9\u5B50\u5A07\u9765\u5982\u82B1\uFF0C\u767D\u817B\u7684\u808C\u80A4\u5149\u6ED1\u5982\u8102\u3002\u5F85\u4EA7\u7684\u5C0F\u8179\u548C\u5C0F\u897F\u74DC\u822C\u7684\u80A5\u4E73\uFF0C\u5706\u6EDA\u6EDA\u9F13\u5728\u4ECD\u662F\u5C11\u5973\u6A21\u6837\u7EA4\u5DE7\u7684\u5A07\u8EAF\u4E0A\uFF0C\u4EFF\u4F5B\u4E09\u4E2A\u7A81\u5140\u7684\u5F02\u7269\u3002</p><p>\u7136\u800C\u8FD9\u79CD\u4E0D\u534F\u8C03\u7684\u7ED3\u5408\uFF0C\u5374\u6709\u79CD\u5F02\u4E4E\u5BFB\u5E38\u7684\u8273\u6001\uFF0C\u5C31\u50CF\u4E00\u4E2A\u7A1A\u5AE9\u7684\u5E7C\u5973\u633A\u7740\u6210\u4EBA\u7684\u4E73\u623F\u5356\u5F04\u98CE\u60C5\u3002\u66F4\u4EE4\u4EBA\u5FC3\u52A8\u7684\uFF0C\u5219\u662F\u5979\u8138\u4E0A\u65E0\u5948\u7684\u795E\u60C5\u2014\u2014\u5BF9\u4E8E\u5F3A\u5236\u589E\u4E73\u548C\u53D7\u5B55\u7684\u4E0D\u7518\u548C\u4E0D\u613F\u3002</p><p>\u7D2B\u73AB\u7741\u5F00\u773C\uFF0C\u5FC3\u5934\u50CF\u88AB\u9488\u523A\u822C\u5FAE\u5FAE\u4E00\u7A92\u3002</p><p>\u6155\u5BB9\u9F99\u9759\u6084\u6084\u770B\u7740\u5979\uFF0C\u773C\u775B\u53C8\u6DF1\u53C8\u4EAE\u3002</p><p>\u300C\u600E\u2026\u2026\u600E\u4E48\u4E86\uFF1F\u300D\u7D2B\u73AB\u4E00\u8138\u65E0\u8F9C\u5730\u8BF4\u3002</p><p>\u6155\u5BB9\u9F99\u76EE\u5149\u79FB\u5411\u5BA4\u89D2\uFF0C\u534A\u664C\u540E\u81EA\u5931\u5730\u4E00\u7B11\uFF0C\u8F7B\u8F7B\u8BF4\u9053\uFF1A\u300C\u4F60\u8FD8\u8981\u9A97\u6211\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u624B\u6307\u4E00\u98A4\uFF0C\u6CA1\u6709\u4F5C\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u51DD\u89C6\u7740\u5979\uFF0C\u7B2C\u4E00\u6B21\u6BEB\u65E0\u4FDD\u7559\u5730\u6D41\u9732\u51FA\u4E07\u822C\u67D4\u60C5\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u65B9\u624D\u662F\u6253\u5F00\u4E86\u5B9D\u85CF\u7684\u5165\u53E3\u5427\uFF1F\u300D</p><p>\u6E29\u6CC9\u7A81\u7136\u53D8\u5F97\u51B0\u51B7\uFF0C\u7D2B\u73AB\u5468\u8EAB\u7684\u8840\u6DB2\u90FD\u51DD\u4F4F\u4E86\u3002</p><p>\u6C89\u9ED8\u826F\u4E45\uFF0C\u6155\u5BB9\u9F99\u64A9\u8D77\u4E00\u6367\u6C34\u6CFC\u5728\u8138\u4E0A\uFF0C\u6CA1\u5934\u6CA1\u8111\u5730\u8BF4\u4E86\u4E00\u53E5\uFF0C\u300C\u6211\u53D7\u4E86\u5F88\u591A\u82E6\u3002\u300D</p><p>\u6C34\u73E0\u4ECE\u4FCA\u6717\u7684\u9762\u5B54\u4E0A\u6EF4\u6EF4\u6EDA\u843D\uFF0C\u4ED6\u4F4E\u58F0\u9053\uFF1A\u300C\u4ECE\u90A3\u65F6\u5019\u8D77\u6211\u5C31\u60F3\u53D8\u5F97\u5F88\u5F3A\uFF0C\u5F3A\u5F97\u53EF\u4EE5\u505A\u4EFB\u4F55\u60F3\u505A\u7684\u4E8B\u3002\u300D</p><p>\u300C\u6211\u4E00\u76F4\u5728\u60F3\u5A18\u3002\u8FD8\u6709\u4F60\u3002\u505A\u68A6\u90FD\u60F3\u77E5\u9053\u4F60\u4EEC\u8FC7\u5F97\u597D\u4E0D\u597D\u2026\u2026\u300D</p><p>\u300C\u2026\u2026\u662F\u4E0D\u662F\u5728\u60F3\u6211\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u8138\u4E0A\u6E7F\u6F09\u6F09\u7684\uFF0C\u4ED6\u626C\u9996\u6795\u5728\u6C60\u6CBF\uFF0C\u58F0\u97F3\u50CF\u98CE\u4E00\u6837\u8F7B\uFF0C\u300C\u4F60\u6BD4\u6211\u60F3\u50CF\u4E2D\u8FD8\u8981\u7F8E\uFF0C\u8FD8\u8981\u52A8\u4EBA\u2026\u2026\u770B\u7740\u4F60\u7684\u809A\u5B50\u4E00\u5929\u5929\u5927\u8D77\u6765\uFF0C\u4F60\u77E5\u9053\u6211\u6709\u591A\u9AD8\u5174\u5417\uFF1F\u300D</p><p>\u300C\u6211\u5E38\u5E38\u5BF9\u81EA\u5DF1\u8BF4\uFF0C\u4F60\u8FD8\u5C0F\uFF0C\u4E0D\u50CF\u5A18\u90A3\u6837\u61C2\u4E8B\uFF0C\u6211\u8981\u5BF9\u4F60\u597D\u4E00\u4E9B\uFF0C\u591A\u539F\u8C05\u4F60\u4E00\u4E9B\u3002\u7EC8\u7A76\u4F60\u4F1A\u50CF\u5A18\u4E00\u6837\uFF0C\u5F00\u5F00\u5FC3\u5FC3\u5730\u966A\u5728\u6211\u8EAB\u8FB9\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u8F7B\u8F7B\u4E00\u7B11\uFF0C\u300C\u8FD8\u8BB0\u5F97\u6211\u4EEC\u4E00\u5BB6\u4E09\u53E3\u5728\u8349\u539F\u4E0A\u9A91\u9A6C\u5417\uFF1F\u6211\u62B1\u7740\u4F60\uFF0C\u8FD8\u6709\u5A18\uFF0C\u5728\u8349\u6D77\u91CC\u8FFD\u9010\u843D\u65E5\u2014\u2014\u90A3\u662F\u6211\u8FD9\u4E00\u8F88\u5B50\u6700\u5F00\u5FC3\u7684\u65E5\u5B50\u4E86\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4F4E\u53F9\u4E00\u58F0\uFF0C\u5750\u8D77\u8EAB\u5B50\uFF0C\u300C\u4F60\u65E2\u7136\u4E0D\u60F3\u544A\u8BC9\u6211\u5B9D\u85CF\u6240\u5728\uFF0C\u90A3\u4E48\u54E5\u54E5\u7B54\u5E94\u4F60\uFF1A\u6211\u7EDD\u4E0D\u53BB\u770B\u4E00\u773C\u3002\u300D</p><p>\u4ED6\u6E38\u8FC7\u6765\uFF0C\u50CF\u5C55\u7FC5\u7684\u96C4\u9E70\uFF0C\u628A\u7D2B\u73AB\u5A07\u5C0F\u7684\u8EAB\u5B50\u5708\u5728\u81C2\u95F4\uFF0C\u58F0\u97F3\u5FAE\u98A4\u7740\u8BF4\u9053\uFF1A\u300C\u8FD9\u6837\u4F60\u4F1A\u7231\u6211\u5417\uFF1F\u300D</p><p>\u7D2B\u73AB\u6014\u6014\u770B\u7740\u4ED6\uFF0C\u534A\u664C\u540E\u51C4\u7136\u4E00\u7B11\uFF0C\u7EA2\u5507\u52A8\u4E86\u52A8\uFF0C\u7528\u5507\u5F62\u65E0\u58F0\u5730\u8BF4\u9053\uFF1A\u300C\u665A\u4E86\u3002\u300D\u63A5\u7740\u5468\u8EAB\u7EA2\u5149\u95EA\u52A8\uFF0C\u51E4\u51F0\u771F\u6C14\u72C2\u6D8C\u800C\u51FA\u3002</p><p>\u90A3\u5F20\u4EE4\u5979\u65E0\u6BD4\u618E\u6068\u7684\u4FCA\u8138\u5FFD\u7136\u53D8\u5F97\u82CD\u767D\uFF0C\u63A5\u7740\u662F\u523A\u76EE\u7684\u8165\u7EA2\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u53E3\u9C9C\u8840\u5C3D\u6570\u55B7\u5728\u5FC3\u7231\u7684\u59B9\u59B9\u8138\u4E0A\u3002\u731D\u7136\u53D7\u88AD\u4E0B\uFF0C\u82E6\u4FEE\u7684\u8FD8\u5929\u8BC0\u5A01\u529B\u5C3D\u663E\uFF0C\u786C\u751F\u751F\u53D7\u4E86\u7D2B\u73AB\u5341\u6210\u529F\u529B\u7684\u4E00\u638C\uFF0C\u7ADF\u7136\u8FD8\u6709\u4F59\u529B\u53CD\u51FB\u3002\u4ED6\u4E0D\u5047\u601D\u7D22\u5730\u4E3E\u81C2\u62B9\u5728\u7D2B\u73AB\u814B\u4E0B\uFF0C\u63A5\u7740\u5C48\u8D77\u819D\u76D6\u3002</p><p>\u8D2F\u6EE1\u592A\u4E00\u771F\u6C14\u7684\u4E24\u638C\u867D\u7136\u53EA\u65BD\u51FA\u4E03\u6210\u5A01\u529B\uFF0C\u4ECD\u9707\u788E\u4E86\u7D2B\u73AB\u7684\u62A4\u4F53\u771F\u6C14\uFF0C\u5979\u6A31\u53E3\u4E00\u5F20\uFF0C\u540C\u6837\u55B7\u51FA\u4E00\u80A1\u8840\u7BAD\u3002\u4E24\u4E2A\u6155\u5BB9\u6C0F\u5AE1\u8109\u7684\u9C9C\u8840\u4EA4\u5408\u800C\u6D41\uFF0C\u6DCB\u6DCB\u6F13\u6F13\u6D12\u6EE1\u6E05\u6C60\u3002</p><p>\u52B2\u98CE\u53CA\u4F53\uFF0C\u819D\u76D6\u5939\u7740\u51CC\u5389\u7684\u98CE\u58F0\u671D\u5706\u6EDA\u6EDA\u7684\u5C0F\u8179\u731B\u51FB\u8FC7\u6765\uFF0C\u6B64\u65F6\u7D2B\u73AB\u4E24\u624B\u8FD8\u5370\u5728\u6155\u5BB9\u9F99\u80F8\u53E3\uFF0C\u518D\u65E0\u529B\u62B5\u6321\u4ED6\u819D\u4E0A\u7684\u4E00\u51FB\u3002</p><p>\u773C\u770B\u5C31\u662F\u8179\u7A7F\u80A0\u65AD\u7684\u7ED3\u5C40\uFF0C\u819D\u76D6\u5374\u7A81\u7136\u505C\u4F4F\u4E86\u3002</p><p>\u7D2B\u73AB\u6293\u4F4F\u8FD9\u4E00\u77AC\u5373\u901D\u7684\u673A\u4F1A\uFF0C\u7EA4\u624B\u5E76\u6307\u5982\u5200\uFF0C\u300C\u5657\u300D\u7684\u4E00\u58F0\uFF0C\u4ECE\u6155\u5BB9\u9F99\u8170\u4FA7\u7A7F\u8FC7\u3002</p><p>\u6155\u5BB9\u9F99\u9759\u9759\u7ACB\u5728\u6C60\u4E2D\uFF0C\u6EDA\u70EB\u7684\u9C9C\u8840\u987A\u7740\u7D2B\u73AB\u7684\u624B\u81C2\u4E00\u80A1\u80A1\u843D\u5165\u6E05\u6F88\u7684\u6CC9\u6C34\uFF0C\u50CF\u4E00\u7C92\u7C92\u739B\u7459\u5728\u6C34\u4E2D\u6D6E\u6D6E\u6C89\u6C89\uFF0C\u6CA1\u6709\u4E00\u4E1D\u878D\u5316\u3002\u7D2B\u73AB\u7684\u9C9C\u8840\u4E5F\u662F\u4E00\u822C\uFF0C\u4F46\u4E24\u4EBA\u7684\u8840\u73E0\u4E00\u78B0\uFF0C\u4FBF\u7ACB\u5373\u5408\u6210\u4E00\u4F53\uFF0C\u518D\u4E0D\u5206\u5F7C\u6B64\u3002</p><p>\u6155\u5BB9\u9F99\u51DD\u89C6\u7740\u7D2B\u73AB\uFF0C\u773C\u4E2D\u5145\u6EE1\u4E86\u54C0\u4F24\uFF0C\u8FD8\u6709\u4E0D\u820D\u3002\u6700\u540E\u76EE\u5149\u505C\u5728\u7D2B\u73AB\u8179\u4E0A\u3002\u5B55\u80B2\u7740\u6155\u5BB9\u6C0F\u9AA8\u8840\u7684\u5C0F\u8179\u4E0A\uFF0C\u4E00\u53EA\u8840\u7EA2\u7684\u51E4\u51F0\u7EB9\u8EAB\uFF0C\u6B63\u5C55\u5F00\u7FC5\u8180\uFF0C\u98D8\u98D8\u6B32\u98DE\u3002</p><p>\u300C\u7559\u4E0B\u4ED6\u2026\u2026\u300D\u6155\u5BB9\u9F99\u52A8\u4E86\u52A8\u5634\u5507\uFF0C\u773C\u4E2D\u7684\u5149\u8292\u6E10\u6E10\u7184\u706D\u3002</p><p>\u7D2B\u73AB\u8F7B\u8F7B\u4E00\u62D4\uFF0C\u624B\u81C2\u8131\u51FA\uFF0C\u6155\u5BB9\u9F99\u6E10\u51B7\u7684\u8EAB\u4F53\u5411\u540E\u5012\u4E0B\uFF0C\u6E85\u8D77\u6F2B\u5929\u8840\u82B1\u3002</p>',70);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
