import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_121" tabindex="-1"><a class="header-anchor" href="#_121" aria-hidden="true">#</a> 121</h1><p>\u5931\u53BB\u751F\u547D\u7684\u9762\u5B54\u6C89\u5728\u6C34\u5E95\uFF0C\u6709\u4E00\u79CD\u82CD\u767D\u7684\u5E73\u9759\u3002\u4E00\u4E32\u7EC6\u5BC6\u6D51\u5706\u7684\u8840\u73E0\u4EFF\u4F5B\u51FA\u5DE2\u7684\u871C\u5CF0\uFF0C\u4ECE\u8170\u4FA7\u7684\u4F24\u53E3\u98DE\u51FA\uFF0C\u5728\u8EAB\u4F53\u5468\u56F4\u7FFB\u6EDA\u8D77\u4F0F\u3002</p><p>\u7EC8\u4E8E\u4EB2\u624B\u6740\u6389\u751F\u6B7B\u5927\u4EC7\uFF0C\u7D2B\u73AB\u5374\u6CA1\u6709\u4E1D\u6BEB\u559C\u60A6\uFF0C\u5FC3\u91CC\u53CD\u800C\u7A7A\u8361\u8361\uFF0C\u50CF\u4F17\u9E1F\u98DE\u5C3D\u7684\u96EA\u539F\u822C\u832B\u7136\u3002</p><p>\u300C\u5BAB\u4E3B\uFF01\u300D\u95E8\u5916\u4E00\u58F0\u60CA\u547C\u3002</p><p>\u6D51\u8EAB\u6D74\u8840\u7684\u7D2B\u73AB\u731B\u5730\u60CA\u9192\u8FC7\u6765\uFF0C\u7ACB\u5373\u817E\u8EAB\u800C\u8D77\uFF0C\u6EF4\u8840\u7684\u53F3\u638C\u53D1\u51FA\u4E00\u9053\u70BD\u70ED\u7684\u771F\u6C14\u3002</p><p>\u767D\u7389\u83BA\u9A87\u7136\u4E3E\u81C2\u5C01\u683C\uFF0C\u5979\u529F\u529B\u672C\u5C31\u4E0D\u53CA\u7D2B\u73AB\uFF0C\u6B64\u65F6\u66F4\u975E\u5BF9\u624B\uFF0C\u4E0E\u51E4\u51F0\u771F\u6C14\u4E00\u89E6\uFF0C\u624B\u81C2\u987F\u65F6\u6298\u65AD\u3002\u51CC\u5389\u7684\u771F\u6C14\u76F4\u5165\u7ECF\u8109\uFF0C\u767D\u7389\u83BA\u53E3\u5410\u9C9C\u8840\uFF0C\u8EAB\u5B50\u5012\u98DE\u51FA\u53BB\uFF0C\u7C89\u80CC\u91CD\u91CD\u8DCC\u5728\u77F3\u58C1\u4E0A\uFF0C\u6454\u5F97\u72FC\u72C8\u4E0D\u582A\u3002</p><p>\u867D\u7136\u4E00\u638C\u8FEB\u5F00\u767D\u7389\u83BA\uFF0C\u7D2B\u73AB\u80F8\u53E3\u4E5F\u75BC\u5982\u5200\u5272\u3002\u5979\u987E\u4E0D\u5F97\u53D6\u8FD9\u8D31\u5A62\u6027\u547D\uFF0C\u7ACB\u5373\u95EA\u8EAB\u63A0\u51FA\u77F3\u5BA4\u3002</p><p>\u767D\u7389\u9E42\u95FB\u58F0\u4ECE\u90BB\u5BA4\u62A2\u51FA\uFF0C\u6B63\u9047\u5230\u904D\u4F53\u7EA2\u5149\u7684\u73AB\u7470\u4ED9\u5B50\u5B9B\u5982\u4E00\u53EA\u8840\u8272\u51E4\u51F0\uFF0C\u75BE\u98DE\u800C\u81F3\u3002\u5979\u4E0D\u77E5\u8F7B\u91CD\uFF0C\u89C1\u59D0\u59D0\u53D7\u4F24\uFF0C\u7ACB\u5373\u6325\u624B\u76F4\u63D2\u7D2B\u73AB\u5C0F\u8179\u3002\u7D2B\u73AB\u604D\u82E5\u672A\u89C9\uFF0C\u6BEB\u4E0D\u505C\u987F\u5730\u5F84\u76F4\u63A0\u8FC7\u3002</p><p>\u767D\u7389\u9E42\u4E94\u6307\u5982\u94A9\uFF0C\u65BD\u51FA\u5341\u6210\u529F\u529B\uFF0C\u8981\u5728\u7D2B\u73AB\u8179\u4E0A\u638F\u51FA\u4E00\u4E2A\u8840\u6DCB\u6DCB\u7684\u5927\u6D1E\u3002\u624B\u638C\u9012\u5165\u7D2B\u73AB\u5468\u8EAB\u6D0B\u6EA2\u7684\u7EA2\u5149\uFF0C\u5C31\u4EFF\u4F5B\u63A2\u5165\u70C8\u706B\u822C\u5267\u75DB\u3002\u63A5\u7740\u683C\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u8155\u9AA8\u5DF2\u88AB\u62E7\u65AD\u3002\u767D\u7389\u9E42\u6367\u7740\u624B\u8155\u75BC\u5F97\u76F4\u6389\u773C\u6CEA\uFF0C\u81EA\u59CB\u81EA\u7EC8\u90FD\u6CA1\u6709\u770B\u6E05\u7D2B\u73AB\u5982\u4F55\u51FA\u624B\u3002</p><p>\u7D2B\u73AB\u5934\u4E5F\u4E0D\u56DE\u5730\u63A0\u5230\u752C\u9053\u5C3D\u5934\uFF0C\u8DDD\u592A\u6781\u56FE\u53EA\u5269\u4E09\u4E08\u8DDD\u79BB\u65F6\uFF0C\u5A07\u8EAF\u500F\u7136\u505C\u4F4F\uFF0C\u659C\u8EAB\u843D\u5728\u9ED1\u767D\u5206\u660E\u7684\u5706\u77F3\u4E0A\uFF0C\u8EAB\u8FB9\u7684\u9634\u5BAB\u4E3B\u626C\u9996\u4E3E\u81C0\uFF0C\u6DEB\u6001\u6A2A\u751F\u5730\u4E0E\u516C\u725B\u8054\u4E3A\u4E00\u4F53\uFF0C\u5B9B\u5982\u6D3B\u7269\u3002\u88AB\u52B2\u98CE\u4E00\u903C\uFF0C\u8273\u5C38\u79C0\u53D1\u98DE\u821E\uFF0C\u5A07\u5A9A\u7684\u773C\u775B\u76F4\u76F4\u770B\u7740\u7D2B\u73AB\uFF0C\u4EFF\u4F5B\u4E5E\u6C42\u5979\u5C06\u81EA\u5DF1\u4E00\u540C\u5E26\u8D70\u3002</p><p>\u65F6\u95F4\u7D27\u8FEB\uFF0C\u4E00\u65E6\u88AB\u4EBA\u53D1\u89C9\uFF0C\u83AB\u8BF4\u6C90\u58F0\u4F20\uFF0C\u5C31\u662F\u53F6\u884C\u5357\u8D76\u6765\u4E5F\u96BE\u4EE5\u8131\u8EAB\u3002\u7D2B\u73AB\u4E00\u628A\u63A8\u5F00\u5E9E\u5927\u7684\u516C\u725B\uFF0C\u53CC\u638C\u6BEB\u4E0D\u72B9\u8C6B\u5730\u6309\u5411\u9634\u9633\u9C7C\u7684\u4E24\u773C\u3002</p><p>\u4E94\u9053\u5173\u9501\u5DF2\u89E3\uFF0C\u9C7C\u773C\u5E94\u624B\u800C\u9677\uFF0C\u6D51\u82E5\u5929\u6210\u7684\u592A\u6781\u56FE\u8F84\u8F84\u5206\u5F00\uFF0C\u9732\u51FA\u4E00\u7EBF\u9ED1\u6697\u7684\u5165\u53E3\u3002</p><p>\u770B\u5230\u9003\u751F\u7684\u5E0C\u671B\uFF0C\u767D\u6C0F\u59D0\u59B9\u4E0D\u987E\u4E00\u5207\u5730\u6491\u8D77\u4F24\u4F53\uFF0C\u51C4\u5389\u5730\u547C\u558A\u9053\uFF1A\u300C\u5E26\u6211\u4EEC\u4E00\u8D77\u8D70\u5427\u2026\u2026\u300D</p><p>\u300C\u8D31\u5A62\uFF01\u300D\u5C61\u906D\u4E24\u5973\u51FA\u5356\u7684\u7D2B\u73AB\u5FC3\u4E0B\u6068\u6781\uFF0C\u5F53\u4E0B\u529F\u805A\u53CC\u638C\uFF0C\u4FBF\u6B32\u53D6\u5979\u4EEC\u7684\u6027\u547D\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u62AB\u5934\u6563\u53D1\uFF0C\u795E\u8272\u6053\u60F6\uFF0C\u8DCC\u8DCC\u649E\u649E\u5730\u8FFD\u6765\uFF0C\u8FDE\u6298\u65AD\u7684\u624B\u81C2\u5782\u5728\u8EAB\u524D\u90FD\u987E\u4E0D\u4E0A\u7406\u4F1A\u3002</p><p>\u7D2B\u73AB\u84E6\u7136\u60F3\u8D77\u521D\u9047\u7684\u573A\u666F\uFF0C\u59D0\u59B9\u4FE9\u767D\u8863\u80DC\u96EA\uFF0C\u7709\u679D\u5982\u753B\uFF0C\u5B9B\u5982\u4E00\u5BF9\u7389\u7422\u7684\u767E\u7075\uFF0C\u51B0\u96EA\u53EF\u7231\u3002\u4E0D\u4EC5\u4ED7\u4E49\u51FA\u624B\uFF0C\u800C\u4E14\u89E3\u8863\u8D60\u9A6C\uFF0C\u4E00\u7247\u70ED\u5FF1\u3002</p><p>\u770B\u7740\u4E24\u4E2A\u5929\u771F\u7684\u5C11\u5973\u5982\u4ECA\u5F62\u5982\u75AF\u9B54\u7684\u60E8\u72B6\uFF0C\u7D2B\u73AB\u5FC3\u4E0B\u4E0D\u7981\u4E00\u8F6F\uFF0C\u638C\u529B\u6536\u4E86\u51E0\u5206\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u5982\u53D7\u7535\u6B9B\uFF0C\u8DCC\u5728\u5730\u4E0A\u7FFB\u6EDA\u4E0D\u5DF2\u3002\u4E00\u8FB9\u54AF\u8840\u4E00\u8FB9\u72B9\u81EA\u54C0\u53F7\uFF0C\u300C\u6C42\u6C42\u4F60\u4E86\uFF0C\u5E26\u6211\u4EEC\u4E00\u8D77\u8D70\u5427\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u624B\u4F38\u51FA\u5BF8\u8BB8\uFF0C\u7EC8\u7A76\u8FD8\u662F\u5FCD\u4F4F\u4E86\u3002\u5979\u4EEC\u7B2C\u4E00\u6B21\u51FA\u5356\uFF0C\u5C31\u4F7F\u98CE\u5E08\u59D0\u548C\u81EA\u5DF1\u843D\u5165\u864E\u53E3\uFF1B\u7B2C\u4E8C\u6B21\u53C8\u51FA\u5356\u4E86\u5E08\u7236\uFF1B\u7B2C\u4E09\u6B21\u5BFC\u81F4\u6BCD\u4EB2\u88AB\u97AD\u6253\u65E9\u4EA7\u3002\u6B64\u4EC7\u6B64\u6068\u4E0D\u6740\u5979\u4EEC\u5DF2\u7ECF\u662F\u5BBD\u6055\u4E86\uFF0C\u5982\u679C\u5E26\u5979\u4EEC\u4E00\u540C\u79BB\u5F00\uFF0C\u8C01\u77E5\u9053\u4F1A\u4E0D\u4F1A\u906D\u5230\u7B2C\u56DB\u6B21\u51FA\u5356\uFF1F\u65E0\u8BBA\u5982\u4F55\u518D\u4E0D\u80FD\u5192\u9669\u3002\u7D2B\u73AB\u4E00\u987F\u8DB3\uFF0C\u7EB5\u8EAB\u8DC3\u5165\u6D1E\u7A74\u3002</p><p>\u6D1E\u7A74\u4EFF\u4F5B\u4E00\u53E3\u6DF1\u4E95\uFF0C\u811A\u4E0B\u9ED1\u6C89\u6C89\u6DF1\u4E0D\u89C1\u5E95\uFF0C\u5F53\u770B\u5230\u5706\u77F3\u4E0B\u4F38\u51FA\u4E00\u679D\u94C1\u81C2\uFF0C\u7D2B\u73AB\u8FDE\u5FD9\u6500\u7D27\uFF0C\u8BD5\u56FE\u7A33\u4F4F\u8EAB\u5F62\u3002\u94C1\u81C2\u4E00\u6C89\uFF0C\u5934\u9876\u7684\u5DE8\u77F3\u968F\u5373\u65CB\u8F6C\u7740\u5408\u7D27\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6323\u624E\u7740\u722C\u4E86\u8FC7\u6765\uFF0C\u6252\u4F4F\u592A\u6781\u56FE\u62FC\u547D\u5730\u62CD\u6253\u3002\u9ED1\u767D\u5206\u660E\u7684\u5DE8\u77F3\u65E0\u60C5\u7684\u6536\u62E2\uFF0C\u8F6C\u773C\u53EA\u5269\u624B\u638C\u5BBD\u7A84\u3002\u767D\u7389\u83BA\u773C\u795E\u91CC\u900F\u51FA\u7EDD\u671B\u7684\u795E\u8272\uFF0C\u8DB4\u5728\u7F1D\u9699\u4E0A\u5636\u58F0\u9053\uFF1A\u300C\u4E0D\u5E26\u6211\u4EEC\u8D70\uFF01\u4F60\u5C31\u6740\u4E86\u6211\u4EEC\u5427\uFF01\u300D</p><p>\u300C\u546F\u300D\uFF0C\u592A\u6781\u56FE\u5408\u6210\u4E00\u4E2A\u6D51\u5706\uFF0C\u518D\u65E0\u4E00\u4E1D\u7F1D\u9699\u3002</p><p>\u552F\u4E00\u7684\u5E0C\u671B\u4E5F\u7834\u706D\u4E86\uFF0C\u59D0\u59B9\u4FE9\u62B1\u7740\u592A\u6781\u56FE\u653E\u58F0\u75DB\u54ED\u3002\u6CEA\u6C34\u6DF7\u7740\u9C9C\u8840\u6E85\u5728\u5706\u77F3\u4E0A\uFF0C\u4F46\u51B0\u51B7\u7684\u77F3\u5757\u5374\u7EB9\u4E1D\u4E0D\u52A8\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5165\u53E3\u5408\u7D27\uFF0C\u6240\u6709\u7684\u5149\u7EBF\u548C\u58F0\u97F3\u90FD\u88AB\u9694\u5728\u53E6\u4E00\u4E2A\u4E16\u754C\uFF0C\u575F\u5893\u822C\u5BC2\u9759\u3002\u4F46\u7D2B\u73AB\u8033\u8FB9\u4ECD\u56DE\u6C64\u7740\u767D\u6C0F\u59D0\u59B9\u51C4\u5389\u7684\u53EB\u58F0\u2014\u2014\u300C\u4E0D\u5E26\u6211\u4EEC\u8D70\uFF01\u5C31\u6740\u4E86\u6211\u4EEC\u5427\uFF01\u300D</p><p>\u4E00\u8F88\u5B50\u7559\u5728\u5730\u72F1\u822C\u7684\u77F3\u5BAB\u4F5C\u4E3A\u6CA1\u6709\u4E1D\u6BEB\u5C0A\u4E25\u7684\u6027\u5974\uFF0C\u5BF9\u79C0\u7F8E\u6D3B\u6CFC\u7684\u59D0\u59B9\u4FE9\u6765\u8BF4\u6BD4\u6B7B\u4EA1\u8FD8\u96BE\u4EE5\u5FCD\u53D7\u3002\u90A3\u51C4\u5389\u7684\u54C0\u547C\u4F7F\u7D2B\u73AB\u60F3\u8D77\u5F53\u65E5\u5728\u6E58\u897F\u7684\u5C71\u91CE\u4E2D\uFF0C\u8EAB\u8D1F\u5185\u4F24\u7684\u767D\u7389\u83BA\u4E00\u8FB9\u5410\u8840\uFF0C\u4E00\u8FB9\u88AB\u4EBA\u8F6E\u5978\u7684\u60E8\u72B6\u3002\u5979\u4EEC\u6BD5\u7ADF\u4E0D\u662F\u574F\u4EBA\uFF0C\u6240\u6709\u7684\u80CC\u53DB\u53EA\u662F\u4E3A\u4E86\u751F\u5B58\u2026\u2026</p><p>\u5FC3\u5934\u4E00\u75BC\uFF0C\u4E00\u53E3\u9C9C\u8840\u6DCB\u6DCB\u6F13\u6F13\u55B7\u5728\u4E73\u4E0A\u3002\u7D2B\u73AB\u8FD9\u624D\u8B66\u89C9\u5230\u81EA\u5DF1\u8EAB\u65E0\u5BF8\u7F15\u3002\u4F46\u6108\u53D1\u6C89\u91CD\u7684\u4F24\u52BF\u4F7F\u5979\u987E\u4E0D\u4E0A\u7F9E\u6DA9\uFF0C\u53D7\u521B\u7684\u7ECF\u8109\u50CF\u88AB\u51B0\u5757\u963B\u585E\u822C\u6897\u585E\u96BE\u901A\uFF0C\u5185\u606F\u4E5F\u51DD\u6EDE\u8D77\u6765\u3002\u4E00\u54AC\u7259\uFF0C\u7D2B\u73AB\u677E\u5F00\u624B\uFF0C\u6ED1\u5411\u811A\u4E0B\u770B\u4E0D\u89C1\u7684\u9ED1\u6697\u4E2D\u3002</p><p>\u5149\u6E9C\u6E9C\u7684\u77F3\u58C1\u6253\u78E8\u5F97\u6BD4\u955C\u9762\u8FD8\u8981\u5149\u6ED1\uFF0C\u7728\u773C\u95F4\u4FBF\u6ED1\u8FC7\u8FD1\u5341\u4E08\u7684\u8DDD\u79BB\u3002\u7D2B\u73AB\u8FD0\u8DB3\u76EE\u529B\uFF0C\u5F85\u770B\u5230\u811A\u4E0B\u4E00\u70B9\u767D\u5149\uFF0C\u8FDE\u5FD9\u5C48\u4F53\u4E00\u7FFB\uFF0C\u8F7B\u8F7B\u843D\u4E0B\u3002</p><p>\u811A\u4E0B\u53D1\u51FA\u6728\u679D\u6298\u65AD\u7684\u5FAE\u54CD\uFF0C\u63A5\u7740\u5347\u8D77\u4E00\u7247\u95EA\u70C1\u7684\u5BD2\u5149\uFF0C\u661F\u4E91\u822C\u56F4\u4F4F\u96EA\u767D\u7684\u811A\u80EB\u3002\u7D2B\u73AB\u51DD\u76EE\u770B\u53BB\uFF0C\u5374\u662F\u8E29\u5230\u4E00\u5177\u673D\u9AA8\u3002\u5979\u6253\u4E86\u4E2A\u54C6\u55E6\uFF0C\u8FDE\u5FD9\u79FB\u5F00\u3002</p><p>\u7D2B\u73AB\u8D8A\u8D70\u8D8A\u662F\u5FC3\u60CA\uFF0C\u8FD9\u5EA7\u5730\u5BAB\u5E9E\u5927\u5F97\u51FA\u4EBA\u610F\u6599\u3002\u4E0D\u4EC5\u6000\u6708\u5CF0\uFF0C\u53EA\u6015\u6574\u4E2A\u5C9B\u5C7F\u4E4B\u4E0B\u90FD\u88AB\u638F\u7A7A\u3002</p><p>\u5730\u5BAB\u5185\u5230\u5904\u90FD\u662F\u6563\u843D\u7684\u9AB8\u9AA8\u548C\u5175\u5203\uFF0C\u5927\u591A\u80A2\u4F53\u4E0D\u5168\uFF0C\u65F6\u9694\u591A\u5E74\uFF0C\u4ECD\u80FD\u770B\u51FA\u5F53\u5E74\u6218\u51B5\u7684\u60E8\u70C8\u3002\u80FD\u903C\u5F97\u661F\u6708\u6E56\u5BAB\u4E3B\u5C01\u95ED\u5730\u5BAB\uFF0C\u540C\u5F52\u4E8E\u5C3D\uFF0C\u771F\u4E0D\u77E5\u4F55\u7B49\u82F1\u96C4\u4EBA\u7269\u3002</p><p>\u8F6C\u4E86\u4E24\u4E2A\u5F2F\u540E\uFF0C\u773C\u524D\u7A81\u7136\u5927\u653E\u5149\u660E\u3002</p><p>\u4E00\u6761\u5BBD\u8FD1\u4E08\u8BB8\u7684\u8D70\u5ECA\u7B14\u76F4\u4F38\u5F00\uFF0C\u4E24\u8FB9\u5E76\u5217\u7740\u5341\u4F59\u95F4\u5BBD\u655E\u7684\u77F3\u5BA4\uFF0C\u91CC\u9762\u6D41\u5149\u6EA2\u5F69\uFF0C\u5C55\u5385\u822C\u5806\u6EE1\u5B9D\u7269\u3002</p><p>\u8FD9\u4FBF\u662F\u7236\u4EB2\u6240\u8BF4\u7684\u5B9D\u85CF\u4E86\u5427\u3002\u786E\u5B9E\u503C\u5F88\u591A\u94B1\uFF0C\u4F46\u5BF9\u7D2B\u73AB\u6765\u8BF4\u5374\u6BEB\u65E0\u7528\u5904\u3002\u5979\u56DB\u4E0B\u9021\u5DE1\uFF0C\u60F3\u627E\u4EF6\u906E\u4F53\u7684\u5E03\u6599\uFF0C\u7ED3\u679C\u53EA\u6709\u5931\u671B\u3002</p><p>\u582A\u582A\u8D70\u5B8C\u957F\u5ECA\uFF0C\u6700\u540E\u4E00\u95F4\u77F3\u5BA4\u89D2\u843D\u91CC\u4E00\u62B9\u5F02\u6837\u7684\u5BD2\u5149\u5438\u5F15\u4E86\u5979\u7684\u773C\u795E\u3002</p><p>\u76F8\u6BD4\u4E8E\u5176\u5B83\u77F3\u5BA4\u5404\u79CD\u5B9D\u7269\u5806\u653E\u6574\u9F50\u7684\u72B6\u51B5\uFF0C\u8FD9\u95F4\u77F3\u5BA4\u7684\u7269\u54C1\u5374\u6781\u4E3A\u51CC\u4E71\u3002\u73CD\u73E0\u3001\u739B\u7459\u3001\u7FE1\u7FE0\u3001\u73CA\u745A\u3001\u5F62\u5F62\u8272\u8272\u7684\u91D1\u997C\u94F6\u952D\u6563\u843D\u6EE1\u5BA4\u3002\u5728\u8000\u773C\u7684\u5B9D\u5149\u4E4B\u95F4\uFF0C\u4E00\u524D\u4E00\u540E\u653E\u7740\u4E24\u5177\u767D\u68EE\u68EE\u7684\u9AA8\u9AB8\u3002</p><p>\u540E\u9762\u4E00\u5177\u56DB\u80A2\u4EA4\u53E0\uFF0C\u8737\u6210\u4E00\u56E2\uFF0C\u8EAB\u4E0A\u8FD8\u76D6\u7740\u672A\u70C2\u5C3D\u7684\u788E\u8863\uFF1B\u524D\u9762\u4E00\u5177\u8F83\u5C0F\u7684\u9AA8\u9AB8\u5219\u5E73\u8EBA\u4E8E\u5730\u3002\u5728\u5B83\u65C1\u8FB9\u653E\u7740\u4E00\u67C4\u957F\u5251\u3002\u5251\u8EAB\u8272\u6CFD\u82CD\u7070\uFF0C\u4EFF\u4F5B\u4E00\u6BB5\u673D\u6728\u3002\u4F46\u6EE1\u5BA4\u7684\u73E0\u5B9D\u5149\u534E\uFF0C\u5374\u65E0\u6CD5\u63A9\u76D6\u5B83\u77EB\u77EB\u4E0D\u7FA4\u7684\u738B\u8005\u4E4B\u6C14\u3002\u9AB8\u9AA8\u95F4\u6254\u7740\u4E00\u652F\u5F62\u5F0F\u53E4\u6734\u7684\u5251\u9798\uFF0C\u4E43\u662F\u9CA8\u9C7C\u76AE\u6240\u5236\u3002</p><p>\u5927\u5B5A\u7075\u9E6B\u5BFA\u4F4D\u4E8E\u6E05\u51C9\u5C71\uFF0C\u8DDD\u6B64\u5343\u91CC\u4E4B\u9065\uFF0C\u4E9F\u9700\u5175\u5203\u9632\u8EAB\u3002\u7D2B\u73AB\u987E\u4E0D\u5F97\u7EC6\u770B\uFF0C\u4FBF\u4FEF\u8EAB\u62FF\u8D77\u957F\u5251\u3002\u5251\u67C4\u752B\u5165\u638C\u4E2D\uFF0C\u8033\u4E2D\u5FFD\u7136\u54CD\u8D77\u4E00\u58F0\u5E7D\u5E7D\u7684\u4F4E\u53F9\u3002\u7D2B\u73AB\u6D51\u8EAB\u5BD2\u6BDB\u76F4\u7AD6\uFF0C\u8FDE\u5FD9\u5408\u5251\u5165\u9798\uFF0C\u4E00\u63D0\u771F\u6C14\uFF0C\u8F7B\u70DF\u822C\u98D8\u8FC7\u957F\u5ECA\u3002</p><p>\u661F\u6708\u6E56\u5728\u7EC8\u5357\u5357\u9E93\uFF0C\u5982\u679C\u80FD\u627E\u5230\u901A\u5F80\u5C71\u5317\u7684\u51FA\u53E3\uFF0C\u4E0D\u4F46\u80FD\u7701\u4E0B\u8DCB\u6D89\u4E4B\u82E6\uFF0C\u501F\u5730\u52BF\u7529\u5F00\u661F\u6708\u6E56\u7684\u8FFD\u5175\uFF0C\u8FD8\u80FD\u2026\u2026\u65E9\u534A\u65E5\u89C1\u5230\u5C55\u626C\u54E5\u54E5\u3002</p><p>\u7D2B\u73AB\u5FC3\u5934\u4E00\u70ED\uFF0C\u53EA\u60F3\u4F0F\u5728\u6CAE\u6E20\u5C55\u626C\u6000\u4E2D\u5927\u54ED\u4E00\u573A\uFF0C\u5C31\u50CF\u4ECE\u524D\u90A3\u6837\uFF0C\u8BA9\u4ED6\u6765\u5206\u62C5\u81EA\u5DF1\u7684\u59D4\u5C48\u3002</p><p>\u7D2B\u73AB\u5BF9\u5730\u5BAB\u7684\u7ED3\u6784\u4E00\u65E0\u6240\u77E5\uFF0C\u53EA\u80FD\u4F9D\u9760\u5F53\u521D\u843D\u4E0B\u65F6\u7684\u65B9\u4F4D\u4E00\u8DEF\u671D\u5317\u884C\u8FDB\u3002\u5728\u86DB\u7F51\u822C\u7684\u5730\u5BAB\u91CC\u76F4\u884C\u5341\u4F59\u91CC\uFF0C\u7B97\u6765\u5DF2\u7ECF\u6DF1\u5165\u6E56\u5E95\uFF0C\u7EC8\u4E8E\u8D70\u5230\u5730\u5BAB\u8FB9\u7F18\u3002</p><p>\u5730\u4E0A\u7684\u9AB8\u9AA8\u7A81\u7136\u589E\u591A\uFF0C\u77ED\u77ED\u5341\u4F59\u6B65\u5185\uFF0C\u5C31\u6563\u843D\u7740\u6570\u5341\u4E2A\u9AB7\u9AC5\u9996\u7EA7\uFF0C\u6709\u4E00\u4E9B\u751A\u81F3\u53EA\u4F59\u4E0B\u988C\uFF0C\u9876\u7AEF\u5DF2\u788E\u4E3A\u9F51\u7C89\u3002</p><p>\u6709\u8FC7\u9634\u957F\u91CE\u7684\u4E00\u756A\u906D\u9047\uFF0C\u7D2B\u73AB\u867D\u4E0D\u4FE1\u6709\u4EBA\u80FD\u591F\u5728\u8FD9\u5C01\u95ED\u767E\u5E74\u7684\u5730\u5BAB\u5185\u6D3B\u5230\u73B0\u5728\uFF0C\u4F46\u8FD8\u662F\u6A2A\u5251\u6321\u5728\u80F8\u524D\uFF0C\u4E00\u6B65\u6B65\u7A7F\u8FC7\u9AB7\u9AC5\u5806\u3002</p><p>\u77F3\u58C1\u4E0A\u5140\u7136\u51FA\u73B0\u4E00\u4E2A\u6D1E\u53E3\uFF0C\u5468\u56F4\u77F3\u5C51\u7C89\u98DE\uFF0C\u663E\u7136\u662F\u88AB\u4EBA\u7528\u786C\u529F\u7838\u5F00\u3002\u5979\u63A2\u5934\u4E00\u770B\uFF0C\u53EA\u89C1\u8FD9\u662F\u6247\u539A\u903E\u5C3A\u8BB8\u7684\u77F3\u95E8\uFF0C\u6F06\u9ED1\u7684\u96A7\u9053\u5185\u9634\u98CE\u9635\u9635\uFF0C\u9ED1\u6C89\u6C89\u7684\u770B\u4E0D\u5230\u5C3D\u5934\u3002</p><p>\u6D1E\u53E3\u53EA\u5BB9\u4E00\u4EBA\u94BB\u5165\uFF0C\u7D2B\u73AB\u5706\u6EDA\u6EDA\u7684\u5C0F\u8179\u6B63\u5361\u5728\u6D1E\u4E2D\uFF0C\u65E0\u6CD5\u7A7F\u8FC7\u3002\u5979\u6C14\u607C\u5730\u5F80\u8179\u4E0A\u62CD\u4E86\u4E00\u638C\uFF0C\u6068\u4E0D\u5F97\u628A\u8FD9\u4E2A\u4E71\u4F26\u7684\u5B7D\u79CD\u4E00\u6254\u4E86\u4E4B\u3002</p><p>\u80CE\u513F\u4E00\u52A8\uFF0C\u4E0D\u77E5\u662F\u624B\u662F\u811A\u731B\u7136\u4E00\u6323\uFF0C\u6491\u5728\u809A\u76AE\u4E0A\u3002\u7D2B\u73AB\u5FC3\u5E95\u4E00\u9635\u523A\u75DB\uFF0C\u773C\u7736\u4E0D\u7981\u6E7F\u4E86\u3002\u5A74\u513F\u90FD\u662F\u65E0\u8F9C\u7684\uFF0C\u4F46\u5B83\u4E0D\u540C\uFF0C\u4ECE\u5B55\u80B2\u90A3\u4E00\u523B\u8D77\uFF0C\u4EB2\u5144\u59B9\u7CBE\u8840\u4EA4\u5408\u7684\u80CC\u5FB7\uFF0C\u5C31\u6CE8\u5B9A\u4E86\u5B83\u7684\u7F6A\u6076\u3002</p><p>\u7D2B\u73AB\u54B3\u4E86\u53E3\u8840\uFF0C\u80F8\u53E3\u7565\u5FAE\u7545\u5FEB\u4E86\u4E9B\u3002\u5979\u62B1\u7740\u59D1\u4E14\u4E00\u8BD5\u7684\u5FC3\u6001\u62D4\u51FA\u957F\u5251\uFF0C\u671D\u77F3\u95E8\u523A\u53BB\u3002</p><p>\u300C\u6C99\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u5251\u950B\u76F4\u5165\u5CA9\u77F3\uFF0C\u8F7B\u6613\u5730\u50CF\u7A7F\u900F\u4E00\u5C42\u8584\u8584\u7684\u7EB8\u5F20\u3002\u7D2B\u73AB\u6115\u7136\u4E3E\u5251\uFF0C\u53EA\u89C1\u5251\u9798\u4E0A\u9542\u7740\u4E24\u4E2A\u82B1\u9E1F\u822C\u7684\u7BC6\u6587\uFF1A\u7384\u5929\u3002</p><p>\u8FD9\u4FBF\u662F\u661F\u6708\u6E56\u9547\u6559\u4E09\u5927\u795E\u5175\u4E4B\u9996\uFF0C\u7384\u5999\u5B50\u5F53\u5E74\u4EB2\u8EAB\u6240\u4F69\u4E4B\u5251\uFF1A\u7384\u5929\u5251\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7EC8\u5357\u5317\u9E93\uFF0C\u98DE\u98DE\u626C\u626C\u7684\u96EA\u82B1\u8986\u76D6\u5C71\u6797\u3002</p><p>\u4E00\u4E2A\u8D64\u88F8\u7684\u5C11\u5973\u7ACB\u5728\u6CA1\u8E1D\u7684\u96EA\u91CE\u4E2D\uFF0C\u7591\u7591\u671B\u7740\u8FD9\u4E2A\u6676\u83B9\u6F84\u5F7B\u7684\u7409\u7483\u4E16\u754C\u3002</p><p>\u5979\u7684\u5BB9\u8C8C\u6BD4\u73AB\u7470\u66F4\u9C9C\u7F8E\uFF0C\u808C\u80A4\u6BD4\u767D\u96EA\u66F4\u7EAF\u6D01\uFF0C\u4F46\u80A5\u767D\u7684\u7855\u4E73\u5374\u6BD4\u6700\u5996\u8273\u7684\u72D0\u7CBE\u66F4\u6DEB\u8361\u3002</p><p>\u7D2B\u73AB\u6342\u7740\u9F13\u80C0\u6B32\u88C2\u7684\u5C0F\u8179\uFF0C\u5BF9\u8179\u7403\u6CE2\u6D6A\u822C\u7684\u9635\u9635\u80CE\u52A8\u604D\u82E5\u672A\u89C9\u3002\u523A\u9AA8\u7684\u51B0\u96EA\u5439\u6253\u5728\u5439\u5F39\u53EF\u7834\u7684\u808C\u80A4\u4E0A\uFF0C\u5BD2\u610F\u5374\u6765\u81EA\u4F53\u5185\u3002\u592A\u4E00\u7ECF\u7684\u9634\u5BD2\u4E4B\u6C14\u6E38\u8D70\u4E8E\u7ECF\u7EDC\u4E4B\u95F4\u2014\u2014\u9664\u4E86\u5373\u5C06\u51FA\u4E16\u7684\u80CE\u513F\uFF0C\u4ED6\u7ED9\u81EA\u5DF1\u7559\u4E0B\u7684\u53EA\u6709\u8FD9\u79CD\u51B0\u51B7\u7684\u4F24\u5BB3\u3002</p><p>\u4E0D\u662F\u5417\uFF1F</p><p>\u7591\u7ACB\u826F\u4E45\uFF0C\u5C11\u5973\u64E6\u53BB\u5634\u89D2\u6BB7\u7EA2\u7684\u8840\u8FF9\uFF0C\u63E1\u7D27\u957F\u5251\u3002\u6F2B\u5929\u98CE\u96EA\u53C8\u4E00\u6B21\u626C\u8D77\uFF0C\u906E\u6CA1\u4E86\u5979\u7684\u8EAB\u5F71\u3002</p>',56);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
