import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_122" tabindex="-1"><a class="header-anchor" href="#_122" aria-hidden="true">#</a> 122</h1><p>\u9EC4\u660F\u7684\u5927\u5B5A\u7075\u9E6B\u5BFA\u6C89\u6D78\u5728\u4E00\u7247\u6A59\u9EC4\u7684\u66AE\u8272\u4E2D\uFF0C\u4F4E\u6C89\u7684\u68B5\u53F7\u4F34\u7740\u7F2D\u7ED5\u7684\u9999\u70DF\uFF0C\u5728\u53E4\u8001\u7684\u5E99\u5ECA\u5185\u4E45\u4E45\u56DE\u6C64\uFF0C\u90A3\u79CD\u6DF1\u9083\u7684\u6148\u60B2\u8D85\u8D8A\u4E86\u751F\u6B7B\u7684\u754C\u9650\uFF0C\u5982\u540C\u4E00\u9053\u6DF3\u539A\u7684\u6696\u6D41\uFF0C\u629A\u6170\u7740\u6765\u8005\u75B2\u5026\u7684\u5FC3\u7075\u3002</p><p>\u77E5\u5BA2\u50E7\u65E0\u8A00\u5730\u5408\u4EC0\u9000\u4E0B\uFF0C\u5E26\u4E0A\u67F4\u95E8\uFF0C\u5C06\u5C0F\u5C0F\u7684\u7985\u9662\u9694\u7EDD\u5728\u7EA2\u5C18\u4E4B\u5916\u3002</p><p>\u5728\u51B0\u5929\u96EA\u5730\u4E2D\u8DCB\u6D89\u5343\u91CC\u540E\uFF0C\u6155\u5BB9\u7D2B\u73AB\u5A07\u5AE9\u7684\u8138\u4E0A\u5E26\u7740\u4E00\u4E1D\u63A9\u4E0D\u4F4F\u7684\u75B2\u5026\uFF0C\u4F46\u6B64\u65F6\uFF0C\u82B3\u5FC3\u5185\u5C3D\u662F\u5E73\u548C\u7684\u559C\u60A6\u3002</p><p>\u9003\u79BB\u661F\u6708\u6E56\u7684\u5F53\u665A\uFF0C\u5979\u5728\u96EA\u5730\u4EA7\u4E0B\u4E00\u4E2A\u5973\u5A74\u3002\u6BCD\u5973\u4FE9\u540C\u6837\u65E9\u4EA7\uFF0C\u53C8\u5404\u81EA\u751F\u4E0B\u4E00\u5B50\u4E00\u5973\uFF0C\u6155\u5BB9\u9F99\u5F53\u53EF\u542B\u7B11\u4E5D\u6CC9\u3002\u5F53\u65F6\u5979\u624B\u6307\u5DF2\u7ECF\u627C\u4F4F\u5A74\u513F\u7684\u8116\u9888\uFF0C\u7EC8\u7A76\u8FD8\u662F\u4E0D\u5FCD\u4E0B\u624B\u3002\u6700\u540E\u53EA\u597D\u62B1\u7740\u4EB2\u751F\u9AA8\u8089\u75DB\u54ED\u4E00\u573A\uFF0C\u7559\u4E0B\u8FD9\u4E2A\u5B7D\u79CD\u7684\u6027\u547D\u3002</p><p>\u4E00\u8DEF\u4E0A\u7D2B\u73AB\u6402\u7740\u5973\u513F\uFF0C\u62D6\u7740\u751F\u4EA7\u8FC7\u7684\u8EAB\u4F53\u663C\u5BBF\u591C\u884C\uFF0C\u4E00\u9762\u7597\u4F24\uFF0C\u4E00\u9762\u5C0F\u5FC3\u7FFC\u7FFC\u5730\u907F\u5F00\u661F\u6708\u6E56\u7684\u8FFD\u5175\u3002\u7ECF\u8FC7\u5341\u4F59\u5929\u7684\u8270\u8F9B\u8DEF\u7A0B\uFF0C\u7EC8\u4E8E\u6765\u5230\u8FD9\u5904\u4F5B\u6559\u5723\u5730\uFF0C\u6B66\u6797\u540D\u5239\u3002</p><p>\u5927\u5B5A\u7075\u9E6B\u5BFA\u7684\u5E84\u4E25\u8083\u7A46\uFF0C\u7ED9\u4E86\u7D2B\u73AB\u96BE\u5F97\u7684\u5B89\u5168\u611F\u3002</p><p>\u4E0D\u8DB3\u4E00\u5E74\u7684\u65F6\u95F4\u5185\uFF0C\u5979\u7ECF\u5386\u5E38\u4EBA\u51E0\u4E16\u4E5F\u672A\u6709\u7684\u75DB\u82E6\u3001\u60CA\u60E7\u548C\u751F\u79BB\u6B7B\u522B\u3002\u7D2B\u73AB\u73B0\u5728\u53EA\u60F3\u4E0E\u6CAE\u6E20\u5C55\u626C\u89C1\u4E0A\u4E00\u9762\uFF0C\u7136\u540E\u5728\u4ED6\u8EAB\u8FB9\u5B89\u5B89\u7A33\u7A33\u5730\u7761\u4E0A\u4E00\u89C9\uFF0C\u6DA4\u5C3D\u8EAB\u4F53\u7684\u56F0\u4E4F\u548C\u4F24\u75DB\u3002</p><p>\u7D2B\u73AB\u6C89\u9759\u5730\u7406\u4E86\u7406\u9B13\u53D1\uFF0C\u8F7B\u8F7B\u63A8\u5F00\u623F\u95E8\u3002</p><p>\u300C\u5431\u54D1\u300D\u4E00\u58F0\uFF0C\u843D\u65E5\u7684\u4F59\u8F89\u6D8C\u5165\u964B\u5BA4\uFF0C\u5C06\u7B80\u964B\u7684\u7269\u4F53\u9540\u4E0A\u4E00\u5C42\u8000\u76EE\u7684\u91D1\u9EC4\u3002</p><p>\u5BA4\u5185\u53EA\u6709\u4E00\u5F20\u84B2\u56E2\uFF0C\u4E00\u5F20\u77EE\u51E0\u548C\u4E00\u4E2A\u80CC\u95E8\u8DBA\u5750\u7684\u50E7\u4EBA\uFF0C\u6B64\u5916\u4E00\u65E0\u957F\u7269\u3002\u9762\u524D\u7070\u6251\u6251\u7684\u50E7\u8863\uFF0C\u4E0E\u5979\u8BB0\u5FC6\u4E2D\u90A3\u4E2A\u9C9C\u8863\u6012\u9A6C\uFF0C\u7389\u6811\u4E34\u98CE\u7684\u6B66\u6797\u5C11\u4FA0\u5927\u76F8\u8FF3\u5EAD\u3002\u4F46\u7D2B\u73AB\u4E00\u773C\u5C31\u8BA4\u51FA\u8FD9\u4E2A\u719F\u6089\u7684\u80CC\u5F71\u3002</p><p>\u7D2B\u73AB\u5FC3\u4E2D\u4E00\u8361\uFF0C\u53EB\u9053\uFF1A\u300C\u5C55\u626C\u54E5\u54E5\uFF01\u300D\u8E0F\u5165\u5EB5\u5802\u3002</p><p>\u53EA\u8FC8\u4E86\u4E00\u6B65\uFF0C\u7D2B\u73AB\u5C31\u505C\u4F4F\u4E86\u3002</p><p>\u4F7F\u5979\u964C\u751F\u7684\u4E0D\u4EC5\u662F\u70E7\u4E86\u6212\u75A4\u7684\u5149\u5934\uFF0C\u8FD8\u6709\u90A3\u4E2A\u80CC\u5F71\u62D2\u4EBA\u4E8E\u5343\u91CC\u4E4B\u5916\u7684\u51B7\u6F20\u3002</p><p>\u542C\u5230\u5979\u7684\u58F0\u97F3\uFF0C\u6CAE\u6E20\u5C55\u626C\u5E76\u6CA1\u6709\u50CF\u5979\u60F3\u50CF\u4E2D\u90A3\u6837\u51B2\u8FC7\u6765\u633D\u4F4F\u81EA\u5DF1\u7684\u624B\uFF0C\u5618\u5BD2\u95EE\u6696\u3002\u4ED6\u6CA1\u6709\u626D\u5934\uFF0C\u751A\u81F3\u8FDE\u59FF\u52BF\u4E5F\u6CA1\u6709\u6362\uFF0C\u53EA\u662F\u5165\u5B9A\u822C\u6F20\u7136\u3002</p><p>\u7D2B\u73AB\u7684\u82B3\u5FC3\u50CF\u88AB\u4EBA\u6BEB\u4E0D\u7559\u60C5\u7684\u6254\u5F00\uFF0C\u4E00\u79CD\u7A7A\u7A7A\u7684\u75BC\u75DB\u4F7F\u5979\u50F5\u7ACB\u5F53\u573A\u3002</p><p>\u4ECE\u5F88\u5C0F\u5F88\u5C0F\u7684\u65F6\u5019\u8D77\uFF0C\u5C55\u626C\u54E5\u54E5\u5C31\u662F\u5979\u7684\u5E87\u62A4\u8005\uFF0C\u4E00\u4E2A\u53EF\u4EE5\u8BA9\u5979\u653E\u5FC3\u5B89\u7761\u7684\u5B81\u9759\u6E2F\u6E7E\u3002\u5728\u5979\u8BB0\u5FC6\u4E2D\uFF0C\u65E0\u8BBA\u53D7\u5230\u4EC0\u4E48\u6837\u7684\u59D4\u5C48\uFF0C\u53EA\u8981\u8EAB\u8FB9\u6709\u5C55\u626C\u54E5\u54E5\uFF0C\u81EA\u5DF1\u90FD\u53EF\u4EE5\u5728\u4ED6\u6000\u91CC\u4E00\u54ED\u4E86\u4E4B\uFF0C\u5C55\u626C\u54E5\u54E5\u81EA\u7136\u4F1A\u66FF\u81EA\u5DF1\u89E3\u51B3\u70E6\u607C\u3002</p><p>\u7136\u800C\u6B64\u65F6\uFF0C\u9694\u7740\u4E24\u6B65\u7684\u8DDD\u79BB\uFF0C\u6155\u5BB9\u7D2B\u73AB\u611F\u89C9\u5374\u6BD4\u5728\u7EC8\u5357\u65F6\u66F4\u4E3A\u9065\u8FDC\u3002\u8FDC\u5F97\u8BA9\u5979\u770B\u4E0D\u6E05\u3001\u542C\u4E0D\u5230\u3001\u6478\u4E0D\u7740\u3002</p><p>\u5915\u9633\u5728\u6C89\u9ED8\u4E2D\u53D8\u6362\u7740\u89D2\u5EA6\uFF0C\u90A3\u4E2A\u719F\u6089\u7684\u80CC\u5F71\u4E00\u52A8\u4E0D\u52A8\uFF0C\u50CF\u70C8\u706B\u711A\u5C3D\u7684\u4F59\u7070\uFF0C\u6CA1\u6709\u4E00\u4E1D\u6E29\u5EA6\u3002\u7D2B\u73AB\u7480\u74A8\u7684\u661F\u7738\u6E10\u6E10\u9EEF\u6DE1\uFF0C\u5FC3\u5E95\u6700\u6DF1\u6700\u6E29\u6696\u7684\u89D2\u843D\u50CF\u88AB\u4EBA\u4E00\u5200\u4E00\u5200\u525C\u7A7A\uFF0C\u53EA\u5269\u4E0B\u51B0\u51B7\u7684\u75DB\u695A\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u4E00\u58F0\u5639\u4EAE\u7684\u557C\u54ED\u5212\u7834\u5BC2\u9759\u3002</p><p>\u7070\u8272\u7684\u50E7\u888D\u95FB\u58F0\u4E00\u98A4\uFF0C\u7D2B\u73AB\u8FD9\u624D\u6CE8\u610F\u5230\u4ED6\u53F3\u8896\u7A7A\u8361\u8361\u6396\u5728\u8170\u95F4\u3002\u5979\u8F7B\u8F7B\u62CD\u7740\u5973\u513F\u67D4\u8F6F\u7684\u8EAB\u4F53\uFF0C\u843D\u5BDE\u7684\u773C\u775B\u91CC\u6CA1\u6709\u4E00\u6EF4\u6CEA\u6C34\u3002\u5979\u539F\u4EE5\u4E3A\u81EA\u5DF1\u4F1A\u54ED\uFF0C\u6B64\u65F6\u624D\u77E5\u9053\u771F\u6B63\u7684\u4F24\u5FC3\u662F\u4E0D\u4F1A\u6D41\u6CEA\u7684\uFF0C\u6709\u7684\u53EA\u662F\u75B2\u5026\uFF0C\u751F\u65E0\u7559\u604B\u7684\u75B2\u5026\u3002</p><p>\u300C\u7A7A\u3001\u7A7A\u7A7A\u2026\u2026\u300D\u6728\u9C7C\u58F0\u50CF\u88AB\u557C\u54ED\u6FC0\u6012\u822C\u91CD\u91CD\u54CD\u4E86\u8D77\u6765\u3002</p><p>\u4E0D\u7528\u62AC\u5934\uFF0C\u7D2B\u73AB\u5C31\u80FD\u542C\u51FA\u58F0\u97F3\u91CC\u7684\u70E6\u71E5\u548C\u75BC\u75DB\u3002</p><p>\u51CC\u4E71\u7684\u6728\u9C7C\u58F0\u50CF\u51CC\u5389\u7684\u8033\u5149\uFF0C\u91CD\u91CD\u6253\u5728\u8138\u4E0A\uFF0C\u8D23\u9A82\u5979\u7684\u80AE\u810F\u548C\u4E0D\u8D1E\uFF0C\u8BA9\u5979\u6EDA\u51FA\u5723\u6D01\u7684\u5E99\u5B87\u3002</p><p>\u7D2B\u73AB\u4FCF\u8138\u987F\u65F6\u53D8\u5F97\u82CD\u767D\uFF0C\u5979\u6014\u6014\u671B\u7740\u5973\u513F\u4E0D\u4F4F\u5F00\u5408\u7684\u5C0F\u5634\uFF0C\u6700\u540E\u51C4\u7136\u4E00\u7B11\u3002</p><p>\u90A3\u7B11\u5BB9\u4EFF\u4F5B\u4E00\u7247\u51CB\u96F6\u7684\u82B1\u74E3\u843D\u5165\u6C34\u4E2D\uFF0C\u8F6C\u77AC\u5C31\u88AB\u6FC0\u6D41\u51B2\u8D70\uFF0C\u4E0D\u7559\u75D5\u8FF9\u3002</p><p>\u7D2B\u73AB\u7528\u5DFE\u5E15\u63A9\u4F4F\u5973\u513F\u7684\u8138\u86CB\uFF0C\u67F4\u6249\u51E0\u4E4E\u540C\u65F6\u4E00\u52A8\uFF0C\u63A5\u7740\u8EAB\u5F71\u4FBF\u5728\u5341\u4E08\u4E4B\u5916\u3002</p><p>\u300C\u70E6\u8BF7\u544A\u77E5\u5706\u76F8\u65B9\u4E08\uFF1A\u661F\u6708\u6E56\u5BAB\u4E3B\u5DF2\u6B7B\uFF0C\u8BF7\u65B9\u4E08\u4EE5\u5929\u751F\u82CD\u751F\u4E3A\u91CD\uFF0C\u4E3A\u6B66\u6797\u9664\u53BB\u8086\u8650\u5343\u5E74\u7684\u90AA\u6559\u3002\u300D\u7D2B\u73AB\u5BF9\u77E5\u5BA2\u50E7\u8BF4\u5B8C\uFF0C\u98D8\u7136\u79BB\u5F00\u5927\u5B5A\u7075\u9E6B\u5BFA\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5341\u4E8C\u6708\u5341\u516D\uFF0C\u9EC4\u6CB3\u98CE\u9675\u6E21\u3002</p><p>\u591C\u8272\u964D\u4E34\uFF0C\u51B0\u5C01\u7684\u957F\u6CB3\u95EA\u7740\u5BD2\u51B7\u7684\u6E05\u5149\u3002\u663C\u95F4\u7EDC\u7ECE\u7684\u8F66\u9A6C\u5DF2\u7136\u7EDD\u8FF9\uFF0C\u5076\u5C14\u4E00\u9635\u957F\u98CE\u5439\u6765\uFF0C\u4E00\u56E2\u56E2\u7EC6\u788E\u7684\u96EA\u7C89\u76D8\u65CB\u800C\u8D77\uFF0C\u5728\u5BC2\u5BE5\u7684\u51B0\u9762\u4E0A\u65CB\u821E\u3002</p><p>\u5341\u51E0\u6761\u6728\u8239\u88AB\u51B0\u5C01\u5728\u5CB8\u8FB9\uFF0C\u6E21\u53E3\u5DF2\u6210\u865A\u8BBE\u3002\u4F46\u4F5C\u4E3A\u65B9\u5706\u6570\u5341\u91CC\u6700\u5927\u7684\u9547\u5B50\uFF0C\u98CE\u51CC\u6E21\u6C47\u96C6\u4E86\u5357\u6765\u5317\u5F80\u7684\u884C\u5BA2\u3002\u79BB\u6CB3\u5CB8\u4E0D\u8FDC\uFF0C\u738B\u8BB0\u5BA2\u6808\u5185\u4EBA\u58F0\u9F0E\u6CB8\uFF0C\u697C\u4E0A\u7684\u5BA2\u623F\u65E9\u5DF2\u7206\u6EE1\uFF0C\u8FDE\u5927\u5385\u5185\u4E5F\u5750\u6EE1\u4E86\u6025\u4E8E\u56DE\u5BB6\u8FC7\u5E74\u7684\u5BA2\u5546\u884C\u4EBA\u3002</p><p>\u8FD9\u4E9B\u4EBA\u65E0\u6CD5\u5B89\u6B47\uFF0C\u53EA\u597D\u56F4\u7740\u5385\u4E2D\u5DE8\u5927\u7684\u706B\u5858\uFF0C\u6D77\u9614\u5929\u7A7A\u7684\u80E1\u5439\u4E71\u4F83\uFF0C\u4EE5\u5EA6\u957F\u591C\u3002</p><p>\u4F17\u4EBA\u6B63\u81EA\u8BF4\u5F97\u9AD8\u5174\uFF0C\u4E00\u4E2A\u82D7\u6761\u7684\u8EAB\u5F71\u6084\u65E0\u58F0\u606F\u5730\u63A9\u8EAB\u800C\u5165\uFF0C\u4E0D\u4E3A\u4EBA\u6CE8\u610F\u5730\u7AD9\u5728\u89D2\u843D\u91CC\u3002\u5979\u8138\u4E0A\u906E\u7740\u9762\u7EB1\uFF0C\u6000\u91CC\u62B1\u7740\u5A74\u513F\u3002\u5A74\u513F\u5DF2\u7ECF\u54ED\u5F97\u6CA1\u6709\u529B\u6C14\uFF0C\u53EA\u4E0D\u65F6\u53D1\u51FA\u5C0F\u732B\u54AA\u822C\u7684\u545C\u54BD\uFF0C\u8BA9\u4EBA\u95FB\u4E4B\u607B\u7136\u3002\u90A3\u5973\u5B50\u4E00\u8FB9\u5475\u54C4\uFF0C\u4E00\u8FB9\u7126\u6025\u7684\u56DB\u4E0B\u5F20\u671B\u3002\u5F85\u770B\u5230\u4E00\u4E2A\u501A\u5899\u800C\u5750\u7684\u5987\u5973\uFF0C\u9732\u5728\u9762\u7EB1\u5916\u7684\u90A3\u53CC\u5999\u76EE\u987F\u65F6\u4E00\u4EAE\u3002</p><p>\u5979\u8270\u96BE\u5730\u7A7F\u8FC7\u4EBA\u7FA4\uFF0C\u8D70\u5230\u90A3\u4E2A\u519C\u5987\u6253\u626E\uFF0C\u6B63\u7ED9\u5B69\u5B50\u5582\u5976\u7684\u5987\u5973\u65C1\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u5927\u5A76\uFF0C\u80FD\u4E0D\u80FD\u5E2E\u6211\u5582\u5582\u5B69\u5B50\uFF1F\u300D</p><p>\u519C\u5987\u62AC\u5934\u4E00\u770B\uFF0C\u300C\u54CE\u54DF\u300D\u4E00\u58F0\uFF0C\u300C\u5927\u59B9\u5B50\uFF0C\u8FD9\u662F\u4F60\u7684\u5B69\u5B50\u5417\uFF1F\u300D</p><p>\u300C\u662F\u3002\u300D</p><p>\u90A3\u519C\u5987\u5FC3\u76F4\u53E3\u5FEB\uFF0C\u300C\u5976\u5B50\u8FD9\u4E48\u5927\uFF0C\u600E\u4E48\u4F1A\u6CA1\u5976\u5462\uFF1F\u300D</p><p>\u5C11\u5973\u8138\u4E0A\u5237\u7684\u6DA8\u5F97\u901A\u7EA2\u3002\u7D2B\u73AB\u5E73\u65F6\u628A\u5973\u513F\u62B1\u5728\u80F8\u524D\uFF0C\u4E73\u623F\u60CA\u4EBA\u7684\u5C3A\u5BF8\u5E76\u4E0D\u660E\u663E\uFF0C\u6B64\u65F6\u5F13\u8170\u8BF4\u8BDD\uFF0C\u53C8\u9012\u51FA\u5973\u513F\uFF0C\u98A4\u5FAE\u5FAE\u7684\u80A5\u4E73\u5782\u5728\u80F8\u524D\uFF0C\u51E0\u4E4E\u8981\u6491\u7834\u5355\u8584\u7684\u8863\u7269\u3002</p><p>\u521D\u4E73\u672C\u6765\u5C31\u8FDF\uFF0C\u5979\u53C8\u662F\u65E9\u4EA7\uFF0C\u5E76\u4E14\u4E73\u623F\u8FD8\u88AB\u4EBA\u4E3A\u589E\u5927\uFF0C\u56E0\u6B64\u751F\u80B2\u591A\u65F6\uFF0C\u5976\u6C34\u4ECD\u8FDF\u8FDF\u672A\u81F3\u3002\u8FD9\u4E00\u8DEF\u5979\u7AED\u529B\u63A9\u85CF\u81EA\u5DF1\u89C1\u4E0D\u5F97\u7684\u5DE8\u4E73\uFF0C\u6B64\u65F6\u88AB\u4EBA\u5728\u5927\u5EAD\u5E7F\u4F17\u4E0B\u4E00\u53E3\u56B7\u7834\uFF0C\u8138\u4E0A\u987F\u65F6\u706B\u8FA3\u8FA3\u4E00\u7247\u3002</p><p>\u770B\u5230\u5468\u56F4\u60CA\u8BE7\u7684\u76EE\u5149\uFF0C\u7D2B\u73AB\u7F9E\u803B\u96BE\u5F53\uFF0C\u53EA\u60F3\u4E00\u8D70\u4E86\u4E4B\u3002\u4F46\u5973\u513F\u6709\u6C14\u65E0\u529B\u7684\u54ED\u58F0\u5374\u4F7F\u5979\u96BE\u4EE5\u8FC8\u6B65\u3002\u4E00\u65F6\u95F4\u5FC3\u4E71\u5982\u9EBB\uFF0C\u62B1\u7740\u5973\u513F\u4E0D\u77E5\u6240\u63AA\u3002</p><p>\u519C\u5987\u5374\u6CA1\u6CE8\u610F\u5979\u7684\u7A98\u8FEB\uFF0C\u5927\u54A7\u54A7\u63A5\u8FC7\u5A74\u513F\uFF0C\u4E0E\u81EA\u5DF1\u7684\u5B69\u5B50\u653E\u5728\u4E00\u8D77\uFF0C\u7136\u540E\u4ECE\u8863\u895F\u91CC\u5766\u7136\u62C9\u51FA\u4E73\u623F\uFF0C\u63EA\u4E86\u63EA\u5976\u5934\u585E\u5230\u5A74\u513F\u5634\u4E2D\u3002</p><p>\u5973\u5A74\u95FB\u5230\u4E73\u9999\uFF0C\u7ACB\u5373\u505C\u4F4F\u54ED\u6CE3\uFF0C\u5C0F\u5634\u62FC\u547D\u4F7F\u529B\uFF0C\u5927\u53E3\u5927\u53E3\u5730\u5438\u542E\u7740\u4E73\u6C41\u3002</p><p>\u7D2B\u73AB\u4E24\u773C\u7D27\u7D27\u76EF\u7740\u5973\u513F\uFF0C\u89C1\u5979\u5403\u5F97\u9999\u751C\uFF0C\u5FC3\u91CC\u7684\u7D27\u5F20\u987F\u65F6\u6D88\u6563\u3002</p><p>\u300C\u8FD8\u6CA1\u6EE1\u6708\u5427\uFF1F\u5403\u8D77\u6765\u50CF\u4E2A\u5C0F\u8001\u864E\u3002\u300D\u519C\u5987\u4E00\u624B\u62B1\u7740\u4E00\u4E2A\u5A74\u513F\uFF0C\u4E00\u8FB9\u62CD\u6253\uFF0C\u4E00\u8FB9\u7B11\u772F\u772F\u5730\u8BF4\u3002</p><p>\u300C\u6CA1\u6709\u5462\u3002\u300D\u7D2B\u73AB\u7FA1\u6155\u5730\u770B\u7740\u519C\u5987\u7565\u663E\u7C97\u72B7\u7684\u4E73\u623F\u3002\u82E5\u8BBA\u7F8E\u611F\uFF0C\u5979\u4E0E\u81EA\u5DF1\u6839\u672C\u65E0\u6CD5\u6BD4\u8F83\u3002\u4F46\u5979\u5B81\u613F\u7528\u81EA\u5DF1\u4E00\u5BF9\u6D51\u5706\u7684\u80A5\u4E73\uFF0C\u6362\u53D6\u4E00\u53EA\u80FD\u6CCC\u4E73\u7684\u56CA\u72B6\u4E73\u623F\u3002</p><p>\u519C\u5987\u6253\u91CF\u7740\u8FD9\u4E2A\u672A\u6EE1\u6708\u5C31\u72EC\u81EA\u62B1\u7740\u5973\u513F\uFF0C\u5728\u5927\u96EA\u4E2D\u8D76\u8DEF\u7684\u5947\u5F02\u5973\u5B50\uFF0C\u5173\u5207\u5730\u8BF4\uFF1A\u300C\u6CA1\u5750\u5B8C\u6708\u5B50\u5C31\u8D76\u8DEF\uFF1F\u8FD9\u53EF\u4E0D\u6210\u554A\uFF0C\u8981\u5F97\u4E86\u75C5\uFF0C\u90A3\u53EF\u662F\u4E00\u8F88\u5B50\u7684\u4E8B\u5462\u3002\u5B69\u5B50\u5979\u7239\u5462\uFF1F\u300D</p><p>\u7D2B\u73AB\u52C9\u5F3A\u4E00\u7B11\uFF0C\u6697\u6697\u634F\u7D27\u624B\u6307\u3002\u5979\u79BB\u5F00\u5927\u5B5A\u7075\u9E6B\u5BFA\u4E4B\u540E\uFF0C\u4FBF\u4E00\u8DEF\u5357\u4E0B\uFF0C\u51C6\u5907\u5148\u8D76\u5F80\u6D1B\u9633\u6551\u51FA\u4E09\u5E08\u59D0\u548C\u6CAE\u6E20\u660E\u5170\uFF0C\u628A\u4E24\u4EBA\u5B89\u987F\u5728\u7EAA\u5E9C\uFF0C\u7559\u4E0B\u5973\u513F\u8BA9\u5979\u4EEC\u7167\u5E94\uFF0C\u7136\u540E\u518D\u8D74\u661F\u6708\u6E56\u6551\u51FA\u6BCD\u4EB2\u3001\u5E08\u7236\u548C\u4E24\u4F4D\u5E08\u59D0\u3002\u505A\u5B8C\u8FD9\u4E9B\uFF0C\u5979\u4FBF\u4E0E\u6BCD\u4EB2\u9690\u5C45\u5728\u98D8\u6885\u5CF0\uFF0C\u7EC8\u8EAB\u4E0D\u518D\u4E0B\u5C71\u3002</p><p>\u519C\u5987\u5520\u5520\u53E8\u53E8\uFF0C\u4E00\u4F1A\u513F\u8BF4\uFF1A\u5B69\u5B50\u5979\u7239\u592A\u4E0D\u50CF\u8BDD\uFF0C\u4E00\u70B9\u513F\u90FD\u4E0D\u77E5\u9053\u5FC3\u75BC\u8001\u5A46\u5B69\u5B50\uFF1B\u4E00\u4F1A\u513F\u53C8\u8BF4\uFF1A\u7A7F\u8FD9\u4E48\u5355\u8584\uFF0C\u8FD9\u5927\u51B7\u7684\u5929\u513F\u53EF\u600E\u4E48\u53D7\u5F97\u4E86\u3002\u867D\u7136\u7F57\u55E6\uFF0C\u4F46\u7D2B\u73AB\u5F88\u4E45\u6CA1\u6709\u4F53\u4F1A\u8FC7\u8FD9\u79CD\u6DF3\u6734\u7684\u6E29\u60C5\uFF0C\u5FC3\u4E0B\u6696\u6D0B\u6D0B\u4E00\u7247\uFF0C\u7D27\u7EF7\u7684\u795E\u7ECF\u4E5F\u677E\u61C8\u4E0B\u6765\u3002</p><p>\u521A\u60F3\u95ED\u4E0A\u773C\u4F11\u606F\u4E00\u4F1A\u513F\uFF0C\u5973\u513F\u7A81\u7136\u4E00\u54B3\uFF0C\u767D\u82B1\u82B1\u5730\u5976\u6C34\u4ECE\u5C0F\u5634\u91CC\u5495\u5495\u53FD\u53FD\u6D41\u5230\u8116\u5B50\u91CC\u3002\u7D2B\u73AB\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u4F38\u624B\u53BB\u62B1\uFF0C\u90A3\u519C\u5987\u5DF2\u7ECF\u5229\u843D\u5730\u64A9\u8D77\u7C97\u5E03\u8863\u895F\uFF0C\u7ED9\u5973\u513F\u64E6\u4E86\u64E6\u5634\uFF0C\u7B11\u9053\uFF1A\u300C\u5C0F\u5BB6\u4F19\u5403\u5F97\u592A\u6025\uFF0C\u545B\u5976\u4E86\u3002\u662F\u7537\u5B69\u8FD8\u662F\u5973\u5B69\uFF1F\u300D</p><p>\u300C\u5973\u5B69\u3002\u300D</p><p>\u300C\u8D77\u540D\u5B57\u4E86\u5417\uFF1F\u300D</p><p>\u300C\u6674\u96EA\u3002\u300D\u5F53\u65F6\u7D2B\u73AB\u53EA\u76FC\u8FD9\u573A\u5927\u96EA\u80FD\u591F\u653E\u6674\u3002</p><p>\u519C\u5987\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u602A\u4E0D\u5F97\u5B69\u5B50\u4ED6\u7239\u8FD9\u4E48\u5FC3\u72E0\u3002\u6211\u5934\u4E24\u80CE\u4E5F\u662F\u5973\u513F\uFF0C\u6211\u7537\u4EBA\u5929\u5929\u6454\u76D8\u5B50\u6253\u7897\uFF0C\u751F\u4E2A\u513F\u5B50\u624D\u518D\u6CA1\u7ED9\u6211\u8138\u8272\u77A7\u3002\u300D</p><p>\u7D2B\u73AB\u82E6\u6DA9\u5730\u4E00\u7B11\uFF0C\u6CA1\u6709\u56DE\u7B54\u3002</p><p>\u519C\u5987\u8FF3\u81EA\u8BF4\u9053\uFF1A\u300C\u54B1\u4EEC\u6C49\u4EBA\u90FD\u662F\u8FD9\u6837\uFF0C\u751F\u4E2A\u5973\u513F\u81EA\u5DF1\u90FD\u62AC\u4E0D\u8D77\u5934\u2026\u2026\u300D</p><p>\u7D2B\u73AB\u62B1\u7740\u819D\u76D6\u5750\u5728\u4E00\u65C1\uFF0C\u770B\u7740\u5973\u513F\u5403\u9971\u540E\u6EE1\u8DB3\u7684\u7761\u5BB9\uFF0C\u5FC3\u795E\u8FDC\u8FDC\u6C64\u5F00\u3002</p><p>\u6BCD\u4EB2\u751F\u4E0B\u7684\u662F\u4E00\u4E2A\u7537\u5B69\uFF0C\u6BCD\u5B50\u4FE9\u73B0\u5728\u597D\u5417\uFF1F\u4ED6\u5DF2\u7ECF\u88AB\u81EA\u5DF1\u4EB2\u624B\u6740\u6B7B\uFF0C\u6709\u53F6\u4F2F\u4F2F\u5728\uFF0C\u5E94\u8BE5\u4E0D\u4F1A\u8650\u5F85\u5979\u4EEC\u5427\u3002\u5E08\u7236\u548C\u5E08\u59D0\u8BF4\u4E0D\u5B9A\u4E5F\u4E0D\u7528\u518D\u88AB\u88F8\u8EAB\u6254\u5728\u51B0\u5929\u96EA\u5730\u91CC\u2014\u2014\u4E24\u5929\u540E\u5B89\u987F\u597D\u7EAA\u5E08\u59D0\u548C\u660E\u5170\uFF0C\u6700\u591A\u4E03\u5929\uFF0C\u5C31\u53EF\u4EE5\u8D76\u5230\u661F\u6708\u6E56\u3002</p><p>\u4E5F\u8BB8\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5728\u4E00\u8D77\u8FC7\u5E74\u2026\u2026</p><p>\u5927\u5385\u4E2D\u5750\u7740\u4E00\u7FA4\u8180\u5927\u8170\u5706\u7684\u6C49\u5B50\uFF0C\u6B63\u56F4\u7740\u706B\u5858\u8C08\u5F97\u70ED\u706B\u671D\u5929\uFF0C\u5FFD\u7136\u6709\u4EBA\u9AD8\u58F0\u9053\uFF1A\u300C\u2026\u2026\u8FD8\u662F\u73AB\u7470\u4ED9\u5B50\uFF01\u300D</p><p>\u7D2B\u73AB\u95FB\u58F0\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u4E3E\u76EE\u770B\u53BB\u3002</p>',61);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
