import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_123" tabindex="-1"><a class="header-anchor" href="#_123" aria-hidden="true">#</a> 123</h1><p>\u4E00\u4F17\u5927\u6C49\u8138\u88AB\u706B\u5149\u6620\u5F97\u901A\u7EA2\uFF0C\u706B\u91CC\u70E4\u7740\u5927\u5757\u7684\u732A\u80A9\uFF0C\u5468\u56F4\u6254\u7740\u51E0\u53E3\u9152\u575B\uFF0C\u4E00\u770B\u4FBF\u662F\u6C5F\u6E56\u8C6A\u5BA2\u3002</p><p>\u300C\u90FD\u8BF4\u73AB\u7470\u4ED9\u5B50\u957F\u5F97\u6F02\u4EAE\uFF0C\u4F60\u4EEC\u8C01\u89C1\u8FC7\uFF1F\u300D</p><p>\u300C\u6211\uFF01\u300D\u4E00\u540D\u6C49\u4E86\u80F8\u53E3\u62CD\u5F97\u5C71\u54CD\uFF0C\u300C\u53BB\u5E74\u6155\u5BB9\u80DC\u5927\u5A5A\uFF0C\u5144\u5F1F\u5949\u7A0B\u5E2E\u4E3B\u7684\u547D\u4EE4\u53BB\u4F0F\u9F99\u6DA7\u9001\u793C\u3002\u627F\u6155\u5BB9\u5BE8\u4E3B\u770B\u5F97\u8D77\uFF0C\u7559\u5144\u5F1F\u4F4F\u4E86\u4E24\u65E5\u3002\u64CD\uFF01\u300D</p><p>\u4ED6\u4E00\u62CD\u5927\u817F\uFF0C\u4E24\u773C\u653E\u5149\uFF0C\u300C\u90FD\u8BF4\u98D8\u6885\u5CF0\u7F8E\u5973\u5982\u4E91\uFF0C\u90A3\u5929\u5144\u5F1F\u771F\u662F\u5F00\u773C\u4E86\u3002\u5F53\u65F6\u98D8\u6885\u5CF0\u6765\u4E86\u4E09\u4E2A\uFF0C\u5BD2\u6708\u5200\u6797\u5973\u4FA0\u662F\u65B0\u5A18\uFF0C\u540E\u9762\u8DDF\u7740\u7275\u4E1D\u624B\u7EAA\u5973\u4FA0\u3002\u8FD9\u4E24\u4E2A\u5F80\u90A3\u513F\u4E00\u7AD9\uFF0C\u771F\u4ED6\u5988\u6BD4\u82B1\u5A07\uFF0C\u6BD4\u7389\u9999\u3002\u5144\u5F1F\u7684\u4E09\u9B42\u516D\u9B44\u4E00\u4E0B\u5C31\u88AB\u52FE\u8D70\u4E86\u4E00\u534A\u3002\u4E56\u4E56\uFF0C\u771F\u6CA1\u60F3\u5230\u4E16\u4E0A\u8FD8\u6709\u8FD9\u79CD\u7F8E\u4EBA\u513F\u3002\u6BD4\u8D77\u6765\u4E1C\u6D77\u7684\u6DF3\u4E8E\u7476\u5C31\u662F\u4E2A\u70E7\u706B\u4E2B\u5934\u3002\u300D</p><p>\u4ED6\u8BF4\u5F97\u53E3\u6CAB\u6A2A\u98DE\uFF0C\u5468\u56F4\u4EBA\u542C\u5F97\u76EE\u77AA\u53E3\u5446\u3002</p><p>\u300C\u5144\u5F1F\u5F53\u65F6\u60F3\uFF0C\u7F8E\u5230\u8FD9\u5730\u6B65\u4E5F\u7B97\u5230\u5934\u4E86\uFF0C\u6155\u5BB9\u80DC\u90A3\u5C0F\u5B50\u4E00\u8868\u4EBA\u6750\uFF0C\u529F\u592B\u5F88\u662F\u4E86\u5F97\uFF0C\u518D\u5A36\u4E86\u5BD2\u6708\u5200\u771F\u662F\u6709\u798F\u6C14\u3002\u8C01\u77E5\u9053\u554A\u2026\u2026\u300D\u4ED6\u6447\u4E86\u6447\u5934\uFF0C\u62FF\u8D77\u9152\u7897\u3002</p><p>\u65C1\u8FB9\u6709\u4EBA\u8FDE\u5FD9\u7ED9\u4E86\u7897\u9152\uFF0C\u300C\u5B5F\u4E09\u54E5\uFF0C\u522B\u5356\u5173\u5B50\u4E86\u3002\u300D</p><p>\u5B5F\u4E09\u54E5\u628A\u7897\u4E3E\u5230\u5634\u8FB9\uFF0C\u300C\u8981\u8BF4\u8273\u798F\uFF0C\u5F53\u4E0A\u6155\u5BB9\u80DC\u7684\u59B9\u592B\u624D\u771F\u662F\u8273\u798F\u9F50\u5929\uFF01\u300D</p><p>\u4ED6\u5495\u561F\u559D\u4E86\u4E00\u5927\u53E3\uFF0C\u7528\u624B\u80CC\u62B9\u4E86\u4E00\u62B9\u5634\uFF0C\u300C\u73AB\u7470\u4ED9\u5B50\u4E00\u9732\u9762\uFF0C\u6EE1\u5C4B\u7684\u4EBA\u90FD\u50BB\u4E86\u3002\u90A3\u4F53\u6001\uFF0C\u90A3\u76F8\u8C8C\uFF0C\u7B80\u76F4\u662F\u5AE6\u5A25\u4E0B\u51E1\uFF01\u5144\u5F1F\u6211\u80FD\u770B\u4E0A\u4E00\u773C\uFF0C\u8FD9\u8F88\u5B50\u4E5F\u4E0D\u7B97\u767D\u6D3B\u4E86\uFF01\u300D</p><p>\u4F17\u4EBA\u8F70\u58F0\u4E00\u7247\uFF0C\u5BF9\u73AB\u7470\u4ED9\u5B50\u7684\u7F8E\u8272\u5FC3\u9A70\u795E\u5F80\u3002</p><p>\u7D2B\u73AB\u5782\u4E0B\u773C\uFF0C\u6402\u7740\u5973\u513F\u8F7B\u8F7B\u6447\u6643\uFF0C\u8138\u4E0A\u6BEB\u65E0\u8868\u60C5\u3002</p><p>\u300C\u8BF4\u5F97\u597D\u542C\uFF0C\u98D8\u6885\u5CF0\u8FD8\u4E0D\u662F\u5408\u95E8\u90FD\u5F53\u4E86\u5A4A\u5B50\uFF1F\u300D\u65C1\u8FB9\u4F20\u6765\u4E00\u4E2A\u523B\u8584\u7684\u58F0\u97F3\u3002</p><p>\u300C\u653E\u5C41\uFF01\u300D\u5B5F\u4E09\u54E5\u4E00\u628A\u6454\u6389\u9152\u7897\uFF0C\u6012\u9053\uFF0C\u300C\u54EA\u4E2A\u4E0D\u8981\u8138\u7684\u6DF7\u86CB\u7F16\u51FA\u6765\u7684\uFF01\u300D</p><p>\u90A3\u4EBA\u51B7\u7B11\u4E00\u58F0\uFF0C\u7AD9\u8D77\u8EAB\u6765\uFF0C\u300C\u98D8\u6885\u5CF0\u8BF8\u5973\u5728\u661F\u6708\u6E56\u4E3A\u5974\u65E9\u5DF2\u8F70\u4F20\u6C5F\u6E56\uFF0C\u8C01\u4E0D\u77E5\u9053\u4F60\u8BF4\u7684\u5BD2\u6708\u5200\u5982\u4ECA\u53EA\u662F\u661F\u6708\u6E56\u7684\u4E00\u6761\u72D7\uFF1F\u300D</p><p>\u300C\u653E\u4ED6\u5976\u5976\u7684\u81ED\u5C41\uFF01\u4EC0\u4E48\u661F\u6708\u6E56\uFF0C\u8001\u5B50\u95EF\u8361\u6C5F\u6E56\u51E0\u5341\u5E74\uFF0C\u4ECE\u6765\u6CA1\u542C\u8BF4\u8FC7\u661F\u6708\u6E56\u7684\u5B57\u53F7\uFF0C\u66F4\u6CA1\u89C1\u8FC7\u4E00\u4E2A\u9E1F\u4EBA\uFF01\u98D8\u6885\u5CF0\u867D\u662F\u5973\u6D41\u4E4B\u8F88\uFF0C\u53EF\u6D41\u971C\u5251\u3001\u5BD2\u6708\u5200\u6B66\u529F\u7CBE\u5F3A\uFF0C\u5C31\u662F\u5927\u5B5A\u7075\u9E6B\u5BFA\u548C\u4E5D\u534E\u5251\u6D3E\u4E5F\u4E0D\u89C1\u5F97\u6709\u8FD9\u7B49\u9AD8\u624B\uFF01\u6797\u5973\u4FA0\u6211\u4EB2\u773C\u89C1\u8FC7\uFF0C\u8C6A\u6C14\u4E0D\u51CF\u987B\u7709\uFF0C\u8BF4\u5979\u4F1A\u5982\u4F55\u5982\u4F55\uFF0C\u9B3C\u624D\u4FE1\uFF01\u300D</p><p>\u90A3\u4EBA\u51B7\u7B11\u9053\uFF1A\u300C\u98CE\u665A\u534E\u548C\u6797\u9999\u8FDC\u786E\u5B9E\u6709\u79CD\uFF0C\u6240\u4EE5\u53D7\u5F97\u82E6\u4E5F\u6700\u591A\u3002\u50CF\u4F60\u8BF4\u7684\u73AB\u7470\u4ED9\u5B50\uFF0C\u539A\u989C\u65E0\u803B\uFF0C\u5BF9\u5E08\u95E8\u60E8\u5267\u4E0D\u7406\u4E0D\u95EE\uFF0C\u4E0D\u4F46\u59D4\u8EAB\u4EC7\u654C\uFF0C\u8FD8\u4E0E\u4EB2\u5A18\u5171\u4E8B\u4E00\u592B\uFF0C\u5982\u6B64\u4E0D\u8981\u8138\u7684\u5973\u4EBA\uFF0C\u4E5F\u662F\u6B66\u6797\u4E00\u7EDD\u2026\u2026\u300D</p><p>\u300C\u53BB\u4F60\u5988\u7684\uFF01\u300D\u5B5F\u4E09\u54E5\u538B\u6839\u4E0D\u4FE1\u4ED6\u7684\u80E1\u626F\uFF0C\u542C\u5230\u4ED6\u5982\u6B64\u4EB5\u6E0E\u81EA\u5DF1\u5FC3\u76EE\u4E2D\u7684\u5973\u795E\uFF0C\u4E00\u628A\u62CE\u8D77\u9152\u575B\u5C31\u6254\u4E86\u8FC7\u53BB\u3002</p><p>\u90A3\u4EBA\u62AC\u624B\u4E00\u62F3\uFF0C\u5C06\u9152\u575B\u6253\u5F97\u7C89\u788E\u3002</p><p>\u5385\u4E2D\u7684\u5BA2\u5546\u884C\u4EBA\u90FD\u88AB\u4E24\u4EBA\u5927\u58F0\u4E89\u5435\u60CA\u9192\uFF0C\u6B64\u65F6\u89C1\u53CC\u65B9\u52A8\u8D77\u624B\u6765\uFF0C\u987F\u65F6\u4E71\u6210\u4E00\u7247\u3002</p><p>\u5B5F\u4E09\u54E5\u4E00\u65F6\u6C14\u6124\u51B2\u660F\u4E86\u5934\u8111\uFF0C\u773C\u89C1\u90A3\u4EBA\u529F\u592B\u4E86\u5F97\uFF0C\u4E8E\u662F\u6C89\u58F0\u95EE\u9053\uFF1A\u300C\u4F60\u662F\u4EC0\u4E48\u4EBA\uFF1F\u300D</p><p>\u90A3\u4EBA\u4ECE\u6000\u4E2D\u6478\u51FA\u4E00\u5F20\u7EB8\u5728\u7A7A\u4E2D\u4E00\u626C\uFF0C\u6717\u58F0\u9053\uFF1A\u300C\u672C\u4EBA\u67F3\u9E23\u6B67\uFF0C\u63A5\u5927\u5B5A\u7075\u9E6B\u5BFA\u65B9\u4E08\u5706\u76F8\u5E08\u53D4\u624B\u4E66\uFF0C\u521A\u5728\u6D1B\u9633\u706D\u6389\u957F\u9E70\u4F1A\u3002\u4ECA\u65E5\u4E0E\u5929\u4E0B\u767D\u9053\u540C\u8D74\u7EC8\u5357\u5C71\uFF0C\u626B\u8361\u661F\u6708\u6E56\u4F59\u5B7D\uFF01\u300D</p><p>\u5B5F\u4E09\u54E5\u5C06\u4FE1\u5C06\u7591\uFF0C\u53EA\u89C1\u90A3\u4EBA\u8EAB\u540E\u4E00\u7FA4\u4EBA\u5C3D\u662F\u8170\u7F20\u767D\u5E26\uFF0C\u795E\u60C5\u6FC0\u6124\uFF0C\u663E\u7136\u90FD\u662F\u5927\u5B5A\u7075\u9E6B\u5BFA\u7684\u4FD7\u5BB6\u5F1F\u5B50\u3002\u4ED6\u6123\u4E86\u4F1A\u513F\u795E\uFF0C\u6014\u6014\u9053\uFF1A\u300C\u73AB\u7470\u4ED9\u5B50\u771F\u4F1A\u90A3\u6837\uFF1F\u300D</p><p>\u67F3\u9E23\u6B67\u6B63\u5BB9\u9053\uFF1A\u300C\u5B5F\u670B\u53CB\u82E5\u662F\u4E0D\u4FE1\uFF0C\u4E0E\u6211\u7B49\u9F50\u8D74\u7EC8\u5357\uFF0C\u4E00\u63A2\u7A76\u7ADF\uFF0C\u5982\u4F55\uFF1F\u300D</p><p>\u5B5F\u4E09\u54E5\u72B9\u8C6B\u672A\u51B3\uFF0C\u67F3\u9E23\u6B67\u8EAB\u540E\u53C8\u6709\u4EBA\u9053\uFF1A\u300C\u73AB\u7470\u4ED9\u5B50\u8DDF\u5979\u4EB2\u5A18\u540C\u4E8B\u4E00\u592B\uFF0C\u662F\u4E1C\u65B9\u5927\u4FA0\u4EB2\u773C\u6240\u89C1\uFF0C\u5C82\u80FD\u6709\u5047\uFF1F\u300D</p><p>\u53E6\u4E00\u4EBA\u63A5\u53E3\u9053\uFF1A\u300C\u4EC0\u4E48\u73AB\u7470\u4ED9\u5B50\uFF0C\u4E0D\u8FC7\u662F\u4E2A\u4E0D\u8981\u8138\u7684\u5A4A\u5B50\u3002\u542C\u8BF4\u5A18\u513F\u4FE9\u90FD\u88AB\u661F\u6708\u6E56\u7684\u4EBA\u5F04\u5927\u4E86\u809A\u5B50\uFF0C\u73B0\u5728\u53EA\u6015\u8BE5\u751F\u4E0B\u6765\u6742\u79CD\u4E86\u5427\u3002\u300D</p><p>\u8FD9\u4E9B\u4EBA\u5BF9\u661F\u6708\u6E56\u6068\u4E4B\u5165\u9AA8\uFF0C\u53E3\u91CC\u4E0D\u5E72\u4E0D\u51C0\uFF0C\u8D8A\u8BF4\u8D8A\u662F\u4E0B\u6D41\uFF0C\u5C06\u73AB\u7470\u4ED9\u5B50\u8BF4\u6210\u662F\u5929\u4E0B\u7B2C\u4E00\u4E0D\u8981\u8138\u7684\u6DEB\u5987\u3002</p><p>\u7D2B\u73AB\u5FC3\u5E95\u6EF4\u8840\uFF0C\u9762\u7EB1\u4E0B\u7684\u4FCF\u8138\u65F6\u7EA2\u65F6\u767D\u3002\u90A3\u4E9B\u65E0\u60C5\u7684\u8FB1\u9A82\u50CF\u4E00\u628A\u628A\u5229\u5203\uFF0C\u5C06\u5979\u5272\u5F97\u4F53\u65E0\u5B8C\u80A4\u3002\u5979\u51DD\u89C6\u7740\u5973\u513F\u606C\u9759\u7684\u7761\u5BB9\uFF0C\u611F\u89C9\u81EA\u5DF1\u4E00\u5BF8\u4E00\u5BF8\u5316\u4E3A\u7070\u70EC\u3002</p><p>\u4F17\u4EBA\u7684\u8A00\u8BED\u8D8A\u6765\u8D8A\u4E0D\u582A\u5165\u8033\uFF0C\u89D2\u843D\u91CC\u4E00\u4E2A\u7626\u957F\u7684\u8EAB\u5F71\u518D\u542C\u4E0D\u4E0B\u53BB\uFF0C\u8E52\u8DDA\u7740\u79BB\u5F00\u5927\u5385\u3002\u7D2B\u73AB\u63A9\u7D27\u5973\u513F\u7684\u8941\u8913\uFF0C\u95EA\u8EAB\u51FA\u4E86\u5BA2\u6808\uFF0C\u8D70\u5165\u65E0\u8FB9\u7684\u98CE\u96EA\u4E4B\u4E2D\u3002</p><p>\u90A3\u4EBA\u559D\u5F97\u9169\u914A\u5927\u9189\uFF0C\u624B\u91CC\u5140\u81EA\u62FF\u7740\u9152\u58F6\u3002\u4ED6\u8EAB\u5B50\u6B6A\u6B6A\u659C\u659C\uFF0C\u6B65\u5C65\u4E0D\u7A33\uFF0C\u8D70\u5F97\u5374\u662F\u6781\u5FEB\uFF0C\u8F6C\u773C\u4FBF\u5230\u4E86\u6CB3\u8FB9\u3002</p><p>\u90A3\u4EBA\u811A\u4E0B\u4E00\u6ED1\u91CD\u91CD\u6454\u5728\u51B0\u9762\u4E0A\uFF0C\u624B\u91CC\u7684\u9152\u58F6\u6EF4\u6E9C\u6E9C\u6ED1\u51FA\u6570\u4E08\u3002\u4ED6\u624B\u811A\u5E76\u7528\uFF0C\u8E09\u8E09\u8DC4\u8DC4\u5730\u722C\u8FC7\u53BB\uFF0C\u6EE1\u5934\u98A4\u6296\u7684\u767D\u53D1\u626B\u8D77\u96EA\u7C92\uFF0C\u5982\u540C\u8427\u7D22\u7684\u843D\u53F6\u3002</p><p>\u7D2B\u73AB\u98D8\u8EAB\u6361\u8D77\u9152\u58F6\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4E1C\u65B9\u4F2F\u4F2F\u3002\u300D</p><p>\u90A3\u4EBA\u6D51\u8EAB\u4E00\u9707\uFF0C\u4E24\u773C\u51DD\u89C6\u7740\u9762\u524D\u6000\u62B1\u5A74\u513F\u7684\u5C11\u5973\uFF0C\u773C\u795E\u6E10\u6E10\u9510\u5229\u8D77\u6765\u3002</p><p>\u826F\u4E45\uFF0C\u4E1C\u65B9\u5E86\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u5728\u8FD9\u91CC\uFF1F\u300D\u58F0\u97F3\u53C8\u5E72\u53C8\u54D1\uFF0C\u66F4\u6709\u79CD\u51B3\u7EDD\u7684\u610F\u5473\u3002</p><p>\u7D2B\u73AB\u6CA1\u6709\u56DE\u7B54\uFF0C\u7EA4\u624B\u5B9B\u5982\u5BD2\u98CE\u91CC\u7684\u51B0\u96D5\uFF0C\u9759\u9759\u4E3E\u7740\u9152\u58F6\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u4E1C\u65B9\u4F2F\u4F2F\uFF0C\u6211\u771F\u7684\u662F\u4E0D\u8981\u8138\u7684\u5973\u4EBA\u5417\uFF1F\u300D</p><p>\u4E1C\u65B9\u5E86\u770B\u7740\u5979\u6000\u91CC\u7684\u5A74\u513F\uFF0C\u618E\u6076\u5730\u8BF4\u9053\uFF1A\u300C\u597D\uFF01\u7ADF\u7136\u751F\u4E0B\u6765\u4E86\uFF01\u4F60\u7236\u4EB2\u3001\u54E5\u54E5\u88AB\u6740\uFF0C\u6BCD\u4EB2\u3001\u5AC2\u5AC2\u53D7\u8FB1\uFF0C\u5E08\u7236\u5E08\u59D0\u751F\u4E0D\u5982\u6B7B\uFF0C\u4F60\u7ADF\u7136\u8FD8\u4E3A\u4EC7\u4EBA\u751F\u4E0B\u5B69\u5B50\uFF01\u300D</p><p>\u4E1C\u65B9\u5E86\u9A88\u6307\u543C\u9053\uFF1A\u300C\u4F60\u77E5\u4E0D\u77E5\u9053\u4EC0\u4E48\u7F9E\u803B\uFF01\u7ADF\u7136\u8FD8\u6709\u8138\u95EE\u4EC0\u4E48\u53EB\u4E0D\u8981\u8138\uFF01\u4F60\u8FD9\u4E2A\u8D2A\u751F\u6015\u6B7B\u7684\u6DEB\u5987\uFF01\u8D31\u4EBA\uFF01\u300D\u4ED6\u58F0\u5636\u529B\u7AED\uFF0C\u988C\u4E0B\u7684\u767D\u987B\u6068\u5F97\u4E00\u9635\u4E71\u6296\u3002</p><p>\u7D2B\u73AB\u6C89\u9ED8\u534A\u664C\uFF0C\u6700\u540E\u51C4\u7136\u9053\uFF1A\u300C\u539F\u6765\u662F\u8FD9\u6837\u3002\u662F\u56E0\u4E3A\u6211\u6CA1\u6709\u53BB\u6B7B\u3002\u662F\u5417\uFF1F\u300D</p><p>\u4E1C\u65B9\u5E86\u6006\u7136\u53EB\u9053\uFF1A\u300C\u4F60\u8FD8\u6709\u4EC0\u4E48\u8138\u6D3B\u5728\u4E16\u4E0A\uFF01\u300D</p><p>\u7D2B\u73AB\u62B1\u7740\u5973\u513F\u7684\u624B\u4E00\u6C89\uFF0C\u6380\u5F00\u8863\u895F\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u4E1C\u65B9\u4F2F\u4F2F\uFF0C\u4F60\u6740\u4E86\u6211\u5427\u3002\u300D</p><p>\u4E1C\u65B9\u5E86\u4E0D\u8FC7\u4E94\u5341\u4F59\u5C81\uFF0C\u4F46\u6EE1\u5934\u767D\u53D1\u5374\u50CF\u5E74\u8FC7\u516B\u5341\u7684\u8870\u673D\u8001\u7FC1\u3002\u4ED6\u54C6\u55E6\u7740\u6478\u51FA\u4F69\u5251\uFF0C\u4F46\u5251\u950B\u5728\u767D\u7389\u822C\u7684\u80F8\u819B\u524D\u6643\u6765\u6643\u53BB\uFF0C\u5374\u600E\u4E48\u4E5F\u4E0B\u4E0D\u4E86\u624B\u3002</p><p>\u7D2B\u73AB\u95ED\u4E0A\u773C\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u6211\u4E0D\u60F3\u6B7B\u3002\u4F46\u5982\u679C\u4E00\u6B7B\u80FD\u6362\u6765\u6211\u5A18\u548C\u5E08\u7236\u3001\u5E08\u59D0\u7684\u5E73\u5B89\uFF0C\u6211\u4E5F\u4E0D\u6015\u6B7B\u3002\u300D</p><p>\u5BD2\u98CE\u4E4D\u8D77\uFF0C\u6CB3\u4E0A\u7684\u96EA\u82B1\u6CE2\u6D9B\u822C\u6EDA\u6D8C\u800C\u81F3\uFF0C\u5728\u7D2B\u73AB\u811A\u524D\u65CB\u8F6C\u800C\u8D77\uFF0C\u50CF\u4E00\u6761\u76D8\u65CB\u7684\u7389\u9F99\uFF0C\u5C06\u6BCD\u5973\u4FE9\u88F9\u5728\u5176\u4E2D\u3002</p><p>\u4F4E\u5A49\u6B32\u7EDD\u7684\u58F0\u97F3\u5728\u82CD\u832B\u7684\u96EA\u591C\u54CD\u8D77\uFF0C\u300C\u6BCF\u4E2A\u4EBA\u90FD\u8BF4\u6211\u4E0D\u8981\u8138\u2026\u2026\u96BE\u9053\u662F\u6211\u7684\u9519\u5417\uFF1F\u96BE\u9053\u6211\u5C31\u662F\u8BE5\u6B7B\uFF1F\u300D</p><p>\u300C\u4E1C\u65B9\u4F2F\u4F2F\uFF0C\u4F60\u544A\u8BC9\u6211\u597D\u5417\uFF1F\u300D</p><p>\u51DD\u5149\u5251\u300C\u53EE\u5577\u300D\u4E00\u58F0\u6389\u5728\u51B0\u4E0A\u3002</p><p>\u7D2B\u73AB\u63A9\u4F4F\u80F8\u53E3\uFF0C\u7389\u8138\u82CD\u767D\u7684\u4EE4\u4EBA\u5FC3\u75BC\u3002\u5979\u8F7B\u58F0\u9053\uFF1A\u300C\u6211\u8981\u5148\u53BB\u6D1B\u9633\u4E00\u8D9F\u3002\u5982\u679C\u8D76\u4E0D\u4E0A\u653B\u6253\u661F\u6708\u6E56\u4E4B\u5F79\uFF0C\u8FD8\u8BF7\u4E1C\u65B9\u4F2F\u4F2F\u591A\u7167\u6599\u6211\u5A18\u4E00\u4E9B\u3002\u300D</p><p>\u8BDD\u97F3\u672A\u843D\uFF0C\u5DF2\u7ECF\u770B\u4E0D\u89C1\u7D2B\u73AB\u7684\u8EAB\u5F71\u3002</p><p>\u4E1C\u65B9\u5E86\u867D\u7136\u9189\u773C\u8FF7\u79BB\uFF0C\u4F46\u76EE\u5149\u8FDC\u8F83\u5E38\u4EBA\u9510\u5229\u3002\u5355\u770B\u7D2B\u73AB\u5FEB\u6377\u65E0\u4F26\u7684\u8EAB\u6CD5\uFF0C\u90A3\u5DF2\u7ECF\u4E0D\u4EC5\u4EC5\u662F\u8F7B\u529F\u5353\u7EDD\uFF0C\u800C\u662F\u8EAB\u6000\u6709\u7740\u6D51\u539A\u65E0\u5339\u7684\u5185\u529F\u3002\u82E5\u975E\u4EB2\u773C\u6240\u89C1\uFF0C\u4E1C\u65B9\u5E86\u7EDD\u4E0D\u4F1A\u76F8\u4FE1\u5979\u5C0F\u5C0F\u5E74\u7EAA\uFF0C\u529F\u529B\u7ADF\u7136\u8FDC\u8D85\u81EA\u5DF1\u3002</p><p>\u53EF\u6068\u5979\u6789\u6709\u7EDD\u4E16\u6B66\u529F\uFF0C\u7ADF\u7136\u5FCD\u5FC3\u8FDE\u4EB2\u5A18\u4E5F\u4E0D\u6551\u3002</p><p>\u60F3\u8D77\u8427\u4F5B\u5974\u6240\u53D7\u7684\u82E6\u96BE\uFF0C\u4E1C\u65B9\u5E86\u4E3E\u8D77\u9152\u58F6\uFF0C\u72E0\u72E0\u704C\u4E86\u4E00\u53E3\u3002\u9152\u6C34\u5165\u53E3\uFF0C\u4ED6\u624D\u60CA\u89C9\u9152\u6C34\u7ADF\u7136\u662F\u6EDA\u70EB\u7684\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E24\u65E5\u540E\uFF0C\u7D2B\u73AB\u8D76\u5230\u6D1B\u9633\u3002</p><p>\u7136\u800C\u9999\u6708\u697C\u5DF2\u662F\u4EBA\u53BB\u697C\u7A7A\u3002\u5979\u968F\u5373\u8D76\u5F80\u957F\u9E70\u4F1A\uFF0C\u53EF\u6614\u65E5\u8C6B\u5DDE\u7684\u6D1B\u9633\u7B2C\u4E00\u5927\u5E2E\uFF0C\u53EA\u5269\u4E0B\u7126\u571F\u3002\u5979\u65AD\u65AD\u7EED\u7EED\u7A83\u542C\u4E86\u770B\u5B88\u8005\u7684\u5BF9\u8BDD\uFF0C\u624D\u77E5\u9053\u56DB\u65E5\u524D\u957F\u9E70\u4F1A\u88AB\u706D\u65F6\uFF0C\u6574\u4E2A\u5E2E\u4F1A\u90FD\u50CF\u84B8\u53D1\u822C\u4E0D\u590D\u5B58\u5728\uFF0C\u53EA\u5269\u4E0B\u859B\u957F\u9E70\u548C\u859B\u6B23\u598D\u7236\u5973\u4FE9\u3002\u9999\u6708\u697C\u65E9\u5728\u534A\u6708\u524D\u5C31\u505C\u6B62\u8425\u4E1A\uFF0C\u697C\u5185\u7684\u5993\u5973\u4E0D\u77E5\u6240\u7EC8\u3002</p><p>\u7D2B\u73AB\u6BEB\u4E0D\u505C\u7559\u5730\u5192\u96EA\u76F4\u5954\u7EC8\u5357\uFF0C\u4EC5\u7528\u4E86\u4E09\u5929\u65F6\u95F4\u5C31\u8D76\u5230\u5C71\u4E0B\u3002\u5979\u4E0D\u613F\u4E0E\u6C5F\u6E56\u4EBA\u58EB\u89C1\u9762\uFF0C\u66F4\u4E0D\u613F\u5E08\u95E8\u8BF8\u5973\u906D\u53D7\u7684\u975E\u4EBA\u6DEB\u8650\u88AB\u4EBA\u770B\u5230\u3002\u56E0\u6B64\u4E0D\u987E\u4E00\u5207\u7684\u6467\u53D1\u7740\u5185\u529B\uFF0C\u663C\u591C\u517C\u7A0B\uFF0C\u60F3\u8D76\u5728\u4F17\u4EBA\u4E4B\u524D\u8FDB\u5165\u661F\u6708\u6E56\uFF0C\u6551\u51FA\u81EA\u5DF1\u7684\u4EB2\u4EBA\u3002</p><p>\u4F46\u9634\u957F\u91CE\u90A3\u4E00\u638C\u5E76\u975E\u597D\u5FC3\uFF0C\u5728\u5C71\u4E0B\uFF0C\u86F0\u4F0F\u6708\u4F59\u7684\u5185\u606F\u7A81\u7136\u53D1\u4F5C\uFF0C\u4E0D\u4F46\u6765\u52BF\u51F6\u731B\u8FD8\u9971\u542B\u6BD2\u6027\u3002\u82E5\u975E\u7D2B\u73AB\u7684\u51E4\u51F0\u5B9D\u5178\u5DF2\u81F3\u5927\u6210\uFF0C\u53D1\u4F5C\u4E4B\u65E5\uFF0C\u5C31\u662F\u5979\u6BD9\u547D\u4E4B\u65F6\u3002</p><p>\u5979\u7528\u4E86\u6570\u5929\u65F6\u95F4\u624D\u628A\u90A3\u80A1\u9634\u6BD2\u7684\u638C\u529B\u9A71\u9010\u8F6C\u5316\u3002\u5C31\u5728\u8FD9\u6BB5\u65E5\u5B50\u91CC\uFF0C\u5979\u53D1\u73B0\u81EA\u5DF1\u7684\u529F\u529B\u518D\u6B21\u5927\u8FDB\uFF0C\u8DDD\u7B2C\u4E5D\u5C42\u51E4\u6E05\u7D2B\u9E3E\u53EA\u6709\u4E00\u6B65\u4E4B\u9065\u3002\u7D2B\u73AB\u4E0D\u6E05\u695A\u81EA\u5DF1\u4E3A\u4F55\u4F1A\u5982\u6B64\u987A\u5229\uFF0C\u601D\u7D22\u7740\u5E08\u7236\u5F53\u65E5\u8BF4\u7684\u300C\u9634\u4E0A\u52A0\u9634\u300D\uFF0C\u5979\u4F3C\u4E4E\u660E\u767D\u4E86\u4E00\u4E9B\u3002</p><p>\u7D2B\u73AB\u62B1\u4F4F\u5973\u513F\u67D4\u58F0\u9053\uFF1A\u300C\u6674\u6674\uFF0C\u5A18\u5E26\u4F60\u53BB\u89C1\u5916\u5A46\u3002\u4F60\u5916\u5A46\u662F\u4E16\u4E0A\u6700\u597D\u7684\u4EBA\uFF0C\u5C31\u50CF\u89C2\u97F3\u83E9\u8428\u4E00\u6837\uFF0C\u53C8\u6F02\u4EAE\u53C8\u6148\u8BE6\u2026\u2026\u8FD8\u6709\u53E6\u4E00\u4E2A\u5A46\u5A46\u3002\u5979\u662F\u4E16\u4E0A\u7B2C\u4E00\u5927\u4FA0\uFF0C\u6240\u6709\u574F\u4EBA\u89C1\u5230\u5979\u90FD\u4F1A\u5BB3\u6015\u2026\u2026\u300D\u7D2B\u73AB\u54FD\u54BD\u8D77\u6765\uFF0C\u5979\u62B9\u4E86\u62B9\u773C\u89D2\uFF0C\u7B11\u9053\uFF1A\u300C\u6674\u6674\u4E0D\u7528\u6015\uFF0C\u5979\u4E00\u5B9A\u4F1A\u559C\u6B22\u4F60\u7684\u2026\u2026\u300D</p><p>\u5C3E\u58F0</p><p>\u6751\u5B50\u91CC\u71C3\u8D77\u7BDD\u706B\uFF0C\u4EBA\u4EEC\u6572\u9523\u6253\u9F13\u559C\u6C14\u6D0B\u6D0B\u3002\u4ECA\u65E5\u662F\u9664\u5915\u4E4B\u591C\u4E86\u3002</p><p>\u5C3E\u58F0\u591C\u665A\u6700\u9ED1\u6697\u7684\u65F6\u523B\uFF0C\u7D2B\u73AB\u518D\u6B21\u8E0F\u4E0A\u6708\u5C9B\u3002</p><p>\u7EC8\u7A76\u8FD8\u662F\u6765\u665A\u4E86\u4E00\u6B65\uFF0C\u5C31\u5728\u5979\u7AED\u529B\u5BF9\u6297\u9634\u957F\u91CE\u9057\u7559\u7684\u6BD2\u529B\u65F6\uFF0C\u6B66\u6797\u767D\u9053\u4E0E\u90AA\u9053\u7B2C\u4E00\u5927\u6559\u661F\u6708\u6E56\u7684\u6218\u6597\u5DF2\u7ECF\u7ED3\u675F\u3002</p><p>\u6574\u4E2A\u5C9B\u5C7F\u50CF\u88AB\u72C2\u98CE\u626B\u8FC7\u4E00\u822C\uFF0C\u518D\u6CA1\u6709\u4E00\u5904\u5B8C\u6574\u7684\u89D2\u843D\uFF0C\u65AD\u6881\u6B8B\u67F1\u90FD\u534A\u63A9\u5728\u767D\u7691\u7691\u7684\u79EF\u96EA\u4E0B\uFF0C\u540C\u65F6\u63A9\u76D6\u7684\uFF0C\u8FD8\u6709\u4E09\u65E5\u524D\u90A3\u573A\u8840\u6218\u7684\u75D5\u8FF9\u3002</p><p>\u7CFB\u7740\u5AC2\u5AC2\u7684\u680F\u6746\u788E\u6210\u6570\u6BB5\uFF0C\u4E0A\u9762\u8FD8\u7CFB\u7740\u534A\u622A\u7ED3\u51B0\u7684\u94B1\u94FE\u3002</p><p>\u7D2B\u73AB\u633D\u8D77\u94C1\u94FE\uFF0C\u6014\u6014\u770B\u7740\u680F\u6746\u3002\u65AD\u53E3\u5904\u8D6B\u7136\u5370\u7740\u4E00\u4E2A\u7EA4\u7EC6\u7684\u638C\u5370\u2500\u2500\u534A\u5C3A\u5BBD\u7684\u6C49\u767D\u7389\u680F\u6746\u7ADF\u662F\u88AB\u4EBA\u4E00\u638C\u51FB\u788E\u7684\u3002</p><p>\u5979\u4E3E\u76EE\u671B\u53BB\uFF0C\u96F6\u788E\u4E0D\u582A\u7684\u77F3\u9636\u4E0A\uFF0C\u50CF\u5F81\uFF5B\u201C\u6587\uFF5D\u7740\u661F\u6708\u6E56\u81F3\u9AD8\uFF5B\u201C\u4EBA\uFF5D\u65E0\u5C1A\u5730\u4F4D\u7684\u795E\uFF5B\u201C\u4E66\uFF5D\u6BBF\u5DF2\u88AB\u70C8\u706B\u711A\uFF5B\u201C\u5C4B\uFF5D\u6BC1\uFF0C\u7C89\u788E\u7684\u7816\u77F3\u95F4\u5806\u6EE1\u70E7\u6210\u7070\u70AD\u7684\u7126\u5C38\uFF0C\u5C11\u8BF4\u4E5F\u6709\u8FD1\u5343\u5177\u4E4B\u591A\u3002\u5468\u56F4\u6563\u843D\u7740\u79CD\u79CD\u5175\u5203\uFF0C\u6216\u5200\u6216\u67AA\u6216\u94A9\u6216\u53C9\uFF0C\u90FD\u50CF\u88AB\u5DE8\u7269\u6376\u51FB\u8FC7\u4E00\u6837\u5F2F\u66F2\u53D8\u5F62\u3002</p><p>\u5979\u76EE\u5149\u970D\u7136\u4E00\u8DF3\uFF0C\u5728\u6B8B\u67F1\u65C1\u770B\u5230\u4E00\u67C4\u719F\u6089\u7684\u957F\u5251\u3002</p><p>\u5251\u8EAB\u5F2F\u4F5C\u66F2\u5C3A\uFF0C\u950B\u5203\u4F9D\u7136\u6E05\u5149\u51DB\u51BD\u3002\u6B63\u662F\u4E1C\u65B9\u5E86\u7684\u51DD\u5149\u5251\u3002</p><p>\u7D2B\u73AB\u7D27\u7D27\u63E1\u7D27\u5251\u67C4\u3002\u8FD9\u4E9B\u70E7\u5F97\u770B\u4E0D\u51FA\u672C\u6765\u9762\u76EE\u7684\u5C38\u4F53\u95F4\uFF0C\u6709\u591A\u5C11\u81EA\u5DF1\u719F\u8BC6\u7684\u4EBA\u5462\uFF1F</p><p>\u81EA\u5DF1\u7684\u4EB2\u4EBA\u5462\uFF1F\u6B7B\u4E86\uFF1F\u70E7\u4E86\uFF1F\u6551\u8D70\u4E86\uFF1F\u8FD8\u662F\u5C31\u6B64\u6D88\u5931\u4E86\uFF1F</p><p>\u5979\u627E\u904D\u5168\u5C9B\uFF0C\u4E5F\u6CA1\u6709\u627E\u5230\u4E1D\u6BEB\u751F\u547D\u9057\u7559\u7684\u8FF9\u8C61\uFF0C\u6F5C\u5E7D\u7891\u574A\u3001\u6B66\u51E4\u522B\u9662\u3001\u4F20\u9999\u4EAD\u3001\u592A\u7384\u9601\u3001\u5E7D\u660E\u5ECA\u3001\u6708\u9B44\u53F0\u2026\u2026\u8FD9\u4E9B\u6D12\u6EE1\u4EB2\u53CB\u8840\u6CEA\u548C\u803B\u8FB1\u7684\u5730\u65B9\uFF0C\u53EA\u5269\u4E0B\u6B7B\u5BC2\u7684\u5E9F\u589F\u3002</p><p>\u7D2B\u73AB\u5446\u5446\u7ACB\u5728\u8352\u51C9\u7684\u96EA\u91CE\u4E2D\uFF0C\u5FC3\u5934\u4E00\u7247\u832B\u7136\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u767D\u96EA\u5728\u9ED1\u6697\u4E2D\u6563\u53D1\u51FA\u4E00\u79CD\u51C4\u6E05\u7684\u6DE1\u84DD\u5149\u8292\uFF0C\u968F\u98CE\u98D8\u8361\u7740\uFF0C\u4EFF\u4F5B\u4E00\u5C42\u51B0\u51B7\u7684\u706B\u7130\u3002</p><p>\u5A18\u4EB2\u3001\u5E08\u7236\u3001\u98CE\u5E08\u59D0\u3001\u5AC2\u5AC2\u3001\u7EAA\u5E08\u59D0\u2026\u2026\u6240\u6709\u7684\u4EB2\u4EBA\u90FD\u4E0D\u77E5\u53BB\u5411\uFF1B\u751A\u81F3\u8FDE\u4EC7\u4EBA\u4E5F\u90FD\u6D88\u5931\u65E0\u8E2A\u2026\u2026</p><p>\u300C\u4F60\u5F80\u4F55\u5904\u53BB\uFF1F\u300D\u6155\u5BB9\u7D2B\u73AB\u8F7B\u8F7B\u5730\u95EE\u81EA\u5DF1\u3002</p><p>\u98CE\u96EA\u88AB\u6C14\u5899\u6240\u9694\uFF0C\u6CA1\u6709\u4E00\u4E1D\u89E6\u53CA\u808C\u80A4\u3002\u5979\u7B2C\u4E00\u6B21\u62E5\u6709\u4E86\u53EF\u4EE5\u5B9E\u73B0\u68A6\u60F3\u7684\u7EDD\u4E16\u6B66\u529F\uFF0C\u5374\u4E0D\u77E5\u9053\u8BE5\u5982\u4F55\u65BD\u5C55\u3002</p><p>\u6240\u6709\u7684\u76EE\u6807\u90FD\u6A21\u7CCA\u65E0\u8FF9\uFF0C\u7A7A\u8361\u8361\u8BA9\u5979\u65E0\u4ECE\u4F7F\u529B\u3002</p><p>\u5929\u9645\u6E10\u6E10\u53D1\u767D\uFF0C\u98D8\u626C\u7684\u96EA\u7C89\u7EC8\u4E8E\u505C\u4E0B\u3002</p><p>\u7D2B\u73AB\u9732\u51FA\u4E00\u4E2A\u51C4\u8273\u7684\u7B11\u5BB9\uFF0C\u8FD9\u7B11\u5BB9\u60CA\u52A8\u4E86\u8EAB\u8FB9\u7684\u4E00\u6811\u7EA2\u6885\uFF0C\u7E41\u82B1\u500F\u500F\u800C\u4E0B\u3002</p><p>\u5973\u5A74\u7741\u7740\u4E4C\u6E9C\u6E9C\u7684\u773C\u775B\u770B\u7740\u8FD9\u4E2A\u964C\u751F\u7684\u4E16\u754C\uFF0C\u5F53\u4E00\u6735\u6885\u82B1\u98D8\u98D8\u6447\u6447\u843D\u5728\u989D\u524D\u51DD\u4F4F\u540E\uFF0C\u5979\u5FFD\u7136\u7B11\u4E86\u8D77\u6765\u3002</p><p>\u6E05\u60A6\u7684\u7B11\u58F0\u5F15\u6765\u7D2B\u73AB\u5954\u6D8C\u7684\u6CEA\u6C34\uFF0C\u4EC0\u4E48\u90FD\u6CA1\u6709\u4E86\uFF0C\u6CA1\u6709\u4EB2\u4EBA\uFF0C\u4E5F\u6CA1\u6709\u5BB6\uFF0C\u5929\u5730\u95F4\u53EA\u5269\u6BCD\u5973\u4FE9\u5B64\u96F6\u96F6\u5730\u76F8\u4F9D\u4E3A\u547D\u3002</p><p>\u5979\u4EB2\u543B\u7740\u5973\u513F\u9999\u8F6F\u7684\u5C0F\u8138\uFF0C\u5583\u5583\u9053\uFF1A\u300C\u6674\u6674\uFF0C\u5A18\u5E26\u4F60\u56DE\u53BB\u2026\u2026\u300D</p><p>\u6668\u5149\u4E2D\uFF0C\u5341\u4E03\u5C81\u7684\u5C11\u5973\u88F9\u7D27\u8863\u895F\uFF0C\u62B1\u7740\u672A\u8DB3\u6708\u7684\u5973\u513F\uFF0C\u5F00\u59CB\u5979\u6F2B\u65E0\u76EE\u7684\u7684\u6D41\u6D6A\u3002</p><p>\u8EAB\u540E\uFF0C\u6EE1\u5730\u9C9C\u7EA2\u7684\u843D\u6885\u4EFF\u4F5B\u4E00\u7247\u672A\u5E72\u7684\u8840\u8FF9\u3002</p><p>\uFF34\uFF28\uFF25\uFF25\uFF2E\uFF24\uFF1F</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u53EC\u96C6\u4EBA\uFF1A\u300C\u771F\u662F\u4E86\u4E0D\u8D77\u7684\u5927\u4F5C\u554A\uFF0C\u8FD9\u7BC7\u7684\u5B57\u6570\u53C8\u7834\u4E86\u5F81\u6587\u7684\u53F2\u4E0A\u7EAA\u5F55\u3002\u300D</p><p>\u4ECE\u4E0D\u4E71\uFF1A\u300C\u6731\u989C\u8840\u7684\u7B2C\u4E94\u6EF4\u7EA2\u6CEA\u300A\u7D2B\u73AB\u300B\uFF0C\u4E00\u5982\u65E2\u5F80\u7684\u5448\u73B0\u51FA\u7D2B\u72C2\u90A3\u79CD\u72C2\u4E71\u6B8B\u66B4\u800C\u53C8\u7CBE\u5DE7\u5996\u5F02\u7684\u98CE\u683C\uFF0C\u540C\u65F6\u8FD9\u4E5F\u662F\u7D2B\u72C2\u7B2C\u4E00\u90E8\u4EE5\u4E71\u4F26\u4E3A\u4E3B\u9898\u7684\u4F5C\u54C1\u3002\u300D</p><p>\u53EC\u96C6\u4EBA\uFF1A\u300C\u4EE5\u4ECE\u5144\u6240\u89C1\uFF0C\u8FD9\u4E00\u7BC7\u6709\u4EC0\u4E48\u7279\u522B\u4EAE\u70B9\u5417\uFF1F\u300D</p><p>\u62B1\u74EE\u7684\u8D31\u4EBA\uFF1A\u7531\u4E8E\u6708\u51B7\u5BD2\u73AB\u5DF2\u7ECF\u51FA\u4E66\uFF0C\u5269\u4E0B\u7684\u90E8\u5206\uFF0C\u57FA\u4E8E\u4E0E\u51FA\u7248\u793E\u7684\u534F\u8BAE\uFF0C\u4E0D\u80FD\u66DD\u5149\u3002\u8FDE\u540C\u8BC4\u8BBA\u90E8\u5206\uFF0C\u6682\u65F6\u5C01\u5370\uFF0C\u7B49\u5F85\u4E66\u51FA\u5B8C\u540E\uFF0C\u518D\u884C\u89E3\u5C01\u3002</p><p>\u53EC\u96C6\u4EBA\uFF1A\u300C\u8C22\u8C22\u7D2B\u72C2\u5144\u7684\u597D\u6587\u7AE0\uFF0C\u672C\u5C4A\u7684\u9664\u5915\u8D3A\u6587\u5230\u6B64\u7ED3\u675F\u3002\u300D</p>',92);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
