import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_13" tabindex="-1"><a class="header-anchor" href="#_13" aria-hidden="true">#</a> 13</h1><p>\u4E09\u6708\u7684\u7FA4\u5C71\u8349\u840C\u82B1\u7EFD\uFF0C\u4E00\u6D3E\u6B23\u6B23\u5411\u8363\u3002\u6696\u6D0B\u6D0B\u7684\u9633\u5149\u7A7F\u8FC7\u5C71\u6797\uFF0C\u843D\u5728\u72ED\u8C37\u4E2D\u7684\u4E00\u5757\u5DE8\u77F3\u4E0A\u3002</p><p>\u4E00\u4E2A\u5A01\u731B\u7684\u5927\u6C49\u4F0F\u5728\u77F3\u4E0A\uFF0C\u91D1\u9EC4\u8272\u7684\u80CC\u810A\u5E03\u6EE1\u6C57\u73E0\uFF0C\u5728\u80A9\u5934\uFF0C\u9732\u51FA\u4E00\u622A\u767D\u51C0\u4F18\u7F8E\u7684\u5C0F\u817F\uFF0C\u79C0\u4E3D\u7684\u811A\u8DBE\u968F\u7740\u5927\u6C49\u7684\u52A8\u4F5C\u5FAE\u5FAE\u6447\u6643\u3002</p><p>\u91D1\u5F00\u7532\u5927\u543C\u4E00\u58F0\uFF0C\u628A\u7CBE\u6DB2\u5C04\u8FDB\u5C11\u5987\u4F53\u5185\u3002\u7136\u540E\u8D64\u8EAB\u7ACB\u5728\u77F3\u4E0A\uFF0C\u770B\u7740\u4E0B\u9762\u7684\u6076\u6597\u3002</p><p>\u6155\u5BB9\u80DC\u8EAB\u4E0A\u5DF2\u5E26\u4E86\u4E03\u5904\u4F24\u75D5\uFF0C\u72B9\u81EA\u82E6\u6218\u4E0D\u9000\u3002\u5468\u56F4\u7684\u767D\u8863\u4EBA\u56F4\u6210\u6247\u5F62\uFF0C\u5200\u67AA\u9F50\u65BD\uFF0C\u8F6E\u756A\u8FDB\u51FB\u3002\u65C1\u8FB9\u5012\u4F0F\u7740\u5341\u4F59\u5177\u5C38\u4F53\uFF0C\u6216\u80F8\u6216\u8170\u6216\u9888\uFF0C\u88AB\u65A9\u9A6C\u5200\u780D\u4E2D\u7684\u4F24\u53E3\u8840\u8089\u6A21\u7CCA\u3002</p><p>\u300C\u9752\u94DC\u9000\u4E0B\uFF0C\u9ED1\u94C1\u4E0A\u3002\u300D\u91D1\u5F00\u7532\u51B7\u58F0\u559D\u9053\u3002</p><p>\u5708\u5916\u7684\u6570\u5341\u4EBA\u4E2D\u8DC3\u51FA\u4E00\u4EBA\uFF0C\u52A0\u5165\u6218\u56E2\u3002\u9752\u94DC\u63D0\u7740\u72FC\u7259\u68D2\u6068\u6068\u9000\u51FA\u3002</p><p>\u300C\u5927\u4F19\u8F6E\u7740\u4E0A\uFF0C\u5C1D\u5C1D\u5BD2\u6708\u5200\u7684\u6ECB\u5473\u3002\u6155\u5BB9\u80DC\uFF01\u51FA\u4E00\u62DB\uFF0C\u5C31\u64CD\u4F60\u8001\u5A46\u4E00\u4E0B\uFF1B\u4F24\u4E00\u4EBA\uFF0C\u4F60\u8001\u5A46\u5C31\u591A\u4E86\u4E2A\u5E72\u8001\u516C\u3002\u4ED4\u7EC6\u770B\u7740\uFF0C\u5927\u4F19\u600E\u4E48\u64CD\u4F60\u4E0B\u8D31\u7684\u8001\u5A46\uFF01\u300D</p><p>\u9752\u94DC\u817E\u8EAB\u800C\u8D77\uFF0C\u6258\u8D77\u6797\u9999\u8FDC\u7684\u819D\u5F2F\uFF0C\u5728\u5979\u79D8\u5904\u638F\u4E86\u4E00\u628A\uFF0C\u6840\u6840\u602A\u7B11\u9053\uFF1A\u300C\u59D3\u6155\u5BB9\u7684\uFF0C\u4F60\u8001\u5A46\u7684\u5C44\u53EF\u771F\u7D27\u3002\u300D</p><p>\u6155\u5BB9\u80DC\u9762\u65E0\u8868\u60C5\uFF0C\u5237\u5237\u5237\u8FDE\u51FA\u4E09\u5200\uFF0C\u53C8\u4F24\u4E86\u4E00\u4EBA\u3002</p><p>\u72DE\u7B11\u58F0\u8FDC\u8FDC\u4F20\u6765\uFF0C\u300C\u6797\u5A4A\u5B50\u8FD9\u8EAB\u8089\u53EF\u771F\u767D\uFF0C\u5AE9\u5F97\u6390\u5F97\u51FA\u6C34\u513F\uFF0C\u6155\u5BB9\u80DC\u4F60\u5C0F\u5B50\u8273\u798F\u4E0D\u6D45\u554A\u3002\u300D</p><p>\u65A9\u9A6C\u5200\u5212\u4E86\u4E2A\u5706\u5F27\uFF0C\u6321\u5F00\u4E00\u628A\u9B3C\u5934\u5200\u3002</p><p>\u300C\u556A\u556A\u300D\u51E0\u58F0\u8106\u54CD\uFF0C\u300C\u8FD9\u5BF9\u5976\u5B50\u53C8\u80A5\u53C8\u5927\uFF0C\u6478\u7740\u771F\u8212\u670D\u2026\u2026\u5582\uFF0C\u6155\u5BB9\u80DC\uFF0C\u5482\u8FC7\u4F60\u8001\u5A46\u7684\u5976\u5934\u6CA1\u6709\uFF0C\u751C\u7740\u5462\uFF01\u300D</p><p>\u5200\u5149\u4E00\u95EA\uFF0C\u780D\u4E2D\u4E00\u540D\u6C49\u5B50\u7684\u5C0F\u817F\u3002</p><p>\u300C\u2026\u2026\u4E5D\u767E\u4E5D\u5341\u516B\u3001\u4E5D\u767E\u4E5D\u5341\u4E5D\u3001\u4E09\u5343\uFF01\u4E09\u5343\u62DB\uFF0C\u54C8\u54C8\uFF0C\u597D\u529F\u592B\uFF01\u80FD\u6491\u8FD9\u4E48\u957F\u65F6\u5019\u3002\u518D\u591A\u6491\u4E00\u4F1A\u513F\uFF0C\u8BA9\u7237\u4ED4\u7EC6\u64CD\u64CD\u4F60\u8001\u5A46\u3002\u300D\u521A\u521A\u8D76\u56DE\u7684\u767D\u94F6\u89E3\u7740\u8863\u670D\u9AD8\u58F0\u8BF4\u9053\u3002</p><p>\u4ED6\u8E22\u4E86\u8E22\u9AD8\u8038\u7684\u4E73\u623F\uFF0C\u300C\u4ED6\u5988\u7684\uFF0C\u600E\u4E48\u8FD9\u4E48\u810F\uFF1F\u300D</p><p>\u9ED1\u94C1\u7B11\u9053\uFF1A\u300C\u4F60\u53BB\u8FFD\u90A3\u4E2A\u4E2B\u5934\uFF0C\u54B1\u4EEC\u53EF\u6CA1\u95F2\u7740\uFF0C\u8FD9\u90FD\u5E72\u4E86\u5341\u6765\u56DE\u4E86\u3002\u300D</p><p>\u300C\u64CD\uFF0C\u5565\u9E21\u5DF4\u5BD2\u6708\u5200\u3001\u795E\u4ED9\u4FA0\u4FA3\uFF0C\u8FD8\u4E0D\u662F\u8BA9\u4EBA\u968F\u4FBF\u64CD\u7684\u70C2\u8D27\u3002\u6155\u5BB9\u80DC\uFF0C\u770B\u770B\u4F60\u8001\u5A46\u7684\u9A9A\u5C44\u2026\u2026\u300D</p><p>\u6155\u5BB9\u80DC\u65A9\u9A6C\u5200\u72E0\u547D\u4E00\u62A1\uFF0C\u8FEB\u5F00\u56F4\u653B\u7684\u4F17\u4EBA\uFF0C\u62AC\u5934\u671D\u77F3\u4E0A\u770B\u53BB\u3002</p><p>\u65B0\u5A5A\u59BB\u5B50\u767D\u5AE9\u7684\u8EAB\u4F53\u60AC\u5728\u534A\u7A7A\uFF0C\u4E30\u6EE1\u7684\u5927\u817F\u88AB\u51E0\u4E2A\u7537\u4EBA\u72E0\u72E0\u62D7\u5230\u8EAB\u540E\uFF0C\u5A07\u7F8E\u7684\u7389\u6237\u7EA4\u6BEB\u6BD5\u9732\u3002\u767D\u94F6\u634F\u7740\u7EC6\u5AE9\u7684\u82B1\u74E3\u7528\u529B\u5411\u4E24\u8FB9\u62C9\u5F00\uFF0C\u539F\u672C\u7EC6\u7A84\u7684\u79D8\u5904\u88AB\u626F\u6210\u6843\u5F62\uFF0C\u8FDE\u6700\u9690\u5BC6\u7684\u8089\u7A74\u4E5F\u5B8C\u5168\u66B4\u9732\u51FA\u6765\u3002</p><p>\u767D\u94F6\u5E76\u8D77\u624B\u6307\u6345\u5165\u59BB\u5B50\u8FF7\u4EBA\u7684\u8089\u7A74\uFF0C\u7C97\u66B4\u5730\u6405\u5F04\u8D77\u6765\u3002\u7EA2\u8273\u7684\u5AE9\u8089\u626D\u52A8\u7740\uFF0C\u6D41\u51FA\u80A1\u80A1\u767D\u6D53\u7684\u6DB2\u4F53\u2026\u2026</p><p>\u6155\u5BB9\u80DC\u80F8\u53E3\u70B8\u88C2\u822C\u5267\u75DB\uFF0C\u63E1\u7740\u957F\u5200\u7684\u5927\u624B\u98A4\u6296\u8D77\u6765\u3002\u56F4\u653B\u7684\u5E2E\u4F17\u6563\u5728\u4E00\u65C1\uFF0C\u6EE1\u8138\u51B7\u7B11\u5730\u770B\u7740\u4ED6\u3002</p><p>\u767D\u94F6\u638F\u6478\u7247\u523B\uFF0C\u62E3\u8D77\u94F6\u67AA\uFF0C\u5C06\u67AA\u5C3E\u5BF9\u51C6\u8089\u7A74\u72E0\u72E0\u4E00\u6345\uFF0C\u6CA1\u5165\u534A\u5C3A\u6709\u4F59\u3002\u6797\u9999\u8FDC\u4E0B\u4F53\u4E00\u9635\u5267\u70C8\u5730\u6536\u7F29\uFF0C\u5341\u51E0\u4E2A\u7537\u4EBA\u7684\u7CBE\u6DB2\u4ECE\u4E2D\u98DE\u6E85\u51FA\u6765\u3002</p><p>\u6155\u5BB9\u80DC\u5C11\u5E74\u5F97\u5FD7\uFF0C\u7EB5\u6A2A\u6C5F\u6E56\u65E0\u5F80\u4E0D\u5229\uFF0C\u4E0E\u6797\u9999\u8FDC\u6210\u5A5A\u540E\u66F4\u88AB\u89C6\u4E3A\u795E\u4ED9\u4FA0\u4FA3\uFF0C\u5374\u4E0D\u6599\u8FD9\u5149\u5929\u5316\u65E5\u4E4B\u4E0B\u773C\u7741\u7741\u770B\u7740\u59BB\u5B50\u88AB\u4EBA\u8F6E\u5978\u51CC\u8FB1\u3002</p><p>\u300C\u8001\u9ED1\uFF0C\u628A\u6797\u5A4A\u5B50\u7684\u5C44\u7FFB\u5F00\uFF0C\u8BA9\u6155\u5BB9\u5927\u4FA0\u770B\u6E05\u695A\u3002\u300D</p><p>\u9ED1\u94C1\u6DEB\u7B11\u7740\u626F\u5F00\u4E24\u7247\u9634\u5507\uFF0C\u8BA9\u4F17\u4EBA\u770B\u6E05\u94F6\u67AA\u5728\u6ED1\u817B\u7684\u7EA2\u7A74\u5185\u8FDB\u51FA\u7684\u60C5\u666F\u3002</p><p>\u6155\u5BB9\u80DC\u56DE\u5200\u6A2A\u62D6\uFF0C\u5934\u9885\u5E26\u7740\u4E00\u4E32\u8840\u6CEA\u98DE\u4E0A\u534A\u7A7A\u3002</p><p>\u91D1\u5F00\u7532\u4E00\u628A\u63A5\u8FC7\u5934\u9885\uFF0C\u51B7\u7B11\u9053\uFF1A\u300C\u53EF\u60DC\u53EF\u60DC\uFF0C\u89C1\u4E0D\u5230\u4F60\u778E\u773C\u7684\u8001\u5A46\u50CF\u72D7\u4E00\u6837\u88AB\u4EBA\u64CD\u7684\u4FCF\u6A21\u6837\u4E86\u3002\u300D</p><p>\u767D\u94F6\u6296\u624B\u62D4\u51FA\u94F6\u67AA\uFF0C\u6345\u5165\u6797\u9999\u8FDC\u809B\u4E2D\uFF0C\u7136\u540E\u5C06\u5979\u6309\u5728\u77F3\u4E0A\uFF0C\u72E0\u547D\u64CD\u5F04\u3002\u94F6\u67AA\u78E8\u64E6\u7740\u5CA9\u77F3\uFF0C\u6025\u4FC3\u54CD\u52A8\u7740\uFF0C\u6797\u9999\u8FDC\u79C0\u53D1\u9ED1\u7011\u822C\u62AB\u6563\u5F00\u6765\uFF0C\u63D2\u7740\u94A2\u9488\u7684\u7F8E\u76EE\u4E2D\uFF0C\u7EC6\u7EC6\u7684\u8840\u6CEA\u4ECE\u6CBE\u6EE1\u7CBE\u6DB2\u7684\u8138\u988A\u4E0A\u4E0D\u4F4F\u6DCC\u4E0B\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u4F60\u2026\u2026\u4F60\u2026\u2026\u4F60\u662F\u2026\u9F99\u513F\u2026\u2026\u300D\u767E\u82B1\u89C2\u97F3\u98A4\u58F0\u9053\u3002</p><p>\u5BAB\u4E3B\u76EF\u7740\u5979\u7684\u53CC\u773C\uFF0C\u4E00\u8A00\u4E0D\u53D1\uFF0C\u4F46\u51B0\u51B7\u7684\u76EE\u5149\u5DF2\u7ECF\u8BF4\u660E\u4E86\u4E00\u5207\u3002</p><p>\u300C\u4F60\u2026\u2026\u600E\u4E48\u4F1A\u5728\u8FD9\u91CC\u2026\u2026\u4F60\u600E\u4E48\u4F1A\u53D8\u6210\u8FD9\u4E2A\u6837\u5B50\u2026\u2026\u300D\u767E\u82B1\u89C2\u97F3\u770B\u7740\u4ED6\u602A\u5F02\u7684\u9633\u5177\u75DB\u54ED\u5931\u58F0\u3002</p><p>\u300C\u8FD9\u90FD\u662F\u62DC\u4F60\u6240\u8D50\u2026\u2026\u300D\u5BAB\u4E3B\u58F0\u97F3\u50CF\u751F\u9508\u822C\u5636\u54D1\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u8138\u4E0A\u6302\u7740\u900F\u660E\u7684\u6CEA\u73E0\uFF0C\u6014\u6014\u770B\u7740\u81EA\u5DF1\u7684\u4EB2\u751F\u9AA8\u8089\u3002</p><p>\u8427\u4F5B\u5974\u672C\u662F\u5927\u71D5\u7687\u5E1D\u6155\u5BB9\u7941\u7684\u5BA0\u5983\u3002\u5341\u516D\u5E74\u524D\u71D5\u56FD\u5927\u5C06\u59DA\u5174\u7A81\u7136\u53DB\u4E71\uFF0C\u653B\u5165\u4EAC\u57CE\uFF0C\u6155\u5BB9\u5BB6\u65CF\u4E00\u591C\u4E4B\u95F4\u56FD\u7834\u5BB6\u4EA1\u3002\u5F53\u591C\u6765\u88AD\u7684\u6709\u661F\u6708\u6E56\u6570\u5341\u540D\u9AD8\u624B\uFF0C\u6DF7\u4E71\u4E2D\u5BAB\u4E2D\u4EB2\u4F8D\u6155\u5BB9\u536B\u4E34\u5371\u53D7\u547D\uFF0C\u63A5\u8FC7\u5B9D\u85CF\u5730\u56FE\uFF0C\u62FC\u6B7B\u6551\u51FA\u6709\u5B55\u5728\u8EAB\u7684\u8427\u7687\u5983\uFF0C\u5374\u5931\u843D\u4E86\u592A\u5B50\u6155\u5BB9\u9F99\u3002</p><p>\u4ED6\u672C\u60F3\u7B49\u7687\u5983\u751F\u4E0B\u5B69\u5B50\u540E\uFF0C\u8D77\u51FA\u5B9D\u85CF\uFF0C\u56FE\u8C0B\u590D\u56FD\uFF0C\u5374\u4E0D\u6599\u662F\u4E2A\u5973\u513F\u3002\u7070\u5FC3\u4E4B\u4F59\uFF0C\u6155\u5BB9\u536B\u9690\u5C45\u4F0F\u9F99\u6DA7\uFF0C\u53EA\u56FE\u4E2A\u5E73\u5B89\u7F62\u4E86\u3002</p><p>\u661F\u6708\u6E56\u9634\u5BAB\u4E3B\u7684\u76EE\u7684\u53EA\u5728\u71D5\u5E1D\u6155\u5BB9\u7941\uFF0C\u7ED3\u679C\u6155\u5BB9\u7941\u81EA\u6740\u8EAB\u6B7B\uFF0C\u8427\u4F5B\u5974\u9003\u5F97\u65E0\u5F71\u65E0\u8E2A\uFF0C\u53EA\u6293\u5230\u5E74\u65B9\u4E94\u5C81\u7684\u5927\u71D5\u592A\u5B50\uFF0C\u4FBF\u628A\u706B\u6C14\u90FD\u6492\u5728\u8FD9\u4E2A\u5B69\u5B50\u8EAB\u4E0A\uFF0C\u65BD\u4EE5\u8BF8\u822C\u9177\u5211\u3002</p><p>\u5341\u4F59\u5E74\u6765\u7684\u6298\u78E8\uFF0C\u6155\u5BB9\u9F99\u975E\u4F46\u6CA1\u6B7B\uFF0C\u53CD\u800C\u957F\u5F97\u8EAB\u957F\u7389\u7ACB\u4FCA\u96C5\u975E\u51E1\uFF0C\u4E0E\u6155\u5BB9\u7941\u5F53\u5E74\u4E00\u822C\u65E0\u4E8C\u3002\u9634\u59EC\u5E72\u8106\u628A\u4ED6\u7559\u5728\u5BC6\u5BA4\uFF0C\u4F5C\u4E3A\u5A08\u7AE5\u6536\u4E3A\u5DF1\u7528\u3002\u6155\u5BB9\u9F99\u5929\u4EFD\u6781\u9AD8\uFF0C\u4ED6\u88C5\u4F5C\u6D51\u5FD8\u4E86\u5C0F\u65F6\u5019\u4E8B\u60C5\u7684\u6837\u5B50\uFF0C\u5C3D\u5FC3\u7AED\u529B\u4F3A\u5019\u5996\u5987\u3002</p><p>\u9634\u59EC\u672C\u6765\u53EA\u628A\u4ED6\u5F53\u6210\u5BA0\u7269\uFF0C\u4E0D\u66FE\u4F20\u4ED6\u6B66\u529F\u3002\u540E\u6765\u6155\u5BB9\u9F99\u9633\u5177\u6539\u9020\u7684\u8D8A\u6765\u8D8A\u5389\u5BB3\uFF0C\u8FDE\u5979\u4E5F\u5403\u4E0D\u6D88\uFF0C\u4E8E\u662F\u4FBF\u628A\u6155\u5BB9\u5F53\u6210\u4E00\u4EF6\u5211\u5177\uFF0C\u4E13\u95E8\u7528\u6765\u6298\u78E8\u63B3\u5165\u5BAB\u7684\u5973\u5B50\u3002</p><p>\u5176\u4E2D\u6709\u4E00\u4E2A\u5973\u5B50\u672C\u662F\u6C5F\u6E56\u4E2D\u4EE4\u4EBA\u95FB\u540D\u4E27\u80C6\u7684\u8273\u5973\uFF0C\u64C5\u957F\u91C7\u8865\u4E4B\u672F\uFF0C\u56E0\u4E3A\u53D7\u4E0D\u8FC7\u6298\u78E8\uFF0C\u4FBF\u628A\u529F\u6CD5\u90FD\u4F20\u7ED9\u4E86\u6155\u5BB9\u9F99\uFF0C\u60F3\u6C42\u4ED6\u653E\u8FC7\u81EA\u5DF1\u3002\u7ED3\u679C\u53CD\u88AB\u6155\u5BB9\u9F99\u5148\u5438\u5E72\u529F\u529B\u3002</p><p>\u6155\u5BB9\u9F99\u5947\u529F\u5728\u8EAB\uFF0C\u85C9\u673A\u4E0D\u52A8\u58F0\u8272\u5730\u53D6\u5438\u4E86\u5341\u4F59\u540D\u5973\u5B50\u7684\u529F\u529B\u3002\u8FD9\u4E9B\u5973\u5B50\u6B66\u529F\u9AD8\u4F4E\u4E0D\u4E00\uFF0C\u95E8\u6D3E\u5404\u5F02\uFF0C\u4ED6\u6765\u8005\u4E0D\u62D2\uFF0C\u5C3D\u5F97\u5176\u957F\u3002\u800C\u540E\u53C8\u5F97\u4EBA\u6697\u4E2D\u6307\u70B9\uFF0C\u8FDB\u5883\u4E00\u65E5\u5343\u91CC\u3002</p><p>\u6570\u6708\u524D\u4ED6\u8D81\u661F\u6708\u5BAB\u4E3B\u7EC3\u529F\u65F6\u7A81\u7136\u51FA\u624B\uFF0C\u5236\u4F4F\u4E86\u5996\u5987\uFF0C\u7136\u540E\u7ACB\u5373\u5047\u4F20\u65E8\u610F\uFF0C\u767B\u4E0A\u5BAB\u4E3B\u4E4B\u4F4D\u3002\u4F17\u4F4D\u957F\u8001\u867D\u7136\u5FC3\u6709\u4F59\u8651\uFF0C\u4F46\u6155\u5BB9\u9F99\u5F97\u5230\u53F6\u884C\u5357\u4E0E\u6731\u90AA\u9752\u6811\u4E24\u4F4D\u62A4\u6CD5\u7684\u652F\u6301\uFF0C\u672C\u8EAB\u5C3D\u5F97\u9634\u59EC\u529F\u529B\uFF0C\u53C8\u6740\u4F10\u51B3\u65AD\uFF0C\u63A5\u8FDE\u5904\u6B7B\u4E24\u4F4D\u957F\u8001\uFF0C\u4F59\u4E0B\u7684\u90FD\u51DB\u7136\u76F8\u4ECE\uFF0C\u4E0D\u6562\u7A0D\u6709\u8FDD\u6297\u3002</p><p>\u4ED6\u77E5\u9053\u81EA\u5DF1\u7684\u4F4D\u5B50\u8FD8\u672A\u5750\u7A33\uFF0C\u60F3\u5C3D\u529E\u6CD5\u63D0\u62D4\u65B0\u4EBA\uFF0C\u6E05\u9664\u65E7\u6709\u52BF\u529B\u3002\u5982\u4ECA\u571F\u3001\u706B\u4E24\u5802\u5DF2\u7ECF\u90FD\u6362\u6210\u4ED6\u7684\u5FC3\u8179\u3002</p><p>\u9634\u5BAB\u4E3B\u4E00\u76F4\u544A\u8BC9\u6155\u5BB9\u9F99\uFF0C\u662F\u4ED6\u6BCD\u4EB2\u628A\u4ED6\u4E22\u4E0B\u4E0D\u7BA1\uFF0C\u4E0E\u4ED6\u4EBA\u79C1\u5954\uFF0C\u5BAB\u4E3B\u89C1\u4ED6\u53EF\u601C\u624D\u6536\u56DE\u6765\u629A\u517B\u3002\u6155\u5BB9\u9F99\u867D\u7136\u4E0D\u4FE1\uFF0C\u4F46\u5BF9\u629B\u5F03\u4E86\u81EA\u5DF1\u7684\u6BCD\u4EB2\u5374\u6068\u4E4B\u5165\u9AA8\u3002\u5F85\u624B\u5934\u6709\u4E86\u52BF\u529B\uFF0C\u4ED6\u7ACB\u5373\u6D3E\u4EBA\u5BFB\u627E\u6BCD\u4EB2\u7684\u4E0B\u843D\uFF0C\u4E00\u4E2A\u6708\u524D\uFF0C\u7EC8\u4E8E\u5F97\u77E5\u6BCD\u4EB2\u662F\u5728\u4F0F\u9F99\u6DA7\u3002</p><p>\u4E0D\u4F46\u5AC1\u4E86\u4EBA\uFF0C\u8FD8\u751F\u4E0B\u4E86\u4E24\u4E2A\u5B69\u5B50\u3002</p><p>\u6155\u5BB9\u9F99\u6C14\u6068\u586B\u81BA\uFF0C\u5F53\u5373\u4FBF\u547D\u970D\u72C2\u7130\u548C\u5C60\u6000\u6C89\u706D\u6389\u4F0F\u9F99\u6DA7\uFF0C\u628A\u767E\u82B1\u89C2\u97F3\u548C\u6155\u5BB9\u7D2B\u73AB\u63B3\u81F3\u5BAB\u4E2D\u3002\u4ED6\u4EE5\u4E3A\u6BCD\u4EB2\u5931\u8D1E\uFF0C\u56E0\u6B64\u5236\u4F5C\u4E86\u77F3\u9A74\u7B49\u7269\uFF0C\u7528\u6765\u60E9\u7F5A\u8FD9\u4E2A\u80CC\u592B\u629B\u5B50\u7684\u6DEB\u5987\u3002\u6B64\u65F6\u5F97\u77E5\u6155\u5BB9\u536B\u672C\u662F\u592A\u76D1\uFF0C\u4E0D\u7981\u6012\u6C14\u5C3D\u53BB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u591A\u5E74\u672A\u5F97\u6BCD\u7231\u7684\u6155\u5BB9\u9F99\uFF0C\u5BF9\u6BCD\u4EB2\u7684\u8089\u4F53\u4EA7\u751F\u4E86\u83AB\u5927\u7684\u5174\u8DA3\u3002\u4ED6\u653E\u7F13\u58F0\u97F3\uFF0C\u6162\u6162\u9053\uFF1A\u300C\u90A3\u4E2A\u5996\u5987\u5DF2\u7ECF\u88AB\u5B69\u513F\u5236\u4F4F\u3002\u5A18\uFF0C\u4E0E\u5B69\u513F\u6B22\u597D\u540E\uFF0C\u54B1\u4EEC\u4E00\u8D77\u53BB\u6536\u62FE\u5979\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u8FDE\u5FD9\u5408\u7D27\u53CC\u817F\uFF0C\u60CA\u53EB\u9053\uFF1A\u300C\u9F99\u513F\uFF0C\u6211\u662F\u4F60\u4EB2\u5A18\uFF0C\u600E\u4E48\u53EF\u4EE5\u2026\u2026\u300D</p><p>\u300C\u4EB2\u5A18\u53C8\u5982\u4F55\uFF1F\u6211\u542C\u8BF4\u5357\u671D\u5218\u5B8B\u7684\u7687\u5E1D\u8FD8\u4E0E\u4EB2\u5A18\u4EA4\u6B22\u5462\u2014\u2014\u5A18\uFF0C\u4F60\u653E\u5FC3\uFF0C\u5B69\u513F\u4F1A\u6E29\u67D4\u4E00\u4E9B\u2026\u2026\u300D</p><p>\u8427\u4F5B\u5974\u6323\u624E\u7740\u8EB2\u5230\u4E00\u65C1\uFF0C\u8EAB\u5B50\u8737\u6210\u4E00\u56E2\uFF0C\u54ED\u53EB\u9053\uFF1A\u300C\u9F99\u513F\u2026\u2026\u4F60\u600E\u4E48\u53EF\u4EE5\u505A\u8FD9\u79CD\u79BD\u517D\u884C\u5F84\u5462\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u51B7\u54FC\u4E00\u58F0\uFF0C\u62B1\u4F4F\u6BCD\u4EB2\u9999\u8F6F\u7684\u8089\u4F53\uFF0C\u9633\u5177\u4ECE\u81C0\u4FA7\u6ED1\u5411\u79D8\u5904\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u62FC\u547D\u7528\u624B\u6321\u4F4F\u4E0B\u4F53\uFF0C\u73E0\u6CEA\u98DE\u6E85\u3002</p><p>\u6155\u5BB9\u9F99\u4E0D\u8010\u70E6\u8D77\u6765\uFF0C\u63B0\u5F00\u6BCD\u4EB2\u7684\u5927\u817F\uFF0C\u7528\u819D\u76D6\u538B\u7D27\uFF0C\u52C3\u8D77\u7684\u9633\u5177\u7ACB\u523B\u62B5\u5728\u5FAE\u7EFD\u7684\u82B1\u74E3\u4E0A\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u54ED\u7684\u5598\u4E0D\u8FC7\u6C14\u6765\uFF0C\u62BD\u54BD\u7740\u6342\u4F4F\u4E0B\u8179\uFF0C\u300C\u5B69\u5B50\u3001\u5B69\u5B50\uFF0C\u4E0D\u8981\u554A\u2026\u2026\u300D</p><p>\u55C5\u5230\u6BCD\u4EB2\u82AC\u82B3\u7684\u4F53\u9999\uFF0C\u6155\u5BB9\u9F99\u65E9\u5DF2\u6309\u637A\u4E0D\u4F4F\u5FC3\u5934\u7684\u6B32\u706B\uFF0C\u4E00\u633A\u8170\uFF0C\u7ACB\u523B\u63D2\u5165\u6ED1\u817B\u7684\u82B1\u5F84\u3002</p><p>\u767E\u82B1\u89C2\u97F3\u9762\u8272\u53D8\u5F97\u60E8\u767D\uFF0C\u60B2\u9E23\u4E00\u58F0\uFF0C\u6B7B\u6B7B\u6342\u4F4F\u9762\u5B54\u3002</p><p>\u300C\u5A18\u3001\u5A18\u2026\u2026\u300D\u5341\u516D\u5E74\u6765\u6155\u5BB9\u9F99\u7EC8\u4E8E\u91CD\u65B0\u56DE\u5230\u6BCD\u4EB2\u7684\u6000\u62B1\uFF0C\u751A\u81F3\u8FDB\u5165\u4EB2\u6BCD\u4F53\u5185\uFF0C\u4ED6\u5174\u594B\u5730\u6D51\u8EAB\u98A4\u6296\uFF0C\u5982\u7591\u5982\u9189\u5730\u5728\u6BCD\u4EB2\u9999\u8F6F\u7684\u8EAB\u4E0A\u8D77\u4F0F\u3002</p><p>\u4E00\u65C1\u7684\u661F\u6708\u5BAB\u4E3B\u4ECD\u5B89\u8BE6\u5730\u4F0F\u5728\u53F0\u4E0A\uFF0C\u50CF\u4E00\u53EA\u8774\u8776\u51DD\u56FA\u4E86\u5979\u7684\u7F8E\u4E3D\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6155\u5BB9\u7D2B\u73AB\u501A\u4ED7\u8F7B\u529F\u9003\u51FA\u661F\u6708\u6E56\u91D1\u5802\u5E2E\u4F17\u7684\u8FFD\u6355\uFF0C\u4E00\u8DEF\u4E0D\u6562\u7A0D\u4F5C\u505C\u7559\uFF0C\u76F4\u5954\u98D8\u6885\u5CF0\u3002\u5F85\u770B\u5230\u5CF0\u9876\u7684\u5C0F\u5C0F\u5EB5\u5802\uFF0C\u7D2B\u73AB\u773C\u524D\u4E00\u9ED1\uFF0C\u5012\u5728\u767D\u7691\u7691\u7684\u96EA\u5730\u4E2D\u3002\u6885\u6811\u4E00\u9635\u8F7B\u6447\uFF0C\u843D\u82B1\u65CB\u8F6C\u7740\u6389\u5728\u73AB\u7470\u4ED9\u5B50\u7684\u7EA2\u8863\u4E0A\u3002</p><p>\u300C\u6765\uFF0C\u559D\u70B9\u6C34\u3002\u300D</p><p>\u4E00\u53EA\u67D4\u8F6F\u7684\u624B\u638C\u6276\u5728\u8111\u540E\uFF0C\u5C06\u5979\u6258\u4E86\u8D77\u6765\u3002\u7D2B\u73AB\u6CA1\u6709\u7741\u773C\u4FBF\u6251\u8EAB\u62B1\u4F4F\u90A3\u4E2A\u6E29\u6696\u7684\u8EAB\u5B50\uFF0C\u53EB\u9053\uFF1A\u300C\u5927\u5E08\u59D0\u2026\u2026\u300D</p><p>\u98CE\u665A\u534E\u8FDE\u5FD9\u653E\u4E0B\u6C64\u836F\uFF0C\u67D4\u58F0\u5B89\u6170\u3002\u5979\u6BD4\u7D2B\u73AB\u5927\u4E86\u5341\u5C81\uFF0C\u53CC\u7709\u4FEE\u957F\u5165\u9B13\uFF0C\u76EE\u5982\u5BD2\u661F\u3002\u867D\u7136\u672A\u66FE\u5243\u5EA6\uFF0C\u4F46\u5979\u957F\u5E74\u8FFD\u968F\u5E08\u7236\uFF0C\u56E0\u6B64\u53EA\u7A7F\u4E86\u4EF6\u6DE1\u9752\u8272\u7684\u957F\u888D\uFF0C\u8FE5\u5F02\u4E8E\u51E0\u4F4D\u5E08\u59B9\u7684\u8273\u8272\u3002\u4F46\u5979\u9880\u957F\u7684\u8EAB\u6750\u548C\u8131\u4FD7\u7684\u6C14\u8D28\u4E0E\u4F17\u5973\u76F8\u6BD4\uFF0C\u6BEB\u4E0D\u900A\u8272\u3002</p><p>\u96EA\u5CF0\u795E\u5C3C\u5374\u4E0D\u5728\u5C71\u4E0A\u3002\u6708\u524D\u795E\u5C3C\u8D74\u5357\u6D77\u4E91\u6E38\uFF0C\u98D8\u6885\u5CF0\u53EA\u5269\u98CE\u665A\u534E\u4E00\u4EBA\u3002\u542C\u5B8C\u5E08\u59B9\u7684\u54ED\u8BC9\uFF0C\u98CE\u665A\u534E\u6C89\u601D\u7247\u523B\uFF0C\u62FF\u8D77\u6D41\u971C\u5251\uFF0C\u300C\u4F60\u5728\u8FD9\u91CC\u7B49\u5E08\u7236\uFF0C\u6211\u4E0B\u5C71\u53BB\u5BFB\u6797\u5E08\u59B9\u3002\u300D</p><p>\u7D2B\u73AB\u6025\u9053\uFF1A\u300C\u5E08\u59D0\uFF0C\u4F60\u4E00\u4E2A\u4EBA\u600E\u4E48\u884C\uFF1F\u300D</p><p>\u98CE\u665A\u534E\u62CD\u62CD\u5979\u7684\u80A9\u5934\uFF0C\u300C\u653E\u5FC3\u5427\u3002\u6211\u5728\u6697\u5904\uFF0C\u4E0D\u4F1A\u4E0E\u4ED6\u4EEC\u786C\u62FC\u3002\u300D</p><p>\u6155\u5BB9\u7D2B\u73AB\u55EB\u5685\u9053\uFF1A\u300C\u2026\u2026\u6211\u4E5F\u53BB\u2026\u2026\u300D</p><p>\u300C\u4F60\u4F24\u52BF\u8FD8\u672A\u75CA\u7652\uFF0C\u5728\u8FD9\u91CC\u4E5F\u597D\u7980\u62A5\u5E08\u7236\u3002\u300D</p><p>\u7D2B\u73AB\u773C\u5708\u53C8\u7EA2\u4E86\u8D77\u6765\uFF0C\u300C\u6797\u5E08\u59D0\u3001\u7EAA\u5E08\u59D0\u90FD\u662F\u4E3A\u6211\u88AB\u64D2\uFF0C\u6211\u4E5F\u8981\u53BB\u6551\u5979\u4EEC\u2026\u2026\u300D</p><p>\u628A\u5C0F\u5E08\u59B9\u4E00\u4EBA\u7559\u5728\u5C71\u4E0A\u4E5F\u4E0D\u662F\u529E\u6CD5\uFF0C\u98CE\u665A\u534E\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u6211\u5148\u52A9\u4F60\u7597\u4F24\uFF0C\u660E\u5929\u4E00\u8D77\u4E0B\u5C71\u597D\u4E86\u3002\u300D</p>',72);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
