import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_14" tabindex="-1"><a class="header-anchor" href="#_14" aria-hidden="true">#</a> 14</h1><p>\u9E70\u5634\u5CE1\u98CE\u5149\u5982\u6614\uFF0C\u7A7A\u8361\u8361\u4E86\u65E0\u4EBA\u5F71\uFF0C\u53EA\u6709\u904D\u5730\u8840\u8FF9\uFF0C\u8BC9\u8BF4\u7740\u4E09\u5929\u524D\u7684\u6076\u6218\u3002</p><p>\u98CE\u665A\u534E\u6E38\u76EE\u56DB\u987E\uFF0C\u7A81\u7136\u8DC3\u4E0A\u90A3\u5757\u5DE8\u77F3\u3002\u5F53\u65E5\u6563\u843D\u7684\u8863\u7269\u5DF2\u7ECF\u88AB\u5C71\u98CE\u5439\u8D70\uFF0C\u53EA\u7559\u4E0B\u5927\u7247\u5927\u7247\u5E72\u6DB8\u7684\u767D\u8272\u6DB2\u4F53\u3002</p><p>\u6155\u5BB9\u7D2B\u73AB\u8DDF\u7740\u8DC3\u4E86\u4E0A\u6765\uFF0C\u53EA\u770B\u4E86\u4E00\u773C\uFF0C\u80F8\u53E3\u4FBF\u88AB\u5835\u4F4F\u3002\u767D\u8272\u7684\u6C61\u6E0D\u5370\u5728\u9752\u9ED1\u8272\u7684\u77F3\u5934\u4E0A\u5206\u5916\u9192\u76EE\uFF0C\u9690\u9690\u663E\u51FA\u4E00\u4E2A\u5973\u4EBA\u4E0A\u534A\u8EAB\u7684\u5F62\u72B6\u3002\u8EAB\u5F62\u4E0B\u9762\u79EF\u4E86\u539A\u539A\u4E00\u5C42\u9EC4\u767D\u76F8\u95F4\u7684\u6C61\u6E0D\uFF0C\u4EE4\u4EBA\u89C1\u4E4B\u6B32\u5455\uFF0C\u4E0A\u9762\u7565\u7A00\u8584\u4E86\u4E9B\uFF0C\u5374\u5939\u7740\u4E24\u6EE9\u53D1\u9ED1\u7684\u8840\u8FF9\u3002</p><p>\u51E4\u665A\u534E\u4E0D\u613F\u8BA9\u5C0F\u5E08\u59B9\u591A\u770B\uFF0C\u8FDE\u5FD9\u62C9\u7740\u5979\u8DC3\u4E0B\u5DE8\u77F3\u3002</p><p>\u59D0\u59B9\u4FE9\u4E00\u8DEF\u65E0\u8A00\uFF0C\u8111\u4E2D\u5374\u90FD\u8BB0\u7740\u77F3\u4E0A\u7684\u767D\u8272\u4EBA\u5F62\u3002\u867D\u7136\u6CA1\u6709\u7EAA\u7709\u59A9\u7684\u6D88\u606F\uFF0C\u4F46\u843D\u5230\u661F\u6708\u6E56\u6076\u5F92\u624B\u4E2D\uFF0C\u5A07\u602F\u602F\u7684\u7EAA\u5E08\u59B9\u53EF\u600E\u4E48\u627F\u53D7\u5F97\u4E86\uFF1F</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6C90\u58F0\u4F20\u5374\u4E0D\u7BA1\u7EAA\u7709\u59A9\u662F\u5426\u627F\u53D7\u5F97\u4E86\uFF0C\u53EA\u8981\u4E0D\u6B7B\u5C31\u884C\u3002\u4E00\u8DEF\u4E0A\u4E0D\u4EC5\u661F\u6708\u6E56\u5E2E\u4F17\u968F\u65F6\u90FD\u53EF\u4EE5\u4FB5\u5165\u5979\u7684\u8EAB\u4F53\uFF0C\u6C90\u58F0\u4F20\u5174\u81F4\u6765\u65F6\u751A\u81F3\u628A\u5979\u6254\u5230\u8857\u5934\u6751\u4E2D\u4EFB\u4EBA\u73A9\u5F04\u3002</p><p>\u4ED6\u4E0E\u970D\u72C2\u7130\u4E0D\u540C\uFF0C\u5BF9\u66B4\u8650\u624B\u6BB5\u5174\u8DA3\u4E0D\u5927\uFF0C\u5374\u6700\u559C\u6B22\u770B\u5973\u5B50\u5C48\u8FB1\u7684\u6A21\u6837\u3002\u5BF9\u65B9\u8D8A\u9AD8\u8D35\uFF0C\u6C90\u58F0\u4F20\u5C31\u8D8A\u75DB\u5FEB\u3002\u88AB\u7B49\u56DE\u5230\u661F\u6708\u6E56\uFF0C\u8FD9\u4E2A\u6E29\u5A49\u548C\u987A\u7684\u8C6A\u95E8\u5343\u91D1\u5DF2\u7ECF\u88AB\u5978\u6DEB\u65E0\u6570\u6B21\u3002</p><p>\u6155\u5BB9\u9F99\u7FFB\u5F00\u7EAA\u7709\u59A9\u7684\u773C\u76AE\u770B\u4E86\u770B\uFF0C\u7709\u5934\u5FAE\u76B1\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u6155\u5BB9\u7D2B\u73AB\u5462\uFF1F\u300D</p><p>\u6C90\u58F0\u4F20\u5F2F\u4E0B\u4F5D\u507B\u7684\u8EAB\u5B50\uFF0C\u300C\u5C5E\u4E0B\u65E0\u80FD\uFF0C\u6155\u5BB9\u7D2B\u73AB\u8D1F\u4F24\u9003\u8D70\uFF0C\u8BF7\u5BAB\u4E3B\u6CBB\u7F6A\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u65E9\u5C31\u60F3\u9664\u6389\u8FD9\u4E2A\u8001\u5BB6\u4F19\uFF0C\u4F46\u6C90\u58F0\u4F20\u662F\u661F\u6708\u6E56\u5143\u8001\uFF0C\u5C45\u6728\u5802\u957F\u8001\u4E4B\u4F4D\u5DF2\u6709\u4E8C\u5341\u4F59\u5E74\uFF0C\u7D20\u6709\u5A01\u671B\uFF0C\u4ED6\u4E5F\u4E0D\u6562\u8F7B\u6613\u4E0B\u624B\uFF0C\u4E8E\u662F\u5475\u5475\u4E00\u7B11\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u6C90\u957F\u8001\u5B64\u8EAB\u4E00\u4EBA\u80FD\u751F\u64D2\u96EA\u5CF0\u795E\u5C3C\u95E8\u4E0B\u9AD8\u5F92\uFF0C\u5DF2\u662F\u5927\u529F\u4E00\u4EF6\uFF0C\u4F55\u7F6A\u4E4B\u6709\u54C7\u3002\u300D</p><p>\u6C90\u58F0\u4F20\u795E\u58F0\u6728\u7136\uFF0C\u8EAC\u8EAB\u9053\uFF1A\u300C\u591A\u8C22\u5BAB\u4E3B\u6055\u7F6A\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u76EF\u7740\u6C90\u58F0\u4F20\u7684\u80CC\u5F71\u770B\u4E86\u4E00\u4F1A\u513F\uFF0C\u62D6\u7740\u7EAA\u7709\u59A9\u7684\u811A\u8E1D\u8D70\u5165\u77F3\u5BAB\u3002\u7EAA\u7709\u59A9\u79C0\u53D1\u62D6\u5728\u77F3\u677F\u4E0A\uFF0C\u4E24\u773C\u65E0\u795E\uFF0C\u88AB\u626F\u5F00\u7684\u53CC\u817F\u95F4\u53C8\u7EA2\u53C8\u80BF\uFF0C\u5E78\u597D\u6C90\u58F0\u4F20\u9001\u6765\u65F6\u8FD8\u628A\u5979\u6D17\u4E86\u6D17\uFF0C\u624D\u6CA1\u6709\u5F53\u65F6\u7CBE\u6DB2\u56DB\u6EA2\u7684\u6837\u5B50\u3002</p><p>\u6155\u5BB9\u9F99\u63A8\u5F00\u7389\u95E8\uFF0C\u7B11\u9053\uFF1A\u300C\u5A18\uFF0C\u5B69\u513F\u6765\u770B\u4F60\u4E86\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u5012\u5728\u5E8A\u4E0A\uFF0C\u5446\u5446\u770B\u7740\u5BA4\u9876\uFF0C\u604D\u82E5\u672A\u95FB\u3002</p><p>\u6155\u5BB9\u9F99\u628A\u7EAA\u7709\u59A9\u6254\u5230\u5E8A\u4E0A\uFF0C\u4E00\u8FB9\u5978\u6DEB\u53D6\u4E50\uFF0C\u4E00\u8FB9\u5438\u53D6\u5979\u7684\u529F\u529B\uFF0C\u5FAE\u7B11\u7740\u9053\uFF1A\u300C\u96EA\u5CF0\u795E\u5C3C\u95E8\u4E0B\u679C\u7136\u4E0D\u4FD7\uFF0C\u5E74\u7EAA\u8F7B\u8F7B\u529F\u529B\u53EF\u4E0D\u6D45\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u773C\u73E0\u5446\u6EDE\u5730\u8F6C\u4E86\u4E00\u4E0B\uFF0C\u6162\u6162\u8BF4\u9053\uFF1A\u300C\u2026\u2026\u5979\u662F\u73AB\u513F\u7684\u5E08\u59D0\uFF0C\u4F60\u5C31\u653E\u8FC7\u5979\u5427\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u542B\u7B11\u9053\uFF1A\u300C\u5A18\u65E2\u7136\u5429\u5490\u4E86\uFF0C\u5B69\u513F\u81EA\u7136\u542C\u4ECE\uFF0C\u6211\u7EDD\u5BF9\u4E0D\u4F1A\u5F04\u6B7B\u5979\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u8270\u96BE\u5730\u5598\u4E86\u53E3\u6C14\uFF0C\u5934\u8F7B\u8F7B\u4FA7\u5230\u4E00\u8FB9\u3002</p><p>\u7EAA\u7709\u59A9\u65E9\u5DF2\u88AB\u6298\u78E8\u5F97\u795E\u5FD7\u4E0D\u6E05\uFF0C\u6B7B\u5C38\u822C\u6BEB\u65E0\u53CD\u5E94\u3002</p><p>\u5343\u5A07\u767E\u5A9A\u7684\u5A07\u5C0F\u59D0\u88AB\u641E\u6210\u8FD9\u822C\u6A21\u6837\uFF0C\u6155\u5BB9\u9F99\u4E5F\u6CA1\u592A\u5927\u5174\u8DA3\uFF0C\u5438\u53D6\u5B8C\u7EAA\u7709\u59A9\u7684\u529F\u529B\uFF0C\u4FBF\u7CBE\u795E\u6296\u64DE\u7684\u7AD9\u8D77\u8EAB\u6765\uFF0C\u8D70\u5230\u8273\u5973\u8EAB\u540E\u3002</p><p>\u4ED6\u4E24\u624B\u62CE\u8D77\u9634\u59EC\u7684\u82B1\u74E3\u5411\u4E24\u65C1\u7528\u529B\u626F\u5F00\uFF0C\u76F4\u5230\u8089\u7A74\u5185\u7684\u5AE9\u8089\u7FFB\u51FA\u4F53\u5916\uFF0C\u7EFD\u6210\u4E00\u6735\u5927\u5982\u624B\u638C\u7684\u8089\u82B1\u624D\u7B11\u563B\u563B\u5730\u8BF4\uFF1A\u300C\u5A18\uFF0C\u6211\u5E26\u4F60\u53BB\u770B\u573A\u597D\u620F\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u6276\u8D77\u767E\u82B1\u89C2\u97F3\uFF0C\u631F\u7740\u8F6F\u7EF5\u7EF5\u7684\u661F\u6708\u5BAB\u4E3B\uFF0C\u8D70\u5165\u53F3\u9996\u7B2C\u4E00\u4E2A\u752C\u9053\u7684\u7B2C\u4E8C\u95F4\u77F3\u5BA4\u3002\u77F3\u5BA4\u95E8\u6963\u4E0A\u9542\u7740\u4E00\u4E2A\u5C0F\u5C0F\u7684\u300C\u4E11\u300D\u5B57\u3002</p><p>\u63A8\u5F00\u95E8\uFF0C\u91CC\u9762\u4FBF\u4F20\u6765\u4E00\u9635\u4F4E\u6C89\u7684\u543C\u58F0\u3002</p><p>\u6C89\u95F7\u60A0\u957F\u7684\u58F0\u97F3\u56DE\u6C64\u5728\u77F3\u5BA4\u5185\uFF0C\u867D\u4E0D\u51CC\u5389\uFF0C\u5374\u5145\u6EE1\u72C2\u66B4\u7684\u610F\u5473\uFF0C\u8427\u4F5B\u5974\u987F\u65F6\u4E00\u9635\u5FC3\u60B8\u3002</p><p>\u6155\u5BB9\u9F99\u62FF\u51FA\u4E00\u9897\u660E\u73E0\u653E\u5728\u58C1\u4FA7\u7684\u706F\u53F0\u4E0A\uFF0C\u73E0\u8F89\u6E10\u653E\u5149\u660E\uFF0C\u6620\u51FA\u4E00\u5934\u58EE\u7855\u65E0\u6BD4\u7684\u5DE8\u725B\u3002\u89D2\u5982\u5F2F\u5200\uFF0C\u8E44\u4F3C\u94DC\u7897\uFF0C\u5468\u8EAB\u904D\u88AB\u5C3A\u8BB8\u957F\u77ED\u7684\u9B03\u6BDB\uFF0C\u6BE1\u6BEF\u822C\u5782\u5728\u5730\u4E0A\u3002</p><p>\u9634\u59EC\u88AB\u6155\u5BB9\u9F99\u6446\u6210\u8DEA\u4F0F\u7684\u59FF\u52BF\uFF0C\u81C0\u90E8\u9AD8\u9AD8\u62AC\u8D77\uFF0C\u96EA\u56E2\u822C\u7684\u81C0\u8089\u95F4\u5A07\u8273\u7684\u5AE9\u8089\u534A\u5F00\u534A\u95ED\uFF0C\u5A9A\u6001\u6A2A\u751F\u3002\u6155\u5BB9\u9F99\u5206\u5F00\u5DE8\u725B\u8EAB\u4E0B\u7684\u9B03\u6BDB\uFF0C\u62C9\u51FA\u4E00\u53EA\u7C97\u5982\u624B\u81C2\u7684\u9633\u5177\uFF0C\u5C06\u62F3\u5934\u5927\u7684\u9F9F\u5934\u9001\u5230\u661F\u6708\u5BAB\u4E3B\u79D8\u5904\uFF0C\u7136\u540E\u671D\u8273\u5987\u82B1\u8482\u4E0A\u8F7B\u8F7B\u5F39\u4E86\u4E00\u4E0B\u3002</p><p>\u4ED6\u9605\u5973\u6781\u591A\uFF0C\u6DF1\u77E5\u5973\u6027\u7684\u654F\u611F\u6240\u5728\uFF0C\u8FD9\u4E00\u5F39\u867D\u8F7B\uFF0C\u52B2\u529B\u5374\u5206\u4E86\u6570\u5C42\uFF0C\u76F4\u5165\u7ECF\u8109\u3002\u8273\u5973\u4E0B\u4F53\u4E00\u9635\u6296\u98A4\uFF0C\u6696\u878D\u878D\u7684\u9634\u7CBE\u5E94\u624B\u55B7\u51FA\uFF0C\u6B63\u5C04\u5728\u9F9F\u5934\u4E0A\u3002</p><p>\u5DE8\u725B\u626C\u5934\u543C\u4E86\u4E00\u58F0\uFF0C\u5DE8\u9633\u4E00\u633A\uFF0C\u7855\u5927\u7684\u9F9F\u5934\u50CF\u94C1\u67F1\u9876\u4F4F\u661F\u6708\u5BAB\u4E3B\u81C0\u95F4\u3002\u4F46\u5B83\u7684\u9633\u5177\u5B9E\u5728\u592A\u8FC7\u7C97\u58EE\uFF0C\u9976\u662F\u9634\u59EC\u534A\u5E74\u6765\u500D\u53D7\u6298\u78E8\uFF0C\u4E5F\u65E0\u6CD5\u8F7B\u6613\u5BB9\u7EB3\u3002\u88AB\u5DE8\u725B\u5728\u81C0\u95F4\u4E00\u9876\uFF0C\u5979\u5149\u6DA6\u7684\u8EAB\u4F53\u987A\u7740\u684C\u9762\u5411\u524D\u6ED1\u52A8\uFF0C\u9876\u5728\u77F3\u58C1\u4E0A\uFF0C\u67D4\u9888\u8F6F\u8F6F\u4E00\u4FA7\uFF0C\u9732\u51FA\u4E00\u5F20\u8273\u4E3D\u7684\u9762\u5B54\u3002</p><p>\u5979\u773C\u795E\u4E2D\u5145\u6EE1\u523B\u9AA8\u7684\u6068\u610F\uFF0C\u663E\u7136\u8EAB\u4F53\u867D\u4E0D\u80FD\u52A8\uFF0C\u4F46\u795E\u667A\u4F9D\u7136\u6E05\u695A\u3002</p><p>\u5DE8\u725B\u94DC\u94C3\u822C\u7684\u5DE8\u773C\u4E2D\u5E03\u6EE1\u8840\u4E1D\uFF0C\u5411\u524D\u8E0F\u4E86\u4E00\u6B65\uFF0C\u957F\u9B03\u906E\u4F4F\u8273\u5973\u96EA\u767D\u7684\u8EAB\u4F53\u3002</p><p>\u9634\u59EC\u7F8E\u76EE\u731B\u7136\u7741\u5927\uFF0C\u5589\u5934\u300C\u5443\u5443\u300D\u8FDE\u58F0\u3002</p><p>\u6155\u5BB9\u9F99\u542B\u7B11\u64A9\u5F00\u9B03\u6BDB\uFF0C\u89C2\u8D4F\u4EC7\u4EBA\u88AB\u5DE8\u725B\u5978\u6DEB\u7684\u8273\u666F\u3002\u624B\u81C2\u7C97\u7684\u5DE8\u9633\u5927\u534A\u5DF2\u523A\u5165\u8273\u5987\u4F53\u5185\uFF0C\u8FDB\u5165\u65F6\u7EA2\u8273\u8273\u7684\u5AE9\u8089\u4E00\u4E1D\u4E0D\u5269\u5C3D\u88AB\u6324\u5165\u8089\u7A74\uFF0C\u53EA\u89C1\u4E00\u652F\u9752\u7B4B\u66B4\u9732\u7684\u7C97\u9ED1\u8089\u68D2\u76F4\u76F4\u6CA1\u5165\u96EA\u81C0\u6B63\u4E2D\uFF0C\u51E0\u4E4E\u5C06\u6D51\u5706\u7684\u7389\u81C0\u6491\u788E\uFF1B\u62D4\u51FA\u65F6\u96EA\u81C0\u4E2D\u50CF\u662F\u9C9C\u82B1\u76DB\u5F00\u822C\uFF0C\u7FFB\u51FA\u4E00\u56E2\u5A07\u7EA2\u3002\u8089\u82B1\u65F6\u6536\u65F6\u653E\uFF0C\u900F\u660E\u7684\u6DEB\u6DB2\u70B9\u70B9\u6EF4\u6EF4\u4ECE\u8089\u68D2\u4E0A\u6E85\u843D\u4E0B\u6765\u3002</p><p>\u9634\u59EC\u9876\u7740\u77F3\u58C1\u4E00\u52A8\u4E0D\u52A8\uFF0C\u53EA\u6709\u96EA\u767D\u7684\u5C0F\u8179\u4E00\u9F13\u4E00\u9F13\uFF0C\u663E\u793A\u7740\u5DE8\u9633\u8FDB\u51FA\u6A21\u6837\u3002</p><p>\u6155\u5BB9\u9F99\u6309\u5728\u661F\u6708\u5BAB\u4E3B\u6ED1\u817B\u7684\u809A\u76AE\u4E0A\uFF0C\u611F\u53D7\u5DE8\u725B\u62BD\u9001\u7684\u529B\u5EA6\uFF0C\u7B11\u9053\uFF1A\u300C\u8D31\u4EBA\uFF0C\u4F60\u4E0D\u662F\u559C\u6B22\u88AB\u5927\u5BB6\u4F19\u64CD\u5417\uFF1F\u8FD9\u4E0B\u723D\u4E86\u5417\uFF1F\u300D</p><p>\u9634\u59EC\u5185\u529F\u5C3D\u5931\uFF0C\u88AB\u624B\u81C2\u822C\u7684\u5DE8\u9633\u4E00\u9635\u731B\u6345\uFF0C\u4E0B\u4F53\u5267\u75DB\u6B32\u88C2\uFF0C\u51E0\u4E4E\u6655\u4E86\u8FC7\u53BB\u3002</p><p>\u6155\u5BB9\u9F99\u628A\u767E\u82B1\u89C2\u97F3\u62B1\u5230\u5DE8\u725B\u8EAB\u540E\uFF0C\u8BA9\u5979\u770B\u6E05\u5DE8\u725B\u4E24\u817F\u95F4\u90A3\u4E2A\u53D8\u5F62\u7684\u96EA\u81C0\u548C\u4E0D\u65AD\u7FFB\u5377\u7684\u5AE9\u8089\uFF0C\u5F97\u610F\u5730\u8BF4\uFF1A\u300C\u5A18\uFF0C\u8FD9\u4E2A\u8D31\u4EBA\u5BB3\u5F97\u6211\u4EEC\u5BB6\u7834\u4EBA\u4EA1\uFF0C\u4ECA\u5929\u5B69\u513F\u7EC8\u4E8E\u62A5\u4EC7\u4E86\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u5E76\u672A\u89C1\u8FC7\u9634\u59EC\uFF0C\u6B64\u65F6\u770B\u5230\u8FD9\u6837\u4E00\u4E2A\u7F8E\u8273\u7684\u5987\u4EBA\u88AB\u513F\u5B50\u5982\u6B64\u51CC\u8FB1\uFF0C\u5FC3\u5934\u4E0D\u4F46\u4E86\u65E0\u6068\u610F\uFF0C\u53CD\u800C\u6697\u751F\u601C\u60DC\u3002\u5979\u4F4E\u58F0\u8BF4\uFF1A\u300C\u6740\u4E86\u5979\u5427\u3002\u300D</p><p>\u6155\u5BB9\u9F99\u4E00\u6014\uFF0C\u300C\u4F55\u5FC5\u6740\u4E86\u5979\uFF1F\u8BA9\u5979\u6D3B\u7740\u8BA9\u54B1\u4EEC\u60F3\u600E\u4E48\u73A9\u5C31\u600E\u4E48\u73A9\uFF0C\u4E0D\u6BD4\u6740\u4E86\u5979\u66F4\u597D\uFF1F\u300D</p><p>\u5DE8\u725B\u5411\u524D\u72E0\u72E0\u4E00\u9876\uFF0C\u7C97\u5927\u7684\u9633\u5177\u6574\u53EA\u6345\u5165\u8089\u7A74\uFF0C\u8FDE\u9634\u961C\u4E0A\u7684\u6BDB\u53D1\u4E5F\u88AB\u5E26\u5165\u4F53\u5185\u3002\u767E\u82B1\u89C2\u97F3\u4E0D\u5FCD\u518D\u770B\uFF0C\u95ED\u7740\u773C\u8BF4\uFF1A\u300C\u6740\u4E86\u5979\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u6C89\u9ED8\u7247\u523B\uFF0C\u70B9\u4E86\u70B9\u5934\uFF0C\u300C\u597D\uFF0C\u6211\u6740\u4E86\u5979\u3002\u300D\u53CC\u81C2\u4E00\u7D27\uFF0C\u62E5\u7D27\u8427\u4F5B\u5974\u7684\u8EAB\u4F53\uFF0C\u70ED\u60C5\u5982\u706B\u5730\u8BF4\uFF1A\u300C\u5A18\uFF0C\u6765\u548C\u5B69\u513F\u6B22\u597D\u4E00\u6B21\u2026\u2026\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u5FC3\u5982\u5200\u5272\uFF0C\u4E00\u638C\u6253\u5728\u6155\u5BB9\u9F99\u8138\u4E0A\uFF0C\u75DB\u5FC3\u75BE\u9996\u5730\u8BF4\uFF1A\u300C\u4F60\u600E\u4E48\u53D8\u6210\u8FD9\u6837\u7684\u79BD\u517D\uFF1F\u8FDE\u4EB2\u5A18\u4E5F\u4E0D\u653E\u8FC7\uFF1F\u300D</p><p>\u6155\u5BB9\u773C\u89D2\u4E00\u8DF3\uFF0C\u72DE\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u4E5F\u5BC2\u5BDE\u8FD9\u4E48\u591A\u5E74\u4E86\uFF0C\u5C31\u8BA9\u5B69\u513F\u597D\u597D\u5B89\u6170\u4F60\u5427\u2026\u2026\u4E0A\u4E00\u6B21\u4F60\u4E0D\u5C31\u88AB\u5B69\u513F\u64CD\u5F97\u6B32\u4ED9\u6B32\u6B7B\u5417\uFF1F\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u7389\u8138\u6DA8\u5F97\u901A\u7EA2\u3002\u90A3\u5929\u5979\u786E\u5B9E\u88AB\u513F\u5B50\u5978\u5F97\u9AD8\u6F6E\u8FED\u8D77\uFF0C\u90A3\u6839\u602A\u5F02\u7684\u8089\u68D2\u4F3C\u4E4E\u6BCF\u4E00\u4E0B\u90FD\u9876\u5230\u4F53\u5185\u6700\u9178\u9EBB\u7684\u5730\u65B9\uFF0C\u6C79\u6D8C\u7684\u6DEB\u6C34\u51E0\u4E4E\u6D78\u6E7F\u4E86\u6574\u6761\u88AB\u8925\u3002</p><p>\u6B64\u523B\u88AB\u513F\u5B50\u5F53\u9762\u8BF4\u51FA\uFF0C\u5979\u53C8\u7F9E\u53C8\u6068\uFF0C\u96EA\u767D\u7684\u7EA4\u624B\u6323\u624E\u7740\u62FC\u547D\u6253\u5728\u513F\u5B50\u80A9\u5934\u3002</p><p>\u6155\u5BB9\u9F99\u54C8\u54C8\u4E00\u7B11\uFF0C\u62B1\u7740\u6BCD\u4EB2\u65CB\u98CE\u822C\u63A0\u8FDB\u81EA\u5DF1\u6240\u5C45\u7684\u5929\u5B57\u7532\u5BA4\u3002</p><p>\u5E7D\u5E7D\u73E0\u8F89\u4E2D\uFF0C\u6620\u51FA\u4E00\u5934\u5DE8\u725B\u5C16\u5229\u7684\u957F\u89D2\uFF0C\u548C\u5B83\u8EAB\u4E0B\u4E00\u5177\u5A07\u8273\u6B32\u6EF4\u7684\u7F8E\u4F53\u3002</p><p>\u7EAA\u7709\u59A9\u4ECD\u8EBA\u5728\u5730\u6BEF\u4E0A\uFF0C\u5A07\u7F8E\u7684\u8EAB\u4F53\u5927\u534A\u88AB\u96EA\u767D\u7684\u957F\u7ED2\u906E\u63A9\uFF0C\u53EA\u6709\u80F8\u524D\u9AD8\u8038\u7684\u5706\u4E73\u633A\u7740\u4E24\u7C92\u6BB7\u7EA2\u7684\u4E73\u5934\uFF0C\u4EFF\u4F5B\u96EA\u91CE\u4E2D\u6A31\u6843\uFF0C\u7EA2\u8273\u593A\u76EE\u3002</p><p>\u6155\u5BB9\u9F99\u632F\u94C3\u5524\u6765\u4F8D\u4ECE\uFF0C\u300C\u628A\u7EAA\u5A4A\u5B50\u9001\u5230\u4EB2\u5B57\u4E19\u5BA4\u3002\u55EF\uFF0C\u6BCF\u5929\u6700\u591A\u4E8C\u5341\u4EBA\uFF0C\u522B\u628A\u5979\u5F04\u6B7B\u4E86\u3002\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u4ECD\u5728\u5F92\u52B3\u5730\u6323\u624E\u7740\uFF0C\u542C\u5230\u8FD9\u53E5\u8BDD\u4E0D\u7531\u7684\u4E00\u5446\uFF0C\u300C\u4F60\u4E0D\u662F\u7B54\u5E94\u653E\u8FC7\u5979\u5417\uFF1F\u300D</p><p>\u6155\u5BB9\u9F99\u6DEB\u7B11\u7740\u5728\u6BCD\u4EB2\u8138\u4E0A\u6478\u4E86\u4E00\u628A\uFF0C\u300C\u5B69\u513F\u53EA\u7B54\u5E94\u4E0D\u5F04\u6B7B\u5979\uFF0C\u5A18\u521A\u624D\u4E5F\u542C\u89C1\u4E86\u3002\u4EE5\u524D\u5BAB\u91CC\u63B3\u6765\u7684\u5973\u5B50\uFF0C\u6709\u7684\u4E00\u5929\u80FD\u63A5\u4E00\u767E\u591A\u4EBA\u5462\u3002\u300D</p><p>\u300C\u5979\u662F\u4F60\u59B9\u59B9\u7684\u5E08\u59D0\uFF01\u4F60\u600E\u4E48\u53EF\u4EE5\u8FD9\u6837\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u7684\u8138\u8272\u7A81\u7136\u53D8\u5F97\u4E25\u8083\u8D77\u6765\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u5A18\uFF0C\u6155\u5BB9\u7D2B\u73AB\u771F\u662F\u6211\u7684\u4EB2\u59B9\u59B9\u5417\uFF1F\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u54ED\u7740\u70B9\u4E86\u70B9\u5934\uFF0C\u300C\u73AB\u513F\u662F\u4F60\u7239\u7684\u9057\u8179\u5B50\uFF0C\u4F60\u7684\u4EB2\u59B9\u59B9\u3002\u5B69\u5B50\uFF0C\u5A18\u6536\u517B\u80DC\u513F\uFF0C\u5C31\u662F\u628A\u4ED6\u5F53\u6210\u4F60\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u76EE\u5149\u4E00\u5BD2\uFF0C\u534A\u664C\u53C8\u95EE\u9053\uFF1A\u300C\u59B9\u59B9\u4EBA\u79F0\u73AB\u7470\u4ED9\u5B50\uFF0C\u662F\u4E0D\u662F\u957F\u5F97\u5F88\u7F8E\uFF1F\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u542C\u51FA\u4ED6\u58F0\u97F3\u91CC\u7684\u6DEB\u90AA\u610F\u5473\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u4F60\u2026\u2026\u4F60\u60F3\u600E\u4E48\u6837\u2026\u2026\u5979\u53EF\u662F\u4F60\u7684\u4EB2\u59B9\u59B9\u2026\u2026\u300D</p><p>\u6155\u5BB9\u9F99\u8214\u4E86\u8214\u5634\u5507\uFF0C\u300C\u4EB2\u59B9\u59B9\u624D\u662F\u6B63\u597D\u2014\u2014\u90A3\u6837\u751F\u4E0B\u7684\u5B69\u5B50\u624D\u80FD\u4FDD\u8BC1\u6211\u4EEC\u5BB6\u65CF\u8840\u7EDF\u7684\u7EAF\u6B63\u2026\u2026\u300D</p><p>\u767E\u82B1\u89C2\u97F3\u60CA\u53EB\u7740\u6367\u4F4F\u513F\u5B50\u6076\u9B54\u822C\u7684\u4FCA\u8138\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u90A3\u662F\u4E71\u4F26\uFF01\u4F5B\u7956\u83E9\u8428\u4E0D\u4F1A\u653E\u8FC7\u4F60\u7684\uFF01\u751F\u4E0B\u7684\u5B69\u5B50\u53EA\u4F1A\u662F\u767D\u75F4\uFF01\u4F60\u4F1A\u88AB\u96F7\u5288\u7684\uFF01\u300D</p><p>\u6155\u5BB9\u9F99\u5657\u54E7\u4E00\u7B11\uFF0C\u300C\u5A18\uFF0C\u4F60\u8FD8\u4FE1\u4EC0\u4E48\u83E9\u8428\u5462\u3002\u8BF4\u4E71\u4F26\uFF0C\u8FD9\u624D\u662F\u5462\uFF01\u300D\u8BF4\u7740\u91CD\u91CD\u538B\u5728\u6BCD\u4EB2\u8EAB\u4E0A\uFF0C\u8089\u68D2\u957F\u9A71\u76F4\u5165\uFF0C\u633A\u8FDB\u8089\u7A74\u3002\u767E\u82B1\u89C2\u97F3\u75DB\u4E0D\u6B32\u751F\u7684\u6367\u4F4F\u9762\u5B54\uFF0C\u6CEA\u6C34\u4ECE\u6307\u7F1D\u95F4\u4E0D\u4F4F\u6D8C\u51FA\u3002</p><p>\u6155\u5BB9\u9F99\u4E00\u8FB9\u62BD\u9001\uFF0C\u4E00\u8FB9\u60A0\u7136\u795E\u5F80\u5730\u60F3\u50CF\u7740\u59B9\u59B9\u7684\u7F8E\u8C8C\u8EAB\u4F53\u3002\u767E\u5E74\u6765\u6570\u5341\u56FD\u5BB6\u65CB\u8D77\u65CB\u706D\uFF0C\u4EA1\u56FD\u7684\u539F\u56E0\u5982\u51FA\u4E00\u8F99\uFF0C\u90FD\u662F\u8001\u5B50\u82F1\u96C4\u6253\u5F97\u5929\u4E0B\uFF0C\u53C8\u88AB\u65E0\u80FD\u7684\u513F\u5B50\u8F7B\u6613\u4E22\u5F03\uFF0C\u8FD9\u90FD\u662F\u8840\u7EDF\u7684\u7F18\u6545\u3002</p><p>\u9634\u59EC\u7684\u8BDD\u4ED6\u8FD8\u8BB0\u5F97\uFF1A\u6781\u897F\u4E4B\u5904\u6709\u4E00\u56FD\u5EA6\uFF0C\u5386\u4EE3\u7687\u5BA4\u90FD\u662F\u4EB2\u5144\u59B9\u4E92\u76F8\u5A5A\u914D\uFF0C\u867D\u7136\u751F\u4E0B\u7684\u5B69\u5B50\u591A\u662F\u767D\u75F4\uFF0C\u4F46\u95F4\u6216\u4F1A\u6709\u5929\u624D\u2026\u2026</p><p>\u300C\u518D\u591A\u7684\u767D\u75F4\u6211\u4E5F\u4E0D\u6015\uFF0C\u53EA\u8981\u6709\u4E00\u4E2A\u5929\u624D\u7684\u513F\u5B50\u80FD\u7EE7\u627F\u6211\u7684\u5B9D\u5EA7\uFF0C\u518D\u7559\u4E00\u7FA4\u5973\u513F\u4E0E\u4ED6\u5A5A\u914D\u5C31\u884C\u4E86\uFF01\u300D</p>',63);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
